db.iliad.insert({ pg : 23335, line : "gives a fellow-feeling to whole clans and nations, and thus enlists"});
db.iliad.insert({ pg : 23336, line : "their affections in behalf of those time-honoured representatives of"});
db.iliad.insert({ pg : 23337, line : "their ancient blood, in whose success they feel a personal interest?"});
db.iliad.insert({ pg : 23338, line : "Hence the delight when we recognize an act of nobility or justice in"});
db.iliad.insert({ pg : 23339, line : "our hereditary princes"});
db.iliad.insert({ pg : 23341, line : "\"'Tuque prior, tu parce genus qui ducis Olympo,"});
