db.iliadfr.insert({ pg : 66, line : "Chant 1"});
db.iliadfr.insert({ pg : 68, line : "Chante, déesse, du Pèlèiade Akhilleus la colère désastreuse, qui"});
db.iliadfr.insert({ pg : 69, line : "de maux infinis accabla les Akhaiens, et précipita chez Aidès tant"});
db.iliadfr.insert({ pg : 70, line : "de fortes âmes de héros, livrés eux-mêmes en pâture aux chiens et"});
db.iliadfr.insert({ pg : 71, line : "à tous les oiseaux carnassiers. Et le dessein de Zeus"});
db.iliadfr.insert({ pg : 72, line : "s'accomplissait ainsi, depuis qu'une querelle avait divisé"});
db.iliadfr.insert({ pg : 73, line : "l'Atréide, roi des hommes, et le divin Akhilleus."});
db.iliadfr.insert({ pg : 75, line : "Qui d'entre les dieux les jeta dans cette dissension? Le fils de"});
db.iliadfr.insert({ pg : 76, line : "Zeus et de Lètô. Irrité contre le roi, il suscita dans l'armée un"});
db.iliadfr.insert({ pg : 77, line : "mal mortel, et les peuples périssaient, parce que l'Atréide avait"});
db.iliadfr.insert({ pg : 78, line : "couvert d'opprobre Khrysès le sacrificateur."});
db.iliadfr.insert({ pg : 80, line : "Et celui-ci était venu vers les nefs rapides des Akhaiens pour"});
db.iliadfr.insert({ pg : 81, line : "racheter sa fille; et, portant le prix infini de"});
db.iliadfr.insert({ pg : 82, line : "l'affranchissement, et, dans ses mains, les bandelettes de"});
db.iliadfr.insert({ pg : 83, line : "l'Archer Apollôn, suspendues au sceptre d'or, il conjura tous les"});
db.iliadfr.insert({ pg : 84, line : "Akhaiens, et surtout les deux Atréides, princes des peuples:"});
db.iliadfr.insert({ pg : 86, line : "-- Atréides, et vous, anciens aux belles knèmides, que les dieux"});
db.iliadfr.insert({ pg : 87, line : "qui habitent les demeures olympiennes vous donnent de détruire la"});
db.iliadfr.insert({ pg : 88, line : "ville de Priamos et de vous retourner heureusement; mais rendez-"});
db.iliadfr.insert({ pg : 89, line : "moi ma fille bien-aimée et recevez le prix de l'affranchissement,"});
db.iliadfr.insert({ pg : 90, line : "si vous révérez le fils de Zeus, l'archer Apollôn."});
db.iliadfr.insert({ pg : 92, line : "Et tous les Akhaiens, par des rumeurs favorables, voulaient qu'on"});
db.iliadfr.insert({ pg : 93, line : "respectât le sacrificateur et qu'on reçût le prix splendide; mais"});
db.iliadfr.insert({ pg : 94, line : "cela ne plut point à l'âme de l'Atréide Agamemnôn, et il le chassa"});
db.iliadfr.insert({ pg : 95, line : "outrageusement, et il lui dit cette parole violente:"});
db.iliadfr.insert({ pg : 97, line : "-- Prends garde, vieillard, que je te rencontre auprès des nefs"});
db.iliadfr.insert({ pg : 98, line : "creuses, soit que tu t'y attardes, soit que tu reviennes, de peur"});
db.iliadfr.insert({ pg : 99, line : "que le sceptre et les bandelettes du dieu ne te protègent plus. Je"});
db.iliadfr.insert({ pg : 100, line : "n'affranchirai point ta fille. La vieillesse l'atteindra, en ma"});
db.iliadfr.insert({ pg : 101, line : "demeure, dans Argos, loin de sa patrie, tissant la toile et"});
db.iliadfr.insert({ pg : 102, line : "partageant mon lit. Mais, va! ne m'irrite point, afin de t'en"});
db.iliadfr.insert({ pg : 103, line : "retourner sauf."});
db.iliadfr.insert({ pg : 105, line : "Il parla ainsi, et le vieillard trembla et obéit. Et il allait,"});
db.iliadfr.insert({ pg : 106, line : "silencieux, le long du rivage de la mer aux bruits sans nombre."});
db.iliadfr.insert({ pg : 107, line : "Et, se voyant éloigné, il conjura le roi Apollôn que Lètô à la"});
db.iliadfr.insert({ pg : 108, line : "belle chevelure enfanta:"});
db.iliadfr.insert({ pg : 110, line : "-- Entends-moi, porteur de l'arc d'argent, qui protèges Khrysè et"});
db.iliadfr.insert({ pg : 111, line : "Killa la sainte, et commandes fortement sur Ténédos, Smintheus! Si"});
db.iliadfr.insert({ pg : 112, line : "jamais j'ai orné ton beau temple, si jamais j'ai brûlé pour toi"});
db.iliadfr.insert({ pg : 113, line : "les cuisses grasses des taureaux et des chèvres, exauce mon voeu:"});
db.iliadfr.insert({ pg : 114, line : "que les Danaens expient mes larmes sous tes flèches!"});
db.iliadfr.insert({ pg : 116, line : "Il parla ainsi en priant, et Phoibos Apollôn l'entendit; et, du"});
db.iliadfr.insert({ pg : 117, line : "sommet Olympien, il se précipita, irrité dans son coeur, portant"});
db.iliadfr.insert({ pg : 118, line : "l'arc sur ses épaules, avec le plein carquois. Et les flèches"});
db.iliadfr.insert({ pg : 119, line : "sonnaient sur le dos du dieu irrité, à chacun de ses mouvements."});
db.iliadfr.insert({ pg : 120, line : "Et il allait, semblable à la nuit."});
db.iliadfr.insert({ pg : 122, line : "Assis à l'écart, loin des nefs, il lança une flèche, et un bruit"});
db.iliadfr.insert({ pg : 123, line : "terrible sortit de l'arc d'argent. Il frappa les mulets d'abord et"});
db.iliadfr.insert({ pg : 124, line : "les chiens rapides; mais, ensuite, il perça les hommes eux-mêmes"});
db.iliadfr.insert({ pg : 125, line : "du trait qui tue. Et sans cesse les bûchers brûlaient, lourds de"});
db.iliadfr.insert({ pg : 126, line : "cadavres."});
db.iliadfr.insert({ pg : 128, line : "Depuis neuf jours les flèches divines sifflaient à travers"});
db.iliadfr.insert({ pg : 129, line : "l'armée; et, le dixième, Akhilleus convoqua les peuples dans"});
db.iliadfr.insert({ pg : 130, line : "l'agora. Hèrè aux bras blancs le lui avait inspiré, anxieuse des"});
db.iliadfr.insert({ pg : 131, line : "Danaens et les voyant périr. Et quand ils furent tous réunis, se"});
db.iliadfr.insert({ pg : 132, line : "levant au milieu d'eux, Akhilleus aux pieds rapides parla ainsi:"});
db.iliadfr.insert({ pg : 134, line : "-- Atréide, je pense qu'il nous faut reculer et reprendre nos"});
db.iliadfr.insert({ pg : 135, line : "courses errantes sur la mer, si toutefois nous évitons la mort,"});
db.iliadfr.insert({ pg : 136, line : "car, toutes deux, la guerre et la contagion domptent les Akhaiens."});
db.iliadfr.insert({ pg : 137, line : "Hâtons-nous d'interroger un divinateur ou un sacrificateur, ou un"});
db.iliadfr.insert({ pg : 138, line : "interprète des songes, car le songe vient de Zeus. Qu'il dise"});
db.iliadfr.insert({ pg : 139, line : "pourquoi Phoibos Apollôn est irrité, soit qu'il nous reproche des"});
db.iliadfr.insert({ pg : 140, line : "voeux négligés ou qu'il demande des hécatombes promises. Sachons"});
db.iliadfr.insert({ pg : 141, line : "si, content de la graisse fumante des agneaux et des belles"});
db.iliadfr.insert({ pg : 142, line : "chèvres, il écartera de nous cette contagion."});
db.iliadfr.insert({ pg : 144, line : "Ayant ainsi parlé, il s'assit. Et le Thestoride Kalkhas,"});
db.iliadfr.insert({ pg : 145, line : "l'excellent divinateur, se leva. Il savait les choses présentes,"});
db.iliadfr.insert({ pg : 146, line : "futures et passées, et il avait conduit à Ilion les nefs"});
db.iliadfr.insert({ pg : 147, line : "Akhaiennes, à l'aide de la science sacrée dont l'avait doué"});
db.iliadfr.insert({ pg : 148, line : "Phoibos Apollôn. Très sage, il dit dans l'agora:"});
db.iliadfr.insert({ pg : 150, line : "-- Ô Akhilleus, cher à Zeus, tu m'ordonnes d'expliquer la colère"});
db.iliadfr.insert({ pg : 151, line : "du roi Apollôn l'archer. Je le ferai, mais promets d'abord et jure"});
db.iliadfr.insert({ pg : 152, line : "que tu me défendras de ta parole et de tes mains; car, sans doute,"});
db.iliadfr.insert({ pg : 153, line : "je vais irriter l'homme qui commande à tous les Argiens et à qui"});
db.iliadfr.insert({ pg : 154, line : "tous les Akhaiens obéissent. Un roi est trop puissant contre un"});
db.iliadfr.insert({ pg : 155, line : "inférieur qui l'irrite. Bien que, dans l'instant, il refrène sa"});
db.iliadfr.insert({ pg : 156, line : "colère, il l'assouvit un jour, après l'avoir couvée dans son"});
db.iliadfr.insert({ pg : 157, line : "coeur. Dis-moi donc que tu me protégeras."});
db.iliadfr.insert({ pg : 159, line : "Et Akhilleus aux pieds rapides, lui répondant, parla ainsi:"});
db.iliadfr.insert({ pg : 161, line : "-- Dis sans peur ce que tu sais. Non! par Apollôn, cher à Zeus, et"});
db.iliadfr.insert({ pg : 162, line : "dont tu découvres aux Danaens les volontés sacrées, non! nul"});
db.iliadfr.insert({ pg : 163, line : "d'entre eux, Kalkhas, moi vivant et les yeux ouverts, ne portera"});
db.iliadfr.insert({ pg : 164, line : "sur toi des mains violentes auprès des nefs creuses, quand même tu"});
db.iliadfr.insert({ pg : 165, line : "nommerais Agamemnôn, qui se glorifie d'être le plus puissant des"});
db.iliadfr.insert({ pg : 166, line : "Akhaiens."});
db.iliadfr.insert({ pg : 168, line : "Et le divinateur irréprochable prit courage et dit:"});
db.iliadfr.insert({ pg : 170, line : "-- Apollôn ne vous reproche ni voeux ni hécatombes; mais il venge"});
db.iliadfr.insert({ pg : 171, line : "son sacrificateur, qu'Agamemnôn a couvert dopprobre, car il n'a"});
db.iliadfr.insert({ pg : 172, line : "point délivré sa fille, dont il a refusé le prix"});
db.iliadfr.insert({ pg : 173, line : "d'affranchissement. Et c'est pour cela que l'archer Apollôn vous"});
db.iliadfr.insert({ pg : 174, line : "accable de maux; et il vous en accablera, et il n'écartera point"});
db.iliadfr.insert({ pg : 175, line : "les lourdes kères de la contagion, que vous n'ayez rendu à son"});
db.iliadfr.insert({ pg : 176, line : "père bien-aimé la jeune fille aux sourcils arqués, et qu'une"});
db.iliadfr.insert({ pg : 177, line : "hécatombe sacrée n'ait été conduite à Khrysè. Alors nous"});
db.iliadfr.insert({ pg : 178, line : "apaiserons le dieu."});
db.iliadfr.insert({ pg : 180, line : "Ayant ainsi parlé, il s'assit. Et le héros Atréide Agamemnôn, qui"});
db.iliadfr.insert({ pg : 181, line : "commande au loin, se leva, plein de douleur; et une noire colère"});
db.iliadfr.insert({ pg : 182, line : "emplissait sa poitrine, et ses yeux étaient pareils à des feux"});
db.iliadfr.insert({ pg : 183, line : "flambants. Furieux contre Kalkhas, il parla ainsi:"});
db.iliadfr.insert({ pg : 185, line : "-- Divinateur malheureux, jamais tu ne m'as rien dit d'agréable."});
db.iliadfr.insert({ pg : 186, line : "Les maux seuls te sont doux à prédire. Tu n'as jamais ni bien"});
db.iliadfr.insert({ pg : 187, line : "parlé ni bien agi; et voici maintenant qu'au milieu des Danaens,"});
db.iliadfr.insert({ pg : 188, line : "dans l'agora, tu prophétises que l'archer Apollon nous accable de"});
db.iliadfr.insert({ pg : 189, line : "maux parce que je nai point voulu recevoir le prix splendide de"});
db.iliadfr.insert({ pg : 190, line : "la vierge Khrysèis, aimant mieux la retenir dans ma demeure"});
db.iliadfr.insert({ pg : 191, line : "lointaine. En effet, je la préfère à Klytaimnestrè, que j'ai"});
db.iliadfr.insert({ pg : 192, line : "épousée vierge. Elle ne lui est inférieure ni par le corps, ni par"});
db.iliadfr.insert({ pg : 193, line : "la taille, ni par l'intelligence, ni par l'habileté aux travaux."});
db.iliadfr.insert({ pg : 194, line : "Mais je la veux rendre. Je préfère le salut des peuples à leur"});
db.iliadfr.insert({ pg : 195, line : "destruction. Donc, préparez-moi promptement un prix, afin que,"});
db.iliadfr.insert({ pg : 196, line : "seul d'entre tous les Argiens, je ne sois point dépouillé. Cela ne"});
db.iliadfr.insert({ pg : 197, line : "conviendrait point; car, vous le voyez, ma part m'est retirée."});
db.iliadfr.insert({ pg : 199, line : "Et le divan Akhilleus aux pieds rapides lui répondit:"});
db.iliadfr.insert({ pg : 201, line : "-- Très orgueilleux Atréide, le plus avare des hommes, comment les"});
db.iliadfr.insert({ pg : 202, line : "magnanimes Akhaiens te donneraient-ils un autre prix? Avons-nous"});
db.iliadfr.insert({ pg : 203, line : "des dépouilles à mettre en commun? Celles que nous avons enlevées"});
db.iliadfr.insert({ pg : 204, line : "des villes saccagées ont été distribuées, et il ne convient point"});
db.iliadfr.insert({ pg : 205, line : "que les hommes en fassent un nouveau partage. Mais toi, remets"});
db.iliadfr.insert({ pg : 206, line : "cette jeune fille à son dieu, et nous, Akhaiens, nous te rendrons"});
db.iliadfr.insert({ pg : 207, line : "le triple et le quadruple, si jamais Zeus nous donne de détruire"});
db.iliadfr.insert({ pg : 208, line : "Troiè aux fortes murailles."});
db.iliadfr.insert({ pg : 210, line : "Et le roi Agamemnôn, lui répondant, parla ainsi:"});
db.iliadfr.insert({ pg : 212, line : "-- Ne crois point me tromper, quelque brave que tu sois, Akhilleus"});
db.iliadfr.insert({ pg : 213, line : "semblable à un dieu, car tu ne me séduiras ni ne me persuaderas."});
db.iliadfr.insert({ pg : 214, line : "Veux-tu, tandis que tu gardes ta part, que je reste assis dans mon"});
db.iliadfr.insert({ pg : 215, line : "indigence, en affranchissant cette jeune fille? Si les magnanimes"});
db.iliadfr.insert({ pg : 216, line : "Akhaiens satisfont mon coeur par un prix d'une valeur égale, soit."});
db.iliadfr.insert({ pg : 217, line : "Sinon, je ravirai le tien, ou celui d'Aias, ou celui d'Odysseus;"});
db.iliadfr.insert({ pg : 218, line : "et je l'emporterai, et celui-là s'indignera vers qui j'irai. Mais"});
db.iliadfr.insert({ pg : 219, line : "nous songerons à ceci plus tard. Donc, lançons une nef noire à la"});
db.iliadfr.insert({ pg : 220, line : "mer divine, munie d'avirons, chargée d'une hécatombe, et faisons-y"});
db.iliadfr.insert({ pg : 221, line : "monter Khrysèis aux belles joues, sous la conduite d'un chef,"});
db.iliadfr.insert({ pg : 222, line : "Aias, Idoméneus, ou le divin Odysseus, ou toi-même, Pèléide, le"});
db.iliadfr.insert({ pg : 223, line : "plus effrayant des hommes, afin d'apaiser l'archer Apollôn par les"});
db.iliadfr.insert({ pg : 224, line : "sacrifices accomplis."});
db.iliadfr.insert({ pg : 226, line : "Et Akhilleus aux pieds rapides, le regardant d'un oeil sombre,"});
db.iliadfr.insert({ pg : 227, line : "parla ainsi;"});
db.iliadfr.insert({ pg : 229, line : "-- Ah! revêtu d'impudence, âpre au gain! Comment un seul d'entre"});
db.iliadfr.insert({ pg : 230, line : "les Akhaiens se hâterait-il de t'obéir, soit qu'il faille tendre"});
db.iliadfr.insert({ pg : 231, line : "une embuscade, soit qu'on doive combattre courageusement contre"});
db.iliadfr.insert({ pg : 232, line : "les hommes? Je ne suis point venu pour ma propre cause attaquer"});
db.iliadfr.insert({ pg : 233, line : "les Troiens armés de lances, car ils ne m'ont jamais nui. Jamais"});
db.iliadfr.insert({ pg : 234, line : "ils ne m'ont enlevé ni mes boeufs ni mes chevaux; jamais, dans la"});
db.iliadfr.insert({ pg : 235, line : "fructueuse Phthiè, ils n'ont ravagé mes moissons: car un grand"});
db.iliadfr.insert({ pg : 236, line : "nombre de montagnes ombragées et la mer sonnante nous séparent."});
db.iliadfr.insert({ pg : 237, line : "Mais nous t'avons suivi pour te plaire, impudent! pour venger"});
db.iliadfr.insert({ pg : 238, line : "Ménélaos et toi, oeil de chien! Et tu ne t'en soucies ni ne t'en"});
db.iliadfr.insert({ pg : 239, line : "souviens, et tu me menaces de m'enlever la récompense pour"});
db.iliadfr.insert({ pg : 240, line : "laquelle j'ai tant travaillé et que m'ont donnée les fils des"});
db.iliadfr.insert({ pg : 241, line : "Akhaiens! Certes, je n'ai jamais une part égale à la tienne quand"});
db.iliadfr.insert({ pg : 242, line : "on saccage une ville troienne bien peuplée; et cependant mes mains"});
db.iliadfr.insert({ pg : 243, line : "portent le plus lourd fardeau de la guerre impétueuse. Et, quand"});
db.iliadfr.insert({ pg : 244, line : "vient l'heure du partage, la meilleure part est pour toi; et,"});
db.iliadfr.insert({ pg : 245, line : "ployant sous la fatigue du combat, je retourne vers mes nefs,"});
db.iliadfr.insert({ pg : 246, line : "satisfait d'une récompense modique. Aujourd'hui, je pars pour la"});
db.iliadfr.insert({ pg : 247, line : "Phthiè, car mieux vaut regagner ma demeure sur mes nefs"});
db.iliadfr.insert({ pg : 248, line : "éperonnées. Et je ne pense point qu'après m'avoir outragé tu"});
db.iliadfr.insert({ pg : 249, line : "recueilles ici des dépouilles et des richesses."});
db.iliadfr.insert({ pg : 251, line : "Et le roi des hommes, Agamemnôn, lui répondit:"});
db.iliadfr.insert({ pg : 253, line : "-- Fuis, si ton coeur t'y pousse. Je ne te demande point de rester"});
db.iliadfr.insert({ pg : 254, line : "pour ma cause. Mille autres seront avec moi, surtout le très sage"});
db.iliadfr.insert({ pg : 255, line : "Zeus. Tu m'es le plus odieux des rois nourris par le Kronide. Tu"});
db.iliadfr.insert({ pg : 256, line : "ne te plais que dans la dissension, la guerre et le combat. Si tu"});
db.iliadfr.insert({ pg : 257, line : "es brave, c'est que les dieux l'ont voulu sans doute. Retourne"});
db.iliadfr.insert({ pg : 258, line : "dans ta demeure avec tes nefs et tes compagnons; commande aux"});
db.iliadfr.insert({ pg : 259, line : "Myrmidones; je n'ai nul souci de ta colère, mais je te préviens de"});
db.iliadfr.insert({ pg : 260, line : "ceci; puisque Phoibos Apollôn m'enlève Khrysèis, je la renverrai"});
db.iliadfr.insert({ pg : 261, line : "sur une de mes nefs avec mes compagnons, et moi-même j'irai sous"});
db.iliadfr.insert({ pg : 262, line : "ta tente et j'en entraînerai Breisèis aux belles joues, qui fut"});
db.iliadfr.insert({ pg : 263, line : "ton partage, afin que tu comprennes que je suis plus puissant que"});
db.iliadfr.insert({ pg : 264, line : "toi, et que chacun redoute de se dire mon égal en face."});
db.iliadfr.insert({ pg : 266, line : "Il parla ainsi, et le Pèléiôn fut ampli d'angoisse, et son coeur,"});
db.iliadfr.insert({ pg : 267, line : "dans sa mâle poitrine, délibéra si, prenant l'épée aiguë sur sa"});
db.iliadfr.insert({ pg : 268, line : "cuisse, il écarterait la foule et tuerait l'Atréide, ou s'il"});
db.iliadfr.insert({ pg : 269, line : "apaisent sa colère et refrénerait sa fureur."});
db.iliadfr.insert({ pg : 271, line : "Et tandis qu'il délibérait dans son âme et dans son esprit, et"});
db.iliadfr.insert({ pg : 272, line : "qu'il arrachait sa grande épée de la gaine, Athènè vint de"});
db.iliadfr.insert({ pg : 273, line : "l'Ouranos, car Hèrè aux bras blancs l'avait envoyée, aimant et"});
db.iliadfr.insert({ pg : 274, line : "protégeant les deux rois. Elle se tint en arrière et saisit le"});
db.iliadfr.insert({ pg : 275, line : "Pèléiôn par sa chevelure blonde; visible pour lui seul, car nul"});
db.iliadfr.insert({ pg : 276, line : "autre ne la voyait. Et Akhilleus, stupéfait, se retourna, et"});
db.iliadfr.insert({ pg : 277, line : "aussitôt il reconnut Athènè, dont les yeux étaient terribles, et"});
db.iliadfr.insert({ pg : 278, line : "il lui dit en paroles ailées:"});
db.iliadfr.insert({ pg : 280, line : "-- Pourquoi es-tu venue, fille de Zeus tempétueux? Est-ce afin de"});
db.iliadfr.insert({ pg : 281, line : "voir l'outrage qui m'est fait par l'Atréide Agamemnôn? Mais je te"});
db.iliadfr.insert({ pg : 282, line : "le dis, et ma parole s'accomplira, je pense: il va rendre lâme à"});
db.iliadfr.insert({ pg : 283, line : "cause de son insolence."});
db.iliadfr.insert({ pg : 285, line : "Et Athènè aux yeux clairs lui répondit:"});
db.iliadfr.insert({ pg : 287, line : "-- Je suis venue de l'Ouranos pour apaiser ta colère, si tu veux"});
db.iliadfr.insert({ pg : 288, line : "obéir. La divine Hèrè aux bras blancs m'a envoyée, vous aimant et"});
db.iliadfr.insert({ pg : 289, line : "vous protégeant tous deux. Donc, arrête; ne prends point l'épée en"});
db.iliadfr.insert({ pg : 290, line : "main, venge-toi en paroles, quoi qu'il arrive. Et je te le dis, et"});
db.iliadfr.insert({ pg : 291, line : "ceci s'accomplira: bientôt ton injure te sera payée par trois fois"});
db.iliadfr.insert({ pg : 292, line : "autant de présents splendides. Réprime-toi et obéis-nous."});
db.iliadfr.insert({ pg : 294, line : "Et Akhilleus aux pieds rapides, lui répondant, parla ainsi:"});
db.iliadfr.insert({ pg : 296, line : "-- Déesse, il faut observer ton ordre, bien que je sois irrité"});
db.iliadfr.insert({ pg : 297, line : "dans l'âme. Cela est pour le mieux sans doute, car les dieux"});
db.iliadfr.insert({ pg : 298, line : "exaucent qui leur obéit."});
db.iliadfr.insert({ pg : 300, line : "Il parla ainsi, et, frappant d'une main lourde la poignée"});
db.iliadfr.insert({ pg : 301, line : "d'argent, il repoussa sa grande épée dans la gaine et n'enfreignit"});
db.iliadfr.insert({ pg : 302, line : "point l'ordre d'Athènè."});
db.iliadfr.insert({ pg : 304, line : "Et celle-ci retourna auprès des autres dieux, dans les demeures"});
db.iliadfr.insert({ pg : 305, line : "olympiennes de Zeus tempétueux."});
db.iliadfr.insert({ pg : 307, line : "Et le Pèléide, débordant de colère, interpella l'Atréide avec"});
db.iliadfr.insert({ pg : 308, line : "d'âpres paroles:"});
db.iliadfr.insert({ pg : 310, line : "-- Lourd de vin, oeil de chien, coeur de cerf! jamais tu n'as osé,"});
db.iliadfr.insert({ pg : 311, line : "dans ton âme, t'armer pour le combat avec les hommes, ni tendre"});
db.iliadfr.insert({ pg : 312, line : "des embuscades avec les princes des Akhaiens. Cela t'épouvanterait"});
db.iliadfr.insert({ pg : 313, line : "comme la mort elle-même. Certes, il est beaucoup plus aisé, dans"});
db.iliadfr.insert({ pg : 314, line : "la vaste armée Akhaienne, d'enlever la part de celui qui te"});
db.iliadfr.insert({ pg : 315, line : "contredit, roi qui manges ton peuple, parce que tu commandes à des"});
db.iliadfr.insert({ pg : 316, line : "hommes vils. S'il n'en était pas ainsi, Atréide, cette insolence"});
db.iliadfr.insert({ pg : 317, line : "serait la dernière. Mais je te le dis, et j'en jure un grand"});
db.iliadfr.insert({ pg : 318, line : "serment: par ce sceptre qui ne produit ni feuilles, ni rameaux, et"});
db.iliadfr.insert({ pg : 319, line : "qui ne reverdira plus, depuis qu'il a été tranché du tronc sur les"});
db.iliadfr.insert({ pg : 320, line : "montagnes et que l'airain l'a dépouillé de feuilles et d'écorce;"});
db.iliadfr.insert({ pg : 321, line : "et par le sceptre que les fils des Akhaiens portent aux mains"});
db.iliadfr.insert({ pg : 322, line : "quand ils jugent et gardent les lois au nom de Zeus, je te le jure"});
db.iliadfr.insert({ pg : 323, line : "par un grand serment: certes, bientôt le regret d'Akhilleus"});
db.iliadfr.insert({ pg : 324, line : "envahira tous les fils des Akhaiens, et tu gémiras de ne pouvoir"});
db.iliadfr.insert({ pg : 325, line : "les défendre, quand ils tomberont en foule sous le tueur d'hommes"});
db.iliadfr.insert({ pg : 326, line : "Hektôr; et tu seras irrité et déchiré au fond de ton âme d'avoir"});
db.iliadfr.insert({ pg : 327, line : "outragé le plus brave des Akhaiens."});
db.iliadfr.insert({ pg : 329, line : "Ainsi parla le Pèlëide, et il jeta contre terre le sceptre aux"});
db.iliadfr.insert({ pg : 330, line : "clous d'or, et il s'assit. Et l'Atréide s'irritait aussi; mais"});
db.iliadfr.insert({ pg : 331, line : "l'excellent agorète des Pyliens, l'harmonieux Nestôr, se leva."});
db.iliadfr.insert({ pg : 333, line : "Et la parole coulait de sa langue, douce comme le miel. Et il"});
db.iliadfr.insert({ pg : 334, line : "avait déjà vécu deux âges d'hommes nés et nourris avec lui dans la"});
db.iliadfr.insert({ pg : 335, line : "divine Pylos, et il régnait sur le troisième âge. Très sage, il"});
db.iliadfr.insert({ pg : 336, line : "dit dans l'agora:"});
db.iliadfr.insert({ pg : 338, line : "-- Ô dieux! Certes. un grand deuil envahit la terre Akhaienne!"});
db.iliadfr.insert({ pg : 339, line : "Voici que Priamos se réjouira et que les fils de Priamos et tous"});
db.iliadfr.insert({ pg : 340, line : "les autres Troiens se réjouiront aussi dans leur coeur, quand ils"});
db.iliadfr.insert({ pg : 341, line : "apprendront vos querelles, à vous qui êtes au-dessus des Danaens"});
db.iliadfr.insert({ pg : 342, line : "dans l'agora et dans le combat. Mais laissez-vous persuader, car"});
db.iliadfr.insert({ pg : 343, line : "vous êtes tous deux moins âgés que moi. J'ai vécu autrefois avec"});
db.iliadfr.insert({ pg : 344, line : "des hommes plus braves que vous, et jamais ils ne m'ont cru"});
db.iliadfr.insert({ pg : 345, line : "moindre qu'eux. Non, jamais je n'ai vu et je ne reverrai des"});
db.iliadfr.insert({ pg : 346, line : "hommes tels que Peirithoos, et Dryas, prince des peuples, Kainéos,"});
db.iliadfr.insert({ pg : 347, line : "Exadios, Polyphèmos semblable à un dieu, et Thèseus Aigéide pareil"});
db.iliadfr.insert({ pg : 348, line : "aux immortels. Certes, ils étaient les plus braves des hommes"});
db.iliadfr.insert({ pg : 349, line : "nourris sur la terre, et ils combattaient contre les plus braves,"});
db.iliadfr.insert({ pg : 350, line : "les centaures des montagnes; et ils les tuèrent terriblement. Et"});
db.iliadfr.insert({ pg : 351, line : "j'étais avec eux, étant allé loin de Pylos et de la terre d'Apiè,"});
db.iliadfr.insert({ pg : 352, line : "et ils m'avaient appelé, et je combattais selon mes forces, car"});
db.iliadfr.insert({ pg : 353, line : "nul des hommes qui sont aujourd'hui sur la terre n'aurait pu leur"});
db.iliadfr.insert({ pg : 354, line : "résister. Mais ils écoutaient mes conseils et s'y conformaient."});
db.iliadfr.insert({ pg : 355, line : "Obéissez donc, car cela est pour le mieux. Il n'est point permis à"});
db.iliadfr.insert({ pg : 356, line : "Agamemnôn, bien que le plus puissant, d'enlever au Pèléide la"});
db.iliadfr.insert({ pg : 357, line : "vierge que lui ont donnée les fils des Akhaiens, mais tu ne dois"});
db.iliadfr.insert({ pg : 358, line : "point aussi, Pèléide, résister au roi, car tu n'es point l'égal de"});
db.iliadfr.insert({ pg : 359, line : "ce porte sceptre que Zeus a glorifié. Si tu es le plus brave, si"});
db.iliadfr.insert({ pg : 360, line : "une mère divine t'a enfanté, celui-ci est le plus puissant et"});
db.iliadfr.insert({ pg : 361, line : "commande à un plus grand nombre. Atréide, renonce à ta colère, et"});
db.iliadfr.insert({ pg : 362, line : "je supplie Akhilleus de réprimer la sienne, car il est le solide"});
db.iliadfr.insert({ pg : 363, line : "bouclier des Akhaiens dans la guerre mauvaise."});
db.iliadfr.insert({ pg : 365, line : "Et le roi Agamemnôn parla ainsi:"});
db.iliadfr.insert({ pg : 367, line : "-- Vieillard, tu as dit sagement et bien; mais cet homme veut être"});
db.iliadfr.insert({ pg : 368, line : "au-dessus de tous, commander à tous et dominer sur tous. Je ne"});
db.iliadfr.insert({ pg : 369, line : "pense point que personne y consente. Si les dieux qui vivent"});
db.iliadfr.insert({ pg : 370, line : "toujours l'ont fait brave, lui ont-ils permis d'insulter?"});
db.iliadfr.insert({ pg : 372, line : "Et le divin Akhilleus lui répondit:"});
db.iliadfr.insert({ pg : 374, line : "-- Certes, je mériterais d'être nommé lâche et vil si, à chacune"});
db.iliadfr.insert({ pg : 375, line : "de tes paroles, je te complaisais en toute chose. Commande aux"});
db.iliadfr.insert({ pg : 376, line : "autres, mais non à moi, car ne pense point que je t'obéisse jamais"});
db.iliadfr.insert({ pg : 377, line : "plus désormais. Je te dirai ceci; garde-le dans ton esprit: Je ne"});
db.iliadfr.insert({ pg : 378, line : "combattrai point contre aucun autre à cause de cette vierge,"});
db.iliadfr.insert({ pg : 379, line : "puisque vous m'enlevez ce que vous m'avez donné. Mais tu"});
db.iliadfr.insert({ pg : 380, line : "n'emporteras rien contre mon gré de toutes les autres choses qui"});
db.iliadfr.insert({ pg : 381, line : "sont dans ma nef noire et rapide. Tente-le, fais-toi ce danger, et"});
db.iliadfr.insert({ pg : 382, line : "que ceux-ci le voient, et aussitôt ton sang noir ruissellera"});
db.iliadfr.insert({ pg : 383, line : "autour de ma lance."});
db.iliadfr.insert({ pg : 385, line : "S'étant ainsi outragés de paroles, ils se levèrent et rompirent"});
db.iliadfr.insert({ pg : 386, line : "l'agora auprès des nefs des Akhaiens. Et le Pèléide se retira,"});
db.iliadfr.insert({ pg : 387, line : "avec le Ménoitiade et ses compagnons, vers ses tentes. Et"});
db.iliadfr.insert({ pg : 388, line : "l'Atréide lança à la mer une nef rapide, l'arma de vingt avirons,"});
db.iliadfr.insert({ pg : 389, line : "y mit une hécatombe pour le dieu et y conduisit lui-même Khrysèis"});
db.iliadfr.insert({ pg : 390, line : "aux belles joues. Et le chef fut le subtil Odysseus."});
db.iliadfr.insert({ pg : 392, line : "Et comme ils naviguaient sur les routes marines, l'Atréide ordonna"});
db.iliadfr.insert({ pg : 393, line : "aux peuples de se purifier. Et ils se purifiaient tous, et ils"});
db.iliadfr.insert({ pg : 394, line : "jetaient leurs souillures dans la mer, et ils sacrifiaient à"});
db.iliadfr.insert({ pg : 395, line : "Apollôn des hécatombes choisies de taureaux et de chèvres, le long"});
db.iliadfr.insert({ pg : 396, line : "du rivage de la mer inféconde. Et l'odeur en montait vers"});
db.iliadfr.insert({ pg : 397, line : "l'Ouranos, dans un tourbillon de fumée."});
db.iliadfr.insert({ pg : 399, line : "Et pendant qu'ils faisaient ainsi, Agamemnôn n'oubliait ni sa"});
db.iliadfr.insert({ pg : 400, line : "colère, ni la menace faite à Akhilleus. Et il interpella"});
db.iliadfr.insert({ pg : 401, line : "Talthybios et Eurybatès, qui étaient ses hérauts familiers."});
db.iliadfr.insert({ pg : 403, line : "-- Allez à la tente du Pèléide Akhilleus. Saisissez de la main"});
db.iliadfr.insert({ pg : 404, line : "Breisèis aux belles joues; et, s'il ne la donnait pas, j'irai la"});
db.iliadfr.insert({ pg : 405, line : "saisir moi-même avec un plus grand nombre, et ceci lui sera plus"});
db.iliadfr.insert({ pg : 406, line : "douloureux."});
db.iliadfr.insert({ pg : 408, line : "Et il les envoya avec ces âpres paroles. Et ils marchaient à"});
db.iliadfr.insert({ pg : 409, line : "regret le long du rivage de la mer inféconde, et ils parvinrent"});
db.iliadfr.insert({ pg : 410, line : "aux tentes et aux nefs des Myrmidones. Et ils trouvèrent le"});
db.iliadfr.insert({ pg : 411, line : "Pèléide assis auprès de sa tente et de sa nef noire, et Akhilleus"});
db.iliadfr.insert({ pg : 412, line : "ne fut point joyeux de les voir. Enrayés et pleins de respect, ils"});
db.iliadfr.insert({ pg : 413, line : "se tenaient devant le roi, et ils ne lui parlaient, ni ne"});
db.iliadfr.insert({ pg : 414, line : "l'interrogeaient. Et il les comprit dans son âme et dit:"});
db.iliadfr.insert({ pg : 416, line : "-- Salut, messagers de Zeus et des hommes! Approchez. Vous n'êtes"});
db.iliadfr.insert({ pg : 417, line : "point coupables envers moi, mais bien Agamemnôn, qui vous envoie"});
db.iliadfr.insert({ pg : 418, line : "pour la vierge Breisèis. Debout, divin Patroklos, amène-la, et"});
db.iliadfr.insert({ pg : 419, line : "qu'ils l'entraînent! Mais qu'ils soient témoins devant les dieux"});
db.iliadfr.insert({ pg : 420, line : "heureux, devant les hommes mortels et devant ce roi féroce, si"});
db.iliadfr.insert({ pg : 421, line : "jamais on a besoin de moi pour conjurer la destruction de tous;"});
db.iliadfr.insert({ pg : 422, line : "car, certes, il est plein de fureur dans ses pensées mauvaises, et"});
db.iliadfr.insert({ pg : 423, line : "il ne se souvient de rien, et il ne prévoit rien, de façon que les"});
db.iliadfr.insert({ pg : 424, line : "Akhaiens combattent saufs auprès des nefs."});
db.iliadfr.insert({ pg : 426, line : "Il parla ainsi, et Patroklos obéit à son compagnon bien-aimé. Il"});
db.iliadfr.insert({ pg : 427, line : "conduisit hors de la tente Breisèis aux belles joues, et il la"});
db.iliadfr.insert({ pg : 428, line : "livra pour être entraînée. Et les hérauts retournèrent aux nefs"});
db.iliadfr.insert({ pg : 429, line : "des anciens, et la jeune femme allait les suivant à contrecoeur."});
db.iliadfr.insert({ pg : 430, line : "Et Akhilleus, en pleurant, s'assit, loin des siens, sur le rivage"});
db.iliadfr.insert({ pg : 431, line : "blanc d'écume, et, regardant la haute mer toute noire, les mains"});
db.iliadfr.insert({ pg : 432, line : "étendues, il supplia sa mère bien-aimée:"});
db.iliadfr.insert({ pg : 434, line : "-- Mère! puisque tu m'as enfanté pour vivre peu de temps,"});
db.iliadfr.insert({ pg : 435, line : "l'Olympien Zeus qui tonne dans les nues devrait m'accorder au"});
db.iliadfr.insert({ pg : 436, line : "moins quelque honneur; mais il le fait maintenant moins que"});
db.iliadfr.insert({ pg : 437, line : "jamais. Et voici que l'Atréide Agamemnôn, qui commande au loin,"});
db.iliadfr.insert({ pg : 438, line : "m'a couvert d'opprobre, et qu'il possède ma récompense qu'il m'a"});
db.iliadfr.insert({ pg : 439, line : "enlevée."});
db.iliadfr.insert({ pg : 441, line : "Il parla ainsi, versant des larmes. Et sa mère vénérable"});
db.iliadfr.insert({ pg : 442, line : "l'entendit, assise au fond de l'abîme, auprès de son vieux père."});
db.iliadfr.insert({ pg : 443, line : "Et, aussitôt, elle émergea de la blanche mer, comme une nuée; et,"});
db.iliadfr.insert({ pg : 444, line : "s'asseyant devant son fils qui pleurait, elle le caressa de la"});
db.iliadfr.insert({ pg : 445, line : "main et lui parla:"});
db.iliadfr.insert({ pg : 447, line : "-- Mon enfant, pourquoi pleures-tu? Quelle amertume est entrée"});
db.iliadfr.insert({ pg : 448, line : "dans ton âme? Parle, ne cache rien afin que nous sachions tous"});
db.iliadfr.insert({ pg : 449, line : "deux."});
db.iliadfr.insert({ pg : 451, line : "Et Akhilleus aux pieds rapides parla avec un profond soupir:"});
db.iliadfr.insert({ pg : 453, line : "-- Tu le sais; pourquoi te dire ce que tu sais? Nous sommes allés"});
db.iliadfr.insert({ pg : 454, line : "contre Thèbè la sainte, ville d'Êétiôn, et nous l'avons saccagée,"});
db.iliadfr.insert({ pg : 455, line : "et nous en avons tout enlevé; et les fils des Akhaiens, s'étant"});
db.iliadfr.insert({ pg : 456, line : "partagé les dépouilles, donnèrent à l'Atréide Agamemnôn Khrysèis"});
db.iliadfr.insert({ pg : 457, line : "aux belles joues. Mais bientôt Khrysès, sacrificateur de l'archer"});
db.iliadfr.insert({ pg : 458, line : "Apollôn, vint aux nefs rapides des Akhaiens revêtus d'airain, pour"});
db.iliadfr.insert({ pg : 459, line : "racheter sa fille. Et il portait le prix infini de"});
db.iliadfr.insert({ pg : 460, line : "l'affranchissement, et, dans ses mains les bandelettes de l'archer"});
db.iliadfr.insert({ pg : 461, line : "Apollôn, suspendues au sceptre d'or. Et, suppliant, il pria tous"});
db.iliadfr.insert({ pg : 462, line : "les Akhaiens, et surtout les deux Atréides, princes des peuples."});
db.iliadfr.insert({ pg : 463, line : "Et tous les Akhaiens, par des rumeurs favorables, voulaient qu'on"});
db.iliadfr.insert({ pg : 464, line : "respectât le sacrificateur et qu'on reçût le prix splendide. Mais"});
db.iliadfr.insert({ pg : 465, line : "cela ne plut point à l'âme de l'Atréide Agamemnôn, et il le chassa"});
db.iliadfr.insert({ pg : 466, line : "outrageusement avec une parole violente. Et le vieillard irrité se"});
db.iliadfr.insert({ pg : 467, line : "retira. Mais Apollôn exauça son voeu, car il lui est très cher. Il"});
db.iliadfr.insert({ pg : 468, line : "envoya contre les Argiens une flèche mauvaise; et les peuples"});
db.iliadfr.insert({ pg : 469, line : "périssaient amoncelés; et les traits du dieu sifflaient au travers"});
db.iliadfr.insert({ pg : 470, line : "de la vaste armée Akhaienne. Un divinateur sage interprétait dans"});
db.iliadfr.insert({ pg : 471, line : "l'agora les volontés sacrées d'Apollôn. Aussitôt, le premier, je"});
db.iliadfr.insert({ pg : 472, line : "voulus qu'on apaisât le dieu. Mais la colère saisit l'Atréide, et,"});
db.iliadfr.insert({ pg : 473, line : "se levant soudainement, il prononça une menace qui s'est"});
db.iliadfr.insert({ pg : 474, line : "accomplie. Les Akhaiens aux sourcils arqués ont conduit la jeune"});
db.iliadfr.insert({ pg : 475, line : "vierge à Khrysè, sur une nef rapide, et portant des présents au"});
db.iliadfr.insert({ pg : 476, line : "dieu; mais deux hérauts viennent d'entraîner de ma tente la vierge"});
db.iliadfr.insert({ pg : 477, line : "Breisèis que les Akhaiens m'avaient donnée. Pour toi, si tu le"});
db.iliadfr.insert({ pg : 478, line : "veux, secours ton fils bien-aimé. Monte à l'Ouranos Olympien et"});
db.iliadfr.insert({ pg : 479, line : "supplie Zeus, si jamais tu as touché son coeur par tes paroles ou"});
db.iliadfr.insert({ pg : 480, line : "par tes actions. Souvent je t'ai entendue, dans les demeures"});
db.iliadfr.insert({ pg : 481, line : "paternelles, quand tu disais que, seule parmi les immortels, tu"});
db.iliadfr.insert({ pg : 482, line : "avais détourné un indigne traitement du Kroniôn qui amasse les"});
db.iliadfr.insert({ pg : 483, line : "nuées, alors que les autres Olympiens, Hèrè et Poseidaôn et Pallas"});
db.iliadfr.insert({ pg : 484, line : "Athènè le voulaient enchaîner. Et toi, déesse, tu accourus, et tu"});
db.iliadfr.insert({ pg : 485, line : "le délivras de ses liens, en appelant dans le vaste Olympes le"});
db.iliadfr.insert({ pg : 486, line : "géant aux cent mains que les dieux nomment Briaréôs, et les hommes"});
db.iliadfr.insert({ pg : 487, line : "Aigaiôs. Et celui-ci était beaucoup plus fort que son père, et il"});
db.iliadfr.insert({ pg : 488, line : "s'assit, orgueilleux de sa gloire, auprès du Kroniôn; et les dieux"});
db.iliadfr.insert({ pg : 489, line : "heureux en furent épouvanté, et n'enchaînèrent point Zeus."});
db.iliadfr.insert({ pg : 490, line : "Maintenant rappelle ceci en sa mémoire; presse ses genoux; et que,"});
db.iliadfr.insert({ pg : 491, line : "venant en aide aux Troiens, ceux-ci repoussent, avec un grand"});
db.iliadfr.insert({ pg : 492, line : "massacre, les Akhaiens contre la mer et dans leurs nefs. Que les"});
db.iliadfr.insert({ pg : 493, line : "Argiens jouissent de leur roi, et que l'Atréide Agamemnôn qui"});
db.iliadfr.insert({ pg : 494, line : "commande au loin souffre de sa faute, puisqu'il a outragé le plus"});
db.iliadfr.insert({ pg : 495, line : "brave des Akhaiens."});
db.iliadfr.insert({ pg : 497, line : "Et Thétis, répandant des larmes, lui répondit:"});
db.iliadfr.insert({ pg : 499, line : "-- Hélas! mon enfant, pourquoi t'ai-je enfanté et nourri pour une"});
db.iliadfr.insert({ pg : 500, line : "destinée mauvaise! Oh! que n'es-tu resté dans tes nefs, calme et"});
db.iliadfr.insert({ pg : 501, line : "sans larmes du moins, puisque tu ne dois vivre que peu de jours!"});
db.iliadfr.insert({ pg : 502, line : "Mais te voici très malheureux et devant mourir très vite, parce"});
db.iliadfr.insert({ pg : 503, line : "que je t'ai enfanté dans mes demeures pour une destinée mauvaise!"});
db.iliadfr.insert({ pg : 504, line : "Cependant, j'irai dans l'Olympos neigeux, et je parlerai à Zeus"});
db.iliadfr.insert({ pg : 505, line : "qui se réjouit de la foudre, et peut-être m'écoutera-t-il. Pour"});
db.iliadfr.insert({ pg : 506, line : "toi, assis dans tes nefs rapides, reste irrité contre les Akhaiens"});
db.iliadfr.insert({ pg : 507, line : "et abstiens-toi du combat. Zeus est allé hier du côté de"});
db.iliadfr.insert({ pg : 508, line : "l'Okéanos, à un festin que lui ont donné les Aithiopiens"});
db.iliadfr.insert({ pg : 509, line : "irréprochables, et tous les dieux l'ont suivi. Le douzième jour il"});
db.iliadfr.insert({ pg : 510, line : "reviendra dans l'Olympos. Alors j'irai dans la demeure d'airain de"});
db.iliadfr.insert({ pg : 511, line : "Zeus et je presserai ses genoux, et je pense qu'il en sera touché."});
db.iliadfr.insert({ pg : 513, line : "Ayant ainsi parlé, elle partit et laissa Akhilleus irrité dans son"});
db.iliadfr.insert({ pg : 514, line : "coeur au souvenir de la jeune femme à la belle ceinture qu'on lui"});
db.iliadfr.insert({ pg : 515, line : "avait enlevée par violence."});
db.iliadfr.insert({ pg : 517, line : "Et Odysseus, conduisant l'hécatombe sacrée, parvint à Krysè. Et"});
db.iliadfr.insert({ pg : 518, line : "les Akhaiens, étant entrés dans le port profond, plièrent les"});
db.iliadfr.insert({ pg : 519, line : "voiles qui furent déposées dans la nef noire. Ils abattirent"});
db.iliadfr.insert({ pg : 520, line : "joyeusement sur l'avant le mât dégagé de ses manoeuvres; et,"});
db.iliadfr.insert({ pg : 521, line : "menant la nef à force d'avirons, après avoir amarré les câbles et"});
db.iliadfr.insert({ pg : 522, line : "mouillé les roches, ils descendirent sur le rivage de la mer, avec"});
db.iliadfr.insert({ pg : 523, line : "l'hécatombe promise à l'archer Apollôn. Khrysèis sortit aussitôt"});
db.iliadfr.insert({ pg : 524, line : "de la nef, et le subtil Odysseus, la conduisant vers l'autel, la"});
db.iliadfr.insert({ pg : 525, line : "remit aux mains de son père bien-aimé, et dit:"});
db.iliadfr.insert({ pg : 527, line : "-- Ô Khrysès! le roi des hommes, Agamemnôn, m'a envoyé pour te"});
db.iliadfr.insert({ pg : 528, line : "rendre ta fille et pour sacrifier une hécatombe sacrée à Phoibos"});
db.iliadfr.insert({ pg : 529, line : "en faveur des Danaens, afin que nous apaisions le dieu qui accable"});
db.iliadfr.insert({ pg : 530, line : "les Argiens de calamités déplorables."});
db.iliadfr.insert({ pg : 532, line : "Ayant ainsi parlé, il lui remit aux mains sa fille bien-aimée, et"});
db.iliadfr.insert({ pg : 533, line : "le vieillard la reçut plein de joie. Aussitôt les Akhaiens"});
db.iliadfr.insert({ pg : 534, line : "rangèrent la riche hécatombe dans l'ordre consacré, autour de"});
db.iliadfr.insert({ pg : 535, line : "l'autel bâti selon le rite. Et ils se lavèrent les mains, et ils"});
db.iliadfr.insert({ pg : 536, line : "préparèrent les orges salées; et Khrysès, à haute voix, les bras"});
db.iliadfr.insert({ pg : 537, line : "levés, priait pour eux:"});
db.iliadfr.insert({ pg : 539, line : "-- Entends-moi, porteur de l'arc d'argent, qui protèges Khrysè et"});
db.iliadfr.insert({ pg : 540, line : "la divine Killa, et commandes fortement sur Ténédos. Déjà tu as"});
db.iliadfr.insert({ pg : 541, line : "exaucé ma prière; tu m'as honoré et tu as couvert d'affliction les"});
db.iliadfr.insert({ pg : 542, line : "peuples des Akhaiens. Maintenant écoute mon voeu, et détourne loin"});
db.iliadfr.insert({ pg : 543, line : "d'eux la contagion."});
db.iliadfr.insert({ pg : 545, line : "Il parla ainsi en priant, et Phoibos Apollôn l'exauça. Et, après"});
db.iliadfr.insert({ pg : 546, line : "avoir prié et répandu les orges salées, renversant en arrière le"});
db.iliadfr.insert({ pg : 547, line : "cou des victimes, ils les égorgèrent et les écorchèrent. On coupa"});
db.iliadfr.insert({ pg : 548, line : "les cuisses, on les couvrit de graisse des deux côtés, et on posa"});
db.iliadfr.insert({ pg : 549, line : "sur elles les entrailles crues."});
db.iliadfr.insert({ pg : 551, line : "Et le vieillard les brûlait sur du bois sec et les arrosait d'une"});
db.iliadfr.insert({ pg : 552, line : "libation de vin rouge. Les jeunes hommes, auprès de lui, tenaient"});
db.iliadfr.insert({ pg : 553, line : "en mains des broches à cinq pointes. Et, les cuisses étant"});
db.iliadfr.insert({ pg : 554, line : "consumées, ils goûtèrent les entrailles; et, séparant le reste en"});
db.iliadfr.insert({ pg : 555, line : "plusieurs morceaux, ils les trans-fixèrent de leurs broches et les"});
db.iliadfr.insert({ pg : 556, line : "tirent cuire avec soin, et le tout fut retiré du feu. Après avoir"});
db.iliadfr.insert({ pg : 557, line : "achevé ce travail, ils préparèrent le repas; et tous furent"});
db.iliadfr.insert({ pg : 558, line : "conviés, et nul ne se plaignit, dans son âme, de l'inégalité des"});
db.iliadfr.insert({ pg : 559, line : "parts."});
db.iliadfr.insert({ pg : 561, line : "Ayant assouvi la faim et la soif, les jeunes hommes couronnèrent"});
db.iliadfr.insert({ pg : 562, line : "de vin les patères et les répartirent entre tous à pleines coupes."});
db.iliadfr.insert({ pg : 563, line : "Et, durant tout le jour, les jeunes Akhaiens apaisèrent le dieu"});
db.iliadfr.insert({ pg : 564, line : "par leurs hymnes, chantant le joyeux paian et célébrant l'archer"});
db.iliadfr.insert({ pg : 565, line : "Apollôn qui se réjouissait dans son coeur de les entendre."});
db.iliadfr.insert({ pg : 567, line : "Quand Hélios tomba et que les ombres furent venues, ils se"});
db.iliadfr.insert({ pg : 568, line : "couchèrent auprès des câbles, à la proue de leur nef; et quand"});
db.iliadfr.insert({ pg : 569, line : "Éôs, aux doigts rosés, née au matin, apparut, ils s'en"});
db.iliadfr.insert({ pg : 570, line : "retournèrent vers la vaste armée des Akhaiens, et l'archer Apollôn"});
db.iliadfr.insert({ pg : 571, line : "leur envoya un vent propice. Et ils dressèrent le mât, et ils"});
db.iliadfr.insert({ pg : 572, line : "déployèrent les voiles blanches; et le vent les gonfla par le"});
db.iliadfr.insert({ pg : 573, line : "milieu; et l'onde pourprée sonnait avec bruit autour de la carène"});
db.iliadfr.insert({ pg : 574, line : "de la nef qui courait sur l'eau en faisant sa route."});
db.iliadfr.insert({ pg : 576, line : "Puis, étant parvenus à la vaste armée des Akhaiens, ils tirèrent"});
db.iliadfr.insert({ pg : 577, line : "la nef noire au plus haut des sables de la plage; et, l'ayant"});
db.iliadfr.insert({ pg : 578, line : "assujettie sur de longs rouleaux, ils se dispersèrent parmi les"});
db.iliadfr.insert({ pg : 579, line : "tentes et les nefs."});
db.iliadfr.insert({ pg : 581, line : "Mais le divin fils de Pèleus, Akhilleus aux pieds rapides, assis"});
db.iliadfr.insert({ pg : 582, line : "auprès de ses nefs légères, couvait son ressentiment; et il ne se"});
db.iliadfr.insert({ pg : 583, line : "montrait plus ni dans l'agora qui illustre les hommes, ni dans le"});
db.iliadfr.insert({ pg : 584, line : "combat. Et il restait là, se dévorant le coeur et regrettant le"});
db.iliadfr.insert({ pg : 585, line : "cri de guerre et la mêlée."});
db.iliadfr.insert({ pg : 587, line : "Quand Éôs, reparut pour la douzième fois, les dieux qui vivent"});
db.iliadfr.insert({ pg : 588, line : "toujours revinrent ensemble dans l'Olympos, et Zeus marchait en"});
db.iliadfr.insert({ pg : 589, line : "tête. Et Thétis n'oublia point les prières de son fils; et,"});
db.iliadfr.insert({ pg : 590, line : "émergeant de l'écume de la mer, elle monta, matinale, à travers le"});
db.iliadfr.insert({ pg : 591, line : "vaste Ouranos, jusqu'à l'Olympos, où elle trouva celui qui voit"});
db.iliadfr.insert({ pg : 592, line : "tout, le Kronide, assis loin des autres dieux, sur le plus haut"});
db.iliadfr.insert({ pg : 593, line : "faîte de l'Olympos aux cimes nombreuses. Elle s'assit devant lui,"});
db.iliadfr.insert({ pg : 594, line : "embrassa ses genoux de la main gauche, lui toucha le menton de la"});
db.iliadfr.insert({ pg : 595, line : "main droite, et le suppliant, elle dit au roi Zeus Kroniôn:"});
db.iliadfr.insert({ pg : 597, line : "-- Père Zeus! si jamais, entre les immortels, je t'ai servi, soit"});
db.iliadfr.insert({ pg : 598, line : "par mes paroles, soit par mes actions, exauce ma prière. Honore"});
db.iliadfr.insert({ pg : 599, line : "mon fils qui, de tous les vivants, est le plus proche de la mort."});
db.iliadfr.insert({ pg : 600, line : "Voici que le roi des hommes, Agamemnôn, la outragé, et qu'il"});
db.iliadfr.insert({ pg : 601, line : "possède sa récompense qu'il lui a enlevée. Mais toi, du moins,"});
db.iliadfr.insert({ pg : 602, line : "honore-le, Olympien, très sage Zeus, et donne le dessus aux"});
db.iliadfr.insert({ pg : 603, line : "Troyens jusqu'à ce que les Akhaiens aient honoré mon fils et lui"});
db.iliadfr.insert({ pg : 604, line : "aient rendu hommage."});
db.iliadfr.insert({ pg : 606, line : "Elle parla ainsi, et Zeus, qui amasse les nuées, ne répondit pas"});
db.iliadfr.insert({ pg : 607, line : "et resta longtemps muet. Et Thétis, ayant saisi ses genoux qu'elle"});
db.iliadfr.insert({ pg : 608, line : "tenait embrassés, dit une seconde fois:"});
db.iliadfr.insert({ pg : 610, line : "-- Consens et promets avec sincérité, ou refuse-moi, car tu ne"});
db.iliadfr.insert({ pg : 611, line : "peux craindre rien. Que je sache si je suis la plus méprisée des"});
db.iliadfr.insert({ pg : 612, line : "déesses!"});
db.iliadfr.insert({ pg : 614, line : "Et Zeus qui amasse les nuées, avec un profond soupir, lui dit:"});
db.iliadfr.insert({ pg : 616, line : "-- Certes, ceci va causer de grands malheurs, quand tu m'auras mis"});
db.iliadfr.insert({ pg : 617, line : "en lutte avec Hèrè, et quand elle m'aura irrité par des paroles"});
db.iliadfr.insert({ pg : 618, line : "outrageantes. Elle ne cesse, en effet, parmi les dieux immortels,"});
db.iliadfr.insert({ pg : 619, line : "de me reprocher de soutenir les Troiens dans le combat."});
db.iliadfr.insert({ pg : 620, line : "Maintenant, retire-toi en hâte, de peur que Hèrè t'aperçoive. Je"});
db.iliadfr.insert({ pg : 621, line : "songerai à faire ce que tu demandes, et je t'en donne pour gage le"});
db.iliadfr.insert({ pg : 622, line : "signe de ma tête, afin que tu sois convaincue. Et c'est le plus"});
db.iliadfr.insert({ pg : 623, line : "grand de mes signes pour les immortels. Et je ne puis ni révoquer,"});
db.iliadfr.insert({ pg : 624, line : "ni renier, ni négliger ce que j'ai promis par un signe de ma tête."});
db.iliadfr.insert({ pg : 626, line : "Et le Kroniôn, ayant parlé, fronça ses sourcils bleus. Et la"});
db.iliadfr.insert({ pg : 627, line : "chevelure ambroisienne s'agita sur la tête immortelle du roi, et"});
db.iliadfr.insert({ pg : 628, line : "le vaste Olympos en fut ébranlé."});
db.iliadfr.insert({ pg : 630, line : "Tous deux s'étant ainsi parlé, se séparèrent. Et Thétis sauta dans"});
db.iliadfr.insert({ pg : 631, line : "la mer profonde du haut de l'Olympos éblouissant, et Zeus rentra"});
db.iliadfr.insert({ pg : 632, line : "dans sa demeure. Et tous les dieux se levèrent de leurs sièges à"});
db.iliadfr.insert({ pg : 633, line : "l'aspect de leur père, et nul n'osa l'attendre, et tous"});
db.iliadfr.insert({ pg : 634, line : "s'empressèrent au-devant de lui, et il s'assit sur son thrône."});
db.iliadfr.insert({ pg : 635, line : "Mais Hèrè n'avait pas été trompée, l'ayant vu se concerter avec la"});
db.iliadfr.insert({ pg : 636, line : "fille du vieillard de la mer, Thétis aux pieds d'argent. Et elle"});
db.iliadfr.insert({ pg : 637, line : "adressa d'amers reproches à Zeus Kroniôn:"});
db.iliadfr.insert({ pg : 639, line : "-- Qui d'entre les dieux, ô plein de ruses, s'est encore concerté"});
db.iliadfr.insert({ pg : 640, line : "avec toi? Il te plaît sans cesse de prendre, loin de moi, de"});
db.iliadfr.insert({ pg : 641, line : "secrètes résolutions, et jamais tu ne me dis ce que tu médites."});
db.iliadfr.insert({ pg : 643, line : "Et le père des dieux et des hommes lui répondit:"});
db.iliadfr.insert({ pg : 645, line : "-- Hèrè, n'espère point connaître toutes mes pensées. Elles te"});
db.iliadfr.insert({ pg : 646, line : "seraient terribles, bien que tu sois mon épouse. Celle qu'il"});
db.iliadfr.insert({ pg : 647, line : "convient que tu saches, aucun des dieux et des hommes ne la"});
db.iliadfr.insert({ pg : 648, line : "connaîtra avant toi; mais pour celle que je médite loin des dieux,"});
db.iliadfr.insert({ pg : 649, line : "ne la recherche ni ne l'examine."});
db.iliadfr.insert({ pg : 651, line : "Et la vénérable Hèrè aux yeux de boeuf lui répondit:"});
db.iliadfr.insert({ pg : 653, line : "-- Terrible Kronide, quelle parole as-tu dite? Certes, je ne t'ai"});
db.iliadfr.insert({ pg : 654, line : "jamais interrogé et n'ai point recherché tes pensées, et tu"});
db.iliadfr.insert({ pg : 655, line : "médites ce qu'il te plaît dans ton esprit. Mais je tremble que la"});
db.iliadfr.insert({ pg : 656, line : "fille du vieillard de la mer, Thétis aux pieds d'argent, ne t'ait"});
db.iliadfr.insert({ pg : 657, line : "séduit; car, dès le matin, elle s'est assise auprès de toi et elle"});
db.iliadfr.insert({ pg : 658, line : "a saisi tes genoux. Tu lui as promis, je pense, que tu honorerais"});
db.iliadfr.insert({ pg : 659, line : "Akhilleus et que tu ferais tomber un grand nombre d'hommes auprès"});
db.iliadfr.insert({ pg : 660, line : "des nefs des Akhaiens."});
db.iliadfr.insert({ pg : 662, line : "Et Zeus qui amasse les nuées lui répondit, et il dit:"});
db.iliadfr.insert({ pg : 664, line : "-- Insensée! tu me soupçonnes sans cesse et je ne puis me cacher"});
db.iliadfr.insert({ pg : 665, line : "de toi. Mais, dans ton impuissance, tu ne feras que t'éloigner de"});
db.iliadfr.insert({ pg : 666, line : "mon coeur, et ta peine en sera plus terrible. Si tes soupçons sont"});
db.iliadfr.insert({ pg : 667, line : "vrais, sache qu'il me plaît d'agir ainsi. Donc, tais-toi et obéis"});
db.iliadfr.insert({ pg : 668, line : "à mes paroles. Prends garde que tous les dieux Olympiens ne"});
db.iliadfr.insert({ pg : 669, line : "puissent te défendre, si j'étends sur toi mes mains sacrées."});
db.iliadfr.insert({ pg : 671, line : "Il parla ainsi, et la vénérable Hèra aux yeux de boeuf fut saisie"});
db.iliadfr.insert({ pg : 672, line : "de crainte, et elle demeura muette, domptant son coeur altier. Et,"});
db.iliadfr.insert({ pg : 673, line : "dans la demeure de Zeus, les dieux ouraniens gémirent."});
db.iliadfr.insert({ pg : 675, line : "Et l'illustre ouvrier Hèphaistos commença de parler, pour consoler"});
db.iliadfr.insert({ pg : 676, line : "sa mère bien-aimée, Hèrè aux bras blancs:"});
db.iliadfr.insert({ pg : 678, line : "-- Certes, nos maux seront funestes et intolérables, si vous vous"});
db.iliadfr.insert({ pg : 679, line : "querellez ainsi pour des mortels, et si vous mettez le tumulte"});
db.iliadfr.insert({ pg : 680, line : "parmi les dieux. Nos festins brillants perdront leur joie, si le"});
db.iliadfr.insert({ pg : 681, line : "mal l'emporte. Je conseille à ma mère, bien qu'elle soit déjà"});
db.iliadfr.insert({ pg : 682, line : "persuadée de ceci, de calmer Zeus, mon père bien-aimé, afin qu'il"});
db.iliadfr.insert({ pg : 683, line : "ne s'irrite point de nouveau et qu'il ne trouble plus nos festins."});
db.iliadfr.insert({ pg : 684, line : "Certes, si l'Olympien qui darde les éclairs le veut, il peut nous"});
db.iliadfr.insert({ pg : 685, line : "précipiter de nos trônes, car il est le plus puissant. Tente donc"});
db.iliadfr.insert({ pg : 686, line : "de le fléchir par de douces paroles, et aussitôt l'Olympien nous"});
db.iliadfr.insert({ pg : 687, line : "sera bienveillant."});
db.iliadfr.insert({ pg : 689, line : "Il parla ainsi, et, s'étant élancé, il remit une coupe profonde"});
db.iliadfr.insert({ pg : 690, line : "aux mains de sa mère bien-aimée et lui dit:"});
db.iliadfr.insert({ pg : 692, line : "-- Sois patiente, ma mère, et, bien qu'affligée, supporte ta"});
db.iliadfr.insert({ pg : 693, line : "disgrâce, de peur que je te voie maltraitée, toi qui m'es chère,"});
db.iliadfr.insert({ pg : 694, line : "et que, malgré ma douleur, je ne puisse te secourir, car"});
db.iliadfr.insert({ pg : 695, line : "l'Olympien est un terrible adversaire. Déjà, une fois, comme je"});
db.iliadfr.insert({ pg : 696, line : "voulais te défendre, il me saisit par un pied et me rejeta du haut"});
db.iliadfr.insert({ pg : 697, line : "des demeures divines. Tout un jour je roulai, et, avec Hélios, qui"});
db.iliadfr.insert({ pg : 698, line : "se couchait, je tombai dans Lèmnos, presque sans vie. Là les"});
db.iliadfr.insert({ pg : 699, line : "hommes Sintiens me reçurent dans ma chute."});
db.iliadfr.insert({ pg : 701, line : "Il parla ainsi, et la divine Hèrè aux bras blancs sourit, et elle"});
db.iliadfr.insert({ pg : 702, line : "reçut la coupe de son fils. Et il versait, par la droite, à tous"});
db.iliadfr.insert({ pg : 703, line : "les autres dieux, puisant le doux nektar dans le kratère. Et un"});
db.iliadfr.insert({ pg : 704, line : "rire inextinguible s'éleva parmi les dieux heureux, quand ils"});
db.iliadfr.insert({ pg : 705, line : "virent Hèphaistos s'agiter dans la demeure."});
db.iliadfr.insert({ pg : 707, line : "Et ils se livraient ainsi au festin, tout le jour, jusqu'au"});
db.iliadfr.insert({ pg : 708, line : "coucher de Hélios. Et nul d'entre eux ne fut privé d'une égale"});
db.iliadfr.insert({ pg : 709, line : "part du repas, ni des sons de la lyre magnifique que tenait"});
db.iliadfr.insert({ pg : 710, line : "Apollôn, tandis que les Muses chantaient tour à tour d'une belle"});
db.iliadfr.insert({ pg : 711, line : "voix. Mais après que la brillante lumière Hélienne se fut couchée,"});
db.iliadfr.insert({ pg : 712, line : "eux aussi se retirèrent, chacun dans la demeure que l'illustre"});
db.iliadfr.insert({ pg : 713, line : "Hèphaistos boiteux des deux pieds avait construite habilement. Et"});
db.iliadfr.insert({ pg : 714, line : "l'Olympien Zeus, qui darde les éclairs, se rendit vers sa couche,"});
db.iliadfr.insert({ pg : 715, line : "là où il reposait quand le doux sommeil le saisissait. Et il s'y"});
db.iliadfr.insert({ pg : 716, line : "endormit, et, auprès de lui, Hèrè au trône d'or."});
db.iliadfr.insert({ pg : 719, line : "Chant 2"});
db.iliadfr.insert({ pg : 721, line : "Les dieux et les cavaliers armés de casques dormaient tous dans la"});
db.iliadfr.insert({ pg : 722, line : "nuit; mais le profond sommeil ne saisissait point Zeus, et il"});
db.iliadfr.insert({ pg : 723, line : "cherchait dans son esprit comment il honorerait Akhilleus et"});
db.iliadfr.insert({ pg : 724, line : "tuerait une foule d'hommes auprès des nefs des Akhaiens. Et ce"});
db.iliadfr.insert({ pg : 725, line : "dessein lui parut le meilleur, dans son esprit, d'envoyer un songe"});
db.iliadfr.insert({ pg : 726, line : "menteur à l'Atréide Agamemnôn. Et, l'ayant appelé, il lui dit ces"});
db.iliadfr.insert({ pg : 727, line : "paroles ailées:"});
db.iliadfr.insert({ pg : 729, line : "-- Va, songe menteur, vers les nefs rapides des Akhaiens. Entre"});
db.iliadfr.insert({ pg : 730, line : "dans la tente de l'Atréide Agamemnôn et porte-lui très fidèlement"});
db.iliadfr.insert({ pg : 731, line : "mon ordre. Qu'il arme la foule des Akhaiens chevelus, car voici"});
db.iliadfr.insert({ pg : 732, line : "qu'il va s'emparer de la ville aux larges rues des Troiens. Les"});
db.iliadfr.insert({ pg : 733, line : "immortels qui habitent les demeures Olympiennes ne sont plus"});
db.iliadfr.insert({ pg : 734, line : "divisés, car Hèrè les a tous fléchis par ses supplications, et les"});
db.iliadfr.insert({ pg : 735, line : "calamités sont suspendues sur les Troiens."});
db.iliadfr.insert({ pg : 737, line : "Il parla ainsi, et, l'ayant entendu, le songe partit. Et il"});
db.iliadfr.insert({ pg : 738, line : "parvint aussitôt aux nefs rapides des Akhaiens, et il s'approcha"});
db.iliadfr.insert({ pg : 739, line : "de l'Atréide Agamemnôn qui dormait sous sa tente et qu'un sommeil"});
db.iliadfr.insert({ pg : 740, line : "ambroisien enveloppait. Et il se tint auprès de la tête du roi. Et"});
db.iliadfr.insert({ pg : 741, line : "il était semblable au Nèlèiôn Nestôr, qui, de tous les vieillards,"});
db.iliadfr.insert({ pg : 742, line : "était le plus honoré d'Agamemnôn. Et, sous cette forme, le songe"});
db.iliadfr.insert({ pg : 743, line : "divin parla ainsi:"});
db.iliadfr.insert({ pg : 745, line : "-- Tu dors, fils du brave Atreus dompteur de chevaux? Il ne faut"});
db.iliadfr.insert({ pg : 746, line : "pas qu'un homme sage à qui les peuples ont été confiés, et qui a"});
db.iliadfr.insert({ pg : 747, line : "tant de soucis dans l'esprit, dorme toute la nuit. Et maintenant,"});
db.iliadfr.insert({ pg : 748, line : "écoute-moi sans tarder, car je te suis envoyé par Zeus qui, de"});
db.iliadfr.insert({ pg : 749, line : "loin, s'inquiète de toi et te prend en pitié. Il t'ordonne d'armer"});
db.iliadfr.insert({ pg : 750, line : "la foule des Akhaiens chevelus, car voici que tu vas t'emparer de"});
db.iliadfr.insert({ pg : 751, line : "la ville aux larges rues des Troiens. Les immortels qui habitent"});
db.iliadfr.insert({ pg : 752, line : "les demeures Olympiennes ne sont plus divisés, car Hèrè les a tous"});
db.iliadfr.insert({ pg : 753, line : "fléchis par ses supplications, et les calamités sont suspendues"});
db.iliadfr.insert({ pg : 754, line : "sur les Troiens. Garde ces paroles dans ton esprit et n'oublie"});
db.iliadfr.insert({ pg : 755, line : "rien quand le doux sommeil t'aura quitté."});
db.iliadfr.insert({ pg : 757, line : "Ayant ainsi parlé, il disparut et le laissa rouler dans son esprit"});
db.iliadfr.insert({ pg : 758, line : "ces paroles qui ne devaient point s'accomplir. Et l'insensé crut"});
db.iliadfr.insert({ pg : 759, line : "qu'il allait s'emparer, ce jour-là, de la ville de Priamos, ne"});
db.iliadfr.insert({ pg : 760, line : "sachant point ce que Zeus méditait. Et le Kronide se préparait à"});
db.iliadfr.insert({ pg : 761, line : "répandre encore, en de terribles batailles, les douleurs et les"});
db.iliadfr.insert({ pg : 762, line : "gémissements sur les Troiens et sur les Danaens."});
db.iliadfr.insert({ pg : 764, line : "Et l'Atréide s'éveilla, et la voix divine résonnait autour de lui."});
db.iliadfr.insert({ pg : 765, line : "Il se leva et revêtit sa tunique moelleuse, belle et neuve. Et il"});
db.iliadfr.insert({ pg : 766, line : "se couvrit d'un large manteau et noua à ses pieds robustes de"});
db.iliadfr.insert({ pg : 767, line : "belles sandales, et il suspendit à ses épaules l'épée aux clous"});
db.iliadfr.insert({ pg : 768, line : "d'argent. Enfin, il prit le sceptre immortel de ses pères et"});
db.iliadfr.insert({ pg : 769, line : "marcha ainsi vers les nefs des Akhaiens revêtus d'airain."});
db.iliadfr.insert({ pg : 771, line : "Et la divine Éôs gravit le haut Olympos, annonçant la lumière à"});
db.iliadfr.insert({ pg : 772, line : "Zeus et aux immortels. Et l'Atréide ordonna aux hérauts à la voix"});
db.iliadfr.insert({ pg : 773, line : "sonore de convoquer à l'agora les Akhaiens chevelus. Et ils les"});
db.iliadfr.insert({ pg : 774, line : "convoquèrent, et tous accoururent en foule; et l'Atréide réunit un"});
db.iliadfr.insert({ pg : 775, line : "conseil de chefs magnanimes, auprès de la nef de Nestôr, roi de"});
db.iliadfr.insert({ pg : 776, line : "Pylos. Et, les ayant réunis, il consulta leur sagesse:"});
db.iliadfr.insert({ pg : 778, line : "-- Amis, entendez-moi. Un songe divin m'a été envoyé dans mon"});
db.iliadfr.insert({ pg : 779, line : "sommeil, au milieu de la nuit ambroisienne. Et il était semblable"});
db.iliadfr.insert({ pg : 780, line : "au divin Nestôr par le visage et la stature, et il s'est arrêté"});
db.iliadfr.insert({ pg : 781, line : "au-dessus de ma tête, et il m'a parlé ainsi:"});
db.iliadfr.insert({ pg : 783, line : "-- Tu dors, fils du brave Atreus dompteur de chevaux? Il ne faut"});
db.iliadfr.insert({ pg : 784, line : "point qu'un homme sage à qui les peuples ont été confiés, et qui a"});
db.iliadfr.insert({ pg : 785, line : "tant de soucis dans l'esprit, dorme toute la nuit. Et maintenant,"});
db.iliadfr.insert({ pg : 786, line : "écoute-moi sans tarder, car je te suis envoyé par Zeus qui, de"});
db.iliadfr.insert({ pg : 787, line : "loin, s'inquiète de toi et te prend en pitié. Il t'ordonne d'armer"});
db.iliadfr.insert({ pg : 788, line : "la foule des Akhaiens chevelus, car voici que tu vas t'emparer de"});
db.iliadfr.insert({ pg : 789, line : "la ville aux larges rues des Troiens. Les immortels qui habitent"});
db.iliadfr.insert({ pg : 790, line : "les demeures Olympiennes ne sont plus divisés, car Hèrè les a tous"});
db.iliadfr.insert({ pg : 791, line : "fléchis par ses supplications, et les calamités sont suspendues"});
db.iliadfr.insert({ pg : 792, line : "sur les Troiens. Garde ces paroles dans ton esprit."});
db.iliadfr.insert({ pg : 794, line : "En parlant ainsi il s'envola, et le doux sommeil me quitta."});
db.iliadfr.insert({ pg : 795, line : "Maintenant, songeons à armer les fils des Akhaiens. D'abord, je"});
db.iliadfr.insert({ pg : 796, line : "les tenterai par mes paroles, comme il est permis, et je les"});
db.iliadfr.insert({ pg : 797, line : "pousserai à fuir sur leurs nefs chargées de rameurs. Vous, par vos"});
db.iliadfr.insert({ pg : 798, line : "paroles, forcez-les de rester."});
db.iliadfr.insert({ pg : 800, line : "Ayant ainsi parlé, il s'assit. Et Nestôr se leva, et il était roi"});
db.iliadfr.insert({ pg : 801, line : "de la sablonneuse Pylos, et, les haranguant avec sagesse, il leur"});
db.iliadfr.insert({ pg : 802, line : "dit:"});
db.iliadfr.insert({ pg : 804, line : "-- Ô amis! rois et princes des Argiens, si quelqu'autre des"});
db.iliadfr.insert({ pg : 805, line : "Akhaiens nous eût dit ce songe, nous aurions pu croire qu'il"});
db.iliadfr.insert({ pg : 806, line : "mentait, et nous l'aurions repoussé; mais celui qui l'a entendu se"});
db.iliadfr.insert({ pg : 807, line : "glorifie d'être le plus puissant dans l'armée. Songeons donc à"});
db.iliadfr.insert({ pg : 808, line : "armer les fils des Akhaiens."});
db.iliadfr.insert({ pg : 810, line : "Ayant ainsi parlé, il sortit le premier de l'agora. Et les autres"});
db.iliadfr.insert({ pg : 811, line : "rois porte sceptres se levèrent et obéirent au prince des peuples."});
db.iliadfr.insert({ pg : 812, line : "Et les peuples accouraient. Ainsi des essaims d'abeilles"});
db.iliadfr.insert({ pg : 813, line : "innombrables sortent toujours et sans cesse d'une roche creuse et"});
db.iliadfr.insert({ pg : 814, line : "volent par légions sur les fleurs du printemps, et les unes"});
db.iliadfr.insert({ pg : 815, line : "tourbillonnent d'un côté, et les autres de l'autre. Ainsi la"});
db.iliadfr.insert({ pg : 816, line : "multitude des peuples, hors des nefs et des tentes, s'avançait"});
db.iliadfr.insert({ pg : 817, line : "vers l'agora, sur le rivage immense. Et, au milieu d'eux, Ossa,"});
db.iliadfr.insert({ pg : 818, line : "messagère de Zeus, excitait et hâtait leur course, et ils se"});
db.iliadfr.insert({ pg : 819, line : "réunissaient."});
db.iliadfr.insert({ pg : 821, line : "Et l'agora était pleine de tumulte, et la terre gémissait sous le"});
db.iliadfr.insert({ pg : 822, line : "poids des peuples. Et, comme les clameurs redoublaient, les"});
db.iliadfr.insert({ pg : 823, line : "hérauts à la voix sonore les contraignaient de se taire et"});
db.iliadfr.insert({ pg : 824, line : "d'écouter les rois divins. Et la foule s'assit et resta"});
db.iliadfr.insert({ pg : 825, line : "silencieuse; et le divin Agamemnôn se leva, tenant son sceptre."});
db.iliadfr.insert({ pg : 826, line : "Hèphaistos, l'ayant fait, l'avait donné au roi Zeus Kroniôn. Zeus"});
db.iliadfr.insert({ pg : 827, line : "le donna au messager, tueur d'Argos; et le roi Herméias le donna à"});
db.iliadfr.insert({ pg : 828, line : "Pélops, dompteur de chevaux, et Pélops le donna au prince des"});
db.iliadfr.insert({ pg : 829, line : "peuples Atreus. Atreus, en mourant, le laissa à Thyestès riche en"});
db.iliadfr.insert({ pg : 830, line : "troupeaux, et Thyestès le laissa à Agamemnôn, afin que ce dernier"});
db.iliadfr.insert({ pg : 831, line : "le portât et commandât sur un grand nombre d'îles et sur tout"});
db.iliadfr.insert({ pg : 832, line : "Argos. Appuyé sur ce sceptre, il parla ainsi aux Argiens:"});
db.iliadfr.insert({ pg : 834, line : "-- Ô amis! héros Danaens, serviteurs d'Arès, Zeus Kronide"});
db.iliadfr.insert({ pg : 835, line : "m'accable de maux terribles. L'impitoyable! Autrefois il me promit"});
db.iliadfr.insert({ pg : 836, line : "que je reviendrais après avoir conquis Ilios aux fortes murailles;"});
db.iliadfr.insert({ pg : 837, line : "mais il me trompait, et voici qu'il me faut rentrer sans gloire"});
db.iliadfr.insert({ pg : 838, line : "dans Argos, ayant perdu un grand nombre d'hommes. Et cela plaît au"});
db.iliadfr.insert({ pg : 839, line : "tout puissant Zeus qui a renversé et qui renversera tant de hautes"});
db.iliadfr.insert({ pg : 840, line : "citadelles, car sa force est très grande. Certes, ceci sera une"});
db.iliadfr.insert({ pg : 841, line : "honte dans la postérité, que la race courageuse et innombrable des"});
db.iliadfr.insert({ pg : 842, line : "Akhaiens ait combattu tant d'années, et vainement, des hommes"});
db.iliadfr.insert({ pg : 843, line : "moins nombreux, sans qu'on puisse prévoir la fin de la lutte. Car,"});
db.iliadfr.insert({ pg : 844, line : "si, ayant scellé par serment d'inviolables traités, nous, Akhaiens"});
db.iliadfr.insert({ pg : 845, line : "et Troiens, nous faisions un dénombrement des deux races; et que,"});
db.iliadfr.insert({ pg : 846, line : "les habitants de Troiè s'étant réunis, nous nous rangions par"});
db.iliadfr.insert({ pg : 847, line : "décades, comptant un seul Troien pour présenter la coupe à chacune"});
db.iliadfr.insert({ pg : 848, line : "d'elles, certes, beaucoup de décades manqueraient d'échansons,"});
db.iliadfr.insert({ pg : 849, line : "tant les fils des Argiens sont plus nombreux que les Troiens qui"});
db.iliadfr.insert({ pg : 850, line : "habitent cette ville. Mais voici que de nombreux alliés, habiles à"});
db.iliadfr.insert({ pg : 851, line : "lancer la pique, s'opposent victorieusement à mon désir de"});
db.iliadfr.insert({ pg : 852, line : "renverser la citadelle populeuse de Troiè. Neuf années du grand"});
db.iliadfr.insert({ pg : 853, line : "Zeus se sont écoulées déjà, et le bois de nos nefs se corrompt, et"});
db.iliadfr.insert({ pg : 854, line : "les cordages tombent en poussière; et nos femmes et nos petits"});
db.iliadfr.insert({ pg : 855, line : "enfants restent en nous attendant dans nos demeures, et la tâche"});
db.iliadfr.insert({ pg : 856, line : "est inachevée pour laquelle nous sommes venus. Allons! fuyons tous"});
db.iliadfr.insert({ pg : 857, line : "sur nos nefs vers la chère terre natale. Nous ne prendrons jamais"});
db.iliadfr.insert({ pg : 858, line : "la grande Troiè!"});
db.iliadfr.insert({ pg : 860, line : "Il parla ainsi, et ses paroles agitèrent l'esprit de la multitude"});
db.iliadfr.insert({ pg : 861, line : "qui n'avait point assisté au conseil. Et l'agora fut agitée comme"});
db.iliadfr.insert({ pg : 862, line : "les vastes flots de la mer Ikarienne que remuent l'Euros et le"});
db.iliadfr.insert({ pg : 863, line : "Notos échappés des nuées du père Zeus, ou comme un champ d'épis"});
db.iliadfr.insert({ pg : 864, line : "que bouleverse Zéphyros qui tombe impétueusement sur la grande"});
db.iliadfr.insert({ pg : 865, line : "moisson. Telle l'agora était agitée. Et ils se ruaient tous vers"});
db.iliadfr.insert({ pg : 866, line : "les nefs, avec des clameurs, et soulevant de leurs pieds un nuage"});
db.iliadfr.insert({ pg : 867, line : "immobile de poussière. Et ils s'exhortaient à saisir les nefs et à"});
db.iliadfr.insert({ pg : 868, line : "les traîner à la mer divine. Les cris montaient dans l'Ouranos,"});
db.iliadfr.insert({ pg : 869, line : "hâtant le départ; et ils dégageaient les canaux et retiraient déjà"});
db.iliadfr.insert({ pg : 870, line : "les rouleaux des nefs. Alors, les Argiens se seraient retirés,"});
db.iliadfr.insert({ pg : 871, line : "contre la destinée, si Hèrè n'avait parlé ainsi à Athènè:"});
db.iliadfr.insert({ pg : 873, line : "-- Ah fille indomptée de Zeus tempétueux, les Argiens fuiront-ils"});
db.iliadfr.insert({ pg : 874, line : "vers leurs demeures et la chère terre natale, sur le vaste dos de"});
db.iliadfr.insert({ pg : 875, line : "la mer, laissant à Priamos et aux Troiens leur gloire et"});
db.iliadfr.insert({ pg : 876, line : "l'Argienne Hélénè pour laquelle tant d'Akhaiens sont morts devant"});
db.iliadfr.insert({ pg : 877, line : "Troiè, loin de la chère patrie? Va trouver le peuple des Akhaiens"});
db.iliadfr.insert({ pg : 878, line : "armés d'airain. Retiens chaque guerrier par de douces paroles, et"});
db.iliadfr.insert({ pg : 879, line : "ne permets pas qu'on traîne les nefs à la mer."});
db.iliadfr.insert({ pg : 881, line : "Elle parla ainsi, et la divine Athènè aux yeux clairs obéit. Et"});
db.iliadfr.insert({ pg : 882, line : "elle sauta du faîte de l'Olympos, et, parvenue aussitôt aux nefs"});
db.iliadfr.insert({ pg : 883, line : "rapides des Akhaiens, elle trouva Odysseus, semblable à Zeus par"});
db.iliadfr.insert({ pg : 884, line : "l'intelligence, qui restait immobile. Et il ne saisissait point sa"});
db.iliadfr.insert({ pg : 885, line : "nef noire bien construite, car la douleur emplissait son coeur et"});
db.iliadfr.insert({ pg : 886, line : "son âme. Et, s'arrêtant auprès de lui, Athènè aux yeux clairs"});
db.iliadfr.insert({ pg : 887, line : "parla ainsi:"});
db.iliadfr.insert({ pg : 889, line : "-- Divin Laertiade, sage Odysseus, fuirez-vous donc tous dans vos"});
db.iliadfr.insert({ pg : 890, line : "nefs chargées de rameurs, laissant à Priamos et aux Troiens leur"});
db.iliadfr.insert({ pg : 891, line : "gloire et l'Argienne Hélénè pour laquelle tant d'Akhaiens sont"});
db.iliadfr.insert({ pg : 892, line : "morts devant Troiè, loin de la chère patrie? Va! hâte-toi d'aller"});
db.iliadfr.insert({ pg : 893, line : "vers le peuple des Akhaiens. Retiens chaque guerrier par de douces"});
db.iliadfr.insert({ pg : 894, line : "paroles, et ne permets pas qu'on traîne les nefs à la mer."});
db.iliadfr.insert({ pg : 896, line : "Elle parla ainsi, et il reconnut la voix de la déesse, et il"});
db.iliadfr.insert({ pg : 897, line : "courut, jetant son manteau que releva le héraut Eurybatès"});
db.iliadfr.insert({ pg : 898, line : "d'Ithakè, qui le suivait. Et, rencontrant l'Atréide Agamemnôn, il"});
db.iliadfr.insert({ pg : 899, line : "reçut de lui le sceptre immortel de ses pères, et, avec ce"});
db.iliadfr.insert({ pg : 900, line : "sceptre, il marcha vers les nefs des Akhaiens revêtus d'airain. Et"});
db.iliadfr.insert({ pg : 901, line : "quand il se trouvait en face d'un roi ou d'un homme illustre, il"});
db.iliadfr.insert({ pg : 902, line : "l'arrêtait par de douces paroles:"});
db.iliadfr.insert({ pg : 904, line : "-- Malheureux! Il ne te convient pas de trembler comme un lâche."});
db.iliadfr.insert({ pg : 905, line : "Reste et arrête les autres. Tu ne sais pas la vraie pensée de"});
db.iliadfr.insert({ pg : 906, line : "l'Atréide. Maintenant il tente les fils des Akhaiens, et bientôt"});
db.iliadfr.insert({ pg : 907, line : "il les punira. Nous n'avons point tous entendu ce qu'il a dit dans"});
db.iliadfr.insert({ pg : 908, line : "le conseil. Craignons que, dans sa colère, il outrage les fils des"});
db.iliadfr.insert({ pg : 909, line : "Akhaiens, car la colère d'un roi nourrisson de Zeus est"});
db.iliadfr.insert({ pg : 910, line : "redoutable, et le très sage Zeus l'aime, et sa gloire vient de"});
db.iliadfr.insert({ pg : 911, line : "Zeus."});
db.iliadfr.insert({ pg : 913, line : "Mais quand il rencontrait quelque guerrier obscur et plein de"});
db.iliadfr.insert({ pg : 914, line : "clameurs, il le frappait du sceptre et le réprimait par de rudes"});
db.iliadfr.insert({ pg : 915, line : "paroles:"});
db.iliadfr.insert({ pg : 917, line : "-- Arrête, misérable! écoute ceux qui te sont supérieurs, lâche et"});
db.iliadfr.insert({ pg : 918, line : "sans force, toi qui n'as aucun rang ni dans le combat ni dans le"});
db.iliadfr.insert({ pg : 919, line : "conseil. Certes, tous les Akhaiens ne seront point rois ici. La"});
db.iliadfr.insert({ pg : 920, line : "multitude des maîtres ne vaut rien. Il ne faut qu'un chef, un seul"});
db.iliadfr.insert({ pg : 921, line : "roi, à qui le fils de Kronos empli de ruses a remis le sceptre et"});
db.iliadfr.insert({ pg : 922, line : "les lois, afin qu'il règne sur tous."});
db.iliadfr.insert({ pg : 924, line : "Ainsi Odysseus refrénait puissamment l'armée. Et ils se"});
db.iliadfr.insert({ pg : 925, line : "précipitaient de nouveau, tumultueux, vers l'agora, loin des nefs"});
db.iliadfr.insert({ pg : 926, line : "et des tentes, comme lorsque les flots aux bruits sans nombre se"});
db.iliadfr.insert({ pg : 927, line : "brisent en grondant sur le vaste rivage, et que la haute mer en"});
db.iliadfr.insert({ pg : 928, line : "retentit. Et tous étaient assis à leurs rangs. Et, seul, Thersitès"});
db.iliadfr.insert({ pg : 929, line : "poursuivait ses clameurs. Il abondait en paroles insolentes et"});
db.iliadfr.insert({ pg : 930, line : "outrageantes, même contre les rois, et parlait sans mesure, afin"});
db.iliadfr.insert({ pg : 931, line : "d'exciter le rire des Argiens. Et c'était l'homme le plus difforme"});
db.iliadfr.insert({ pg : 932, line : "qui fût venu devant Ilios. Il était louche et boiteux, et ses"});
db.iliadfr.insert({ pg : 933, line : "épaules recourbées se rejoignaient sur sa poitrine, et quelques"});
db.iliadfr.insert({ pg : 934, line : "cheveux épars poussaient sur sa tête pointue. Et il haïssait"});
db.iliadfr.insert({ pg : 935, line : "surtout Akhilleus et Odysseus, et il les outrageait. Et il"});
db.iliadfr.insert({ pg : 936, line : "poussait des cris injurieux contre le divin Agamemnôn. Les"});
db.iliadfr.insert({ pg : 937, line : "Akhaiens le méprisaient et le haïssaient, mais, d'une voix haute,"});
db.iliadfr.insert({ pg : 938, line : "il outrageait ainsi Agamemnôn:"});
db.iliadfr.insert({ pg : 940, line : "-- Atréide, que te faut-il encore, et que veux-tu? Tes tentes sont"});
db.iliadfr.insert({ pg : 941, line : "pleines d'airain et de nombreuses femmes fort belles que nous te"});
db.iliadfr.insert({ pg : 942, line : "donnons d'abord, nous, Akhaiens, quand nous prenons une ville. As-"});
db.iliadfr.insert({ pg : 943, line : "tu besoin de l'or qu'un Troien dompteur de chevaux t'apportera"});
db.iliadfr.insert({ pg : 944, line : "pour l'affranchissement de son fils que j'aurai amené enchaîné, ou"});
db.iliadfr.insert({ pg : 945, line : "qu'un autre Akhaien aura dompté? Te faut-il une jeune femme que tu"});
db.iliadfr.insert({ pg : 946, line : "possèdes et que tu ne quittes plus? Il ne convient point qu'un"});
db.iliadfr.insert({ pg : 947, line : "chef accable de maux les Akhaiens. Ô lâches! opprobres vivants!"});
db.iliadfr.insert({ pg : 948, line : "Akhaiennes et non Akhaiens! Retournons dans nos demeures avec les"});
db.iliadfr.insert({ pg : 949, line : "nefs; laissons-le, seul devant Troiè, amasser des dépouilles, et"});
db.iliadfr.insert({ pg : 950, line : "qu'il sache si nous lui étions nécessaires ou non. N'a-t-il point"});
db.iliadfr.insert({ pg : 951, line : "outragé Akhilleus, meilleur guerrier que lui, et enlevé sa"});
db.iliadfr.insert({ pg : 952, line : "récompense? Certes, Akhilleus n'a point de colère dans l'âme, car"});
db.iliadfr.insert({ pg : 953, line : "c'eût été, Atréide, ta dernière insolence!"});
db.iliadfr.insert({ pg : 955, line : "Il parla ainsi, outrageant Agamemnôn, prince des peuples. Et le"});
db.iliadfr.insert({ pg : 956, line : "divin Odysseus, s'arrêtant devant lui, le regarda d'un oeil sombre"});
db.iliadfr.insert({ pg : 957, line : "et lui dit rudement:"});
db.iliadfr.insert({ pg : 959, line : "-- Thersitès, infatigable harangueur, silence! Et cesse de t'en"});
db.iliadfr.insert({ pg : 960, line : "prendre aux rois. Je ne pense point qu'il soit un homme plus vil"});
db.iliadfr.insert({ pg : 961, line : "que toi parmi ceux qui sont venus devant Troiè avec les Atréides,"});
db.iliadfr.insert({ pg : 962, line : "et tu ne devrais point haranguer avec le nom des rois à la bouche,"});
db.iliadfr.insert({ pg : 963, line : "ni les outrager, ni exciter au retour. Nous ne savons point quelle"});
db.iliadfr.insert({ pg : 964, line : "sera notre destinée, et s'il est bon ou mauvais que nous partions."});
db.iliadfr.insert({ pg : 965, line : "Et voici que tu te plais à outrager l'Atréide Agamemnôn, prince"});
db.iliadfr.insert({ pg : 966, line : "des peuples, parce que les héros Danaens l'ont comblé de dons! Et"});
db.iliadfr.insert({ pg : 967, line : "c'est pour cela que tu harangues? Mais je te le dis, et ma parole"});
db.iliadfr.insert({ pg : 968, line : "s'accomplira: si je te rencontre encore plein de rage comme"});
db.iliadfr.insert({ pg : 969, line : "maintenant, que ma tête saute de mes épaules, que je ne sois plus"});
db.iliadfr.insert({ pg : 970, line : "nommé le père de Tèlémakhos, si je ne te saisis, et, t'ayant"});
db.iliadfr.insert({ pg : 971, line : "arraché ton vêtement, ton manteau et ce qui couvre ta nudité, je"});
db.iliadfr.insert({ pg : 972, line : "ne te renvoie, sanglotant, de l'agora aux nefs rapides, en te"});
db.iliadfr.insert({ pg : 973, line : "frappant de coups terribles"});
db.iliadfr.insert({ pg : 975, line : "Il parla ainsi, et il le frappa du sceptre sur le dos et les"});
db.iliadfr.insert({ pg : 976, line : "épaules. Et Thersitès se courba, et les larmes lui tombèrent des"});
db.iliadfr.insert({ pg : 977, line : "yeux. Une tumeur saignante lui gonfla le dos sous le coup du"});
db.iliadfr.insert({ pg : 978, line : "sceptre d'or, et il s'assit, tremblant et gémissant, hideux à"});
db.iliadfr.insert({ pg : 979, line : "voir, et il essuya ses yeux. Et les Akhaiens, bien que soucieux,"});
db.iliadfr.insert({ pg : 980, line : "rirent aux éclats; et, se regardant les uns les autres, ils se"});
db.iliadfr.insert({ pg : 981, line : "disaient:"});
db.iliadfr.insert({ pg : 983, line : "-- Certes, Odysseus a déjà fait mille choses excellentes, par ses"});
db.iliadfr.insert({ pg : 984, line : "sages conseils et par sa science guerrière; mais ce qu'il a fait"});
db.iliadfr.insert({ pg : 985, line : "de mieux, entre tous les Argiens, a été de réduire au silence ce"});
db.iliadfr.insert({ pg : 986, line : "harangueur injurieux. De longtemps, il se gardera d'outrager les"});
db.iliadfr.insert({ pg : 987, line : "rois par ses paroles injurieuses."});
db.iliadfr.insert({ pg : 989, line : "La multitude parlait ainsi. Et le preneur de villes, Odysseus, se"});
db.iliadfr.insert({ pg : 990, line : "leva, tenant son sceptre. Auprès de lui, Athènè aux yeux clairs,"});
db.iliadfr.insert({ pg : 991, line : "semblable à un héraut, ordonna à la foule de se taire, afin que"});
db.iliadfr.insert({ pg : 992, line : "tous les fils des Akhaiens, les plus proches et les plus éloignés,"});
db.iliadfr.insert({ pg : 993, line : "pussent entendre et comprendre. Et l'excellent agorète parla"});
db.iliadfr.insert({ pg : 994, line : "ainsi:"});
db.iliadfr.insert({ pg : 996, line : "-- Roi Atréide, voici que les Akhaiens veulent te couvrir"});
db.iliadfr.insert({ pg : 997, line : "d'opprobre en face des hommes vivants, et ils ne tiennent point la"});
db.iliadfr.insert({ pg : 998, line : "promesse qu'ils te firent, en venant d'Argos féconde en chevaux,"});
db.iliadfr.insert({ pg : 999, line : "de ne retourner qu'après avoir renversé la forte muraille d'Ilios."});
db.iliadfr.insert({ pg : 1000, line : "Et voici qu'ils pleurent, pleins du désir de leurs demeures, comme"});
db.iliadfr.insert({ pg : 1001, line : "des enfants et des veuves. Certes, c'est une amère douleur de fuir"});
db.iliadfr.insert({ pg : 1002, line : "après tant de maux soufferts. Je sais, il est vrai, qu'un"});
db.iliadfr.insert({ pg : 1003, line : "voyageur, éloigné de sa femme depuis un seul mois, s'irrite auprès"});
db.iliadfr.insert({ pg : 1004, line : "de sa nef chargée de rameurs, que retiennent les vents d'hiver et"});
db.iliadfr.insert({ pg : 1005, line : "la mer soulevée. Or, voici neuf années bientôt que nous sommes"});
db.iliadfr.insert({ pg : 1006, line : "ici. Je n'en veux donc point aux Akhaiens de s'irriter auprès de"});
db.iliadfr.insert({ pg : 1007, line : "leurs nefs éperonnées; mais il est honteux d'être restés si"});
db.iliadfr.insert({ pg : 1008, line : "longtemps et de s'en retourner les mains vides. Souffrez donc,"});
db.iliadfr.insert({ pg : 1009, line : "amis, et demeurez ici quelque temps encore, afin que nous sachions"});
db.iliadfr.insert({ pg : 1010, line : "si Kalkhas a dit vrai ou faux. Et nous le savons, et vous en êtes"});
db.iliadfr.insert({ pg : 1011, line : "tous témoins, vous que les kères de la mort n'ont point emportés."});
db.iliadfr.insert({ pg : 1012, line : "Était-ce donc hier? Les nefs des Akhaiens étaient réunies devant"});
db.iliadfr.insert({ pg : 1013, line : "Aulis, portant les calamités à Priamos et aux Troiens. Et nous"});
db.iliadfr.insert({ pg : 1014, line : "étions autour de la source, auprès des autels sacrés, offrant aux"});
db.iliadfr.insert({ pg : 1015, line : "immortels de complètes hécatombes, sous un beau platane; et, à son"});
db.iliadfr.insert({ pg : 1016, line : "ombre, coulait une eau vive, quand nous vîmes un grand prodige. Un"});
db.iliadfr.insert({ pg : 1017, line : "dragon terrible, au dos ensanglanté, envoyé de l'Olympien lui-"});
db.iliadfr.insert({ pg : 1018, line : "même, sortit de dessous l'autel et rampa vers le platane. Là"});
db.iliadfr.insert({ pg : 1019, line : "étaient huit petits passereaux, tout jeunes, sur la branche la"});
db.iliadfr.insert({ pg : 1020, line : "plus haute et blottis sous les feuilles; et la mère qui les avait"});
db.iliadfr.insert({ pg : 1021, line : "enfantés était la neuvième. Et le dragon les dévorait cruellement,"});
db.iliadfr.insert({ pg : 1022, line : "et ils criaient, et la mère, désolée, volait tout autour de ses"});
db.iliadfr.insert({ pg : 1023, line : "petits. Et, comme elle emplissait l'air de cris, il la saisit par"});
db.iliadfr.insert({ pg : 1024, line : "une aile; et quand il eut mangé la mère et les petits, le dieu qui"});
db.iliadfr.insert({ pg : 1025, line : "l'avait envoyé en fit un signe mémorable; car le fils de Kronos"});
db.iliadfr.insert({ pg : 1026, line : "empli de ruses le changea en pierre. Et nous admirions ceci, et"});
db.iliadfr.insert({ pg : 1027, line : "les choses terribles qui étaient dans les hécatombes des dieux. Et"});
db.iliadfr.insert({ pg : 1028, line : "voici que Kalkhas nous révéla aussitôt les volontés divines:"});
db.iliadfr.insert({ pg : 1030, line : "-- Pourquoi êtes-vous muets, Akhaiens chevelus? Ceci est un grand"});
db.iliadfr.insert({ pg : 1031, line : "signe du très sage Zeus; et ces choses s'accompliront fort tard,"});
db.iliadfr.insert({ pg : 1032, line : "mais la gloire n'en périra jamais. De même que ce dragon a mangé"});
db.iliadfr.insert({ pg : 1033, line : "les petits passereaux, et ils étaient huit, et la mère qui les"});
db.iliadfr.insert({ pg : 1034, line : "avait enfantés, et elle était la neuvième, de même nous"});
db.iliadfr.insert({ pg : 1035, line : "combattrons pendant neuf années, et, dans la dixième, nous"});
db.iliadfr.insert({ pg : 1036, line : "prendrons Troiè aux larges rues."});
db.iliadfr.insert({ pg : 1038, line : "-- C'est ainsi qu'il parla, et ses paroles se sont accomplies."});
db.iliadfr.insert({ pg : 1039, line : "Restez donc tous, Akhaiens aux belles knèmides, jusqu'à ce que"});
db.iliadfr.insert({ pg : 1040, line : "nous prenions la grande citadelle de Priamos."});
db.iliadfr.insert({ pg : 1042, line : "Il parla ainsi, et les Argiens, par des cris éclatants,"});
db.iliadfr.insert({ pg : 1043, line : "applaudissaient la harangue du divin Odysseus. Et, à ces cris, les"});
db.iliadfr.insert({ pg : 1044, line : "nefs creuses rendirent des sons terribles. Et le cavalier"});
db.iliadfr.insert({ pg : 1045, line : "Gérennien Nestôr leur dit:"});
db.iliadfr.insert({ pg : 1047, line : "-- Ah! certes, ceci est une agora d'enfants étrangers aux fatigues"});
db.iliadfr.insert({ pg : 1048, line : "de la guerre! Où iront nos paroles et nos serments? Les conseils"});
db.iliadfr.insert({ pg : 1049, line : "et la sagesse des hommes, et les libations de vin pur, et les"});
db.iliadfr.insert({ pg : 1050, line : "mains serrées en gage de notre foi commune, tout sera-t-il jeté au"});
db.iliadfr.insert({ pg : 1051, line : "feu? Nous ne combattons qu'en paroles vaines, et nous n'avons rien"});
db.iliadfr.insert({ pg : 1052, line : "trouvé de bon après tant d'années. Atréide, sois donc inébranlable"});
db.iliadfr.insert({ pg : 1053, line : "et commande les Argiens dans les rudes batailles. Laisse périr un"});
db.iliadfr.insert({ pg : 1054, line : "ou deux lâches qui conspirent contre les Akhaiens et voudraient"});
db.iliadfr.insert({ pg : 1055, line : "regagner Argos avant de savoir si Zeus tempétueux a menti. Mais"});
db.iliadfr.insert({ pg : 1056, line : "ils n'y réussiront pas. Moi, je dis que le terrible Kroniôn"});
db.iliadfr.insert({ pg : 1057, line : "engagea sa promesse le jour où les Argiens montaient dans les nefs"});
db.iliadfr.insert({ pg : 1058, line : "rapides pour porter aux Troiens les Kères de la mort, car il tonna"});
db.iliadfr.insert({ pg : 1059, line : "à notre droite, par un signe heureux. Donc, que nul ne se hâte de"});
db.iliadfr.insert({ pg : 1060, line : "s'en retourner avant d'avoir entraîné la femme de quelque Troien"});
db.iliadfr.insert({ pg : 1061, line : "et vengé le rapt de Hélénè et tous les maux qu'il a causés. Et si"});
db.iliadfr.insert({ pg : 1062, line : "quelqu'un veut fuir malgré tout, qu'il saisisse sa nef noire et"});
db.iliadfr.insert({ pg : 1063, line : "bien construite, afin de trouver une prompte mort. Mais, ô roi,"});
db.iliadfr.insert({ pg : 1064, line : "délibère avec une pensée droite et écoute mes conseils. Ce que je"});
db.iliadfr.insert({ pg : 1065, line : "dirai ne doit pas être négligé. Sépare les hommes par races et par"});
db.iliadfr.insert({ pg : 1066, line : "tribus, et que celles-ci se viennent en aide les unes les autres."});
db.iliadfr.insert({ pg : 1067, line : "Si tu fais ainsi, et que les Akhaiens t'obéissent, tu connaîtras"});
db.iliadfr.insert({ pg : 1068, line : "la lâcheté ou le courage des chefs et des hommes, car chacun"});
db.iliadfr.insert({ pg : 1069, line : "combattra selon ses forces. Et si tu ne renverses point cette"});
db.iliadfr.insert({ pg : 1070, line : "ville, tu sauras si c'est par la volonté divine ou par la faute"});
db.iliadfr.insert({ pg : 1071, line : "des hommes."});
db.iliadfr.insert({ pg : 1073, line : "Et le roi Agamemnôn, lui répondant, parla ainsi"});
db.iliadfr.insert({ pg : 1075, line : "-- Certes, vieillard, tu surpasses dans l'agora tous les fils des"});
db.iliadfr.insert({ pg : 1076, line : "Akhaiens. Ô père Zeus! Athènè! Apollôn! Si j'avais dix conseillers"});
db.iliadfr.insert({ pg : 1077, line : "tels que toi parmi les Akhaiens, la ville du roi Priamos tomberait"});
db.iliadfr.insert({ pg : 1078, line : "bientôt, emportée et saccagée par nos mains! Mais le Kronide Zeus"});
db.iliadfr.insert({ pg : 1079, line : "tempétueux m'a accablé de maux en me jetant au milieu de querelles"});
db.iliadfr.insert({ pg : 1080, line : "fatales. Akhilleus et moi nous nous sommes divisés à cause d'une"});
db.iliadfr.insert({ pg : 1081, line : "jeune vierge, et je me suis irrité le premier. Si jamais nous nous"});
db.iliadfr.insert({ pg : 1082, line : "réunissons, la ruine des Troiens ne sera point retardée, même d'un"});
db.iliadfr.insert({ pg : 1083, line : "jour. Maintenant, allez prendre votre repas, afin que nous"});
db.iliadfr.insert({ pg : 1084, line : "combattions. Et que, d'abord, chacun aiguise sa lance, consolide"});
db.iliadfr.insert({ pg : 1085, line : "son bouclier, donne à manger à ses chevaux, s'occupe attentivement"});
db.iliadfr.insert({ pg : 1086, line : "de son char et de toutes les choses de la guerre, afin que nous"});
db.iliadfr.insert({ pg : 1087, line : "fassions tout le jour l'oeuvre du terrible Arès. Et nous n'aurons"});
db.iliadfr.insert({ pg : 1088, line : "nul relâche, jusqu'à ce que la nuit sépare les hommes furieux. La"});
db.iliadfr.insert({ pg : 1089, line : "courroie du bouclier préservateur sera trempée de la sueur de"});
db.iliadfr.insert({ pg : 1090, line : "chaque poitrine, et la main guerrière se fatiguera autour de la"});
db.iliadfr.insert({ pg : 1091, line : "lance, et le cheval fumera, inondé de sueur, en traînant le char"});
db.iliadfr.insert({ pg : 1092, line : "solide. Et, je le dis, celui que je verrai loin du combat, auprès"});
db.iliadfr.insert({ pg : 1093, line : "des nefs éperonnées, celui-là n'évitera point les chiens et les"});
db.iliadfr.insert({ pg : 1094, line : "oiseaux carnassiers."});
db.iliadfr.insert({ pg : 1096, line : "Il parla ainsi, et les Argiens jetèrent de grands cris, avec le"});
db.iliadfr.insert({ pg : 1097, line : "bruit que fait la mer quand le Notos la pousse contre une côte"});
db.iliadfr.insert({ pg : 1098, line : "élevée, sur un roc avancé que les flots ne cessent jamais"});
db.iliadfr.insert({ pg : 1099, line : "d'assiéger, de quelque côté que soufflent les vents. Et ils"});
db.iliadfr.insert({ pg : 1100, line : "coururent, se dispersant au milieu des nefs; et la fumée sortit"});
db.iliadfr.insert({ pg : 1101, line : "des tentes, et ils prirent leur repas. Et chacun d'eux sacrifiait"});
db.iliadfr.insert({ pg : 1102, line : "à l'un des dieux qui vivent toujours, afin d'éviter les blessures"});
db.iliadfr.insert({ pg : 1103, line : "d'Arès et la mort. Et le roi des hommes, Agamemnôn, sacrifia un"});
db.iliadfr.insert({ pg : 1104, line : "taureau gras, de cinq ans, au très puissant Kroniôn, et il"});
db.iliadfr.insert({ pg : 1105, line : "convoqua les plus illustres des Panakhaiens, Nestôr, le roi"});
db.iliadfr.insert({ pg : 1106, line : "Idoméneus, les deux Aias et le fils de Tydeus. Odysseus, égal à"});
db.iliadfr.insert({ pg : 1107, line : "Zeus par l'intelligence, fut le sixième. Ménélaos, brave au"});
db.iliadfr.insert({ pg : 1108, line : "combat, vint de lui-même, sachant les desseins de son frère."});
db.iliadfr.insert({ pg : 1109, line : "Entourant le taureau, ils prirent les orges salées, et, au milieu"});
db.iliadfr.insert({ pg : 1110, line : "d'eux, le roi des hommes, Agamemnôn, dit en priant:"});
db.iliadfr.insert({ pg : 1112, line : "-- Zeus! Très glorieux, très grand, qui amasses les noires nuées"});
db.iliadfr.insert({ pg : 1113, line : "et qui habites l'aithèr! puisse Hélios ne point se coucher et la"});
db.iliadfr.insert({ pg : 1114, line : "nuit ne point venir avant que j'aie renversé la demeure enflammée"});
db.iliadfr.insert({ pg : 1115, line : "de Priamos, après avoir brûlé ses portes et brisé, de l'épée, la"});
db.iliadfr.insert({ pg : 1116, line : "cuirasse de Hektôr sur sa poitrine, vu la foule de ses compagnons,"});
db.iliadfr.insert({ pg : 1117, line : "couchés autour de lui dans la poussière, mordre de leurs dents la"});
db.iliadfr.insert({ pg : 1118, line : "terre!"});
db.iliadfr.insert({ pg : 1120, line : "Il parla ainsi, et le Kroniôn accepta le sacrifice, mais il ne"});
db.iliadfr.insert({ pg : 1121, line : "l'exauça pas, lui réservant de plus longues fatigues. Et, après"});
db.iliadfr.insert({ pg : 1122, line : "qu'ils eurent prié et jeté les orges salées, ils renversèrent la"});
db.iliadfr.insert({ pg : 1123, line : "tête du taureau; et, l'ayant égorgé et dépouillé, ils coupèrent"});
db.iliadfr.insert({ pg : 1124, line : "les cuisses qu'ils couvrirent deux fois de graisse; et, posant"});
db.iliadfr.insert({ pg : 1125, line : "par-dessus des morceaux sanglants, ils les rôtissaient avec des"});
db.iliadfr.insert({ pg : 1126, line : "rameaux sans feuilles, et ils tenaient les entrailles sur le feu."});
db.iliadfr.insert({ pg : 1127, line : "Et quand les cuisses furent rôties et qu'ils eurent goûté aux"});
db.iliadfr.insert({ pg : 1128, line : "entrailles, ils coupèrent le reste par morceaux qu'ils"});
db.iliadfr.insert({ pg : 1129, line : "embrochèrent et firent rôtir avec soin, et ils retirèrent le tout."});
db.iliadfr.insert({ pg : 1130, line : "Et, après ce travail, ils préparèrent le repas, et aucun ne put se"});
db.iliadfr.insert({ pg : 1131, line : "plaindre d'une part inégale. Puis, ayant assouvi la faim et la"});
db.iliadfr.insert({ pg : 1132, line : "soif, le cavalier Gérennien Nestôr parla ainsi:"});
db.iliadfr.insert({ pg : 1134, line : "-- Très glorieux roi des hommes, Atréide Agamemnôn, ne tardons pas"});
db.iliadfr.insert({ pg : 1135, line : "plus longtemps à faire ce que Zeus nous permet d'accomplir."});
db.iliadfr.insert({ pg : 1136, line : "Allons! que les hérauts, par leurs clameurs, rassemblent auprès"});
db.iliadfr.insert({ pg : 1137, line : "des nefs l'armée des Akhaiens revêtus d'airain; et nous, nous"});
db.iliadfr.insert({ pg : 1138, line : "mêlant à la foule guerrière des Akhaiens, excitons à l'instant"});
db.iliadfr.insert({ pg : 1139, line : "l'impétueux Arès."});
db.iliadfr.insert({ pg : 1141, line : "Il parla ainsi, et le roi des hommes, Agamnemnôn, obéit, et il"});
db.iliadfr.insert({ pg : 1142, line : "ordonna aux hérauts à la voix éclatante d'appeler au combat les"});
db.iliadfr.insert({ pg : 1143, line : "Akhaiens chevelus. Et, autour de l'Atréiôn, les rois divins"});
db.iliadfr.insert({ pg : 1144, line : "couraient çà et là, rangeant l'armée. Et, au milieu d'eux, Athènè"});
db.iliadfr.insert({ pg : 1145, line : "aux yeux clairs portait l'Aigide glorieuse, impérissable et"});
db.iliadfr.insert({ pg : 1146, line : "immortelle. Et cent franges d'or bien tissues, chacune du prix de"});
db.iliadfr.insert({ pg : 1147, line : "cent boeufs, y étaient suspendues. Avec cette aigide, elle allait"});
db.iliadfr.insert({ pg : 1148, line : "ardemment à travers l'armée des Akhaiens, poussant chacun en"});
db.iliadfr.insert({ pg : 1149, line : "avant, lui mettant la force et le courage au coeur, afin qu'il"});
db.iliadfr.insert({ pg : 1150, line : "guerroyât et combattît sans relâche. Et aussitôt il leur semblait"});
db.iliadfr.insert({ pg : 1151, line : "plus doux de combattre que de retourner sur leurs nefs creuses"});
db.iliadfr.insert({ pg : 1152, line : "vers la chère terre natale. Comme un feu ardent qui brûle une"});
db.iliadfr.insert({ pg : 1153, line : "grande forêt au faîte d'une montagne, et dont la lumière"});
db.iliadfr.insert({ pg : 1154, line : "resplendit au loin, de même s'allumait dans l'Ouranos l'airain"});
db.iliadfr.insert({ pg : 1155, line : "étincelant des hommes qui marchaient."});
db.iliadfr.insert({ pg : 1157, line : "Comme les multitudes ailées des oies, des grues ou des cygnes au"});
db.iliadfr.insert({ pg : 1158, line : "long cou, dans les prairies d'Asios, sur les bords du Kaystrios,"});
db.iliadfr.insert({ pg : 1159, line : "volent çà et là, agitant leurs ailes joyeuses, et se devançant les"});
db.iliadfr.insert({ pg : 1160, line : "uns les autres avec des cris dont la prairie résonne, de même les"});
db.iliadfr.insert({ pg : 1161, line : "innombrables tribus Akhaiennes roulaient en torrents dans la"});
db.iliadfr.insert({ pg : 1162, line : "plaine du Skamandros, loin des nefs et des tentes; et, sous leurs"});
db.iliadfr.insert({ pg : 1163, line : "pieds et ceux des chevaux, la terre mugissait terriblement. Et ils"});
db.iliadfr.insert({ pg : 1164, line : "s'arrêtèrent dans la plaine fleurie du Skamandros, par milliers,"});
db.iliadfr.insert({ pg : 1165, line : "tels que les feuilles et les fleurs du printemps. Aussi nombreux"});
db.iliadfr.insert({ pg : 1166, line : "que les tourbillons infinis de mouches qui bourdonnent autour de"});
db.iliadfr.insert({ pg : 1167, line : "l'étable, dans la saison printanière, quand le lait abondant"});
db.iliadfr.insert({ pg : 1168, line : "blanchit les vases, les Akhaiens chevelus s'arrêtaient dans la"});
db.iliadfr.insert({ pg : 1169, line : "plaine en face des Troiens, désirant les détruire. Comme les"});
db.iliadfr.insert({ pg : 1170, line : "bergers reconnaissent aisément leurs immenses troupeaux de chèvres"});
db.iliadfr.insert({ pg : 1171, line : "confondus dans les pâturages, ainsi les chefs rangeaient leurs"});
db.iliadfr.insert({ pg : 1172, line : "hommes. Et le grand roi Agamemnôn était au milieu d'eux, semblable"});
db.iliadfr.insert({ pg : 1173, line : "par les yeux et la tête à Zeus qui se réjouit de la foudre, par la"});
db.iliadfr.insert({ pg : 1174, line : "stature à Arès, et par l'ampleur de la poitrine à Poseidaôn. Comme"});
db.iliadfr.insert({ pg : 1175, line : "un taureau l'emporte sur le reste du troupeau et s'élève au-dessus"});
db.iliadfr.insert({ pg : 1176, line : "des génisses qui l'environnent, de même Zeus, en ce jour, faisait"});
db.iliadfr.insert({ pg : 1177, line : "resplendir l'Atréide entre d'innombrables héros."});
db.iliadfr.insert({ pg : 1179, line : "Et maintenant, Muses, qui habitez les demeures Olympiennes, vous"});
db.iliadfr.insert({ pg : 1180, line : "qui êtes déesses, et présentes à tout, et qui savez toutes choses,"});
db.iliadfr.insert({ pg : 1181, line : "tandis que nous ne savons rien et n'entendons seulement qu'un"});
db.iliadfr.insert({ pg : 1182, line : "bruit de gloire, dites les rois et les princes des Danaens. Car je"});
db.iliadfr.insert({ pg : 1183, line : "ne pourrais nommer ni décrire la multitude, même ayant dix"});
db.iliadfr.insert({ pg : 1184, line : "langues, dix bouches, une voix infatigable et une poitrine"});
db.iliadfr.insert({ pg : 1185, line : "d'airain, si les Muses Olympiades, filles de Zeus tempétueux, ne"});
db.iliadfr.insert({ pg : 1186, line : "me rappellent ceux qui vinrent sous Ilios. Je dirai donc les chefs"});
db.iliadfr.insert({ pg : 1187, line : "et toutes les nefs."});
db.iliadfr.insert({ pg : 1189, line : "Pènéléôs et Lèitos, et Arkésilaos, et Prothoènôr, et Klonios"});
db.iliadfr.insert({ pg : 1190, line : "commandaient aux Boiôtiens. Et c'étaient ceux qui habitaient Hyriè"});
db.iliadfr.insert({ pg : 1191, line : "et la pierreuse Aulis, et Skhoinos, et Skôlos, et les nombreuses"});
db.iliadfr.insert({ pg : 1192, line : "collines d'Étéôn, et Thespéia, et Graia, et la grande Mikalèsos;"});
db.iliadfr.insert({ pg : 1193, line : "et ceux qui habitaient autour de Harma et d'Eilésios et d'Érythra;"});
db.iliadfr.insert({ pg : 1194, line : "et ceux qui habitaient Éléôn et Hilè, et Pétéôn, Okaliè et Médéôn"});
db.iliadfr.insert({ pg : 1195, line : "bien bâtie, Kôpa et Eutrèsis et Thisbé abondante en colombes; et"});
db.iliadfr.insert({ pg : 1196, line : "ceux qui habitaient Korônéia et Haliartos aux grandes prairies; et"});
db.iliadfr.insert({ pg : 1197, line : "ceux qui habitaient Plataia; et ceux qui vivaient dans Glissa; et"});
db.iliadfr.insert({ pg : 1198, line : "ceux qui habitaient la cité bien bâtie de Hypothèba, et la sainte"});
db.iliadfr.insert({ pg : 1199, line : "Onkhestos, bois sacré de Poseidaôn; et ceux qui habitaient Arnè"});
db.iliadfr.insert({ pg : 1200, line : "qui abonde en raisin, et Midéia, et la sainte Nissa, et la ville"});
db.iliadfr.insert({ pg : 1201, line : "frontière Anthèdôn. Et ils étaient venus sur cinquante nefs, et"});
db.iliadfr.insert({ pg : 1202, line : "chacune portait cent vingt jeunes Boiôtiens."});
db.iliadfr.insert({ pg : 1204, line : "Et ceux qui habitaient Asplèdôn et Orkhomènos de Mynias étaient"});
db.iliadfr.insert({ pg : 1205, line : "commandés par Askalaphos et Ialménos, fils d'Arès. Et Astyokhè"});
db.iliadfr.insert({ pg : 1206, line : "Azéide les avait enfantés dans la demeure d'Aktôr; le puissant"});
db.iliadfr.insert({ pg : 1207, line : "Arès ayant surpris la vierge innocente dans les chambres hautes."});
db.iliadfr.insert({ pg : 1208, line : "Et ils étaient venus sur trente nefs creuses."});
db.iliadfr.insert({ pg : 1210, line : "Et Skhédios et Épistrophos, fils du magnanime Iphitos Naubolide,"});
db.iliadfr.insert({ pg : 1211, line : "commandaient aux Phôkèens. Et c'étaient ceux qui habitaient"});
db.iliadfr.insert({ pg : 1212, line : "Kiparissos et la pierreuse Pythôn et la sainte Krissa, et Daulis"});
db.iliadfr.insert({ pg : 1213, line : "et Panopè; et ceux qui habitaient autour d'Anémôréia et de"});
db.iliadfr.insert({ pg : 1214, line : "Hyampolis; et ceux qui habitaient auprès du divin fleuve Kèphisos"});
db.iliadfr.insert({ pg : 1215, line : "et qui possédaient Lilaia, à la source du Kèphisos. Et ils étaient"});
db.iliadfr.insert({ pg : 1216, line : "venus sur quarante nefs noires, et leurs chefs les rangèrent à la"});
db.iliadfr.insert({ pg : 1217, line : "gauche des Boiôtiens."});
db.iliadfr.insert({ pg : 1219, line : "Et l'agile Aias Oilèide commandait aux Lokriens. Il était beaucoup"});
db.iliadfr.insert({ pg : 1220, line : "moins grand qu'Aias Télamônien, et sa cuirasse était de lin; mais,"});
db.iliadfr.insert({ pg : 1221, line : "par la lance, il excellait entre les Panhellènes et les Akhaiens."});
db.iliadfr.insert({ pg : 1222, line : "Et il commandait à ceux qui habitaient Kynos et Kalliaros, et"});
db.iliadfr.insert({ pg : 1223, line : "Bèssa et Scarphè, et l'heureuse Augéia, et Tarphè, et Thronios,"});
db.iliadfr.insert({ pg : 1224, line : "auprès du Boagrios. Et tous ces Lokriens, qui habitaient au-delà"});
db.iliadfr.insert({ pg : 1225, line : "de la sainte Euboiè, étaient venus sur quarante nefs noires."});
db.iliadfr.insert({ pg : 1227, line : "Et les Abantes, pleins de courage, qui habitaient l'Euboia et"});
db.iliadfr.insert({ pg : 1228, line : "Khalkis, et Eirétria, et Histiaia qui abonde en raisin, et la"});
db.iliadfr.insert({ pg : 1229, line : "maritime Kèrinthos, et la haute citadelle de Diôs; et ceux qui"});
db.iliadfr.insert({ pg : 1230, line : "habitaient Karistos et Styra étaient commandés par Éléphènôr"});
db.iliadfr.insert({ pg : 1231, line : "Khalkodontiade, de la race d'Arès; et il était le prince des"});
db.iliadfr.insert({ pg : 1232, line : "magnanimes Abantes. Et les Abantes agiles, aux cheveux flottant"});
db.iliadfr.insert({ pg : 1233, line : "sur le dos, braves guerriers, désiraient percer de près les"});
db.iliadfr.insert({ pg : 1234, line : "cuirasses ennemies de leurs piques de frêne. Et ils étaient venus"});
db.iliadfr.insert({ pg : 1235, line : "sur quarante nefs noires."});
db.iliadfr.insert({ pg : 1237, line : "Et ceux qui habitaient Athènes, ville forte et bien bâtie du"});
db.iliadfr.insert({ pg : 1238, line : "magnanime Érékhtheus que nourrit Athènè, fille de Zeus, après que"});
db.iliadfr.insert({ pg : 1239, line : "la terre féconde l'eut enfanté, et qu'elle plaça dans le temple"});
db.iliadfr.insert({ pg : 1240, line : "abondant où les fils des Athènaiens offrent chaque année, pour lui"});
db.iliadfr.insert({ pg : 1241, line : "plaire, des hécatombes de taureaux et d'agneaux; ceux-là étaient"});
db.iliadfr.insert({ pg : 1242, line : "commandés par Ménèstheus, fils de Pétéos. Jamais aucun homme"});
db.iliadfr.insert({ pg : 1243, line : "vivant, si ce n'était Nestôr, qui était plus âgé, ne fut son égal"});
db.iliadfr.insert({ pg : 1244, line : "pour ranger en bataille les cavaliers et les porte boucliers. Et"});
db.iliadfr.insert({ pg : 1245, line : "ils étaient venus sur cinquante nefs noires."});
db.iliadfr.insert({ pg : 1247, line : "Et Aias avait amené douze nefs de Salamis, et il les avait placées"});
db.iliadfr.insert({ pg : 1248, line : "auprès des Athènaiens."});
db.iliadfr.insert({ pg : 1250, line : "Et ceux qui habitaient Argos et la forte Tiryntha, Hermionè et"});
db.iliadfr.insert({ pg : 1251, line : "Asinè aux golfes profonds, Troixènè, Eiôna et Épidauros qui abonde"});
db.iliadfr.insert({ pg : 1252, line : "en vignes; et ceux qui habitaient Aigina et Masès étaient"});
db.iliadfr.insert({ pg : 1253, line : "commandés par Diomèdès, hardi au combat, et par Sthénélôs, fils de"});
db.iliadfr.insert({ pg : 1254, line : "l'illustre Kapaneus, et par Euryalos, semblable aux dieux, fils du"});
db.iliadfr.insert({ pg : 1255, line : "roi Mèkisteus Taliônide. Mais Diomèdès, hardi au combat, les"});
db.iliadfr.insert({ pg : 1256, line : "commandait tous. Et ils étaient venus sur quatre-vingts nefs"});
db.iliadfr.insert({ pg : 1257, line : "noires."});
db.iliadfr.insert({ pg : 1259, line : "Et ceux qui habitaient la ville forte et bien bâtie de Mykènè, et"});
db.iliadfr.insert({ pg : 1260, line : "la riche Korinthos et Kléôn; et ceux qui habitaient Ornéia et"});
db.iliadfr.insert({ pg : 1261, line : "l'heureuse Araithyréè, et Sikiôn où régna, le premier, Adrèstos;"});
db.iliadfr.insert({ pg : 1262, line : "et ceux qui habitaient Hipérèsia et la haute Gonoessa et Pellèna,"});
db.iliadfr.insert({ pg : 1263, line : "et qui vivaient autour d'Aigion et de la grande Hélikè, et sur"});
db.iliadfr.insert({ pg : 1264, line : "toute la côte, étaient commandés par le roi Agamemnôn Atréide. Et"});
db.iliadfr.insert({ pg : 1265, line : "ils étaient venus sur cent nefs, et ils étaient les plus nombreux"});
db.iliadfr.insert({ pg : 1266, line : "et les plus braves des guerriers. Et l'Atréide, revêtu de l'airain"});
db.iliadfr.insert({ pg : 1267, line : "splendide, était fier de commander à tous les héros, étant lui-"});
db.iliadfr.insert({ pg : 1268, line : "même très brave, et ayant amené le plus de guerriers."});
db.iliadfr.insert({ pg : 1270, line : "Et ceux qui habitaient la grande Lakédaimôn dans sa creuse vallée,"});
db.iliadfr.insert({ pg : 1271, line : "et Pharis et Sparta, et Messa qui abonde en colombes, et Bryséia"});
db.iliadfr.insert({ pg : 1272, line : "et l'heureuse Augéia, Amykla et la maritime Hélos; et ceux qui"});
db.iliadfr.insert({ pg : 1273, line : "habitaient Laas et Oitylos, étaient commandés par Ménélaos hardi"});
db.iliadfr.insert({ pg : 1274, line : "au combat, et séparés des guerriers de son frère. Et ils étaient"});
db.iliadfr.insert({ pg : 1275, line : "venus sur soixante nefs. Et Ménélaos était au milieu d'eux,"});
db.iliadfr.insert({ pg : 1276, line : "confiant dans son courage, et les excitant à combattre; car, plus"});
db.iliadfr.insert({ pg : 1277, line : "qu'eux, il désirait venger le rapt de Hélénè et les maux qui en"});
db.iliadfr.insert({ pg : 1278, line : "venaient."});
db.iliadfr.insert({ pg : 1280, line : "Et ceux qui habitaient Pylos et l'heureuse Arènè, et Thryos"});
db.iliadfr.insert({ pg : 1281, line : "traversée par l'Alphéos, et Aipy habilement construite, et"});
db.iliadfr.insert({ pg : 1282, line : "Kiparissè et Amphigènéia, Ptéléon, Hélos et Dôrion, où les Muses,"});
db.iliadfr.insert({ pg : 1283, line : "ayant rencontré le Thrakien Thamyris qui venait d'Oikhaliè, de"});
db.iliadfr.insert({ pg : 1284, line : "chez le roi Eurytos l'Oikhalien, le rendirent muet, parce qu'il"});
db.iliadfr.insert({ pg : 1285, line : "s'était vanté de vaincre en chantant les Muses elles-mêmes, filles"});
db.iliadfr.insert({ pg : 1286, line : "de Zeus tempétueux. Et celles-ci, irritées, lui ôtèrent la science"});
db.iliadfr.insert({ pg : 1287, line : "divine de chanter et de jouer de la kithare. Et ceux-là étaient"});
db.iliadfr.insert({ pg : 1288, line : "commandés par le cavalier Gérennien Nestôr. Et ils étaient venus"});
db.iliadfr.insert({ pg : 1289, line : "sur quatre-vingt-dix nefs creuses."});
db.iliadfr.insert({ pg : 1291, line : "Et ceux qui habitaient l'Arkadia, aux pieds de la haute montagne"});
db.iliadfr.insert({ pg : 1292, line : "de Killènè où naissent les hommes braves, auprès du tombeau"});
db.iliadfr.insert({ pg : 1293, line : "d'Aipytios; et ceux qui habitaient Phénéos et Orkhoménos riche en"});
db.iliadfr.insert({ pg : 1294, line : "troupeaux, et Ripè, et Stratiè, et Enispè battue des vents; et"});
db.iliadfr.insert({ pg : 1295, line : "ceux qui habitaient Tégéè et l'heureuse Mantinéè, et Stimphèlos et"});
db.iliadfr.insert({ pg : 1296, line : "Parrhasiè, étaient commandés par le fils d'Ankaios, le roi"});
db.iliadfr.insert({ pg : 1297, line : "Agapènôr. Et ils étaient venus sur cinquante nefs, et dans chacune"});
db.iliadfr.insert({ pg : 1298, line : "il y avait un grand nombre d'Arkadiens belliqueux. Et le roi"});
db.iliadfr.insert({ pg : 1299, line : "Agamemnôn leur avait donné des nefs bien construites pour"});
db.iliadfr.insert({ pg : 1300, line : "traverser la noire mer, car ils ne s'occupaient point des travaux"});
db.iliadfr.insert({ pg : 1301, line : "de la mer."});
db.iliadfr.insert({ pg : 1303, line : "Et ceux qui habitaient Bouprasios et la divine Élis, et la terre"});
db.iliadfr.insert({ pg : 1304, line : "qui renferme Hyrininè et la ville frontière de Myrsinè, et la"});
db.iliadfr.insert({ pg : 1305, line : "roche Olénienne et Aleisios, étaient venus sous quatre chefs, et"});
db.iliadfr.insert({ pg : 1306, line : "chaque chef conduisait dix nefs rapides où étaient de nombreux"});
db.iliadfr.insert({ pg : 1307, line : "ÉpéiensAmphimakhos et Thalpios commandaient les uns; et le premier"});
db.iliadfr.insert({ pg : 1308, line : "était fils de Kléatos, et le second d'Eurytos Aktoriôn. Et le"});
db.iliadfr.insert({ pg : 1309, line : "robuste Diôrès Amarynkéide commandait les autres, et le divin"});
db.iliadfr.insert({ pg : 1310, line : "Polyxeinos commandait aux derniers; et il était fils d'Agasthéneus"});
db.iliadfr.insert({ pg : 1311, line : "Augéiade."});
db.iliadfr.insert({ pg : 1313, line : "Et ceux qui habitaient Doulikiôn et les saintes îles Ekhinades qui"});
db.iliadfr.insert({ pg : 1314, line : "sont à l'horizon de la mer, en face de l'Élis, étaient commandés"});
db.iliadfr.insert({ pg : 1315, line : "par Mégès Phyléide, semblable à Arès. Et il était fils de Phyleus,"});
db.iliadfr.insert({ pg : 1316, line : "habile cavalier cher à Zeus, qui, s'étant irrité contre son père,"});
db.iliadfr.insert({ pg : 1317, line : "s'était réfugié à Doulikhiôn. Et ils étaient venus sur quarante"});
db.iliadfr.insert({ pg : 1318, line : "nefs noires."});
db.iliadfr.insert({ pg : 1320, line : "Et Odysseus commandait les magnanimes Képhallèniens, et ceux qui"});
db.iliadfr.insert({ pg : 1321, line : "habitaient Ithakè et le Nèritos aux forêts agitées, et ceux qui"});
db.iliadfr.insert({ pg : 1322, line : "habitaient Krokyléia et l'aride Aigilipa et Zakyntos et Samos, et"});
db.iliadfr.insert({ pg : 1323, line : "ceux qui habitaient l'Épeiros sur la rive opposée. Et Odysseus,"});
db.iliadfr.insert({ pg : 1324, line : "égal à Zeus par l'intelligence, les commandait. Et ils étaient"});
db.iliadfr.insert({ pg : 1325, line : "venus sur douze nefs rouges."});
db.iliadfr.insert({ pg : 1327, line : "Et Thoas Andraimonide commandait les Aitôliens qui habitaient"});
db.iliadfr.insert({ pg : 1328, line : "Pleurôn et Olénos, et Pylènè, et la maritime Khalkis, et la"});
db.iliadfr.insert({ pg : 1329, line : "pierreuse Kalidôn. Car les fils du magnanime Oineus étaient morts,"});
db.iliadfr.insert({ pg : 1330, line : "et lui-même était mort, et le blond Méléagros était mort, et Thoas"});
db.iliadfr.insert({ pg : 1331, line : "commandait maintenant les Aitôliens. Et ils étaient venus sur"});
db.iliadfr.insert({ pg : 1332, line : "quarante nefs noires."});
db.iliadfr.insert({ pg : 1334, line : "Et Idoméneus, habile à lancer la pique, commandait les Krètois et"});
db.iliadfr.insert({ pg : 1335, line : "ceux qui habitaient Knôssos et la forte Gorcyna, et les villes"});
db.iliadfr.insert({ pg : 1336, line : "populeuses de Lyktos, de Milètos, de Lykastos, de Phaistos et de"});
db.iliadfr.insert({ pg : 1337, line : "Rhytiôn, et d'autres qui habitaient aussi la Krètè aux cent"});
db.iliadfr.insert({ pg : 1338, line : "villes. Et Idoméneus, habile à lancer la pique, les commandait"});
db.iliadfr.insert({ pg : 1339, line : "avec Mèrionès, pareil au tueur d'hommes Arès. Et ils étaient venus"});
db.iliadfr.insert({ pg : 1340, line : "sur quatre-vingts nefs noires."});
db.iliadfr.insert({ pg : 1342, line : "Et Tlèpolémos Hèraklide, très fort et très grand, avait conduit de"});
db.iliadfr.insert({ pg : 1343, line : "Rhodos, sur neuf nefs, les fiers Rhodiens qui habitaient les trois"});
db.iliadfr.insert({ pg : 1344, line : "parties de Rhodos: Lindos, Ièlissos et la riche Kameiros. Et"});
db.iliadfr.insert({ pg : 1345, line : "Tlèpolémos, habile à lancer la pique, les commandait. Et"});
db.iliadfr.insert({ pg : 1346, line : "Astyokhéia avait donné ce fils au grand Hèraklès, après que ce"});
db.iliadfr.insert({ pg : 1347, line : "dernier l'eut emmenée d'Éphyrè, des bords du Sellèis, où il avait"});
db.iliadfr.insert({ pg : 1348, line : "renversé beaucoup de villes défendues par des jeunes hommes. Et"});
db.iliadfr.insert({ pg : 1349, line : "Tlèpolémos, élevé dans la belle demeure, tua l'oncle de son père,"});
db.iliadfr.insert({ pg : 1350, line : "Likymnios, race d'Arès. Et il construisit des nefs, rassembla une"});
db.iliadfr.insert({ pg : 1351, line : "grande multitude et s'enfuit sur la mer, car les fils et les"});
db.iliadfr.insert({ pg : 1352, line : "petits-fils du grand Hèraklès le menaçaient. Ayant erré et subi"});
db.iliadfr.insert({ pg : 1353, line : "beaucoup de maux, il arriva dans Rhodos, où ils se partagèrent en"});
db.iliadfr.insert({ pg : 1354, line : "trois tribus, et Zeus, qui commande aux dieux et aux hommes, les"});
db.iliadfr.insert({ pg : 1355, line : "aima et les combla de richesses."});
db.iliadfr.insert({ pg : 1357, line : "Et Nireus avait amené de Symè trois nefs. Et il était né d'Aglaiè"});
db.iliadfr.insert({ pg : 1358, line : "et du roi Kharopos, et c'était le plus beau de tous les Danaens,"});
db.iliadfr.insert({ pg : 1359, line : "après l'irréprochable Pèléiôn, mais il n'était point brave et"});
db.iliadfr.insert({ pg : 1360, line : "commandait peu de guerriers."});
db.iliadfr.insert({ pg : 1362, line : "Et ceux qui habitaient Nisyros et Krapathos, et Kasos, et Kôs,"});
db.iliadfr.insert({ pg : 1363, line : "ville d'Eurypylos, et les îles Kalynades, étaient commandés par"});
db.iliadfr.insert({ pg : 1364, line : "Pheidippos et Antiphos, deux fils du roi Thessalos Hèrakléide. Et"});
db.iliadfr.insert({ pg : 1365, line : "ils étaient venus sur trente nefs creuses."});
db.iliadfr.insert({ pg : 1367, line : "Et je nommerai aussi ceux qui habitaient Argos Pélasgique, et Alos"});
db.iliadfr.insert({ pg : 1368, line : "et Alopè, et ceux qui habitaient Trakinè et la Phthiè, et la"});
db.iliadfr.insert({ pg : 1369, line : "Hellas aux belles femmes. Et ils se nommaient Myrmidones, ou"});
db.iliadfr.insert({ pg : 1370, line : "Hellènes, ou Akhaiens, et Akhilleus commandait leurs cinquante"});
db.iliadfr.insert({ pg : 1371, line : "nefs. Mais ils ne se souvenaient plus des clameurs de la guerre,"});
db.iliadfr.insert({ pg : 1372, line : "n'ayant plus de chef qui les menât. Car le divin Akhilleus aux"});
db.iliadfr.insert({ pg : 1373, line : "pieds rapides était couché dans ses nefs, irrité au souvenir de la"});
db.iliadfr.insert({ pg : 1374, line : "vierge Breisèis aux beaux cheveux qu'il avait emmenée de"});
db.iliadfr.insert({ pg : 1375, line : "Lyrnèssos, après avoir pris cette ville et renversé les murailles"});
db.iliadfr.insert({ pg : 1376, line : "de Thèbè avec de grandes fatigues. Là, il avait tué les belliqueux"});
db.iliadfr.insert({ pg : 1377, line : "Mènytos et Épistrophos, fils du roi Évènos Sélèpiade. Et, dans sa"});
db.iliadfr.insert({ pg : 1378, line : "douleur, il restait couché mais il devait se relever bientôt."});
db.iliadfr.insert({ pg : 1380, line : "Et ceux qui habitaient Phylakè et la fertile Pyrrhasos consacrée à"});
db.iliadfr.insert({ pg : 1381, line : "Dèmètèr, et Itôn riche en troupeaux, et la maritime Antrôn, et"});
db.iliadfr.insert({ pg : 1382, line : "Ptéléos aux grasses prairies, étaient commandés par le brave"});
db.iliadfr.insert({ pg : 1383, line : "Prôtésilaos quand il vivait; mais déjà la terre noire le"});
db.iliadfr.insert({ pg : 1384, line : "renfermait; et sa femme se meurtrissait le visage, seule à"});
db.iliadfr.insert({ pg : 1385, line : "Phylakè, dans sa demeure abandonnée; car un guerrier Dardanien le"});
db.iliadfr.insert({ pg : 1386, line : "tua, comme il s'élançait de sa nef, le premier de tous les"});
db.iliadfr.insert({ pg : 1387, line : "Akhaiens. Mais ses guerriers n'étaient point sans chef, et ils"});
db.iliadfr.insert({ pg : 1388, line : "étaient commandés par un nourrisson d'Arès, Podarkès, fils"});
db.iliadfr.insert({ pg : 1389, line : "d'Iphiklos riche en troupeaux, et il était frère du magnanime"});
db.iliadfr.insert({ pg : 1390, line : "Prôtésilaos. Et ce héros était l'aîné et le plus brave, et ses"});
db.iliadfr.insert({ pg : 1391, line : "guerriers le regrettaient. Et ils étaient venus sur quarante nefs"});
db.iliadfr.insert({ pg : 1392, line : "noires."});
db.iliadfr.insert({ pg : 1394, line : "Et ceux qui habitaient Phéra, auprès du lac Boibèis, et Boibè, et"});
db.iliadfr.insert({ pg : 1395, line : "Glaphyra, et Iôlkos, étaient commandés, sur onze nefs, par le fils"});
db.iliadfr.insert({ pg : 1396, line : "bien-aimé d'Admètès, Eumèlos, qu'Alkèstis, la gloire des femmes et"});
db.iliadfr.insert({ pg : 1397, line : "la plus belle des filles de Pèlias, avait donné à Admètès."});
db.iliadfr.insert({ pg : 1399, line : "Et ceux qui habitaient Mèthônè et Thaumakè, et Méliboia et l'aride"});
db.iliadfr.insert({ pg : 1400, line : "Olizôn, Philoktètès, très excellent archer, les commandait, sur"});
db.iliadfr.insert({ pg : 1401, line : "sept nefs. Et dans chaque nef étaient cinquante rameurs,"});
db.iliadfr.insert({ pg : 1402, line : "excellents archers, et très braves. Et Philoktètès était couché"});
db.iliadfr.insert({ pg : 1403, line : "dans une île, en proie à des maux terribles, dans la divine"});
db.iliadfr.insert({ pg : 1404, line : "Lèmnôs, où les fils des Akhaiens le laissèrent, souffrant de la"});
db.iliadfr.insert({ pg : 1405, line : "mauvaise blessure d'un serpent venimeux. C'est là qu'il gisait,"});
db.iliadfr.insert({ pg : 1406, line : "plein de tristesse. Mais les Argiens devaient bientôt se souvenir,"});
db.iliadfr.insert({ pg : 1407, line : "dans leurs nefs, du roi Philoktètès. Et ses guerriers n'étaient"});
db.iliadfr.insert({ pg : 1408, line : "point sans chef, s'ils regrettaient celui-là. Et Médôn les"});
db.iliadfr.insert({ pg : 1409, line : "commandait, et il était fils du brave Oileus, de qui Rhènè l'avait"});
db.iliadfr.insert({ pg : 1410, line : "conçu."});
db.iliadfr.insert({ pg : 1412, line : "Et ceux qui habitaient Trikkè et la montueuse Ithomè, et Oikhaliè,"});
db.iliadfr.insert({ pg : 1413, line : "ville d'Eurytos Oikhalien, étaient commandés par les deux fils"});
db.iliadfr.insert({ pg : 1414, line : "d'Asklèpios, Podaleirios et Makhaôn. Et ils étaient venus sur"});
db.iliadfr.insert({ pg : 1415, line : "trente nefs creuses."});
db.iliadfr.insert({ pg : 1417, line : "Et ceux qui habitaient Orménios et la fontaine Hypéréia, et"});
db.iliadfr.insert({ pg : 1418, line : "Astériôn, et les cimes neigeuses du Titanos, étaient commandés par"});
db.iliadfr.insert({ pg : 1419, line : "Eurypylos, illustre fils d'Évaimôn. Et ils étaient venus sur"});
db.iliadfr.insert({ pg : 1420, line : "quarante nefs noires."});
db.iliadfr.insert({ pg : 1422, line : "Et ceux qui habitaient Argissa et Gyrtônè, Orthè et Élonè, et la"});
db.iliadfr.insert({ pg : 1423, line : "blanche Oloossôn, étaient commandés par le belliqueux Polypoitès,"});
db.iliadfr.insert({ pg : 1424, line : "fils de Peirithoos qu'engendra l'éternel Zeus. Et l'illustre"});
db.iliadfr.insert({ pg : 1425, line : "Hippodaméia le donna pour fils à Peirithoos le jour où celui-ci"});
db.iliadfr.insert({ pg : 1426, line : "dompta les centaures féroces et les chassa du Pèliôn jusqu'aux"});
db.iliadfr.insert({ pg : 1427, line : "monts Aithiens. Et Polypoitès ne commandait point seul, mais avec"});
db.iliadfr.insert({ pg : 1428, line : "Léonteus, nourrisson d'Arès, et fils du magnanime Koronos"});
db.iliadfr.insert({ pg : 1429, line : "Kainéide. Et ils étaient venus sur quarante nefs noires."});
db.iliadfr.insert({ pg : 1431, line : "Et Gouneus avait amené de Kyphos, sur vingt-deux nefs, les Éniènes"});
db.iliadfr.insert({ pg : 1432, line : "et les braves Péraibes qui habitaient la froide Dôdônè, et ceux"});
db.iliadfr.insert({ pg : 1433, line : "qui habitaient les champs baignés par l'heureux Titarèsios qui"});
db.iliadfr.insert({ pg : 1434, line : "jette ses belles eaux dans le Pènéios, et ne se mêle point au"});
db.iliadfr.insert({ pg : 1435, line : "Pènéios aux tourbillons d'argent, mais coule à sa surface comme de"});
db.iliadfr.insert({ pg : 1436, line : "l'huile. Et sa source est Styx par qui jurent les dieux."});
db.iliadfr.insert({ pg : 1438, line : "Et Prothoos, fils de Tenthrèdôn, commandait les Magnètes qui"});
db.iliadfr.insert({ pg : 1439, line : "habitaient auprès du Pènéios et du Pèliôn aux forêts secouées par"});
db.iliadfr.insert({ pg : 1440, line : "le vent. Et l'agile Prothoos les commandait, et ils étaient venus"});
db.iliadfr.insert({ pg : 1441, line : "sur quarante nefs noires."});
db.iliadfr.insert({ pg : 1443, line : "Et tels étaient les rois et les chefs des Danaens."});
db.iliadfr.insert({ pg : 1445, line : "Dis-moi, Muse, quel était le plus brave, et qui avait les"});
db.iliadfr.insert({ pg : 1446, line : "meilleurs chevaux parmi ceux qui avaient suivi les Atréides."});
db.iliadfr.insert({ pg : 1448, line : "Les meilleurs chevaux étaient ceux du Phèrètiade Eumèlos. Et ils"});
db.iliadfr.insert({ pg : 1449, line : "étaient rapides comme les oiseaux, du même poil, du même âge et de"});
db.iliadfr.insert({ pg : 1450, line : "la même taille. Apollôn à l'arc d'argent éleva et nourrit sur le"});
db.iliadfr.insert({ pg : 1451, line : "mont Piérè ces cavales qui portaient la terreur d'Arès. Et le plus"});
db.iliadfr.insert({ pg : 1452, line : "brave des guerriers était Aias Télamônien, depuis qu'Akhilleus se"});
db.iliadfr.insert({ pg : 1453, line : "livrait à sa colère; car celui-ci était de beaucoup le plus fort,"});
db.iliadfr.insert({ pg : 1454, line : "et les chevaux qui traînaient l'irréprochable Pèléiôn étaient de"});
db.iliadfr.insert({ pg : 1455, line : "beaucoup les meilleurs. Mais voici qu'il était couché dans sa nef"});
db.iliadfr.insert({ pg : 1456, line : "éperonnée, couvant sa fureur contre Agamemnôn. Et ses guerriers,"});
db.iliadfr.insert({ pg : 1457, line : "sur le rivage de la mer, lançaient pacifiquement le disque, la"});
db.iliadfr.insert({ pg : 1458, line : "pique ou la flèche; et les chevaux, auprès des chars, broyaient le"});
db.iliadfr.insert({ pg : 1459, line : "lotos et le sélinos des marais; et les chars solides restaient"});
db.iliadfr.insert({ pg : 1460, line : "sous les tentes des chefs; et ceux-ci, regrettant leur roi cher à"});
db.iliadfr.insert({ pg : 1461, line : "Arès, erraient à travers le camp et ne combattaient point."});
db.iliadfr.insert({ pg : 1463, line : "Et les Akhaiens roulaient sur la terre comme un incendie; et la"});
db.iliadfr.insert({ pg : 1464, line : "terre mugissait comme lorsque Zeus tonnant la fouette à coups de"});
db.iliadfr.insert({ pg : 1465, line : "foudre autour des rochers Arimiens où l'on dit que Typhôeus est"});
db.iliadfr.insert({ pg : 1466, line : "couché. Ainsi la terre rendait un grand mugissement sous les pieds"});
db.iliadfr.insert({ pg : 1467, line : "des Akhaiens qui franchissaient rapidement la plaine."});
db.iliadfr.insert({ pg : 1469, line : "Et la légère Iris, qui va comme le vent, envoyée de Zeus"});
db.iliadfr.insert({ pg : 1470, line : "tempêtueux, vint annoncer aux Troiens la nouvelle effrayante. Et"});
db.iliadfr.insert({ pg : 1471, line : "ils étaient réunis, jeunes et vieux, à l'agora, devant les"});
db.iliadfr.insert({ pg : 1472, line : "vestibules de Priamos. Et la légère Iris s'approcha, semblable par"});
db.iliadfr.insert({ pg : 1473, line : "le visage et la voix à Politès Priamide, qui, se fiant à la"});
db.iliadfr.insert({ pg : 1474, line : "rapidité de sa course, s'était assis sur la haute tombe du vieux"});
db.iliadfr.insert({ pg : 1475, line : "Aisyètas, pour observer le moment où les Akhaiens se"});
db.iliadfr.insert({ pg : 1476, line : "précipiteraient hors des nefs."});
db.iliadfr.insert({ pg : 1478, line : "Et la légère Iris, étant semblable à lui, parla ainsi:"});
db.iliadfr.insert({ pg : 1480, line : "-- Ô vieillard! tu te plais aux paroles sans fin, comme autrefois,"});
db.iliadfr.insert({ pg : 1481, line : "du temps de la paix; mais voici qu'une bataille inévitable se"});
db.iliadfr.insert({ pg : 1482, line : "prépare. Certes, j'ai vu un grand nombre de combats, mais je n'ai"});
db.iliadfr.insert({ pg : 1483, line : "point encore vu une armée aussi formidable et aussi innombrable."});
db.iliadfr.insert({ pg : 1484, line : "Elle est pareille aux feuilles et aux grains de sable; et voici"});
db.iliadfr.insert({ pg : 1485, line : "qu'elle vient, à travers la plaine, combattre autour de la ville."});
db.iliadfr.insert({ pg : 1486, line : "Hektôr, c'est à toi d'agir. Il y a de nombreux alliés dans la"});
db.iliadfr.insert({ pg : 1487, line : "grande ville de Priamos, de races et de langues diverses. Que"});
db.iliadfr.insert({ pg : 1488, line : "chaque chef arme les siens et les mène au combat."});
db.iliadfr.insert({ pg : 1490, line : "Elle parla ainsi, et Hektôr reconnut sa voix, et il rompit"});
db.iliadfr.insert({ pg : 1491, line : "l'agora, et tous coururent aux armes. Et les portes s'ouvrirent,"});
db.iliadfr.insert({ pg : 1492, line : "et la foule des hommes, fantassins et cavaliers, en sortit à grand"});
db.iliadfr.insert({ pg : 1493, line : "bruit. Et il y avait, en avant de la ville, une haute colline qui"});
db.iliadfr.insert({ pg : 1494, line : "s'inclinait de tous côtés dans la plaine; et les hommes la"});
db.iliadfr.insert({ pg : 1495, line : "nommaient Batéia, et les immortels, le tombeau de l'agile Myrinnè."});
db.iliadfr.insert({ pg : 1496, line : "Là, se rangèrent les Troiens et les alliés."});
db.iliadfr.insert({ pg : 1498, line : "Et le grand Hektôr Priamide au beau casque commandait les Troiens,"});
db.iliadfr.insert({ pg : 1499, line : "et il était suivi d'hommes nombreux et braves qui désiraient"});
db.iliadfr.insert({ pg : 1500, line : "frapper de la pique."});
db.iliadfr.insert({ pg : 1502, line : "Et le vaillant fils d'Ankhisès, Ainéias, commandait les"});
db.iliadfr.insert({ pg : 1503, line : "Dardaniens. Et la divine Aphroditè l'avait donné pour fils à"});
db.iliadfr.insert({ pg : 1504, line : "Ankhisès, s'étant unie à un mortel, quoique déesse, sur les cîmes"});
db.iliadfr.insert({ pg : 1505, line : "de l'Ida. Et il ne commandait point seul; mais les deux"});
db.iliadfr.insert({ pg : 1506, line : "Anténorides l'accompagnaient, Arkhilokhos et Akamas, habiles à"});
db.iliadfr.insert({ pg : 1507, line : "tous les combats."});
db.iliadfr.insert({ pg : 1509, line : "Et ceux qui habitaient Zéléia, aux pieds de la dernière chaîne de"});
db.iliadfr.insert({ pg : 1510, line : "l'Ida, les riches Troadiens qui boivent l'eau profonde de"});
db.iliadfr.insert({ pg : 1511, line : "l'Aisèpos, étaient commandés par l'illustre fils de Lykaôn,"});
db.iliadfr.insert({ pg : 1512, line : "Pandaros, à qui Apollôn lui-même avait donné son arc."});
db.iliadfr.insert({ pg : 1514, line : "Et ceux qui habitaient Adrèstéia et Apeisos, et Pithyéia et les"});
db.iliadfr.insert({ pg : 1515, line : "hauteurs de Tèréiè, étaient commandés par Adrèstos et par Amphios"});
db.iliadfr.insert({ pg : 1516, line : "à la cuirasse de lin. Et ils étaient tous deux fils de Mérops, le"});
db.iliadfr.insert({ pg : 1517, line : "Perkôsien, qui, n'ayant point d'égal dans la science divinatoire,"});
db.iliadfr.insert({ pg : 1518, line : "leur défendit de tenter la guerre qui dévore les hommes; mais ils"});
db.iliadfr.insert({ pg : 1519, line : "ne lui obéirent point, parce que les kères de la noire mort les"});
db.iliadfr.insert({ pg : 1520, line : "entraînaient."});
db.iliadfr.insert({ pg : 1522, line : "Et ceux qui habitaient Perkôtè et Praktios, et Sèstos et Abydos,"});
db.iliadfr.insert({ pg : 1523, line : "et la divine Arisbè, étaient commandés par Asios Hyrtakide, que"});
db.iliadfr.insert({ pg : 1524, line : "des chevaux grands et ardents avaient amené des bords du fleuve"});
db.iliadfr.insert({ pg : 1525, line : "Sellèis."});
db.iliadfr.insert({ pg : 1527, line : "Et les tribus Pélasgiques habiles à lancer la pique, et ceux qui"});
db.iliadfr.insert({ pg : 1528, line : "habitaient Larissa aux plaines fertiles, étaient commandés par"});
db.iliadfr.insert({ pg : 1529, line : "Hippothoos et Pyleus, nourrissons d'Arès, fils du Pélasge Lèthos"});
db.iliadfr.insert({ pg : 1530, line : "Teutamide."});
db.iliadfr.insert({ pg : 1532, line : "Et Akamas commandait les Thrakiens, et le héros Peirôs ceux"});
db.iliadfr.insert({ pg : 1533, line : "qu'enferme le Hellespontos rapide."});
db.iliadfr.insert({ pg : 1535, line : "Et Euphèmos commandait les braves Kikoniens, et il était fils de"});
db.iliadfr.insert({ pg : 1536, line : "Troizènos Kéade, cher à Zeus."});
db.iliadfr.insert({ pg : 1538, line : "Et Pyraikhmès commandait les archers Paiones, venus de la terre"});
db.iliadfr.insert({ pg : 1539, line : "lointaine d'Amydôn et du large Axios qui répand ses belles eaux"});
db.iliadfr.insert({ pg : 1540, line : "sur la terre."});
db.iliadfr.insert({ pg : 1542, line : "Et le brave Pylaiméneus commandait les Paphlagones, du pays des"});
db.iliadfr.insert({ pg : 1543, line : "Énètiens, où naissent les mules sauvages. Et ils habitaient aussi"});
db.iliadfr.insert({ pg : 1544, line : "Kytôros et Sésamos, et les belles villes du fleuve Parthénios, et"});
db.iliadfr.insert({ pg : 1545, line : "Krômna, et Aigialos et la haute Érythinos."});
db.iliadfr.insert({ pg : 1547, line : "Et Dios et Épistrophos commandaient les Halizônes, venus de la"});
db.iliadfr.insert({ pg : 1548, line : "lointaine Alybè, où germe l'argent."});
db.iliadfr.insert({ pg : 1550, line : "Et Khromis et le divinateur Eunomos commandaient les Mysiens. Mais"});
db.iliadfr.insert({ pg : 1551, line : "Eunomos ne devina point la noire mort, et il devait tomber sous la"});
db.iliadfr.insert({ pg : 1552, line : "main du rapide Aiakide, dans le fleuve où celui-ci devait tuer"});
db.iliadfr.insert({ pg : 1553, line : "tant de Troiens."});
db.iliadfr.insert({ pg : 1555, line : "Et Phorkys commandait les Phrygiens, avec Askanios pareil à un"});
db.iliadfr.insert({ pg : 1556, line : "dieu. Et ils étaient venus d'Askaniè, désirant le combat."});
db.iliadfr.insert({ pg : 1558, line : "Et Mesthlès et Antiphos, fils de Pylaiméneus, nés sur les bords du"});
db.iliadfr.insert({ pg : 1559, line : "lac de Gygéia, commandaient les Maiones qui habitent aux pieds du"});
db.iliadfr.insert({ pg : 1560, line : "Tmôlos."});
db.iliadfr.insert({ pg : 1562, line : "Et Nastès commandait les Kariens au langage barbare qui habitaient"});
db.iliadfr.insert({ pg : 1563, line : "Milètos et les hauteurs Phthiriennes, et les bords du Maiandros ét"});
db.iliadfr.insert({ pg : 1564, line : "les cimes de Mykalè. Et Amphimakhos et Nastès les commandaient, et"});
db.iliadfr.insert({ pg : 1565, line : "ils étaient les fils illustres de Nomiôn. Et Amphimakhos"});
db.iliadfr.insert({ pg : 1566, line : "combattait chargé d'or comme une femme, et ceci ne lui fit point"});
db.iliadfr.insert({ pg : 1567, line : "éviter la noire mort, le malheureux! Car il devait tomber sous la"});
db.iliadfr.insert({ pg : 1568, line : "main du rapide Aiakide, dans le fleuve, et le brave Akhilleus"});
db.iliadfr.insert({ pg : 1569, line : "devait enlever son or."});
db.iliadfr.insert({ pg : 1571, line : "Et l'irréprochable Sarpèdôn commandait les Lykiens, avec"});
db.iliadfr.insert({ pg : 1572, line : "l'irréprochable Glaukos. Et ils étaient venus de la lointaine"});
db.iliadfr.insert({ pg : 1573, line : "Lykiè et du Xanthos plein de tourbillons."});
db.iliadfr.insert({ pg : 1576, line : "Chant 3"});
db.iliadfr.insert({ pg : 1578, line : "Quand tous, de chaque côté, se furent rangés sous leurs chefs, les"});
db.iliadfr.insert({ pg : 1579, line : "Troiens s'avancèrent, pleins de clameurs et de bruit, comme des"});
db.iliadfr.insert({ pg : 1580, line : "oiseaux. Ainsi, le cri des grues monte dans l'air, quand, fuyant"});
db.iliadfr.insert({ pg : 1581, line : "l'hiver et les pluies abondantes, elles volent sur les flots"});
db.iliadfr.insert({ pg : 1582, line : "d'Okéanos, portant le massacre et la kèr de la mort aux Pygmées."});
db.iliadfr.insert({ pg : 1583, line : "Et elles livrent dans l'air un rude combat. Mais les Akhaiens"});
db.iliadfr.insert({ pg : 1584, line : "allaient en silence, respirant la force, et, dans leur coeur,"});
db.iliadfr.insert({ pg : 1585, line : "désirant s'entre aider. Comme le Notos enveloppe les hauteurs de"});
db.iliadfr.insert({ pg : 1586, line : "la montagne d'un brouillard odieux au berger et plus propice au"});
db.iliadfr.insert({ pg : 1587, line : "voleur que la nuit même, de sorte qu'on ne peut voir au-delà d'une"});
db.iliadfr.insert({ pg : 1588, line : "pierre qu'on a jetée; de même une noire poussière montait sous les"});
db.iliadfr.insert({ pg : 1589, line : "pieds de ceux qui marchaient, et ils traversaient rapidement la"});
db.iliadfr.insert({ pg : 1590, line : "plaine."});
db.iliadfr.insert({ pg : 1592, line : "Et quand ils furent proches les uns des autres, le divin"});
db.iliadfr.insert({ pg : 1593, line : "Alexandros apparut en tête des Troiens, ayant une peau de léopard"});
db.iliadfr.insert({ pg : 1594, line : "sur les épaules, et l'arc recourbé et l'épée. Et, agitant deux"});
db.iliadfr.insert({ pg : 1595, line : "piques d'airain, il appelait les plus braves des Argiens à"});
db.iliadfr.insert({ pg : 1596, line : "combattre un rude combat. Et dès que Ménélaos, cher à Arès, l'eut"});
db.iliadfr.insert({ pg : 1597, line : "aperçu qui devançait l'armée et qui marchait à grands pas, comme"});
db.iliadfr.insert({ pg : 1598, line : "un lion se réjouit, quand il a faim, de rencontrer un cerf cornu"});
db.iliadfr.insert({ pg : 1599, line : "ou une chèvre sauvage, et dévore sa proie, bien que les chiens"});
db.iliadfr.insert({ pg : 1600, line : "agiles et les ardents jeunes hommes le poursuivent, de même"});
db.iliadfr.insert({ pg : 1601, line : "Ménélaos se réjouit quand il vit devant lui le divin Alexandros."});
db.iliadfr.insert({ pg : 1602, line : "Et il espéra se venger de celui qui l'avait outragé, et il sauta"});
db.iliadfr.insert({ pg : 1603, line : "du char avec ses armes."});
db.iliadfr.insert({ pg : 1605, line : "Et dès que le divin Alexandros l'eut aperçu en tête de l'armée,"});
db.iliadfr.insert({ pg : 1606, line : "son coeur se serra, et il recula parmi les siens pour éviter la"});
db.iliadfr.insert({ pg : 1607, line : "kèr de la mort. Si quelqu'un, dans les gorges des montagnes, voit"});
db.iliadfr.insert({ pg : 1608, line : "un serpent, il saute en arrière, et ses genoux tremblent, et ses"});
db.iliadfr.insert({ pg : 1609, line : "joues pâlissent. De même le divin Alexandros, craignant le fils"});
db.iliadfr.insert({ pg : 1610, line : "d'Atreus, rentra dans la foule des hardis Troiens."});
db.iliadfr.insert({ pg : 1612, line : "Et Hektôr, l'ayant vu, l'accabla de paroles amères:"});
db.iliadfr.insert({ pg : 1614, line : "-- Misérable Pâris, qui n'as que ta beauté, trompeur et efféminé,"});
db.iliadfr.insert({ pg : 1615, line : "plût aux dieux que tu ne fusses point né, ou que tu fusses mort"});
db.iliadfr.insert({ pg : 1616, line : "avant tes dernières noces! Certes, cela eût mieux valu de"});
db.iliadfr.insert({ pg : 1617, line : "beaucoup, plutôt que d'être l'opprobre et la risée de tous! Voici"});
db.iliadfr.insert({ pg : 1618, line : "que les Akhaiens chevelus rient de mépris, car ils croyaient que"});
db.iliadfr.insert({ pg : 1619, line : "tu combattais hardiment hors des rangs, parce que ton visage est"});
db.iliadfr.insert({ pg : 1620, line : "beau; mais il n'y a dans ton coeur ni force ni courage. Pourquoi,"});
db.iliadfr.insert({ pg : 1621, line : "étant un lâche, as-tu traversé la mer sur tes nefs rapides, avec"});
db.iliadfr.insert({ pg : 1622, line : "tes meilleurs compagnons, et, mêlé à des étrangers, as-tu enlevé"});
db.iliadfr.insert({ pg : 1623, line : "une très belle jeune femme du pays d'Apy, parente d'hommes"});
db.iliadfr.insert({ pg : 1624, line : "belliqueux? Immense malheur pour ton père, pour ta ville et pour"});
db.iliadfr.insert({ pg : 1625, line : "tout le peuple; joie pour nos ennemis et honte pour toi-même! Et"});
db.iliadfr.insert({ pg : 1626, line : "tu n'as point osé attendre Ménélaos, cher à Arès. Tu saurais"});
db.iliadfr.insert({ pg : 1627, line : "maintenant de quel guerrier tu retiens la femme. Ni ta kithare, ni"});
db.iliadfr.insert({ pg : 1628, line : "les dons d'Aphrodite, ta chevelure et ta beauté, ne t'auraient"});
db.iliadfr.insert({ pg : 1629, line : "sauvé d'être traîné dans la poussière. Mais les Troiens ont trop"});
db.iliadfr.insert({ pg : 1630, line : "de respect, car autrement, tu serais déjà revêtu d'une tunique de"});
db.iliadfr.insert({ pg : 1631, line : "pierres, pour prix des maux que tu as causés."});
db.iliadfr.insert({ pg : 1633, line : "Et le divin Alexandros lui répondit:"});
db.iliadfr.insert({ pg : 1635, line : "-- Hektôr, tu m'as réprimandé justement. Ton coeur est toujours"});
db.iliadfr.insert({ pg : 1636, line : "indompté, comme la hache qui fend le bois et accroît la force de"});
db.iliadfr.insert({ pg : 1637, line : "l'ouvrier constructeur de nefs. Telle est l'âme indomptée qui est"});
db.iliadfr.insert({ pg : 1638, line : "dans ta poitrine. Ne me reproche point les dons aimables"});
db.iliadfr.insert({ pg : 1639, line : "d'Aphrodite d'or. Il ne faut point rejeter les dons glorieux des"});
db.iliadfr.insert({ pg : 1640, line : "dieux, car eux seuls en disposent, et nul ne les pourrait prendre"});
db.iliadfr.insert({ pg : 1641, line : "à son gré. Mais si tu veux maintenant que je combatte et que je"});
db.iliadfr.insert({ pg : 1642, line : "lutte, arrête les Troiens et les Akhaiens, afin que nous"});
db.iliadfr.insert({ pg : 1643, line : "combattions moi et Ménélaos, cher à Arès, au milieu de tous, pour"});
db.iliadfr.insert({ pg : 1644, line : "Hélénè et pour toutes ses richesses. Et le vainqueur emportera"});
db.iliadfr.insert({ pg : 1645, line : "cette femme et toutes ses richesses, et, après avoir échangé des"});
db.iliadfr.insert({ pg : 1646, line : "serments inviolables, vous, Troiens, habiterez la féconde Troiè,"});
db.iliadfr.insert({ pg : 1647, line : "et les Akhaiens retourneront dans Argos, nourrice de chevaux, et"});
db.iliadfr.insert({ pg : 1648, line : "dans l'Akhaiè aux belles femmes."});
db.iliadfr.insert({ pg : 1650, line : "Il parla ainsi, et Hektôr en eut une grande joie, et il s'avança,"});
db.iliadfr.insert({ pg : 1651, line : "arrêtant les phalanges des Troiens, à l'aide de sa pique qu'il"});
db.iliadfr.insert({ pg : 1652, line : "tenait par le milieu. Et ils s'arrêtèrent. Et les Akhaiens"});
db.iliadfr.insert({ pg : 1653, line : "chevelus tiraient sur lui et le frappaient de flèches et de"});
db.iliadfr.insert({ pg : 1654, line : "pierres. Mais le roi des hommes, Agamemnôn, cria à voix haute:"});
db.iliadfr.insert({ pg : 1656, line : "-- Arrêtez, Argiens! ne frappez point, fils des Akhaiens! Hektôr"});
db.iliadfr.insert({ pg : 1657, line : "au casque mouvant semble vouloir dire quelques mots."});
db.iliadfr.insert({ pg : 1659, line : "Il parla ainsi, et ils cessèrent et firent silence, et Hektôr"});
db.iliadfr.insert({ pg : 1660, line : "parla au milieu d'eux:"});
db.iliadfr.insert({ pg : 1662, line : "-- Ecoutez, Troiens et Akhaiens, ce que dit Alexandros qui causa"});
db.iliadfr.insert({ pg : 1663, line : "cette guerre. Il désire que les Troiens et les Akhaiens déposent"});
db.iliadfr.insert({ pg : 1664, line : "leurs belles armes sur la terre nourricière, et que lui et"});
db.iliadfr.insert({ pg : 1665, line : "Ménélaos, cher à Arès, combattent, seuls, au milieu de tous, pour"});
db.iliadfr.insert({ pg : 1666, line : "Hélénè et pour toutes ses richesses. Et le vainqueur emportera"});
db.iliadfr.insert({ pg : 1667, line : "cette femme et toutes ses richesses, et nous échangerons des"});
db.iliadfr.insert({ pg : 1668, line : "serments inviolables."});
db.iliadfr.insert({ pg : 1670, line : "Il parla ainsi, et tous restèrent silencieux. Et Ménélaos, hardi"});
db.iliadfr.insert({ pg : 1671, line : "au combat, leur dit:"});
db.iliadfr.insert({ pg : 1673, line : "-- Ecoutez-moi maintenant. Une grande douleur serre mon coeur, et"});
db.iliadfr.insert({ pg : 1674, line : "j'espère que les Argiens et les Troiens vont cesser la guerre, car"});
db.iliadfr.insert({ pg : 1675, line : "vous avez subi des maux infinis pour ma querelle et pour l'injure"});
db.iliadfr.insert({ pg : 1676, line : "que m'a faite Alexandros. Que celui des deux à qui sont réservées"});
db.iliadfr.insert({ pg : 1677, line : "la moire et la mort, meure donc; et vous, cessez aussitôt de"});
db.iliadfr.insert({ pg : 1678, line : "combattre. Apportez un agneau noir pour Gaia et un agneau blanc"});
db.iliadfr.insert({ pg : 1679, line : "pour Hélios, et nous en apporterons autant pour Zeus. Et vous"});
db.iliadfr.insert({ pg : 1680, line : "amènerez Priamos lui-même, pour qu'il se lie par des serments, car"});
db.iliadfr.insert({ pg : 1681, line : "ses enfants sont parjures et sans foi, et que personne ne puisse"});
db.iliadfr.insert({ pg : 1682, line : "violer les serments de Zeus. L'esprit des jeunes hommes est léger,"});
db.iliadfr.insert({ pg : 1683, line : "mais, dans ses actions, le vieillard regarde à la fois l'avenir et"});
db.iliadfr.insert({ pg : 1684, line : "le passé et agit avec équité."});
db.iliadfr.insert({ pg : 1686, line : "Il parla ainsi, et les Troiens et les Akhaiens se réjouirent,"});
db.iliadfr.insert({ pg : 1687, line : "espérant mettre fin à la guerre mauvaise. Et ils retinrent les"});
db.iliadfr.insert({ pg : 1688, line : "chevaux dans les rangs, et ils se dépouillèrent de leurs armes"});
db.iliadfr.insert({ pg : 1689, line : "déposées sur la terre. Et il y avait peu d'espace entre les deux"});
db.iliadfr.insert({ pg : 1690, line : "armées. Et Hektôr envoya deux hérauts à la ville pour apporter"});
db.iliadfr.insert({ pg : 1691, line : "deux agneaux et appeler Priamos. Et le roi Agamemnôn envoya"});
db.iliadfr.insert({ pg : 1692, line : "Talthybios aux nefs creuses pour y prendre un agneau, et"});
db.iliadfr.insert({ pg : 1693, line : "Talthybios obéit au divin Agamemnôn."});
db.iliadfr.insert({ pg : 1695, line : "Et la messagère Iris s'envola chez Hélénè aux bras blancs, s'étant"});
db.iliadfr.insert({ pg : 1696, line : "faite semblable à sa belle-soeur Laodikè, la plus belle des filles"});
db.iliadfr.insert({ pg : 1697, line : "de Priamos, et qu'avait épousée l'Anténoride Élikaôn."});
db.iliadfr.insert({ pg : 1699, line : "Et elle trouva Hélénè dans sa demeure, tissant une grande toile"});
db.iliadfr.insert({ pg : 1700, line : "double, blanche comme le marbre, et y retraçant les nombreuses"});
db.iliadfr.insert({ pg : 1701, line : "batailles que les Troiens dompteurs de chevaux et les Akhaiens"});
db.iliadfr.insert({ pg : 1702, line : "revêtus d'airain avaient subies pour elle par les mains d'Arès. Et"});
db.iliadfr.insert({ pg : 1703, line : "Iris aux pieds légers, s'étant approchée, lui dit:"});
db.iliadfr.insert({ pg : 1705, line : "-- Viens, chère nymphe, voir le spectacle admirable des Troiens"});
db.iliadfr.insert({ pg : 1706, line : "dompteurs de chevaux et des Akhaiens revêtus d'airain. Ils"});
db.iliadfr.insert({ pg : 1707, line : "combattaient tantôt dans la plaine, pleins de la fureur d'Arès, et"});
db.iliadfr.insert({ pg : 1708, line : "les voici maintenant assis en silence, appuyés sur leurs"});
db.iliadfr.insert({ pg : 1709, line : "boucliers, et la guerre a cessé, et les piques sont enfoncées en"});
db.iliadfr.insert({ pg : 1710, line : "terre. Alexandros et Ménélaos cher à Arès combattront pour toi, de"});
db.iliadfr.insert({ pg : 1711, line : "leurs longues piques, et tu seras l'épouse bien-aimée du"});
db.iliadfr.insert({ pg : 1712, line : "vainqueur."});
db.iliadfr.insert({ pg : 1714, line : "Et la déesse, ayant ainsi parlé, jeta dans son coeur un doux"});
db.iliadfr.insert({ pg : 1715, line : "souvenir de son premier mari, et de son pays, et de ses parents."});
db.iliadfr.insert({ pg : 1716, line : "Et Hélénè, s'étant couverte aussitôt de voiles blancs, sortit de"});
db.iliadfr.insert({ pg : 1717, line : "la chambre nuptiale en pleurant; et deux femmes la suivaient,"});
db.iliadfr.insert({ pg : 1718, line : "Aithrè, fille de Pittheus, et Klyménè aux yeux de boeuf. Et voici"});
db.iliadfr.insert({ pg : 1719, line : "qu'elles arrivèrent aux portes Skaies. Priamos, Panthoos,"});
db.iliadfr.insert({ pg : 1720, line : "Thymoitès, Lampos, Klytios, lbkétaôn, nourrisson d'Arès, Oukalégôn"});
db.iliadfr.insert({ pg : 1721, line : "et Antènôr, très sages tous deux, siégeaient, vieillards"});
db.iliadfr.insert({ pg : 1722, line : "vénérables, au-dessus des portes Skaies. Et la vieillesse les"});
db.iliadfr.insert({ pg : 1723, line : "écartait de la guerre; mais c'étaient d'excellents agorètes; et"});
db.iliadfr.insert({ pg : 1724, line : "ils étaient pareils à des cigales qui, dans les bois, assises sur"});
db.iliadfr.insert({ pg : 1725, line : "un arbre, élèvent leur voix mélodieuse. Tels étaient les princes"});
db.iliadfr.insert({ pg : 1726, line : "des Troiens, assis sur la tour. Et quand ils virent Hélénè qui"});
db.iliadfr.insert({ pg : 1727, line : "montait vers eux, ils se dirent les uns aux autres, et à voix"});
db.iliadfr.insert({ pg : 1728, line : "basse, ces paroles ailées:"});
db.iliadfr.insert({ pg : 1730, line : "Certes, il est juste que les Troiens et les Akhaiens aux belles"});
db.iliadfr.insert({ pg : 1731, line : "knèmides subissent tant de maux, et depuis si longtemps, pour une"});
db.iliadfr.insert({ pg : 1732, line : "telle femme, car elle ressemble aux déesses immortelles par sa"});
db.iliadfr.insert({ pg : 1733, line : "beauté. Mais, malgré cela, qu'elle s'en retourne sur ses nefs, et"});
db.iliadfr.insert({ pg : 1734, line : "qu'elle ne nous laisse point, à nous et à nos enfants, un souvenir"});
db.iliadfr.insert({ pg : 1735, line : "misérable."});
db.iliadfr.insert({ pg : 1737, line : "Ils parlaient ainsi, et Priamos appela Hélénè:"});
db.iliadfr.insert({ pg : 1739, line : "-- Viens, chère enfant, approche, assieds-toi auprès de moi, afin"});
db.iliadfr.insert({ pg : 1740, line : "de revoir ton premier mari, et tes parents, et tes amis. Tu n'es"});
db.iliadfr.insert({ pg : 1741, line : "point la cause de nos malheurs. Ce sont les dieux seuls qui m'ont"});
db.iliadfr.insert({ pg : 1742, line : "accablé de cette rude guerre Akhaienne. Dis-moi le nom de ce"});
db.iliadfr.insert({ pg : 1743, line : "guerrier d'une haute stature; quel est cet Akhaien grand et"});
db.iliadfr.insert({ pg : 1744, line : "vigoureux? D'autres ont une taille plus élevée, mais je n'ai"});
db.iliadfr.insert({ pg : 1745, line : "jamais vu de mes yeux un homme aussi beau et majestueux. Il a"});
db.iliadfr.insert({ pg : 1746, line : "l'aspect d'un roi."});
db.iliadfr.insert({ pg : 1748, line : "Et Hélénè, la divine femme, lui répondit:"});
db.iliadfr.insert({ pg : 1750, line : "-- Tu m'es vénérable et redoutable, père bien-aimé. Que n'ai-je"});
db.iliadfr.insert({ pg : 1751, line : "subi la noire mort quand j'ai suivi ton fils, abandonnant ma"});
db.iliadfr.insert({ pg : 1752, line : "chambre nuptiale et ma fille née en mon pays lointain, et mes"});
db.iliadfr.insert({ pg : 1753, line : "frères, et les chères compagnes de ma jeunesse! Mais telle n'a"});
db.iliadfr.insert({ pg : 1754, line : "point été ma destinée, et c'est pour cela que je me consume en"});
db.iliadfr.insert({ pg : 1755, line : "pleurant. Je te dirai ce que tu m'as demandé. Cet homme est le roi"});
db.iliadfr.insert({ pg : 1756, line : "Agamemnôn Atréide, qui commande au loin, roi habile et brave"});
db.iliadfr.insert({ pg : 1757, line : "guerrier. Et il fut mon beau-frère, à moi infâme, s'il m'est"});
db.iliadfr.insert({ pg : 1758, line : "permis de dire qu'il le fut."});
db.iliadfr.insert({ pg : 1760, line : "Elle parla ainsi, et le vieillard, plein d'admiration, s'écria:"});
db.iliadfr.insert({ pg : 1762, line : "-- Ô heureux Atréide, né pour d'heureuses destinées! Certes, de"});
db.iliadfr.insert({ pg : 1763, line : "nombreux fils des Akhaiens te sont soumis. Autrefois, dans la"});
db.iliadfr.insert({ pg : 1764, line : "Phrygiè féconde en vignes, j'ai vu de nombreux Phrygiens, habiles"});
db.iliadfr.insert({ pg : 1765, line : "cavaliers, tribus belliqueuses d'Otreus et de Mygdôn égal aux"});
db.iliadfr.insert({ pg : 1766, line : "dieux, et qui étaient campés sur les bords du Sangarios. Et"});
db.iliadfr.insert({ pg : 1767, line : "j'étais au milieu d'eux, étant leur allié, quand vinrent les"});
db.iliadfr.insert({ pg : 1768, line : "Amazones viriles. Mais ils n'étaient point aussi nombreux que les"});
db.iliadfr.insert({ pg : 1769, line : "Akhaiens."});
db.iliadfr.insert({ pg : 1771, line : "Puis, ayant vu Odysseus, le vieillard interrogea Hélénè:"});
db.iliadfr.insert({ pg : 1773, line : "-- Dis-moi aussi, chère enfant, qui est celui-ci. Il est moins"});
db.iliadfr.insert({ pg : 1774, line : "grand que l'Atréide Agamemnôn, mais plus large des épaules et de"});
db.iliadfr.insert({ pg : 1775, line : "la poitrine. Et ses armes sont couchées sur la terre nourricière,"});
db.iliadfr.insert({ pg : 1776, line : "et il marche, parmi les hommes, comme un bélier chargé de laine au"});
db.iliadfr.insert({ pg : 1777, line : "milieu d'un grand troupeau de brebis blanches."});
db.iliadfr.insert({ pg : 1779, line : "Et Hélénè, fille de Zeus, lui répondit:"});
db.iliadfr.insert({ pg : 1781, line : "-- Celui-ci est le subtil Laertiade Odysseus, nourri dans le pays"});
db.iliadfr.insert({ pg : 1782, line : "stérile d'Ithakè. Et il est plein de ruses et de prudence."});
db.iliadfr.insert({ pg : 1784, line : "Et le sage Antènôr lui répondit:"});
db.iliadfr.insert({ pg : 1786, line : "-- Ô femme! tu as dit une parole vraie. Le divin Odysseus vint"});
db.iliadfr.insert({ pg : 1787, line : "autrefois ici, envoyé pour toi, avec Ménélaos cher à Arès, et je"});
db.iliadfr.insert({ pg : 1788, line : "les reçus dans mes demeures, et j'ai appris à connaître leur"});
db.iliadfr.insert({ pg : 1789, line : "aspect et leur sagesse. Quand ils venaient à l'agora des Troiens,"});
db.iliadfr.insert({ pg : 1790, line : "debout, Ménélaos surpassait Odysseus des épaules, mais, assis, le"});
db.iliadfr.insert({ pg : 1791, line : "plus majestueux était Odysseus. Et quand ils haranguaient devant"});
db.iliadfr.insert({ pg : 1792, line : "tous, certes, Ménélaos, bien que le plus jeune, parlait avec force"});
db.iliadfr.insert({ pg : 1793, line : "et concision, en peu de mots, mais avec une clarté précise et"});
db.iliadfr.insert({ pg : 1794, line : "allant droit au but. Et quand le subtil Odysseus se levait, il se"});
db.iliadfr.insert({ pg : 1795, line : "tenait immobile, les yeux baissés, n'agitant le sceptre ni en"});
db.iliadfr.insert({ pg : 1796, line : "avant ni en arrière, comme un agorète inexpérimenté. On eût dit"});
db.iliadfr.insert({ pg : 1797, line : "qu'il était plein d'une sombre colère et tel qu'un insensé. Mais"});
db.iliadfr.insert({ pg : 1798, line : "quand il exhalait de sa poitrine sa voix sonore, ses paroles"});
db.iliadfr.insert({ pg : 1799, line : "pleuvaient, semblables aux neiges de l'hiver. En ce moment, nul"});
db.iliadfr.insert({ pg : 1800, line : "n'aurait osé lutter contre lui; mais, au premier aspect, nous ne"});
db.iliadfr.insert({ pg : 1801, line : "l'admirions pas autant."});
db.iliadfr.insert({ pg : 1803, line : "Ayant vu Aias, une troisième fois le vieillard interrogea Hélénè:"});
db.iliadfr.insert({ pg : 1805, line : "-- Qui est cet autre guerrier Akhaien, grand et athlétique, qui"});
db.iliadfr.insert({ pg : 1806, line : "surpasse tous les Argiens de la tête et des épaules?"});
db.iliadfr.insert({ pg : 1808, line : "Et Hélénè au long péplos, la divine femme, lui répondit:"});
db.iliadfr.insert({ pg : 1810, line : "-- Celui-ci est le grand Aias, le bouclier des Akhaiens. Et voici,"});
db.iliadfr.insert({ pg : 1811, line : "parmi les Krètois, Idoméneus tel qu'un dieu, et les princes"});
db.iliadfr.insert({ pg : 1812, line : "Krètois l'environnent. Souvent, Ménélaos cher à Arès le reçut dans"});
db.iliadfr.insert({ pg : 1813, line : "nos demeures, quand il venait de la Krètè. Et voici tous les"});
db.iliadfr.insert({ pg : 1814, line : "autres Akhaiens aux yeux noirs, et je les reconnais, et je"});
db.iliadfr.insert({ pg : 1815, line : "pourrais dire leurs noms. Mais je ne vois point les deux princes"});
db.iliadfr.insert({ pg : 1816, line : "des peuples, Kastôr dompteur de chevaux et Polydeukès invincible"});
db.iliadfr.insert({ pg : 1817, line : "au pugilat, mes propres frères, car une même mère nous a enfantés."});
db.iliadfr.insert({ pg : 1818, line : "N'auraient-ils point quitté l'heureuse Lakédaimôn, ou, s'ils sont"});
db.iliadfr.insert({ pg : 1819, line : "venus sur leurs nefs rapides, ne veulent-ils point se montrer au"});
db.iliadfr.insert({ pg : 1820, line : "milieu des hommes, à cause de ma honte et de mon opprobre?"});
db.iliadfr.insert({ pg : 1822, line : "Elle parla ainsi, mais déjà la terre féconde les renfermait, à"});
db.iliadfr.insert({ pg : 1823, line : "Lakédaimôn, dans la chère patrie."});
db.iliadfr.insert({ pg : 1825, line : "Et les hérauts, à travers la ville, portaient les gages sincères"});
db.iliadfr.insert({ pg : 1826, line : "des dieux, deux agneaux, et, dans une outre de peau de chèvre, le"});
db.iliadfr.insert({ pg : 1827, line : "vin joyeux, fruit de la terre. Et le héraut Idaios portait un"});
db.iliadfr.insert({ pg : 1828, line : "kratère étincelant et des coupes d'or; et, s'approchant, il excita"});
db.iliadfr.insert({ pg : 1829, line : "le vieillard par ces paroles:"});
db.iliadfr.insert({ pg : 1831, line : "-- Lève-toi, Laomédontiade! Les princes des Troiens dompteurs de"});
db.iliadfr.insert({ pg : 1832, line : "chevaux et des Akhaiens revêtus d'airain t'invitent à descendre"});
db.iliadfr.insert({ pg : 1833, line : "dans la plaine, afin que vous échangiez des serments inviolables."});
db.iliadfr.insert({ pg : 1834, line : "Et Alexandros et Ménélaos cher à Arès combattront pour Hélénè avec"});
db.iliadfr.insert({ pg : 1835, line : "leurs longues piques, et ses richesses appartiendront au"});
db.iliadfr.insert({ pg : 1836, line : "vainqueur. Et tous, ayant fait alliance et échangé des serments"});
db.iliadfr.insert({ pg : 1837, line : "inviolables, nous, Troiens, habiterons la féconde Troiè, et les"});
db.iliadfr.insert({ pg : 1838, line : "Akhaiens retourneront dans Argos nourrice de chevaux et dans"});
db.iliadfr.insert({ pg : 1839, line : "l'Akhaiè aux belles femmes."});
db.iliadfr.insert({ pg : 1841, line : "Il parla ainsi, et le vieillard frémit, et il ordonna à ses"});
db.iliadfr.insert({ pg : 1842, line : "compagnons d'atteler les chevaux, et ils obéirent promptement. Et"});
db.iliadfr.insert({ pg : 1843, line : "Priamos monta, tenant les rênes, et, auprès de lui, Antènôr entra"});
db.iliadfr.insert({ pg : 1844, line : "dans le beau char; et, par les portes Skaies, tous deux poussèrent"});
db.iliadfr.insert({ pg : 1845, line : "les chevaux agiles dans la plaine."});
db.iliadfr.insert({ pg : 1847, line : "Et quand ils furent arrivés au milieu des Troiens et des Akhaiens,"});
db.iliadfr.insert({ pg : 1848, line : "ils descendirent du char sur la terre nourricière et se placèrent"});
db.iliadfr.insert({ pg : 1849, line : "au milieu des Troiens et des Akhaiens."});
db.iliadfr.insert({ pg : 1851, line : "Et, aussitôt, le roi des hommes, Agamemnôn, se leva, ainsi que le"});
db.iliadfr.insert({ pg : 1852, line : "subtil Odysseus. Puis, les hérauts vénérables réunirent les gages"});
db.iliadfr.insert({ pg : 1853, line : "sincères des dieux, mêlant le vin dans le kratère et versant de"});
db.iliadfr.insert({ pg : 1854, line : "l'eau sur les mains des rois. Et l'Atréide Agamemnôn, tirant le"});
db.iliadfr.insert({ pg : 1855, line : "couteau toujours suspendu à côté de la grande gaine de l'épée,"});
db.iliadfr.insert({ pg : 1856, line : "coupa du poil sur la tête des agneaux, et les hérauts le"});
db.iliadfr.insert({ pg : 1857, line : "distribuèrent aux princes des Troiens et des Akhaiens. Et, au"});
db.iliadfr.insert({ pg : 1858, line : "milieu d'eux, l'Atréide pria, à haute voix, les mains étendues:"});
db.iliadfr.insert({ pg : 1860, line : "-- Père Zeus, qui commandes du haut de l'Ida, très glorieux, très"});
db.iliadfr.insert({ pg : 1861, line : "grand! Hélios, qui vois et entends tout! fleuves et Gaia! et vous"});
db.iliadfr.insert({ pg : 1862, line : "qui, sous la terre, châtiez les parjures, soyez tous témoins,"});
db.iliadfr.insert({ pg : 1863, line : "scellez nos serments inviolables. Si Alexandros tue Ménélaos,"});
db.iliadfr.insert({ pg : 1864, line : "qu'il garde Hélénè et toutes ses richesses, et nous retournerons"});
db.iliadfr.insert({ pg : 1865, line : "sur nos nefs rapides; mais si le blond Ménélaos tue Alexandros,"});
db.iliadfr.insert({ pg : 1866, line : "que les Troiens rendent Hélénè et toutes ses richesses, et qu'ils"});
db.iliadfr.insert({ pg : 1867, line : "payent aux Argiens, comme il est juste, un tribut dont se"});
db.iliadfr.insert({ pg : 1868, line : "souviendront les hommes futurs. Mais si, Alexandros mort, Priamos"});
db.iliadfr.insert({ pg : 1869, line : "et les fils de Priamos refusaient de payer ce tribut, je resterai"});
db.iliadfr.insert({ pg : 1870, line : "et combattrai pour ceci, jusqu'à ce que je termine la guerre."});
db.iliadfr.insert({ pg : 1872, line : "Il parla ainsi, et, de l'airain cruel, il trancha la gorge des"});
db.iliadfr.insert({ pg : 1873, line : "agneaux et il les jeta palpitants sur la terre et rendant l'âme,"});
db.iliadfr.insert({ pg : 1874, line : "car l'airain leur avait enlevé la vie. Et tous, puisant le vin du"});
db.iliadfr.insert({ pg : 1875, line : "kratère avec des coupes, ils le répandirent et prièrent les dieux"});
db.iliadfr.insert({ pg : 1876, line : "qui vivent toujours. Et les Troiens et les Akhaiens disaient:"});
db.iliadfr.insert({ pg : 1878, line : "-- Zeus, très glorieux, très grand, et vous, dieux immortels! que"});
db.iliadfr.insert({ pg : 1879, line : "la cervelle de celui qui violera le premier ce serment, et la"});
db.iliadfr.insert({ pg : 1880, line : "cervelle de ses fils, soient répandues sur la terre comme ce vin,"});
db.iliadfr.insert({ pg : 1881, line : "et que leurs femmes soient outragées par autrui!"});
db.iliadfr.insert({ pg : 1883, line : "Mais le Kroniôn ne les exauça point. Et le Dardanide Priamos parla"});
db.iliadfr.insert({ pg : 1884, line : "et leur dit:"});
db.iliadfr.insert({ pg : 1886, line : "-- Ecoutez-moi, Troiens et Akhaiens aux belles knèmides. Je"});
db.iliadfr.insert({ pg : 1887, line : "retourne vers la hauteur d'Ilios, car je ne saurais voir de mes"});
db.iliadfr.insert({ pg : 1888, line : "yeux mon fils bien-aimé lutter contre Ménélaos cher à Arès. Zeus"});
db.iliadfr.insert({ pg : 1889, line : "et les dieux immortels savent seuls auquel des deux est réservée"});
db.iliadfr.insert({ pg : 1890, line : "la mort."});
db.iliadfr.insert({ pg : 1892, line : "Ayant ainsi parlé, le divin vieillard plaça les agneaux dans le"});
db.iliadfr.insert({ pg : 1893, line : "char, y monta, et saisit les rênes. Et Antènôr, auprès de lui,"});
db.iliadfr.insert({ pg : 1894, line : "entra dans le beau char, et ils retournèrent vers Ilios."});
db.iliadfr.insert({ pg : 1896, line : "Et le Priamide Hektôr et le divin Odysseus mesurèrent l'arène"});
db.iliadfr.insert({ pg : 1897, line : "d'abord, et remuèrent les sorts dans un casque, pour savoir qui"});
db.iliadfr.insert({ pg : 1898, line : "lancerait le premier la pique d'airain. Et les peuples priaient et"});
db.iliadfr.insert({ pg : 1899, line : "levaient les mains vers les dieux, et les Troiens et les Akhaiens"});
db.iliadfr.insert({ pg : 1900, line : "disaient:"});
db.iliadfr.insert({ pg : 1902, line : "-- Père Zeus, qui commandes au haut de l'Ida, très glorieux, très"});
db.iliadfr.insert({ pg : 1903, line : "grand! que celui qui nous a causé tant de maux descende chez"});
db.iliadfr.insert({ pg : 1904, line : "Aidès, et puissions-nous sceller une alliance et des traités"});
db.iliadfr.insert({ pg : 1905, line : "inviolables!"});
db.iliadfr.insert({ pg : 1907, line : "Ils parlèrent ainsi, et le grand Hektôr au casque mouvant agita"});
db.iliadfr.insert({ pg : 1908, line : "les sorts en détournent les yeux, et celui de Pâris sortit le"});
db.iliadfr.insert({ pg : 1909, line : "premier. Et tous s'assirent en rangs, chacun auprès de ses chevaux"});
db.iliadfr.insert({ pg : 1910, line : "agiles et de ses armes éclatantes. Et le divin Alexandros, l'époux"});
db.iliadfr.insert({ pg : 1911, line : "de Hélénè aux beaux cheveux, couvrit ses épaules de ses belles"});
db.iliadfr.insert({ pg : 1912, line : "armes. Et il mit autour de ses jambes ses belles knèmides aux"});
db.iliadfr.insert({ pg : 1913, line : "agrafes d'argent, et, sur sa poitrine, la cuirasse de son frère"});
db.iliadfr.insert({ pg : 1914, line : "Lykaôn, faite à sa taille; et il suspendit à ses épaules l'épée"});
db.iliadfr.insert({ pg : 1915, line : "d'airain aux clous d'argent. Puis il prit le bouclier vaste et"});
db.iliadfr.insert({ pg : 1916, line : "lourd, et il mit sur sa tête guerrière un riche casque orné de"});
db.iliadfr.insert({ pg : 1917, line : "crins, et ce panache s'agitait fièrement; et il saisit une forte"});
db.iliadfr.insert({ pg : 1918, line : "pique faite pour ses mains. Et le brave Ménélaos se couvrit aussi"});
db.iliadfr.insert({ pg : 1919, line : "de ses armes."});
db.iliadfr.insert({ pg : 1921, line : "Tous deux, s'étant armés, avancèrent au milieu des Troiens et des"});
db.iliadfr.insert({ pg : 1922, line : "Akhaiens, se jetant de sombres regards; et les Troiens dompteurs"});
db.iliadfr.insert({ pg : 1923, line : "de chevaux et les Akhaiens aux belles knèmides les regardaient"});
db.iliadfr.insert({ pg : 1924, line : "avec terreur. Ils s'arrêtèrent en face l'un de l'autre, agitant"});
db.iliadfr.insert({ pg : 1925, line : "les piques et pleins de fureur."});
db.iliadfr.insert({ pg : 1927, line : "Et Alexandros lança le premier sa longue pique et frappa le"});
db.iliadfr.insert({ pg : 1928, line : "bouclier poli de l'Atréide, mais il ne perça point l'airain, et la"});
db.iliadfr.insert({ pg : 1929, line : "pointe se ploya sur le dur bouclier. Et Ménélaos, levant sa pique,"});
db.iliadfr.insert({ pg : 1930, line : "supplia le père Zeus:"});
db.iliadfr.insert({ pg : 1932, line : "-- Père Zeus! fais que je punisse le divin Alexandros, qui le"});
db.iliadfr.insert({ pg : 1933, line : "premier m'a outragé, et fais qu'il tombe sous mes mains, afin que,"});
db.iliadfr.insert({ pg : 1934, line : "parmi les hommes futurs, chacun tremble d'outrager l'hôte qui"});
db.iliadfr.insert({ pg : 1935, line : "l'aura reçu avec bienveillance!"});
db.iliadfr.insert({ pg : 1937, line : "Ayant parlé ainsi, il brandit sa longue pique, et, la lançant, il"});
db.iliadfr.insert({ pg : 1938, line : "en frappa le bouclier poli du Priamide. Et la forte pique, à"});
db.iliadfr.insert({ pg : 1939, line : "travers le bouclier éclatant, perça la riche cuirasse et déchira"});
db.iliadfr.insert({ pg : 1940, line : "la tunique auprès du flanc. Et Alexandros, se courbant, évita la"});
db.iliadfr.insert({ pg : 1941, line : "noire kèr. Et l'Atréide, ayant tiré l'épée aux clous d'argent, en"});
db.iliadfr.insert({ pg : 1942, line : "frappa le cône du casque; mais l'épée, rompue en trois ou quatre"});
db.iliadfr.insert({ pg : 1943, line : "morceaux, tomba de sa main, et l'Atréide gémit en regardant le"});
db.iliadfr.insert({ pg : 1944, line : "vaste Ouranos:"});
db.iliadfr.insert({ pg : 1946, line : "-- Père Zeus! nul d'entre les dieux n'est plus inexorable que toi."});
db.iliadfr.insert({ pg : 1947, line : "Certes, j'espérais me venger de l'outrage d'Alexandros et l'épée"});
db.iliadfr.insert({ pg : 1948, line : "s'est rompue dans ma main, et la pique a été vainement lancée, et"});
db.iliadfr.insert({ pg : 1949, line : "je ne l'ai point frappé!"});
db.iliadfr.insert({ pg : 1951, line : "Il parla ainsi, et, d'un bond, il le saisit par les crins du"});
db.iliadfr.insert({ pg : 1952, line : "casque, et il le traîna vers les Akhaiens aux belles knèmides. Et"});
db.iliadfr.insert({ pg : 1953, line : "le cuir habilement orné, qui liait le casque sous le menton,"});
db.iliadfr.insert({ pg : 1954, line : "étouffait le cou délicat d'Alexandros; et l'Atréide l'eût traîné"});
db.iliadfr.insert({ pg : 1955, line : "et eût remporté une grande gloire, si la fille de Zeus, Aphroditè,"});
db.iliadfr.insert({ pg : 1956, line : "ayant vu cela, n'eût rompu le cuir de boeuf; et le casque vide"});
db.iliadfr.insert({ pg : 1957, line : "suivit la main musculeuse de Ménélaos. Et celui-ci le fit"});
db.iliadfr.insert({ pg : 1958, line : "tournoyer et le jeta au milieu des Akhaiens aux belles knèmides,"});
db.iliadfr.insert({ pg : 1959, line : "et ses chers compagnons l'emportèrent. Puis, il se rua de nouveau"});
db.iliadfr.insert({ pg : 1960, line : "désirant tuer le Priamide de sa pique d'airain; mais Aphroditè,"});
db.iliadfr.insert({ pg : 1961, line : "étant déesse, enleva très facilement Alexandros en l'enveloppant"});
db.iliadfr.insert({ pg : 1962, line : "d'une nuée épaisse, et elle le déposa dans sa chambre nuptiale,"});
db.iliadfr.insert({ pg : 1963, line : "sur son lit parfumé. Et elle sortit pour appeler Hélénè, quelle"});
db.iliadfr.insert({ pg : 1964, line : "trouva sur la haute tour, au milieu de la foule des Troiennes. Et"});
db.iliadfr.insert({ pg : 1965, line : "la divine Aphroditè, s'étant faite semblable à une vieille femme"});
db.iliadfr.insert({ pg : 1966, line : "habile à tisser la laine, et qui la tissait pour Hélénè dans la"});
db.iliadfr.insert({ pg : 1967, line : "populeuse Lakédaimôn, et qui aimait Hélénè, saisit celle-ci par sa"});
db.iliadfr.insert({ pg : 1968, line : "robe nektaréenne et lui dit:"});
db.iliadfr.insert({ pg : 1970, line : "-- Viens! Alexandros t'invite à revenir. Il est couché, plein de"});
db.iliadfr.insert({ pg : 1971, line : "beauté et richement vêtu, sur son lit habilement travaillé. Tu ne"});
db.iliadfr.insert({ pg : 1972, line : "dirais point qu'il vient de lutter contre un homme, mais tu"});
db.iliadfr.insert({ pg : 1973, line : "croirais qu'il va aux danses, ou qu'il repose au retour des"});
db.iliadfr.insert({ pg : 1974, line : "danses."});
db.iliadfr.insert({ pg : 1976, line : "Elle parla ainsi, et elle troubla le coeur de Hélénè. Mais dès que"});
db.iliadfr.insert({ pg : 1977, line : "celle-ci eut vu le beau cou de la déesse, et son sein d'où"});
db.iliadfr.insert({ pg : 1978, line : "naissent les désirs, et ses yeux éclatants, elle fut saisie de"});
db.iliadfr.insert({ pg : 1979, line : "terreur, et, la nommant de son nom, elle lui dit:"});
db.iliadfr.insert({ pg : 1981, line : "-- Ô mauvaise! Pourquoi veux-tu me tromper encore? Me conduiras-tu"});
db.iliadfr.insert({ pg : 1982, line : "dans quelque autre ville populeuse de la Phrygiè ou de l'heureuse"});
db.iliadfr.insert({ pg : 1983, line : "Maioniè, si un homme qui t'est cher y habite? Est-ce parce que"});
db.iliadfr.insert({ pg : 1984, line : "Ménélaos, ayant vaincu le divin Alexandros, veut m'emmener dans"});
db.iliadfr.insert({ pg : 1985, line : "ses demeures, moi qui me suis odieuse, que tu viens de nouveau me"});
db.iliadfr.insert({ pg : 1986, line : "tendre des pièges? Va plutôt! abandonne la demeure des dieux, ne"});
db.iliadfr.insert({ pg : 1987, line : "retourne plus dans l'Olympos, et reste auprès de lui, toujours"});
db.iliadfr.insert({ pg : 1988, line : "inquiète; et prends-le sous ta garde, jusqu'à ce qu'il fasse de"});
db.iliadfr.insert({ pg : 1989, line : "toi sa femme ou son esclave! Pour moi, je n'irai plus orner son"});
db.iliadfr.insert({ pg : 1990, line : "lit, car ce serait trop de honte, et toutes les Troiennes me"});
db.iliadfr.insert({ pg : 1991, line : "blâmeraient, et j'ai trop d'amers chagrins dans le coeur."});
db.iliadfr.insert({ pg : 1993, line : "Et la divine Aphroditè, pleine de colère, lui dit:"});
db.iliadfr.insert({ pg : 1995, line : "-- Malheureuse! crains de m'irriter, de peur que je t'abandonne"});
db.iliadfr.insert({ pg : 1996, line : "dans ma colère, et que je te haïsse autant que je t'ai aimée, et"});
db.iliadfr.insert({ pg : 1997, line : "que, jetant des haines inexorables entre les Troiens et les"});
db.iliadfr.insert({ pg : 1998, line : "Akhaiens, je te fasse périr d'une mort violente!"});
db.iliadfr.insert({ pg : 2000, line : "Elle parla ainsi, et Hélénè, fille de Zeus, fut saisie de terreur,"});
db.iliadfr.insert({ pg : 2001, line : "et, couverte de sa robe éclatante de blancheur, elle marcha en"});
db.iliadfr.insert({ pg : 2002, line : "silence, s'éloignant des Troiennes, sur les pas de la déesse."});
db.iliadfr.insert({ pg : 2004, line : "Et quand elles furent parvenues à la belle demeure d'Alexandros,"});
db.iliadfr.insert({ pg : 2005, line : "toutes les servantes se mirent à leur tâche, et la divine femme"});
db.iliadfr.insert({ pg : 2006, line : "monta dans la haute chambre nuptiale. Aphroditè qui aime les"});
db.iliadfr.insert({ pg : 2007, line : "sourires avança un siège pour elle auprès d'Alexandros, et Hélénè,"});
db.iliadfr.insert({ pg : 2008, line : "fille de Zeus tempétueux, s'y assit en détournant les yeux; mais"});
db.iliadfr.insert({ pg : 2009, line : "elle adressa ces reproches à son époux:"});
db.iliadfr.insert({ pg : 2011, line : "-- Te voici revenu du combat. Que n'y restais-tu, mort et dompté"});
db.iliadfr.insert({ pg : 2012, line : "par l'homme brave qui fut mon premier mari! Ne te vantais-tu pas"});
db.iliadfr.insert({ pg : 2013, line : "de l'emporter sur Ménélaos cher à Arès, par ton courage, par ta"});
db.iliadfr.insert({ pg : 2014, line : "force et par ta lance? Va! défie encore Ménélaos cher à Arès, et"});
db.iliadfr.insert({ pg : 2015, line : "combats de nouveau contre lui; mais non, je te conseille plutôt de"});
db.iliadfr.insert({ pg : 2016, line : "ne plus lutter contre le blond Ménélaos, de peur qu'il te dompte"});
db.iliadfr.insert({ pg : 2017, line : "aussitôt de sa lance!"});
db.iliadfr.insert({ pg : 2019, line : "Et Pâris, lui répondant, parla ainsi:"});
db.iliadfr.insert({ pg : 2021, line : "-- Femme! ne blesse pas mon coeur par d'amères paroles. Il est"});
db.iliadfr.insert({ pg : 2022, line : "vrai, Ménélaos m'a vaincu à l'aide d'Athènè, mais je le vaincrai"});
db.iliadfr.insert({ pg : 2023, line : "plus tard, car nous avons aussi des dieux qui nous sont amis."});
db.iliadfr.insert({ pg : 2024, line : "Viens! couchons-nous et aimons-nous! Jamais le désir ne m'a brûlé"});
db.iliadfr.insert({ pg : 2025, line : "ainsi, même lorsque, naviguant sur mes nefs rapides, après t'avoir"});
db.iliadfr.insert({ pg : 2026, line : "enlevée de l'heureuse Lakédaimôn, je m'unis d'amour avec toi dans"});
db.iliadfr.insert({ pg : 2027, line : "l'île de Kranaè, tant je t'aime maintenant et suis saisi de"});
db.iliadfr.insert({ pg : 2028, line : "désirs!"});
db.iliadfr.insert({ pg : 2030, line : "Il parla ainsi et marcha vers son lit, et l'épouse le suivit, et"});
db.iliadfr.insert({ pg : 2031, line : "ils se couchèrent dans le lit bien construit."});
db.iliadfr.insert({ pg : 2033, line : "Cependant l'Atréide courait comme une bête féroce au travers de la"});
db.iliadfr.insert({ pg : 2034, line : "foule, cherchant le divin Alexandros. Et nul des Troiens ni des"});
db.iliadfr.insert({ pg : 2035, line : "illustres alliés ne put montrer Alexandros à Ménélaos cher à Arès."});
db.iliadfr.insert({ pg : 2036, line : "Et certes, s'ils l'avaient vu, ils ne l'auraient point caché, car"});
db.iliadfr.insert({ pg : 2037, line : "ils le haïssaient tous comme la noire kèr. Et le roi des hommes,"});
db.iliadfr.insert({ pg : 2038, line : "Agamemnôn, leur parla ainsi:"});
db.iliadfr.insert({ pg : 2040, line : "-- Ecoutez-moi, Troiens, Dardaniens et alliés. La victoire,"});
db.iliadfr.insert({ pg : 2041, line : "certes, est à Ménélaos cher à Arès. Rendez-nous donc l'Argienne"});
db.iliadfr.insert({ pg : 2042, line : "Hélénè et ses richesses, et payez, comme il est juste, un tribut"});
db.iliadfr.insert({ pg : 2043, line : "dont se souviendront les hommes futurs."});
db.iliadfr.insert({ pg : 2045, line : "L'Atréide parla ainsi, et tous les Akhaiens applaudirent."});
db.iliadfr.insert({ pg : 2048, line : "Chant 4"});
db.iliadfr.insert({ pg : 2050, line : "Les dieux, assis auprès de Zeus, étaient réunis sur le pavé d'or,"});
db.iliadfr.insert({ pg : 2051, line : "et la vénérable Hèbè versait le nektar, et tous, buvant les coupes"});
db.iliadfr.insert({ pg : 2052, line : "d'or, regardaient la ville des Troiens. Et le Kronide voulut"});
db.iliadfr.insert({ pg : 2053, line : "irriter Hèrè par des paroles mordantes, et il dit:"});
db.iliadfr.insert({ pg : 2055, line : "-- Deux déesses défendent Ménélaos, Hèrè l'Argienne et la"});
db.iliadfr.insert({ pg : 2056, line : "protectrice Athènè; mais elles restent assises et ne font que"});
db.iliadfr.insert({ pg : 2057, line : "regarder, tandis qu'Aphroditè qui aime les sourires ne quitte"});
db.iliadfr.insert({ pg : 2058, line : "jamais Alexandros et écarte de lui les kères. Et voici qu'elle l'a"});
db.iliadfr.insert({ pg : 2059, line : "sauvé comme il allait périr. Mais la victoire est à Ménélaos cher"});
db.iliadfr.insert({ pg : 2060, line : "à Arès. Songeons donc à ceci. Faut-il exciter de nouveau la guerre"});
db.iliadfr.insert({ pg : 2061, line : "mauvaise et le rude combat, ou sceller l'alliance entre les deux"});
db.iliadfr.insert({ pg : 2062, line : "peuples? S'il plaît à tous les dieux, la ville du roi Priamos"});
db.iliadfr.insert({ pg : 2063, line : "restera debout, et Ménélaos emmènera l'Argienne Hélénè."});
db.iliadfr.insert({ pg : 2065, line : "Il parla ainsi, et les déesses Athènè et Hèrè se mordirent les"});
db.iliadfr.insert({ pg : 2066, line : "lèvres, et, assises à côté l'une de l'autre, elles méditaient la"});
db.iliadfr.insert({ pg : 2067, line : "destruction des Troiens. Et Athènè restait muette, irritée contre"});
db.iliadfr.insert({ pg : 2068, line : "son père Zeus, et une sauvage colère la brûlait; mais Hèrè ne put"});
db.iliadfr.insert({ pg : 2069, line : "contenir la sienne et dit:"});
db.iliadfr.insert({ pg : 2071, line : "Très dur Kronide, quelle parole as-tu dite? Veux-tu rendre vaines"});
db.iliadfr.insert({ pg : 2072, line : "toutes mes fatigues et la sueur que j'ai suée? J'ai lassé mes"});
db.iliadfr.insert({ pg : 2073, line : "chevaux en rassemblant les peuples contre Priamos et contre ses"});
db.iliadfr.insert({ pg : 2074, line : "enfants. Fais donc, mais les dieux ne t'approuveront pas."});
db.iliadfr.insert({ pg : 2076, line : "Et Zeus qui amasse les nuées, très irrité, lui dit:"});
db.iliadfr.insert({ pg : 2078, line : "-- Malheureuse! Quels maux si grands Priamos et les enfants de"});
db.iliadfr.insert({ pg : 2079, line : "Priamos t'ont-ils causés, que tu veuilles sans relâche détruire la"});
db.iliadfr.insert({ pg : 2080, line : "forte citadelle d'Ilios? Si, dans ses larges murailles, tu pouvais"});
db.iliadfr.insert({ pg : 2081, line : "dévorer Priamos et les enfants de Priamos et les autres Troiens,"});
db.iliadfr.insert({ pg : 2082, line : "peut-être ta haine serait elle assouvie. Fais selon ta volonté, et"});
db.iliadfr.insert({ pg : 2083, line : "que cette dissension cesse désormais entre nous. Mais je te dirai"});
db.iliadfr.insert({ pg : 2084, line : "ceci, et garde mes paroles dans ton esprit: Si jamais je veux"});
db.iliadfr.insert({ pg : 2085, line : "aussi détruire une ville habitée par des hommes qui te sont amis,"});
db.iliadfr.insert({ pg : 2086, line : "ne t'oppose point à ma colère et laisse-moi agir, car c'est à"});
db.iliadfr.insert({ pg : 2087, line : "contrecoeur que je te livre celle-ci. De toutes les villes"});
db.iliadfr.insert({ pg : 2088, line : "habitées par les hommes terrestres, sous Hélios et sous l'Ouranos"});
db.iliadfr.insert({ pg : 2089, line : "étoilé, aucune ne m'est plus chère que la ville sacrée d'Ilios, où"});
db.iliadfr.insert({ pg : 2090, line : "sont Priamos et le peuple de Priamos qui tient la lance. Là, mon"});
db.iliadfr.insert({ pg : 2091, line : "autel n'a jamais manqué de nourriture, de libations, et de"});
db.iliadfr.insert({ pg : 2092, line : "graisse; car nous avons cet honneur en partage."});
db.iliadfr.insert({ pg : 2094, line : "Et la vénérable Hèrè aux yeux de boeuf lui répondit:"});
db.iliadfr.insert({ pg : 2096, line : "-- Certes, j'ai trois villes qui me sont très chères, Argos,"});
db.iliadfr.insert({ pg : 2097, line : "Spartè et Mykènè aux larges rues. Détruis-les quand tu les haïras,"});
db.iliadfr.insert({ pg : 2098, line : "et je ne les défendrai point; mais je m'opposerais en vain à ta"});
db.iliadfr.insert({ pg : 2099, line : "volonté, puisque tu es infiniment plus puissant. Il ne faut pas"});
db.iliadfr.insert({ pg : 2100, line : "que tu rendes mes fatigues vaines. Je suis déesse aussi, et ma"});
db.iliadfr.insert({ pg : 2101, line : "race est la tienne. Le subtil Kronos m'a engendrée, et je suis"});
db.iliadfr.insert({ pg : 2102, line : "deux fois vénérable, par mon origine et parce que je suis ton"});
db.iliadfr.insert({ pg : 2103, line : "épouse, à toi qui commandes à tous les immortels. Cédons-nous donc"});
db.iliadfr.insert({ pg : 2104, line : "tour à tour, et les dieux immortels nous obéiront. Ordonne"});
db.iliadfr.insert({ pg : 2105, line : "qu'Athènè se mêle au rude combat des Troiens et des Akhaiens."});
db.iliadfr.insert({ pg : 2106, line : "Qu'elle pousse les Troiens à outrager, les premiers, les fiers"});
db.iliadfr.insert({ pg : 2107, line : "Akhaiens, malgré l'alliance jurée."});
db.iliadfr.insert({ pg : 2109, line : "Elle parla ainsi, et le père des hommes et des dieux le voulut, et"});
db.iliadfr.insert({ pg : 2110, line : "il dit à Athènè ces paroles ailées:"});
db.iliadfr.insert({ pg : 2112, line : "-- Va très promptement au milieu des Troiens et des Akhaiens, et"});
db.iliadfr.insert({ pg : 2113, line : "pousse les Troiens à outrager, les premiers, les fiers Akhaiens,"});
db.iliadfr.insert({ pg : 2114, line : "malgré l'alliance jurée."});
db.iliadfr.insert({ pg : 2116, line : "Ayant ainsi parlé, il excita Athènè déjà pleine de ce désir, et"});
db.iliadfr.insert({ pg : 2117, line : "elle se précipita des sommets de l'Olympos. Comme un signe"});
db.iliadfr.insert({ pg : 2118, line : "lumineux que le fils du subtil Kronos envoie aux marins et aux"});
db.iliadfr.insert({ pg : 2119, line : "peuples nombreux, et d'où jaillissent mille étincelles, Pallas"});
db.iliadfr.insert({ pg : 2120, line : "Athènè s'élança sur la terre et tomba au milieu des deux armées."});
db.iliadfr.insert({ pg : 2121, line : "Et sa vue emplit de frayeur les Troiens dompteurs de chevaux et"});
db.iliadfr.insert({ pg : 2122, line : "les Akhaiens aux belles knèmides. Et ils se disaient entre eux: --"});
db.iliadfr.insert({ pg : 2123, line : "Certes, la guerre mauvaise et le rude combat vont recommencer, ou"});
db.iliadfr.insert({ pg : 2124, line : "Zeus va sceller l'alliance entre les deux peuples, car il règle la"});
db.iliadfr.insert({ pg : 2125, line : "guerre parmi les hommes."});
db.iliadfr.insert({ pg : 2127, line : "Ils parlaient ainsi, et Athènè se mêla aux Troiens, semblable au"});
db.iliadfr.insert({ pg : 2128, line : "brave Laodokos Anténoride, et cherchant Pandaros égal aux dieux."});
db.iliadfr.insert({ pg : 2129, line : "Et elle trouva debout le brave et irréprochable fils de Lykaôn,"});
db.iliadfr.insert({ pg : 2130, line : "et, autour de lui, la foule des hardis porte boucliers qui"});
db.iliadfr.insert({ pg : 2131, line : "l'avaient suivi des bords de l'Aisèpos. Et, s'étant approchée,"});
db.iliadfr.insert({ pg : 2132, line : "Athènè lui dit en paroles ailées:"});
db.iliadfr.insert({ pg : 2134, line : "-- Te laisseras-tu persuader par moi, brave fils de Lykaôn, et"});
db.iliadfr.insert({ pg : 2135, line : "oserais-tu lancer une flèche rapide à Ménélaos? Certes, tu serais"});
db.iliadfr.insert({ pg : 2136, line : "comblé de gloire et de gratitude par tous les Troiens et surtout"});
db.iliadfr.insert({ pg : 2137, line : "par le roi Alexandros. Et il te ferait de riches présents, s'il"});
db.iliadfr.insert({ pg : 2138, line : "voyait le brave Ménélaos, fils d'Atreus, dompté par ta flèche et"});
db.iliadfr.insert({ pg : 2139, line : "montant sur le bûcher funéraire. Courage! Tire contre le noble"});
db.iliadfr.insert({ pg : 2140, line : "Ménélaos, et promets une belle hécatombe à l'illustre archer"});
db.iliadfr.insert({ pg : 2141, line : "Apollôn Lykien, quand tu seras de retour dans la citadelle de"});
db.iliadfr.insert({ pg : 2142, line : "Zéléiè la sainte."});
db.iliadfr.insert({ pg : 2144, line : "Athènè parla ainsi, et elle persuada l'insensé. Et il tira de"});
db.iliadfr.insert({ pg : 2145, line : "l'étui un arc luisant, dépouille d'une chèvre sauvage et"});
db.iliadfr.insert({ pg : 2146, line : "bondissante qu'il avait percée à la poitrine, comme elle sortait"});
db.iliadfr.insert({ pg : 2147, line : "d'un creux de rocher. Et elle était tombée morte sur la pierre. Et"});
db.iliadfr.insert({ pg : 2148, line : "ses cornes étaient hautes de seize palmes. Un excellent ouvrier"});
db.iliadfr.insert({ pg : 2149, line : "les travailla, les polit et les dora à chaque extrémité. Et"});
db.iliadfr.insert({ pg : 2150, line : "Pandaros, ayant bandé cet arc, le posa à terre, et ses braves"});
db.iliadfr.insert({ pg : 2151, line : "compagnons le couvrirent de leurs boucliers, de peur que les fils"});
db.iliadfr.insert({ pg : 2152, line : "des courageux Akhaiens vinssent à se ruer avant que le brave"});
db.iliadfr.insert({ pg : 2153, line : "Ménélaos, chef des Akhaiens, ne fût frappé."});
db.iliadfr.insert({ pg : 2155, line : "Et Pandaros ouvrit le carquois et en tira une flèche neuve, ailée,"});
db.iliadfr.insert({ pg : 2156, line : "source d'amères douleurs. Et il promit à l'illustre archer Apollôn"});
db.iliadfr.insert({ pg : 2157, line : "Lykien une belle hécatombe d'agneaux premiers-nés, quand il serait"});
db.iliadfr.insert({ pg : 2158, line : "de retour dans la citadelle de Zéléiè la sainte."});
db.iliadfr.insert({ pg : 2160, line : "Et il saisit à la fois la flèche et le nerf de boeuf, et, les"});
db.iliadfr.insert({ pg : 2161, line : "ayant attirés, le nerf toucha sa mamelle, et la pointe d'airain"});
db.iliadfr.insert({ pg : 2162, line : "toucha l'arc, et le nerf vibra avec force, et la flèche aiguë"});
db.iliadfr.insert({ pg : 2163, line : "s'élança, désirant voler au travers de la foule."});
db.iliadfr.insert({ pg : 2165, line : "Mais les dieux heureux ne t'oublièrent point, Ménélaos! Et la"});
db.iliadfr.insert({ pg : 2166, line : "terrible fille de Zeus se tint la première devant toi pour"});
db.iliadfr.insert({ pg : 2167, line : "détourner la flèche amère. Elle la détourna comme une mère chasse"});
db.iliadfr.insert({ pg : 2168, line : "une mouche loin de son enfant enveloppé par le doux sommeil. Et"});
db.iliadfr.insert({ pg : 2169, line : "elle la dirigea là où les anneaux d'or du baudrier forment comme"});
db.iliadfr.insert({ pg : 2170, line : "une seconde cuirasse. Et la flèche amère tomba sur le solide"});
db.iliadfr.insert({ pg : 2171, line : "baudrier, et elle le perça ainsi que la cuirasse artistement ornée"});
db.iliadfr.insert({ pg : 2172, line : "et la mitre qui, par-dessous, garantissait la peau des traits. Et"});
db.iliadfr.insert({ pg : 2173, line : "la flèche la perça aussi, et elle effleura la peau du héros, et un"});
db.iliadfr.insert({ pg : 2174, line : "sang noir jaillit de la blessure."});
db.iliadfr.insert({ pg : 2176, line : "Comme une femme Maionienne ou Karienne teint de pourpre l'ivoire"});
db.iliadfr.insert({ pg : 2177, line : "destiné à orner le mors des chevaux, et qu'elle garde dans sa"});
db.iliadfr.insert({ pg : 2178, line : "demeure, et que tous les cavaliers désirent, car il est l'ornement"});
db.iliadfr.insert({ pg : 2179, line : "d'un roi, la parure du cheval et l'orgueil du cavalier, ainsi,"});
db.iliadfr.insert({ pg : 2180, line : "Ménélaos, le sang rougit tes belles cuisses et tes jambes"});
db.iliadfr.insert({ pg : 2181, line : "jusqu'aux chevilles. Et le roi des hommes, Agamemnôn, frémit de"});
db.iliadfr.insert({ pg : 2182, line : "voir ce sang noir couler de la blessure; et Ménélaos cher à Arès"});
db.iliadfr.insert({ pg : 2183, line : "frémit aussi. Mais quand il vit que le fer de la flèche avait à"});
db.iliadfr.insert({ pg : 2184, line : "peine pénétré, son coeur se raffermit; et, au milieu de ses"});
db.iliadfr.insert({ pg : 2185, line : "compagnons qui se lamentaient, Agamemnôn qui commande au loin,"});
db.iliadfr.insert({ pg : 2186, line : "prenant la main de Ménélaos, lui dit en gémissant:"});
db.iliadfr.insert({ pg : 2188, line : "-- Cher frère, c'était ta mort que je décidais par ce traité, en"});
db.iliadfr.insert({ pg : 2189, line : "t'envoyant seul combattre les Troiens pour tous les Akhaiens,"});
db.iliadfr.insert({ pg : 2190, line : "puisqu'ils t'ont frappé et ont foulé aux pieds des serments"});
db.iliadfr.insert({ pg : 2191, line : "inviolables. Mais ces serments ne seront point vains, ni le sang"});
db.iliadfr.insert({ pg : 2192, line : "des agneaux, ni les libations sacrées, ni le gage de nos mains"});
db.iliadfr.insert({ pg : 2193, line : "unies. Si l'Olympien ne les frappe point maintenant, il les punira"});
db.iliadfr.insert({ pg : 2194, line : "plus tard; et ils expieront par des calamités terribles cette"});
db.iliadfr.insert({ pg : 2195, line : "trahison qui retombera sur leurs têtes, sur leurs femmes et sur"});
db.iliadfr.insert({ pg : 2196, line : "leurs enfants. Car je le sais, dans mon esprit, un jour viendra où"});
db.iliadfr.insert({ pg : 2197, line : "la sainte Ilios périra, et Priamos, et le peuple de Priamos habile"});
db.iliadfr.insert({ pg : 2198, line : "à manier la lance. Zeus Kronide qui habite l'aithèr agitera d'en"});
db.iliadfr.insert({ pg : 2199, line : "haut sur eux sa terrible Aigide, indigné de cette trahison qui"});
db.iliadfr.insert({ pg : 2200, line : "sera châtiée. Ô Ménélaos, ce serait une amère douleur pour moi si,"});
db.iliadfr.insert({ pg : 2201, line : "accomplissant tes destinées, tu mourais. Couvert d'opprobre je"});
db.iliadfr.insert({ pg : 2202, line : "retournerais dans Argos, car les Akhaiens voudraient aussitôt"});
db.iliadfr.insert({ pg : 2203, line : "rentrer dans la terre natale, et nous abandonnerions l'Argienne"});
db.iliadfr.insert({ pg : 2204, line : "Hélénè comme un triomphe à Priamos et aux Troiens. Et les"});
db.iliadfr.insert({ pg : 2205, line : "orgueilleux Troiens diraient, foulant la tombe de l'illustre"});
db.iliadfr.insert({ pg : 2206, line : "Ménélaos:"});
db.iliadfr.insert({ pg : 2208, line : "-- Plaise aux dieux qu'Agamemnôn assouvisse toujours ainsi sa"});
db.iliadfr.insert({ pg : 2209, line : "colère! Il a conduit ici l'armée inutile des Akhaiens, et voici"});
db.iliadfr.insert({ pg : 2210, line : "qu'il est retourné dans son pays bien-aimé, abandonnant le brave"});
db.iliadfr.insert({ pg : 2211, line : "Ménèlaos!"});
db.iliadfr.insert({ pg : 2213, line : "-- Ils parleront ainsi un jour; mais, alors, que la profonde terre"});
db.iliadfr.insert({ pg : 2214, line : "m'engloutisse!"});
db.iliadfr.insert({ pg : 2216, line : "Et le blond Ménélaos, le rassurant, parla ainsi:"});
db.iliadfr.insert({ pg : 2218, line : "-- Reprends courage, et n'effraye point le peuple des Akhaiens. Le"});
db.iliadfr.insert({ pg : 2219, line : "trait aigu ne m'a point blessé à mort, et le baudrier m'a"});
db.iliadfr.insert({ pg : 2220, line : "préservé, ainsi que la cuirasse, le tablier et la mitre que de"});
db.iliadfr.insert({ pg : 2221, line : "bons armuriers ont forgée."});
db.iliadfr.insert({ pg : 2223, line : "Et Agamemnôn qui commande au loin, lui répondant, parla ainsi:"});
db.iliadfr.insert({ pg : 2225, line : "-- Plaise aux dieux que cela soit, ô cher Ménélaos! Mais un"});
db.iliadfr.insert({ pg : 2226, line : "médecin soignera ta blessure et mettra le remède qui apaise les"});
db.iliadfr.insert({ pg : 2227, line : "noires douleurs."});
db.iliadfr.insert({ pg : 2229, line : "Il parla ainsi, et appela le héraut divin Talthybios:"});
db.iliadfr.insert({ pg : 2231, line : "-- Talthybios, appelle le plus promptement possible"});
db.iliadfr.insert({ pg : 2232, line : "l'irréprochable médecin Makhaôn Asklépiade, afin qu'il voie le"});
db.iliadfr.insert({ pg : 2233, line : "brave Ménélaos, prince des Akhaiens, qu'un habile archer Troien ou"});
db.iliadfr.insert({ pg : 2234, line : "Lykien a frappé d'une flèche. Il triomphe, et nous sommes dans le"});
db.iliadfr.insert({ pg : 2235, line : "deuil."});
db.iliadfr.insert({ pg : 2237, line : "Il parla ainsi, et le héraut lui obéit. Et il chercha, parmi le"});
db.iliadfr.insert({ pg : 2238, line : "peuple des Akhaiens aux tuniques d'airain, le héros Makhaôn, qu'il"});
db.iliadfr.insert({ pg : 2239, line : "trouva debout au milieu de la foule belliqueuse des porte"});
db.iliadfr.insert({ pg : 2240, line : "boucliers qui l'avaient suivi de Trikkè, nourrice de chevaux. Et,"});
db.iliadfr.insert({ pg : 2241, line : "s'approchant, il dit ces paroles ailées:"});
db.iliadfr.insert({ pg : 2243, line : "-- Lève-toi, Asklépiade! Agamemnôn, qui commande au loin,"});
db.iliadfr.insert({ pg : 2244, line : "t'appelle, afin que tu voies le brave Ménélaos, fils d'Atreus,"});
db.iliadfr.insert({ pg : 2245, line : "qu'un habile archer Troien ou Lykien a frappé d'une flèche. Il"});
db.iliadfr.insert({ pg : 2246, line : "triomphe, et nous sommes dans le deuil."});
db.iliadfr.insert({ pg : 2248, line : "Il parla ainsi, et le coeur de Makhaôn fut ému dans sa poitrine."});
db.iliadfr.insert({ pg : 2249, line : "Et ils marchèrent à travers l'armée immense des Akhaiens; et quand"});
db.iliadfr.insert({ pg : 2250, line : "ils furent arrivés à l'endroit où le blond Ménélaos avait été"});
db.iliadfr.insert({ pg : 2251, line : "blessé et était assis, égal aux dieux, en un cercle formé par les"});
db.iliadfr.insert({ pg : 2252, line : "princes, aussitôt Makhaôn arracha le trait du solide baudrier, en"});
db.iliadfr.insert({ pg : 2253, line : "ployant les crochets aigus; et il détacha le riche baudrier, et le"});
db.iliadfr.insert({ pg : 2254, line : "tablier et la mitre que de bons armuriers avaient forgée. Et,"});
db.iliadfr.insert({ pg : 2255, line : "après avoir examiné la plaie faite par la flèche amère, et sucé le"});
db.iliadfr.insert({ pg : 2256, line : "sang, il y versa adroitement un doux baume que Khirôn avait"});
db.iliadfr.insert({ pg : 2257, line : "autrefois donné à son père qu'il aimait."});
db.iliadfr.insert({ pg : 2259, line : "Et tandis qu'ils s'empressaient autour de Ménélaos hardi au"});
db.iliadfr.insert({ pg : 2260, line : "combat, l'armée des Troiens, porteurs de boucliers, s'avançait, et"});
db.iliadfr.insert({ pg : 2261, line : "les Akhaiens se couvrirent de nouveau de leurs armes, désirant"});
db.iliadfr.insert({ pg : 2262, line : "combattre."});
db.iliadfr.insert({ pg : 2264, line : "Et le divin Agamemnôn n'hésita ni se ralentit, mais il se prépara"});
db.iliadfr.insert({ pg : 2265, line : "en hâte pour la glorieuse bataille. Et il laissa ses chevaux et"});
db.iliadfr.insert({ pg : 2266, line : "son char orné d'airain; et le serviteur Eurymédôn, fils de"});
db.iliadfr.insert({ pg : 2267, line : "Ptolémaios Peiraide, les retint à l'écart, et l'Atréide lui"});
db.iliadfr.insert({ pg : 2268, line : "ordonna de ne point s'éloigner, afin qu'il pût monter dans le"});
db.iliadfr.insert({ pg : 2269, line : "char, si la fatigue l'accablait pendant qu'il donnait partout ses"});
db.iliadfr.insert({ pg : 2270, line : "ordres. Et il marcha à travers la foule des hommes. Et il"});
db.iliadfr.insert({ pg : 2271, line : "encourageait encore ceux des Danaens aux rapides chevaux, qu'il"});
db.iliadfr.insert({ pg : 2272, line : "voyait pleins d'ardeur:"});
db.iliadfr.insert({ pg : 2274, line : "-- Argiens! ne perdez rien de cette ardeur impétueuse, car le père"});
db.iliadfr.insert({ pg : 2275, line : "Zeus ne protégera point le parjure. Ceux qui, les premiers, ont"});
db.iliadfr.insert({ pg : 2276, line : "violé nos traités, les vautours mangeront leur chair; et, quand"});
db.iliadfr.insert({ pg : 2277, line : "nous aurons pris leur ville, nous emmènerons sur nos nefs leurs"});
db.iliadfr.insert({ pg : 2278, line : "femmes bien-aimées et leurs petits enfants."});
db.iliadfr.insert({ pg : 2280, line : "Et ceux qu'il voyait lents au rude combat, il leur disait ces"});
db.iliadfr.insert({ pg : 2281, line : "paroles irritées:"});
db.iliadfr.insert({ pg : 2283, line : "-- Argiens promis à la pique ennemie! lâches, n'avez-vous point de"});
db.iliadfr.insert({ pg : 2284, line : "honte? Pourquoi restez-vous glacés de peur, comme des biches qui,"});
db.iliadfr.insert({ pg : 2285, line : "après avoir couru à travers la vaste plaine, s'arrêtent épuisées"});
db.iliadfr.insert({ pg : 2286, line : "et n'ayant plus de force au coeur? C'est ainsi que, glacés de"});
db.iliadfr.insert({ pg : 2287, line : "peur, vous vous arrêtez et ne combattez point. Attendez-vous que"});
db.iliadfr.insert({ pg : 2288, line : "les Troiens pénètrent jusqu'aux nefs aux belles poupes, sur le"});
db.iliadfr.insert({ pg : 2289, line : "rivage de la blanche mer, et que le Kroniôn vous aide?"});
db.iliadfr.insert({ pg : 2291, line : "C'est ainsi qu'il donnait ses ordres en parcourant la foule des"});
db.iliadfr.insert({ pg : 2292, line : "hommes. Et il parvint là où les Krètois s'armaient autour du brave"});
db.iliadfr.insert({ pg : 2293, line : "Idoméneus. Et Idoméneus, pareil à un fort sanglier, était au"});
db.iliadfr.insert({ pg : 2294, line : "premier rang; et Mèrionès hâtait les dernières phalanges. Et le"});
db.iliadfr.insert({ pg : 2295, line : "roi des hommes, Agamemnôn, ayant vu cela, s'en réjouit et dit à"});
db.iliadfr.insert({ pg : 2296, line : "Idoméneus ces paroles flatteuses:"});
db.iliadfr.insert({ pg : 2298, line : "-- Idoméneus, certes, je t'honore au-dessus de tous les Danaens"});
db.iliadfr.insert({ pg : 2299, line : "aux rapides chevaux, soit dans le combat, soit dans les repas,"});
db.iliadfr.insert({ pg : 2300, line : "quand les princes des Akhaiens mêlent le vin vieux dans les"});
db.iliadfr.insert({ pg : 2301, line : "kratères. Et si les autres Akhaiens chevelus boivent avec mesure,"});
db.iliadfr.insert({ pg : 2302, line : "ta coupe est toujours aussi pleine que la mienne, et tu bois selon"});
db.iliadfr.insert({ pg : 2303, line : "ton désir. Cours donc au combat, et sois tel que tu as toujours"});
db.iliadfr.insert({ pg : 2304, line : "été."});
db.iliadfr.insert({ pg : 2306, line : "Et le prince des Krètois, Idoméneus, lui répondit:"});
db.iliadfr.insert({ pg : 2308, line : "Atréide, je te serai toujours fidèle comme je te l'ai promis. Va!"});
db.iliadfr.insert({ pg : 2309, line : "encourage les autres Akhaiens chevelus, afin que nous combattions"});
db.iliadfr.insert({ pg : 2310, line : "promptement, puisque les Troiens ont violé nos traités. La mort et"});
db.iliadfr.insert({ pg : 2311, line : "les calamités les accableront, puisque, les premiers, ils se sont"});
db.iliadfr.insert({ pg : 2312, line : "parjurés."});
db.iliadfr.insert({ pg : 2314, line : "Il parla ainsi, et l'Atréide s'éloigna, plein de joie. Et il alla"});
db.iliadfr.insert({ pg : 2315, line : "vers les Aias, à travers la foule des hommes. Et les Aias"});
db.iliadfr.insert({ pg : 2316, line : "s'étaient armés, suivis d'un nuage de guerriers. Comme une nuée"});
db.iliadfr.insert({ pg : 2317, line : "qu'un chevrier a vue d'une hauteur, s'élargissant sur la mer, sous"});
db.iliadfr.insert({ pg : 2318, line : "le souffle de Zéphyros, et qui, par tourbillons épais, lui"});
db.iliadfr.insert({ pg : 2319, line : "apparaît de loin plus noire que la poix, de sorte qu'il s'inquiète"});
db.iliadfr.insert({ pg : 2320, line : "et pousse ses chèvres dans une caverne; de même les noires"});
db.iliadfr.insert({ pg : 2321, line : "phalanges hérissées de boucliers et de piques des jeunes hommes"});
db.iliadfr.insert({ pg : 2322, line : "nourrissons de Zeus se mouvaient derrière les Aias pour le rude"});
db.iliadfr.insert({ pg : 2323, line : "combat. Et Agamemnôn qui commande au loin, les ayant vus, se"});
db.iliadfr.insert({ pg : 2324, line : "réjouit et dit ces paroles ailées:"});
db.iliadfr.insert({ pg : 2326, line : "-- Aias! Princes des Argiens aux tuniques d'airain, il ne serait"});
db.iliadfr.insert({ pg : 2327, line : "point juste de vous ordonner d'exciter vos hommes, car vous les"});
db.iliadfr.insert({ pg : 2328, line : "pressez de combattre bravement. Père Zeus! Athènè! Apollôn! que"});
db.iliadfr.insert({ pg : 2329, line : "votre courage emplisse tous les coeurs! Bientôt la ville du roi"});
db.iliadfr.insert({ pg : 2330, line : "Priamos, s'il en était ainsi, serait renversée, détruite et"});
db.iliadfr.insert({ pg : 2331, line : "saccagée par nos mains."});
db.iliadfr.insert({ pg : 2333, line : "Ayant ainsi parlé, il les laissa et marcha vers d'autres. Et il"});
db.iliadfr.insert({ pg : 2334, line : "trouva Nestôr, l'harmonieux agorète des Pyliens, qui animait et"});
db.iliadfr.insert({ pg : 2335, line : "rangeait en bataille ses compagnons autour du grand Pélagôn,"});
db.iliadfr.insert({ pg : 2336, line : "d'Alastôr, de Khromios, de Haimôn et de Bias, prince des peuples."});
db.iliadfr.insert({ pg : 2337, line : "Et il rangeait en avant les cavaliers, les chevaux et les chars,"});
db.iliadfr.insert({ pg : 2338, line : "et en arrière les fantassins braves et nombreux, pour être le"});
db.iliadfr.insert({ pg : 2339, line : "rempart de la guerre, et les lâches au milieu, afin que chacun"});
db.iliadfr.insert({ pg : 2340, line : "d'eux combattît forcément. Et il enseignait les cavaliers, leur"});
db.iliadfr.insert({ pg : 2341, line : "ordonnant de contenir les chevaux et de ne point courir au hasard"});
db.iliadfr.insert({ pg : 2342, line : "dans la mêlée:"});
db.iliadfr.insert({ pg : 2344, line : "-- Que nul ne s'élance en avant des autres pour combattre les"});
db.iliadfr.insert({ pg : 2345, line : "Troiens, et que nul ne recule, car vous serez sans force. Que le"});
db.iliadfr.insert({ pg : 2346, line : "guerrier qui abandonnera son char pour un autre combatte plutôt de"});
db.iliadfr.insert({ pg : 2347, line : "la pique, car ce sera pour le mieux, et c'est ainsi que les hommes"});
db.iliadfr.insert({ pg : 2348, line : "anciens, qui ont eu ce courage et cette prudence, ont renversé les"});
db.iliadfr.insert({ pg : 2349, line : "villes et les murailles."});
db.iliadfr.insert({ pg : 2351, line : "Et le vieillard les exhortait ainsi, étant habile dans la guerre"});
db.iliadfr.insert({ pg : 2352, line : "depuis longtemps. Et Agamemnôn qui commande au loin, l'ayant vu,"});
db.iliadfr.insert({ pg : 2353, line : "se réjouit et lui dit ces paroles ailées:"});
db.iliadfr.insert({ pg : 2355, line : "-- Ô vieillard! plût aux dieux que tes genoux eussent autant de"});
db.iliadfr.insert({ pg : 2356, line : "vigueur, que tu eusses autant de force que ton coeur a de courage!"});
db.iliadfr.insert({ pg : 2357, line : "Mais la vieillesse, qui est la même pour tous, t'accable. Plût aux"});
db.iliadfr.insert({ pg : 2358, line : "dieux qu'elle accablât plutôt tout autre guerrier, et que tu"});
db.iliadfr.insert({ pg : 2359, line : "fusses des plus jeunes"});
db.iliadfr.insert({ pg : 2361, line : "Et le cavalier Gérennien Nestôr lui répondit:"});
db.iliadfr.insert({ pg : 2363, line : "-- Certes, Atréide, je voudrais être encore ce que j'étais quand"});
db.iliadfr.insert({ pg : 2364, line : "je tuai le divin Éreuthaliôn. Mais les dieux ne prodiguent point"});
db.iliadfr.insert({ pg : 2365, line : "tous leurs dons aux hommes. Alors, j'étais jeune, et voici que la"});
db.iliadfr.insert({ pg : 2366, line : "vieillesse s'est emparée de moi. Mais tel que je suis, je me"});
db.iliadfr.insert({ pg : 2367, line : "mêlerai aux cavaliers et je les exciterai par mes conseils et par"});
db.iliadfr.insert({ pg : 2368, line : "mes paroles, car c'est la part des vieillards."});
db.iliadfr.insert({ pg : 2370, line : "Il parla ainsi, et l'Atréide, joyeux, alla plus loin. Et il trouva"});
db.iliadfr.insert({ pg : 2371, line : "le cavalier Ménèstheus immobile, et autour de lui les Athènaiens"});
db.iliadfr.insert({ pg : 2372, line : "belliqueux, et, auprès, le subtil Odysseus, et autour de ce"});
db.iliadfr.insert({ pg : 2373, line : "dernier la foule hardie des Képhallèniens. Et ils n'avaient point"});
db.iliadfr.insert({ pg : 2374, line : "entendu le cri de guerre, car les phalanges des Troiens dompteurs"});
db.iliadfr.insert({ pg : 2375, line : "de chevaux et des Akhaiens commençaient de s'ébranler. Et ils se"});
db.iliadfr.insert({ pg : 2376, line : "tenaient immobiles, attendant que d'autres phalanges Akhaiennes,"});
db.iliadfr.insert({ pg : 2377, line : "s'élançant contre les Troiens, commençassent le combat. Et"});
db.iliadfr.insert({ pg : 2378, line : "Agamemnôn, les ayant vus, les injuria et leur dit ces paroles"});
db.iliadfr.insert({ pg : 2379, line : "ailées:"});
db.iliadfr.insert({ pg : 2381, line : "-- Ô fils de Pétéos, d'un roi issu de Zeus, et toi, qui es"});
db.iliadfr.insert({ pg : 2382, line : "toujours plein de ruses subtiles, pourquoi, saisis de terreur,"});
db.iliadfr.insert({ pg : 2383, line : "attendez-vous que d'autres combattent? Il vous appartenait de"});
db.iliadfr.insert({ pg : 2384, line : "courir en avant dans le combat furieux, ainsi que vous assistez"});
db.iliadfr.insert({ pg : 2385, line : "les premiers à mes festins, où se réunissent les plus vénérables"});
db.iliadfr.insert({ pg : 2386, line : "des Akhaiens. Là, sans doute, il vous est doux de manger des"});
db.iliadfr.insert({ pg : 2387, line : "viandes rôties et de boire des coupes de bon vin autant qu'il vous"});
db.iliadfr.insert({ pg : 2388, line : "plaît. Et voici que, maintenant, vous verriez avec joie dix"});
db.iliadfr.insert({ pg : 2389, line : "phalanges des Akhaiens combattre avant vous, armées de l'airain"});
db.iliadfr.insert({ pg : 2390, line : "meurtrier!"});
db.iliadfr.insert({ pg : 2392, line : "Et le subtil Odysseus, avec un sombre regard, lui répondit:"});
db.iliadfr.insert({ pg : 2394, line : "-- Atréide, quelle parole s'est échappée de ta bouche? Comment"});
db.iliadfr.insert({ pg : 2395, line : "oses-tu dire que nous hésitons devant le combat? Lorsque nous"});
db.iliadfr.insert({ pg : 2396, line : "pousserons le rude Arès contre les Troiens dompteurs de chevaux,"});
db.iliadfr.insert({ pg : 2397, line : "tu verras, si tu le veux, et si cela te plaît le père bien-aimé de"});
db.iliadfr.insert({ pg : 2398, line : "Tèlémakhos au milieu des Troiens dompteurs de chevaux. Mais tu as"});
db.iliadfr.insert({ pg : 2399, line : "dit une parole vaine."});
db.iliadfr.insert({ pg : 2401, line : "Et Agamemnôn qui commande au loin, le voyant irrité, sourit, et,"});
db.iliadfr.insert({ pg : 2402, line : "se rétractant, lui répondit:"});
db.iliadfr.insert({ pg : 2404, line : "-- Subtil Odysseus, divin Laertiade, je ne veux t'adresser ni"});
db.iliadfr.insert({ pg : 2405, line : "injures ni reproches. Je sais que ton coeur, dans ta poitrine, est"});
db.iliadfr.insert({ pg : 2406, line : "plein de desseins excellents, car tes pensées sont les miennes."});
db.iliadfr.insert({ pg : 2407, line : "Nous réparerons ceci, si j'ai mal parlé. Va donc, et que les dieux"});
db.iliadfr.insert({ pg : 2408, line : "rendent mes paroles vaines!"});
db.iliadfr.insert({ pg : 2410, line : "Ayant ainsi parlé, il les laissa et alla vers d'autres. Et il"});
db.iliadfr.insert({ pg : 2411, line : "trouva Diomèdès, l'orgueilleux fils de Tydeus, immobile au milieu"});
db.iliadfr.insert({ pg : 2412, line : "de ses chevaux et de ses chars solides. Et Sthénélos, fils de"});
db.iliadfr.insert({ pg : 2413, line : "Kapaneus, était auprès de lui. Et Agamemnôn qui commande au loin,"});
db.iliadfr.insert({ pg : 2414, line : "les ayant vus, l'injuria et lui dit ces paroles ailées:"});
db.iliadfr.insert({ pg : 2416, line : "-- Ah! fils du brave Tydeus dompteur de chevaux, pourquoi"});
db.iliadfr.insert({ pg : 2417, line : "trembles-tu et regardes-tu entre les rangs? Certes, Tydeus n'avait"});
db.iliadfr.insert({ pg : 2418, line : "point coutume de trembler, mais il combattait hardiment l'ennemi,"});
db.iliadfr.insert({ pg : 2419, line : "et hors des rangs, en avant de ses compagnons. Je ne l'ai point vu"});
db.iliadfr.insert({ pg : 2420, line : "dans la guerre, mais on dit qu'il était au-dessus de tous. Il vint"});
db.iliadfr.insert({ pg : 2421, line : "à Mykènè avec Polyneikès égal aux dieux, pour rassembler les"});
db.iliadfr.insert({ pg : 2422, line : "peuples et faire une expédition contre les saintes murailles de"});
db.iliadfr.insert({ pg : 2423, line : "Thèbè. Et ils nous conjuraient de leur donner de courageux alliés,"});
db.iliadfr.insert({ pg : 2424, line : "et tous y consentaient, mais les signes contraires de Zeus nous en"});
db.iliadfr.insert({ pg : 2425, line : "empêchèrent. Et ils partirent, et quand ils furent arrivés auprès"});
db.iliadfr.insert({ pg : 2426, line : "de l'Asopos plein de joncs et d'herbes, Tydeus fut l'envoyé des"});
db.iliadfr.insert({ pg : 2427, line : "Akhaiens. Et il partit, et il trouva les Kadméiônes, en grand"});
db.iliadfr.insert({ pg : 2428, line : "nombre, mangeant dans la demeure de la force Étéokléenne. Et là,"});
db.iliadfr.insert({ pg : 2429, line : "le cavalier Tydeus ne fut point effrayé, bien qu'étranger et seul"});
db.iliadfr.insert({ pg : 2430, line : "au milieu des nombreux Kadméiônes. Et il les provoqua aux luttes"});
db.iliadfr.insert({ pg : 2431, line : "et les vainquit aisément, car Athènè le protégeait. Mais les"});
db.iliadfr.insert({ pg : 2432, line : "cavaliers Kadméiônes, pleins de colère, lui dressèrent, à son"});
db.iliadfr.insert({ pg : 2433, line : "départ, une embuscade de nombreux guerriers' commandés par Maiôn"});
db.iliadfr.insert({ pg : 2434, line : "Haimonide, tel que les immortels, et par Lyképhontès, hardi"});
db.iliadfr.insert({ pg : 2435, line : "guerrier, fils d'Autophonos. Et Tydeus les tua tous et n'en laissa"});
db.iliadfr.insert({ pg : 2436, line : "revenir qu'un seul. Obéissant aux signes des dieux, il laissa"});
db.iliadfr.insert({ pg : 2437, line : "revenir Maiôn. Tel était Tydeus l'Aitôlien; mais il a engendré un"});
db.iliadfr.insert({ pg : 2438, line : "fils qui ne le vaut point dans le combat, s'il parle mieux dans"});
db.iliadfr.insert({ pg : 2439, line : "l'Agora."});
db.iliadfr.insert({ pg : 2441, line : "Il parla ainsi, et le brave Diomèdès ne répondit rien, plein de"});
db.iliadfr.insert({ pg : 2442, line : "respect pour le roi vénérable. Mais le fils de l'illustre Kapaneus"});
db.iliadfr.insert({ pg : 2443, line : "répondit à l'Atréide:"});
db.iliadfr.insert({ pg : 2445, line : "-- Atréide, ne mens point, sachant que tu mens. Certes nous nous"});
db.iliadfr.insert({ pg : 2446, line : "glorifions de valoir beaucoup mieux que nos pères, nous qui,"});
db.iliadfr.insert({ pg : 2447, line : "confiants dans les signes des dieux, et avec l'aide de Zeus, avons"});
db.iliadfr.insert({ pg : 2448, line : "pris Thèbè aux sept portes, ayant conduit sous ses fortes"});
db.iliadfr.insert({ pg : 2449, line : "murailles des peuples moins nombreux. Nos pères ont péri par leurs"});
db.iliadfr.insert({ pg : 2450, line : "propres fautes. Ne compare donc point leur gloire à la nôtre."});
db.iliadfr.insert({ pg : 2452, line : "Et le robuste Diomèdès, avec un sombre regard, lui répondit:"});
db.iliadfr.insert({ pg : 2454, line : "-- Ami, tais-toi et obéis. Je ne m'irrite point de ce que le"});
db.iliadfr.insert({ pg : 2455, line : "prince des peuples, Agamemnôn, excite les Akhaiens aux belles"});
db.iliadfr.insert({ pg : 2456, line : "knèmides à combattre; car si les Akhaiens détruisent les Troiens"});
db.iliadfr.insert({ pg : 2457, line : "et prennent la sainte Ilios, il en aura la gloire; mais si les"});
db.iliadfr.insert({ pg : 2458, line : "Akhaiens sont détruits, il en portera le deuil. Occupons-nous tous"});
db.iliadfr.insert({ pg : 2459, line : "deux de la guerre impétueuse."});
db.iliadfr.insert({ pg : 2461, line : "Il parla ainsi, et sauta de son char à terre avec ses armes, et"});
db.iliadfr.insert({ pg : 2462, line : "l'airain retentit terriblement sur la poitrine du roi, et ce bruit"});
db.iliadfr.insert({ pg : 2463, line : "aurait troublé le coeur du plus brave."});
db.iliadfr.insert({ pg : 2465, line : "Et comme le flot de la mer roule avec rapidité vers le rivage,"});
db.iliadfr.insert({ pg : 2466, line : "poussé par Zéphyros, et, se gonflant d'abord sur la haute mer, se"});
db.iliadfr.insert({ pg : 2467, line : "brise violemment contre terre, et se hérisse autour des"});
db.iliadfr.insert({ pg : 2468, line : "promontoires en vomissant l'écume de la mer, de même les phalanges"});
db.iliadfr.insert({ pg : 2469, line : "pressées des Danaens se ruaient au combat. Et chaque chef donnait"});
db.iliadfr.insert({ pg : 2470, line : "ses ordres, et le reste marchait en silence. On eût dit une grande"});
db.iliadfr.insert({ pg : 2471, line : "multitude muette, pleine de respect pour ses chefs. Et les armes"});
db.iliadfr.insert({ pg : 2472, line : "brillantes resplendissaient tandis qu'ils marchaient en ordre."});
db.iliadfr.insert({ pg : 2473, line : "Mais, tels que les nombreuses brebis d'un homme riche, et qui"});
db.iliadfr.insert({ pg : 2474, line : "bêlent sans cesse à la voix des agneaux, tandis qu'on trait leur"});
db.iliadfr.insert({ pg : 2475, line : "lait blanc dans l'étable, les Troiens poussaient des cris confus"});
db.iliadfr.insert({ pg : 2476, line : "et tumultueux de tous les points de la vaste armée. Et leurs cris"});
db.iliadfr.insert({ pg : 2477, line : "étaient poussés en beaucoup de langues diverses, par des hommes"});
db.iliadfr.insert({ pg : 2478, line : "venus d'un grand nombre de pays lointains."});
db.iliadfr.insert({ pg : 2480, line : "Et Arès excitait les uns, et Athènè aux yeux clairs excitait les"});
db.iliadfr.insert({ pg : 2481, line : "autres, et partout allaient la crainte et la terreur et la"});
db.iliadfr.insert({ pg : 2482, line : "furieuse et insatiable Éris, soeur et compagne d'Arès tueur"});
db.iliadfr.insert({ pg : 2483, line : "d'hommes, et qui, d'abord, est faible, et qui, les pieds sur la"});
db.iliadfr.insert({ pg : 2484, line : "terre, porte bientôt sa tête dans l'Ouranos. Et elle s'avançait à"});
db.iliadfr.insert({ pg : 2485, line : "travers la foule, éveillant la haine et multipliant les"});
db.iliadfr.insert({ pg : 2486, line : "gémissements des hommes."});
db.iliadfr.insert({ pg : 2488, line : "Et quand ils se furent rencontrés, ils mêlèrent leurs boucliers,"});
db.iliadfr.insert({ pg : 2489, line : "leurs piques et la force des hommes aux cuirasses d'airain; et les"});
db.iliadfr.insert({ pg : 2490, line : "boucliers bombés se heurtèrent, et un vaste tumulte retentit. Et"});
db.iliadfr.insert({ pg : 2491, line : "on entendait les cris de victoire et les hurlements des hommes qui"});
db.iliadfr.insert({ pg : 2492, line : "renversaient ou étaient renversés, et le sang inondait la terre."});
db.iliadfr.insert({ pg : 2493, line : "Comme des fleuves, gonflés par l'hiver, tombent du haut des"});
db.iliadfr.insert({ pg : 2494, line : "montagnes et mêlent leurs eaux furieuses dans une vallée qu'ils"});
db.iliadfr.insert({ pg : 2495, line : "creusent profondément, et dont un berger entend de loin le fracas,"});
db.iliadfr.insert({ pg : 2496, line : "de même le tumulte des hommes confondus roulait."});
db.iliadfr.insert({ pg : 2498, line : "Et, le premier, Antilokhos tua Ekhépôlos Thalysiade, courageux"});
db.iliadfr.insert({ pg : 2499, line : "Troien, brave entre tous ceux qui combattaient en avant. Et il le"});
db.iliadfr.insert({ pg : 2500, line : "frappa au casque couvert de crins épais, et il perça le front, et"});
db.iliadfr.insert({ pg : 2501, line : "la pointe d'airain entra dans l'os. Et le Troien tomba comme une"});
db.iliadfr.insert({ pg : 2502, line : "tour dans le rude combat. Et le roi Elphènôr Khalkodontiade,"});
db.iliadfr.insert({ pg : 2503, line : "prince des magnanimes Abantes, le prit par les pieds pour le"});
db.iliadfr.insert({ pg : 2504, line : "traîner à l'abri des traits et le dépouiller de ses armes; mais sa"});
db.iliadfr.insert({ pg : 2505, line : "tentative fut brève, car le magnanime Agènôr, l'ayant vu traîner"});
db.iliadfr.insert({ pg : 2506, line : "le cadavre, le perça au côté, d'une pique d'airain, sous le"});
db.iliadfr.insert({ pg : 2507, line : "bouclier, tandis qu'il se courbait, et le tua. Et, sur lui, se rua"});
db.iliadfr.insert({ pg : 2508, line : "un combat furieux de Troiens et d'Akhaiens; et, comme des loups,"});
db.iliadfr.insert({ pg : 2509, line : "ils se jetaient les uns sur les autres, et chaque guerrier en"});
db.iliadfr.insert({ pg : 2510, line : "renversait un autre."});
db.iliadfr.insert({ pg : 2512, line : "C'est là qu'Aias Télamônien tua Simoéisios, fils d'Anthémiôn,"});
db.iliadfr.insert({ pg : 2513, line : "jeune et beau, et que sa mère, descendant de l'Ida pour visiter"});
db.iliadfr.insert({ pg : 2514, line : "ses troupeaux avec ses parents, avait enfanté sur les rives du"});
db.iliadfr.insert({ pg : 2515, line : "Simoéis, et c'est pourquoi on le nommait Simoéisios. Mais il ne"});
db.iliadfr.insert({ pg : 2516, line : "rendit pas à ses parents bien-aimés le prix de leurs soins, car sa"});
db.iliadfr.insert({ pg : 2517, line : "vie fut brève, ayant été dompté par la pique du magnanime Aias. Et"});
db.iliadfr.insert({ pg : 2518, line : "celui-ci le frappa à la poitrine, près de la mamelle droite, et la"});
db.iliadfr.insert({ pg : 2519, line : "pique d'airain sortit par l'épaule. Et Simoéisios tomba dans la"});
db.iliadfr.insert({ pg : 2520, line : "poussière comme un peuplier dont l'écorce est lisse, et qui,"});
db.iliadfr.insert({ pg : 2521, line : "poussant au milieu d'un grand marais, commence à se couvrir de"});
db.iliadfr.insert({ pg : 2522, line : "hauts rameaux, quand un constructeur de chars le tranche à l'aide"});
db.iliadfr.insert({ pg : 2523, line : "du fer aiguisé pour en faire la roue d'un beau char; et il gît,"});
db.iliadfr.insert({ pg : 2524, line : "flétri, aux bords du fleuve. Et le divin Aias dépouilla ainsi"});
db.iliadfr.insert({ pg : 2525, line : "Simoéisios Anthémionide."});
db.iliadfr.insert({ pg : 2527, line : "Et le Priamide Antiphos à la cuirasse éclatante, du milieu de la"});
db.iliadfr.insert({ pg : 2528, line : "foule, lança contre Aias sa pique aiguë; mais elle le manqua et"});
db.iliadfr.insert({ pg : 2529, line : "frappa à l'aine Leukos, brave compagnon d'Odysseus, tandis qu'il"});
db.iliadfr.insert({ pg : 2530, line : "traînait le cadavre, et le cadavre lui échappa des mains. Et"});
db.iliadfr.insert({ pg : 2531, line : "Odysseus, irrité de cette mort, s'avança, armé de l'airain"});
db.iliadfr.insert({ pg : 2532, line : "éclatant, au-delà des premiers rangs, regardant autour de lui et"});
db.iliadfr.insert({ pg : 2533, line : "agitant sa pique éclatante. Et les Troiens reculèrent devant"});
db.iliadfr.insert({ pg : 2534, line : "l'homme menaçant; mais il ne lança point sa pique en vain, car il"});
db.iliadfr.insert({ pg : 2535, line : "frappa Dèmokoôn, fils naturel de Priamos, et qui était venu"});
db.iliadfr.insert({ pg : 2536, line : "d'Abydos avec ses chevaux rapides. Et Odysseus, vengeant son"});
db.iliadfr.insert({ pg : 2537, line : "compagnon, frappa Dèmokoôn à la tempe, et la pointe d'airain"});
db.iliadfr.insert({ pg : 2538, line : "sortit par l'autre tempe, et l'obscurité couvrit ses yeux. Et il"});
db.iliadfr.insert({ pg : 2539, line : "tomba avec bruit, et ses armes retentirent. Et les Troiens les"});
db.iliadfr.insert({ pg : 2540, line : "plus avancés reculèrent, et même l'illustre Hektôr. Et les"});
db.iliadfr.insert({ pg : 2541, line : "Akhaiens poussaient de grands cris, entraînant les cadavres et se"});
db.iliadfr.insert({ pg : 2542, line : "ruant en avant. Et Apollôn s'indigna, les ayant vus du faîte de"});
db.iliadfr.insert({ pg : 2543, line : "Pergamos, et d'une voix haute il excita les Troiens:"});
db.iliadfr.insert({ pg : 2545, line : "-- Troiens, dompteurs de chevaux, ne le cédez point aux Akhaiens."});
db.iliadfr.insert({ pg : 2546, line : "Leur peau n'est ni de pierre ni de fer pour résister, quand elle"});
db.iliadfr.insert({ pg : 2547, line : "en est frappée, à l'airain qui coupe la chair. Akhilleus, le fils"});
db.iliadfr.insert({ pg : 2548, line : "de Thétis à la belle chevelure, ne combat point; il couve, près de"});
db.iliadfr.insert({ pg : 2549, line : "ses nefs, la colère qui lui ronge le coeur."});
db.iliadfr.insert({ pg : 2551, line : "Ainsi parla le dieu terrible du haut de la citadelle. Et"});
db.iliadfr.insert({ pg : 2552, line : "Tritogénéia, la glorieuse fille de Zeus, marchant au travers de la"});
db.iliadfr.insert({ pg : 2553, line : "foule, excitait les Akhaiens là où ils reculaient."});
db.iliadfr.insert({ pg : 2555, line : "Et la Moire saisit Diôrès Amarynkéide, et il fut frappé à la"});
db.iliadfr.insert({ pg : 2556, line : "cheville droite d'une pierre anguleuse. Et ce fut l'Imbraside"});
db.iliadfr.insert({ pg : 2557, line : "Peiros, prince des Thrakiens, et qui était venu d'Ainos, qui le"});
db.iliadfr.insert({ pg : 2558, line : "frappa. Et la pierre rude fracassa les deux tendons et les os. Et"});
db.iliadfr.insert({ pg : 2559, line : "Diôrès tomba à la renverse dans la poussière, étendant les mains"});
db.iliadfr.insert({ pg : 2560, line : "vers ses compagnons et respirant à peine. Et Peiros accourut et"});
db.iliadfr.insert({ pg : 2561, line : "enfonça sa pique près du nombril, et les intestins se répandirent"});
db.iliadfr.insert({ pg : 2562, line : "à terre, et l'obscurité couvrit ses yeux. Et comme Peiros"});
db.iliadfr.insert({ pg : 2563, line : "s'élançait, l'Aitôlien Moas le frappa de sa pique dans la"});
db.iliadfr.insert({ pg : 2564, line : "poitrine, au-dessus de la mamelle, et l'airain traversa le poumon."});
db.iliadfr.insert({ pg : 2565, line : "Puis il accourut, arracha de la poitrine la pique terrible, et,"});
db.iliadfr.insert({ pg : 2566, line : "tirant son épée aiguë, il ouvrit le ventre de l'homme et le tua."});
db.iliadfr.insert({ pg : 2567, line : "Mais il ne le dépouilla point de ses armes, car les Thrakiens aux"});
db.iliadfr.insert({ pg : 2568, line : "cheveux ras et aux longues lances entourèrent leur chef, et"});
db.iliadfr.insert({ pg : 2569, line : "repoussèrent Moas, tout robuste, hardi et grand qu'il était. Et il"});
db.iliadfr.insert({ pg : 2570, line : "recula loin d'eux. Ainsi les deux chefs, l'un des Thrakiens,"});
db.iliadfr.insert({ pg : 2571, line : "l'autre des Épéiens aux tuniques d'airain, étaient couchés côte à"});
db.iliadfr.insert({ pg : 2572, line : "côte dans la poussière, et les cadavres s'amassaient autour d'eux."});
db.iliadfr.insert({ pg : 2574, line : "Si un guerrier, sans peur du combat, et que l'airain aigu n'eût"});
db.iliadfr.insert({ pg : 2575, line : "encore ni frappé ni blessé, eût parcouru la mêlée furieuse, et que"});
db.iliadfr.insert({ pg : 2576, line : "Pallas Athènè l'eût conduit par la main, écartant de lui"});
db.iliadfr.insert({ pg : 2577, line : "l'impétuosité des traits, certes, il eût vu, en ce jour, une"});
db.iliadfr.insert({ pg : 2578, line : "multitude de Troiens et d'Akhaiens renversés et couchés"});
db.iliadfr.insert({ pg : 2579, line : "confusément sur la poussière."});
db.iliadfr.insert({ pg : 2582, line : "Chant 5"});
db.iliadfr.insert({ pg : 2584, line : "Alors, Pallas Athènè donna la force et l'audace au Tydéide"});
db.iliadfr.insert({ pg : 2585, line : "Diomèdès, afin qu'il s'illustrât entre tous les Argiens et"});
db.iliadfr.insert({ pg : 2586, line : "remportât une grande gloire. Et elle fit jaillir de son casque et"});
db.iliadfr.insert({ pg : 2587, line : "de son bouclier un feu inextinguible, semblable à l'étoile de"});
db.iliadfr.insert({ pg : 2588, line : "l'automne qui éclate et resplendit hors de l'Okéanos. Tel ce feu"});
db.iliadfr.insert({ pg : 2589, line : "jaillissait de sa tête et de ses épaules. Et elle le poussa dans"});
db.iliadfr.insert({ pg : 2590, line : "la mêlée où tous se ruaient tumultueusement."});
db.iliadfr.insert({ pg : 2592, line : "Parmi les Troiens vivait Darès, riche et irréprochable"});
db.iliadfr.insert({ pg : 2593, line : "sacrificateur de Hèphaistos, et il avait deux fils, Phygeus et"});
db.iliadfr.insert({ pg : 2594, line : "Idaios, habiles à tous les combats. Et tous deux, sur un même"});
db.iliadfr.insert({ pg : 2595, line : "char, se ruèrent contre le Tydéide, qui était à pied. Et,"});
db.iliadfr.insert({ pg : 2596, line : "lorsqu'ils se furent rapprochés, Phygeus, le premier, lança sa"});
db.iliadfr.insert({ pg : 2597, line : "longue pique, et la pointe effleura l'épaule gauche du Tydéide,"});
db.iliadfr.insert({ pg : 2598, line : "mais il ne le blessa point. Et celui-ci, à son tour, lança sa"});
db.iliadfr.insert({ pg : 2599, line : "pique, et le trait ne fut point inutile qui partit de sa main, car"});
db.iliadfr.insert({ pg : 2600, line : "il s'enfonça dans la poitrine, entre les mamelles, et jeta le"});
db.iliadfr.insert({ pg : 2601, line : "guerrier à bas. Et Idaios s'enfuit, abandonnant son beau char et"});
db.iliadfr.insert({ pg : 2602, line : "n'osant défendre son frère tué. Certes, il n'eût point, pour cela,"});
db.iliadfr.insert({ pg : 2603, line : "évité la noire mort; mais Hèphaistos, l'ayant enveloppé d'une"});
db.iliadfr.insert({ pg : 2604, line : "nuée, l'enleva, afin que la vieillesse de leur vieux père ne fût"});
db.iliadfr.insert({ pg : 2605, line : "point désespérée. Et le fils du magnanime Tydeus saisit leurs"});
db.iliadfr.insert({ pg : 2606, line : "chevaux, qu'il remit à ses compagnons pour être conduits aux nefs"});
db.iliadfr.insert({ pg : 2607, line : "creuses."});
db.iliadfr.insert({ pg : 2609, line : "Et les magnanimes Troiens, voyant les deux fils de Darès, l'un en"});
db.iliadfr.insert({ pg : 2610, line : "fuite et l'autre mort auprès de son char, furent troublés jusqu'au"});
db.iliadfr.insert({ pg : 2611, line : "fond de leurs coeurs. Mais Athènè aux yeux clairs, saisissant le"});
db.iliadfr.insert({ pg : 2612, line : "furieux Arès par la main, lui parla ainsi:"});
db.iliadfr.insert({ pg : 2614, line : "-- Arès, Arès, fléau des hommes, tout sanglant, et qui renverses"});
db.iliadfr.insert({ pg : 2615, line : "les murailles, ne laisserons-nous point combattre les Troiens et"});
db.iliadfr.insert({ pg : 2616, line : "les Akhaiens? Que le père Zeus accorde la gloire à qui il voudra."});
db.iliadfr.insert({ pg : 2617, line : "Retirons-nous et évitons la colère de Zeus."});
db.iliadfr.insert({ pg : 2619, line : "Ayant ainsi parlé, elle conduisit le furieux Arès hors du combat"});
db.iliadfr.insert({ pg : 2620, line : "et le fit asseoir sur la haute rive du Skamandros. Et les Danaens"});
db.iliadfr.insert({ pg : 2621, line : "repoussèrent les Troiens. Chacun des chefs tua un guerrier. Et, le"});
db.iliadfr.insert({ pg : 2622, line : "premier, le roi Agamemnôn précipita de son char le grand Odios,"});
db.iliadfr.insert({ pg : 2623, line : "chef des Alizônes. Comme celui-ci fuyait, il lui enfonça sa pique"});
db.iliadfr.insert({ pg : 2624, line : "dans le dos, entre les épaules, et elle traversa la poitrine, et"});
db.iliadfr.insert({ pg : 2625, line : "les armes d'Odios résonnèrent dans sa chute."});
db.iliadfr.insert({ pg : 2627, line : "Et Idoméneus tua Phaistos, fils du Maiônien Bôros, qui était venu"});
db.iliadfr.insert({ pg : 2628, line : "de la fertile Tarnè, l'illustre Idoméneus le perça à l'épaule"});
db.iliadfr.insert({ pg : 2629, line : "droite, de sa longue pique, comme il montait sur son char. Et il"});
db.iliadfr.insert({ pg : 2630, line : "tomba, et une ombre affreuse l'enveloppa, et les serviteurs"});
db.iliadfr.insert({ pg : 2631, line : "d'Idoméneus le dépouillèrent."});
db.iliadfr.insert({ pg : 2633, line : "Et l'Atréide Ménélaos tua de sa pique aiguë Skamandrios habile à"});
db.iliadfr.insert({ pg : 2634, line : "la chasse, fils de Strophios. C'était un excellent chasseur"});
db.iliadfr.insert({ pg : 2635, line : "qu'Artémis avait instruit elle-même à percer les bêtes fauves, et"});
db.iliadfr.insert({ pg : 2636, line : "qu'elle avait nourri dans les bois, sur les montagnes. Mais ni son"});
db.iliadfr.insert({ pg : 2637, line : "habileté à lancer les traits, ni Artémis qui se réjouit de ses"});
db.iliadfr.insert({ pg : 2638, line : "flèches, ne lui servirent. Comme il fuyait, l'illustre Atréide"});
db.iliadfr.insert({ pg : 2639, line : "Ménélaos le perça de sa pique dans le dos, entre les deux épaules,"});
db.iliadfr.insert({ pg : 2640, line : "et lui traversa la poitrine. Et il tomba sur la face, et ses armes"});
db.iliadfr.insert({ pg : 2641, line : "résonnèrent."});
db.iliadfr.insert({ pg : 2643, line : "Et Mèrionès tua Phéréklos, fils du charpentier Harmôn, qui"});
db.iliadfr.insert({ pg : 2644, line : "fabriquait adroitement toute chose de ses mains et que Pallas"});
db.iliadfr.insert({ pg : 2645, line : "Athènè aimait beaucoup. Et c'était lui qui avait construit pour"});
db.iliadfr.insert({ pg : 2646, line : "Alexandros ces nefs égales qui devaient causer tant de maux aux"});
db.iliadfr.insert({ pg : 2647, line : "Troiens et à lui-même; car il ignorait les oracles des dieux. Et"});
db.iliadfr.insert({ pg : 2648, line : "Mèrionès, poursuivant Phéréklos, le frappa à la fesse droite, et"});
db.iliadfr.insert({ pg : 2649, line : "la pointe pénétra dans l'os jusque dans la vessie. Et il tomba en"});
db.iliadfr.insert({ pg : 2650, line : "gémissant, et la mort l'enveloppa."});
db.iliadfr.insert({ pg : 2652, line : "Et Mégès tua Pèdaios, fils illégitime d'Antènôr, mais que la"});
db.iliadfr.insert({ pg : 2653, line : "divine Théanô avait nourri avec soin au milieu de ses enfants"});
db.iliadfr.insert({ pg : 2654, line : "bien-aimés, afin de plaire à son mari. Et l'illustre Phyléide,"});
db.iliadfr.insert({ pg : 2655, line : "s'approchant de lui, le frappa de sa pique aiguë derrière la tête."});
db.iliadfr.insert({ pg : 2656, line : "Et l'airain, à travers les dents, coupa la langue, et il tomba"});
db.iliadfr.insert({ pg : 2657, line : "dans la poussière en serrant de ses dents le froid airain."});
db.iliadfr.insert({ pg : 2659, line : "Et l'Évaimonide Eurypylos tua le divin Hypsènôr, fils du magnanime"});
db.iliadfr.insert({ pg : 2660, line : "Dolopiôn, sacrificateur du Skamandros, et que le peuple honorait"});
db.iliadfr.insert({ pg : 2661, line : "comme un dieu. Et l'illustre fils d'Évaimôn, Eurypylos, se ruant"});
db.iliadfr.insert({ pg : 2662, line : "sur lui, comme il fuyait, le frappa de l'épée à l'épaule et lui"});
db.iliadfr.insert({ pg : 2663, line : "coupa le bras, qui tomba sanglant et lourd. Et la mort pourprée et"});
db.iliadfr.insert({ pg : 2664, line : "la Moire violente emplirent ses yeux."});
db.iliadfr.insert({ pg : 2666, line : "Tandis qu'ils combattaient ainsi dans la rude mêlée, nul n'aurait"});
db.iliadfr.insert({ pg : 2667, line : "pu reconnaître si le Tydéide était du côté des Troiens ou du côté"});
db.iliadfr.insert({ pg : 2668, line : "des Akhaiens. Il courait à travers la plaine, semblable à un"});
db.iliadfr.insert({ pg : 2669, line : "fleuve furieux et débordé qui roule impétueusement et renverse les"});
db.iliadfr.insert({ pg : 2670, line : "ponts. Ni les digues ne l'arrêtent, ni les enclos des vergers"});
db.iliadfr.insert({ pg : 2671, line : "verdoyants, car la pluie de Zeus abonde, et les beaux travaux des"});
db.iliadfr.insert({ pg : 2672, line : "jeunes hommes sont détruits. Ainsi les épaisses phalanges des"});
db.iliadfr.insert({ pg : 2673, line : "Troiens se dissipaient devant le Tydéide, et leur multitude ne"});
db.iliadfr.insert({ pg : 2674, line : "pouvait soutenir son choc."});
db.iliadfr.insert({ pg : 2676, line : "Et l'illustre fils de Lykaôn, l'ayant aperçu se ruant par la"});
db.iliadfr.insert({ pg : 2677, line : "plaine et dispersant les phalanges, tendit aussitôt contre lui son"});
db.iliadfr.insert({ pg : 2678, line : "arc recourbé, et, comme il s'élançait, le frappa à l'épaule"});
db.iliadfr.insert({ pg : 2679, line : "droite, au défaut de la cuirasse. Et la flèche acerbe vola en"});
db.iliadfr.insert({ pg : 2680, line : "sifflant et s'enfonça, et la cuirasse ruissela de sang. Et"});
db.iliadfr.insert({ pg : 2681, line : "l'illustre fils de Lykaôn s'écria d'une voix haute:"});
db.iliadfr.insert({ pg : 2683, line : "-- Courage, Troiens, cavaliers magnanimes! Le plus brave des"});
db.iliadfr.insert({ pg : 2684, line : "Akhaiens est blessé, et je ne pense pas qu'il supporte longtemps"});
db.iliadfr.insert({ pg : 2685, line : "ma flèche violente, s'il est vrai que le roi, fils de Zeus, m'ait"});
db.iliadfr.insert({ pg : 2686, line : "poussé à quitter la Lykiè."});
db.iliadfr.insert({ pg : 2688, line : "Il parla ainsi orgueilleusement, mais la flèche rapide n'avait"});
db.iliadfr.insert({ pg : 2689, line : "point tué le Tydéide, qui, reculant, s'arrêta devant ses chevaux"});
db.iliadfr.insert({ pg : 2690, line : "et son char, et dit à Sthénélos, fils de Kapaneus:"});
db.iliadfr.insert({ pg : 2692, line : "-- Hâte-toi, ami Kapanéide! Descends du char et retire cette"});
db.iliadfr.insert({ pg : 2693, line : "flèche amère."});
db.iliadfr.insert({ pg : 2695, line : "Il parla ainsi, et Sthénélos, sautant à bas du char, arracha de"});
db.iliadfr.insert({ pg : 2696, line : "l'épaule la flèche rapide. Et le sang jaillit sur la tunique, et"});
db.iliadfr.insert({ pg : 2697, line : "Diomèdès hardi au combat pria ainsi:"});
db.iliadfr.insert({ pg : 2699, line : "-- Entends-moi, fille indomptée de Zeus tempétueux! Si jamais tu"});
db.iliadfr.insert({ pg : 2700, line : "nous as protégés, mon père et moi, dans la guerre cruelle, Athènè!"});
db.iliadfr.insert({ pg : 2701, line : "secours-moi de nouveau. Accorde-moi de tuer ce guerrier. Amène-le"});
db.iliadfr.insert({ pg : 2702, line : "au-devant de ma pique impétueuse, lui qui m'a blessé le premier,"});
db.iliadfr.insert({ pg : 2703, line : "et qui s'en glorifie, et qui pense que je ne verrai pas longtemps"});
db.iliadfr.insert({ pg : 2704, line : "encore la splendide lumière de Hélios."});
db.iliadfr.insert({ pg : 2706, line : "Il parla ainsi en priant, et Pallas Athènè l'exauça. Elle rendit"});
db.iliadfr.insert({ pg : 2707, line : "tous ses membres, et ses pieds et ses mains plus agiles; et"});
db.iliadfr.insert({ pg : 2708, line : "s'approchant, elle lui dit en paroles ailées:"});
db.iliadfr.insert({ pg : 2710, line : "-- Reprends courage, ô Diomèdès, et combats contre les Troiens,"});
db.iliadfr.insert({ pg : 2711, line : "car j'ai mis dans ta poitrine l'intrépide vigueur que possédait le"});
db.iliadfr.insert({ pg : 2712, line : "porte-bouclier, le cavalier Tydeus. Et j'ai dissipé le nuage qui"});
db.iliadfr.insert({ pg : 2713, line : "était sur tes yeux, afin que tu reconnaisses les dieux et les"});
db.iliadfr.insert({ pg : 2714, line : "hommes. Si un immortel venait te tenter, ne lutte point contre les"});
db.iliadfr.insert({ pg : 2715, line : "dieux immortels; mais si Aphroditè, la fille de Zeus, descendait"});
db.iliadfr.insert({ pg : 2716, line : "dans la mêlée, frappe-la de l'airain aigu."});
db.iliadfr.insert({ pg : 2718, line : "Ayant ainsi parlé, Athènè aux yeux clairs s'éloigna, et le Tydéide"});
db.iliadfr.insert({ pg : 2719, line : "retourna à la charge, mêlé aux premiers rangs. Et, naguère, il"});
db.iliadfr.insert({ pg : 2720, line : "était, certes, plein d'ardeur pour combattre les Troiens, mais son"});
db.iliadfr.insert({ pg : 2721, line : "courage est maintenant trois fois plus grand. Il est comme un lion"});
db.iliadfr.insert({ pg : 2722, line : "qui, dans un champ où paissaient des brebis laineuses, au moment"});
db.iliadfr.insert({ pg : 2723, line : "où il sautait vers l'étable, a été blessé par un pâtre, et non"});
db.iliadfr.insert({ pg : 2724, line : "tué. Cette blessure accroît ses forces. Il entre dans l'étable et"});
db.iliadfr.insert({ pg : 2725, line : "disperse les brebis, qu'on n'ose plus défendre. Et celles-ci"});
db.iliadfr.insert({ pg : 2726, line : "gisent égorgées, les unes sur les autres; et le lion bondit hors"});
db.iliadfr.insert({ pg : 2727, line : "de l'enclos. Ainsi le brave Diomèdès se rua sur les Troiens."});
db.iliadfr.insert({ pg : 2729, line : "Alors, il tua Astynoos et Hypeirôn, princes des peuples. Et il"});
db.iliadfr.insert({ pg : 2730, line : "perça l'un, de sa pique d'airain, au-dessus de la mamelle; et, de"});
db.iliadfr.insert({ pg : 2731, line : "sa grande épée, il brisa la clavicule de l'autre et sépara la tête"});
db.iliadfr.insert({ pg : 2732, line : "de l'épaule et du dos. Puis, les abandonnant, il se jeta sur Abas"});
db.iliadfr.insert({ pg : 2733, line : "et Polyeidos, fils du vieux Eurydamas, interprète des songes. Mais"});
db.iliadfr.insert({ pg : 2734, line : "le vieillard ne les avait point consultés au départ de ses"});
db.iliadfr.insert({ pg : 2735, line : "enfants. Et le brave Diomèdès les tua."});
db.iliadfr.insert({ pg : 2737, line : "Et il se jeta sur Xanthos et Thoôn, fils tardifs de Phainopos, qui"});
db.iliadfr.insert({ pg : 2738, line : "les avait eus dans sa triste vieillesse, et qui n'avait point"});
db.iliadfr.insert({ pg : 2739, line : "engendré d'autres enfants à qui il pût laisser ses biens. Et le"});
db.iliadfr.insert({ pg : 2740, line : "Tydéide les tua, leur arrachant l'âme et ne laissant que le deuil"});
db.iliadfr.insert({ pg : 2741, line : "et les tristes douleurs à leur père, qui ne devait point les"});
db.iliadfr.insert({ pg : 2742, line : "revoir vivants au retour du combat, et dont l'héritage serait"});
db.iliadfr.insert({ pg : 2743, line : "partagé selon la loi."});
db.iliadfr.insert({ pg : 2745, line : "Et Diomèdès saisit deux fils du Dardanide Priamos, montés sur un"});
db.iliadfr.insert({ pg : 2746, line : "même char, Ekhémôn et Khromios. Comme un lion, bondissant sur des"});
db.iliadfr.insert({ pg : 2747, line : "boeufs, brise le cou d'une génisse ou d'un taureau paissant dans"});
db.iliadfr.insert({ pg : 2748, line : "les bois, ainsi le fils de Tydeus, les renversant tous deux de"});
db.iliadfr.insert({ pg : 2749, line : "leur char, les dépouilla de leurs armes et remit leurs chevaux à"});
db.iliadfr.insert({ pg : 2750, line : "ses compagnons pour être conduits aux nefs."});
db.iliadfr.insert({ pg : 2752, line : "Mais Ainéias, le voyant dissiper les lignes des guerriers,"});
db.iliadfr.insert({ pg : 2753, line : "s'avança à travers la mêlée et le bruissement des piques,"});
db.iliadfr.insert({ pg : 2754, line : "cherchant de tous côtés le divin Pandaros. Et il rencontra le"});
db.iliadfr.insert({ pg : 2755, line : "brave et irréprochable fils de Lykaôn, et, s'approchant, il lui"});
db.iliadfr.insert({ pg : 2756, line : "dit:"});
db.iliadfr.insert({ pg : 2758, line : "-- Pandaros! où sont ton arc et tes flèches? Et ta gloire, quel"});
db.iliadfr.insert({ pg : 2759, line : "guerrier pourrait te la disputer? Qui pourrait, en Lykiè, se"});
db.iliadfr.insert({ pg : 2760, line : "glorifier de l'emporter sur toi? Allons, tends les mains vers Zeus"});
db.iliadfr.insert({ pg : 2761, line : "et envoie une flèche à ce guerrier. Je ne sais qui il est, mais il"});
db.iliadfr.insert({ pg : 2762, line : "triomphe et il a déjà infligé de grands maux aux Troiens. Déjà il"});
db.iliadfr.insert({ pg : 2763, line : "a fait ployer les genoux d'une multitude de braves. Peut-être est-"});
db.iliadfr.insert({ pg : 2764, line : "ce un dieu irrité contre les Troiens à cause de sacrifices"});
db.iliadfr.insert({ pg : 2765, line : "négligés. Et la colère d'un dieu est lourde."});
db.iliadfr.insert({ pg : 2767, line : "Et l'illustre fils de Lykaôn lui répondit:"});
db.iliadfr.insert({ pg : 2769, line : "-- Ainéias, conseiller des Troiens revêtus d'airain, je crois que"});
db.iliadfr.insert({ pg : 2770, line : "ce guerrier est le Tydéide. Je le reconnais à son bouclier, à son"});
db.iliadfr.insert({ pg : 2771, line : "casque aux trois cônes et à ses chevaux. Cependant, je ne sais si"});
db.iliadfr.insert({ pg : 2772, line : "ce n'est point un dieu. Si ce guerrier est le brave fils de"});
db.iliadfr.insert({ pg : 2773, line : "Tydeus, comme je l'ai dit, certes, il n'est point ainsi furieux"});
db.iliadfr.insert({ pg : 2774, line : "sans l'appui d'un dieu. Sans doute, un des immortels, couvert"});
db.iliadfr.insert({ pg : 2775, line : "d'une nuée, se tient auprès de lui et détourne les flèches"});
db.iliadfr.insert({ pg : 2776, line : "rapides. Déjà je l'ai frappé d'un trait à l'épaule droite, au"});
db.iliadfr.insert({ pg : 2777, line : "défaut de la cuirasse. J'étais certain de l'avoir envoyé chez"});
db.iliadfr.insert({ pg : 2778, line : "Aidès, et voici que je ne l'ai point tué. Sans doute quelque dieu"});
db.iliadfr.insert({ pg : 2779, line : "est irrité contre nous. Ni mes chevaux ni mon char ne sont ici."});
db.iliadfr.insert({ pg : 2780, line : "J'ai, dans les demeures de Lykaôn, onze beaux chars tout neufs,"});
db.iliadfr.insert({ pg : 2781, line : "couverts de larges draperies. Auprès de chacun d'eux sont deux"});
db.iliadfr.insert({ pg : 2782, line : "chevaux qui paissent l'orge et l'avoine. Certes, le belliqueux"});
db.iliadfr.insert({ pg : 2783, line : "vieillard Lykaôn, quand je partis de mes belles demeures, me donna"});
db.iliadfr.insert({ pg : 2784, line : "de nombreux conseils. Il m'ordonna, monté sur mon char et traîné"});
db.iliadfr.insert({ pg : 2785, line : "par mes chevaux, de devancer tous les Troiens dans les mâles"});
db.iliadfr.insert({ pg : 2786, line : "combats. J'aurais mieux fait d'obéir; mais je ne le voulus point,"});
db.iliadfr.insert({ pg : 2787, line : "désirant épargner mes chevaux accoutumés à manger abondamment, et"});
db.iliadfr.insert({ pg : 2788, line : "de peur qu'ils manquassent de nourriture au milieu de guerriers"});
db.iliadfr.insert({ pg : 2789, line : "assiégés. Je les laissai, et vins à pied vers Ilios, certain de"});
db.iliadfr.insert({ pg : 2790, line : "mon arc, dont je ne devais pas me glorifier cependant. Déjà, je"});
db.iliadfr.insert({ pg : 2791, line : "l'ai tendu contre deux chefs, l'Atréide et le Tydéide, et je les"});
db.iliadfr.insert({ pg : 2792, line : "ai blessés, et j'ai fait couler leur sang, et je n'ai fait que les"});
db.iliadfr.insert({ pg : 2793, line : "irriter. Certes, ce fut par une mauvaise destinée que je détachais"});
db.iliadfr.insert({ pg : 2794, line : "du mur cet arc recourbé, le jour funeste où je vins, dans la"});
db.iliadfr.insert({ pg : 2795, line : "riante Ilios, commander aux Troiens, pour plaire au divin Hektôr."});
db.iliadfr.insert({ pg : 2796, line : "Si je retourne jamais, et si je revois de mes yeux ma patrie et ma"});
db.iliadfr.insert({ pg : 2797, line : "femme et ma haute demeure, qu'aussitôt un ennemi me coupe la tête,"});
db.iliadfr.insert({ pg : 2798, line : "si je ne jette, brisé de mes mains, dans le feu éclatant, cet arc"});
db.iliadfr.insert({ pg : 2799, line : "qui m'aura été un compagnon inutile!"});
db.iliadfr.insert({ pg : 2801, line : "Et le chef des Troiens, Ainéias, lui répondit:"});
db.iliadfr.insert({ pg : 2803, line : "-- Ne parle point tant. Rien ne changera si nous ne poussons à cet"});
db.iliadfr.insert({ pg : 2804, line : "homme, sur notre char et nos chevaux, et couverts de nos armes."});
db.iliadfr.insert({ pg : 2805, line : "Tiens! monte sur mon char, et vois quels sont les chevaux de Trôs,"});
db.iliadfr.insert({ pg : 2806, line : "habiles à poursuivre ou à fuir rapidement dans la plaine. Ils nous"});
db.iliadfr.insert({ pg : 2807, line : "ramèneront saufs dans la ville, si Zeus donne la victoire au"});
db.iliadfr.insert({ pg : 2808, line : "Tydéide Diomèdès. Viens! saisis le fouet et les belles rênes, et"});
db.iliadfr.insert({ pg : 2809, line : "je descendrai pour combattre; ou combats toi-même, et je guiderai"});
db.iliadfr.insert({ pg : 2810, line : "les chevaux."});
db.iliadfr.insert({ pg : 2812, line : "Et l'illustre fils de Lykaôn lui répondit:"});
db.iliadfr.insert({ pg : 2814, line : "-- Ainéias, charge-toi des rênes et des chevaux. Ils traîneront"});
db.iliadfr.insert({ pg : 2815, line : "mieux le char sous le conducteur accoutumé, si nous prenions la"});
db.iliadfr.insert({ pg : 2816, line : "fuite devant le fils de Tydeus. Peut-être, pleins de terreur,"});
db.iliadfr.insert({ pg : 2817, line : "resteraient-ils inertes et ne voudraient-ils plus nous emporter"});
db.iliadfr.insert({ pg : 2818, line : "hors du combat, n'entendant plus ta voix."});
db.iliadfr.insert({ pg : 2820, line : "Ayant ainsi parlé, ils montèrent sur le char brillant et"});
db.iliadfr.insert({ pg : 2821, line : "poussèrent les chevaux rapides contre le Tydéide. Et l'illustre"});
db.iliadfr.insert({ pg : 2822, line : "fils de Kapaneus, Sthénélos, les vit; et aussitôt il dit au"});
db.iliadfr.insert({ pg : 2823, line : "Tydéide ces paroles ailées:"});
db.iliadfr.insert({ pg : 2825, line : "-- Tydéide Diomèdès, le plus cher à mon âme, je vois deux braves"});
db.iliadfr.insert({ pg : 2826, line : "guerriers qui se préparent à te combattre. Tous deux sont pleins"});
db.iliadfr.insert({ pg : 2827, line : "de force. L'un est l'habile archer Pandaros, qui se glorifie"});
db.iliadfr.insert({ pg : 2828, line : "d'être le fils de Lykaôn. L'autre est Ainéias, qui se glorifie"});
db.iliadfr.insert({ pg : 2829, line : "d'être le fils du magnanime Ankhisès, et qui a pour mère Aphroditè"});
db.iliadfr.insert({ pg : 2830, line : "elle-même. Reculons donc, et ne te jette point en avant, si tu ne"});
db.iliadfr.insert({ pg : 2831, line : "veux perdre ta chère âme."});
db.iliadfr.insert({ pg : 2833, line : "Et le brave Diomèdès, le regardant d'un oeil sombre, lui répondit:"});
db.iliadfr.insert({ pg : 2835, line : "-- Ne parle point de fuir, car je ne pense point que tu me"});
db.iliadfr.insert({ pg : 2836, line : "persuades. Ce n'est point la coutume de ma race de fuir et de"});
db.iliadfr.insert({ pg : 2837, line : "trembler. Je possède encore toutes mes forces. J'irai au-devant de"});
db.iliadfr.insert({ pg : 2838, line : "ces guerriers. Pallas Athènè ne me permet point de craindre. Leurs"});
db.iliadfr.insert({ pg : 2839, line : "chevaux rapides ne nous les arracheront point tous deux, si, du"});
db.iliadfr.insert({ pg : 2840, line : "moins, un seul en réchappe. Mais je te le dis, et souviens-toi de"});
db.iliadfr.insert({ pg : 2841, line : "mes paroles: si la sage Athènè me donnait la gloire de les tuer"});
db.iliadfr.insert({ pg : 2842, line : "tous deux, arrête nos chevaux rapides, attache les rênes au char,"});
db.iliadfr.insert({ pg : 2843, line : "cours aux chevaux d'Ainéias et pousse-les parmi les Akhaiens aux"});
db.iliadfr.insert({ pg : 2844, line : "belles knèmides. Ils sont de la race de ceux que le prévoyant Zeus"});
db.iliadfr.insert({ pg : 2845, line : "donna à Trôs en échange de son fils Ganymèdès, et ce sont les"});
db.iliadfr.insert({ pg : 2846, line : "meilleurs chevaux qui soient sous Éôs et Hélios. Le roi des"});
db.iliadfr.insert({ pg : 2847, line : "hommes, Ankhisès, à l'insu de Laomédôn, fit saillir des cavales"});
db.iliadfr.insert({ pg : 2848, line : "par ces étalons, et il en eut six rejetons. Il en retient quatre"});
db.iliadfr.insert({ pg : 2849, line : "qu'il nourrit à la crèche, et il a donné ces deux-ci, rapides à la"});
db.iliadfr.insert({ pg : 2850, line : "fuite, à Ainéias. Si nous les enlevons, nous remporterons une"});
db.iliadfr.insert({ pg : 2851, line : "grande gloire."});
db.iliadfr.insert({ pg : 2853, line : "Pendant qu'ils se parlaient ainsi, les deux Troiens poussaient"});
db.iliadfr.insert({ pg : 2854, line : "vers eux leurs chevaux rapides, et le premier, l'illustre fils de"});
db.iliadfr.insert({ pg : 2855, line : "Lykaôn, s'écria:"});
db.iliadfr.insert({ pg : 2857, line : "-- Très brave et très excellent guerrier, fils de l'illustre"});
db.iliadfr.insert({ pg : 2858, line : "Tydeus, mon trait rapide, ma flèche amère, ne t'a point tué; mais"});
db.iliadfr.insert({ pg : 2859, line : "je vais tenter de te percer de ma pique."});
db.iliadfr.insert({ pg : 2861, line : "Il parla, et, lançant sa longue pique, frappa le bouclier du"});
db.iliadfr.insert({ pg : 2862, line : "Tydéide. La pointe d'airain siffla et s'enfonça dans la cuirasse,"});
db.iliadfr.insert({ pg : 2863, line : "et l'illustre fils de Lykaôn cria à voix haute:"});
db.iliadfr.insert({ pg : 2865, line : "-- Tu es blessé dans le ventre! Je ne pense point que tu survives"});
db.iliadfr.insert({ pg : 2866, line : "longtemps, et tu vas me donner une grande gloire."});
db.iliadfr.insert({ pg : 2868, line : "Et le brave Diomèdès lui répondit avec calme:"});
db.iliadfr.insert({ pg : 2870, line : "-- Tu m'as manqué, loin de m'atteindre; mais je ne pense pas que"});
db.iliadfr.insert({ pg : 2871, line : "vous vous reposiez avant qu'un de vous, au moins, ne tombe et ne"});
db.iliadfr.insert({ pg : 2872, line : "rassasie de son sang Arès, l'audacieux combattant."});
db.iliadfr.insert({ pg : 2874, line : "Il parla ainsi, et lança sa pique. Et Athènè la dirigea au-dessus"});
db.iliadfr.insert({ pg : 2875, line : "du nez, auprès de l'oeil, et l'airain indompté traversa les"});
db.iliadfr.insert({ pg : 2876, line : "blanches dents, coupa l'extrémité de la langue et sortit sous le"});
db.iliadfr.insert({ pg : 2877, line : "menton. Et Pandaros tomba du char, et ses armes brillantes, aux"});
db.iliadfr.insert({ pg : 2878, line : "couleurs variées, résonnèrent sur lui, et les chevaux aux pieds"});
db.iliadfr.insert({ pg : 2879, line : "rapides frémirent, et la vie et les forces de l'homme furent"});
db.iliadfr.insert({ pg : 2880, line : "brisées."});
db.iliadfr.insert({ pg : 2882, line : "Alors Ainéias s'élança avec son bouclier et sa longue pique, de"});
db.iliadfr.insert({ pg : 2883, line : "peur que les Akhaiens n'enlevassent le cadavre. Et, tout autour,"});
db.iliadfr.insert({ pg : 2884, line : "il allait comme un lion confiant dans ses forces, brandissant sa"});
db.iliadfr.insert({ pg : 2885, line : "pique et son bouclier bombé, prêt à tuer celui qui oserait"});
db.iliadfr.insert({ pg : 2886, line : "approcher, et criant horriblement. Mais le Tydéide saisit de sa"});
db.iliadfr.insert({ pg : 2887, line : "main un lourd rocher que deux hommes, de ceux qui vivent"});
db.iliadfr.insert({ pg : 2888, line : "aujourd'hui, ne pourraient soulever. Seul, il le remua facilement."});
db.iliadfr.insert({ pg : 2889, line : "Et il en frappa Ainéias à la cuisse, là où le fémur tourne dans le"});
db.iliadfr.insert({ pg : 2890, line : "cotyle. Et la pierre rugueuse heurta le cotyle, rompit les deux"});
db.iliadfr.insert({ pg : 2891, line : "muscles supérieurs et déchira la peau. Le héros, tombant sur les"});
db.iliadfr.insert({ pg : 2892, line : "genoux, s'appuya d'une main lourde sur la terre, et une nuit noire"});
db.iliadfr.insert({ pg : 2893, line : "couvrit ses yeux. Et le roi des hommes, Ainéias, eût sans doute"});
db.iliadfr.insert({ pg : 2894, line : "péri, si la fille de Zeus, Aphroditè, ne l'eût aperçu: car elle"});
db.iliadfr.insert({ pg : 2895, line : "était sa mère, l'ayant conçu d'Ankhisès, comme il paissait ses"});
db.iliadfr.insert({ pg : 2896, line : "boeufs. Elle jeta ses bras blancs autour de son fils bien-aimé et"});
db.iliadfr.insert({ pg : 2897, line : "l'enveloppa des plis de son péplos éclatant, afin de le garantir"});
db.iliadfr.insert({ pg : 2898, line : "des traits, et de peur qu'un des guerriers Danaens enfonçât"});
db.iliadfr.insert({ pg : 2899, line : "l'airain dans sa poitrine et lui arrachât l'âme. Et elle enleva"});
db.iliadfr.insert({ pg : 2900, line : "hors de la mêlée son fils bien-aimé."});
db.iliadfr.insert({ pg : 2902, line : "Mais le fils de Kapaneus n'oublia point l'ordre que lui avait"});
db.iliadfr.insert({ pg : 2903, line : "donné Diomèdès hardi au combat. Il arrêta brusquement les chevaux"});
db.iliadfr.insert({ pg : 2904, line : "aux sabots massifs, en attachant au char les rênes tendues; et, se"});
db.iliadfr.insert({ pg : 2905, line : "précipitant vers les chevaux aux longues crinières d'Ainéias, il"});
db.iliadfr.insert({ pg : 2906, line : "les poussa du côté des Akhaiens aux belles knèmides. Et il les"});
db.iliadfr.insert({ pg : 2907, line : "remit à son cher compagnon Deipylos, qu'il honorait au-dessus de"});
db.iliadfr.insert({ pg : 2908, line : "tous, tant leurs âmes étaient d'accord, afin que celui-ci les"});
db.iliadfr.insert({ pg : 2909, line : "conduisît aux nefs creuses."});
db.iliadfr.insert({ pg : 2911, line : "Puis le héros, remontant sur son char, saisit les belles rênes,"});
db.iliadfr.insert({ pg : 2912, line : "et, traîné par ses chevaux aux sabots massifs, suivit le Tydéide."});
db.iliadfr.insert({ pg : 2913, line : "Et celui-ci, de l'airain meurtrier, pressait ardemment Aphroditè,"});
db.iliadfr.insert({ pg : 2914, line : "sachant que c'était une déesse pleine de faiblesse, et qu'elle"});
db.iliadfr.insert({ pg : 2915, line : "n'était point de ces divinités qui se mêlent aux luttes des"});
db.iliadfr.insert({ pg : 2916, line : "guerriers, comme Athènè ou comme Ényô, la destructrice des"});
db.iliadfr.insert({ pg : 2917, line : "citadelles. Et, la poursuivant dans la mêlée tumultueuse, le fils"});
db.iliadfr.insert({ pg : 2918, line : "du magnanime Tydeus bondit, et de sa pique aiguë blessa sa main"});
db.iliadfr.insert({ pg : 2919, line : "délicate. Et aussitôt l'airain perça la peau divine à travers le"});
db.iliadfr.insert({ pg : 2920, line : "péplos que les Kharites avaient tissé elles-mêmes. Et le sang"});
db.iliadfr.insert({ pg : 2921, line : "immortel de la déesse coula, subtil, et tel qu'il sort des dieux"});
db.iliadfr.insert({ pg : 2922, line : "heureux. Car ils ne mangent point de pain, ils ne boivent point le"});
db.iliadfr.insert({ pg : 2923, line : "vin ardent, et c'est pourquoi ils n'ont point notre sang et sont"});
db.iliadfr.insert({ pg : 2924, line : "nommés immortels. Elle poussa un grand cri et laissa tomber son"});
db.iliadfr.insert({ pg : 2925, line : "fils; mais Phoibos Apollôn le releva de ses mains et l'enveloppa"});
db.iliadfr.insert({ pg : 2926, line : "d'une noire nuée, de peur qu'un des cavaliers Danaens enfonçât"});
db.iliadfr.insert({ pg : 2927, line : "l'airain dans sa poitrine et lui arrachât l'âme. Et Diomèdès hardi"});
db.iliadfr.insert({ pg : 2928, line : "au combat cria d'une voix haute à la déesse:"});
db.iliadfr.insert({ pg : 2930, line : "-- Fille de Zeus, fuis la guerre et le combat. Ne te suffit-il pas"});
db.iliadfr.insert({ pg : 2931, line : "de tromper de faibles femmes? Si tu retournes jamais au combat,"});
db.iliadfr.insert({ pg : 2932, line : "certes, je pense que la guerre et son nom seul te feront trembler"});
db.iliadfr.insert({ pg : 2933, line : "désormais."});
db.iliadfr.insert({ pg : 2935, line : "Il parla ainsi, et Aphroditè s'envola, pleine d'affliction et"});
db.iliadfr.insert({ pg : 2936, line : "gémissant profondément. Iris aux pieds rapides la conduisit hors"});
db.iliadfr.insert({ pg : 2937, line : "de la mêlée, accablée de douleurs, et son beau corps était devenu"});
db.iliadfr.insert({ pg : 2938, line : "noir. Et elle rencontra l'impétueux Arès assis à la gauche de la"});
db.iliadfr.insert({ pg : 2939, line : "bataille. Sa pique et ses chevaux rapides étaient couverts d'une"});
db.iliadfr.insert({ pg : 2940, line : "nuée. Et Aphroditè, tombant à genoux, supplia son frère bien-aimé"});
db.iliadfr.insert({ pg : 2941, line : "de lui donner ses chevaux liés par des courroies d'or:"});
db.iliadfr.insert({ pg : 2943, line : "-- Frère bien-aimé, secours-moi! Donne-moi tes chevaux pour que"});
db.iliadfr.insert({ pg : 2944, line : "j'aille dans l'Olympos, qui est la demeure des immortels. Je"});
db.iliadfr.insert({ pg : 2945, line : "souffre cruellement d'une blessure que m'a faite le guerrier"});
db.iliadfr.insert({ pg : 2946, line : "mortel Tydéide, qui combattrait maintenant le père Zeus lui-même."});
db.iliadfr.insert({ pg : 2948, line : "Elle parla ainsi, et Arès lui donna ses chevaux aux aigrettes"});
db.iliadfr.insert({ pg : 2949, line : "dorées. Et, gémissant dans sa chère âme, elle monta sur le char."});
db.iliadfr.insert({ pg : 2950, line : "Iris monta auprès d'elle, prit les rênes en mains et frappa les"});
db.iliadfr.insert({ pg : 2951, line : "chevaux du fouet, et ceux-ci s'envolèrent et atteignirent aussitôt"});
db.iliadfr.insert({ pg : 2952, line : "le haut Olympos, demeure des dieux. Et la rapide Iris arrêta les"});
db.iliadfr.insert({ pg : 2953, line : "chevaux aux pieds prompts comme le vent, et, sautant du char, leur"});
db.iliadfr.insert({ pg : 2954, line : "donna leur nourriture immortelle. Et la divine Aphroditè tomba aux"});
db.iliadfr.insert({ pg : 2955, line : "genoux de Diônè sa mère; et celle-ci, entourant sa fille de ses"});
db.iliadfr.insert({ pg : 2956, line : "bras, la caressa et lui dit:"});
db.iliadfr.insert({ pg : 2958, line : "-- Quel Ouranien, chère fille, t'a ainsi traitée, comme si tu"});
db.iliadfr.insert({ pg : 2959, line : "avais ouvertement commis une action mauvaise?"});
db.iliadfr.insert({ pg : 2961, line : "Et Aphroditè qui aime les sourires lui répondit:"});
db.iliadfr.insert({ pg : 2963, line : "-- L'audacieux Diomèdès, fils de Tydeus, m'a blessée, parce que"});
db.iliadfr.insert({ pg : 2964, line : "j'emportais hors de la mêlée mon fils bien-aimé Ainéias, qui m'est"});
db.iliadfr.insert({ pg : 2965, line : "le plus cher de tous les hommes. La bataille furieuse n'est plus"});
db.iliadfr.insert({ pg : 2966, line : "seulement entre les Troiens et les Akhaiens, mais les Danaens"});
db.iliadfr.insert({ pg : 2967, line : "combattent déjà contre les immortels."});
db.iliadfr.insert({ pg : 2969, line : "Et l'illustre déesse Diônè lui répondit:"});
db.iliadfr.insert({ pg : 2971, line : "-- Subis et endure ton mal, ma fille, bien que tu sois affligée."});
db.iliadfr.insert({ pg : 2972, line : "Déjà plusieurs habitants des demeures ouraniennes, par leurs"});
db.iliadfr.insert({ pg : 2973, line : "discordes mutuelles, ont beaucoup souffert de la part des hommes."});
db.iliadfr.insert({ pg : 2974, line : "Arès a subi de grands maux quand Otos et le robuste Éphialtès,"});
db.iliadfr.insert({ pg : 2975, line : "fils d'Aloè, le lièrent de fortes chaînes. Il resta treize mois"});
db.iliadfr.insert({ pg : 2976, line : "enchaîné dans une prison d'airain. Et peut-être qu'Arès,"});
db.iliadfr.insert({ pg : 2977, line : "insatiable de combats, eût péri, si la belle Ériboia, leur"});
db.iliadfr.insert({ pg : 2978, line : "marâtre, n'eût averti Herméias, qui délivra furtivement Arès"});
db.iliadfr.insert({ pg : 2979, line : "respirant à peine, tant les lourdes chaînes l'avaient dompté. Hèrè"});
db.iliadfr.insert({ pg : 2980, line : "souffrit aussi quand le vigoureux Amphitryonade la blessa à la"});
db.iliadfr.insert({ pg : 2981, line : "mamelle droite d'une flèche à trois pointes, et une irrémédiable"});
db.iliadfr.insert({ pg : 2982, line : "douleur la saisit. Et le grand Aidès souffrit entre tous quand le"});
db.iliadfr.insert({ pg : 2983, line : "même homme, fils de Zeus tempétueux, le blessa, sur le seuil du"});
db.iliadfr.insert({ pg : 2984, line : "Hadès, au milieu des morts, d'une flèche rapide, et l'accabla de"});
db.iliadfr.insert({ pg : 2985, line : "douleurs. Et il vint dans la demeure de Zeus, dans le grand"});
db.iliadfr.insert({ pg : 2986, line : "Olympos, plein de maux et gémissant dans son coeur, car la flèche"});
db.iliadfr.insert({ pg : 2987, line : "était fixée dans sa large épaule et torturait son âme. Et Paièôn,"});
db.iliadfr.insert({ pg : 2988, line : "répandant de doux baumes sur la plaie, guérit Aidès, car il"});
db.iliadfr.insert({ pg : 2989, line : "n'était point mortel comme un homme. Et tel était Hèraklès, impie,"});
db.iliadfr.insert({ pg : 2990, line : "irrésistible, se souciant peu de commettre des actions mauvaises"});
db.iliadfr.insert({ pg : 2991, line : "et frappant de ses flèches les dieux qui habitent l'Olympos. C'est"});
db.iliadfr.insert({ pg : 2992, line : "la divine Athènè aux yeux clairs qui a excité un insensé contre"});
db.iliadfr.insert({ pg : 2993, line : "toi. Et le fils de Tydeus ne sait pas, dans son âme, qu'il ne vit"});
db.iliadfr.insert({ pg : 2994, line : "pas longtemps celui qui lutte contre les immortels. Ses enfants,"});
db.iliadfr.insert({ pg : 2995, line : "assis sur ses genoux, ne le nomment point leur père au retour de"});
db.iliadfr.insert({ pg : 2996, line : "la guerre et de la rude bataille. Maintenant, que le Tydéide"});
db.iliadfr.insert({ pg : 2997, line : "craigne, malgré sa force, qu'un plus redoutable que toi ne le"});
db.iliadfr.insert({ pg : 2998, line : "combatte. Qu'il craigne que la sage fille d'Adrèstès, Aigialéia,"});
db.iliadfr.insert({ pg : 2999, line : "la noble femme du dompteur de chevaux Diomèdès, gémisse bientôt en"});
db.iliadfr.insert({ pg : 3000, line : "s'éveillant et en troublant ses serviteurs, parce qu'elle pleurera"});
db.iliadfr.insert({ pg : 3001, line : "son premier mari, le plus brave des Akhaiens!"});
db.iliadfr.insert({ pg : 3003, line : "Elle parla ainsi, et, de ses deux mains, étancha la plaie, et"});
db.iliadfr.insert({ pg : 3004, line : "celle-ci fut guérie, et les amères douleurs furent calmées."});
db.iliadfr.insert({ pg : 3006, line : "Mais Hèrè et Athènè, qui les regardaient, tentèrent d'irriter le"});
db.iliadfr.insert({ pg : 3007, line : "Kronide Zeus par des paroles mordantes. Et la divine Athènè aux"});
db.iliadfr.insert({ pg : 3008, line : "yeux clairs parla ainsi la première:"});
db.iliadfr.insert({ pg : 3010, line : "-- Père Zeus, peut-être seras-tu irrité de ce que je vais dire;"});
db.iliadfr.insert({ pg : 3011, line : "mais voici qu'Aphroditè, en cherchant à mener quelque femme"});
db.iliadfr.insert({ pg : 3012, line : "Akhaienne au milieu des Troiens qu'elle aime tendrement, en"});
db.iliadfr.insert({ pg : 3013, line : "s'efforçant de séduire par ses caresses une des Akhaiennes au beau"});
db.iliadfr.insert({ pg : 3014, line : "péplos, a déchiré sa main délicate à une agrafe d'or."});
db.iliadfr.insert({ pg : 3016, line : "Elle parla ainsi, et le père des hommes et des dieux sourit, et,"});
db.iliadfr.insert({ pg : 3017, line : "appelant Aphroditè d'or, il lui dit:"});
db.iliadfr.insert({ pg : 3019, line : "-- Ma fille, les travaux de la guerre ne te sont point confiés,"});
db.iliadfr.insert({ pg : 3020, line : "mais à l'impétueux Arès et à Athènè. Ne songe qu'aux douces joies"});
db.iliadfr.insert({ pg : 3021, line : "des Hyménées."});
db.iliadfr.insert({ pg : 3023, line : "Et ils parlaient ainsi entre eux. Et Diomèdès hardi au combat se"});
db.iliadfr.insert({ pg : 3024, line : "ruait toujours sur Ainéias, bien qu'il sût qu'Apollôn le couvrait"});
db.iliadfr.insert({ pg : 3025, line : "des deux mains. Mais il ne respectait même plus un grand dieu,"});
db.iliadfr.insert({ pg : 3026, line : "désirant tuer Ainéias et le dépouiller de ses armes illustres. Et"});
db.iliadfr.insert({ pg : 3027, line : "trois fois il se rua, désirant le tuer, et trois fois Apollôn"});
db.iliadfr.insert({ pg : 3028, line : "repoussa son bouclier éclatant. Mais, quand il bondit une"});
db.iliadfr.insert({ pg : 3029, line : "quatrième fois, semblable à un dieu, Apollôn lui dit d'une voix"});
db.iliadfr.insert({ pg : 3030, line : "terrible:"});
db.iliadfr.insert({ pg : 3032, line : "-- Prends garde, Tydéide, et ne t'égale point aux dieux, car la"});
db.iliadfr.insert({ pg : 3033, line : "race des dieux immortels n'est point semblable à celle des hommes"});
db.iliadfr.insert({ pg : 3034, line : "qui marchent sur la terre."});
db.iliadfr.insert({ pg : 3036, line : "Il parla ainsi, et le Tydéide recula un peu, de peur d'exciter la"});
db.iliadfr.insert({ pg : 3037, line : "colère de l'archer Apollôn. Et celui-ci déposa Ainéias loin de la"});
db.iliadfr.insert({ pg : 3038, line : "mêlée, dans la sainte Pergamos, où était bâti son temple. Et Lètô"});
db.iliadfr.insert({ pg : 3039, line : "et Artémis qui se réjouit de ses flèches prirent soin de ce"});
db.iliadfr.insert({ pg : 3040, line : "guerrier et l'honorèrent dans le vaste sanctuaire. Et Apollôn à"});
db.iliadfr.insert({ pg : 3041, line : "l'arc d'argent suscita une image vaine semblable à Ainéias et"});
db.iliadfr.insert({ pg : 3042, line : "portant des armes pareilles. Et autour de cette image les Troiens"});
db.iliadfr.insert({ pg : 3043, line : "et les divins Akhaiens se frappaient sur les peaux de boeuf qui"});
db.iliadfr.insert({ pg : 3044, line : "couvraient leurs poitrines, sur les boucliers bombés et sur les"});
db.iliadfr.insert({ pg : 3045, line : "cuirasses légères. Alors, le roi Phoibos Apollôn dit à l'impétueux"});
db.iliadfr.insert({ pg : 3046, line : "Arès:"});
db.iliadfr.insert({ pg : 3048, line : "-- Arès, Arès, fléau des hommes sanglant, et qui renverses les"});
db.iliadfr.insert({ pg : 3049, line : "murailles, ne vas-tu pas chasser hors de la mêlée ce guerrier, le"});
db.iliadfr.insert({ pg : 3050, line : "Tydéide, qui, certes, combattrait maintenant même contre le père"});
db.iliadfr.insert({ pg : 3051, line : "Zeus? Déjà il a blessé la main d'Aphroditè, puis il a bondi sur"});
db.iliadfr.insert({ pg : 3052, line : "moi, semblable à un dieu."});
db.iliadfr.insert({ pg : 3054, line : "Ayant ainsi parlé, il retourna s'asseoir sur la haute Pergamos, et"});
db.iliadfr.insert({ pg : 3055, line : "le cruel Arès, se mêlant aux Troiens, les excita à combattre,"});
db.iliadfr.insert({ pg : 3056, line : "ayant pris la forme de l'impétueux Akamas, prince des Thrakiens."});
db.iliadfr.insert({ pg : 3057, line : "Et il exhorta les fils de Priamos, nourrissons de Zeus:"});
db.iliadfr.insert({ pg : 3059, line : "-- Ô fils du roi Priamos, nourris par Zeus, jusqu'à quand"});
db.iliadfr.insert({ pg : 3060, line : "laisserez-vous les Akhaiens massacrer votre peuple? Attendrez-vous"});
db.iliadfr.insert({ pg : 3061, line : "qu'ils combattent autour de nos portes solides? Un guerrier est"});
db.iliadfr.insert({ pg : 3062, line : "tombé que nous honorions autant que le divin Hektôr, Ainéias, fils"});
db.iliadfr.insert({ pg : 3063, line : "du magnanime Ankhisès. Allons! Enlevons notre brave compagnon hors"});
db.iliadfr.insert({ pg : 3064, line : "de la mêlée."});
db.iliadfr.insert({ pg : 3066, line : "Ayant ainsi parlé, il excita la force et le courage de chacun. Et"});
db.iliadfr.insert({ pg : 3067, line : "Sarpèdôn dit ces dures paroles au divin Hektôr:"});
db.iliadfr.insert({ pg : 3069, line : "-- Hektôr, qu'est devenu ton ancien courage? Tu te vantais naguère"});
db.iliadfr.insert({ pg : 3070, line : "de sauver ta ville, sans l'aide des autres guerriers, seul, avec"});
db.iliadfr.insert({ pg : 3071, line : "tes frères et tes parents, et je n'en ai guère encore aperçu"});
db.iliadfr.insert({ pg : 3072, line : "aucun, car ils tremblent tous comme des chiens devant le lion."});
db.iliadfr.insert({ pg : 3073, line : "C'est nous, vos alliés, qui combattons. Me voici, moi, qui suis"});
db.iliadfr.insert({ pg : 3074, line : "venu de très loin pour vous secourir. Elle est éloignée, en effet,"});
db.iliadfr.insert({ pg : 3075, line : "la Lykiè où coule le Xanthos plein de tourbillons. J'y ai laissé"});
db.iliadfr.insert({ pg : 3076, line : "ma femme bien-aimée et mon petit enfant, et mes nombreux domaines"});
db.iliadfr.insert({ pg : 3077, line : "que le pauvre convoite. Et, cependant, j'excite les Lykiens au"});
db.iliadfr.insert({ pg : 3078, line : "combat, et je suis prêt moi-même à lutter contre les hommes, bien"});
db.iliadfr.insert({ pg : 3079, line : "que je n'aie rien à redouter ou à perdre des maux que vous"});
db.iliadfr.insert({ pg : 3080, line : "apportent les Akhaiens, ou des biens qu'ils veulent vous enlever."});
db.iliadfr.insert({ pg : 3081, line : "Et tu restes immobile, et tu ne commandes même pas à tes guerriers"});
db.iliadfr.insert({ pg : 3082, line : "de résister et de défendre leurs femmes! Ne crains-tu pas"});
db.iliadfr.insert({ pg : 3083, line : "qu'enveloppés tous comme dans un filet de lin, vous deveniez la"});
db.iliadfr.insert({ pg : 3084, line : "proie des guerriers ennemis? Sans doute, les Akhaiens renverseront"});
db.iliadfr.insert({ pg : 3085, line : "bientôt votre ville aux nombreux habitants. C'est à toi qu'il"});
db.iliadfr.insert({ pg : 3086, line : "appartient de songer à ces choses, nuit et jour, et de supplier"});
db.iliadfr.insert({ pg : 3087, line : "les princes alliés, afin qu'ils tiennent fermement et qu'ils"});
db.iliadfr.insert({ pg : 3088, line : "cessent leurs durs reproches."});
db.iliadfr.insert({ pg : 3090, line : "Sarpèdôn parla ainsi, et il mordit l'âme de Hektôr, et celui-ci"});
db.iliadfr.insert({ pg : 3091, line : "sauta aussitôt de son char avec ses armes, et, brandissant deux"});
db.iliadfr.insert({ pg : 3092, line : "lances aiguës, courut de toutes parts à travers l'armée,"});
db.iliadfr.insert({ pg : 3093, line : "l'excitant à combattre un rude combat. Et les Troiens revinrent à"});
db.iliadfr.insert({ pg : 3094, line : "la charge et tinrent tête aux Akhaiens. Et les Argiens les"});
db.iliadfr.insert({ pg : 3095, line : "attendirent de pied ferme."});
db.iliadfr.insert({ pg : 3097, line : "Ainsi que, dans les aires sacrées, à l'aide des vanneurs et du"});
db.iliadfr.insert({ pg : 3098, line : "vent, la blonde Dèmètèr sépare le bon grain de la paille, et que"});
db.iliadfr.insert({ pg : 3099, line : "celle-ci, amoncelée, est couverte d'une poudre blanche, de même"});
db.iliadfr.insert({ pg : 3100, line : "les Akhaiens étaient enveloppés d'une poussière blanche qui"});
db.iliadfr.insert({ pg : 3101, line : "montait du milieu d'eux vers l'Ouranos, et que soulevaient les"});
db.iliadfr.insert({ pg : 3102, line : "pieds des chevaux frappant la terre, tandis que les guerriers se"});
db.iliadfr.insert({ pg : 3103, line : "mêlaient de nouveau et que les conducteurs de chars les ramenaient"});
db.iliadfr.insert({ pg : 3104, line : "au combat. Et le furieux Arès, couvert d'une nuée, allait de"});
db.iliadfr.insert({ pg : 3105, line : "toutes parts, excitant les Troiens. Et il obéissait ainsi aux"});
db.iliadfr.insert({ pg : 3106, line : "ordres que lui avait donnés Phoibos Apollôn qui porte une épée"});
db.iliadfr.insert({ pg : 3107, line : "d'or, quand celui-ci avait vu partir Athènè, protectrice des"});
db.iliadfr.insert({ pg : 3108, line : "Danaens."});
db.iliadfr.insert({ pg : 3110, line : "Et l'archer Apollôn fit sortir Ainéias du sanctuaire et remplit de"});
db.iliadfr.insert({ pg : 3111, line : "vigueur la poitrine du prince des peuples. Et ce dernier reparut"});
db.iliadfr.insert({ pg : 3112, line : "au milieu de ses compagnons, pleins de joie de le voir vivant,"});
db.iliadfr.insert({ pg : 3113, line : "sain et sauf et possédant toutes ses forces. Mais ils ne lui"});
db.iliadfr.insert({ pg : 3114, line : "dirent rien, car les travaux que leur préparaient Arès, fléau des"});
db.iliadfr.insert({ pg : 3115, line : "hommes, Apollôn et Éris, ne leur permirent point de l'interroger."});
db.iliadfr.insert({ pg : 3117, line : "Et les deux Aias, Odysseus et Diomèdès exhortaient les Danaens au"});
db.iliadfr.insert({ pg : 3118, line : "combat; et ceux-ci, sans craindre les forces et l'impétuosité des"});
db.iliadfr.insert({ pg : 3119, line : "Troiens, les attendaient de pied ferme, semblables à ces nuées que"});
db.iliadfr.insert({ pg : 3120, line : "le Kroniôn arrête à la cime des montagnes, quand le Boréas et les"});
db.iliadfr.insert({ pg : 3121, line : "autres vents violents se sont calmés, eux dont le souffle disperse"});
db.iliadfr.insert({ pg : 3122, line : "les nuages épais et immobiles. Ainsi les Danaens attendaient les"});
db.iliadfr.insert({ pg : 3123, line : "Troiens de pied ferme. Et l'Atréide, courant çà et là au milieu"});
db.iliadfr.insert({ pg : 3124, line : "d'eux, les excitait ainsi:"});
db.iliadfr.insert({ pg : 3126, line : "-- Amis, soyez des hommes! ruez-vous, d'un coeur ferme, dans la"});
db.iliadfr.insert({ pg : 3127, line : "rude bataille. Ce sont les plus braves qui échappent en plus grand"});
db.iliadfr.insert({ pg : 3128, line : "nombre à la mort; mais ceux qui fuient n'ont ni force ni gloire."});
db.iliadfr.insert({ pg : 3130, line : "Il parla, et, lançant sa longue pique, il perça, au premier rang,"});
db.iliadfr.insert({ pg : 3131, line : "le guerrier Dèikoôn Pergaside, compagnon du magnanime Ainéias, et"});
db.iliadfr.insert({ pg : 3132, line : "que les Troiens honoraient autant que les fils de Priamos, parce"});
db.iliadfr.insert({ pg : 3133, line : "qu'il était toujours parmi les premiers au combat. Et le roi"});
db.iliadfr.insert({ pg : 3134, line : "Agamemnôn le frappa de sa pique dans le bouclier qui n'arrêta"});
db.iliadfr.insert({ pg : 3135, line : "point le coup, car la pique le traversa et entra dans le ventre en"});
db.iliadfr.insert({ pg : 3136, line : "déchirant le ceinturon. Et il tomba avec bruit, et ses armes"});
db.iliadfr.insert({ pg : 3137, line : "résonnèrent sur son corps."});
db.iliadfr.insert({ pg : 3139, line : "Alors, Ainéias tua deux braves guerriers Danaens, fils de Dioklès,"});
db.iliadfr.insert({ pg : 3140, line : "Krèthôn et Orsilokhos. Et leur père habitait Phèrè bien bâtie, et"});
db.iliadfr.insert({ pg : 3141, line : "il était riche, et il descendait du fleuve Alphéios qui coule"});
db.iliadfr.insert({ pg : 3142, line : "largement sur la terre des Pyliens. Et l'Alphéios avait engendré"});
db.iliadfr.insert({ pg : 3143, line : "Orsilokhos, chef de nombreux guerriers; et Orsilokhos avait"});
db.iliadfr.insert({ pg : 3144, line : "engendré le magnanime Dioklès, et de Dioklès étaient nés deux fils"});
db.iliadfr.insert({ pg : 3145, line : "jumeaux, Krèthôn et Orsilokhos, habiles à tous les combats. Tout"});
db.iliadfr.insert({ pg : 3146, line : "jeunes encore, ils vinrent sur leurs nefs noires vers Ilios aux"});
db.iliadfr.insert({ pg : 3147, line : "bons chevaux, ayant suivi les Argiens pour la cause et l'honneur"});
db.iliadfr.insert({ pg : 3148, line : "des Atréides, Agamemnôn et Ménélaos, et c'est là que la mort les"});
db.iliadfr.insert({ pg : 3149, line : "atteignit. Comme deux jeunes lions nourris par leur mère sur le"});
db.iliadfr.insert({ pg : 3150, line : "sommet des montagnes, au fond des épaisses forêts, et qui enlèvent"});
db.iliadfr.insert({ pg : 3151, line : "les boeufs et les brebis, et qui dévastent les étables jusqu'à ce"});
db.iliadfr.insert({ pg : 3152, line : "qu'ils soient tués de l'airain aigu par les mains des pâtres, tels"});
db.iliadfr.insert({ pg : 3153, line : "ils tombèrent tous deux, frappés par les mains d'Ainéias, pareils"});
db.iliadfr.insert({ pg : 3154, line : "à des pins élevés."});
db.iliadfr.insert({ pg : 3156, line : "Et Ménélaos, hardi au combat, eut pitié de leur chute, et il"});
db.iliadfr.insert({ pg : 3157, line : "s'avança au premier rang, vêtu de l'airain étincelant et"});
db.iliadfr.insert({ pg : 3158, line : "brandissant sa pique. Et Arès l'excitait afin qu'il tombât sous"});
db.iliadfr.insert({ pg : 3159, line : "les mains d'Ainéias. Mais Antilokhos, fils du magnanime Nestôr, le"});
db.iliadfr.insert({ pg : 3160, line : "vit et s'avança au premier rang, car il craignait pour le prince"});
db.iliadfr.insert({ pg : 3161, line : "des peuples, dont la mort eût rendu leurs travaux inutiles. Et ils"});
db.iliadfr.insert({ pg : 3162, line : "croisaient déjà leurs piques aiguës, prêts à se combattre, quand"});
db.iliadfr.insert({ pg : 3163, line : "Antilokhos vint se placer auprès du prince des peuples. Et"});
db.iliadfr.insert({ pg : 3164, line : "Ainéias, bien que très brave, recula, voyant les deux guerriers"});
db.iliadfr.insert({ pg : 3165, line : "prêts à l'attaquer. Et ceux-ci entraînèrent les morts parmi les"});
db.iliadfr.insert({ pg : 3166, line : "Akhaiens, et, les remettant à leurs compagnons, revinrent"});
db.iliadfr.insert({ pg : 3167, line : "combattre au premier rang."});
db.iliadfr.insert({ pg : 3169, line : "Alors ils tuèrent Pylaiménès, égal à Arès, chef des magnanimes"});
db.iliadfr.insert({ pg : 3170, line : "Paphlagones porteurs de boucliers. Et l'illustre Atréide Ménélaos"});
db.iliadfr.insert({ pg : 3171, line : "le perça de sa pique à la clavicule. Et Antilokhos frappa au"});
db.iliadfr.insert({ pg : 3172, line : "coude, d'un coup de pierre, le conducteur de son char, le brave"});
db.iliadfr.insert({ pg : 3173, line : "Atymniade Mydôn, comme il faisait reculer ses chevaux aux sabots"});
db.iliadfr.insert({ pg : 3174, line : "massifs. Et les blanches rênes ornées d'ivoire s'échappèrent de"});
db.iliadfr.insert({ pg : 3175, line : "ses mains, et Antilokhos, sautant sur lui, le perça à la tempe"});
db.iliadfr.insert({ pg : 3176, line : "d'un coup d'épée. Et, ne respirant plus, il tomba du beau char, la"});
db.iliadfr.insert({ pg : 3177, line : "tête et les épaules enfoncées dans le sable qui était creusé en"});
db.iliadfr.insert({ pg : 3178, line : "cet endroit. Ses chevaux le foulèrent aux pieds, et Antilokhos les"});
db.iliadfr.insert({ pg : 3179, line : "chassa vers l'armée des Akhaiens."});
db.iliadfr.insert({ pg : 3181, line : "Mais Hektôr, les ayant aperçus tous deux, se rua à travers la"});
db.iliadfr.insert({ pg : 3182, line : "mêlée en poussant des cris. Et les braves phalanges des Troiens le"});
db.iliadfr.insert({ pg : 3183, line : "suivaient, et devant elles marchaient Arès et la vénérable Ényô."});
db.iliadfr.insert({ pg : 3184, line : "Celle-ci menait le tumulte immense du combat, et Arès, brandissant"});
db.iliadfr.insert({ pg : 3185, line : "une grande pique, allait tantôt devant et tantôt derrière Hektôr."});
db.iliadfr.insert({ pg : 3187, line : "Et Diomèdès hardi au combat ayant vu Arès, frémit. Comme un"});
db.iliadfr.insert({ pg : 3188, line : "voyageur troublé s'arrête, au bout d'une plaine immense, sur le"});
db.iliadfr.insert({ pg : 3189, line : "bord d'un fleuve impétueux qui tombe dans la mer, et qui recule à"});
db.iliadfr.insert({ pg : 3190, line : "la vue de l'onde bouillonnante, ainsi le Tydéide recula et dit aux"});
db.iliadfr.insert({ pg : 3191, line : "siens:"});
db.iliadfr.insert({ pg : 3193, line : "-- Ô amis, combien nous admirions justement le divin Hektôr,"});
db.iliadfr.insert({ pg : 3194, line : "habile à lancer la pique et audacieux en combattant! Quelque dieu"});
db.iliadfr.insert({ pg : 3195, line : "se tient toujours à son côté et détourne de lui la mort."});
db.iliadfr.insert({ pg : 3196, line : "Maintenant, voici qu'Arès l'accompagne, semblable à un guerrier."});
db.iliadfr.insert({ pg : 3197, line : "C'est pourquoi reculons devant les Troiens et ne vous hâtez point"});
db.iliadfr.insert({ pg : 3198, line : "de combattre les dieux."});
db.iliadfr.insert({ pg : 3200, line : "Il parla ainsi, et les Troiens approchèrent. Alors, Hektôr tua"});
db.iliadfr.insert({ pg : 3201, line : "deux guerriers habiles au combat et montés sur un même char,"});
db.iliadfr.insert({ pg : 3202, line : "Ménèsthès et Ankhialos."});
db.iliadfr.insert({ pg : 3204, line : "Et le grand Télamônien Aias eut pitié de leur chute, et, marchant"});
db.iliadfr.insert({ pg : 3205, line : "en avant, il lança sa pique brillante. Et il frappa Amphiôn, fils"});
db.iliadfr.insert({ pg : 3206, line : "de Sélagos, qui habitait Paisos, et qui était fort riche. Mais sa"});
db.iliadfr.insert({ pg : 3207, line : "Moire l'avait envoyé secourir les Priamides. Et le Télamônien Aias"});
db.iliadfr.insert({ pg : 3208, line : "l'atteignit au ceinturon, et la longue pique resta enfoncée dans"});
db.iliadfr.insert({ pg : 3209, line : "le bas-ventre. Et il tomba avec bruit, et l'illustre Aias accourut"});
db.iliadfr.insert({ pg : 3210, line : "pour le dépouiller de ses armes. Mais les Troiens le couvrirent"});
db.iliadfr.insert({ pg : 3211, line : "d'une grêle de piques aiguës et brillantes, et son bouclier en fut"});
db.iliadfr.insert({ pg : 3212, line : "hérissé. Cependant, pressant du pied le cadavre, il en arracha sa"});
db.iliadfr.insert({ pg : 3213, line : "pique d'airain; mais il ne put enlever les belles armes, étant"});
db.iliadfr.insert({ pg : 3214, line : "accablé de traits. Et il craignit la vigoureuse attaque des braves"});
db.iliadfr.insert({ pg : 3215, line : "Troiens qui le pressaient de leurs piques et le firent reculer,"});
db.iliadfr.insert({ pg : 3216, line : "bien qu'il fût grand, fort et illustre."});
db.iliadfr.insert({ pg : 3218, line : "Et c'est ainsi qu'ils luttaient dans la rude mêlée. Et voici que"});
db.iliadfr.insert({ pg : 3219, line : "la Moire violente amena, en face du divin Sarpèdôn, le grand et"});
db.iliadfr.insert({ pg : 3220, line : "vigoureux Hèraklide Tlèpolémos. Et quand ils se furent rencontrés"});
db.iliadfr.insert({ pg : 3221, line : "tous deux, le fils et le petit-fils de Zeus qui amasse les nuées,"});
db.iliadfr.insert({ pg : 3222, line : "Tlèpolémos, le premier, parla ainsi:"});
db.iliadfr.insert({ pg : 3224, line : "-- Sarpèdôn, chef des Lykiens, quelle nécessité te pousse"});
db.iliadfr.insert({ pg : 3225, line : "tremblant dans la mêlée, toi qui n'es qu'un guerrier inhabile? Des"});
db.iliadfr.insert({ pg : 3226, line : "menteurs disent que tu es fils de Zeus tempétueux, tandis que tu"});
db.iliadfr.insert({ pg : 3227, line : "es loin de valoir les guerriers qui naquirent de Zeus, aux temps"});
db.iliadfr.insert({ pg : 3228, line : "antiques des hommes, tels que le robuste Hèraklès au coeur de"});
db.iliadfr.insert({ pg : 3229, line : "lion, mon père. Et il vint ici autrefois, à cause des chevaux de"});
db.iliadfr.insert({ pg : 3230, line : "Laomédôn et, avec six nefs seulement et peu de compagnons, il"});
db.iliadfr.insert({ pg : 3231, line : "renversa Ilios et dépeupla ses rues. Mais toi, tu n'es qu'un"});
db.iliadfr.insert({ pg : 3232, line : "lâche, et tes guerriers succombent. Et je ne pense point que, même"});
db.iliadfr.insert({ pg : 3233, line : "étant brave, tu aies apporté de Lykiè un grand secours aux"});
db.iliadfr.insert({ pg : 3234, line : "Troiens, car, tué par moi, tu vas descendre au seuil d'Aidès."});
db.iliadfr.insert({ pg : 3236, line : "Et Sarpèdôn, chef des Lykiens, lui répondit:"});
db.iliadfr.insert({ pg : 3238, line : "-- Tlèpolémos, certes, Hèraklès renversa la sainte Ilios, grâce à"});
db.iliadfr.insert({ pg : 3239, line : "la témérité de l'illustre Laomédôn qui lui adressa injustement de"});
db.iliadfr.insert({ pg : 3240, line : "mauvaises paroles et lui refusa les cavales qu'il était venu"});
db.iliadfr.insert({ pg : 3241, line : "chercher de si loin. Mais, pour toi, je te prédis la mort et la"});
db.iliadfr.insert({ pg : 3242, line : "noire kèr, et je vais t'envoyer, tué par ma pique et me donnant"});
db.iliadfr.insert({ pg : 3243, line : "une grande gloire, vers Aidès qui a d'illustres chevaux."});
db.iliadfr.insert({ pg : 3245, line : "Sarpèdôn parla ainsi. Et Tlèpolémos leva sa pique de frêne, et les"});
db.iliadfr.insert({ pg : 3246, line : "deux longues piques s'élancèrent en même temps de leurs mains. Et"});
db.iliadfr.insert({ pg : 3247, line : "Sarpèdôn le frappa au milieu du cou, et la pointe amère le"});
db.iliadfr.insert({ pg : 3248, line : "traversa de part en part. Et la noire nuit enveloppa les yeux de"});
db.iliadfr.insert({ pg : 3249, line : "Tlèpolémos. Mais celui-ci avait percé de sa longue pique la cuisse"});
db.iliadfr.insert({ pg : 3250, line : "gauche de Sarpèdôn, et la pointe était restée engagée dans l'os,"});
db.iliadfr.insert({ pg : 3251, line : "et le Kronide, son père, avait détourné la mort de lui. Et les"});
db.iliadfr.insert({ pg : 3252, line : "braves compagnons de Sarpèdôn l'enlevèrent hors de la mêlée. Et il"});
db.iliadfr.insert({ pg : 3253, line : "gémissait, traînant la longue pique de frêne restée dans la"});
db.iliadfr.insert({ pg : 3254, line : "blessure, car aucun d'eux n'avait songé à l'arracher de la cuisse"});
db.iliadfr.insert({ pg : 3255, line : "du guerrier, pour qu'il pût monter sur son char, tant ils se"});
db.iliadfr.insert({ pg : 3256, line : "hâtaient."});
db.iliadfr.insert({ pg : 3258, line : "De leur côté, les Akhaiens aux belles knèmides emportaient"});
db.iliadfr.insert({ pg : 3259, line : "Tlèpolémos hors de la mêlée. Et le divin Odysseus au coeur ferme,"});
db.iliadfr.insert({ pg : 3260, line : "l'ayant aperçu, s'affligea dans son âme; et il délibéra dans son"});
db.iliadfr.insert({ pg : 3261, line : "esprit et dans son coeur s'il poursuivrait le fils de Zeus qui"});
db.iliadfr.insert({ pg : 3262, line : "tonne hautement, ou s'il arracherait l'âme à une multitude de"});
db.iliadfr.insert({ pg : 3263, line : "Lykiens. Mais il n'était point dans la destinée du magnanime"});
db.iliadfr.insert({ pg : 3264, line : "Odysseus de tuer avec l'airain aigu le brave fils de Zeus. C'est"});
db.iliadfr.insert({ pg : 3265, line : "pourquoi Athènè lui inspira de se jeter sur la foule des Lykiens."});
db.iliadfr.insert({ pg : 3266, line : "Alors il tua Koiranos et Alastôr, et Khromios et Alkandros et"});
db.iliadfr.insert({ pg : 3267, line : "Halios, et Noèmôn et Prytanis. Et le divin Odysseus eût tué une"});
db.iliadfr.insert({ pg : 3268, line : "plus grande foule de Lykiens, si le grand Hektôr au casque mouvant"});
db.iliadfr.insert({ pg : 3269, line : "ne l'eût aperçu. Et il s'élança aux premiers rangs, armé de"});
db.iliadfr.insert({ pg : 3270, line : "l'airain éclatant, jetant la terreur parmi les Danaens. Et"});
db.iliadfr.insert({ pg : 3271, line : "Sarpèdôn, fils de Zeus, se réjouit de sa venue et lui dit cette"});
db.iliadfr.insert({ pg : 3272, line : "parole lamentable:"});
db.iliadfr.insert({ pg : 3274, line : "-- Priamide, ne permets pas que je reste la proie des Danaens, et"});
db.iliadfr.insert({ pg : 3275, line : "viens à mon aide, afin que je puisse au moins expirer dans votre"});
db.iliadfr.insert({ pg : 3276, line : "ville, puisque je ne dois plus revoir la chère patrie, et ma femme"});
db.iliadfr.insert({ pg : 3277, line : "bien-aimée et mon petit enfant."});
db.iliadfr.insert({ pg : 3279, line : "Mais Hektôr au casque mouvant ne lui répondit pas, et il s'élança"});
db.iliadfr.insert({ pg : 3280, line : "en avant, plein du désir de repousser promptement les Argiens et"});
db.iliadfr.insert({ pg : 3281, line : "d'arracher l'âme à une foule d'entre eux. Et les compagnons du"});
db.iliadfr.insert({ pg : 3282, line : "divin Sarpèdôn le déposèrent sous le beau hêtre de Zeus"});
db.iliadfr.insert({ pg : 3283, line : "tempétueux, et le brave Pélagôn, qui était le plus cher de ses"});
db.iliadfr.insert({ pg : 3284, line : "compagnons, lui arracha hors de la cuisse la pique de frêne. Et"});
db.iliadfr.insert({ pg : 3285, line : "son âme défaillit, et une nuée épaisse couvrit ses yeux. Mais le"});
db.iliadfr.insert({ pg : 3286, line : "souffle de Boréas le ranima, et il ressaisit son âme qui"});
db.iliadfr.insert({ pg : 3287, line : "s'évanouissait."});
db.iliadfr.insert({ pg : 3289, line : "Et les Akhaiens, devant Arès et Hektôr au casque d'airain, ne"});
db.iliadfr.insert({ pg : 3290, line : "fuyaient point vers les nefs noires et ne se ruaient pas non plus"});
db.iliadfr.insert({ pg : 3291, line : "dans la mêlée, mais reculaient toujours, ayant aperçu Arès parmi"});
db.iliadfr.insert({ pg : 3292, line : "les Troiens. Alors, quel fut le guerrier qui, le premier, fut tué"});
db.iliadfr.insert({ pg : 3293, line : "par Hektôr Priamide et par Arès vêtu d'airain, et quel fut le"});
db.iliadfr.insert({ pg : 3294, line : "dernier? Teuthras, semblable à un dieu, et l'habile cavalier"});
db.iliadfr.insert({ pg : 3295, line : "Orestès, et Trèkhos, combattant Aitôlien; Oinomaos et l'Oinopide"});
db.iliadfr.insert({ pg : 3296, line : "Hélénos, et Oresbios qui portait une mitre brillante. Et celui-ci"});
db.iliadfr.insert({ pg : 3297, line : "habitait Hylè, où il prenait soin de ses richesses, au milieu du"});
db.iliadfr.insert({ pg : 3298, line : "lac Kèphisside, non loin des riches tribus des Boiôtiens."});
db.iliadfr.insert({ pg : 3300, line : "Et la divine Hèrè aux bras blancs, voyant que les Argiens"});
db.iliadfr.insert({ pg : 3301, line : "périssaient dans la rude mêlée, dit à Athènè ces paroles ailées:"});
db.iliadfr.insert({ pg : 3303, line : "-- Ah! fille indomptable de Zeus tempétueux, certes, nous aurons"});
db.iliadfr.insert({ pg : 3304, line : "vainement promis à Ménélaos qu'il retournerait dans sa patrie"});
db.iliadfr.insert({ pg : 3305, line : "après avoir renversé Ilios aux fortes murailles, si nous laissons"});
db.iliadfr.insert({ pg : 3306, line : "ainsi le cruel Arès répandre sa fureur. Viens, et souvenons-nous"});
db.iliadfr.insert({ pg : 3307, line : "de notre courage impétueux."});
db.iliadfr.insert({ pg : 3309, line : "Elle parla ainsi, et la divine Athènè aux yeux clairs obéit. La"});
db.iliadfr.insert({ pg : 3310, line : "vénérable déesse Hèrè, fille du grand Kronos, se hâta de mettre à"});
db.iliadfr.insert({ pg : 3311, line : "ses chevaux leurs harnais d'or. Hèbè attacha promptement les roues"});
db.iliadfr.insert({ pg : 3312, line : "au char, aux deux bouts de l'essieu de fer. Et les roues étaient"});
db.iliadfr.insert({ pg : 3313, line : "d'airain à huit rayons, et les jantes étaient d'un or"});
db.iliadfr.insert({ pg : 3314, line : "incorruptible, mais, par-dessus, étaient posées des bandes"});
db.iliadfr.insert({ pg : 3315, line : "d'airain admirables à voir. Les deux moyeux étaient revêtus"});
db.iliadfr.insert({ pg : 3316, line : "d'argent, et le siège était suspendu à des courroies d'or et"});
db.iliadfr.insert({ pg : 3317, line : "d'argent, et deux cercles étaient placés en avant d'où sortait le"});
db.iliadfr.insert({ pg : 3318, line : "timon d'argent, et, à l'extrémité du timon, Hèrè lia le beau joug"});
db.iliadfr.insert({ pg : 3319, line : "d'or et les belles courroies d'or. Puis, avide de discorde et de"});
db.iliadfr.insert({ pg : 3320, line : "cris de guerre, elle soumit au joug ses chevaux aux pieds rapides."});
db.iliadfr.insert({ pg : 3322, line : "Et Athènè, fille de Zeus tempétueux, laissa tomber sur le pavé de"});
db.iliadfr.insert({ pg : 3323, line : "la demeure paternelle le péplos subtil, aux ornements variés,"});
db.iliadfr.insert({ pg : 3324, line : "qu'elle avait fait et achevé de ses mains. Et elle revêtit la"});
db.iliadfr.insert({ pg : 3325, line : "cuirasse de Zeus qui amasse les nuées, et l'armure de la guerre"});
db.iliadfr.insert({ pg : 3326, line : "lamentable. Elle plaça autour de ses épaules l'Aigide aux longues"});
db.iliadfr.insert({ pg : 3327, line : "franges, horrible, et que la fuite environnait. Et là, se tenaient"});
db.iliadfr.insert({ pg : 3328, line : "la discorde, la force et l'effrayante poursuite, et la tête"});
db.iliadfr.insert({ pg : 3329, line : "affreuse, horrible et divine du monstre Gorgô. Et Athènè posa sur"});
db.iliadfr.insert({ pg : 3330, line : "sa tête un casque hérissé d'aigrettes, aux quatre cônes d'or, et"});
db.iliadfr.insert({ pg : 3331, line : "qui eût recouvert les habitants de cent villes. Et elle monta sur"});
db.iliadfr.insert({ pg : 3332, line : "le char splendide, et elle saisit une pique lourde, grande,"});
db.iliadfr.insert({ pg : 3333, line : "solide, avec laquelle elle domptait la foule des hommes héroïques,"});
db.iliadfr.insert({ pg : 3334, line : "contre lesquels elle s'irritait, étant la fille d'un père"});
db.iliadfr.insert({ pg : 3335, line : "puissant."});
db.iliadfr.insert({ pg : 3337, line : "Hèrè pressa du fouet les chevaux rapides, et, devant eux,"});
db.iliadfr.insert({ pg : 3338, line : "s'ouvrirent d'elles-mêmes les portes ouraniennes que gardaient les"});
db.iliadfr.insert({ pg : 3339, line : "Heures. Et celles-ci, veillant sur le grand Ouranos et sur"});
db.iliadfr.insert({ pg : 3340, line : "l'Olympos, ouvraient ou fermaient la nuée épaisse qui flottait"});
db.iliadfr.insert({ pg : 3341, line : "autour. Et les chevaux dociles franchirent ces portes, et les"});
db.iliadfr.insert({ pg : 3342, line : "déesses trouvèrent le Kroniôn assis, loin des dieux, sur le plus"});
db.iliadfr.insert({ pg : 3343, line : "haut sommet de l'Olympos aux cimes sans nombre. Et la divine Hèrè"});
db.iliadfr.insert({ pg : 3344, line : "aux bras blancs, retenant ses chevaux, parla ainsi au très haut"});
db.iliadfr.insert({ pg : 3345, line : "Zeus Kronide:"});
db.iliadfr.insert({ pg : 3347, line : "-- Zeus, ne réprimeras-tu pas les cruelles violences d'Arès qui"});
db.iliadfr.insert({ pg : 3348, line : "cause impudemment tant de ravages parmi les peuples Akhaiens? J'en"});
db.iliadfr.insert({ pg : 3349, line : "ai une grande douleur; et voici qu'Aphroditè et Apollôn à l'arc"});
db.iliadfr.insert({ pg : 3350, line : "d'argent se réjouissent d'avoir excité cet insensé qui ignore"});
db.iliadfr.insert({ pg : 3351, line : "toute justice. Père Zeus, ne t'irriteras-tu point contre moi, si"});
db.iliadfr.insert({ pg : 3352, line : "je chasse de la mêlée Arès rudement châtié?"});
db.iliadfr.insert({ pg : 3354, line : "Et Zeus qui amasse les nuées lui répondit:"});
db.iliadfr.insert({ pg : 3356, line : "-- Va! excite contre lui la dévastatrice Athènè, qui est"});
db.iliadfr.insert({ pg : 3357, line : "accoutumée à lui infliger de rudes châtiments."});
db.iliadfr.insert({ pg : 3359, line : "Il parla ainsi, et la divine Hèrè aux bras blancs obéit, et elle"});
db.iliadfr.insert({ pg : 3360, line : "frappa ses chevaux, et ils s'envolèrent entre la terre et"});
db.iliadfr.insert({ pg : 3361, line : "l'Ouranos étoilé. Autant un homme, assis sur une roche élevée, et"});
db.iliadfr.insert({ pg : 3362, line : "regardant la mer pourprée, voit d'espace aérien, autant les"});
db.iliadfr.insert({ pg : 3363, line : "chevaux des dieux en franchirent d'un saut. Et quand les deux"});
db.iliadfr.insert({ pg : 3364, line : "déesses furent parvenues devant Ilios, là où le Skamandros et le"});
db.iliadfr.insert({ pg : 3365, line : "Simoïs unissent leurs cours, la divine Hèrè aux bras blancs détela"});
db.iliadfr.insert({ pg : 3366, line : "ses chevaux et les enveloppa d'une nuée épaisse. Et le Simoïs fit"});
db.iliadfr.insert({ pg : 3367, line : "croître pour eux une pâture ambroisienne. Et les déesses,"});
db.iliadfr.insert({ pg : 3368, line : "semblables dans leur vol à de jeunes colombes, se hâtèrent de"});
db.iliadfr.insert({ pg : 3369, line : "secourir les Argiens."});
db.iliadfr.insert({ pg : 3371, line : "Et quand elles parvinrent là où les Akhaiens luttaient en foule"});
db.iliadfr.insert({ pg : 3372, line : "autour de la force du dompteur de chevaux Diomèdès, tels que des"});
db.iliadfr.insert({ pg : 3373, line : "lions mangeurs de chair crue, ou de sauvages et opiniâtres"});
db.iliadfr.insert({ pg : 3374, line : "sangliers, la divine Hèrè aux bras blancs s'arrêta et jeta un"});
db.iliadfr.insert({ pg : 3375, line : "grand cri, ayant pris la forme du magnanime Stentôr à la voix"});
db.iliadfr.insert({ pg : 3376, line : "d'airain, qui criait aussi haut que cinquante autres:"});
db.iliadfr.insert({ pg : 3378, line : "-- Honte à vous, ô Argiens, fiers d'être beaux, mais couverts"});
db.iliadfr.insert({ pg : 3379, line : "d'opprobre! Aussi longtemps que le divin Akhilleus se rua dans la"});
db.iliadfr.insert({ pg : 3380, line : "mêlée, jamais les Troiens n'osèrent passer les portes"});
db.iliadfr.insert({ pg : 3381, line : "Dardaniennes; et, maintenant, voici qu'ils combattent loin"});
db.iliadfr.insert({ pg : 3382, line : "d'Ilios, devant les nefs creuses!"});
db.iliadfr.insert({ pg : 3384, line : "Ayant ainsi parlé, elle ranima le courage de chacun. Et la déesse"});
db.iliadfr.insert({ pg : 3385, line : "Athènè aux yeux clairs, cherchant le Tydéide, rencontra ce roi"});
db.iliadfr.insert({ pg : 3386, line : "auprès de ses chevaux et de son char. Et il rafraîchissait la"});
db.iliadfr.insert({ pg : 3387, line : "blessure que lui avait faite la flèche de Pandaros. Et la sueur"});
db.iliadfr.insert({ pg : 3388, line : "l'inondait sous le large ceinturon d'où pendait son bouclier"});
db.iliadfr.insert({ pg : 3389, line : "bombé; et ses mains étaient lasses. Il soulevait son ceinturon et"});
db.iliadfr.insert({ pg : 3390, line : "étanchait un sang noir. Et la déesse, auprès du joug, lui parla"});
db.iliadfr.insert({ pg : 3391, line : "ainsi:"});
db.iliadfr.insert({ pg : 3393, line : "-- Certes, Tydeus n'a point engendré un fils semblable à lui."});
db.iliadfr.insert({ pg : 3394, line : "Tydeus était de petite taille, mais c'était un homme. Je lui"});
db.iliadfr.insert({ pg : 3395, line : "défendis vainement de combattre quand il vint seul, envoyé à Thèbè"});
db.iliadfr.insert({ pg : 3396, line : "par les Akhaiens, au milieu des innombrables Kadméiônes. Et je lui"});
db.iliadfr.insert({ pg : 3397, line : "ordonnai de s'asseoir paisiblement à leurs repas, dans leurs"});
db.iliadfr.insert({ pg : 3398, line : "demeures. Cependant, ayant toujours le coeur aussi ferme, il"});
db.iliadfr.insert({ pg : 3399, line : "provoqua les jeunes Kadméiônes et les vainquit aisément, car"});
db.iliadfr.insert({ pg : 3400, line : "j'étais sa protectrice assidue. Certes, aujourd'hui, je te"});
db.iliadfr.insert({ pg : 3401, line : "protège, je te défends et je te pousse à combattre ardemment les"});
db.iliadfr.insert({ pg : 3402, line : "Troiens. Mais la fatigue a rompu tes membres, ou la crainte t'a"});
db.iliadfr.insert({ pg : 3403, line : "saisi le coeur, et tu n'es plus le fils de l'excellent cavalier"});
db.iliadfr.insert({ pg : 3404, line : "Tydeus Oinéide."});
db.iliadfr.insert({ pg : 3406, line : "Et le brave Diomèdès lui répondit:"});
db.iliadfr.insert({ pg : 3408, line : "-- Je te reconnais, déesse, fille de Zeus tempétueux. Je te"});
db.iliadfr.insert({ pg : 3409, line : "parlerai franchement et ne te cacherai rien. Ni la crainte ni la"});
db.iliadfr.insert({ pg : 3410, line : "faiblesse ne m'accablent, mais je me souviens de tes ordres. Tu"});
db.iliadfr.insert({ pg : 3411, line : "m'as défendu de combattre les dieux heureux, mais de frapper de"});
db.iliadfr.insert({ pg : 3412, line : "l'airain aigu Aphroditè, la fille de Zeus, si elle descendait dans"});
db.iliadfr.insert({ pg : 3413, line : "la mêlée. C'est pourquoi je recule maintenant, et j'ai ordonné à"});
db.iliadfr.insert({ pg : 3414, line : "tous les Argiens de se réunir ici, car j'ai reconnu Arès qui"});
db.iliadfr.insert({ pg : 3415, line : "dirige le combat."});
db.iliadfr.insert({ pg : 3417, line : "Et la divine Athènè aux yeux clairs lui répondit:"});
db.iliadfr.insert({ pg : 3419, line : "-- Tydéide Diomèdès, le plus cher à mon coeur, ne crains ni Arès"});
db.iliadfr.insert({ pg : 3420, line : "ni aucun des autres immortels, car je suis pour toi une"});
db.iliadfr.insert({ pg : 3421, line : "protectrice assidue. Viens! pousse contre Arès tes chevaux aux"});
db.iliadfr.insert({ pg : 3422, line : "sabots massifs; frappe-le, et ne respecte pas le furieux Arès, ce"});
db.iliadfr.insert({ pg : 3423, line : "dieu changeant et insensé qui, naguère, nous avait promis, à moi"});
db.iliadfr.insert({ pg : 3424, line : "et à Hèrè, de combattre les Troiens et de secourir les Argiens, et"});
db.iliadfr.insert({ pg : 3425, line : "qui, maintenant, s'est tourné du côté des Troiens et oublie ses"});
db.iliadfr.insert({ pg : 3426, line : "promesses."});
db.iliadfr.insert({ pg : 3428, line : "Ayant ainsi parlé, elle saisit de la main Sthénélos pour le faire"});
db.iliadfr.insert({ pg : 3429, line : "descendre du char, et celui-ci sauta promptement à terre. Et elle"});
db.iliadfr.insert({ pg : 3430, line : "monta auprès du divin Diomèdès, et l'essieu du char gémit sous le"});
db.iliadfr.insert({ pg : 3431, line : "poids, car il portait une déesse puissante et un brave guerrier."});
db.iliadfr.insert({ pg : 3432, line : "Et Pallas Athènè, saisissant le fouet et les rênes, poussa vers"});
db.iliadfr.insert({ pg : 3433, line : "Arès les chevaux aux sabots massifs. Et le dieu venait de tuer le"});
db.iliadfr.insert({ pg : 3434, line : "grand Périphas, le plus brave des Aitôliens, illustre fils"});
db.iliadfr.insert({ pg : 3435, line : "d'Okhèsios; et, tout sanglant, il le dépouillait; mais Athènè mit"});
db.iliadfr.insert({ pg : 3436, line : "le casque d'Aidès, pour que le puissant Arès ne la reconnût pas."});
db.iliadfr.insert({ pg : 3437, line : "Et dès que le fléau des hommes, Arès, eut aperçu le divin"});
db.iliadfr.insert({ pg : 3438, line : "Diomèdès, il laissa le grand Périphas étendu dans la poussière, là"});
db.iliadfr.insert({ pg : 3439, line : "où, l'ayant tué, il lui avait arraché l'âme, et il marcha droit à"});
db.iliadfr.insert({ pg : 3440, line : "l'habile cavalier Diomèdès."});
db.iliadfr.insert({ pg : 3442, line : "Et quand ils se furent rapprochés l'un de l'autre, Arès, le"});
db.iliadfr.insert({ pg : 3443, line : "premier, lança sa pique d'airain par-dessus le joug et les rênes"});
db.iliadfr.insert({ pg : 3444, line : "des chevaux, voulant arracher l'âme du Tydéide; mais la divine"});
db.iliadfr.insert({ pg : 3445, line : "Athènè aux yeux clairs, saisissant le trait d'une main, le"});
db.iliadfr.insert({ pg : 3446, line : "détourna du char, afin de le rendre inutile. Puis, Diomèdès hardi"});
db.iliadfr.insert({ pg : 3447, line : "au combat lança impétueusement sa pique d'airain, et Pallas Athènè"});
db.iliadfr.insert({ pg : 3448, line : "la dirigea dans le bas ventre, sous le ceinturon."});
db.iliadfr.insert({ pg : 3450, line : "Et le dieu fut blessé, et la pique, ramenée en arrière, déchira sa"});
db.iliadfr.insert({ pg : 3451, line : "belle peau, et le féroce Arès poussa un cri aussi fort que la"});
db.iliadfr.insert({ pg : 3452, line : "clameur de dix mille guerriers se ruant dans la mêlée. Et"});
db.iliadfr.insert({ pg : 3453, line : "l'épouvante saisit les Akhaiens et les Troiens, tant avait retenti"});
db.iliadfr.insert({ pg : 3454, line : "le cri d'Arès insatiable de combats. Et, comme apparaît, au-"});
db.iliadfr.insert({ pg : 3455, line : "dessous des nuées, une noire vapeur chassée par un vent brûlant,"});
db.iliadfr.insert({ pg : 3456, line : "ainsi Arès apparut au brave Tydéide Diomèdès, tandis qu'il"});
db.iliadfr.insert({ pg : 3457, line : "traversait le vaste Ouranos, au milieu des nuages. Et il parvint à"});
db.iliadfr.insert({ pg : 3458, line : "la demeure des dieux, dans le haut Olympos. Et il s'assit auprès"});
db.iliadfr.insert({ pg : 3459, line : "de Zeus Kroniôn, gémissant dans son coeur; et, lui montrant le"});
db.iliadfr.insert({ pg : 3460, line : "sang immortel qui coulait de sa blessure, il lui dit en paroles"});
db.iliadfr.insert({ pg : 3461, line : "ailées:"});
db.iliadfr.insert({ pg : 3463, line : "-- Père Zeus, ne t'indigneras-tu point de voir ces violences?"});
db.iliadfr.insert({ pg : 3464, line : "Toujours, nous, les dieux, nous nous faisons souffrir cruellement"});
db.iliadfr.insert({ pg : 3465, line : "pour la cause des hommes. Mais c'est toi qui es la source de nos"});
db.iliadfr.insert({ pg : 3466, line : "querelles, car tu as enfanté une fille insensée, perverse et"});
db.iliadfr.insert({ pg : 3467, line : "inique. Nous, les dieux Olympiens, nous t'obéissons et nous te"});
db.iliadfr.insert({ pg : 3468, line : "sommes également soumis; mais jamais tu ne blâmes ni ne réprimes"});
db.iliadfr.insert({ pg : 3469, line : "celle-ci, et tu lui permets tout, parce que tu as engendré seul"});
db.iliadfr.insert({ pg : 3470, line : "cette fille funeste qui pousse le fils de Tydeus, le magnanime"});
db.iliadfr.insert({ pg : 3471, line : "Diomèdès, à se jeter furieux sur les dieux immortels. Il a blessé"});
db.iliadfr.insert({ pg : 3472, line : "d'abord la main d'Aphroditè, puis, il s'est rué sur moi, semblable"});
db.iliadfr.insert({ pg : 3473, line : "à un dieu, et si mes pieds rapides ne m'avaient emporté, je"});
db.iliadfr.insert({ pg : 3474, line : "subirais mille maux, couché vivant au milieu des cadavres et livré"});
db.iliadfr.insert({ pg : 3475, line : "sans force aux coups de l'airain."});
db.iliadfr.insert({ pg : 3477, line : "Et Zeus qui amasse les nuées, le regardant d'un oeil sombre, lui"});
db.iliadfr.insert({ pg : 3478, line : "répondit:"});
db.iliadfr.insert({ pg : 3480, line : "-- Cesse de te plaindre à moi, dieu changeant! Je te hais le plus"});
db.iliadfr.insert({ pg : 3481, line : "entre tous les Olympiens, car tu n'aimes que la discorde, la"});
db.iliadfr.insert({ pg : 3482, line : "guerre et le combat, et tu as l'esprit intraitable de ta mère,"});
db.iliadfr.insert({ pg : 3483, line : "Hèrè, que mes paroles répriment à peine. C'est son exemple qui"});
db.iliadfr.insert({ pg : 3484, line : "cause tes maux. Mais je ne permettrai pas que tu souffres plus"});
db.iliadfr.insert({ pg : 3485, line : "longtemps, car tu es mon fils, et c'est de moi que ta mère t'a"});
db.iliadfr.insert({ pg : 3486, line : "conçu. Méchant comme tu es, si tu étais né de quelque autre dieu,"});
db.iliadfr.insert({ pg : 3487, line : "depuis longtemps déjà tu serais le dernier des Ouraniens."});
db.iliadfr.insert({ pg : 3489, line : "Il parla ainsi et ordonna à Paièôn de le guérir, et celui-ci le"});
db.iliadfr.insert({ pg : 3490, line : "guérit en arrosant sa blessure de doux remèdes liquides, car il"});
db.iliadfr.insert({ pg : 3491, line : "n'était point mortel. Aussi vite le lait blanc s'épaissit quand on"});
db.iliadfr.insert({ pg : 3492, line : "l'agite, aussi vite le furieux Arès fut guéri. Hèbè le baigna et"});
db.iliadfr.insert({ pg : 3493, line : "le revêtit de beaux vêtements, et il s'assit, fier de cet honneur,"});
db.iliadfr.insert({ pg : 3494, line : "auprès de Zeus Kroniôn. Et l'Argienne Hèrè et la protectrice"});
db.iliadfr.insert({ pg : 3495, line : "Athènè rentrèrent dans la demeure du grand Zeus, après avoir"});
db.iliadfr.insert({ pg : 3496, line : "chassé le cruel Arès de la mêlée guerrière."});
db.iliadfr.insert({ pg : 3499, line : "Chant 6"});
db.iliadfr.insert({ pg : 3501, line : "Livrée à elle-même, la rude bataille des Troiens et des Akhaiens"});
db.iliadfr.insert({ pg : 3502, line : "se répandit confusément çà et là par la plaine. Et ils se"});
db.iliadfr.insert({ pg : 3503, line : "frappaient, les uns les autres, de leurs lances d'airain, entre"});
db.iliadfr.insert({ pg : 3504, line : "les eaux courantes du Simoïs et du Xanthos."});
db.iliadfr.insert({ pg : 3506, line : "Et, le premier, Aias Télamônien enfonça la phalange des Troiens et"});
db.iliadfr.insert({ pg : 3507, line : "ralluma l'espérance de ses compagnons, ayant percé un guerrier, le"});
db.iliadfr.insert({ pg : 3508, line : "plus courageux d'entre les Thrakiens, le fils d'Eussôros, Akamas,"});
db.iliadfr.insert({ pg : 3509, line : "qui était robuste et grand. Il frappa le cône du casque à"});
db.iliadfr.insert({ pg : 3510, line : "l'épaisse crinière de cheval, et la pointe d'airain, ouvrant le"});
db.iliadfr.insert({ pg : 3511, line : "front, s'enfonça à travers l'os, et les ténèbres couvrirent ses"});
db.iliadfr.insert({ pg : 3512, line : "yeux."});
db.iliadfr.insert({ pg : 3514, line : "Et Diomèdès hardi au combat tua Axylos Teuthranide qui habitait"});
db.iliadfr.insert({ pg : 3515, line : "dans Arisbè bien bâtie, était riche et bienveillant aux hommes, et"});
db.iliadfr.insert({ pg : 3516, line : "les recevait tous avec amitié, sa demeure étant au bord de la"});
db.iliadfr.insert({ pg : 3517, line : "route. Mais nul alors ne se mit au-devant de lui pour détourner la"});
db.iliadfr.insert({ pg : 3518, line : "sombre mort. Et Diomèdès le tua, ainsi que son serviteur Kalésios,"});
db.iliadfr.insert({ pg : 3519, line : "qui dirigeait ses chevaux, et tous deux descendirent sous la"});
db.iliadfr.insert({ pg : 3520, line : "terre."});
db.iliadfr.insert({ pg : 3522, line : "Et Euryalos tua Drèsos et Opheltios, et il se jeta sur Aisèpos et"});
db.iliadfr.insert({ pg : 3523, line : "Pèdasos, que la nymphe naïade Abarbaréè avait conçus autrefois de"});
db.iliadfr.insert({ pg : 3524, line : "l'irréprochable Boukoliôn. Et Boukoliôn était fils du noble"});
db.iliadfr.insert({ pg : 3525, line : "Laomédôn, et il était son premier-né, et sa mère l'avait enfanté"});
db.iliadfr.insert({ pg : 3526, line : "en secret. En paissant ses brebis, il s'était uni à la nymphe sur"});
db.iliadfr.insert({ pg : 3527, line : "une même couche; et, enceinte, elle avait enfanté deux fils"});
db.iliadfr.insert({ pg : 3528, line : "jumeaux; mais le Mèkistèiade brisa leur force et leurs souples"});
db.iliadfr.insert({ pg : 3529, line : "membres, et arracha leurs armures de leurs épaules."});
db.iliadfr.insert({ pg : 3531, line : "Et Polypoitès prompt au combat tua Astyalos; et Odysseus tua"});
db.iliadfr.insert({ pg : 3532, line : "Pidytès le Perkosien, par la lance d'airain; et Teukros tua le"});
db.iliadfr.insert({ pg : 3533, line : "divin Arétaôn."});
db.iliadfr.insert({ pg : 3535, line : "Et Antilokhos Nestoréide tua Ablèros de sa lance éclatante; et le"});
db.iliadfr.insert({ pg : 3536, line : "roi des hommes, Agamemnôn, tua Élatos qui habitait la haute"});
db.iliadfr.insert({ pg : 3537, line : "Pèdasos, sur les bords du Saméoïs au beau cours. Et le héros"});
db.iliadfr.insert({ pg : 3538, line : "Lèitos tua Phylakos qui fuyait, et Eurypylos tua Mélanthios. Puis,"});
db.iliadfr.insert({ pg : 3539, line : "Ménélaos hardi au combat prit Adrèstos vivant. Arrêtés par une"});
db.iliadfr.insert({ pg : 3540, line : "branche de tamaris, les deux chevaux de celui-ci, ayant rompu le"});
db.iliadfr.insert({ pg : 3541, line : "char près du timon, s'enfuyaient, épouvantés, par la plaine, du"});
db.iliadfr.insert({ pg : 3542, line : "côté de la ville, avec d'autres chevaux effrayés, et Adrèstos"});
db.iliadfr.insert({ pg : 3543, line : "avait roulé du char, auprès de la roue, la face dans la poussière."});
db.iliadfr.insert({ pg : 3544, line : "Et l'Atréide Ménélaos, armé d'une longue lance, s'arrêta devant"});
db.iliadfr.insert({ pg : 3545, line : "lui; et Adrèstos saisit ses genoux et le supplia:"});
db.iliadfr.insert({ pg : 3547, line : "-- Laisse-moi la vie, fils d'Atreus, et accepte une riche rançon."});
db.iliadfr.insert({ pg : 3548, line : "Une multitude de choses précieuses sont dans la demeure de mon"});
db.iliadfr.insert({ pg : 3549, line : "père, et il est riche. Il a de l'airain, de l'or et du fer ouvragé"});
db.iliadfr.insert({ pg : 3550, line : "dont il te fera de larges dons, s'il apprend que je vis encore sur"});
db.iliadfr.insert({ pg : 3551, line : "les nefs des Argiens."});
db.iliadfr.insert({ pg : 3553, line : "Il parla ainsi, et déjà il persuadait le coeur de Ménélaos, et"});
db.iliadfr.insert({ pg : 3554, line : "celui-ci allait le remettre à son serviteur pour qu'il l'emmenât"});
db.iliadfr.insert({ pg : 3555, line : "vers les nefs rapides des Akhaiens; mais Agamemnôn vint en courant"});
db.iliadfr.insert({ pg : 3556, line : "au-devant de lui, et lui cria cette dure parole:"});
db.iliadfr.insert({ pg : 3558, line : "-- Ô lâche Ménélaos, pourquoi prendre ainsi pitié des hommes?"});
db.iliadfr.insert({ pg : 3559, line : "Certes, les Troiens ont accompli d'excellentes actions dans ta"});
db.iliadfr.insert({ pg : 3560, line : "demeure! que nul n'évite une fin terrible et n'échappe de nos"});
db.iliadfr.insert({ pg : 3561, line : "mains! pas même l'enfant dans le sein de sa mère! qu'ils meurent"});
db.iliadfr.insert({ pg : 3562, line : "tous avec Ilios, sans sépulture et sans mémoire!"});
db.iliadfr.insert({ pg : 3564, line : "Par ces paroles équitables, le héros changea l'esprit de son frère"});
db.iliadfr.insert({ pg : 3565, line : "qui repoussa le héros Adrèstos. Et le roi Agamemnôn le frappa au"});
db.iliadfr.insert({ pg : 3566, line : "front et le renversa, et l'Atréide, lui mettant le pied sur la"});
db.iliadfr.insert({ pg : 3567, line : "poitrine, arracha la lance de frêne."});
db.iliadfr.insert({ pg : 3569, line : "Et Nestôr, à haute voix, animait les Argiens:"});
db.iliadfr.insert({ pg : 3571, line : "-- Ô amis, héros Danaens, serviteurs d'Arès, que nul ne s'attarde,"});
db.iliadfr.insert({ pg : 3572, line : "dans son désir des dépouilles et pour en porter beaucoup vers les"});
db.iliadfr.insert({ pg : 3573, line : "nefs! Tuons des hommes! Vous dépouillerez ensuite à loisir les"});
db.iliadfr.insert({ pg : 3574, line : "morts couchés dans la plaine!"});
db.iliadfr.insert({ pg : 3576, line : "Ayant ainsi parlé, il excitait la force et le courage de chacun."});
db.iliadfr.insert({ pg : 3577, line : "Et les Troiens, domptés par leur lâcheté, eussent regagné la haute"});
db.iliadfr.insert({ pg : 3578, line : "Ilios, devant les Akhaiens chers à Arès, si le Priamide Hélénos,"});
db.iliadfr.insert({ pg : 3579, line : "le plus illustre de tous les divinateurs, ayant abordé Ainéias et"});
db.iliadfr.insert({ pg : 3580, line : "Hektôr, ne leur eût dit:"});
db.iliadfr.insert({ pg : 3582, line : "-- Ainéias et Hektôr, puisque le fardeau des Troiens et des"});
db.iliadfr.insert({ pg : 3583, line : "Lykiens pèse tout entier sur vous qui êtes les princes du combat"});
db.iliadfr.insert({ pg : 3584, line : "et des délibérations, debout ici, arrêtez de toutes parts ce"});
db.iliadfr.insert({ pg : 3585, line : "peuple devant les portes, avant qu'ils se réfugient tous jusque"});
db.iliadfr.insert({ pg : 3586, line : "dans les bras des femmes et soient en risée aux ennemis. Et quand"});
db.iliadfr.insert({ pg : 3587, line : "vous aurez exhorté toutes les phalanges, nous combattrons,"});
db.iliadfr.insert({ pg : 3588, line : "inébranlables, contre les Danaens, bien que rompus de lassitude;"});
db.iliadfr.insert({ pg : 3589, line : "mais la nécessité le veut. Puis, Hektôr, rends-toi à la ville, et"});
db.iliadfr.insert({ pg : 3590, line : "dis à notre mère qu'ayant réuni les femmes âgées dans le temple"});
db.iliadfr.insert({ pg : 3591, line : "d'Athènè aux yeux clairs, au sommet de la citadelle, et ouvrant"});
db.iliadfr.insert({ pg : 3592, line : "les portes de la maison sacrée, elle pose sur les genoux d'Athènè"});
db.iliadfr.insert({ pg : 3593, line : "à la belle chevelure le péplos le plus riche et le plus grand qui"});
db.iliadfr.insert({ pg : 3594, line : "soit dans sa demeure, et celui qu'elle aime le plus; et qu'elle"});
db.iliadfr.insert({ pg : 3595, line : "s'engage à sacrifier dans son temple douze génisses d'un an encore"});
db.iliadfr.insert({ pg : 3596, line : "indomptées, si elle prend pitié de la ville et des femmes"});
db.iliadfr.insert({ pg : 3597, line : "Troiennes et de leurs enfants, et si elle détourne de la sainte"});
db.iliadfr.insert({ pg : 3598, line : "Ilios le fils de Tydeus, le féroce guerrier qui répand le plus de"});
db.iliadfr.insert({ pg : 3599, line : "terreur et qui est, je pense, le plus brave des Akhaiens. Jamais"});
db.iliadfr.insert({ pg : 3600, line : "nous n'avons autant redouté Akhilleus, ce chef des hommes, et"});
db.iliadfr.insert({ pg : 3601, line : "qu'on dit le fils d'une déesse; car Diomèdès est plein d'une"});
db.iliadfr.insert({ pg : 3602, line : "grande fureur, et nul ne peut égaler son courage."});
db.iliadfr.insert({ pg : 3604, line : "Il parla ainsi, et Hektôr obéit à son frère. Et il sauta hors du"});
db.iliadfr.insert({ pg : 3605, line : "char avec ses armes, et, agitant deux lances aiguës, il allait de"});
db.iliadfr.insert({ pg : 3606, line : "tous côtés par l'armée, excitant au combat, et il suscita une rude"});
db.iliadfr.insert({ pg : 3607, line : "bataille. Et tous, s'étant retournés, firent tête aux Akhaiens; et"});
db.iliadfr.insert({ pg : 3608, line : "ceux-ci, reculant, cessèrent le carnage, car ils croyaient qu'un"});
db.iliadfr.insert({ pg : 3609, line : "immortel était descendu de l'Ouranos étoilé pour secourir les"});
db.iliadfr.insert({ pg : 3610, line : "Troiens, ces derniers revenant ainsi à la charge. Et, d'une voix"});
db.iliadfr.insert({ pg : 3611, line : "haute, Hektôr excitait les Troiens:"});
db.iliadfr.insert({ pg : 3613, line : "-- Braves Troiens, et vous, alliés venus de si loin, soyez des"});
db.iliadfr.insert({ pg : 3614, line : "hommes! Souvenez-vous de tout votre courage, tandis que j'irai"});
db.iliadfr.insert({ pg : 3615, line : "vers Ilios dire à nos vieillards prudents et à nos femmes de"});
db.iliadfr.insert({ pg : 3616, line : "supplier les dieux et de leur vouer des hécatombes."});
db.iliadfr.insert({ pg : 3618, line : "Ayant ainsi parlé, Hektôr au beau casque s'éloigna, et le cuir"});
db.iliadfr.insert({ pg : 3619, line : "noir qui bordait tout autour l'extrémité du bouclier arrondi"});
db.iliadfr.insert({ pg : 3620, line : "heurtait ses talons et son cou."});
db.iliadfr.insert({ pg : 3622, line : "Et Glaukos, fils de Hippolokhos, et le fils de Tydeus, prompts à"});
db.iliadfr.insert({ pg : 3623, line : "combattre, s'avancèrent entre les deux armées. Et quand ils furent"});
db.iliadfr.insert({ pg : 3624, line : "en face l'un de l'autre, le premier, Diomèdès hardi au combat lui"});
db.iliadfr.insert({ pg : 3625, line : "parla ainsi:"});
db.iliadfr.insert({ pg : 3627, line : "-- Qui es-tu entre les hommes mortels, ô très brave? Je ne t'ai"});
db.iliadfr.insert({ pg : 3628, line : "jamais vu jusqu'ici dans le combat qui glorifie les guerriers; et"});
db.iliadfr.insert({ pg : 3629, line : "certes, maintenant, tu l'emportes de beaucoup sur eux tous par ta"});
db.iliadfr.insert({ pg : 3630, line : "fermeté, puisque tu as attendu ma longue lance. Ce sont les fils"});
db.iliadfr.insert({ pg : 3631, line : "des malheureux qui s'opposent à mon courage. Mais si tu es quelque"});
db.iliadfr.insert({ pg : 3632, line : "immortel, et si tu viens de l'Ouranos, je ne combattrai point"});
db.iliadfr.insert({ pg : 3633, line : "contre les Ouraniens. Car le fils de Dryas, le brave Lykoorgos, ne"});
db.iliadfr.insert({ pg : 3634, line : "vécut pas longtemps, lui qui combattait contre les dieux"});
db.iliadfr.insert({ pg : 3635, line : "ouraniens. Et il poursuivait, sur le sacré Nysa, les nourrices du"});
db.iliadfr.insert({ pg : 3636, line : "furieux Dionysos; et celles-ci, frappées du fouet du tueur"});
db.iliadfr.insert({ pg : 3637, line : "d'hommes Lykoorgos, jetèrent leurs Thyrses; et Dionysos, effrayé,"});
db.iliadfr.insert({ pg : 3638, line : "sauta dans la mer, et Thétis le reçut dans son sein, tremblant et"});
db.iliadfr.insert({ pg : 3639, line : "saisi d'un grand frisson à cause des menaces du guerrier. Et les"});
db.iliadfr.insert({ pg : 3640, line : "dieux qui vivent en repos furent irrités contre celui-ci; et le"});
db.iliadfr.insert({ pg : 3641, line : "fils de Kronos le rendit aveugle, et il ne vécut pas longtemps,"});
db.iliadfr.insert({ pg : 3642, line : "parce qu'il était odieux à tous les immortels. Moi, je ne voudrais"});
db.iliadfr.insert({ pg : 3643, line : "point combattre contre les dieux heureux. Mais si tu es un des"});
db.iliadfr.insert({ pg : 3644, line : "mortels qui mangent les fruits de la terre, approche, afin"});
db.iliadfr.insert({ pg : 3645, line : "d'atteindre plus promptement aux bornes de la mort."});
db.iliadfr.insert({ pg : 3647, line : "Et l'illustre fils de Hippolokhos lui répondit:"});
db.iliadfr.insert({ pg : 3649, line : "-- Magnanime Tydéide, pourquoi t'informes-tu de ma race? La"});
db.iliadfr.insert({ pg : 3650, line : "génération des hommes est semblable à celle des feuilles. Le vent"});
db.iliadfr.insert({ pg : 3651, line : "répand les feuilles sur la terre, et la forêt germe et en produit"});
db.iliadfr.insert({ pg : 3652, line : "de nouvelles, et le temps du printemps arrive. C'est ainsi que la"});
db.iliadfr.insert({ pg : 3653, line : "génération des hommes naît et s'éteint. Mais si tu veux savoir"});
db.iliadfr.insert({ pg : 3654, line : "quelle est ma race que connaissent de nombreux guerriers, sache"});
db.iliadfr.insert({ pg : 3655, line : "qu'il est une ville, Éphyrè, au fond de la terre d'Argos féconde"});
db.iliadfr.insert({ pg : 3656, line : "en chevaux. Là vécut Sisyphos, le plus rusé des hommes, Sisyphos"});
db.iliadfr.insert({ pg : 3657, line : "Aiolidès; et il engendra Glaukos, et Glaukos engendra"});
db.iliadfr.insert({ pg : 3658, line : "l'irréprochable Bellérophontès, à qui les dieux donnèrent la"});
db.iliadfr.insert({ pg : 3659, line : "beauté et la vigueur charmante. Mais Proitos, qui était le plus"});
db.iliadfr.insert({ pg : 3660, line : "puissant des Argiens, car Zeus les avait soumis à son sceptre, eut"});
db.iliadfr.insert({ pg : 3661, line : "contre lui de mauvaises pensées et le chassa de son peuple. Car la"});
db.iliadfr.insert({ pg : 3662, line : "femme de Proitos, la divine Antéia, désira ardemment s'unir au"});
db.iliadfr.insert({ pg : 3663, line : "fils de Glaukos par un amour secret; mais elle ne persuada point"});
db.iliadfr.insert({ pg : 3664, line : "le sage et prudent Bellérophontès; et, pleine de mensonge, elle"});
db.iliadfr.insert({ pg : 3665, line : "parla ainsi au roi Proitos:"});
db.iliadfr.insert({ pg : 3667, line : "-- Meurs, Proitos, ou tue Bellérophontès qui, par violence, a"});
db.iliadfr.insert({ pg : 3668, line : "voulu s'unir d'amour à moi."});
db.iliadfr.insert({ pg : 3670, line : "Elle parla ainsi, et, à ces paroles, la colère saisit le roi. Et"});
db.iliadfr.insert({ pg : 3671, line : "il ne tua point Bellérophontès, redoutant pieusement ce meurtre"});
db.iliadfr.insert({ pg : 3672, line : "dans son esprit; mais il l'envoya en Lykiè avec des tablettes où"});
db.iliadfr.insert({ pg : 3673, line : "il avait tracé des signes de mort, afin qu'il les remît à son"});
db.iliadfr.insert({ pg : 3674, line : "beau-père et que celui-ci le tuât. Et Bellérophontès alla en Lykiè"});
db.iliadfr.insert({ pg : 3675, line : "sous les heureux auspices des dieux. Et quand il y fut arrivé, sur"});
db.iliadfr.insert({ pg : 3676, line : "les bords du rapide Xanthos, le roi de la grande Lykiè le reçut"});
db.iliadfr.insert({ pg : 3677, line : "avec honneur, lui fut hospitalier pendant neuf jours et sacrifia"});
db.iliadfr.insert({ pg : 3678, line : "neuf boeufs. Mais quand Eôs aux doigts rosés reparut pour la"});
db.iliadfr.insert({ pg : 3679, line : "dixième fois, alors il l'interrogea et demanda à voir les signes"});
db.iliadfr.insert({ pg : 3680, line : "envoyés par son gendre Proitos. Et, quand il les eut vus, il lui"});
db.iliadfr.insert({ pg : 3681, line : "ordonna d'abord de tuer l'indomptable Khimaira. Celle-ci était née"});
db.iliadfr.insert({ pg : 3682, line : "des dieux et non des hommes, lion par devant, dragon par"});
db.iliadfr.insert({ pg : 3683, line : "l'arrière, et chèvre par le milieu du corps. Et elle soufflait des"});
db.iliadfr.insert({ pg : 3684, line : "flammes violentes. Mais il la tua, s'étant fié aux prodiges des"});
db.iliadfr.insert({ pg : 3685, line : "dieux. Puis, il combattit les Solymes illustres, et il disait"});
db.iliadfr.insert({ pg : 3686, line : "avoir entrepris là le plus rude combat des guerriers. Enfin il tua"});
db.iliadfr.insert({ pg : 3687, line : "les Amazones viriles. Comme il revenait, le roi lui tendit un"});
db.iliadfr.insert({ pg : 3688, line : "piège rusé, ayant choisi et placé en embuscade les plus braves"});
db.iliadfr.insert({ pg : 3689, line : "guerriers de la grande Lykiè. Mais nul d'entre eux ne revit sa"});
db.iliadfr.insert({ pg : 3690, line : "demeure, car l'irréprochable Bellérophontès les tua tous. Et le"});
db.iliadfr.insert({ pg : 3691, line : "roi connut alors que cet homme était de la race illustre d'un"});
db.iliadfr.insert({ pg : 3692, line : "dieu, et il le retint et lui donna sa fille et la moitié de sa"});
db.iliadfr.insert({ pg : 3693, line : "domination royale. Et les Lykiens lui choisirent un domaine, le"});
db.iliadfr.insert({ pg : 3694, line : "meilleur de tous, plein d'arbres et de champs, afin qu'il le"});
db.iliadfr.insert({ pg : 3695, line : "cultivât. Et sa femme donna trois enfants au brave Bellérophontès:"});
db.iliadfr.insert({ pg : 3696, line : "Isandros, Hippolokhos et Laodaméia. Et le sage Zeus s'unit à"});
db.iliadfr.insert({ pg : 3697, line : "Laodaméia, et elle enfanta le divin Sarpèdôn couvert d'airain."});
db.iliadfr.insert({ pg : 3698, line : "Mais quand Bellérophontès fut en haine aux dieux, il errait seul"});
db.iliadfr.insert({ pg : 3699, line : "dans le désert d'Alèios. Arès insatiable de guerre tua son fils"});
db.iliadfr.insert({ pg : 3700, line : "Isandros, tandis que celui-ci combattait les illustres Solymes."});
db.iliadfr.insert({ pg : 3701, line : "Artémis aux rênes d'or, irritée, tua Laodaméia; et Hippolokhos m'a"});
db.iliadfr.insert({ pg : 3702, line : "engendré, et je dis que je suis né de lui. Et il m'a envoyé à"});
db.iliadfr.insert({ pg : 3703, line : "Troiè, m'ordonnant d'être le premier parmi les plus braves, afin"});
db.iliadfr.insert({ pg : 3704, line : "de ne point déshonorer la génération de mes pères qui ont habité"});
db.iliadfr.insert({ pg : 3705, line : "Éphyrè et la grande Lykiè. Je me glorifie d'être de cette race et"});
db.iliadfr.insert({ pg : 3706, line : "de ce sang."});
db.iliadfr.insert({ pg : 3708, line : "Il parla ainsi, et Diomèdès brave au combat fut joyeux, et il"});
db.iliadfr.insert({ pg : 3709, line : "enfonça sa lance dans la terre nourricière, et il dit avec"});
db.iliadfr.insert({ pg : 3710, line : "bienveillance au prince des peuples:"});
db.iliadfr.insert({ pg : 3712, line : "-- Tu es certainement mon ancien hôte paternel. Autrefois, le"});
db.iliadfr.insert({ pg : 3713, line : "noble Oineus reçut pendant vingt jours dans ses demeures"});
db.iliadfr.insert({ pg : 3714, line : "hospitalières l'irréprochable Bellérophontès. Et ils se firent de"});
db.iliadfr.insert({ pg : 3715, line : "beaux présents. Oineus donna un splendide ceinturon de pourpre, et"});
db.iliadfr.insert({ pg : 3716, line : "Bellérophontès donna une coupe d'or très creuse que j'ai laissée,"});
db.iliadfr.insert({ pg : 3717, line : "en partant, dans mes demeures. Je ne me souviens point de Tydeus,"});
db.iliadfr.insert({ pg : 3718, line : "car il me laissa tout petit quand l'armée des Akhaiens périt"});
db.iliadfr.insert({ pg : 3719, line : "devant Thèbè. C'est pourquoi je suis un ami pour toi dans Argos,"});
db.iliadfr.insert({ pg : 3720, line : "et tu seras le mien en Lykiè quand j'irai vers ce peuple. Évitons"});
db.iliadfr.insert({ pg : 3721, line : "nos lances, même dans la mêlée. J'ai à tuer assez d'autres Troiens"});
db.iliadfr.insert({ pg : 3722, line : "illustres et d'alliés, soit qu'un dieu me les amène, soit que je"});
db.iliadfr.insert({ pg : 3723, line : "les atteigne, et toi assez d'Akhaiens, si tu le peux. Echangeons"});
db.iliadfr.insert({ pg : 3724, line : "nos armes, afin que tous sachent que nous sommes des hôtes"});
db.iliadfr.insert({ pg : 3725, line : "paternels."});
db.iliadfr.insert({ pg : 3727, line : "Ayant ainsi parlé tous deux, ils descendirent de leurs chars et se"});
db.iliadfr.insert({ pg : 3728, line : "serrèrent la main et échangèrent leur foi. Mais le Kronide Zeus"});
db.iliadfr.insert({ pg : 3729, line : "troubla l'esprit de Glaukos qui donna au Tydéide Diomèdès des"});
db.iliadfr.insert({ pg : 3730, line : "armes d'or du prix de cent boeufs pour des armes d'airain du prix"});
db.iliadfr.insert({ pg : 3731, line : "de neuf boeufs."});
db.iliadfr.insert({ pg : 3733, line : "Dès que Hektôr fut arrivé aux portes Skaies et au hêtre, toutes"});
db.iliadfr.insert({ pg : 3734, line : "les femmes et toutes les filles des Troiens couraient autour de"});
db.iliadfr.insert({ pg : 3735, line : "lui, s'inquiétant de leurs fils, de leurs frères, de leurs"});
db.iliadfr.insert({ pg : 3736, line : "concitoyens et de leurs maris. Et il leur ordonna de supplier"});
db.iliadfr.insert({ pg : 3737, line : "toutes ensemble les dieux, un grand deuil étant réservé à beaucoup"});
db.iliadfr.insert({ pg : 3738, line : "d'entre elles. Et quand il fut parvenu à la belle demeure de"});
db.iliadfr.insert({ pg : 3739, line : "Priamos aux portiques éclatants, -- et là s'élevaient cinquante"});
db.iliadfr.insert({ pg : 3740, line : "chambres nuptiales de pierre polie, construites les unes auprès"});
db.iliadfr.insert({ pg : 3741, line : "des autres, où couchaient les fils de Priamos avec leurs femmes"});
db.iliadfr.insert({ pg : 3742, line : "légitimes; et, en face, dans la cour, étaient douze hautes"});
db.iliadfr.insert({ pg : 3743, line : "chambres nuptiales de pierre polie, construites les unes auprès"});
db.iliadfr.insert({ pg : 3744, line : "des autres, où couchaient les gendres de Priamos avec leurs femmes"});
db.iliadfr.insert({ pg : 3745, line : "chastes, -- sa mère vénérable vint au-devant de lui, comme elle"});
db.iliadfr.insert({ pg : 3746, line : "allait chez Laodikè, la plus belle de ses filles, et elle lui prit"});
db.iliadfr.insert({ pg : 3747, line : "la main et parla ainsi:"});
db.iliadfr.insert({ pg : 3749, line : "-- Enfant, pourquoi as-tu quitté la rude bataille? Les fils odieux"});
db.iliadfr.insert({ pg : 3750, line : "des Akhaiens nous pressent sans doute et combattent autour de la"});
db.iliadfr.insert({ pg : 3751, line : "ville, et tu es venu tendre les mains vers Zeus, dans la"});
db.iliadfr.insert({ pg : 3752, line : "citadelle? Attends un peu, et je t'apporterai un vin mielleux afin"});
db.iliadfr.insert({ pg : 3753, line : "que tu en fasses des libations au père Zeus et aux autres"});
db.iliadfr.insert({ pg : 3754, line : "immortels, et que tu sois ranimé, en ayant bu; car le vin augmente"});
db.iliadfr.insert({ pg : 3755, line : "la force du guerrier fatigué; et ta fatigue a été grande, tandis"});
db.iliadfr.insert({ pg : 3756, line : "que tu défendais tes concitoyens."});
db.iliadfr.insert({ pg : 3758, line : "Et le grand Hektôr au casque mouvant lui répondit:"});
db.iliadfr.insert({ pg : 3760, line : "-- Ne m'apporte pas un vin mielleux, mère vénérable, de peur que"});
db.iliadfr.insert({ pg : 3761, line : "tu m'affaiblisses et que je perde force et courage. Je craindrais"});
db.iliadfr.insert({ pg : 3762, line : "de faire des libations de vin pur à Zeus avec des mains souillées,"});
db.iliadfr.insert({ pg : 3763, line : "car il n'est point permis, plein de sang et de poussière,"});
db.iliadfr.insert({ pg : 3764, line : "d'implorer le Krôniôn qui amasse les nuées. Donc, porte des"});
db.iliadfr.insert({ pg : 3765, line : "parfums et réunis les femmes âgées dans le temple d'Athènè"});
db.iliadfr.insert({ pg : 3766, line : "dévastatrice; et dépose sur les genoux d'Athènè à la belle"});
db.iliadfr.insert({ pg : 3767, line : "chevelure le péplos le plus riche et le plus grand qui soit dans"});
db.iliadfr.insert({ pg : 3768, line : "ta demeure, et celui que tu aimes le plus; et promets de sacrifier"});
db.iliadfr.insert({ pg : 3769, line : "dans son temple douze génisses d'un an, encore indomptées, si elle"});
db.iliadfr.insert({ pg : 3770, line : "prend pitié de la ville et des femmes Troiennes et de leurs"});
db.iliadfr.insert({ pg : 3771, line : "enfants, et si elle détourne de la sainte Ilios le fils de Tydeus,"});
db.iliadfr.insert({ pg : 3772, line : "le féroce guerrier qui répand le plus de terreur. Va donc au"});
db.iliadfr.insert({ pg : 3773, line : "temple d'Athènè dévastatrice, et moi, j'irai vers Pâris, afin de"});
db.iliadfr.insert({ pg : 3774, line : "l'appeler, si pourtant il veut entendre ma voix. Plût aux dieux"});
db.iliadfr.insert({ pg : 3775, line : "que la terre s'ouvrît sous lui! car l'Olympien l'a certainement"});
db.iliadfr.insert({ pg : 3776, line : "nourri pour la ruine entière des Troiens, du magnanime Priamos et"});
db.iliadfr.insert({ pg : 3777, line : "de ses fils. Si je le voyais descendre chez Aidès, mon âme serait"});
db.iliadfr.insert({ pg : 3778, line : "délivrée de ses amères douleurs."});
db.iliadfr.insert({ pg : 3780, line : "Il parla ainsi, et Hékabè se rendit à sa demeure et commanda aux"});
db.iliadfr.insert({ pg : 3781, line : "servantes; et celles-ci, par la ville, réunirent les femmes âgées."});
db.iliadfr.insert({ pg : 3782, line : "Puis Hékabè entra dans sa chambre nuptiale parfumée où étaient des"});
db.iliadfr.insert({ pg : 3783, line : "péplos diversement peints, ouvrage des femmes Sidoniennes que le"});
db.iliadfr.insert({ pg : 3784, line : "divin Alexandros avait ramenées de Sidôn, dans sa navigation sur"});
db.iliadfr.insert({ pg : 3785, line : "la haute mer par où il avait conduit Hélènè née d'un père divin."});
db.iliadfr.insert({ pg : 3786, line : "Et, pour l'offrir à Athènè, Hékabè en prit un, le plus beau, le"});
db.iliadfr.insert({ pg : 3787, line : "plus varié et le plus grand; et il brillait comme une étoile et il"});
db.iliadfr.insert({ pg : 3788, line : "était placé le dernier. Et elle se mit en marche, et les femmes"});
db.iliadfr.insert({ pg : 3789, line : "âgées la suivaient."});
db.iliadfr.insert({ pg : 3791, line : "Et quand elles furent arrivées dans le temple d'Athènè, Théanô aux"});
db.iliadfr.insert({ pg : 3792, line : "belles joues, fille de Kissèis, femme du dompteur de chevaux"});
db.iliadfr.insert({ pg : 3793, line : "Antènôr, leur ouvrit les portes, car les Troiens l'avaient faite"});
db.iliadfr.insert({ pg : 3794, line : "prêtresse d'Athènè. Et toutes, avec un gémissement, tendirent les"});
db.iliadfr.insert({ pg : 3795, line : "mains vers Athènè. Et Théanô aux belles joues, ayant reçu le"});
db.iliadfr.insert({ pg : 3796, line : "péplos, le déposa sur les genoux d'Athènè à la belle chevelure,"});
db.iliadfr.insert({ pg : 3797, line : "et, en le lui vouant, elle priait la fille du grand Zeus:"});
db.iliadfr.insert({ pg : 3799, line : "-- Vénérable Athènè, gardienne de la ville, très divine déesse,"});
db.iliadfr.insert({ pg : 3800, line : "brise la lance de Diomèdès, et fais-le tomber lui-même devant les"});
db.iliadfr.insert({ pg : 3801, line : "portes Skaies, afin que nous te sacrifiions dans ton temple douze"});
db.iliadfr.insert({ pg : 3802, line : "génisses d'un an, encore indomptées, si tu prends pitié de la"});
db.iliadfr.insert({ pg : 3803, line : "ville, des femmes Troiennes et de leurs enfants."});
db.iliadfr.insert({ pg : 3805, line : "Elle parla ainsi dans son voeu, et elles suppliaient ainsi la"});
db.iliadfr.insert({ pg : 3806, line : "fille du grand Zeus; mais Pallas Athènè les refusa."});
db.iliadfr.insert({ pg : 3808, line : "Et Hektôr gagna les belles demeures d'Alexandros, que celui-ci"});
db.iliadfr.insert({ pg : 3809, line : "avait construites lui-même à l'aide des meilleurs ouvriers de la"});
db.iliadfr.insert({ pg : 3810, line : "riche Troiè. Et ils avaient construit une chambre nuptiale, une"});
db.iliadfr.insert({ pg : 3811, line : "maison et une cour, auprès des demeures de Priamos et de Hektôr,"});
db.iliadfr.insert({ pg : 3812, line : "au sommet de la citadelle. Ce fut là que Hektôr, cher à Zeus,"});
db.iliadfr.insert({ pg : 3813, line : "entra. Et il tenait à la main une lance haute de dix coudées; et"});
db.iliadfr.insert({ pg : 3814, line : "une pointe d'airain étincelait à l'extrémité de la lance, fixée"});
db.iliadfr.insert({ pg : 3815, line : "par un anneau d'or. Et, dans la chambre nuptiale, il trouva"});
db.iliadfr.insert({ pg : 3816, line : "Alexandros qui s'occupait de ses belles armes, polissant son"});
db.iliadfr.insert({ pg : 3817, line : "bouclier, sa cuirasse et ses arcs recourbés. Et l'Argienne Hélénè"});
db.iliadfr.insert({ pg : 3818, line : "était assise au milieu de ses femmes, dirigeant leurs beaux"});
db.iliadfr.insert({ pg : 3819, line : "travaux."});
db.iliadfr.insert({ pg : 3821, line : "Et Hektôr, ayant regardé Pâris, lui dit ces paroles outrageantes:"});
db.iliadfr.insert({ pg : 3823, line : "-- Misérable! la colère que tu as ressentie n'était point bonne."});
db.iliadfr.insert({ pg : 3824, line : "Nos troupes périssent autour de la ville, sous les hautes"});
db.iliadfr.insert({ pg : 3825, line : "murailles. Grâce à toi, les clameurs de la guerre montent avec"});
db.iliadfr.insert({ pg : 3826, line : "fureur autour de cette ville, et tu blâmerais toi-même celui que"});
db.iliadfr.insert({ pg : 3827, line : "tu verrais s'éloigner de la rude bataille. Lève-toi donc, si tu ne"});
db.iliadfr.insert({ pg : 3828, line : "veux voir la ville consumée bientôt par la flamme ardente."});
db.iliadfr.insert({ pg : 3830, line : "Et le divin Alexandros lui répondit:"});
db.iliadfr.insert({ pg : 3832, line : "-- Hektôr, puisque tu ne m'as point blâmé avec violence, mais dans"});
db.iliadfr.insert({ pg : 3833, line : "la juste mesure, je te répondrai. Je ne restais point dans ma"});
db.iliadfr.insert({ pg : 3834, line : "chambre nuptiale par colère ou par indignation contre les Troiens,"});
db.iliadfr.insert({ pg : 3835, line : "mais pour me livrer à la douleur. Maintenant que mon épouse me"});
db.iliadfr.insert({ pg : 3836, line : "conseille par de douces paroles de retourner au combat, je crois,"});
db.iliadfr.insert({ pg : 3837, line : "comme elle, que cela est pour le mieux. La victoire exauce tour à"});
db.iliadfr.insert({ pg : 3838, line : "tour les guerriers. Mais attends que je revête mes armes"});
db.iliadfr.insert({ pg : 3839, line : "belliqueuses, ou précède-moi, je vais te suivre."});
db.iliadfr.insert({ pg : 3841, line : "Il parla ainsi, et Hektôr ne lui répondit rien; et Hélénè dit à"});
db.iliadfr.insert({ pg : 3842, line : "Hektôr ces douces paroles:"});
db.iliadfr.insert({ pg : 3844, line : "-- Mon frère, frère d'une misérable chienne de malheur, et"});
db.iliadfr.insert({ pg : 3845, line : "horrible! Plût aux dieux qu'au jour même où ma mère m'enfanta un"});
db.iliadfr.insert({ pg : 3846, line : "furieux souffle de vent m'eût emportée sur une montagne ou abîmée"});
db.iliadfr.insert({ pg : 3847, line : "dans la mer tumultueuse, et que l'onde m'eût engloutie, avant que"});
db.iliadfr.insert({ pg : 3848, line : "ces choses fussent arrivées! Mais, puisque les dieux avaient"});
db.iliadfr.insert({ pg : 3849, line : "résolu ces maux, je voudrais être la femme d'un meilleur guerrier,"});
db.iliadfr.insert({ pg : 3850, line : "et qui souffrît au moins de l'indignation et des exécrations des"});
db.iliadfr.insert({ pg : 3851, line : "hommes. Mais celui-ci n'a point un coeur inébranlable, et il ne"});
db.iliadfr.insert({ pg : 3852, line : "l'aura jamais, et je pense qu'il en portera bientôt la peine."});
db.iliadfr.insert({ pg : 3853, line : "Viens, mon frère, entre et prends ce siège, car ton âme est pleine"});
db.iliadfr.insert({ pg : 3854, line : "d'un lourd souci, grâce à moi, chienne que je suis, et grâce au"});
db.iliadfr.insert({ pg : 3855, line : "crime d'Alexandros. Zeus nous a fait à tous deux une mauvaise"});
db.iliadfr.insert({ pg : 3856, line : "destinée, afin que nous soyons célèbres par là chez les hommes qui"});
db.iliadfr.insert({ pg : 3857, line : "naîtront dans l'avenir."});
db.iliadfr.insert({ pg : 3859, line : "Et le grand Hektôr au casque mouvant lui répondit:"});
db.iliadfr.insert({ pg : 3861, line : "-- Ne me fais point asseoir, Hélénè, bien que tu m'aimes, car tu"});
db.iliadfr.insert({ pg : 3862, line : "ne me persuaderas point. Mon coeur est plein du désir de secourir"});
db.iliadfr.insert({ pg : 3863, line : "les Troiens qui regrettent vivement mon absence. Mais excite"});
db.iliadfr.insert({ pg : 3864, line : "Pâris, et qu'il se hâte de me suivre, tandis que je serai encore"});
db.iliadfr.insert({ pg : 3865, line : "dans la ville. Je vais, dans ma demeure, revoir mes serviteurs, ma"});
db.iliadfr.insert({ pg : 3866, line : "femme bien-aimée et mon petit enfant. Je ne sais s'il me sera"});
db.iliadfr.insert({ pg : 3867, line : "permis de les revoir jamais plus, ou si les dieux me dompteront"});
db.iliadfr.insert({ pg : 3868, line : "par les mains des Akhaiens."});
db.iliadfr.insert({ pg : 3870, line : "Ayant ainsi parlé, Hektôr au casque mouvant sortit et parvint"});
db.iliadfr.insert({ pg : 3871, line : "bientôt à ses demeures, et il n'y trouva point Andromakhè aux bras"});
db.iliadfr.insert({ pg : 3872, line : "blancs, car elle était sortie avec son fils et une servante au"});
db.iliadfr.insert({ pg : 3873, line : "beau péplos, et elle se tenait sur la tour, pleurant et gémissant."});
db.iliadfr.insert({ pg : 3874, line : "Hektôr, n'ayant point trouvé dans ses demeures sa femme"});
db.iliadfr.insert({ pg : 3875, line : "irréprochable, s'arrêta sur le seuil et parla ainsi aux servantes:"});
db.iliadfr.insert({ pg : 3877, line : "-- Venez, servantes, et dites-moi la vérité. Où est allée, hors"});
db.iliadfr.insert({ pg : 3878, line : "des demeures, Andromakhè aux bras blancs? Est-ce chez mes soeurs,"});
db.iliadfr.insert({ pg : 3879, line : "ou chez mes belles-soeurs au beau péplos, ou dans le temple"});
db.iliadfr.insert({ pg : 3880, line : "d'Athènè avec les autres Troiennes qui apaisent la puissante"});
db.iliadfr.insert({ pg : 3881, line : "déesse à la belle chevelure?"});
db.iliadfr.insert({ pg : 3883, line : "Et la vigilante intendante lui répondit:"});
db.iliadfr.insert({ pg : 3885, line : "-- Hektôr, puisque tu veux que nous disions la vérité, elle n'est"});
db.iliadfr.insert({ pg : 3886, line : "point allée chez tes soeurs, ni chez tes belles-soeurs au beau"});
db.iliadfr.insert({ pg : 3887, line : "péplos, ni dans le temple d'Athènè avec les autres Troiennes qui"});
db.iliadfr.insert({ pg : 3888, line : "apaisent la puissante déesse à la belle chevelure; mais elle est"});
db.iliadfr.insert({ pg : 3889, line : "au faîte de la vaste tour d'Ilios, ayant appris une grande"});
db.iliadfr.insert({ pg : 3890, line : "victoire des Akhaiens sur les Troiens. Et, pleine d'égarement,"});
db.iliadfr.insert({ pg : 3891, line : "elle s'est hâtée de courir aux murailles, et la nourrice, auprès"});
db.iliadfr.insert({ pg : 3892, line : "d'elle, portait l'enfant."});
db.iliadfr.insert({ pg : 3894, line : "Et la femme intendante parla ainsi. Hektôr, étant sorti de ses"});
db.iliadfr.insert({ pg : 3895, line : "demeures, reprit son chemin à travers les rues magnifiquement"});
db.iliadfr.insert({ pg : 3896, line : "construites et populeuses, et, traversant la grande ville, il"});
db.iliadfr.insert({ pg : 3897, line : "arriva aux portes Skaies par où il devait sortir dans la plaine."});
db.iliadfr.insert({ pg : 3898, line : "Et sa femme, qui lui apporta une riche dot, accourut au-devant de"});
db.iliadfr.insert({ pg : 3899, line : "lui, Andromakhè, fille du magnanime Êétiôn qui habita sous le"});
db.iliadfr.insert({ pg : 3900, line : "Plakos couvert de forêts, dans Thèbè Hypoplakienne, et qui"});
db.iliadfr.insert({ pg : 3901, line : "commanda aux Kilikiens. Et sa fille était la femme de Hektôr au"});
db.iliadfr.insert({ pg : 3902, line : "casque d'airain. Et quand elle vint au-devant de lui, une servante"});
db.iliadfr.insert({ pg : 3903, line : "l'accompagnait qui portait sur le sein son jeune fils, petit"});
db.iliadfr.insert({ pg : 3904, line : "enfant encore, le Hektoréide bien-aimé, semblable à une belle"});
db.iliadfr.insert({ pg : 3905, line : "étoile. Hektôr le nommait Skamandrios, mais les autres Troiens"});
db.iliadfr.insert({ pg : 3906, line : "Astyanax, parce que Hektôr seul protégeait Troiè. Et il sourit en"});
db.iliadfr.insert({ pg : 3907, line : "regardant son fils en silence; mais Andromakhè, se tenant auprès"});
db.iliadfr.insert({ pg : 3908, line : "de lui en pleurant, prit sa main et lui parla ainsi:"});
db.iliadfr.insert({ pg : 3910, line : "-- Malheureux, ton courage te perdra; et tu n'as pitié ni de ton"});
db.iliadfr.insert({ pg : 3911, line : "fils enfant, ni de moi, misérable, qui serai bientôt ta veuve, car"});
db.iliadfr.insert({ pg : 3912, line : "les Akhaiens te tueront en se ruant tous contre toi. Il vaudrait"});
db.iliadfr.insert({ pg : 3913, line : "mieux pour moi, après t'avoir perdu, subir la sépulture, car rien"});
db.iliadfr.insert({ pg : 3914, line : "ne me consolera quand tu auras accompli ta destinée, et il ne me"});
db.iliadfr.insert({ pg : 3915, line : "restera que mes douleurs. Je n'ai plus ni mon père ni ma mère"});
db.iliadfr.insert({ pg : 3916, line : "vénérable. Le divin Akhilleus tua mon père, quand il saccagea la"});
db.iliadfr.insert({ pg : 3917, line : "ville populeuse des Kilikiens, Thèbè aux portes hautes. Il tua"});
db.iliadfr.insert({ pg : 3918, line : "Êétiôn, mais il ne le dépouilla point, par un respect pieux. Il le"});
db.iliadfr.insert({ pg : 3919, line : "brûla avec ses belles armes et il lui éleva un tombeau, et les"});
db.iliadfr.insert({ pg : 3920, line : "nymphes orestiades, filles de Zeus tempétueux, plantèrent des"});
db.iliadfr.insert({ pg : 3921, line : "ormes autour. J'avais sept frères dans nos demeures; et tous"});
db.iliadfr.insert({ pg : 3922, line : "descendirent en un jour chez Aidès, car le divin Akhilleus aux"});
db.iliadfr.insert({ pg : 3923, line : "pieds rapides les tua tous, auprès de leurs boeufs aux pieds lents"});
db.iliadfr.insert({ pg : 3924, line : "et de leurs blanches brebis. Et il emmena, avec les autres"});
db.iliadfr.insert({ pg : 3925, line : "dépouilles, ma mère qui régnait sous le Plakos planté d'arbres, et"});
db.iliadfr.insert({ pg : 3926, line : "il l'affranchit bientôt pour une grande rançon; mais Artémis qui"});
db.iliadfr.insert({ pg : 3927, line : "se réjouit de ses flèches la perça dans nos demeures. Hektôr! Tu"});
db.iliadfr.insert({ pg : 3928, line : "es pour moi un père, une mère vénérable, un frère et un époux"});
db.iliadfr.insert({ pg : 3929, line : "plein de jeunesse! Aie pitié! Reste sur cette tour; ne fais point"});
db.iliadfr.insert({ pg : 3930, line : "ton fils orphelin et ta femme veuve. Réunis l'armée auprès de ce"});
db.iliadfr.insert({ pg : 3931, line : "figuier sauvage où l'accès de la ville est le plus facile. Déjà,"});
db.iliadfr.insert({ pg : 3932, line : "trois fois, les plus courageux des Akhaiens ont tenté cet assaut,"});
db.iliadfr.insert({ pg : 3933, line : "les deux Aias, l'illustre Idoméneus, les Atréides et le brave fils"});
db.iliadfr.insert({ pg : 3934, line : "de Tydeus, soit par le conseil d'un divinateur, soit par le seul"});
db.iliadfr.insert({ pg : 3935, line : "élan de leur courage."});
db.iliadfr.insert({ pg : 3937, line : "Et le grand Hektôr au casque mouvant lui répondit:"});
db.iliadfr.insert({ pg : 3939, line : "-- Certes, femme, ces inquiétudes me possèdent aussi, mais je"});
db.iliadfr.insert({ pg : 3940, line : "redouterais cruellement les Troiens et les Troiennes aux longs"});
db.iliadfr.insert({ pg : 3941, line : "péplos traînants, si, comme un lâche, je fuyais le combat. Et mon"});
db.iliadfr.insert({ pg : 3942, line : "coeur ne me pousse point à fuir, car j'ai appris à être toujours"});
db.iliadfr.insert({ pg : 3943, line : "audacieux et à combattre, parmi les premiers, pour la gloire de"});
db.iliadfr.insert({ pg : 3944, line : "mon père et pour la mienne. Je sais, dans mon esprit et dans mon"});
db.iliadfr.insert({ pg : 3945, line : "coeur, qu'un jour viendra où la sainte Troiè périra, et Priamos,"});
db.iliadfr.insert({ pg : 3946, line : "et le brave peuple de Priamos. Mais ni le malheur futur des"});
db.iliadfr.insert({ pg : 3947, line : "Troiens ni celui de Hékabè elle-même, du roi Priamos et de mes"});
db.iliadfr.insert({ pg : 3948, line : "frères courageux qui tomberont en foule sous les guerriers"});
db.iliadfr.insert({ pg : 3949, line : "ennemis, ne m'afflige autant que le tien, quand un Akhaien"});
db.iliadfr.insert({ pg : 3950, line : "cuirassé d'airain te ravira la liberté et t'emmènera pleurante! Et"});
db.iliadfr.insert({ pg : 3951, line : "tu tisseras la toile de l'étranger, et tu porteras de force l'eau"});
db.iliadfr.insert({ pg : 3952, line : "de Messèis et de Hypéréiè, car la dure nécessité le voudra. Et,"});
db.iliadfr.insert({ pg : 3953, line : "sans doute, quelqu'un dira, te voyant répandre des larmes: --"});
db.iliadfr.insert({ pg : 3954, line : "Celle-ci est la femme de Hektôr qui était le plus brave des"});
db.iliadfr.insert({ pg : 3955, line : "Troiens dompteurs de chevaux quand il combattait autour de Troiè."});
db.iliadfr.insert({ pg : 3956, line : "-- Quelqu'un dira cela, et tu seras déchirée d'une grande douleur,"});
db.iliadfr.insert({ pg : 3957, line : "en songeant à cet époux que tu auras perdu, et qui, seul, pourrait"});
db.iliadfr.insert({ pg : 3958, line : "finir ta servitude. Mais que la lourde terre me recouvre mort,"});
db.iliadfr.insert({ pg : 3959, line : "avant que j'entende tes cris et que je te voie arracher d'ici!"});
db.iliadfr.insert({ pg : 3961, line : "Ayant ainsi parlé, l'illustre Hektôr tendit les mains vers son"});
db.iliadfr.insert({ pg : 3962, line : "fils, mais l'enfant se rejeta en arrière dans le sein de la"});
db.iliadfr.insert({ pg : 3963, line : "nourrice à la belle ceinture, épouvanté à l'aspect de son père"});
db.iliadfr.insert({ pg : 3964, line : "bien-aimé, et de l'airain et de la queue de cheval qui s'agitait"});
db.iliadfr.insert({ pg : 3965, line : "terriblement sur le cône du casque. Et le père bien-aimé sourit et"});
db.iliadfr.insert({ pg : 3966, line : "la mère vénérable aussi. Et l'illustre Hektôr ôta son casque et le"});
db.iliadfr.insert({ pg : 3967, line : "déposa resplendissant sur la terre. Et il baisa son fils bien-"});
db.iliadfr.insert({ pg : 3968, line : "aimé, et, le berçant dans ses bras, il supplia Zeus et les autres"});
db.iliadfr.insert({ pg : 3969, line : "dieux:"});
db.iliadfr.insert({ pg : 3971, line : "-- Zeus, et vous, dieux, faites que mon fils s'illustre comme moi"});
db.iliadfr.insert({ pg : 3972, line : "parmi les Troiens, qu'il soit plein de force et qu'il règne"});
db.iliadfr.insert({ pg : 3973, line : "puissamment dans Troiè! Qu'on dise un jour, le voyant revenir du"});
db.iliadfr.insert({ pg : 3974, line : "combat: Celui-ci est plus brave que son père! Qu'ayant tué le"});
db.iliadfr.insert({ pg : 3975, line : "guerrier ennemi, il rapporte de sanglantes dépouilles, et que le"});
db.iliadfr.insert({ pg : 3976, line : "coeur de sa mère en soit réjoui!"});
db.iliadfr.insert({ pg : 3978, line : "Ayant ainsi parlé, il déposa son enfant entre les bras de sa femme"});
db.iliadfr.insert({ pg : 3979, line : "bien-aimée, qui le reçut sur son sein parfumé, en pleurant et en"});
db.iliadfr.insert({ pg : 3980, line : "souriant; et le guerrier, voyant cela, la caressa de la main et"});
db.iliadfr.insert({ pg : 3981, line : "lui dit:"});
db.iliadfr.insert({ pg : 3983, line : "-- Malheureuse, ne te désespère point à cause de moi. Aucun"});
db.iliadfr.insert({ pg : 3984, line : "guerrier ne m'enverra chez Aidès contre ma destinée, et nul homme"});
db.iliadfr.insert({ pg : 3985, line : "vivant ne peut fuir sa destinée, lâche ou brave. Mais retourne"});
db.iliadfr.insert({ pg : 3986, line : "dans tes demeures, prends soin de tes travaux, de la toile et de"});
db.iliadfr.insert({ pg : 3987, line : "la quenouille, et mets tes servantes à leur tâche. Le souci de la"});
db.iliadfr.insert({ pg : 3988, line : "guerre appartient à tous les guerriers qui sont nés dans Ilios, et"});
db.iliadfr.insert({ pg : 3989, line : "surtout à moi."});
db.iliadfr.insert({ pg : 3991, line : "Ayant ainsi parlé, l'illustre Hektôr reprit son casque à flottante"});
db.iliadfr.insert({ pg : 3992, line : "queue de cheval. Et l'épouse bien-aimée retourna vers ses"});
db.iliadfr.insert({ pg : 3993, line : "demeures, regardant en arrière et versant des larmes. Et aussitôt"});
db.iliadfr.insert({ pg : 3994, line : "qu'elle fut arrivée aux demeures du tueur d'hommes Hektôr, elle y"});
db.iliadfr.insert({ pg : 3995, line : "trouva ses nombreuses servantes en proie à une grande douleur. Et"});
db.iliadfr.insert({ pg : 3996, line : "celles-ci pleuraient, dans ses demeures, Hektôr encore vivant, ne"});
db.iliadfr.insert({ pg : 3997, line : "pensant pas qu'il revînt jamais plus du combat, ayant échappé aux"});
db.iliadfr.insert({ pg : 3998, line : "mains guerrières des Akhaiens."});
db.iliadfr.insert({ pg : 4000, line : "Et Pâris ne s'attardait point dans ses hautes demeures mais, ayant"});
db.iliadfr.insert({ pg : 4001, line : "revêtu ses armes excellentes, d'un airain varié, il parcourait la"});
db.iliadfr.insert({ pg : 4002, line : "ville, de ses pieds rapides, tel qu'un étalon qui, longtemps"});
db.iliadfr.insert({ pg : 4003, line : "nourri d'orge à la crèche, ses liens étant rompus, court dans la"});
db.iliadfr.insert({ pg : 4004, line : "plaine en frappant la terre et saute dans le fleuve au beau cours"});
db.iliadfr.insert({ pg : 4005, line : "où il a coutume de se baigner. Et il redresse la tête, et ses"});
db.iliadfr.insert({ pg : 4006, line : "crins flottent épars sur ses épaules, et, fier de sa beauté, ses"});
db.iliadfr.insert({ pg : 4007, line : "jarrets le portent d'un trait aux lieux où paissent les chevaux."});
db.iliadfr.insert({ pg : 4008, line : "Ainsi Pâris Priamide, sous ses armes éclatantes comme l'éclair,"});
db.iliadfr.insert({ pg : 4009, line : "descendait de la hauteur de Pergamos; et ses pieds rapides le"});
db.iliadfr.insert({ pg : 4010, line : "portaient; et voici qu'il rencontra le divin Hektôr, son frère,"});
db.iliadfr.insert({ pg : 4011, line : "comme celui-ci quittait le lieu où il s'était entretenu avec"});
db.iliadfr.insert({ pg : 4012, line : "Andromakhè."});
db.iliadfr.insert({ pg : 4014, line : "Et, le premier, le roi Alexandros lui dit:"});
db.iliadfr.insert({ pg : 4016, line : "-- Frère vénéré, sans doute je t'ai retardé et je ne suis point"});
db.iliadfr.insert({ pg : 4017, line : "venu promptement comme tu me l'avais ordonné."});
db.iliadfr.insert({ pg : 4019, line : "Hektôr au casque mouvant lui répondit:"});
db.iliadfr.insert({ pg : 4021, line : "-- Ami, aucun guerrier, avec équité, ne peut te blâmer dans le"});
db.iliadfr.insert({ pg : 4022, line : "combat, car tu es brave; mais tu te lasses vite, et tu refuses"});
db.iliadfr.insert({ pg : 4023, line : "alors de combattre, et mon coeur est attristé par les outrages que"});
db.iliadfr.insert({ pg : 4024, line : "t'adressent les Troiens qui subissent tant de maux à cause de toi."});
db.iliadfr.insert({ pg : 4025, line : "Mais, allons! et nous apaiserons ces ressentiments, si Zeus nous"});
db.iliadfr.insert({ pg : 4026, line : "donne d'offrir un jour, dans nos demeures, un libre kratère aux"});
db.iliadfr.insert({ pg : 4027, line : "dieux ouraniens qui vivent toujours, après avoir chassé loin de"});
db.iliadfr.insert({ pg : 4028, line : "Troiè les Akhaiens aux belles knèmides."});
db.iliadfr.insert({ pg : 4031, line : "Chant 7"});
db.iliadfr.insert({ pg : 4033, line : "Ayant ainsi parlé, l'illustre Hektôr sortit des portes, et son"});
db.iliadfr.insert({ pg : 4034, line : "frère Alexandros l'accompagnait, et tous deux, dans leur coeur,"});
db.iliadfr.insert({ pg : 4035, line : "étaient pleins du désir de combattre. Comme un dieu envoie un vent"});
db.iliadfr.insert({ pg : 4036, line : "propice aux matelots suppliants qui se sont épuisés à battre la"});
db.iliadfr.insert({ pg : 4037, line : "mer de leurs avirons polis, de sorte que leurs membres sont rompus"});
db.iliadfr.insert({ pg : 4038, line : "de fatigue, de même les Priamides apparurent aux Troiens qui les"});
db.iliadfr.insert({ pg : 4039, line : "désiraient."});
db.iliadfr.insert({ pg : 4041, line : "Et aussitôt Alexandros tua le fils du roi Arèithoos, Ménèsthios,"});
db.iliadfr.insert({ pg : 4042, line : "qui habitait dans Arnè, et que Arèithoos qui combattait avec une"});
db.iliadfr.insert({ pg : 4043, line : "massue engendra de Philomédousa aux yeux de boeuf. Et Hektôr tua,"});
db.iliadfr.insert({ pg : 4044, line : "de sa pique aiguë, Eionèos; et l'airain le frappa au cou, sous le"});
db.iliadfr.insert({ pg : 4045, line : "casque, et brisa ses forces. Et Glaukos, fils de Hippolokhos, chef"});
db.iliadfr.insert({ pg : 4046, line : "des Lykiens, blessa, de sa pique, entre les épaules, au milieu de"});
db.iliadfr.insert({ pg : 4047, line : "la mêlée, Iphinoos Dexiade qui sautait sur ses chevaux rapides. Et"});
db.iliadfr.insert({ pg : 4048, line : "il tomba sur la terre, et ses forces furent brisées."});
db.iliadfr.insert({ pg : 4050, line : "Et la divine Athènè aux yeux clairs, ayant vu les Argiens qui"});
db.iliadfr.insert({ pg : 4051, line : "périssaient dans la rude bataille, descendit à la hâte du faîte de"});
db.iliadfr.insert({ pg : 4052, line : "l'Olympos devant la sainte Ilios, et Apollôn accourut vers elle,"});
db.iliadfr.insert({ pg : 4053, line : "voulant donner la victoire aux Troiens, et l'ayant vue de la"});
db.iliadfr.insert({ pg : 4054, line : "hauteur de Pergamos. Et ils se rencontrèrent auprès du hêtre, et"});
db.iliadfr.insert({ pg : 4055, line : "le roi Apollôn, fils de Zeus, parla le premier:"});
db.iliadfr.insert({ pg : 4057, line : "-- Pourquoi, pleine d'ardeur, viens-tu de nouveau de l'Olympos,"});
db.iliadfr.insert({ pg : 4058, line : "fille du grand Zeus? Est-ce pour assurer aux Danaens la victoire"});
db.iliadfr.insert({ pg : 4059, line : "douteuse? Car tu n'as nulle pitié des Troiens qui périssent. Mais,"});
db.iliadfr.insert({ pg : 4060, line : "si tu veux m'en croire, ceci sera pour le mieux. Arrêtons pour"});
db.iliadfr.insert({ pg : 4061, line : "aujourd'hui la guerre et le combat. Tous lutteront ensuite jusqu'à"});
db.iliadfr.insert({ pg : 4062, line : "la chute de Troiè, puisqu'il vous plaît, à vous, immortels, de"});
db.iliadfr.insert({ pg : 4063, line : "renverser cette ville."});
db.iliadfr.insert({ pg : 4065, line : "Et la déesse aux yeux clairs, Athènè, lui répondit:"});
db.iliadfr.insert({ pg : 4067, line : "-- Qu'il en soit ainsi, ô archer! C'est dans ce même dessein que"});
db.iliadfr.insert({ pg : 4068, line : "je suis venue de l'Olympos vers les Troiens et les Akhaiens. Mais"});
db.iliadfr.insert({ pg : 4069, line : "comment arrêteras-tu le combat des guerriers?"});
db.iliadfr.insert({ pg : 4071, line : "Et le roi Apollôn, fils de Zeus, lui répondit:"});
db.iliadfr.insert({ pg : 4073, line : "-- Excitons le solide courage de Hektôr dompteur de chevaux, et"});
db.iliadfr.insert({ pg : 4074, line : "qu'il provoque, seul, un des Danaens à combattre un rude combat."});
db.iliadfr.insert({ pg : 4075, line : "Et les Akhaiens aux knèmides d'airain exciteront un des leurs à"});
db.iliadfr.insert({ pg : 4076, line : "combattre le divin Hektôr."});
db.iliadfr.insert({ pg : 4078, line : "Il parla ainsi, et la divine Athènè aux yeux clairs consentit. Et"});
db.iliadfr.insert({ pg : 4079, line : "Hélénos, le cher fils de Priamos, devina dans son esprit ce qu'il"});
db.iliadfr.insert({ pg : 4080, line : "avait plu aux dieux de décider, et il s'approcha de Hektôr et lui"});
db.iliadfr.insert({ pg : 4081, line : "parla ainsi:"});
db.iliadfr.insert({ pg : 4083, line : "-- Hektôr Priamide, égal à Zeus en sagesse, voudras-tu m'en"});
db.iliadfr.insert({ pg : 4084, line : "croire, moi qui suis ton frère? Fais que les Troiens et tous les"});
db.iliadfr.insert({ pg : 4085, line : "Akhaiens s'arrêtent, et provoque le plus brave des Akhaiens à"});
db.iliadfr.insert({ pg : 4086, line : "combattre contre toi un rude combat. Ta moire n'est point de"});
db.iliadfr.insert({ pg : 4087, line : "mourir et de subir aujourd'hui ta destinée, car j'ai entendu la"});
db.iliadfr.insert({ pg : 4088, line : "voix des dieux qui vivent toujours."});
db.iliadfr.insert({ pg : 4090, line : "Il parla ainsi, et Hektôr s'en réjouit, et, s'avançant en tête des"});
db.iliadfr.insert({ pg : 4091, line : "Troiens, il arrêta leurs phalanges à l'aide de la pique qu'il"});
db.iliadfr.insert({ pg : 4092, line : "tenait par le milieu, et tous s'arrêtèrent. Et Agamemnôn contint"});
db.iliadfr.insert({ pg : 4093, line : "aussi les Akhaiens aux belles knèmides. Et Athènè et Apollôn qui"});
db.iliadfr.insert({ pg : 4094, line : "porte l'arc d'argent, semblables à des vautours, s'assirent sous"});
db.iliadfr.insert({ pg : 4095, line : "le hêtre élevé du père Zeus tempétueux qui se réjouit des"});
db.iliadfr.insert({ pg : 4096, line : "guerriers. Et les deux armées, par rangs épais, s'assirent,"});
db.iliadfr.insert({ pg : 4097, line : "hérissées et brillantes de boucliers, de casques et de piques."});
db.iliadfr.insert({ pg : 4098, line : "Comme, au souffle de Zéphyros, l'ombre se répand sur la mer qui"});
db.iliadfr.insert({ pg : 4099, line : "devient toute noire, de même les rangs des Akhaiens et des Troiens"});
db.iliadfr.insert({ pg : 4100, line : "couvraient la plaine. Et Hektôr leur parla ainsi:"});
db.iliadfr.insert({ pg : 4102, line : "-- Écoutez-moi, Troiens et Akhaiens aux belles knèmides, afin que"});
db.iliadfr.insert({ pg : 4103, line : "je vous dise ce que mon coeur m'ordonne de dire. Le sublime"});
db.iliadfr.insert({ pg : 4104, line : "Kronide n'a point scellé notre alliance, mais il songe à nous"});
db.iliadfr.insert({ pg : 4105, line : "accabler tous de calamités, jusqu'à ce que vous preniez Troiè aux"});
db.iliadfr.insert({ pg : 4106, line : "fortes tours, ou que vous soyez domptés auprès des nefs qui"});
db.iliadfr.insert({ pg : 4107, line : "fendent la mer. Puisque vous êtes les princes des Panakhaiens, que"});
db.iliadfr.insert({ pg : 4108, line : "celui d'entre vous que son courage poussera à combattre contre moi"});
db.iliadfr.insert({ pg : 4109, line : "sorte des rangs et combatte le divin Hektôr. Je vous le dis, et"});
db.iliadfr.insert({ pg : 4110, line : "que Zeus soit témoin: si celui-là me tue de sa pique d'airain, me"});
db.iliadfr.insert({ pg : 4111, line : "dépouillant de mes armes, il les emportera dans ses nefs creuses;"});
db.iliadfr.insert({ pg : 4112, line : "mais il renverra mon corps dans ma demeure, afin que les Troiens"});
db.iliadfr.insert({ pg : 4113, line : "et les femmes des Troiens brûlent mon cadavre sur un bûcher; et,"});
db.iliadfr.insert({ pg : 4114, line : "si je le tue, et qu'Apollôn me donne cette gloire, j'emporterai"});
db.iliadfr.insert({ pg : 4115, line : "ses armes dans la sainte Ilios et je les suspendrai dans le temple"});
db.iliadfr.insert({ pg : 4116, line : "de l'archer Apollôn; mais je renverrai son corps aux nefs solides,"});
db.iliadfr.insert({ pg : 4117, line : "afin que les Akhaiens chevelus l'ensevelissent. Et ils lui"});
db.iliadfr.insert({ pg : 4118, line : "élèveront un tombeau sur le rivage du large Hellèspontos. Et"});
db.iliadfr.insert({ pg : 4119, line : "quelqu'un d'entre les hommes futurs, naviguant sur la noire mer,"});
db.iliadfr.insert({ pg : 4120, line : "dans sa nef solide, dira, voyant ce tombeau d'un guerrier mort"});
db.iliadfr.insert({ pg : 4121, line : "depuis longtemps: -- Celui-ci fut tué autrefois par l'illustre"});
db.iliadfr.insert({ pg : 4122, line : "Hektôr dont le courage était grand. Il le dira, et ma gloire ne"});
db.iliadfr.insert({ pg : 4123, line : "mourra jamais."});
db.iliadfr.insert({ pg : 4125, line : "Il parla ainsi, et tous restèrent muets, n'osant refuser ni"});
db.iliadfr.insert({ pg : 4126, line : "accepter. Alors Ménélaos se leva et dit, plein de reproches, et"});
db.iliadfr.insert({ pg : 4127, line : "soupirant profondément:"});
db.iliadfr.insert({ pg : 4129, line : "-- Hélas! Akhaiennes menaçantes, et non plus Akhaiens! certes,"});
db.iliadfr.insert({ pg : 4130, line : "ceci nous sera un grand opprobre, si aucun des Danaens ne se lève"});
db.iliadfr.insert({ pg : 4131, line : "contre Hektôr. Mais que la terre et l'eau vous manquent, à vous"});
db.iliadfr.insert({ pg : 4132, line : "qui restez assis sans courage et sans gloire! Moi, je m'armerai"});
db.iliadfr.insert({ pg : 4133, line : "donc contre Hektôr, car la victoire enfin est entre les mains des"});
db.iliadfr.insert({ pg : 4134, line : "dieux immortels."});
db.iliadfr.insert({ pg : 4136, line : "Il parla ainsi, et il se couvrait de ses belles armes. Alors,"});
db.iliadfr.insert({ pg : 4137, line : "Ménélaos, tu aurais trouvé la fin de ta vie sous les mains de"});
db.iliadfr.insert({ pg : 4138, line : "Hektôr, car il était beaucoup plus fort que toi, si les rois des"});
db.iliadfr.insert({ pg : 4139, line : "Akhaiens, s'étant levés, ne t'eussent retenu. Et l'Atréide"});
db.iliadfr.insert({ pg : 4140, line : "Agamemnôn qui commande au loin lui prit la main et lui dit:"});
db.iliadfr.insert({ pg : 4142, line : "-- Insensé Ménélaos, nourrisson de Zeus, d'où te vient cette"});
db.iliadfr.insert({ pg : 4143, line : "démence? Contiens-toi, malgré ta douleur. Cesse de vouloir"});
db.iliadfr.insert({ pg : 4144, line : "combattre contre un meilleur guerrier que toi, le Priamide Hektôr,"});
db.iliadfr.insert({ pg : 4145, line : "que tous redoutent. Akhilleus, qui est beaucoup plus fort que toi"});
db.iliadfr.insert({ pg : 4146, line : "dans la bataille qui illustre les guerriers, craint de le"});
db.iliadfr.insert({ pg : 4147, line : "rencontrer. Reste donc assis dans les rangs de tes compagnons, et"});
db.iliadfr.insert({ pg : 4148, line : "les Akhaiens exciteront un autre combattant. Bien que le Priamide"});
db.iliadfr.insert({ pg : 4149, line : "soit brave et insatiable de guerre, je pense qu'il se reposera"});
db.iliadfr.insert({ pg : 4150, line : "volontiers, s'il échappe à ce rude combat."});
db.iliadfr.insert({ pg : 4152, line : "Il parla ainsi, et l'esprit du héros fut persuadé par les paroles"});
db.iliadfr.insert({ pg : 4153, line : "sages de son frère, et il lui obéit. Et ses serviteurs, joyeux,"});
db.iliadfr.insert({ pg : 4154, line : "enlevèrent les armes de ses épaules. Et Nestôr se leva au milieu"});
db.iliadfr.insert({ pg : 4155, line : "des Argiens et dit:"});
db.iliadfr.insert({ pg : 4157, line : "-- Ah! certes, un grand deuil envahit la terre Akhaienne! Et le"});
db.iliadfr.insert({ pg : 4158, line : "vieux cavalier Pèleus, excellent et sage agorète des Myrmidônes,"});
db.iliadfr.insert({ pg : 4159, line : "va gémir grandement, lui qui, autrefois, m'interrogeant dans sa"});
db.iliadfr.insert({ pg : 4160, line : "demeure, apprenait, plein de joie, quels étaient les pères et les"});
db.iliadfr.insert({ pg : 4161, line : "fils de tous les Akhaiens! Quand il saura que tous sont épouvantés"});
db.iliadfr.insert({ pg : 4162, line : "par Hektôr, il étendra souvent les mains vers les immortels, afin"});
db.iliadfr.insert({ pg : 4163, line : "que son âme, hors de son corps, descende dans la demeure d'Aidès!"});
db.iliadfr.insert({ pg : 4164, line : "Plût à vous, ô Zeus, Athènè et Apollôn, que je fusse plein de"});
db.iliadfr.insert({ pg : 4165, line : "jeunesse, comme au temps où, près du rapide Kéladontès, les"});
db.iliadfr.insert({ pg : 4166, line : "Pyliens combattaient les Arkadiens armés de piques, sous les murs"});
db.iliadfr.insert({ pg : 4167, line : "de Phéia où viennent les eaux courantes du Iardanos. Au milieu"});
db.iliadfr.insert({ pg : 4168, line : "d'eux était le divin guerrier Éreuthaliôn, portant sur ses épaules"});
db.iliadfr.insert({ pg : 4169, line : "les armes du roi Arèithoos, du divin Arèithoos que les hommes et"});
db.iliadfr.insert({ pg : 4170, line : "les femmes aux belles ceintures appelaient le porte-massue, parce"});
db.iliadfr.insert({ pg : 4171, line : "qu'il ne combattait ni avec l'arc, ni avec la longue pique, mais"});
db.iliadfr.insert({ pg : 4172, line : "qu'il rompait les rangs ennemis à l'aide d'une massue de fer."});
db.iliadfr.insert({ pg : 4173, line : "Lykoorgos le tua par ruse, et non par force, dans une route"});
db.iliadfr.insert({ pg : 4174, line : "étroite, où la massue de fer ne put écarter de lui la mort. Là,"});
db.iliadfr.insert({ pg : 4175, line : "Lykoorgos, le prévenant, le perça de sa pique dans le milieu du"});
db.iliadfr.insert({ pg : 4176, line : "corps, et le renversa sur la terre. Et il le dépouilla des armes"});
db.iliadfr.insert({ pg : 4177, line : "que lui avait données le rude Arès. Dès lors, Lykoorgos les porta"});
db.iliadfr.insert({ pg : 4178, line : "dans la guerre; mais, devenu vieux dans ses demeures, il les donna"});
db.iliadfr.insert({ pg : 4179, line : "à son cher compagnon Éreuthaliôn, qui, étant ainsi armé,"});
db.iliadfr.insert({ pg : 4180, line : "provoquait les plus braves. Et tous tremblaient, pleins de"});
db.iliadfr.insert({ pg : 4181, line : "crainte, et nul n'osait. Et mon coeur hardi me poussa à combattre,"});
db.iliadfr.insert({ pg : 4182, line : "confiant dans mes forces, bien que le plus jeune de tous. Et je"});
db.iliadfr.insert({ pg : 4183, line : "combattis, et Athènè m'accorda la victoire, et je tuai ce très"});
db.iliadfr.insert({ pg : 4184, line : "robuste et très brave guerrier dont le grand corps couvrit un"});
db.iliadfr.insert({ pg : 4185, line : "vaste espace. Plût aux dieux que je fusse ainsi plein de jeunesse"});
db.iliadfr.insert({ pg : 4186, line : "et que mes forces fussent intactes! Hektôr au casque mouvant"});
db.iliadfr.insert({ pg : 4187, line : "commencerait aussitôt le combat. Mais vous ne vous hâtez point de"});
db.iliadfr.insert({ pg : 4188, line : "lutter contre Hektôr, vous qui êtes les plus braves des"});
db.iliadfr.insert({ pg : 4189, line : "Panakhaiens."});
db.iliadfr.insert({ pg : 4191, line : "Et le vieillard leur fit ces reproches, et neuf d'entre eux se"});
db.iliadfr.insert({ pg : 4192, line : "levèrent. Et le premier fut le roi des hommes, Agamemnôn. Puis, le"});
db.iliadfr.insert({ pg : 4193, line : "brave Diomèdès Tydéide se leva. Et après eux se levèrent les Aias"});
db.iliadfr.insert({ pg : 4194, line : "revêtus d'une grande force, et Idoméneus et le compagnon"});
db.iliadfr.insert({ pg : 4195, line : "d'Idoméneus, Mèrionès, semblable au tueur de guerriers Arès, et"});
db.iliadfr.insert({ pg : 4196, line : "Eurypylos, l'illustre fils d'Évaimôn, et Thoas Andraimonide et le"});
db.iliadfr.insert({ pg : 4197, line : "divin Odysseus. Tous voulaient combattre contre le divin Hektôr."});
db.iliadfr.insert({ pg : 4198, line : "Et le cavalier Gérennien Nestôr dit au milieu d'eux:"});
db.iliadfr.insert({ pg : 4200, line : "-- Remuez maintenant tous les sorts, et celui qui sera choisi par"});
db.iliadfr.insert({ pg : 4201, line : "le sort combattra pour tous les Akhaiens aux belles knèmides, et"});
db.iliadfr.insert({ pg : 4202, line : "il se réjouira de son courage, s'il échappe au rude combat et à la"});
db.iliadfr.insert({ pg : 4203, line : "lutte dangereuse."});
db.iliadfr.insert({ pg : 4205, line : "Il parla ainsi, et chacun marqua son signe, et tous furent mêlés"});
db.iliadfr.insert({ pg : 4206, line : "dans le casque de l'Atréide Agamemnôn. Et les peuples priaient,"});
db.iliadfr.insert({ pg : 4207, line : "élevant les mains vers les dieux, et chacun disait, regardant le"});
db.iliadfr.insert({ pg : 4208, line : "large Ouranos:"});
db.iliadfr.insert({ pg : 4210, line : "-- Père Zeus, fais sortir le signe d'Aias, ou du fils de Tydeus,"});
db.iliadfr.insert({ pg : 4211, line : "ou du roi de la très riche Mykènè!"});
db.iliadfr.insert({ pg : 4213, line : "Ils parlèrent ainsi, et le cavalier Gérennien Nestôr agita le"});
db.iliadfr.insert({ pg : 4214, line : "casque et en fit sortir le signe d'Aias que tous désiraient. Un"});
db.iliadfr.insert({ pg : 4215, line : "héraut le prit, le présentant par la droite aux princes Akhaiens."});
db.iliadfr.insert({ pg : 4216, line : "Et ceux qui ne le reconnaissaient point le refusaient. Mais quand"});
db.iliadfr.insert({ pg : 4217, line : "il parvint à celui qui l'avait marqué et jeté dans le casque, à"});
db.iliadfr.insert({ pg : 4218, line : "l'illustre Aias, celui-ci le reconnut aussitôt, et, le laissant"});
db.iliadfr.insert({ pg : 4219, line : "tomber à ses pieds, il dit, plein de joie:"});
db.iliadfr.insert({ pg : 4221, line : "-- Ô amis, ce signe est le mien; et je m'en réjouis dans mon"});
db.iliadfr.insert({ pg : 4222, line : "coeur, et je pense que je dompterai le divin Hectôr. Allons!"});
db.iliadfr.insert({ pg : 4223, line : "pendant que je revêtirai mes armes belliqueuses, suppliez tout"});
db.iliadfr.insert({ pg : 4224, line : "bas, afin que les Troiens ne vous entendent point, le roi Zeus"});
db.iliadfr.insert({ pg : 4225, line : "Kroniôn; ou priez-le tout haut, car nous ne craignons personne."});
db.iliadfr.insert({ pg : 4226, line : "Quel guerrier pourrait me dompter aisément, à l'aide de sa force"});
db.iliadfr.insert({ pg : 4227, line : "ou de ma faiblesse? Je suis né dans Salamis, et je n'y ai point"});
db.iliadfr.insert({ pg : 4228, line : "été élevé sans gloire."});
db.iliadfr.insert({ pg : 4230, line : "Il parla ainsi, et tous suppliaient le père Zeus Kroniôn, et"});
db.iliadfr.insert({ pg : 4231, line : "chacun disait, regardant le vaste Ouranos:"});
db.iliadfr.insert({ pg : 4233, line : "-- Père Zeus, qui commandes de l'Ida, très auguste, très grand,"});
db.iliadfr.insert({ pg : 4234, line : "donne la victoire à Aias et qu'il remporte une gloire brillante;"});
db.iliadfr.insert({ pg : 4235, line : "mais, si tu aimes Hektôr et le protèges, fais que le courage et la"});
db.iliadfr.insert({ pg : 4236, line : "gloire des deux guerriers soient égaux."});
db.iliadfr.insert({ pg : 4238, line : "Ils parlèrent ainsi, et Aias s'armait de l'airain éclatant. Et"});
db.iliadfr.insert({ pg : 4239, line : "après qu'il eut couvert son corps de ses armes, il marcha en"});
db.iliadfr.insert({ pg : 4240, line : "avant, pareil au monstrueux Arès que le Kroniôn envoie au milieu"});
db.iliadfr.insert({ pg : 4241, line : "des guerriers qu'il pousse à combattre, le coeur plein de fureur."});
db.iliadfr.insert({ pg : 4242, line : "Ainsi marchait le grand Aias, rempart des Akhaiens, avec un"});
db.iliadfr.insert({ pg : 4243, line : "sourire terrible, à grands pas, et brandissant sa longue pique. Et"});
db.iliadfr.insert({ pg : 4244, line : "les Argiens se réjouissaient en le regardant, et un tremblement"});
db.iliadfr.insert({ pg : 4245, line : "saisit les membres des Troiens, et le coeur de Hektôr lui-même"});
db.iliadfr.insert({ pg : 4246, line : "palpita dans sa poitrine; mais il ne pouvait reculer dans la foule"});
db.iliadfr.insert({ pg : 4247, line : "des siens, ni fuir le combat, puisqu'il l'avait demandé. Et Aias"});
db.iliadfr.insert({ pg : 4248, line : "s'approcha, portant un bouclier fait d'airain et de sept peaux de"});
db.iliadfr.insert({ pg : 4249, line : "boeuf, et tel qu'une tour. Et l'excellent ouvrier Tykhios qui"});
db.iliadfr.insert({ pg : 4250, line : "habitait Hylè l'avait fabriqué à l'aide de sept peaux de forts"});
db.iliadfr.insert({ pg : 4251, line : "taureaux, recouvertes d'une plaque d'airain. Et Aias Télamônien,"});
db.iliadfr.insert({ pg : 4252, line : "portant ce bouclier devant sa poitrine, s'approcha de Hektôr, et"});
db.iliadfr.insert({ pg : 4253, line : "dit ces paroles menaçantes:"});
db.iliadfr.insert({ pg : 4255, line : "-- Maintenant, Hektôr, tu sauras, seul à seul, quels sont les"});
db.iliadfr.insert({ pg : 4256, line : "chefs des Danaens, sans compter Akhilleus au coeur de lion, qui"});
db.iliadfr.insert({ pg : 4257, line : "rompt les phalanges des guerriers. Il repose aujourd'hui, sur le"});
db.iliadfr.insert({ pg : 4258, line : "rivage de la mer, dans ses nefs aux poupes recourbées, irrité"});
db.iliadfr.insert({ pg : 4259, line : "contre Agamemnôn le prince des peuples; mais nous pouvons tous"});
db.iliadfr.insert({ pg : 4260, line : "combattre contre toi. Commence donc le combat."});
db.iliadfr.insert({ pg : 4262, line : "Et Hektôr au casque mouvant lui répondit:"});
db.iliadfr.insert({ pg : 4264, line : "-- Divin Aias Télamônien, prince des peuples, ne m'éprouve point"});
db.iliadfr.insert({ pg : 4265, line : "comme si j'étais un faible enfant ou une femme qui ignore les"});
db.iliadfr.insert({ pg : 4266, line : "travaux de la guerre. Je sais combattre et tuer les hommes, et"});
db.iliadfr.insert({ pg : 4267, line : "mouvoir mon dur bouclier de la main droite ou de la main gauche,"});
db.iliadfr.insert({ pg : 4268, line : "et il m'est permis de combattre audacieusement. Je sais, dans la"});
db.iliadfr.insert({ pg : 4269, line : "rude bataille, de pied ferme marcher au son d'Arès, et me jeter"});
db.iliadfr.insert({ pg : 4270, line : "dans la mêlée sur mes cavales rapides. Mais je ne veux point"});
db.iliadfr.insert({ pg : 4271, line : "frapper un homme tel que toi par surprise, mais en face, si je"});
db.iliadfr.insert({ pg : 4272, line : "puis."});
db.iliadfr.insert({ pg : 4274, line : "Il parla ainsi, et il lança sa longue pique vibrante et frappa le"});
db.iliadfr.insert({ pg : 4275, line : "grand bouclier d'Aias. Et la pique irrésistible pénétra à travers"});
db.iliadfr.insert({ pg : 4276, line : "les sept peaux de boeuf jusqu'à la dernière lame d'airain. Et le"});
db.iliadfr.insert({ pg : 4277, line : "divin Aias lança aussi sa longue pique, et il en frappa le"});
db.iliadfr.insert({ pg : 4278, line : "bouclier égal du Priamide; et la pique solide pénétra dans le"});
db.iliadfr.insert({ pg : 4279, line : "bouclier éclatant, et, perçant la cuirasse artistement faite,"});
db.iliadfr.insert({ pg : 4280, line : "déchira la tunique sur le flanc. Mais le Priamide se courba et"});
db.iliadfr.insert({ pg : 4281, line : "évita la noire kèr."});
db.iliadfr.insert({ pg : 4283, line : "Et tous deux, relevant leurs piques, se ruèrent, semblables à des"});
db.iliadfr.insert({ pg : 4284, line : "lions mangeurs de chair crue, ou à des sangliers dont la vigueur"});
db.iliadfr.insert({ pg : 4285, line : "est grande. Et le Priamide frappa de sa pique le milieu du"});
db.iliadfr.insert({ pg : 4286, line : "bouclier, mais il n'en perça point l'airain, et la pointe s'y"});
db.iliadfr.insert({ pg : 4287, line : "tordit. Et Aias, bondissant, frappa le bouclier, qu'il traversa de"});
db.iliadfr.insert({ pg : 4288, line : "sa pique, et il arrêta Hektôr qui se ruait, et il lui blessa la"});
db.iliadfr.insert({ pg : 4289, line : "gorge, et un sang noir en jaillit. Mais Hektôr au casque mouvant"});
db.iliadfr.insert({ pg : 4290, line : "ne cessa point de combattre, et, reculant, il prit de sa forte"});
db.iliadfr.insert({ pg : 4291, line : "main une pierre grande, noire et rugueuse, qui gisait sur la"});
db.iliadfr.insert({ pg : 4292, line : "plaine, et il frappa le milieu du grand bouclier couvert de sept"});
db.iliadfr.insert({ pg : 4293, line : "peaux de boeuf, et l'airain résonna sourdement. Et Aias, soulevant"});
db.iliadfr.insert({ pg : 4294, line : "à son tour une pierre plus grande encore, la lança en lui"});
db.iliadfr.insert({ pg : 4295, line : "imprimant une force immense. Et, de cette pierre, il brisa le"});
db.iliadfr.insert({ pg : 4296, line : "bouclier, et les genoux du Priamide fléchirent, et il tomba à la"});
db.iliadfr.insert({ pg : 4297, line : "renverse sous le bouclier. Mais Apollôn le releva aussitôt. Et"});
db.iliadfr.insert({ pg : 4298, line : "déjà ils se seraient frappés tous deux de leurs épées, en se ruant"});
db.iliadfr.insert({ pg : 4299, line : "l'un contre l'autre, si les hérauts, messagers de Zeus et des"});
db.iliadfr.insert({ pg : 4300, line : "hommes, n'étaient survenus, l'un du côté des Troiens, l'autre du"});
db.iliadfr.insert({ pg : 4301, line : "côté des Akhaiens cuirassés, Talthybios et Idaios, sages tous"});
db.iliadfr.insert({ pg : 4302, line : "deux. Et ils levèrent leurs sceptres entre les deux guerriers, et"});
db.iliadfr.insert({ pg : 4303, line : "Idaios, plein de conseils prudents, leur dit:"});
db.iliadfr.insert({ pg : 4305, line : "-- Ne combattez pas plus longtemps, mes chers fils. Zeus qui"});
db.iliadfr.insert({ pg : 4306, line : "amasse les nuées vous aime tous deux, et tous deux vous êtes très"});
db.iliadfr.insert({ pg : 4307, line : "braves, comme nous le savons tous. Mais voici la nuit, et il est"});
db.iliadfr.insert({ pg : 4308, line : "bon d'obéir à la nuit."});
db.iliadfr.insert({ pg : 4310, line : "Et le Télamônien Aias lui répondit:"});
db.iliadfr.insert({ pg : 4312, line : "-- Idaios, ordonne à Hektôr de parler. C'est lui qui a provoqué au"});
db.iliadfr.insert({ pg : 4313, line : "combat les plus braves d'entre nous. Qu'il décide, et j'obéirai,"});
db.iliadfr.insert({ pg : 4314, line : "et je ferai ce qu'il fera."});
db.iliadfr.insert({ pg : 4316, line : "Et le grand Hektôr au casque mouvant lui répondit:"});
db.iliadfr.insert({ pg : 4318, line : "-- Aias, un dieu t'a donné la prudence, la force et la grandeur,"});
db.iliadfr.insert({ pg : 4319, line : "et tu l'emportes par ta lance sur tous les Akhaiens. Cessons pour"});
db.iliadfr.insert({ pg : 4320, line : "aujourd'hui la lutte et le combat. Nous combattrons de nouveau"});
db.iliadfr.insert({ pg : 4321, line : "plus tard, jusqu'à ce qu'un dieu en décide et donne à l'un de nous"});
db.iliadfr.insert({ pg : 4322, line : "la victoire. Voici la nuit, et il est bon d'obéir à la nuit, afin"});
db.iliadfr.insert({ pg : 4323, line : "que tu réjouisses, auprès des nefs Akhaiennes, tes concitoyens et"});
db.iliadfr.insert({ pg : 4324, line : "tes compagnons, et que j'aille, dans la grande ville du roi"});
db.iliadfr.insert({ pg : 4325, line : "Priamos, réjouir les Troiens et les Troiennes ornées de longues"});
db.iliadfr.insert({ pg : 4326, line : "robes, qui prieront pour moi dans les temples divins. Mais"});
db.iliadfr.insert({ pg : 4327, line : "faisons-nous de mutuels et illustres dons, afin que les Akhaiens"});
db.iliadfr.insert({ pg : 4328, line : "et les Troiens disent: Ils ont combattu pour la discorde qui brûle"});
db.iliadfr.insert({ pg : 4329, line : "le coeur, et voici qu'ils se sont séparés avec amitié."});
db.iliadfr.insert({ pg : 4331, line : "Ayant ainsi parlé, il offrit à Aias l'épée aux clous d'argent,"});
db.iliadfr.insert({ pg : 4332, line : "avec la gaine et les courroies artistement travaillées, et Aias"});
db.iliadfr.insert({ pg : 4333, line : "lui donna un ceinturon éclatant, couleur de pourpre. Et ils se"});
db.iliadfr.insert({ pg : 4334, line : "retirèrent, l'un vers l'armée des Akhaiens, l'autre vers les"});
db.iliadfr.insert({ pg : 4335, line : "Troiens. Et ceux-ci se réjouirent en foule, quand ils virent"});
db.iliadfr.insert({ pg : 4336, line : "Hektôr vivant et sauf, échappé des mains invaincues et de la force"});
db.iliadfr.insert({ pg : 4337, line : "d'Aias. Et ils l'emmenèrent vers la ville, après avoir désespéré"});
db.iliadfr.insert({ pg : 4338, line : "de son salut."});
db.iliadfr.insert({ pg : 4340, line : "Et, de leur côté, les Akhaiens bien armés conduisirent au divin"});
db.iliadfr.insert({ pg : 4341, line : "Agamemnôn Aias joyeux de sa victoire. Et quand ils furent arrivés"});
db.iliadfr.insert({ pg : 4342, line : "aux tentes de l'Atréide, le roi des hommes Agamemnôn sacrifia au"});
db.iliadfr.insert({ pg : 4343, line : "puissant Kroniôn un taureau de cinq ans. Après l'avoir écorché,"});
db.iliadfr.insert({ pg : 4344, line : "disposé et coupé adroitement en morceaux, ils percèrent ceux-ci de"});
db.iliadfr.insert({ pg : 4345, line : "broches, les firent rôtir avec soin et les retirèrent du feu."});
db.iliadfr.insert({ pg : 4346, line : "Puis, ils préparèrent le repas et se mirent à manger, et aucun ne"});
db.iliadfr.insert({ pg : 4347, line : "put se plaindre, en son âme, de manquer d'une part égale. Mais le"});
db.iliadfr.insert({ pg : 4348, line : "héros Atréide Agamemnôn, qui commande au loin, honora Aias du dos"});
db.iliadfr.insert({ pg : 4349, line : "entier. Et, tous ayant bu et mangé selon leur soif et leur faim,"});
db.iliadfr.insert({ pg : 4350, line : "le vieillard Nestôr ouvrit le premier le conseil et parla ainsi,"});
db.iliadfr.insert({ pg : 4351, line : "plein de prudence:"});
db.iliadfr.insert({ pg : 4353, line : "-- Atréides, et vous, chefs des Akhaiens, beaucoup d'Akhaiens"});
db.iliadfr.insert({ pg : 4354, line : "chevelus sont morts, dont le rude Arès a répandu le sang noir sur"});
db.iliadfr.insert({ pg : 4355, line : "les bords du clair Skamandros, et dont les âmes sont descendues"});
db.iliadfr.insert({ pg : 4356, line : "chez Aidès. C'est pourquoi il faut suspendre le combat dès la"});
db.iliadfr.insert({ pg : 4357, line : "lueur du matin. Puis, nous étant réunis, nous enlèverons les"});
db.iliadfr.insert({ pg : 4358, line : "cadavres à l'aide de nos boeufs et de nos mulets, et nous les"});
db.iliadfr.insert({ pg : 4359, line : "brûlerons devant les nefs, afin que chacun en rapporte les cendres"});
db.iliadfr.insert({ pg : 4360, line : "à ses fils, quand tous seront de retour dans la terre de la"});
db.iliadfr.insert({ pg : 4361, line : "patrie. Et nous leur élèverons, autour d'un seul bûcher, un même"});
db.iliadfr.insert({ pg : 4362, line : "tombeau dans la plaine. Et tout auprès, nous construirons aussitôt"});
db.iliadfr.insert({ pg : 4363, line : "de hautes tours qui nous protégeront nous et nos nefs. Et nous y"});
db.iliadfr.insert({ pg : 4364, line : "mettrons des portes solides pour le passage des cavaliers, et nous"});
db.iliadfr.insert({ pg : 4365, line : "creuserons en dehors un fossé profond qui arrêtera les cavaliers"});
db.iliadfr.insert({ pg : 4366, line : "et les chevaux, si les braves Troiens poussent le combat jusque"});
db.iliadfr.insert({ pg : 4367, line : "là."});
db.iliadfr.insert({ pg : 4369, line : "Il parla ainsi, et tous les rois l'approuvèrent."});
db.iliadfr.insert({ pg : 4371, line : "Et l'agora tumultueuse et troublée des Troiens s'était réunie"});
db.iliadfr.insert({ pg : 4372, line : "devant les portes de Priamos, sur la haute citadelle d'Ilios. Et"});
db.iliadfr.insert({ pg : 4373, line : "le sage Antènôr parla ainsi le premier:"});
db.iliadfr.insert({ pg : 4375, line : "-- Écoutez-moi, Troiens, Dardaniens et alliés, afin que je dise ce"});
db.iliadfr.insert({ pg : 4376, line : "que mon coeur m'ordonne. Allons! rendons aux Atréides l'Argienne"});
db.iliadfr.insert({ pg : 4377, line : "Hélénè et toutes ses richesses, et qu'ils les emmènent. Nous"});
db.iliadfr.insert({ pg : 4378, line : "combattons maintenant contre les serments sacrés que nous avons"});
db.iliadfr.insert({ pg : 4379, line : "jurés, et je n'espère rien de bon pour nous, si vous ne faites ce"});
db.iliadfr.insert({ pg : 4380, line : "que je dis."});
db.iliadfr.insert({ pg : 4382, line : "Ayant ainsi parlé, il s'assit. Et alors se leva du milieu de tous"});
db.iliadfr.insert({ pg : 4383, line : "le divin Alexandros, l'époux de Hélénè à la belle chevelure. Et il"});
db.iliadfr.insert({ pg : 4384, line : "répondit en paroles ailées:"});
db.iliadfr.insert({ pg : 4386, line : "-- Antènôr, ce que tu as dit ne m'est point agréable. Tu aurais pu"});
db.iliadfr.insert({ pg : 4387, line : "concevoir de meilleurs desseins, et, si tu as parlé sérieusement,"});
db.iliadfr.insert({ pg : 4388, line : "certes, les dieux t'ont ravi l'esprit. Mais je parle devant les"});
db.iliadfr.insert({ pg : 4389, line : "Troiens dompteurs de chevaux, et je repousse ce que tu as dit. Je"});
db.iliadfr.insert({ pg : 4390, line : "ne rendrai point cette femme. Pour les richesses que j'ai"});
db.iliadfr.insert({ pg : 4391, line : "emportées d'Argos dans ma demeure, je veux les rendre toutes, et"});
db.iliadfr.insert({ pg : 4392, line : "j'y ajouterai des miennes."});
db.iliadfr.insert({ pg : 4394, line : "Ayant ainsi parlé, il s'assit. Et, au milieu de tous, se leva le"});
db.iliadfr.insert({ pg : 4395, line : "Dardanide Priamos, semblable à un dieu par sa prudence. Et, plein"});
db.iliadfr.insert({ pg : 4396, line : "de sagesse, il parla ainsi et dit:"});
db.iliadfr.insert({ pg : 4398, line : "-- Écoutez-moi, Troiens, Dardaniens et alliés, afin que je dise ce"});
db.iliadfr.insert({ pg : 4399, line : "que mon coeur m'ordonne. Maintenant, prenez votre repas comme"});
db.iliadfr.insert({ pg : 4400, line : "d'habitude, et faites tour à tour bonne garde. Que dès le matin"});
db.iliadfr.insert({ pg : 4401, line : "Idaios se rende aux nefs creuses, afin de porter aux Atréides"});
db.iliadfr.insert({ pg : 4402, line : "Agamemnôn et Ménélaos l'offre d'Alexandros d'où viennent nos"});
db.iliadfr.insert({ pg : 4403, line : "discordes. Et qu'il leur demande, par de sages paroles, s'ils"});
db.iliadfr.insert({ pg : 4404, line : "veulent suspendre la triste guerre jusqu'à ce que nous ayons brûlé"});
db.iliadfr.insert({ pg : 4405, line : "les cadavres. Nous combattrons ensuite de nouveau, en attendant"});
db.iliadfr.insert({ pg : 4406, line : "que le sort décide entre nous et donne la victoire à l'un des deux"});
db.iliadfr.insert({ pg : 4407, line : "peuples."});
db.iliadfr.insert({ pg : 4409, line : "Il parla ainsi, et ceux qui l'écoutaient obéirent, et l'armée prit"});
db.iliadfr.insert({ pg : 4410, line : "son repas comme d'habitude. Dès le matin, Idaios se rendit aux"});
db.iliadfr.insert({ pg : 4411, line : "nefs creuses. Et il trouva les Danaens, nourrissons de Zeus,"});
db.iliadfr.insert({ pg : 4412, line : "réunis dans l'agora, auprès de la poupe de la nef d'Agamemnôn. Et,"});
db.iliadfr.insert({ pg : 4413, line : "se tenant au milieu d'eux, il parla ainsi:"});
db.iliadfr.insert({ pg : 4415, line : "-- Atréides et Akhaiens aux belles knèmides, Priamos et les"});
db.iliadfr.insert({ pg : 4416, line : "illustres Troiens m'ordonnent de vous porter l'offre d'Alexandros"});
db.iliadfr.insert({ pg : 4417, line : "d'où viennent nos discordes, si toutefois elle vous est agréable."});
db.iliadfr.insert({ pg : 4418, line : "Toutes les richesses qu'Alexandros a rapportées dans Ilios sur ses"});
db.iliadfr.insert({ pg : 4419, line : "nefs creuses, -- plût aux dieux qu'il fût mort auparavant! -- il"});
db.iliadfr.insert({ pg : 4420, line : "veut les rendre et y ajouter des siennes; mais il refuse de rendre"});
db.iliadfr.insert({ pg : 4421, line : "la jeune épouse de l'illustre Ménélaos, malgré les supplications"});
db.iliadfr.insert({ pg : 4422, line : "des Troiens. Et ils m'ont aussi ordonné de vous demander si vous"});
db.iliadfr.insert({ pg : 4423, line : "voulez suspendre la triste guerre jusqu'à ce que nous ayons brûlé"});
db.iliadfr.insert({ pg : 4424, line : "les cadavres. Nous combattrons ensuite de nouveau, en attendant"});
db.iliadfr.insert({ pg : 4425, line : "que le sort décide entre nous et donne la victoire à l'un des deux"});
db.iliadfr.insert({ pg : 4426, line : "peuples."});
db.iliadfr.insert({ pg : 4428, line : "Il parla ainsi, et tous restèrent muets. Et Diomèdès hardi au"});
db.iliadfr.insert({ pg : 4429, line : "combat parla ainsi:"});
db.iliadfr.insert({ pg : 4431, line : "-- Qu'aucun de nous n'accepte les richesses d'Alexandros ni Hélénè"});
db.iliadfr.insert({ pg : 4432, line : "elle-même. Il est manifeste pour tous, fût-ce pour un enfant, que"});
db.iliadfr.insert({ pg : 4433, line : "le suprême désastre est suspendu sur la tête des Troiens."});
db.iliadfr.insert({ pg : 4435, line : "Il parla ainsi, et tous les fils des Akhaiens poussèrent des"});
db.iliadfr.insert({ pg : 4436, line : "acclamations, admirant les paroles du dompteur de chevaux"});
db.iliadfr.insert({ pg : 4437, line : "Diomèdès. Et le roi Agamemnôn dit à Idaios"});
db.iliadfr.insert({ pg : 4439, line : "-- Idaios, tu as entendu la réponse des Akhaiens. Ils t'ont"});
db.iliadfr.insert({ pg : 4440, line : "répondu, et ce qu'ils disent me plaît. Cependant, je ne vous"});
db.iliadfr.insert({ pg : 4441, line : "refuse point de brûler vos morts et d'honorer par le feu les"});
db.iliadfr.insert({ pg : 4442, line : "cadavres de ceux qui ont succombé. Que l'époux de Hèrè, Zeus qui"});
db.iliadfr.insert({ pg : 4443, line : "tonne dans les hauteurs, soit témoin de notre traité!"});
db.iliadfr.insert({ pg : 4445, line : "Ayant ainsi parlé, il éleva son sceptre vers tous les dieux. Et"});
db.iliadfr.insert({ pg : 4446, line : "Idaios retourna dans la sainte Ilios, où les Troiens et les"});
db.iliadfr.insert({ pg : 4447, line : "Dardaniens étaient réunis en agora, attendant son retour. Et il"});
db.iliadfr.insert({ pg : 4448, line : "arriva, et, au milieu d'eux, il rendit compte de son message. Et"});
db.iliadfr.insert({ pg : 4449, line : "aussitôt ils s'empressèrent de transporter, ceux-ci les cadavres,"});
db.iliadfr.insert({ pg : 4450, line : "ceux-là le bois du bûcher. Et les Argiens, de leur côté,"});
db.iliadfr.insert({ pg : 4451, line : "s'exhortaient, loin des nefs creuses, à relever leurs morts et à"});
db.iliadfr.insert({ pg : 4452, line : "construire le bûcher."});
db.iliadfr.insert({ pg : 4454, line : "Hélios, à son lever, frappait les campagnes de ses rayons, et,"});
db.iliadfr.insert({ pg : 4455, line : "montant dans l'Ouranos, sortait doucement du cours profond de"});
db.iliadfr.insert({ pg : 4456, line : "l'Okéanos. Et les deux armées accouraient l'une vers l'autre."});
db.iliadfr.insert({ pg : 4457, line : "Alors, il leur fut difficile de reconnaître leurs guerriers; mais"});
db.iliadfr.insert({ pg : 4458, line : "quand ils eurent lavé leur poussière sanglante, ils les déposèrent"});
db.iliadfr.insert({ pg : 4459, line : "sur les chars en répandant des larmes brûlantes. Et le grand"});
db.iliadfr.insert({ pg : 4460, line : "Priamos ne leur permit point de gémir, et ils amassèrent les morts"});
db.iliadfr.insert({ pg : 4461, line : "sur le bûcher, se lamentant dans leur coeur. Et, après les avoir"});
db.iliadfr.insert({ pg : 4462, line : "brûlés, ils retournèrent vers la sainte Ilios."});
db.iliadfr.insert({ pg : 4464, line : "De leur côté, les Akhaiens aux belles knèmides amassèrent les"});
db.iliadfr.insert({ pg : 4465, line : "cadavres sur le bûcher, tristes dans leur coeur. Et, après les"});
db.iliadfr.insert({ pg : 4466, line : "avoir brûlés, ils s'en retournèrent vers les nefs creuses. Éôs"});
db.iliadfr.insert({ pg : 4467, line : "n'était point levée encore, et déjà la nuit était douteuse, quand"});
db.iliadfr.insert({ pg : 4468, line : "un peuple des Akhaiens vint élever dans la plaine un seul tombeau"});
db.iliadfr.insert({ pg : 4469, line : "sur l'unique bûcher. Et, non loin, d'autres guerriers"});
db.iliadfr.insert({ pg : 4470, line : "construisirent, pour se protéger eux-mêmes et les nefs, de hautes"});
db.iliadfr.insert({ pg : 4471, line : "tours avec des portes solides pour le passage des cavaliers. Et"});
db.iliadfr.insert({ pg : 4472, line : "ils creusèrent, au dehors et tout autour, un fossé profond, large"});
db.iliadfr.insert({ pg : 4473, line : "et grand, qu'ils défendirent avec des pieux. Et c'est ainsi que"});
db.iliadfr.insert({ pg : 4474, line : "travaillaient les Akhaiens chevelus."});
db.iliadfr.insert({ pg : 4476, line : "Et les dieux, assis auprès du foudroyant Zeus, regardaient avec"});
db.iliadfr.insert({ pg : 4477, line : "admiration ce grand travail des Akhaiens aux tuniques d'airain."});
db.iliadfr.insert({ pg : 4478, line : "Et, au milieu d'eux, Poseidaôn qui ébranle la terre parla ainsi:"});
db.iliadfr.insert({ pg : 4480, line : "-- Père Zeus, qui donc, parmi les mortels qui vivent sur la terre"});
db.iliadfr.insert({ pg : 4481, line : "immense, fera connaître désormais aux immortels sa pensée et ses"});
db.iliadfr.insert({ pg : 4482, line : "desseins? Ne vois-tu pas que les Akhaiens chevelus ont construit"});
db.iliadfr.insert({ pg : 4483, line : "une muraille devant leurs nefs, avec un fossé tout autour, et"});
db.iliadfr.insert({ pg : 4484, line : "qu'ils n'ont point offert d'illustres hécatombes aux dieux? La"});
db.iliadfr.insert({ pg : 4485, line : "gloire de ceci se répandra autant que la lumière d'Éôs; et les"});
db.iliadfr.insert({ pg : 4486, line : "murs que Phoibos Apollôn et moi avons élevés au héros Laomédôn"});
db.iliadfr.insert({ pg : 4487, line : "seront oubliés."});
db.iliadfr.insert({ pg : 4489, line : "Et Zeus qui amasse les nuées, avec un profond soupir, lui"});
db.iliadfr.insert({ pg : 4490, line : "répondit:"});
db.iliadfr.insert({ pg : 4492, line : "-- Ah! Très puissant, qui ébranles la terre, qu'as-tu dit? Un"});
db.iliadfr.insert({ pg : 4493, line : "dieu, moins doué de force que toi, n'aurait point cette crainte."});
db.iliadfr.insert({ pg : 4494, line : "Certes, ta gloire se répandra aussi loin que la lumière d'Éôs."});
db.iliadfr.insert({ pg : 4495, line : "Reprends courage, et quand les Akhaiens chevelus auront regagné"});
db.iliadfr.insert({ pg : 4496, line : "sur leurs nefs la terre bien-aimée de la patrie, engloutis tout"});
db.iliadfr.insert({ pg : 4497, line : "entier dans la mer ce mur écroulé, couvre de nouveau de sables le"});
db.iliadfr.insert({ pg : 4498, line : "vaste rivage, et que cette immense muraille des Akhaiens"});
db.iliadfr.insert({ pg : 4499, line : "s'évanouisse devant toi."});
db.iliadfr.insert({ pg : 4501, line : "Et ils s'entretenaient ainsi. Et Hélios se coucha, et le travail"});
db.iliadfr.insert({ pg : 4502, line : "des Akhaiens fut terminé. Et ceux-ci tuaient des boeufs sous les"});
db.iliadfr.insert({ pg : 4503, line : "tentes, et ils prenaient leurs repas. Et plusieurs nefs avaient"});
db.iliadfr.insert({ pg : 4504, line : "apporté de Lemnos le vin qu'avait envoyé le Ièsonide Eunèos, que"});
db.iliadfr.insert({ pg : 4505, line : "Hypsipylè avait conçu du prince des peuples Ièsôn. Et le Ièsonide"});
db.iliadfr.insert({ pg : 4506, line : "avait donné aux Atréides mille mesures de vin. Et les Akhaiens"});
db.iliadfr.insert({ pg : 4507, line : "chevelus leur achetaient ce vin, ceux-ci avec de l'airain, ceux-là"});
db.iliadfr.insert({ pg : 4508, line : "avec du fer brillant; les uns avec des peaux de boeufs, les autres"});
db.iliadfr.insert({ pg : 4509, line : "avec les boeufs eux-mêmes, et d'autres avec leurs esclaves. Et"});
db.iliadfr.insert({ pg : 4510, line : "tous enfin préparaient l'excellent repas."});
db.iliadfr.insert({ pg : 4512, line : "Et, pendant toute la nuit, les Akhaiens chevelus mangeaient; et"});
db.iliadfr.insert({ pg : 4513, line : "les Troiens aussi et les alliés mangeaient dans la ville. Et, au"});
db.iliadfr.insert({ pg : 4514, line : "milieu de la nuit, le sage Zeus, leur préparant de nouvelles"});
db.iliadfr.insert({ pg : 4515, line : "calamités, tonna terriblement; et la pâle crainte les saisit. Et"});
db.iliadfr.insert({ pg : 4516, line : "ils répandaient le vin hors des coupes, et aucun n'osa boire avant"});
db.iliadfr.insert({ pg : 4517, line : "de faire des libations au très puissant Kroniôn. Enfin, s'étant"});
db.iliadfr.insert({ pg : 4518, line : "couchés, ils goûtèrent la douceur du sommeil."});
db.iliadfr.insert({ pg : 4521, line : "Chant 8"});
db.iliadfr.insert({ pg : 4523, line : "Éôs au péplos couleur de safran éclairait toute la terre, et Zeus"});
db.iliadfr.insert({ pg : 4524, line : "qui se réjouit de la foudre convoqua l'agora des dieux sur le plus"});
db.iliadfr.insert({ pg : 4525, line : "haut faîte de l'Olympos aux sommets sans nombre. Et il leur parla,"});
db.iliadfr.insert({ pg : 4526, line : "et ils écoutaient respectueusement:"});
db.iliadfr.insert({ pg : 4528, line : "-- Écoutez-moi tous, dieux et déesses, afin que je vous dise ce"});
db.iliadfr.insert({ pg : 4529, line : "que j'ai résolu dans mon coeur. Et que nul dieu, mâle ou femelle,"});
db.iliadfr.insert({ pg : 4530, line : "ne résiste à mon ordre; mais obéissez tous, afin que j'achève"});
db.iliadfr.insert({ pg : 4531, line : "promptement mon oeuvre. Car si j'apprends que quelqu'un des dieux"});
db.iliadfr.insert({ pg : 4532, line : "est allé secourir soit les Troiens, soit les Danaens, celui-là"});
db.iliadfr.insert({ pg : 4533, line : "reviendra dans l'Olympos honteusement châtié. Et je le saisirai,"});
db.iliadfr.insert({ pg : 4534, line : "et je le jetterai au loin, dans le plus creux des gouffres de la"});
db.iliadfr.insert({ pg : 4535, line : "terre, au fond du noir Tartaros qui a des portes de fer et un"});
db.iliadfr.insert({ pg : 4536, line : "seuil d'airain, au-dessous de la demeure d'Aidès, autant que la"});
db.iliadfr.insert({ pg : 4537, line : "terre est au-dessous de l'Ouranos. Et il saura que je suis le plus"});
db.iliadfr.insert({ pg : 4538, line : "fort de tous les dieux. Debout, dieux! tentez-le, et vous le"});
db.iliadfr.insert({ pg : 4539, line : "saurez. Suspendez une chaîne d'or du faîte de l'Ouranos, et tous,"});
db.iliadfr.insert({ pg : 4540, line : "dieux et déesses, attachez-vous à cette chaîne. Vous n'entraînerez"});
db.iliadfr.insert({ pg : 4541, line : "jamais, malgré vos efforts, de l'Ouranos sur la terre, Zeus le"});
db.iliadfr.insert({ pg : 4542, line : "modérateur suprême. Et moi, certes, si je le voulais, je vous"});
db.iliadfr.insert({ pg : 4543, line : "enlèverais tous, et la terre et la mer, et j'attacherais cette"});
db.iliadfr.insert({ pg : 4544, line : "chaîne au faîte de l'Olympos, et tout y resterait suspendu, tant"});
db.iliadfr.insert({ pg : 4545, line : "je suis au-dessus des dieux et des hommes!"});
db.iliadfr.insert({ pg : 4547, line : "Il parla ainsi, et tous restèrent muets, stupéfaits de ces"});
db.iliadfr.insert({ pg : 4548, line : "paroles, car il avait durement parlé. Et Athènè, la déesse aux"});
db.iliadfr.insert({ pg : 4549, line : "yeux clairs, lui dit:"});
db.iliadfr.insert({ pg : 4551, line : "-- Ô notre père! Kronide, le plus haut des rois, nous savons bien"});
db.iliadfr.insert({ pg : 4552, line : "que ta force ne le cède à aucune autre; mais nous gémissons sur"});
db.iliadfr.insert({ pg : 4553, line : "les Danaens, habiles à lancer la pique, qui vont périr par une"});
db.iliadfr.insert({ pg : 4554, line : "destinée mauvaise. Certes, nous ne combattrons pas, si tu le veux"});
db.iliadfr.insert({ pg : 4555, line : "ainsi, mais nous conseillerons les Argiens, afin qu'ils ne"});
db.iliadfr.insert({ pg : 4556, line : "périssent point tous, grâce à ta colère."});
db.iliadfr.insert({ pg : 4558, line : "Et Zeus qui amasse les nuées, souriant, lui dit:"});
db.iliadfr.insert({ pg : 4560, line : "-- Reprends courage, Tritogénéia, chère enfant. Certes, j'ai parlé"});
db.iliadfr.insert({ pg : 4561, line : "très rudement, mais je veux être doux pour toi."});
db.iliadfr.insert({ pg : 4563, line : "Ayant ainsi parlé, il lia au char les chevaux aux pieds d'airain,"});
db.iliadfr.insert({ pg : 4564, line : "rapides, ayant pour crinières des chevelures d'or; et il"});
db.iliadfr.insert({ pg : 4565, line : "s'enveloppa d'un vêtement d'or; et il prit un fouet d'or bien"});
db.iliadfr.insert({ pg : 4566, line : "travaillé, et il monta sur son char. Et il frappa les chevaux du"});
db.iliadfr.insert({ pg : 4567, line : "fouet, et ils volèrent aussitôt entre la terre et l'Ouranos"});
db.iliadfr.insert({ pg : 4568, line : "étoilé. Il parvint sur l'Ida qui abonde en sources, où vivent les"});
db.iliadfr.insert({ pg : 4569, line : "bêtes sauvages, et sur le Gargaros, où il possède une enceinte"});
db.iliadfr.insert({ pg : 4570, line : "sacrée et un autel parfumé. Le père des hommes et des dieux y"});
db.iliadfr.insert({ pg : 4571, line : "arrêta ses chevaux, les délia et les enveloppa d'une grande nuée."});
db.iliadfr.insert({ pg : 4572, line : "Et il s'assit sur le faîte, plein de gloire, regardant la ville"});
db.iliadfr.insert({ pg : 4573, line : "des Troiens et les nefs des Akhaiens."});
db.iliadfr.insert({ pg : 4575, line : "Et les Akhaiens chevelus s'armaient, ayant mangé en hâte sous les"});
db.iliadfr.insert({ pg : 4576, line : "tentes; et les Troiens aussi s'armaient dans la ville; et ils"});
db.iliadfr.insert({ pg : 4577, line : "étaient moins nombreux, mais brûlants du désir de combattre, par"});
db.iliadfr.insert({ pg : 4578, line : "nécessité, pour leurs enfants et pour leurs femmes. Et les portes"});
db.iliadfr.insert({ pg : 4579, line : "s'ouvraient, et les peuples, fantassins et cavaliers, se ruaient"});
db.iliadfr.insert({ pg : 4580, line : "au dehors, et il s'élevait un bruit immense."});
db.iliadfr.insert({ pg : 4582, line : "Et quand ils se furent rencontrés, les piques et les forces des"});
db.iliadfr.insert({ pg : 4583, line : "guerriers aux cuirasses d'airain se mêlèrent confusément, et les"});
db.iliadfr.insert({ pg : 4584, line : "boucliers bombés se heurtèrent, et il s'éleva un bruit immense. On"});
db.iliadfr.insert({ pg : 4585, line : "entendait les cris de joie et les lamentations de ceux qui tuaient"});
db.iliadfr.insert({ pg : 4586, line : "ou mouraient, et la terre ruisselait de sang; et tant qu'Éôs"});
db.iliadfr.insert({ pg : 4587, line : "brilla et que le jour sacré monta, les traits frappèrent les"});
db.iliadfr.insert({ pg : 4588, line : "hommes, et les hommes tombaient. Mais quand Hélios fut parvenu au"});
db.iliadfr.insert({ pg : 4589, line : "faîte de l'Ouranos, le père Zeus étendit ses balances d'or, et il"});
db.iliadfr.insert({ pg : 4590, line : "y plaça deux kères de la mort qui rend immobile à jamais, la kèr"});
db.iliadfr.insert({ pg : 4591, line : "des Troiens dompteurs de chevaux et la kèr des Akhaiens aux"});
db.iliadfr.insert({ pg : 4592, line : "cuirasses d'airain. Il éleva les balances, les tenant par le"});
db.iliadfr.insert({ pg : 4593, line : "milieu, et le jour fatal des Akhaiens s'inclina; et la destinée"});
db.iliadfr.insert({ pg : 4594, line : "des Akhaiens toucha la terre nourricière, et celle des Troiens"});
db.iliadfr.insert({ pg : 4595, line : "monta vers le large Ouranos. Et il roula le tonnerre immense sur"});
db.iliadfr.insert({ pg : 4596, line : "l'Ida, et il lança l'ardent éclair au milieu du peuple guerrier"});
db.iliadfr.insert({ pg : 4597, line : "des Akhaiens; et, l'ayant vu, ils restèrent stupéfaits et pâles de"});
db.iliadfr.insert({ pg : 4598, line : "terreur."});
db.iliadfr.insert({ pg : 4600, line : "Ni Idoméneus, ni Agamemnôn, ni les deux Aias, serviteurs d'Arès,"});
db.iliadfr.insert({ pg : 4601, line : "n'osèrent rester. Le Gérennien Nestôr, rempart des Akhaiens, resta"});
db.iliadfr.insert({ pg : 4602, line : "seul, mais contre son gré, par la chute de son cheval. Le divin"});
db.iliadfr.insert({ pg : 4603, line : "Alexandros, l'époux de Hélénè aux beaux cheveux, avait percé le"});
db.iliadfr.insert({ pg : 4604, line : "cheval d'une flèche au sommet de la tête, endroit mortel, là où"});
db.iliadfr.insert({ pg : 4605, line : "croissent les premiers crins. Et, l'airain ayant pénétré dans la"});
db.iliadfr.insert({ pg : 4606, line : "cervelle, le cheval, saisi de douleur, se roulait et épouvantait"});
db.iliadfr.insert({ pg : 4607, line : "les autres chevaux. Et, comme le vieillard se hâtait de couper les"});
db.iliadfr.insert({ pg : 4608, line : "rênes avec l'épée, les rapides chevaux de Hektôr, portant leur"});
db.iliadfr.insert({ pg : 4609, line : "brave conducteur, approchaient dans la mêlée, et le vieillard eût"});
db.iliadfr.insert({ pg : 4610, line : "perdu la vie, si Diomèdès ne l'eût vu. Et il jeta un cri terrible,"});
db.iliadfr.insert({ pg : 4611, line : "appelant Odysseus:"});
db.iliadfr.insert({ pg : 4613, line : "-- Divin Laertiade, subtil Odysseus, pourquoi fuis-tu, tournant le"});
db.iliadfr.insert({ pg : 4614, line : "dos comme un lâche dans la mêlée? Crains qu'on ne te perce d'une"});
db.iliadfr.insert({ pg : 4615, line : "pique dans le dos, tandis que tu fuis. Reste, et repoussons ce"});
db.iliadfr.insert({ pg : 4616, line : "rude guerrier loin de ce vieillard."});
db.iliadfr.insert({ pg : 4618, line : "Il parla ainsi, mais le divin et patient Odysseus ne l'entendit"});
db.iliadfr.insert({ pg : 4619, line : "point et passa outre vers les nefs creuses des Akhaiens. Et le"});
db.iliadfr.insert({ pg : 4620, line : "Tydéide, bien que seul, se mêla aux combattants avancés, et se"});
db.iliadfr.insert({ pg : 4621, line : "tint debout devant les chevaux du vieux Nèlèide, et il lui dit ces"});
db.iliadfr.insert({ pg : 4622, line : "paroles ailées:"});
db.iliadfr.insert({ pg : 4624, line : "-- Ô vieillard, voici que de jeunes guerriers te pressent avec"});
db.iliadfr.insert({ pg : 4625, line : "fureur. Ta force est dissoute, la lourde vieillesse t'accable, ton"});
db.iliadfr.insert({ pg : 4626, line : "serviteur est faible et tes chevaux sont lents. Mais monte sur mon"});
db.iliadfr.insert({ pg : 4627, line : "char, et tu verras quels sont les chevaux de Trôs que j'ai pris à"});
db.iliadfr.insert({ pg : 4628, line : "Ainéias, et qui savent, avec une rapidité égale, poursuivre"});
db.iliadfr.insert({ pg : 4629, line : "l'ennemi ou fuir à travers la plaine. Que nos serviteurs prennent"});
db.iliadfr.insert({ pg : 4630, line : "soin de tes chevaux, et poussons ceux-ci sur les Troiens dompteurs"});
db.iliadfr.insert({ pg : 4631, line : "de chevaux, et que Hektôr sache si ma pique est furieuse entre mes"});
db.iliadfr.insert({ pg : 4632, line : "mains."});
db.iliadfr.insert({ pg : 4634, line : "Il parla ainsi, et le cavalier Gérennien Nestôr lui obéit. Et les"});
db.iliadfr.insert({ pg : 4635, line : "deux braves serviteurs, Sthénélos et Eurymédôn, prirent soin de"});
db.iliadfr.insert({ pg : 4636, line : "ses cavales. Et les deux rois montèrent sur le char de Diomèdès,"});
db.iliadfr.insert({ pg : 4637, line : "et Nestôr saisit les rênes brillantes et fouetta les chevaux; et"});
db.iliadfr.insert({ pg : 4638, line : "ils approchèrent. Et le fils de Tydeus lança sa pique contre le"});
db.iliadfr.insert({ pg : 4639, line : "Priamide qui venait à lui, et il le manqua; mais il frappa dans la"});
db.iliadfr.insert({ pg : 4640, line : "poitrine, près de la mamelle, Éniopeus, fils du magnanime"});
db.iliadfr.insert({ pg : 4641, line : "Thèbaios, et qui tenait les rênes des chevaux. Et celui-ci tomba"});
db.iliadfr.insert({ pg : 4642, line : "du char, et ses chevaux rapides reculèrent, et il perdit l'âme et"});
db.iliadfr.insert({ pg : 4643, line : "la force. Une amère douleur enveloppa l'âme de Hektôr à cause de"});
db.iliadfr.insert({ pg : 4644, line : "son compagnon; mais il le laissa gisant, malgré sa douleur, et"});
db.iliadfr.insert({ pg : 4645, line : "chercha un autre brave conducteur. Et ses chevaux n'en manquèrent"});
db.iliadfr.insert({ pg : 4646, line : "pas longtemps, car il trouva promptement le hardi Arképtolémos"});
db.iliadfr.insert({ pg : 4647, line : "Iphitide; et il lui confia les chevaux rapides, et il lui remit"});
db.iliadfr.insert({ pg : 4648, line : "les rênes en main."});
db.iliadfr.insert({ pg : 4650, line : "Alors, il serait arrivé un désastre, et des actions furieuses"});
db.iliadfr.insert({ pg : 4651, line : "auraient été commises, et les Troiens auraient été renfermés dans"});
db.iliadfr.insert({ pg : 4652, line : "Ilios comme des agneaux, si le père des hommes et des dieux ne"});
db.iliadfr.insert({ pg : 4653, line : "s'était aperçu de ceci. Et il tonna fortement, lançant la foudre"});
db.iliadfr.insert({ pg : 4654, line : "éclatante devant les chevaux de Diomèdès; et l'ardente flamme du"});
db.iliadfr.insert({ pg : 4655, line : "soufre brûlant jaillit. Les chevaux effrayés s'abattirent sous le"});
db.iliadfr.insert({ pg : 4656, line : "char, et les rênes splendides échappèrent des mains de Nestôr; et"});
db.iliadfr.insert({ pg : 4657, line : "il craignit dans son coeur, et il dit à Diomèdès:"});
db.iliadfr.insert({ pg : 4659, line : "-- Tydéide! retourne, fais fuir les chevaux aux sabots épais. Ne"});
db.iliadfr.insert({ pg : 4660, line : "vois-tu point que Zeus ne t'aide pas? Voici que Zeus Kronide donne"});
db.iliadfr.insert({ pg : 4661, line : "maintenant la victoire à Hektôr, et il nous la donnera aussi,"});
db.iliadfr.insert({ pg : 4662, line : "selon sa volonté. Le plus brave des hommes ne peut rien contre la"});
db.iliadfr.insert({ pg : 4663, line : "volonté de Zeus dont la force est sans égale."});
db.iliadfr.insert({ pg : 4665, line : "Et Diomèdès hardi au combat lui répondit:"});
db.iliadfr.insert({ pg : 4667, line : "-- Oui, vieillard, tu as dit vrai, et selon la justice; mais une"});
db.iliadfr.insert({ pg : 4668, line : "amère douleur envahit mon âme. Hektôr dira, haranguant les"});
db.iliadfr.insert({ pg : 4669, line : "Troiens: Le Tydéide a fui devant moi vers ses nefs!' Avant qu'il"});
db.iliadfr.insert({ pg : 4670, line : "se glorifie de ceci, que la terre profonde m'engloutisse!"});
db.iliadfr.insert({ pg : 4672, line : "Et le cavalier Gérennien Nestôr lui répondit:"});
db.iliadfr.insert({ pg : 4674, line : "-- Ah! fils du brave Tydeus, qu'as-tu dit? Si Hektôr te nommait"});
db.iliadfr.insert({ pg : 4675, line : "lâche et faible, ni les Troiens, ni les Dardaniens, ne l'en"});
db.iliadfr.insert({ pg : 4676, line : "croiraient, ni les femmes des magnanimes Troiens porteurs de"});
db.iliadfr.insert({ pg : 4677, line : "boucliers, elles dont tu as renversé dans la poussière les jeunes"});
db.iliadfr.insert({ pg : 4678, line : "époux."});
db.iliadfr.insert({ pg : 4680, line : "Ayant ainsi parlé, il prit la fuite, poussant les chevaux aux"});
db.iliadfr.insert({ pg : 4681, line : "sabots massifs à travers la mêlée. Et les Troiens et Hektôr, avec"});
db.iliadfr.insert({ pg : 4682, line : "de grands cris, les accablaient de traits; et le grand Hektôr au"});
db.iliadfr.insert({ pg : 4683, line : "casque mouvant cria d'une voix haute:"});
db.iliadfr.insert({ pg : 4685, line : "-- Tydéide, certes, les cavaliers Danaens t'honoraient entre tous,"});
db.iliadfr.insert({ pg : 4686, line : "te réservant la meilleure place, et les viandes, et les coupes"});
db.iliadfr.insert({ pg : 4687, line : "pleines. Aujourd'hui, ils t'auront en mépris, car tu n'es plus"});
db.iliadfr.insert({ pg : 4688, line : "qu'une femme! Va donc, fille lâche! Tu es par ma faute sur nos"});
db.iliadfr.insert({ pg : 4689, line : "tours, et tu emmèneras point nos femmes dans tes nefs. Auparavant,"});
db.iliadfr.insert({ pg : 4690, line : "je t'aurai donné la mort."});
db.iliadfr.insert({ pg : 4692, line : "Il parla ainsi, et le Tydéide hésita, voulant fuir et combattre"});
db.iliadfr.insert({ pg : 4693, line : "face à face. Et il hésita trois fois dans son esprit et dans son"});
db.iliadfr.insert({ pg : 4694, line : "coeur; et trois fois le sage Zeus tonna du haut des monts Idaiens,"});
db.iliadfr.insert({ pg : 4695, line : "en signe de victoire pour les Troiens. Et Hektôr, d'une voix"});
db.iliadfr.insert({ pg : 4696, line : "puissante, animait les Troiens:"});
db.iliadfr.insert({ pg : 4698, line : "-- Troiens, Lykiens et hardis Dardaniens, amis, soyez des hommes"});
db.iliadfr.insert({ pg : 4699, line : "et souvenez-vous de votre force et de votre courage. Je sens que"});
db.iliadfr.insert({ pg : 4700, line : "le Kroniôn me promet la victoire et une grande gloire, et réserve"});
db.iliadfr.insert({ pg : 4701, line : "la défaite aux Danaens. Les insensés! Ils ont élevé ces murailles"});
db.iliadfr.insert({ pg : 4702, line : "inutiles et méprisables qui n'arrêteront point ma force; et mes"});
db.iliadfr.insert({ pg : 4703, line : "chevaux sauteront aisément par-dessus le fossé profond. Mais quand"});
db.iliadfr.insert({ pg : 4704, line : "j'aurai atteint les nefs creuses, souvenez-vous de préparer le feu"});
db.iliadfr.insert({ pg : 4705, line : "destructeur, afin que je brûle les nefs, et qu'auprès des nefs je"});
db.iliadfr.insert({ pg : 4706, line : "tue les Argiens eux-mêmes, aveuglés par la fumée."});
db.iliadfr.insert({ pg : 4708, line : "Ayant ainsi parlé, il dit à ses chevaux:"});
db.iliadfr.insert({ pg : 4710, line : "-- Xanthos, Podargos, Aithôn et divin Lampos, payez-moi les soins"});
db.iliadfr.insert({ pg : 4711, line : "infinis d'Andromakhè, fille du magnanime Êétiôn, qui vous présente"});
db.iliadfr.insert({ pg : 4712, line : "le doux froment et vous verse du vin, quand vous le désirez, même"});
db.iliadfr.insert({ pg : 4713, line : "avant moi qui me glorifie d'être son jeune époux. Hâtez-vous donc,"});
db.iliadfr.insert({ pg : 4714, line : "courez! Si nous ne pouvons enlever le bouclier de Nestôr, qui est"});
db.iliadfr.insert({ pg : 4715, line : "tout en or ainsi que ses poignées, et dont la gloire est parvenue"});
db.iliadfr.insert({ pg : 4716, line : "jusqu'à l'Ouranos, et la riche cuirasse de Diomèdès dompteur de"});
db.iliadfr.insert({ pg : 4717, line : "chevaux, et que Hèphaistos a forgée avec soin, j'espère que les"});
db.iliadfr.insert({ pg : 4718, line : "Akhaiens remonteront cette nuit même dans leurs nefs rapides."});
db.iliadfr.insert({ pg : 4720, line : "Il parla ainsi dans son désir, et le vénérable Hèrè s'en indigna;"});
db.iliadfr.insert({ pg : 4721, line : "et elle s'agita sur son trône, et le vaste Olympos s'ébranla. Et"});
db.iliadfr.insert({ pg : 4722, line : "elle dit en face au grand Poseidaôn:"});
db.iliadfr.insert({ pg : 4724, line : "-- Toi qui ébranle la terre, ah! Tout-puissant, ton coeur n'est-il"});
db.iliadfr.insert({ pg : 4725, line : "point ému dans ta poitrine quand les Danaens périssent? Ils"});
db.iliadfr.insert({ pg : 4726, line : "t'offrent cependant, dans Hélikè et dans Aigas, un grand nombre de"});
db.iliadfr.insert({ pg : 4727, line : "beaux présents. Donne-leur donc la victoire. Si nous voulions,"});
db.iliadfr.insert({ pg : 4728, line : "nous tous qui soutenons les Danaens, repousser les Troiens et"});
db.iliadfr.insert({ pg : 4729, line : "résister à Zeus dont la voix sonne au loin, il serait bientôt seul"});
db.iliadfr.insert({ pg : 4730, line : "assis sur l'Ida."});
db.iliadfr.insert({ pg : 4732, line : "Et le puissant qui ébranle la terre, plein de colère, lui dit:"});
db.iliadfr.insert({ pg : 4734, line : "-- Audacieuse Hèrè, qu'as-tu dit? Je ne veux point que nous"});
db.iliadfr.insert({ pg : 4735, line : "combattions Zeus Kroniôn, car il est bien plus fort que nous."});
db.iliadfr.insert({ pg : 4737, line : "Et tandis qu'ils se parlaient ainsi, tout l'espace qui séparait"});
db.iliadfr.insert({ pg : 4738, line : "les nefs du fossé était empli confusément de chevaux et de"});
db.iliadfr.insert({ pg : 4739, line : "porteurs de boucliers, car Hektôr Priamide, semblable à"});
db.iliadfr.insert({ pg : 4740, line : "l'impétueux Arès, les avait enfermés là, Zeus l'ayant glorifié. Et"});
db.iliadfr.insert({ pg : 4741, line : "il eût consumé les nefs égales, à l'aide du feu, si la vénérable"});
db.iliadfr.insert({ pg : 4742, line : "Hèrè n'eût inspiré à Agamemnôn de ranimer à la hâte les Akhaiens."});
db.iliadfr.insert({ pg : 4743, line : "Et il parcourut les tentes et les nefs des Akhaiens, portant à sa"});
db.iliadfr.insert({ pg : 4744, line : "main robuste un grand manteau pourpré. Et il s'arrêta sur la"});
db.iliadfr.insert({ pg : 4745, line : "grande et noire nef d'Odysseus, qui était au centre de toutes,"});
db.iliadfr.insert({ pg : 4746, line : "afin d'être entendu des deux extrémités, des tentes d'Aias"});
db.iliadfr.insert({ pg : 4747, line : "Télamôniade à celles d'Akhilleus, car tous deux avaient tiré sur"});
db.iliadfr.insert({ pg : 4748, line : "le sable leurs nefs égales aux bouts du camp, certains de leur"});
db.iliadfr.insert({ pg : 4749, line : "force et de leur courage. Et là, d'une voix haute, il cria aux"});
db.iliadfr.insert({ pg : 4750, line : "Akhaiens:"});
db.iliadfr.insert({ pg : 4752, line : "-- Honte à vous, Argiens couverts d'opprobre, qui n'avez qu'une"});
db.iliadfr.insert({ pg : 4753, line : "vaine beauté! Que sont devenues vos paroles orgueilleuses, quand,"});
db.iliadfr.insert({ pg : 4754, line : "à Lemnos, mangeant la chair des boeufs aux longues cornes, et"});
db.iliadfr.insert({ pg : 4755, line : "buvant les kratères pleins de vin, vous vous vantiez d'être les"});
db.iliadfr.insert({ pg : 4756, line : "plus braves et de vaincre les Troiens, un contre cent et contre"});
db.iliadfr.insert({ pg : 4757, line : "deux cents? Et maintenant, nous ne pouvons même résister à un seul"});
db.iliadfr.insert({ pg : 4758, line : "d'entre eux, à Hektôr qui va consumer nos nefs par le feu. Père"});
db.iliadfr.insert({ pg : 4759, line : "Zeus! as-tu déjà accablé d'un tel désastre quelqu'un des rois"});
db.iliadfr.insert({ pg : 4760, line : "tout-puissants, et l'as-tu privé de tant de gloire? Certes, je"});
db.iliadfr.insert({ pg : 4761, line : "n'ai jamais passé devant tes temples magnifiques, quand je vins"});
db.iliadfr.insert({ pg : 4762, line : "ici pour ma ruine, sur ma nef chargée de rameurs, plein du désir"});
db.iliadfr.insert({ pg : 4763, line : "de renverser les hautes murailles de Troiè, sans brûler sur tes"});
db.iliadfr.insert({ pg : 4764, line : "nombreux autels la graisse et les cuisses des boeufs. Ô Zeus!"});
db.iliadfr.insert({ pg : 4765, line : "exauce donc mon voeu: que nous puissions au moins échapper et nous"});
db.iliadfr.insert({ pg : 4766, line : "enfuir, et que les Troiens ne tuent pas tous les Akhaiens!"});
db.iliadfr.insert({ pg : 4768, line : "Il parla ainsi, et le père Zeus eut pitié de ses larmes, et il"});
db.iliadfr.insert({ pg : 4769, line : "promit par un signe que les peuples ne périraient pas. Et il"});
db.iliadfr.insert({ pg : 4770, line : "envoya un aigle, le plus sûr des oiseaux, tenant entre ses serres"});
db.iliadfr.insert({ pg : 4771, line : "le jeune faon d'une biche agile. Et l'aigle jeta ce faon sur"});
db.iliadfr.insert({ pg : 4772, line : "l'autel magnifique de Zeus, où les Akhaiens sacrifiaient à Zeus,"});
db.iliadfr.insert({ pg : 4773, line : "source de tous les oracles. Et quand ils virent l'oiseau envoyé"});
db.iliadfr.insert({ pg : 4774, line : "par Zeus, il retournèrent dans la mêlée et se ruèrent sur les"});
db.iliadfr.insert({ pg : 4775, line : "Troiens."});
db.iliadfr.insert({ pg : 4777, line : "Et alors aucun des Danaens innombrables ne put se glorifier,"});
db.iliadfr.insert({ pg : 4778, line : "poussant ses chevaux rapides au-delà du fossé, d'avoir devancé le"});
db.iliadfr.insert({ pg : 4779, line : "Tydéide et combattu le premier. Et, tout d'abord, il tua un"});
db.iliadfr.insert({ pg : 4780, line : "guerrier Troien, Agélaos Phradmonide, qui fuyait. Et il lui"});
db.iliadfr.insert({ pg : 4781, line : "enfonça sa pique dans le dos, entre les épaules; et la pique"});
db.iliadfr.insert({ pg : 4782, line : "traversa la poitrine. Le Troien tomba du char, et ses armes"});
db.iliadfr.insert({ pg : 4783, line : "retentirent."});
db.iliadfr.insert({ pg : 4785, line : "Et les Atréides le suivaient, et les deux Aias pleins d'une"});
db.iliadfr.insert({ pg : 4786, line : "vigueur indomptable, et Idoméneus, et Mèrionès, tel qu'Arès,"});
db.iliadfr.insert({ pg : 4787, line : "compagnon d'Idoméneus, et le tueur d'hommes Euryalos, et"});
db.iliadfr.insert({ pg : 4788, line : "Eurypylos, fils illustre d'Évaimôn. Et Teukros survint le"});
db.iliadfr.insert({ pg : 4789, line : "neuvième, avec son arc tendu, et se tenant derrière le bouclier"});
db.iliadfr.insert({ pg : 4790, line : "d'Aias Télamôniade. Et quand le grand Aias soulevait le bouclier,"});
db.iliadfr.insert({ pg : 4791, line : "Teukros, regardant de toutes parts, ajustait et frappait un ennemi"});
db.iliadfr.insert({ pg : 4792, line : "dans la mêlée, et celui-ci tombait mort. Et il revenait auprès"});
db.iliadfr.insert({ pg : 4793, line : "d'Aias comme un enfant vers sa mère, et Aias l'abritait de"});
db.iliadfr.insert({ pg : 4794, line : "l'éclatant bouclier."});
db.iliadfr.insert({ pg : 4796, line : "Quel fut le premier Troien que tua l'irréprochable Teukros?"});
db.iliadfr.insert({ pg : 4797, line : "D'abord Orsilokhos, puis Orménos, et Ophélestès, et Daitôr, et"});
db.iliadfr.insert({ pg : 4798, line : "Khromios, et le divin Lykophontès, et Amopaôn Polyaimonide, et"});
db.iliadfr.insert({ pg : 4799, line : "Ménalippos. Et il les coucha tour à tour sur la terre nourricière."});
db.iliadfr.insert({ pg : 4800, line : "Et le roi des hommes, Agamemnôn, plein de joie de le voir"});
db.iliadfr.insert({ pg : 4801, line : "renverser de ses flèches les phalanges des Troiens, s'approcha et"});
db.iliadfr.insert({ pg : 4802, line : "lui dit:"});
db.iliadfr.insert({ pg : 4804, line : "-- Cher Teukros Télamônien, prince des peuples, continue à lancer"});
db.iliadfr.insert({ pg : 4805, line : "tes flèches pour le salut des Danaens, et pour glorifier ton père"});
db.iliadfr.insert({ pg : 4806, line : "Télamôn qui t'a nourri et soigné dans ses demeures tout petit et"});
db.iliadfr.insert({ pg : 4807, line : "bien que bâtard. Et je te le dis, et ma parole s'accomplira: si"});
db.iliadfr.insert({ pg : 4808, line : "Zeus tempétueux et Athènè me donnent de renverser la forte"});
db.iliadfr.insert({ pg : 4809, line : "citadelle d'Ilios, le premier après moi tu recevras une glorieuse"});
db.iliadfr.insert({ pg : 4810, line : "récompense: un trépied, deux chevaux et un char, et une femme qui"});
db.iliadfr.insert({ pg : 4811, line : "partagera ton lit."});
db.iliadfr.insert({ pg : 4813, line : "Et l'irréprochable Teukros lui répondit:"});
db.iliadfr.insert({ pg : 4815, line : "-- Très illustre Atréide, pourquoi m'excites-tu quand je suis"});
db.iliadfr.insert({ pg : 4816, line : "plein d'ardeur? Certes, je ferai de mon mieux et selon mes forces."});
db.iliadfr.insert({ pg : 4817, line : "Depuis que nous les repoussons vers Ilios, je tue les guerriers de"});
db.iliadfr.insert({ pg : 4818, line : "mes flèches. J'en ai lancé huit, et toutes se sont enfoncées dans"});
db.iliadfr.insert({ pg : 4819, line : "la chair des jeunes hommes impétueux; mais je ne puis frapper ce"});
db.iliadfr.insert({ pg : 4820, line : "chien enragé!"});
db.iliadfr.insert({ pg : 4822, line : "Il parla ainsi, et il lança une flèche contre Hektôr, plein du"});
db.iliadfr.insert({ pg : 4823, line : "désir de l'atteindre, et il le manqua. Et la flèche perça la"});
db.iliadfr.insert({ pg : 4824, line : "poitrine de l'irréprochable Gorgythiôn, brave fils de Priamos,"});
db.iliadfr.insert({ pg : 4825, line : "qu'avait enfanté la belle Kathanéira, venue d'Aisimè, et semblable"});
db.iliadfr.insert({ pg : 4826, line : "aux déesses par sa beauté. Et, comme un pavot, dans un jardin,"});
db.iliadfr.insert({ pg : 4827, line : "penche la tête sous le poids de ses fruits et des rosées"});
db.iliadfr.insert({ pg : 4828, line : "printanières, de même le Priamide pencha la tête sous le poids de"});
db.iliadfr.insert({ pg : 4829, line : "son casque. Et Teukros lança une autre flèche contre Hektôr, plein"});
db.iliadfr.insert({ pg : 4830, line : "du désir de l'atteindre, et il le manqua encore; et il perça, près"});
db.iliadfr.insert({ pg : 4831, line : "de la mamelle, le brave Arkhéptolémos, conducteur des chevaux de"});
db.iliadfr.insert({ pg : 4832, line : "Hektôr; et Arkhéptolémos tomba du char; ses chevaux rapides"});
db.iliadfr.insert({ pg : 4833, line : "reculèrent, et sa vie et sa force furent anéanties. Le regret amer"});
db.iliadfr.insert({ pg : 4834, line : "de son compagnon serra le coeur de Hektôr, mais, malgré sa"});
db.iliadfr.insert({ pg : 4835, line : "douleur, il le laissa gisant, et il ordonna à son frère Kébriôn de"});
db.iliadfr.insert({ pg : 4836, line : "prendre les rênes, et ce dernier obéit."});
db.iliadfr.insert({ pg : 4838, line : "Alors, Hektôr sauta du char éclatant, poussant un cri terrible;"});
db.iliadfr.insert({ pg : 4839, line : "et, saisissant une pierre, il courut à Teukros, plein du désir de"});
db.iliadfr.insert({ pg : 4840, line : "l'en frapper. Et le Télamônien avait tiré du carquois une flèche"});
db.iliadfr.insert({ pg : 4841, line : "amère, et il la plaçait sur le nerf, quand Hektôr au casque"});
db.iliadfr.insert({ pg : 4842, line : "mouvant, comme il tendait l'arc, le frappa de la pierre dure à"});
db.iliadfr.insert({ pg : 4843, line : "l'épaule, là où la clavicule sépare le cou de la poitrine, à un"});
db.iliadfr.insert({ pg : 4844, line : "endroit mortel. Et le nerf de l'arc fut brisé, et le poignet fut"});
db.iliadfr.insert({ pg : 4845, line : "écrasé, et l'arc s'échappa de sa main, et il tomba à genoux. Mais"});
db.iliadfr.insert({ pg : 4846, line : "Aias n'abandonna point son frère tombé, et il accourut, le"});
db.iliadfr.insert({ pg : 4847, line : "couvrant de son bouclier. Puis, ses deux chers compagnons,"});
db.iliadfr.insert({ pg : 4848, line : "Mèkisteus, fils d'Ekhios, et le divin Alastôr, emportèrent vers"});
db.iliadfr.insert({ pg : 4849, line : "les nefs creuses Teukros qui poussait des gémissements."});
db.iliadfr.insert({ pg : 4851, line : "Et l'Olympien rendit de nouveau le courage aux Troiens, et ils"});
db.iliadfr.insert({ pg : 4852, line : "repoussèrent les Akhaiens jusqu'au fossé profond; et Hektôr"});
db.iliadfr.insert({ pg : 4853, line : "marchait en avant, répandant la terreur de sa force. Comme un"});
db.iliadfr.insert({ pg : 4854, line : "chien qui poursuit de ses pieds rapides un sanglier sauvage ou un"});
db.iliadfr.insert({ pg : 4855, line : "lion, le touche aux cuisses et aux fesses, épiant l'instant où il"});
db.iliadfr.insert({ pg : 4856, line : "se retournera, de même Hektôr poursuivait les Akhaiens chevelus,"});
db.iliadfr.insert({ pg : 4857, line : "tuant toujours celui qui restait en arrière. Et les Akhaiens"});
db.iliadfr.insert({ pg : 4858, line : "fuyaient. Et beaucoup tombaient sous les mains des Troiens, en"});
db.iliadfr.insert({ pg : 4859, line : "traversant les pieux et le fossé. Mais les autres s'arrêtèrent"});
db.iliadfr.insert({ pg : 4860, line : "auprès des nefs, s'animant entre eux, levant les bras et suppliant"});
db.iliadfr.insert({ pg : 4861, line : "tous les dieux. Et Hektôr poussait de tous côtés ses chevaux aux"});
db.iliadfr.insert({ pg : 4862, line : "belles crinières, ayant les yeux de Gorgô et du sanguinaire Arès."});
db.iliadfr.insert({ pg : 4863, line : "Et la divine Hèrè aux bras blancs, à cette vue, fut saisie de"});
db.iliadfr.insert({ pg : 4864, line : "pitié et dit à Athènè ces paroles ailées:"});
db.iliadfr.insert({ pg : 4866, line : "-- Ah! fille de Zeus tempétueux, ne secourrons-nous point, en ce"});
db.iliadfr.insert({ pg : 4867, line : "combat suprême, les Danaens qui périssent? Car voici que, par une"});
db.iliadfr.insert({ pg : 4868, line : "destinée mauvaise, ils vont périr sous la violence d'un seul"});
db.iliadfr.insert({ pg : 4869, line : "homme. Le Priamide Hektôr est plein d'une fureur intolérable, et"});
db.iliadfr.insert({ pg : 4870, line : "il les accable de maux."});
db.iliadfr.insert({ pg : 4872, line : "Et la divine Athènè aux yeux clairs lui répondit:"});
db.iliadfr.insert({ pg : 4874, line : "-- Certes, le Priamide aurait déjà perdu la force avec la vie et"});
db.iliadfr.insert({ pg : 4875, line : "serait tombé mort sous la main des Argiens, sur sa terre natale,"});
db.iliadfr.insert({ pg : 4876, line : "si mon père, toujours irrité, dur et inique, ne s'opposait à ma"});
db.iliadfr.insert({ pg : 4877, line : "volonté. Et il ne se souvient plus que j'ai souvent secouru son"});
db.iliadfr.insert({ pg : 4878, line : "fils accablé de travaux par Eurystheus. Hèraklès criait vers"});
db.iliadfr.insert({ pg : 4879, line : "l'Ouranos, et Zeus m'envoya pour le secourir. Certes, si j'avais"});
db.iliadfr.insert({ pg : 4880, line : "prévu ceci, quand Hèraklès fut envoyé dans les demeures aux portes"});
db.iliadfr.insert({ pg : 4881, line : "massives d'Aidès, pour enlever, de l'Érébos, le chien du haïssable"});
db.iliadfr.insert({ pg : 4882, line : "Aidès, certes, il n'aurait point repassé l'eau courante et"});
db.iliadfr.insert({ pg : 4883, line : "profonde de Styx! Et Zeus me hait, et il cède aux désirs de Thétis"});
db.iliadfr.insert({ pg : 4884, line : "qui a embrassé ses genoux et lui a caressé la barbe, le suppliant"});
db.iliadfr.insert({ pg : 4885, line : "d'honorer Akhilleus le destructeur de citadelles. Et il me nommera"});
db.iliadfr.insert({ pg : 4886, line : "encore sa chère fille aux yeux clairs! Mais attelle nos chevaux"});
db.iliadfr.insert({ pg : 4887, line : "aux sabots massifs, tandis que j'irai dans la demeure de Zeus"});
db.iliadfr.insert({ pg : 4888, line : "prendre l'Aigide et me couvrir de mes armes guerrières. Je verrai"});
db.iliadfr.insert({ pg : 4889, line : "si le Priamide Hektôr au casque mouvant sera joyeux de nous voir"});
db.iliadfr.insert({ pg : 4890, line : "descendre toutes deux dans la mêlée. Certes, plus d'un Troien"});
db.iliadfr.insert({ pg : 4891, line : "couché devant les nefs des Akhaiens va rassasier les chiens et les"});
db.iliadfr.insert({ pg : 4892, line : "oiseaux carnassiers de sa graisse et de sa chair!"});
db.iliadfr.insert({ pg : 4894, line : "Elle parla ainsi, et la divine Hèrè aux bras blancs obéit. Et la"});
db.iliadfr.insert({ pg : 4895, line : "divine et vénérable Hèrè, fille du grand Kronos, se hâta d'atteler"});
db.iliadfr.insert({ pg : 4896, line : "les chevaux liés par des harnais d'or. Et Athènè, fille de Zeus"});
db.iliadfr.insert({ pg : 4897, line : "tempétueux, laissa tomber son riche péplos, qu'elle avait"});
db.iliadfr.insert({ pg : 4898, line : "travaillé de ses mains, sur le pavé de la demeure de son père, et"});
db.iliadfr.insert({ pg : 4899, line : "elle prit la cuirasse de Zeus qui amasse les nuées, et elle se"});
db.iliadfr.insert({ pg : 4900, line : "revêtit de ses armes pour la guerre lamentable."});
db.iliadfr.insert({ pg : 4902, line : "Et elle monta dans le char flamboyant, et elle saisit la lance"});
db.iliadfr.insert({ pg : 4903, line : "lourde, grande et solide, avec laquelle, étant la fille d'un père"});
db.iliadfr.insert({ pg : 4904, line : "tout-puissant, elle dompte la foule des héros contre qui elle"});
db.iliadfr.insert({ pg : 4905, line : "s'irrite. Et Hèrè frappa du fouet les chevaux rapides, et les"});
db.iliadfr.insert({ pg : 4906, line : "portes de l'Ouranos s'ouvrirent d'elles-mêmes en criant, gardées"});
db.iliadfr.insert({ pg : 4907, line : "par les Heures qui sont chargées d'ouvrir le grand Ouranos et"});
db.iliadfr.insert({ pg : 4908, line : "l'Olympos, ou de les fermer avec un nuage épais. Et ce fut par là"});
db.iliadfr.insert({ pg : 4909, line : "que les déesses poussèrent les chevaux obéissant à l'aiguillon. Et"});
db.iliadfr.insert({ pg : 4910, line : "le père Zeus, les ayant vues de l'Ida, fut saisi d'une grande"});
db.iliadfr.insert({ pg : 4911, line : "colère, et il envoya la messagère Iris aux ailes d'or:"});
db.iliadfr.insert({ pg : 4913, line : "-- Va! hâte-toi, légère Iris! Fais-les reculer, et qu'elles ne se"});
db.iliadfr.insert({ pg : 4914, line : "présentent point devant moi, car ceci serait dangereux pour elles."});
db.iliadfr.insert({ pg : 4915, line : "Je le dis, et ma parole s'accomplira: J'écraserai les chevaux"});
db.iliadfr.insert({ pg : 4916, line : "rapides sous leur char que je briserai, et je les en précipiterai,"});
db.iliadfr.insert({ pg : 4917, line : "et, avant dix ans, elles ne guériront point des plaies que leur"});
db.iliadfr.insert({ pg : 4918, line : "fera la foudre. Athènè aux yeux clairs saura qu'elle a combattu"});
db.iliadfr.insert({ pg : 4919, line : "son père. Ma colère n'est point aussi grande contre Hèrè, car elle"});
db.iliadfr.insert({ pg : 4920, line : "est habituée à toujours résister à ma volonté."});
db.iliadfr.insert({ pg : 4922, line : "Il parla ainsi, et la messagère Iris aux pieds prompts comme le"});
db.iliadfr.insert({ pg : 4923, line : "vent s'élança, et elle descendit des cimes Idaiennes dans le grand"});
db.iliadfr.insert({ pg : 4924, line : "Olympos, et elle les arrêta aux premières portes de l'Olympos aux"});
db.iliadfr.insert({ pg : 4925, line : "vallées sans nombre, et elle leur dit les paroles de Zeus:"});
db.iliadfr.insert({ pg : 4927, line : "-- Où allez-vous? Pourquoi votre coeur est-il ainsi troublé? Le"});
db.iliadfr.insert({ pg : 4928, line : "Kronide ne veut pas qu'on vienne en aide aux Argiens. Voici la"});
db.iliadfr.insert({ pg : 4929, line : "menace du fils de Kronos, s'il agit selon sa parole: il écrasera"});
db.iliadfr.insert({ pg : 4930, line : "les chevaux rapides sous votre char qu'il brisera, et il vous en"});
db.iliadfr.insert({ pg : 4931, line : "précipitera, et, avant dix ans, vous ne guérirez point des plaies"});
db.iliadfr.insert({ pg : 4932, line : "que vous fera la foudre. Athènè aux yeux clairs, tu sauras que tu"});
db.iliadfr.insert({ pg : 4933, line : "as combattu ton père! Sa colère n'est point aussi grande contre"});
db.iliadfr.insert({ pg : 4934, line : "Hèrè, car elle est habituée à toujours résister à sa volonté. Mais"});
db.iliadfr.insert({ pg : 4935, line : "toi, très violente et audacieuse chienne, oseras-tu lever ta lance"});
db.iliadfr.insert({ pg : 4936, line : "terrible contre Zeus?"});
db.iliadfr.insert({ pg : 4938, line : "Ayant ainsi parlé, Iris aux pieds rapides s'envola, et Hèrè dit à"});
db.iliadfr.insert({ pg : 4939, line : "Athènè:"});
db.iliadfr.insert({ pg : 4941, line : "-- Ah! fille de Zeus tempétueux, je ne puis permettre que nous"});
db.iliadfr.insert({ pg : 4942, line : "combattions contre Zeus pour des mortels. Que l'un meure, que"});
db.iliadfr.insert({ pg : 4943, line : "l'autre vive, soit! Et que Zeus décide, comme il est juste, et"});
db.iliadfr.insert({ pg : 4944, line : "selon sa volonté, entre les Troiens et les Danaens."});
db.iliadfr.insert({ pg : 4946, line : "Ayant ainsi parlé, elle fit retourner les chevaux aux sabots"});
db.iliadfr.insert({ pg : 4947, line : "massifs, et les Heures dételèrent les chevaux aux belles crinières"});
db.iliadfr.insert({ pg : 4948, line : "et les attachèrent aux crèches divines, et appuyèrent le char"});
db.iliadfr.insert({ pg : 4949, line : "contre le mur éclatant. Et les déesses, le coeur triste,"});
db.iliadfr.insert({ pg : 4950, line : "s'assirent sur des sièges d'or au milieu des autres dieux. Et le"});
db.iliadfr.insert({ pg : 4951, line : "père Zeus poussa du haut de l'Ida, vers l'Olympos, son char aux"});
db.iliadfr.insert({ pg : 4952, line : "belles roues et ses chevaux, et il parvint aux sièges des dieux."});
db.iliadfr.insert({ pg : 4953, line : "Et l'illustre qui ébranle la terre détela les chevaux, posa le"});
db.iliadfr.insert({ pg : 4954, line : "char sur un autel et le couvrit d'un voile de lin. Et Zeus à la"});
db.iliadfr.insert({ pg : 4955, line : "grande voix s'assit sur son trône d'or, et le large Olympos"});
db.iliadfr.insert({ pg : 4956, line : "trembla sous lui. Et Athènè et Hèrè étaient assises loin de Zeus,"});
db.iliadfr.insert({ pg : 4957, line : "et elles ne lui parlaient ni ne l'interrogeaient; mais il les"});
db.iliadfr.insert({ pg : 4958, line : "devina et dit:"});
db.iliadfr.insert({ pg : 4960, line : "-- Athènè et Hèrè, pourquoi êtes-vous ainsi affligées? Vous ne"});
db.iliadfr.insert({ pg : 4961, line : "vous êtes point longtemps fatiguées, du moins, dans la bataille"});
db.iliadfr.insert({ pg : 4962, line : "qui illustre les guerriers, afin d'anéantir les Troiens pour qui"});
db.iliadfr.insert({ pg : 4963, line : "vous avez tant de haine. Non! Tous les dieux de l'Olympos ne me"});
db.iliadfr.insert({ pg : 4964, line : "résisteront point, tant la force de mes mains invincibles est"});
db.iliadfr.insert({ pg : 4965, line : "grande. La terreur a fait trembler vos beaux membres avant d'avoir"});
db.iliadfr.insert({ pg : 4966, line : "vu la guerre et la mêlée violente. Et je le dis, et ma parole se"});
db.iliadfr.insert({ pg : 4967, line : "serait accomplie: frappées toutes deux de la foudre, vous ne"});
db.iliadfr.insert({ pg : 4968, line : "seriez point revenues sur votre char dans l'Olympos qui est la"});
db.iliadfr.insert({ pg : 4969, line : "demeure des immortels."});
db.iliadfr.insert({ pg : 4971, line : "Et il parla ainsi, et Athènè et Hèrè gémissaient, assises à côté"});
db.iliadfr.insert({ pg : 4972, line : "l'une de l'autre, et méditant le malheur des Troiens. Et Athènè"});
db.iliadfr.insert({ pg : 4973, line : "restait muette, irritée contre son père Zeus, et une sauvage"});
db.iliadfr.insert({ pg : 4974, line : "colère la brûlait; mais Hèrè ne put contenir la sienne, et elle"});
db.iliadfr.insert({ pg : 4975, line : "dit:"});
db.iliadfr.insert({ pg : 4977, line : "-- Très dur Kronide, quelle parole as-tu dite? Nous savons bien"});
db.iliadfr.insert({ pg : 4978, line : "que ta force est grande, mais nous gémissons sur les belliqueux"});
db.iliadfr.insert({ pg : 4979, line : "Danaens qui vont périr par une destinée mauvaise. Nous ne"});
db.iliadfr.insert({ pg : 4980, line : "combattrons point, si tu le veux; mais nous aiderons les Argiens"});
db.iliadfr.insert({ pg : 4981, line : "de nos conseils, afin qu'ils ne périssent point tous par ta"});
db.iliadfr.insert({ pg : 4982, line : "colère."});
db.iliadfr.insert({ pg : 4984, line : "Et Zeus qui amasse les nuées lui répondit:"});
db.iliadfr.insert({ pg : 4986, line : "-- Certes, au retour d'Éôs, tu pourras voir, vénérable Hèrè aux"});
db.iliadfr.insert({ pg : 4987, line : "yeux de boeuf, le tout-puissant Kroniôn mieux détruire encore"});
db.iliadfr.insert({ pg : 4988, line : "l'armée innombrable des Argiens; car le brave Hektôr ne cessera"});
db.iliadfr.insert({ pg : 4989, line : "point de combattre, que le rapide Pèléiôn ne se soit levé auprès"});
db.iliadfr.insert({ pg : 4990, line : "des nefs, le jour où les Akhaiens combattront sous leurs poupes,"});
db.iliadfr.insert({ pg : 4991, line : "luttant dans un étroit espace sur le cadavre de Patroklos. Ceci"});
db.iliadfr.insert({ pg : 4992, line : "est fatal. Je me soucie peu de ta colère, quand même tu irais aux"});
db.iliadfr.insert({ pg : 4993, line : "dernières limites de la terre et de la mer, où sont couchés"});
db.iliadfr.insert({ pg : 4994, line : "Iapétos et Kronos, loin des vents et de la lumière de Hélios, fils"});
db.iliadfr.insert({ pg : 4995, line : "de Hypériôn, dans l'enceinte du creux Tartaros. Quand même tu"});
db.iliadfr.insert({ pg : 4996, line : "irais là, je me soucie peu de ta colère, car rien n'est plus"});
db.iliadfr.insert({ pg : 4997, line : "impudent que toi."});
db.iliadfr.insert({ pg : 4999, line : "Il parla ainsi, et Hèrè aux bras blancs ne répondit rien. Et la"});
db.iliadfr.insert({ pg : 5000, line : "brillante lumière Hélienne tomba dans l'Okéanos, laissant la noire"});
db.iliadfr.insert({ pg : 5001, line : "nuit sur la terre nourricière. La lumière disparut contre le gré"});
db.iliadfr.insert({ pg : 5002, line : "des Troiens, mais la noire nuit fut la bienvenue des Akhaiens qui"});
db.iliadfr.insert({ pg : 5003, line : "la désiraient ardemment."});
db.iliadfr.insert({ pg : 5005, line : "Et l'illustre Hektôr réunit l'agora des Troiens, les ayant"});
db.iliadfr.insert({ pg : 5006, line : "conduits loin des nefs, sur les bords du fleuve tourbillonnant, en"});
db.iliadfr.insert({ pg : 5007, line : "un lieu où il n'y avait point de cadavres. Et ils descendirent de"});
db.iliadfr.insert({ pg : 5008, line : "leurs chevaux pour écouter les paroles de Hektôr cher à Zeus. Et"});
db.iliadfr.insert({ pg : 5009, line : "il tenait à la main une pique de onze coudées, à la brillante"});
db.iliadfr.insert({ pg : 5010, line : "pointe d'airain retenue par un anneau d'or. Et, appuyé sur cette"});
db.iliadfr.insert({ pg : 5011, line : "pique, il dit aux Troiens ces paroles ailées:"});
db.iliadfr.insert({ pg : 5013, line : "-- Écoutez-moi, Troiens, Dardaniens et alliés. J'espérais ne"});
db.iliadfr.insert({ pg : 5014, line : "retourner dans Ilios battue des vents qu'après avoir détruit les"});
db.iliadfr.insert({ pg : 5015, line : "nefs et tous les Akhaiens; mais les ténèbres sont venues qui ont"});
db.iliadfr.insert({ pg : 5016, line : "sauvé les Argiens et les nefs sur le rivage de la mer. C'est"});
db.iliadfr.insert({ pg : 5017, line : "pourquoi, obéissons à la nuit noire, et préparons le repas."});
db.iliadfr.insert({ pg : 5018, line : "Dételez les chevaux aux belles crinières et donnez-leur de la"});
db.iliadfr.insert({ pg : 5019, line : "nourriture. Amenez promptement de la ville des boeufs et de"});
db.iliadfr.insert({ pg : 5020, line : "grasses brebis, et apportez un doux vin de vos demeures, et"});
db.iliadfr.insert({ pg : 5021, line : "amassez beaucoup de bois, afin que, toute la nuit, jusqu'au retour"});
db.iliadfr.insert({ pg : 5022, line : "d'Éôs qui naît le matin, nous allumions beaucoup de feux dont"});
db.iliadfr.insert({ pg : 5023, line : "l'éclat s'élève dans l'Ouranos, et afin que les Akhaiens chevelus"});
db.iliadfr.insert({ pg : 5024, line : "ne profitent pas de la nuit pour fuir sur le vaste dos de la mer."});
db.iliadfr.insert({ pg : 5025, line : "Qu'ils ne montent point tranquillement du moins sur leurs nefs, et"});
db.iliadfr.insert({ pg : 5026, line : "que chacun d'eux, en montant sur sa nef, emporte dans son pays une"});
db.iliadfr.insert({ pg : 5027, line : "blessure faite par nos piques et nos lances aiguës! Que tout autre"});
db.iliadfr.insert({ pg : 5028, line : "redoute désormais d'apporter la guerre lamentable aux Troiens"});
db.iliadfr.insert({ pg : 5029, line : "dompteurs de chevaux. Que les hérauts chers à Zeus appellent, par"});
db.iliadfr.insert({ pg : 5030, line : "la ville, les jeunes enfants et les vieillards aux tempes blanches"});
db.iliadfr.insert({ pg : 5031, line : "à se réunir sur les tours élevées par les dieux; et que les femmes"});
db.iliadfr.insert({ pg : 5032, line : "timides, chacune dans sa demeure, allument de grands feux, afin"});
db.iliadfr.insert({ pg : 5033, line : "qu'on veille avec vigilance, de peur qu'on entre par surprise dans"});
db.iliadfr.insert({ pg : 5034, line : "la ville, en l'absence des hommes. Qu'il soit fait comme je le"});
db.iliadfr.insert({ pg : 5035, line : "dis, magnanimes Troiens, car mes paroles sont salutaires. Dès le"});
db.iliadfr.insert({ pg : 5036, line : "retour d'Éôs je parlerai encore aux Troiens dompteurs de chevaux."});
db.iliadfr.insert({ pg : 5037, line : "Je me vante, ayant supplié Zeus et les autres dieux, de chasser"});
db.iliadfr.insert({ pg : 5038, line : "bientôt d'ici ces chiens que les kères ont amenés sur les nefs"});
db.iliadfr.insert({ pg : 5039, line : "noires. Veillons sur nous-mêmes pendant la nuit; mais, dès la"});
db.iliadfr.insert({ pg : 5040, line : "première heure du matin, couvrons-nous de nos armes et poussons"});
db.iliadfr.insert({ pg : 5041, line : "l'impétueux Arès sur les nefs creuses. Je saurai si le brave"});
db.iliadfr.insert({ pg : 5042, line : "Diomèdès Tydéide me repoussera loin des nefs jusqu'aux murailles,"});
db.iliadfr.insert({ pg : 5043, line : "ou si, le perçant de l'airain, j'emporterai ses dépouilles"});
db.iliadfr.insert({ pg : 5044, line : "sanglantes. Demain, il pourra se glorifier de sa force, s'il"});
db.iliadfr.insert({ pg : 5045, line : "résiste à ma pique; mais j'espère plutôt que, demain, quand Hélios"});
db.iliadfr.insert({ pg : 5046, line : "se lèvera, il tombera des premiers, tout sanglant, au milieu d'une"});
db.iliadfr.insert({ pg : 5047, line : "foule de ses compagnons. Et plût aux dieux que je fusse immortel"});
db.iliadfr.insert({ pg : 5048, line : "et toujours jeune, et honoré comme Athènè et Apollôn, autant qu'il"});
db.iliadfr.insert({ pg : 5049, line : "est certain que ce jour sera funeste aux Argiens!"});
db.iliadfr.insert({ pg : 5051, line : "Hektôr parla ainsi, et les Troiens poussèrent des acclamations. Et"});
db.iliadfr.insert({ pg : 5052, line : "ils détachèrent du joug les chevaux mouillés de sueur, et ils les"});
db.iliadfr.insert({ pg : 5053, line : "lièrent avec des lanières auprès des chars; et ils amenèrent"});
db.iliadfr.insert({ pg : 5054, line : "promptement de la ville des boeufs et des brebis grasses; et ils"});
db.iliadfr.insert({ pg : 5055, line : "apportèrent un doux vin et du pain de leurs demeures, et ils"});
db.iliadfr.insert({ pg : 5056, line : "amassèrent beaucoup de bois. Puis, ils sacrifièrent de complètes"});
db.iliadfr.insert({ pg : 5057, line : "hécatombes aux immortels, et le vent en portait la fumée épaisse"});
db.iliadfr.insert({ pg : 5058, line : "et douce dans l'Ouranos. Mais les dieux heureux n'en voulurent"});
db.iliadfr.insert({ pg : 5059, line : "point et la dédaignèrent, car ils haîssaient la sainte Ilios, et"});
db.iliadfr.insert({ pg : 5060, line : "Priamos, et le peuple de Priamos aux piques de frêne."});
db.iliadfr.insert({ pg : 5062, line : "Et les Troiens, pleins d'espérance, passaient la nuit sur le"});
db.iliadfr.insert({ pg : 5063, line : "sentier de la guerre, ayant allumé de grands feux. Comme, lorsque"});
db.iliadfr.insert({ pg : 5064, line : "les astres étincellent dans l'Ouranos autour de la claire Sélènè,"});
db.iliadfr.insert({ pg : 5065, line : "et que le vent ne trouble point l'air, on voit s'éclairer les"});
db.iliadfr.insert({ pg : 5066, line : "cimes et les hauts promontoires et les vallées, et que l'aithèr"});
db.iliadfr.insert({ pg : 5067, line : "infini s'ouvre au faîte de l'Ouranos, et que le berger joyeux voit"});
db.iliadfr.insert({ pg : 5068, line : "luire tous les astres; de même, entre les nefs et l'eau courante"});
db.iliadfr.insert({ pg : 5069, line : "du Xanthos, les feux des Troiens brillaient devant Ilios. Mille"});
db.iliadfr.insert({ pg : 5070, line : "feux brûlaient ainsi dans la plaine; et, près de chacun, étaient"});
db.iliadfr.insert({ pg : 5071, line : "assis cinquante guerriers autour de la flamme ardente. Et les"});
db.iliadfr.insert({ pg : 5072, line : "chevaux, mangeant l'orge et l'avoine, se tenaient auprès des"});
db.iliadfr.insert({ pg : 5073, line : "chars, attendant Éôs au beau trône."});
db.iliadfr.insert({ pg : 5076, line : "Chant 9"});
db.iliadfr.insert({ pg : 5078, line : "Tandis que les Troiens plaçaient ainsi leurs gardes, le désir de"});
db.iliadfr.insert({ pg : 5079, line : "la fuite, qui accompagne la froide terreur, saisissait les"});
db.iliadfr.insert({ pg : 5080, line : "Akhaiens. Et les plus braves étaient frappés d'une accablante"});
db.iliadfr.insert({ pg : 5081, line : "tristesse."});
db.iliadfr.insert({ pg : 5083, line : "De même, lorsque les deux vents Boréas et Zéphyros, soufflant de"});
db.iliadfr.insert({ pg : 5084, line : "la Thrèkè, bouleversent la haute mer poissonneuse, et que l'onde"});
db.iliadfr.insert({ pg : 5085, line : "noire se gonfle et se déroule en masses d'écume, ainsi, dans leurs"});
db.iliadfr.insert({ pg : 5086, line : "poitrines, se déchirait le coeur des Akhaiens. Et l'Atréide,"});
db.iliadfr.insert({ pg : 5087, line : "frappé d'une grande douleur, ordonna aux hérauts à la voix sonore"});
db.iliadfr.insert({ pg : 5088, line : "d'appeler, chacun par son nom, et sans clameurs, les hommes à"});
db.iliadfr.insert({ pg : 5089, line : "l'agora. Et lui-même appela les plus proches. Et tous vinrent"});
db.iliadfr.insert({ pg : 5090, line : "s'asseoir dans l'agora, pleins de tristesse. Et Agamemnôn se leva,"});
db.iliadfr.insert({ pg : 5091, line : "versant des larmes, comme une source abondante qui tombe largement"});
db.iliadfr.insert({ pg : 5092, line : "d'une roche élevée. Et, avec un profond soupir, il dit aux"});
db.iliadfr.insert({ pg : 5093, line : "Argiens:"});
db.iliadfr.insert({ pg : 5095, line : "-- Ô amis, rois et chefs des Argiens, le Kronide Zeus m'a accablé"});
db.iliadfr.insert({ pg : 5096, line : "d'un lourd malheur, lui qui m'avait solennellement promis que je"});
db.iliadfr.insert({ pg : 5097, line : "ne m'en retournerais qu'après avoir détruit Ilios aux murailles"});
db.iliadfr.insert({ pg : 5098, line : "solides. Maintenant, il médite une fraude funeste, et il m'ordonne"});
db.iliadfr.insert({ pg : 5099, line : "de retourner sans gloire dans Argos, quand j'ai perdu tant de"});
db.iliadfr.insert({ pg : 5100, line : "guerriers déjà! Et ceci plaît au tout-puissant Zeus qui a renversé"});
db.iliadfr.insert({ pg : 5101, line : "les citadelles de tant de villes, et qui en renversera encore, car"});
db.iliadfr.insert({ pg : 5102, line : "sa puissance est très grande. Allons! obéissez tous à mes paroles:"});
db.iliadfr.insert({ pg : 5103, line : "fuyons sur nos nefs vers la terre bien-aimée de la patrie. Nous ne"});
db.iliadfr.insert({ pg : 5104, line : "prendrons jamais Ilios aux larges rues."});
db.iliadfr.insert({ pg : 5106, line : "Il parla ainsi, et tous restèrent muets, et les fils des Akhaiens"});
db.iliadfr.insert({ pg : 5107, line : "étaient tristes et silencieux. Enfin, Diomèdès hardi au combat"});
db.iliadfr.insert({ pg : 5108, line : "parla au milieu d'eux:"});
db.iliadfr.insert({ pg : 5110, line : "-- Atréide, je combattrai le premier tes paroles insensées, comme"});
db.iliadfr.insert({ pg : 5111, line : "il est permis, ô roi, dans l'agora; et tu ne t'en irriteras pas,"});
db.iliadfr.insert({ pg : 5112, line : "car toi-même tu m'as outragé déjà au milieu des Danaens, me"});
db.iliadfr.insert({ pg : 5113, line : "nommant faible et lâche. Et ceci, les Argiens le savent, jeunes et"});
db.iliadfr.insert({ pg : 5114, line : "vieux. Certes, le fils du subtil Kronos t'a doué inégalement. Il"});
db.iliadfr.insert({ pg : 5115, line : "t'a accordé le sceptre et les honneurs suprêmes, mais il ne t'a"});
db.iliadfr.insert({ pg : 5116, line : "point donné la fermeté de l'âme, qui est la plus grande vertu."});
db.iliadfr.insert({ pg : 5117, line : "Malheureux! penses-tu que les fils des Akhaiens soient aussi"});
db.iliadfr.insert({ pg : 5118, line : "faibles et aussi lâches que tu le dis? Si ton coeur te pousse à"});
db.iliadfr.insert({ pg : 5119, line : "retourner en arrière, va! voici la route; et les nombreuses nefs"});
db.iliadfr.insert({ pg : 5120, line : "qui t'ont suivi de Mykènè sont là, auprès du rivage de la mer."});
db.iliadfr.insert({ pg : 5121, line : "Mais tous les autres Akhaiens chevelus resteront jusqu'à ce que"});
db.iliadfr.insert({ pg : 5122, line : "nous ayons renversé Ilios. Et s'ils veulent eux-mêmes fuir sur"});
db.iliadfr.insert({ pg : 5123, line : "leurs nefs vers la terre bien-aimée de la patrie, moi et Sthénélos"});
db.iliadfr.insert({ pg : 5124, line : "nous combattrons jusqu'à ce que nous ayons vu la fin d'Ilios, car"});
db.iliadfr.insert({ pg : 5125, line : "nous sommes venus ici sur la foi des dieux!"});
db.iliadfr.insert({ pg : 5127, line : "Il parla ainsi, et tous les fils des Akhaiens applaudirent,"});
db.iliadfr.insert({ pg : 5128, line : "admirant le discours du dompteur de chevaux Diomèdès. Et le"});
db.iliadfr.insert({ pg : 5129, line : "cavalier Nestôr, se levant au milieu d'eux, parla ainsi:"});
db.iliadfr.insert({ pg : 5131, line : "-- Tydéide, tu es le plus hardi au combat, et tu es aussi le"});
db.iliadfr.insert({ pg : 5132, line : "premier à l'agora parmi tes égaux en âge. Nul ne blâmera tes"});
db.iliadfr.insert({ pg : 5133, line : "paroles, et aucun des Akhaiens ne les contredira mais tu n'as pas"});
db.iliadfr.insert({ pg : 5134, line : "tout dit. À la vérité, tu es jeune, et tu pourrais être le moins"});
db.iliadfr.insert({ pg : 5135, line : "âgé de mes fils; et, cependant, tu parles avec prudence devant les"});
db.iliadfr.insert({ pg : 5136, line : "rois des Argiens, et comme il convient. C'est à moi de tout"});
db.iliadfr.insert({ pg : 5137, line : "prévoir et de tout dire, car je me glorifie d'être plus vieux que"});
db.iliadfr.insert({ pg : 5138, line : "toi. Et nul ne blâmera mes paroles, pas même le roi Agamemnôn. Il"});
db.iliadfr.insert({ pg : 5139, line : "est sans intelligence, sans justice et sans foyers domestiques,"});
db.iliadfr.insert({ pg : 5140, line : "celui qui aime les affreuses discordes intestines. Mais obéissons"});
db.iliadfr.insert({ pg : 5141, line : "maintenant à la nuit noire: préparons notre repas, plaçons des"});
db.iliadfr.insert({ pg : 5142, line : "gardes choisies auprès du fossé profond, en avant des murailles."});
db.iliadfr.insert({ pg : 5143, line : "C'est aux jeunes hommes de prendre ce soin, et c'est à toi,"});
db.iliadfr.insert({ pg : 5144, line : "Atréide, qui es le chef suprême, de le leur commander. Puis, offre"});
db.iliadfr.insert({ pg : 5145, line : "un repas aux chefs, car ceci est convenable et t'appartient. Tes"});
db.iliadfr.insert({ pg : 5146, line : "tentes sont pleines du vin que les nefs des Akhaiens t'apportent"});
db.iliadfr.insert({ pg : 5147, line : "chaque jour de la Thrèkè, à travers l'immensité de la haute mer."});
db.iliadfr.insert({ pg : 5148, line : "Tu peux aisément beaucoup offrir, et tu commandes à un grand"});
db.iliadfr.insert({ pg : 5149, line : "nombre de serviteurs. Quand les chefs seront assemblés, obéis à"});
db.iliadfr.insert({ pg : 5150, line : "qui te donnera le meilleur conseil; car les Akhaiens ont tous"});
db.iliadfr.insert({ pg : 5151, line : "besoin de sages conseils au moment où les ennemis allument tant de"});
db.iliadfr.insert({ pg : 5152, line : "feux auprès des nefs. Qui de nous pourrait s'en réjouir? Cette"});
db.iliadfr.insert({ pg : 5153, line : "nuit, l'armée sera perdue ou sauvée."});
db.iliadfr.insert({ pg : 5155, line : "Il parla ainsi, et tous, l'ayant écouté, obéirent. Et les gardes"});
db.iliadfr.insert({ pg : 5156, line : "armées sortirent, conduites par le Nestoréide Thrasymèdès, prince"});
db.iliadfr.insert({ pg : 5157, line : "des peuples, par Askalaphos et Ialménos, fils d'Arès, par"});
db.iliadfr.insert({ pg : 5158, line : "Mèrionès, Apharèos et Dèipiros, et par le divin Lykomèdès, fils de"});
db.iliadfr.insert({ pg : 5159, line : "Kréôn. Et les sept chefs des gardes conduisaient, chacun, cent"});
db.iliadfr.insert({ pg : 5160, line : "jeunes guerriers armés de longues piques. Et ils se placèrent"});
db.iliadfr.insert({ pg : 5161, line : "entre le fossé et la muraille, et ils allumèrent des feux et"});
db.iliadfr.insert({ pg : 5162, line : "prirent leur repas. Et l'Atréide conduisit les chefs des Akhaiens"});
db.iliadfr.insert({ pg : 5163, line : "sous sa tente et leur offrit un abondant repas. Et tous étendirent"});
db.iliadfr.insert({ pg : 5164, line : "les mains vers les mets. Et, quand ils eurent assouvi la soif et"});
db.iliadfr.insert({ pg : 5165, line : "la faim, le premier d'entre eux, le vieillard Nestôr, qui avait"});
db.iliadfr.insert({ pg : 5166, line : "déjà donné le meilleur conseil, parla ainsi, plein de sagesse, et"});
db.iliadfr.insert({ pg : 5167, line : "dit:"});
db.iliadfr.insert({ pg : 5169, line : "-- Très illustre Atréide Agamemnôn, roi des hommes, je commencerai"});
db.iliadfr.insert({ pg : 5170, line : "et je finirai par toi, car tu commandes à de nombreux peuples, et"});
db.iliadfr.insert({ pg : 5171, line : "Zeus t'a donné le sceptre et les droits afin que tu les gouvernes."});
db.iliadfr.insert({ pg : 5172, line : "C'est pourquoi il faut que tu saches parler et entendre, et"});
db.iliadfr.insert({ pg : 5173, line : "accueillir les sages conseils, si leur coeur ordonne aux autres"});
db.iliadfr.insert({ pg : 5174, line : "chefs de t'en donner de meilleurs. Et je te dirai ce qu'il y a de"});
db.iliadfr.insert({ pg : 5175, line : "mieux à faire, car personne n'a une meilleure pensée que celle que"});
db.iliadfr.insert({ pg : 5176, line : "je médite maintenant, et depuis longtemps, depuis le jour où tu as"});
db.iliadfr.insert({ pg : 5177, line : "enlevé, ô race divine, contre notre gré, la vierge Breisèis de la"});
db.iliadfr.insert({ pg : 5178, line : "tente d'Akhilleus irrité. Et j'ai voulu te dissuader, et, cédant à"});
db.iliadfr.insert({ pg : 5179, line : "ton coeur orgueilleux, tu as outragé le plus brave des hommes, que"});
db.iliadfr.insert({ pg : 5180, line : "les immortels mêmes honorent, et tu lui as enlevé sa récompense."});
db.iliadfr.insert({ pg : 5181, line : "Délibérons donc aujourd'hui, et cherchons comment nous pourrons"});
db.iliadfr.insert({ pg : 5182, line : "apaiser Akhilleus par des présents pacifiques et par des paroles"});
db.iliadfr.insert({ pg : 5183, line : "flatteuses."});
db.iliadfr.insert({ pg : 5185, line : "Et le roi des hommes, Agamemnôn, lui répondit:"});
db.iliadfr.insert({ pg : 5187, line : "-- Ô vieillard, tu ne mens point en rappelant mes injustices. J'ai"});
db.iliadfr.insert({ pg : 5188, line : "commis une offense, et je ne le nie point. Un guerrier que Zeus"});
db.iliadfr.insert({ pg : 5189, line : "aime dans son coeur l'emporte sur tous les guerriers. Et c'est"});
db.iliadfr.insert({ pg : 5190, line : "pour l'honorer qu'il accable aujourd'hui l'armée des Akhaiens."});
db.iliadfr.insert({ pg : 5191, line : "Mais, puisque j'ai failli en obéissant à de funestes pensées, je"});
db.iliadfr.insert({ pg : 5192, line : "veux maintenant apaiser Akhilleus et lui offrir des présents"});
db.iliadfr.insert({ pg : 5193, line : "infinis. Et je vous dirai quels sont ces dons illustres: sept"});
db.iliadfr.insert({ pg : 5194, line : "trépieds vierges du feu, dix talents d'or, vingt bassins qu'on"});
db.iliadfr.insert({ pg : 5195, line : "peut exposer à la flamme, douze chevaux robustes qui ont toujours"});
db.iliadfr.insert({ pg : 5196, line : "remporté les premiers prix par la rapidité de leur course. Et il"});
db.iliadfr.insert({ pg : 5197, line : "ne manquerait plus de rien, et il serait comblé d'or celui qui"});
db.iliadfr.insert({ pg : 5198, line : "posséderait les prix que m'ont rapportés ces chevaux aux sabots"});
db.iliadfr.insert({ pg : 5199, line : "massifs. Et je donnerai encore au Pèléide sept belles femmes"});
db.iliadfr.insert({ pg : 5200, line : "Lesbiennes, habiles aux travaux, qu'il a prises lui-même dans"});
db.iliadfr.insert({ pg : 5201, line : "Lesbos bien peuplée, et que j'ai choisies, car elles étaient plus"});
db.iliadfr.insert({ pg : 5202, line : "belles que toutes les autres femmes. Et je les lui donnerai, et,"});
db.iliadfr.insert({ pg : 5203, line : "avec elles, celle que je lui ai enlevée, la vierge Breisèis; et je"});
db.iliadfr.insert({ pg : 5204, line : "jurerai un grand serment qu'elle n'a point connu mon lit, et que"});
db.iliadfr.insert({ pg : 5205, line : "je l'ai respectée. Toutes ces choses lui seront livrées aussitôt."});
db.iliadfr.insert({ pg : 5206, line : "Et si les dieux nous donnent de renverser la grande ville de"});
db.iliadfr.insert({ pg : 5207, line : "Priamos, il remplira abondamment sa nef d'or et d'airain. Et quand"});
db.iliadfr.insert({ pg : 5208, line : "nous, Akhaiens, partagerons la proie, qu'il choisisse vingt femmes"});
db.iliadfr.insert({ pg : 5209, line : "Troiennes, les plus belles après l'Argienne Hélénè. Et si nous"});
db.iliadfr.insert({ pg : 5210, line : "retournons dans la fertile Argos, en Akhaiè, qu'il soit mon"});
db.iliadfr.insert({ pg : 5211, line : "gendre, et je l'honorerai autant qu'Orestès, mon unique fils"});
db.iliadfr.insert({ pg : 5212, line : "nourri dans les délices. J'ai trois filles dans mes riches"});
db.iliadfr.insert({ pg : 5213, line : "demeures, Khrysothémis, Laodikè et Iphianassa. Qu'il emmène, sans"});
db.iliadfr.insert({ pg : 5214, line : "lui assurer une dot, celle qu'il aimera le mieux, dans les"});
db.iliadfr.insert({ pg : 5215, line : "demeures de Pèleus. Ce sera moi qui la doterai, comme jamais"});
db.iliadfr.insert({ pg : 5216, line : "personne n'a doté sa fille, car je lui donnerai sept villes très"});
db.iliadfr.insert({ pg : 5217, line : "illustres: Kardamylè, Énopè, Hira aux prés verdoyants, la divine"});
db.iliadfr.insert({ pg : 5218, line : "Phèra, Anthéia aux gras pâturages, la belle Aipéia et Pèdasos"});
db.iliadfr.insert({ pg : 5219, line : "riche en vignes. Toutes sont aux bords de la mer, auprès de la"});
db.iliadfr.insert({ pg : 5220, line : "sablonneuse Pylos. Leurs habitants abondent en boeufs et en"});
db.iliadfr.insert({ pg : 5221, line : "troupeaux, et, par leurs dons, ils l'honoreront comme un dieu; et,"});
db.iliadfr.insert({ pg : 5222, line : "sous son sceptre, ils lui payeront de riches tributs. Je lui"});
db.iliadfr.insert({ pg : 5223, line : "donnerai tout cela s'il dépose sa colère. Qu'il s'apaise donc."});
db.iliadfr.insert({ pg : 5224, line : "Aidès seul est implacable et indompté, et c'est pourquoi, de tous"});
db.iliadfr.insert({ pg : 5225, line : "les dieux, il est le plus haï des hommes. Qu'il me cède comme il"});
db.iliadfr.insert({ pg : 5226, line : "est juste, puisque je suis plus puissant et plus âgé que lui."});
db.iliadfr.insert({ pg : 5228, line : "Et le cavalier Gérennien Nestôr lui répondit:"});
db.iliadfr.insert({ pg : 5230, line : "-- Très illustre Atréide Agamemnôn, roi des hommes, certes, ils ne"});
db.iliadfr.insert({ pg : 5231, line : "sont point à mépriser les présents que tu offres au roi Akhilleus."});
db.iliadfr.insert({ pg : 5232, line : "Allons! envoyons promptement des messagers choisis sous la tente"});
db.iliadfr.insert({ pg : 5233, line : "du Pèléide Akhilleus. Je les désignerai moi-même, et ils obéiront."});
db.iliadfr.insert({ pg : 5234, line : "Que Phoinix aimé de Zeus les conduise, et ce seront le grand Aias"});
db.iliadfr.insert({ pg : 5235, line : "et le divin Odysseus, suivis des hérauts Hodios et Eurybatès."});
db.iliadfr.insert({ pg : 5236, line : "Trempons nos mains dans l'eau, et supplions en silence Zeus"});
db.iliadfr.insert({ pg : 5237, line : "Kronide de nous prendre en pitié."});
db.iliadfr.insert({ pg : 5239, line : "Il parla ainsi, et tous furent satisfaits de ses paroles. Et les"});
db.iliadfr.insert({ pg : 5240, line : "hérauts versèrent aussitôt de l'eau sur leurs mains, et les jeunes"});
db.iliadfr.insert({ pg : 5241, line : "hommes emplirent les kratères de vin qu'ils distribuèrent, selon"});
db.iliadfr.insert({ pg : 5242, line : "l'ordre, à pleines coupes. Et, après avoir bu autant qu'ils le"});
db.iliadfr.insert({ pg : 5243, line : "voulaient, ils sortirent de la tente de l'Atréide Agamemnôn. Et le"});
db.iliadfr.insert({ pg : 5244, line : "cavalier Gérennien Nestôr exhorta longuement chacun d'eux, et"});
db.iliadfr.insert({ pg : 5245, line : "surtout Odysseus, à faire tous leurs efforts pour apaiser et"});
db.iliadfr.insert({ pg : 5246, line : "fléchir l'irréprochable Pèléide. Et ils allaient le long du rivage"});
db.iliadfr.insert({ pg : 5247, line : "de la mer aux bruits sans nombre, suppliant celui qui entoure la"});
db.iliadfr.insert({ pg : 5248, line : "terre de leur accorder de toucher le grand coeur de l'Aiakide."});
db.iliadfr.insert({ pg : 5250, line : "Et ils parvinrent aux nefs et aux tentes des Myrmidones. Et ils"});
db.iliadfr.insert({ pg : 5251, line : "trouvèrent le Pèléide qui charmait son âme en jouant d'une kithare"});
db.iliadfr.insert({ pg : 5252, line : "aux doux sons, belle, artistement faite et surmontée d'un joug"});
db.iliadfr.insert({ pg : 5253, line : "d'argent, et qu'il avait prise parmi les dépouilles, après avoir"});
db.iliadfr.insert({ pg : 5254, line : "détruit la ville d'Êétiôn. Et il charmait son âme, et il chantait"});
db.iliadfr.insert({ pg : 5255, line : "les actions glorieuses des hommes. Et Patroklos, seul, était assis"});
db.iliadfr.insert({ pg : 5256, line : "auprès de lui, l'écoutant en silence jusqu'à ce qu'il eût cessé de"});
db.iliadfr.insert({ pg : 5257, line : "chanter."});
db.iliadfr.insert({ pg : 5259, line : "Et ils s'avancèrent, précédés par le divin Odysseus, et ils"});
db.iliadfr.insert({ pg : 5260, line : "s'arrêtèrent devant le Pèléide. Et Akhilleus, étonné, se leva de"});
db.iliadfr.insert({ pg : 5261, line : "son siège, avec sa kithare, et Patroklos se leva aussi en voyant"});
db.iliadfr.insert({ pg : 5262, line : "les guerriers. Et Akhilleus aux pieds rapides leur parla ainsi:"});
db.iliadfr.insert({ pg : 5264, line : "-- Je vous salue, guerriers. Certes, vous êtes les bienvenus, mais"});
db.iliadfr.insert({ pg : 5265, line : "quelle nécessité vous amène, vous qui, malgré ma colère, m'êtes"});
db.iliadfr.insert({ pg : 5266, line : "les plus chers parmi les Akhaiens?"});
db.iliadfr.insert({ pg : 5268, line : "Ayant ainsi parlé, le divin Akhilleus les conduisit et les fit"});
db.iliadfr.insert({ pg : 5269, line : "asseoir sur des sièges aux draperies pourprées. Et aussitôt il dit"});
db.iliadfr.insert({ pg : 5270, line : "à Patroklos:"});
db.iliadfr.insert({ pg : 5272, line : "-- Fils de Ménoitios, apporte un grand kratère, fais un doux"});
db.iliadfr.insert({ pg : 5273, line : "mélange, et prépare des coupes pour chacun de nous, car des hommes"});
db.iliadfr.insert({ pg : 5274, line : "très chers sont venus sous ma tente."});
db.iliadfr.insert({ pg : 5276, line : "Il parla ainsi, et Patroklos obéit à son cher compagnon. Et"});
db.iliadfr.insert({ pg : 5277, line : "Akhilleus étendit sur un grand billot, auprès du feu, le dos d'une"});
db.iliadfr.insert({ pg : 5278, line : "brebis, celui d'une chèvre grasse et celui d'un porc gras. Et"});
db.iliadfr.insert({ pg : 5279, line : "tandis qu'Automédôn maintenait les chairs, le divin Akhilleus les"});
db.iliadfr.insert({ pg : 5280, line : "coupait par morceaux et les embrochait. Et le Ménoitiade, homme"});
db.iliadfr.insert({ pg : 5281, line : "semblable à un dieu, allumait un grand feu. Et quand la flamme"});
db.iliadfr.insert({ pg : 5282, line : "tomba et s'éteignit, il étendit les broches au-dessus des charbons"});
db.iliadfr.insert({ pg : 5283, line : "en les appuyant sur des pierres, et il les aspergea de sel sacré."});
db.iliadfr.insert({ pg : 5284, line : "Et Patroklos, ayant rôti les chairs et les ayant posées sur la"});
db.iliadfr.insert({ pg : 5285, line : "table, distribua le pain dans de belles corbeilles. Et Akhilleus"});
db.iliadfr.insert({ pg : 5286, line : "coupa les viandes, et il s'assit en face du divin Odysseus, et il"});
db.iliadfr.insert({ pg : 5287, line : "ordonna à Patroklos de sacrifier aux dieux. Et celui-ci fit des"});
db.iliadfr.insert({ pg : 5288, line : "libations dans le feu. Et tous étendirent les mains vers les mets"});
db.iliadfr.insert({ pg : 5289, line : "offerts. Et quand ils eurent assouvi la faim et la soif, Aias fit"});
db.iliadfr.insert({ pg : 5290, line : "signe à Phoinix. Aussitôt le divin Odysseus le comprit, et,"});
db.iliadfr.insert({ pg : 5291, line : "remplissant sa coupe de vin, il parla ainsi à Akhilleus:"});
db.iliadfr.insert({ pg : 5293, line : "-- Salut, Akhilleus! Aucun de nous n'a manqué d'une part égale,"});
db.iliadfr.insert({ pg : 5294, line : "soit sous la tente de l'Atréide Agamemnôn, soit ici. Les mets y"});
db.iliadfr.insert({ pg : 5295, line : "abondent également. Mais il ne nous est point permis de goûter la"});
db.iliadfr.insert({ pg : 5296, line : "joie des repas, car nous redoutons un grand désastre, ô race"});
db.iliadfr.insert({ pg : 5297, line : "divine! et nous l'attendons, et nous ne savons si nos nefs solides"});
db.iliadfr.insert({ pg : 5298, line : "périront ou seront sauvées, à moins que tu ne t'armes de ton"});
db.iliadfr.insert({ pg : 5299, line : "courage. Voici que les Troiens orgueilleux et leurs alliés venus"});
db.iliadfr.insert({ pg : 5300, line : "de loin ont assis leur camp devant nos murailles et nos nefs. Et"});
db.iliadfr.insert({ pg : 5301, line : "ils ont allumé des feux sans nombre, et ils disent que rien ne les"});
db.iliadfr.insert({ pg : 5302, line : "retiendra plus et qu'ils vont se jeter sur nos nefs noires. Et le"});
db.iliadfr.insert({ pg : 5303, line : "Kronide Zeus a lancé l'éclair, montrant à leur droite des signes"});
db.iliadfr.insert({ pg : 5304, line : "propices. Hektôr, appuyé par Zeus, et très orgueilleux de sa"});
db.iliadfr.insert({ pg : 5305, line : "force, est plein d'une fureur terrible, n'honorant plus ni les"});
db.iliadfr.insert({ pg : 5306, line : "hommes ni les dieux. Une rage s'est emparée de lui. Il fait des"});
db.iliadfr.insert({ pg : 5307, line : "imprécations pour que la divine Éôs reparaisse promptement. Il se"});
db.iliadfr.insert({ pg : 5308, line : "vante de rompre bientôt les éperons de nos nefs et de consumer"});
db.iliadfr.insert({ pg : 5309, line : "celles-ci dans le feu ardent, et de massacrer les Akhaiens"});
db.iliadfr.insert({ pg : 5310, line : "aveuglés par la fumée. Je crains bien, dans mon esprit, que les"});
db.iliadfr.insert({ pg : 5311, line : "dieux n'accomplissent ses menaces, et que nous périssions"});
db.iliadfr.insert({ pg : 5312, line : "inévitablement devant Troiè, loin de la fertile Argos nourrice de"});
db.iliadfr.insert({ pg : 5313, line : "chevaux. Lève-toi, si tu veux, au dernier moment, sauver les fils"});
db.iliadfr.insert({ pg : 5314, line : "des Akhaiens de la rage des Troiens. Sinon, tu seras saisi de"});
db.iliadfr.insert({ pg : 5315, line : "douleur, car il n'y a point de remède contre un mal accompli."});
db.iliadfr.insert({ pg : 5316, line : "Songe donc maintenant à reculer le dernier jour des Danaens. Ô"});
db.iliadfr.insert({ pg : 5317, line : "ami, ton père Pèleus te disait, le jour où il t'envoya, de la"});
db.iliadfr.insert({ pg : 5318, line : "Phthiè, vers Agamemnôn: -- Mon fils, Athènè et Hèrè te donneront"});
db.iliadfr.insert({ pg : 5319, line : "la victoire, s'il leur plaît; mais réprime ton grand coeur dans ta"});
db.iliadfr.insert({ pg : 5320, line : "poitrine, car la bienveillance est au-dessus de tout. Fuis la"});
db.iliadfr.insert({ pg : 5321, line : "discorde qui engendre les maux, afin que les Argiens, jeunes et"});
db.iliadfr.insert({ pg : 5322, line : "vieux, t'honorent.' Ainsi parlait le vieillard, et tu as oublié"});
db.iliadfr.insert({ pg : 5323, line : "ses paroles; mais aujourd'hui apaise-toi, refrène la colère qui"});
db.iliadfr.insert({ pg : 5324, line : "ronge le coeur, et Agamemnôn te fera des présents dignes de toi."});
db.iliadfr.insert({ pg : 5325, line : "Si tu veux m'écouter, je te dirai ceux qu'il promet de remettre"});
db.iliadfr.insert({ pg : 5326, line : "sous tes tentes: -- sept trépieds vierges du feu, dix talents"});
db.iliadfr.insert({ pg : 5327, line : "d'or, vingt bassins qu'on peut exposer à la flamme, douze chevaux"});
db.iliadfr.insert({ pg : 5328, line : "robustes qui ont toujours remporté les premiers prix par la"});
db.iliadfr.insert({ pg : 5329, line : "rapidité de leur course. Et il ne manquerait plus de rien, et il"});
db.iliadfr.insert({ pg : 5330, line : "serait comblé d'or, celui qui posséderait les prix qu'ont"});
db.iliadfr.insert({ pg : 5331, line : "rapportés à l'Atréide Agamemnôn ces chevaux aux sabots massifs. Et"});
db.iliadfr.insert({ pg : 5332, line : "il te donnera encore sept belles femmes Lesbiennes, habiles aux"});
db.iliadfr.insert({ pg : 5333, line : "travaux, que tu as prises toi-même dans Lesbos bien peuplée, et"});
db.iliadfr.insert({ pg : 5334, line : "qu'il a choisies, car elles étaient plus belles que toutes les"});
db.iliadfr.insert({ pg : 5335, line : "autres femmes. Et il te les donnera, et, avec elles, celle qu'il"});
db.iliadfr.insert({ pg : 5336, line : "t'a enlevée, la vierge Breisèis; et il jurera un grand serment"});
db.iliadfr.insert({ pg : 5337, line : "qu'elle n'a point connu son lit et qu'il l'a respectée. Toutes ces"});
db.iliadfr.insert({ pg : 5338, line : "choses te seront livrées aussitôt. Mais si les dieux nous donnent"});
db.iliadfr.insert({ pg : 5339, line : "de renverser la grande ville de Priamos, tu rempliras abondamment"});
db.iliadfr.insert({ pg : 5340, line : "ta nef d'or et d'airain. Et quand nous, Akhaiens, nous partagerons"});
db.iliadfr.insert({ pg : 5341, line : "la proie, tu choisiras vingt femmes Troiennes, les plus belles"});
db.iliadfr.insert({ pg : 5342, line : "après l'Argienne Hélénè. Et si nous retournons dans la fertile"});
db.iliadfr.insert({ pg : 5343, line : "Argos, en Akhaiè, tu seras son gendre, et il t'honorera autant"});
db.iliadfr.insert({ pg : 5344, line : "qu'Orestès, son unique fils nourri dans les délices. Il a trois"});
db.iliadfr.insert({ pg : 5345, line : "filles dans ses riches demeures: Krysothémis, Laodikè et"});
db.iliadfr.insert({ pg : 5346, line : "Iphianassa. Tu emmèneras, sans lui assurer une dot, celle que tu"});
db.iliadfr.insert({ pg : 5347, line : "aimeras le mieux, dans les demeures de Pèleus. Ce sera lui qui la"});
db.iliadfr.insert({ pg : 5348, line : "dotera comme jamais personne n'a doté sa fille, car il te donnera"});
db.iliadfr.insert({ pg : 5349, line : "sept villes très illustres: Kardamylè, Énopè, Hira aux prés"});
db.iliadfr.insert({ pg : 5350, line : "verdoyants, la divine Phèra, Anthéia aux gras pâturages, la belle"});
db.iliadfr.insert({ pg : 5351, line : "Aipéia et Pèdasos riche en vignes. Toutes sont aux bords de la"});
db.iliadfr.insert({ pg : 5352, line : "mer, auprès de la sablonneuse Pylos. Leurs habitants abondent en"});
db.iliadfr.insert({ pg : 5353, line : "boeufs et en troupeaux. Et, par leurs dons, ils t'honoreront comme"});
db.iliadfr.insert({ pg : 5354, line : "un dieu; et, sous ton sceptre, ils te payeront de riches tributs."});
db.iliadfr.insert({ pg : 5355, line : "Et il te donnera tout cela si tu déposes ta colère. Mais si"});
db.iliadfr.insert({ pg : 5356, line : "l'Atréide et ses présents te sont odieux, aie pitié du moins des"});
db.iliadfr.insert({ pg : 5357, line : "Panakhaiens accablés de douleur dans leur camp et qui t'honoreront"});
db.iliadfr.insert({ pg : 5358, line : "comme un dieu. Certes, tu leur devras une grande gloire, et tu"});
db.iliadfr.insert({ pg : 5359, line : "tueras Hektôr qui viendra à ta rencontre et qui se vante que nul"});
db.iliadfr.insert({ pg : 5360, line : "ne peut se comparer à lui de tous les Danaens que les nefs ont"});
db.iliadfr.insert({ pg : 5361, line : "apportés ici."});
db.iliadfr.insert({ pg : 5363, line : "Et Akhilleus aux pieds rapides lui répondit:"});
db.iliadfr.insert({ pg : 5365, line : "-- Divin Laertiade, très subtil Odysseus, il faut que je dise"});
db.iliadfr.insert({ pg : 5366, line : "clairement ce que j'ai résolu et ce qui s'accomplira, afin que"});
db.iliadfr.insert({ pg : 5367, line : "vous n'insistiez pas tour à tour. Celui qui cache sa pensée dans"});
db.iliadfr.insert({ pg : 5368, line : "son âme et ne dit point la vérité m'est plus odieux que le seuil"});
db.iliadfr.insert({ pg : 5369, line : "d'Aidès. Je dirai donc ce qui me semble préférable. Ni l'Atréide"});
db.iliadfr.insert({ pg : 5370, line : "Agamemnôn, ni les autres Danaens ne me persuaderont, puisqu'il ne"});
db.iliadfr.insert({ pg : 5371, line : "m'a servi à rien de combattre sans relâche les guerriers ennemis."});
db.iliadfr.insert({ pg : 5372, line : "Celui qui reste au camp et celui qui combat avec courage ont une"});
db.iliadfr.insert({ pg : 5373, line : "même part. Le lâche et le brave remportent le même honneur, et"});
db.iliadfr.insert({ pg : 5374, line : "l'homme oisif est tué comme celui qui agit. Rien ne m'est resté"});
db.iliadfr.insert({ pg : 5375, line : "d'avoir souffert des maux sans nombre et d'avoir exposé mon âme en"});
db.iliadfr.insert({ pg : 5376, line : "combattant. Comme l'oiseau qui porte à ses petits sans plume la"});
db.iliadfr.insert({ pg : 5377, line : "nourriture qu'il a ramassée et dont il n'a rien gardé pour lui-"});
db.iliadfr.insert({ pg : 5378, line : "même, j'ai passé sans sommeil d'innombrables nuits, j'ai lutté"});
db.iliadfr.insert({ pg : 5379, line : "contre les hommes pendant des journées sanglantes, pour la cause"});
db.iliadfr.insert({ pg : 5380, line : "de vos femmes; j'ai dévasté, à l'aide de mes nefs, douze villes,"});
db.iliadfr.insert({ pg : 5381, line : "demeures des hommes; sur terre, j'en ai pris onze autour de la"});
db.iliadfr.insert({ pg : 5382, line : "fertile Ilios; j'ai rapporté de toutes ces villes mille choses"});
db.iliadfr.insert({ pg : 5383, line : "précieuses et superbes, et j'ai tout donné à l'Atréide Agamemnôn,"});
db.iliadfr.insert({ pg : 5384, line : "tandis qu'assis auprès des nefs rapides, il n'en distribuait"});
db.iliadfr.insert({ pg : 5385, line : "qu'une moindre part aux rois et aux chefs et se réservait la plus"});
db.iliadfr.insert({ pg : 5386, line : "grande. Du moins ceux-ci ont gardé ce qu'il leur a donné; mais, de"});
db.iliadfr.insert({ pg : 5387, line : "tous les Akhaiens, à moi seul il m'a enlevé ma récompense! Qu'il"});
db.iliadfr.insert({ pg : 5388, line : "se réjouisse donc de cette femme et qu'il en jouisse! Pourquoi les"});
db.iliadfr.insert({ pg : 5389, line : "Argiens combattent-ils les Troiens? Pourquoi les Atréides ont-ils"});
db.iliadfr.insert({ pg : 5390, line : "conduit ici cette nombreuse armée? N'est-ce point pour la cause de"});
db.iliadfr.insert({ pg : 5391, line : "Hélénè à la belle chevelure? Sont-ils les seuls de tous les hommes"});
db.iliadfr.insert({ pg : 5392, line : "qui aiment leurs femmes? Tout homme sage et bon aime la sienne et"});
db.iliadfr.insert({ pg : 5393, line : "en prend soin. Et moi aussi, j'aimais celle-ci dans mon coeur,"});
db.iliadfr.insert({ pg : 5394, line : "bien que captive. Maintenant que, de ses mains, il m'a arraché ma"});
db.iliadfr.insert({ pg : 5395, line : "récompense, et qu'il m'a volé, il ne me persuadera, ni ne me"});
db.iliadfr.insert({ pg : 5396, line : "trompera plus, car je suis averti. Qu'il délibère avec toi, ô"});
db.iliadfr.insert({ pg : 5397, line : "Odysseus, et avec les autres rois, afin d'éloigner des nefs la"});
db.iliadfr.insert({ pg : 5398, line : "flamme ardente. Déjà il a fait sans moi de nombreux travaux; il a"});
db.iliadfr.insert({ pg : 5399, line : "construit un mur et creusé un fossé profond et large, défendu par"});
db.iliadfr.insert({ pg : 5400, line : "des pieux. Mais il n'en a pas réprimé davantage la violence du"});
db.iliadfr.insert({ pg : 5401, line : "tueur d'hommes Hektôr. Quand je combattais au milieu des Akhaiens,"});
db.iliadfr.insert({ pg : 5402, line : "Hektôr ne sortait que rarement de ses murailles. À peine se"});
db.iliadfr.insert({ pg : 5403, line : "hasardait-il devant les portes Skaies et auprès du hêtre. Et il"});
db.iliadfr.insert({ pg : 5404, line : "m'y attendit une fois, et à peine put-il échapper à mon"});
db.iliadfr.insert({ pg : 5405, line : "impétuosité. Maintenant, puisque je ne veux plus combattre le"});
db.iliadfr.insert({ pg : 5406, line : "divin Hektôr, demain, ayant sacrifié à Zeus et à tous les dieux,"});
db.iliadfr.insert({ pg : 5407, line : "je traînerai à la mer mes nefs chargées; et tu verras, si tu le"});
db.iliadfr.insert({ pg : 5408, line : "veux et si tu t'en soucies, mes nefs voguer, dès le matin, sur le"});
db.iliadfr.insert({ pg : 5409, line : "Hellespontos poissonneux, sous l'effort vigoureux des rameurs. Et"});
db.iliadfr.insert({ pg : 5410, line : "si l'illustre qui entoure la terre me donne une heureuse"});
db.iliadfr.insert({ pg : 5411, line : "navigation, le troisième jour j'arriverai dans la fertile Phthiè,"});
db.iliadfr.insert({ pg : 5412, line : "où sont les richesses que j'y ai laissées quand je vins ici pour"});
db.iliadfr.insert({ pg : 5413, line : "mon malheur. Et j'y conduirai l'or et le rouge airain, et les"});
db.iliadfr.insert({ pg : 5414, line : "belles femmes et le fer luisant que le sort m'a accordés, car le"});
db.iliadfr.insert({ pg : 5415, line : "roi Atréide Agamemnôn m'a arraché la récompense qu'il m'avait"});
db.iliadfr.insert({ pg : 5416, line : "donnée. Et répète-lui ouvertement ce que je dis, afin que les"});
db.iliadfr.insert({ pg : 5417, line : "Akhaiens s'indignent, s'il espère tromper de nouveau quelqu'autre"});
db.iliadfr.insert({ pg : 5418, line : "des Danaens. Mais, bien qu'il ait l'impudence d'un chien, il"});
db.iliadfr.insert({ pg : 5419, line : "n'oserait me regarder en face. Je ne veux plus ni délibérer, ni"});
db.iliadfr.insert({ pg : 5420, line : "agir avec lui, car il m'a trompé et outragé. C'est assez. Mais"});
db.iliadfr.insert({ pg : 5421, line : "qu'il reste en repos dans sa méchanceté, car le très sage Zeus lui"});
db.iliadfr.insert({ pg : 5422, line : "a ravi l'esprit. Ses dons me sont odieux, et lui, je l'honore"});
db.iliadfr.insert({ pg : 5423, line : "autant que la demeure d'Aidès. Et il me donnerait dix et vingt"});
db.iliadfr.insert({ pg : 5424, line : "fois plus de richesses qu'il n'en a et qu'il n'en aura, qu'il n'en"});
db.iliadfr.insert({ pg : 5425, line : "vient d'Orkhoménos, ou de Thèba dans l'Aigyptia, où les trésors"});
db.iliadfr.insert({ pg : 5426, line : "abondent dans les demeures, qui a cent portes, et qui, par"});
db.iliadfr.insert({ pg : 5427, line : "chacune, voit sortir deux cents guerriers avec chevaux et chars;"});
db.iliadfr.insert({ pg : 5428, line : "et il me ferait autant de présents qu'il y a de grains de sable et"});
db.iliadfr.insert({ pg : 5429, line : "de poussière, qu'il n'apaiserait point mon coeur avant d'avoir"});
db.iliadfr.insert({ pg : 5430, line : "expié l'outrage sanglant qu'il m'a fait. Et je ne prendrai point"});
db.iliadfr.insert({ pg : 5431, line : "pour femme légitime la fille de l'Atréide Agamemnôn, fût-elle plus"});
db.iliadfr.insert({ pg : 5432, line : "belle qu'Aphroditè d'or et plus habile aux travaux qu'Athènè aux"});
db.iliadfr.insert({ pg : 5433, line : "yeux clairs. Je ne la prendrai point pour femme légitime. Qu'il"});
db.iliadfr.insert({ pg : 5434, line : "choisisse un autre Akhaien qui lui plaise et qui soit un roi plus"});
db.iliadfr.insert({ pg : 5435, line : "puissant. Si les dieux me gardent, et si je rentre dans ma"});
db.iliadfr.insert({ pg : 5436, line : "demeure, Pèleus me choisira lui-même une femme légitime. Il y a,"});
db.iliadfr.insert({ pg : 5437, line : "dans l'Akhaiè, la Hellas et la Phthiè, de nombreuses jeunes filles"});
db.iliadfr.insert({ pg : 5438, line : "de chefs guerriers qui défendent les citadelles, et je ferai de"});
db.iliadfr.insert({ pg : 5439, line : "l'une d'elles ma femme légitime bien-aimée. Et mon coeur généreux"});
db.iliadfr.insert({ pg : 5440, line : "me pousse à prendre une femme légitime et à jouir des biens acquis"});
db.iliadfr.insert({ pg : 5441, line : "par le vieillard Pèleus. Toutes les richesses que renfermait la"});
db.iliadfr.insert({ pg : 5442, line : "grande Ilios aux nombreux habitants pendant la paix, avant la"});
db.iliadfr.insert({ pg : 5443, line : "venue des fils des Akhaiens, ne sont point d'un prix égal à la"});
db.iliadfr.insert({ pg : 5444, line : "vie, non plus que celles que renferme le sanctuaire de pierre de"});
db.iliadfr.insert({ pg : 5445, line : "l'archer Phoibos Apollôn, dans l'âpre Pythô. Les boeufs, les"});
db.iliadfr.insert({ pg : 5446, line : "grasses brebis, les trépieds, les blondes crinières des chevaux,"});
db.iliadfr.insert({ pg : 5447, line : "tout cela peut être conquis; mais l'âme qui s'est une fois"});
db.iliadfr.insert({ pg : 5448, line : "échappée d'entre nos dents ne peut être ressaisie ni rappelée. Ma"});
db.iliadfr.insert({ pg : 5449, line : "mère, la déesse Thétis aux pieds d'argent, m'a dit que deux kères"});
db.iliadfr.insert({ pg : 5450, line : "m'étaient offertes pour arriver à la mort. Si je reste et si je"});
db.iliadfr.insert({ pg : 5451, line : "combats autour de la ville des Troiens, je ne retournerai jamais"});
db.iliadfr.insert({ pg : 5452, line : "dans mes demeures, mais ma gloire sera immortelle. Si je retourne"});
db.iliadfr.insert({ pg : 5453, line : "vers ma demeure, dans la terre bien-aimée de ma patrie, je perdrai"});
db.iliadfr.insert({ pg : 5454, line : "toute gloire, mais je vivrai très vieux, et la mort ne me saisira"});
db.iliadfr.insert({ pg : 5455, line : "qu'après de très longues années. Je conseille à tous les Akhaiens"});
db.iliadfr.insert({ pg : 5456, line : "de retourner vers leurs demeures, car vous ne verrez jamais le"});
db.iliadfr.insert({ pg : 5457, line : "dernier jour de la haute Ilios. Zeus qui tonne puissamment la"});
db.iliadfr.insert({ pg : 5458, line : "protège de ses mains et a rempli son peuple d'une grande audace."});
db.iliadfr.insert({ pg : 5459, line : "Pour vous, allez porter ma réponse aux chefs des Akhaiens, car"});
db.iliadfr.insert({ pg : 5460, line : "c'est là le partage des anciens; et ils chercheront dans leur"});
db.iliadfr.insert({ pg : 5461, line : "esprit un meilleur moyen de sauver les nefs et les tribus"});
db.iliadfr.insert({ pg : 5462, line : "Akhaiennes, car ma colère rend inutile celui qu'ils avaient"});
db.iliadfr.insert({ pg : 5463, line : "trouvé. Et Phoinix restera et couchera ici, afin de me suivre"});
db.iliadfr.insert({ pg : 5464, line : "demain, sur mes nefs, dans notre patrie, s'il le désire, du moins,"});
db.iliadfr.insert({ pg : 5465, line : "car je ne le contraindrai point."});
db.iliadfr.insert({ pg : 5467, line : "Il parla ainsi, et tous restèrent muets, accablés de ce discours"});
db.iliadfr.insert({ pg : 5468, line : "et de ce dur refus. Enfin, le vieux cavalier Phoinix parla ainsi,"});
db.iliadfr.insert({ pg : 5469, line : "versant des larmes, tant il craignait pour les nefs des Akhaiens:"});
db.iliadfr.insert({ pg : 5471, line : "-- Si déjà tu as résolu ton retour, illustre Akhilleus, et si tu"});
db.iliadfr.insert({ pg : 5472, line : "refuses d'éloigner des nefs rapides la violence du feu"});
db.iliadfr.insert({ pg : 5473, line : "destructeur, parce que la colère est tombée dans ton coeur,"});
db.iliadfr.insert({ pg : 5474, line : "comment, cher fils, pourrai-je t'abandonner et rester seul ici? Le"});
db.iliadfr.insert({ pg : 5475, line : "vieux cavalier Pèleus m'ordonna de t'accompagner le jour où il"});
db.iliadfr.insert({ pg : 5476, line : "t'envoya, loin de la Phthiè, vers Agamemnôn, tout jeune encore,"});
db.iliadfr.insert({ pg : 5477, line : "ignorant la guerre lamentable et l'agora où les hommes deviennent"});
db.iliadfr.insert({ pg : 5478, line : "illustres. Et il m'ordonna de t'accompagner afin que je pusse"});
db.iliadfr.insert({ pg : 5479, line : "t'enseigner à parler et à agir. C'est pourquoi je ne veux point me"});
db.iliadfr.insert({ pg : 5480, line : "séparer de toi, cher fils, même quand un dieu me promettrait de"});
db.iliadfr.insert({ pg : 5481, line : "m'épargner la vieillesse et me rendrait à ma jeunesse florissante,"});
db.iliadfr.insert({ pg : 5482, line : "tel que j'étais quand je quittai pour la première fois la Hellas"});
db.iliadfr.insert({ pg : 5483, line : "aux belles femmes, fuyant la colère de mon père Amyntôr Orménide."});
db.iliadfr.insert({ pg : 5484, line : "Et il s'était irrité contre moi à cause de sa concubine aux beaux"});
db.iliadfr.insert({ pg : 5485, line : "cheveux qu'il aimait et pour laquelle il méprisait sa femme"});
db.iliadfr.insert({ pg : 5486, line : "légitime, ma mère. Et celle-ci me suppliait toujours, à genoux, de"});
db.iliadfr.insert({ pg : 5487, line : "séduire cette concubine, pour que le vieillard la prît en haine."});
db.iliadfr.insert({ pg : 5488, line : "Et je lui obéis, et mon père, s'en étant aperçu, se répandit en"});
db.iliadfr.insert({ pg : 5489, line : "imprécations, et supplia les odieuses Erinnyes, leur demandant que"});
db.iliadfr.insert({ pg : 5490, line : "je ne sentisse jamais sur mes genoux un fils bien-aimé, né de moi;"});
db.iliadfr.insert({ pg : 5491, line : "et les dieux, Zeus le souterrain et la cruelle Perséphonéia"});
db.iliadfr.insert({ pg : 5492, line : "accomplirent ses imprécations. Alors je ne pus me résoudre dans"});
db.iliadfr.insert({ pg : 5493, line : "mon âme à rester dans les demeures de mon père irrité. Et de"});
db.iliadfr.insert({ pg : 5494, line : "nombreux amis et parents, venus de tous côtés, me retinrent. Et"});
db.iliadfr.insert({ pg : 5495, line : "ils tuèrent beaucoup de grasses brebis et de boeufs noirs aux"});
db.iliadfr.insert({ pg : 5496, line : "pieds lents; et ils passèrent à l'ardeur du feu les porcs lourds"});
db.iliadfr.insert({ pg : 5497, line : "de graisse, et ils burent, par grandes cruches, le vin du"});
db.iliadfr.insert({ pg : 5498, line : "vieillard. Et pendant neuf nuits ils dormirent autour de moi, et"});
db.iliadfr.insert({ pg : 5499, line : "chacun me gardait tour à tour. L'un se tenait sous le portique de"});
db.iliadfr.insert({ pg : 5500, line : "la cour, l'autre dans le vestibule de la salle bien fermée. Et le"});
db.iliadfr.insert({ pg : 5501, line : "feu ne s'éteignait jamais. Mais, dans l'obscurité de la dixième"});
db.iliadfr.insert({ pg : 5502, line : "nuit, ayant rompu les portes de la salle, j'échappai facilement à"});
db.iliadfr.insert({ pg : 5503, line : "mes gardiens et aux serviteurs, et je m'enfuis loin de la grande"});
db.iliadfr.insert({ pg : 5504, line : "Hellas, et j'arrivai dans la fertile Phthiè, nourrice de brebis,"});
db.iliadfr.insert({ pg : 5505, line : "auprès du roi Pèleus. Et il me reçut avec bienveillance, et il"});
db.iliadfr.insert({ pg : 5506, line : "m'aima comme un père aime un fils unique, né dans son extrême"});
db.iliadfr.insert({ pg : 5507, line : "vieillesse, au milieu de ses domaines. Et il me fit riche, et il"});
db.iliadfr.insert({ pg : 5508, line : "me donna à gouverner un peuple, aux confins de la Phthiè, et je"});
db.iliadfr.insert({ pg : 5509, line : "commandai aux Dolopiens. Et je t'ai aimé de même dans mon coeur, ô"});
db.iliadfr.insert({ pg : 5510, line : "Akhilleus égal aux dieux. Et tu ne voulais t'asseoir aux repas et"});
db.iliadfr.insert({ pg : 5511, line : "manger dans tes demeures qu'assis sur mes genoux, et rejetant"});
db.iliadfr.insert({ pg : 5512, line : "parfois le vin et les mets dont tu étais rassasié, sur ma poitrine"});
db.iliadfr.insert({ pg : 5513, line : "et ma tunique, comme font les petits enfants. Et j'ai beaucoup"});
db.iliadfr.insert({ pg : 5514, line : "souffert et beaucoup travaillé pour toi, pensant que, si les dieux"});
db.iliadfr.insert({ pg : 5515, line : "m'avaient refusé une postérité, je t'adopterais pour fils, ô"});
db.iliadfr.insert({ pg : 5516, line : "Akhilleus semblable aux dieux, afin que tu pusses un jour me"});
db.iliadfr.insert({ pg : 5517, line : "défendre des outrages et de la mort. Ô Akhilleus, apaise ta grande"});
db.iliadfr.insert({ pg : 5518, line : "âme, car il ne te convient pas d 'avoir un coeur sans pitié. Les"});
db.iliadfr.insert({ pg : 5519, line : "dieux eux-mêmes sont exorables, bien qu'ils n'aient point d'égaux"});
db.iliadfr.insert({ pg : 5520, line : "en vertu, en honneurs et en puissance; et les hommes les"});
db.iliadfr.insert({ pg : 5521, line : "fléchissent cependant par les prières, par les voeux, par les"});
db.iliadfr.insert({ pg : 5522, line : "libations et par l'odeur des sacrifices, quand ils les ont"});
db.iliadfr.insert({ pg : 5523, line : "offensés en leur désobéissant. Les prières, filles du grand Zeus,"});
db.iliadfr.insert({ pg : 5524, line : "boiteuses, ridées et louches, suivent à grand'peine Atè. Et celle-"});
db.iliadfr.insert({ pg : 5525, line : "ci, douée de force et de rapidité, les précède de très loin et"});
db.iliadfr.insert({ pg : 5526, line : "court sur la face de la terre en maltraitant les hommes. Et les"});
db.iliadfr.insert({ pg : 5527, line : "prières la suivent, en guérissant les maux qu'elle a faits,"});
db.iliadfr.insert({ pg : 5528, line : "secourant et exauçant celui qui les vénère, elles qui sont filles"});
db.iliadfr.insert({ pg : 5529, line : "de Zeus. Mais elles supplient Zeus Kroniôn de faire poursuivre et"});
db.iliadfr.insert({ pg : 5530, line : "châtier par Atè celui qui les repousse et les renie. C'est"});
db.iliadfr.insert({ pg : 5531, line : "pourquoi, ô Akhilleus, rends aux filles de Zeus l'honneur qui"});
db.iliadfr.insert({ pg : 5532, line : "fléchit l'âme des plus braves. Si l'Atréide ne t'offrait point de"});
db.iliadfr.insert({ pg : 5533, line : "présents, s'il ne t'en annonçait point d'autres encore, s'il"});
db.iliadfr.insert({ pg : 5534, line : "gardait sa colère, je ne t'exhorterais point à déposer la tienne,"});
db.iliadfr.insert({ pg : 5535, line : "et à secourir les Argiens qui, cependant, désespèrent du salut."});
db.iliadfr.insert({ pg : 5536, line : "Mais voici qu'il t'offre dès aujourd'hui de nombreux présents et"});
db.iliadfr.insert({ pg : 5537, line : "qu'il t'en annonce d'autres encore, et qu'il t'envoie, en"});
db.iliadfr.insert({ pg : 5538, line : "suppliants, les premiers chefs de l'armée Akhaienne, ceux qui te"});
db.iliadfr.insert({ pg : 5539, line : "sont chers entre tous les Argiens. Ne méprise donc point leurs"});
db.iliadfr.insert({ pg : 5540, line : "paroles, afin que nous ne blâmions point la colère que tu"});
db.iliadfr.insert({ pg : 5541, line : "ressentais; car nous avons appris que les anciens héros qu'une"});
db.iliadfr.insert({ pg : 5542, line : "violente colère avait saisis se laissaient fléchir par des"});
db.iliadfr.insert({ pg : 5543, line : "présents et par des paroles pacifiques. Je me souviens d'une"});
db.iliadfr.insert({ pg : 5544, line : "histoire antique. Certes, elle n'est point récente. Amis, je vous"});
db.iliadfr.insert({ pg : 5545, line : "la dirai: les Kourètes combattaient les Aitôliens belliqueux,"});
db.iliadfr.insert({ pg : 5546, line : "autour de la ville de Kalidôn; et les Kourètes voulaient la"});
db.iliadfr.insert({ pg : 5547, line : "saccager. Et Artémis au siège d'or avait attiré cette calamité sur"});
db.iliadfr.insert({ pg : 5548, line : "les Aitôliens, irritée qu'elle était de ce qu'Oineus ne lui eût"});
db.iliadfr.insert({ pg : 5549, line : "point offert de prémices dans ses grasses prairies. Tous les dieux"});
db.iliadfr.insert({ pg : 5550, line : "avaient joui de ses hécatombes; mais, oublieux ou imprudent, il"});
db.iliadfr.insert({ pg : 5551, line : "n'avait point sacrifié à la seule fille du grand Zeus, ce qui"});
db.iliadfr.insert({ pg : 5552, line : "causa des maux amers; car, dans sa colère, la race divine qui se"});
db.iliadfr.insert({ pg : 5553, line : "réjouit de ses flèches suscita un sanglier sauvage, aux blanches"});
db.iliadfr.insert({ pg : 5554, line : "défenses, qui causa des maux innombrables, dévasta les champs"});
db.iliadfr.insert({ pg : 5555, line : "d'Oineus et arracha de grands arbres, avec racines et fleurs."});
db.iliadfr.insert({ pg : 5557, line : "Et le fils d'Oineus, Méléagros, tua ce sanglier, après avoir"});
db.iliadfr.insert({ pg : 5558, line : "appelé, des villes prochaines, des hommes chasseurs et des chiens."});
db.iliadfr.insert({ pg : 5559, line : "Et cette bête sauvage ne fut point domptée par peu de chasseurs,"});
db.iliadfr.insert({ pg : 5560, line : "et elle en fit monter plusieurs sur le bûcher. Mais Artémis excita"});
db.iliadfr.insert({ pg : 5561, line : "la discorde et la guerre entre les Kourètes et les magnanimes"});
db.iliadfr.insert({ pg : 5562, line : "Aitôliens, à cause de la hure du sanglier et de sa dépouille"});
db.iliadfr.insert({ pg : 5563, line : "hérissée. Aussi longtemps que Méléagros cher à Arès combattit, les"});
db.iliadfr.insert({ pg : 5564, line : "Kourètes, vaincus, ne purent rester hors de leurs murailles; mais"});
db.iliadfr.insert({ pg : 5565, line : "la colère, qui trouble l'esprit des plus sages, envahit l'âme de"});
db.iliadfr.insert({ pg : 5566, line : "Méléagros, et irrité dans son coeur contre sa mère Althaiè, il"});
db.iliadfr.insert({ pg : 5567, line : "resta inactif auprès de sa femme légitime, la belle Kléopatrè,"});
db.iliadfr.insert({ pg : 5568, line : "fille de la vierge Marpissè Événide et d'Idaios, le plus brave des"});
db.iliadfr.insert({ pg : 5569, line : "hommes qui fussent alors sur la terre. Et celui-ci avait tendu son"});
db.iliadfr.insert({ pg : 5570, line : "arc contre le roi Phoibos Apollôn, à cause de la belle nymphe"});
db.iliadfr.insert({ pg : 5571, line : "Marpissè. Et le père et la mère vénérable de Kléopatrè l'avaient"});
db.iliadfr.insert({ pg : 5572, line : "surnommée Alkyonè, parce que la mère d' Alkyôn avait gémi"});
db.iliadfr.insert({ pg : 5573, line : "amèrement quand l' archer Phoibos Apollôn la ravit. Et Méléagros"});
db.iliadfr.insert({ pg : 5574, line : "restait auprès de Kléopatrè, couvant une ardente colère dans son"});
db.iliadfr.insert({ pg : 5575, line : "coeur, à cause des imprécations de sa mère qui suppliait en"});
db.iliadfr.insert({ pg : 5576, line : "gémissant les dieux de venger le meurtre fraternel. Et, les genoux"});
db.iliadfr.insert({ pg : 5577, line : "ployés, le sein baigné de pleurs, frappant de ses mains la terre"});
db.iliadfr.insert({ pg : 5578, line : "nourricière, elle conjurait Aidès et la cruelle Perséphonéia de"});
db.iliadfr.insert({ pg : 5579, line : "donner la mort à son fils Méléagros. Et Érinnys à l'âme"});
db.iliadfr.insert({ pg : 5580, line : "implacable, qui erre dans la nuit, l'entendit du fond de l'Érébos."});
db.iliadfr.insert({ pg : 5581, line : "Et les Kourètes se ruèrent, en fureur et en tumulte, contre les"});
db.iliadfr.insert({ pg : 5582, line : "portes de la ville, et ils heurtaient les tours. Et les vieillards"});
db.iliadfr.insert({ pg : 5583, line : "Aitôliens supplièrent Méléagros; et ils lui envoyèrent les sacrés"});
db.iliadfr.insert({ pg : 5584, line : "sacrificateurs des dieux, afin qu'il sortît et secourût les siens."});
db.iliadfr.insert({ pg : 5585, line : "Et ils lui offrirent un très riche présent, lui disant de choisir"});
db.iliadfr.insert({ pg : 5586, line : "le plus fertile et le plus beau domaine de l'heureuse Kalydôn,"});
db.iliadfr.insert({ pg : 5587, line : "vaste de cinquante arpents, moitié en vignes, moitié en terres"});
db.iliadfr.insert({ pg : 5588, line : "arables. Et le vieux cavalier Oineus le suppliait, debout sur le"});
db.iliadfr.insert({ pg : 5589, line : "seuil élevé de la chambre nuptiale et frappant les portes"});
db.iliadfr.insert({ pg : 5590, line : "massives. Et ses soeurs et sa mère vénérable le suppliaient aussi;"});
db.iliadfr.insert({ pg : 5591, line : "mais il ne les écoutait point, non plus que ses plus chers"});
db.iliadfr.insert({ pg : 5592, line : "compagnons, et ils ne pouvaient apaiser son coeur. Mais déjà les"});
db.iliadfr.insert({ pg : 5593, line : "Kourètes escaladaient les tours, incendiaient la ville et"});
db.iliadfr.insert({ pg : 5594, line : "approchaient de la chambre nuptiale. Alors, la belle jeune femme"});
db.iliadfr.insert({ pg : 5595, line : "le supplia à son tour, et elle lui rappela les calamités qui"});
db.iliadfr.insert({ pg : 5596, line : "accablent les habitants d'une ville prise d'assaut: les hommes"});
db.iliadfr.insert({ pg : 5597, line : "tués, les demeures réduites en cendre, les enfants et les jeunes"});
db.iliadfr.insert({ pg : 5598, line : "femmes emmenés. Et enfin son âme fut ébranlée au tableau de ces"});
db.iliadfr.insert({ pg : 5599, line : "misères. Et il se leva, revêtit ses armes éclatantes, et recula le"});
db.iliadfr.insert({ pg : 5600, line : "dernier jour des Aitôliens, car il avait déposé sa colère. Et ils"});
db.iliadfr.insert({ pg : 5601, line : "ne lui firent point de nombreux et riches présents, et cependant"});
db.iliadfr.insert({ pg : 5602, line : "il les sauva ainsi. Mais ne songe point à ces choses, ami, et"});
db.iliadfr.insert({ pg : 5603, line : "qu'un dieu contraire ne te détermine point à faire de même. Il"});
db.iliadfr.insert({ pg : 5604, line : "serait plus honteux pour toi de ne secourir les nefs que lorsqu'"});
db.iliadfr.insert({ pg : 5605, line : "elles seront en flammes. Viens! reçois ces présents, et les"});
db.iliadfr.insert({ pg : 5606, line : "Akhaiens t'honoreront comme un dieu. Si tu combattais plus tard,"});
db.iliadfr.insert({ pg : 5607, line : "sans accepter ces dons, tu serais moins honoré, même si tu"});
db.iliadfr.insert({ pg : 5608, line : "repoussais le danger loin des nefs."});
db.iliadfr.insert({ pg : 5610, line : "Et Akhilleus aux pieds rapides lui répondit:"});
db.iliadfr.insert({ pg : 5612, line : "-- Ô Phoinix, père divin et vénérable, je n'ai nul besoin"});
db.iliadfr.insert({ pg : 5613, line : "d'honneurs. Je suis assez honoré par la volonté de Zeus qui me"});
db.iliadfr.insert({ pg : 5614, line : "retient auprès de mes nefs aux poupes recourbées, et je le serai"});
db.iliadfr.insert({ pg : 5615, line : "tant qu'il y aura un souffle dans ma poitrine et que mes genoux"});
db.iliadfr.insert({ pg : 5616, line : "pourront se mouvoir. Mais je te le dis, garde mes paroles dans ton"});
db.iliadfr.insert({ pg : 5617, line : "esprit: Ne trouble point mon coeur, en pleurant et en gémissant, à"});
db.iliadfr.insert({ pg : 5618, line : "cause du héros Atréide, car il ne te convient point de l'aimer, à"});
db.iliadfr.insert({ pg : 5619, line : "moins de me devenir odieux, à moi qui t'aime. Il est juste que tu"});
db.iliadfr.insert({ pg : 5620, line : "haïsses celui qui me hait. Règne avec moi et défends ta part de"});
db.iliadfr.insert({ pg : 5621, line : "mon honneur. Ceux-ci vont partir, et tu resteras ici, couché sur"});
db.iliadfr.insert({ pg : 5622, line : "un lit moelleux; et, aux premières lueurs d'Éôs, nous délibérerons"});
db.iliadfr.insert({ pg : 5623, line : "s'il nous faut retourner vers notre patrie, ou rester."});
db.iliadfr.insert({ pg : 5625, line : "Il parla, et, de ses sourcils, il fit signe à Patroklos, afin que"});
db.iliadfr.insert({ pg : 5626, line : "celui-ci préparât le lit épais de Phoinix et que les envoyés"});
db.iliadfr.insert({ pg : 5627, line : "sortissent promptement de la tente. Mais le Télamônien Aias,"});
db.iliadfr.insert({ pg : 5628, line : "semblable à un dieu, parla ainsi:"});
db.iliadfr.insert({ pg : 5630, line : "-- Divin Laertiade, très subtil Odysseus, allons-nous-en! Ces"});
db.iliadfr.insert({ pg : 5631, line : "discours n' auront point de fin, et il nous faut rapporter"});
db.iliadfr.insert({ pg : 5632, line : "promptement une réponse, bien que mauvaise, aux Danaens qui nous"});
db.iliadfr.insert({ pg : 5633, line : "attendent. Akhilleus garde une colère orgueilleuse dans son coeur"});
db.iliadfr.insert({ pg : 5634, line : "implacable. Dur, il se soucie peu de l'amitié de ses compagnons"});
db.iliadfr.insert({ pg : 5635, line : "qui l'honorent entre tous auprès des nefs. Ô inexorable!"});
db.iliadfr.insert({ pg : 5636, line : "n'accepte-t-on point le prix du meurtre d'un frère ou d'un fils?"});
db.iliadfr.insert({ pg : 5637, line : "Et celui qui a tué reste au milieu de son peuple, dès qu'il a"});
db.iliadfr.insert({ pg : 5638, line : "expié son crime, et son ennemi, satisfait, s'apaise. Les dieux ont"});
db.iliadfr.insert({ pg : 5639, line : "allumé dans ta poitrine une sombre et inextinguible colère, à"});
db.iliadfr.insert({ pg : 5640, line : "cause d'une seule jeune fille, quand nous t'en offrons sept très"});
db.iliadfr.insert({ pg : 5641, line : "belles et un grand nombre d'autres présents. C'est pourquoi,"});
db.iliadfr.insert({ pg : 5642, line : "prends un esprit plus doux, et respecte ta demeure, puisque nous"});
db.iliadfr.insert({ pg : 5643, line : "sommes tes hôtes domestiques envoyés par la foule des Danaens, et"});
db.iliadfr.insert({ pg : 5644, line : "que nous désirons être les plus chers de tes amis, entre tous les"});
db.iliadfr.insert({ pg : 5645, line : "Akhaiens."});
db.iliadfr.insert({ pg : 5647, line : "Et Akhilleus aux pieds rapides lui répondit:"});
db.iliadfr.insert({ pg : 5649, line : "-- Divin Aias Télamônien, prince des peuples, ce que tu as dit est"});
db.iliadfr.insert({ pg : 5650, line : "sage, mais mon coeur se gonfle de colère quand je songe à"});
db.iliadfr.insert({ pg : 5651, line : "l'Atréide qui m'a outragé au milieu des Danaens, comme il eût fait"});
db.iliadfr.insert({ pg : 5652, line : "d'un misérable. Allez donc, et rapportez votre message. Je ne me"});
db.iliadfr.insert({ pg : 5653, line : "soucierai plus de la guerre sanglante avant que le divin Hektôr,"});
db.iliadfr.insert({ pg : 5654, line : "le fils du brave Priamos, ne soit parvenu jusqu'aux tentes et aux"});
db.iliadfr.insert({ pg : 5655, line : "nefs des Myrmidones, après avoir massacré les Argiens et incendié"});
db.iliadfr.insert({ pg : 5656, line : "leurs nefs. C'est devant ma tente et ma nef noire que je"});
db.iliadfr.insert({ pg : 5657, line : "repousserai le furieux Hektôr loin de la mêlée."});
db.iliadfr.insert({ pg : 5659, line : "Il parla ainsi. Et chacun, ayant saisi une coupe profonde, fit ses"});
db.iliadfr.insert({ pg : 5660, line : "libations, et ils s'en retournèrent vers les nefs, et Odysseus les"});
db.iliadfr.insert({ pg : 5661, line : "conduisait."});
db.iliadfr.insert({ pg : 5663, line : "Et Patroklos commanda à ses compagnons et aux servantes de"});
db.iliadfr.insert({ pg : 5664, line : "préparer promptement le lit épais de Phoinix. Et, lui obéissant,"});
db.iliadfr.insert({ pg : 5665, line : "elles préparèrent le lit, comme il l'avait commandé. Et elles le"});
db.iliadfr.insert({ pg : 5666, line : "firent de peaux de brebis, de couvertures et de fins tissus de"});
db.iliadfr.insert({ pg : 5667, line : "lin. Et le vieillard se coucha, en attendant la divine Éôs. Et"});
db.iliadfr.insert({ pg : 5668, line : "Akhilleus se coucha dans le fond de la tente bien construite, et,"});
db.iliadfr.insert({ pg : 5669, line : "auprès de lui, se coucha une femme qu'il avait amenée de Lesbos,"});
db.iliadfr.insert({ pg : 5670, line : "la fille de Phorbas, Diomèda aux belles joues. Et Patroklos se"});
db.iliadfr.insert({ pg : 5671, line : "coucha dans une autre partie de la tente, et, auprès de lui, se"});
db.iliadfr.insert({ pg : 5672, line : "coucha la belle Iphis que lui avait donnée le divin Akhilleus"});
db.iliadfr.insert({ pg : 5673, line : "quand il prit la haute Skyros, citadelle d'Ényeus."});
db.iliadfr.insert({ pg : 5675, line : "Et, les envoyés étant arrivés aux tentes de l'Atréide, les fils"});
db.iliadfr.insert({ pg : 5676, line : "des Akhaiens, leur offrant des coupes d'or, s'empressèrent autour"});
db.iliadfr.insert({ pg : 5677, line : "d'eux, et ils les interrogeaient. Et, le premier, le roi des"});
db.iliadfr.insert({ pg : 5678, line : "hommes, Agamemnôn, les interrogea ainsi:"});
db.iliadfr.insert({ pg : 5680, line : "-- Dis-moi, Odysseus, très digne de louanges, illustre gloire des"});
db.iliadfr.insert({ pg : 5681, line : "Akhaiens, veut-il défendre les nefs de la flamme ardente, ou"});
db.iliadfr.insert({ pg : 5682, line : "refuse-t-il, ayant gardé sa colère dans son coeur orgueilleux?"});
db.iliadfr.insert({ pg : 5684, line : "Et le patient et divin Odysseus lui répondit:"});
db.iliadfr.insert({ pg : 5686, line : "-- Très illustre Atréide Agamemnôn, roi des hommes, il ne veut"});
db.iliadfr.insert({ pg : 5687, line : "point éteindre sa colère, et il n'est que plus irrité. Il refuse"});
db.iliadfr.insert({ pg : 5688, line : "tes dons. Il te conseille de délibérer avec les autres Argiens"});
db.iliadfr.insert({ pg : 5689, line : "comment tu sauveras les nefs et l'armée des Akhaiens. Il menace,"});
db.iliadfr.insert({ pg : 5690, line : "dès les premières lueurs d'Éôs, de traîner à la mer ses nefs"});
db.iliadfr.insert({ pg : 5691, line : "solides; et il exhorte les autres Argiens à retourner vers leur"});
db.iliadfr.insert({ pg : 5692, line : "patrie, car il dit que vous ne verrez jamais le dernier jour de la"});
db.iliadfr.insert({ pg : 5693, line : "haute Ilios, et que Zeus qui tonne puissamment la protège de ses"});
db.iliadfr.insert({ pg : 5694, line : "mains et a rempli son peuple d'une grande audace. Il a parlé"});
db.iliadfr.insert({ pg : 5695, line : "ainsi, et ceux qui m'ont suivi, Aias et les deux hérauts pleins de"});
db.iliadfr.insert({ pg : 5696, line : "prudence peuvent l'affirmer. Et le vieillard Phoinix s'est couché"});
db.iliadfr.insert({ pg : 5697, line : "sous sa tente, et il l'emmènera demain sur ses nefs vers leur"});
db.iliadfr.insert({ pg : 5698, line : "chère patrie, s'il le désire, car il ne veut point le contraindre."});
db.iliadfr.insert({ pg : 5700, line : "Il parla ainsi, et tous restèrent muets, accablés de ce discours"});
db.iliadfr.insert({ pg : 5701, line : "et de ces dures paroles. Et les fils des Akhaiens restèrent"});
db.iliadfr.insert({ pg : 5702, line : "longtemps muets et tristes. Enfin, Diomèdès hardi au combat parla"});
db.iliadfr.insert({ pg : 5703, line : "ainsi:"});
db.iliadfr.insert({ pg : 5705, line : "-- Très illustre roi des hommes, Atréide Agamemnôn, plût aux dieux"});
db.iliadfr.insert({ pg : 5706, line : "que tu n'eusses point supplié l'irréprochable Pèléide, en lui"});
db.iliadfr.insert({ pg : 5707, line : "offrant des dons infinis! Il avait un coeur orgueilleux, et tu as"});
db.iliadfr.insert({ pg : 5708, line : "enflé son orgueil. Laissons-le; qu'il parte ou qu'il reste. Il"});
db.iliadfr.insert({ pg : 5709, line : "combattra de nouveau quand il lui plaira et qu'un dieu l'y"});
db.iliadfr.insert({ pg : 5710, line : "poussera. Allons! faites tous ce que je vais dire. Reposons-nous,"});
db.iliadfr.insert({ pg : 5711, line : "puisque nous avons ranimé notre âme en buvant et en mangeant, ce"});
db.iliadfr.insert({ pg : 5712, line : "qui donne la force et le courage. Mais aussitôt que la belle Éôs"});
db.iliadfr.insert({ pg : 5713, line : "aux doigts rosés paraîtra, rangeons l'armée et les chars devant"});
db.iliadfr.insert({ pg : 5714, line : "les nefs. Alors, Atréide, exhorte les hommes au combat, et combats"});
db.iliadfr.insert({ pg : 5715, line : "toi-même aux premiers rangs."});
db.iliadfr.insert({ pg : 5717, line : "Il parla ainsi, et tous les rois applaudirent, admirant les"});
db.iliadfr.insert({ pg : 5718, line : "paroles de l'habile cavalier Diomèdès. Et après avoir fait des"});
db.iliadfr.insert({ pg : 5719, line : "libations, ils se retirèrent sous leurs tentes, où ils se"});
db.iliadfr.insert({ pg : 5720, line : "couchèrent et s'endormirent."});
db.iliadfr.insert({ pg : 5723, line : "Chant 10"});
db.iliadfr.insert({ pg : 5725, line : "Les chefs des Panakhaiens dormaient dans la nuit, auprès des nefs,"});
db.iliadfr.insert({ pg : 5726, line : "domptés par le sommeil; mais le doux sommeil ne saisissait point"});
db.iliadfr.insert({ pg : 5727, line : "l'Atréide Agamemnôn, prince des peuples, et il roulait beaucoup de"});
db.iliadfr.insert({ pg : 5728, line : "pensées dans son esprit."});
db.iliadfr.insert({ pg : 5730, line : "De même que l'époux de Hèrè lance la foudre, ce grand bruit"});
db.iliadfr.insert({ pg : 5731, line : "précurseur des batailles amères, ou de la pluie abondante, ou de"});
db.iliadfr.insert({ pg : 5732, line : "la grêle pressée, ou de la neige qui blanchit les campagnes; de"});
db.iliadfr.insert({ pg : 5733, line : "même Agamemnôn poussait de nombreux soupirs du fond de sa"});
db.iliadfr.insert({ pg : 5734, line : "poitrine, et tout son coeur tremblait quand il contemplait le camp"});
db.iliadfr.insert({ pg : 5735, line : "des Troiens et la multitude des feux qui brûlaient devant Ilios,"});
db.iliadfr.insert({ pg : 5736, line : "et qu'il entendait le son des flûtes et la rumeur des hommes. Et"});
db.iliadfr.insert({ pg : 5737, line : "il regardait ensuite l'armée des Akhaiens, et il arrachait ses"});
db.iliadfr.insert({ pg : 5738, line : "cheveux qu'il vouait à l'éternel Zeus, et il gémissait dans son"});
db.iliadfr.insert({ pg : 5739, line : "coeur magnanime."});
db.iliadfr.insert({ pg : 5741, line : "Et il vit que le mieux était de se rendre auprès du Nèlèiôn Nestôr"});
db.iliadfr.insert({ pg : 5742, line : "pour délibérer sur le moyen de sauver ses guerriers et de trouver"});
db.iliadfr.insert({ pg : 5743, line : "un remède aux maux qui accablaient tous les Danaens. Et, s'étant"});
db.iliadfr.insert({ pg : 5744, line : "levé, il revêtit une tunique, attacha de belles sandales à ses"});
db.iliadfr.insert({ pg : 5745, line : "pieds robustes, s'enveloppa de la peau rude d'un lion grand et"});
db.iliadfr.insert({ pg : 5746, line : "fauve, et saisit une lance."});
db.iliadfr.insert({ pg : 5748, line : "Et voici que la même terreur envahissait Ménélaos. Le sommeil"});
db.iliadfr.insert({ pg : 5749, line : "n'avait point fermé ses paupières, et il tremblait en songeant aux"});
db.iliadfr.insert({ pg : 5750, line : "souffrances des Argiens qui, pour sa cause ayant traversé la vaste"});
db.iliadfr.insert({ pg : 5751, line : "mer, étaient venus devant Troiè, pleins d'ardeur belliqueuse. Et"});
db.iliadfr.insert({ pg : 5752, line : "il couvrit son large dos de la peau tachetée d'un léopard, posa un"});
db.iliadfr.insert({ pg : 5753, line : "casque d'airain sur sa tête, saisit une lance de sa main robuste"});
db.iliadfr.insert({ pg : 5754, line : "et sortit pour éveiller son frère qui commandait à tous les"});
db.iliadfr.insert({ pg : 5755, line : "Argiens, et qu'ils honoraient comme un dieu. Et il le rencontra,"});
db.iliadfr.insert({ pg : 5756, line : "revêtu de ses belles armes, auprès de la poupe de sa nef; et"});
db.iliadfr.insert({ pg : 5757, line : "Agamemnôn fut joyeux de le voir, et le brave Ménélaos parla ainsi"});
db.iliadfr.insert({ pg : 5758, line : "le premier:"});
db.iliadfr.insert({ pg : 5760, line : "-- Pourquoi t'armes-tu, frère? Veux-tu envoyer un de nos"});
db.iliadfr.insert({ pg : 5761, line : "compagnons épier les Troiens? Je crains qu'aucun de ceux qui te le"});
db.iliadfr.insert({ pg : 5762, line : "promettront n'ose, seul dans la nuit divine, épier les guerriers"});
db.iliadfr.insert({ pg : 5763, line : "ennemis. Celui qui le fera, certes, sera plein d'audace."});
db.iliadfr.insert({ pg : 5765, line : "Et le roi Agamemnôn, lui répondant, parla ainsi:"});
db.iliadfr.insert({ pg : 5767, line : "-- Il nous faut à tous deux un sage conseil, ô Ménélaos,"});
db.iliadfr.insert({ pg : 5768, line : "nourrisson de Zeus, qui nous aide à sauver les Argiens et les"});
db.iliadfr.insert({ pg : 5769, line : "nefs, puisque l'esprit de Zeus nous est contraire, et qu'il se"});
db.iliadfr.insert({ pg : 5770, line : "complaît aux sacrifices de Hektôr beaucoup plus qu'aux nôtres; car"});
db.iliadfr.insert({ pg : 5771, line : "je n'ai jamais ni vu, ni entendu dire qu'un seul homme ait"});
db.iliadfr.insert({ pg : 5772, line : "accompli, en un jour, autant de rudes travaux que Hektôr cher à"});
db.iliadfr.insert({ pg : 5773, line : "Zeus contre les fils des Akhaiens, bien qu'il ne soit né ni d'une"});
db.iliadfr.insert({ pg : 5774, line : "déesse ni d'un dieu. Et je pense que les Argiens se souviendront"});
db.iliadfr.insert({ pg : 5775, line : "amèrement et longtemps de tous les maux qu'il leur a faits. Mais,"});
db.iliadfr.insert({ pg : 5776, line : "va! Cours vers les nefs; appelle Aias et Idoméneus. Moi, je vais"});
db.iliadfr.insert({ pg : 5777, line : "trouver le divin Nestôr, afin qu'il se lève et vienne vers la"});
db.iliadfr.insert({ pg : 5778, line : "troupe sacrée des gardes, et qu'il leur commande. Ils l'écouteront"});
db.iliadfr.insert({ pg : 5779, line : "avec plus de respect que d'autres, car son fils est à leur tête,"});
db.iliadfr.insert({ pg : 5780, line : "avec Mèrionès, le compagnon d'Idoméneus. C'est à eux que nous"});
db.iliadfr.insert({ pg : 5781, line : "avons donné le commandement des gardes."});
db.iliadfr.insert({ pg : 5783, line : "Et le brave Ménélaos lui répondit:"});
db.iliadfr.insert({ pg : 5785, line : "-- Comment faut-il obéir à ton ordre? Resterai-je au milieu d'eux,"});
db.iliadfr.insert({ pg : 5786, line : "en t'attendant, ou reviendrai-je promptement vers toi, après les"});
db.iliadfr.insert({ pg : 5787, line : "avoir avertis?"});
db.iliadfr.insert({ pg : 5789, line : "Et le roi des hommes, Agamemnôn, lui répondit:"});
db.iliadfr.insert({ pg : 5791, line : "-- Reste, afin que nous ne nous égarions point tous deux en venant"});
db.iliadfr.insert({ pg : 5792, line : "au hasard au-devant l'un de l'autre, car le camp a de nombreuses"});
db.iliadfr.insert({ pg : 5793, line : "routes. Parle à voix haute sur ton chemin et recommande la"});
db.iliadfr.insert({ pg : 5794, line : "vigilance. Adjure chaque guerrier au nom de ses pères et de ses"});
db.iliadfr.insert({ pg : 5795, line : "descendants; donne des louanges à tous, et ne montre point un"});
db.iliadfr.insert({ pg : 5796, line : "esprit orgueilleux. Il faut que nous agissions ainsi par nous-"});
db.iliadfr.insert({ pg : 5797, line : "mêmes, car, dès le berceau, Zeus nous a infligé cette lourde"});
db.iliadfr.insert({ pg : 5798, line : "tâche."});
db.iliadfr.insert({ pg : 5800, line : "Ayant ainsi parlé, il congédia son frère, après lui avoir donné de"});
db.iliadfr.insert({ pg : 5801, line : "sages avis, et il se rendit auprès de Nestôr, prince des peuples."});
db.iliadfr.insert({ pg : 5802, line : "Et il le trouva sous sa tente, non loin de sa nef noire, couché"});
db.iliadfr.insert({ pg : 5803, line : "sur un lit épais. Et autour de lui étaient répandues ses armes aux"});
db.iliadfr.insert({ pg : 5804, line : "reflets variés, le bouclier, les deux lances, et le casque"});
db.iliadfr.insert({ pg : 5805, line : "étincelant, et le riche ceinturon que ceignait le vieillard quand"});
db.iliadfr.insert({ pg : 5806, line : "il s'armait pour la guerre terrible, à la tête des siens; car il"});
db.iliadfr.insert({ pg : 5807, line : "ne se laissait point accabler par la triste vieillesse. Et,"});
db.iliadfr.insert({ pg : 5808, line : "s'étant soulevé, la tête appuyée sur le bras, il parla ainsi à"});
db.iliadfr.insert({ pg : 5809, line : "l'Atréide:"});
db.iliadfr.insert({ pg : 5811, line : "-- Qui es-tu, qui viens seul vers les nefs, à travers le camp, au"});
db.iliadfr.insert({ pg : 5812, line : "milieu de la nuit noire, quand tous les hommes mortels sont"});
db.iliadfr.insert({ pg : 5813, line : "endormis? Cherches-tu quelque garde ou quelqu'un de tes"});
db.iliadfr.insert({ pg : 5814, line : "compagnons? Parle, ne reste pas muet en m'approchant. Que te faut-"});
db.iliadfr.insert({ pg : 5815, line : "il?"});
db.iliadfr.insert({ pg : 5817, line : "Et le roi des hommes, Agamemnôn, lui répondit:"});
db.iliadfr.insert({ pg : 5819, line : "-- Ô Nestôr Nèlèiade, illustre gloire des Akhaiens, reconnais"});
db.iliadfr.insert({ pg : 5820, line : "l'Atréide Agamemnôn, celui que Zeus accable entre tous de travaux"});
db.iliadfr.insert({ pg : 5821, line : "infinis, jusqu'à ce que le souffle manque à ma poitrine et que mes"});
db.iliadfr.insert({ pg : 5822, line : "genoux cessent de se mouvoir. J'erre ainsi, parce que le doux"});
db.iliadfr.insert({ pg : 5823, line : "sommeil n'abaisse point mes paupières, et que la guerre et la"});
db.iliadfr.insert({ pg : 5824, line : "ruine des Akhaiens me rongent de soucis. Je tremble pour les"});
db.iliadfr.insert({ pg : 5825, line : "Danaens, et je suis troublé, et mon coeur n'est plus ferme, et il"});
db.iliadfr.insert({ pg : 5826, line : "bondit hors de mon sein, et mes membres illustres frémissent. Si"});
db.iliadfr.insert({ pg : 5827, line : "tu sais ce qu'il faut entreprendre, et puisque tu ne dors pas,"});
db.iliadfr.insert({ pg : 5828, line : "viens; rendons-nous auprès des gardes, et sachons si, rompus de"});
db.iliadfr.insert({ pg : 5829, line : "fatigue, ils dorment et oublient de veiller. Les guerriers ennemis"});
db.iliadfr.insert({ pg : 5830, line : "ne sont pas éloignés, et nous ne savons s'ils ne méditent point de"});
db.iliadfr.insert({ pg : 5831, line : "combattre cette nuit."});
db.iliadfr.insert({ pg : 5833, line : "Et le cavalier Gérennien Nestôr lui répondit:"});
db.iliadfr.insert({ pg : 5835, line : "-- Atréide Agamemnôn, très illustre roi des hommes, le prudent"});
db.iliadfr.insert({ pg : 5836, line : "Zeus n'accordera peut-être pas à Hektôr tout ce qu'il espère; et"});
db.iliadfr.insert({ pg : 5837, line : "je pense qu'il ressentira à son tour de cruelles douleurs si"});
db.iliadfr.insert({ pg : 5838, line : "Akhilleus arrache de son coeur sa colère fatale. Mais je te"});
db.iliadfr.insert({ pg : 5839, line : "suivrai volontiers, et nous appellerons les autres chefs: le"});
db.iliadfr.insert({ pg : 5840, line : "Tydéide illustre par sa lance, et Odysseus, et l'agile Aias, et le"});
db.iliadfr.insert({ pg : 5841, line : "robuste fils de Phyleus, et le divin Aias aussi, et le roi"});
db.iliadfr.insert({ pg : 5842, line : "Idoméneus. Les nefs de ceux-ci sont très éloignées. Cependant, je"});
db.iliadfr.insert({ pg : 5843, line : "blâme hautement Ménélaos, bien que je l'aime et le vénère, et même"});
db.iliadfr.insert({ pg : 5844, line : "quand tu t'en irriterais contre moi. Pourquoi dort-il et te"});
db.iliadfr.insert({ pg : 5845, line : "laisse-t-il agir seul? Il devrait lui-même exciter tous les chefs,"});
db.iliadfr.insert({ pg : 5846, line : "car une inexorable nécessité nous assiège."});
db.iliadfr.insert({ pg : 5848, line : "Et le roi des hommes, Agamemnôn, lui répondit:"});
db.iliadfr.insert({ pg : 5850, line : "-- Ô vieillard, je t'ai parfois poussé à le blâmer, car il est"});
db.iliadfr.insert({ pg : 5851, line : "souvent négligent et ne veut point agir, non qu'il manque"});
db.iliadfr.insert({ pg : 5852, line : "d'intelligence ou d'activité, mais parce qu'il me regarde et"});
db.iliadfr.insert({ pg : 5853, line : "attend que je lui donne l'exemple. Mais voici qu'il s'est levé"});
db.iliadfr.insert({ pg : 5854, line : "avant moi et qu'il m'a rencontré. Et je l'ai envoyé appeler ceux"});
db.iliadfr.insert({ pg : 5855, line : "que tu nommes. Allons! nous les trouverons devant les portes, au"});
db.iliadfr.insert({ pg : 5856, line : "milieu des gardes; car c'est là que j'ai ordonné qu'ils se"});
db.iliadfr.insert({ pg : 5857, line : "réunissent."});
db.iliadfr.insert({ pg : 5859, line : "Et le cavalier Gérennien Nestôr lui répondit:"});
db.iliadfr.insert({ pg : 5861, line : "-- Nul d'entre les Argiens ne s'irritera contre lui et ne"});
db.iliadfr.insert({ pg : 5862, line : "résistera à ses exhortations et à ses ordres."});
db.iliadfr.insert({ pg : 5864, line : "Ayant ainsi parlé, il se couvrit la poitrine d'une tunique,"});
db.iliadfr.insert({ pg : 5865, line : "attacha de belles sandales à ses pieds robustes, agrafa un manteau"});
db.iliadfr.insert({ pg : 5866, line : "fait d'une double laine pourprée, saisit une forte lance à pointe"});
db.iliadfr.insert({ pg : 5867, line : "d'airain et s'avança vers les nefs des Akhaiens cuirassés. Et le"});
db.iliadfr.insert({ pg : 5868, line : "cavalier Gérennien Nestôr, parlant à haute voix, éveilla Odysseus"});
db.iliadfr.insert({ pg : 5869, line : "égal à Zeus en prudence; et celui-ci, aussitôt qu'il eut entendu,"});
db.iliadfr.insert({ pg : 5870, line : "sortit de sa tente et leur dit:"});
db.iliadfr.insert({ pg : 5872, line : "-- Pourquoi errez-vous seuls auprès des nefs, à travers le camp,"});
db.iliadfr.insert({ pg : 5873, line : "au milieu de la nuit divine? Quelle nécessité si grande vous y"});
db.iliadfr.insert({ pg : 5874, line : "oblige?"});
db.iliadfr.insert({ pg : 5876, line : "Et le cavalier Gérennien Nestôr lui répondit:"});
db.iliadfr.insert({ pg : 5878, line : "-- Laertiade, issu de Zeus, subtil Odysseus, ne t'irrite pas. Une"});
db.iliadfr.insert({ pg : 5879, line : "profonde inquiétude trouble les Akhaiens. Suis-nous donc et"});
db.iliadfr.insert({ pg : 5880, line : "éveillons chaque chef, afin de délibérer s'il faut fuir ou"});
db.iliadfr.insert({ pg : 5881, line : "combattre."});
db.iliadfr.insert({ pg : 5883, line : "Il parla ainsi, et le subtil Odysseus, étant rentré sous sa tente,"});
db.iliadfr.insert({ pg : 5884, line : "jeta un bouclier éclatant sur ses épaules et revint à eux. Et ils"});
db.iliadfr.insert({ pg : 5885, line : "se rendirent auprès du Tydéide Diomèdès, et ils le virent hors de"});
db.iliadfr.insert({ pg : 5886, line : "sa tente avec ses armes. Et ses compagnons dormaient autour, le"});
db.iliadfr.insert({ pg : 5887, line : "bouclier sous la tête. Leurs lances étaient plantées droites, et"});
db.iliadfr.insert({ pg : 5888, line : "l'airain brillait comme l'éclair de Zeus. Et le héros dormait"});
db.iliadfr.insert({ pg : 5889, line : "aussi, couché sur la peau d'un boeuf sauvage, un tapis splendide"});
db.iliadfr.insert({ pg : 5890, line : "sous la tête. Et le cavalier Gérennien Nestôr, s'approchant, le"});
db.iliadfr.insert({ pg : 5891, line : "poussa du pied et lui parla rudement:"});
db.iliadfr.insert({ pg : 5893, line : "-- Lève-toi, fils de Tydeus! Pourquoi dors-tu pendant cette nuit?"});
db.iliadfr.insert({ pg : 5894, line : "N'entends-tu pas les Troiens, dans leur camp, sur la hauteur, non"});
db.iliadfr.insert({ pg : 5895, line : "loin des nefs? Peu d'espace nous sépare d'eux."});
db.iliadfr.insert({ pg : 5897, line : "Il parla ainsi, et Diomèdès, sortant aussitôt de son repos, lui"});
db.iliadfr.insert({ pg : 5898, line : "répondit par ces paroles ailées:"});
db.iliadfr.insert({ pg : 5900, line : "-- Tu ne te ménages pas assez, vieillard. Les jeunes fils des"});
db.iliadfr.insert({ pg : 5901, line : "Akhaiens ne peuvent-ils aller de tous côtés dans le camp éveiller"});
db.iliadfr.insert({ pg : 5902, line : "chacun des rois? Vieillard, tu es infatigable, en vérité."});
db.iliadfr.insert({ pg : 5904, line : "Et le cavalier Gérennien Nestôr lui répondit:"});
db.iliadfr.insert({ pg : 5906, line : "-- Certes, ami, tout ce que tu as dit est très sage. J'ai des"});
db.iliadfr.insert({ pg : 5907, line : "guerriers nombreux et des fils irréprochables. Un d'entre eux"});
db.iliadfr.insert({ pg : 5908, line : "aurait pu parcourir le camp. Mais une dure nécessité assiège les"});
db.iliadfr.insert({ pg : 5909, line : "Akhaiens; la vie ou la mort des Argiens est sur le tranchant de"});
db.iliadfr.insert({ pg : 5910, line : "l'épée. Viens donc, et, si tu me plains, car tu es plus jeune que"});
db.iliadfr.insert({ pg : 5911, line : "moi, éveille l'agile Aias et le fils de Phyleus."});
db.iliadfr.insert({ pg : 5913, line : "Il parla ainsi et Diomèdès, se couvrant les épaules de la peau"});
db.iliadfr.insert({ pg : 5914, line : "d'un grand lion fauve, prit une lance, courut éveiller les deux"});
db.iliadfr.insert({ pg : 5915, line : "rois et les amena. Et bientôt ils arrivèrent tous au milieu des"});
db.iliadfr.insert({ pg : 5916, line : "gardes, dont les chefs ne dormaient point et veillaient en armes,"});
db.iliadfr.insert({ pg : 5917, line : "avec vigilance. Comme des chiens qui gardent activement des brebis"});
db.iliadfr.insert({ pg : 5918, line : "dans l'étable, et qui, entendant une bête féroce sortie des bois"});
db.iliadfr.insert({ pg : 5919, line : "sur les montagnes, hurlent contre elle au milieu des cris des"});
db.iliadfr.insert({ pg : 5920, line : "pâtres; de même veillaient les gardes, et le doux sommeil"});
db.iliadfr.insert({ pg : 5921, line : "n'abaissait point leurs paupières pendant cette triste nuit; mais"});
db.iliadfr.insert({ pg : 5922, line : "ils étaient tournés du côté de la plaine, écoutant si les Troiens"});
db.iliadfr.insert({ pg : 5923, line : "s'avançaient. Et le vieillard Nestôr, les ayant vus, en fut"});
db.iliadfr.insert({ pg : 5924, line : "réjoui; et, les félicitant, il leur dit en paroles ailées:"});
db.iliadfr.insert({ pg : 5926, line : "-- C'est ainsi, chers enfants, qu'il faut veiller. Que le sommeil"});
db.iliadfr.insert({ pg : 5927, line : "ne saisisse aucun d'entre vous, de peur que nous ne soyons le"});
db.iliadfr.insert({ pg : 5928, line : "jouet de l'ennemi."});
db.iliadfr.insert({ pg : 5930, line : "Ayant ainsi parlé, il passa le fossé, et les rois Argiens"});
db.iliadfr.insert({ pg : 5931, line : "convoqués au conseil le suivirent, et, avec eux, Mèrionès et"});
db.iliadfr.insert({ pg : 5932, line : "l'illustre fils de Nestôr, appelés à délibérer aussi. Et,"});
db.iliadfr.insert({ pg : 5933, line : "lorsqu'ils eurent passé le fossé, ils s'arrêtèrent en un lieu d'où"});
db.iliadfr.insert({ pg : 5934, line : "l'on voyait le champ de bataille, là où le robuste Hektôr, ayant"});
db.iliadfr.insert({ pg : 5935, line : "défait les Argiens, avait commencé sa retraite dès que la nuit eut"});
db.iliadfr.insert({ pg : 5936, line : "répandu ses ténèbres. Et c'est là qu'ils délibéraient entre eux."});
db.iliadfr.insert({ pg : 5937, line : "Et le cavalier Gérennien Nestôr parla ainsi le premier:"});
db.iliadfr.insert({ pg : 5939, line : "-- Ô amis, quelque guerrier, sûr de son coeur audacieux, veut-il"});
db.iliadfr.insert({ pg : 5940, line : "aller au milieu des Troiens magnanimes? Peut-être se saisirait-il"});
db.iliadfr.insert({ pg : 5941, line : "d'un ennemi sorti de son camp, ou entendrait-il les Troiens qui"});
db.iliadfr.insert({ pg : 5942, line : "délibèrent entre eux, soit qu'ils veuillent rester loin des nefs,"});
db.iliadfr.insert({ pg : 5943, line : "soit qu'ils ne veuillent retourner dans leur ville, qu'ayant"});
db.iliadfr.insert({ pg : 5944, line : "dompté les Akhaiens. Il apprendrait tout et reviendrait vers nous,"});
db.iliadfr.insert({ pg : 5945, line : "sans blessure, et il aurait une grande gloire sous l'Ouranos,"});
db.iliadfr.insert({ pg : 5946, line : "parmi les hommes, ainsi qu'une noble récompense. Les chefs qui"});
db.iliadfr.insert({ pg : 5947, line : "commandent sur nos nefs, tous, tant qu'ils sont, lui donneraient,"});
db.iliadfr.insert({ pg : 5948, line : "chacun, une brebis noire allaitant un agneau, et ce don serait"});
db.iliadfr.insert({ pg : 5949, line : "sans égal; et toujours il serait admis à nos repas et à nos fêtes."});
db.iliadfr.insert({ pg : 5951, line : "Il parla ainsi, et tous restèrent muets, mais le brave Diomèdès"});
db.iliadfr.insert({ pg : 5952, line : "répondit:"});
db.iliadfr.insert({ pg : 5954, line : "-- Nestôr, mon coeur et mon esprit courageux me poussent à entrer"});
db.iliadfr.insert({ pg : 5955, line : "dans le camp prochain des guerriers ennemis; mais, si quelque"});
db.iliadfr.insert({ pg : 5956, line : "héros veut me suivre, mon espoir sera plus grand et ma confiance"});
db.iliadfr.insert({ pg : 5957, line : "sera plus ferme. Quand deux hommes marchent ensemble, l'un conçoit"});
db.iliadfr.insert({ pg : 5958, line : "avant l'autre ce qui est utile. Ce n'est pas qu'un seul ne le"});
db.iliadfr.insert({ pg : 5959, line : "puisse, mais son esprit est plus lent et sa résolution est"});
db.iliadfr.insert({ pg : 5960, line : "moindre."});
db.iliadfr.insert({ pg : 5962, line : "Il parla ainsi, et beaucoup voulurent le suivre: les deux Aias,"});
db.iliadfr.insert({ pg : 5963, line : "nourrissons d'Arès, et le fils de Nestôr, et Mèrionès, et"});
db.iliadfr.insert({ pg : 5964, line : "l'Atréide Ménélaos illustre par sa lance. Laudacieux Odysseus"});
db.iliadfr.insert({ pg : 5965, line : "voulut aussi pénétrer dans le camp des Troiens. Et le roi des"});
db.iliadfr.insert({ pg : 5966, line : "hommes, Agamemnôn, parla ainsi au milieu d'eux:"});
db.iliadfr.insert({ pg : 5968, line : "-- Tydéide Diomèdès, le plus cher à mon âme, choisis, dans le"});
db.iliadfr.insert({ pg : 5969, line : "meilleur de ces héros, le compagnon que tu voudras, puisque tous"});
db.iliadfr.insert({ pg : 5970, line : "s'offrent à toi; mais ne néglige point, par respect, le plus"});
db.iliadfr.insert({ pg : 5971, line : "robuste pour un plus faible, même s'il était un roi plus puissant."});
db.iliadfr.insert({ pg : 5973, line : "Il parla ainsi, et il craignait pour le blond Ménélaos mais le"});
db.iliadfr.insert({ pg : 5974, line : "brave Diomèdès répondit:"});
db.iliadfr.insert({ pg : 5976, line : "-- Puisque tu m'ordonnes de choisir moi-même un compagnon, comment"});
db.iliadfr.insert({ pg : 5977, line : "pourrais-je oublier le divin Odysseus qui montre dans tous les"});
db.iliadfr.insert({ pg : 5978, line : "travaux un coeur irréprochable et un esprit viril, et qui est aimé"});
db.iliadfr.insert({ pg : 5979, line : "de Pallas Athènè? S'il m'accompagne, nous reviendrons tous deux du"});
db.iliadfr.insert({ pg : 5980, line : "milieu des flammes, car il est plein d'intelligence."});
db.iliadfr.insert({ pg : 5982, line : "Et le patient et divin Odysseus lui répondit:"});
db.iliadfr.insert({ pg : 5984, line : "-- Tydéide Diomèdès, ne me loue ni ne me blâme outre mesure. Tu"});
db.iliadfr.insert({ pg : 5985, line : "parles au milieu des Argiens qui me connaissent. Allons! la nuit"});
db.iliadfr.insert({ pg : 5986, line : "passe; déjà l'aube est proche; les étoiles s'inclinent. Les deux"});
db.iliadfr.insert({ pg : 5987, line : "premières parties de la nuit se sont écoulées, et la troisième"});
db.iliadfr.insert({ pg : 5988, line : "seule nous reste."});
db.iliadfr.insert({ pg : 5990, line : "Ayant ainsi parlé, ils se couvrirent de leurs lourdes armes."});
db.iliadfr.insert({ pg : 5991, line : "Thrasymèdès, ferme au combat, donna au Tydéide une épée à deux"});
db.iliadfr.insert({ pg : 5992, line : "tranchants, car la sienne était restée sur les nefs, et un"});
db.iliadfr.insert({ pg : 5993, line : "bouclier. Et Diomèdès mit sur sa tête un casque fait d'une peau de"});
db.iliadfr.insert({ pg : 5994, line : "taureau, terne et sans crinière, tel qu'en portaient les plus"});
db.iliadfr.insert({ pg : 5995, line : "jeunes guerriers. Et Mèrionès donna à Odysseus un arc, un carquois"});
db.iliadfr.insert({ pg : 5996, line : "et une épée. Et le Laertiade mit sur sa tête un casque fait de"});
db.iliadfr.insert({ pg : 5997, line : "peau, fortement lié, en dedans, de courroies, que les dents"});
db.iliadfr.insert({ pg : 5998, line : "blanches d'un sanglier hérissaient de toutes parts au dehors, et"});
db.iliadfr.insert({ pg : 5999, line : "couvert de poils au milieu. Autolykos avait autrefois enlevé ce"});
db.iliadfr.insert({ pg : 6000, line : "casque dans Éléôn, quand il força la solide demeure d'Amyntôr"});
db.iliadfr.insert({ pg : 6001, line : "Orménide; et il le donna, dans Skandéia, au Kythérien Amphidamas;"});
db.iliadfr.insert({ pg : 6002, line : "et Amphidamas le donna à son hôte Molos, et Molos à son fils"});
db.iliadfr.insert({ pg : 6003, line : "Mèrionès. Maintenant Odysseus le mit sur sa tête."});
db.iliadfr.insert({ pg : 6005, line : "Et après avoir revêtu leurs armes, les deux guerriers partirent,"});
db.iliadfr.insert({ pg : 6006, line : "quittant les autres chefs. Et Pallas Athènè envoya, au bord de la"});
db.iliadfr.insert({ pg : 6007, line : "route, un héron propice, qu'ils ne virent point dans la nuit"});
db.iliadfr.insert({ pg : 6008, line : "obscure, mais qu'ils entendirent crier. Et Odysseus, tout joyeux,"});
db.iliadfr.insert({ pg : 6009, line : "pria Athènè:"});
db.iliadfr.insert({ pg : 6011, line : "-- Entends-moi, fille de Zeus tempétueux, toi qui viens à mon aide"});
db.iliadfr.insert({ pg : 6012, line : "dans tous mes travaux, et à qui je ne cache rien de tout ce que je"});
db.iliadfr.insert({ pg : 6013, line : "fais. À cette heure, sois-moi favorable encore, Athènè! Accorde-"});
db.iliadfr.insert({ pg : 6014, line : "nous de revenir vers nos nefs illustres, ayant accompli une grande"});
db.iliadfr.insert({ pg : 6015, line : "action qui soit amère aux Troiens."});
db.iliadfr.insert({ pg : 6017, line : "Et le brave Diomèdès la pria aussi:"});
db.iliadfr.insert({ pg : 6019, line : "-- Entends-moi, fille indomptée de Zeus! Protège-moi maintenant,"});
db.iliadfr.insert({ pg : 6020, line : "comme tu protégeas le divin Tydeus, mon père, dans Thèbè, où il"});
db.iliadfr.insert({ pg : 6021, line : "fut envoyé par les Akhaiens. Il laissa les Akhaiens cuirassés sur"});
db.iliadfr.insert({ pg : 6022, line : "les bords de l'Asôpos; et il portait une parole pacifique aux"});
db.iliadfr.insert({ pg : 6023, line : "Kadméiens; mais, au retour, il accomplit des actions mémorables,"});
db.iliadfr.insert({ pg : 6024, line : "avec ton aide, déesse, qui le protégeais! Maintenant, sois-moi"});
db.iliadfr.insert({ pg : 6025, line : "favorable aussi, et je te sacrifierai une génisse d'un an, au"});
db.iliadfr.insert({ pg : 6026, line : "large front, indomptée, car elle n'aura jamais été soumise au"});
db.iliadfr.insert({ pg : 6027, line : "joug. Et je te la sacrifierai, en répandant de l'or sur ses"});
db.iliadfr.insert({ pg : 6028, line : "cornes."});
db.iliadfr.insert({ pg : 6030, line : "Ils parlèrent ainsi en priant, et Pallas Athènè les entendit. Et,"});
db.iliadfr.insert({ pg : 6031, line : "après qu'ils eurent prié la fille du grand Zeus, ils s'avancèrent"});
db.iliadfr.insert({ pg : 6032, line : "comme deux lions, à travers la nuit épaisse et le carnage et les"});
db.iliadfr.insert({ pg : 6033, line : "cadavres et les armes et le sang noir."});
db.iliadfr.insert({ pg : 6035, line : "Mais Hektôr aussi n'avait point permis aux Troiens magnanimes de"});
db.iliadfr.insert({ pg : 6036, line : "dormir; et il avait convoqué les plus illustres des chefs et des"});
db.iliadfr.insert({ pg : 6037, line : "princes, et il délibérait prudemment avec eux:"});
db.iliadfr.insert({ pg : 6039, line : "-- Qui d'entre vous méritera une grande récompense, en me"});
db.iliadfr.insert({ pg : 6040, line : "promettant d'accomplir ce que je désire? Cette récompense sera"});
db.iliadfr.insert({ pg : 6041, line : "suffisante. Je lui donnerai un char et deux chevaux au beau col,"});
db.iliadfr.insert({ pg : 6042, line : "les meilleurs entre tous ceux qui sont auprès des nefs rapides des"});
db.iliadfr.insert({ pg : 6043, line : "Akhaiens. Il remporterait une grande gloire celui qui oserait"});
db.iliadfr.insert({ pg : 6044, line : "approcher des nefs rapides, et reconnaître si les Argiens veillent"});
db.iliadfr.insert({ pg : 6045, line : "toujours devant les nefs, ou si, domptés par nos mains, ils se"});
db.iliadfr.insert({ pg : 6046, line : "préparent à fuir et ne veulent plus même veiller pendant la nuit,"});
db.iliadfr.insert({ pg : 6047, line : "accablés par la fatigue."});
db.iliadfr.insert({ pg : 6049, line : "Il parla ainsi, et tous restèrent muets. Et il y avait, parmi les"});
db.iliadfr.insert({ pg : 6050, line : "Troiens, Dolôn, fils d'Eumèdos, divin héraut, riche en or et en"});
db.iliadfr.insert({ pg : 6051, line : "airain. Dolôn n'était point beau, mais il avait des pieds agiles;"});
db.iliadfr.insert({ pg : 6052, line : "et c'était un fils unique avec cinq soeurs. Il se leva, et il dit"});
db.iliadfr.insert({ pg : 6053, line : "à Hektôr et aux Troiens:"});
db.iliadfr.insert({ pg : 6055, line : "-- Hektôr, mon coeur et mon esprit courageux me poussent à aller"});
db.iliadfr.insert({ pg : 6056, line : "vers les nefs rapides, à la découverte; mais lève ton sceptre et"});
db.iliadfr.insert({ pg : 6057, line : "jure que tu me donneras les chevaux et le char orné d'airain qui"});
db.iliadfr.insert({ pg : 6058, line : "portent l'irréprochable Pèléiôn. Je ne te serai point un espion"});
db.iliadfr.insert({ pg : 6059, line : "inhabile et au-dessous de ton attente. J'irai de tous côtés dans"});
db.iliadfr.insert({ pg : 6060, line : "le camp, et je parviendrai jusqu'à la nef d'Agamemnôn, où, sans"});
db.iliadfr.insert({ pg : 6061, line : "doute, les premiers d'entre les rois délibèrent s'il faut fuir ou"});
db.iliadfr.insert({ pg : 6062, line : "combattre."});
db.iliadfr.insert({ pg : 6064, line : "Il parla ainsi, et le Priamide saisit son sceptre et fit ce"});
db.iliadfr.insert({ pg : 6065, line : "serment:"});
db.iliadfr.insert({ pg : 6067, line : "-- Que l'époux de Hèrè, Zeus au grand bruit, le sache: nul autre"});
db.iliadfr.insert({ pg : 6068, line : "guerrier Troien ne sera jamais traîné par ces chevaux, car ils"});
db.iliadfr.insert({ pg : 6069, line : "n'illustreront que toi seul, selon ma promesse."});
db.iliadfr.insert({ pg : 6071, line : "Il parla ainsi, jurant un vain serment, et il excita Dolôn. Et"});
db.iliadfr.insert({ pg : 6072, line : "celui-ci jeta aussitôt sur ses épaules un arc recourbé, se couvrit"});
db.iliadfr.insert({ pg : 6073, line : "de la peau d'un loup blanc, mit sur sa tête un casque de peau de"});
db.iliadfr.insert({ pg : 6074, line : "belette, et prit une lance aiguë. Et il s'avança vers les nefs,"});
db.iliadfr.insert({ pg : 6075, line : "hors du camp; mais il ne devait point revenir des nefs rendre"});
db.iliadfr.insert({ pg : 6076, line : "compte à Hektôr de son message. Lorsqu'il eut dépassé la foule des"});
db.iliadfr.insert({ pg : 6077, line : "hommes et des chevaux, il courut rapidement. Et le divin Odysseus"});
db.iliadfr.insert({ pg : 6078, line : "le vit arriver et dit à Diomèdès:"});
db.iliadfr.insert({ pg : 6080, line : "-- Ô Diomèdès, cet homme vient du camp ennemi. Je ne sais s'il"});
db.iliadfr.insert({ pg : 6081, line : "veut espionner nos nefs, ou dépouiller quelque cadavre parmi les"});
db.iliadfr.insert({ pg : 6082, line : "morts. Laissons-le nous dépasser un peu dans la plaine, et nous le"});
db.iliadfr.insert({ pg : 6083, line : "poursuivrons, et nous le prendrons aussitôt. S'il court plus"});
db.iliadfr.insert({ pg : 6084, line : "rapidement que nous, pousse-le vers les nefs, loin de son camp, en"});
db.iliadfr.insert({ pg : 6085, line : "le menaçant de ta lance, afin qu'il ne se réfugie point dans la"});
db.iliadfr.insert({ pg : 6086, line : "ville."});
db.iliadfr.insert({ pg : 6088, line : "Ayant ainsi parlé, ils se cachèrent hors du chemin parmi les"});
db.iliadfr.insert({ pg : 6089, line : "cadavres, et le Troien les dépassa promptement dans son"});
db.iliadfr.insert({ pg : 6090, line : "imprudence. Et il s'était à peine éloigné de la longueur d'un"});
db.iliadfr.insert({ pg : 6091, line : "sillon que tracent deux mules, qui valent mieux que les boeufs"});
db.iliadfr.insert({ pg : 6092, line : "pour tracer un sillon dans une terre dure, que les deux guerriers"});
db.iliadfr.insert({ pg : 6093, line : "le suivirent. Et il les entendit, et il s'arrêta inquiet. Et il"});
db.iliadfr.insert({ pg : 6094, line : "pensait dans son esprit que ses compagnons accouraient pour le"});
db.iliadfr.insert({ pg : 6095, line : "rappeler par l'ordre de Hektôr; mais à une portée de trait"});
db.iliadfr.insert({ pg : 6096, line : "environ, il reconnut des guerriers ennemis, et agitant ses jambes"});
db.iliadfr.insert({ pg : 6097, line : "rapides, il prit la fuite, et les deux Argiens le poussaient avec"});
db.iliadfr.insert({ pg : 6098, line : "autant de hâte."});
db.iliadfr.insert({ pg : 6100, line : "Ainsi que deux bons chiens de chasse, aux dents aiguës,"});
db.iliadfr.insert({ pg : 6101, line : "poursuivent de près, dans un bois, un faon ou un lièvre qui les"});
db.iliadfr.insert({ pg : 6102, line : "devance en criant, ainsi le Tydéide et Odysseus, le destructeur de"});
db.iliadfr.insert({ pg : 6103, line : "citadelles, poursuivaient ardemment le Troien, en le rejetant loin"});
db.iliadfr.insert({ pg : 6104, line : "de son camp. Et, comme il allait bientôt se mêler aux gardes en"});
db.iliadfr.insert({ pg : 6105, line : "fuyant vers les nefs, Athènè donna une plus grande force au"});
db.iliadfr.insert({ pg : 6106, line : "Tydéide, afin qu'il ne frappât point le second coup, et qu'un des"});
db.iliadfr.insert({ pg : 6107, line : "Akhaiens cuirassés ne pût se glorifier d'avoir fait la première"});
db.iliadfr.insert({ pg : 6108, line : "blessure. Et le robuste Diomèdès, agitant sa lance, parla ainsi:"});
db.iliadfr.insert({ pg : 6110, line : "-- Arrête, ou je te frapperai de ma lance, et je ne pense pas que"});
db.iliadfr.insert({ pg : 6111, line : "tu évites longtemps de recevoir la dure mort de ma main."});
db.iliadfr.insert({ pg : 6113, line : "Il parla ainsi et fit partir sa lance qui ne perça point le"});
db.iliadfr.insert({ pg : 6114, line : "Troien; mais la pointe du trait effleura seulement l'épaule droite"});
db.iliadfr.insert({ pg : 6115, line : "et s'enfonça en terre. Et Dolôn s'arrêta plein de crainte,"});
db.iliadfr.insert({ pg : 6116, line : "épouvanté, tremblant, pâle, et ses dents claquaient."});
db.iliadfr.insert({ pg : 6118, line : "Et les deux guerriers, haletants, lui saisirent les mains, et il"});
db.iliadfr.insert({ pg : 6119, line : "leur dit en pleurant:"});
db.iliadfr.insert({ pg : 6121, line : "-- Prenez-moi vivant. Je me rachèterai. J'ai dans mes demeures de"});
db.iliadfr.insert({ pg : 6122, line : "l'or et du fer propre à être travaillé. Pour mon affranchissement,"});
db.iliadfr.insert({ pg : 6123, line : "mon père vous en donnera la plus grande part, s'il apprend que je"});
db.iliadfr.insert({ pg : 6124, line : "suis vivant sur les nefs des Akhaiens."});
db.iliadfr.insert({ pg : 6126, line : "Et le subtil Odysseus lui répondit:"});
db.iliadfr.insert({ pg : 6128, line : "-- Prends courage, et que la mort ne soit pas présente à ton"});
db.iliadfr.insert({ pg : 6129, line : "esprit; mais dis-moi la vérité. Pourquoi viens-tu seul, de ton"});
db.iliadfr.insert({ pg : 6130, line : "camp, vers les nefs, par la nuit obscure, quand tous les hommes"});
db.iliadfr.insert({ pg : 6131, line : "mortels sont endormis? Serait-ce pour dépouiller les cadavres"});
db.iliadfr.insert({ pg : 6132, line : "parmi les morts, ou Hektôr t'a-t-il envoyé observer ce qui se"});
db.iliadfr.insert({ pg : 6133, line : "passe auprès des nefs creuses, ou viens-tu de ton propre"});
db.iliadfr.insert({ pg : 6134, line : "mouvement?"});
db.iliadfr.insert({ pg : 6136, line : "Et Dolôn, dont les membres tremblaient, leur répondit:"});
db.iliadfr.insert({ pg : 6138, line : "-- Hektôr, contre ma volonté, m'a poussé à ma ruine. Ayant promis"});
db.iliadfr.insert({ pg : 6139, line : "de me donner les chevaux aux sabots massifs de l'illustre Pèléiôn"});
db.iliadfr.insert({ pg : 6140, line : "et son char orné d'airain, il m'a ordonné d'aller et de"});
db.iliadfr.insert({ pg : 6141, line : "m'approcher, pendant la nuit obscure et rapide, des guerriers"});
db.iliadfr.insert({ pg : 6142, line : "ennemis, et de voir s'ils gardent toujours leurs nefs rapides, ou"});
db.iliadfr.insert({ pg : 6143, line : "si, domptés par nos mains, vous délibérez, prêts à fuir, et ne"});
db.iliadfr.insert({ pg : 6144, line : "pouvant même plus veiller, étant rompus de fatigue."});
db.iliadfr.insert({ pg : 6146, line : "Et le subtil Odysseus, en souriant, lui répondit:"});
db.iliadfr.insert({ pg : 6148, line : "-- Certes, tu espérais, dans ton esprit, une grande récompense, en"});
db.iliadfr.insert({ pg : 6149, line : "désirant les chevaux du brave Aiakide, car ils ne peuvent être"});
db.iliadfr.insert({ pg : 6150, line : "domptés et conduits par des guerriers mortels, sauf par Akhilleus"});
db.iliadfr.insert({ pg : 6151, line : "qu'une mère immortelle a enfanté. Mais dis-moi la vérité. Où as-tu"});
db.iliadfr.insert({ pg : 6152, line : "laissé Hektôr, prince des peuples? Où sont ses armes belliqueuses"});
db.iliadfr.insert({ pg : 6153, line : "et ses chevaux? Où sont les sentinelles et les tentes des autres"});
db.iliadfr.insert({ pg : 6154, line : "Troiens? Dis-nous s'ils délibèrent entre eux, soit qu'ils aient"});
db.iliadfr.insert({ pg : 6155, line : "dessein de rester où ils sont, loin des nefs, soit qu'ils désirent"});
db.iliadfr.insert({ pg : 6156, line : "ne rentrer dans la ville qu'après avoir dompté les Akhaiens."});
db.iliadfr.insert({ pg : 6158, line : "Et Dolôn, fils d'Eumèdos, lui répondit:"});
db.iliadfr.insert({ pg : 6160, line : "-- Je te dirai toute la vérité. Hektôr, dans le conseil, délibère"});
db.iliadfr.insert({ pg : 6161, line : "auprès du tombeau du divin Ilos, loin du bruit. Il n'y a point de"});
db.iliadfr.insert({ pg : 6162, line : "gardes autour du camp, car tous les Troiens veillent devant leurs"});
db.iliadfr.insert({ pg : 6163, line : "feux, pressés par la nécessité et s'excitant les uns les autres;"});
db.iliadfr.insert({ pg : 6164, line : "mais les alliés, venus de diverses contrées, dorment tous, se"});
db.iliadfr.insert({ pg : 6165, line : "fiant à la vigilance des Troiens, et n'ayant avec eux ni leurs"});
db.iliadfr.insert({ pg : 6166, line : "enfants, ni leurs femmes."});
db.iliadfr.insert({ pg : 6168, line : "Et le subtil Odysseus lui dit:"});
db.iliadfr.insert({ pg : 6170, line : "-- Sont-ils mêlés aux braves Troiens, ou dorment-ils à l'écart?"});
db.iliadfr.insert({ pg : 6171, line : "Parle clairement, afin que je comprenne."});
db.iliadfr.insert({ pg : 6173, line : "Et Dolôn, fils d'Eumèdos, lui répondit:"});
db.iliadfr.insert({ pg : 6175, line : "-- Je te dirai toute la vérité. Auprès de la mer sont les Kariens,"});
db.iliadfr.insert({ pg : 6176, line : "les Paiones aux arcs recourbés, les Léléges, les Kaukônes et les"});
db.iliadfr.insert({ pg : 6177, line : "divins Pélasges; du côté de Thymbrè sont les Lykiens, les Mysiens"});
db.iliadfr.insert({ pg : 6178, line : "orgueilleux, les cavaliers Phrygiens et les Maiones qui combattent"});
db.iliadfr.insert({ pg : 6179, line : "sur des chars. Mais pourquoi me demandez-vous ces choses? Si vous"});
db.iliadfr.insert({ pg : 6180, line : "désirez entrer dans le camp des Troiens, les Thrèkiens récemment"});
db.iliadfr.insert({ pg : 6181, line : "arrivés sont à l'écart, aux extrémités du camp, et leur roi,"});
db.iliadfr.insert({ pg : 6182, line : "Rhèsos Eionéide, est avec eux. J'ai vu ses grands et magnifiques"});
db.iliadfr.insert({ pg : 6183, line : "chevaux. Ils sont plus blancs que la neige, et semblables aux"});
db.iliadfr.insert({ pg : 6184, line : "vents quand ils courent. Et j'ai vu son char orné d'or et"});
db.iliadfr.insert({ pg : 6185, line : "d'argent, et ses grandes armes d'or, admirables aux yeux, et qui"});
db.iliadfr.insert({ pg : 6186, line : "conviennent moins à des hommes mortels qu'aux dieux qui vivent"});
db.iliadfr.insert({ pg : 6187, line : "toujours. Maintenant, conduisez-moi vers vos nefs rapides, ou,"});
db.iliadfr.insert({ pg : 6188, line : "m'attachant avec des liens solides, laissez-moi ici jusqu'à votre"});
db.iliadfr.insert({ pg : 6189, line : "retour, quand vous aurez reconnu si j'ai dit la vérité ou si j'ai"});
db.iliadfr.insert({ pg : 6190, line : "menti."});
db.iliadfr.insert({ pg : 6192, line : "Et le robuste Diomèdès, le regardant d'un oeil sombre, lui"});
db.iliadfr.insert({ pg : 6193, line : "répondit:"});
db.iliadfr.insert({ pg : 6195, line : "-- Dolôn, ne pense pas m'échapper, puisque tu es tombé entre nos"});
db.iliadfr.insert({ pg : 6196, line : "mains, bien que tes paroles soient bonnes. Si nous acceptons le"});
db.iliadfr.insert({ pg : 6197, line : "prix de ton affranchissement, et si nous te renvoyons, certes, tu"});
db.iliadfr.insert({ pg : 6198, line : "reviendras auprès des nefs rapides des Akhaiens, pour espionner ou"});
db.iliadfr.insert({ pg : 6199, line : "combattre; mais, si tu perds la vie, dompté par mes mains, tu ne"});
db.iliadfr.insert({ pg : 6200, line : "nuiras jamais plus aux Argiens."});
db.iliadfr.insert({ pg : 6202, line : "Il parla ainsi, et comme Dolôn le suppliait en lui touchant la"});
db.iliadfr.insert({ pg : 6203, line : "barbe de la main, il le frappa brusquement de son épée au milieu"});
db.iliadfr.insert({ pg : 6204, line : "de la gorge et trancha les deux muscles. Et le Troien parlait"});
db.iliadfr.insert({ pg : 6205, line : "encore quand sa tête tomba dans la poussière. Et ils arrachèrent"});
db.iliadfr.insert({ pg : 6206, line : "le casque de peau de belette, et la peau de loup, et l'arc"});
db.iliadfr.insert({ pg : 6207, line : "flexible et la longue lance. Et le divin Odysseus, les soulevant"});
db.iliadfr.insert({ pg : 6208, line : "vers le ciel, les voua, en priant, à la dévastatrice Athènè."});
db.iliadfr.insert({ pg : 6210, line : "-- Réjouis-toi de ces armes, déesse! Nous t'invoquons, toi qui es"});
db.iliadfr.insert({ pg : 6211, line : "la première entre tous les Olympiens immortels. Conduis-nous où"});
db.iliadfr.insert({ pg : 6212, line : "sont les guerriers Thrèkiens, leurs chevaux et leurs tentes."});
db.iliadfr.insert({ pg : 6214, line : "Il parla ainsi, et, levant les bras, il posa ces armes sur un"});
db.iliadfr.insert({ pg : 6215, line : "tamaris qu'il marqua d'un signe en nouant les roseaux et les"});
db.iliadfr.insert({ pg : 6216, line : "larges branches, afin de les reconnaître au retour, dans la nuit"});
db.iliadfr.insert({ pg : 6217, line : "noire."});
db.iliadfr.insert({ pg : 6219, line : "Et ils marchèrent ensuite à travers les armes et la plaine"});
db.iliadfr.insert({ pg : 6220, line : "sanglante, et ils parvinrent bientôt aux tentes des guerriers"});
db.iliadfr.insert({ pg : 6221, line : "Thrèkiens. Et ceux-ci dormaient, rompus de fatigue; et leurs"});
db.iliadfr.insert({ pg : 6222, line : "belles armes étaient couchées à terre auprès d'eux, sur trois"});
db.iliadfr.insert({ pg : 6223, line : "rangs. Et, auprès de chaque homme, il y avait deux chevaux. Et, au"});
db.iliadfr.insert({ pg : 6224, line : "milieu, dormait Rhèsos, et, auprès de lui, ses chevaux rapides"});
db.iliadfr.insert({ pg : 6225, line : "étaient attachés avec des courroies, derrière le char."});
db.iliadfr.insert({ pg : 6227, line : "Et Odysseus le vit le premier, et il le montra à Diomèdès:"});
db.iliadfr.insert({ pg : 6229, line : "-- Diomèdès, voici l'homme et les chevaux dont nous a parlé Dolôn"});
db.iliadfr.insert({ pg : 6230, line : "que nous avons tué. Allons! use de ta force et sers-toi de tes"});
db.iliadfr.insert({ pg : 6231, line : "armes. Détache ces chevaux, ou je le ferai moi-même si tu"});
db.iliadfr.insert({ pg : 6232, line : "préfères."});
db.iliadfr.insert({ pg : 6234, line : "Il parla ainsi, et Athènè aux yeux clairs donna une grande force à"});
db.iliadfr.insert({ pg : 6235, line : "Diomèdès. Et il tuait çà et là; et ceux qu'il frappait de l'épée"});
db.iliadfr.insert({ pg : 6236, line : "gémissaient, et la terre ruisselait de sang. Comme un lion,"});
db.iliadfr.insert({ pg : 6237, line : "tombant au milieu de troupeaux sans gardiens, se rue sur les"});
db.iliadfr.insert({ pg : 6238, line : "chèvres et les brebis; ainsi le fils de Tydeus se rua sur les"});
db.iliadfr.insert({ pg : 6239, line : "Thrèkiens, jusqu'à ce qu'il en eût tué douze. Et dès que le"});
db.iliadfr.insert({ pg : 6240, line : "Tydéide avait frappé, Odysseus, qui le suivait, traînait à l'écart"});
db.iliadfr.insert({ pg : 6241, line : "le cadavre par les pieds, pensant dans son esprit que les chevaux"});
db.iliadfr.insert({ pg : 6242, line : "aux belles crinières passeraient plus librement, et ne"});
db.iliadfr.insert({ pg : 6243, line : "s'effaroucheraient point, n'étant pas accoutumés à marcher sur les"});
db.iliadfr.insert({ pg : 6244, line : "morts. Et, lorsque le fils de Tydeus s'approcha du roi, ce fut le"});
db.iliadfr.insert({ pg : 6245, line : "treizième qu'il priva de sa chère âme. Et sur la tête de Rhèsos,"});
db.iliadfr.insert({ pg : 6246, line : "qui râlait, un songe fatal planait cette nuit-là, sous la forme de"});
db.iliadfr.insert({ pg : 6247, line : "l'Oinéide, et par la volonté d'Athènè."});
db.iliadfr.insert({ pg : 6249, line : "Cependant le patient Odysseus détacha les chevaux aux sabots"});
db.iliadfr.insert({ pg : 6250, line : "massifs, et, les liant avec les courroies, il les conduisit hors"});
db.iliadfr.insert({ pg : 6251, line : "du camp, les frappant de son arc, car il avait oublié de saisir le"});
db.iliadfr.insert({ pg : 6252, line : "fouet étincelant resté dans le beau char. Et, alors, il siffla"});
db.iliadfr.insert({ pg : 6253, line : "pour avertir le divin Diomèdès. Et celui-ci délibérait dans son"});
db.iliadfr.insert({ pg : 6254, line : "esprit si, avec plus d'audace encore, il n'entraînerait point, par"});
db.iliadfr.insert({ pg : 6255, line : "le timon, le char où étaient déposées les belles armes, ou s'il"});
db.iliadfr.insert({ pg : 6256, line : "arracherait la vie à un plus grand nombre de Thrèkiens. Pendant"});
db.iliadfr.insert({ pg : 6257, line : "qu'il délibérait ainsi dans son esprit, Athènè s'approcha et lui"});
db.iliadfr.insert({ pg : 6258, line : "dit:"});
db.iliadfr.insert({ pg : 6260, line : "-- Songe au retour, fils du magnanime Tydeus, de peur qu'un dieu"});
db.iliadfr.insert({ pg : 6261, line : "n'éveille les Troiens et que tu ne sois contraint de fuir vers les"});
db.iliadfr.insert({ pg : 6262, line : "nefs creuses."});
db.iliadfr.insert({ pg : 6264, line : "Elle parla ainsi, et il comprit les paroles de la déesse, et il"});
db.iliadfr.insert({ pg : 6265, line : "sauta sur les chevaux, et Odysseus les frappa de son arc, et ils"});
db.iliadfr.insert({ pg : 6266, line : "volaient vers les nefs rapides des Akhaiens. Mais Apollôn à l'arc"});
db.iliadfr.insert({ pg : 6267, line : "d'argent de ses yeux perçants vit Athènè auprès du fils de Tydeus."});
db.iliadfr.insert({ pg : 6268, line : "Irrité, il entra dans le camp des Troiens et réveilla le chef"});
db.iliadfr.insert({ pg : 6269, line : "Thrèkien Hippokoôn, brave parent de Rhèsos. Et celui-là, se"});
db.iliadfr.insert({ pg : 6270, line : "levant, vit déserte la place où étaient les chevaux rapides, et"});
db.iliadfr.insert({ pg : 6271, line : "les hommes palpitant dans leur sang; et il gémit, appelant son"});
db.iliadfr.insert({ pg : 6272, line : "cher compagnon par son nom. Et une immense clameur s'éleva parmi"});
db.iliadfr.insert({ pg : 6273, line : "les Troiens qui accouraient; et ils s'étonnaient de cette action"});
db.iliadfr.insert({ pg : 6274, line : "audacieuse, et que les hommes qui l'avaient accomplie fussent"});
db.iliadfr.insert({ pg : 6275, line : "retournés sains et saufs vers les nefs creuses."});
db.iliadfr.insert({ pg : 6277, line : "Et quand ceux-ci furent arrivés là où ils avaient tué l'espion de"});
db.iliadfr.insert({ pg : 6278, line : "Hektôr, Odysseus, cher à Zeus, arrêta les chevaux rapides. Et le"});
db.iliadfr.insert({ pg : 6279, line : "Tydéide, sautant à terre, remit aux mains d'Odysseus les"});
db.iliadfr.insert({ pg : 6280, line : "dépouilles sanglantes, et remonta. Et ils excitèrent les chevaux"});
db.iliadfr.insert({ pg : 6281, line : "qui volaient avec ardeur vers les nefs creuses. Et, le premier,"});
db.iliadfr.insert({ pg : 6282, line : "Nestôr entendit leur bruit et dit:"});
db.iliadfr.insert({ pg : 6284, line : "-- Ô amis, chefs et princes des Argiens, mentirai-je ou dirai-je"});
db.iliadfr.insert({ pg : 6285, line : "vrai? Mon coeur m'ordonne de parler. Le galop de chevaux rapides"});
db.iliadfr.insert({ pg : 6286, line : "frappe mes oreilles. Plaise aux dieux que, déjà, Odysseus et le"});
db.iliadfr.insert({ pg : 6287, line : "robuste Diomèdès aient enlevé aux Troiens des chevaux aux sabots"});
db.iliadfr.insert({ pg : 6288, line : "massifs; mais je crains avec véhémence, dans mon esprit, que les"});
db.iliadfr.insert({ pg : 6289, line : "plus braves des Argiens n'aient pu échapper à la foule des"});
db.iliadfr.insert({ pg : 6290, line : "Troiens!"});
db.iliadfr.insert({ pg : 6292, line : "Il avait à peine parlé, et les deux rois arrivèrent et"});
db.iliadfr.insert({ pg : 6293, line : "descendirent. Et tous, pleins de joie, les saluèrent de la main,"});
db.iliadfr.insert({ pg : 6294, line : "avec des paroles flatteuses. Et, le premier, le cavalier Gérennien"});
db.iliadfr.insert({ pg : 6295, line : "Nestôr les interrogea:"});
db.iliadfr.insert({ pg : 6297, line : "-- Dis-moi, Odysseus comblé de louanges, gloire des Akhaiens,"});
db.iliadfr.insert({ pg : 6298, line : "comment avez-vous enlevé ces chevaux? Est-ce en entrant dans le"});
db.iliadfr.insert({ pg : 6299, line : "camp des Troiens, ou avez-vous rencontré un dieu qui vous en ait"});
db.iliadfr.insert({ pg : 6300, line : "fait don? Ils sont semblables aux rayons de Hélios! Je me mêle,"});
db.iliadfr.insert({ pg : 6301, line : "certes, toujours aux Troiens, et je ne pense pas qu'on m'ait vu"});
db.iliadfr.insert({ pg : 6302, line : "rester auprès des nefs, bien que je sois vieux; mais je n'ai"});
db.iliadfr.insert({ pg : 6303, line : "jamais vu de tels chevaux. Je soupçonne qu'un dieu vous les a"});
db.iliadfr.insert({ pg : 6304, line : "donnés, car Zeus qui amasse les nuées vous aime tous deux, et"});
db.iliadfr.insert({ pg : 6305, line : "Athènè aux yeux clairs, fille de Zeus tempétueux, vous aime aussi."});
db.iliadfr.insert({ pg : 6307, line : "Et le subtil Odysseus lui répondit:"});
db.iliadfr.insert({ pg : 6309, line : "-- Nestôr Nèlèiade, gloire des Akhaiens, sans doute un dieu, s'il"});
db.iliadfr.insert({ pg : 6310, line : "l'eût voulu, nous eût donné des chevaux même au-dessus de ceux-ci,"});
db.iliadfr.insert({ pg : 6311, line : "car les dieux peuvent tout. Mais ces chevaux, sur lesquels tu"});
db.iliadfr.insert({ pg : 6312, line : "m'interroges, ô vieillard, sont Thrèkiens et arrivés récemment. Le"});
db.iliadfr.insert({ pg : 6313, line : "hardi Diomèdès a tué leur roi et douze des plus braves compagnons"});
db.iliadfr.insert({ pg : 6314, line : "de celui-ci. Nous avons tué, non loin des nefs, un quatorzième"});
db.iliadfr.insert({ pg : 6315, line : "guerrier, un espion que Hektôr et les illustres Troiens envoyaient"});
db.iliadfr.insert({ pg : 6316, line : "dans notre camp."});
db.iliadfr.insert({ pg : 6318, line : "Il parla ainsi, joyeux, et fit sauter le fossé aux chevaux. Et les"});
db.iliadfr.insert({ pg : 6319, line : "autres chefs Argiens, joyeux aussi, vinrent jusqu'à la tente"});
db.iliadfr.insert({ pg : 6320, line : "solide du Tydéide. Et ils attachèrent, avec de bonnes courroies,"});
db.iliadfr.insert({ pg : 6321, line : "les étalons Thrèkiens à la crèche devant laquelle les rapides"});
db.iliadfr.insert({ pg : 6322, line : "chevaux de Diomèdès se tenaient, broyant le doux froment. Et"});
db.iliadfr.insert({ pg : 6323, line : "Odysseus posa les dépouilles sanglantes de Dolôn sur la poupe de"});
db.iliadfr.insert({ pg : 6324, line : "sa nef, pour qu'elles fussent vouées à Athènè. Et tous deux, étant"});
db.iliadfr.insert({ pg : 6325, line : "entrés dans la mer pour enlever leur sueur, lavèrent leurs jambes,"});
db.iliadfr.insert({ pg : 6326, line : "leurs cuisses et leurs épaules. Et après que l'eau de la mer eut"});
db.iliadfr.insert({ pg : 6327, line : "enlevé leur sueur et qu'ils se furent ranimés, ils entrèrent dans"});
db.iliadfr.insert({ pg : 6328, line : "des baignoires polies. Et, s'étant parfumés d'une huile épaisse,"});
db.iliadfr.insert({ pg : 6329, line : "ils s'assirent pour le repas du matin, puisant dans un plein"});
db.iliadfr.insert({ pg : 6330, line : "kratère pour faire, en honneur d'Athènè, des libations de vin"});
db.iliadfr.insert({ pg : 6331, line : "doux."});
db.iliadfr.insert({ pg : 6334, line : "Chant 11"});
db.iliadfr.insert({ pg : 6336, line : "Éôs quitta le lit du brillant Tithôn, afin de porter la lumière"});
db.iliadfr.insert({ pg : 6337, line : "aux immortels et aux vivants. Et Zeus envoya Éris vers les nefs"});
db.iliadfr.insert({ pg : 6338, line : "rapides des Akhaiens, portant dans ses mains le signe terrible de"});
db.iliadfr.insert({ pg : 6339, line : "la guerre. Et elle s'arrêta sur la nef large et noire d'Odysseus,"});
db.iliadfr.insert({ pg : 6340, line : "qui était au centre, pour que son cri fût entendu de tous côtés,"});
db.iliadfr.insert({ pg : 6341, line : "depuis les tentes du Télamônien Aias jusqu'à celles d'Akhilleus;"});
db.iliadfr.insert({ pg : 6342, line : "car ceux-ci, confiants dans leur courage et la force de leurs"});
db.iliadfr.insert({ pg : 6343, line : "mains, avaient placé leurs nefs égales aux deux extrémités du"});
db.iliadfr.insert({ pg : 6344, line : "camp. De ce lieu, la déesse poussa un cri retentissant et horrible"});
db.iliadfr.insert({ pg : 6345, line : "qui souffla au coeur de chacun des Akhaiens un ardent désir de"});
db.iliadfr.insert({ pg : 6346, line : "guerroyer et de combattre sans relâche. Et, aussitôt, la guerre"});
db.iliadfr.insert({ pg : 6347, line : "leur fut plus douce que le retour, sur les nefs creuses, dans la"});
db.iliadfr.insert({ pg : 6348, line : "terre bien-aimée de la patrie."});
db.iliadfr.insert({ pg : 6350, line : "Et l'Atréide, élevant la voix, ordonna aux Argiens de s'armer; et"});
db.iliadfr.insert({ pg : 6351, line : "lui-même se couvrit de l'airain éclatant. Et, d'abord, il entoura"});
db.iliadfr.insert({ pg : 6352, line : "ses jambes de belles knèmides retenues par des agrafes d'argent."});
db.iliadfr.insert({ pg : 6353, line : "Ensuite, il ceignit sa poitrine d'une cuirasse que lui avait"});
db.iliadfr.insert({ pg : 6354, line : "autrefois donnée Kinyrès, son hôte. Kinyrès, ayant appris dans"});
db.iliadfr.insert({ pg : 6355, line : "Kypros par la renommée que les Akhaiens voguaient vers Ilios sur"});
db.iliadfr.insert({ pg : 6356, line : "leurs nefs, avait fait ce présent au roi. Et cette cuirasse avait"});
db.iliadfr.insert({ pg : 6357, line : "dix cannelures en émail noir, douze en or, vingt en étain. Et"});
db.iliadfr.insert({ pg : 6358, line : "trois dragons azurés s'enroulaient jusqu'au col, semblables aux"});
db.iliadfr.insert({ pg : 6359, line : "Iris que le Kroniôn fixa dans la nuée pour être un signe aux"});
db.iliadfr.insert({ pg : 6360, line : "vivants."});
db.iliadfr.insert({ pg : 6362, line : "Et il suspendit à ses épaules l'épée où étincelaient des clous"});
db.iliadfr.insert({ pg : 6363, line : "d'or dans la gaîne d'argent soutenue par des courroies d'or. Il"});
db.iliadfr.insert({ pg : 6364, line : "s'abrita tout entier sous un beau bouclier aux dix cercles"});
db.iliadfr.insert({ pg : 6365, line : "d'airain et aux vingt bosses d'étain blanc, au milieu desquelles"});
db.iliadfr.insert({ pg : 6366, line : "il y en avait une d'émail noir où s'enroulait Gorgô à l'aspect"});
db.iliadfr.insert({ pg : 6367, line : "effrayant et aux regards horribles. Auprès étaient la Crainte et"});
db.iliadfr.insert({ pg : 6368, line : "la Terreur. Et ce bouclier était suspendu à une courroie d'argent"});
db.iliadfr.insert({ pg : 6369, line : "où s'enroulait un dragon azuré dont le col se terminait en trois"});
db.iliadfr.insert({ pg : 6370, line : "têtes. Et il mit un casque chevelu orné de quatre cônes et"});
db.iliadfr.insert({ pg : 6371, line : "d'aigrettes de crin qui s'agitaient terriblement. Et il prit deux"});
db.iliadfr.insert({ pg : 6372, line : "lances solides aux pointes d'airain qui brillaient jusqu'à"});
db.iliadfr.insert({ pg : 6373, line : "l'Ouranos. Et Athènaiè et Hèrè éveillèrent un grand bruit pour"});
db.iliadfr.insert({ pg : 6374, line : "honorer le roi de la riche Mykènè."});
db.iliadfr.insert({ pg : 6376, line : "Et les chefs ordonnèrent aux conducteurs des chars de retenir les"});
db.iliadfr.insert({ pg : 6377, line : "chevaux auprès du fossé, tandis qu'ils se ruaient couverts de"});
db.iliadfr.insert({ pg : 6378, line : "leurs armes. Et une immense clameur s'éleva avant le jour. Et les"});
db.iliadfr.insert({ pg : 6379, line : "chars et les chevaux, rangés auprès du fossé, suivaient à peu de"});
db.iliadfr.insert({ pg : 6380, line : "distance les guerriers; ceux-ci les précédèrent, et le cruel"});
db.iliadfr.insert({ pg : 6381, line : "Kronide excita un grand tumulte et fit pleuvoir du haut de"});
db.iliadfr.insert({ pg : 6382, line : "l'aithèr des rosées teintes de sang, en signe qu'il allait"});
db.iliadfr.insert({ pg : 6383, line : "précipiter chez Aidès une foule de têtes illustres."});
db.iliadfr.insert({ pg : 6385, line : "De leur côté, les Troiens se rangeaient sur la hauteur autour du"});
db.iliadfr.insert({ pg : 6386, line : "grand Hektôr, de l'irréprochable Polydamas, d'Ainéias qui, dans"});
db.iliadfr.insert({ pg : 6387, line : "Ilios, était honoré comme un dieu par les Troiens, des trois"});
db.iliadfr.insert({ pg : 6388, line : "Anténorides, Polybos, le divin Agènôr et le jeune Akamas,"});
db.iliadfr.insert({ pg : 6389, line : "semblable aux immortels."});
db.iliadfr.insert({ pg : 6391, line : "Et, entre les premiers combattants, Hektôr portait son bouclier"});
db.iliadfr.insert({ pg : 6392, line : "poli. De même qu'une étoile désastreuse s'éveille, brillante, et"});
db.iliadfr.insert({ pg : 6393, line : "s'avance à travers les nuées obscures, de même Hektôr apparaissait"});
db.iliadfr.insert({ pg : 6394, line : "en tête des premiers combattants, ou au milieu d'eux, et leur"});
db.iliadfr.insert({ pg : 6395, line : "commandant à tous; et il resplendissait, couvert d'airain, pareil"});
db.iliadfr.insert({ pg : 6396, line : "à l'éclair du père Zeus tempêtueux."});
db.iliadfr.insert({ pg : 6398, line : "Et, comme deux troupes opposées de moissonneurs qui tranchent les"});
db.iliadfr.insert({ pg : 6399, line : "gerbes dans le champ d'un homme riche, les Troiens et les Akhaiens"});
db.iliadfr.insert({ pg : 6400, line : "s'entretuaient, se ruant les uns contre les autres, oublieux de la"});
db.iliadfr.insert({ pg : 6401, line : "fuite funeste, inébranlables et tels que des loups."});
db.iliadfr.insert({ pg : 6403, line : "Et la désastreuse Éris se réjouissait de les voir, car, seule de"});
db.iliadfr.insert({ pg : 6404, line : "tous les dieux, elle assistait au combat. Et les autres immortels"});
db.iliadfr.insert({ pg : 6405, line : "étaient absents, et chacun d'eux était assis, tranquille dans sa"});
db.iliadfr.insert({ pg : 6406, line : "belle demeure, sur les sommets de l'Olympos. Et ils blâmaient le"});
db.iliadfr.insert({ pg : 6407, line : "Kroniôn qui amasse les noires nuées, parce qu'il voulait donner"});
db.iliadfr.insert({ pg : 6408, line : "une grande gloire aux Troiens. Mais le père Zeus, assis à l'écart,"});
db.iliadfr.insert({ pg : 6409, line : "ne s'inquiétait point d'eux. Et il siégeait, plein de gloire,"});
db.iliadfr.insert({ pg : 6410, line : "regardant la ville des Troiens et les nefs des Akhaiens, et"});
db.iliadfr.insert({ pg : 6411, line : "l'éclat de l'airain, et ceux qui reculaient, et ceux qui"});
db.iliadfr.insert({ pg : 6412, line : "s'élançaient."});
db.iliadfr.insert({ pg : 6414, line : "Tant que l'aube dura et que le jour sacré prit de la force, les"});
db.iliadfr.insert({ pg : 6415, line : "traits sifflèrent des deux côtés et les hommes moururent; mais,"});
db.iliadfr.insert({ pg : 6416, line : "vers l'heure où le bûcheron prend son repas dans les gorges de la"});
db.iliadfr.insert({ pg : 6417, line : "montagne, et que, les bras rompus d'avoir coupé les grands arbres,"});
db.iliadfr.insert({ pg : 6418, line : "et le coeur défaillant, il ressent le désir d'une douce"});
db.iliadfr.insert({ pg : 6419, line : "nourriture, les Danaens, s'exhortant les uns les autres, rompirent"});
db.iliadfr.insert({ pg : 6420, line : "les phalanges. Et Agamemnôn bondit le premier et tua le guerrier"});
db.iliadfr.insert({ pg : 6421, line : "Bianôr, prince des peuples, et son compagnon Oileus qui conduisait"});
db.iliadfr.insert({ pg : 6422, line : "les chevaux. Et celui-ci, sautant du char, lui avait fait face. Et"});
db.iliadfr.insert({ pg : 6423, line : "l'Atréide, comme il sautait, le frappa au front de la lance aiguë,"});
db.iliadfr.insert({ pg : 6424, line : "et le casque épais ne résista point à l'airain qui y pénétra,"});
db.iliadfr.insert({ pg : 6425, line : "brisa le crâne et traversa la cervelle du guerrier qui s'élançait."});
db.iliadfr.insert({ pg : 6426, line : "Et le roi des hommes, Agamemnôn, les abandonna tous deux en ce"});
db.iliadfr.insert({ pg : 6427, line : "lieu, après avoir arraché leurs cuirasses étincelantes."});
db.iliadfr.insert({ pg : 6429, line : "Puis, il s'avança pour tuer Isos et Antiphos, deux fils de"});
db.iliadfr.insert({ pg : 6430, line : "Priamos, l'un bâtard et l'autre légitime, montés sur le même char."});
db.iliadfr.insert({ pg : 6431, line : "Et le bâtard tenait les rênes, et l'illustre Antiphos combattait."});
db.iliadfr.insert({ pg : 6432, line : "Akhilleus les avait autrefois saisis et liés avec des branches"});
db.iliadfr.insert({ pg : 6433, line : "d'osier, sur les sommets de l'Ida, comme ils paissaient leurs"});
db.iliadfr.insert({ pg : 6434, line : "brebis; et il avait accepté le prix de leur affranchissement. Mais"});
db.iliadfr.insert({ pg : 6435, line : "voici que l'Atréide Agamemnôn qui commandait au loin perça Isos"});
db.iliadfr.insert({ pg : 6436, line : "d'un coup de lance au-dessus de la mamelle, et, frappant Antiphos"});
db.iliadfr.insert({ pg : 6437, line : "de l'épée auprès de l'oreille, le renversa du char. Et, comme il"});
db.iliadfr.insert({ pg : 6438, line : "leur arrachait leurs belles armes, il les reconnut, les ayant vus"});
db.iliadfr.insert({ pg : 6439, line : "auprès des nefs, quand Akhilleus aux pieds rapides les y avait"});
db.iliadfr.insert({ pg : 6440, line : "amenés des sommets de l'Ida."});
db.iliadfr.insert({ pg : 6442, line : "Ainsi un lion brise aisément, dans son antre, les saisissant avec"});
db.iliadfr.insert({ pg : 6443, line : "ses fortes dents, les faibles petits d'une biche légère, et"});
db.iliadfr.insert({ pg : 6444, line : "arrache leur âme délicate. Et la biche accourt, mais elle ne peut"});
db.iliadfr.insert({ pg : 6445, line : "les secourir, car une profonde terreur la saisit; et elle s'élance"});
db.iliadfr.insert({ pg : 6446, line : "à travers les fourrés de chênes des bois, effarée et suant"});
db.iliadfr.insert({ pg : 6447, line : "d'épouvante devant la fureur de la puissante bête féroce. De même"});
db.iliadfr.insert({ pg : 6448, line : "nul ne put conjurer la perte des Priamides, et tous fuyaient"});
db.iliadfr.insert({ pg : 6449, line : "devant les Argiens."});
db.iliadfr.insert({ pg : 6451, line : "Et le roi Agamemnôn saisit sur le même char Peisandros et le brave"});
db.iliadfr.insert({ pg : 6452, line : "Hippolokhos, fils tous deux du belliqueux Antimakhos. Et celui-ci,"});
db.iliadfr.insert({ pg : 6453, line : "ayant accepté l'or et les présents splendides d'Alexandros,"});
db.iliadfr.insert({ pg : 6454, line : "n'avait pas permis que Hélénè fût rendue au brave Ménélaos. Et"});
db.iliadfr.insert({ pg : 6455, line : "comme l'Atréide se ruait sur eux, tel qu'un lion, ils furent"});
db.iliadfr.insert({ pg : 6456, line : "troublés; et, les souples rênes étant tombées de leurs mains,"});
db.iliadfr.insert({ pg : 6457, line : "leurs chevaux rapides les emportaient. Et, prosternés sur le char,"});
db.iliadfr.insert({ pg : 6458, line : "ils suppliaient Agamemnôn:"});
db.iliadfr.insert({ pg : 6460, line : "-- Prends-nous vivants, fils d'Atreus, et reçois le prix de notre"});
db.iliadfr.insert({ pg : 6461, line : "affranchissement. De nombreuses richesses sont amassées dans les"});
db.iliadfr.insert({ pg : 6462, line : "demeures d'Antimakhos, l'or, l'airain et le fer propre à être"});
db.iliadfr.insert({ pg : 6463, line : "travaillé. Notre père t'en donnera la plus grande partie pour"});
db.iliadfr.insert({ pg : 6464, line : "notre affranchissement, s'il apprend que nous sommes vivants sur"});
db.iliadfr.insert({ pg : 6465, line : "les nefs des Akhaiens."});
db.iliadfr.insert({ pg : 6467, line : "En pleurant, ils adressaient au roi ces douces paroles, mais ils"});
db.iliadfr.insert({ pg : 6468, line : "entendirent une dure réponse:"});
db.iliadfr.insert({ pg : 6470, line : "-- Si vous êtes les fils du brave Antimakhos qui, autrefois, dans"});
db.iliadfr.insert({ pg : 6471, line : "l'agora des Troiens, conseillait de tuer nos envoyés, Ménélaos et"});
db.iliadfr.insert({ pg : 6472, line : "le divin Odysseus, et de ne point les laisser revenir vers les"});
db.iliadfr.insert({ pg : 6473, line : "Akhaiens, maintenant vous allez payer l'injure de votre père."});
db.iliadfr.insert({ pg : 6475, line : "Il parla ainsi, et, frappant de sa lance Peisandros à la poitrine,"});
db.iliadfr.insert({ pg : 6476, line : "il le renversa dans la poussière, et, comme Hippolokhos sautait,"});
db.iliadfr.insert({ pg : 6477, line : "il le tua à terre; et, lui coupant les bras et le cou, il le fit"});
db.iliadfr.insert({ pg : 6478, line : "rouler comme un tronc mort à travers la foule. Et il les abandonna"});
db.iliadfr.insert({ pg : 6479, line : "pour se ruer sur les phalanges en désordre, suivi des Akhaiens aux"});
db.iliadfr.insert({ pg : 6480, line : "belles knèmides. Et les piétons tuaient les piétons qui fuyaient,"});
db.iliadfr.insert({ pg : 6481, line : "et les cavaliers tuaient les cavaliers. Et, sous leurs pieds, et"});
db.iliadfr.insert({ pg : 6482, line : "sous les pieds sonores des chevaux, une grande poussière montait"});
db.iliadfr.insert({ pg : 6483, line : "de la plaine dans l'air. Et le roi Agamemnôn allait, tuant"});
db.iliadfr.insert({ pg : 6484, line : "toujours et excitant les Argiens."});
db.iliadfr.insert({ pg : 6486, line : "Ainsi, quand la flamme désastreuse dévore une épaisse forêt, et"});
db.iliadfr.insert({ pg : 6487, line : "quand le vent qui tourbillonne l'active de tous côtés, les arbres"});
db.iliadfr.insert({ pg : 6488, line : "tombent sous l'impétuosité du feu. De même, sous l'Atréide"});
db.iliadfr.insert({ pg : 6489, line : "Agamemnôn, tombaient les têtes des Troiens en fuite. Les chevaux"});
db.iliadfr.insert({ pg : 6490, line : "entraînaient, effarés, la tête haute, les chars vides à travers"});
db.iliadfr.insert({ pg : 6491, line : "les rangs, et regrettaient leurs conducteurs irréprochables qui"});
db.iliadfr.insert({ pg : 6492, line : "gisaient contre terre, plus agréables aux oiseaux carnassiers qu'à"});
db.iliadfr.insert({ pg : 6493, line : "leurs femmes."});
db.iliadfr.insert({ pg : 6495, line : "Et Zeus conduisit Hektôr loin des lances, loin de la poussière,"});
db.iliadfr.insert({ pg : 6496, line : "loin du carnage et du sang. Et l'Atréide, excitant les Danaens,"});
db.iliadfr.insert({ pg : 6497, line : "poursuivait ardemment l'ennemi. Et les Troiens, auprès du tombeau"});
db.iliadfr.insert({ pg : 6498, line : "de l'antique Dardanide Ilos, se précipitaient dans la plaine,"});
db.iliadfr.insert({ pg : 6499, line : "désirant rentrer dans la ville. Et ils approchaient du figuier, et"});
db.iliadfr.insert({ pg : 6500, line : "l'Atréide les poursuivait, baignant de leur sang ses mains rudes,"});
db.iliadfr.insert({ pg : 6501, line : "et poussant des cris. Et, lorsqu'ils furent parvenus au hêtre et"});
db.iliadfr.insert({ pg : 6502, line : "aux portes Skaies, ils s'arrêtèrent, s'attendant les uns les"});
db.iliadfr.insert({ pg : 6503, line : "autres. Et la multitude fuyait dispersée à travers la plaine,"});
db.iliadfr.insert({ pg : 6504, line : "comme un troupeau de vaches qu'un lion, brusquement survenu,"});
db.iliadfr.insert({ pg : 6505, line : "épouvante au milieu de la nuit; mais une seule d'entre elles meurt"});
db.iliadfr.insert({ pg : 6506, line : "chaque fois. Le lion, l'ayant saisie de ses fortes dents, lui"});
db.iliadfr.insert({ pg : 6507, line : "brise le cou, boit son sang et dévore ses entrailles. Ainsi"});
db.iliadfr.insert({ pg : 6508, line : "l'Atréide Agamemnôn les poursuivait, tuant toujours le dernier; et"});
db.iliadfr.insert({ pg : 6509, line : "ils fuyaient. Un grand nombre d'entre eux tombait, la tête la"});
db.iliadfr.insert({ pg : 6510, line : "première, ou se renversait du haut des chars sous les mains de"});
db.iliadfr.insert({ pg : 6511, line : "l'Atréide dont la lance était furieuse. Mais, quand on fut parvenu"});
db.iliadfr.insert({ pg : 6512, line : "à la ville et à ses hautes murailles, le père des hommes et des"});
db.iliadfr.insert({ pg : 6513, line : "dieux descendit de l'Ouranos sur les sommets de l'Ida aux sources"});
db.iliadfr.insert({ pg : 6514, line : "abondantes, avec la foudre aux mains, et il appela la messagère"});
db.iliadfr.insert({ pg : 6515, line : "Iris aux ailes d'or:"});
db.iliadfr.insert({ pg : 6517, line : "-- Va! rapide Iris, et dis à Hektôr qu'il se tienne en repos et"});
db.iliadfr.insert({ pg : 6518, line : "qu'il ordonne au reste de l'armée de combattre l'ennemi aussi"});
db.iliadfr.insert({ pg : 6519, line : "longtemps qu'il verra le prince des peuples, Agamemnôn, se jeter"});
db.iliadfr.insert({ pg : 6520, line : "furieux aux premiers rangs et rompre les lignes des guerriers."});
db.iliadfr.insert({ pg : 6521, line : "Mais, dès que l'Atréide, frappé d'un coup de lance ou blessé d'une"});
db.iliadfr.insert({ pg : 6522, line : "flèche, remontera sur son char, je rendrai au Priamide la force de"});
db.iliadfr.insert({ pg : 6523, line : "tuer; et il tuera, étant parvenu aux nefs bien construites,"});
db.iliadfr.insert({ pg : 6524, line : "jusqu'à ce que Hélios tombe et que la nuit sacrée s'élève."});
db.iliadfr.insert({ pg : 6526, line : "Il parla ainsi, et la rapide Iris aux pieds prompts comme le vent"});
db.iliadfr.insert({ pg : 6527, line : "lui obéit. Et elle descendit des sommets de l'Ida vers la sainte"});
db.iliadfr.insert({ pg : 6528, line : "Ilios, et elle trouva le fils du belliqueux Priamos, le divin"});
db.iliadfr.insert({ pg : 6529, line : "Hektôr, debout sur son char solide. Et Iris aux pieds rapides"});
db.iliadfr.insert({ pg : 6530, line : "s'approcha et lui dit:"});
db.iliadfr.insert({ pg : 6532, line : "-- Fils de Priamos, Hektôr, égal à Zeus en sagesse, le père Zeus"});
db.iliadfr.insert({ pg : 6533, line : "m'envoie te dire ceci: Tiens-toi en repos, et ordonne au reste de"});
db.iliadfr.insert({ pg : 6534, line : "l'armée de combattre l'ennemi, aussi longtemps que tu verras le"});
db.iliadfr.insert({ pg : 6535, line : "prince des peuples, Agamemnôn, se jeter furieux aux premiers rangs"});
db.iliadfr.insert({ pg : 6536, line : "des combattants et rompre les lignes des guerriers; mais dès que"});
db.iliadfr.insert({ pg : 6537, line : "l'Atréide, frappé d'un coup de lance ou blessé d'une flèche,"});
db.iliadfr.insert({ pg : 6538, line : "remontera sur son char, Zeus te rendra la force de tuer, et tu"});
db.iliadfr.insert({ pg : 6539, line : "tueras, étant parvenu aux nefs bien construites, jusqu'à ce que"});
db.iliadfr.insert({ pg : 6540, line : "Hélios tombe et que la nuit sacrée s'élève."});
db.iliadfr.insert({ pg : 6542, line : "Ayant ainsi parlé, Iris aux pieds rapides disparut. Et Hektôr,"});
db.iliadfr.insert({ pg : 6543, line : "sautant du haut de son char, avec ses armes, et agitant ses lances"});
db.iliadfr.insert({ pg : 6544, line : "aiguës, courut de tous côtés à travers l'armée, l'excitant au"});
db.iliadfr.insert({ pg : 6545, line : "combat. Et les Troiens, se retournant, firent face aux Akhaiens."});
db.iliadfr.insert({ pg : 6546, line : "Et les Argiens s'arrêtèrent, serrant leurs phalanges pour soutenir"});
db.iliadfr.insert({ pg : 6547, line : "le combat; mais Agamemnôn se rua en avant, voulant combattre le"});
db.iliadfr.insert({ pg : 6548, line : "premier."});
db.iliadfr.insert({ pg : 6550, line : "Dites-moi maintenant, Muses qui habitez les demeures ouraniennes,"});
db.iliadfr.insert({ pg : 6551, line : "celui des Troiens ou des illustres alliés qui s'avança le premier"});
db.iliadfr.insert({ pg : 6552, line : "contre Agamemnôn. Ce fut Iphidamas Anténoride, grand et robuste,"});
db.iliadfr.insert({ pg : 6553, line : "élevé dans la fertile Thrèkiè, nourrice de brebis. Et son aïeul"});
db.iliadfr.insert({ pg : 6554, line : "maternel Kisseus, qui engendra Théanô aux belles joues, l'éleva"});
db.iliadfr.insert({ pg : 6555, line : "tout enfant dans ses demeures; et quand il eut atteint la"});
db.iliadfr.insert({ pg : 6556, line : "glorieuse puberté, il le retint en lui donnant sa fille pour"});
db.iliadfr.insert({ pg : 6557, line : "femme. Et quand le jeune guerrier apprit l'arrivée des Akhaiens,"});
db.iliadfr.insert({ pg : 6558, line : "il quitta sa demeure nuptiale et vint avec douze nefs aux poupes"});
db.iliadfr.insert({ pg : 6559, line : "recourbées qu'il laissa à Perkopè. Et il vint à pied jusque dans"});
db.iliadfr.insert({ pg : 6560, line : "Ilios. Et ce fut lui qui s'avança contre Agamemnôn. Tous deux"});
db.iliadfr.insert({ pg : 6561, line : "s'étant rencontrés, l'Atréide le manqua de sa lance qui se"});
db.iliadfr.insert({ pg : 6562, line : "détourna du but. Et Iphidamas frappa au-dessous de la cuirasse,"});
db.iliadfr.insert({ pg : 6563, line : "sur le ceinturon; et il poussa sa lance avec vigueur, sans la"});
db.iliadfr.insert({ pg : 6564, line : "quitter; mais il ne perça point le ceinturon habilement fait, et"});
db.iliadfr.insert({ pg : 6565, line : "la pointe de l'arme, rencontrant une lame d'argent, se tordit"});
db.iliadfr.insert({ pg : 6566, line : "comme du plomb. Et Agamemnôn qui commande au loin, rapide comme un"});
db.iliadfr.insert({ pg : 6567, line : "lion, saisit la lance, et, l'arrachant, frappa de son épée"});
db.iliadfr.insert({ pg : 6568, line : "l'Anténoride au cou, et le tua. Ainsi ce malheureux, en secourant"});
db.iliadfr.insert({ pg : 6569, line : "ses concitoyens, s'endormit d'un sommeil d'airain, loin de sa"});
db.iliadfr.insert({ pg : 6570, line : "jeune femme dont il n'avait point vu le bonheur. Et il lui avait"});
db.iliadfr.insert({ pg : 6571, line : "fait de nombreux présents, lui ayant d'abord donné cent boeufs, et"});
db.iliadfr.insert({ pg : 6572, line : "lui ayant promis mille chèvres et brebis. Et voici que l'Atréide"});
db.iliadfr.insert({ pg : 6573, line : "Agamemnôn le dépouilla, et rentra dans la foule des Akhaiens,"});
db.iliadfr.insert({ pg : 6574, line : "emportant ses belles armes."});
db.iliadfr.insert({ pg : 6576, line : "Et l'illustre guerrier Koôn, l'aîné des Anténorides, l'aperçut, et"});
db.iliadfr.insert({ pg : 6577, line : "une amère douleur obscurcit ses yeux quand il vit son frère mort."});
db.iliadfr.insert({ pg : 6578, line : "En se cachant, il frappa le divin Agamemnôn d'un coup de lance au"});
db.iliadfr.insert({ pg : 6579, line : "milieu du bras, sous le coude, et la pointe de l'arme brillante"});
db.iliadfr.insert({ pg : 6580, line : "traversa le bras. Et le roi des hommes, Agamemnôn, frissonna;"});
db.iliadfr.insert({ pg : 6581, line : "mais, loin d'abandonner le combat, il se rua sur Koôn, armé de sa"});
db.iliadfr.insert({ pg : 6582, line : "lance solide. Et celui-ci traînait par les pieds son frère"});
db.iliadfr.insert({ pg : 6583, line : "Iphidamas, né du même père, et il appelait les plus braves à son"});
db.iliadfr.insert({ pg : 6584, line : "aide. Mais, comme il l'entraînait, l'Atréide le frappa de sa lance"});
db.iliadfr.insert({ pg : 6585, line : "d'airain sous son bouclier rond, et il le tua; et il lui coupa la"});
db.iliadfr.insert({ pg : 6586, line : "tête sur le corps même d'Iphidamas. Ainsi les deux fils d'Antènôr,"});
db.iliadfr.insert({ pg : 6587, line : "sous la main du roi Atréide, accomplissant leurs destinées,"});
db.iliadfr.insert({ pg : 6588, line : "descendirent aux demeures d'Aidès."});
db.iliadfr.insert({ pg : 6590, line : "Et l'Atréide continua d'enfoncer les lignes des guerriers à coups"});
db.iliadfr.insert({ pg : 6591, line : "de lance, d'épée ou de lourdes roches, aussi longtemps que le sang"});
db.iliadfr.insert({ pg : 6592, line : "coula, chaud, de sa blessure; mais dès que la plaie fut desséchée,"});
db.iliadfr.insert({ pg : 6593, line : "que le sang s'arrêta, les douleurs aiguës domptèrent sa force,"});
db.iliadfr.insert({ pg : 6594, line : "semblables à ces douleurs amères que les filles de Hèrè, les"});
db.iliadfr.insert({ pg : 6595, line : "Éileithyes, envoient comme des traits acerbes à la femme qui"});
db.iliadfr.insert({ pg : 6596, line : "enfante. Ainsi les douleurs aiguës domptèrent la force de"});
db.iliadfr.insert({ pg : 6597, line : "l'Atréide. Il monta sur son char, ordonnant au conducteur des"});
db.iliadfr.insert({ pg : 6598, line : "chevaux de les pousser vers les nefs creuses, car il défaillait"});
db.iliadfr.insert({ pg : 6599, line : "dans son coeur. Et il dit aux Danaens, criant à haute voix pour"});
db.iliadfr.insert({ pg : 6600, line : "être entendu:"});
db.iliadfr.insert({ pg : 6602, line : "-- Ô amis, chefs et princes des Argiens, c'est à vous maintenant"});
db.iliadfr.insert({ pg : 6603, line : "d'éloigner le combat désastreux des nefs qui traversent la mer,"});
db.iliadfr.insert({ pg : 6604, line : "puisque le sage Zeus ne me permet pas de combattre les Troiens"});
db.iliadfr.insert({ pg : 6605, line : "pendant toute la durée du jour."});
db.iliadfr.insert({ pg : 6607, line : "Il parla ainsi, et le conducteur du char fouetta les chevaux aux"});
db.iliadfr.insert({ pg : 6608, line : "beaux crins du côté des nefs creuses, et ils couraient avec"});
db.iliadfr.insert({ pg : 6609, line : "ardeur, le poitrail écumant, soulevant la poussière et entraînant"});
db.iliadfr.insert({ pg : 6610, line : "leur roi blessé, loin du combat. Et dès que Hektôr s'aperçut de la"});
db.iliadfr.insert({ pg : 6611, line : "retraite d'Agamemnôn, il excita à haute voix les Troiens et les"});
db.iliadfr.insert({ pg : 6612, line : "Lykiens."});
db.iliadfr.insert({ pg : 6614, line : "-- Troiens, Lykiens et Dardaniens, hardis combattants, soyez des"});
db.iliadfr.insert({ pg : 6615, line : "hommes! Amis, souvenez-vous de votre courage intrépide. Ce"});
db.iliadfr.insert({ pg : 6616, line : "guerrier si brave se retire, et Zeus Kronide veut me donner une"});
db.iliadfr.insert({ pg : 6617, line : "grande gloire. Poussez droit vos chevaux aux durs sabots sur les"});
db.iliadfr.insert({ pg : 6618, line : "robustes Danaens, afin de remporter une gloire sans égale."});
db.iliadfr.insert({ pg : 6620, line : "Ayant ainsi parlé, il excita la force et le courage de chacun. De"});
db.iliadfr.insert({ pg : 6621, line : "même qu'un chasseur excite les chiens aux blanches dents contre un"});
db.iliadfr.insert({ pg : 6622, line : "sauvage sanglier ou contre un lion, de même le Priamide Hektôr,"});
db.iliadfr.insert({ pg : 6623, line : "semblable au cruel Arès, excita les magnanimes Troiens contre les"});
db.iliadfr.insert({ pg : 6624, line : "Akhaiens. Et lui-même, sûr de son courage, se rua des premiers"});
db.iliadfr.insert({ pg : 6625, line : "dans la mêlée, semblable au tourbillon orageux qui tombe sur la"});
db.iliadfr.insert({ pg : 6626, line : "haute mer et la bouleverse."});
db.iliadfr.insert({ pg : 6628, line : "Et, maintenant, quel fut le premier, quel fut le dernier que tua"});
db.iliadfr.insert({ pg : 6629, line : "le Priamide Hektôr, quand Zeus voulut le glorifier? Assaios,"});
db.iliadfr.insert({ pg : 6630, line : "d'abord, et Autonoos, et Opitès, et Dolops Klytide, et Opheltiôn,"});
db.iliadfr.insert({ pg : 6631, line : "et Agélaos, et Aisymnos, Oros et le magnanime Hipponoos. Et il tua"});
db.iliadfr.insert({ pg : 6632, line : "chacun de ces princes Danaens. Puis, il tomba sur la multitude,"});
db.iliadfr.insert({ pg : 6633, line : "tel que Zéphyros qui agite les nuées, lorsqu'il flagelle les"});
db.iliadfr.insert({ pg : 6634, line : "vapeurs tempêtueuses amassées par le Notos furieux, qu'il déroule"});
db.iliadfr.insert({ pg : 6635, line : "les flots énormes, et, de ses souffles épars, disperse l'écume"});
db.iliadfr.insert({ pg : 6636, line : "dans les hauteurs de l'air. De même, Hektôr fit tomber une foule"});
db.iliadfr.insert({ pg : 6637, line : "de têtes guerrières."});
db.iliadfr.insert({ pg : 6639, line : "Alors, c'eût été le jour d'un désastre fatal et de maux"});
db.iliadfr.insert({ pg : 6640, line : "incurables, et les Argiens, dans leur fuite, eussent succombé"});
db.iliadfr.insert({ pg : 6641, line : "auprès des nefs, si Odysseus n'eût exhorté le Tydéide Diomèdès:"});
db.iliadfr.insert({ pg : 6643, line : "-- Tydéide, avons-nous oublié notre courage intrépide? Viens"});
db.iliadfr.insert({ pg : 6644, line : "auprès de moi, très cher; car ce nous serait un grand opprobre si"});
db.iliadfr.insert({ pg : 6645, line : "Hektôr au casque mouvant s'emparait des nefs."});
db.iliadfr.insert({ pg : 6647, line : "Et le robuste Diomèdès lui répondit:"});
db.iliadfr.insert({ pg : 6649, line : "-- Me voici, certes, prêt à combattre. Mais notre joie sera brève,"});
db.iliadfr.insert({ pg : 6650, line : "puisque Zeus qui amasse les nuées veut donner la victoire aux"});
db.iliadfr.insert({ pg : 6651, line : "Troiens."});
db.iliadfr.insert({ pg : 6653, line : "Il parla ainsi, et il renversa Tymbraios de son char, l'ayant"});
db.iliadfr.insert({ pg : 6654, line : "frappé de sa lance à la mamelle gauche. Et Odysseus tua Moliôn, le"});
db.iliadfr.insert({ pg : 6655, line : "divin compagnon de Thymbraios. Et ils abandonnèrent les deux"});
db.iliadfr.insert({ pg : 6656, line : "guerriers ainsi éloignés du combat, et ils se jetèrent dans la"});
db.iliadfr.insert({ pg : 6657, line : "mêlée. Et comme deux sangliers audacieux qui reviennent sur les"});
db.iliadfr.insert({ pg : 6658, line : "chiens chasseurs, ils contraignirent les Troiens de reculer, et"});
db.iliadfr.insert({ pg : 6659, line : "les Akhaiens, en proie au divin Hektôr, respirèrent un moment. Et"});
db.iliadfr.insert({ pg : 6660, line : "les deux rois prirent un char et deux guerriers très braves, fils"});
db.iliadfr.insert({ pg : 6661, line : "du Perkosien Mérops, habile divinateur, qui avait défendu à ses"});
db.iliadfr.insert({ pg : 6662, line : "fils de partir pour la guerre fatale. Mais ils ne lui obéirent"});
db.iliadfr.insert({ pg : 6663, line : "pas, et les kères de la mort les entraînèrent. Et l'illustre"});
db.iliadfr.insert({ pg : 6664, line : "Tydéide Diomèdès leur enleva l'âme et la vie, et les dépouilla de"});
db.iliadfr.insert({ pg : 6665, line : "leurs belles armes, tandis qu'Odysseus tuait Hippodamos et"});
db.iliadfr.insert({ pg : 6666, line : "Hypeirokhos. Alors, le Kroniôn, les regardant du haut de l'Ida,"});
db.iliadfr.insert({ pg : 6667, line : "rétablit le combat, afin qu'ils se tuassent également des deux"});
db.iliadfr.insert({ pg : 6668, line : "côtés."});
db.iliadfr.insert({ pg : 6670, line : "Et le fils de Tydeus blessa de sa lance à la cuisse le héros"});
db.iliadfr.insert({ pg : 6671, line : "Agastrophos Paionide. Et les chevaux du Paionide étaient trop"});
db.iliadfr.insert({ pg : 6672, line : "éloignés pour l'aider à fuir; et il gémissait dans son âme de ce"});
db.iliadfr.insert({ pg : 6673, line : "que le conducteur du char l'eût retenu en arrière, tandis qu'il"});
db.iliadfr.insert({ pg : 6674, line : "s'élançait à pied parmi les combattants, jusqu'à ce qu'il eût"});
db.iliadfr.insert({ pg : 6675, line : "perdu la douce vie. Mais Hektôr, l'ayant vu aux premières lignes,"});
db.iliadfr.insert({ pg : 6676, line : "se rua en poussant de grands cris, suivi des phalanges Troiennes."});
db.iliadfr.insert({ pg : 6677, line : "Et le hardi Diomèdès, à cette vue, frissonna et dit à Odysseus"});
db.iliadfr.insert({ pg : 6678, line : "debout près de lui:"});
db.iliadfr.insert({ pg : 6680, line : "-- C'est sur nous que le furieux Hektôr roule ce tourbillon"});
db.iliadfr.insert({ pg : 6681, line : "sinistre; mais restons inébranlables, et nous repousserons son"});
db.iliadfr.insert({ pg : 6682, line : "attaque."});
db.iliadfr.insert({ pg : 6684, line : "Il parla ainsi, et il lança sa longue pique qui ne se détourna pas"});
db.iliadfr.insert({ pg : 6685, line : "du but, car le coup atteignit la tête du Priamide, au sommet du"});
db.iliadfr.insert({ pg : 6686, line : "casque. La pointe d'airain ne pénétra point et fut repoussée, et"});
db.iliadfr.insert({ pg : 6687, line : "le triple airain du casque que Phoibos Apollôn avait donné au"});
db.iliadfr.insert({ pg : 6688, line : "Priamide le garantit; mais il recula aussitôt, rentra dans la"});
db.iliadfr.insert({ pg : 6689, line : "foule, et, tombant sur ses genoux, appuya contre terre sa main"});
db.iliadfr.insert({ pg : 6690, line : "robuste, et la noire nuit couvrit ses yeux."});
db.iliadfr.insert({ pg : 6692, line : "Et, pendant que Diomèdès, suivant de près le vol impétueux de sa"});
db.iliadfr.insert({ pg : 6693, line : "lance, la relevait à l'endroit où elle était tombée, Hektôr,"});
db.iliadfr.insert({ pg : 6694, line : "ranimé, monta sur son char, se perdit dans la foule et évita la"});
db.iliadfr.insert({ pg : 6695, line : "noire mort. Et le robuste Diomèdès, le menaçant de sa lance, lui"});
db.iliadfr.insert({ pg : 6696, line : "cria:"});
db.iliadfr.insert({ pg : 6698, line : "-- Ô chien! tu as de nouveau évité la mort qui a passé près de"});
db.iliadfr.insert({ pg : 6699, line : "toi. Phoibos Apollôn t'a sauvé encore une fois, lui que tu"});
db.iliadfr.insert({ pg : 6700, line : "supplies toujours au milieu du choc des lances. Mais, certes, je"});
db.iliadfr.insert({ pg : 6701, line : "te tuerai si je te retrouve et qu'un des dieux me vienne en aide."});
db.iliadfr.insert({ pg : 6702, line : "Maintenant, je vais attaquer tous ceux que je pourrai saisir."});
db.iliadfr.insert({ pg : 6704, line : "Et, parlant ainsi, il tua l'illustre Paionide."});
db.iliadfr.insert({ pg : 6706, line : "Mais Alexandros, l'époux de Hélénè à la belle chevelure, appuyé"});
db.iliadfr.insert({ pg : 6707, line : "contre la colonne du tombeau de l'antique guerrier Dardanide Ilos,"});
db.iliadfr.insert({ pg : 6708, line : "tendit son arc contre le Tydéide Diomèdès, prince des peuples. Et,"});
db.iliadfr.insert({ pg : 6709, line : "comme celui-ci arrachait la cuirasse brillante, le bouclier et le"});
db.iliadfr.insert({ pg : 6710, line : "casque épais du robuste Agastrophos, Alexandros tendit l'arc de"});
db.iliadfr.insert({ pg : 6711, line : "corne et perça d'une flèche certaine le pied droit de Diomèdès;"});
db.iliadfr.insert({ pg : 6712, line : "et, à travers le pied, la flèche s'enfonça en terre. Et"});
db.iliadfr.insert({ pg : 6713, line : "Alexandros, riant aux éclats, sortit de son abri, et dit en se"});
db.iliadfr.insert({ pg : 6714, line : "vantant:"});
db.iliadfr.insert({ pg : 6716, line : "-- Te voilà blessé! ma flèche n'a pas été vaine. Plût aux dieux"});
db.iliadfr.insert({ pg : 6717, line : "qu'elle se fût enfoncée dans ton ventre et que je t'eusse tué! Les"});
db.iliadfr.insert({ pg : 6718, line : "Troiens, qui te redoutent, comme des chèvres en face d'un lion,"});
db.iliadfr.insert({ pg : 6719, line : "respireraient plus à l'aise."});
db.iliadfr.insert({ pg : 6721, line : "Et l'intrépide et robuste Diomèdès lui répondit:"});
db.iliadfr.insert({ pg : 6723, line : "-- Misérable archer, aussi vain de tes cheveux que de ton arc,"});
db.iliadfr.insert({ pg : 6724, line : "séducteur de vierges! si tu combattais face à face contre moi, tes"});
db.iliadfr.insert({ pg : 6725, line : "flèches te seraient d'un vain secours. Voici que tu te glorifies"});
db.iliadfr.insert({ pg : 6726, line : "pour m'avoir percé le pied! Je m'en soucie autant que si une femme"});
db.iliadfr.insert({ pg : 6727, line : "ou un enfant m'avait atteint par imprudence. Le trait d'un lâche"});
db.iliadfr.insert({ pg : 6728, line : "est aussi vil que lui. Mais celui que je touche seulement de ma"});
db.iliadfr.insert({ pg : 6729, line : "lance expire aussitôt. Sa femme se déchire les joues, ses enfants"});
db.iliadfr.insert({ pg : 6730, line : "sont orphelins, et il rougit la terre de son sang, et il se"});
db.iliadfr.insert({ pg : 6731, line : "corrompt, et il y a autour de lui plus d'oiseaux carnassiers que"});
db.iliadfr.insert({ pg : 6732, line : "de femmes en pleurs."});
db.iliadfr.insert({ pg : 6734, line : "Il parla ainsi, et l'illustre Odysseus se plaça devant lui; et, se"});
db.iliadfr.insert({ pg : 6735, line : "baissant, il arracha la flèche de son pied; mais aussitôt il"});
db.iliadfr.insert({ pg : 6736, line : "ressentit dans tout le corps une amère douleur. Et, le coeur"});
db.iliadfr.insert({ pg : 6737, line : "défaillant, il monta sur son char, ordonnant au conducteur de le"});
db.iliadfr.insert({ pg : 6738, line : "ramener aux nefs creuses."});
db.iliadfr.insert({ pg : 6740, line : "Et l'illustre Odysseus, resté seul, car tous les Argiens s'étaient"});
db.iliadfr.insert({ pg : 6741, line : "enfuis, gémit et se dit dans son coeur magnanime:"});
db.iliadfr.insert({ pg : 6743, line : "-- Hélas! que vais-je devenir? Ce serait une grande honte que de"});
db.iliadfr.insert({ pg : 6744, line : "reculer devant cette multitude; mais ne serait-il pas plus cruel"});
db.iliadfr.insert({ pg : 6745, line : "de mourir seul ici, puisque le Kroniôn a mis tous les Danaens en"});
db.iliadfr.insert({ pg : 6746, line : "fuite? Mais pourquoi délibérer dans mon coeur? Je sais que les"});
db.iliadfr.insert({ pg : 6747, line : "lâches seuls reculent dans la mêlée. Le brave, au contraire,"});
db.iliadfr.insert({ pg : 6748, line : "combat de pied ferme, soit qu'il frappe, soit qu'il soit frappé."});
db.iliadfr.insert({ pg : 6750, line : "Pendant qu'il délibérait ainsi dans son esprit et dans son coeur,"});
db.iliadfr.insert({ pg : 6751, line : "les phalanges des Troiens porteurs de boucliers survinrent et"});
db.iliadfr.insert({ pg : 6752, line : "enfermèrent de tous côtés leur fléau. De même que les chiens"});
db.iliadfr.insert({ pg : 6753, line : "vigoureux et les jeunes chasseurs entourent un sanglier, dans"});
db.iliadfr.insert({ pg : 6754, line : "l'épaisseur d'un bois, et que celui-ci leur fait tête en aiguisant"});
db.iliadfr.insert({ pg : 6755, line : "ses blanches défenses dans ses mâchoires torses, et que tous"});
db.iliadfr.insert({ pg : 6756, line : "l'environnent malgré ses défenses furieuses et son aspect"});
db.iliadfr.insert({ pg : 6757, line : "horrible; de même, les Troiens se pressaient autour d'Odysseus"});
db.iliadfr.insert({ pg : 6758, line : "cher à Zeus. Mais le Laertiade blessa d'abord l'irréprochable"});
db.iliadfr.insert({ pg : 6759, line : "Deiopis à l'épaule, de sa lance aiguë; et il tua Thoôn et Ennomos."});
db.iliadfr.insert({ pg : 6760, line : "Et comme Khersidamas sautait de son char, il le perça sous le"});
db.iliadfr.insert({ pg : 6761, line : "bouclier, au nombril; et le Troien roula dans la poussière,"});
db.iliadfr.insert({ pg : 6762, line : "saisissant la terre à pleines mains. Et le Laertiade les"});
db.iliadfr.insert({ pg : 6763, line : "abandonna, et il blessa de sa lance Kharops Hippaside, frère de"});
db.iliadfr.insert({ pg : 6764, line : "l'illustre Sôkos. Et Sôkos, semblable à un dieu, accourant au"});
db.iliadfr.insert({ pg : 6765, line : "secours de son frère, s'approcha et lui dit:"});
db.iliadfr.insert({ pg : 6767, line : "-- Ô Odysseus, insatiable de ruses et de travaux, aujourd'hui tu"});
db.iliadfr.insert({ pg : 6768, line : "triompheras des deux Hippasides, et, les ayant tués, tu enlèveras"});
db.iliadfr.insert({ pg : 6769, line : "leurs armes, ou, frappé de ma lance, tu perdras la vie."});
db.iliadfr.insert({ pg : 6771, line : "Ayant ainsi parlé, il frappa le bouclier arrondi, et la lance"});
db.iliadfr.insert({ pg : 6772, line : "solide perça le bouclier étincelant, et, à travers la cuirasse"});
db.iliadfr.insert({ pg : 6773, line : "habilement travaillée, déchira la peau au-dessus des poumons; mais"});
db.iliadfr.insert({ pg : 6774, line : "Athènè ne permit pas qu'elle pénétrât jusqu'aux entrailles. Et"});
db.iliadfr.insert({ pg : 6775, line : "Odysseus, sentant que le coup n'était pas mortel, recula et dit à"});
db.iliadfr.insert({ pg : 6776, line : "Sôkos:"});
db.iliadfr.insert({ pg : 6778, line : "-- Malheureux! voici que la mort accablante va te saisir. Tu me"});
db.iliadfr.insert({ pg : 6779, line : "contrains de ne plus combattre les Troiens, mais je t'apporte"});
db.iliadfr.insert({ pg : 6780, line : "aujourd'hui la noire mort; et, dompté par ma lance, tu vas me"});
db.iliadfr.insert({ pg : 6781, line : "combler de gloire et rendre ton âme à Aidès aux beaux chevaux."});
db.iliadfr.insert({ pg : 6783, line : "Il parla ainsi, et, comme Sôkos fuyait, il le frappa de sa lance"});
db.iliadfr.insert({ pg : 6784, line : "dans le dos, entre les épaules, et lui traversa la poitrine. Il"});
db.iliadfr.insert({ pg : 6785, line : "tomba avec bruit, et le divin Odysseus s'écria en se glorifiant:"});
db.iliadfr.insert({ pg : 6787, line : "-- Ô Sôkos, fils de l'habile cavalier Hippasos, la mort t'a"});
db.iliadfr.insert({ pg : 6788, line : "devancé et tu n'as pu lui échapper. Ah! malheureux! ton père et ta"});
db.iliadfr.insert({ pg : 6789, line : "mère vénérable ne fermeront point tes yeux, et les seuls oiseaux"});
db.iliadfr.insert({ pg : 6790, line : "carnassiers agiteront autour de toi leurs lourdes ailes. Mais"});
db.iliadfr.insert({ pg : 6791, line : "quand je serai mort, les divins Akhaiens célébreront mes"});
db.iliadfr.insert({ pg : 6792, line : "funérailles."});
db.iliadfr.insert({ pg : 6794, line : "Ayant ainsi parlé, il arracha de son bouclier et de son corps la"});
db.iliadfr.insert({ pg : 6795, line : "lance solide du brave Sôkos, et aussitôt son sang jaillit de la"});
db.iliadfr.insert({ pg : 6796, line : "plaie, et son coeur se troubla. Et les magnanimes Troiens, voyant"});
db.iliadfr.insert({ pg : 6797, line : "le sang d'Odysseus, se ruèrent en foule sur lui; et il reculait,"});
db.iliadfr.insert({ pg : 6798, line : "en appelant ses compagnons. Et il cria trois fois aussi haut que"});
db.iliadfr.insert({ pg : 6799, line : "le peut un homme, et le brave Ménélaos l'entendit trois fois et"});
db.iliadfr.insert({ pg : 6800, line : "dit aussitôt au Télamônien Aias:"});
db.iliadfr.insert({ pg : 6802, line : "-- Divin Aias Télamônien, prince des peuples, j'entends la voix du"});
db.iliadfr.insert({ pg : 6803, line : "patient Odysseus, semblable à celle d'un homme que les Troiens"});
db.iliadfr.insert({ pg : 6804, line : "auraient enveloppé dans la mêlée. Allons à travers la foule. Il"});
db.iliadfr.insert({ pg : 6805, line : "faut le secourir. Je crains qu'il ait été abandonné au milieu des"});
db.iliadfr.insert({ pg : 6806, line : "Troiens, et que, malgré son courage, il périsse, laissant d'amers"});
db.iliadfr.insert({ pg : 6807, line : "regrets aux Danaens."});
db.iliadfr.insert({ pg : 6809, line : "Ayant ainsi parlé, il s'élança, et le divin Aias le suivit, et ils"});
db.iliadfr.insert({ pg : 6810, line : "trouvèrent Odysseus au milieu des Troiens qui l'enveloppaient."});
db.iliadfr.insert({ pg : 6812, line : "Ainsi des loups affamés, sur les montagnes, hurlent autour d'un"});
db.iliadfr.insert({ pg : 6813, line : "vieux cerf qu'un chasseur a blessé d'une flèche. Il a fui, tant"});
db.iliadfr.insert({ pg : 6814, line : "que son sang a été tiède et que ses genoux ont pu se mouvoir; mais"});
db.iliadfr.insert({ pg : 6815, line : "dès qu'il est tombé sous le coup de la flèche rapide, les loups"});
db.iliadfr.insert({ pg : 6816, line : "carnassiers le déchirent sur les montagnes, au fond des bois. Et"});
db.iliadfr.insert({ pg : 6817, line : "voici qu'un lion survient qui enlève la proie, tandis que les"});
db.iliadfr.insert({ pg : 6818, line : "loups s'enfuient épouvantés. Ainsi les robustes Troiens se"});
db.iliadfr.insert({ pg : 6819, line : "pressaient autour du subtil et prudent Odysseus qui, se ruant à"});
db.iliadfr.insert({ pg : 6820, line : "coups de lance, éloignait sa dernière heure. Et Aias, portant un"});
db.iliadfr.insert({ pg : 6821, line : "bouclier semblable à une tour, parut à son côté, et les Troiens"});
db.iliadfr.insert({ pg : 6822, line : "prirent la fuite çà et là. Et le brave Ménélaos, saisissant"});
db.iliadfr.insert({ pg : 6823, line : "Odysseus par la main, le retira de la mêlée, tandis qu'un"});
db.iliadfr.insert({ pg : 6824, line : "serviteur faisait approcher le char."});
db.iliadfr.insert({ pg : 6826, line : "Et Aias, bondissant au milieu des Troiens, tua Doryklos, bâtard de"});
db.iliadfr.insert({ pg : 6827, line : "Priamos, et Pandokos, et Lysandros, et Pyrasos, et Pylartès. De"});
db.iliadfr.insert({ pg : 6828, line : "même qu'un fleuve, gonflé par les pluies de Zeus, descend, comme"});
db.iliadfr.insert({ pg : 6829, line : "un torrent, des montagnes dans la plaine, emportant un grand"});
db.iliadfr.insert({ pg : 6830, line : "nombre de chênes déracinés et de pins, et roule ses limons dans la"});
db.iliadfr.insert({ pg : 6831, line : "mer; de même l'illustre Aias, se ruant dans la mêlée, tuait les"});
db.iliadfr.insert({ pg : 6832, line : "hommes et les chevaux."});
db.iliadfr.insert({ pg : 6834, line : "Hektôr ignorait ceci, car il combattait vers la gauche, sur les"});
db.iliadfr.insert({ pg : 6835, line : "rives du fleuve Skamandros, là où les têtes des hommes tombaient"});
db.iliadfr.insert({ pg : 6836, line : "en plus grand nombre, et où de grandes clameurs s'élevaient autour"});
db.iliadfr.insert({ pg : 6837, line : "du cavalier Nestôr et du brave Idoméneus. Hektôr les assiégeait de"});
db.iliadfr.insert({ pg : 6838, line : "sa lance et de ses chevaux, et rompait les phalanges des"});
db.iliadfr.insert({ pg : 6839, line : "guerriers; mais les divins Akhaiens n'eussent point reculé, si"});
db.iliadfr.insert({ pg : 6840, line : "Alexandros, l'époux de la belle Hélénè, n'eût blessé à l'épaule"});
db.iliadfr.insert({ pg : 6841, line : "droite, d'une flèche à trois pointes, le brave Makhaôn, prince des"});
db.iliadfr.insert({ pg : 6842, line : "peuples. Alors les vigoureux Akhaiens craignirent, s'ils"});
db.iliadfr.insert({ pg : 6843, line : "reculaient, d'exposer la vie de ce guerrier."});
db.iliadfr.insert({ pg : 6845, line : "Et, aussitôt, Idoméneus dit au divin Nestôr:"});
db.iliadfr.insert({ pg : 6847, line : "-- Ô Nestôr Nèlèiade, gloire des Akhaiens, hâte-toi, monte sur ton"});
db.iliadfr.insert({ pg : 6848, line : "char avec Makhaôn, et pousse vers les nefs tes chevaux aux sabots"});
db.iliadfr.insert({ pg : 6849, line : "massifs. Un médecin vaut plusieurs hommes, car il sait extraire"});
db.iliadfr.insert({ pg : 6850, line : "les flèches et répandre les doux baumes dans les blessures."});
db.iliadfr.insert({ pg : 6852, line : "Il parla ainsi, et le cavalier Gérennien Nestôr lui obéit. Et il"});
db.iliadfr.insert({ pg : 6853, line : "monta sur son char avec Makhaôn, fils de l'irréprochable médecin"});
db.iliadfr.insert({ pg : 6854, line : "Asklèpios. Et il flagellait les chevaux, et ceux-ci volaient"});
db.iliadfr.insert({ pg : 6855, line : "ardemment vers les nefs creuses."});
db.iliadfr.insert({ pg : 6857, line : "Cependant Kébrionès, assis auprès de Hektôr sur le même char, vit"});
db.iliadfr.insert({ pg : 6858, line : "au loin le trouble des Troiens et dit au Priamide:"});
db.iliadfr.insert({ pg : 6860, line : "-- Hektôr, tandis que nous combattons ici les Danaens, à"});
db.iliadfr.insert({ pg : 6861, line : "l'extrémité de la mêlée, les autres Troiens fuient pêle-mêle avec"});
db.iliadfr.insert({ pg : 6862, line : "leurs chars. C'est le Télamônien Aias qui les a rompus. Je le"});
db.iliadfr.insert({ pg : 6863, line : "reconnais bien, car il porte un vaste bouclier sur ses épaules."});
db.iliadfr.insert({ pg : 6864, line : "C'est pourquoi il nous faut pousser nos chevaux et notre char de"});
db.iliadfr.insert({ pg : 6865, line : "ce côté, là où les cavaliers et les piétons s'entretuent et où"});
db.iliadfr.insert({ pg : 6866, line : "s'élève une immense clameur."});
db.iliadfr.insert({ pg : 6868, line : "Il parla ainsi et frappa du fouet éclatant les chevaux aux belles"});
db.iliadfr.insert({ pg : 6869, line : "crinières; et, sous le fouet, ceux-ci entraînèrent rapidement le"});
db.iliadfr.insert({ pg : 6870, line : "char entre les Troiens et les Akhaiens, écrasant les cadavres et"});
db.iliadfr.insert({ pg : 6871, line : "les armes. Et les jantes et les moyeux des roues étaient aspergés"});
db.iliadfr.insert({ pg : 6872, line : "du sang qui jaillissait sous les sabots des chevaux. Et le"});
db.iliadfr.insert({ pg : 6873, line : "Priamide, plein du désir de pénétrer dans la mêlée et de rompre"});
db.iliadfr.insert({ pg : 6874, line : "les phalanges, apportait le trouble et la mort aux Danaens, et il"});
db.iliadfr.insert({ pg : 6875, line : "assiégeait leurs lignes ébranlées, en les attaquant à coups de"});
db.iliadfr.insert({ pg : 6876, line : "lance, d'épée et de lourdes roches. Mais il évitait d'attaquer le"});
db.iliadfr.insert({ pg : 6877, line : "Télamônien Aias."});
db.iliadfr.insert({ pg : 6879, line : "Alors le père Zeus saisit Aias d'une crainte soudaine. Et celui-"});
db.iliadfr.insert({ pg : 6880, line : "ci, étonné, s'arrêta. Et, rejetant sur son dos son bouclier aux"});
db.iliadfr.insert({ pg : 6881, line : "sept peaux de boeuf, il recula, regardant toujours la foule."});
db.iliadfr.insert({ pg : 6882, line : "Semblable à une bête fauve, il reculait pas à pas, faisant face à"});
db.iliadfr.insert({ pg : 6883, line : "l'ennemi. Comme un lion fauve que les chiens et les pâtres"});
db.iliadfr.insert({ pg : 6884, line : "chassent loin de l'étable des boeufs, car ils veillaient avec"});
db.iliadfr.insert({ pg : 6885, line : "vigilance, sans qu'il ait pu savourer les chairs grasses dont il"});
db.iliadfr.insert({ pg : 6886, line : "était avide, bien qu'il se soit précipité avec fureur, et qui,"});
db.iliadfr.insert({ pg : 6887, line : "accablé sous les torches et les traits que lui lancent des mains"});
db.iliadfr.insert({ pg : 6888, line : "audacieuses, s'éloigne, au matin, plein de tristesse et frémissant"});
db.iliadfr.insert({ pg : 6889, line : "de rage; de même Aias reculait, le coeur troublé, devant les"});
db.iliadfr.insert({ pg : 6890, line : "Troiens, craignant pour les nefs des Akhaiens."});
db.iliadfr.insert({ pg : 6892, line : "De même un âne têtu entre dans un champ, malgré les efforts des"});
db.iliadfr.insert({ pg : 6893, line : "enfants qui brisent leurs bâtons sur son dos. Il continue à paître"});
db.iliadfr.insert({ pg : 6894, line : "la moisson, sans se soucier des faibles coups qui l'atteignent, et"});
db.iliadfr.insert({ pg : 6895, line : "se retire à grand'peine quand il est rassasié. Ainsi les"});
db.iliadfr.insert({ pg : 6896, line : "magnanimes Troiens et leurs alliés frappaient de leurs lances"});
db.iliadfr.insert({ pg : 6897, line : "Aias, le grand fils de Télamôn. Ils frappaient son bouclier, et le"});
db.iliadfr.insert({ pg : 6898, line : "poursuivaient; mais Aias, reprenant parfois ses forces"});
db.iliadfr.insert({ pg : 6899, line : "impétueuses, se retournait et repoussait les phalanges des"});
db.iliadfr.insert({ pg : 6900, line : "cavaliers Troiens; puis, il reculait de nouveau, les empêchant"});
db.iliadfr.insert({ pg : 6901, line : "ainsi de se précipiter tous à la fois vers les nefs rapides. Or,"});
db.iliadfr.insert({ pg : 6902, line : "il combattait seul dans l'intervalle qui séparait les Troiens et"});
db.iliadfr.insert({ pg : 6903, line : "les Akhaiens. Et les traits hérissaient son grand bouclier, ou"});
db.iliadfr.insert({ pg : 6904, line : "s'enfonçaient en terre sans se rassasier de sa chair blanche dont"});
db.iliadfr.insert({ pg : 6905, line : "ils étaient avides."});
db.iliadfr.insert({ pg : 6907, line : "Et l'illustre fils d'Évaimôn, Eurypylos, l'aperçut ainsi assiégé"});
db.iliadfr.insert({ pg : 6908, line : "d'un nuage de traits. Et il accourut à ses côtés, et il lança sa"});
db.iliadfr.insert({ pg : 6909, line : "pique éclatante. Et il perça le Phausiade Apisaôn, prince des"});
db.iliadfr.insert({ pg : 6910, line : "peuples, dans le foie, sous le diaphragme, et il le tua. Et"});
db.iliadfr.insert({ pg : 6911, line : "Eurypylos, s'élançant, lui arracha ses armes. Mais lorsque le"});
db.iliadfr.insert({ pg : 6912, line : "divin Alexandros le vit emportant les armes d'Apisaôn, il tendit"});
db.iliadfr.insert({ pg : 6913, line : "son arc contre lui et il le perça d'une flèche à la cuisse droite."});
db.iliadfr.insert({ pg : 6914, line : "Le roseau se brisa, la cuisse s'engourdit, et l'Évaimônide,"});
db.iliadfr.insert({ pg : 6915, line : "rentrant dans la foule de ses compagnons, afin d'éviter la mort,"});
db.iliadfr.insert({ pg : 6916, line : "cria d'une voix haute afin d'être entendu des Danaens:"});
db.iliadfr.insert({ pg : 6918, line : "-- Ô amis, chefs et princes des Argiens, arrêtez et retournez-"});
db.iliadfr.insert({ pg : 6919, line : "vous. Éloignez la dernière heure d'Aias qui est accablé de traits,"});
db.iliadfr.insert({ pg : 6920, line : "et qui, je pense, ne sortira pas vivant de la mêlée terrible."});
db.iliadfr.insert({ pg : 6921, line : "Serrez-vous donc autour d'Aias, le grand fils de Télamôn."});
db.iliadfr.insert({ pg : 6923, line : "Eurypylos, blessé, parla ainsi; mais ses compagnons se pressèrent"});
db.iliadfr.insert({ pg : 6924, line : "autour de lui, le bouclier incliné et la lance en arrêt. Et Aias,"});
db.iliadfr.insert({ pg : 6925, line : "les ayant rejoints, fit avec eux face à l'ennemi. Et ils"});
db.iliadfr.insert({ pg : 6926, line : "combattirent de nouveau, tels que des flammes ardentes."});
db.iliadfr.insert({ pg : 6928, line : "Mais les cavales du Nèlèide emportaient loin du combat, et"});
db.iliadfr.insert({ pg : 6929, line : "couvertes d'écume, Nestôr, et Makhaôn, prince des peuples."});
db.iliadfr.insert({ pg : 6931, line : "Et le divin Akhilleus aux pieds rapides les reconnut. Et, debout"});
db.iliadfr.insert({ pg : 6932, line : "sur la poupe de sa vaste nef, il regardait le rude combat et la"});
db.iliadfr.insert({ pg : 6933, line : "défaite lamentable. Et il appela son compagnon Patroklos. Celui-ci"});
db.iliadfr.insert({ pg : 6934, line : "l'entendit et sortit de ses tentes, semblable à Arès. Et ce fut"});
db.iliadfr.insert({ pg : 6935, line : "l'origine de son malheur. Et le brave fils de Ménoitios dit le"});
db.iliadfr.insert({ pg : 6936, line : "premier:"});
db.iliadfr.insert({ pg : 6938, line : "-- Pourquoi m'appelles-tu, Akhilleus? Que veux-tu de moi?"});
db.iliadfr.insert({ pg : 6940, line : "Et Akhilleus aux pieds rapides lui répondit:"});
db.iliadfr.insert({ pg : 6942, line : "-- Divin Ménoitiade, très cher à mon âme, j'espère maintenant que"});
db.iliadfr.insert({ pg : 6943, line : "les Akhaiens ne tarderont pas à tomber suppliants à mes genoux,"});
db.iliadfr.insert({ pg : 6944, line : "car une intolérable nécessité les assiège. Va donc, Patroklos cher"});
db.iliadfr.insert({ pg : 6945, line : "à Zeus, et demande à Nestôr quel est le guerrier blessé qu'il"});
db.iliadfr.insert({ pg : 6946, line : "ramène du combat. Il ressemble à l'Asklèpiade Makhaôn, mais je"});
db.iliadfr.insert({ pg : 6947, line : "n'ai point vu son visage, et les chevaux l'ont emporté rapidement."});
db.iliadfr.insert({ pg : 6949, line : "Il parla ainsi, et Patroklos obéit à son cher compagnon, et il"});
db.iliadfr.insert({ pg : 6950, line : "s'élança vers les tentes et les nefs des Akhaiens."});
db.iliadfr.insert({ pg : 6952, line : "Et quand Nestôr et Makhaôn furent arrivés aux tentes du Nèlèide,"});
db.iliadfr.insert({ pg : 6953, line : "ils sautèrent du char sur la terre nourricière. Et le serviteur du"});
db.iliadfr.insert({ pg : 6954, line : "vieillard, Eurymèdôn, détela les chevaux. Et les deux rois, ayant"});
db.iliadfr.insert({ pg : 6955, line : "séché leur sueur au vent de la mer, entrèrent sous la tente et"});
db.iliadfr.insert({ pg : 6956, line : "prirent des sièges, et Hékamèdè aux beaux cheveux leur prépara à"});
db.iliadfr.insert({ pg : 6957, line : "boire. Et Nestôr l'avait amenée de Ténédos qu'Akhilleus venait de"});
db.iliadfr.insert({ pg : 6958, line : "détruire; et c'était la fille du magnanime Arsinoos, et les"});
db.iliadfr.insert({ pg : 6959, line : "Akhaiens l'avaient donnée au Nèlèide parce qu'il les surpassait"});
db.iliadfr.insert({ pg : 6960, line : "tous par sa prudence."});
db.iliadfr.insert({ pg : 6962, line : "Elle posa devant eux une belle table aux pieds de métal azuré, et,"});
db.iliadfr.insert({ pg : 6963, line : "sur cette table, un bassin d'airain poli avec des oignons pour"});
db.iliadfr.insert({ pg : 6964, line : "exciter à boire, et du miel vierge et de la farine sacrée; puis,"});
db.iliadfr.insert({ pg : 6965, line : "une très-belle coupe enrichie de clous d'or, que le vieillard"});
db.iliadfr.insert({ pg : 6966, line : "avait apportée de ses demeures. Et cette coupe avait quatre anses"});
db.iliadfr.insert({ pg : 6967, line : "et deux fonds, et, sur chaque anse, deux colombes d'or semblaient"});
db.iliadfr.insert({ pg : 6968, line : "manger. Tout autre l'eût soulevée avec peine quand elle était"});
db.iliadfr.insert({ pg : 6969, line : "remplie, mais le vieux Nestôr la soulevait facilement."});
db.iliadfr.insert({ pg : 6971, line : "Et la jeune femme, semblable aux déesses, prépara une boisson de"});
db.iliadfr.insert({ pg : 6972, line : "vin de Pramneios, et sur ce vin elle râpa, avec de l'airain, du"});
db.iliadfr.insert({ pg : 6973, line : "fromage de chèvre, qu'elle aspergea de blanche farine. Et, après"});
db.iliadfr.insert({ pg : 6974, line : "ces préparatifs, elle invita les deux rois à boire; et ceux-ci,"});
db.iliadfr.insert({ pg : 6975, line : "ayant bu et étanché la soif brûlante, charmèrent leur repos en"});
db.iliadfr.insert({ pg : 6976, line : "parlant tour à tour."});
db.iliadfr.insert({ pg : 6978, line : "Et le divin Patroklos parut alors à l'entrée de la tente. Et le"});
db.iliadfr.insert({ pg : 6979, line : "vieillard, l'ayant aperçu, se leva de son siège éclatant, le prit"});
db.iliadfr.insert({ pg : 6980, line : "par la main et voulut le faire asseoir; mais Patroklos recula et"});
db.iliadfr.insert({ pg : 6981, line : "lui dit:"});
db.iliadfr.insert({ pg : 6983, line : "-- Je ne puis me reposer, divin vieillard, et tu ne me persuaderas"});
db.iliadfr.insert({ pg : 6984, line : "pas. Il est terrible et irritable celui qui m'envoie te demander"});
db.iliadfr.insert({ pg : 6985, line : "quel est le guerrier blessé que tu as ramené. Mais je le vois et"});
db.iliadfr.insert({ pg : 6986, line : "je reconnais Makhaôn, prince des peuples. Maintenant je"});
db.iliadfr.insert({ pg : 6987, line : "retournerai vers Akhilleus pour lui donner cette nouvelle, car tu"});
db.iliadfr.insert({ pg : 6988, line : "sais, divin vieillard, combien il est impatient et prompt à"});
db.iliadfr.insert({ pg : 6989, line : "accuser, même un innocent."});
db.iliadfr.insert({ pg : 6991, line : "Et le cavalier Gérennien Nestôr lui répondit:"});
db.iliadfr.insert({ pg : 6993, line : "-- Pourquoi Akhilleus a-t-il ainsi pitié des fils des Akhaiens que"});
db.iliadfr.insert({ pg : 6994, line : "les traits ont percés? Ignore-t-il donc le deuil qui enveloppe"});
db.iliadfr.insert({ pg : 6995, line : "l'armée? Déjà les plus braves gisent sur leurs nefs, frappés ou"});
db.iliadfr.insert({ pg : 6996, line : "blessés. Le robuste Tydéide Diomèdès est blessé, et Odysseus"});
db.iliadfr.insert({ pg : 6997, line : "illustre par sa lance, et Agamemnôn. Une flèche a percé la cuisse"});
db.iliadfr.insert({ pg : 6998, line : "d'Eurypylos, et c'est aussi une flèche qui a frappé Makhaôn que je"});
db.iliadfr.insert({ pg : 6999, line : "viens de ramener du combat. Mais le brave Akhilleus n'a ni souci"});
db.iliadfr.insert({ pg : 7000, line : "ni pitié des Danaens. Attend-il que les nefs rapides soient en"});
db.iliadfr.insert({ pg : 7001, line : "proie aux flammes, malgré les Argiens, et que ceux-ci périssent"});
db.iliadfr.insert({ pg : 7002, line : "jusqu'au dernier? Je n'ai plus la force qui animait autrefois mes"});
db.iliadfr.insert({ pg : 7003, line : "membres agiles. Plût aux dieux que je fusse florissant de jeunesse"});
db.iliadfr.insert({ pg : 7004, line : "et de vigueur, comme au temps où une dissension s'éleva entre nous"});
db.iliadfr.insert({ pg : 7005, line : "et les Élidiens, à cause d'un enlèvement de boeufs, quand je tuai"});
db.iliadfr.insert({ pg : 7006, line : "le robuste Hypeirokhide Itymoneus qui habitait Élis, et dont"});
db.iliadfr.insert({ pg : 7007, line : "j'enlevai les boeufs par représailles. Et il les défendait, mais"});
db.iliadfr.insert({ pg : 7008, line : "je le frappai d'un coup de lance, aux premiers rangs, et il tomba."});
db.iliadfr.insert({ pg : 7009, line : "Et ses tribus sauvages s'enfuirent en tumulte, et nous enlevâmes"});
db.iliadfr.insert({ pg : 7010, line : "un grand butin: cinquante troupeaux de boeufs, autant de brebis,"});
db.iliadfr.insert({ pg : 7011, line : "autant de porcs et autant de chèvres, cent cinquante cavales baies"});
db.iliadfr.insert({ pg : 7012, line : "et leurs nombreux poulains. Et nous les conduisîmes, pendant la"});
db.iliadfr.insert({ pg : 7013, line : "nuit, dans Pylos, la ville de Nèleus. Et Nèleus se réjouit dans"});
db.iliadfr.insert({ pg : 7014, line : "son coeur, parce que j'avais fait toutes ces choses, ayant"});
db.iliadfr.insert({ pg : 7015, line : "combattu pour la première fois. Et, au lever du jour, les hérauts"});
db.iliadfr.insert({ pg : 7016, line : "convoquèrent ceux dont les troupeaux avaient été emmenés dans la"});
db.iliadfr.insert({ pg : 7017, line : "fertile Élis; et les chefs Pyliens, s'étant réunis, partagèrent le"});
db.iliadfr.insert({ pg : 7018, line : "butin. Mais alors les Épéiens nous opprimaient, car nous étions"});
db.iliadfr.insert({ pg : 7019, line : "peu nombreux et nous avions beaucoup souffert dans Pylos, depuis"});
db.iliadfr.insert({ pg : 7020, line : "que Hèraklès nous avait accablés, il y avait quelques années, en"});
db.iliadfr.insert({ pg : 7021, line : "tuant les premiers de la ville. Et nous étions douze fils"});
db.iliadfr.insert({ pg : 7022, line : "irréprochables de Nèleus, et j'étais resté le dernier, car tous"});
db.iliadfr.insert({ pg : 7023, line : "les autres avaient péri; et c'est pourquoi les orgueilleux Épéiens"});
db.iliadfr.insert({ pg : 7024, line : "cuirassés nous accablaient d'injustes outrages. Le vieillard"});
db.iliadfr.insert({ pg : 7025, line : "Nèleus reçut en partage un troupeau de boeufs et un troupeau de"});
db.iliadfr.insert({ pg : 7026, line : "brebis, trois cents têtes de bétail et leurs bergers, car la"});
db.iliadfr.insert({ pg : 7027, line : "divine Élis lui avait beaucoup enlevé de richesses. Le roi des"});
db.iliadfr.insert({ pg : 7028, line : "hommes, Augéias, avait retenu quatre de ses chevaux, avec leurs"});
db.iliadfr.insert({ pg : 7029, line : "chars, qui se rendaient aux jeux, et il n'avait renvoyé que le"});
db.iliadfr.insert({ pg : 7030, line : "conducteur plein de tristesse de cette perte. Et le vieux Nèleus"});
db.iliadfr.insert({ pg : 7031, line : "en fut très irrité; et c'est pourquoi il reçut une grande part du"});
db.iliadfr.insert({ pg : 7032, line : "butin; mais il distribua le reste au peuple par portions égales."});
db.iliadfr.insert({ pg : 7033, line : "Et comme nous partagions le butin, en faisant des sacrifices, les"});
db.iliadfr.insert({ pg : 7034, line : "Épéiens survinrent, le troisième jour, en grand nombre, avec leurs"});
db.iliadfr.insert({ pg : 7035, line : "chevaux aux sabots massifs, et les deux Molionides, jeunes encore,"});
db.iliadfr.insert({ pg : 7036, line : "et inhabiles malgré leur force et leur courage. Or, Thryôessa"});
db.iliadfr.insert({ pg : 7037, line : "s'élevait sur une hauteur, non loin de l'Alphéos, aux confins de"});
db.iliadfr.insert({ pg : 7038, line : "la sablonneuse Pylos. Et l'ennemi l'assiégeait, désirant la"});
db.iliadfr.insert({ pg : 7039, line : "détruire. Mais, comme ils traversaient les plaines, Athènè,"});
db.iliadfr.insert({ pg : 7040, line : "pendant la nuit, descendit vers nous du haut de l'Olympos pour"});
db.iliadfr.insert({ pg : 7041, line : "nous appeler aux armes; et elle rassembla aisément les peuples"});
db.iliadfr.insert({ pg : 7042, line : "dans Pylos. Et tous étaient pleins d'ardeur. Nèleus me défendit de"});
db.iliadfr.insert({ pg : 7043, line : "m'armer, et il cacha mes chevaux, car il pensait que je n'étais"});
db.iliadfr.insert({ pg : 7044, line : "pas assez fort pour combattre. Mais je partis à pied, et je"});
db.iliadfr.insert({ pg : 7045, line : "m'illustrai au milieu des cavaliers, parce que Athènè me guidait"});
db.iliadfr.insert({ pg : 7046, line : "au combat. Et tous, cavaliers et piétons Pyliens, nous attendîmes"});
db.iliadfr.insert({ pg : 7047, line : "la divine Éôs auprès d'Arènè, là où le fleuve Minyéios tombe dans"});
db.iliadfr.insert({ pg : 7048, line : "la mer. Vers midi, arrivés sur les bords sacrés de l'Alphéos, nous"});
db.iliadfr.insert({ pg : 7049, line : "fîmes de grands sacrifices au puissant Zeus, offrant aussi un"});
db.iliadfr.insert({ pg : 7050, line : "taureau à l'Alphéos, un autre taureau à Poseidaôn, et une génisse"});
db.iliadfr.insert({ pg : 7051, line : "indomptée à Athènè aux yeux clairs. Puis, chacun de nous, ayant"});
db.iliadfr.insert({ pg : 7052, line : "pris son repas dans les rangs, se coucha avec ses armes sur les"});
db.iliadfr.insert({ pg : 7053, line : "rives du fleuve. Cependant les magnanimes Épéiens assiégeaient la"});
db.iliadfr.insert({ pg : 7054, line : "ville, désirant la détruire; et voici que les durs travaux d'Arès"});
db.iliadfr.insert({ pg : 7055, line : "leur apparurent. Quand Hélios resplendit sur la terre, nous"});
db.iliadfr.insert({ pg : 7056, line : "courûmes au combat, en suppliant Zeus et Athènè. Et dès que les"});
db.iliadfr.insert({ pg : 7057, line : "Pyliens et les Épéiens se furent attaqués, le premier je tuai un"});
db.iliadfr.insert({ pg : 7058, line : "guerrier et je me saisis de ses chevaux aux sabots massifs. Et"});
db.iliadfr.insert({ pg : 7059, line : "c'était le brave Moulios, gendre d'Augéias, car il avait épousé sa"});
db.iliadfr.insert({ pg : 7060, line : "fille, la blonde Agamèdè, qui connaissait toutes les plantes"});
db.iliadfr.insert({ pg : 7061, line : "médicinales qui poussent sur la vaste terre. Et je le perçai de ma"});
db.iliadfr.insert({ pg : 7062, line : "lance d'airain, comme il s'élançait, et il tomba dans la"});
db.iliadfr.insert({ pg : 7063, line : "poussière; et je sautai sur son char, et je combattis aux premiers"});
db.iliadfr.insert({ pg : 7064, line : "rangs; et les magnanimes Épéiens s'enfuirent épouvantés, quand ils"});
db.iliadfr.insert({ pg : 7065, line : "virent tomber ce guerrier, chef des cavaliers, le plus brave"});
db.iliadfr.insert({ pg : 7066, line : "d'entre eux. Et je me jetai sur eux, semblable à une noire"});
db.iliadfr.insert({ pg : 7067, line : "tempête. Je m'emparai de cinquante chars, et je tuai de ma lance"});
db.iliadfr.insert({ pg : 7068, line : "deux guerriers sur chaque char. Sans doute j'eusse tué aussi les"});
db.iliadfr.insert({ pg : 7069, line : "deux jeunes Aktorides, si leur aïeul Poseidaôn qui commande au"});
db.iliadfr.insert({ pg : 7070, line : "loin ne les eût enlevés de la mêlée, en les enveloppant d'une nuée"});
db.iliadfr.insert({ pg : 7071, line : "épaisse. Alors Zeus accorda aux Pyliens une grande victoire. Nous"});
db.iliadfr.insert({ pg : 7072, line : "poursuivîmes au loin l'ennemi à travers la plaine, tuant les"});
db.iliadfr.insert({ pg : 7073, line : "hommes et enlevant de belles armes, et poussant nos chevaux"});
db.iliadfr.insert({ pg : 7074, line : "jusqu'à Bouprasios féconde en fruits, jusqu'à la pierreuse Olènè"});
db.iliadfr.insert({ pg : 7075, line : "et Alèsios qu'on nomme maintenant Kolônè. Et Athènè rappela"});
db.iliadfr.insert({ pg : 7076, line : "l'armée, et je tuai encore un guerrier; et les Akhaiens, quittant"});
db.iliadfr.insert({ pg : 7077, line : "Bouprasios, ramenèrent leurs chevaux rapides vers Pylos. Et tous"});
db.iliadfr.insert({ pg : 7078, line : "rendaient grâces parmi les dieux à Zeus, et parmi les guerriers à"});
db.iliadfr.insert({ pg : 7079, line : "Nestôr. Tel je fus au milieu des braves; mais Akhilleus n'use de"});
db.iliadfr.insert({ pg : 7080, line : "sa force que pour lui seul, et je pense qu'il ressentira un jour"});
db.iliadfr.insert({ pg : 7081, line : "d'amers regrets, quand toute l'armée Akhaienne aura péri. Ô ami,"});
db.iliadfr.insert({ pg : 7082, line : "Ménoitios t'adressa de sages paroles quand, loin de la Phthiè, il"});
db.iliadfr.insert({ pg : 7083, line : "t'envoya vers Agamemnôn. Nous étions là, le divin Odysseus et moi,"});
db.iliadfr.insert({ pg : 7084, line : "et nous entendîmes facilement ce qu'il te dit dans ses demeures."});
db.iliadfr.insert({ pg : 7085, line : "Et nous étions venus vers les riches demeures de Pèleus,"});
db.iliadfr.insert({ pg : 7086, line : "parcourant l'Akhaiè fertile, afin de rassembler les guerriers."});
db.iliadfr.insert({ pg : 7087, line : "Nous y trouvâmes le héros Ménoitios, et toi, et Akhilleus. Et le"});
db.iliadfr.insert({ pg : 7088, line : "vieux cavalier Pèleus brûlait, dans ses cours intérieures, les"});
db.iliadfr.insert({ pg : 7089, line : "cuisses grasses d'un boeuf en l'honneur de Zeus qui se réjouit de"});
db.iliadfr.insert({ pg : 7090, line : "la foudre. Et il tenait une coupe d'or, et il répandait des"});
db.iliadfr.insert({ pg : 7091, line : "libations de vin noir sur les feux sacrés, et vous prépariez les"});
db.iliadfr.insert({ pg : 7092, line : "chairs du boeuf. Nous restions debout sous le vestibule; mais"});
db.iliadfr.insert({ pg : 7093, line : "Akhilleus, surpris, se leva, nous conduisit par la main, nous fit"});
db.iliadfr.insert({ pg : 7094, line : "asseoir et posa devant nous la nourriture hospitalière qu'il est"});
db.iliadfr.insert({ pg : 7095, line : "d'usage d'offrir aux étrangers. Et, après nous être rassasiés de"});
db.iliadfr.insert({ pg : 7096, line : "boire et de manger, je commençai à parler, vous exhortant à nous"});
db.iliadfr.insert({ pg : 7097, line : "suivre. Et vous y consentîtes volontiers, et les deux vieillards"});
db.iliadfr.insert({ pg : 7098, line : "vous adressèrent de sages paroles. D'abord, le vieux Pèleus"});
db.iliadfr.insert({ pg : 7099, line : "recommanda à Akhilleus de surpasser tous les autres guerriers en"});
db.iliadfr.insert({ pg : 7100, line : "courage; puis le fils d'Aktôr, Ménoitios, te dit: -- Mon fils,"});
db.iliadfr.insert({ pg : 7101, line : "Akhilleus t'est supérieur par la naissance, mais tu es plus âgé"});
db.iliadfr.insert({ pg : 7102, line : "que lui. Ses forces sont plus grandes que les tiennes, mais parle-"});
db.iliadfr.insert({ pg : 7103, line : "lui avec sagesse, avertis-le, guide-le, et il obéira aux"});
db.iliadfr.insert({ pg : 7104, line : "excellents conseils.'"});
db.iliadfr.insert({ pg : 7106, line : "Le vieillard te donna ces instructions, mais tu les as oubliées."});
db.iliadfr.insert({ pg : 7107, line : "Parle donc au brave Akhilleus; peut-être écoutera-t-il tes"});
db.iliadfr.insert({ pg : 7108, line : "paroles. Qui sait si, grâces à un dieu, tu ne toucheras point son"});
db.iliadfr.insert({ pg : 7109, line : "coeur? Le conseil d'un ami est bon à suivre. Mais si, dans son"});
db.iliadfr.insert({ pg : 7110, line : "esprit, il redoute quelque oracle ou un avertissement que lui a"});
db.iliadfr.insert({ pg : 7111, line : "donné sa mère vénérable de la part de Zeus, qu'il t'envoie"});
db.iliadfr.insert({ pg : 7112, line : "combattre au moins, et que l'armée des Myrmidones te suive; et"});
db.iliadfr.insert({ pg : 7113, line : "peut-être sauveras-tu les Danaens. S'il te confiait ses belles"});
db.iliadfr.insert({ pg : 7114, line : "armes, peut-être les Troiens te prendraient-ils pour lui, et,"});
db.iliadfr.insert({ pg : 7115, line : "s'enfuyant, laisseraient-ils respirer les fils accablés des"});
db.iliadfr.insert({ pg : 7116, line : "Akhaiens; et le repos est de courte durée à la guerre. Or, des"});
db.iliadfr.insert({ pg : 7117, line : "troupes riches repousseraient aisément vers la ville, loin des"});
db.iliadfr.insert({ pg : 7118, line : "nefs et des tentes, des hommes fatigués par le combat."});
db.iliadfr.insert({ pg : 7120, line : "Il parla ainsi, et il remua le coeur de Patroklos, et celui-ci se"});
db.iliadfr.insert({ pg : 7121, line : "hâta de retourner vers les nefs de l'Aiakide Akhilleus. Mais,"});
db.iliadfr.insert({ pg : 7122, line : "lorsque, dans sa course, il fut arrivé aux nefs du divin Odysseus,"});
db.iliadfr.insert({ pg : 7123, line : "là où étaient l'agora et le lieu de justice, et où l'on dressait"});
db.iliadfr.insert({ pg : 7124, line : "les autels des dieux, il rencontra le magnanime Évaimônide"});
db.iliadfr.insert({ pg : 7125, line : "Eurypylos qui revenait du combat, boitant et la cuisse percée"});
db.iliadfr.insert({ pg : 7126, line : "d'une flèche. Et la sueur tombait de sa tête et de ses épaules, et"});
db.iliadfr.insert({ pg : 7127, line : "un sang noir sortait de sa profonde blessure; mais son coeur était"});
db.iliadfr.insert({ pg : 7128, line : "toujours ferme. Et, en le voyant, le robuste fils de Ménoitios fut"});
db.iliadfr.insert({ pg : 7129, line : "saisi de compassion, et il lui dit ces paroles ailées:"});
db.iliadfr.insert({ pg : 7131, line : "-- Ah! malheureux chefs et princes des Danaens, serez-vous donc,"});
db.iliadfr.insert({ pg : 7132, line : "loin de vos amis, loin de la terre natale, la pâture des chiens"});
db.iliadfr.insert({ pg : 7133, line : "qui se rassasieront de votre graisse blanche dans Ilios? Mais dis-"});
db.iliadfr.insert({ pg : 7134, line : "moi, divin héros Eurypylos, les Akhaiens soutiendront-ils l'effort"});
db.iliadfr.insert({ pg : 7135, line : "du cruel Hektôr, ou périront-ils sous sa lance?"});
db.iliadfr.insert({ pg : 7137, line : "Et le sage Eurypylos lui répondit:"});
db.iliadfr.insert({ pg : 7139, line : "-- Divin Patroklos, il n'y a plus de salut pour les Akhaiens, et"});
db.iliadfr.insert({ pg : 7140, line : "ils périront devant les nefs noires. Les plus robustes et les plus"});
db.iliadfr.insert({ pg : 7141, line : "braves gisent dans leurs nefs, frappés ou blessés par les mains"});
db.iliadfr.insert({ pg : 7142, line : "des Troiens dont les forces augmentent toujours. Mais sauve-moi en"});
db.iliadfr.insert({ pg : 7143, line : "me ramenant dans ma nef noire. Arrache cette flèche de ma cuisse,"});
db.iliadfr.insert({ pg : 7144, line : "baigne d'une eau tiède la plaie et le sang qui en coule, et verse"});
db.iliadfr.insert({ pg : 7145, line : "dans ma blessure ces doux et excellents baumes que tu tiens"});
db.iliadfr.insert({ pg : 7146, line : "d'Akhilleus qui les a reçus de Kheirôn, le plus juste des"});
db.iliadfr.insert({ pg : 7147, line : "centaures. Des deux médecins, Podaleirios et Makhaôn, l'un, je"});
db.iliadfr.insert({ pg : 7148, line : "pense, est dans sa tente, blessé lui-même et manquant de médecins,"});
db.iliadfr.insert({ pg : 7149, line : "et l'autre soutient dans la plaine le dur combat contre les"});
db.iliadfr.insert({ pg : 7150, line : "Troiens."});
db.iliadfr.insert({ pg : 7152, line : "Et le robuste fils de Ménoitios lui répondit:"});
db.iliadfr.insert({ pg : 7154, line : "-- Héros Eurypylos, comment finiront ces choses, et que ferons-"});
db.iliadfr.insert({ pg : 7155, line : "nous? Je vais répéter à Akhilleus les paroles du cavalier"});
db.iliadfr.insert({ pg : 7156, line : "Gérennien Nestôr, rempart des Akhaiens; mais, cependant, je ne"});
db.iliadfr.insert({ pg : 7157, line : "t'abandonnerai pas dans ta détresse."});
db.iliadfr.insert({ pg : 7159, line : "Il parla ainsi, et, le soutenant contre sa poitrine, il conduisit"});
db.iliadfr.insert({ pg : 7160, line : "le prince des peuples jusque dans sa tente. Et le serviteur"});
db.iliadfr.insert({ pg : 7161, line : "d'Eurypylos, en le voyant, prépara un lit de peaux de boeuf; et le"});
db.iliadfr.insert({ pg : 7162, line : "héros s'y coucha; et le Ménoitiade, à l'aide d'un couteau, retira"});
db.iliadfr.insert({ pg : 7163, line : "de la cuisse le trait acerbe et aigu, lava le sang noir avec de"});
db.iliadfr.insert({ pg : 7164, line : "l'eau tiède, et, de ses mains, exprima dans la plaie le suc d'une"});
db.iliadfr.insert({ pg : 7165, line : "racine amère qui adoucissait et calmait. Et toutes les douleurs du"});
db.iliadfr.insert({ pg : 7166, line : "héros disparurent, et la blessure se ferma, et le sang cessa de"});
db.iliadfr.insert({ pg : 7167, line : "couler."});
db.iliadfr.insert({ pg : 7170, line : "Chant 12"});
db.iliadfr.insert({ pg : 7172, line : "Ainsi le robuste fils de Ménoitios prenait soin d'Eurypylos dans"});
db.iliadfr.insert({ pg : 7173, line : "ses tentes. Et les Argiens et les Troiens combattaient avec"});
db.iliadfr.insert({ pg : 7174, line : "fureur, et le fossé et la vaste muraille ne devaient pas longtemps"});
db.iliadfr.insert({ pg : 7175, line : "protéger les Danaens. Quand ils l'avaient élevée pour sauvegarder"});
db.iliadfr.insert({ pg : 7176, line : "les nefs rapides et le nombreux butin, ils n'avaient point offert"});
db.iliadfr.insert({ pg : 7177, line : "de riches hécatombes aux dieux, et cette muraille, ayant été"});
db.iliadfr.insert({ pg : 7178, line : "construite malgré les dieux, ne devait pas être de longue durée."});
db.iliadfr.insert({ pg : 7180, line : "Tant que Hektôr fut vivant, et que le Pèléide garda sa colère, et"});
db.iliadfr.insert({ pg : 7181, line : "que la ville du roi Priamos fut épargnée, le grand mur des"});
db.iliadfr.insert({ pg : 7182, line : "Akhaiens subsista; mais, après que les plus illustres des Troiens"});
db.iliadfr.insert({ pg : 7183, line : "furent morts, et que, parmi les Argiens, les uns eurent péri et"});
db.iliadfr.insert({ pg : 7184, line : "les autres survécu, et que la ville de Priamos eut été renversée"});
db.iliadfr.insert({ pg : 7185, line : "dans la dixième année, les Argiens s'en retournèrent dans leur"});
db.iliadfr.insert({ pg : 7186, line : "chère patrie."});
db.iliadfr.insert({ pg : 7188, line : "Alors, Poseidaôn et Apollôn se décidèrent à détruire cette"});
db.iliadfr.insert({ pg : 7189, line : "muraille, en réunissant la violence des fleuves qui coulent à la"});
db.iliadfr.insert({ pg : 7190, line : "mer des sommets de l'Ida: le Rhèsos, le Heptaporos, le Karèsos, le"});
db.iliadfr.insert({ pg : 7191, line : "Rhodios, le Grènikos, l'Aisépos, le divin Skamandros et le Simoïs,"});
db.iliadfr.insert({ pg : 7192, line : "où tant de casques et de boucliers roulèrent dans la poussière"});
db.iliadfr.insert({ pg : 7193, line : "avec la foule des guerriers demi-dieux. Et Phoibos Apollôn les"});
db.iliadfr.insert({ pg : 7194, line : "réunit tous, et, pendant neuf jours, dirigea leurs courants contre"});
db.iliadfr.insert({ pg : 7195, line : "cette muraille. Et Zeus pleuvait continuellement, afin que les"});
db.iliadfr.insert({ pg : 7196, line : "débris fussent submergés plus tôt par la mer. Et Poseidaôn lui-"});
db.iliadfr.insert({ pg : 7197, line : "même, le trident en main, fit s'écrouler, sous l'effort des eaux,"});
db.iliadfr.insert({ pg : 7198, line : "les poutres et les pierres et les fondements que les Akhaiens"});
db.iliadfr.insert({ pg : 7199, line : "avaient péniblement construits. Et il mit la muraille au niveau du"});
db.iliadfr.insert({ pg : 7200, line : "rapide Hellespontos; et, sur ces débris, les sables s'étant"});
db.iliadfr.insert({ pg : 7201, line : "amoncelés comme auparavant sur le vaste rivage, le dieu fit"});
db.iliadfr.insert({ pg : 7202, line : "retourner les fleuves dans les lits où ils avaient coutume de"});
db.iliadfr.insert({ pg : 7203, line : "rouler leurs belles eaux."});
db.iliadfr.insert({ pg : 7205, line : "Ainsi, dans l'avenir, devaient faire Poseidaôn et Apollôn. Mais,"});
db.iliadfr.insert({ pg : 7206, line : "aujourd'hui, autour du mur solide, éclataient les clameurs de la"});
db.iliadfr.insert({ pg : 7207, line : "guerre et le combat; et les poutres des tours criaient sous les"});
db.iliadfr.insert({ pg : 7208, line : "coups, et les Argiens, sous le fouet de Zeus, étaient acculés"});
db.iliadfr.insert({ pg : 7209, line : "contre les nefs creuses, redoutant le robuste Hektôr, maître de la"});
db.iliadfr.insert({ pg : 7210, line : "fuite. Et celui-ci combattait toujours, semblable à un tourbillon."});
db.iliadfr.insert({ pg : 7212, line : "De même, quand un sanglier ou un lion, fier de sa vigueur, se"});
db.iliadfr.insert({ pg : 7213, line : "retourne contre les chiens et les chasseurs, ceux-ci, se serrant,"});
db.iliadfr.insert({ pg : 7214, line : "s'arrêtent en face et lui dardent un grand nombre de traits; mais"});
db.iliadfr.insert({ pg : 7215, line : "son coeur orgueilleux ne tremble ni ne s'épouvante, et son audace"});
db.iliadfr.insert({ pg : 7216, line : "cause sa perte. Il tente souvent d'enfoncer les lignes des"});
db.iliadfr.insert({ pg : 7217, line : "chasseurs, et là où il se rue, elles cèdent toujours. Ainsi, se"});
db.iliadfr.insert({ pg : 7218, line : "ruant dans la mêlée, Hektôr exhortait ses compagnons à franchir le"});
db.iliadfr.insert({ pg : 7219, line : "fossé; mais ses chevaux rapides n'osaient eux-mêmes avancer, et,"});
db.iliadfr.insert({ pg : 7220, line : "en hennissant, ils s'arrêtaient sur le bord, car le fossé creux"});
db.iliadfr.insert({ pg : 7221, line : "les effrayait, ne pouvant être franchi ou traversé facilement. Des"});
db.iliadfr.insert({ pg : 7222, line : "deux côtés se dressaient de hauts talus hérissés de pals aigus"});
db.iliadfr.insert({ pg : 7223, line : "plantés par les fils des Akhaiens, épais, solides et tournés"});
db.iliadfr.insert({ pg : 7224, line : "contre les guerriers ennemis. Des chevaux traînant un char léger"});
db.iliadfr.insert({ pg : 7225, line : "n'auraient pu y pénétrer aisément; mais les hommes de pied"});
db.iliadfr.insert({ pg : 7226, line : "désiraient tenter l'escalade. Et alors Polydamas s'approcha du"});
db.iliadfr.insert({ pg : 7227, line : "brave Hektôr et lui dit:"});
db.iliadfr.insert({ pg : 7229, line : "-- Hektôr, et vous, chefs des Troiens et des alliés, nous poussons"});
db.iliadfr.insert({ pg : 7230, line : "imprudemment à travers ce fossé nos chevaux rapides, car le"});
db.iliadfr.insert({ pg : 7231, line : "passage en est difficile. Des pals aigus s'y dressent en effet, et"});
db.iliadfr.insert({ pg : 7232, line : "derrière eux monte le mur des Akhaiens. On ne peut ici ni"});
db.iliadfr.insert({ pg : 7233, line : "combattre sur les chars, ni en descendre. La voie est étroite, et"});
db.iliadfr.insert({ pg : 7234, line : "je pense que nous y périrons. Puisse Zeus qui tonne dans les"});
db.iliadfr.insert({ pg : 7235, line : "hauteurs accabler les Argiens de mille maux et venir en aide aux"});
db.iliadfr.insert({ pg : 7236, line : "Troiens aussi sûrement que je voudrais voir à l'instant ceux-là"});
db.iliadfr.insert({ pg : 7237, line : "périr tous, sans gloire, loin d'Argos. Mais, s'ils reviennent sur"});
db.iliadfr.insert({ pg : 7238, line : "nous et nous repoussent des nefs, nous serons précipités dans le"});
db.iliadfr.insert({ pg : 7239, line : "fossé creux; et je ne pense pas qu'un seul d'entre nous, dans sa"});
db.iliadfr.insert({ pg : 7240, line : "fuite, puisse regagner la ville. Écoutez donc et obéissez à mes"});
db.iliadfr.insert({ pg : 7241, line : "paroles. Que les conducteurs retiennent les chevaux au bord de ce"});
db.iliadfr.insert({ pg : 7242, line : "fossé, et nous, à pied, couverts de nos armes, nous suivrons tous"});
db.iliadfr.insert({ pg : 7243, line : "Hektôr, et les Akhaiens ne résisteront pas, si, en effet, leur"});
db.iliadfr.insert({ pg : 7244, line : "ruine est proche."});
db.iliadfr.insert({ pg : 7246, line : "Polydamas parla ainsi, et ce sage conseil plut à Hektôr, et,"});
db.iliadfr.insert({ pg : 7247, line : "aussitôt, il sauta de son char avec ses armes; et, comme le divin"});
db.iliadfr.insert({ pg : 7248, line : "Hektôr, les autres Troiens sautèrent aussi de leurs chars, et ils"});
db.iliadfr.insert({ pg : 7249, line : "ordonnèrent aux conducteurs de ranger les chevaux sur le bord du"});
db.iliadfr.insert({ pg : 7250, line : "fossé; et, se divisant en cinq corps, ils suivirent leurs chefs."});
db.iliadfr.insert({ pg : 7252, line : "Avec Hektôr et l'irréprochable Polydamas marchaient les plus"});
db.iliadfr.insert({ pg : 7253, line : "nombreux et les plus braves, ceux qui désiraient avec le plus"});
db.iliadfr.insert({ pg : 7254, line : "d'ardeur enfoncer la muraille; et leur troisième chef était"});
db.iliadfr.insert({ pg : 7255, line : "Kébrionès, car Hektôr avait laissé à la garde du char un moins"});
db.iliadfr.insert({ pg : 7256, line : "brave guerrier. Et le deuxième corps était commandé par Alkathoos,"});
db.iliadfr.insert({ pg : 7257, line : "Pâris et Agènôr. Et le troisième corps obéissait à Hélénos et au"});
db.iliadfr.insert({ pg : 7258, line : "divin Dèiphobos, deux fils de Priamos, et au héros Asios Hyrtakide"});
db.iliadfr.insert({ pg : 7259, line : "que ses chevaux au poil roux et de haute taille avaient amené"});
db.iliadfr.insert({ pg : 7260, line : "d'Arisba et des bords du Sellèis. Et le chef du quatrième corps"});
db.iliadfr.insert({ pg : 7261, line : "était le noble fils d'Ankhisès, Ainéias; et avec lui commandaient"});
db.iliadfr.insert({ pg : 7262, line : "les deux Anténorides, Arkélokhos et Akamas, habiles au combat. Et"});
db.iliadfr.insert({ pg : 7263, line : "Sarpèdôn, avec Glaukos et le magnanime Astéropaios, commandait les"});
db.iliadfr.insert({ pg : 7264, line : "illustres alliés. Et ces guerriers étaient les plus courageux"});
db.iliadfr.insert({ pg : 7265, line : "après Hektôr, car il les surpassait tous."});
db.iliadfr.insert({ pg : 7267, line : "Et s'étant couverts de leurs boucliers de cuir, ils allèrent droit"});
db.iliadfr.insert({ pg : 7268, line : "aux Danaens, ne pensant pas que ceux-ci pussent résister, et"});
db.iliadfr.insert({ pg : 7269, line : "certains d'envahir les nefs noires. Ainsi les Troiens et leurs"});
db.iliadfr.insert({ pg : 7270, line : "alliés venus de loin obéissaient au sage conseil de"});
db.iliadfr.insert({ pg : 7271, line : "l'irréprochable Polydamas; mais le Hyrtakide Asios, prince des"});
db.iliadfr.insert({ pg : 7272, line : "hommes, ne voulut point abandonner ses chevaux et leur conducteur,"});
db.iliadfr.insert({ pg : 7273, line : "et il s'élança avec eux vers les nefs rapides. Insensé! Il ne"});
db.iliadfr.insert({ pg : 7274, line : "devait point, ayant évité la noire kèr, fier de ses chevaux et de"});
db.iliadfr.insert({ pg : 7275, line : "son char, revenir des nefs vers la haute Ilios; et déjà la triste"});
db.iliadfr.insert({ pg : 7276, line : "moire l'enveloppait de la lance de l'illustre Deukalide Idoméneus."});
db.iliadfr.insert({ pg : 7278, line : "Et il se rua sur la gauche des nefs, à l'endroit où les Akhaiens"});
db.iliadfr.insert({ pg : 7279, line : "ramenaient dans le camp leurs chevaux et leurs chars. Il trouva"});
db.iliadfr.insert({ pg : 7280, line : "les portes ouvertes, car ni les battants, ni les barrières"});
db.iliadfr.insert({ pg : 7281, line : "n'étaient fermés, afin que les guerriers, dans leur fuite, pussent"});
db.iliadfr.insert({ pg : 7282, line : "regagner les nefs. Plein d'orgueil, il poussa ses chevaux de ce"});
db.iliadfr.insert({ pg : 7283, line : "côté, et ses compagnons le suivaient avec de perçantes clameurs,"});
db.iliadfr.insert({ pg : 7284, line : "ne pensant pas que les Akhaiens pussent résister, et certains"});
db.iliadfr.insert({ pg : 7285, line : "d'envahir les nefs noires."});
db.iliadfr.insert({ pg : 7287, line : "Les insensés! Ils rencontrèrent devant les portes deux braves"});
db.iliadfr.insert({ pg : 7288, line : "guerriers, fils magnanimes des belliqueux Lapithes. Et l'un était"});
db.iliadfr.insert({ pg : 7289, line : "le robuste Polypoitès, fils de Peirithoos, et l'autre, Léonteus,"});
db.iliadfr.insert({ pg : 7290, line : "semblable au tueur Arès. Et tous deux, devant les hautes portes,"});
db.iliadfr.insert({ pg : 7291, line : "ils se tenaient comme deux chênes, sur les montagnes, bravant les"});
db.iliadfr.insert({ pg : 7292, line : "tempêtes et la pluie, affermis par leurs larges racines. Ainsi,"});
db.iliadfr.insert({ pg : 7293, line : "certains de leurs forces et de leur courage, ils attendaient le"});
db.iliadfr.insert({ pg : 7294, line : "choc du grand Asios et ne reculaient point."});
db.iliadfr.insert({ pg : 7296, line : "Et, droit au mur bien construit, avec de grandes clameurs, se"});
db.iliadfr.insert({ pg : 7297, line : "ruaient, le bouclier sur la tête, le prince Asios, Iamènès,"});
db.iliadfr.insert({ pg : 7298, line : "Orestès, Adamas Asiade, Thoôn et Oinomaos. Et, par leurs cris, les"});
db.iliadfr.insert({ pg : 7299, line : "deux Lapithes exhortaient les Akhaiens à venir défendre les nefs."});
db.iliadfr.insert({ pg : 7300, line : "Mais, voyant les Troiens escalader la muraille, les Danaens pleins"});
db.iliadfr.insert({ pg : 7301, line : "de terreur poussaient de grands cris. Alors, les deux Lapithes, se"});
db.iliadfr.insert({ pg : 7302, line : "jetant devant les portes, combattirent tels que deux sangliers"});
db.iliadfr.insert({ pg : 7303, line : "sauvages qui, sur les montagnes, forcés par les chasseurs et les"});
db.iliadfr.insert({ pg : 7304, line : "chiens, se retournent impétueusement et brisent les arbustes dont"});
db.iliadfr.insert({ pg : 7305, line : "ils arrachent les racines. Et ils grincent des dents jusqu'à ce"});
db.iliadfr.insert({ pg : 7306, line : "qu'un trait leur ait arraché la vie."});
db.iliadfr.insert({ pg : 7308, line : "Ainsi l'airain éclatant résonnait sur la poitrine des deux"});
db.iliadfr.insert({ pg : 7309, line : "guerriers frappés par les traits; et ils combattaient"});
db.iliadfr.insert({ pg : 7310, line : "courageusement, confiants dans leurs forces et dans leurs"});
db.iliadfr.insert({ pg : 7311, line : "compagnons."});
db.iliadfr.insert({ pg : 7313, line : "Et ceux-ci lançaient des pierres du haut des tours bien"});
db.iliadfr.insert({ pg : 7314, line : "construites, pour se défendre, eux, leurs tentes et leurs nefs"});
db.iliadfr.insert({ pg : 7315, line : "rapides. Et de même que la lourde neige, que la violence du vent"});
db.iliadfr.insert({ pg : 7316, line : "qui agite les nuées noires verse, épaisse, sur la terre"});
db.iliadfr.insert({ pg : 7317, line : "nourricière, de même les traits pleuvaient des mains des Akhaiens"});
db.iliadfr.insert({ pg : 7318, line : "et des Troiens. Et les casques et les boucliers bombés sonnaient,"});
db.iliadfr.insert({ pg : 7319, line : "heurtés par les pierres. Alors, gémissant et se frappant les"});
db.iliadfr.insert({ pg : 7320, line : "cuisses, Asios Hyrtakide parla ainsi, indigné:"});
db.iliadfr.insert({ pg : 7322, line : "-- Père Zeus! certes, tu n'aimes qu'à mentir, car je ne pensais"});
db.iliadfr.insert({ pg : 7323, line : "pas que les héros Akhaiens pussent soutenir notre vigueur et nos"});
db.iliadfr.insert({ pg : 7324, line : "mains inévitables. Voici que, pareils aux guêpes au corsage"});
db.iliadfr.insert({ pg : 7325, line : "mobile, ou aux abeilles qui bâtissent leurs ruches dans un sentier"});
db.iliadfr.insert({ pg : 7326, line : "ardu, et qui n'abandonnent point leurs demeures creuses, mais"});
db.iliadfr.insert({ pg : 7327, line : "défendent leur jeune famille contre les chasseurs, voici que ces"});
db.iliadfr.insert({ pg : 7328, line : "deux guerriers, seuls devant les portes, ne reculent point,"});
db.iliadfr.insert({ pg : 7329, line : "attendant d'être morts ou vainqueurs."});
db.iliadfr.insert({ pg : 7331, line : "Il parla ainsi, mais il ne fléchit point l'âme de Zeus qui, dans"});
db.iliadfr.insert({ pg : 7332, line : "son coeur, voulait glorifier Hektôr."});
db.iliadfr.insert({ pg : 7334, line : "Et d'autres aussi combattaient autour des portes; mais, à qui"});
db.iliadfr.insert({ pg : 7335, line : "n'est point dieu, il est difficile de tout raconter. Et çà et là,"});
db.iliadfr.insert({ pg : 7336, line : "autour du mur, roulait un feu dévorant de pierres. Et les Argiens,"});
db.iliadfr.insert({ pg : 7337, line : "en gémissant de cette nécessité, combattaient pour leurs nefs. Et"});
db.iliadfr.insert({ pg : 7338, line : "tous les dieux étaient tristes qui soutenaient les Danaens dans"});
db.iliadfr.insert({ pg : 7339, line : "les batailles."});
db.iliadfr.insert({ pg : 7341, line : "Et, alors, le robuste fils de Peirithoos, Polypoitès, frappa"});
db.iliadfr.insert({ pg : 7342, line : "Damasos de sa lance, sur le casque d'airain; mais le casque ne"});
db.iliadfr.insert({ pg : 7343, line : "résista point, et la pointe d'airain, rompant l'os, écrasa la"});
db.iliadfr.insert({ pg : 7344, line : "cervelle, et l'homme furieux fut dompté. Et Polypoitès tua ensuite"});
db.iliadfr.insert({ pg : 7345, line : "Pylôn et Ormènios. Et le fils d'Antimakhos, Léonteus, nourrisson"});
db.iliadfr.insert({ pg : 7346, line : "d'Arès, de sa lance perça Hippomakhos à la ceinture, à travers le"});
db.iliadfr.insert({ pg : 7347, line : "baudrier. Puis, ayant tiré l'épée aiguë hors de la gaine, et se"});
db.iliadfr.insert({ pg : 7348, line : "ruant dans la foule, il frappa Antiphatès, et celui-ci tomba à la"});
db.iliadfr.insert({ pg : 7349, line : "renverse. Puis, Léonteus entassa Ménôn, Iamènos et Orestès sur la"});
db.iliadfr.insert({ pg : 7350, line : "terre nourricière."});
db.iliadfr.insert({ pg : 7352, line : "Et tandis que les deux Lapithes enlevaient leurs armes splendides,"});
db.iliadfr.insert({ pg : 7353, line : "derrière Polydamas et Hektôr accouraient de jeunes guerriers,"});
db.iliadfr.insert({ pg : 7354, line : "nombreux et très braves, pleins du désir de rompre la muraille et"});
db.iliadfr.insert({ pg : 7355, line : "de brûler les nefs. Mais ils hésitèrent au bord du fossé. En"});
db.iliadfr.insert({ pg : 7356, line : "effet, comme ils allaient le franchir, ils virent un signe"});
db.iliadfr.insert({ pg : 7357, line : "augural. Un aigle, volant dans les hautes nuées, apparut à leur"});
db.iliadfr.insert({ pg : 7358, line : "gauche, et il portait entre ses serres un grand dragon sanglant,"});
db.iliadfr.insert({ pg : 7359, line : "mais qui vivait et palpitait encore, et combattait toujours, et"});
db.iliadfr.insert({ pg : 7360, line : "mordait l'aigle à la poitrine et au cou. Et celui-ci, vaincu par"});
db.iliadfr.insert({ pg : 7361, line : "la douleur, le laissa choir au milieu de la foule, et s'envola"});
db.iliadfr.insert({ pg : 7362, line : "dans le vent en poussant des cris. Et les Troiens frémirent"});
db.iliadfr.insert({ pg : 7363, line : "d'horreur en face du dragon aux couleurs variées qui gisait au"});
db.iliadfr.insert({ pg : 7364, line : "milieu d'eux, signe de Zeus tempétueux. Et alors Polydamas parla"});
db.iliadfr.insert({ pg : 7365, line : "ainsi au brave Hektôr:"});
db.iliadfr.insert({ pg : 7367, line : "-- Hektôr, toujours, dans l'agora, tu repousses et tu blâmes mes"});
db.iliadfr.insert({ pg : 7368, line : "conseils prudents, car tu veux qu'aucun guerrier ne dise autrement"});
db.iliadfr.insert({ pg : 7369, line : "que toi, dans l'agora ou dans le combat; et il faut que nous ne"});
db.iliadfr.insert({ pg : 7370, line : "servions qu'à augmenter ton pouvoir. Mais je parlerai cependant,"});
db.iliadfr.insert({ pg : 7371, line : "car mes paroles seront bonnes. N'allons point assiéger les nefs"});
db.iliadfr.insert({ pg : 7372, line : "Akhaiennes, car ceci arrivera, si un vrai signe est apparu aux"});
db.iliadfr.insert({ pg : 7373, line : "Troiens, prêts à franchir le fossé, cet aigle qui, volant dans les"});
db.iliadfr.insert({ pg : 7374, line : "hautes nuées, portait entre ses serres ce grand dragon sanglant,"});
db.iliadfr.insert({ pg : 7375, line : "mais vivant encore, et qui l'a laissé choir avant de le livrer en"});
db.iliadfr.insert({ pg : 7376, line : "pâture à ses petits dans son aire. C'est pourquoi, même si nous"});
db.iliadfr.insert({ pg : 7377, line : "rompions de force les portes et les murailles des Akhaiens, même"});
db.iliadfr.insert({ pg : 7378, line : "s'ils fuyaient, nous ne reviendrions point par les mêmes chemins"});
db.iliadfr.insert({ pg : 7379, line : "et en bon ordre; mais nous abandonnerions de nombreux Troiens que"});
db.iliadfr.insert({ pg : 7380, line : "les Akhaiens auraient tués avec l'airain, en défendant leurs nefs."});
db.iliadfr.insert({ pg : 7381, line : "Ainsi doit parler tout augure savant dans les prodiges divins, et"});
db.iliadfr.insert({ pg : 7382, line : "les peuples doivent lui obéir."});
db.iliadfr.insert({ pg : 7384, line : "Et Hektôr au casque mouvant, le regardant d'un oeil sombre, lui"});
db.iliadfr.insert({ pg : 7385, line : "dit:"});
db.iliadfr.insert({ pg : 7387, line : "-- Polydamas, certes, tes paroles ne me plaisent point, et, sans"});
db.iliadfr.insert({ pg : 7388, line : "doute, tu le sais, tes conseils auraient pu être meilleurs. Si tu"});
db.iliadfr.insert({ pg : 7389, line : "as parlé sincèrement, c'est que les dieux t'ont ravi"});
db.iliadfr.insert({ pg : 7390, line : "l'intelligence, puisque tu nous ordonnes d'oublier la volonté de"});
db.iliadfr.insert({ pg : 7391, line : "Zeus qui tonne dans les hauteurs, et les promesses qu'il m'a"});
db.iliadfr.insert({ pg : 7392, line : "faites et confirmées par un signe de sa tête. Tu veux que nous"});
db.iliadfr.insert({ pg : 7393, line : "obéissions à des oiseaux qui étendent leurs ailes! Je ne m'en"});
db.iliadfr.insert({ pg : 7394, line : "inquiète point, je n'en ai nul souci, soit qu'ils volent à ma"});
db.iliadfr.insert({ pg : 7395, line : "droite, vers Éôs ou Hélios, soit qu'ils volent à ma gauche, vers"});
db.iliadfr.insert({ pg : 7396, line : "le sombre couchant. Nous n'obéirons qu'à la volonté du grand Zeus"});
db.iliadfr.insert({ pg : 7397, line : "qui commande aux hommes mortels et aux immortels. Le meilleur des"});
db.iliadfr.insert({ pg : 7398, line : "augures est de combattre pour sa patrie. Pourquoi crains-tu la"});
db.iliadfr.insert({ pg : 7399, line : "guerre et le combat? Même quand nous tomberions tous autour des"});
db.iliadfr.insert({ pg : 7400, line : "nefs des Argiens, tu ne dois point craindre la mort, car ton coeur"});
db.iliadfr.insert({ pg : 7401, line : "ne te pousse point à combattre courageusement. Mais si tu te"});
db.iliadfr.insert({ pg : 7402, line : "retires de la mêlée, si tu pousses les guerriers à fuir, aussitôt,"});
db.iliadfr.insert({ pg : 7403, line : "frappé de ma lance, tu rendras l'esprit."});
db.iliadfr.insert({ pg : 7405, line : "Il parla ainsi et s'élança, et tous le suivirent avec une clameur"});
db.iliadfr.insert({ pg : 7406, line : "immense. Et Zeus qui se réjouit de la foudre souleva, des cimes de"});
db.iliadfr.insert({ pg : 7407, line : "l'Ida, un tourbillon de vent qui couvrit les nefs de poussière,"});
db.iliadfr.insert({ pg : 7408, line : "amollit le courage des Akhaiens et assura la gloire à Hektôr et"});
db.iliadfr.insert({ pg : 7409, line : "aux Troiens qui, confiants dans les signes de Zeus et dans leur"});
db.iliadfr.insert({ pg : 7410, line : "vigueur, tentaient de rompre la grande muraille des Akhaiens."});
db.iliadfr.insert({ pg : 7412, line : "Et ils arrachaient les créneaux, et ils démolissaient les"});
db.iliadfr.insert({ pg : 7413, line : "parapets, et ils ébranlaient avec des leviers les piles que les"});
db.iliadfr.insert({ pg : 7414, line : "Akhaiens avaient posées d'abord en terre pour soutenir les tours."});
db.iliadfr.insert({ pg : 7415, line : "Et ils les arrachaient, espérant détruire la muraille des"});
db.iliadfr.insert({ pg : 7416, line : "Akhaiens. Mais les Danaens ne reculaient point, et, couvrant les"});
db.iliadfr.insert({ pg : 7417, line : "parapets de leurs boucliers de peaux de boeuf, ils en repoussaient"});
db.iliadfr.insert({ pg : 7418, line : "les ennemis qui assiégeaient la muraille."});
db.iliadfr.insert({ pg : 7420, line : "Et les deux Aias couraient çà et là sur les tours, ranimant le"});
db.iliadfr.insert({ pg : 7421, line : "courage des Akhaiens. Tantôt par des paroles flatteuses, tantôt"});
db.iliadfr.insert({ pg : 7422, line : "par de rudes paroles, ils excitaient ceux qu'ils voyaient se"});
db.iliadfr.insert({ pg : 7423, line : "retirer du combat:"});
db.iliadfr.insert({ pg : 7425, line : "-- Amis! vous, les plus vaillants des Argiens, ou les moins"});
db.iliadfr.insert({ pg : 7426, line : "braves, car tous les guerriers ne sont pas égaux dans la mêlée,"});
db.iliadfr.insert({ pg : 7427, line : "c'est maintenant, vous le voyez, qu'il faut combattre, tous tant"});
db.iliadfr.insert({ pg : 7428, line : "que vous êtes. Que nul ne se retire vers les nefs devant les"});
db.iliadfr.insert({ pg : 7429, line : "menaces de l'ennemi. En avant! Exhortez-vous les uns les autres."});
db.iliadfr.insert({ pg : 7430, line : "Peut-être que l'Olympien foudroyant Zeus nous donnera de repousser"});
db.iliadfr.insert({ pg : 7431, line : "les Troiens jusque dans la ville."});
db.iliadfr.insert({ pg : 7433, line : "Et c'est ainsi que d'une voix belliqueuse ils excitaient les"});
db.iliadfr.insert({ pg : 7434, line : "Akhaiens."});
db.iliadfr.insert({ pg : 7436, line : "De même que, par un jour d'hiver, tombent les flocons amoncelés de"});
db.iliadfr.insert({ pg : 7437, line : "la neige, quand le sage Zeus, manifestant ses traits, les répand"});
db.iliadfr.insert({ pg : 7438, line : "sur les hommes mortels, et que les vents se taisent, tandis que la"});
db.iliadfr.insert({ pg : 7439, line : "neige couvre les cimes des grandes montagnes, et les hauts"});
db.iliadfr.insert({ pg : 7440, line : "promontoires, et les campagnes herbues, et les vastes travaux des"});
db.iliadfr.insert({ pg : 7441, line : "laboureurs, et qu'elle tombe aussi sur les rivages de la mer"});
db.iliadfr.insert({ pg : 7442, line : "écumeuse où les flots la fondent, pendant que la pluie de Zeus"});
db.iliadfr.insert({ pg : 7443, line : "enveloppe tout le reste; de même une grêle de pierres volait des"});
db.iliadfr.insert({ pg : 7444, line : "Akhaiens aux Troiens et des Troiens aux Akhaiens, et un"});
db.iliadfr.insert({ pg : 7445, line : "retentissement s'élevait tout autour de la muraille."});
db.iliadfr.insert({ pg : 7447, line : "Mais ni les Troiens ni l'illustre Hektôr n'auraient alors rompu"});
db.iliadfr.insert({ pg : 7448, line : "les portes de la muraille ni la longue barrière, si le sage Zeus"});
db.iliadfr.insert({ pg : 7449, line : "n'eût poussé son fils Sarpèdôn contre les Argiens, comme un lion"});
db.iliadfr.insert({ pg : 7450, line : "contre des boeufs aux cornes recourbées."});
db.iliadfr.insert({ pg : 7452, line : "Et il tenait devant lui un bouclier d'une rondeur égale, beau,"});
db.iliadfr.insert({ pg : 7453, line : "revêtu de lames d'airain que l'ouvrier avait appliquées sur"});
db.iliadfr.insert({ pg : 7454, line : "d'épaisses peaux de boeuf, et entouré de longs cercles d'or. Et,"});
db.iliadfr.insert({ pg : 7455, line : "tenant ce bouclier et agitant deux lances, Sarpèdôn s'avançait,"});
db.iliadfr.insert({ pg : 7456, line : "comme un lion nourri sur les montagnes, qui, depuis longtemps"});
db.iliadfr.insert({ pg : 7457, line : "affamé, est excité par son coeur audacieux à enlever les brebis"});
db.iliadfr.insert({ pg : 7458, line : "jusque dans l'enclos profond, et qui, bien qu'elles soient gardées"});
db.iliadfr.insert({ pg : 7459, line : "par les chiens et par les pasteurs armés de lances, ne recule"});
db.iliadfr.insert({ pg : 7460, line : "point sans tenter le péril, mais d'un bond saisit sa proie, s'il"});
db.iliadfr.insert({ pg : 7461, line : "n'est d'abord percé par un trait rapide. Ainsi le coeur du divin"});
db.iliadfr.insert({ pg : 7462, line : "Sarpèdôn le poussait à enfoncer le rempart et à rompre les"});
db.iliadfr.insert({ pg : 7463, line : "parapets. Et il dit à Glaukos, fils de Hippolokhos:"});
db.iliadfr.insert({ pg : 7465, line : "-- Glaukos, pourquoi, dans la Lykiè, sommes-nous grandement"});
db.iliadfr.insert({ pg : 7466, line : "honorés par les meilleures places, les viandes et les coupes"});
db.iliadfr.insert({ pg : 7467, line : "pleines, et sommes-nous regardés comme des dieux? Pourquoi"});
db.iliadfr.insert({ pg : 7468, line : "cultivons-nous un grand domaine florissant, sur les rives du"});
db.iliadfr.insert({ pg : 7469, line : "Xanthos, une terre plantée de vignes et de blé? C'est afin que"});
db.iliadfr.insert({ pg : 7470, line : "nous soyons debout, en tête des Lykiens, dans l'ardente bataille."});
db.iliadfr.insert({ pg : 7471, line : "C'est afin que chacun des Lykiens bien armés dise: Nos rois, qui"});
db.iliadfr.insert({ pg : 7472, line : "gouvernent la Lykiè, ne sont pas sans gloire. S'ils mangent les"});
db.iliadfr.insert({ pg : 7473, line : "grasses brebis, s'ils boivent le vin excellent et doux, ils sont"});
db.iliadfr.insert({ pg : 7474, line : "pleins de courage et de vigueur, et ils combattent en tête des"});
db.iliadfr.insert({ pg : 7475, line : "Lykiens.' Ô ami, si en évitant la guerre nous pouvions rester"});
db.iliadfr.insert({ pg : 7476, line : "jeunes et immortels, je ne combattrais pas au premier rang et je"});
db.iliadfr.insert({ pg : 7477, line : "ne t'enverrais pas à la bataille glorieuse; mais mille chances de"});
db.iliadfr.insert({ pg : 7478, line : "mort nous enveloppent, et il n'est point permis à l'homme vivant"});
db.iliadfr.insert({ pg : 7479, line : "de les éviter ni de les fuir. Allons! donnons une grande gloire à"});
db.iliadfr.insert({ pg : 7480, line : "l'ennemi ou à nous."});
db.iliadfr.insert({ pg : 7482, line : "Il parla ainsi, et Glaukos ne recula point et lui obéit. Et ils"});
db.iliadfr.insert({ pg : 7483, line : "allaient, conduisant la foule des Lykiens. Et le fils de Pétéos,"});
db.iliadfr.insert({ pg : 7484, line : "Ménèstheus, frémit en les voyant, car ils se ruaient à l'assaut de"});
db.iliadfr.insert({ pg : 7485, line : "sa tour. Et il jeta les yeux sur la muraille des Akhaiens,"});
db.iliadfr.insert({ pg : 7486, line : "cherchant quelque chef qui vînt défendre ses compagnons. Et il"});
db.iliadfr.insert({ pg : 7487, line : "aperçut les deux Aias, insatiables de combats, et, auprès d'eux,"});
db.iliadfr.insert({ pg : 7488, line : "Teukros qui sortait de sa tente. Mais ses clameurs ne pouvaient"});
db.iliadfr.insert({ pg : 7489, line : "être entendues, tant était immense le retentissement qui montait"});
db.iliadfr.insert({ pg : 7490, line : "dans l'Ouranos, fracas des boucliers heurtés, des casques aux"});
db.iliadfr.insert({ pg : 7491, line : "crinières de chevaux, des portes assiégées et que les Troiens"});
db.iliadfr.insert({ pg : 7492, line : "s'efforçaient de rompre. Et, alors, Ménèstheus envoya vers Aias le"});
db.iliadfr.insert({ pg : 7493, line : "héraut Thoôs:"});
db.iliadfr.insert({ pg : 7495, line : "-- Va! divin Thoôs, appelle Aias, ou même les deux à la fois, ce"});
db.iliadfr.insert({ pg : 7496, line : "qui serait bien mieux, car c'est de ce côté que la ruine nous"});
db.iliadfr.insert({ pg : 7497, line : "menace. Voici que les chefs Lykiens se ruent sur nous, impétueux"});
db.iliadfr.insert({ pg : 7498, line : "comme ils le sont toujours dans les rudes batailles. Mais si le"});
db.iliadfr.insert({ pg : 7499, line : "combat retient ailleurs les deux Aias, amène au moins le robuste"});
db.iliadfr.insert({ pg : 7500, line : "Télamônien et l'excellent archer Teukros."});
db.iliadfr.insert({ pg : 7502, line : "Il parla ainsi, et Thoôs, l'ayant entendu, obéit, et, courant sur"});
db.iliadfr.insert({ pg : 7503, line : "la muraille des Argiens cuirassés, s'arrêta devant les Aias et"});
db.iliadfr.insert({ pg : 7504, line : "leur dit aussitôt."});
db.iliadfr.insert({ pg : 7506, line : "-- Aias, chefs des Argiens cuirassés, le fils bien-aimé du divin"});
db.iliadfr.insert({ pg : 7507, line : "Pétéos vous demande d'accourir à son aide, tous deux si vous le"});
db.iliadfr.insert({ pg : 7508, line : "pouvez, ce qui serait bien mieux, car c'est de ce côté que la"});
db.iliadfr.insert({ pg : 7509, line : "ruine nous menace. Voici que les chefs Lykiens se ruent sur nous,"});
db.iliadfr.insert({ pg : 7510, line : "impétueux comme ils le sont toujours dans les rudes batailles."});
db.iliadfr.insert({ pg : 7511, line : "Mais si le combat vous retient tous deux, que le robuste Aias"});
db.iliadfr.insert({ pg : 7512, line : "Télamônien vienne au moins, et, avec lui, l'excellent archer"});
db.iliadfr.insert({ pg : 7513, line : "Teukros."});
db.iliadfr.insert({ pg : 7515, line : "Il parla ainsi, et, sans tarder, le grand Télamônien dit aussitôt"});
db.iliadfr.insert({ pg : 7516, line : "à l'Oiliade:"});
db.iliadfr.insert({ pg : 7518, line : "-- Aias, toi et le brave Lykomèdès, inébranlables, excitez les"});
db.iliadfr.insert({ pg : 7519, line : "Danaens au combat. Moi, j'irai à l'aide de Ménèstheus, et je"});
db.iliadfr.insert({ pg : 7520, line : "reviendrai après l'avoir secouru."});
db.iliadfr.insert({ pg : 7522, line : "Ayant ainsi parlé, le Télamônien Aias s'éloigna avec son frère"});
db.iliadfr.insert({ pg : 7523, line : "Teukros né du même père que lui, et, avec eux, Pandiôn, qui"});
db.iliadfr.insert({ pg : 7524, line : "portait l'arc de Teukros."});
db.iliadfr.insert({ pg : 7526, line : "Et quand ils eurent atteint la tour du magnanime Ménèstheus, ils"});
db.iliadfr.insert({ pg : 7527, line : "se placèrent derrière le mur à l'instant même du danger, car les"});
db.iliadfr.insert({ pg : 7528, line : "illustres princes et chefs des Lykiens montaient à l'assaut de la"});
db.iliadfr.insert({ pg : 7529, line : "muraille, semblables à un noir tourbillon. Et ils se"});
db.iliadfr.insert({ pg : 7530, line : "rencontrèrent, et une horrible clameur s'éleva de leur choc."});
db.iliadfr.insert({ pg : 7532, line : "Et Aias Télamônien, le premier, tua un compagnon de Sarpèdôn, le"});
db.iliadfr.insert({ pg : 7533, line : "magnanime Épikleus. Et il le frappa d'un rude bloc de marbre qui"});
db.iliadfr.insert({ pg : 7534, line : "gisait, énorme, en dedans du mur, au sommet du rempart, près des"});
db.iliadfr.insert({ pg : 7535, line : "créneaux, et tel que, de ses deux mains, un jeune guerrier, de"});
db.iliadfr.insert({ pg : 7536, line : "ceux qui vivent de nos jours, ne soulèverait point le pareil."});
db.iliadfr.insert({ pg : 7537, line : "Aias, de son bras tendu, l'enleva en l'air, brisa le casque aux"});
db.iliadfr.insert({ pg : 7538, line : "quatre cônes et écrasa entièrement la tête du guerrier. Et celui-"});
db.iliadfr.insert({ pg : 7539, line : "ci tomba du faîte de la tour, comme un plongeur, et son esprit"});
db.iliadfr.insert({ pg : 7540, line : "abandonna ses ossements."});
db.iliadfr.insert({ pg : 7542, line : "Et Teukros perça d'une flèche le bras nu du brave Glaukos, fils de"});
db.iliadfr.insert({ pg : 7543, line : "Hippolokhos, à l'instant où celui-ci escaladait la haute muraille,"});
db.iliadfr.insert({ pg : 7544, line : "et il l'éloigna du combat. Et Glaukos sauta du mur pour que nul"});
db.iliadfr.insert({ pg : 7545, line : "des Akhaiens ne vît sa blessure et ne l'insultât."});
db.iliadfr.insert({ pg : 7547, line : "Et Sarpèdôn, le voyant fuir, fut saisi de douleur; mais, sans"});
db.iliadfr.insert({ pg : 7548, line : "oublier de combattre, il frappa le Thestoride Alkmaôn de sa lance,"});
db.iliadfr.insert({ pg : 7549, line : "et, la ramenant à lui, il entraîna l'homme la face contre terre,"});
db.iliadfr.insert({ pg : 7550, line : "et les armes d'airain du Thestoride retentirent dans sa chute. Et"});
db.iliadfr.insert({ pg : 7551, line : "Sarpèdôn saisit de ses mains vigoureuses un créneau du mur, et il"});
db.iliadfr.insert({ pg : 7552, line : "l'arracha tout entier, et la muraille resta béante, livrant un"});
db.iliadfr.insert({ pg : 7553, line : "chemin à la multitude."});
db.iliadfr.insert({ pg : 7555, line : "Et Aias et Teukros firent face tous deux. Et Teukros frappa"});
db.iliadfr.insert({ pg : 7556, line : "Sarpèdôn sur le baudrier splendide qui entourait la poitrine, mais"});
db.iliadfr.insert({ pg : 7557, line : "Zeus détourna la flèche du corps de son fils, afin qu'il ne fût"});
db.iliadfr.insert({ pg : 7558, line : "point tué devant les nefs. Et Aias, d'un bond, frappa le bouclier"});
db.iliadfr.insert({ pg : 7559, line : "de Sarpèdôn, et la lance y pénétra, réprimant l'impétuosité du"});
db.iliadfr.insert({ pg : 7560, line : "guerrier qui s'éloigna du mur, mais sans se retirer, car son coeur"});
db.iliadfr.insert({ pg : 7561, line : "espérait la victoire. Et, se retournant, il exhorta ainsi les"});
db.iliadfr.insert({ pg : 7562, line : "nobles Lykiens:"});
db.iliadfr.insert({ pg : 7564, line : "-- Ô Lykiens, pourquoi laissez-vous de côté votre ardent courage?"});
db.iliadfr.insert({ pg : 7565, line : "Il m'est difficile, tout robuste que je suis, de renverser seul"});
db.iliadfr.insert({ pg : 7566, line : "cette muraille et de frayer un chemin vers les nefs. Accourez"});
db.iliadfr.insert({ pg : 7567, line : "donc. Toutes nos forces réunies réussiront mieux."});
db.iliadfr.insert({ pg : 7569, line : "Il parla ainsi, et, touchés de ses reproches, ils se précipitèrent"});
db.iliadfr.insert({ pg : 7570, line : "autour de leur roi. Et les Argiens, de leur côté, derrière la"});
db.iliadfr.insert({ pg : 7571, line : "muraille, renforçaient leurs phalanges, car une lourde tâche leur"});
db.iliadfr.insert({ pg : 7572, line : "était réservée. Et les illustres Lykiens, ayant rompu la muraille,"});
db.iliadfr.insert({ pg : 7573, line : "ne pouvaient cependant se frayer un chemin jusqu'aux nefs. Et les"});
db.iliadfr.insert({ pg : 7574, line : "belliqueux Danaens, les ayant arrêtés, ne pouvaient non plus les"});
db.iliadfr.insert({ pg : 7575, line : "repousser loin de la muraille."});
db.iliadfr.insert({ pg : 7577, line : "De même que deux hommes, la mesure à la main, se querellent sur le"});
db.iliadfr.insert({ pg : 7578, line : "partage d'un champ commun et se disputent la plus petite portion"});
db.iliadfr.insert({ pg : 7579, line : "du terrain, de même, séparés par les créneaux, les combattants"});
db.iliadfr.insert({ pg : 7580, line : "heurtaient de toutes parts les boucliers au grand orbe et les"});
db.iliadfr.insert({ pg : 7581, line : "défenses plus légères. Et beaucoup étaient blessés par l'airain"});
db.iliadfr.insert({ pg : 7582, line : "cruel; et ceux qui, en fuyant, découvraient leur dos, étaient"});
db.iliadfr.insert({ pg : 7583, line : "percés, même à travers les boucliers. Et les tours et les créneaux"});
db.iliadfr.insert({ pg : 7584, line : "étaient inondés du sang des guerriers. Et les Troiens ne pouvaient"});
db.iliadfr.insert({ pg : 7585, line : "mettre en fuite les Akhaiens, mais ils se contenaient les uns les"});
db.iliadfr.insert({ pg : 7586, line : "autres. Telles sont les balances d'une ouvrière équitable. Elle"});
db.iliadfr.insert({ pg : 7587, line : "tient les poids d'un côté et la laine de l'autre, et elle les pèse"});
db.iliadfr.insert({ pg : 7588, line : "et les égalise, afin d'apporter à ses enfants un chétif salaire."});
db.iliadfr.insert({ pg : 7589, line : "Ainsi le combat restait égal entre les deux partis, jusqu'au"});
db.iliadfr.insert({ pg : 7590, line : "moment où Zeus accorda une gloire éclatante au Priamide Hektôr"});
db.iliadfr.insert({ pg : 7591, line : "qui, le premier, franchit le mur des Akhaiens. Et il cria d'une"});
db.iliadfr.insert({ pg : 7592, line : "voix retentissante, afin d'être entendu des Troiens:"});
db.iliadfr.insert({ pg : 7594, line : "-- En avant, cavaliers Troiens! Rompez la muraille des Argiens, et"});
db.iliadfr.insert({ pg : 7595, line : "allumez de vos mains une immense flamme ardente."});
db.iliadfr.insert({ pg : 7597, line : "Il parla ainsi, et tous l'entendirent, et ils se jetèrent sur la"});
db.iliadfr.insert({ pg : 7598, line : "muraille, escaladant les créneaux et dardant les lances aiguës. Et"});
db.iliadfr.insert({ pg : 7599, line : "Hektôr portait une pierre énorme, lourde, pointue, qui gisait"});
db.iliadfr.insert({ pg : 7600, line : "devant les portes, telle que deux très robustes hommes de nos"});
db.iliadfr.insert({ pg : 7601, line : "jours n'en pourraient soulever la pareille de terre, sur leur"});
db.iliadfr.insert({ pg : 7602, line : "chariot. Mais, seul, il l'agitait facilement, car le fils du"});
db.iliadfr.insert({ pg : 7603, line : "subtil Kronos la lui rendait légère. De même qu'un berger porte"});
db.iliadfr.insert({ pg : 7604, line : "aisément dans sa main la toison d'un bélier, et en trouve le poids"});
db.iliadfr.insert({ pg : 7605, line : "léger, de même Hektôr portait la pierre soulevée droit aux ais"});
db.iliadfr.insert({ pg : 7606, line : "doubles qui défendaient les portes, hautes, solides et à deux"});
db.iliadfr.insert({ pg : 7607, line : "battants. Deux poutres les fermaient en dedans, traversées par une"});
db.iliadfr.insert({ pg : 7608, line : "cheville."});
db.iliadfr.insert({ pg : 7610, line : "Et, s'approchant, il se dressa sur ses pieds et frappa la porte"});
db.iliadfr.insert({ pg : 7611, line : "par le milieu, et le choc ne fut pas inutile. Il rompit les deux"});
db.iliadfr.insert({ pg : 7612, line : "gonds, et la pierre enfonça le tout et tomba lourdement de l'autre"});
db.iliadfr.insert({ pg : 7613, line : "côté. Et ni les poutres brisées, ni les battants en éclats ne"});
db.iliadfr.insert({ pg : 7614, line : "résistèrent au choc de la pierre. Et l'illustre Hektôr sauta dans"});
db.iliadfr.insert({ pg : 7615, line : "le camp, semblable à une nuit rapide, tandis que l'airain dont il"});
db.iliadfr.insert({ pg : 7616, line : "était revêtu resplendissait. Et il brandissait deux lances dans"});
db.iliadfr.insert({ pg : 7617, line : "ses mains, et nul, excepté un dieu, n'eût pu l'arrêter dans son"});
db.iliadfr.insert({ pg : 7618, line : "élan."});
db.iliadfr.insert({ pg : 7620, line : "Et le feu luisait dans ses yeux. Et il commanda à la multitude des"});
db.iliadfr.insert({ pg : 7621, line : "Troiens de franchir la muraille, et tous lui obéirent. Les uns"});
db.iliadfr.insert({ pg : 7622, line : "escaladèrent la muraille, les autres enfoncèrent les portes, et"});
db.iliadfr.insert({ pg : 7623, line : "les Danaens s'enfuirent jusqu'aux nefs creuses, et un immense"});
db.iliadfr.insert({ pg : 7624, line : "tumulte s'éleva."});
db.iliadfr.insert({ pg : 7627, line : "Chant 13"});
db.iliadfr.insert({ pg : 7629, line : "Et dès que Zeus eut poussé Hektôr et les Troiens jusqu'aux nefs,"});
db.iliadfr.insert({ pg : 7630, line : "les y laissant soutenir seuls le rude combat, il tourna ses yeux"});
db.iliadfr.insert({ pg : 7631, line : "splendides sur la terre des cavaliers Thrèkiens, des Mysiens, qui"});
db.iliadfr.insert({ pg : 7632, line : "combattent de près, et des illustres Hippomolgues qui se"});
db.iliadfr.insert({ pg : 7633, line : "nourrissent de lait, pauvres, mais les plus justes des hommes. Et"});
db.iliadfr.insert({ pg : 7634, line : "Zeus ne jetait plus ses yeux splendides sur Troiè, ne pensant"});
db.iliadfr.insert({ pg : 7635, line : "point dans son esprit qu'aucun des immortels osât secourir ou les"});
db.iliadfr.insert({ pg : 7636, line : "Troiens, ou les Danaens."});
db.iliadfr.insert({ pg : 7638, line : "Mais celui qui ébranle la terre ne veillait pas en vain, et il"});
db.iliadfr.insert({ pg : 7639, line : "regardait la guerre et le combat, assis sur le plus haut sommet de"});
db.iliadfr.insert({ pg : 7640, line : "la Samothrèkè feuillue, d'où apparaissaient tout l'Ida et la ville"});
db.iliadfr.insert({ pg : 7641, line : "de Priamos et les nefs des Akhaiens. Et là, assis hors de la mer,"});
db.iliadfr.insert({ pg : 7642, line : "il prenait pitié des Akhaiens domptés par les Troiens, et"});
db.iliadfr.insert({ pg : 7643, line : "s'irritait profondément contre Zeus. Et, aussitôt, il descendit du"});
db.iliadfr.insert({ pg : 7644, line : "sommet escarpé, et les hautes montagnes et les forêts tremblaient"});
db.iliadfr.insert({ pg : 7645, line : "sous les pieds immortels de Poseidaôn qui marchait. Et il fit"});
db.iliadfr.insert({ pg : 7646, line : "trois pas, et, au quatrième, il atteignit le terme de sa course,"});
db.iliadfr.insert({ pg : 7647, line : "Aigas, où, dans les gouffres de la mer, étaient ses illustres"});
db.iliadfr.insert({ pg : 7648, line : "demeures d'or, éclatantes et incorruptibles."});
db.iliadfr.insert({ pg : 7650, line : "Et là, il attacha au char ses chevaux rapides, dont les pieds"});
db.iliadfr.insert({ pg : 7651, line : "étaient d'airain et les crinières d'or. Et il se revêtit d'or lui-"});
db.iliadfr.insert({ pg : 7652, line : "même, saisit le fouet d'or habilement travaillé, et monta sur son"});
db.iliadfr.insert({ pg : 7653, line : "char. Et il allait sur les eaux, et, de toutes parts, les cétacés,"});
db.iliadfr.insert({ pg : 7654, line : "émergeant de l'abîme, bondissaient, joyeux, et reconnaissaient"});
db.iliadfr.insert({ pg : 7655, line : "leur roi. Et la mer s'ouvrait avec allégresse, et les chevaux"});
db.iliadfr.insert({ pg : 7656, line : "volaient rapidement sans que l'écume mouillât l'essieu d'airain."});
db.iliadfr.insert({ pg : 7657, line : "Et les chevaux agiles le portèrent jusqu'aux nefs."});
db.iliadfr.insert({ pg : 7659, line : "Et il y avait un antre large dans les gouffres de la mer profonde,"});
db.iliadfr.insert({ pg : 7660, line : "entre Ténédos et l'âpre Imbros. Là, Poseidaôn qui ébranle la terre"});
db.iliadfr.insert({ pg : 7661, line : "arrêta ses chevaux, les délia du char, leur offrit la nourriture"});
db.iliadfr.insert({ pg : 7662, line : "divine et leur mit aux pieds des entraves d'or solides et"});
db.iliadfr.insert({ pg : 7663, line : "indissolubles, afin qu'ils attendissent en paix le retour de leur"});
db.iliadfr.insert({ pg : 7664, line : "roi. Et il s'avança vers l'armée des Akhaiens."});
db.iliadfr.insert({ pg : 7666, line : "Et les Troiens amoncelés, semblables à la flamme, tels qu'une"});
db.iliadfr.insert({ pg : 7667, line : "tempête, pleins de frémissements et de clameurs, se précipitaient,"});
db.iliadfr.insert({ pg : 7668, line : "furieux, derrière le Priamide Hektôr. Et ils espéraient se saisir"});
db.iliadfr.insert({ pg : 7669, line : "des nefs des Akhaiens et y tuer tous les Akhaiens. Mais Poseidaôn"});
db.iliadfr.insert({ pg : 7670, line : "qui entoure la terre et qui la secoue, sorti de la mer profonde,"});
db.iliadfr.insert({ pg : 7671, line : "excitait les Argiens, ayant revêtu le corps de Kalkhas et pris sa"});
db.iliadfr.insert({ pg : 7672, line : "voix infatigable. Et il parla ainsi aux deux Aias, pleins d'ardeur"});
db.iliadfr.insert({ pg : 7673, line : "eux-mêmes:"});
db.iliadfr.insert({ pg : 7675, line : "-- Aias! Vous sauverez les hommes d'Akhaiè, si vous vous souvenez"});
db.iliadfr.insert({ pg : 7676, line : "de votre courage et non de la fuite désastreuse. Ailleurs, je ne"});
db.iliadfr.insert({ pg : 7677, line : "crains pas les efforts des Troiens qui ont franchi notre grande"});
db.iliadfr.insert({ pg : 7678, line : "muraille, car les braves Akhaiens soutiendront l'attaque; mais"});
db.iliadfr.insert({ pg : 7679, line : "c'est ici, je pense, que nous aurons à subir de plus grands maux,"});
db.iliadfr.insert({ pg : 7680, line : "devant Hektôr, plein de rage, semblable à la flamme, et qui se"});
db.iliadfr.insert({ pg : 7681, line : "vante d'être le fils du très puissant Zeus. Puisse un des dieux"});
db.iliadfr.insert({ pg : 7682, line : "vous inspirer de lui résister courageusement! Et vous, exhortez"});
db.iliadfr.insert({ pg : 7683, line : "vos compagnons, afin de rejeter le Priamide, malgré son audace,"});
db.iliadfr.insert({ pg : 7684, line : "loin des nefs rapides, même quand l'Olympien l'exciterait."});
db.iliadfr.insert({ pg : 7686, line : "Celui qui entoure la terre et qui l'ébranle parla ainsi, et, les"});
db.iliadfr.insert({ pg : 7687, line : "frappant de son sceptre, il les remplit de force et de courage et"});
db.iliadfr.insert({ pg : 7688, line : "rendit légers leurs pieds et leurs mains. Et lui-même s'éloigna"});
db.iliadfr.insert({ pg : 7689, line : "aussitôt, comme le rapide épervier, qui, s'élançant à tire-d'aile"});
db.iliadfr.insert({ pg : 7690, line : "du faîte d'un rocher escarpé, poursuit dans la plaine un oiseau"});
db.iliadfr.insert({ pg : 7691, line : "d'une autre race. Ainsi, Poseidaôn qui ébranle la terre s'éloigna"});
db.iliadfr.insert({ pg : 7692, line : "d'eux. Et aussitôt le premier des deux, le rapide Aias Oilèiade,"});
db.iliadfr.insert({ pg : 7693, line : "dit au Télamôniade:"});
db.iliadfr.insert({ pg : 7695, line : "-- Aias, sans doute un des dieux Olympiens, ayant pris la forme du"});
db.iliadfr.insert({ pg : 7696, line : "divinateur, vient de nous ordonner de combattre auprès des nefs."});
db.iliadfr.insert({ pg : 7697, line : "Car ce n'est point là le divinateur Kalkhas. J'ai facilement"});
db.iliadfr.insert({ pg : 7698, line : "reconnu les pieds de celui qui s'éloigne. Les dieux sont aisés à"});
db.iliadfr.insert({ pg : 7699, line : "reconnaître. Je sens mon coeur, dans ma poitrine, plein d'ardeur"});
db.iliadfr.insert({ pg : 7700, line : "pour la guerre et le combat, et mes mains et mes pieds sont plus"});
db.iliadfr.insert({ pg : 7701, line : "légers."});
db.iliadfr.insert({ pg : 7703, line : "Et le Télamônien Aias lui répondit:"});
db.iliadfr.insert({ pg : 7705, line : "-- Et moi aussi, je sens mes mains rudes frémir autour de ma"});
db.iliadfr.insert({ pg : 7706, line : "lance, et ma force me secouer et mes pieds m'emporter en avant. Et"});
db.iliadfr.insert({ pg : 7707, line : "voici que je suis prêt à lutter seul contre le Priamide Hektôr qui"});
db.iliadfr.insert({ pg : 7708, line : "ne se lasse jamais de combattre."});
db.iliadfr.insert({ pg : 7710, line : "Et tandis qu'ils se parlaient ainsi, joyeux de l'ardeur guerrière"});
db.iliadfr.insert({ pg : 7711, line : "que le dieu avait mise dans leurs coeurs, celui-ci, loin d'eux,"});
db.iliadfr.insert({ pg : 7712, line : "encourageait les Akhaiens qui reposaient leur âme auprès des nefs"});
db.iliadfr.insert({ pg : 7713, line : "rapides, car leurs membres étaient rompus de fatigue, et une amère"});
db.iliadfr.insert({ pg : 7714, line : "douleur les saisissait à la vue des Troiens qui avaient franchi la"});
db.iliadfr.insert({ pg : 7715, line : "grande muraille. Et des larmes coulaient de leurs paupières, et"});
db.iliadfr.insert({ pg : 7716, line : "ils n'espéraient plus fuir leur ruine. Mais celui qui ébranle la"});
db.iliadfr.insert({ pg : 7717, line : "terre ranima facilement leurs braves phalanges. Et il exhorta"});
db.iliadfr.insert({ pg : 7718, line : "Teukros, Lèitos, Pénéléos, Thoas, Dèipyros, Mèrionès et"});
db.iliadfr.insert({ pg : 7719, line : "Antilokhos, habiles au combat. Et il leur dit en paroles ailées:"});
db.iliadfr.insert({ pg : 7721, line : "-- Ô honte! jeunes guerriers Argiens, je me fiais en votre courage"});
db.iliadfr.insert({ pg : 7722, line : "pour sauver nos nefs, mais, si vous suspendez le combat, voici que"});
db.iliadfr.insert({ pg : 7723, line : "le jour est venu d'être domptés par les Troiens. Ô douleur! je"});
db.iliadfr.insert({ pg : 7724, line : "vois de mes yeux ce grand prodige terrible que je ne pensais point"});
db.iliadfr.insert({ pg : 7725, line : "voir jamais, les Troiens sur nos nefs! Eux qui, auparavant,"});
db.iliadfr.insert({ pg : 7726, line : "étaient semblables aux cerfs fuyards, pâture des lynx, des"});
db.iliadfr.insert({ pg : 7727, line : "léopards et des loups, errants par les forêts, sans force et"});
db.iliadfr.insert({ pg : 7728, line : "inhabiles au combat! Car les Troiens n'osaient, auparavant, braver"});
db.iliadfr.insert({ pg : 7729, line : "en face la vigueur des Akhaiens; et, maintenant, loin de la ville,"});
db.iliadfr.insert({ pg : 7730, line : "ils combattent auprès des nefs creuses, grâce à la lâcheté du chef"});
db.iliadfr.insert({ pg : 7731, line : "et à la négligence des hommes qui refusent de défendre les nefs"});
db.iliadfr.insert({ pg : 7732, line : "rapides, et s'y laissent tuer. Mais, s'il est vrai que l'Atréide"});
db.iliadfr.insert({ pg : 7733, line : "Agamemnôn qui règne au loin soit coupable d'avoir outragé le"});
db.iliadfr.insert({ pg : 7734, line : "Pèléiôn aux pieds rapides, nous est-il permis pour cela"});
db.iliadfr.insert({ pg : 7735, line : "d'abandonner le combat? Réparons ce mal. Les esprits justes se"});
db.iliadfr.insert({ pg : 7736, line : "guérissent aisément de l'erreur. Vous ne pouvez sans honte oublier"});
db.iliadfr.insert({ pg : 7737, line : "votre courage, étant parmi les plus braves. Je ne m'inquiéterais"});
db.iliadfr.insert({ pg : 7738, line : "point d'un lâche qui fuirait le combat, mais, contre vous, je"});
db.iliadfr.insert({ pg : 7739, line : "m'indigne dans mon coeur. Ô pleins de mollesse, bientôt vous aurez"});
db.iliadfr.insert({ pg : 7740, line : "causé par votre inaction un mal irréparable. Que la honte et mes"});
db.iliadfr.insert({ pg : 7741, line : "reproches entrent dans vos âmes, car voici qu'un grand combat"});
db.iliadfr.insert({ pg : 7742, line : "s'engage et que le brave Hektôr, ayant rompu nos portes et nos"});
db.iliadfr.insert({ pg : 7743, line : "barrières, combat auprès des nefs."});
db.iliadfr.insert({ pg : 7745, line : "Et, parlant ainsi, celui qui ébranle la terre excitait les"});
db.iliadfr.insert({ pg : 7746, line : "Akhaiens. Et autour des deux Aias se pressaient de solides"});
db.iliadfr.insert({ pg : 7747, line : "phalanges qu'auraient louées Arès et Athènè qui excite les"});
db.iliadfr.insert({ pg : 7748, line : "guerriers. Et les plus braves attendaient les Troiens et le divin"});
db.iliadfr.insert({ pg : 7749, line : "Hektôr, lance contre lance, bouclier contre bouclier, casque"});
db.iliadfr.insert({ pg : 7750, line : "contre casque, homme contre homme. Et les crinières, sur les cônes"});
db.iliadfr.insert({ pg : 7751, line : "splendides, se mêlaient, tant les rangs étaient épais; et les"});
db.iliadfr.insert({ pg : 7752, line : "lances s'agitaient entre les mains audacieuses, et tous"});
db.iliadfr.insert({ pg : 7753, line : "marchaient, pleins du désir de combattre."});
db.iliadfr.insert({ pg : 7755, line : "Mais sur eux se ruent une foule de Troiens, derrière Hektôr qui"});
db.iliadfr.insert({ pg : 7756, line : "s'élançait. De même qu'une roche désastreuse qu'un torrent, gonflé"});
db.iliadfr.insert({ pg : 7757, line : "par une immense pluie, roule, déracinée, de la cime d'un mont, et"});
db.iliadfr.insert({ pg : 7758, line : "qui se précipite à travers tous les obstacles jusqu'à ce qu'elle"});
db.iliadfr.insert({ pg : 7759, line : "arrive à la plaine où, bien qu'arrêtée dans sa course, elle remue"});
db.iliadfr.insert({ pg : 7760, line : "encore; de même Hektôr menaçait d'arriver jusqu'à la mer, aux"});
db.iliadfr.insert({ pg : 7761, line : "tentes et aux nefs des Akhaiens; mais il se heurta contre les"});
db.iliadfr.insert({ pg : 7762, line : "masses épaisses d'hommes, contraint de s'arrêter. Et les fils des"});
db.iliadfr.insert({ pg : 7763, line : "Akhaiens le repoussèrent en le frappant de leurs épées et de leurs"});
db.iliadfr.insert({ pg : 7764, line : "lances aiguës. Alors, reculant, il s'écria d'une voix haute aux"});
db.iliadfr.insert({ pg : 7765, line : "Troiens:"});
db.iliadfr.insert({ pg : 7767, line : "-- Troiens, Lykiens et Dardaniens belliqueux, restez fermes. Les"});
db.iliadfr.insert({ pg : 7768, line : "Akhaiens ne me résisteront pas longtemps, bien qu'ils se dressent"});
db.iliadfr.insert({ pg : 7769, line : "maintenant comme une tour; mais ils vont fuir devant ma lance, si"});
db.iliadfr.insert({ pg : 7770, line : "le plus grand des dieux, l'époux tonnant de Hèrè, m'encourage."});
db.iliadfr.insert({ pg : 7772, line : "Il parla ainsi, excitant la force et la vaillance de chacun. Et le"});
db.iliadfr.insert({ pg : 7773, line : "Priamide Dèiphobos, plein de fierté, marchait d'un pied léger au"});
db.iliadfr.insert({ pg : 7774, line : "milieu d'eux, couvert de son bouclier d'une rondeur égale. Et"});
db.iliadfr.insert({ pg : 7775, line : "Mèrionès lança contre lui sa pique étincelante, qui, ne s'égarant"});
db.iliadfr.insert({ pg : 7776, line : "point, frappa le bouclier d'une rondeur égale et fait de peau de"});
db.iliadfr.insert({ pg : 7777, line : "taureau; mais la longue lance y pénétra à peine et se brisa à son"});
db.iliadfr.insert({ pg : 7778, line : "extrémité. Et Dèiphobos éloigna de sa poitrine le bouclier de peau"});
db.iliadfr.insert({ pg : 7779, line : "de taureau, craignant la lance du brave Mèrionès; mais ce héros"});
db.iliadfr.insert({ pg : 7780, line : "rentra dans la foule de ses compagnons, indigné d'avoir manqué la"});
db.iliadfr.insert({ pg : 7781, line : "victoire et rompu sa lance. Et il courut vers les nefs des"});
db.iliadfr.insert({ pg : 7782, line : "Akhaiens, afin d'y chercher une longue pique qu'il avait laissée"});
db.iliadfr.insert({ pg : 7783, line : "dans sa tente. Mais d'autres combattaient, et une immense clameur"});
db.iliadfr.insert({ pg : 7784, line : "s'élevait de tous côtés."});
db.iliadfr.insert({ pg : 7786, line : "Et Teukros Télamônien tua, le premier, le brave guerrier Imbrios,"});
db.iliadfr.insert({ pg : 7787, line : "fils de Mentôr et riche en chevaux. Et, avant l'arrivée des fils"});
db.iliadfr.insert({ pg : 7788, line : "des Akhaiens, il habitait Pèdaios, avec Mèdésikastè, fille"});
db.iliadfr.insert({ pg : 7789, line : "illégitime de Priamos; mais, après l'arrivée des nefs aux doubles"});
db.iliadfr.insert({ pg : 7790, line : "avirons des Danaens, il vint à Ilios et s'illustra parmi les"});
db.iliadfr.insert({ pg : 7791, line : "Troiens."});
db.iliadfr.insert({ pg : 7793, line : "Et le fils de Télamôn, de sa longue lance, le perça sous"});
db.iliadfr.insert({ pg : 7794, line : "l'oreille, et il tomba, comme un frêne qui, tranché par l'airain"});
db.iliadfr.insert({ pg : 7795, line : "sur le sommet d'un mont élevé, couvre la terre de son feuillage"});
db.iliadfr.insert({ pg : 7796, line : "délicat. Il tomba ainsi, et ses belles armes d'airain sonnèrent"});
db.iliadfr.insert({ pg : 7797, line : "autour de lui. Et Teukros accourut pour le dépouiller; mais"});
db.iliadfr.insert({ pg : 7798, line : "Hektôr, comme il s'élançait, lança contre lui sa pique éclatante."});
db.iliadfr.insert({ pg : 7799, line : "Et le Télamônien la vit et l'évita, et la lance du Priamide frappa"});
db.iliadfr.insert({ pg : 7800, line : "dans la poitrine Amphimakhos, fils de Ktéatos Aktorionide, qui"});
db.iliadfr.insert({ pg : 7801, line : "s'avançait. Et sa chute retentit et ses armes sonnèrent sur lui."});
db.iliadfr.insert({ pg : 7802, line : "Et Hektôr s'élança pour dépouiller du casque bien adapté aux"});
db.iliadfr.insert({ pg : 7803, line : "tempes le magnanime Amphimakhos. Mais Aias se rua sur lui, armé"});
db.iliadfr.insert({ pg : 7804, line : "d'une pique étincelante; et, comme Hektôr était entièrement"});
db.iliadfr.insert({ pg : 7805, line : "enveloppé de l'airain effrayant, Aias frappa seulement le bouclier"});
db.iliadfr.insert({ pg : 7806, line : "bombé et le repoussa violemment loin des deux cadavres que les"});
db.iliadfr.insert({ pg : 7807, line : "Akhaiens entraînèrent."});
db.iliadfr.insert({ pg : 7809, line : "Et Stikhios et le divin Ménèstheus, princes des Athènaiens,"});
db.iliadfr.insert({ pg : 7810, line : "portèrent Amphimakhos dans les tentes des Akhaiens, et les Aias,"});
db.iliadfr.insert({ pg : 7811, line : "avides du combat impétueux, se saisirent d'Imbrios. De même que"});
db.iliadfr.insert({ pg : 7812, line : "deux lions, arrachant une chèvre aux dents aiguës des chiens,"});
db.iliadfr.insert({ pg : 7813, line : "l'emportent à travers les taillis épais en la tenant loin de terre"});
db.iliadfr.insert({ pg : 7814, line : "dans leurs mâchoires, de même les deux Aias enlevèrent Imbrios et"});
db.iliadfr.insert({ pg : 7815, line : "le dépouillèrent de ses armes. Et Aias Oilèiade, furieux de la"});
db.iliadfr.insert({ pg : 7816, line : "mort d'Amphimakhos, coupa la tête du Troien, et, la jetant comme"});
db.iliadfr.insert({ pg : 7817, line : "une boule au travers de la multitude, l'envoya rouler dans la"});
db.iliadfr.insert({ pg : 7818, line : "poussière, sous les pieds de Hektôr. Et alors, Poseidaôn, irrité"});
db.iliadfr.insert({ pg : 7819, line : "de la mort de son petit-fils tué dans le combat, courut aux tentes"});
db.iliadfr.insert({ pg : 7820, line : "des Akhaiens, afin d'exciter les Danaens et de préparer des"});
db.iliadfr.insert({ pg : 7821, line : "calamités aux Troiens."});
db.iliadfr.insert({ pg : 7823, line : "Et Idoméneus, illustre par sa lance, le rencontra. Et celui-ci"});
db.iliadfr.insert({ pg : 7824, line : "quittait un de ses compagnons qui, dans le combat, avait été"});
db.iliadfr.insert({ pg : 7825, line : "frappé au jarret par l'airain aigu et emporté par les siens. Et"});
db.iliadfr.insert({ pg : 7826, line : "Idoméneus, l'ayant confié aux médecins, sortait de sa tente, plein"});
db.iliadfr.insert({ pg : 7827, line : "du désir de retourner au combat. Et le roi qui ébranle la terre"});
db.iliadfr.insert({ pg : 7828, line : "lui parla ainsi, ayant pris la figure et la voix de l'Andraimonide"});
db.iliadfr.insert({ pg : 7829, line : "Thoas, qui, dans tout Pleurôn et la haute Kalydôn, commandait aux"});
db.iliadfr.insert({ pg : 7830, line : "Aitôliens, et que ceux-ci honoraient comme un dieu:"});
db.iliadfr.insert({ pg : 7832, line : "-- Idoméneus, prince des Krètois, où sont tes menaces et celles"});
db.iliadfr.insert({ pg : 7833, line : "des Akhaiens aux Troiens?"});
db.iliadfr.insert({ pg : 7835, line : "Et le prince des Krètois, Idoméneus, lui répondit:"});
db.iliadfr.insert({ pg : 7837, line : "-- Ô Thoas, aucun guerrier n'est en faute, autant que j'en puis"});
db.iliadfr.insert({ pg : 7838, line : "juger, car nous combattons tous; aucun n'est retenu par la pâle"});
db.iliadfr.insert({ pg : 7839, line : "crainte, aucun, par indolence, ne refuse le combat dangereux; mais"});
db.iliadfr.insert({ pg : 7840, line : "cela plaît sans doute au très puissant Zeus que les Akhaiens"});
db.iliadfr.insert({ pg : 7841, line : "périssent ici, sans gloire et loin d'Argos. Thoas, toi qui,"});
db.iliadfr.insert({ pg : 7842, line : "toujours plein d'ardeur guerrière, as coutume d'encourager les"});
db.iliadfr.insert({ pg : 7843, line : "faibles, ne cesse pas dans ce moment, et ranime la vaillance de"});
db.iliadfr.insert({ pg : 7844, line : "chaque guerrier."});
db.iliadfr.insert({ pg : 7846, line : "Et Poseidaôn qui ébranle la terre lui répondit:"});
db.iliadfr.insert({ pg : 7848, line : "-- Idoméneus, ne puisse-t-il jamais revenir de la terre Troienne,"});
db.iliadfr.insert({ pg : 7849, line : "puisse-t-il être la proie des chiens, le guerrier qui, en ce jour,"});
db.iliadfr.insert({ pg : 7850, line : "cessera volontairement de combattre! Va! et reviens avec tes"});
db.iliadfr.insert({ pg : 7851, line : "armes. Il faut nous concerter. Peut-être serons-nous tous deux de"});
db.iliadfr.insert({ pg : 7852, line : "quelque utilité. L'union des guerriers est utile, même celle des"});
db.iliadfr.insert({ pg : 7853, line : "plus timides; et nous saurons combattre les héros."});
db.iliadfr.insert({ pg : 7855, line : "Ayant ainsi parlé, le dieu rentra dans la mêlée des hommes, et"});
db.iliadfr.insert({ pg : 7856, line : "Idoméneus regagna ses tentes et revêtit ses belles armes. Il"});
db.iliadfr.insert({ pg : 7857, line : "saisit deux lances et accourut, semblable au feu fulgurant que le"});
db.iliadfr.insert({ pg : 7858, line : "Kroniôn, de sa main, précipite des cimes de l'Olympos enflammé,"});
db.iliadfr.insert({ pg : 7859, line : "comme un signe rayonnant aux hommes vivants. Ainsi resplendissait"});
db.iliadfr.insert({ pg : 7860, line : "l'airain sur la poitrine du roi qui accourait."});
db.iliadfr.insert({ pg : 7862, line : "Et Mèrionès, son brave compagnon, le rencontra non loin de la"});
db.iliadfr.insert({ pg : 7863, line : "tente. Et il venait chercher une lance d'airain. Et Idoméneus lui"});
db.iliadfr.insert({ pg : 7864, line : "parla ainsi:"});
db.iliadfr.insert({ pg : 7866, line : "-- Mèrionès aux pieds rapides, fils de Molos, le plus cher de mes"});
db.iliadfr.insert({ pg : 7867, line : "compagnons, pourquoi quittes-tu la guerre et le combat? Es-tu"});
db.iliadfr.insert({ pg : 7868, line : "blessé, et la pointe du trait te tourmente-t-elle? Viens-tu"});
db.iliadfr.insert({ pg : 7869, line : "m'annoncer quelque chose? Certes, pour moi, je n'ai pas le dessein"});
db.iliadfr.insert({ pg : 7870, line : "de rester dans mes tentes, mais je désire le combat."});
db.iliadfr.insert({ pg : 7872, line : "Et le sage Mèrionès lui répondit:"});
db.iliadfr.insert({ pg : 7874, line : "-- Idoméneus, prince des Krètois cuirassés, je viens afin de"});
db.iliadfr.insert({ pg : 7875, line : "prendre une lance, si, dans tes tentes, il en reste une; car j'ai"});
db.iliadfr.insert({ pg : 7876, line : "rompu la mienne sur le bouclier de l'orgueilleux Dèiphobos."});
db.iliadfr.insert({ pg : 7878, line : "Et Idoméneus, prince des Krètois, lui répondit:"});
db.iliadfr.insert({ pg : 7880, line : "-- Si tu veux des lances, tu en trouveras une, tu en trouveras"});
db.iliadfr.insert({ pg : 7881, line : "vingt, appuyées étincelantes contre les parois de ma tente. Ce"});
db.iliadfr.insert({ pg : 7882, line : "sont des lances Troiennes enlevées à ceux que j'ai tués, car je"});
db.iliadfr.insert({ pg : 7883, line : "combats de près les guerriers ennemis; et c'est pourquoi j'ai des"});
db.iliadfr.insert({ pg : 7884, line : "lances, des boucliers bombés, des casques et des cuirasses"});
db.iliadfr.insert({ pg : 7885, line : "éclatantes."});
db.iliadfr.insert({ pg : 7887, line : "Et le sage Mèrionès lui répondit:"});
db.iliadfr.insert({ pg : 7889, line : "-- Dans ma tente et dans ma nef noire abondent aussi les"});
db.iliadfr.insert({ pg : 7890, line : "dépouilles Troiennes; mais elles sont trop éloignées. Je ne pense"});
db.iliadfr.insert({ pg : 7891, line : "pas aussi avoir jamais oublié mon courage. Je combats au premier"});
db.iliadfr.insert({ pg : 7892, line : "rang, parmi les guerriers illustres, à l'heure où la mêlée"});
db.iliadfr.insert({ pg : 7893, line : "retentit. Quelques-uns des Akhaiens cuirassés peuvent ne m'avoir"});
db.iliadfr.insert({ pg : 7894, line : "point vu, mais toi, tu me connais."});
db.iliadfr.insert({ pg : 7896, line : "Et Idoméneus, prince des Krètois, lui répondit:"});
db.iliadfr.insert({ pg : 7898, line : "-- Je sais quel est ton courage. Pourquoi me parler ainsi? Si nous"});
db.iliadfr.insert({ pg : 7899, line : "étions choisis parmi les plus braves pour une embuscade, car c'est"});
db.iliadfr.insert({ pg : 7900, line : "là que le courage des guerriers éclate, là on distingue le brave"});
db.iliadfr.insert({ pg : 7901, line : "du lâche, car celui-ci change à tout instant de couleur, et son"});
db.iliadfr.insert({ pg : 7902, line : "coeur n'est point assez ferme pour attendre tranquillement en"});
db.iliadfr.insert({ pg : 7903, line : "place; et il remue sans cesse, tantôt sur un pied, tantôt sur"});
db.iliadfr.insert({ pg : 7904, line : "l'autre; et son coeur tremble dans sa poitrine par crainte de la"});
db.iliadfr.insert({ pg : 7905, line : "mort, et ses dents claquent, tandis que le brave ne change point"});
db.iliadfr.insert({ pg : 7906, line : "de couleur, et il ne redoute rien au premier rang des guerriers,"});
db.iliadfr.insert({ pg : 7907, line : "dans l'embuscade, et il souhaite l'ardent combat; certes, donc,"});
db.iliadfr.insert({ pg : 7908, line : "aucun de nous ne blâmerait en cet instant ni ton courage ni ton"});
db.iliadfr.insert({ pg : 7909, line : "bras; et si tu étais blessé alors, ce ne serait point à l'épaule"});
db.iliadfr.insert({ pg : 7910, line : "ou dans le dos que tu serais frappé d'un trait, mais en pleine"});
db.iliadfr.insert({ pg : 7911, line : "poitrine ou dans le ventre, tandis que tu te précipiterais dans la"});
db.iliadfr.insert({ pg : 7912, line : "mêlée des combattants. Va! ne parlons plus, inactifs, comme des"});
db.iliadfr.insert({ pg : 7913, line : "enfants, de peur que ceci nous soit reproché injurieusement. Va"});
db.iliadfr.insert({ pg : 7914, line : "dans ma tente, et prends une lance solide."});
db.iliadfr.insert({ pg : 7916, line : "Il parla ainsi, et Mèrionès, semblable au rapide Arès, saisit"});
db.iliadfr.insert({ pg : 7917, line : "promptement dans la tente une lance d'airain, et il marcha avec"});
db.iliadfr.insert({ pg : 7918, line : "Idoméneus, plein du désir de combattre. Ainsi marche le désastreux"});
db.iliadfr.insert({ pg : 7919, line : "Arès avec la Terreur, sa fille bien-aimée, forte et indomptable,"});
db.iliadfr.insert({ pg : 7920, line : "qui épouvante le plus brave. Ils descendent de la Thrèkè vers les"});
db.iliadfr.insert({ pg : 7921, line : "Épirotes ou les magnanimes Phlègyens, et ils n'exaucent point les"});
db.iliadfr.insert({ pg : 7922, line : "deux peuples à la fois, mais ils accordent la gloire à l'un ou à"});
db.iliadfr.insert({ pg : 7923, line : "l'autre. Ainsi Mèrionès et Idoméneus, princes des hommes,"});
db.iliadfr.insert({ pg : 7924, line : "marchaient, armés de l'airain splendide."});
db.iliadfr.insert({ pg : 7926, line : "Et Mèrionès, le premier, parla ainsi:"});
db.iliadfr.insert({ pg : 7928, line : "-- Deukalide, de quel côté veux-tu entrer dans la mêlée? À droite,"});
db.iliadfr.insert({ pg : 7929, line : "au centre, ou à gauche? C'est là que les Akhaiens chevelus"});
db.iliadfr.insert({ pg : 7930, line : "faiblissent."});
db.iliadfr.insert({ pg : 7932, line : "Et Idoméneus, prince des Krètois, lui répondit:"});
db.iliadfr.insert({ pg : 7934, line : "-- D'autres sont au centre qui défendent les nefs, les deux Aias"});
db.iliadfr.insert({ pg : 7935, line : "et Teukros, le plus habile archer d'entre les Akhaiens, et brave"});
db.iliadfr.insert({ pg : 7936, line : "aussi de pied ferme. Ils suffiront à repousser le Priamide Hektôr."});
db.iliadfr.insert({ pg : 7937, line : "Quelque brave qu'il soit, et quelle que soit son ardeur à"});
db.iliadfr.insert({ pg : 7938, line : "combattre, il ne réussira pas à dompter leur courage et leurs"});
db.iliadfr.insert({ pg : 7939, line : "mains invincibles et à brûler les nefs, à moins que le Kroniôn"});
db.iliadfr.insert({ pg : 7940, line : "lui-même ne jette l'ardente foudre sur les nefs rapides. Jamais le"});
db.iliadfr.insert({ pg : 7941, line : "grand Télamônien Aias ne le cédera à aucun homme né mortel et"});
db.iliadfr.insert({ pg : 7942, line : "nourri des dons de Dèmètèr, vulnérable par l'airain ou par de"});
db.iliadfr.insert({ pg : 7943, line : "lourds rochers. Il ne reculerait même pas devant l'impétueux"});
db.iliadfr.insert({ pg : 7944, line : "Akhilleus, s'il ne peut cependant lutter contre lui en agilité."});
db.iliadfr.insert({ pg : 7945, line : "Allons vers la gauche de l'armée, et voyons promptement si nous"});
db.iliadfr.insert({ pg : 7946, line : "remporterons une grande gloire, ou si nous la donnerons à"});
db.iliadfr.insert({ pg : 7947, line : "l'ennemi."});
db.iliadfr.insert({ pg : 7949, line : "Il parla ainsi, et Mèrionès, semblable au rapide Arès, s'élança du"});
db.iliadfr.insert({ pg : 7950, line : "côté où Idoméneus ordonnait d'aller. Et dès que les Troiens eurent"});
db.iliadfr.insert({ pg : 7951, line : "vu Idoméneus, semblable à la flamme par son courage, avec son"});
db.iliadfr.insert({ pg : 7952, line : "compagnon brillant sous ses armes, s'exhortant les uns les autres,"});
db.iliadfr.insert({ pg : 7953, line : "ils se jetèrent sur lui. Et le combat fut égal entre eux tous"});
db.iliadfr.insert({ pg : 7954, line : "devant les poupes des nefs."});
db.iliadfr.insert({ pg : 7956, line : "De même que les vents tempétueux, en un jour de sécheresse,"});
db.iliadfr.insert({ pg : 7957, line : "soulèvent par les chemins de grands tourbillons de poussière, de"});
db.iliadfr.insert({ pg : 7958, line : "même tous se ruèrent dans une mêlée furieuse afin de s'entretuer"});
db.iliadfr.insert({ pg : 7959, line : "de l'airain aigu. Et la multitude des guerriers se hérissa de"});
db.iliadfr.insert({ pg : 7960, line : "longues lances qui perçaient la chair des combattants. Et la"});
db.iliadfr.insert({ pg : 7961, line : "splendeur de l'airain, des casques étincelants, des cuirasses"});
db.iliadfr.insert({ pg : 7962, line : "polies et des boucliers, éblouissait les yeux. Et il eût été"});
db.iliadfr.insert({ pg : 7963, line : "impitoyable celui qui, loin de s'attrister de ce combat, s'en fût"});
db.iliadfr.insert({ pg : 7964, line : "réjoui."});
db.iliadfr.insert({ pg : 7966, line : "Et les deux fils puissants de Kronos, dans leur volonté contraire,"});
db.iliadfr.insert({ pg : 7967, line : "accablaient ainsi les héros de lourdes calamités. Zeus voulait"});
db.iliadfr.insert({ pg : 7968, line : "donner la victoire à Hektôr et aux Troiens, afin d'honorer"});
db.iliadfr.insert({ pg : 7969, line : "Akhilleus aux pieds rapides; et il ne voulait pas détruire les"});
db.iliadfr.insert({ pg : 7970, line : "tribus Akhaiennes devant Ilios, mais honorer Thétis et son fils"});
db.iliadfr.insert({ pg : 7971, line : "magnanime. Et Poseidaôn, sorti en secret de la blanche mer,"});
db.iliadfr.insert({ pg : 7972, line : "encourageait les Akhaiens, et il gémissait de les voir domptés par"});
db.iliadfr.insert({ pg : 7973, line : "les Troiens, et il s'irritait contre Zeus. Et tous deux avaient la"});
db.iliadfr.insert({ pg : 7974, line : "même origine et le même père, mais Zeus était le plus âgé et"});
db.iliadfr.insert({ pg : 7975, line : "savait plus de choses. Et c'est pourquoi Poseidaôn ne secourait"});
db.iliadfr.insert({ pg : 7976, line : "point ouvertement les Argiens, mais, sous la forme d'un guerrier,"});
db.iliadfr.insert({ pg : 7977, line : "parcourait l'armée en les encourageant."});
db.iliadfr.insert({ pg : 7979, line : "Et tous deux avaient étendu également sur l'un et l'autre parti"});
db.iliadfr.insert({ pg : 7980, line : "les chaînes du combat violent et de la guerre désastreuse, chaînes"});
db.iliadfr.insert({ pg : 7981, line : "infrangibles, indissolubles, et qui rompaient les genoux d'un"});
db.iliadfr.insert({ pg : 7982, line : "grand nombre de héros."});
db.iliadfr.insert({ pg : 7984, line : "Et Idoméneus, bien qu'à demi blanc de vieillesse, exhortant les"});
db.iliadfr.insert({ pg : 7985, line : "Danaens, bondit sur les Troiens qu'il fit reculer. Et il tua"});
db.iliadfr.insert({ pg : 7986, line : "Othryoneus de Kabèsos qui, venu récemment, attiré par le bruit de"});
db.iliadfr.insert({ pg : 7987, line : "la guerre, demandait Kassandrè, la plus belle des filles de"});
db.iliadfr.insert({ pg : 7988, line : "Priamos. Et il n'offrait point de présents, mais il avait promis"});
db.iliadfr.insert({ pg : 7989, line : "de repousser les fils des Akhaiens loin de Troiè. Et le vieillard"});
db.iliadfr.insert({ pg : 7990, line : "Priamos avait juré de lui donner sa fille, et, sur cette promesse,"});
db.iliadfr.insert({ pg : 7991, line : "il combattait bravement. Et, comme il s'avançait avec fierté,"});
db.iliadfr.insert({ pg : 7992, line : "Idoméneus le frappa de sa lance étincelante, et la cuirasse"});
db.iliadfr.insert({ pg : 7993, line : "d'airain ne résista point au coup qui pénétra au milieu du ventre."});
db.iliadfr.insert({ pg : 7994, line : "Et il tomba avec bruit, et Idoméneus s'écria en l'insultant:"});
db.iliadfr.insert({ pg : 7996, line : "-- Othryoneus! je te proclame le premier des hommes si tu tiens la"});
db.iliadfr.insert({ pg : 7997, line : "parole donnée au Dardanide Priamos. Il t'a promis sa fille, et"});
db.iliadfr.insert({ pg : 7998, line : "c'est nous qui accomplirons sa promesse. Et nous te donnerons la"});
db.iliadfr.insert({ pg : 7999, line : "plus belle des filles d'Agamemnôn, venue d'Argos pour t'épouser,"});
db.iliadfr.insert({ pg : 8000, line : "si tu veux avec nous détruire la ville bien peuplée d'Ilios. Mais"});
db.iliadfr.insert({ pg : 8001, line : "suis-nous dans les nefs qui traversent la mer, afin de convenir de"});
db.iliadfr.insert({ pg : 8002, line : "tes noces, car nous aussi, nous sommes d'excellents beaux-pères!"});
db.iliadfr.insert({ pg : 8004, line : "Et le héros Idoméneus parla ainsi, et il le traînait par un pied à"});
db.iliadfr.insert({ pg : 8005, line : "travers la mêlée. Et, pour venger Othryoneus, Asios accourut, à"});
db.iliadfr.insert({ pg : 8006, line : "pied devant son char, et ses chevaux, retenus par leur conducteur,"});
db.iliadfr.insert({ pg : 8007, line : "soufflaient sur ses épaules. Et il désirait percer Idoméneus, mais"});
db.iliadfr.insert({ pg : 8008, line : "celui-ci l'atteignit le premier, de sa lance, dans la gorge, sous"});
db.iliadfr.insert({ pg : 8009, line : "le menton. Et la lance passa au travers du cou, et Asios tomba"});
db.iliadfr.insert({ pg : 8010, line : "comme un chêne ou comme un peuplier, ou comme un pin élevé que des"});
db.iliadfr.insert({ pg : 8011, line : "constructeurs de nefs, sur les montagnes, coupent de leurs haches"});
db.iliadfr.insert({ pg : 8012, line : "récemment aiguisées. Ainsi le guerrier gisait étendu devant ses"});
db.iliadfr.insert({ pg : 8013, line : "chevaux et son char, grinçant des dents et saisissant la poussière"});
db.iliadfr.insert({ pg : 8014, line : "sanglante. Et le conducteur, éperdu, ne songeait pas à éviter"});
db.iliadfr.insert({ pg : 8015, line : "l'ennemi en faisant retourner les chevaux. Et le brave Antilokhos"});
db.iliadfr.insert({ pg : 8016, line : "le frappa de sa lance, et la cuirasse d'airain ne résista pas au"});
db.iliadfr.insert({ pg : 8017, line : "coup qui pénétra au milieu du ventre. Et l'homme tomba, expirant,"});
db.iliadfr.insert({ pg : 8018, line : "du char habilement fait, et le fils du magnanime Nestôr,"});
db.iliadfr.insert({ pg : 8019, line : "Antilokhos, entraîna les chevaux du côté des Akhaiens aux belles"});
db.iliadfr.insert({ pg : 8020, line : "knèmides."});
db.iliadfr.insert({ pg : 8022, line : "Et Dèiphobos, triste de la mort d'Asios, s'approchant d'Idoméneus,"});
db.iliadfr.insert({ pg : 8023, line : "lui lança sa pique étincelante. Mais Idoméneus, l'ayant aperçue,"});
db.iliadfr.insert({ pg : 8024, line : "évita la pique d'airain en se couvrant de son bouclier d'une"});
db.iliadfr.insert({ pg : 8025, line : "rondeur égale fait de peaux de boeuf et d'airain brillant, et"});
db.iliadfr.insert({ pg : 8026, line : "qu'il portait à l'aide de deux manches. Et il en était entièrement"});
db.iliadfr.insert({ pg : 8027, line : "couvert, et l'airain vola par-dessus, effleurant le bouclier qui"});
db.iliadfr.insert({ pg : 8028, line : "résonna. Mais la lance ne s'échappa point en vain d'une main"});
db.iliadfr.insert({ pg : 8029, line : "vigoureuse, et, frappant Hypsènôr Hippaside, prince des peuples,"});
db.iliadfr.insert({ pg : 8030, line : "elle s'enfonça dans son foie et rompit ses genoux. Et Dèiphobos"});
db.iliadfr.insert({ pg : 8031, line : "cria en se glorifiant:"});
db.iliadfr.insert({ pg : 8033, line : "-- Asios ne mourra pas non vengé, et, en allant aux portes solides"});
db.iliadfr.insert({ pg : 8034, line : "d'Aidès, il se réjouira dans son brave coeur, car je lui ai donné"});
db.iliadfr.insert({ pg : 8035, line : "un compagnon."});
db.iliadfr.insert({ pg : 8037, line : "Il parla ainsi, et ses paroles orgueilleuses emplirent les Argiens"});
db.iliadfr.insert({ pg : 8038, line : "de douleur, et surtout le brave Antilokhos. Mais, bien"});
db.iliadfr.insert({ pg : 8039, line : "qu'attristé, il n'oublia point son compagnon, et, courant tout"});
db.iliadfr.insert({ pg : 8040, line : "autour, il le couvrit de son bouclier. Et deux autres compagnons"});
db.iliadfr.insert({ pg : 8041, line : "bien-aimés de Hypsènôr, Mékisteus et le divin Alastôr,"});
db.iliadfr.insert({ pg : 8042, line : "l'emportèrent en gémissant dans les nefs creuses."});
db.iliadfr.insert({ pg : 8044, line : "Et Idoméneus ne laissait point reposer son courage, et il désirait"});
db.iliadfr.insert({ pg : 8045, line : "toujours envelopper quelque Troien de la nuit noire, ou tomber"});
db.iliadfr.insert({ pg : 8046, line : "lui-même en sauvant les Akhaiens de leur ruine. Alors périt le"});
db.iliadfr.insert({ pg : 8047, line : "fils bien-aimé d'Aisyétas nourri par Zeus, le héros Alkathoos,"});
db.iliadfr.insert({ pg : 8048, line : "gendre d'Ankhisès. Et il avait épousé Hippodaméia, l'aînée des"});
db.iliadfr.insert({ pg : 8049, line : "filles d'Ankhisès, très chère, dans leur demeure, à son père et à"});
db.iliadfr.insert({ pg : 8050, line : "sa mère vénérable. Et elle l'emportait sur toutes ses compagnes"});
db.iliadfr.insert({ pg : 8051, line : "par la beauté, l'habileté aux travaux et la prudence et c'est"});
db.iliadfr.insert({ pg : 8052, line : "pourquoi un grand chef l'avait épousée dans la large Troiè. Et"});
db.iliadfr.insert({ pg : 8053, line : "Poseidaôn dompta Alkathoos par les mains d'Idoméneus. Et il"});
db.iliadfr.insert({ pg : 8054, line : "éteignit ses yeux étincelants, et il enchaîna ses beaux membres,"});
db.iliadfr.insert({ pg : 8055, line : "de façon à ce qu'il ne pût ni fuir ni se détourner, mais que, tout"});
db.iliadfr.insert({ pg : 8056, line : "droit comme une colonne ou un arbre élevé, il reçût au milieu de"});
db.iliadfr.insert({ pg : 8057, line : "la poitrine la lance du héros Idoméneus. Et sa cuirasse d'airain,"});
db.iliadfr.insert({ pg : 8058, line : "qui éloignait de lui la mort, résonna, rompue par la lance. Et sa"});
db.iliadfr.insert({ pg : 8059, line : "chute retentit, et la pointe d'airain, dans son coeur qui"});
db.iliadfr.insert({ pg : 8060, line : "palpitait, remua jusqu'à ce que le rude Arès eût épuisé la force"});
db.iliadfr.insert({ pg : 8061, line : "de la lance. Et Idoméneus cria d'une voix terrible en se"});
db.iliadfr.insert({ pg : 8062, line : "glorifiant:"});
db.iliadfr.insert({ pg : 8064, line : "-- Dèiphobos! je pense que les choses sont au moins égales. En"});
db.iliadfr.insert({ pg : 8065, line : "voici trois de tués pour un, et tu te vantais en vain. Malheureux!"});
db.iliadfr.insert({ pg : 8066, line : "ose m'attendre, et tu verras ce que vaut la race de Zeus. Zeus"});
db.iliadfr.insert({ pg : 8067, line : "engendra Minôs, gardien de la Krètè, et Minôs engendra un fils,"});
db.iliadfr.insert({ pg : 8068, line : "l'irréprochable Deukaliôn, et Deukaliôn m'engendra pour être le"});
db.iliadfr.insert({ pg : 8069, line : "chef de nombreux guerriers dans la grande Krètè, et mes nefs m'ont"});
db.iliadfr.insert({ pg : 8070, line : "amené ici pour ton malheur, celui de ton père et celui des"});
db.iliadfr.insert({ pg : 8071, line : "Troiens."});
db.iliadfr.insert({ pg : 8073, line : "Il parla ainsi, et Dèiphobos délibéra s'il irait chercher pour"});
db.iliadfr.insert({ pg : 8074, line : "soutien quelque autre des Troiens magnanimes, ou s'il combattrait"});
db.iliadfr.insert({ pg : 8075, line : "seul. Et il vit qu'il valait mieux aller vers Ainéias. Et il le"});
db.iliadfr.insert({ pg : 8076, line : "trouva debout aux derniers rangs, car il était irrité contre le"});
db.iliadfr.insert({ pg : 8077, line : "divin Priamos qui ne l'honorait pas, bien qu'il fût brave entre"});
db.iliadfr.insert({ pg : 8078, line : "tous les guerriers. Et Dèiphobos, s'approchant, lui dit en paroles"});
db.iliadfr.insert({ pg : 8079, line : "ailées:"});
db.iliadfr.insert({ pg : 8081, line : "-- Ainéias, prince des Troiens, si la gloire te touche, viens"});
db.iliadfr.insert({ pg : 8082, line : "protéger ton beau-frère. Suis-moi, allons vers Alkathoos qui,"});
db.iliadfr.insert({ pg : 8083, line : "époux de ta soeur, a autrefois nourri ton enfance dans ses"});
db.iliadfr.insert({ pg : 8084, line : "demeures. Idoméneus, illustre par sa lance, l'a tué."});
db.iliadfr.insert({ pg : 8086, line : "Il parla ainsi, et le coeur d'Ainéias fut ébranlé dans sa"});
db.iliadfr.insert({ pg : 8087, line : "poitrine, et il marcha pour combattre Idoméneus. Mais celui-ci ne"});
db.iliadfr.insert({ pg : 8088, line : "fut point saisi par la peur comme un enfant, et il attendit, de"});
db.iliadfr.insert({ pg : 8089, line : "même qu'un sanglier des montagnes, certain de sa force, attend,"});
db.iliadfr.insert({ pg : 8090, line : "dans un lieu désert, le tumulte des chasseurs qui s'approchent."});
db.iliadfr.insert({ pg : 8091, line : "Son dos se hérisse, ses yeux lancent du feu, et il aiguise ses"});
db.iliadfr.insert({ pg : 8092, line : "défenses pour repousser aussitôt les chiens et les chasseurs. De"});
db.iliadfr.insert({ pg : 8093, line : "même Idoméneus, illustre par sa lance, ne recula point devant"});
db.iliadfr.insert({ pg : 8094, line : "Ainéias qui accourait au combat. Et il appela ses compagnons"});
db.iliadfr.insert({ pg : 8095, line : "Askalaphos, Apharèos, Dèipyros, Mèrionès et Antilokhos. Et il leur"});
db.iliadfr.insert({ pg : 8096, line : "dit en paroles ailées:"});
db.iliadfr.insert({ pg : 8098, line : "-- Accourez, amis, car je suis seul, et je crains Ainéias aux"});
db.iliadfr.insert({ pg : 8099, line : "pieds rapides qui vient sur moi. Il est très brave, et c'est un"});
db.iliadfr.insert({ pg : 8100, line : "tueur d'hommes, et il est dans la fleur de la jeunesse, à l'âge où"});
db.iliadfr.insert({ pg : 8101, line : "la force est la plus grande. Si nous étions du même âge, avec mon"});
db.iliadfr.insert({ pg : 8102, line : "courage, une grande gloire nous serait donnée, à lui ou à moi."});
db.iliadfr.insert({ pg : 8104, line : "Il parla ainsi, et tous, avec une même ardeur, ils l'entourèrent,"});
db.iliadfr.insert({ pg : 8105, line : "le bouclier sur l'épaule. Et Ainéias, de son côté, appela aussi"});
db.iliadfr.insert({ pg : 8106, line : "ses compagnons, Dèiphobos, Pâris et le divin Agènôr, comme lui"});
db.iliadfr.insert({ pg : 8107, line : "princes des Troiens. Et leurs troupes les suivaient, telles que"});
db.iliadfr.insert({ pg : 8108, line : "des troupeaux de brebis qui suivent le bélier hors du pâturage,"});
db.iliadfr.insert({ pg : 8109, line : "pour aller boire. Et le berger se réjouit dans son âme. De même le"});
db.iliadfr.insert({ pg : 8110, line : "coeur d'Ainéias fut joyeux dans sa poitrine, en voyant la foule"});
db.iliadfr.insert({ pg : 8111, line : "des guerriers qui le suivaient."});
db.iliadfr.insert({ pg : 8113, line : "Et, autour d'Alkathoos, tous dardèrent leurs longues lances, et,"});
db.iliadfr.insert({ pg : 8114, line : "sur les poitrines, l'horrible airain retentissait, tandis qu'ils"});
db.iliadfr.insert({ pg : 8115, line : "se frappaient à l'envi. Et deux braves guerriers, Ainéias et"});
db.iliadfr.insert({ pg : 8116, line : "Idoméneus semblable à Arès, désiraient surtout se percer de"});
db.iliadfr.insert({ pg : 8117, line : "l'airain cruel. Et Ainéias, le premier, lança sa pique contre"});
db.iliadfr.insert({ pg : 8118, line : "Idoméneus; mais celui-ci, l'ayant aperçue, évita la pique d'airain"});
db.iliadfr.insert({ pg : 8119, line : "qui s'enfonça en vibrant dans la terre, inutile, bien que partie"});
db.iliadfr.insert({ pg : 8120, line : "d'une main vigoureuse."});
db.iliadfr.insert({ pg : 8122, line : "Et Idoméneus frappa Oinomaos au milieu du ventre, et la cuirasse"});
db.iliadfr.insert({ pg : 8123, line : "fut rompue, et l'airain s'enfonça dans les intestins, et le"});
db.iliadfr.insert({ pg : 8124, line : "guerrier tomba en saisissant la terre avec les mains. Et Idoméneus"});
db.iliadfr.insert({ pg : 8125, line : "arracha la lance du cadavre, mais il ne put dépouiller les épaules"});
db.iliadfr.insert({ pg : 8126, line : "de leurs belles armes, car il était accablé par les traits. Et il"});
db.iliadfr.insert({ pg : 8127, line : "n'avait plus les pieds vigoureux avec lesquels il s'élançait"});
db.iliadfr.insert({ pg : 8128, line : "autrefois pour reprendre sa pique ou pour éviter celle de"});
db.iliadfr.insert({ pg : 8129, line : "l'ennemi. Il éloignait encore de pied ferme son jour fatal, mais"});
db.iliadfr.insert({ pg : 8130, line : "il ne pouvait plus fuir aisément."});
db.iliadfr.insert({ pg : 8132, line : "Et Dèiphobos, comme il se retirait lentement, toujours irrité"});
db.iliadfr.insert({ pg : 8133, line : "contre lui, voulut le frapper de sa lance étincelante; mais il le"});
db.iliadfr.insert({ pg : 8134, line : "manqua, et la lance perça Askalaphos, fils de Arès. Et la forte"});
db.iliadfr.insert({ pg : 8135, line : "lance s'enfonça dans l'épaule, et le guerrier tomba, saisissant la"});
db.iliadfr.insert({ pg : 8136, line : "terre avec ses mains."});
db.iliadfr.insert({ pg : 8138, line : "Et le terrible Arès plein de clameurs ignorait que son fils fût"});
db.iliadfr.insert({ pg : 8139, line : "tombé mort dans la mêlée violente. Et il était assis au sommet de"});
db.iliadfr.insert({ pg : 8140, line : "l'Olympos, sous les nuées d'or, retenu par la volonté de Zeus,"});
db.iliadfr.insert({ pg : 8141, line : "ainsi que les autres dieux immortels, loin du combat."});
db.iliadfr.insert({ pg : 8143, line : "Et tous se ruèrent autour d'Askalaphos. Et comme Dèiphobos"});
db.iliadfr.insert({ pg : 8144, line : "enlevait son casque brillant, Mèrionès, semblable au rapide Arès,"});
db.iliadfr.insert({ pg : 8145, line : "bondit, et, de sa lance, perça le bras du Troien qui laissa"});
db.iliadfr.insert({ pg : 8146, line : "échapper le casque sonore. Et Mèrionès bondit de nouveau comme un"});
db.iliadfr.insert({ pg : 8147, line : "vautour, et arracha du bras blessé sa forte lance, et rentra dans"});
db.iliadfr.insert({ pg : 8148, line : "les rangs de ses compagnons. Et Politès, frère de Dèiphobos,"});
db.iliadfr.insert({ pg : 8149, line : "entourant celui-ci de ses bras, l'entraîna hors de la mêlée,"});
db.iliadfr.insert({ pg : 8150, line : "derrière les rangs, où se tenaient ses chevaux rapides, et le char"});
db.iliadfr.insert({ pg : 8151, line : "éclatant, et leur conducteur. Et ils le portèrent dans la ville,"});
db.iliadfr.insert({ pg : 8152, line : "poussant des gémissements. Et le sang coulait de sa blessure"});
db.iliadfr.insert({ pg : 8153, line : "fraîche. Et les autres combattaient toujours, et une immense"});
db.iliadfr.insert({ pg : 8154, line : "clameur s'élevait."});
db.iliadfr.insert({ pg : 8156, line : "Et Ainéias, se ruant sur Apharèos Kalètoride, le frappa à la gorge"});
db.iliadfr.insert({ pg : 8157, line : "de sa lance aiguë; et la tête s'inclina, et le bouclier tomba, et"});
db.iliadfr.insert({ pg : 8158, line : "le casque aussi, et la mort fatale l'enveloppa."});
db.iliadfr.insert({ pg : 8160, line : "Et Antilokhos, apercevant le dos de Thoôn, le frappa"});
db.iliadfr.insert({ pg : 8161, line : "impétueusement, et il trancha la veine qui, courant le long du"});
db.iliadfr.insert({ pg : 8162, line : "dos, arrive au cou. Le Troien tomba à la renverse sur la"});
db.iliadfr.insert({ pg : 8163, line : "poussière, étendant les deux mains vers ses compagnons bien-aimés."});
db.iliadfr.insert({ pg : 8164, line : "Et Antilokhos accourut, et, regardant autour de lui, enleva ses"});
db.iliadfr.insert({ pg : 8165, line : "belles armes de ses épaules. Et les Troiens, l'entourant aussitôt,"});
db.iliadfr.insert({ pg : 8166, line : "accablaient de traits son beau et large bouclier; mais ils ne"});
db.iliadfr.insert({ pg : 8167, line : "purent déchirer avec l'airain cruel le corps délicat d'Antilokhos,"});
db.iliadfr.insert({ pg : 8168, line : "car Poseidaôn qui ébranle la terre protégeait le Nestôride contre"});
db.iliadfr.insert({ pg : 8169, line : "la multitude des traits. Et celui-ci ne s'éloignait point de"});
db.iliadfr.insert({ pg : 8170, line : "l'ennemi, mais il tournait sur lui-même, agitant sans cesse sa"});
db.iliadfr.insert({ pg : 8171, line : "lance et cherchant qui il pourrait frapper de loin, ou de près."});
db.iliadfr.insert({ pg : 8173, line : "Et Adamas Asiade, l'ayant aperçu dans la mêlée, le frappa de"});
db.iliadfr.insert({ pg : 8174, line : "l'airain aigu au milieu du bouclier; mais Poseidaôn aux cheveux"});
db.iliadfr.insert({ pg : 8175, line : "bleus refusa au Troien la vie d'Antilokhos, et la moitié du trait"});
db.iliadfr.insert({ pg : 8176, line : "resta dans le bouclier comme un pieu à demi brûlé, et l'autre"});
db.iliadfr.insert({ pg : 8177, line : "tomba sur la terre. Et comme Adamas fuyait la mort dans les rangs"});
db.iliadfr.insert({ pg : 8178, line : "de ses compagnons, Mèrionès, le poursuivant, le perça entre les"});
db.iliadfr.insert({ pg : 8179, line : "parties mâles et le nombril, là où une plaie est mortelle pour les"});
db.iliadfr.insert({ pg : 8180, line : "hommes lamentables. C'est là qu'il enfonça sa lance, et Adamas"});
db.iliadfr.insert({ pg : 8181, line : "tomba palpitant sous le coup, comme un taureau, dompté par la"});
db.iliadfr.insert({ pg : 8182, line : "force des liens, que des bouviers ont mené sur les montagnes."});
db.iliadfr.insert({ pg : 8183, line : "Ainsi Adamas blessé palpita, mais peu de temps, car le héros"});
db.iliadfr.insert({ pg : 8184, line : "Mèrionès arracha la lance de la plaie, et les ténèbres se"});
db.iliadfr.insert({ pg : 8185, line : "répandirent sur les yeux du Troien."});
db.iliadfr.insert({ pg : 8187, line : "Et Hélénos, de sa grande épée de Thrèkè, frappa Dèipyros à la"});
db.iliadfr.insert({ pg : 8188, line : "tempe, et le casque roula sur la terre, et un des Akhaiens le"});
db.iliadfr.insert({ pg : 8189, line : "ramassa sous les pieds des combattants. Et la nuit couvrit les"});
db.iliadfr.insert({ pg : 8190, line : "yeux de Dèipyros."});
db.iliadfr.insert({ pg : 8192, line : "Et la douleur saisit le brave Atréide Ménélaos qui s'avança contre"});
db.iliadfr.insert({ pg : 8193, line : "le prince Hélénos, en lançant sa longue pique. Et le Troien"});
db.iliadfr.insert({ pg : 8194, line : "bandait son arc, et tous deux dardèrent à la fois, l'un sa lance"});
db.iliadfr.insert({ pg : 8195, line : "aiguë, l'autre la flèche jaillissant du nerf. Et le Priamide"});
db.iliadfr.insert({ pg : 8196, line : "frappa de sa flèche la cuirasse bombée, et le trait acerbe y"});
db.iliadfr.insert({ pg : 8197, line : "rebondit. De même que, dans l'aire spacieuse, les fèves noires ou"});
db.iliadfr.insert({ pg : 8198, line : "les pois, au souffle du vent et sous l'effort du vanneur,"});
db.iliadfr.insert({ pg : 8199, line : "rejaillissent du large van, de même la flèche acerbe rebondit loin"});
db.iliadfr.insert({ pg : 8200, line : "de la cuirasse de l'illustre Ménélaos."});
db.iliadfr.insert({ pg : 8202, line : "Et le brave Atréide frappa la main qui tenait l'arc poli, et la"});
db.iliadfr.insert({ pg : 8203, line : "lance aiguë attacha la main à l'arc, et Hélénos rentra dans la"});
db.iliadfr.insert({ pg : 8204, line : "foule de ses compagnons, évitant la mort et traînant le frêne de"});
db.iliadfr.insert({ pg : 8205, line : "la lance suspendu à sa main. Et le magnanime Agènôr arracha le"});
db.iliadfr.insert({ pg : 8206, line : "trait de la blessure qu'il entoura d'une fronde en laine qu'un"});
db.iliadfr.insert({ pg : 8207, line : "serviteur tenait à son côté."});
db.iliadfr.insert({ pg : 8209, line : "Et Peisandros marcha contre l'illustre Ménélaos, et la moire"});
db.iliadfr.insert({ pg : 8210, line : "fatale le conduisait au seuil de la mort, pour qu'il fût dompté"});
db.iliadfr.insert({ pg : 8211, line : "par toi, Ménélaos, dans le rude combat. Quand ils se furent"});
db.iliadfr.insert({ pg : 8212, line : "rencontrés, l'Atréide le manqua, et Peisandros frappa le bouclier"});
db.iliadfr.insert({ pg : 8213, line : "de l'illustre Ménélaos; mais il ne put traverser l'airain, et le"});
db.iliadfr.insert({ pg : 8214, line : "large bouclier repoussa la pique dont la pointe se rompit. Et"});
db.iliadfr.insert({ pg : 8215, line : "Peisandros se réjouissait dans son esprit, espérant la victoire,"});
db.iliadfr.insert({ pg : 8216, line : "et l'illustre Atréide, ayant tiré l'épée aux clous d'argent, sauta"});
db.iliadfr.insert({ pg : 8217, line : "sur lui; mais le Troien saisit, sous le bouclier, la belle hache à"});
db.iliadfr.insert({ pg : 8218, line : "deux tranchants, au manche d'olivier, faite d'un airain excellent,"});
db.iliadfr.insert({ pg : 8219, line : "et ils combattirent."});
db.iliadfr.insert({ pg : 8221, line : "Peisandros frappa le cône du casque au sommet, près de la"});
db.iliadfr.insert({ pg : 8222, line : "crinière, et lui-même fut atteint au front, au-dessus du nez. Et"});
db.iliadfr.insert({ pg : 8223, line : "ses os crièrent, et ses yeux ensanglantés jaillirent à ses pieds,"});
db.iliadfr.insert({ pg : 8224, line : "dans la poussière; et il se renversa et tomba. Et Ménélaos, lui"});
db.iliadfr.insert({ pg : 8225, line : "mettant le pied sur la poitrine, lui arracha ses armes et dit en"});
db.iliadfr.insert({ pg : 8226, line : "se glorifiant:"});
db.iliadfr.insert({ pg : 8228, line : "-- Vous laisserez ainsi les nefs des cavaliers Danaens, ô"});
db.iliadfr.insert({ pg : 8229, line : "parjures, insatiables de la rude bataille! Vous ne m'avez épargné"});
db.iliadfr.insert({ pg : 8230, line : "ni un outrage, ni un opprobre, mauvais chiens, qui n'avez pas"});
db.iliadfr.insert({ pg : 8231, line : "redouté la colère terrible de Zeus hospitalier qui tonne fortement"});
db.iliadfr.insert({ pg : 8232, line : "et qui détruira votre haute citadelle; car vous êtes venus sans"});
db.iliadfr.insert({ pg : 8233, line : "cause, après avoir été reçus en amis, m'enlever, avec toutes mes"});
db.iliadfr.insert({ pg : 8234, line : "richesses, la femme que j'avais épousée vierge. Et, maintenant,"});
db.iliadfr.insert({ pg : 8235, line : "voici que vous tentez de jeter la flamme désastreuse sur nos nefs"});
db.iliadfr.insert({ pg : 8236, line : "qui traversent la mer, et de tuer les héros Akhaiens! Mais vous"});
db.iliadfr.insert({ pg : 8237, line : "serez réprimés, bien que remplis de fureur guerrière. Ô père Zeus,"});
db.iliadfr.insert({ pg : 8238, line : "on dit que tu surpasses en sagesse tous les hommes et tous les"});
db.iliadfr.insert({ pg : 8239, line : "dieux, et c'est de toi que viennent ces choses! N'es-tu pas"});
db.iliadfr.insert({ pg : 8240, line : "favorable aux Troiens parjures, dont l'esprit est impie, et qui ne"});
db.iliadfr.insert({ pg : 8241, line : "peuvent être rassasiés par la guerre désastreuse? Certes, la"});
db.iliadfr.insert({ pg : 8242, line : "satiété nous vient de tout, du sommeil, de l'amour, du chant et de"});
db.iliadfr.insert({ pg : 8243, line : "la danse charmante, qui, cependant, nous plaisent plus que la"});
db.iliadfr.insert({ pg : 8244, line : "guerre; mais les Troiens sont insatiables de combats."});
db.iliadfr.insert({ pg : 8246, line : "Ayant ainsi parlé, l'irréprochable Ménélaos arracha les armes"});
db.iliadfr.insert({ pg : 8247, line : "sanglantes du cadavre, et les remit à ses compagnons; et il se"});
db.iliadfr.insert({ pg : 8248, line : "mêla de nouveau à ceux qui combattaient en avant. Et le fils du"});
db.iliadfr.insert({ pg : 8249, line : "roi Pylaiméneus, Harpaliôn, se jeta sur lui. Et il avait suivi son"});
db.iliadfr.insert({ pg : 8250, line : "père bien-aimé à la guerre de Troiè, et il ne devait point"});
db.iliadfr.insert({ pg : 8251, line : "retourner dans la terre de la patrie. De sa pique il frappa le"});
db.iliadfr.insert({ pg : 8252, line : "milieu du bouclier de l'Atréide, mais l'airain ne put le"});
db.iliadfr.insert({ pg : 8253, line : "traverser, et Harpaliôn, évitant la mort, se réfugia dans la foule"});
db.iliadfr.insert({ pg : 8254, line : "de ses compagnons, regardant de tous côtés pour ne pas être frappé"});
db.iliadfr.insert({ pg : 8255, line : "de l'airain. Et, comme il fuyait, Mèrionès lui lança une flèche"});
db.iliadfr.insert({ pg : 8256, line : "d'airain, et il le perça à la cuisse droite, et la flèche pénétra,"});
db.iliadfr.insert({ pg : 8257, line : "sous l'os, jusque dans la vessie. Et il tomba entre les bras de"});
db.iliadfr.insert({ pg : 8258, line : "ses chers compagnons, rendant l'âme. Il gisait comme un ver sur la"});
db.iliadfr.insert({ pg : 8259, line : "terre, et son sang noir coulait, baignant la terre. Et les"});
db.iliadfr.insert({ pg : 8260, line : "magnanimes Paphlagones, s'empressant et gémissant, le déposèrent"});
db.iliadfr.insert({ pg : 8261, line : "sur son char pour être conduit à la sainte Ilios; et son père,"});
db.iliadfr.insert({ pg : 8262, line : "répandant des larmes, allait avec eux, nul n'ayant vengé son fils"});
db.iliadfr.insert({ pg : 8263, line : "mort."});
db.iliadfr.insert({ pg : 8265, line : "Et Pâris, irrité dans son âme de cette mort, car Harpaliôn était"});
db.iliadfr.insert({ pg : 8266, line : "son hôte entre les nombreux Paphlagones, lança une flèche"});
db.iliadfr.insert({ pg : 8267, line : "d'airain. Et il y avait un guerrier Akhaien, Eukhènor, fils du"});
db.iliadfr.insert({ pg : 8268, line : "divinateur Polyidos, riche et brave, et habitant Korinthos. Et il"});
db.iliadfr.insert({ pg : 8269, line : "était monté sur sa nef, subissant sa destinée, car le bon Polyidos"});
db.iliadfr.insert({ pg : 8270, line : "lui avait dit souvent qu'il mourrait, dans ses demeures, d'un mal"});
db.iliadfr.insert({ pg : 8271, line : "cruel, ou que les Troiens le tueraient parmi les nefs des"});
db.iliadfr.insert({ pg : 8272, line : "Akhaiens. Et il avait voulu éviter à la fois la lourde amende des"});
db.iliadfr.insert({ pg : 8273, line : "Akhaiens, et la maladie cruelle qui l'aurait accablé de douleurs,"});
db.iliadfr.insert({ pg : 8274, line : "mais Pâris le perça au-dessous de l'oreille, et l'âme s'envola de"});
db.iliadfr.insert({ pg : 8275, line : "ses membres, et une horrible nuée l'enveloppa."});
db.iliadfr.insert({ pg : 8277, line : "Tandis qu'ils combattaient, pareils au feu ardent, Hektôr cher à"});
db.iliadfr.insert({ pg : 8278, line : "Zeus ignorait qu'à la gauche des nefs ses peuples étaient défaits"});
db.iliadfr.insert({ pg : 8279, line : "par les Argiens, tant celui qui ébranle la terre animait les"});
db.iliadfr.insert({ pg : 8280, line : "Danaens et les pénétrait de sa force. Et le Priamide se tenait là"});
db.iliadfr.insert({ pg : 8281, line : "où il avait franchi les portes et où il enfonçait les épaisses"});
db.iliadfr.insert({ pg : 8282, line : "lignes des Danaens porteurs de boucliers. Là, les nefs d'Aias et"});
db.iliadfr.insert({ pg : 8283, line : "de Prôtésilaos avaient été tirées sur le rivage de la blanche mer,"});
db.iliadfr.insert({ pg : 8284, line : "et le mur y était peu élevé. Là aussi étaient les plus furieux"});
db.iliadfr.insert({ pg : 8285, line : "combattants, et les chevaux, les Boiôtiens, les Iaônes aux longs"});
db.iliadfr.insert({ pg : 8286, line : "vêtements, les Lokriens, les Phthiotes et les illustres Épéiens,"});
db.iliadfr.insert({ pg : 8287, line : "qui soutenaient l'assaut autour des nefs et ne pouvaient repousser"});
db.iliadfr.insert({ pg : 8288, line : "le divin Hektôr semblable à la flamme."});
db.iliadfr.insert({ pg : 8290, line : "Et là étaient aussi les braves Athènaiens que conduisait"});
db.iliadfr.insert({ pg : 8291, line : "Ménèstheus, fils de Pétéos, suivi de Pheidas, de Stikhios et du"});
db.iliadfr.insert({ pg : 8292, line : "grand Bias. Et les chefs des Épéiens étaient Mégès Phyléide,"});
db.iliadfr.insert({ pg : 8293, line : "Amphiôn et Drakios. Et les chefs des Phthiotes étaient Médôn et"});
db.iliadfr.insert({ pg : 8294, line : "l'agile Ménéptolèmos. Médôn était fils bâtard du divin Oileus, et"});
db.iliadfr.insert({ pg : 8295, line : "frère d'Aias, et il habitait Phylakè, loin de la terre de la"});
db.iliadfr.insert({ pg : 8296, line : "patrie, ayant tué le frère de sa belle-mère Ériopis; et"});
db.iliadfr.insert({ pg : 8297, line : "Ménéptolèmos était fils d'Iphiklos Phylakide. Et ils combattaient"});
db.iliadfr.insert({ pg : 8298, line : "tous deux en tête des Phthiotes magnanimes, parmi les Boiôtiens,"});
db.iliadfr.insert({ pg : 8299, line : "pour défendre les nefs."});
db.iliadfr.insert({ pg : 8301, line : "Et Aias, le fils agile d'Oileus, se tenait toujours auprès d'Aias"});
db.iliadfr.insert({ pg : 8302, line : "Télamônien. Comme deux boeufs noirs traînent ensemble, d'un"});
db.iliadfr.insert({ pg : 8303, line : "souffle égal, une lourde charrue dans une terre nouvelle, tandis"});
db.iliadfr.insert({ pg : 8304, line : "que la sueur coule de la racine de leurs cornes, et que, liés à"});
db.iliadfr.insert({ pg : 8305, line : "distance au même joug, ils vont dans le sillon, ouvrant du soc la"});
db.iliadfr.insert({ pg : 8306, line : "terre profonde, de même les deux Aias allaient ensemble. Mais de"});
db.iliadfr.insert({ pg : 8307, line : "nombreux et braves guerriers suivaient le Télamôniade et portaient"});
db.iliadfr.insert({ pg : 8308, line : "son bouclier, quand la fatigue et la sueur rompaient ses genoux."});
db.iliadfr.insert({ pg : 8309, line : "Et les Lokriens ne suivaient pas le magnanime Oilèiade, car il ne"});
db.iliadfr.insert({ pg : 8310, line : "leur plaisait pas de combattre en ligne. Ils n'avaient ni casques"});
db.iliadfr.insert({ pg : 8311, line : "d'airain hérissés de crins de cheval, ni boucliers bombés, ni"});
db.iliadfr.insert({ pg : 8312, line : "lances de frêne; et ils étaient venus devant Troiè avec des arcs"});
db.iliadfr.insert({ pg : 8313, line : "et des frondes de laine, et ils en accablaient et en rompaient"});
db.iliadfr.insert({ pg : 8314, line : "sans cesse les phalanges Troiennes. Et les premiers combattaient,"});
db.iliadfr.insert({ pg : 8315, line : "couverts de leurs belles armes, contre les Troiens et Hektôr armé"});
db.iliadfr.insert({ pg : 8316, line : "d'airain, et les autres, cachés derrière ceux-là, lançaient sans"});
db.iliadfr.insert({ pg : 8317, line : "cesse des flèches innombrables."});
db.iliadfr.insert({ pg : 8319, line : "Alors, les Troiens se fussent enfuis misérablement, loin des"});
db.iliadfr.insert({ pg : 8320, line : "tentes et des nefs, vers la sainte Ilios, si Polydamas n'eût dit"});
db.iliadfr.insert({ pg : 8321, line : "au brave Hektôr:"});
db.iliadfr.insert({ pg : 8323, line : "-- Hektôr, il est impossible que tu écoutes un conseil. Parce"});
db.iliadfr.insert({ pg : 8324, line : "qu'un dieu t'a donné d'exceller dans la guerre, tu veux aussi"});
db.iliadfr.insert({ pg : 8325, line : "l'emporter par la sagesse. Mais tu ne peux tout posséder. Les"});
db.iliadfr.insert({ pg : 8326, line : "dieux accordent aux uns le courage, aux autres l'art de la danse,"});
db.iliadfr.insert({ pg : 8327, line : "à l'autre la kithare et le chant. Le prévoyant Zeus mit un esprit"});
db.iliadfr.insert({ pg : 8328, line : "sage en celui-ci, et les hommes en profitent, et il sauvegarde les"});
db.iliadfr.insert({ pg : 8329, line : "cités, et il recueille pour lui-même le fruit de sa prudence. La"});
db.iliadfr.insert({ pg : 8330, line : "couronne de la guerre éclate de toutes parts autour de toi, et les"});
db.iliadfr.insert({ pg : 8331, line : "Troiens magnanimes qui ont franchi la muraille fuient avec leurs"});
db.iliadfr.insert({ pg : 8332, line : "armes, ou combattent en petit nombre contre beaucoup, dispersés"});
db.iliadfr.insert({ pg : 8333, line : "autour des nefs. Retourne, et appelle ici tous les chefs, afin que"});
db.iliadfr.insert({ pg : 8334, line : "nous délibérions en conseil si nous devons nous ruer sur les nefs,"});
db.iliadfr.insert({ pg : 8335, line : "en espérant qu'un dieu nous accorde la victoire, ou s'il nous faut"});
db.iliadfr.insert({ pg : 8336, line : "reculer avant d'être entamés. Je crains que les Akhaiens ne"});
db.iliadfr.insert({ pg : 8337, line : "vengent leur défaite d'hier, car il y a dans les nefs un homme"});
db.iliadfr.insert({ pg : 8338, line : "insatiable de guerre, qui, je pense, ne s'abstiendra pas longtemps"});
db.iliadfr.insert({ pg : 8339, line : "de combat."});
db.iliadfr.insert({ pg : 8341, line : "Polydamas parla ainsi, et son conseil prudent persuada Hektôr, et"});
db.iliadfr.insert({ pg : 8342, line : "il sauta de son char à terre avec ses armes, et il dit en paroles"});
db.iliadfr.insert({ pg : 8343, line : "ailées:"});
db.iliadfr.insert({ pg : 8345, line : "-- Polydamas, retiens ici tous les chefs. Moi, j'irai au milieu du"});
db.iliadfr.insert({ pg : 8346, line : "combat et je reviendrai bientôt, les ayant convoqués."});
db.iliadfr.insert({ pg : 8348, line : "Il parla ainsi, et se précipita, pareil à une montagne neigeuse,"});
db.iliadfr.insert({ pg : 8349, line : "parmi les Troiens et les alliés, avec de hautes clameurs. Et,"});
db.iliadfr.insert({ pg : 8350, line : "ayant entendu la voix de Hektôr, ils accouraient tous auprès du"});
db.iliadfr.insert({ pg : 8351, line : "Panthoide Polydamas. Et le Priamide Hektôr allait, cherchant parmi"});
db.iliadfr.insert({ pg : 8352, line : "les combattants, Dèiphobos et le roi Hélénos, et l'Asiade Adamas"});
db.iliadfr.insert({ pg : 8353, line : "et le Hyrtakide Asios. Et il les trouva tous, ou blessés, ou"});
db.iliadfr.insert({ pg : 8354, line : "morts, autour des nefs et des poupes des Akhaiens, ayant rendu"});
db.iliadfr.insert({ pg : 8355, line : "l'âme sous les mains des Argiens."});
db.iliadfr.insert({ pg : 8357, line : "Et il vit, à la gauche de cette bataille meurtrière, le divin"});
db.iliadfr.insert({ pg : 8358, line : "Alexandros, l'époux de Hélénè à la belle chevelure, animant ses"});
db.iliadfr.insert({ pg : 8359, line : "compagnons au combat. Et, s'arrêtant devant lui, il lui dit ces"});
db.iliadfr.insert({ pg : 8360, line : "paroles outrageantes:"});
db.iliadfr.insert({ pg : 8362, line : "-- Misérable Pâris, doué d'une grande beauté, séducteur de femmes,"});
db.iliadfr.insert({ pg : 8363, line : "où sont Dèiphobos, le roi Hélénos, et l'Asiade Adamas et le"});
db.iliadfr.insert({ pg : 8364, line : "Hyrtakide Asios? Où est Othryoneus? Aujourd'hui la sainte Ilios"});
db.iliadfr.insert({ pg : 8365, line : "croule de son faîte, et tu as évité seul cette ruine terrible."});
db.iliadfr.insert({ pg : 8367, line : "Et le divin Alexandros lui répondit:"});
db.iliadfr.insert({ pg : 8369, line : "-- Hektôr, tu te plais à m'accuser quand je ne suis point"});
db.iliadfr.insert({ pg : 8370, line : "coupable. Parfois je me suis retiré du combat, mais ma mère ne m'a"});
db.iliadfr.insert({ pg : 8371, line : "point enfanté lâche. Depuis que tu as excité la lutte de nos"});
db.iliadfr.insert({ pg : 8372, line : "compagnons auprès des nefs, nous avons combattu sans cesse les"});
db.iliadfr.insert({ pg : 8373, line : "Danaens. Ceux que tu demandes sont morts. Seuls, Dèiphobos et le"});
db.iliadfr.insert({ pg : 8374, line : "roi Hélénos ont été tous deux blessés à la main par de longues"});
db.iliadfr.insert({ pg : 8375, line : "lances; mais le Kroniôn leur a épargné la mort. Conduis-nous donc"});
db.iliadfr.insert({ pg : 8376, line : "où ton coeur et ton esprit t'ordonnent d'aller, et nous serons"});
db.iliadfr.insert({ pg : 8377, line : "prompts à te suivre, et je ne pense pas que nous cessions le"});
db.iliadfr.insert({ pg : 8378, line : "combat tant que nos forces le permettront. Il n'est permis à"});
db.iliadfr.insert({ pg : 8379, line : "personne de combattre au-delà de ses forces."});
db.iliadfr.insert({ pg : 8381, line : "Ayant ainsi parlé, le héros fléchit l'âme de son frère, et ils"});
db.iliadfr.insert({ pg : 8382, line : "coururent là où la mêlée était la plus furieuse, là où étaient"});
db.iliadfr.insert({ pg : 8383, line : "Kébrionès et l'irréprochable Polydamas, Phakès, Orthaios, le divin"});
db.iliadfr.insert({ pg : 8384, line : "Polyphoitès, et Palmys, et Askanios et Moros, fils de Hippotiôn."});
db.iliadfr.insert({ pg : 8385, line : "Et ceux-ci avaient succédé depuis la veille aux autres guerriers"});
db.iliadfr.insert({ pg : 8386, line : "de la fertile Askaniè, et déjà Zeus les poussait au combat."});
db.iliadfr.insert({ pg : 8388, line : "Et tous allaient, semblables aux tourbillons de vent que le père"});
db.iliadfr.insert({ pg : 8389, line : "Zeus envoie avec le tonnerre par les campagnes, et dont le bruit"});
db.iliadfr.insert({ pg : 8390, line : "se mêle au retentissement des grandes eaux bouillonnantes et"});
db.iliadfr.insert({ pg : 8391, line : "soulevées de la mer aux rumeurs sans nombre, qui se gonflent,"});
db.iliadfr.insert({ pg : 8392, line : "blanches d'écume, et roulent les unes sur les autres."});
db.iliadfr.insert({ pg : 8394, line : "Ainsi les Troiens se succédaient derrière leurs chefs éclatants"});
db.iliadfr.insert({ pg : 8395, line : "d'airain. Et le Priamide Hektôr les menait, semblable au terrible"});
db.iliadfr.insert({ pg : 8396, line : "Arès, et il portait devant lui son bouclier égal fait de peaux"});
db.iliadfr.insert({ pg : 8397, line : "épaisses recouvertes d'airain. Et autour de ses tempes"});
db.iliadfr.insert({ pg : 8398, line : "resplendissait son casque mouvant, et, sous son bouclier, il"});
db.iliadfr.insert({ pg : 8399, line : "marchait contre les phalanges, cherchant à les enfoncer de tous"});
db.iliadfr.insert({ pg : 8400, line : "côtés. Mais il n'ébranla point l'âme des Akhaiens dans leurs"});
db.iliadfr.insert({ pg : 8401, line : "poitrines, et Aias, le premier, s'avança en le provoquant:"});
db.iliadfr.insert({ pg : 8403, line : "-- Viens, malheureux! Pourquoi tentes-tu d'effrayer les Argiens?"});
db.iliadfr.insert({ pg : 8404, line : "Nous ne sommes pas inhabiles au combat. C'est le fouet fatal de"});
db.iliadfr.insert({ pg : 8405, line : "Zeus qui nous éprouve. Tu espères sans doute, dans ton esprit,"});
db.iliadfr.insert({ pg : 8406, line : "détruire nos nefs, mais nos mains te repousseront, et bientôt ta"});
db.iliadfr.insert({ pg : 8407, line : "ville bien peuplée sera prise et renversée par nous. Et je te le"});
db.iliadfr.insert({ pg : 8408, line : "dis, le temps viendra où, fuyant, tu supplieras le père Zeus et"});
db.iliadfr.insert({ pg : 8409, line : "les autres immortels pour que tes chevaux soient plus rapides que"});
db.iliadfr.insert({ pg : 8410, line : "l'épervier, tandis qu'ils t'emporteront vers la ville à travers la"});
db.iliadfr.insert({ pg : 8411, line : "poussière de la plaine."});
db.iliadfr.insert({ pg : 8413, line : "Et, comme il parlait ainsi, un aigle vola à sa droite dans les"});
db.iliadfr.insert({ pg : 8414, line : "hauteurs, et les Akhaiens se réjouirent de cet augure. Et"});
db.iliadfr.insert({ pg : 8415, line : "l'illustre Hektôr lui répondit:"});
db.iliadfr.insert({ pg : 8417, line : "-- Aias, orgueilleux et insensé, qu'as-tu dit? Plût aux dieux que"});
db.iliadfr.insert({ pg : 8418, line : "je fusse le fils de Zeus tempétueux, et que la vénérable Hèrè"});
db.iliadfr.insert({ pg : 8419, line : "m'eût enfanté, aussi vrai que ce jour sera fatal aux Argiens, et"});
db.iliadfr.insert({ pg : 8420, line : "que tu tomberas toi-même, si tu oses attendre ma longue lance qui"});
db.iliadfr.insert({ pg : 8421, line : "déchirera ton corps délicat, et que tu rassasieras les chiens"});
db.iliadfr.insert({ pg : 8422, line : "d'Ilios et les oiseaux carnassiers de ta graisse et de ta chair,"});
db.iliadfr.insert({ pg : 8423, line : "auprès des nefs des Akhaiens!"});
db.iliadfr.insert({ pg : 8425, line : "Ayant ainsi parlé, il se rua en avant, et ses compagnons le"});
db.iliadfr.insert({ pg : 8426, line : "suivirent avec une immense clameur que l'armée répéta par"});
db.iliadfr.insert({ pg : 8427, line : "derrière. Et les Argiens, se souvenant de leur vigueur,"});
db.iliadfr.insert({ pg : 8428, line : "répondirent par d'autres cris, et la clameur des deux peuples"});
db.iliadfr.insert({ pg : 8429, line : "monta jusque dans l'aithèr, parmi les splendeurs de Zeus."});
db.iliadfr.insert({ pg : 8432, line : "Chant 14"});
db.iliadfr.insert({ pg : 8435, line : "Tout en buvant, Nestôr entendit la clameur des hommes, et il dit à"});
db.iliadfr.insert({ pg : 8436, line : "l'Asklèpiade ces paroles ailées:"});
db.iliadfr.insert({ pg : 8438, line : "-- Divin Makhaôn, que deviendront ces choses? Voici que la clameur"});
db.iliadfr.insert({ pg : 8439, line : "des jeunes hommes grandit autour des nefs. Reste ici, et bois ce"});
db.iliadfr.insert({ pg : 8440, line : "vin qui réchauffe, tandis que Hékamèdè aux beaux cheveux fait"});
db.iliadfr.insert({ pg : 8441, line : "tiédir l'eau qui lavera le sang de ta plaie. Moi, j'irai sur la"});
db.iliadfr.insert({ pg : 8442, line : "hauteur voir ce qui en est."});
db.iliadfr.insert({ pg : 8444, line : "Ayant ainsi parlé, il saisit dans sa tente le bouclier de son"});
db.iliadfr.insert({ pg : 8445, line : "fils, le brave Thrasymèdès qui, lui-même, avait pris le bouclier"});
db.iliadfr.insert({ pg : 8446, line : "éclatant d'airain de son père, et il saisit aussi une forte lance"});
db.iliadfr.insert({ pg : 8447, line : "à pointe d'airain, et, sortant de la tente, il vit une chose"});
db.iliadfr.insert({ pg : 8448, line : "lamentable: les Akhaiens bouleversés et les Troiens magnanimes les"});
db.iliadfr.insert({ pg : 8449, line : "poursuivant, et le mur des Akhaiens renversé. De même, quand"});
db.iliadfr.insert({ pg : 8450, line : "l'onde silencieuse de la grande mer devient toute noire, dans le"});
db.iliadfr.insert({ pg : 8451, line : "pressentiment des vents impétueux, et reste immobile, ne sachant"});
db.iliadfr.insert({ pg : 8452, line : "encore de quel côté ils souffleront; de même, le vieillard,"});
db.iliadfr.insert({ pg : 8453, line : "hésitant, ne savait s'il se mêlerait à la foule des cavaliers"});
db.iliadfr.insert({ pg : 8454, line : "Danaens, ou s'il irait rejoindre Agamemnôn, le prince des peuples."});
db.iliadfr.insert({ pg : 8455, line : "Mais il jugea qu'il était plus utile de rejoindre l'Atréide."});
db.iliadfr.insert({ pg : 8457, line : "Et Troiens et Danaens s'entre-tuaient dans la mêlée, et l'airain"});
db.iliadfr.insert({ pg : 8458, line : "solide sonnait autour de leurs corps, tandis qu'ils se frappaient"});
db.iliadfr.insert({ pg : 8459, line : "de leurs épées et de leurs lances à deux pointes."});
db.iliadfr.insert({ pg : 8461, line : "Et Nestôr rencontra, venant des nefs, les rois divins que l'airain"});
db.iliadfr.insert({ pg : 8462, line : "avait blessés, le Tydéide, et Odysseus, et l'Atréide Agamemnôn."});
db.iliadfr.insert({ pg : 8463, line : "Leurs nefs étaient éloignées du champ de bataille, ayant été"});
db.iliadfr.insert({ pg : 8464, line : "tirées les premières sur le sable de la blanche mer; car celles"});
db.iliadfr.insert({ pg : 8465, line : "qui vinrent les premières s'avançaient jusque dans la plaine, et"});
db.iliadfr.insert({ pg : 8466, line : "le mur protégeait leurs poupes. Tout large qu'il était, le rivage"});
db.iliadfr.insert({ pg : 8467, line : "ne pouvait contenir toutes les nefs sans resserrer le camp; et les"});
db.iliadfr.insert({ pg : 8468, line : "Akhaiens les avaient rangées par files, dans la gorge du rivage,"});
db.iliadfr.insert({ pg : 8469, line : "entre les deux promontoires."});
db.iliadfr.insert({ pg : 8471, line : "Et les rois, l'âme attristée dans leur poitrine, venaient"});
db.iliadfr.insert({ pg : 8472, line : "ensemble, appuyés sur leurs lances. Et leur esprit s'effraya quand"});
db.iliadfr.insert({ pg : 8473, line : "ils virent le vieux Nestôr, et le roi Agamemnôn lui dit aussitôt:"});
db.iliadfr.insert({ pg : 8475, line : "-- Ô Nestôr Nèlèiade, gloire des Akhaiens, pourquoi reviens-tu de"});
db.iliadfr.insert({ pg : 8476, line : "ce combat fatal? Je crains que le brave Hektôr n'accomplisse la"});
db.iliadfr.insert({ pg : 8477, line : "menace qu'il a faite, dans l'agora des Troiens, de ne rentrer dans"});
db.iliadfr.insert({ pg : 8478, line : "Ilios qu'après avoir brûlé les nefs et tué tous les Akhaiens. Il"});
db.iliadfr.insert({ pg : 8479, line : "l'a dit et il le fait. Ah! certes, les Akhaiens aux belles"});
db.iliadfr.insert({ pg : 8480, line : "knèmides ont contre moi la même colère qu'Akhilleus, et ils ne"});
db.iliadfr.insert({ pg : 8481, line : "veulent plus combattre autour des nefs."});
db.iliadfr.insert({ pg : 8483, line : "Et le cavalier Gérennien Nestôr lui répondit:"});
db.iliadfr.insert({ pg : 8485, line : "-- Certes, tu dis vrai, et Zeus qui tonne dans les hauteurs n'y"});
db.iliadfr.insert({ pg : 8486, line : "peut rien lui-même. Le mur est renversé que nous nous flattions"});
db.iliadfr.insert({ pg : 8487, line : "d'avoir élevé devant les nefs comme un rempart inaccessible. Et"});
db.iliadfr.insert({ pg : 8488, line : "voici que les Troiens combattent maintenant au milieu des nefs, et"});
db.iliadfr.insert({ pg : 8489, line : "nous ne saurions reconnaître, en regardant avec le plus"});
db.iliadfr.insert({ pg : 8490, line : "d'attention, de quel côté les Akhaiens roulent bouleversés."});
db.iliadfr.insert({ pg : 8491, line : "Mais ils tombent partout, et leurs clameurs montent dans"});
db.iliadfr.insert({ pg : 8492, line : "l'Ouranos. Pour nous, délibérons sur ces calamités, si toutefois"});
db.iliadfr.insert({ pg : 8493, line : "une résolution peut être utile. Je ne vous engage point à"});
db.iliadfr.insert({ pg : 8494, line : "retourner dans la mêlée, car un blessé ne peut combattre."});
db.iliadfr.insert({ pg : 8496, line : "Et le roi des hommes, Agamemnôn, lui répondit:"});
db.iliadfr.insert({ pg : 8498, line : "-- Nestôr, puisque le combat est au milieu des nefs, et que le mur"});
db.iliadfr.insert({ pg : 8499, line : "et le fossé ont été inutiles qui ont coûté tant de travaux aux"});
db.iliadfr.insert({ pg : 8500, line : "Danaens, et qui devaient, pensions-nous, être un rempart"});
db.iliadfr.insert({ pg : 8501, line : "inaccessible, c'est qu'il plaît, sans doute, au très-puissant Zeus"});
db.iliadfr.insert({ pg : 8502, line : "que les Akhaiens périssent tous, sans gloire, loin d'Argos. Je"});
db.iliadfr.insert({ pg : 8503, line : "reconnaissais autrefois qu'il secourait les Danaens, mais je sais"});
db.iliadfr.insert({ pg : 8504, line : "maintenant qu'il honore les Troiens comme des bienheureux, et"});
db.iliadfr.insert({ pg : 8505, line : "qu'il enchaîne notre vigueur et nos mains. Allons, obéissez à mes"});
db.iliadfr.insert({ pg : 8506, line : "paroles. Traînons à la mer les nefs qui en sont le plus"});
db.iliadfr.insert({ pg : 8507, line : "rapprochées. Restons sur nos ancres jusqu'à la nuit; et, si les"});
db.iliadfr.insert({ pg : 8508, line : "Troiens cessent le combat, nous pourrons mettre à la mer divine le"});
db.iliadfr.insert({ pg : 8509, line : "reste de nos nefs. Il n'y a nulle honte à fuir notre ruine entière"});
db.iliadfr.insert({ pg : 8510, line : "à l'aide de la nuit, et mieux vaut fuir les maux que d'en être"});
db.iliadfr.insert({ pg : 8511, line : "accablé."});
db.iliadfr.insert({ pg : 8513, line : "Et le sage Odysseus, le regardant d'un oeil sombre, lui dit:"});
db.iliadfr.insert({ pg : 8515, line : "-- Atréide, quelle parole mauvaise a passé à travers tes dents? Tu"});
db.iliadfr.insert({ pg : 8516, line : "devrais conduire une armée de lâches au lieu de nous commander,"});
db.iliadfr.insert({ pg : 8517, line : "nous à qui Zeus a donné de poursuivre les guerres rudes, de la"});
db.iliadfr.insert({ pg : 8518, line : "jeunesse à la vieillesse, et jusqu'à la mort. Ainsi, tu veux"});
db.iliadfr.insert({ pg : 8519, line : "renoncer à la grande ville des Troiens pour laquelle nous avons"});
db.iliadfr.insert({ pg : 8520, line : "souffert tant de maux? Tais-toi. Que nul d'entre les Akhaiens"});
db.iliadfr.insert({ pg : 8521, line : "n'entende cette parole que n'aurait dû prononcer aucun homme d'un"});
db.iliadfr.insert({ pg : 8522, line : "esprit juste, un roi à qui obéissent des peuples aussi nombreux"});
db.iliadfr.insert({ pg : 8523, line : "que ceux auxquels tu commandes parmi les Akhaiens. Moi, je"});
db.iliadfr.insert({ pg : 8524, line : "condamne cette parole que tu as dite, cet ordre de traîner à la"});
db.iliadfr.insert({ pg : 8525, line : "mer les nefs bien construites, loin des clameurs du combat. Ne"});
db.iliadfr.insert({ pg : 8526, line : "serait-ce pas combler les désirs des Troiens déjà victorieux?"});
db.iliadfr.insert({ pg : 8527, line : "Comment les Akhaiens soutiendraient-ils le combat, pendant qu'ils"});
db.iliadfr.insert({ pg : 8528, line : "traîneraient les nefs à la mer? Ils ne songeraient qu'aux nefs et"});
db.iliadfr.insert({ pg : 8529, line : "négligeraient le combat. Ton conseil nous serait fatal, prince des"});
db.iliadfr.insert({ pg : 8530, line : "peuples!"});
db.iliadfr.insert({ pg : 8532, line : "Et le roi des hommes, Agamemnôn, lui répondit:"});
db.iliadfr.insert({ pg : 8534, line : "-- Ô Odysseus, tes rudes paroles ont pénétré dans mon coeur. Je ne"});
db.iliadfr.insert({ pg : 8535, line : "veux point que les fils des Akhaiens traînent à la mer, contre"});
db.iliadfr.insert({ pg : 8536, line : "leur gré, les nefs bien construites. Maintenant, si quelqu'un a un"});
db.iliadfr.insert({ pg : 8537, line : "meilleur conseil à donner, jeune ou vieux, qu'il parle, et sa"});
db.iliadfr.insert({ pg : 8538, line : "parole me remplira de joie."});
db.iliadfr.insert({ pg : 8540, line : "Et le brave Diomèdès parla ainsi au milieu d'eux:"});
db.iliadfr.insert({ pg : 8542, line : "-- Celui-là est près de vous, et nous ne chercherons pas"});
db.iliadfr.insert({ pg : 8543, line : "longtemps, si vous voulez obéir. Et vous ne me blâmerez point de"});
db.iliadfr.insert({ pg : 8544, line : "parler parce que je suis le plus jeune, car je suis né d'un père"});
db.iliadfr.insert({ pg : 8545, line : "illustre et je descends d'une race glorieuse. Et mon père est"});
db.iliadfr.insert({ pg : 8546, line : "Tydeus qui occupe un large sépulcre dans Thèbè. Portheus engendra"});
db.iliadfr.insert({ pg : 8547, line : "trois fils irréprochables qui habitaient Pleurôn et la haute"});
db.iliadfr.insert({ pg : 8548, line : "Kalydôn: Agrios, Mélas, et le troisième était le cavalier Oineus,"});
db.iliadfr.insert({ pg : 8549, line : "le père de mon père, et le plus brave des trois. Et celui-ci"});
db.iliadfr.insert({ pg : 8550, line : "demeura chez lui, mais mon père habita Argos. Ainsi le voulurent"});
db.iliadfr.insert({ pg : 8551, line : "Zeus et les autres dieux. Et mon père épousa une des filles"});
db.iliadfr.insert({ pg : 8552, line : "d'Adrestès, et il habitait une maison pleine d'abondance, car il"});
db.iliadfr.insert({ pg : 8553, line : "possédait beaucoup de champs fertiles entourés de grands vergers."});
db.iliadfr.insert({ pg : 8554, line : "Et ses brebis étaient nombreuses, et il était illustre par sa"});
db.iliadfr.insert({ pg : 8555, line : "lance entre tous les Akhaiens. Vous savez que je dis la vérité,"});
db.iliadfr.insert({ pg : 8556, line : "que ma race n'est point vile, et vous ne mépriserez point mes"});
db.iliadfr.insert({ pg : 8557, line : "paroles. Allons vers le champ de bataille, bien que blessés, loin"});
db.iliadfr.insert({ pg : 8558, line : "des traits, afin que nous ne recevions pas blessure sur blessure;"});
db.iliadfr.insert({ pg : 8559, line : "mais animons et excitons les Akhaiens qui déjà se lassent et"});
db.iliadfr.insert({ pg : 8560, line : "cessent de combattre courageusement."});
db.iliadfr.insert({ pg : 8562, line : "Il parla ainsi, et ils l'écoutèrent volontiers et lui obéirent. Et"});
db.iliadfr.insert({ pg : 8563, line : "le roi des hommes, Agamemnôn, les précédait. Et l'illustre qui"});
db.iliadfr.insert({ pg : 8564, line : "ébranle la terre les vit et vint à eux sous la forme d'un"});
db.iliadfr.insert({ pg : 8565, line : "vieillard. Il prit la main droite de l'Atréide Agamemnôn, et il"});
db.iliadfr.insert({ pg : 8566, line : "lui dit:"});
db.iliadfr.insert({ pg : 8567, line : "-- Atréide, maintenant le coeur féroce d'Akhilleus se réjouit dans"});
db.iliadfr.insert({ pg : 8568, line : "sa poitrine, en voyant la fuite et le carnage des Akhaiens. Il a"});
db.iliadfr.insert({ pg : 8569, line : "perdu l'esprit. Qu'un dieu lui rende autant de honte! Tous les"});
db.iliadfr.insert({ pg : 8570, line : "dieux heureux ne sont point irrités contre toi. Les princes et les"});
db.iliadfr.insert({ pg : 8571, line : "chefs des Troiens empliront encore la plaine de poussière, et tu"});
db.iliadfr.insert({ pg : 8572, line : "les verras fuir vers leur ville, loin des nefs et des tentes."});
db.iliadfr.insert({ pg : 8574, line : "Ayant ainsi parlé, il se précipita vers la plaine en poussant un"});
db.iliadfr.insert({ pg : 8575, line : "grand cri, tel que celui que neuf ou dix mille hommes qui se ruent"});
db.iliadfr.insert({ pg : 8576, line : "au combat pourraient pousser de leurs poitrines. Tel fut le cri du"});
db.iliadfr.insert({ pg : 8577, line : "roi qui ébranle la terre. Et il versa la force dans le coeur des"});
db.iliadfr.insert({ pg : 8578, line : "Akhaiens, avec le désir de guerroyer et de combattre."});
db.iliadfr.insert({ pg : 8580, line : "Hèrè regardait, assise sur un trône d'or, au sommet de l'Olympos,"});
db.iliadfr.insert({ pg : 8581, line : "et elle reconnut aussitôt son frère qui s'agitait dans la"});
db.iliadfr.insert({ pg : 8582, line : "glorieuse bataille, et elle se réjouit dans son coeur. Et elle vit"});
db.iliadfr.insert({ pg : 8583, line : "Zeus assis au faîte de l'Ida où naissent les sources, et il lui"});
db.iliadfr.insert({ pg : 8584, line : "était odieux. Aussitôt, la vénérable Hèrè aux yeux de boeuf songea"});
db.iliadfr.insert({ pg : 8585, line : "au moyen de tromper Zeus tempétueux, et ceci lui sembla meilleur"});
db.iliadfr.insert({ pg : 8586, line : "d'aller le trouver sur l'Ida, pour exciter en lui le désir"});
db.iliadfr.insert({ pg : 8587, line : "amoureux de sa beauté, afin qu'un doux et profond sommeil fermât"});
db.iliadfr.insert({ pg : 8588, line : "ses paupières et obscurcît ses pensées."});
db.iliadfr.insert({ pg : 8590, line : "Et elle entra dans la chambre nuptiale que son fils bien-aimé"});
db.iliadfr.insert({ pg : 8591, line : "Hèphaistos avait faite. Et il avait adapté aux portes solides un"});
db.iliadfr.insert({ pg : 8592, line : "verrou secret, et aucun des dieux n'aurait pu les ouvrir. Elle"});
db.iliadfr.insert({ pg : 8593, line : "entra et ferma les portes resplendissantes. Et, d'abord, elle lava"});
db.iliadfr.insert({ pg : 8594, line : "son beau corps avec de l'ambroisie; puis elle se parfuma d'une"});
db.iliadfr.insert({ pg : 8595, line : "huile divine dont l'arôme se répandit dans la demeure de Zeus, sur"});
db.iliadfr.insert({ pg : 8596, line : "la terre et dans l'Ouranos. Et son beau corps étant parfumé, elle"});
db.iliadfr.insert({ pg : 8597, line : "peigna sa chevelure et tressa de ses mains ses cheveux éclatants,"});
db.iliadfr.insert({ pg : 8598, line : "beaux et divins, qui flottaient de sa tête immortelle. Et elle"});
db.iliadfr.insert({ pg : 8599, line : "revêtit une khlamyde divine qu'Athènè avait faite elle-même et"});
db.iliadfr.insert({ pg : 8600, line : "ornée de mille merveilles, et elle la fixa sur sa poitrine avec"});
db.iliadfr.insert({ pg : 8601, line : "des agrafes d'or. Et elle mit une ceinture à cent franges, et à"});
db.iliadfr.insert({ pg : 8602, line : "ses oreilles bien percées des pendants travaillés avec soin et"});
db.iliadfr.insert({ pg : 8603, line : "ornés de trois pierres précieuses. Et la grâce l'enveloppait tout"});
db.iliadfr.insert({ pg : 8604, line : "entière. Ensuite, la déesse mit un beau voile blanc comme Hélios,"});
db.iliadfr.insert({ pg : 8605, line : "et, à ses beaux pieds, de belles sandales. S'étant ainsi parée,"});
db.iliadfr.insert({ pg : 8606, line : "elle sortit de sa chambre nuptiale, et, appelant Aphroditè loin"});
db.iliadfr.insert({ pg : 8607, line : "des autres dieux, elle lui dit:"});
db.iliadfr.insert({ pg : 8609, line : "-- M'accorderas-tu, chère fille, ce que je vais te demander, ou me"});
db.iliadfr.insert({ pg : 8610, line : "refuseras-tu, irritée de ce que je protège les Danaens, et toi les"});
db.iliadfr.insert({ pg : 8611, line : "Troiens?"});
db.iliadfr.insert({ pg : 8613, line : "Et la fille de Zeus, Aphroditè, lui répondit:"});
db.iliadfr.insert({ pg : 8615, line : "-- Vénérable Hèrè, fille du grand Kronos, dis ce que tu désires."});
db.iliadfr.insert({ pg : 8616, line : "Mon coeur m'ordonne de te satisfaire, si je le puis, et si c'est"});
db.iliadfr.insert({ pg : 8617, line : "possible."});
db.iliadfr.insert({ pg : 8619, line : "Et la vénérable Hèrè qui médite des ruses lui répondit:"});
db.iliadfr.insert({ pg : 8621, line : "-- Donne-moi l'amour et le désir à l'aide desquels tu domptes les"});
db.iliadfr.insert({ pg : 8622, line : "dieux immortels et les hommes mortels. Je vais voir, aux limites"});
db.iliadfr.insert({ pg : 8623, line : "de la terre, Okéanos, origine des dieux, et la maternelle Téthys,"});
db.iliadfr.insert({ pg : 8624, line : "qui m'ont élevée et nourrie dans leurs demeures, m'ayant reçue de"});
db.iliadfr.insert({ pg : 8625, line : "Rhéiè, quand Zeus au large regard jeta Kronos sous la terre et"});
db.iliadfr.insert({ pg : 8626, line : "sous la mer stérile. Je vais les voir, afin d'apaiser leurs"});
db.iliadfr.insert({ pg : 8627, line : "dissensions amères. Déjà, depuis longtemps, ils ne partagent plus"});
db.iliadfr.insert({ pg : 8628, line : "le même lit, parce que la colère est entrée dans leur coeur. Si je"});
db.iliadfr.insert({ pg : 8629, line : "puis les persuader par mes paroles, et si je les rends au même"});
db.iliadfr.insert({ pg : 8630, line : "lit, pour qu'ils puissent s'unir d'amour, ils m'appelleront leur"});
db.iliadfr.insert({ pg : 8631, line : "bien-aimée et vénérable."});
db.iliadfr.insert({ pg : 8633, line : "Et Aphroditè qui aime les sourires lui répondit:"});
db.iliadfr.insert({ pg : 8635, line : "-- Il n'est point permis de te rien refuser, à toi qui couches"});
db.iliadfr.insert({ pg : 8636, line : "dans les bras du grand Zeus."});
db.iliadfr.insert({ pg : 8638, line : "Elle parla ainsi, et elle détacha de son sein la ceinture aux"});
db.iliadfr.insert({ pg : 8639, line : "couleurs variées où résident toutes les voluptés, et l'amour, et"});
db.iliadfr.insert({ pg : 8640, line : "le désir, et l'entretien amoureux, et l'éloquence persuasive qui"});
db.iliadfr.insert({ pg : 8641, line : "trouble l'esprit des sages. Et elle mit cette ceinture entre les"});
db.iliadfr.insert({ pg : 8642, line : "mains de Hèrè, et elle lui dit:"});
db.iliadfr.insert({ pg : 8644, line : "-- Reçois cette ceinture aux couleurs variées, où résident toutes"});
db.iliadfr.insert({ pg : 8645, line : "les voluptés, et mets-la sur ton sein, et tu ne reviendras pas"});
db.iliadfr.insert({ pg : 8646, line : "sans avoir fait ce que tu désires."});
db.iliadfr.insert({ pg : 8648, line : "Elle parla ainsi, et la vénérable Hèrè aux yeux de boeuf rit, et,"});
db.iliadfr.insert({ pg : 8649, line : "en riant, elle mit la ceinture sur son sein. Et Aphroditè, la"});
db.iliadfr.insert({ pg : 8650, line : "fille de Zeus, rentra dans sa demeure, et Hèrè, joyeuse, quitta le"});
db.iliadfr.insert({ pg : 8651, line : "faîte de l'Olympos. Puis, traversant la Pièriè et la riante"});
db.iliadfr.insert({ pg : 8652, line : "Émathiè, elle gagna les montagnes neigeuses des Thrèkiens, et ses"});
db.iliadfr.insert({ pg : 8653, line : "pieds ne touchaient point la terre. Et, de l'Athos, elle descendit"});
db.iliadfr.insert({ pg : 8654, line : "vers la mer agitée et parvint à Lemnos, la ville du divin Thoas,"});
db.iliadfr.insert({ pg : 8655, line : "où elle rencontra Hypnos, frère de Thanatos. Elle lui prit la main"});
db.iliadfr.insert({ pg : 8656, line : "et lui dit ces paroles:"});
db.iliadfr.insert({ pg : 8658, line : "-- Hypnos, roi de tous les dieux et de tous les hommes, si jamais"});
db.iliadfr.insert({ pg : 8659, line : "tu m'as écoutée, obéis-moi aujourd'hui, et je ne cesserai de te"});
db.iliadfr.insert({ pg : 8660, line : "rendre grâces. Endors, sous leurs paupières, les yeux splendides"});
db.iliadfr.insert({ pg : 8661, line : "de Zeus, dès que je serai couchée dans ses bras, et je te donnerai"});
db.iliadfr.insert({ pg : 8662, line : "un beau trône incorruptible, tout en or, qu'a fait mon fils"});
db.iliadfr.insert({ pg : 8663, line : "Hèphaistos qui boite des deux pieds; et il y joindra un escabeau"});
db.iliadfr.insert({ pg : 8664, line : "sur lequel tu appuieras tes beaux pieds pendant le repas."});
db.iliadfr.insert({ pg : 8666, line : "Et le doux Hypnos, lui répondant, parla ainsi:"});
db.iliadfr.insert({ pg : 8668, line : "-- Hèrè, vénérable déesse, fille du grand Kronos, j'assoupirai"});
db.iliadfr.insert({ pg : 8669, line : "aisément tout autre des dieux éternels, et même le fleuve Okéanos,"});
db.iliadfr.insert({ pg : 8670, line : "cette source de toutes choses; mais je n'approcherai point du"});
db.iliadfr.insert({ pg : 8671, line : "Kroniôn Zeus et je ne l'endormirai point, à moins qu'il me"});
db.iliadfr.insert({ pg : 8672, line : "l'ordonne. Déjà il m'a averti, grâce à toi, le jour où son fils"});
db.iliadfr.insert({ pg : 8673, line : "magnanime naviguait loin d'Ilios, de la cité dévastée des Troiens."});
db.iliadfr.insert({ pg : 8674, line : "Et j'enveloppai doucement les membres de Zeus tempêtueux, tandis"});
db.iliadfr.insert({ pg : 8675, line : "que tu méditais des calamités, et que, répandant sur la mer le"});
db.iliadfr.insert({ pg : 8676, line : "souffle des vents furieux, tu poussais Hèraklès vers Koôs bien"});
db.iliadfr.insert({ pg : 8677, line : "peuplée, loin de tous ses amis. Et Zeus, s'éveillant indigné,"});
db.iliadfr.insert({ pg : 8678, line : "dispersa tous les dieux par l'Ouranos; et il me cherchait pour me"});
db.iliadfr.insert({ pg : 8679, line : "précipiter du haut de l'aithèr dans la mer, si Nyx qui dompte les"});
db.iliadfr.insert({ pg : 8680, line : "dieux et les hommes, et que je suppliais en fuyant, ne m'eût"});
db.iliadfr.insert({ pg : 8681, line : "sauvé. Et Zeus, bien que très irrité, s'apaisa, craignant de"});
db.iliadfr.insert({ pg : 8682, line : "déplaire à la rapide Nyx. Et maintenant tu m'ordonnes de courir le"});
db.iliadfr.insert({ pg : 8683, line : "même danger!"});
db.iliadfr.insert({ pg : 8685, line : "Il parla ainsi, et la vénérable Hèrè aux yeux de boeuf lui"});
db.iliadfr.insert({ pg : 8686, line : "répondit:"});
db.iliadfr.insert({ pg : 8688, line : "-- Hypnos, pourquoi t'inquiéter ainsi? Penses-tu que Zeus au large"});
db.iliadfr.insert({ pg : 8689, line : "regard s'irrite pour les Troiens autant que pour son fils"});
db.iliadfr.insert({ pg : 8690, line : "Hèraklès? Viens, et je te donnerai pour épouse une des plus jeunes"});
db.iliadfr.insert({ pg : 8691, line : "Kharites, Pasithéiè, que tu désires sans cesse."});
db.iliadfr.insert({ pg : 8693, line : "Elle parla ainsi, et Hypnos, plein de joie, lui répondit:"});
db.iliadfr.insert({ pg : 8695, line : "-- Jure, par l'eau de Styx, un inviolable serment; touche d'une"});
db.iliadfr.insert({ pg : 8696, line : "main la terre et de l'autre la mer marbrée, et qu'ils soient"});
db.iliadfr.insert({ pg : 8697, line : "témoins, les dieux souterrains qui vivent autour de Kronos, que tu"});
db.iliadfr.insert({ pg : 8698, line : "me donneras Pasithéiè que je désire sans cesse."});
db.iliadfr.insert({ pg : 8700, line : "Il parla ainsi, et la déesse Hèrè aux bras blancs jura aussitôt"});
db.iliadfr.insert({ pg : 8701, line : "comme il le désirait, et elle nomma tous les dieux sous-tartaréens"});
db.iliadfr.insert({ pg : 8702, line : "qu'on nomme Titans. Et, après ce serment, ils quittèrent tous deux"});
db.iliadfr.insert({ pg : 8703, line : "Lemnos et Imbros, couverts d'une nuée et faisant rapidement leur"});
db.iliadfr.insert({ pg : 8704, line : "chemin. Et, laissant la mer à Lektos, ils parvinrent à l'Ida qui"});
db.iliadfr.insert({ pg : 8705, line : "abonde en bêtes fauves et en sources, et sous leurs pieds se"});
db.iliadfr.insert({ pg : 8706, line : "mouvait la cime des bois. Là, Hypnos resta en arrière, de peur que"});
db.iliadfr.insert({ pg : 8707, line : "Zeus le vît, et il monta dans un grand pin né sur l'Ida, et qui"});
db.iliadfr.insert({ pg : 8708, line : "s'élevait jusque dans l'aithèr. Et il se blottit dans les épais"});
db.iliadfr.insert({ pg : 8709, line : "rameaux du pin, semblable à l'oiseau bruyant que les hommes"});
db.iliadfr.insert({ pg : 8710, line : "appellent Khalkis et les dieux Kymindis."});
db.iliadfr.insert({ pg : 8712, line : "Hèrè gravit rapidement le haut Gargaros, au faîte de l'Ida. Et"});
db.iliadfr.insert({ pg : 8713, line : "Zeus qui amasse les nuées la vit, et aussitôt le désir s'empara de"});
db.iliadfr.insert({ pg : 8714, line : "lui, comme autrefois, quand ils partagèrent le même lit, loin de"});
db.iliadfr.insert({ pg : 8715, line : "leurs parents bien-aimés. Il s'approcha et lui dit:"});
db.iliadfr.insert({ pg : 8717, line : "-- Hèrè, pourquoi as-tu quitté l'Olympos? Tu n'as ni tes chevaux,"});
db.iliadfr.insert({ pg : 8718, line : "ni ton char."});
db.iliadfr.insert({ pg : 8720, line : "Et la vénérable Hèrè qui médite des ruses lui répondit:"});
db.iliadfr.insert({ pg : 8722, line : "-- Je vais voir, aux limites de la terre, Okéanos, origine des"});
db.iliadfr.insert({ pg : 8723, line : "dieux, et la maternelle Téthys, qui m'ont élevée et nourrie dans"});
db.iliadfr.insert({ pg : 8724, line : "leurs demeures. Je vais les voir, afin d'apaiser leurs dissensions"});
db.iliadfr.insert({ pg : 8725, line : "amères. Déjà, depuis longtemps, ils ne partagent plus le même lit,"});
db.iliadfr.insert({ pg : 8726, line : "parce que la colère est entrée dans leur coeur. Mes chevaux, qui"});
db.iliadfr.insert({ pg : 8727, line : "me portent sur la terre et sur la mer, sont aux pieds de l'Ida aux"});
db.iliadfr.insert({ pg : 8728, line : "nombreuses sources, et c'est à cause de toi que j'ai quitté"});
db.iliadfr.insert({ pg : 8729, line : "l'Olympos, craignant ta colère, si j'allais, en te le cachant,"});
db.iliadfr.insert({ pg : 8730, line : "dans la demeure du profond Okéanos."});
db.iliadfr.insert({ pg : 8732, line : "Et Zeus qui amasse les nuées lui dit:"});
db.iliadfr.insert({ pg : 8734, line : "-- Hèrè, attends et tu partiras ensuite, mais couchons-nous pleins"});
db.iliadfr.insert({ pg : 8735, line : "d'amour. Jamais le désir d'une déesse ou d'une femme n'a dompté"});
db.iliadfr.insert({ pg : 8736, line : "ainsi tout mon coeur. Jamais je n'ai tant aimé, ni l'épouse"});
db.iliadfr.insert({ pg : 8737, line : "d'Ixiôn, qui enfanta Peirithoos semblable à un dieu par la"});
db.iliadfr.insert({ pg : 8738, line : "sagesse, ni la fille d'Akrisiôn, la belle Danaè, qui enfanta"});
db.iliadfr.insert({ pg : 8739, line : "Perseus, le plus illustre de tous les hommes, ni la fille du"});
db.iliadfr.insert({ pg : 8740, line : "magnanime Phoinix, qui enfanta Minôs et Rhadamanthès, ni Sémélè"});
db.iliadfr.insert({ pg : 8741, line : "qui enfanta Diônysos, la joie des hommes, ni Alkmènè qui enfanta"});
db.iliadfr.insert({ pg : 8742, line : "aussi dans Thèbè mon robuste fils Hèraklès, ni la reine Dèmètèr"});
db.iliadfr.insert({ pg : 8743, line : "aux beaux cheveux, ni l'illustre Lètô, ni toi-même; car je n'ai"});
db.iliadfr.insert({ pg : 8744, line : "jamais ressenti pour toi tant de désir et tant d'amour."});
db.iliadfr.insert({ pg : 8746, line : "Et la vénérable Hèrè pleine de ruses lui répondit:"});
db.iliadfr.insert({ pg : 8748, line : "-- Très-redoutable Kronide, qu'as-tu dit? Tu désires que nous nous"});
db.iliadfr.insert({ pg : 8749, line : "unissions d'amour, maintenant, sur le faîte de l'Ida ouvert à tous"});
db.iliadfr.insert({ pg : 8750, line : "les regards! Si quelqu'un des dieux qui vivent toujours nous"});
db.iliadfr.insert({ pg : 8751, line : "voyait couchés et en avertissait tous les autres! Je n'oserais"});
db.iliadfr.insert({ pg : 8752, line : "plus rentrer dans tes demeures, en sortant de ton lit, car ce"});
db.iliadfr.insert({ pg : 8753, line : "serait honteux. Mais, si tels sont ton désir et ta volonté, la"});
db.iliadfr.insert({ pg : 8754, line : "chambre nuptiale que ton fils Hèphaistos a faite a des portes"});
db.iliadfr.insert({ pg : 8755, line : "solides. C'est là que nous irons dormir, puisqu'il te plaît que"});
db.iliadfr.insert({ pg : 8756, line : "nous partagions le même lit."});
db.iliadfr.insert({ pg : 8758, line : "Et Zeus qui amasse les nuées lui répondit:"});
db.iliadfr.insert({ pg : 8760, line : "-- Ne crains pas qu'aucun dieu te voie, ni aucun homme. Je"});
db.iliadfr.insert({ pg : 8761, line : "t'envelopperai d'une nuée d'or, telle que Hélios lui-même ne la"});
db.iliadfr.insert({ pg : 8762, line : "pénétrerait pas, bien que rien n'échappe à sa lumière."});
db.iliadfr.insert({ pg : 8764, line : "Et le fils de Kronos prit l'Épouse dans ses bras. Et sous eux la"});
db.iliadfr.insert({ pg : 8765, line : "terre divine enfanta une herbe nouvelle, le lotos brillant de"});
db.iliadfr.insert({ pg : 8766, line : "rosée, et le safran, et l'hyacinthe épaisse et molle, qui les"});
db.iliadfr.insert({ pg : 8767, line : "soulevaient de terre. Et ils s'endormirent, et une belle nuée d'or"});
db.iliadfr.insert({ pg : 8768, line : "les enveloppait, et d'étincelantes rosées en tombaient."});
db.iliadfr.insert({ pg : 8770, line : "Ainsi dormait, tranquille, le père Zeus sur le haut Gargaros,"});
db.iliadfr.insert({ pg : 8771, line : "dompté par le sommeil et par l'amour, en tenant l'Épouse dans ses"});
db.iliadfr.insert({ pg : 8772, line : "bras. Et le doux Hypnos courut aux nefs des Akhaiens en porter la"});
db.iliadfr.insert({ pg : 8773, line : "nouvelle à celui qui ébranle la terre, et il lui dit en paroles"});
db.iliadfr.insert({ pg : 8774, line : "ailées:"});
db.iliadfr.insert({ pg : 8776, line : "-- Hâte-toi, Poseidaôn, de venir en aide aux Akhaiens, et donne-"});
db.iliadfr.insert({ pg : 8777, line : "leur la victoire au moins quelques instants, pendant que Zeus"});
db.iliadfr.insert({ pg : 8778, line : "dort, car je l'ai assoupi mollement, et Hèrè l'a séduit par"});
db.iliadfr.insert({ pg : 8779, line : "l'amour, afin qu'il s'endormît."});
db.iliadfr.insert({ pg : 8781, line : "Il parla ainsi et retourna vers les illustres tribus des hommes;"});
db.iliadfr.insert({ pg : 8782, line : "mais il excita plus encore Poseidaôn à secourir les Danaens, et"});
db.iliadfr.insert({ pg : 8783, line : "Poseidaôn, s'élançant aux premiers rangs, s'écria:"});
db.iliadfr.insert({ pg : 8785, line : "-- Argiens! laisserons-nous de nouveau la victoire au Priamide"});
db.iliadfr.insert({ pg : 8786, line : "Hektôr, afin qu'il prenne les nefs et se glorifie? Il triomphe,"});
db.iliadfr.insert({ pg : 8787, line : "parce que Akhilleus reste, le coeur irrité, dans ses nefs creuses;"});
db.iliadfr.insert({ pg : 8788, line : "mais nous n'aurons plus un si grand regret d'Akhilleus, si nous"});
db.iliadfr.insert({ pg : 8789, line : "savons nous défendre les uns les autres. Allons! obéissez-moi"});
db.iliadfr.insert({ pg : 8790, line : "tous. Couverts de nos meilleurs et de nos plus grands boucliers,"});
db.iliadfr.insert({ pg : 8791, line : "les casques éclatants en tête et les longues piques en main,"});
db.iliadfr.insert({ pg : 8792, line : "allons! Et je vous conduirai, et je ne pense pas que le Priamide"});
db.iliadfr.insert({ pg : 8793, line : "Hektôr nous attende, bien qu'il soit plein d'audace. Que les plus"});
db.iliadfr.insert({ pg : 8794, line : "braves cèdent leurs boucliers légers, s'ils en ont de tels, aux"});
db.iliadfr.insert({ pg : 8795, line : "guerriers plus faibles, et qu'ils s'abritent sous de plus grands!"});
db.iliadfr.insert({ pg : 8797, line : "Il parla ainsi, et chacun obéit. Et les rois eux-mêmes, quoique"});
db.iliadfr.insert({ pg : 8798, line : "blessés, rangèrent les lignes. Le Tydéide, Odysseus et l'Atréide"});
db.iliadfr.insert({ pg : 8799, line : "Agamemnôn, parcourant les rangs, échangeaient les armes, donnant"});
db.iliadfr.insert({ pg : 8800, line : "les plus fortes aux plus robustes, et les plus faibles aux moins"});
db.iliadfr.insert({ pg : 8801, line : "vigoureux. Et tous s'avancèrent, revêtus de l'airain éclatant, et"});
db.iliadfr.insert({ pg : 8802, line : "celui qui ébranle la terre les précédait, tenant dans sa forte"});
db.iliadfr.insert({ pg : 8803, line : "main une longue et terrible épée, semblable à l'éclair, telle"});
db.iliadfr.insert({ pg : 8804, line : "qu'on ne peut l'affronter dans la mêlée lamentable, et qui pénètre"});
db.iliadfr.insert({ pg : 8805, line : "les hommes de terreur."});
db.iliadfr.insert({ pg : 8807, line : "Et l'illustre Hektôr, de son côté, rangeait les Troiens en"});
db.iliadfr.insert({ pg : 8808, line : "bataille. Et tous deux préparaient une lutte horrible, Poseidaôn à"});
db.iliadfr.insert({ pg : 8809, line : "la chevelure bleue et l'illustre Hektôr, celui-ci secourant les"});
db.iliadfr.insert({ pg : 8810, line : "Troiens et celui-là les Akhaiens. Et la mer inondait la plage"});
db.iliadfr.insert({ pg : 8811, line : "jusqu'aux tentes et aux nefs, et les deux peuples se heurtaient"});
db.iliadfr.insert({ pg : 8812, line : "avec une grande clameur; mais ni l'eau de la mer qui roule sur le"});
db.iliadfr.insert({ pg : 8813, line : "rivage, poussée par le souffle furieux de Boréas, ni le"});
db.iliadfr.insert({ pg : 8814, line : "crépitement d'un vaste incendie qui brûle une forêt, dans les"});
db.iliadfr.insert({ pg : 8815, line : "gorges des montagnes, ni le vent qui rugit dans les grands chênes,"});
db.iliadfr.insert({ pg : 8816, line : "ne sont aussi terribles que n'était immense la clameur des"});
db.iliadfr.insert({ pg : 8817, line : "Akhaiens et des Troiens, se ruant les uns sur les autres."});
db.iliadfr.insert({ pg : 8819, line : "Et, le premier, l'illustre Hektôr lança sa pique contre Aias qui"});
db.iliadfr.insert({ pg : 8820, line : "s'était retourné sur lui, et il ne le manqua pas, car la pique"});
db.iliadfr.insert({ pg : 8821, line : "frappa la poitrine là où les deux baudriers se croisent, celui du"});
db.iliadfr.insert({ pg : 8822, line : "bouclier et celui de l'épée aux clous d'argent; et ils"});
db.iliadfr.insert({ pg : 8823, line : "préservèrent la chair délicate. Hektôr fut affligé qu'un trait"});
db.iliadfr.insert({ pg : 8824, line : "rapide se fût vainement échappé de sa main; et, fuyant la mort, il"});
db.iliadfr.insert({ pg : 8825, line : "se retira dans la foule de ses compagnons. Mais, comme il se"});
db.iliadfr.insert({ pg : 8826, line : "retirait, le grand Télamônien Aias saisit une des roches qui"});
db.iliadfr.insert({ pg : 8827, line : "retenaient les câbles des nefs, et qui se rencontraient sous les"});
db.iliadfr.insert({ pg : 8828, line : "pieds des combattants, et il en frappa Hektôr dans la poitrine,"});
db.iliadfr.insert({ pg : 8829, line : "au-dessus du bouclier, près du cou, après l'avoir soulevée et"});
db.iliadfr.insert({ pg : 8830, line : "l'avoir fait tourbillonner. De même qu'un chêne tombe, déraciné"});
db.iliadfr.insert({ pg : 8831, line : "par l'éclair du grand Zeus, et que l'odeur du soufre s'en exhale,"});
db.iliadfr.insert({ pg : 8832, line : "et que chacun s'en épouvante, tant est terrible la foudre du grand"});
db.iliadfr.insert({ pg : 8833, line : "Zeus; de même la force de Hektôr tomba dans la poussière. Et sa"});
db.iliadfr.insert({ pg : 8834, line : "pique échappa de sa main, et son casque tomba, et son bouclier"});
db.iliadfr.insert({ pg : 8835, line : "aussi, et toutes ses armes d'airain résonnèrent."});
db.iliadfr.insert({ pg : 8837, line : "Et les fils des Akhaiens accoururent avec de grands cris, espérant"});
db.iliadfr.insert({ pg : 8838, line : "l'entraîner, et ils lancèrent d'innombrables traits; mais aucun ne"});
db.iliadfr.insert({ pg : 8839, line : "put blesser le prince des peuples, car les plus braves le"});
db.iliadfr.insert({ pg : 8840, line : "protégèrent aussitôt: Polydamas, Ainéias, et le divin Agènôr, et"});
db.iliadfr.insert({ pg : 8841, line : "Sarpèdôn, le chef des Lykiens, et l'irréprochable Glaukos. Aucun"});
db.iliadfr.insert({ pg : 8842, line : "ne négligea de le secourir, et tous tenaient devant lui leurs"});
db.iliadfr.insert({ pg : 8843, line : "boucliers bombés. Et ses compagnons l'emportèrent dans leurs bras,"});
db.iliadfr.insert({ pg : 8844, line : "loin de la mêlée, jusqu'à l'endroit où se tenaient ses chevaux"});
db.iliadfr.insert({ pg : 8845, line : "rapides, et son char, et leur conducteur. Et ils l'emportèrent"});
db.iliadfr.insert({ pg : 8846, line : "vers la ville, poussant des gémissements. Et quand ils furent"});
db.iliadfr.insert({ pg : 8847, line : "parvenus au gué du Xanthos tourbillonnant qu'engendra l'immortel"});
db.iliadfr.insert({ pg : 8848, line : "Zeus, ils le déposèrent du char sur la terre, et ils le"});
db.iliadfr.insert({ pg : 8849, line : "baignèrent, et, revenant à lui, il ouvrit les yeux. Mais, tombant"});
db.iliadfr.insert({ pg : 8850, line : "à genoux, il vomit un sang noir, et, de nouveau, il se renversa"});
db.iliadfr.insert({ pg : 8851, line : "contre terre, et une nuit noire l'enveloppa, tant le coup d'Aias"});
db.iliadfr.insert({ pg : 8852, line : "l'avait dompté."});
db.iliadfr.insert({ pg : 8854, line : "Les Argiens, voyant qu'on enlevait Hektôr, se ruèrent avec plus"});
db.iliadfr.insert({ pg : 8855, line : "d'ardeur sur les Troiens et ne songèrent qu'à combattre. Le"});
db.iliadfr.insert({ pg : 8856, line : "premier, le fils d'Oileus, le rapide Aias, de sa lance aiguë, en"});
db.iliadfr.insert({ pg : 8857, line : "bondissant, blessa ios Énopide, que l'irréprochable nymphe Nèis"});
db.iliadfr.insert({ pg : 8858, line : "enfanta d'Énops qui paissait ses troupeaux sur les rives du"});
db.iliadfr.insert({ pg : 8859, line : "Satnioïs. Et l'illustre Oilèiade le blessa de sa lance dans le"});
db.iliadfr.insert({ pg : 8860, line : "ventre, et il tomba à la renverse, et, autour de lui, les Troiens"});
db.iliadfr.insert({ pg : 8861, line : "et les Danaens engagèrent une lutte terrible. Et le Panthoide"});
db.iliadfr.insert({ pg : 8862, line : "Polydamas vint le venger, et il frappa Prothoènôr Arèilykide à"});
db.iliadfr.insert({ pg : 8863, line : "l'épaule droite, et la forte lance entra dans l'épaule. Prothoènôr"});
db.iliadfr.insert({ pg : 8864, line : "renversé saisit la poussière avec ses mains, et Polydamas s'écria"});
db.iliadfr.insert({ pg : 8865, line : "insolemment:"});
db.iliadfr.insert({ pg : 8867, line : "-- Je ne pense pas qu'un trait inutile soit parti de la main du"});
db.iliadfr.insert({ pg : 8868, line : "magnanime Panthoide. Un Argien l'a reçu dans le corps, et il"});
db.iliadfr.insert({ pg : 8869, line : "s'appuiera dessus pour descendre dans les demeures d'Aidès."});
db.iliadfr.insert({ pg : 8871, line : "Il parla ainsi, et les Argiens furent remplis de douleur en"});
db.iliadfr.insert({ pg : 8872, line : "l'entendant se glorifier ainsi. Et le belliqueux Télamônien Aias"});
db.iliadfr.insert({ pg : 8873, line : "fut troublé, ayant vu Prothoènôr tomber auprès de lui. Et aussitôt"});
db.iliadfr.insert({ pg : 8874, line : "il lança sa pique contre Polydamas qui se retirait; mais celui-ci"});
db.iliadfr.insert({ pg : 8875, line : "évita la mort en sautant de côté, et l'Anténoride Arkhélokhos"});
db.iliadfr.insert({ pg : 8876, line : "reçut le coup, car les dieux lui destinaient la mort. Et il fut"});
db.iliadfr.insert({ pg : 8877, line : "frappé à la dernière vertèbre du cou, et les deux muscles furent"});
db.iliadfr.insert({ pg : 8878, line : "tranchés, et sa tête, sa bouche et ses narines touchèrent la terre"});
db.iliadfr.insert({ pg : 8879, line : "avant ses genoux."});
db.iliadfr.insert({ pg : 8881, line : "Et Aias cria à l'irréprochable Polydamas:"});
db.iliadfr.insert({ pg : 8883, line : "-- Vois, Polydamas, et dis la vérité. Ce guerrier mort ne suffit-"});
db.iliadfr.insert({ pg : 8884, line : "il pas pour venger Prothoènôr? Il ne me semble ni lâche, ni d'une"});
db.iliadfr.insert({ pg : 8885, line : "race vile. C'est le frère du dompteur de chevaux Antènôr, ou son"});
db.iliadfr.insert({ pg : 8886, line : "fils, car il a le visage de cette famille."});
db.iliadfr.insert({ pg : 8888, line : "Et il parla ainsi, le connaissant bien. Et la douleur saisit les"});
db.iliadfr.insert({ pg : 8889, line : "Troiens. Alors, Akamas, debout devant son frère mort, blessa d'un"});
db.iliadfr.insert({ pg : 8890, line : "coup de lance le Boiôtien Promakhos, comme celui-ci traînait le"});
db.iliadfr.insert({ pg : 8891, line : "cadavre par les pieds. Et Akamas, triomphant, cria:"});
db.iliadfr.insert({ pg : 8893, line : "-- Argiens destinés à la mort, et toujours prodigues de menaces,"});
db.iliadfr.insert({ pg : 8894, line : "la lutte et le deuil ne seront pas pour nous seuls, et vous aussi"});
db.iliadfr.insert({ pg : 8895, line : "vous mourrez! Voyez! votre Promakhos dort dompté par ma lance, et"});
db.iliadfr.insert({ pg : 8896, line : "mon frère n'est pas resté longtemps sans vengeance; aussi, tout"});
db.iliadfr.insert({ pg : 8897, line : "homme souhaite de laisser dans ses demeures un frère qui le venge."});
db.iliadfr.insert({ pg : 8899, line : "Il parla ainsi, et ses paroles insultantes remplirent les Argiens"});
db.iliadfr.insert({ pg : 8900, line : "de douleur, et elles irritèrent surtout l'âme de Pénéléôs qui se"});
db.iliadfr.insert({ pg : 8901, line : "rua sur Akamas. Mais celui-ci n'osa pas soutenir le choc du roi"});
db.iliadfr.insert({ pg : 8902, line : "Pénéléôs qui blessa Ilioneus, fils de ce Phorbas, riche en"});
db.iliadfr.insert({ pg : 8903, line : "troupeaux, que Hermès aimait entre tous les Troiens, et à qui il"});
db.iliadfr.insert({ pg : 8904, line : "avait donné de grands biens. Et il le frappa sous le sourcil, au"});
db.iliadfr.insert({ pg : 8905, line : "fond de l'oeil, d'où la pupille fut arrachée. Et la lance,"});
db.iliadfr.insert({ pg : 8906, line : "traversant l'oeil, sortit derrière la tête, et Ilioneus, les mains"});
db.iliadfr.insert({ pg : 8907, line : "étendues, tomba. Puis, Pénéléôs, tirant de la gaîne son épée"});
db.iliadfr.insert({ pg : 8908, line : "aiguë, coupa la tête qui roula sur la terre avec le casque, et la"});
db.iliadfr.insert({ pg : 8909, line : "forte lance encore fixée dans l'oeil. Et Pénéléôs la saisit, et,"});
db.iliadfr.insert({ pg : 8910, line : "la montrant aux Troiens, il leur cria:"});
db.iliadfr.insert({ pg : 8912, line : "-- Allez de ma part, Troiens, dire au père et à la mère de"});
db.iliadfr.insert({ pg : 8913, line : "l'illustre Ilioneus qu'ils gémissent dans leurs demeures. Ah!"});
db.iliadfr.insert({ pg : 8914, line : "l'épouse de l'Alégénoride Promakhos ne se réjouira pas non plus au"});
db.iliadfr.insert({ pg : 8915, line : "retour de son époux bien-aimé, quand les fils des Akhaiens, loin"});
db.iliadfr.insert({ pg : 8916, line : "de Troiè, s'en retourneront sur leurs nefs!"});
db.iliadfr.insert({ pg : 8918, line : "Il parla ainsi, et la pâle terreur saisit les Troiens, et chacun"});
db.iliadfr.insert({ pg : 8919, line : "d'eux regardait autour de lui, cherchant comment il éviterait la"});
db.iliadfr.insert({ pg : 8920, line : "mort."});
db.iliadfr.insert({ pg : 8922, line : "Dites-moi maintenant, Muses qui habitez les demeures Olympiennes,"});
db.iliadfr.insert({ pg : 8923, line : "celui des Akhaiens qui enleva le premier des dépouilles"});
db.iliadfr.insert({ pg : 8924, line : "sanglantes, quand l'illustre qui ébranle la terre eut fait pencher"});
db.iliadfr.insert({ pg : 8925, line : "la victoire?"});
db.iliadfr.insert({ pg : 8927, line : "Le premier, Aias Télamônien frappa Hyrthios Gyrtiade, chef des"});
db.iliadfr.insert({ pg : 8928, line : "braves Mysiens. Et Antilokhos tua Phalkès et Merméros, et Mèrionès"});
db.iliadfr.insert({ pg : 8929, line : "tua Morys et Hippotiôn, et Teukros tua Prothoôn et Périphètès, et"});
db.iliadfr.insert({ pg : 8930, line : "l'Atréide Ménélaos blessa au côté le prince des peuples Hypérénôr."});
db.iliadfr.insert({ pg : 8931, line : "Il lui déchira les intestins, et l'âme s'échappa par l'horrible"});
db.iliadfr.insert({ pg : 8932, line : "blessure, et un brouillard couvrit ses yeux. Mais Aias, l'agile"});
db.iliadfr.insert({ pg : 8933, line : "fils d'Oileus, en tua bien plus encore, car nul n'était son égal"});
db.iliadfr.insert({ pg : 8934, line : "pour atteindre ceux que Zeus met en fuite."});
db.iliadfr.insert({ pg : 8937, line : "Chant 15"});
db.iliadfr.insert({ pg : 8939, line : "Les Troiens franchissaient, dans leur fuite, les pieux et le"});
db.iliadfr.insert({ pg : 8940, line : "fossé, et beaucoup tombaient sous les mains des Danaens. Et ils"});
db.iliadfr.insert({ pg : 8941, line : "s'arrêtèrent auprès de leurs chars, pâles de terreur."});
db.iliadfr.insert({ pg : 8943, line : "Mais Zeus s'éveilla, sur les sommets de l'Ida, auprès de Hèrè au"});
db.iliadfr.insert({ pg : 8944, line : "trône d'or. Et, se levant, il regarda et vit les Troiens et les"});
db.iliadfr.insert({ pg : 8945, line : "Akhaiens, et les premiers en pleine déroute, et les Argiens, ayant"});
db.iliadfr.insert({ pg : 8946, line : "au milieu d'eux le roi Poseidaôn, les poussant avec fureur. Et il"});
db.iliadfr.insert({ pg : 8947, line : "vit Hektôr gisant dans la plaine, entouré de ses compagnons,"});
db.iliadfr.insert({ pg : 8948, line : "respirant à peine et vomissant le sang, car ce n'était pas le plus"});
db.iliadfr.insert({ pg : 8949, line : "faible des Akhaiens qui l'avait blessé."});
db.iliadfr.insert({ pg : 8951, line : "Et le père des hommes et des dieux fut rempli de pitié en le"});
db.iliadfr.insert({ pg : 8952, line : "voyant, et, avec un regard sombre, il dit à Hèrè:"});
db.iliadfr.insert({ pg : 8954, line : "-- Ô astucieuse! ta ruse a éloigné le divin Hektôr du combat et"});
db.iliadfr.insert({ pg : 8955, line : "mis ses troupes en fuite. Je ne sais si tu ne recueilleras pas la"});
db.iliadfr.insert({ pg : 8956, line : "première le fruit de tes ruses, et si je ne t'accablerai point de"});
db.iliadfr.insert({ pg : 8957, line : "coups. Ne te souvient-il plus du jour où tu étais suspendue dans"});
db.iliadfr.insert({ pg : 8958, line : "l'air, avec une enclume à chaque pied, les mains liées d'une"});
db.iliadfr.insert({ pg : 8959, line : "solide chaîne d'or, et où tu pendais ainsi de l'aithèr et des"});
db.iliadfr.insert({ pg : 8960, line : "nuées? Tous les dieux, par le grand Olympos, te regardaient avec"});
db.iliadfr.insert({ pg : 8961, line : "douleur et ne pouvaient te secourir, car celui que j'aurais saisi,"});
db.iliadfr.insert({ pg : 8962, line : "je l'aurais précipité de l'Ouranos, et il serait arrivé sur la"});
db.iliadfr.insert({ pg : 8963, line : "terre, respirant à peine. Et cependant ma colère, à cause des"});
db.iliadfr.insert({ pg : 8964, line : "souffrances du divin Hèraklès, n'était point assouvie. C'était toi"});
db.iliadfr.insert({ pg : 8965, line : "qui, l'accablant de maux, avais appelé Boréas et les tempêtes sur"});
db.iliadfr.insert({ pg : 8966, line : "la mer stérile, et qui l'avais rejeté vers Koôs bien peuplée. Mais"});
db.iliadfr.insert({ pg : 8967, line : "je le délivrai et le ramenai dans Argos féconde en chevaux."});
db.iliadfr.insert({ pg : 8968, line : "Souviens-toi de ces choses et renonce à tes ruses, et sache qu'il"});
db.iliadfr.insert({ pg : 8969, line : "ne te suffit pas, pour me tromper, de te donner à moi sur ce lit,"});
db.iliadfr.insert({ pg : 8970, line : "loin des dieux."});
db.iliadfr.insert({ pg : 8972, line : "Il parla ainsi, et la vénérable Hèrè frissonna et lui répondit en"});
db.iliadfr.insert({ pg : 8973, line : "paroles ailées:"});
db.iliadfr.insert({ pg : 8975, line : "-- Que Gaia le sache, et le large Ouranos, et l'eau souterraine de"});
db.iliadfr.insert({ pg : 8976, line : "Styx, ce qui est le plus grand serment des dieux heureux, et ta"});
db.iliadfr.insert({ pg : 8977, line : "tête sacrée, et notre lit nuptial que je n'attesterai jamais en"});
db.iliadfr.insert({ pg : 8978, line : "vain! Ce n'est point par mon conseil que Poseidaôn qui ébranle la"});
db.iliadfr.insert({ pg : 8979, line : "terre a dompté les Troiens et Hektôr. Son coeur seul l'a poussé,"});
db.iliadfr.insert({ pg : 8980, line : "ayant compassion des Akhaiens désespérés autour de leurs nefs."});
db.iliadfr.insert({ pg : 8981, line : "Mais j'irai et je lui conseillerai, ô Zeus qui amasses les noires"});
db.iliadfr.insert({ pg : 8982, line : "nuées, de se retirer où tu le voudras."});
db.iliadfr.insert({ pg : 8984, line : "Elle parla ainsi, et le père des dieux et des hommes sourit, et"});
db.iliadfr.insert({ pg : 8985, line : "lui répondit ces paroles ailées:"});
db.iliadfr.insert({ pg : 8987, line : "-- Si tu penses comme moi, étant assise au milieu des immortels, ô"});
db.iliadfr.insert({ pg : 8988, line : "vénérable Hèrè aux yeux de boeuf, Poseidaôn lui-même, quoi qu'il"});
db.iliadfr.insert({ pg : 8989, line : "veuille, se conformera aussitôt à notre volonté. Si tu as dit la"});
db.iliadfr.insert({ pg : 8990, line : "vérité dans ton coeur, va dans l'assemblée des dieux, appelle Iris"});
db.iliadfr.insert({ pg : 8991, line : "et l'illustre archer Apollôn, afin que l'une aille, vers l'armée"});
db.iliadfr.insert({ pg : 8992, line : "des Akhaiens cuirassés, dire au roi Poseidaôn qu'il se retire de"});
db.iliadfr.insert({ pg : 8993, line : "la mêlée, et qu'il rentre dans ses demeures; et que Phoibos"});
db.iliadfr.insert({ pg : 8994, line : "Apollôn ranime les forces de Hektôr et apaise les douleurs qui"});
db.iliadfr.insert({ pg : 8995, line : "l'accablent, afin que le Priamide attaque de nouveau les Akhaiens"});
db.iliadfr.insert({ pg : 8996, line : "et les mette en fuite. Et ils fuiront jusqu'aux nefs du Pèléide"});
db.iliadfr.insert({ pg : 8997, line : "Akhilleus qui suscitera son compagnon Patroklos. Et l'illustre"});
db.iliadfr.insert({ pg : 8998, line : "Hektôr tuera Patroklos devant Ilios, là où celui-ci aura dompté"});
db.iliadfr.insert({ pg : 8999, line : "une multitude de guerriers, et, entre autres, mon fils, le divin"});
db.iliadfr.insert({ pg : 9000, line : "Sarpèdôn. Et le divin Akhilleus, furieux, tuera Hektôr. Et,"});
db.iliadfr.insert({ pg : 9001, line : "désormais, je repousserai toujours les Troiens loin des nefs,"});
db.iliadfr.insert({ pg : 9002, line : "jusqu'au jour où les Akhaiens prendront la haute Ilios par les"});
db.iliadfr.insert({ pg : 9003, line : "conseils d'Athènè. Mais je ne déposerai point ma colère, et je ne"});
db.iliadfr.insert({ pg : 9004, line : "permettrai à aucun des immortels de secourir les Danaens, tant que"});
db.iliadfr.insert({ pg : 9005, line : "ne seront point accomplis et le désir du Pèléide et la promesse"});
db.iliadfr.insert({ pg : 9006, line : "que j'ai faite par un signe de ma tête, le jour où la déesse"});
db.iliadfr.insert({ pg : 9007, line : "Thétis, embrassant mes genoux, m'a supplié d'honorer Akhilleus, le"});
db.iliadfr.insert({ pg : 9008, line : "dévastateur de citadelles."});
db.iliadfr.insert({ pg : 9010, line : "Il parla ainsi, et la déesse Hèrè aux bras blancs se hâta de"});
db.iliadfr.insert({ pg : 9011, line : "monter des cimes de l'Ida dans le haut Olympos. Ainsi vole la"});
db.iliadfr.insert({ pg : 9012, line : "pensée d'un homme qui, ayant parcouru de nombreuses contrées et se"});
db.iliadfr.insert({ pg : 9013, line : "souvenant de ce qu'il a vu, se dit: J'étais là! La vénérable Hèrè"});
db.iliadfr.insert({ pg : 9014, line : "vola aussi promptement, et elle arriva dans l'assemblée des dieux,"});
db.iliadfr.insert({ pg : 9015, line : "sur le haut Olympos où sont les demeures de Zeus. Et tous se"});
db.iliadfr.insert({ pg : 9016, line : "levèrent en la voyant, et lui offrirent la coupe qu'elle reçut de"});
db.iliadfr.insert({ pg : 9017, line : "Thémis aux belles joues, car celle-ci était venue la première au-"});
db.iliadfr.insert({ pg : 9018, line : "devant d'elle et lui avait dit en paroles ailées:"});
db.iliadfr.insert({ pg : 9020, line : "-- Hèrè, pourquoi viens-tu, toute troublée? Est-ce le fils de"});
db.iliadfr.insert({ pg : 9021, line : "Kronos, ton époux, qui t'a effrayée?"});
db.iliadfr.insert({ pg : 9023, line : "Et la déesse Hèrè aux bras blancs lui répondit:"});
db.iliadfr.insert({ pg : 9025, line : "-- Divine Thémis, ne m'interroge point. Tu sais combien son âme"});
db.iliadfr.insert({ pg : 9026, line : "est orgueilleuse et dure. Préside le festin des dieux dans ces"});
db.iliadfr.insert({ pg : 9027, line : "demeures. Tu sauras avec tous les immortels les desseins fatals de"});
db.iliadfr.insert({ pg : 9028, line : "Zeus. Je ne pense pas que ni les hommes, ni les dieux puissent se"});
db.iliadfr.insert({ pg : 9029, line : "réjouir désormais dans leurs festins."});
db.iliadfr.insert({ pg : 9031, line : "La vénérable Hèrè parla et s'assit. Et les dieux s'attristèrent"});
db.iliadfr.insert({ pg : 9032, line : "dans les demeures de Zeus; mais la fille de Kronos sourit"});
db.iliadfr.insert({ pg : 9033, line : "amèrement, tandis que son front était sombre au-dessus de ses"});
db.iliadfr.insert({ pg : 9034, line : "sourcils bleus; et elle dit indignée:"});
db.iliadfr.insert({ pg : 9036, line : "-- Insensés que nous sommes nous nous irritons contre Zeus et nous"});
db.iliadfr.insert({ pg : 9037, line : "voulons le dompter, soit par la flatterie, soit par la violence;"});
db.iliadfr.insert({ pg : 9038, line : "et, assis à l'écart, il ne s'en soucie ni ne s'en émeut, sachant"});
db.iliadfr.insert({ pg : 9039, line : "qu'il l'emporte sur tous les dieux immortels par la force et la"});
db.iliadfr.insert({ pg : 9040, line : "puissance. Subissez donc les maux qu'il lui plaît d'envoyer à"});
db.iliadfr.insert({ pg : 9041, line : "chacun de vous. Déjà le malheur atteint Arès; son fils a péri dans"});
db.iliadfr.insert({ pg : 9042, line : "la mêlée, Askalaphos, celui de tous les hommes qu'il aimait le"});
db.iliadfr.insert({ pg : 9043, line : "mieux, et que le puissant Arès disait être son fils."});
db.iliadfr.insert({ pg : 9045, line : "Elle parla ainsi, et Arès, frappant de ses deux mains ses cuisses"});
db.iliadfr.insert({ pg : 9046, line : "vigoureuses, dit en gémissant:"});
db.iliadfr.insert({ pg : 9048, line : "-- Ne vous irritez point, habitants des demeures Olympiennes, si"});
db.iliadfr.insert({ pg : 9049, line : "je descends aux nefs des Akhaiens pour venger le meurtre de mon"});
db.iliadfr.insert({ pg : 9050, line : "fils, quand même ma destinée serait de tomber parmi les morts, le"});
db.iliadfr.insert({ pg : 9051, line : "sang et la poussière, frappé de l'éclair de Zeus!"});
db.iliadfr.insert({ pg : 9053, line : "Il parla ainsi, et il ordonna à la Crainte et à la Fuite d'atteler"});
db.iliadfr.insert({ pg : 9054, line : "ses chevaux, et il se couvrit de ses armes splendides. Et, alors,"});
db.iliadfr.insert({ pg : 9055, line : "une colère bien plus grande et bien plus terrible se fût soulevée"});
db.iliadfr.insert({ pg : 9056, line : "dans l'âme de Zeus contre les immortels, si Athènè, craignant pour"});
db.iliadfr.insert({ pg : 9057, line : "tous les dieux, n'eût sauté dans le parvis, hors du trône où elle"});
db.iliadfr.insert({ pg : 9058, line : "était assise. Et elle arracha le casque de la tête d'Arès, et le"});
db.iliadfr.insert({ pg : 9059, line : "bouclier de ses épaules et la lance d'airain de sa main robuste,"});
db.iliadfr.insert({ pg : 9060, line : "et elle réprimanda l'impétueux Arès:"});
db.iliadfr.insert({ pg : 9062, line : "-- Insensé! tu perds l'esprit et tu vas périr. As-tu des oreilles"});
db.iliadfr.insert({ pg : 9063, line : "pour ne point entendre? N'as-tu plus ni intelligence, ni pudeur?"});
db.iliadfr.insert({ pg : 9064, line : "N'as-tu point écouté les paroles de la déesse Hèrè aux bras blancs"});
db.iliadfr.insert({ pg : 9065, line : "que Zeus a envoyée dans l'Olympos? Veux-tu, toi-même, frappé de"});
db.iliadfr.insert({ pg : 9066, line : "mille maux, revenir, accablé et gémissant, après avoir attiré des"});
db.iliadfr.insert({ pg : 9067, line : "calamités sur les autres dieux? Zeus laissera aussitôt les Troiens"});
db.iliadfr.insert({ pg : 9068, line : "et les Akhaiens magnanimes, et il viendra nous précipiter de"});
db.iliadfr.insert({ pg : 9069, line : "l'Olympos, innocents ou coupables. Je t'ordonne d'apaiser la"});
db.iliadfr.insert({ pg : 9070, line : "colère du meurtre de ton fils. Déjà de plus braves et de plus"});
db.iliadfr.insert({ pg : 9071, line : "vigoureux que lui sont morts, ou seront tués. Il est difficile de"});
db.iliadfr.insert({ pg : 9072, line : "sauver de la mort les générations des hommes."});
db.iliadfr.insert({ pg : 9074, line : "Ayant ainsi parlé, elle fit asseoir l'impétueux Arès sur son"});
db.iliadfr.insert({ pg : 9075, line : "trône. Puis, Hèrè appela, hors de l'Olympos, Apollôn et Iris, qui"});
db.iliadfr.insert({ pg : 9076, line : "est la messagère de tous les dieux immortels, et elle leur dit en"});
db.iliadfr.insert({ pg : 9077, line : "paroles ailées:"});
db.iliadfr.insert({ pg : 9079, line : "-- Zeus vous ordonne de venir promptement sur l'Ida, et, quand"});
db.iliadfr.insert({ pg : 9080, line : "vous l'aurez vu, faites ce qu'il vous ordonnera."});
db.iliadfr.insert({ pg : 9082, line : "Ayant ainsi parlé, la vénérable Hèrè rentra et s'assit sur son"});
db.iliadfr.insert({ pg : 9083, line : "trône. Et les deux immortels s'envolèrent à la hâte, et ils"});
db.iliadfr.insert({ pg : 9084, line : "arrivèrent sur l'Ida où naissent les sources et les bêtes fauves."});
db.iliadfr.insert({ pg : 9085, line : "Et ils virent Zeus au large regard assis sur le faîte du Gargaros,"});
db.iliadfr.insert({ pg : 9086, line : "et il s'était enveloppé d'une nuée parfumée. Et ils s'arrêtèrent"});
db.iliadfr.insert({ pg : 9087, line : "devant Zeus qui amasse les nuées. Et, satisfait, dans son esprit,"});
db.iliadfr.insert({ pg : 9088, line : "qu'ils eussent obéi promptement aux ordres de l'épouse bien-aimée,"});
db.iliadfr.insert({ pg : 9089, line : "il dit d'abord en paroles ailées à Iris:"});
db.iliadfr.insert({ pg : 9091, line : "-- Va! rapide Iris, parle au roi Poseidaôn, et sois une messagère"});
db.iliadfr.insert({ pg : 9092, line : "fidèle. Dis-lui qu'il se retire de la mêlée, et qu'il reste, soit"});
db.iliadfr.insert({ pg : 9093, line : "dans l'assemblée des dieux, soit dans la mer divine. Mais s'il"});
db.iliadfr.insert({ pg : 9094, line : "n'obéissait pas à mes ordres et s'il les méprisait, qu'il délibère"});
db.iliadfr.insert({ pg : 9095, line : "et réfléchisse dans son esprit. Malgré sa vigueur, il ne pourra"});
db.iliadfr.insert({ pg : 9096, line : "soutenir mon attaque, car mes forces surpassent de beaucoup les"});
db.iliadfr.insert({ pg : 9097, line : "siennes, et je suis l'aîné. Qu'il craigne donc de se croire l'égal"});
db.iliadfr.insert({ pg : 9098, line : "de celui que tous les autres dieux redoutent."});
db.iliadfr.insert({ pg : 9100, line : "Il parla ainsi, et la rapide Iris aux pieds aériens descendit du"});
db.iliadfr.insert({ pg : 9101, line : "faîte des cimes Idaiennes, vers la sainte Ilios. Comme la neige"});
db.iliadfr.insert({ pg : 9102, line : "vole du milieu des nuées, ou la grêle chassée par le souffle"});
db.iliadfr.insert({ pg : 9103, line : "impétueux de Boréas, ainsi volait la rapide Iris; et, s'arrêtant"});
db.iliadfr.insert({ pg : 9104, line : "devant lui, elle dit à l'illustre qui ébranle la terre:"});
db.iliadfr.insert({ pg : 9106, line : "-- Poseidaôn aux cheveux bleus, je suis envoyée par Zeus"});
db.iliadfr.insert({ pg : 9107, line : "tempétueux. Il te commande de te retirer de la mêlée et de rester,"});
db.iliadfr.insert({ pg : 9108, line : "soit dans l'assemblée des dieux, soit dans la mer divine. Si tu"});
db.iliadfr.insert({ pg : 9109, line : "n'obéissais pas à ses ordres, et si tu les méprisais, il te menace"});
db.iliadfr.insert({ pg : 9110, line : "de venir te combattre, et il te conseille d'éviter son bras, car"});
db.iliadfr.insert({ pg : 9111, line : "ses forces sont de beaucoup supérieures aux tiennes, et il est"});
db.iliadfr.insert({ pg : 9112, line : "l'aîné. Il t'avertit de ne point te croire l'égal de celui que"});
db.iliadfr.insert({ pg : 9113, line : "tous les dieux redoutent."});
db.iliadfr.insert({ pg : 9115, line : "Et l'illustre qui ébranle la terre, indigné, lui répondit:"});
db.iliadfr.insert({ pg : 9117, line : "-- Ah! certes, bien qu'il soit grand, il parle avec orgueil, s'il"});
db.iliadfr.insert({ pg : 9118, line : "veut me réduire par la force, moi, son égal. Nous sommes trois"});
db.iliadfr.insert({ pg : 9119, line : "frères nés de Kronos, et qu'enfanta Rhéiè: Zeus, moi et Aidès qui"});
db.iliadfr.insert({ pg : 9120, line : "commande aux ombres. On fit trois parts du monde, et chacun de"});
db.iliadfr.insert({ pg : 9121, line : "nous reçut la sienne. Et le sort décida que j'habiterais toujours"});
db.iliadfr.insert({ pg : 9122, line : "la blanche mer, et Aidès eut les noires ténèbres, et Zeus eut le"});
db.iliadfr.insert({ pg : 9123, line : "large Ouranos, dans les nuées et dans l'aithèr. Mais le haut"});
db.iliadfr.insert({ pg : 9124, line : "Olympos et la terre furent communs à tous. C'est pourquoi je ne"});
db.iliadfr.insert({ pg : 9125, line : "ferai point la volonté de Zeus, bien qu'il soit puissant. Qu'il"});
db.iliadfr.insert({ pg : 9126, line : "garde tranquillement sa part; il ne m'épouvantera pas comme un"});
db.iliadfr.insert({ pg : 9127, line : "lâche. Qu'il menace à son gré les fils et les filles qu'il a"});
db.iliadfr.insert({ pg : 9128, line : "engendrés, puisque la nécessité les contraint de lui obéir."});
db.iliadfr.insert({ pg : 9130, line : "Et la rapide Iris aux pieds aériens lui répondit:"});
db.iliadfr.insert({ pg : 9132, line : "-- Poseidaôn aux cheveux bleus, me faut-il rapporter à Zeus cette"});
db.iliadfr.insert({ pg : 9133, line : "parole dure et hautaine? Ne changeras-tu point? L'esprit des sages"});
db.iliadfr.insert({ pg : 9134, line : "n'est point inflexible, et tu sais que les Érinnyes suivent les"});
db.iliadfr.insert({ pg : 9135, line : "aînés."});
db.iliadfr.insert({ pg : 9137, line : "Et Poseidaôn qui ébranle la terre lui répondit:"});
db.iliadfr.insert({ pg : 9139, line : "-- Déesse Iris, tu as bien parlé. Il est bon qu'un messager"});
db.iliadfr.insert({ pg : 9140, line : "possède la prudence; mais une amère douleur emplit mon esprit et"});
db.iliadfr.insert({ pg : 9141, line : "mon coeur quand Zeus veut, par des paroles violentes, réduire son"});
db.iliadfr.insert({ pg : 9142, line : "égal en honneurs et en droits. Je céderai, quoique indigné; mais"});
db.iliadfr.insert({ pg : 9143, line : "je te le dis, et je le menacerai de ceci: Si, malgré nous, -- moi,"});
db.iliadfr.insert({ pg : 9144, line : "la dévastatrice Athènè, Hèrè, Hermès et le roi Hèphaistos, -- il"});
db.iliadfr.insert({ pg : 9145, line : "épargne la haute Ilios et refuse de la détruire et de donner la"});
db.iliadfr.insert({ pg : 9146, line : "victoire aux Argiens, qu'il sache que notre haine sera inexorable."});
db.iliadfr.insert({ pg : 9148, line : "Ayant ainsi parlé, il laissa le peuple des Akhaiens et rentra dans"});
db.iliadfr.insert({ pg : 9149, line : "la mer. Et les héros Akhaiens le regrettaient. Et alors Zeus qui"});
db.iliadfr.insert({ pg : 9150, line : "amasse les nuées dit à Apollôn:"});
db.iliadfr.insert({ pg : 9152, line : "-- Va maintenant, cher Phoibos, vers Hektôr armé d'airain, car"});
db.iliadfr.insert({ pg : 9153, line : "voici que celui qui ébranle la terre est rentré dans la mer,"});
db.iliadfr.insert({ pg : 9154, line : "fuyant ma fureur. Certes, ils auraient entendu un combat terrible"});
db.iliadfr.insert({ pg : 9155, line : "les dieux souterrains qui vivent autour de Kronos; mais il vaut"});
db.iliadfr.insert({ pg : 9156, line : "mieux pour tous deux que, malgré sa colère, il ait évité mes"});
db.iliadfr.insert({ pg : 9157, line : "mains, car cette lutte aurait fait couler de grandes sueurs. Mais"});
db.iliadfr.insert({ pg : 9158, line : "toi, prends l'aigide aux franges d'or, afin d'épouvanter, en la"});
db.iliadfr.insert({ pg : 9159, line : "secouant, les héros Akhaiens. Archer, prends soin de l'illustre"});
db.iliadfr.insert({ pg : 9160, line : "Hektôr et remplis-le d'une grande force, pour qu'il chasse les"});
db.iliadfr.insert({ pg : 9161, line : "Akhaiens jusqu'aux nefs et jusqu'au Hellespontos; et je songerai"});
db.iliadfr.insert({ pg : 9162, line : "alors comment je permettrai aux Akhaiens de respirer."});
db.iliadfr.insert({ pg : 9164, line : "Il parla ainsi, et Apollôn se hâta d'obéir à son père. Et il"});
db.iliadfr.insert({ pg : 9165, line : "descendit du faîte de l'Ida, semblable à un épervier tueur de"});
db.iliadfr.insert({ pg : 9166, line : "colombes, et le plus impétueux des oiseaux. Et il trouva le divin"});
db.iliadfr.insert({ pg : 9167, line : "Hektôr, le fils du sage Priamos, non plus couché, mais assis, et"});
db.iliadfr.insert({ pg : 9168, line : "se ranimant, et reconnaissant ses compagnons autour de lui. Et le"});
db.iliadfr.insert({ pg : 9169, line : "râle et la sueur avaient disparu par la seule pensée de Zeus"});
db.iliadfr.insert({ pg : 9170, line : "tempétueux. Et Apollôn s'approcha et lui dit:"});
db.iliadfr.insert({ pg : 9172, line : "-- Hektôr, fils de Priamos, pourquoi rester assis, sans forces,"});
db.iliadfr.insert({ pg : 9173, line : "loin des tiens? Es-tu la proie de quelque douleur?"});
db.iliadfr.insert({ pg : 9175, line : "Et Hektôr au casque mouvant lui répondit d'une voix faible:"});
db.iliadfr.insert({ pg : 9177, line : "-- Qui es-tu, ô le meilleur des dieux, qui m'interroges ainsi? Ne"});
db.iliadfr.insert({ pg : 9178, line : "sais-tu pas qu'auprès des nefs Akhaiennes, tandis que je tuais ses"});
db.iliadfr.insert({ pg : 9179, line : "compagnons, le brave Aias m'a frappé d'un rocher dans la poitrine"});
db.iliadfr.insert({ pg : 9180, line : "et a rompu mes forces et mon courage?"});
db.iliadfr.insert({ pg : 9182, line : "Certes, j'ai cru voir aujourd'hui les morts et la demeure d'Aidès,"});
db.iliadfr.insert({ pg : 9183, line : "en rendant ma chère âme."});
db.iliadfr.insert({ pg : 9185, line : "Et le royal archer Apollôn lui répondit:"});
db.iliadfr.insert({ pg : 9187, line : "-- Prends courage! Du haut de l'Ida, le Kroniôn a envoyé pour te"});
db.iliadfr.insert({ pg : 9188, line : "secourir Phoibos Apollôn à l'épée d'or. Toi et ta haute citadelle,"});
db.iliadfr.insert({ pg : 9189, line : "je vous ai protégés et je vous protège toujours. Viens! excite les"});
db.iliadfr.insert({ pg : 9190, line : "cavaliers à pousser leurs chevaux rapides vers les nefs creuses,"});
db.iliadfr.insert({ pg : 9191, line : "et j'irai devant toi, et j'aplanirai la voie aux chevaux, et je"});
db.iliadfr.insert({ pg : 9192, line : "mettrai en fuite les héros Akhaiens."});
db.iliadfr.insert({ pg : 9194, line : "Ayant ainsi parlé, il remplit le prince des peuples d'une grande"});
db.iliadfr.insert({ pg : 9195, line : "force. Comme un étalon, longtemps retenu à la crèche et nourri"});
db.iliadfr.insert({ pg : 9196, line : "d'orge abondante, qui rompt son lien, et qui court, frappant la"});
db.iliadfr.insert({ pg : 9197, line : "terre de ses quatre pieds, se plonger dans le fleuve clair, et"});
db.iliadfr.insert({ pg : 9198, line : "qui, la tête haute, secouant ses crins sur ses épaules, fier de sa"});
db.iliadfr.insert({ pg : 9199, line : "beauté, bondit aisément jusqu'aux lieux accoutumés où paissent les"});
db.iliadfr.insert({ pg : 9200, line : "cavales; de même Hektôr, à la voix du dieu, courait de ses pieds"});
db.iliadfr.insert({ pg : 9201, line : "rapides, excitant les cavaliers. Comme des chiens et des"});
db.iliadfr.insert({ pg : 9202, line : "campagnards qui poursuivent un cerf rameux, ou une chèvre sauvage"});
db.iliadfr.insert({ pg : 9203, line : "qui se dérobe sous une roche creuse ou dans la forêt sombre, et"});
db.iliadfr.insert({ pg : 9204, line : "qu'ils ne peuvent atteindre, quand un lion à longue barbe,"});
db.iliadfr.insert({ pg : 9205, line : "survenant tout à coup à leurs cris, les disperse aussitôt malgré"});
db.iliadfr.insert({ pg : 9206, line : "leur impétuosité, de même les Danaens, poursuivant l'ennemi de"});
db.iliadfr.insert({ pg : 9207, line : "leurs lances à deux pointes, s'épouvantèrent en voyant Hektôr"});
db.iliadfr.insert({ pg : 9208, line : "parcourir les lignes Troiennes, et leur âme tomba à leurs pieds."});
db.iliadfr.insert({ pg : 9210, line : "Et Thoas Andraimonide les excitait. Et c'était le meilleur"});
db.iliadfr.insert({ pg : 9211, line : "guerrier Aitôlien, habile au combat de la lance et ferme dans la"});
db.iliadfr.insert({ pg : 9212, line : "mêlée. Et peu d'Akhaiens l'emportaient sur lui dans l'agora. Et il"});
db.iliadfr.insert({ pg : 9213, line : "s'écria:"});
db.iliadfr.insert({ pg : 9215, line : "-- Ah! certes, je vois de mes yeux un grand prodige. Voici le"});
db.iliadfr.insert({ pg : 9216, line : "Priamide échappé à la mort. Chacun de nous espérait qu'il avait"});
db.iliadfr.insert({ pg : 9217, line : "péri par les mains d'Aias Télamônien; mais sans doute un dieu l'a"});
db.iliadfr.insert({ pg : 9218, line : "sauvé de nouveau, lui qui a rompu les genoux de tant de Danaens,"});
db.iliadfr.insert({ pg : 9219, line : "et qui va en rompre encore, car ce n'est point sans l'aide de Zeus"});
db.iliadfr.insert({ pg : 9220, line : "tonnant qu'il revient furieux au combat. Mais, allons! et obéissez"});
db.iliadfr.insert({ pg : 9221, line : "tous. Que la multitude retourne aux nefs, et tenons ferme, nous"});
db.iliadfr.insert({ pg : 9222, line : "qui sommes les plus braves de l'armée. Tendons vers lui nos"});
db.iliadfr.insert({ pg : 9223, line : "grandes lances, et je ne pense pas qu'il puisse, malgré ses"});
db.iliadfr.insert({ pg : 9224, line : "forces, enfoncer les lignes des Danaens."});
db.iliadfr.insert({ pg : 9226, line : "Il parla ainsi, et tous l'entendirent et obéirent. Et autour de"});
db.iliadfr.insert({ pg : 9227, line : "lui étaient les Aias et le roi Idoméneus, et Teukros et Mèrionès,"});
db.iliadfr.insert({ pg : 9228, line : "et Mégès semblable à Arès; et ils se préparaient au combat,"});
db.iliadfr.insert({ pg : 9229, line : "réunissant les plus braves, contre Hektôr et les Troiens. Et,"});
db.iliadfr.insert({ pg : 9230, line : "derrière eux, la multitude retournait vers les nefs des Akhaiens."});
db.iliadfr.insert({ pg : 9232, line : "Et les Troiens frappèrent les premiers. Hektôr les précédait,"});
db.iliadfr.insert({ pg : 9233, line : "accompagné de Phoibos Apollôn, les épaules couvertes d'une nuée et"});
db.iliadfr.insert({ pg : 9234, line : "tenant l'aigide terrible, aux longues franges, que le forgeron"});
db.iliadfr.insert({ pg : 9235, line : "Hèphaistos donna à Zeus pour épouvanter les hommes. Et, tenant"});
db.iliadfr.insert({ pg : 9236, line : "l'aigide en main, il menait les Troiens. Et les Argiens les"});
db.iliadfr.insert({ pg : 9237, line : "attendaient de pied ferme, et une clameur s'éleva des deux côtés."});
db.iliadfr.insert({ pg : 9238, line : "Les flèches jaillissaient des nerfs et les lances des mains"});
db.iliadfr.insert({ pg : 9239, line : "robustes; et les unes pénétraient dans la chair des jeunes hommes,"});
db.iliadfr.insert({ pg : 9240, line : "et les autres entraient en terre, avides de sang, mais sans avoir"});
db.iliadfr.insert({ pg : 9241, line : "percé le beau corps des combattants."});
db.iliadfr.insert({ pg : 9243, line : "Aussi longtemps que Phoibos Apollôn tint l'aigide immobile en ses"});
db.iliadfr.insert({ pg : 9244, line : "mains, les traits percèrent des deux côtés, et les guerriers"});
db.iliadfr.insert({ pg : 9245, line : "tombèrent; mais quand il la secoua devant la face des cavaliers"});
db.iliadfr.insert({ pg : 9246, line : "Danaens, en poussant des cris terribles, leur coeur se troubla"});
db.iliadfr.insert({ pg : 9247, line : "dans leurs poitrines, et ils oublièrent leur force et leur"});
db.iliadfr.insert({ pg : 9248, line : "courage."});
db.iliadfr.insert({ pg : 9250, line : "Comme un troupeau de boeufs, ou un grand troupeau de brebis, que"});
db.iliadfr.insert({ pg : 9251, line : "deux bêtes féroces, au milieu de la nuit, bouleversent"});
db.iliadfr.insert({ pg : 9252, line : "soudainement, en l'absence de leur gardien, de même les Akhaiens"});
db.iliadfr.insert({ pg : 9253, line : "furent saisis de terreur, et Apollôn les mit en fuite et donna la"});
db.iliadfr.insert({ pg : 9254, line : "victoire à Hektôr et aux Troiens. Alors, dans cette fuite, chaque"});
db.iliadfr.insert({ pg : 9255, line : "homme tua un autre homme. Hektôr tua Stikhios et Arkésilaos, l'un,"});
db.iliadfr.insert({ pg : 9256, line : "chef des Boiôtiens aux tuniques d'airain, l'autre, fidèle"});
db.iliadfr.insert({ pg : 9257, line : "compagnon du magnanime Ménèstheus. Et Ainéias tua Médôn et Iasos."});
db.iliadfr.insert({ pg : 9258, line : "Et Médôn était bâtard du divin Oileus et frère d'Aias; mais il"});
db.iliadfr.insert({ pg : 9259, line : "habitait Phylakè, loin de sa patrie, ayant tué le frère de sa"});
db.iliadfr.insert({ pg : 9260, line : "belle-mère Ériopis, femme d'Oileus. Et Iasos était un chef"});
db.iliadfr.insert({ pg : 9261, line : "Athènaien et fils de Sphèlos Boukolide."});
db.iliadfr.insert({ pg : 9263, line : "Et Polydamas tua Mèkistheus, et Politès tua Ekhios qui combattait"});
db.iliadfr.insert({ pg : 9264, line : "aux premiers rangs. Et le divin Agènôr tua Klônios, et Pâris"});
db.iliadfr.insert({ pg : 9265, line : "frappa au sommet de l'épaule, par derrière, Dèiokhos qui fuyait,"});
db.iliadfr.insert({ pg : 9266, line : "et l'airain le traversa."});
db.iliadfr.insert({ pg : 9268, line : "Tandis que les vainqueurs dépouillaient les cadavres de leurs"});
db.iliadfr.insert({ pg : 9269, line : "armes, les Akhaiens franchissaient les pieux, dans le fossé, et"});
db.iliadfr.insert({ pg : 9270, line : "fuyaient çà et là, derrière la muraille, contraints par la"});
db.iliadfr.insert({ pg : 9271, line : "nécessité. Mais Hektôr commanda à haute voix aux Troiens de se"});
db.iliadfr.insert({ pg : 9272, line : "ruer sur les nefs et de laisser là les dépouilles sanglantes:"});
db.iliadfr.insert({ pg : 9274, line : "-- Celui que je verrai loin des nefs, je lui donnerai la mort. Ni"});
db.iliadfr.insert({ pg : 9275, line : "ses frères, ni ses soeurs ne mettront son corps sur le bûcher, et"});
db.iliadfr.insert({ pg : 9276, line : "les chiens le déchireront devant notre ville."});
db.iliadfr.insert({ pg : 9278, line : "Ayant ainsi parlé, il poussa les chevaux du fouet, en entraînant"});
db.iliadfr.insert({ pg : 9279, line : "les Troiens, et tous, avec des cris menaçants et une clameur"});
db.iliadfr.insert({ pg : 9280, line : "immense, ils poussaient leurs chars en avant. Et Phoibos Apollôn"});
db.iliadfr.insert({ pg : 9281, line : "jeta facilement du pied les bords du fossé dans le milieu, et, le"});
db.iliadfr.insert({ pg : 9282, line : "comblant, le fit aussi large que l'espace parcouru par le trait"});
db.iliadfr.insert({ pg : 9283, line : "que lance un guerrier vigoureux. Et tous s'y jetèrent en foule, et"});
db.iliadfr.insert({ pg : 9284, line : "Apollôn, les précédant avec l'aigide éclatante, renversa le mur"});
db.iliadfr.insert({ pg : 9285, line : "des Akhaiens aussi aisément qu'un enfant renverse, auprès de la"});
db.iliadfr.insert({ pg : 9286, line : "mer, les petits monceaux de sable qu'il a amassés et qu'il"});
db.iliadfr.insert({ pg : 9287, line : "disperse en se jouant. Ainsi, archer Apollôn, tu dispersas"});
db.iliadfr.insert({ pg : 9288, line : "l'oeuvre qui avait coûté tant de peines et de misères aux Argiens,"});
db.iliadfr.insert({ pg : 9289, line : "et tu les mis en fuite."});
db.iliadfr.insert({ pg : 9291, line : "Et ils s'arrêtèrent auprès des nefs, s'exhortant les uns les"});
db.iliadfr.insert({ pg : 9292, line : "autres; et, les mains étendues vers les dieux, ils les"});
db.iliadfr.insert({ pg : 9293, line : "imploraient. Et le Gérennien Nestôr, rempart des Akhaiens, priait,"});
db.iliadfr.insert({ pg : 9294, line : "les bras levés vers l'Ouranos étoilé:"});
db.iliadfr.insert({ pg : 9296, line : "-- Père Zeus! si jamais, dans la fertile Argos, brûlant pour toi"});
db.iliadfr.insert({ pg : 9297, line : "les cuisses grasses des boeufs et des brebis, nous t'avons supplié"});
db.iliadfr.insert({ pg : 9298, line : "de nous accorder le retour, et si tu l'as promis d'un signe de ta"});
db.iliadfr.insert({ pg : 9299, line : "tête, souviens-toi, ô Olympien! Éloigne notre jour suprême, et ne"});
db.iliadfr.insert({ pg : 9300, line : "permets pas que les Akhaiens soient domptés par les Troiens."});
db.iliadfr.insert({ pg : 9302, line : "Il parla ainsi en priant, et le sage Zeus entendit la prière du"});
db.iliadfr.insert({ pg : 9303, line : "vieux Nèlèiade et tonna. Et, au bruit du tonnerre, les Troiens,"});
db.iliadfr.insert({ pg : 9304, line : "croyant comprendre la pensée de Zeus tempêtueux, se ruèrent plus"});
db.iliadfr.insert({ pg : 9305, line : "furieux sur les Argiens. Comme les grandes lames de la haute mer"});
db.iliadfr.insert({ pg : 9306, line : "assiègent les flancs d'une nef, poussées par la violence du vent,"});
db.iliadfr.insert({ pg : 9307, line : "car c'est elle qui gonfle les eaux; de même les Troiens"});
db.iliadfr.insert({ pg : 9308, line : "escaladaient le mur avec de grandes clameurs; et ils poussaient"});
db.iliadfr.insert({ pg : 9309, line : "leurs chevaux et combattaient devant les nefs à coups de lances"});
db.iliadfr.insert({ pg : 9310, line : "aiguës; et les Akhaiens, du haut de leurs nefs noires, les"});
db.iliadfr.insert({ pg : 9311, line : "repoussaient avec ces longs pieux, couchés dans les nefs, et qui,"});
db.iliadfr.insert({ pg : 9312, line : "cerclés d'airain, servent dans le combat naval."});
db.iliadfr.insert({ pg : 9314, line : "Tant que les Akhaiens et les Troiens combattirent au-delà du mur,"});
db.iliadfr.insert({ pg : 9315, line : "loin des nefs rapides, Patroklos, assis sous la tente de"});
db.iliadfr.insert({ pg : 9316, line : "l'irréprochable Eurypylos, le charma par ses paroles et baigna sa"});
db.iliadfr.insert({ pg : 9317, line : "blessure de baumes qui guérissent les douleurs amères; mais quand"});
db.iliadfr.insert({ pg : 9318, line : "il vit que les Troiens avaient franchi le mur, et que les Akhaiens"});
db.iliadfr.insert({ pg : 9319, line : "fuyaient avec des cris, il gémit, et frappa ses cuisses de ses"});
db.iliadfr.insert({ pg : 9320, line : "mains, et il dit en pleurant:"});
db.iliadfr.insert({ pg : 9322, line : "-- Eurypylos, je ne puis rester plus longtemps, bien que tu"});
db.iliadfr.insert({ pg : 9323, line : "souffres, car voici une mêlée suprême. Qu'un de tes compagnons te"});
db.iliadfr.insert({ pg : 9324, line : "soigne; il faut que je retourne vers Akhilleus et que je l'exhorte"});
db.iliadfr.insert({ pg : 9325, line : "à combattre. Qui sait si, un dieu m'aidant, je ne toucherai point"});
db.iliadfr.insert({ pg : 9326, line : "son âme? Le conseil d'un ami est excellent."});
db.iliadfr.insert({ pg : 9328, line : "Ayant ainsi parlé, il s'éloigna."});
db.iliadfr.insert({ pg : 9330, line : "Cependant les Akhaiens soutenaient l'assaut des Troiens. Et ceux-"});
db.iliadfr.insert({ pg : 9331, line : "ci ne pouvaient rompre les phalanges des Danaens et envahir les"});
db.iliadfr.insert({ pg : 9332, line : "tentes et les nefs, et ceux-là ne pouvaient repousser l'ennemi"});
db.iliadfr.insert({ pg : 9333, line : "loin des nefs. Comme le bois dont on construit une nef est mis de"});
db.iliadfr.insert({ pg : 9334, line : "niveau par un habile ouvrier à qui Athènè a enseigné toute sa"});
db.iliadfr.insert({ pg : 9335, line : "science, de même le combat était partout égal autour des nefs."});
db.iliadfr.insert({ pg : 9337, line : "Et le Priamide attaqua l'illustre Aias. Et tous deux soutenaient"});
db.iliadfr.insert({ pg : 9338, line : "le travail du combat autour des nefs, et l'un ne pouvait éloigner"});
db.iliadfr.insert({ pg : 9339, line : "l'autre pour incendier les nefs, et l'autre ne pouvait repousser"});
db.iliadfr.insert({ pg : 9340, line : "le premier que soutenait un dieu. Et l'illustre Aias frappa de sa"});
db.iliadfr.insert({ pg : 9341, line : "lance Kalètôr, fils de Klytios, comme celui-ci portait le feu sur"});
db.iliadfr.insert({ pg : 9342, line : "les nefs; et Kalètôr tomba renversé, laissant échapper la torche"});
db.iliadfr.insert({ pg : 9343, line : "de ses mains. Et quand Hektôr vit son parent tomber dans la"});
db.iliadfr.insert({ pg : 9344, line : "poussière devant la nef noire, il cria aux Troiens et aux Lykiens:"});
db.iliadfr.insert({ pg : 9346, line : "-- Troiens, Lykiens et Dardaniens belliqueux, n'abandonnez point"});
db.iliadfr.insert({ pg : 9347, line : "le combat étroitement engagé, mais enlevez le fils de Klytios, et"});
db.iliadfr.insert({ pg : 9348, line : "que les Akhaiens ne le dépouillent point de ses armes."});
db.iliadfr.insert({ pg : 9350, line : "Il parla ainsi, et lança sa pique éclatante contre Aias, mais il"});
db.iliadfr.insert({ pg : 9351, line : "le manqua, et il atteignit Lykophôn, fils de Mastôr, compagnon"});
db.iliadfr.insert({ pg : 9352, line : "d'Aias, et qui habitait avec celui-ci, depuis qu'il avait tué un"});
db.iliadfr.insert({ pg : 9353, line : "homme dans la divine Kythèrè. Et le Priamide le frappa de sa lance"});
db.iliadfr.insert({ pg : 9354, line : "aiguë au-dessus de l'oreille, auprès d'Aias, et Lykophôn tomba du"});
db.iliadfr.insert({ pg : 9355, line : "haut de la poupe sur la poussière, et ses forces furent dissoutes."});
db.iliadfr.insert({ pg : 9356, line : "Et Aias, frémissant, appela son frère:"});
db.iliadfr.insert({ pg : 9358, line : "-- Ami Teukros, notre fidèle compagnon est mort, lui qui, loin de"});
db.iliadfr.insert({ pg : 9359, line : "Kythèrè, vivait auprès de nous et que nous honorions autant que"});
db.iliadfr.insert({ pg : 9360, line : "nos parents bien-aimés. Le magnanime Hektôr l'a tué. Où sont tes"});
db.iliadfr.insert({ pg : 9361, line : "flèches mortelles et l'arc que t'a donné Phoibos Apollôn?"});
db.iliadfr.insert({ pg : 9363, line : "Il parla ainsi, et Teukros l'entendit, et il accourut, tenant en"});
db.iliadfr.insert({ pg : 9364, line : "main son arc recourbé et le carquois plein de flèches. Et il lança"});
db.iliadfr.insert({ pg : 9365, line : "ses flèches aux Troiens. Et il frappa Kléitos, fils de Peisènôr,"});
db.iliadfr.insert({ pg : 9366, line : "compagnon de l'illustre Panthoide Polydamas, dont il conduisait le"});
db.iliadfr.insert({ pg : 9367, line : "char et les chevaux à travers les phalanges bouleversées, afin de"});
db.iliadfr.insert({ pg : 9368, line : "plaire à Hektôr et aux Troiens. Mais le malheur l'accabla sans que"});
db.iliadfr.insert({ pg : 9369, line : "nul pût le secourir; et la flèche fatale entra derrière le cou, et"});
db.iliadfr.insert({ pg : 9370, line : "il tomba du char, et les chevaux reculèrent, secouant le char"});
db.iliadfr.insert({ pg : 9371, line : "vide."});
db.iliadfr.insert({ pg : 9373, line : "Et le prince Polydamas, l'ayant vu, accourut promptement aux"});
db.iliadfr.insert({ pg : 9374, line : "chevaux et les confia à Astynoos, fils de Protiaôn, lui"});
db.iliadfr.insert({ pg : 9375, line : "recommandant de les tenir près de lui. Et il se mêla de nouveau"});
db.iliadfr.insert({ pg : 9376, line : "aux combattants."});
db.iliadfr.insert({ pg : 9378, line : "Et Teukros lança une flèche contre Hektôr, et il l'eût retranché"});
db.iliadfr.insert({ pg : 9379, line : "du combat, auprès des nefs des Akhaiens, s'il l'avait atteint, et"});
db.iliadfr.insert({ pg : 9380, line : "lui eût arraché l'âme; mais il ne put échapper au regard du sage"});
db.iliadfr.insert({ pg : 9381, line : "Zeus qui veillait sur Hektôr. Et Zeus priva de cette gloire le"});
db.iliadfr.insert({ pg : 9382, line : "Télamônien Teukros, car il rompit le nerf bien tendu, comme"});
db.iliadfr.insert({ pg : 9383, line : "Teukros tendait l'arc excellent. Et la flèche à pointe d'airain"});
db.iliadfr.insert({ pg : 9384, line : "s'égara, et l'arc tomba des mains de l'archer. Et Teukros frémit"});
db.iliadfr.insert({ pg : 9385, line : "et dit à son frère:"});
db.iliadfr.insert({ pg : 9387, line : "-- Ah! certes, quelque dieu nous traverse dans le combat. Il m'a"});
db.iliadfr.insert({ pg : 9388, line : "arraché l'arc des mains et rompu le nerf tout neuf que j'y avais"});
db.iliadfr.insert({ pg : 9389, line : "attaché moi-même ce matin, afin qu'il pût lancer beaucoup de"});
db.iliadfr.insert({ pg : 9390, line : "flèches."});
db.iliadfr.insert({ pg : 9392, line : "Et le grand Télamônien Aias lui répondit:"});
db.iliadfr.insert({ pg : 9394, line : "-- Ô ami, laisse ton arc et tes flèches, puisqu'un dieu jaloux des"});
db.iliadfr.insert({ pg : 9395, line : "Danaens disperse tes traits. Prends une longue lance, mets un"});
db.iliadfr.insert({ pg : 9396, line : "bouclier sur tes épaules et combats les Troiens en excitant les"});
db.iliadfr.insert({ pg : 9397, line : "troupes. Que ce ne soit pas du moins sans peine qu'ils se rendent"});
db.iliadfr.insert({ pg : 9398, line : "maîtres de nos nefs bien construites. Mais souvenons-nous de"});
db.iliadfr.insert({ pg : 9399, line : "combattre."});
db.iliadfr.insert({ pg : 9401, line : "Il parla ainsi, et Teukros, déposant son arc dans sa tente, saisit"});
db.iliadfr.insert({ pg : 9402, line : "une solide lance à pointe d'airain, mit un bouclier à quatre lames"});
db.iliadfr.insert({ pg : 9403, line : "sur ses épaules, un excellent casque à crinière sur sa tête, et se"});
db.iliadfr.insert({ pg : 9404, line : "hâta de revenir auprès d'Aias. Mais quand Hektôr eut vu que les"});
db.iliadfr.insert({ pg : 9405, line : "flèches de Teukros lui étaient devenues inutiles, il cria à haute"});
db.iliadfr.insert({ pg : 9406, line : "voix aux Troiens et aux Lykiens:"});
db.iliadfr.insert({ pg : 9408, line : "-- Troiens, Lykiens et belliqueux Dardaniens, soyez des hommes, et"});
db.iliadfr.insert({ pg : 9409, line : "souvenez-vous de votre force et de votre courage auprès des nefs"});
db.iliadfr.insert({ pg : 9410, line : "creuses! Je vois de mes yeux les flèches d'un brave archer brisées"});
db.iliadfr.insert({ pg : 9411, line : "par Zeus. Il est facile de comprendre à qui le puissant Kroniôn"});
db.iliadfr.insert({ pg : 9412, line : "accorde ou refuse son aide, qui il menace et qui il veut couvrir"});
db.iliadfr.insert({ pg : 9413, line : "de gloire. Maintenant, il brise les forces des Akhaiens et il nous"});
db.iliadfr.insert({ pg : 9414, line : "protège. Combattez fermement autour des nefs. Si l'un de vous est"});
db.iliadfr.insert({ pg : 9415, line : "blessé et meurt, qu'il meure sans regrets, car il est glorieux de"});
db.iliadfr.insert({ pg : 9416, line : "mourir pour la patrie, car il sauvera sa femme, ses enfants et"});
db.iliadfr.insert({ pg : 9417, line : "tout son patrimoine, si les Akhaiens retournent, sur leurs nefs,"});
db.iliadfr.insert({ pg : 9418, line : "dans la chère terre de leurs aïeux."});
db.iliadfr.insert({ pg : 9420, line : "Ayant ainsi parlé, il excita la force et le courage de chacun. Et"});
db.iliadfr.insert({ pg : 9421, line : "Aias, de son côté, exhortait ses compagnons:"});
db.iliadfr.insert({ pg : 9423, line : "-- Ô honte! c'est maintenant, Argiens, qu'il faut périr ou sauver"});
db.iliadfr.insert({ pg : 9424, line : "les nefs. Espérez-vous, si Hektôr au casque mouvant se saisit de"});
db.iliadfr.insert({ pg : 9425, line : "vos nefs, retourner à pied dans la patrie? Ne l'entendez-vous"});
db.iliadfr.insert({ pg : 9426, line : "point exciter ses guerriers, ce Hektôr qui veut brûler nos nefs?"});
db.iliadfr.insert({ pg : 9427, line : "Ce n'est point aux danses qu'il les pousse, mais au combat. Le"});
db.iliadfr.insert({ pg : 9428, line : "mieux est de leur opposer nos bras et notre vigueur. Il faut"});
db.iliadfr.insert({ pg : 9429, line : "mourir promptement ou vivre, au lieu de nous consumer dans un"});
db.iliadfr.insert({ pg : 9430, line : "combat sans fin contre des hommes qui ne nous valent pas."});
db.iliadfr.insert({ pg : 9432, line : "Ayant ainsi parlé, il ranima le courage de chacun. Alors Hektôr"});
db.iliadfr.insert({ pg : 9433, line : "tua Skhédios, fils de Périmèdès, chef des Phôkèens; et Aias tua"});
db.iliadfr.insert({ pg : 9434, line : "Laodamas, chef des hommes de pied, fils illustre d'Antènôr. Et"});
db.iliadfr.insert({ pg : 9435, line : "Polydamas tua Otos le Kyllénien, compagnon du Phyléide, chef des"});
db.iliadfr.insert({ pg : 9436, line : "magnanimes Épéiens. Et Mégès, l'ayant vu, s'élança sur Polydamas;"});
db.iliadfr.insert({ pg : 9437, line : "mais celui-ci, s'étant courbé, échappa au coup de la pique, car"});
db.iliadfr.insert({ pg : 9438, line : "Apollôn ne permit pas que le Panthoide tombât parmi les"});
db.iliadfr.insert({ pg : 9439, line : "combattants; et la pique de Mégès perça la poitrine de Kreismos"});
db.iliadfr.insert({ pg : 9440, line : "qui tomba avec bruit. Et comme le Phyléide lui arrachait ses"});
db.iliadfr.insert({ pg : 9441, line : "armes, le brave Dolops Lampétide se jeta sur lui, Dolops"});
db.iliadfr.insert({ pg : 9442, line : "qu'engendra le Laomédontiade Lampos, le meilleur des hommes"});
db.iliadfr.insert({ pg : 9443, line : "mortels. Et il perça de sa lance le milieu du bouclier de Mégès,"});
db.iliadfr.insert({ pg : 9444, line : "mais son épaisse cuirasse préserva celui-ci. C'était la cuirasse"});
db.iliadfr.insert({ pg : 9445, line : "que Phyleus apporta autrefois d'Éphyrè, des bords du fleuve"});
db.iliadfr.insert({ pg : 9446, line : "Sellèis. Et son hôte, le roi des hommes, Euphètès, la lui avait"});
db.iliadfr.insert({ pg : 9447, line : "donnée, pour la porter dans les mêlées comme un rempart contre"});
db.iliadfr.insert({ pg : 9448, line : "l'ennemi. Et, maintenant, elle préserva son fils de la mort. Et"});
db.iliadfr.insert({ pg : 9449, line : "Mégès frappa de son épée le cône du casque d'airain à crinière de"});
db.iliadfr.insert({ pg : 9450, line : "cheval, et l'aigrette rompue tomba dans la poussière, ayant été"});
db.iliadfr.insert({ pg : 9451, line : "teinte récemment d'une couleur de pourpre. Et tandis que Mégès"});
db.iliadfr.insert({ pg : 9452, line : "combattait encore et espérait la victoire, le brave Ménélaos"});
db.iliadfr.insert({ pg : 9453, line : "accourut à son aide, et, venant à la dérobée, frappa l'épaule du"});
db.iliadfr.insert({ pg : 9454, line : "Troien. Et la pointe d'airain traversa la poitrine, et le guerrier"});
db.iliadfr.insert({ pg : 9455, line : "tomba sur la face."});
db.iliadfr.insert({ pg : 9457, line : "Et les deux Akhaiens s'élançaient pour le dépouiller de ses armes"});
db.iliadfr.insert({ pg : 9458, line : "d'airain; mais Hektôr excita les parents de Dolops, et surtout il"});
db.iliadfr.insert({ pg : 9459, line : "réprimanda le Hikétaonide, le brave Ménalippos, qui paissait,"});
db.iliadfr.insert({ pg : 9460, line : "avant la guerre, ses boeufs aux pieds flexibles dans Perkôtè, mais"});
db.iliadfr.insert({ pg : 9461, line : "qui vint à Ilios quand les nefs Danaennes aux doubles avirons"});
db.iliadfr.insert({ pg : 9462, line : "arrivèrent. Et il brillait parmi les Troiens, et il habitait"});
db.iliadfr.insert({ pg : 9463, line : "auprès de Priamos qui l'honorait à l'égal de ses fils. Et Hektôr"});
db.iliadfr.insert({ pg : 9464, line : "lui adressa ces paroles dures et sévères:"});
db.iliadfr.insert({ pg : 9466, line : "-- Ainsi, Ménalippos, nous restons inertes. Ton parent mort ne"});
db.iliadfr.insert({ pg : 9467, line : "touche-t-il point ton coeur? Ne vois-tu pas qu'ils arrachent les"});
db.iliadfr.insert({ pg : 9468, line : "armes de Dolops? Suis-moi. Ce n'est plus de loin qu'il faut"});
db.iliadfr.insert({ pg : 9469, line : "combattre les Argiens. Nous les tuerons, ou la haute Ilios sera"});
db.iliadfr.insert({ pg : 9470, line : "prise et ils égorgeront ses citoyens."});
db.iliadfr.insert({ pg : 9472, line : "En parlant ainsi, il s'élança, et Ménalippos le suivit, semblable"});
db.iliadfr.insert({ pg : 9473, line : "à un dieu. Et le grand Télamônien Aias exhortait les Akhaiens:"});
db.iliadfr.insert({ pg : 9475, line : "-- Ô amis! soyez des hommes. Ayez honte de fuir et faites face au"});
db.iliadfr.insert({ pg : 9476, line : "combat. Les braves sont plutôt sauvés que tués, et les lâches"});
db.iliadfr.insert({ pg : 9477, line : "seuls n'ont ni gloire, ni salut."});
db.iliadfr.insert({ pg : 9479, line : "Il parla ainsi, et les Akhaiens retinrent ses paroles dans leur"});
db.iliadfr.insert({ pg : 9480, line : "esprit, prêts à s'entre-aider; et ils faisaient comme un mur"});
db.iliadfr.insert({ pg : 9481, line : "d'airain autour des nefs; et Zeus excitait les Troiens contre eux."});
db.iliadfr.insert({ pg : 9482, line : "Et le brave Ménélaos anima ainsi Antilokhos:"});
db.iliadfr.insert({ pg : 9484, line : "-- Antilokhos, nul d'entre les Akhaiens n'est plus jeune que toi,"});
db.iliadfr.insert({ pg : 9485, line : "ni plus rapide, ni plus brave au combat. Plût aux dieux que tu"});
db.iliadfr.insert({ pg : 9486, line : "pusses tuer quelque Troien!"});
db.iliadfr.insert({ pg : 9488, line : "Il parla ainsi, et il le laissa excité par ces paroles. Et"});
db.iliadfr.insert({ pg : 9489, line : "Antilokhos se jeta parmi les combattants et lança sa pique"});
db.iliadfr.insert({ pg : 9490, line : "éclatante, et les Troiens reculèrent; mais la pique ne fut point"});
db.iliadfr.insert({ pg : 9491, line : "lancée en vain, car elle perça à la poitrine, près de la mamelle,"});
db.iliadfr.insert({ pg : 9492, line : "Ménalippos, l'orgueilleux fils de Hikétaôn. Et il tomba et ses"});
db.iliadfr.insert({ pg : 9493, line : "armes sonnèrent. Et le brave Antilokhos se jeta sur lui, comme un"});
db.iliadfr.insert({ pg : 9494, line : "chien sur un faon qu'un chasseur a percé tandis qu'il bondissait"});
db.iliadfr.insert({ pg : 9495, line : "hors du gîte. Ainsi, Ménalippos, le belliqueux Antilokhos sauta"});
db.iliadfr.insert({ pg : 9496, line : "sur toi pour t'arracher tes armes; mais le divin Hektôr, l'ayant"});
db.iliadfr.insert({ pg : 9497, line : "vu, courut sur lui à travers la mêlée. Et Antilokhos ne l'attendit"});
db.iliadfr.insert({ pg : 9498, line : "pas, quoique brave, et il prit la fuite, comme une bête fauve qui,"});
db.iliadfr.insert({ pg : 9499, line : "ayant tué un chien, ou le bouvier au milieu des boeufs, fuit avant"});
db.iliadfr.insert({ pg : 9500, line : "que la foule des hommes la poursuive. Ainsi fuyait le Nestôride."});
db.iliadfr.insert({ pg : 9501, line : "Et les Troiens et Hektôr, avec de grands cris, l'accablaient de"});
db.iliadfr.insert({ pg : 9502, line : "traits violents; mais il leur fit face, arrivé auprès de ses"});
db.iliadfr.insert({ pg : 9503, line : "compagnons."});
db.iliadfr.insert({ pg : 9505, line : "Et les Troiens, semblables à des lions mangeurs de chair crue, se"});
db.iliadfr.insert({ pg : 9506, line : "ruaient sur les nefs, accomplissant ainsi les ordres de Zeus, car"});
db.iliadfr.insert({ pg : 9507, line : "il leur inspirait la force et il troublait l'âme des Argiens,"});
db.iliadfr.insert({ pg : 9508, line : "voulant donner une grande gloire au Priamide Hektôr, et le laisser"});
db.iliadfr.insert({ pg : 9509, line : "jeter la flamme ardente sur les nefs aux poupes recourbées, afin"});
db.iliadfr.insert({ pg : 9510, line : "d'exaucer la fatale prière de Thétis. Et le sage Zeus attendait"});
db.iliadfr.insert({ pg : 9511, line : "qu'il eût vu le feu embraser une nef, et alors il repousserait les"});
db.iliadfr.insert({ pg : 9512, line : "Troiens loin des nefs et rendrait la victoire aux Danaens. C'est"});
db.iliadfr.insert({ pg : 9513, line : "pourquoi il entraînait vers les nefs creuses le Priamide Hektôr"});
db.iliadfr.insert({ pg : 9514, line : "déjà plein d'ardeur, furieux, agitant sa lance comme Arès, ou"});
db.iliadfr.insert({ pg : 9515, line : "pareil à un incendie terrible qui gronde sur les montagnes, dans"});
db.iliadfr.insert({ pg : 9516, line : "l'épaisseur d'une forêt profonde. Et la bouche de Hektôr écumait,"});
db.iliadfr.insert({ pg : 9517, line : "et ses yeux flambaient sous ses sourcils, et son casque s'agitait"});
db.iliadfr.insert({ pg : 9518, line : "sur sa tête guerrière."});
db.iliadfr.insert({ pg : 9520, line : "Et Zeus lui venait en aide, l'honorant et le glorifiant parmi les"});
db.iliadfr.insert({ pg : 9521, line : "hommes, car sa vie devait être brève, et voici que Pallas Athènè"});
db.iliadfr.insert({ pg : 9522, line : "préparait le jour fatal où il tomberait sous la violence du"});
db.iliadfr.insert({ pg : 9523, line : "Pèléide."});
db.iliadfr.insert({ pg : 9525, line : "Et il tentait de rompre les lignes des guerriers, se ruant là où"});
db.iliadfr.insert({ pg : 9526, line : "il voyait la mêlée la plus pressée et les armes les plus belles."});
db.iliadfr.insert({ pg : 9527, line : "Mais, malgré son désir, il ne pouvait rompre l'armée ennemie, car"});
db.iliadfr.insert({ pg : 9528, line : "celle-ci résistait comme une tour, ou comme une roche énorme et"});
db.iliadfr.insert({ pg : 9529, line : "haute qui, se dressant près de la blanche mer, soutient le souffle"});
db.iliadfr.insert({ pg : 9530, line : "rugissant des vents et le choc des grandes lames qui se brisent"});
db.iliadfr.insert({ pg : 9531, line : "contre elle. Ainsi les Danaens soutenaient fermement l'assaut des"});
db.iliadfr.insert({ pg : 9532, line : "Troiens et ne fuyaient point, tandis que Hektôr, éclatant comme le"});
db.iliadfr.insert({ pg : 9533, line : "feu, bondissait de tous côtés dans la mêlée."});
db.iliadfr.insert({ pg : 9535, line : "Comme l'eau de la mer, enflée par les vents qui soufflent avec"});
db.iliadfr.insert({ pg : 9536, line : "véhémence du milieu des nuées, assiège une nef rapide et la couvre"});
db.iliadfr.insert({ pg : 9537, line : "tout entière d'écume, tandis que le vent frémit dans la voile et"});
db.iliadfr.insert({ pg : 9538, line : "que les matelots sont épouvantés, parce que la mort est proche; de"});
db.iliadfr.insert({ pg : 9539, line : "même le coeur des Akhaiens se rompait dans leurs poitrines."});
db.iliadfr.insert({ pg : 9541, line : "Ou, quand il arrive qu'un lion désastreux tombe au milieu des"});
db.iliadfr.insert({ pg : 9542, line : "boeufs innombrables qui paissent dans un vaste marécage, de même"});
db.iliadfr.insert({ pg : 9543, line : "que le bouvier, ne sachant point combattre les bêtes fauves pour"});
db.iliadfr.insert({ pg : 9544, line : "le salut de ses boeufs noirs, va tantôt à un bout, tantôt à"});
db.iliadfr.insert({ pg : 9545, line : "l'autre bout du troupeau, tandis que le lion bondit au milieu des"});
db.iliadfr.insert({ pg : 9546, line : "génisses qui s'épouvantent et en dévore une; de même les Akhaiens"});
db.iliadfr.insert({ pg : 9547, line : "étaient bouleversés par Hektôr et par le père Zeus."});
db.iliadfr.insert({ pg : 9549, line : "Cependant, le Priamide n'avait tué que le seul Périphètès de"});
db.iliadfr.insert({ pg : 9550, line : "Mykènè, fils bien-aimé de Kypreus, qui portait à la force"});
db.iliadfr.insert({ pg : 9551, line : "Hèrakléenne les ordres du roi Eurystheus. Il était né fils"});
db.iliadfr.insert({ pg : 9552, line : "excellent d'un père indigne, et, par toutes les vertus, par son"});
db.iliadfr.insert({ pg : 9553, line : "courage et par sa sagesse, il était le premier des Mykènaiens. Et"});
db.iliadfr.insert({ pg : 9554, line : "il donna une grande gloire à Hektôr, car, en se retournant, il"});
db.iliadfr.insert({ pg : 9555, line : "heurta le bord du grand bouclier qui le couvrait tout entier et le"});
db.iliadfr.insert({ pg : 9556, line : "préservait des traits; et, les pieds embarrassés, il tomba en"});
db.iliadfr.insert({ pg : 9557, line : "arrière, et, dans sa chute, son casque résonna autour de ses"});
db.iliadfr.insert({ pg : 9558, line : "tempes. Alors, Hektôr, l'ayant vu, accourut et lui perça la"});
db.iliadfr.insert({ pg : 9559, line : "poitrine d'un coup de lance, au milieu de ses compagnons qui"});
db.iliadfr.insert({ pg : 9560, line : "n'osèrent le secourir, tant ils redoutaient le divin Hektôr."});
db.iliadfr.insert({ pg : 9562, line : "Et les Argiens qui, d'abord, étaient devant les nefs, se"});
db.iliadfr.insert({ pg : 9563, line : "réfugiaient maintenant au milieu de celles qui, les premières,"});
db.iliadfr.insert({ pg : 9564, line : "avaient été tirées sur le sable. Puis, cédant à la force, ils"});
db.iliadfr.insert({ pg : 9565, line : "abandonnèrent aussi les intervalles de celles-ci, mais, s'arrêtant"});
db.iliadfr.insert({ pg : 9566, line : "devant les tentes, ils ne se dispersèrent point dans le camp, car"});
db.iliadfr.insert({ pg : 9567, line : "la honte et la terreur les retenaient, et ils s'exhortaient les"});
db.iliadfr.insert({ pg : 9568, line : "uns les autres."});
db.iliadfr.insert({ pg : 9570, line : "Alors, le Gérennien Nestôr, rempart des Akhaiens, attestant leurs"});
db.iliadfr.insert({ pg : 9571, line : "parents, adjura chaque guerrier:"});
db.iliadfr.insert({ pg : 9573, line : "-- Ô amis, soyez des hommes! Craignez la honte en face des autres"});
db.iliadfr.insert({ pg : 9574, line : "hommes. Souvenez-vous de vos fils, de vos femmes, de vos domaines,"});
db.iliadfr.insert({ pg : 9575, line : "de vos parents qui vivent encore ou qui sont morts. Je vous adjure"});
db.iliadfr.insert({ pg : 9576, line : "en leur nom de tenir ferme et de ne pas fuir."});
db.iliadfr.insert({ pg : 9578, line : "Il parla ainsi, et il ranima leur force et leur courage. Alors,"});
db.iliadfr.insert({ pg : 9579, line : "Athènè dissipa la nuée épaisse qui couvrait leurs yeux, et la"});
db.iliadfr.insert({ pg : 9580, line : "lumière se fit de toutes parts, autant sur les nefs que sur le"});
db.iliadfr.insert({ pg : 9581, line : "champ de bataille. Et ceux qui fuyaient, comme ceux qui luttaient,"});
db.iliadfr.insert({ pg : 9582, line : "et ceux qui combattaient auprès des nefs rapides, virent le brave"});
db.iliadfr.insert({ pg : 9583, line : "Hektôr et ses compagnons."});
db.iliadfr.insert({ pg : 9585, line : "Et il ne plut point à l'âme du magnanime Aias de rester où étaient"});
db.iliadfr.insert({ pg : 9586, line : "les autres fils des Akhaiens. Et il s'avança, traversant les"});
db.iliadfr.insert({ pg : 9587, line : "poupes des nefs et agitant un grand pieu cerclé d'airain et long"});
db.iliadfr.insert({ pg : 9588, line : "de vingt-deux coudées. Comme un habile cavalier qui, ayant mis"});
db.iliadfr.insert({ pg : 9589, line : "ensemble quatre chevaux très agiles, les pousse vers une grande"});
db.iliadfr.insert({ pg : 9590, line : "ville, sur le chemin public, et que les hommes et les femmes"});
db.iliadfr.insert({ pg : 9591, line : "admirent, tandis qu'il saute de l'un à l'autre, et qu'ils courent"});
db.iliadfr.insert({ pg : 9592, line : "toujours; de même Aias marchait rapidement sur les poupes des"});
db.iliadfr.insert({ pg : 9593, line : "nefs, et sa voix montait dans l'Ouranos, tandis qu'il excitait par"});
db.iliadfr.insert({ pg : 9594, line : "de grandes clameurs les Danaens à sauver les tentes et les nefs."});
db.iliadfr.insert({ pg : 9596, line : "Hektôr, de son côté, ne restait point dans la foule des Troiens"});
db.iliadfr.insert({ pg : 9597, line : "bien armés. Comme un aigle fauve qui tombe sur une multitude"});
db.iliadfr.insert({ pg : 9598, line : "d'oiseaux, paissant le long d'un fleuve, oies, grues et cygnes aux"});
db.iliadfr.insert({ pg : 9599, line : "longs cous; de même Hektôr se précipita sur une nef à proue bleue."});
db.iliadfr.insert({ pg : 9600, line : "Et, de sa grande main, Zeus le poussait par derrière, et tout son"});
db.iliadfr.insert({ pg : 9601, line : "peuple avec lui. Et, de nouveau, une violente mêlée s'engagea"});
db.iliadfr.insert({ pg : 9602, line : "autour des nefs. On eût dit des hommes infatigables et indomptés"});
db.iliadfr.insert({ pg : 9603, line : "se ruant à un premier combat, tant ils luttaient tous avec ardeur."});
db.iliadfr.insert({ pg : 9604, line : "Et les Akhaiens, n'espérant pas échapper au carnage, se croyaient"});
db.iliadfr.insert({ pg : 9605, line : "destinés à la mort, et les Troiens espéraient, dans leur coeur,"});
db.iliadfr.insert({ pg : 9606, line : "brûler les nefs et tuer les héros Akhaiens. Et ils se ruaient,"});
db.iliadfr.insert({ pg : 9607, line : "avec ces pensées, les uns contre les autres."});
db.iliadfr.insert({ pg : 9609, line : "Hektôr saisit la poupe de la nef belle et rapide qui avait amené"});
db.iliadfr.insert({ pg : 9610, line : "Prôtésilaos à Troiè et qui n'avait point dû le ramener dans la"});
db.iliadfr.insert({ pg : 9611, line : "terre de la patrie. Et les Akhaiens et les Troiens s'entre-tuaient"});
db.iliadfr.insert({ pg : 9612, line : "pour cette nef. Et l'impétuosité des flèches et des piques ne leur"});
db.iliadfr.insert({ pg : 9613, line : "suffisant plus, ils se frappaient, dans une même pensée, avec les"});
db.iliadfr.insert({ pg : 9614, line : "doubles haches tranchantes, les grandes épées et les lances"});
db.iliadfr.insert({ pg : 9615, line : "aiguës. Et beaucoup de beaux glaives à poignée noire tombaient sur"});
db.iliadfr.insert({ pg : 9616, line : "le sable des mains et des épaules des hommes qui combattaient, et"});
db.iliadfr.insert({ pg : 9617, line : "la terre était trempée d'un sang noir. Mais Hektôr saisissant de"});
db.iliadfr.insert({ pg : 9618, line : "ses mains les ornements de la poupe, et s'y attachant, cria aux"});
db.iliadfr.insert({ pg : 9619, line : "Troiens:"});
db.iliadfr.insert({ pg : 9621, line : "-- Apportez le feu, et poussez des clameurs en vous ruant! Zeus"});
db.iliadfr.insert({ pg : 9622, line : "nous offre le jour de la vengeance en nous livrant ces nefs qui,"});
db.iliadfr.insert({ pg : 9623, line : "venues vers Ilios contre la volonté des dieux, nous ont apporté"});
db.iliadfr.insert({ pg : 9624, line : "tant de calamités, par la lâcheté des vieillards qui me retenaient"});
db.iliadfr.insert({ pg : 9625, line : "et retenaient l'armée quand je voulais marcher et combattre ici."});
db.iliadfr.insert({ pg : 9626, line : "Mais si le prévoyant Zeus aveuglait alors notre esprit, maintenant"});
db.iliadfr.insert({ pg : 9627, line : "c'est lui-même qui nous excite et nous pousse!"});
db.iliadfr.insert({ pg : 9629, line : "Il parla ainsi, et tous se jetèrent avec plus de fureur sur les"});
db.iliadfr.insert({ pg : 9630, line : "Akhaiens. Et Aias ne put soutenir plus longtemps l'assaut, car il"});
db.iliadfr.insert({ pg : 9631, line : "était accablé de traits; et il recula, de peur de mourir, jusqu'au"});
db.iliadfr.insert({ pg : 9632, line : "banc des rameurs, long de sept pieds, et il abandonna la poupe de"});
db.iliadfr.insert({ pg : 9633, line : "la nef. Mais, du banc où il était, il éloignait à coups de lance"});
db.iliadfr.insert({ pg : 9634, line : "chaque Troien qui apportait le feu infatigable. Et, avec"});
db.iliadfr.insert({ pg : 9635, line : "d'horribles cris, il exhortait les Danaens:"});
db.iliadfr.insert({ pg : 9637, line : "-- Ô amis, héros Danaens, serviteurs d'Arès, soyez des hommes!"});
db.iliadfr.insert({ pg : 9638, line : "Souvenez-vous de votre force et de votre courage. Pensez-vous"});
db.iliadfr.insert({ pg : 9639, line : "trouver derrière vous d'autres défenseurs, ou une muraille plus"});
db.iliadfr.insert({ pg : 9640, line : "inaccessible qui vous préserve de la mort? Nous n'avons point ici"});
db.iliadfr.insert({ pg : 9641, line : "de ville ceinte de tours d'où nous puissions repousser l'ennemi et"});
db.iliadfr.insert({ pg : 9642, line : "assurer notre salut. Mais nous sommes ici dans les plaines des"});
db.iliadfr.insert({ pg : 9643, line : "Troiens bien armés, acculés contre la mer, loin de la terre de la"});
db.iliadfr.insert({ pg : 9644, line : "patrie, et notre salut est dans nos mains et non dans la lassitude"});
db.iliadfr.insert({ pg : 9645, line : "du combat."});
db.iliadfr.insert({ pg : 9647, line : "Il parla ainsi, et, furieux, il traversait de sa lance aiguë"});
db.iliadfr.insert({ pg : 9648, line : "chaque Troien qui apportait le feu sur les nefs creuses afin de"});
db.iliadfr.insert({ pg : 9649, line : "plaire à Hektôr et de lui obéir. Et, ceux-là, Aias les traversait"});
db.iliadfr.insert({ pg : 9650, line : "de sa lance aiguë, et il en tua douze devant les nefs."});
db.iliadfr.insert({ pg : 9653, line : "Chant 16"});
db.iliadfr.insert({ pg : 9655, line : "Et ils combattaient ainsi pour les nefs bien construites. Et"});
db.iliadfr.insert({ pg : 9656, line : "Patroklos se tenait devant le prince des peuples, Akhilleus,"});
db.iliadfr.insert({ pg : 9657, line : "versant de chaudes larmes, comme une source d'eau noire qui flue"});
db.iliadfr.insert({ pg : 9658, line : "du haut d'un rocher. Et le divin Akhilleus en eut compassion, et"});
db.iliadfr.insert({ pg : 9659, line : "il lui dit ces paroles ailées:"});
db.iliadfr.insert({ pg : 9661, line : "-- Pourquoi pleures-tu, Patroklos, comme une petite fille qui"});
db.iliadfr.insert({ pg : 9662, line : "court après sa mère, saisit sa robe et la regarde en pleurant"});
db.iliadfr.insert({ pg : 9663, line : "jusqu'à ce que celle-ci la prenne dans ses bras? Semblable à cette"});
db.iliadfr.insert({ pg : 9664, line : "enfant, ô Patroklos, tu verses des larmes abondantes. Quel message"});
db.iliadfr.insert({ pg : 9665, line : "as-tu pour les Myrmidones ou pour moi? As-tu seul reçu quelque"});
db.iliadfr.insert({ pg : 9666, line : "nouvelle de la Phthiè? On dit cependant que le fils d'Aktôr,"});
db.iliadfr.insert({ pg : 9667, line : "Ménoitios, et l'Aiakide Pèleus vivent encore parmi les Myrmidones."});
db.iliadfr.insert({ pg : 9668, line : "Certes, nous serions accablés, s'ils étaient morts. Mais peut-être"});
db.iliadfr.insert({ pg : 9669, line : "pleures-tu pour les Argiens qui périssent auprès des nefs creuses,"});
db.iliadfr.insert({ pg : 9670, line : "par leur propre iniquité? Parle, ne me cache rien afin que nous"});
db.iliadfr.insert({ pg : 9671, line : "sachions tous deux."});
db.iliadfr.insert({ pg : 9673, line : "Et le cavalier Patroklos, avec un profond soupir, lui répondit:"});
db.iliadfr.insert({ pg : 9675, line : "-- Ô Akhilleus, fils de Pèleus, le plus brave des Akhaiens, ne"});
db.iliadfr.insert({ pg : 9676, line : "t'irrite point, car de grandes calamités accablent les Akhaiens."});
db.iliadfr.insert({ pg : 9677, line : "Déjà les plus braves d'entre eux gisent dans les nefs, frappés et"});
db.iliadfr.insert({ pg : 9678, line : "blessés. Le robuste Tydéide Diomèdès est blessé, et Odysseus"});
db.iliadfr.insert({ pg : 9679, line : "illustre par sa lance, et Agamemnôn. Eurypylos a la cuisse percée"});
db.iliadfr.insert({ pg : 9680, line : "d'une flèche; et les médecins les soignent et baignent leurs"});
db.iliadfr.insert({ pg : 9681, line : "blessures avec des baumes. Mais toi, Akhilleus, tu es implacable!"});
db.iliadfr.insert({ pg : 9682, line : "Ô Pèlèiade, doué d'un courage inutile, qu'une colère telle que la"});
db.iliadfr.insert({ pg : 9683, line : "tienne ne me saisisse jamais! À qui viendras-tu désormais en aide,"});
db.iliadfr.insert({ pg : 9684, line : "si tu ne sauves pas les Argiens de cette ruine terrible? Ô"});
db.iliadfr.insert({ pg : 9685, line : "inexorable! Le cavalier Pèleus n'est point ton père, Thétis ne t'a"});
db.iliadfr.insert({ pg : 9686, line : "point conçu. La mer bleue t'a enfanté et ton âme est dure comme"});
db.iliadfr.insert({ pg : 9687, line : "les hauts rochers. Si tu fuis l'accomplissement d'un oracle, et si"});
db.iliadfr.insert({ pg : 9688, line : "ta mère vénérable t'a averti de la part de Zeus, au moins envoie-"});
db.iliadfr.insert({ pg : 9689, line : "moi promptement à la tête des Myrmidones, et que j'apporte une"});
db.iliadfr.insert({ pg : 9690, line : "lueur de salut aux Danaens! Laisse-moi couvrir mes épaules de tes"});
db.iliadfr.insert({ pg : 9691, line : "armes. Les Troiens reculeront, me prenant pour toi, et les fils"});
db.iliadfr.insert({ pg : 9692, line : "belliqueux des Akhaiens respireront, et nous chasserons"});
db.iliadfr.insert({ pg : 9693, line : "facilement, nouveaux combattants, ces hommes écrasés de fatigue,"});
db.iliadfr.insert({ pg : 9694, line : "loin des tentes et des nefs, vers leur ville."});
db.iliadfr.insert({ pg : 9696, line : "Il parla ainsi, suppliant, l'insensé! cherchant la mort et la kèr"});
db.iliadfr.insert({ pg : 9697, line : "fatale. Et Akhilleus aux pieds rapides lui répondit en gémissant:"});
db.iliadfr.insert({ pg : 9699, line : "-- Divin Patroklos, qu'as-tu dit? Je ne m'inquiète d'aucun oracle,"});
db.iliadfr.insert({ pg : 9700, line : "et ma mère vénérable ne m'a rien annoncé de la part de Zeus. Mais"});
db.iliadfr.insert({ pg : 9701, line : "un noir chagrin est dans mon coeur et trouble mon esprit, depuis"});
db.iliadfr.insert({ pg : 9702, line : "que cet homme, dont la puissance est la plus haute, m'a arraché ma"});
db.iliadfr.insert({ pg : 9703, line : "récompense, à moi qui suis son égal! Tel est le noir chagrin qui"});
db.iliadfr.insert({ pg : 9704, line : "me ronge. Cette jeune femme que j'avais conquise par ma lance,"});
db.iliadfr.insert({ pg : 9705, line : "après avoir renversé une ville aux fortes murailles, et que les"});
db.iliadfr.insert({ pg : 9706, line : "fils des Akhaiens m'avaient donnée en récompense, le roi Atréide"});
db.iliadfr.insert({ pg : 9707, line : "Agamemnôn me l'a arrachée des mains, comme à un vil vagabond! Mais"});
db.iliadfr.insert({ pg : 9708, line : "oublions le passé. Sans doute je ne puis nourrir dans mon coeur"});
db.iliadfr.insert({ pg : 9709, line : "une colère éternelle. J'avais résolu de ne la déposer que le jour"});
db.iliadfr.insert({ pg : 9710, line : "où les clameurs de la guerre parviendraient jusqu'à mes nefs."});
db.iliadfr.insert({ pg : 9711, line : "Couvre donc tes épaules de mes armes illustres, et mène les braves"});
db.iliadfr.insert({ pg : 9712, line : "Myrmidones au combat, puisqu'une noire nuée de Troiens enveloppe"});
db.iliadfr.insert({ pg : 9713, line : "les nefs. Voici que les Argiens sont acculés contre le rivage de"});
db.iliadfr.insert({ pg : 9714, line : "la mer, dans un espace très-étroit, et toute la ville des Troiens"});
db.iliadfr.insert({ pg : 9715, line : "s'est ruée sur eux avec audace, car ils ne voient point le front"});
db.iliadfr.insert({ pg : 9716, line : "de mon casque resplendir. Certes, dans leur fuite, ils empliraient"});
db.iliadfr.insert({ pg : 9717, line : "les fossés des champs de leurs cadavres, si le roi Agamemnôn ne"});
db.iliadfr.insert({ pg : 9718, line : "m'avait point outragé; et maintenant ils assiègent le camp. La"});
db.iliadfr.insert({ pg : 9719, line : "lance furieuse du Tydéide Diomèdès ne s'agite plus dans ses mains"});
db.iliadfr.insert({ pg : 9720, line : "pour sauver les Danaens de la mort, et je n'entends plus la voix"});
db.iliadfr.insert({ pg : 9721, line : "de l'Atréide sortir de sa tête détestée, mais celle du tueur"});
db.iliadfr.insert({ pg : 9722, line : "d'hommes Hektôr, qui excite les Troiens de toutes parts. Et la"});
db.iliadfr.insert({ pg : 9723, line : "clameur de ceux-ci remplit toute la plaine, et ils bouleversent"});
db.iliadfr.insert({ pg : 9724, line : "les Akhaiens. Va, Patroklos, rue-toi sur eux, et repousse cette"});
db.iliadfr.insert({ pg : 9725, line : "ruine loin des nefs. Ne les laisse pas détruire les nefs par le"});
db.iliadfr.insert({ pg : 9726, line : "feu ardent, et que le doux retour ne nous soit pas ravi. Mais"});
db.iliadfr.insert({ pg : 9727, line : "garde mes paroles dans ton esprit, si tu veux que je sois honoré"});
db.iliadfr.insert({ pg : 9728, line : "et glorifié par tous les Danaens, et qu'ils me rendent cette belle"});
db.iliadfr.insert({ pg : 9729, line : "jeune femme et un grand nombre de présents splendides, par"});
db.iliadfr.insert({ pg : 9730, line : "surcroît. Repousse les Troiens loin des nefs et reviens. Si"});
db.iliadfr.insert({ pg : 9731, line : "l'Époux de Hèrè, qui tonne au loin, te donne la victoire, ne"});
db.iliadfr.insert({ pg : 9732, line : "dompte pas sans moi les Troiens belliqueux; car tu me couvrirais"});
db.iliadfr.insert({ pg : 9733, line : "de honte, si, les ayant vaincus, et plein de l'orgueil et de"});
db.iliadfr.insert({ pg : 9734, line : "l'ivresse du combat, tu menais l'armée à Ilios. Crains qu'un des"});
db.iliadfr.insert({ pg : 9735, line : "dieux éternels ne se rue sur toi du haut de l'Olympos, surtout"});
db.iliadfr.insert({ pg : 9736, line : "l'archer Apollôn qui protège les Troiens. Reviens après avoir"});
db.iliadfr.insert({ pg : 9737, line : "sauvé les nefs, et laisse-les combattre dans la plaine. Qu'il vous"});
db.iliadfr.insert({ pg : 9738, line : "plaise, ô père Zeus, ô Athènè, ô Apollôn, que nul d'entre les"});
db.iliadfr.insert({ pg : 9739, line : "Troiens et les Akhaiens n'évite la mort, et que, seuls, nous"});
db.iliadfr.insert({ pg : 9740, line : "survivions tous deux et renversions les murailles sacrées d'Ilios!"});
db.iliadfr.insert({ pg : 9742, line : "Et ils se parlaient ainsi. Mais Aias ne suffisait plus au combat,"});
db.iliadfr.insert({ pg : 9743, line : "tant il était accablé de traits. Et l'esprit de Zeus et les"});
db.iliadfr.insert({ pg : 9744, line : "Troiens illustres l'emportaient sur lui; et son casque splendide,"});
db.iliadfr.insert({ pg : 9745, line : "dont les aigrettes étaient rompues par les coups, sonnait autour"});
db.iliadfr.insert({ pg : 9746, line : "de ses tempes, et son épaule fatiguée ne pouvait plus soutenir le"});
db.iliadfr.insert({ pg : 9747, line : "poids du bouclier. Et cependant, malgré la nuée des traits, ils ne"});
db.iliadfr.insert({ pg : 9748, line : "pouvaient l'ébranler, bien que respirant à peine, inondé de la"});
db.iliadfr.insert({ pg : 9749, line : "sueur de tous ses membres, et haletant sous des maux multipliés."});
db.iliadfr.insert({ pg : 9751, line : "Et Hektôr frappa de sa grande épée la lance de frêne d'Aias, et il"});
db.iliadfr.insert({ pg : 9752, line : "la coupa là où la pointe se joignait au bois; et le Télamônien"});
db.iliadfr.insert({ pg : 9753, line : "Aias n'agita plus dans sa main qu'une lance mutilée, car la pointe"});
db.iliadfr.insert({ pg : 9754, line : "d'airain, en tombant, sonna contre terre. Et Aias, dans son coeur"});
db.iliadfr.insert({ pg : 9755, line : "irréprochable, reconnut avec horreur l'oeuvre des dieux, et vit"});
db.iliadfr.insert({ pg : 9756, line : "que Zeus qui tonne dans les hauteurs, domptant son courage,"});
db.iliadfr.insert({ pg : 9757, line : "donnait la victoire aux Troiens. Et il se retira loin des traits,"});
db.iliadfr.insert({ pg : 9758, line : "et les Troiens jetèrent le feu infatigable sur la nef rapide, et"});
db.iliadfr.insert({ pg : 9759, line : "la flamme inextinguible enveloppa aussitôt la poupe, et Akhilleus,"});
db.iliadfr.insert({ pg : 9760, line : "frappant ses cuisses, dit à Patroklos:"});
db.iliadfr.insert({ pg : 9762, line : "-- Hâte-toi, divin Patroklos! Je vois le feu ardent sur les nefs."});
db.iliadfr.insert({ pg : 9763, line : "Si elles brûlent, nous ne pourrons plus songer au retour. Revêts"});
db.iliadfr.insert({ pg : 9764, line : "promptement mes armes, et j'assemblerai mon peuple."});
db.iliadfr.insert({ pg : 9766, line : "Il parla ainsi, et Patroklos se couvrit de l'airain splendide. Il"});
db.iliadfr.insert({ pg : 9767, line : "attacha de belles knèmides à ses jambes avec des agrafes d'argent;"});
db.iliadfr.insert({ pg : 9768, line : "il mit sur sa poitrine la cuirasse étincelante, aux mille reflets,"});
db.iliadfr.insert({ pg : 9769, line : "du rapide Akhilleus, et il suspendit à ses épaules l'épée d'airain"});
db.iliadfr.insert({ pg : 9770, line : "aux clous d'argent. Puis, il prit le grand et solide bouclier, et"});
db.iliadfr.insert({ pg : 9771, line : "il posa sur sa noble tête le casque magnifique à la terrible"});
db.iliadfr.insert({ pg : 9772, line : "aigrette de crins, et de ses mains il saisit de fortes piques;"});
db.iliadfr.insert({ pg : 9773, line : "mais il laissa la lance lourde, immense et solide, de"});
db.iliadfr.insert({ pg : 9774, line : "l'irréprochable Aiakide, la lance Pèliade que Kheirôn avait"});
db.iliadfr.insert({ pg : 9775, line : "apportée à son père bien-aimé des cimes du Pèlios, afin d'être la"});
db.iliadfr.insert({ pg : 9776, line : "mort des héros. Et Patroklos ordonna à Automédôn, qu'il honorait"});
db.iliadfr.insert({ pg : 9777, line : "le plus après Akhilleus, et qui lui était le plus fidèle dans le"});
db.iliadfr.insert({ pg : 9778, line : "combat, d'atteler les chevaux au char. Et c'est pourquoi Automédôn"});
db.iliadfr.insert({ pg : 9779, line : "soumit au joug les chevaux rapides, Xanthos et Balios, qui, tous"});
db.iliadfr.insert({ pg : 9780, line : "deux, volaient comme le vent, et que la Harpye Podargè avait"});
db.iliadfr.insert({ pg : 9781, line : "conçus de Zéphyros, lorsqu'elle paissait dans une prairie aux"});
db.iliadfr.insert({ pg : 9782, line : "bords du fleuve Okéanos. Et Automédôn lia au-delà du timon"});
db.iliadfr.insert({ pg : 9783, line : "l'irréprochable Pèdasos qu'Akhilleus avait amené de la ville"});
db.iliadfr.insert({ pg : 9784, line : "saccagée de Êétiôn. Et Pèdasos, bien que mortel, suivait les"});
db.iliadfr.insert({ pg : 9785, line : "chevaux immortels."});
db.iliadfr.insert({ pg : 9787, line : "Et Akhilleus armait les Myrmidones sous leurs tentes. De même que"});
db.iliadfr.insert({ pg : 9788, line : "des loups mangeurs de chair crue et pleins d'une grande force qui,"});
db.iliadfr.insert({ pg : 9789, line : "dévorant un grand cerf rameux qu'ils ont tué sur les montagnes,"});
db.iliadfr.insert({ pg : 9790, line : "vont en troupe, la gueule rouge de sang et vomissant le sang,"});
db.iliadfr.insert({ pg : 9791, line : "laper de leurs langues légères les eaux de la source noire, tandis"});
db.iliadfr.insert({ pg : 9792, line : "que leur ventre s'enfle et que leur coeur est toujours intrépide;"});
db.iliadfr.insert({ pg : 9793, line : "de même les chefs des Myrmidones se pressaient autour du brave"});
db.iliadfr.insert({ pg : 9794, line : "compagnon du rapide Aiakide. Et, au milieu d'eux, le belliqueux"});
db.iliadfr.insert({ pg : 9795, line : "Akhilleus excitait les porteurs de boucliers et les chevaux."});
db.iliadfr.insert({ pg : 9797, line : "Et Akhilleus cher à Zeus avait conduit à Troiè cinquante nefs"});
db.iliadfr.insert({ pg : 9798, line : "rapides, et cinquante guerriers étaient assis sur les bancs de"});
db.iliadfr.insert({ pg : 9799, line : "rameurs de chacune, et cinq chefs les commandaient sous ses"});
db.iliadfr.insert({ pg : 9800, line : "ordres."});
db.iliadfr.insert({ pg : 9802, line : "Et le premier chef était Ménèsthios à la cuirasse étincelante, aux"});
db.iliadfr.insert({ pg : 9803, line : "mille reflets, fils du fleuve Sperkhios qui tombait de Zeus. Et la"});
db.iliadfr.insert({ pg : 9804, line : "belle Polydorè, fille de Pèleus, femme mortelle épouse d'un dieu,"});
db.iliadfr.insert({ pg : 9805, line : "l'avait conçu de l'infatigable Sperkhios; mais Bôros, fils de"});
db.iliadfr.insert({ pg : 9806, line : "Périèreus, l'ayant épousée en la dotant richement, passait pour"});
db.iliadfr.insert({ pg : 9807, line : "être le père de Ménèsthios."});
db.iliadfr.insert({ pg : 9809, line : "Et le deuxième chef était le brave Eudôros, conçu en secret, et"});
db.iliadfr.insert({ pg : 9810, line : "qu'avait enfanté la belle Polymèlè, habile dans les danses, fille"});
db.iliadfr.insert({ pg : 9811, line : "de Phylas. Et le tueur d'Argos l'aima, l'ayant vue dans un choeur"});
db.iliadfr.insert({ pg : 9812, line : "de la tumultueuse Artémis à l'arc d'or. Et l'illustre Herméias,"});
db.iliadfr.insert({ pg : 9813, line : "montant aussitôt dans les combles de la demeure, coucha"});
db.iliadfr.insert({ pg : 9814, line : "secrètement avec elle, et elle lui donna un fils illustre, l'agile"});
db.iliadfr.insert({ pg : 9815, line : "et brave Eudôros. Et après qu'Eiléithya qui préside aux douloureux"});
db.iliadfr.insert({ pg : 9816, line : "enfantements l'eut conduit à la lumière, et qu'il eut vu la"});
db.iliadfr.insert({ pg : 9817, line : "splendeur de Hélios, le robuste Aktoride Ekhékhleus conduisit"});
db.iliadfr.insert({ pg : 9818, line : "Polymèlè dans ses demeures et lui fit mille dons nuptiaux. Et le"});
db.iliadfr.insert({ pg : 9819, line : "vieux Phylas éleva et nourrit avec soin Eudôros, comme s'il était"});
db.iliadfr.insert({ pg : 9820, line : "son fils."});
db.iliadfr.insert({ pg : 9822, line : "Et le troisième chef était le brave Peisandros Maimalide qui"});
db.iliadfr.insert({ pg : 9823, line : "excellait au combat de la lance, parmi les Myrmidones, après"});
db.iliadfr.insert({ pg : 9824, line : "Patroklos."});
db.iliadfr.insert({ pg : 9826, line : "Et le quatrième chef était le vieux cavalier Phoinix, et le"});
db.iliadfr.insert({ pg : 9827, line : "cinquième était l'irréprochable Akhimédôn, fils de Laerkeus."});
db.iliadfr.insert({ pg : 9829, line : "Et Akhilleus, les ayant tous rangés sous leurs chefs, leur dit en"});
db.iliadfr.insert({ pg : 9830, line : "paroles sévères:"});
db.iliadfr.insert({ pg : 9832, line : "-- Myrmidones, qu'aucun de vous n'oublie les menaces que, dans les"});
db.iliadfr.insert({ pg : 9833, line : "nefs rapides, vous adressiez aux Troiens, durant les jours de ma"});
db.iliadfr.insert({ pg : 9834, line : "colère, quand vous m'accusiez moi-même, disant: -- Ô dur fils de"});
db.iliadfr.insert({ pg : 9835, line : "Pèleus, sans doute une mère farouche t'a nourri de fiel, toi qui"});
db.iliadfr.insert({ pg : 9836, line : "retiens de force tes compagnons sur leurs nefs! Que nous"});
db.iliadfr.insert({ pg : 9837, line : "retournions au moins dans nos demeures sur les nefs qui fendent la"});
db.iliadfr.insert({ pg : 9838, line : "mer, puisqu'une colère inexorable est entrée dans ton coeur. --"});
db.iliadfr.insert({ pg : 9839, line : "Souvent vous me parliez ainsi. Aujourd'hui, voici le grand combat"});
db.iliadfr.insert({ pg : 9840, line : "dont vous étiez avides. Que chacun de vous, avec un coeur solide,"});
db.iliadfr.insert({ pg : 9841, line : "lutte donc contre les Troiens."});
db.iliadfr.insert({ pg : 9843, line : "Il parla ainsi, et il excita la force et le courage de chacun, et"});
db.iliadfr.insert({ pg : 9844, line : "ils serrèrent leurs rangs. De même qu'un homme fortifie de pierres"});
db.iliadfr.insert({ pg : 9845, line : "épaisses le mur d'une haute maison qui soutiendra l'effort des"});
db.iliadfr.insert({ pg : 9846, line : "vents, de même les casques et les boucliers bombés se pressèrent,"});
db.iliadfr.insert({ pg : 9847, line : "tous se soutenant les uns les autres, boucliers contre boucliers,"});
db.iliadfr.insert({ pg : 9848, line : "casques à crinières étincelantes contre casques, homme contre"});
db.iliadfr.insert({ pg : 9849, line : "homme. Et Patroklos et Automédôn, qui n'avaient qu'une âme, se"});
db.iliadfr.insert({ pg : 9850, line : "mirent en tête des Myrmidones."});
db.iliadfr.insert({ pg : 9852, line : "Mais Akhilleus entra sous sa tente, et souleva le couvercle d'un"});
db.iliadfr.insert({ pg : 9853, line : "coffre riche et bien fait, et plein de tuniques, de manteaux"});
db.iliadfr.insert({ pg : 9854, line : "impénétrables au vent et de tapis velus. Et là se trouvait une"});
db.iliadfr.insert({ pg : 9855, line : "coupe d'un beau travail dans laquelle le vin ardent n'avait été"});
db.iliadfr.insert({ pg : 9856, line : "versé que pour Akhilleus seul entre tous les hommes, et qui"});
db.iliadfr.insert({ pg : 9857, line : "n'avait fait de libations qu'au père Zeus seul entre tous les"});
db.iliadfr.insert({ pg : 9858, line : "dieux. Et, l'ayant retirée du coffre, il la purifia avec du"});
db.iliadfr.insert({ pg : 9859, line : "soufre, puis il la lava avec de l'eau pure et claire, et il lava"});
db.iliadfr.insert({ pg : 9860, line : "ses mains aussi; et, puisant le vin ardent, faisant des libations"});
db.iliadfr.insert({ pg : 9861, line : "et regardant l'Ouranos, il pria debout au milieu de tous, et Zeus"});
db.iliadfr.insert({ pg : 9862, line : "qui se réjouit de la foudre l'entendit et le vit:"});
db.iliadfr.insert({ pg : 9864, line : "-- Zeus! roi Dôdônaien, Pélasgique, qui, habitant au loin,"});
db.iliadfr.insert({ pg : 9865, line : "commandes sur Dôdônè enveloppée par l'hiver, au milieu de tes"});
db.iliadfr.insert({ pg : 9866, line : "divinateurs, les Selles, qui ne se lavent point les pieds et"});
db.iliadfr.insert({ pg : 9867, line : "dorment sur la terre, si tu as déjà exaucé ma prière, et si, pour"});
db.iliadfr.insert({ pg : 9868, line : "m'honorer, tu as rudement châtié le peuple des Akhaiens, accomplis"});
db.iliadfr.insert({ pg : 9869, line : "encore mon voeu! Je reste dans l'enceinte de mes nefs, mais"});
db.iliadfr.insert({ pg : 9870, line : "j'envoie mon compagnon combattre en tête de nombreux Myrmidones. Ô"});
db.iliadfr.insert({ pg : 9871, line : "Prévoyant Zeus! donne-lui la victoire, affermis son coeur dans sa"});
db.iliadfr.insert({ pg : 9872, line : "poitrine, et que Hektôr apprenne que mon compagnon sait combattre"});
db.iliadfr.insert({ pg : 9873, line : "seul et que ses mains robustes n'attendent point pour agir que je"});
db.iliadfr.insert({ pg : 9874, line : "me rue dans le carnage d'Arès. Mais, ayant repoussé la guerre et"});
db.iliadfr.insert({ pg : 9875, line : "ses clameurs loin des nefs, qu'il revienne, sain et sauf, vers mes"});
db.iliadfr.insert({ pg : 9876, line : "nefs rapides, avec mes armes et mes braves compagnons!"});
db.iliadfr.insert({ pg : 9878, line : "Il parla ainsi en priant, et le sage Zeus l'entendit, et il exauça"});
db.iliadfr.insert({ pg : 9879, line : "une partie de sa prière, et il rejeta l'autre. Il voulut bien que"});
db.iliadfr.insert({ pg : 9880, line : "Patroklos repoussât la guerre et le combat loin des nefs, mais il"});
db.iliadfr.insert({ pg : 9881, line : "ne voulut pas qu'il revînt sain et sauf du combat. Après avoir"});
db.iliadfr.insert({ pg : 9882, line : "fait des libations et supplié le père Zeus, le Pèléide rentra sous"});
db.iliadfr.insert({ pg : 9883, line : "sa tente et déposa la coupe dans le coffre; et il sortit de"});
db.iliadfr.insert({ pg : 9884, line : "nouveau pour regarder la rude mêlée des Troiens et des Akhaiens."});
db.iliadfr.insert({ pg : 9886, line : "Et les Myrmidones, rangés sous le magnanime Patroklos, se ruèrent,"});
db.iliadfr.insert({ pg : 9887, line : "pleins d'ardeur, contre les Troiens. Et ils se répandaient"});
db.iliadfr.insert({ pg : 9888, line : "semblables à des guêpes, nichées sur le bord du chemin, et que des"});
db.iliadfr.insert({ pg : 9889, line : "enfants se plaisent à irriter dans leurs nids. Et ces insensés"});
db.iliadfr.insert({ pg : 9890, line : "préparent un grand mal pour beaucoup; car, si un voyageur les"});
db.iliadfr.insert({ pg : 9891, line : "excite involontairement au passage, les guêpes au coeur intrépide"});
db.iliadfr.insert({ pg : 9892, line : "tourbillonnent et défendent leurs petits. Ainsi les braves"});
db.iliadfr.insert({ pg : 9893, line : "Myrmidones se répandaient hors des nefs; et une immense clameur"});
db.iliadfr.insert({ pg : 9894, line : "s'éleva; et Patroklos exhorta ainsi ses compagnons à voix haute:"});
db.iliadfr.insert({ pg : 9896, line : "-- Myrmidones, compagnons du Pèléide Akhilleus, amis, soyez des"});
db.iliadfr.insert({ pg : 9897, line : "hommes, et souvenez-vous de votre force et de votre courage, afin"});
db.iliadfr.insert({ pg : 9898, line : "d'honorer le Pèléide, le plus brave des hommes, auprès des nefs"});
db.iliadfr.insert({ pg : 9899, line : "des Argiens, et nous, ses belliqueux compagnons. Et que l'Atréide"});
db.iliadfr.insert({ pg : 9900, line : "Agamemnôn qui commande au loin reconnaisse sa faute, lui qui a"});
db.iliadfr.insert({ pg : 9901, line : "outragé le plus brave des Akhaiens."});
db.iliadfr.insert({ pg : 9903, line : "Il parla ainsi, et il excita leur force et leur courage, et ils se"});
db.iliadfr.insert({ pg : 9904, line : "ruèrent avec fureur sur les Troiens, et les nefs résonnèrent des"});
db.iliadfr.insert({ pg : 9905, line : "hautes clameurs des Akhaiens. Et, alors, les Troiens virent le"});
db.iliadfr.insert({ pg : 9906, line : "brave fils de Ménoitios et son compagnon, tous deux"});
db.iliadfr.insert({ pg : 9907, line : "resplendissants sous leurs armes. Leurs coeurs en furent émus, et"});
db.iliadfr.insert({ pg : 9908, line : "leurs phalanges se troublèrent; et ils crurent que le Pèléide aux"});
db.iliadfr.insert({ pg : 9909, line : "pieds rapides avait déposé sa colère auprès des nefs. Et chacun"});
db.iliadfr.insert({ pg : 9910, line : "regardait de tous côtés comment il éviterait la mort."});
db.iliadfr.insert({ pg : 9912, line : "Et Patroklos, le premier, lança sa pique éclatante au plus épais"});
db.iliadfr.insert({ pg : 9913, line : "de la mêlée tumultueuse, autour de la poupe de la nef du magnanime"});
db.iliadfr.insert({ pg : 9914, line : "Prôtésilaos. Et il frappa Pyraikhmès, qui avait amené les"});
db.iliadfr.insert({ pg : 9915, line : "cavaliers Paiones d'Amydônè et des bords de l'Axios au large"});
db.iliadfr.insert({ pg : 9916, line : "cours; et il le frappa à l'épaule droite, et Pyraikhmès tomba dans"});
db.iliadfr.insert({ pg : 9917, line : "la poussière en gémissant, et les Paiones prirent la fuite."});
db.iliadfr.insert({ pg : 9918, line : "Patroklos les dispersa tous ainsi, ayant tué leur chef qui"});
db.iliadfr.insert({ pg : 9919, line : "excellait dans le combat. Et il arracha le feu de la nef, et il"});
db.iliadfr.insert({ pg : 9920, line : "l'éteignit. Et les Troiens, dans un immense tumulte, s'enfuirent"});
db.iliadfr.insert({ pg : 9921, line : "loin de la nef à demi brûlée, et les Danaens, sortant en foule des"});
db.iliadfr.insert({ pg : 9922, line : "nefs creuses, se jetèrent sur eux, et une haute clameur s'éleva."});
db.iliadfr.insert({ pg : 9923, line : "De même que, le foudroyant Zeus ayant dissipé les nuées noires au"});
db.iliadfr.insert({ pg : 9924, line : "faîte d'une grande montagne, tout apparaît soudainement, les"});
db.iliadfr.insert({ pg : 9925, line : "cavernes, les cimes aiguës et les bois, et qu'une immense sérénité"});
db.iliadfr.insert({ pg : 9926, line : "se répand dans l'aithèr; de même les Danaens respirèrent après"});
db.iliadfr.insert({ pg : 9927, line : "avoir éloigné des nefs la flamme ennemie. Mais ce ne fut point la"});
db.iliadfr.insert({ pg : 9928, line : "fin du combat. Les Troiens, repoussés des nefs noires par les"});
db.iliadfr.insert({ pg : 9929, line : "Akhaiens belliqueux, ne fuyaient point bouleversés, mais ils"});
db.iliadfr.insert({ pg : 9930, line : "résistaient encore, bien que cédant à la nécessité. Alors, dans la"});
db.iliadfr.insert({ pg : 9931, line : "mêlée élargie, chaque chef Akhaien tua un guerrier."});
db.iliadfr.insert({ pg : 9933, line : "Et, le premier de tous, le brave fils de Ménoitios perça de sa"});
db.iliadfr.insert({ pg : 9934, line : "pique aiguë la cuisse d'Arèilykos qui fuyait. L'airain traversa la"});
db.iliadfr.insert({ pg : 9935, line : "cuisse et brisa l'os, et l'homme tomba la face contre terre. Et le"});
db.iliadfr.insert({ pg : 9936, line : "brave Ménélaos frappa Thoas à l'endroit de la poitrine que le"});
db.iliadfr.insert({ pg : 9937, line : "bouclier ne couvrait pas, et il rompit ses forces. Et le Phyléide,"});
db.iliadfr.insert({ pg : 9938, line : "voyant Amphiklos qui s'élançait, le prévint en le frappant au bas"});
db.iliadfr.insert({ pg : 9939, line : "de la cuisse, là où les muscles sont très-épais; et la pointe"});
db.iliadfr.insert({ pg : 9940, line : "d'airain déchira les nerfs, et l'obscurité couvrit les yeux"});
db.iliadfr.insert({ pg : 9941, line : "d'Amphiklos. Et la lance aiguë du Nestôride blessa Atymnios, et"});
db.iliadfr.insert({ pg : 9942, line : "l'airain traversa les entrailles, et le Troien tomba devant"});
db.iliadfr.insert({ pg : 9943, line : "Antilokhos. Et Maris, irrité de la mort de son frère, et debout"});
db.iliadfr.insert({ pg : 9944, line : "devant le cadavre, lança sa pique contre Antilokhos; mais le divin"});
db.iliadfr.insert({ pg : 9945, line : "Thrasymèdès le prévint, comme il allait frapper, et le perça près"});
db.iliadfr.insert({ pg : 9946, line : "de l'épaule, et la pointe d'airain, tranchant tous les muscles,"});
db.iliadfr.insert({ pg : 9947, line : "dépouilla l'os de toute sa chair. Et Maris tomba avec bruit, et un"});
db.iliadfr.insert({ pg : 9948, line : "noir brouillard couvrit ses yeux. Ainsi descendirent dans l'Érébos"});
db.iliadfr.insert({ pg : 9949, line : "deux frères, braves compagnons de Sarpèdôn, et tous deux fils"});
db.iliadfr.insert({ pg : 9950, line : "d'Amisôdaros qui avait nourri l'indomptable Khimaira pour la"});
db.iliadfr.insert({ pg : 9951, line : "destruction des hommes."});
db.iliadfr.insert({ pg : 9953, line : "Aias Oiliade saisit vivant Kléoboulos embarrassé dans la mêlée, et"});
db.iliadfr.insert({ pg : 9954, line : "il le tua en le frappant de son épée à la gorge, et toute l'épée y"});
db.iliadfr.insert({ pg : 9955, line : "entra chaude de sang, et la mort pourprée et la Moire violente"});
db.iliadfr.insert({ pg : 9956, line : "obscurcirent ses yeux. Pènéléôs et Lykôn, s'attaquant, se"});
db.iliadfr.insert({ pg : 9957, line : "manquèrent de leurs lances et combattirent avec leurs épées. Lykôn"});
db.iliadfr.insert({ pg : 9958, line : "frappa le cône du casque à aigrette de crins, et l'épée se rompit;"});
db.iliadfr.insert({ pg : 9959, line : "mais Pènéléôs le perça au cou, sous l'oreille, et l'épée y entra"});
db.iliadfr.insert({ pg : 9960, line : "tout entière, et la tête fut suspendue à la peau, et Lykôn fut"});
db.iliadfr.insert({ pg : 9961, line : "tué. Et Mèrionès, poursuivant avec rapidité Akamas qui montait sur"});
db.iliadfr.insert({ pg : 9962, line : "son char, le frappa à l'épaule droite, et le Troien tomba du char,"});
db.iliadfr.insert({ pg : 9963, line : "et une nuée obscurcit ses yeux."});
db.iliadfr.insert({ pg : 9965, line : "Idoméneus frappa de sa pique Érymas dans la bouche, et la pique"});
db.iliadfr.insert({ pg : 9966, line : "d'airain pénétra jusque dans la cervelle en brisant les os blancs;"});
db.iliadfr.insert({ pg : 9967, line : "et toutes les dents furent ébranlées, et les deux yeux s'emplirent"});
db.iliadfr.insert({ pg : 9968, line : "de sang, et le sang jaillit de la bouche et des narines, et la"});
db.iliadfr.insert({ pg : 9969, line : "nuée noire de la mort l'enveloppa."});
db.iliadfr.insert({ pg : 9971, line : "Ainsi les chefs Danaens tuèrent chacun un guerrier. De même que"});
db.iliadfr.insert({ pg : 9972, line : "des loups féroces se jettent, dans les montagnes, sur des agneaux"});
db.iliadfr.insert({ pg : 9973, line : "ou des chevreaux que les bergers imprudents ont laissés, dispersés"});
db.iliadfr.insert({ pg : 9974, line : "çà et là, et qui les emportent tout tremblants; de même les"});
db.iliadfr.insert({ pg : 9975, line : "Danaens bouleversaient les Troiens qui fuyaient tumultueusement,"});
db.iliadfr.insert({ pg : 9976, line : "oubliant leur force et leur courage."});
db.iliadfr.insert({ pg : 9978, line : "Et le grand Aias désirait surtout atteindre Hektôr arme d'airain;"});
db.iliadfr.insert({ pg : 9979, line : "mais celui-ci, habile au combat, couvrant ses larges épaules de"});
db.iliadfr.insert({ pg : 9980, line : "son bouclier de peau de taureau, observait le bruit strident des"});
db.iliadfr.insert({ pg : 9981, line : "flèches et le son des piques. Et il comprenait les chances du"});
db.iliadfr.insert({ pg : 9982, line : "combat; et toujours ferme, il protégeait ses chers compagnons. De"});
db.iliadfr.insert({ pg : 9983, line : "même qu'une nuée monte de l'Olympos jusque dans l'Ouranos, quand"});
db.iliadfr.insert({ pg : 9984, line : "Zeus excite la tempête dans la sérénité de l'aithèr, de même la"});
db.iliadfr.insert({ pg : 9985, line : "clameur et la fuite s'élançaient des nefs. Et les Troiens ne"});
db.iliadfr.insert({ pg : 9986, line : "repassèrent point le fossé aisément. Les chevaux rapides de Hektôr"});
db.iliadfr.insert({ pg : 9987, line : "l'emportèrent loin de son peuple que le fossé profond arrêtait. Et"});
db.iliadfr.insert({ pg : 9988, line : "une multitude de chevaux s'y précipitaient, brisant les timons et"});
db.iliadfr.insert({ pg : 9989, line : "abandonnant les chars des princes. Et Patroklos les poursuivait"});
db.iliadfr.insert({ pg : 9990, line : "avec fureur, exhortant les Danaens et méditant la ruine des"});
db.iliadfr.insert({ pg : 9991, line : "Troiens. Et ceux-ci, pleins de clameurs, emplissaient les chemins"});
db.iliadfr.insert({ pg : 9992, line : "de leur fuite; et une vaste poussière montait vers les nuées, et"});
db.iliadfr.insert({ pg : 9993, line : "les chevaux aux sabots massifs couraient vers la ville, loin des"});
db.iliadfr.insert({ pg : 9994, line : "nefs et des tentes. Et Patroklos poussait, avec des cris"});
db.iliadfr.insert({ pg : 9995, line : "menaçants, cette armée bouleversée. Et les hommes tombaient hors"});
db.iliadfr.insert({ pg : 9996, line : "des chars sous les essieux, et les chars bondissants"});
db.iliadfr.insert({ pg : 9997, line : "retentissaient. Et les chevaux immortels et rapides, illustres"});
db.iliadfr.insert({ pg : 9998, line : "présents des dieux à Pèleus, franchirent le fossé profond, pleins"});
db.iliadfr.insert({ pg : 9999, line : "du désir de la course. Et le coeur de Patroklos le poussait vers"});
db.iliadfr.insert({ pg : 10000, line : "Hektôr, afin de le frapper de sa pique; mais les chevaux rapides"});
db.iliadfr.insert({ pg : 10001, line : "du Priamide l'avaient emporté."});
db.iliadfr.insert({ pg : 10003, line : "Dans les jours de l'automne, quand la terre est accablée sous de"});
db.iliadfr.insert({ pg : 10004, line : "noirs tourbillons, et quand Zeus répand une pluie abondante,"});
db.iliadfr.insert({ pg : 10005, line : "irrité contre les hommes qui jugeaient avec iniquité dans l'agora"});
db.iliadfr.insert({ pg : 10006, line : "et chassaient la justice, sans respect des dieux, de même qu'ils"});
db.iliadfr.insert({ pg : 10007, line : "voient maintenant les torrents creuser leurs campagnes et se"});
db.iliadfr.insert({ pg : 10008, line : "précipiter dans la mer pourprée du haut des rochers escarpés,"});
db.iliadfr.insert({ pg : 10009, line : "détruisant de tous côtés les travaux des hommes; de même on voyait"});
db.iliadfr.insert({ pg : 10010, line : "les cavales troiennes courir épouvantées. Et Patroklos, ayant"});
db.iliadfr.insert({ pg : 10011, line : "rompu les premières phalanges, les repoussa vers les nefs et ne"});
db.iliadfr.insert({ pg : 10012, line : "leur permit pas de regagner la ville qu'elles désiraient"});
db.iliadfr.insert({ pg : 10013, line : "atteindre. Et il les massacrait, en les poursuivant, entre les"});
db.iliadfr.insert({ pg : 10014, line : "nefs, le fleuve et les hautes murailles, et il tirait vengeance"});
db.iliadfr.insert({ pg : 10015, line : "d'un grand nombre d'hommes. Et il frappa d'abord Pronoos, de sa"});
db.iliadfr.insert({ pg : 10016, line : "pique éclatante, dans la poitrine découverte par le bouclier. Et"});
db.iliadfr.insert({ pg : 10017, line : "les forces du Troien furent rompues, et il retentit en tombant. Et"});
db.iliadfr.insert({ pg : 10018, line : "il attaqua Thestôr, fils d'Énops. Et Thestôr était affaissé sur le"});
db.iliadfr.insert({ pg : 10019, line : "siège du char, l'esprit troublé; et les rênes lui étaient tombées"});
db.iliadfr.insert({ pg : 10020, line : "des mains. Patroklos le frappa de sa lance à la joue droite, et"});
db.iliadfr.insert({ pg : 10021, line : "l'airain passa à travers les dents, et, comme il le ramenait, il"});
db.iliadfr.insert({ pg : 10022, line : "arracha l'homme du char. Ainsi un homme, assis au faîte d'un haut"});
db.iliadfr.insert({ pg : 10023, line : "rocher qui avance, à l'aide de l'hameçon brillant et de la ligne,"});
db.iliadfr.insert({ pg : 10024, line : "attire un grand poisson hors de la mer. Ainsi Patroklos enleva du"});
db.iliadfr.insert({ pg : 10025, line : "char, à l'aide de sa lance éclatante, Thestôr, la bouche béante;"});
db.iliadfr.insert({ pg : 10026, line : "et celui-ci, en tombant, rendit l'âme. Puis il frappa d'une pierre"});
db.iliadfr.insert({ pg : 10027, line : "dans la tête Éryalos, qui s'élançait, et dont la tête s'ouvrit en"});
db.iliadfr.insert({ pg : 10028, line : "deux, sous le casque solide, et qui tomba et rendit l'âme,"});
db.iliadfr.insert({ pg : 10029, line : "enveloppé par la mort. Puis, Patroklos coucha, domptés, sur la"});
db.iliadfr.insert({ pg : 10030, line : "terre nourricière, Érymas, Amphotéros, Épaltès, Tlépolémos"});
db.iliadfr.insert({ pg : 10031, line : "Damastoride, Ékhios, Pyrès, Ipheus, Évippos et l'Argéade"});
db.iliadfr.insert({ pg : 10032, line : "Polymèlos. Mais Sarpèdôn, voyant ses compagnons tués et dépouillés"});
db.iliadfr.insert({ pg : 10033, line : "de leurs armes par les mains du Ménoitiade Patroklos, exhorta les"});
db.iliadfr.insert({ pg : 10034, line : "irréprochables Lykiens:"});
db.iliadfr.insert({ pg : 10036, line : "-- Ô honte! Pourquoi fuyez-vous, Lykiens? Vous êtes maintenant"});
db.iliadfr.insert({ pg : 10037, line : "bien rapides! J'irai contre ce guerrier, et je saurai s'il me"});
db.iliadfr.insert({ pg : 10038, line : "domptera, lui qui a accablé les Troiens de tant de maux et qui a"});
db.iliadfr.insert({ pg : 10039, line : "rompu les genoux de tant de braves."});
db.iliadfr.insert({ pg : 10041, line : "Il parla ainsi, et il sauta avec ses armes, de son char, sur la"});
db.iliadfr.insert({ pg : 10042, line : "terre. Et Patroklos le vit et sauta de son char. De même que deux"});
db.iliadfr.insert({ pg : 10043, line : "vautours aux becs recourbés et aux serres aiguës, sur une roche"});
db.iliadfr.insert({ pg : 10044, line : "escarpée luttent avec de grands cris; de même ils se ruèrent l'un"});
db.iliadfr.insert({ pg : 10045, line : "sur l'autre avec des clameurs. Et le fils du subtil Kronos les"});
db.iliadfr.insert({ pg : 10046, line : "ayant vus, fut rempli de compassion, et il dit à Hèrè, sa soeur et"});
db.iliadfr.insert({ pg : 10047, line : "son épouse:"});
db.iliadfr.insert({ pg : 10049, line : "-- Hélas! voici que la destinée de Sarpèdôn qui m'est très-cher"});
db.iliadfr.insert({ pg : 10050, line : "parmi les hommes, est d'être tué par le Ménoitiade Patroklos, et"});
db.iliadfr.insert({ pg : 10051, line : "mon coeur hésitant délibère dans ma poitrine si je le"});
db.iliadfr.insert({ pg : 10052, line : "transporterai vivant du combat lamentable au milieu du riche"});
db.iliadfr.insert({ pg : 10053, line : "peuple de Lykiè, ou si je le dompterai par les mains du"});
db.iliadfr.insert({ pg : 10054, line : "Ménoitiade."});
db.iliadfr.insert({ pg : 10056, line : "Et la vénérable Hèrè aux yeux de boeuf lui répondit:"});
db.iliadfr.insert({ pg : 10058, line : "-- Redoutable Kronide, quelle parole as-tu dite? Tu veux"});
db.iliadfr.insert({ pg : 10059, line : "affranchir de la triste mort un homme mortel depuis longtemps voué"});
db.iliadfr.insert({ pg : 10060, line : "au destin? Fais-le, mais nous tous, les dieux, nous ne"});
db.iliadfr.insert({ pg : 10061, line : "t'approuverons pas. Je te dirai ceci, et retiens-le dans ton"});
db.iliadfr.insert({ pg : 10062, line : "esprit: Si tu envoies Sarpèdôn vivant dans ses demeures, songe"});
db.iliadfr.insert({ pg : 10063, line : "que, désormais, chacun des dieux voudra aussi sauver un fils bien-"});
db.iliadfr.insert({ pg : 10064, line : "aimé de la rude mêlée. Il y a, en effet, beaucoup de fils des"});
db.iliadfr.insert({ pg : 10065, line : "dieux qui combattent autour de la grande ville de Priamos, de ces"});
db.iliadfr.insert({ pg : 10066, line : "dieux que tu auras irrités. Si Sarpèdôn t'est cher et que ton"});
db.iliadfr.insert({ pg : 10067, line : "coeur le plaigne, laisse-le tomber dans la rude mêlée sous les"});
db.iliadfr.insert({ pg : 10068, line : "mains du Ménoitiade Patroklos; mais dès qu'il aura rendu l'âme et"});
db.iliadfr.insert({ pg : 10069, line : "la vie, envoie Thanatos et le doux Hypnos afin qu'ils le"});
db.iliadfr.insert({ pg : 10070, line : "transportent chez le peuple de la grande Lykiè. Ses parents et ses"});
db.iliadfr.insert({ pg : 10071, line : "concitoyens l'enseveliront, et ils lui élèveront un tombeau et une"});
db.iliadfr.insert({ pg : 10072, line : "colonne; car c'est là l'honneur des morts."});
db.iliadfr.insert({ pg : 10074, line : "Elle parla ainsi, et le père des hommes et des dieux consentit. Et"});
db.iliadfr.insert({ pg : 10075, line : "il versa sur la terre une pluie de sang, afin d'honorer son fils"});
db.iliadfr.insert({ pg : 10076, line : "bien-aimé que Patroklos devait tuer dans la fertile Troiè, loin de"});
db.iliadfr.insert({ pg : 10077, line : "sa patrie."});
db.iliadfr.insert({ pg : 10079, line : "Et les deux héros s'étant rencontrés, Patroklos frappa dans le"});
db.iliadfr.insert({ pg : 10080, line : "ventre l'illustre Thrasymèdès qui conduisait le char du roi"});
db.iliadfr.insert({ pg : 10081, line : "Sarpèdôn, et il le tua. Et Sarpèdôn s'élança; mais sa pique"});
db.iliadfr.insert({ pg : 10082, line : "éclatante, s'étant égarée, blessa à l'épaule le cheval Pèdasos qui"});
db.iliadfr.insert({ pg : 10083, line : "hennit, tomba dans la poussière et rendit l'âme. Et ses compagnons"});
db.iliadfr.insert({ pg : 10084, line : "se cabrèrent, et le joug cria, et les rênes furent entremêlées."});
db.iliadfr.insert({ pg : 10085, line : "Mais le brave Automédôn mit fin à ce trouble. Il se leva, et,"});
db.iliadfr.insert({ pg : 10086, line : "tirant la longue épée qui pendait sur sa cuisse robuste, il"});
db.iliadfr.insert({ pg : 10087, line : "trancha les traits qui étaient au-delà du timon. Et les deux"});
db.iliadfr.insert({ pg : 10088, line : "autres chevaux, se remettant au joug, obéirent aux rênes, et les"});
db.iliadfr.insert({ pg : 10089, line : "deux guerriers continuèrent le combat lamentable."});
db.iliadfr.insert({ pg : 10091, line : "Alors la pique éclatante de Sarpèdôn s'égara encore, car la pointe"});
db.iliadfr.insert({ pg : 10092, line : "d'airain effleura l'épaule gauche de Patroklos sans le blesser. Et"});
db.iliadfr.insert({ pg : 10093, line : "celui-ci se rua avec l'airain, et le trait ne s'échappa point"});
db.iliadfr.insert({ pg : 10094, line : "vainement de sa main, car il frappa Sarpèdôn à cette cloison qui"});
db.iliadfr.insert({ pg : 10095, line : "enferme le coeur vivant. Et il tomba comme tombe un chêne, ou un"});
db.iliadfr.insert({ pg : 10096, line : "peuplier, ou un grand pin que les bûcherons, sur les montagnes,"});
db.iliadfr.insert({ pg : 10097, line : "coupent de leurs haches tranchantes, pour construire des nefs. Et"});
db.iliadfr.insert({ pg : 10098, line : "il était étendu devant ses chevaux et son char, grinçant des dents"});
db.iliadfr.insert({ pg : 10099, line : "et saisissant la poussière sanglante. De même qu'un taureau"});
db.iliadfr.insert({ pg : 10100, line : "magnanime qu'un lion fauve a saisi parmi les boeufs aux pieds"});
db.iliadfr.insert({ pg : 10101, line : "flexibles, et qui meurt en mugissant sous les dents du lion, de"});
db.iliadfr.insert({ pg : 10102, line : "même le roi des Lykiens porteurs de boucliers gémissait, dompté"});
db.iliadfr.insert({ pg : 10103, line : "par Patroklos. Et il appela son cher compagnon"});
db.iliadfr.insert({ pg : 10105, line : "-- Ami Glaukos, brave entre les hommes, c'est maintenant qu'il te"});
db.iliadfr.insert({ pg : 10106, line : "faut combattre intrépidement. Si la mêlée lamentable ne trouble"});
db.iliadfr.insert({ pg : 10107, line : "point ton coeur, sois prompt. Les appelant de tous côtés, exhorte"});
db.iliadfr.insert({ pg : 10108, line : "les chefs Lykiens à combattre pour Sarpèdôn, et combats toi-même"});
db.iliadfr.insert({ pg : 10109, line : "pour moi. Je serais à jamais ton opprobre et ta honte si les"});
db.iliadfr.insert({ pg : 10110, line : "Akhaiens me dépouillaient de mes armes dans le combat des nefs."});
db.iliadfr.insert({ pg : 10111, line : "Sois ferme, et exhorte tout mon peuple."});
db.iliadfr.insert({ pg : 10113, line : "Il parla ainsi, et l'ombre de la mort couvrit ses yeux et ses"});
db.iliadfr.insert({ pg : 10114, line : "narines. Et Patroklos, lui mettant le pied sur la poitrine,"});
db.iliadfr.insert({ pg : 10115, line : "arracha sa lance, et les entrailles la suivirent, et le Ménoitiade"});
db.iliadfr.insert({ pg : 10116, line : "arracha en même temps sa lance et l'âme de Sarpèdôn."});
db.iliadfr.insert({ pg : 10118, line : "Les Myrmidones saisirent les chevaux haletants et qui voulaient"});
db.iliadfr.insert({ pg : 10119, line : "fuir depuis que le char de leurs maîtres était vide. Mais, en"});
db.iliadfr.insert({ pg : 10120, line : "entendant la voix de Sarpèdôn, Glaukos ressentit une amère"});
db.iliadfr.insert({ pg : 10121, line : "douleur, et son coeur fut déchiré de ne pouvoir le secourir."});
db.iliadfr.insert({ pg : 10122, line : "Pressant de sa main son bras cruellement blessé par la flèche que"});
db.iliadfr.insert({ pg : 10123, line : "lui avait lancée Teukros, du haut de la muraille, en défendant ses"});
db.iliadfr.insert({ pg : 10124, line : "compagnons, il supplia ainsi l'archer Apollôn:"});
db.iliadfr.insert({ pg : 10126, line : "-- Entends-moi, ô roi! soit de la riche Lykiè, soit de Troiè, car"});
db.iliadfr.insert({ pg : 10127, line : "tu peux entendre de tout lieu les plaintes de l'homme qui gémit,"});
db.iliadfr.insert({ pg : 10128, line : "et voici que la douleur me ronge. Je subis une blessure cruelle,"});
db.iliadfr.insert({ pg : 10129, line : "et ma main est en proie à de grands maux, et mon sang coule sans"});
db.iliadfr.insert({ pg : 10130, line : "cesse, et mon épaule est très-lourde, et je ne puis ni saisir ma"});
db.iliadfr.insert({ pg : 10131, line : "lance, ni combattre l'ennemi. Et voici que le plus illustre des"});
db.iliadfr.insert({ pg : 10132, line : "hommes est mort, Sarpèdôn, fils de Zeus qui n'a point secouru son"});
db.iliadfr.insert({ pg : 10133, line : "fils. Mais toi, ô roi! guéris cette blessure amère, apaise mon"});
db.iliadfr.insert({ pg : 10134, line : "mal, afin que j'excite les Lykiens à combattre et que je combatte"});
db.iliadfr.insert({ pg : 10135, line : "moi-même pour ce cadavre."});
db.iliadfr.insert({ pg : 10137, line : "Il parla ainsi en priant, et Phoibos Apollôn l'entendit et apaisa"});
db.iliadfr.insert({ pg : 10138, line : "aussitôt sa douleur. Et le sang noir cessa de couler de sa"});
db.iliadfr.insert({ pg : 10139, line : "blessure amère, et la force lui fut rendue. Glaukos connut dans"});
db.iliadfr.insert({ pg : 10140, line : "son esprit que le grand dieu avait exaucé sa prière, et il se"});
db.iliadfr.insert({ pg : 10141, line : "réjouit. Et d'abord, courant de tous côtés, il excita les chefs"});
db.iliadfr.insert({ pg : 10142, line : "Lykiens à combattre pour Sarpèdôn puis, marchant à grands pas vers"});
db.iliadfr.insert({ pg : 10143, line : "les Troiens, il chercha Polydamas Panthoide, le divin Agènôr,"});
db.iliadfr.insert({ pg : 10144, line : "Ainéias et Hektôr armé d'airain, et il leur dit ces paroles"});
db.iliadfr.insert({ pg : 10145, line : "ailées:"});
db.iliadfr.insert({ pg : 10147, line : "-- Hektôr, tu oublies tes alliés qui, pour toi, rendent l'âme loin"});
db.iliadfr.insert({ pg : 10148, line : "de leurs amis et de la terre de la patrie, et tu refuses de les"});
db.iliadfr.insert({ pg : 10149, line : "secourir. Le chef des Lykiens porteurs de boucliers est mort,"});
db.iliadfr.insert({ pg : 10150, line : "Sarpèdôn, qui protégeait la Lykiè par sa justice et par sa vertu."});
db.iliadfr.insert({ pg : 10151, line : "Arès d'airain l'a tué par la lance de Patroklos. Venez, amis, et"});
db.iliadfr.insert({ pg : 10152, line : "indignez-vous. Que les Myrmidones, irrités à cause de tant"});
db.iliadfr.insert({ pg : 10153, line : "d'Akhaiens que nous avons tués de nos lances rapides auprès des"});
db.iliadfr.insert({ pg : 10154, line : "nefs, n'enlèvent point les armes et n'insultent point le cadavre"});
db.iliadfr.insert({ pg : 10155, line : "de Sarpèdôn."});
db.iliadfr.insert({ pg : 10157, line : "Il parla ainsi, et une intolérable et irrésistible douleur saisit"});
db.iliadfr.insert({ pg : 10158, line : "les Troiens, car Sarpèdôn, bien qu'étranger, était le rempart de"});
db.iliadfr.insert({ pg : 10159, line : "leur ville, et des peuples nombreux le suivaient, et lui-même"});
db.iliadfr.insert({ pg : 10160, line : "excellait dans le combat. Et ils marchèrent avec ardeur droit aux"});
db.iliadfr.insert({ pg : 10161, line : "Danaens, menés par Hektôr irrité à cause de Sarpèdôn. Mais le"});
db.iliadfr.insert({ pg : 10162, line : "coeur solide de Patroklos Ménoitiade excitait aussi les Akhaiens,"});
db.iliadfr.insert({ pg : 10163, line : "et il dit aux deux Aias prompts aux combats:"});
db.iliadfr.insert({ pg : 10165, line : "-- Aias! soyez aujourd'hui tels que vous avez toujours été parmi"});
db.iliadfr.insert({ pg : 10166, line : "les plus braves et les meilleurs. Il est tombé l'homme qui, le"});
db.iliadfr.insert({ pg : 10167, line : "premier, a franchi le mur des Akhaiens, Sarpèdôn! Insultons ce"});
db.iliadfr.insert({ pg : 10168, line : "cadavre et arrachons ses armes de ses épaules, et tuons de"});
db.iliadfr.insert({ pg : 10169, line : "l'airain tous ceux de ses compagnons qui voudraient le défendre."});
db.iliadfr.insert({ pg : 10171, line : "Il parla ainsi, et les Aias se hâtèrent de lui venir en aide; et"});
db.iliadfr.insert({ pg : 10172, line : "de chaque côté, Troiens, Lykiens, Myrmidones et Akhaiens, serrant"});
db.iliadfr.insert({ pg : 10173, line : "leurs phalanges, se ruaient avec d'horribles clameurs autour du"});
db.iliadfr.insert({ pg : 10174, line : "cadavre, et les armes des hommes retentissaient. Et Zeus répandit"});
db.iliadfr.insert({ pg : 10175, line : "sur la mêlée une obscurité affreuse, afin que le labeur du combat"});
db.iliadfr.insert({ pg : 10176, line : "pour son fils bien-aimé fût plus terrible. Et d'abord les Troiens"});
db.iliadfr.insert({ pg : 10177, line : "repoussèrent les Akhaiens aux sourcils arqués; et un des meilleurs"});
db.iliadfr.insert({ pg : 10178, line : "parmi les Myrmidones fut tué, le divin Épeigeus, fils du magnanime"});
db.iliadfr.insert({ pg : 10179, line : "Agakleus. Et Épeigeus commandait autrefois dans Boudéiôn bien"});
db.iliadfr.insert({ pg : 10180, line : "peuplée; mais, ayant tué son brave beau-frère, il vint en"});
db.iliadfr.insert({ pg : 10181, line : "suppliant auprès de Pèleus et de Thétis aux pieds d'argent, qui"});
db.iliadfr.insert({ pg : 10182, line : "l'envoyèrent, avec le mâle Akhilleus, vers Ilios aux beaux"});
db.iliadfr.insert({ pg : 10183, line : "chevaux, combattre les Troiens. Et comme il mettait la main sur le"});
db.iliadfr.insert({ pg : 10184, line : "cadavre, l'illustre Hektôr le frappa d'une pierre à la tête, et la"});
db.iliadfr.insert({ pg : 10185, line : "tête se fendit en deux, sous le casque solide; et il tomba la face"});
db.iliadfr.insert({ pg : 10186, line : "sur le cadavre. Puis, l'affreuse mort l'enveloppa lui-même, et"});
db.iliadfr.insert({ pg : 10187, line : "Patroklos fut saisi de douleur, à cause de son compagnon tué."});
db.iliadfr.insert({ pg : 10189, line : "Et il se rua à travers les combattants, semblable à un épervier"});
db.iliadfr.insert({ pg : 10190, line : "rapide qui terrifie les geais et les étourneaux. Ainsi le cavalier"});
db.iliadfr.insert({ pg : 10191, line : "Patroklos se rua contre les Lykiens et les Troiens, irrité dans"});
db.iliadfr.insert({ pg : 10192, line : "son coeur à cause de son compagnon. Et il frappa d'une pierre au"});
db.iliadfr.insert({ pg : 10193, line : "cou Sthénélaos Ithaiménide, et les nerfs furent rompus; et les"});
db.iliadfr.insert({ pg : 10194, line : "premiers rangs et l'illustre Hektôr reculèrent d'autant d'espace"});
db.iliadfr.insert({ pg : 10195, line : "qu'en parcourt une pique bien lancée, dans le combat contre des"});
db.iliadfr.insert({ pg : 10196, line : "hommes intrépides ou dans les jeux. Autant reculèrent les Troiens"});
db.iliadfr.insert({ pg : 10197, line : "et s'avancèrent les Akhaiens."});
db.iliadfr.insert({ pg : 10199, line : "Et, le premier, Glaukos, chef des Lykiens porteurs de boucliers,"});
db.iliadfr.insert({ pg : 10200, line : "se retournant, tua le magnanime Bathykleus, fils bien-aimé de"});
db.iliadfr.insert({ pg : 10201, line : "Khalkôn, qui habitait l'Hellas et qui était illustre parmi les"});
db.iliadfr.insert({ pg : 10202, line : "Myrmidones par ses domaines et par ses richesses. Et, Bathykleus"});
db.iliadfr.insert({ pg : 10203, line : "le poursuivant, Glaukos se retourna subitement et le frappa de sa"});
db.iliadfr.insert({ pg : 10204, line : "lance au milieu de la poitrine, et il tomba avec bruit, et une"});
db.iliadfr.insert({ pg : 10205, line : "lourde douleur saisit les Akhaiens quand le guerrier tomba, et les"});
db.iliadfr.insert({ pg : 10206, line : "Troiens se réjouirent; mais les Akhaiens infatigables, se"});
db.iliadfr.insert({ pg : 10207, line : "souvenant de leur courage, se jetèrent en foule autour du cadavre."});
db.iliadfr.insert({ pg : 10209, line : "Alors Mèrionès tua un guerrier Troien, le brave Laogôn, fils"});
db.iliadfr.insert({ pg : 10210, line : "d'Onètôr, prêtre de Zeus Idaien, et que le peuple honorait comme"});
db.iliadfr.insert({ pg : 10211, line : "un dieu. Il le frappa sous la mâchoire et l'oreille, et l'âme"});
db.iliadfr.insert({ pg : 10212, line : "abandonna aussitôt ses membres, et l'affreux brouillard"});
db.iliadfr.insert({ pg : 10213, line : "l'enveloppa. Et Ainéias lança sa pique d'airain contre Mèrionès,"});
db.iliadfr.insert({ pg : 10214, line : "et il espérait l'atteindre sous le bouclier, comme il s'élançait;"});
db.iliadfr.insert({ pg : 10215, line : "mais celui-ci évita la pique d'airain en se courbant, et la longue"});
db.iliadfr.insert({ pg : 10216, line : "pique s'enfonça en terre et vibra jusqu'à ce que le robuste Arès"});
db.iliadfr.insert({ pg : 10217, line : "eût épuisé sa force. Et la pique d'Ainéias vibrait ainsi parce"});
db.iliadfr.insert({ pg : 10218, line : "qu'elle était partie d'une main vigoureuse. Et Ainéias, irrité,"});
db.iliadfr.insert({ pg : 10219, line : "lui dit:"});
db.iliadfr.insert({ pg : 10221, line : "-- Mèrionès, bien que tu sois un agile sauteur, ma pique t'eût"});
db.iliadfr.insert({ pg : 10222, line : "rendu immobile à jamais, si je t'avais atteint."});
db.iliadfr.insert({ pg : 10224, line : "Et Mèrionès illustre par sa lance lui répondit:"});
db.iliadfr.insert({ pg : 10226, line : "-- Ainéias, il te sera difficile, malgré ta vigueur, de rompre les"});
db.iliadfr.insert({ pg : 10227, line : "forces de tous ceux qui te combattront. Si moi aussi, je"});
db.iliadfr.insert({ pg : 10228, line : "t'atteignais de l'airain aigu, bien que tu sois robuste et"});
db.iliadfr.insert({ pg : 10229, line : "confiant dans tes forces, tu me donnerais la gloire et ton âme à"});
db.iliadfr.insert({ pg : 10230, line : "Aidès illustre par ses chevaux."});
db.iliadfr.insert({ pg : 10232, line : "Il parla ainsi, et le robuste fils de Ménoitios le réprimanda:"});
db.iliadfr.insert({ pg : 10234, line : "-- Mèrionès, pourquoi tant parler, étant brave? Ô ami! ce n'est"});
db.iliadfr.insert({ pg : 10235, line : "point par des paroles outrageantes que tu repousseras les Troiens"});
db.iliadfr.insert({ pg : 10236, line : "loin de ce cadavre. La fin de la guerre est dans nos mains. Les"});
db.iliadfr.insert({ pg : 10237, line : "paroles conviennent à l'agora. Il ne s'agit point ici de parler,"});
db.iliadfr.insert({ pg : 10238, line : "mais de combattre."});
db.iliadfr.insert({ pg : 10240, line : "Il parla ainsi, et marcha en avant, et le divin Mèrionès le"});
db.iliadfr.insert({ pg : 10241, line : "suivit. Et de même que les bûcherons font un grand tumulte dans"});
db.iliadfr.insert({ pg : 10242, line : "les gorges des montagnes, et que l'écho retentit au loin; de même"});
db.iliadfr.insert({ pg : 10243, line : "la grande plaine frémissait sous les guerriers qui frappaient, de"});
db.iliadfr.insert({ pg : 10244, line : "leurs épées et de leurs lances, l'airain et le cuir des solides"});
db.iliadfr.insert({ pg : 10245, line : "boucliers; et nul n'aurait plus reconnu le divin Sarpèdôn, tant il"});
db.iliadfr.insert({ pg : 10246, line : "était couvert de traits, de sang et de poussière. Et tous se"});
db.iliadfr.insert({ pg : 10247, line : "ruaient sans cesse autour de son cadavre, comme les mouches qui"});
db.iliadfr.insert({ pg : 10248, line : "bourdonnent, au printemps, dans l'étable, autour des vases remplis"});
db.iliadfr.insert({ pg : 10249, line : "de lait. C'est ainsi qu'ils se ruaient en foule autour de ce"});
db.iliadfr.insert({ pg : 10250, line : "cadavre."});
db.iliadfr.insert({ pg : 10252, line : "Et Zeus, ne détournant point ses yeux splendides de la rude mêlée,"});
db.iliadfr.insert({ pg : 10253, line : "délibérait dans son esprit sur la mort de Patroklos, hésitant si"});
db.iliadfr.insert({ pg : 10254, line : "l'illustre Hektôr le tuerait de suite avec l'airain, dans la"});
db.iliadfr.insert({ pg : 10255, line : "mêlée, sur le divin Sarpèdôn, et lui arracherait ses armes des"});
db.iliadfr.insert({ pg : 10256, line : "épaules, ou si la rude mêlée serait prolongée pour la mort d'un"});
db.iliadfr.insert({ pg : 10257, line : "plus grand nombre. Et il sembla meilleur à Zeus que le brave"});
db.iliadfr.insert({ pg : 10258, line : "compagnon du Pèléide Akhilleus repoussât, vers la ville, Hektôr et"});
db.iliadfr.insert({ pg : 10259, line : "les Troiens, et arrachât l'âme de beaucoup de guerriers. Et c'est"});
db.iliadfr.insert({ pg : 10260, line : "pourquoi il amollit le courage de Hektôr qui, montant sur son"});
db.iliadfr.insert({ pg : 10261, line : "char, prit la fuite en ordonnant aux Troiens de fuir aussi, car il"});
db.iliadfr.insert({ pg : 10262, line : "avait reconnu les balances sacrées de Zeus. Et les illustres"});
db.iliadfr.insert({ pg : 10263, line : "Lykiens ne restèrent point, et ils prirent aussi la fuite en"});
db.iliadfr.insert({ pg : 10264, line : "voyant leur roi couché, le coeur percé, au milieu des cadavres,"});
db.iliadfr.insert({ pg : 10265, line : "car beaucoup étaient tombés pendant que le Kroniôn excitait le"});
db.iliadfr.insert({ pg : 10266, line : "combat. Et les Akhaiens arrachèrent des épaules de Sarpèdôn ses"});
db.iliadfr.insert({ pg : 10267, line : "belles armes resplendissantes, et le robuste fils de Ménoitios les"});
db.iliadfr.insert({ pg : 10268, line : "donna à ses compagnons pour être portées aux nefs creuses. Et"});
db.iliadfr.insert({ pg : 10269, line : "alors Zeus qui amasse les nuées dit à Apollôn:"});
db.iliadfr.insert({ pg : 10271, line : "-- Va maintenant, cher Phoibos. Purifie Sarpèdôn, hors de la"});
db.iliadfr.insert({ pg : 10272, line : "mêlée, du sang noir qui le souille. Lave-le dans les eaux du"});
db.iliadfr.insert({ pg : 10273, line : "fleuve, et, l'ayant oint d'ambroisie, couvre-le de vêtements"});
db.iliadfr.insert({ pg : 10274, line : "immortels. Puis, remets-le aux Jumeaux rapides, Hypnos et"});
db.iliadfr.insert({ pg : 10275, line : "Thanatos, pour qu'ils le portent chez le riche peuple de la grande"});
db.iliadfr.insert({ pg : 10276, line : "Lykiè. Ses parents et ses amis l'enseveliront et lui élèveront un"});
db.iliadfr.insert({ pg : 10277, line : "tombeau et une colonne, car c'est là l'honneur des morts."});
db.iliadfr.insert({ pg : 10279, line : "Il parla ainsi, et Apollôn, se hâtant d'obéir à son père,"});
db.iliadfr.insert({ pg : 10280, line : "descendit des cimes Idaiennes dans la mêlée et enleva Sarpèdôn"});
db.iliadfr.insert({ pg : 10281, line : "loin des traits. Et il le transporta pour le laver dans les eaux"});
db.iliadfr.insert({ pg : 10282, line : "du fleuve, l'oignit d'ambroisie, le couvrit de vêtements immortels"});
db.iliadfr.insert({ pg : 10283, line : "et le confia aux Jumeaux rapides, Hypnos et Thanatos, qui le"});
db.iliadfr.insert({ pg : 10284, line : "transportèrent aussitôt chez le riche peuple de la grande Lykiè."});
db.iliadfr.insert({ pg : 10286, line : "Et Patroklos, excitant Automédôn et ses chevaux, poursuivait les"});
db.iliadfr.insert({ pg : 10287, line : "Lykiens et les Troiens, pour son malheur, l'insensé! car s'il"});
db.iliadfr.insert({ pg : 10288, line : "avait obéi à l'ordre du Pèléide, il aurait évité la kèr mauvaise"});
db.iliadfr.insert({ pg : 10289, line : "de la noire mort. Mais l'esprit de Zeus est plus puissant que"});
db.iliadfr.insert({ pg : 10290, line : "celui des hommes. Il terrifie le brave que lui-même a poussé au"});
db.iliadfr.insert({ pg : 10291, line : "combat, et il lui enlève la victoire."});
db.iliadfr.insert({ pg : 10293, line : "Et, maintenant, quel fut le premier, quel fut le dernier que tu"});
db.iliadfr.insert({ pg : 10294, line : "tuas, ô Patroklos, quand les dieux préparèrent ta mort? Adrèstès,"});
db.iliadfr.insert({ pg : 10295, line : "Autonoos et Ekhéklos, Périmos Mégade et Épistôr, et Mélanippos;"});
db.iliadfr.insert({ pg : 10296, line : "puis, Élasos, Moulios et Phylartès. Il tua ceux-ci, et les autres"});
db.iliadfr.insert({ pg : 10297, line : "échappèrent par la fuite. Et alors les fils des Akhaiens eussent"});
db.iliadfr.insert({ pg : 10298, line : "pris la haute Ilios par les mains de Patroklos furieux, si Phoibos"});
db.iliadfr.insert({ pg : 10299, line : "Apollôn, debout au faîte d'une tour solide, préparant la perte du"});
db.iliadfr.insert({ pg : 10300, line : "Ménoitiade, ne fût venu en aide aux Troiens. Et trois fois"});
db.iliadfr.insert({ pg : 10301, line : "Patroklos s'élança jusqu'au relief de la haute muraille, et trois"});
db.iliadfr.insert({ pg : 10302, line : "fois Apollôn le repoussa de ses mains immortelles, en heurtant son"});
db.iliadfr.insert({ pg : 10303, line : "bouclier éclatant. Et, quand il s'élança une quatrième fois,"});
db.iliadfr.insert({ pg : 10304, line : "semblable à un dieu, l'archer Apollôn lui dit ces paroles"});
db.iliadfr.insert({ pg : 10305, line : "menaçantes:"});
db.iliadfr.insert({ pg : 10307, line : "-- Retire-toi, divin Patroklos. Il n'est pas dans ta destinée de"});
db.iliadfr.insert({ pg : 10308, line : "renverser de ta lance la haute citadelle des magnanimes Troiens."});
db.iliadfr.insert({ pg : 10309, line : "Akhilleus lui-même ne le pourra point, bien qu'il te soit très-"});
db.iliadfr.insert({ pg : 10310, line : "supérieur."});
db.iliadfr.insert({ pg : 10312, line : "Il parla ainsi, et Patroklos recula au loin pour éviter la colère"});
db.iliadfr.insert({ pg : 10313, line : "de l'archer Apollôn. Et Hektôr, retenant ses chevaux aux sabots"});
db.iliadfr.insert({ pg : 10314, line : "solides près des Portes Skaies, hésitait s'il retournerait au"});
db.iliadfr.insert({ pg : 10315, line : "combat, ou s'il ordonnerait aux troupes de se renfermer dans les"});
db.iliadfr.insert({ pg : 10316, line : "murailles."});
db.iliadfr.insert({ pg : 10318, line : "Et Phoibos Apollôn s'approcha de lui, semblable au jeune et brave"});
db.iliadfr.insert({ pg : 10319, line : "guerrier Asios, fils de Dymas, frère de Hékabè et oncle du"});
db.iliadfr.insert({ pg : 10320, line : "dompteur de chevaux Hektôr, et qui habitait la Phrygiè sur les"});
db.iliadfr.insert({ pg : 10321, line : "bords du Sangarios. Et, semblable à Asios, Phoibos Apollôn dit à"});
db.iliadfr.insert({ pg : 10322, line : "Hektôr:"});
db.iliadfr.insert({ pg : 10324, line : "-- Hektôr, pourquoi t'éloignes-tu du combat? Cela ne te convient"});
db.iliadfr.insert({ pg : 10325, line : "pas. Plût aux dieux que je te fusse supérieur autant que je te"});
db.iliadfr.insert({ pg : 10326, line : "suis inférieur, il te serait fatal d'avoir quitté le combat."});
db.iliadfr.insert({ pg : 10327, line : "Allons, pousse tes chevaux aux sabots massifs contre Patroklos. Tu"});
db.iliadfr.insert({ pg : 10328, line : "le tueras peut-être, et Apollôn te donnera la victoire."});
db.iliadfr.insert({ pg : 10330, line : "Ayant ainsi parlé, le dieu rentra dans la foule des guerriers. Et"});
db.iliadfr.insert({ pg : 10331, line : "l'illustre Hektôr ordonna au brave Kébrionès d'exciter ses chevaux"});
db.iliadfr.insert({ pg : 10332, line : "vers la mêlée. Et Apollôn, au milieu de la foule, répandit le"});
db.iliadfr.insert({ pg : 10333, line : "trouble parmi les Argiens et accorda la victoire à Hektôr et aux"});
db.iliadfr.insert({ pg : 10334, line : "Troiens. Et le Priamide, laissant tous les autres Danaens,"});
db.iliadfr.insert({ pg : 10335, line : "poussait vers le seul Patroklos ses chevaux aux sabots massifs. Et"});
db.iliadfr.insert({ pg : 10336, line : "Patroklos, de son côté, sauta de son char, tenant sa pique de la"});
db.iliadfr.insert({ pg : 10337, line : "main gauche. Et il saisit de la droite un morceau de marbre, rude"});
db.iliadfr.insert({ pg : 10338, line : "et anguleux, d'abord caché dans sa main, et qu'il lança avec"});
db.iliadfr.insert({ pg : 10339, line : "effort. Et ce ne fut pas en vain, car cette pierre aiguë frappa au"});
db.iliadfr.insert({ pg : 10340, line : "front le conducteur de chevaux Kébrionès, bâtard de l'illustre"});
db.iliadfr.insert({ pg : 10341, line : "Priamos. Et la pierre coupa les deux sourcils, et l'os ne résista"});
db.iliadfr.insert({ pg : 10342, line : "pas, et les yeux du Troien jaillirent à ses pieds dans la"});
db.iliadfr.insert({ pg : 10343, line : "poussière. Et, semblable au plongeur, il tomba du char, et son âme"});
db.iliadfr.insert({ pg : 10344, line : "abandonna ses membres. Et le cavalier Patroklos cria avec une"});
db.iliadfr.insert({ pg : 10345, line : "raillerie amère:"});
db.iliadfr.insert({ pg : 10347, line : "-- Ah! certes, voici un homme agile! Comme il plonge! Vraiment, il"});
db.iliadfr.insert({ pg : 10348, line : "rassasierait de coquillages toute une multitude, en sautant de sa"});
db.iliadfr.insert({ pg : 10349, line : "nef dans la mer, même si elle était agitée, puisqu'il plonge aussi"});
db.iliadfr.insert({ pg : 10350, line : "aisément du haut d'un char. Certes, il y a d'excellents plongeurs"});
db.iliadfr.insert({ pg : 10351, line : "parmi les Troiens!"});
db.iliadfr.insert({ pg : 10353, line : "Ayant ainsi parlé, il s'élança sur le héros Kébrionès, comme un"});
db.iliadfr.insert({ pg : 10354, line : "lion impétueux qui va dévaster une étable et recevoir une blessure"});
db.iliadfr.insert({ pg : 10355, line : "en pleine poitrine, car il se perd par sa propre ardeur. Ainsi,"});
db.iliadfr.insert({ pg : 10356, line : "Patroklos, tu te ruas sur Kébrionès. Et le Priamide sauta de son"});
db.iliadfr.insert({ pg : 10357, line : "char, et tous deux luttèrent pour le cadavre, comme deux lions"});
db.iliadfr.insert({ pg : 10358, line : "pleins de faim combattent, sur les montagnes, pour une biche"});
db.iliadfr.insert({ pg : 10359, line : "égorgée. Ainsi, sur le cadavre de Kébrionès, les deux habiles"});
db.iliadfr.insert({ pg : 10360, line : "guerriers, Patroklos Ménoitiade et l'illustre Hektôr, désiraient"});
db.iliadfr.insert({ pg : 10361, line : "se percer l'un l'autre de l'airain cruel. Et le Priamide tenait le"});
db.iliadfr.insert({ pg : 10362, line : "cadavre par la tête et ne lâchait point prise, tandis que"});
db.iliadfr.insert({ pg : 10363, line : "Patroklos le tenait par les pieds. Et les Troiens et les Danaens"});
db.iliadfr.insert({ pg : 10364, line : "engagèrent alors un rude combat."});
db.iliadfr.insert({ pg : 10366, line : "De même que l'Euros et le Notos, par leur rencontre furieuse,"});
db.iliadfr.insert({ pg : 10367, line : "bouleversent, dans les gorges des montagnes, une haute forêt de"});
db.iliadfr.insert({ pg : 10368, line : "hêtres, de frênes et de cornouillers à écorce épaisse, qui"});
db.iliadfr.insert({ pg : 10369, line : "heurtent leurs vastes rameaux et se rompent avec bruit; ainsi les"});
db.iliadfr.insert({ pg : 10370, line : "Troiens et les Akhaiens, se ruant les uns sur les autres,"});
db.iliadfr.insert({ pg : 10371, line : "combattaient et ne fuyaient point honteusement. Et les lances"});
db.iliadfr.insert({ pg : 10372, line : "aiguës, et les flèches ailées qui jaillissaient des nerfs"});
db.iliadfr.insert({ pg : 10373, line : "s'enfonçaient autour de Kébrionès, et de lourds rochers brisaient"});
db.iliadfr.insert({ pg : 10374, line : "les bouchers. Et là, Kébrionès gisait, grand, oublieux des chevaux"});
db.iliadfr.insert({ pg : 10375, line : "et du char, et dans un tourbillon de poussière. Aussi longtemps"});
db.iliadfr.insert({ pg : 10376, line : "que Hélios tint le milieu de l'Ouranos, les traits jaillirent des"});
db.iliadfr.insert({ pg : 10377, line : "deux côtés, et les deux peuples périssaient également; mais"});
db.iliadfr.insert({ pg : 10378, line : "lorsqu'il déclina, les Akhaiens furent les plus forts et ils"});
db.iliadfr.insert({ pg : 10379, line : "entraînèrent le héros Kébrionès loin des traits et du tumulte des"});
db.iliadfr.insert({ pg : 10380, line : "Troiens, et ils lui arrachèrent ses armes des épaules."});
db.iliadfr.insert({ pg : 10382, line : "Et Patroklos, méditant la perte des Troiens, se rua en avant. Il"});
db.iliadfr.insert({ pg : 10383, line : "se rua trois fois, tel que le rapide Arès, poussant des cris"});
db.iliadfr.insert({ pg : 10384, line : "horribles, et il tua neuf guerriers. Mais quand il s'élança une"});
db.iliadfr.insert({ pg : 10385, line : "quatrième fois, semblable à un dieu, alors, Patroklos, la fin de"});
db.iliadfr.insert({ pg : 10386, line : "ta vie approcha! Phoibos à travers la mêlée, vint à lui, terrible."});
db.iliadfr.insert({ pg : 10387, line : "Et le Ménoitiade ne vit point le dieu qui s'était enveloppé d'une"});
db.iliadfr.insert({ pg : 10388, line : "épaisse nuée. Et Phoibos se tint derrière lui et le frappa de la"});
db.iliadfr.insert({ pg : 10389, line : "main dans le dos, entre les larges épaules, et ses yeux furent"});
db.iliadfr.insert({ pg : 10390, line : "troublés par le vertige. Et Phoibos Apollôn lui arracha de la tête"});
db.iliadfr.insert({ pg : 10391, line : "son casque, qui roula sous les pieds des chevaux en retentissant,"});
db.iliadfr.insert({ pg : 10392, line : "et dont l'aigrette fut souillée de sang et de poussière. Et il"});
db.iliadfr.insert({ pg : 10393, line : "n'était point arrivé à ce casque d'être souillé de poussière quand"});
db.iliadfr.insert({ pg : 10394, line : "il protégeait le beau front du divin Akhilleus; mais Zeus voulait"});
db.iliadfr.insert({ pg : 10395, line : "donner ce casque au Priamide Hektôr, afin qu'il le portât, car sa"});
db.iliadfr.insert({ pg : 10396, line : "mort était proche."});
db.iliadfr.insert({ pg : 10398, line : "Et la longue et lourde lance de Patroklos se brisa dans sa main,"});
db.iliadfr.insert({ pg : 10399, line : "et le roi Apollôn, fils de Zeus, détacha sa cuirasse. Son esprit"});
db.iliadfr.insert({ pg : 10400, line : "fut saisi de stupeur, et ses membres furent inertes, et il"});
db.iliadfr.insert({ pg : 10401, line : "s'arrêta stupéfait."});
db.iliadfr.insert({ pg : 10403, line : "Alors le Dardanien Panthoide Euphorbos, excellent cavalier, et"});
db.iliadfr.insert({ pg : 10404, line : "habile, entre les meilleurs, à lancer la pique, et qui avait déjà"});
db.iliadfr.insert({ pg : 10405, line : "précipité vingt guerriers de leurs chars, s'approcha du Ménoitiade"});
db.iliadfr.insert({ pg : 10406, line : "par derrière et le blessa d'un coup de lance aiguë. Et ce fut le"});
db.iliadfr.insert({ pg : 10407, line : "premier qui te blessa, dompteur de chevaux Patroklos! Mais il ne"});
db.iliadfr.insert({ pg : 10408, line : "t'abattit point, et, retirant sa lance, il recula aussitôt dans la"});
db.iliadfr.insert({ pg : 10409, line : "foule, redoutant Patroklos désarmé. Et celui-ci, frappé par un"});
db.iliadfr.insert({ pg : 10410, line : "dieu et par la lance d'un homme, recula aussi dans la foule de ses"});
db.iliadfr.insert({ pg : 10411, line : "compagnons, pour éviter la mort."});
db.iliadfr.insert({ pg : 10413, line : "Et dès que Hektôr eut vu le magnanime Patroklos se retirer, blessé"});
db.iliadfr.insert({ pg : 10414, line : "par l'airain aigu, il se jeta sur lui et le frappa dans le côté"});
db.iliadfr.insert({ pg : 10415, line : "d'un coup de lance qui le traversa. Et le Ménoitiade tomba avec"});
db.iliadfr.insert({ pg : 10416, line : "bruit, et la douleur saisit le peuple des Akhaiens. De même un"});
db.iliadfr.insert({ pg : 10417, line : "lion dompte dans le combat un robuste sanglier, car ils"});
db.iliadfr.insert({ pg : 10418, line : "combattaient ardemment sur le faîte des montagnes, pour un peu"});
db.iliadfr.insert({ pg : 10419, line : "d'eau qu'ils voulaient boire tous deux; mais le lion dompte avec"});
db.iliadfr.insert({ pg : 10420, line : "violence le sanglier haletant. Ainsi le Priamide Hektôr arracha"});
db.iliadfr.insert({ pg : 10421, line : "l'âme du brave fils de Ménoitios, et, plein d'orgueil, il"});
db.iliadfr.insert({ pg : 10422, line : "l'insulta par ces paroles ailées:"});
db.iliadfr.insert({ pg : 10424, line : "-- Patroklos, tu espérais sans doute renverser notre ville et"});
db.iliadfr.insert({ pg : 10425, line : "emmener, captives sur tes nefs, nos femmes, dans ta chère terre"});
db.iliadfr.insert({ pg : 10426, line : "natale? Ô insensé! c'est pour les protéger que les rapides chevaux"});
db.iliadfr.insert({ pg : 10427, line : "de Hektôr l'ont mené au combat, car je l'emporte par ma lance sur"});
db.iliadfr.insert({ pg : 10428, line : "tous les Troiens belliqueux, et j'éloigne leur dernier jour. Mais"});
db.iliadfr.insert({ pg : 10429, line : "toi, les oiseaux carnassiers te mangeront. Ah! malheureux! le"});
db.iliadfr.insert({ pg : 10430, line : "brave Akhilleus ne t'a point sauvé, lui qui, t'envoyant combattre,"});
db.iliadfr.insert({ pg : 10431, line : "tandis qu'il restait, te disait sans doute: -- Ne reviens point,"});
db.iliadfr.insert({ pg : 10432, line : "dompteur de chevaux Patroklos, dans les nefs creuses, avant"});
db.iliadfr.insert({ pg : 10433, line : "d'avoir arraché de sa poitrine la cuirasse sanglante du tueur"});
db.iliadfr.insert({ pg : 10434, line : "d'hommes Hektôr. Il t'a parlé ainsi sans doute, et il t'a persuadé"});
db.iliadfr.insert({ pg : 10435, line : "dans ta démence!"});
db.iliadfr.insert({ pg : 10437, line : "Et le cavalier Patroklos, respirant à peine, lui répondit::"});
db.iliadfr.insert({ pg : 10439, line : "-- Hektôr, maintenant tu te glorifies, car le Kronide et Apollôn"});
db.iliadfr.insert({ pg : 10440, line : "t'ont donné la victoire. Ils m'ont aisément dompté, en m'enlevant"});
db.iliadfr.insert({ pg : 10441, line : "mes armes des épaules; mais, si vingt guerriers tels que toi"});
db.iliadfr.insert({ pg : 10442, line : "m'avaient attaqué, ils seraient tous morts par ma lance. C'est la"});
db.iliadfr.insert({ pg : 10443, line : "Moire violente et le fils de Lètô, et, parmi les hommes,"});
db.iliadfr.insert({ pg : 10444, line : "Euphorbos, qui me tuent; mais toi, tu n'es venu que le dernier. Je"});
db.iliadfr.insert({ pg : 10445, line : "te le dis, garde mes paroles dans ton esprit: Tu ne vivras point"});
db.iliadfr.insert({ pg : 10446, line : "longtemps, et ta mort est proche. La Moire violente va te dompter"});
db.iliadfr.insert({ pg : 10447, line : "par les mains de l'irréprochable Aiakide Akhilleus."});
db.iliadfr.insert({ pg : 10449, line : "Il parla ainsi et mourut, et son âme abandonna son corps et"});
db.iliadfr.insert({ pg : 10450, line : "descendit chez Aidès, en pleurant sa destinée, sa force et sa"});
db.iliadfr.insert({ pg : 10451, line : "jeunesse."});
db.iliadfr.insert({ pg : 10453, line : "Et l'illustre Hektôr répondit au cadavre du Ménoitiade:"});
db.iliadfr.insert({ pg : 10455, line : "-- Patroklos, pourquoi m'annoncer la mort? Qui sait si Akhilleus,"});
db.iliadfr.insert({ pg : 10456, line : "le fils de Thétis aux beaux cheveux, ne rendra point l'esprit sous"});
db.iliadfr.insert({ pg : 10457, line : "ma lance?"});
db.iliadfr.insert({ pg : 10459, line : "Ayant ainsi parlé, il lui mit le pied sur le corps, et, le"});
db.iliadfr.insert({ pg : 10460, line : "repoussant, arracha de la plaie sa lance d'airain. Et aussitôt il"});
db.iliadfr.insert({ pg : 10461, line : "courut sur Automédôn, le divin compagnon du rapide Aiakide,"});
db.iliadfr.insert({ pg : 10462, line : "voulant l'abattre; mais les chevaux immortels, présents splendides"});
db.iliadfr.insert({ pg : 10463, line : "que les dieux avaient faits à Pèleus, enlevèrent Automédôn."});
db.iliadfr.insert({ pg : 10466, line : "Chant 17"});
db.iliadfr.insert({ pg : 10468, line : "Et le brave Ménélaos, fils d'Atreus, ayant vu que Patroklos avait"});
db.iliadfr.insert({ pg : 10469, line : "été tué par les Troiens, courut aux premiers rangs, armé de"});
db.iliadfr.insert({ pg : 10470, line : "l'airain splendide. Et il allait autour du cadavre, comme une"});
db.iliadfr.insert({ pg : 10471, line : "vache gémissante, qui n'avait point encore connu l'enfantement,"});
db.iliadfr.insert({ pg : 10472, line : "court autour du veau son premier-né. Ainsi le blond Ménélaos"});
db.iliadfr.insert({ pg : 10473, line : "allait autour de Patroklos, et, le gardant de sa lance et de son"});
db.iliadfr.insert({ pg : 10474, line : "bouclier égal, il se préparait à tuer celui qui approcherait. Et"});
db.iliadfr.insert({ pg : 10475, line : "le Panthoide, habile à lancer la pique, n'oublia point"});
db.iliadfr.insert({ pg : 10476, line : "l'irréprochable Patroklos qui gisait là, et il s'arrêta devant le"});
db.iliadfr.insert({ pg : 10477, line : "cadavre, et il dit au brave Ménélaos:"});
db.iliadfr.insert({ pg : 10479, line : "-- Atréide Ménélaos, illustre prince des peuples, recule, laisse"});
db.iliadfr.insert({ pg : 10480, line : "ce cadavre, et livre-moi ces dépouilles sanglantes, car, le"});
db.iliadfr.insert({ pg : 10481, line : "premier d'entre les Troiens et les alliés, j'ai blessé Patroklos"});
db.iliadfr.insert({ pg : 10482, line : "de ma lance dans la rude mêlée. Laisse-moi donc remporter cette"});
db.iliadfr.insert({ pg : 10483, line : "gloire parmi les Troiens, ou je te frapperai et j'arracherai ta"});
db.iliadfr.insert({ pg : 10484, line : "chère âme."});
db.iliadfr.insert({ pg : 10486, line : "Et le blond Ménélaos, indigné, lui répondit:"});
db.iliadfr.insert({ pg : 10488, line : "-- Père Zeus! quelle honte de se vanter au-delà de ses forces! Ni"});
db.iliadfr.insert({ pg : 10489, line : "la rage du léopard, ni celle du lion, ni celle du sanglier féroce"});
db.iliadfr.insert({ pg : 10490, line : "dont l'âme est toujours furieuse dans sa vaste poitrine, ne"});
db.iliadfr.insert({ pg : 10491, line : "surpassent l'orgueil des fils de Panthos! Le robuste cavalier"});
db.iliadfr.insert({ pg : 10492, line : "Hypérènôr se glorifiait de sa jeunesse lorsqu'il m'insulta, disant"});
db.iliadfr.insert({ pg : 10493, line : "que j'étais le plus lâche des Danaens; et je pense que ses pieds"});
db.iliadfr.insert({ pg : 10494, line : "rapides ne le porteront plus désormais vers l'épouse bien-aimée et"});
db.iliadfr.insert({ pg : 10495, line : "les parents vénérables. Ainsi je romprai tes forces si tu me tiens"});
db.iliadfr.insert({ pg : 10496, line : "tête; et je t'avertis de rentrer dans la foule et de ne point me"});
db.iliadfr.insert({ pg : 10497, line : "braver, avant que le malheur soit tombé sur toi. L'insensé seul ne"});
db.iliadfr.insert({ pg : 10498, line : "reconnaît que ce qui est accompli."});
db.iliadfr.insert({ pg : 10500, line : "Il parla ainsi, et il ne persuada point Euphorbos qui lui"});
db.iliadfr.insert({ pg : 10501, line : "répondit:"});
db.iliadfr.insert({ pg : 10503, line : "-- Divin Ménélaos, certes, maintenant tu vas payer le sang de mon"});
db.iliadfr.insert({ pg : 10504, line : "frère que tu as tué. Tu t'en glorifies, et tu as rendu sa femme"});
db.iliadfr.insert({ pg : 10505, line : "veuve dans la profonde chambre nuptiale, et tu as accablé ses"});
db.iliadfr.insert({ pg : 10506, line : "parents d'une douleur amère. Et moi, je vengerai ces malheureux et"});
db.iliadfr.insert({ pg : 10507, line : "je remettrai aux mains de Panthos et de la divine Phrontis ta tête"});
db.iliadfr.insert({ pg : 10508, line : "et tes armes. Mais ne retardons pas plus longtemps le combat qui"});
db.iliadfr.insert({ pg : 10509, line : "amènera la victoire ou la défaite de l'un de nous."});
db.iliadfr.insert({ pg : 10511, line : "Il parla ainsi, et il frappa le bouclier d'une rondeur égale; mais"});
db.iliadfr.insert({ pg : 10512, line : "il ne put le traverser, et la pointe d'airain se recourba sur le"});
db.iliadfr.insert({ pg : 10513, line : "solide bouclier. Et l'Atréide Ménélaos, suppliant le père Zeus, se"});
db.iliadfr.insert({ pg : 10514, line : "rua avec l'airain; et comme Euphorbos reculait, il le perça à la"});
db.iliadfr.insert({ pg : 10515, line : "gorge, et la pointe, poussée par une main robuste, traversa le cou"});
db.iliadfr.insert({ pg : 10516, line : "délicat. Et le Panthoide tomba avec bruit, et ses armes"});
db.iliadfr.insert({ pg : 10517, line : "retentirent sur lui. Et ses cheveux, qui avaient les reflets de"});
db.iliadfr.insert({ pg : 10518, line : "l'or et de l'argent, et qui étaient semblables aux cheveux des"});
db.iliadfr.insert({ pg : 10519, line : "Kharites, furent souillés de sang. De même qu'un jeune olivier"});
db.iliadfr.insert({ pg : 10520, line : "qu'un homme a planté dans un lieu solitaire, où l'eau jaillit"});
db.iliadfr.insert({ pg : 10521, line : "abondante et nourrit sa verdeur, et que le souffle des vents"});
db.iliadfr.insert({ pg : 10522, line : "mobiles balance, tandis qu'il se couvre de fleurs blanches, mais"});
db.iliadfr.insert({ pg : 10523, line : "qu'un grand tourbillon enveloppe brusquement, arrache et renverse"});
db.iliadfr.insert({ pg : 10524, line : "contre terre; de même l'Atréide Ménélaos tua le brave Panthoide"});
db.iliadfr.insert({ pg : 10525, line : "Euphorbos, et le dépouilla de ses armes."});
db.iliadfr.insert({ pg : 10527, line : "Quand un lion montagnard, sûr de sa force, enlève la meilleure"});
db.iliadfr.insert({ pg : 10528, line : "vache d'un grand troupeau qui paît, lui brise le cou avec ses"});
db.iliadfr.insert({ pg : 10529, line : "fortes dents, boit son sang et mange ses entrailles, les chiens et"});
db.iliadfr.insert({ pg : 10530, line : "les bergers poussent, de loin, de grandes clameurs et n'approchent"});
db.iliadfr.insert({ pg : 10531, line : "point, parce que la blême terreur les a saisis. De même nul"});
db.iliadfr.insert({ pg : 10532, line : "d'entre les Troiens n'osait attaquer l'illustre Ménélaos; et il"});
db.iliadfr.insert({ pg : 10533, line : "eût aisément enlevé les belles armes du Panthoide, si Phoibos"});
db.iliadfr.insert({ pg : 10534, line : "Apollôn, par envie, n'eût excité contre lui Hektôr semblable au"});
db.iliadfr.insert({ pg : 10535, line : "rapide Arès. Et, sous la forme de Mentès, chef des Kikones, il dit"});
db.iliadfr.insert({ pg : 10536, line : "au Priamide ces paroles ailées:"});
db.iliadfr.insert({ pg : 10538, line : "-- Hektôr, où cours-tu ainsi? pourquoi poursuis-tu follement les"});
db.iliadfr.insert({ pg : 10539, line : "chevaux du brave Akhilleus, qui ne peuvent être ni soumis, ni"});
db.iliadfr.insert({ pg : 10540, line : "conduits par aucun homme mortel, autre qu'Akhilleus qu'une mère"});
db.iliadfr.insert({ pg : 10541, line : "immortelle a enfanté? Voici, pendant ce temps, que le brave"});
db.iliadfr.insert({ pg : 10542, line : "Ménélaos, fils d'Atreus, pour défendre Patroklos, a tué le plus"});
db.iliadfr.insert({ pg : 10543, line : "courageux des Troiens, le Panthoide Euphorbos, et rompu sa vigueur"});
db.iliadfr.insert({ pg : 10544, line : "impétueuse."});
db.iliadfr.insert({ pg : 10546, line : "Le dieu parla ainsi et rentra dans la foule des hommes. Et une"});
db.iliadfr.insert({ pg : 10547, line : "amère douleur saisit le coeur sombre de Hektôr. Il regarda autour"});
db.iliadfr.insert({ pg : 10548, line : "de lui dans la mêlée, et il vit Ménélaos enlevant les belles armes"});
db.iliadfr.insert({ pg : 10549, line : "d'Euphorbos, et le Panthoide gisant contre terre, et le sang qui"});
db.iliadfr.insert({ pg : 10550, line : "coulait de la plaie ouverte. Avec de hautes clameurs, armé de"});
db.iliadfr.insert({ pg : 10551, line : "l'airain éclatant, et semblable au feu inextinguible de"});
db.iliadfr.insert({ pg : 10552, line : "Hèphaistos, il s'élança aux premiers rangs. Et le fils d'Atreus"});
db.iliadfr.insert({ pg : 10553, line : "l'entendit et le vit, et il gémit, disant dans son coeur"});
db.iliadfr.insert({ pg : 10554, line : "magnanime:"});
db.iliadfr.insert({ pg : 10556, line : "-- Hélas! si j'abandonne ces belles armes et Patroklos qui est"});
db.iliadfr.insert({ pg : 10557, line : "mort pour ma cause, les Danaens qui me verront seront indignés;"});
db.iliadfr.insert({ pg : 10558, line : "mais si je combats seul contre Hektôr et les Troiens, je crains"});
db.iliadfr.insert({ pg : 10559, line : "que cette multitude m'enveloppe, car Hektôr au casque mouvant mène"});
db.iliadfr.insert({ pg : 10560, line : "avec lui tous les Troiens. Mais pourquoi délibérer dans ma chère"});
db.iliadfr.insert({ pg : 10561, line : "âme? Quand un homme veut lutter contre un autre homme qu'un dieu"});
db.iliadfr.insert({ pg : 10562, line : "honore, aussitôt une lourde calamité est suspendue sur lui. C'est"});
db.iliadfr.insert({ pg : 10563, line : "pourquoi aucun Danaen ne me blâmera de me retirer devant Hektôr,"});
db.iliadfr.insert({ pg : 10564, line : "puisqu'il est poussé par un dieu. Si j'entendais le brave Aias"});
db.iliadfr.insert({ pg : 10565, line : "dans la mêlée, nous retournerions tous deux au combat, même contre"});
db.iliadfr.insert({ pg : 10566, line : "un dieu, et nous sauverions ce cadavre pour le Pèléide Akhilleus,"});
db.iliadfr.insert({ pg : 10567, line : "et dans nos maux ceci serait pour le mieux."});
db.iliadfr.insert({ pg : 10569, line : "Et tandis qu'il délibérait dans son esprit et dans son coeur, les"});
db.iliadfr.insert({ pg : 10570, line : "phalanges Troiennes arrivaient conduites par Hektôr. Ménélaos"});
db.iliadfr.insert({ pg : 10571, line : "recula et abandonna le cadavre, mais en se retournant, comme un"});
db.iliadfr.insert({ pg : 10572, line : "lion à longue barbe que les chiens et les bergers chassent de"});
db.iliadfr.insert({ pg : 10573, line : "l'étable avec des lances et des cris, et dont le coeur farouche"});
db.iliadfr.insert({ pg : 10574, line : "est troublé, et qui ne s'éloigne qu'à regret de l'enclos. Ainsi le"});
db.iliadfr.insert({ pg : 10575, line : "blond Ménélaos s'éloigna de Patroklos. Et il se retourna dès qu'il"});
db.iliadfr.insert({ pg : 10576, line : "eut rejoint ses compagnons, et, cherchant partout des yeux le"});
db.iliadfr.insert({ pg : 10577, line : "grand Aias Télamônien, il le vit à la gauche de la mêlée,"});
db.iliadfr.insert({ pg : 10578, line : "exhortant ses compagnons et les excitant à combattre, car Phoibos"});
db.iliadfr.insert({ pg : 10579, line : "Apollôn avait jeté une grande terreur en eux. Et Ménélaos courut à"});
db.iliadfr.insert({ pg : 10580, line : "lui et lui dit aussitôt:"});
db.iliadfr.insert({ pg : 10582, line : "-- Aias, viens, ami! hâtons-nous pour Patroklos qui est mort, et"});
db.iliadfr.insert({ pg : 10583, line : "rapportons au moins son cadavre à Akhilleus, car c'est Hektôr au"});
db.iliadfr.insert({ pg : 10584, line : "casque mouvant qui a ses armes."});
db.iliadfr.insert({ pg : 10586, line : "Il parla ainsi, et l'âme du brave Aias fut remuée, et il se jeta"});
db.iliadfr.insert({ pg : 10587, line : "aux premiers rangs, avec le blond Ménélaos."});
db.iliadfr.insert({ pg : 10589, line : "Et le Priamide, après avoir dépouillé Patroklos de ses armes"});
db.iliadfr.insert({ pg : 10590, line : "illustres, l'entraînait pour lui couper la tête avec l'airain et"});
db.iliadfr.insert({ pg : 10591, line : "livrer son cadavre aux chiens troiens; mais Aias arriva, portant"});
db.iliadfr.insert({ pg : 10592, line : "un bouclier semblable à une tour. Et Hektôr rentra dans la foule"});
db.iliadfr.insert({ pg : 10593, line : "de ses compagnons; et, montant sur son char, il donna les belles"});
db.iliadfr.insert({ pg : 10594, line : "armes aux Troiens, pour être portées à Ilios et pour répandre le"});
db.iliadfr.insert({ pg : 10595, line : "bruit de sa gloire."});
db.iliadfr.insert({ pg : 10597, line : "Et Aias marchait autour du Ménoitiade, le couvrant de son"});
db.iliadfr.insert({ pg : 10598, line : "bouclier, comme une lionne autour de ses petits. Elle les menait à"});
db.iliadfr.insert({ pg : 10599, line : "travers la forêt, quand les chasseurs surviennent. Aussitôt,"});
db.iliadfr.insert({ pg : 10600, line : "pleine de fureur, elle fronce les sourcils et en couvre ses yeux."});
db.iliadfr.insert({ pg : 10601, line : "Ainsi Aias marchait autour du héros Patroklos, et le brave Atréide"});
db.iliadfr.insert({ pg : 10602, line : "Ménélaos se tenait près de lui, avec un grand deuil dans la"});
db.iliadfr.insert({ pg : 10603, line : "poitrine."});
db.iliadfr.insert({ pg : 10605, line : "Mais le fils de Hippolokhos, Glaukos, chef des hommes de Lykiè,"});
db.iliadfr.insert({ pg : 10606, line : "regardant Hektôr d'un oeil sombre, lui dit ces dures paroles:"});
db.iliadfr.insert({ pg : 10608, line : "-- Hektôr, tu as l'aspect du plus brave des hommes, mais tu n'es"});
db.iliadfr.insert({ pg : 10609, line : "pas tel dans le combat, et tu ne mérites point ta gloire, car tu"});
db.iliadfr.insert({ pg : 10610, line : "ne sais que fuir. Songe maintenant à sauver ta ville et ta"});
db.iliadfr.insert({ pg : 10611, line : "citadelle, seul avec les peuples nés dans Ilios. Jamais plus les"});
db.iliadfr.insert({ pg : 10612, line : "Lykiens ne lutteront contre les Danaens pour Troiè, puisque tu"});
db.iliadfr.insert({ pg : 10613, line : "n'en as point de reconnaissance, bien qu'ils combattent"});
db.iliadfr.insert({ pg : 10614, line : "éternellement. Lâche comment défendrais-tu même un faible guerrier"});
db.iliadfr.insert({ pg : 10615, line : "dans la mêlée, puisque tu as abandonné, en proie aux Akhaiens,"});
db.iliadfr.insert({ pg : 10616, line : "Sarpèdôn, ton hôte et ton compagnon, lui qui, vivant, fut d'un si"});
db.iliadfr.insert({ pg : 10617, line : "grand secours à ta ville et à toi-même, et que maintenant tu"});
db.iliadfr.insert({ pg : 10618, line : "abandonnes aux chiens! C'est pourquoi, si les Lykiens m'obéissent,"});
db.iliadfr.insert({ pg : 10619, line : "nous retournerons dans nos demeures, et la ruine d'Ilios sera"});
db.iliadfr.insert({ pg : 10620, line : "proche. Si les Troiens avaient l'audace et la force de ceux qui"});
db.iliadfr.insert({ pg : 10621, line : "combattent pour la patrie, nous traînerions dans Ilios, dans la"});
db.iliadfr.insert({ pg : 10622, line : "grande ville de Priamos, le cadavre de Patroklos; et, aussitôt,"});
db.iliadfr.insert({ pg : 10623, line : "les Argiens nous rendraient les belles armes de Sarpèdôn et"});
db.iliadfr.insert({ pg : 10624, line : "Sarpèdôn lui-même; car il a été tué, le compagnon de cet homme qui"});
db.iliadfr.insert({ pg : 10625, line : "est le plus formidable des Argiens auprès des nefs et qui a les"});
db.iliadfr.insert({ pg : 10626, line : "plus braves compagnons. Mais tu n'as pas osé soutenir l'attaque du"});
db.iliadfr.insert({ pg : 10627, line : "magnanime Aias, ni ses regards, dans la mêlée; et tu as redouté de"});
db.iliadfr.insert({ pg : 10628, line : "combattre, car il l'emporte de beaucoup sur toi!"});
db.iliadfr.insert({ pg : 10630, line : "Et, le regardant d'un oeil sombre, Hektôr au casque mouvant lui"});
db.iliadfr.insert({ pg : 10631, line : "répondit:"});
db.iliadfr.insert({ pg : 10633, line : "-- Glaukos, pourquoi parles-tu si outrageusement? Certes, ami, je"});
db.iliadfr.insert({ pg : 10634, line : "te croyais supérieur en prudence à tous ceux qui habitent la"});
db.iliadfr.insert({ pg : 10635, line : "fertile Lykiè, et maintenant je te blâme d'avoir parlé ainsi,"});
db.iliadfr.insert({ pg : 10636, line : "disant que je n'ai pas osé attendre le grand Aias. Jamais ni le"});
db.iliadfr.insert({ pg : 10637, line : "bruit des chars, ni le retentissement de la mêlée ne m'ont"});
db.iliadfr.insert({ pg : 10638, line : "épouvanté; mais l'esprit de Zeus tempétueux terrifie aisément le"});
db.iliadfr.insert({ pg : 10639, line : "brave et lui enlève la victoire, bien qu'il l'ait poussé au"});
db.iliadfr.insert({ pg : 10640, line : "combat. Mais viens et tu verras en ce jour si je suis un lâche,"});
db.iliadfr.insert({ pg : 10641, line : "comme tu le dis, et si je saurai rompre la vigueur des Danaens qui"});
db.iliadfr.insert({ pg : 10642, line : "défendront le cadavre de Patroklos."});
db.iliadfr.insert({ pg : 10644, line : "Il parla ainsi, et il exhorta les Troiens à voix haute:"});
db.iliadfr.insert({ pg : 10646, line : "-- Troiens, Lykiens et braves Dardaniens, soyez des hommes, amis!"});
db.iliadfr.insert({ pg : 10647, line : "Souvenez-vous de votre force et de votre courage, tandis que je"});
db.iliadfr.insert({ pg : 10648, line : "vais revêtir les armes de l'irréprochable Akhilleus, enlevées à"});
db.iliadfr.insert({ pg : 10649, line : "Patroklos que j'ai tué."});
db.iliadfr.insert({ pg : 10651, line : "Ayant ainsi parlé, Hektôr, s'éloignant de la mêlée, courut"});
db.iliadfr.insert({ pg : 10652, line : "rapidement vers ses compagnons qui portaient à Ilios les armes"});
db.iliadfr.insert({ pg : 10653, line : "illustres du Pèléide. Et, loin de la mêlée lamentable, il changea"});
db.iliadfr.insert({ pg : 10654, line : "d'armes et donna les siennes pour être portées dans la sainte"});
db.iliadfr.insert({ pg : 10655, line : "Ilios. Et il se couvrit des armes immortelles du Pèléide"});
db.iliadfr.insert({ pg : 10656, line : "Akhilleus, que les dieux ouraniens avaient données à Pèleus. Et"});
db.iliadfr.insert({ pg : 10657, line : "celui-ci, étant vieux, les avait données à son fils; mais le fils"});
db.iliadfr.insert({ pg : 10658, line : "ne devait point vieillir sous les armes paternelles."});
db.iliadfr.insert({ pg : 10660, line : "Et quand Zeus qui amasse les nuées vit Hektôr couvert des armes du"});
db.iliadfr.insert({ pg : 10661, line : "divin Pèléide, il secoua la tête et dit dans son esprit:"});
db.iliadfr.insert({ pg : 10663, line : "-- Ô malheureux! tu ne songes point à la mort qui est proche de"});
db.iliadfr.insert({ pg : 10664, line : "toi, et tu revêts les armes immortelles du plus brave des hommes,"});
db.iliadfr.insert({ pg : 10665, line : "devant qui tous les guerriers frémissent; et tu as tué son"});
db.iliadfr.insert({ pg : 10666, line : "compagnon si doux et si courageux, et tu as outrageusement arraché"});
db.iliadfr.insert({ pg : 10667, line : "ses armes de sa tête et de ses épaules! Mais je te donnerai une"});
db.iliadfr.insert({ pg : 10668, line : "grande gloire en retour de ce que Andromakhè ne recevra point,"});
db.iliadfr.insert({ pg : 10669, line : "après le combat, les armes illustres du Pèléide."});
db.iliadfr.insert({ pg : 10671, line : "Zeus parla ainsi, et il scella sa promesse en abaissant ses"});
db.iliadfr.insert({ pg : 10672, line : "sourcils bleus. Et il adapta les armes au corps du Priamide qui,"});
db.iliadfr.insert({ pg : 10673, line : "hardi et furieux comme Arès, sentit couler dans tous ses membres"});
db.iliadfr.insert({ pg : 10674, line : "la force et le courage. Et, poussant de hautes clameurs, il"});
db.iliadfr.insert({ pg : 10675, line : "apparut aux illustres alliés et aux Troiens, semblable à"});
db.iliadfr.insert({ pg : 10676, line : "Akhilleus, car il resplendissait sous les armes du magnanime"});
db.iliadfr.insert({ pg : 10677, line : "Pèléide. Et, allant de l'un à l'autre, il les exhortait tous:"});
db.iliadfr.insert({ pg : 10678, line : "Mesthlès, Glaukos, Médôn, Thersilokhos, Astéropaios, Deisinôr,"});
db.iliadfr.insert({ pg : 10679, line : "Hippothoos et Phorkis, et Khromios et le divinateur Ennomos. Et,"});
db.iliadfr.insert({ pg : 10680, line : "les excitant par des paroles rapides, il leur parla ainsi:"});
db.iliadfr.insert({ pg : 10682, line : "-- Entendez-moi, innombrables peuples alliés et voisins d'Ilios!"});
db.iliadfr.insert({ pg : 10683, line : "Je n'ai point appelé une multitude inactive quand je vous ai"});
db.iliadfr.insert({ pg : 10684, line : "convoqués de vos villes, mais je vous ai demandé de défendre"});
db.iliadfr.insert({ pg : 10685, line : "ardemment les femmes des Troiens et leurs petits enfants contre"});
db.iliadfr.insert({ pg : 10686, line : "les Akhaiens belliqueux. Pour vous, j'ai épuisé mes peuples de"});
db.iliadfr.insert({ pg : 10687, line : "vivres et de présents et j'ai nourri vos forces. Que chacun"});
db.iliadfr.insert({ pg : 10688, line : "combatte donc, triomphe ou périsse, car c'est le sort de la"});
db.iliadfr.insert({ pg : 10689, line : "guerre. Celui qui entraînera le corps de Patroklos vers les"});
db.iliadfr.insert({ pg : 10690, line : "Troiens dompteurs de chevaux aura, pour sa part, la moitié des"});
db.iliadfr.insert({ pg : 10691, line : "dépouilles, et j'aurai l'autre moitié, et sa gloire sera égale à"});
db.iliadfr.insert({ pg : 10692, line : "la mienne."});
db.iliadfr.insert({ pg : 10694, line : "Il parla ainsi, et tous, les lances tendues, se ruèrent sur les"});
db.iliadfr.insert({ pg : 10695, line : "Danaens, espérant arracher au Télamônien Aias le cadavre de"});
db.iliadfr.insert({ pg : 10696, line : "Patroklos. Les insensés! Il devait plutôt arracher, sur ce"});
db.iliadfr.insert({ pg : 10697, line : "cadavre, l'âme de beaucoup d'entre eux. Et il dit au brave"});
db.iliadfr.insert({ pg : 10698, line : "Ménélaos:"});
db.iliadfr.insert({ pg : 10700, line : "-- Divin Ménélaos, ô ami! je n'espère pas que nous revenions de ce"});
db.iliadfr.insert({ pg : 10701, line : "combat, et, certes, je crains moins pour le cadavre de Patroklos,"});
db.iliadfr.insert({ pg : 10702, line : "que les chiens troiens et les oiseaux carnassiers vont bientôt"});
db.iliadfr.insert({ pg : 10703, line : "dévorer, que pour ma tête et la tienne, car Hektôr couvre le champ"});
db.iliadfr.insert({ pg : 10704, line : "de bataille comme une nuée, et la lourde ruine pend sur nous."});
db.iliadfr.insert({ pg : 10705, line : "Hâte-toi, appelle les princes des Danaens, s'ils t'entendent."});
db.iliadfr.insert({ pg : 10707, line : "Il parla ainsi, et le brave Ménélaos s'empressa d'appeler à grands"});
db.iliadfr.insert({ pg : 10708, line : "cris les Danaens:"});
db.iliadfr.insert({ pg : 10710, line : "-- Ô amis! Princes et chefs des Argiens, vous qui mangez aux repas"});
db.iliadfr.insert({ pg : 10711, line : "des Atréides Agamemnôn et Ménélaos, et qui commandez les"});
db.iliadfr.insert({ pg : 10712, line : "phalanges, car tout honneur et toute gloire viennent de Zeus;"});
db.iliadfr.insert({ pg : 10713, line : "comme il m'est difficile de vous reconnaître dans le tourbillon de"});
db.iliadfr.insert({ pg : 10714, line : "la mêlée, que chacun de vous accoure de lui-même, indigné que"});
db.iliadfr.insert({ pg : 10715, line : "Patroklos soit livré en pâture aux chiens troiens."});
db.iliadfr.insert({ pg : 10717, line : "Il parla ainsi, et le rapide Aias, fils d'Oileus, vint le premier,"});
db.iliadfr.insert({ pg : 10718, line : "en courant à travers la mêlée, et, après lui, Idoméneus, et le"});
db.iliadfr.insert({ pg : 10719, line : "compagnon d'Idoméneus, Mèrionès, semblable au tueur d'hommes Arès."});
db.iliadfr.insert({ pg : 10720, line : "Mais qui pourrait, dans son esprit, dire les noms de tous ceux qui"});
db.iliadfr.insert({ pg : 10721, line : "vinrent rétablir le combat des Akhaiens?"});
db.iliadfr.insert({ pg : 10723, line : "Et les Troiens avançaient, et Hektôr les menait. De même que le"});
db.iliadfr.insert({ pg : 10724, line : "large courant d'un fleuve tombé de Zeus se précipite à la mer, et"});
db.iliadfr.insert({ pg : 10725, line : "que la mer s'enfle hors de son lit, et que les rivages résonnent"});
db.iliadfr.insert({ pg : 10726, line : "au loin; de même retentissait la clameur des Troiens. Mais les"});
db.iliadfr.insert({ pg : 10727, line : "Akhaiens se tenaient debout autour du Ménoitiade, n'ayant qu'une"});
db.iliadfr.insert({ pg : 10728, line : "âme et couverts de leurs boucliers d'airain. Et Zeus répandait une"});
db.iliadfr.insert({ pg : 10729, line : "nuée épaisse sur leurs casques éclatants; car il n'avait point haï"});
db.iliadfr.insert({ pg : 10730, line : "le Ménoitiade pendant que, vivant, il était le compagnon de"});
db.iliadfr.insert({ pg : 10731, line : "l'Aiakide; et il ne voulait pas qu'il fût livré en pâture aux"});
db.iliadfr.insert({ pg : 10732, line : "chiens troiens; et il anima ses compagnons à le défendre."});
db.iliadfr.insert({ pg : 10734, line : "Et, d'abord, les Troiens repoussèrent les Akhaiens aux sourcils"});
db.iliadfr.insert({ pg : 10735, line : "arqués. Ceux-ci prirent la fuite, abandonnant le cadavre; et les"});
db.iliadfr.insert({ pg : 10736, line : "Troiens ne les poursuivirent point, malgré leur désir du meurtre;"});
db.iliadfr.insert({ pg : 10737, line : "mais ils entraînaient le cadavre. Et les Akhaiens ne"});
db.iliadfr.insert({ pg : 10738, line : "l'abandonnèrent pas longtemps; et, les ramenant aussitôt, Aias, le"});
db.iliadfr.insert({ pg : 10739, line : "premier des Danaens par l'aspect héroïque et les actions, après"});
db.iliadfr.insert({ pg : 10740, line : "l'irréprochable Pèléide, se rua aux premiers rangs, semblable par"});
db.iliadfr.insert({ pg : 10741, line : "la fureur à un sanglier qui, rebroussant à travers les taillis,"});
db.iliadfr.insert({ pg : 10742, line : "disperse les chiens et les jeunes hommes. Ainsi le grand Aias,"});
db.iliadfr.insert({ pg : 10743, line : "fils de l'illustre Télamôn, dispersa aisément les phalanges"});
db.iliadfr.insert({ pg : 10744, line : "Troiennes qui se pressaient autour de Patroklos, espérant"});
db.iliadfr.insert({ pg : 10745, line : "l'entraîner dans Ilios et remporter cette gloire."});
db.iliadfr.insert({ pg : 10747, line : "Et Hippothoos, fils du Pélasge Lèthos, ayant lié le tendon par une"});
db.iliadfr.insert({ pg : 10748, line : "courroie, traînait Patroklos par un pied dans la mêlée, afin de"});
db.iliadfr.insert({ pg : 10749, line : "plaire à Hektôr et aux Troiens; mais il lui en arriva malheur,"});
db.iliadfr.insert({ pg : 10750, line : "sans que nul pût le sauver, car le Télamônien, se ruant au milieu"});
db.iliadfr.insert({ pg : 10751, line : "de la foule, le frappa sur son casque d'airain, et le casque à"});
db.iliadfr.insert({ pg : 10752, line : "crinière fut brisé par la grande lance et la main vigoureuse"});
db.iliadfr.insert({ pg : 10753, line : "d'Aias, et l'airain de la pointe traversa la cervelle qui jaillit"});
db.iliadfr.insert({ pg : 10754, line : "sanglante de la plaie, et ses forces furent rompues. Il lâcha le"});
db.iliadfr.insert({ pg : 10755, line : "pied du magnanime Patroklos et tomba lui-même sur le cadavre, loin"});
db.iliadfr.insert({ pg : 10756, line : "de Larissè; et il ne rendit point à ses parents bien-aimés les"});
db.iliadfr.insert({ pg : 10757, line : "soins qu'ils lui avaient donnés, et sa vie fut brève, ayant été"});
db.iliadfr.insert({ pg : 10758, line : "ainsi dompté par le magnanime Aias."});
db.iliadfr.insert({ pg : 10760, line : "Hektôr lança contre Aias sa pique éclatante, mais celui-ci,"});
db.iliadfr.insert({ pg : 10761, line : "l'ayant aperçue, évita la pique d'airain qui frappa le magnanime"});
db.iliadfr.insert({ pg : 10762, line : "Skhédios, fils d'Iphitos, et le plus brave des Phôkèens, et qui"});
db.iliadfr.insert({ pg : 10763, line : "habitait la grande Panopè, commandant à de nombreux peuples. La"});
db.iliadfr.insert({ pg : 10764, line : "pique le perça au milieu de la gorge, et la pointe d'airain sortit"});
db.iliadfr.insert({ pg : 10765, line : "au sommet de l'épaule. Il tomba avec bruit et ses armes"});
db.iliadfr.insert({ pg : 10766, line : "retentirent sur lui. Et Aias perça au milieu du ventre le brave"});
db.iliadfr.insert({ pg : 10767, line : "Phorkys, fils de Phainops, qui défendait le corps de Hippothoos."});
db.iliadfr.insert({ pg : 10768, line : "L'airain rompit le creux de la cuirasse et déchira les entrailles."});
db.iliadfr.insert({ pg : 10769, line : "Il tomba, saisissant la terre avec ses mains, et les premiers"});
db.iliadfr.insert({ pg : 10770, line : "rangs, ainsi que Hektôr, reculèrent. Et les Argiens, avec de"});
db.iliadfr.insert({ pg : 10771, line : "grands cris, entraînèrent, morts, Phorkys et Hippothoos, et"});
db.iliadfr.insert({ pg : 10772, line : "enlevèrent leurs armes."});
db.iliadfr.insert({ pg : 10774, line : "Alors, les Troiens eussent été mis en fuite par les braves"});
db.iliadfr.insert({ pg : 10775, line : "Akhaiens et fussent rentrés dans Ilios, domptés par leur propre"});
db.iliadfr.insert({ pg : 10776, line : "lâcheté, et les Akhaiens eussent remporté la victoire, malgré"});
db.iliadfr.insert({ pg : 10777, line : "Zeus, par leur vigueur et leur courage, si Apollôn lui-même n'eût"});
db.iliadfr.insert({ pg : 10778, line : "excité Ainéias, sous la forme du héraut Périphas Épytide qui avait"});
db.iliadfr.insert({ pg : 10779, line : "vieilli, auprès de son vieux père, dans l'étude et la science de"});
db.iliadfr.insert({ pg : 10780, line : "la sagesse. Semblable à Périphas, le fils de Zeus parla ainsi:"});
db.iliadfr.insert({ pg : 10782, line : "-- Ainéias, comment sauveriez-vous la sainte Ilios, même malgré la"});
db.iliadfr.insert({ pg : 10783, line : "volonté d'un dieu? En étant tels que des guerriers que j'ai vus,"});
db.iliadfr.insert({ pg : 10784, line : "confiants dans leur propre courage, autant que dans la vigueur et"});
db.iliadfr.insert({ pg : 10785, line : "le nombre de leur peuple. Zeus nous offre la victoire plutôt"});
db.iliadfr.insert({ pg : 10786, line : "qu'aux Danaens, mais vous êtes des lâches qui ne savez pas"});
db.iliadfr.insert({ pg : 10787, line : "combattre."});
db.iliadfr.insert({ pg : 10789, line : "Il parla ainsi, et Ainéias reconnut l'archer Apollôn, et il cria"});
db.iliadfr.insert({ pg : 10790, line : "aussitôt à Hektôr:"});
db.iliadfr.insert({ pg : 10792, line : "-- Hektôr, et vous, chefs des Troiens et des alliés, c'est une"});
db.iliadfr.insert({ pg : 10793, line : "honte de fuir vers Ilios, vaincus, à cause de notre lâcheté, par"});
db.iliadfr.insert({ pg : 10794, line : "les braves Akhaiens. Voici qu'un des dieux s'est approché de moi,"});
db.iliadfr.insert({ pg : 10795, line : "et il m'a dit que le très puissant Zeus nous était propice dans le"});
db.iliadfr.insert({ pg : 10796, line : "combat. C'est pourquoi, marchons aux Danaens, et qu'ils"});
db.iliadfr.insert({ pg : 10797, line : "n'emportent pas sans peine, jusqu'aux nefs, Patroklos mort."});
db.iliadfr.insert({ pg : 10799, line : "Il parla ainsi, et il s'élança parmi les premiers combattants, et"});
db.iliadfr.insert({ pg : 10800, line : "les Troiens firent face aux Akhaiens. Et Ainéias blessa d'un coup"});
db.iliadfr.insert({ pg : 10801, line : "de lance Leiokritos, fils d'Arisbas, et brave compagnon de"});
db.iliadfr.insert({ pg : 10802, line : "Lykomèdès. Et le brave Lykomèdès fut saisi de compassion en le"});
db.iliadfr.insert({ pg : 10803, line : "voyant tomber. Il s'approcha, et, lançant sa pique brillante, il"});
db.iliadfr.insert({ pg : 10804, line : "perça dans le foie le Hippaside Apisaôn, prince des peuples, et il"});
db.iliadfr.insert({ pg : 10805, line : "rompit ses forces. Le Hippaside était venu de la fertile Paioniè,"});
db.iliadfr.insert({ pg : 10806, line : "et il était le premier des Paiones, après Astéropaios. Et le brave"});
db.iliadfr.insert({ pg : 10807, line : "Astéropaios fut saisi de compassion en le voyant tomber, et il se"});
db.iliadfr.insert({ pg : 10808, line : "rua en avant pour combattre les Danaens, mais vainement, car les"});
db.iliadfr.insert({ pg : 10809, line : "Akhaiens se tenaient tous, hérissés de lances, autour de"});
db.iliadfr.insert({ pg : 10810, line : "Patroklos. Et Aias les exhortait ardemment, et il leur ordonnait"});
db.iliadfr.insert({ pg : 10811, line : "de ne point s'écarter du cadavre en s'élançant hors des rangs,"});
db.iliadfr.insert({ pg : 10812, line : "mais de rester autour de Patroklos et de tenir ferme. Le grand"});
db.iliadfr.insert({ pg : 10813, line : "Aias commandait ainsi; et la terre était baignée d'un sang"});
db.iliadfr.insert({ pg : 10814, line : "pourpré, et tous tombaient les uns sur les autres, Troiens, alliés"});
db.iliadfr.insert({ pg : 10815, line : "et Danaens; mais ceux-ci périssaient en plus petit nombre, car ils"});
db.iliadfr.insert({ pg : 10816, line : "n'oubliaient point de s'entr'aider dans la mêlée. Et tous"});
db.iliadfr.insert({ pg : 10817, line : "luttaient, pareils à un incendie; et nul n'aurait pu dire si"});
db.iliadfr.insert({ pg : 10818, line : "Hélios brillait, ou Sélènè, tant les braves qui s'agitaient autour"});
db.iliadfr.insert({ pg : 10819, line : "du Ménoitiade étaient enveloppés d'un noir brouillard."});
db.iliadfr.insert({ pg : 10821, line : "Ailleurs, d'autres Troiens et d'autres Akhaiens aux belles"});
db.iliadfr.insert({ pg : 10822, line : "knèmides combattaient à l'aise sous un air serein; et là se"});
db.iliadfr.insert({ pg : 10823, line : "répandait l'étincelante splendeur de Hélios, et il n'y avait de"});
db.iliadfr.insert({ pg : 10824, line : "nuées ni sur la terre, ni sur les montagnes. Et ils combattaient"});
db.iliadfr.insert({ pg : 10825, line : "mollement, évitant les traits de part et d'autre, et séparés par"});
db.iliadfr.insert({ pg : 10826, line : "un large espace. Mais, au centre, sous le noir brouillard, les"});
db.iliadfr.insert({ pg : 10827, line : "plus braves, se frappant de l'airain cruel, subissaient tous les"});
db.iliadfr.insert({ pg : 10828, line : "maux de la guerre. Et là, deux excellents guerriers, Thrasymèdès"});
db.iliadfr.insert({ pg : 10829, line : "et Antilokhos, ne savaient pas que l'irréprochable Patroklos fût"});
db.iliadfr.insert({ pg : 10830, line : "mort. Ils pensaient qu'il était vivant et qu'il combattait les"});
db.iliadfr.insert({ pg : 10831, line : "Troiens au fort de la mêlée, tandis qu'eux-mêmes luttaient pour le"});
db.iliadfr.insert({ pg : 10832, line : "salut de leurs compagnons, loin du Ménoitiade, comme Nestôr le"});
db.iliadfr.insert({ pg : 10833, line : "leur avait ordonné, quand il les envoya des nefs noires au combat."});
db.iliadfr.insert({ pg : 10835, line : "Et, pendant tout le jour, le carnage continua autour de Patroklos,"});
db.iliadfr.insert({ pg : 10836, line : "du brave compagnon du rapide Aiakide, et tous avaient les genoux,"});
db.iliadfr.insert({ pg : 10837, line : "les pieds, les mains et les yeux souillés de poussière et de sang."});
db.iliadfr.insert({ pg : 10838, line : "De même qu'un homme ordonne à ses serviteurs de tendre une grande"});
db.iliadfr.insert({ pg : 10839, line : "peau de boeuf tout imprégnée de graisse liquide, et que ceux-ci la"});
db.iliadfr.insert({ pg : 10840, line : "tendent en cercle, et que, sous leurs efforts, la graisse pénètre"});
db.iliadfr.insert({ pg : 10841, line : "dans la peau; de même, de tous les côtés, les combattants"});
db.iliadfr.insert({ pg : 10842, line : "traînaient çà et là le cadavre dans un étroit espace, les Troiens"});
db.iliadfr.insert({ pg : 10843, line : "vers Ilios et les Akhaiens vers les nefs creuses; et un affreux"});
db.iliadfr.insert({ pg : 10844, line : "tumulte s'élevait, qui eût réjoui Athènè et Arès qui irrite le"});
db.iliadfr.insert({ pg : 10845, line : "combat. Ainsi Zeus heurta, tout le jour, la mêlée des hommes et"});
db.iliadfr.insert({ pg : 10846, line : "des chevaux sur le cadavre de Patroklos."});
db.iliadfr.insert({ pg : 10848, line : "Mais le divin Akhilleus ignorait la mort du Ménoitiade, car les"});
db.iliadfr.insert({ pg : 10849, line : "hommes combattaient, loin des nefs, sous les murailles de Troiè."});
db.iliadfr.insert({ pg : 10850, line : "Et il pensait que Patroklos reviendrait vivant, après avoir poussé"});
db.iliadfr.insert({ pg : 10851, line : "jusqu'aux portes de la ville, sachant qu'il ne devait point"});
db.iliadfr.insert({ pg : 10852, line : "renverser Ilios sans lui, et même avec lui. Souvent, en effet, il"});
db.iliadfr.insert({ pg : 10853, line : "l'avait entendu dire à sa mère qui lui révélait la pensée de Zeus;"});
db.iliadfr.insert({ pg : 10854, line : "mais sa mère ne lui avait pas annoncé un si grand malheur, et il"});
db.iliadfr.insert({ pg : 10855, line : "ne savait pas que son plus cher compagnon périrait."});
db.iliadfr.insert({ pg : 10857, line : "Et tous, autour du cadavre, combattaient, infatigables, de leurs"});
db.iliadfr.insert({ pg : 10858, line : "lances aiguës, et s'entre-tuaient. Et les Akhaiens cuirassés"});
db.iliadfr.insert({ pg : 10859, line : "disaient:"});
db.iliadfr.insert({ pg : 10861, line : "-- Ô amis! il serait honteux de retourner vers les nefs creuses!"});
db.iliadfr.insert({ pg : 10862, line : "Que la noire terre nous engloutisse ici, plutôt que de laisser les"});
db.iliadfr.insert({ pg : 10863, line : "braves Troiens entraîner ce cadavre vers leur ville et remporter"});
db.iliadfr.insert({ pg : 10864, line : "cette gloire!"});
db.iliadfr.insert({ pg : 10866, line : "Et les Troiens magnanimes disaient:"});
db.iliadfr.insert({ pg : 10868, line : "-- Ô amis! si la moire veut que nous tombions tous ici, soit! mais"});
db.iliadfr.insert({ pg : 10869, line : "que nul ne recule!"});
db.iliadfr.insert({ pg : 10871, line : "Chacun parlait ainsi et animait le courage de ses compagnons, et"});
db.iliadfr.insert({ pg : 10872, line : "ils combattaient, et le retentissement de l'airain montait dans"});
db.iliadfr.insert({ pg : 10873, line : "l'Ouranos, par les airs stériles. Et les chevaux de l'Aiakide"});
db.iliadfr.insert({ pg : 10874, line : "pleuraient, hors de la mêlée, parce qu'ils avaient perdu leur"});
db.iliadfr.insert({ pg : 10875, line : "conducteur couché sur la poussière par le tueur d'hommes Hektôr."});
db.iliadfr.insert({ pg : 10876, line : "Et, vainement, Automédôn, le fils du brave Diorès, les excitait du"});
db.iliadfr.insert({ pg : 10877, line : "fouet ou leur adressait de flatteuses paroles, ils ne voulaient"});
db.iliadfr.insert({ pg : 10878, line : "point aller vers le large Hellespontos, ni vers la mêlée des"});
db.iliadfr.insert({ pg : 10879, line : "Akhaiens; et, de même qu'une colonne qui reste debout sur la tombe"});
db.iliadfr.insert({ pg : 10880, line : "d'un homme ou d'une femme, ils restaient immobiles devant le beau"});
db.iliadfr.insert({ pg : 10881, line : "char, la tête courbée vers la terre. Et de chaudes larmes"});
db.iliadfr.insert({ pg : 10882, line : "tombaient de leurs paupières, car ils regrettaient leur"});
db.iliadfr.insert({ pg : 10883, line : "conducteur; et leurs crinières florissantes pendaient, souillées,"});
db.iliadfr.insert({ pg : 10884, line : "des deux côtés du joug. Et le Kroniôn fut saisi de compassion en"});
db.iliadfr.insert({ pg : 10885, line : "les voyant, et, secouant la tête, il dit dans son esprit:"});
db.iliadfr.insert({ pg : 10887, line : "-- Ah! malheureux! pourquoi vous avons-nous donnés au roi Pèleus"});
db.iliadfr.insert({ pg : 10888, line : "qui est mortel, vous qui ne connaîtrez point la vieillesse et qui"});
db.iliadfr.insert({ pg : 10889, line : "êtes immortels? Était-ce pour que vous subissiez aussi les"});
db.iliadfr.insert({ pg : 10890, line : "douleurs humaines? Car l'homme est le plus malheureux de tous les"});
db.iliadfr.insert({ pg : 10891, line : "êtres qui respirent, ou qui rampent sur la terre. Mais le Priamide"});
db.iliadfr.insert({ pg : 10892, line : "Hektôr ne vous conduira jamais, ni vous, ni vos chars splendides."});
db.iliadfr.insert({ pg : 10893, line : "N'est-ce pas assez qu'il possède les armes et qu'il s'en glorifie?"});
db.iliadfr.insert({ pg : 10894, line : "Je remplirai vos genoux et votre âme de vigueur, afin que vous"});
db.iliadfr.insert({ pg : 10895, line : "rameniez Automédôn de la mêlée, vers les nefs creuses; car je"});
db.iliadfr.insert({ pg : 10896, line : "donnerai la victoire aux Troiens, jusqu'à ce qu'ils touchent aux"});
db.iliadfr.insert({ pg : 10897, line : "nefs bien construites, jusqu'à ce que Hélios tombe et que l'ombre"});
db.iliadfr.insert({ pg : 10898, line : "sacrée arrive."});
db.iliadfr.insert({ pg : 10900, line : "Ayant ainsi parlé, il inspira une grande force aux chevaux, et"});
db.iliadfr.insert({ pg : 10901, line : "ceux-ci, secouant la poussière de leurs crins sur la terre,"});
db.iliadfr.insert({ pg : 10902, line : "entraînèrent rapidement le char léger entre les Troiens et les"});
db.iliadfr.insert({ pg : 10903, line : "Akhaiens. Et Automédôn, bien que pleurant son compagnon, excitait"});
db.iliadfr.insert({ pg : 10904, line : "l'impétuosité des chevaux, tel qu'un vautour sur des oies. Et il"});
db.iliadfr.insert({ pg : 10905, line : "s'éloignait ainsi de la foule des Troiens, et il revenait se ruer"});
db.iliadfr.insert({ pg : 10906, line : "dans la mêlée; mais il poursuivait les guerriers sans les tuer, ne"});
db.iliadfr.insert({ pg : 10907, line : "pouvant à la fois, seul sur le char sacré, combattre de la lance"});
db.iliadfr.insert({ pg : 10908, line : "et diriger les chevaux rapides. Enfin, un de ses compagnons,"});
db.iliadfr.insert({ pg : 10909, line : "Alkimédôn, fils de Laerkeus Aimonide, le vit de ses yeux, et,"});
db.iliadfr.insert({ pg : 10910, line : "s'arrêtant auprès du char, dit à Automédôn:"});
db.iliadfr.insert({ pg : 10912, line : "-- Automédôn, quel dieu t'ayant mis dans l'âme un dessein insensé,"});
db.iliadfr.insert({ pg : 10913, line : "t'a ravi l'esprit? Tu veux combattre seul aux premiers rangs,"});
db.iliadfr.insert({ pg : 10914, line : "contre les Troiens, et ton compagnon est mort, et Hektôr se"});
db.iliadfr.insert({ pg : 10915, line : "glorifie de porter sur ses épaules les armes de l'Aiakide!"});
db.iliadfr.insert({ pg : 10917, line : "Et le fils de Diorès, Automédôn, lui répondit:"});
db.iliadfr.insert({ pg : 10919, line : "-- Alkimédôn, nul des Akhaiens ne pourrait dompter les chevaux"});
db.iliadfr.insert({ pg : 10920, line : "immortels, si ce n'est toi. Patroklos, vivant, seul le pouvait,"});
db.iliadfr.insert({ pg : 10921, line : "étant semblable aux dieux par sa prudence. Maintenant, la mort et"});
db.iliadfr.insert({ pg : 10922, line : "la moire l'ont saisi. Prends le fouet et les rênes splendides, et"});
db.iliadfr.insert({ pg : 10923, line : "je descendrai pour combattre."});
db.iliadfr.insert({ pg : 10925, line : "Il parla ainsi, et Alkimédôn monta sur le char et prit le fouet et"});
db.iliadfr.insert({ pg : 10926, line : "les rênes, et Automédôn descendit; mais l'illustre Hektôr, l'ayant"});
db.iliadfr.insert({ pg : 10927, line : "vu, dit aussitôt à Ainéias:"});
db.iliadfr.insert({ pg : 10929, line : "-- Ainéias, prince des Troiens cuirassés, je vois les deux chevaux"});
db.iliadfr.insert({ pg : 10930, line : "du rapide Aiakide qui courent dans la mêlée avec des conducteurs"});
db.iliadfr.insert({ pg : 10931, line : "vils, et j'espère les saisir, si tu veux m'aider, car, sans doute,"});
db.iliadfr.insert({ pg : 10932, line : "ces hommes n'oseront point nous tenir tête."});
db.iliadfr.insert({ pg : 10934, line : "Il parla, et l'irréprochable fils d'Ankhisès consentit, et ils"});
db.iliadfr.insert({ pg : 10935, line : "marchèrent, abritant leurs épaules des cuirs secs et solides que"});
db.iliadfr.insert({ pg : 10936, line : "recouvrait l'airain. Et avec eux marchaient Khromios et Arètos"});
db.iliadfr.insert({ pg : 10937, line : "semblable à un dieu. Et les insensés espéraient tuer les deux"});
db.iliadfr.insert({ pg : 10938, line : "Akhaiens et se saisir des chevaux au large cou; mais ils ne"});
db.iliadfr.insert({ pg : 10939, line : "devaient point revenir sans avoir répandu leur sang sous les mains"});
db.iliadfr.insert({ pg : 10940, line : "d'Automédôn. Et celui-ci supplia le père Zeus, et, plein de force"});
db.iliadfr.insert({ pg : 10941, line : "et de courage dans son coeur sombre, il dit à son compagnon"});
db.iliadfr.insert({ pg : 10942, line : "fidèle, Alkimédôn:"});
db.iliadfr.insert({ pg : 10944, line : "-- Alkimédôn, ne retiens point les chevaux loin de moi, mais"});
db.iliadfr.insert({ pg : 10945, line : "qu'ils soufflent sur mon dos, car je ne pense pas que la fureur du"});
db.iliadfr.insert({ pg : 10946, line : "Priamide Hektôr s'apaise, avant qu'il nous ait tués et qu'il ait"});
db.iliadfr.insert({ pg : 10947, line : "saisi les chevaux aux belles crinières d'Akhilleus, ou qu'il soit"});
db.iliadfr.insert({ pg : 10948, line : "lui-même tombé sous nos mains."});
db.iliadfr.insert({ pg : 10950, line : "Ayant ainsi parlé, il appela les Aias et Ménélaos:"});
db.iliadfr.insert({ pg : 10952, line : "-- Aias et Ménélaos, chefs des Argiens, remettez ce cadavre aux"});
db.iliadfr.insert({ pg : 10953, line : "plus braves, et qu'ils le défendent, et qu'ils repoussent la foule"});
db.iliadfr.insert({ pg : 10954, line : "des hommes; mais éloignez notre dernier jour, à nous qui sommes"});
db.iliadfr.insert({ pg : 10955, line : "vivants, car voici que Hektôr et Ainéias, les plus terribles des"});
db.iliadfr.insert({ pg : 10956, line : "Troiens, se ruent sur nous à travers la mêlée lamentable. Mais la"});
db.iliadfr.insert({ pg : 10957, line : "destinée est sur les genoux des dieux! Je lance ma pique, me"});
db.iliadfr.insert({ pg : 10958, line : "confiant en Zeus."});
db.iliadfr.insert({ pg : 10960, line : "Il parla, et il lança sa longue pique, et il frappa le bouclier"});
db.iliadfr.insert({ pg : 10961, line : "égal d'Arètos. Et le bouclier n'arrêta point l'airain qui le"});
db.iliadfr.insert({ pg : 10962, line : "traversa et entra dans le ventre à travers le baudrier. De même,"});
db.iliadfr.insert({ pg : 10963, line : "quand un jeune homme, armé d'une hache tranchante, frappe entre"});
db.iliadfr.insert({ pg : 10964, line : "les deux cornes d'un boeuf sauvage, il coupe le nerf, et l'animal"});
db.iliadfr.insert({ pg : 10965, line : "bondit et tombe. De même Arètos bondit, et tomba à la renverse, et"});
db.iliadfr.insert({ pg : 10966, line : "la pique, à travers les entrailles, rompit ses forces. Et Hektôr"});
db.iliadfr.insert({ pg : 10967, line : "lança sa pique éclatante contre Automédôn; mais celui-ci, l'ayant"});
db.iliadfr.insert({ pg : 10968, line : "vu, évita en se baissant la pique d'airain qui, par-dessus lui,"});
db.iliadfr.insert({ pg : 10969, line : "plongea en terre et vibra jusqu'à ce que Arès eût épuisé sa"});
db.iliadfr.insert({ pg : 10970, line : "vigueur. Et tous deux se jetaient l'un sur l'autre avec leurs"});
db.iliadfr.insert({ pg : 10971, line : "épées, quand les rapides Aias, à la voix de leur compagnon, se"});
db.iliadfr.insert({ pg : 10972, line : "ruèrent à travers la mêlée. Et Hektôr, Ainéias et Khromios pareil"});
db.iliadfr.insert({ pg : 10973, line : "à un dieu reculèrent, laissant Arètos couché, le ventre ouvert. Et"});
db.iliadfr.insert({ pg : 10974, line : "Automédôn, pareil au rapide Arès, le dépouillant de ses armes, dit"});
db.iliadfr.insert({ pg : 10975, line : "en se glorifiant:"});
db.iliadfr.insert({ pg : 10977, line : "-- Du moins, j'ai un peu soulagé ma douleur de la mort du"});
db.iliadfr.insert({ pg : 10978, line : "Ménoitiade, bien que je n'aie tué qu'un homme très inférieur à"});
db.iliadfr.insert({ pg : 10979, line : "lui."});
db.iliadfr.insert({ pg : 10981, line : "Et il mit sur le char les dépouilles sanglantes, et il y monta,"});
db.iliadfr.insert({ pg : 10982, line : "les pieds et les mains sanglants, comme un lion qui vient de"});
db.iliadfr.insert({ pg : 10983, line : "manger un taureau."});
db.iliadfr.insert({ pg : 10985, line : "Et, de nouveau, la mêlée affreuse et lamentable recommença sur"});
db.iliadfr.insert({ pg : 10986, line : "Patroklos. Et Athènè, descendant de l'Ouranos, anima le combat,"});
db.iliadfr.insert({ pg : 10987, line : "car Zeus au large regard l'avait envoyée afin d'encourager les"});
db.iliadfr.insert({ pg : 10988, line : "Danaens, son esprit étant changé. De même que l'Ouranien Zeus"});
db.iliadfr.insert({ pg : 10989, line : "envoie aux vivants une Iris pourprée, signe de guerre ou de"});
db.iliadfr.insert({ pg : 10990, line : "froides tempêtes, qui interrompt les travaux des hommes et nuit"});
db.iliadfr.insert({ pg : 10991, line : "aux troupeaux; de même Athènè, s'enveloppant d'une nuée pourprée,"});
db.iliadfr.insert({ pg : 10992, line : "se mêla à la foule des Akhaiens. Et, d'abord, elle excita le fils"});
db.iliadfr.insert({ pg : 10993, line : "d'Atreus, parlant ainsi au brave Ménélaos, sous la forme de"});
db.iliadfr.insert({ pg : 10994, line : "Phoinix à la voix mâle:"});
db.iliadfr.insert({ pg : 10996, line : "-- Quelle honte et quelle douleur pour toi, Ménélaos, si les"});
db.iliadfr.insert({ pg : 10997, line : "chiens rapides des Troiens mangeaient, sous leurs murailles, le"});
db.iliadfr.insert({ pg : 10998, line : "cher compagnon de l'illustre Akhilleus Mais sois ferme, et"});
db.iliadfr.insert({ pg : 10999, line : "encourage tout ton peuple."});
db.iliadfr.insert({ pg : 11001, line : "Et le brave Ménélaos lui répondit:"});
db.iliadfr.insert({ pg : 11003, line : "-- Phoinix, mon père, vieillard vénérable, plût aux dieux"});
db.iliadfr.insert({ pg : 11004, line : "qu'Athènè me donnât la force et repoussât loin de moi les traits."});
db.iliadfr.insert({ pg : 11005, line : "J'irais et je défendrais Patroklos, car, en mourant, il a"});
db.iliadfr.insert({ pg : 11006, line : "violemment déchiré mon coeur. Mais la vigueur de Hektôr est comme"});
db.iliadfr.insert({ pg : 11007, line : "celle du feu, et il ne cesse de tuer avec l'airain, et Zeus lui"});
db.iliadfr.insert({ pg : 11008, line : "donne la victoire."});
db.iliadfr.insert({ pg : 11010, line : "Il parla ainsi, et Athènè aux yeux clairs se réjouit parce qu'il"});
db.iliadfr.insert({ pg : 11011, line : "l'avait implorée avant tous les dieux. Et elle répandit la vigueur"});
db.iliadfr.insert({ pg : 11012, line : "dans ses épaules et dans ses genoux, et elle mit dans sa poitrine"});
db.iliadfr.insert({ pg : 11013, line : "l'audace de la mouche qui, toujours et vainement chassée, se plaît"});
db.iliadfr.insert({ pg : 11014, line : "à mordre, car le sang de l'homme lui est doux. Et elle mit cette"});
db.iliadfr.insert({ pg : 11015, line : "audace dans son coeur sombre; et, retournant vers Patroklos, il"});
db.iliadfr.insert({ pg : 11016, line : "lança sa pique brillante. Et parmi les Troiens se trouvait Podès,"});
db.iliadfr.insert({ pg : 11017, line : "fils d'Êétiôn, riche, brave, et très honoré par Hektôr entre tous"});
db.iliadfr.insert({ pg : 11018, line : "les autres, parce qu'il était son plus cher convive. Le blond"});
db.iliadfr.insert({ pg : 11019, line : "Ménélaos le frappa sur le baudrier, comme il fuyait; et l'airain"});
db.iliadfr.insert({ pg : 11020, line : "le traversa, et il tomba avec bruit, et l'Atréide Ménélaos"});
db.iliadfr.insert({ pg : 11021, line : "entraîna son cadavre du côté des Akhaiens. Et Apollôn excita"});
db.iliadfr.insert({ pg : 11022, line : "Hektôr, sous la forme de Phainops Asiade qui habitait Abydos, et"});
db.iliadfr.insert({ pg : 11023, line : "qui était le plus cher des hôtes du Priamide. Et l'archer Apollôn"});
db.iliadfr.insert({ pg : 11024, line : "dit à celui-ci, sous la forme de Phainops:"});
db.iliadfr.insert({ pg : 11026, line : "-- Hektôr, qui d'entre les Akhaiens te redoutera désormais, si tu"});
db.iliadfr.insert({ pg : 11027, line : "crains Ménélaos qui n'est qu'un faible guerrier, et qui enlève"});
db.iliadfr.insert({ pg : 11028, line : "seul ce cadavre, après avoir tué ton compagnon fidèle, brave entre"});
db.iliadfr.insert({ pg : 11029, line : "les hommes, Podès, fils d'Êétiôn?"});
db.iliadfr.insert({ pg : 11031, line : "Il parla ainsi, et la noire nuée de la douleur enveloppa Hektôr,"});
db.iliadfr.insert({ pg : 11032, line : "et il se rua aux premiers rangs, armé de l'airain splendide. Et"});
db.iliadfr.insert({ pg : 11033, line : "alors le Kroniôn saisit l'aigide aux franges éclatantes, et il"});
db.iliadfr.insert({ pg : 11034, line : "couvrit l'Ida de nuées, et, fulgurant, il tonna fortement,"});
db.iliadfr.insert({ pg : 11035, line : "secouant l'aigide, donnant la victoire aux Troiens et mettant les"});
db.iliadfr.insert({ pg : 11036, line : "Akhaiens en fuite."});
db.iliadfr.insert({ pg : 11038, line : "Et, le premier, le Boiôtien Pènéléôs prit la fuite, blessé par"});
db.iliadfr.insert({ pg : 11039, line : "Polydamas d'un coup de lance qui lui avait traversé le haut de"});
db.iliadfr.insert({ pg : 11040, line : "l'épaule jusqu'à l'os. Et Hektôr blessa à la main Lèitos, fils du"});
db.iliadfr.insert({ pg : 11041, line : "magnanime Alektryôn; et il le mit en fuite, épouvanté et regardant"});
db.iliadfr.insert({ pg : 11042, line : "de tous côtés, car il n'espérait plus pouvoir tenir une lance pour"});
db.iliadfr.insert({ pg : 11043, line : "le combat."});
db.iliadfr.insert({ pg : 11045, line : "Et comme Hektôr se jetait sur Lèitos, Idoméneus le frappa à la"});
db.iliadfr.insert({ pg : 11046, line : "cuirasse, au-dessous de la mamelle, mais la longue pique se rompit"});
db.iliadfr.insert({ pg : 11047, line : "là où la pointe s'unit au bois, et les Troiens poussèrent des"});
db.iliadfr.insert({ pg : 11048, line : "clameurs; et, contre Idoméneus Deukalide debout sur son char,"});
db.iliadfr.insert({ pg : 11049, line : "Hektôr lança sa pique qui s'égara et perça le conducteur de"});
db.iliadfr.insert({ pg : 11050, line : "Mèrionès, Koiranos, qui l'avait suivi de la populeuse Lyktos."});
db.iliadfr.insert({ pg : 11051, line : "Idoméneus étant venu à pied des nefs aux doubles avirons, il eût"});
db.iliadfr.insert({ pg : 11052, line : "donné une grande gloire aux Troiens, si Koiranos n'eût amené"});
db.iliadfr.insert({ pg : 11053, line : "aussitôt les chevaux rapides. Et il fut le salut d'Idoméneus, et"});
db.iliadfr.insert({ pg : 11054, line : "il lui conserva la lumière; mais lui-même rendit l'âme sous le"});
db.iliadfr.insert({ pg : 11055, line : "tueur d'hommes Hektôr qui le perça entre la mâchoire et l'oreille."});
db.iliadfr.insert({ pg : 11056, line : "La pique ébranla les dents et trancha la moitié de la langue."});
db.iliadfr.insert({ pg : 11057, line : "Koiranos tomba du char, laissant traîner les rênes. Et Mèrionès,"});
db.iliadfr.insert({ pg : 11058, line : "les saisissant à terre, dit à Idoméneus:"});
db.iliadfr.insert({ pg : 11060, line : "-- Fouette maintenant les rapides chevaux jusqu'aux nefs; tu vois"});
db.iliadfr.insert({ pg : 11061, line : "comme moi que la victoire échappe aux Akhaiens."});
db.iliadfr.insert({ pg : 11063, line : "Il parla ainsi, et Idoméneus fouetta les chevaux aux belles"});
db.iliadfr.insert({ pg : 11064, line : "crinières, jusqu'aux nefs creuses, car la crainte avait envahi son"});
db.iliadfr.insert({ pg : 11065, line : "coeur. Et le magnanime Aias et Ménélaos reconnurent aussi que la"});
db.iliadfr.insert({ pg : 11066, line : "victoire échappait aux Akhaiens et que Zeus la donnait aux"});
db.iliadfr.insert({ pg : 11067, line : "Troiens. Et le grand Télamônien Aias dit le premier:"});
db.iliadfr.insert({ pg : 11069, line : "-- Ô dieux! le plus insensé comprendrait maintenant que le père"});
db.iliadfr.insert({ pg : 11070, line : "Zeus donne la victoire aux Troiens. Tous leurs traits portent, que"});
db.iliadfr.insert({ pg : 11071, line : "ce soit la main d'un lâche qui les envoie ou d'un brave; Zeus les"});
db.iliadfr.insert({ pg : 11072, line : "dirige, et les nôtres tombent vains et impuissants sur la terre."});
db.iliadfr.insert({ pg : 11073, line : "Allons, songeons au moins au meilleur moyen d'entraîner le cadavre"});
db.iliadfr.insert({ pg : 11074, line : "de Patroklos, et nous réjouirons ensuite nos compagnons par notre"});
db.iliadfr.insert({ pg : 11075, line : "retour. Ils s'attristent en nous regardant, car ils pensent que"});
db.iliadfr.insert({ pg : 11076, line : "nous n'échapperons pas aux mains inévitables et à la vigueur du"});
db.iliadfr.insert({ pg : 11077, line : "tueur d'hommes Hektôr, mais que nous serons rejetés vers les nefs"});
db.iliadfr.insert({ pg : 11078, line : "noires. Plût aux dieux qu'un de nous annonçât promptement ce"});
db.iliadfr.insert({ pg : 11079, line : "malheur au Pèléide! Je ne pense pas qu'il sache que son cher"});
db.iliadfr.insert({ pg : 11080, line : "compagnon est mort. Mais je ne sais qui nous pourrions envoyer"});
db.iliadfr.insert({ pg : 11081, line : "parmi les Akhaiens. Un brouillard noir nous enveloppe tous, les"});
db.iliadfr.insert({ pg : 11082, line : "hommes et les chevaux. Père Zeus, délivre de cette obscurité les"});
db.iliadfr.insert({ pg : 11083, line : "fils des Akhaiens; rends-nous la clarté, que nos yeux puissent"});
db.iliadfr.insert({ pg : 11084, line : "voir; et si tu veux nous perdre dans ta colère, que ce soit du"});
db.iliadfr.insert({ pg : 11085, line : "moins à la lumière!"});
db.iliadfr.insert({ pg : 11087, line : "Il parla ainsi, et le père Zeus eut compassion de ses larmes, et"});
db.iliadfr.insert({ pg : 11088, line : "il dispersa aussitôt le brouillard et dissipa la nuée. Hélios"});
db.iliadfr.insert({ pg : 11089, line : "brilla, et toute l'armée apparut. Et Aias dit au brave Ménélaos:"});
db.iliadfr.insert({ pg : 11091, line : "-- Divin Ménélaos, cherche maintenant Antilokhos, le magnanime"});
db.iliadfr.insert({ pg : 11092, line : "fils de Nestôr, si toutefois il est encore vivant, et qu'il se"});
db.iliadfr.insert({ pg : 11093, line : "hâte d'aller dire au belliqueux Akhilleus que le plus cher de ses"});
db.iliadfr.insert({ pg : 11094, line : "compagnons est mort."});
db.iliadfr.insert({ pg : 11096, line : "Il parla ainsi, et le brave Ménélaos se hâta d'obéir, et il"});
db.iliadfr.insert({ pg : 11097, line : "s'éloigna, comme un lion qui, fatigué d'avoir lutté contre les"});
db.iliadfr.insert({ pg : 11098, line : "chiens et les hommes, s'éloigne de l'enclos; car, toute la nuit,"});
db.iliadfr.insert({ pg : 11099, line : "par leur vigilance, ils ne lui ont point permis d'enlever les"});
db.iliadfr.insert({ pg : 11100, line : "boeufs gras. Il s'est rué sur eux, plein du désir des chairs"});
db.iliadfr.insert({ pg : 11101, line : "fraîches; mais la foule des traits a volé de leurs mains"});
db.iliadfr.insert({ pg : 11102, line : "audacieuses, ainsi que les torches ardentes qu'il redoute malgré"});
db.iliadfr.insert({ pg : 11103, line : "sa fureur; et, vers le matin, il s'éloigne, le coeur attristé. De"});
db.iliadfr.insert({ pg : 11104, line : "même le brave Ménélaos s'éloignait contre son gré du corps de"});
db.iliadfr.insert({ pg : 11105, line : "Patroklos, car il craignait que les Akhaiens terrifiés ne"});
db.iliadfr.insert({ pg : 11106, line : "l'abandonnassent en proie à l'ennemi. Et il exhorta Mèrionès et"});
db.iliadfr.insert({ pg : 11107, line : "les Aias:"});
db.iliadfr.insert({ pg : 11109, line : "-- Aias, chefs des Argiens, et toi, Mèrionès, souvenez-vous de la"});
db.iliadfr.insert({ pg : 11110, line : "douceur du malheureux Patroklos! Pendant sa vie, il était plein de"});
db.iliadfr.insert({ pg : 11111, line : "douceur pour tous; et, maintenant, la mort et la moire l'ont"});
db.iliadfr.insert({ pg : 11112, line : "saisi!"});
db.iliadfr.insert({ pg : 11114, line : "Ayant ainsi parlé, le blond Ménélaos s'éloigna, regardant de tous"});
db.iliadfr.insert({ pg : 11115, line : "les côtés, comme l'aigle qui, dit-on, est, de tous les oiseaux de"});
db.iliadfr.insert({ pg : 11116, line : "l'Ouranos, celui dont la vue est la plus perçante, car, des"});
db.iliadfr.insert({ pg : 11117, line : "hauteurs où il vit, il aperçoit le lièvre qui gîte sous un arbuste"});
db.iliadfr.insert({ pg : 11118, line : "feuillu; et il tombe aussitôt sur lui, le saisit et lui arrache"});
db.iliadfr.insert({ pg : 11119, line : "l'âme. De même, divin Ménélaos, tes yeux clairs regardaient de"});
db.iliadfr.insert({ pg : 11120, line : "tous côtés, dans la foule des Akhaiens, s'ils voyaient, vivant, le"});
db.iliadfr.insert({ pg : 11121, line : "fils de Nestôr. Et Ménélaos le reconnut, à la gauche de la mêlée,"});
db.iliadfr.insert({ pg : 11122, line : "excitant ses compagnons au combat. Et, s'approchant, le blond"});
db.iliadfr.insert({ pg : 11123, line : "Ménélaos lui dit:"});
db.iliadfr.insert({ pg : 11125, line : "-- Viens, divin Antilokhos! apprends une triste nouvelle. Plût aux"});
db.iliadfr.insert({ pg : 11126, line : "dieux que ceci ne fût jamais arrivé! Sans doute tu sais déjà qu'un"});
db.iliadfr.insert({ pg : 11127, line : "dieu accable les Akhaiens et donne la victoire aux Troiens. Le"});
db.iliadfr.insert({ pg : 11128, line : "meilleur des Akhaiens a été tué, Patroklos, qui laisse de grands"});
db.iliadfr.insert({ pg : 11129, line : "regrets aux Danaens. Mais toi, cours aux nefs des Akhaiens, et"});
db.iliadfr.insert({ pg : 11130, line : "annonce ce malheur au Pèléide. Qu'il vienne promptement sauver son"});
db.iliadfr.insert({ pg : 11131, line : "cadavre nu, car Hektôr au casque mouvant possède ses armes."});
db.iliadfr.insert({ pg : 11133, line : "Il parla ainsi, et Antilokhos, accablé par ces paroles, resta"});
db.iliadfr.insert({ pg : 11134, line : "longtemps muet, et ses yeux s'emplirent de larmes, et la voix lui"});
db.iliadfr.insert({ pg : 11135, line : "manqua; mais il obéit à l'ordre de Ménélaos. Et il remit ses armes"});
db.iliadfr.insert({ pg : 11136, line : "à l'irréprochable Laodokos, son ami, qui conduisait ses chevaux"});
db.iliadfr.insert({ pg : 11137, line : "aux sabots massifs, et il s'éloigna en courant. Et ses pieds"});
db.iliadfr.insert({ pg : 11138, line : "l'emportaient, pleurant, afin d'annoncer au Pèléide Akhilleus la"});
db.iliadfr.insert({ pg : 11139, line : "triste nouvelle."});
db.iliadfr.insert({ pg : 11141, line : "Et tu ne voulus point, divin Ménélaos, venir en aide aux"});
db.iliadfr.insert({ pg : 11142, line : "compagnons attristés d'Antilokhos, aux Pyliens qui le"});
db.iliadfr.insert({ pg : 11143, line : "regrettaient. Et il leur laissa le divin Thrasymèdès, et il"});
db.iliadfr.insert({ pg : 11144, line : "retourna auprès du héros Patroklos, et, parvenu jusqu'aux Aias, il"});
db.iliadfr.insert({ pg : 11145, line : "leur dit:"});
db.iliadfr.insert({ pg : 11147, line : "-- J'ai envoyé Antilokhos vers les nefs, afin de parler au Pèléiôn"});
db.iliadfr.insert({ pg : 11148, line : "aux pieds rapides; mais je ne pense pas que le Pèlèiade vienne"});
db.iliadfr.insert({ pg : 11149, line : "maintenant, bien que très irrité contre le divin Hektôr, car il ne"});
db.iliadfr.insert({ pg : 11150, line : "peut combattre sans armes. Songeons, pour le mieux, de quelle"});
db.iliadfr.insert({ pg : 11151, line : "façon nous entraînerons ce cadavre, et comment nous éviterons"});
db.iliadfr.insert({ pg : 11152, line : "nous-mêmes la mort et la moire à travers le tumulte des Troiens."});
db.iliadfr.insert({ pg : 11154, line : "Et le grand Aias Télamônien lui répondit:"});
db.iliadfr.insert({ pg : 11156, line : "-- Tu as bien dit, ô illustre Ménélaos. Toi et Mèrionès, enlevez"});
db.iliadfr.insert({ pg : 11157, line : "promptement le cadavre et emportez-le hors de la mêlée; et,"});
db.iliadfr.insert({ pg : 11158, line : "derrière vous, nous repousserons les Troiens et le divin Hektôr,"});
db.iliadfr.insert({ pg : 11159, line : "nous qui avons la même âme et le même nom, et qui savons affronter"});
db.iliadfr.insert({ pg : 11160, line : "tous deux le combat terrible."});
db.iliadfr.insert({ pg : 11162, line : "Il parla ainsi, et, dans leurs bras, ils enlevèrent le cadavre. Et"});
db.iliadfr.insert({ pg : 11163, line : "les Troiens poussèrent des cris horribles en voyant les Akhaiens"});
db.iliadfr.insert({ pg : 11164, line : "enlever Patroklos. Et ils se ruèrent, semblables à des chiens qui,"});
db.iliadfr.insert({ pg : 11165, line : "devançant les chasseurs, s'amassent sur un sanglier blessé qu'ils"});
db.iliadfr.insert({ pg : 11166, line : "veulent déchirer. Mais s'il se retourne, confiant dans sa force,"});
db.iliadfr.insert({ pg : 11167, line : "ils s'arrêtent et fuient çà et là. Ainsi les Troiens se ruaient en"});
db.iliadfr.insert({ pg : 11168, line : "foule, frappant de l'épée et de la lance; mais, quand les Aias se"});
db.iliadfr.insert({ pg : 11169, line : "retournaient et leur tenaient tête, ils changeaient de couleur, et"});
db.iliadfr.insert({ pg : 11170, line : "aucun n'osait les combattre pour leur disputer ce cadavre."});
db.iliadfr.insert({ pg : 11172, line : "Et ils emportaient ainsi avec ardeur le cadavre, hors de la mêlée,"});
db.iliadfr.insert({ pg : 11173, line : "vers les nefs creuses. Et le combat les suivait, acharné et"});
db.iliadfr.insert({ pg : 11174, line : "terrible, comme un incendie qui éclate brusquement dans une ville;"});
db.iliadfr.insert({ pg : 11175, line : "et les maisons croulent dans une vaste flamme que tourmente la"});
db.iliadfr.insert({ pg : 11176, line : "violence du vent. Ainsi le tumulte sans trêve des chevaux et des"});
db.iliadfr.insert({ pg : 11177, line : "hommes poursuivait les Akhaiens. Comme des mulets vigoureux, se"});
db.iliadfr.insert({ pg : 11178, line : "hâtant, malgré le travail et la sueur, traînent par l'âpre chemin"});
db.iliadfr.insert({ pg : 11179, line : "d'une montagne, soit une poutre, soit un mât; ainsi Ménélaos et"});
db.iliadfr.insert({ pg : 11180, line : "Mèrionès emportaient à la hâte le cadavre. Et derrière eux, les"});
db.iliadfr.insert({ pg : 11181, line : "Aias repoussaient les Troiens, comme une colline boisée, qui"});
db.iliadfr.insert({ pg : 11182, line : "s'étend par la plaine, repousse les courants furieux des fleuves"});
db.iliadfr.insert({ pg : 11183, line : "rapides qui ne peuvent la rompre et qu'elle rejette toujours vers"});
db.iliadfr.insert({ pg : 11184, line : "la plaine. Ainsi les Aias repoussaient la foule des Troiens qui"});
db.iliadfr.insert({ pg : 11185, line : "les poursuivaient, conduits par Ainéias Ankhisiade et par"});
db.iliadfr.insert({ pg : 11186, line : "l'illustre Hektôr. Comme une troupe d'étourneaux et de geais vole"});
db.iliadfr.insert({ pg : 11187, line : "en poussant des cris aigus, à l'approche de l'épervier qui tue les"});
db.iliadfr.insert({ pg : 11188, line : "petits oiseaux, de même les fils des Akhaiens couraient avec des"});
db.iliadfr.insert({ pg : 11189, line : "clameurs perçantes, devant Ainéias et Hektôr, et oublieux du"});
db.iliadfr.insert({ pg : 11190, line : "combat. Et les belles armes des Danaens en fuite emplissaient les"});
db.iliadfr.insert({ pg : 11191, line : "bords du fossé et le fossé lui-même; mais le carnage ne cessait"});
db.iliadfr.insert({ pg : 11192, line : "point."});
db.iliadfr.insert({ pg : 11195, line : "Chant 18"});
db.iliadfr.insert({ pg : 11197, line : "Et ils combattaient ainsi, comme le feu ardent. Et Antilokhos vint"});
db.iliadfr.insert({ pg : 11198, line : "à Akhilleus aux pieds rapides, et il le trouva devant ses nefs aux"});
db.iliadfr.insert({ pg : 11199, line : "antennes dressées, songeant dans son esprit aux choses accomplies"});
db.iliadfr.insert({ pg : 11200, line : "déjà; et, gémissant, il disait dans son coeur magnanime:"});
db.iliadfr.insert({ pg : 11202, line : "-- Ô dieux! pourquoi les Akhaiens chevelus, dispersés par la"});
db.iliadfr.insert({ pg : 11203, line : "plaine, sont-ils repoussés tumultueusement vers les nefs? Que les"});
db.iliadfr.insert({ pg : 11204, line : "dieux m'épargnent ces cruelles douleurs qu'autrefois ma mère"});
db.iliadfr.insert({ pg : 11205, line : "m'annonça, quand elle me disait que le meilleur des Myrmidones,"});
db.iliadfr.insert({ pg : 11206, line : "moi vivant, perdrait la lumière de Hélios sous les mains des"});
db.iliadfr.insert({ pg : 11207, line : "Troiens. Sans doute il est déjà mort, le brave fils de Ménoitios,"});
db.iliadfr.insert({ pg : 11208, line : "le malheureux! Certes, j'avais ordonné qu'ayant repoussé le feu"});
db.iliadfr.insert({ pg : 11209, line : "ennemi, il revînt aux nefs sans combattre Hektôr."});
db.iliadfr.insert({ pg : 11211, line : "Tandis qu'il roulait ceci dans son esprit et dans son coeur, le"});
db.iliadfr.insert({ pg : 11212, line : "fils de l'illustre Nestôr s'approcha de lui, et, versant de"});
db.iliadfr.insert({ pg : 11213, line : "chaudes larmes, dit la triste nouvelle:"});
db.iliadfr.insert({ pg : 11215, line : "-- Hélas! fils du belliqueux Pèleus, certes, tu vas entendre une"});
db.iliadfr.insert({ pg : 11216, line : "triste nouvelle; et plût aux dieux que ceci ne fût point arrivé!"});
db.iliadfr.insert({ pg : 11217, line : "Patroklos gît mort, et tous combattent pour son cadavre nu, car"});
db.iliadfr.insert({ pg : 11218, line : "Hektôr possède ses armes."});
db.iliadfr.insert({ pg : 11220, line : "Il parla ainsi, et la noire nuée de la douleur enveloppa"});
db.iliadfr.insert({ pg : 11221, line : "Akhilleus, et il saisit de ses deux mains la poussière du foyer et"});
db.iliadfr.insert({ pg : 11222, line : "la répandit sur sa tête, et il en souilla sa belle face; et la"});
db.iliadfr.insert({ pg : 11223, line : "noire poussière souilla sa tunique nektaréenne; et, lui-même,"});
db.iliadfr.insert({ pg : 11224, line : "étendu tout entier dans la poussière, gisait, et des deux mains"});
db.iliadfr.insert({ pg : 11225, line : "arrachait sa chevelure. Et les femmes, que lui et Patroklos"});
db.iliadfr.insert({ pg : 11226, line : "avaient prises, hurlaient violemment, affligées dans leur coeur;"});
db.iliadfr.insert({ pg : 11227, line : "et toutes, hors des tentes, entouraient le belliqueux Akhilleus,"});
db.iliadfr.insert({ pg : 11228, line : "et elles se frappaient la poitrine, et leurs genoux étaient"});
db.iliadfr.insert({ pg : 11229, line : "rompus. Antilokhos aussi gémissait, répandant des larmes, et"});
db.iliadfr.insert({ pg : 11230, line : "tenait les mains d'Akhilleus qui sanglotait dans son noble coeur."});
db.iliadfr.insert({ pg : 11231, line : "Et le Nestôride craignait qu'il se tranchât la gorge avec"});
db.iliadfr.insert({ pg : 11232, line : "l'airain."});
db.iliadfr.insert({ pg : 11234, line : "Akhilleus poussait des sanglots terribles, et sa mère vénérable"});
db.iliadfr.insert({ pg : 11235, line : "l'entendit, assise dans les gouffres de la mer, auprès de son"});
db.iliadfr.insert({ pg : 11236, line : "vieux père. Et elle se lamenta aussitôt. Et autour de la déesse"});
db.iliadfr.insert({ pg : 11237, line : "étaient rassemblées toutes les nèrèides qui sont au fond de la"});
db.iliadfr.insert({ pg : 11238, line : "mer: Glaukè, et Thaléia, et Kymodokè, et Nèsaiè, et Spéiô, et"});
db.iliadfr.insert({ pg : 11239, line : "Thoè, et Haliè aux yeux de boeuf, et Kymothoè, et Alkaiè, et"});
db.iliadfr.insert({ pg : 11240, line : "Limnoréia, et Mélitè, et Iaira, et Amphithoè, et Agavè, et Lôtô,"});
db.iliadfr.insert({ pg : 11241, line : "et Prôtô, et Phérousa, Dynaménè, et Dexaménè et Amphinomè, et"});
db.iliadfr.insert({ pg : 11242, line : "Kallianassa, et Dôris, et Panopè, et l'illustre Galatéia, et"});
db.iliadfr.insert({ pg : 11243, line : "Nèmertès, et Abseudès, et Kallianéira, et Klyménè, et Ianéira, et"});
db.iliadfr.insert({ pg : 11244, line : "Ianassa, et Maira, et Oreithya, et Amathéia aux beaux cheveux, et"});
db.iliadfr.insert({ pg : 11245, line : "les autres nèrèides qui sont dans la profonde mer. Et elles"});
db.iliadfr.insert({ pg : 11246, line : "emplissaient la grotte d'argent, et elles se frappaient la"});
db.iliadfr.insert({ pg : 11247, line : "poitrine, et Thétis se lamentait ainsi:"});
db.iliadfr.insert({ pg : 11249, line : "-- Ecoutez-moi, soeurs nèrèides, afin que vous sachiez les"});
db.iliadfr.insert({ pg : 11250, line : "douleurs qui déchirent mon âme, hélas! à moi, malheureuse, qui ai"});
db.iliadfr.insert({ pg : 11251, line : "enfanté un homme illustre, un fils irréprochable et brave, le plus"});
db.iliadfr.insert({ pg : 11252, line : "courageux des héros, et qui a grandi comme un arbre. Je l'ai élevé"});
db.iliadfr.insert({ pg : 11253, line : "comme une plante dans une terre fertile, et je l'ai envoyé vers"});
db.iliadfr.insert({ pg : 11254, line : "Ilios, sur ses nefs aux poupes recourbées, combattre les Troiens."});
db.iliadfr.insert({ pg : 11255, line : "Et je ne le verrai point revenir dans mes demeures, dans la maison"});
db.iliadfr.insert({ pg : 11256, line : "Pèléienne. Voici qu'il est vivant, et qu'il voit la lumière de"});
db.iliadfr.insert({ pg : 11257, line : "Hélios, et qu'il souffre, et je ne puis le secourir. Mais j'irai"});
db.iliadfr.insert({ pg : 11258, line : "vers mon fils bien-aimé, et je saurai de lui-même quelle douleur"});
db.iliadfr.insert({ pg : 11259, line : "l'accable loin du combat."});
db.iliadfr.insert({ pg : 11261, line : "Ayant ainsi parlé, elle quitta la grotte, et toutes la suivaient,"});
db.iliadfr.insert({ pg : 11262, line : "pleurantes; et l'eau de la mer s'ouvrait devant elles. Puis, elles"});
db.iliadfr.insert({ pg : 11263, line : "parvinrent à la riche Troie, et elles abordèrent là où les"});
db.iliadfr.insert({ pg : 11264, line : "Myrmidones, autour d'Akhilleus aux pieds rapides, avaient tiré"});
db.iliadfr.insert({ pg : 11265, line : "leurs nombreuses nefs sur le rivage. Et sa mère vénérable le"});
db.iliadfr.insert({ pg : 11266, line : "trouva poussant de profonds soupirs; et elle prit, en pleurant, la"});
db.iliadfr.insert({ pg : 11267, line : "tête de son fils, et elle lui dit en gémissant ces paroles ailées:"});
db.iliadfr.insert({ pg : 11269, line : "-- Mon enfant, pourquoi pleures-tu? Quelle douleur envahit ton"});
db.iliadfr.insert({ pg : 11270, line : "âme? Parle, ne me cache rien, afin que nous sachions tous deux."});
db.iliadfr.insert({ pg : 11271, line : "Zeus, ainsi que je l'en avais supplié de mes mains étendues, a"});
db.iliadfr.insert({ pg : 11272, line : "rejeté tous les fils des Akhaiens auprès des nefs, et ils"});
db.iliadfr.insert({ pg : 11273, line : "souffrent de grands maux, parce que tu leur manques."});
db.iliadfr.insert({ pg : 11275, line : "Et Akhilleus aux pieds rapides, avec de profonds soupirs, lui"});
db.iliadfr.insert({ pg : 11276, line : "répondit:"});
db.iliadfr.insert({ pg : 11278, line : "-- Ma mère, l'Olympien m'a exaucé; mais qu'en ai-je retiré,"});
db.iliadfr.insert({ pg : 11279, line : "puisque mon cher compagnon Patroklos est mort, lui que j'honorais"});
db.iliadfr.insert({ pg : 11280, line : "entre tous autant que moi-même? Je l'ai perdu. Hektôr, l'ayant"});
db.iliadfr.insert({ pg : 11281, line : "tué, lui a arraché mes belles, grandes et admirables armes,"});
db.iliadfr.insert({ pg : 11282, line : "présents splendides des dieux à Pèleus, le jour où ils te firent"});
db.iliadfr.insert({ pg : 11283, line : "partager le lit d'un homme mortel. Plût aux dieux que tu fusses"});
db.iliadfr.insert({ pg : 11284, line : "restée avec les déesses de la mer, et que Pèleus eût épousé plutôt"});
db.iliadfr.insert({ pg : 11285, line : "une femme mortelle! Maintenant, une douleur éternelle emplira ton"});
db.iliadfr.insert({ pg : 11286, line : "âme, à cause de la mort de ton fils que tu ne verras plus revenir"});
db.iliadfr.insert({ pg : 11287, line : "dans tes demeures; car je ne veux plus vivre, ni m'inquiéter des"});
db.iliadfr.insert({ pg : 11288, line : "hommes, à moins que Hektôr, percé par ma lance, ne rende l'âme, et"});
db.iliadfr.insert({ pg : 11289, line : "que Patroklos Ménoitiade, livré en pâture aux chiens, ne soit"});
db.iliadfr.insert({ pg : 11290, line : "vengé."});
db.iliadfr.insert({ pg : 11292, line : "Et Thétis, versant des larmes, lui répondit:"});
db.iliadfr.insert({ pg : 11294, line : "-- Mon enfant, dois-tu donc bientôt mourir, comme tu le dis? C'est"});
db.iliadfr.insert({ pg : 11295, line : "ta mort qui doit suivre celle de Hektôr!"});
db.iliadfr.insert({ pg : 11297, line : "Et Akhilleùs aux pieds rapides, en gémissant lui répondit:"});
db.iliadfr.insert({ pg : 11299, line : "-- Je mourrai donc, puisque je n'ai pu secourir mon compagnon,"});
db.iliadfr.insert({ pg : 11300, line : "pendant qu'on le tuait. Il est mort loin de la patrie, et il m'a"});
db.iliadfr.insert({ pg : 11301, line : "conjuré de le venger. Je mourrai maintenant, puisque je ne"});
db.iliadfr.insert({ pg : 11302, line : "retournerai point dans la patrie, et que je n'ai sauvé ni"});
db.iliadfr.insert({ pg : 11303, line : "Patroklos, ni ceux de mes compagnons qui sont tombés en foule sous"});
db.iliadfr.insert({ pg : 11304, line : "le divin Hektôr, tandis que j'étais assis sur mes nefs, inutile"});
db.iliadfr.insert({ pg : 11305, line : "fardeau de la terre, moi qui l'emporte sur tous les Akhaiens dans"});
db.iliadfr.insert({ pg : 11306, line : "le combat; car d'autres sont meilleurs dans l'agora. Ah! que la"});
db.iliadfr.insert({ pg : 11307, line : "dissension périsse parmi les dieux! et, parmi les hommes, périsse"});
db.iliadfr.insert({ pg : 11308, line : "la colère qui trouble le plus sage, et qui, plus douce que le miel"});
db.iliadfr.insert({ pg : 11309, line : "liquide, se gonfle, comme la fumée dans la poitrine des hommes!"});
db.iliadfr.insert({ pg : 11310, line : "C'est ainsi que le roi des hommes, Agamemnôn, a provoqué ma"});
db.iliadfr.insert({ pg : 11311, line : "colère. Mais oublions le passé, malgré nos douleurs, et, dans"});
db.iliadfr.insert({ pg : 11312, line : "notre poitrine, ployons notre âme à la nécessité. Je chercherai"});
db.iliadfr.insert({ pg : 11313, line : "Hektôr qui m'a enlevé cette chère tête, et je recevrai la mort"});
db.iliadfr.insert({ pg : 11314, line : "quand il plaira à Zeus et aux autres dieux immortels. La force"});
db.iliadfr.insert({ pg : 11315, line : "Hèrakléenne n'évita point la mort, lui qui était très-cher au roi"});
db.iliadfr.insert({ pg : 11316, line : "Zeus Kroniôn; mais l'inévitable colère de Hèrè et la moire le"});
db.iliadfr.insert({ pg : 11317, line : "domptèrent. Si une moire semblable m'attend, on me couchera mort"});
db.iliadfr.insert({ pg : 11318, line : "sur le bûcher, mais, auparavant, je remporterai une grande gloire."});
db.iliadfr.insert({ pg : 11319, line : "Et que la Troadienne, ou la Dardanienne, essuie de ses deux mains"});
db.iliadfr.insert({ pg : 11320, line : "ses joues délicates couvertes de larmes, car je la contraindrai de"});
db.iliadfr.insert({ pg : 11321, line : "gémir misérablement; et elles comprendront que je me suis"});
db.iliadfr.insert({ pg : 11322, line : "longtemps éloigné du combat. Ne me retiens donc pas, malgré ta"});
db.iliadfr.insert({ pg : 11323, line : "tendresse, car tu ne me persuaderas point."});
db.iliadfr.insert({ pg : 11325, line : "Et la déesse Thétis aux pieds d'argent lui répondit:"});
db.iliadfr.insert({ pg : 11327, line : "-- Certes, mon fils, tu as bien dit: il est beau de venger la"});
db.iliadfr.insert({ pg : 11328, line : "ruine cruelle de ses compagnons. Mais tes armes d'airain, belles"});
db.iliadfr.insert({ pg : 11329, line : "et splendides, sont parmi les Troiens. Hektôr au casque mouvant se"});
db.iliadfr.insert({ pg : 11330, line : "glorifie d'en avoir couvert ses épaules; mais je ne pense pas"});
db.iliadfr.insert({ pg : 11331, line : "qu'il s'en réjouisse longtemps, car le meurtre est auprès de lui."});
db.iliadfr.insert({ pg : 11332, line : "N'entre point dans la mêlée d'Arès avant que tu m'aies revue de"});
db.iliadfr.insert({ pg : 11333, line : "tes yeux. Je reviendrai demain, comme Hélios se lèvera, avec de"});
db.iliadfr.insert({ pg : 11334, line : "belles armes venant du roi Hèphaistos."});
db.iliadfr.insert({ pg : 11336, line : "Ayant ainsi parlé, elle quitta son fils et dit à ses soeurs de la"});
db.iliadfr.insert({ pg : 11337, line : "mer:"});
db.iliadfr.insert({ pg : 11339, line : "-- Rentrez à la hâte dans le large sein de la mer, et retournez"});
db.iliadfr.insert({ pg : 11340, line : "dans les demeures de notre vieux père, et dites-lui tout ceci."});
db.iliadfr.insert({ pg : 11341, line : "Moi, je vais dans le vaste Olympos, auprès de l'illustre ouvrier"});
db.iliadfr.insert({ pg : 11342, line : "Hèphaistos, afin de lui demander de belles armes splendides pour"});
db.iliadfr.insert({ pg : 11343, line : "mon fils."});
db.iliadfr.insert({ pg : 11345, line : "Elle parla ainsi, et les nèrèides disparurent aussitôt sous l'eau"});
db.iliadfr.insert({ pg : 11346, line : "de la mer, et la déesse Thétis aux pieds d'argent monta de nouveau"});
db.iliadfr.insert({ pg : 11347, line : "dans l'Olympos, afin d'en rapporter de belles et illustres armes"});
db.iliadfr.insert({ pg : 11348, line : "pour son fils."});
db.iliadfr.insert({ pg : 11350, line : "Et, tandis que ses pieds la portaient dans l'Olympos, les"});
db.iliadfr.insert({ pg : 11351, line : "Akhaiens, avec un grand tumulte, vers les nefs et le Hellespontos,"});
db.iliadfr.insert({ pg : 11352, line : "fuyaient devant le tueur d'hommes Hektôr."});
db.iliadfr.insert({ pg : 11354, line : "Et les Akhaiens aux belles knèmides n'avaient pu enlever hors des"});
db.iliadfr.insert({ pg : 11355, line : "traits le cadavre de Patroklos, du compagnon d'Akhilleus; et tout"});
db.iliadfr.insert({ pg : 11356, line : "le peuple de Troiè, et les chevaux, et le Priamide Hektôr,"});
db.iliadfr.insert({ pg : 11357, line : "semblable à la flamme par sa fureur, poursuivaient toujours"});
db.iliadfr.insert({ pg : 11358, line : "Patroklos. Et, trois fois, l'illustre Hektôr le saisit par les"});
db.iliadfr.insert({ pg : 11359, line : "pieds, désirant l'entraîner, et excitant les Troiens, et, trois"});
db.iliadfr.insert({ pg : 11360, line : "fois, les Aias, revêtus d'une force impétueuse, le repoussèrent"});
db.iliadfr.insert({ pg : 11361, line : "loin du cadavre; et lui, certain de son courage, tantôt se ruait"});
db.iliadfr.insert({ pg : 11362, line : "dans la mêlée, tantôt s'arrêtait avec de grands cris, mais jamais"});
db.iliadfr.insert({ pg : 11363, line : "ne reculait. De même que les bergers campagnards ne peuvent"});
db.iliadfr.insert({ pg : 11364, line : "chasser loin de sa proie un lion fauve et affamé, de même les deux"});
db.iliadfr.insert({ pg : 11365, line : "Aias ne pouvaient repousser le Priamide Hektôr loin du cadavre; et"});
db.iliadfr.insert({ pg : 11366, line : "il l'eût entraîné, et il eût remporté une grande gloire, si la"});
db.iliadfr.insert({ pg : 11367, line : "rapide Iris aux pieds aériens vers le Pèléide ne fût venue à la"});
db.iliadfr.insert({ pg : 11368, line : "hâte de l'Olympos, afin qu'il se montrât. Hèrè l'avait envoyée,"});
db.iliadfr.insert({ pg : 11369, line : "Zeus et les autres dieux l'ignorant. Et, debout auprès de lui,"});
db.iliadfr.insert({ pg : 11370, line : "elle dit en paroles ailées:"});
db.iliadfr.insert({ pg : 11372, line : "-- Lève-toi, Pèléide, le plus effrayant des hommes, et secours"});
db.iliadfr.insert({ pg : 11373, line : "Patroklos pour qui on combat avec fureur devant les nefs. C'est là"});
db.iliadfr.insert({ pg : 11374, line : "que tous s'entre-tuent, les Akhaiens pour le défendre, et les"});
db.iliadfr.insert({ pg : 11375, line : "Troiens pour l'entraîner vers Ilios battue des vents. Et"});
db.iliadfr.insert({ pg : 11376, line : "l'illustre Hektôr espère surtout l'entraîner, et il veut mettre,"});
db.iliadfr.insert({ pg : 11377, line : "après l'avoir coupée, la tête de Patroklos au bout d'un pieu."});
db.iliadfr.insert({ pg : 11378, line : "Lève-toi; ne reste pas plus longtemps inerte, et que la honte te"});
db.iliadfr.insert({ pg : 11379, line : "saisisse en songeant à Patroklos devenu le jouet des chiens"});
db.iliadfr.insert({ pg : 11380, line : "troiens. Ce serait un opprobre pour toi, si son cadavre était"});
db.iliadfr.insert({ pg : 11381, line : "souillé."});
db.iliadfr.insert({ pg : 11383, line : "Et le divin et rapide Akhilleus lui dit:"});
db.iliadfr.insert({ pg : 11385, line : "-- Déesse Iris, qui d'entre les dieux t'a envoyée vers moi?"});
db.iliadfr.insert({ pg : 11387, line : "Et la rapide Iris aux pieds aériens lui répondit:"});
db.iliadfr.insert({ pg : 11389, line : "-- Hèrè, la glorieuse épouse de Zeus, m'a envoyée; et le sublime"});
db.iliadfr.insert({ pg : 11390, line : "Kronide et tous les immortels qui habitent l'Olympos neigeux"});
db.iliadfr.insert({ pg : 11391, line : "l'ignorent."});
db.iliadfr.insert({ pg : 11393, line : "Et Akhilleus aux pieds rapides, lui répondant, parla ainsi:"});
db.iliadfr.insert({ pg : 11395, line : "-- Comment irais-je au combat, puisqu'ils ont mes armes? Ma mère"});
db.iliadfr.insert({ pg : 11396, line : "bien-aimée me le défend, avant que je l'aie vue, de mes yeux,"});
db.iliadfr.insert({ pg : 11397, line : "reparaître avec de belles armes venant de Hèphaistos. Je ne puis"});
db.iliadfr.insert({ pg : 11398, line : "revêtir celles d'aucun autre guerrier, sauf le bouclier d'Aias"});
db.iliadfr.insert({ pg : 11399, line : "Télamôniade; mais il combat sans doute aux premiers rangs, tuant"});
db.iliadfr.insert({ pg : 11400, line : "les ennemis, de sa lance, autour du cadavre de Patroklos."});
db.iliadfr.insert({ pg : 11402, line : "Et la rapide Iris aux pieds aériens lui répondit:"});
db.iliadfr.insert({ pg : 11404, line : "-- Certes, nous savons que tes belles armes te sont enlevées;"});
db.iliadfr.insert({ pg : 11405, line : "mais, tel que te voilà, apparais aux Troiens sur le bord du fossé;"});
db.iliadfr.insert({ pg : 11406, line : "et ils reculeront épouvantés, et les braves fils des Akhaiens"});
db.iliadfr.insert({ pg : 11407, line : "respireront. Il ne s'agit que de respirer un moment."});
db.iliadfr.insert({ pg : 11409, line : "Ayant ainsi parlé, la rapide Iris disparut. Et Akhilleus cher à"});
db.iliadfr.insert({ pg : 11410, line : "Zeus se leva; et, sur ses robustes épaules, Athènè mit l'aigide"});
db.iliadfr.insert({ pg : 11411, line : "frangée; et la grande déesse ceignit la tête du héros d'une nuée"});
db.iliadfr.insert({ pg : 11412, line : "d'or sur laquelle elle alluma une flamme resplendissante. De même,"});
db.iliadfr.insert({ pg : 11413, line : "dans une île lointaine, la fumée monte vers l'aithèr, du milieu"});
db.iliadfr.insert({ pg : 11414, line : "d'une ville assiégée. Tout le jour, les citoyens ont combattu avec"});
db.iliadfr.insert({ pg : 11415, line : "fureur hors de la ville; mais, au déclin de Hélios, ils allument"});
db.iliadfr.insert({ pg : 11416, line : "des feux ardents dont la splendeur monte dans l'air, et sera peut-"});
db.iliadfr.insert({ pg : 11417, line : "être vue des peuples voisins qui viendront sur leurs nefs les"});
db.iliadfr.insert({ pg : 11418, line : "délivrer d'Arès. Ainsi, une haute clarté montait de la tête d'"});
db.iliadfr.insert({ pg : 11419, line : "Akhilleus jusque dans l'aithèr. Et il s'arrêta sur le bord du"});
db.iliadfr.insert({ pg : 11420, line : "fossé, sans se mêler aux Akhaiens, car il obéissait à l'ordre"});
db.iliadfr.insert({ pg : 11421, line : "prudent de sa mère. Là, debout, il poussa un cri, et Pallas Athènè"});
db.iliadfr.insert({ pg : 11422, line : "cria aussi, et un immense tumulte s'éleva parmi les Troiens. Et"});
db.iliadfr.insert({ pg : 11423, line : "l'illustre voix de l'Aiakide était semblable au son strident de la"});
db.iliadfr.insert({ pg : 11424, line : "trompette, autour d'une ville assiégée par des ennemis acharnés."});
db.iliadfr.insert({ pg : 11426, line : "Et, dès que les Troiens eurent entendu la voix d'airain de"});
db.iliadfr.insert({ pg : 11427, line : "l'Aiakide, ils frémirent tous; et les chevaux aux belles crinières"});
db.iliadfr.insert({ pg : 11428, line : "tournèrent les chars, car ils pressentaient des malheurs, et leurs"});
db.iliadfr.insert({ pg : 11429, line : "conducteurs furent épouvantés quand ils virent cette flamme"});
db.iliadfr.insert({ pg : 11430, line : "infatigable et horrible qui brûlait sur la tête du magnanime"});
db.iliadfr.insert({ pg : 11431, line : "Pèléiôn et que nourrissait la déesse aux yeux clairs Athènè. Et,"});
db.iliadfr.insert({ pg : 11432, line : "trois fois, sur le bord du fossé, le divin Akhilleus cria, et,"});
db.iliadfr.insert({ pg : 11433, line : "trois fois, les Troiens furent bouleversés, et les illustres"});
db.iliadfr.insert({ pg : 11434, line : "alliés; et douze des plus braves périrent au milieu de leurs chars"});
db.iliadfr.insert({ pg : 11435, line : "et de leurs lances."});
db.iliadfr.insert({ pg : 11437, line : "Mais les Akhaiens, emportant avec ardeur Patroklos hors des"});
db.iliadfr.insert({ pg : 11438, line : "traits, le déposèrent sur un lit. Et ses chers compagnons"});
db.iliadfr.insert({ pg : 11439, line : "pleuraient autour, et, avec eux, marchait Akhilleus aux pieds"});
db.iliadfr.insert({ pg : 11440, line : "rapides. Et il versait de chaudes larmes, voyant son cher"});
db.iliadfr.insert({ pg : 11441, line : "compagnon couché dans le cercueil, percé par l'airain aigu, lui"});
db.iliadfr.insert({ pg : 11442, line : "qu'il avait envoyé au combat avec ses chevaux et son char, et"});
db.iliadfr.insert({ pg : 11443, line : "qu'il ne devait point revoir vivant."});
db.iliadfr.insert({ pg : 11445, line : "Et la vénérable Hèrè aux yeux de boeuf commanda à l'infatigable"});
db.iliadfr.insert({ pg : 11446, line : "Hélios de retourner aux sources d'Okéanos, et Hélios disparut à"});
db.iliadfr.insert({ pg : 11447, line : "regret; et les divins Akhaiens mirent fin à la mêlée violente et à"});
db.iliadfr.insert({ pg : 11448, line : "la guerre lamentable. Et les Troiens, abandonnant aussi le rude"});
db.iliadfr.insert({ pg : 11449, line : "combat, délièrent les chevaux rapides, et s'assemblèrent pour"});
db.iliadfr.insert({ pg : 11450, line : "l'agora, avant le repas. Et l'agora les vit debout, aucun ne"});
db.iliadfr.insert({ pg : 11451, line : "voulant s'asseoir, car la terreur les tenait depuis qu'Akhilleus"});
db.iliadfr.insert({ pg : 11452, line : "avait reparu, lui qui, depuis longtemps, ne se mêlait plus au"});
db.iliadfr.insert({ pg : 11453, line : "combat. Et le sage Polydamas Panthoide commença de parler. Et seul"});
db.iliadfr.insert({ pg : 11454, line : "il voyait le passé et l'avenir. Et c'était le compagnon de Hektôr,"});
db.iliadfr.insert({ pg : 11455, line : "étant né la même nuit; mais il le surpassait en sagesse, autant"});
db.iliadfr.insert({ pg : 11456, line : "que Hektôr l'emportait en courage. Plein de prudence, il leur dit"});
db.iliadfr.insert({ pg : 11457, line : "dans l'agora:"});
db.iliadfr.insert({ pg : 11459, line : "-- Amis, délibérez mûrement. Je conseille de marcher vers la"});
db.iliadfr.insert({ pg : 11460, line : "ville, et de ne point attendre la divine Éôs auprès des nefs, car"});
db.iliadfr.insert({ pg : 11461, line : "nous sommes loin des murs. Aussi longtemps que cet homme a été"});
db.iliadfr.insert({ pg : 11462, line : "irrité contre le divin Agamemnôn, il était plus aisé de dompter"});
db.iliadfr.insert({ pg : 11463, line : "les Akhaiens. Et je me réjouissais de coucher auprès des nefs"});
db.iliadfr.insert({ pg : 11464, line : "rapides, espérant saisir les nefs aux deux rangs d'avirons; mais"});
db.iliadfr.insert({ pg : 11465, line : "je redoute maintenant le rapide Pèléiôn; car, dans son coeur"});
db.iliadfr.insert({ pg : 11466, line : "indomptable, il ne voudra point rester dans la plaine où les"});
db.iliadfr.insert({ pg : 11467, line : "Troyens et les Akhaiens déploient la force d'Arès, mais il"});
db.iliadfr.insert({ pg : 11468, line : "combattra pour s'emparer de notre ville et de nos femmes. Allons"});
db.iliadfr.insert({ pg : 11469, line : "vers Ilios; obéissez-moi et faites ainsi. Maintenant, la nuit"});
db.iliadfr.insert({ pg : 11470, line : "contraire retient le rapide Pèléiôn; mais s'il nous attaque demain"});
db.iliadfr.insert({ pg : 11471, line : "avec fureur, celui qui le sentira, alors fuira volontiers vers la"});
db.iliadfr.insert({ pg : 11472, line : "sainte Ilios, s'il lui échappe. Et les chiens et les oiseaux"});
db.iliadfr.insert({ pg : 11473, line : "carnassiers mangeront une foule de Troiens. Plaise aux dieux qu'on"});
db.iliadfr.insert({ pg : 11474, line : "ne me le dise jamais! Si vous obéissez à mes paroles, bien qu'à"});
db.iliadfr.insert({ pg : 11475, line : "regret, nous reprendrons des forces cette nuit; et ses tours, ses"});
db.iliadfr.insert({ pg : 11476, line : "hautes portes et leurs barrières longues et solides protégeront la"});
db.iliadfr.insert({ pg : 11477, line : "ville. Demain, armés dès le matin, nous serons debout sur nos"});
db.iliadfr.insert({ pg : 11478, line : "tours; et le travail lui sera lourds s'il vient de ses nefs"});
db.iliadfr.insert({ pg : 11479, line : "assiéger nos murailles. Et il s'en retournera vers les nefs, ayant"});
db.iliadfr.insert({ pg : 11480, line : "épuisé ses chevaux au grand cou à courir sous les murs de la"});
db.iliadfr.insert({ pg : 11481, line : "ville. Et il ne pourra point pénétrer dans Ilios et il ne la"});
db.iliadfr.insert({ pg : 11482, line : "détruira jamais, et, auparavant, les chiens rapides le mangeront."});
db.iliadfr.insert({ pg : 11484, line : "Et Hektôr au casque mouvant, avec un sombre regard, lui répondit:"});
db.iliadfr.insert({ pg : 11486, line : "-- Polydamas, il me déplaît que tu nous ordonnes de nous renfermer"});
db.iliadfr.insert({ pg : 11487, line : "encore dans la ville. N'êtes-vous donc point las d'être enfermés"});
db.iliadfr.insert({ pg : 11488, line : "dans nos tours? Autrefois, tous les hommes qui parlent des langues"});
db.iliadfr.insert({ pg : 11489, line : "diverses vantaient la ville de Priamos, abondante en or, riche en"});
db.iliadfr.insert({ pg : 11490, line : "airain. Aujourd'hui, les trésors qui étaient dans nos demeures"});
db.iliadfr.insert({ pg : 11491, line : "sont dissipés. Depuis que le grand Zeus est irrité, la plupart de"});
db.iliadfr.insert({ pg : 11492, line : "nos biens ont été transportés en Phrygiè et dans la belle Maioniè."});
db.iliadfr.insert({ pg : 11493, line : "Et maintenant que le fils du subtil Kronos m'a donné la victoire"});
db.iliadfr.insert({ pg : 11494, line : "auprès des nefs et m'a permis d'acculer les Akhaiens à la mer, ô"});
db.iliadfr.insert({ pg : 11495, line : "insensé, ne répands point de telles pensées dans le peuple. Aucun"});
db.iliadfr.insert({ pg : 11496, line : "des Troiens ne t'obéira, je ne le permettrai point. Allons! faites"});
db.iliadfr.insert({ pg : 11497, line : "ce que je vais dire. Prenez le repas dans les rangs. N'oubliez"});
db.iliadfr.insert({ pg : 11498, line : "point de veiller, chacun à son tour. Si quelque Troien craint pour"});
db.iliadfr.insert({ pg : 11499, line : "ses richesses, qu'il les donne au peuple afin que tous en"});
db.iliadfr.insert({ pg : 11500, line : "profitent, et cela vaudra mieux que d'en faire jouir les Akhaiens."});
db.iliadfr.insert({ pg : 11501, line : "Demain, dès le matin, nous recommencerons le rude combat auprès"});
db.iliadfr.insert({ pg : 11502, line : "des nefs creuses. Et, si le divin Akhilleus se lève auprès des"});
db.iliadfr.insert({ pg : 11503, line : "nefs, la rencontre lui sera rude; car je ne le fuira pas dans le"});
db.iliadfr.insert({ pg : 11504, line : "combat violent, mais je lui tiendrai courageusement tête. Ou il"});
db.iliadfr.insert({ pg : 11505, line : "remportera une grande gloire, ou je triompherai. Arès est commun à"});
db.iliadfr.insert({ pg : 11506, line : "tous, et, souvent, il tue celui qui voulait tuer."});
db.iliadfr.insert({ pg : 11508, line : "Hektôr parla ainsi, et les Troiens applaudirent, les insensés! car"});
db.iliadfr.insert({ pg : 11509, line : "Pallas Athènè leur avait ravi l'esprit. Et ils applaudirent les"});
db.iliadfr.insert({ pg : 11510, line : "paroles funestes de Hektôr, et ils n'écoutèrent point le sage"});
db.iliadfr.insert({ pg : 11511, line : "conseil de Polydamas, et ils prirent leur repas dans les rangs."});
db.iliadfr.insert({ pg : 11513, line : "Mais les Akhaiens, pendant toute la nuit, pleurèrent autour de"});
db.iliadfr.insert({ pg : 11514, line : "Patroklos. Et le Pèléide menait le deuil lamentable, posant ses"});
db.iliadfr.insert({ pg : 11515, line : "mains tueuses d'hommes sur la poitrine de son compagnon, et"});
db.iliadfr.insert({ pg : 11516, line : "gémissant, comme une lionne à longue barbe dont un chasseur a"});
db.iliadfr.insert({ pg : 11517, line : "enlevé les petits dans une épaisse forêt. Elle arrive trop tard,"});
db.iliadfr.insert({ pg : 11518, line : "et elle gémit, cherchant par toutes les vallées les traces de"});
db.iliadfr.insert({ pg : 11519, line : "l'homme; et une violente colère la saisit. Ainsi Akhilleus, avec"});
db.iliadfr.insert({ pg : 11520, line : "de profonds soupirs, dit aux Myrmidones:"});
db.iliadfr.insert({ pg : 11522, line : "-- Ô dieux! Certes, j'ai prononcé une parole vaine, le jour où,"});
db.iliadfr.insert({ pg : 11523, line : "consolant le héros Ménoitios dans ses demeures, je lui disais que"});
db.iliadfr.insert({ pg : 11524, line : "je ramènerais son fils illustre, après qu'il aurait renversé Ilios"});
db.iliadfr.insert({ pg : 11525, line : "et pris sa part des dépouilles. Mais Zeus n'accomplit pas tous les"});
db.iliadfr.insert({ pg : 11526, line : "désirs des hommes. Nous rougirons tous deux la terre devant Troiè,"});
db.iliadfr.insert({ pg : 11527, line : "et le vieux cavalier Pèleus ne me reverra plus dans ses demeures,"});
db.iliadfr.insert({ pg : 11528, line : "ni ma mère Thétis, car cette terre me gardera. Ô Patroklos,"});
db.iliadfr.insert({ pg : 11529, line : "puisque je subirai la tombe le dernier, je ne t'ensevelirai point"});
db.iliadfr.insert({ pg : 11530, line : "avant de t'avoir apporté les armes et la tête de Hektôr, ton"});
db.iliadfr.insert({ pg : 11531, line : "magnanime meurtrier. Et je tuerai devant ton bûcher douze"});
db.iliadfr.insert({ pg : 11532, line : "illustres fils des Troiens, car je suis irrité de ta mort. Et,"});
db.iliadfr.insert({ pg : 11533, line : "pendant ce temps, tu resteras couché sur mes nefs aux poupes"});
db.iliadfr.insert({ pg : 11534, line : "recourbées; et autour de toi, les Troiennes et les Dardaniennes au"});
db.iliadfr.insert({ pg : 11535, line : "large sein que nous avons conquises tous deux par notre force et"});
db.iliadfr.insert({ pg : 11536, line : "nos lances, après avoir renversé beaucoup de riches cités d'hommes"});
db.iliadfr.insert({ pg : 11537, line : "aux diverses langues, gémiront nuit et jour en versant des larmes."});
db.iliadfr.insert({ pg : 11539, line : "Le divin Akhilleus parla ainsi, et il ordonna à ses compagnons de"});
db.iliadfr.insert({ pg : 11540, line : "mettre un grand trépied sur le feu, afin de laver promptement les"});
db.iliadfr.insert({ pg : 11541, line : "souillures sanglantes de Patroklos. Et ils mirent sur le feu"});
db.iliadfr.insert({ pg : 11542, line : "ardent le trépied des ablutions, et ils y versèrent l'eau; et, au-"});
db.iliadfr.insert({ pg : 11543, line : "dessous, ils allumèrent le bois. Et la flamme enveloppa le ventre"});
db.iliadfr.insert({ pg : 11544, line : "du trépied, et l'eau chauffa. Et quand l'eau fut chaude dans le"});
db.iliadfr.insert({ pg : 11545, line : "trépied brillant, ils lavèrent Patroklos; et, l'ayant oint d'une"});
db.iliadfr.insert({ pg : 11546, line : "huile grasse, ils emplirent ses plaies d'un baume de neuf ans; et,"});
db.iliadfr.insert({ pg : 11547, line : "le déposant sur le lit, ils le couvrirent d'un lin léger, de la"});
db.iliadfr.insert({ pg : 11548, line : "tête aux pieds, et, par-dessus, d'un vêtement blanc. Ensuite,"});
db.iliadfr.insert({ pg : 11549, line : "pendant toute la nuit, les Myrmidones gémirent, pleurant"});
db.iliadfr.insert({ pg : 11550, line : "Patroklos. Mais Zeus dit à Hèrè sa soeur et son épouse:"});
db.iliadfr.insert({ pg : 11552, line : "-- Tu as enfin réussi, vénérable Hèrè aux yeux de boeuf! Voici"});
db.iliadfr.insert({ pg : 11553, line : "qu'Akhilleus aux pieds rapides s'est levé. Les Akhaiens chevelus"});
db.iliadfr.insert({ pg : 11554, line : "ne seraient-ils point nés de toi?"});
db.iliadfr.insert({ pg : 11556, line : "Et la vénérable Hèrè aux yeux de boeuf lui répondit:"});
db.iliadfr.insert({ pg : 11558, line : "-- Très dur Kronide, quelle parole as-tu dite? Un homme, bien que"});
db.iliadfr.insert({ pg : 11559, line : "mortel, et doué de peu d'intelligence, peut se venger d'un autre"});
db.iliadfr.insert({ pg : 11560, line : "homme; et moi, qui suis la plus puissante des déesses, et par ma"});
db.iliadfr.insert({ pg : 11561, line : "naissance, et parce que je suis ton épouse à toi qui règnes sur"});
db.iliadfr.insert({ pg : 11562, line : "les immortels, je ne pourrais méditer la perte des Troiens!"});
db.iliadfr.insert({ pg : 11564, line : "Et ils parlaient ainsi. Et Thétis aux pieds d'argent parvint à la"});
db.iliadfr.insert({ pg : 11565, line : "demeure de Hèphaistos, incorruptible, étoilée, admirable aux"});
db.iliadfr.insert({ pg : 11566, line : "immortels eux-mêmes; faite d'airain, et que le Boiteux avait"});
db.iliadfr.insert({ pg : 11567, line : "construite de ses mains."});
db.iliadfr.insert({ pg : 11569, line : "Et elle le trouva suant et se remuant autour des soufflets, et"});
db.iliadfr.insert({ pg : 11570, line : "haletant. Et il forgeait vingt trépieds pour être placés autour de"});
db.iliadfr.insert({ pg : 11571, line : "sa demeure solide. Et il les avait posés sur des roues d'or afin"});
db.iliadfr.insert({ pg : 11572, line : "qu'ils se rendissent d'eux-mêmes à l'assemblée divine, et qu'ils"});
db.iliadfr.insert({ pg : 11573, line : "en revinssent de même. Il ne leur manquait, pour être finis, que"});
db.iliadfr.insert({ pg : 11574, line : "des anses aux formes variées. Hèphaistos les préparait et en"});
db.iliadfr.insert({ pg : 11575, line : "forgeait les attaches. Et tandis qu'il travaillait à ces oeuvres"});
db.iliadfr.insert({ pg : 11576, line : "habiles, la déesse Thétis aux pieds d'argent s'approcha. Et Kharis"});
db.iliadfr.insert({ pg : 11577, line : "aux belles bandelettes, qu'avait épousée l'illustre Boiteux des"});
db.iliadfr.insert({ pg : 11578, line : "deux pieds, l'ayant vue, lui prit la main et lui dit:"});
db.iliadfr.insert({ pg : 11580, line : "-- Ô Thétis au large péplos, vénérable et chère, pourquoi viens-tu"});
db.iliadfr.insert({ pg : 11581, line : "dans notre demeure où nous te voyons si rarement? Mais suis-moi,"});
db.iliadfr.insert({ pg : 11582, line : "et je t'offrirai les mets hospitaliers."});
db.iliadfr.insert({ pg : 11584, line : "Ayant ainsi parlé, la très noble déesse la conduisit. Et, l'ayant"});
db.iliadfr.insert({ pg : 11585, line : "fait asseoir sur un trône aux clous d'argent, beau et"});
db.iliadfr.insert({ pg : 11586, line : "ingénieusement fait, elle plaça un escabeau sous ses pieds et"});
db.iliadfr.insert({ pg : 11587, line : "appela l'illustre ouvrier Hèphaistos:"});
db.iliadfr.insert({ pg : 11589, line : "-- Viens, Hèphaistos! Thétis a besoin de toi."});
db.iliadfr.insert({ pg : 11591, line : "Et l'illustre Boiteux des deux pieds lui répondit:"});
db.iliadfr.insert({ pg : 11593, line : "-- Certes, elle est toute puissante sur moi, la déesse vénérable"});
db.iliadfr.insert({ pg : 11594, line : "qui est entrée ici. C'est elle qui me sauva, quand je fus"});
db.iliadfr.insert({ pg : 11595, line : "précipité d'en haut par ma mère impitoyable qui voulait me cacher"});
db.iliadfr.insert({ pg : 11596, line : "aux dieux parce que j'étais boiteux. Que de douleurs j'eusse"});
db.iliadfr.insert({ pg : 11597, line : "endurées alors, si Thétis, et Eurynomè, la fille d'Okéanos au"});
db.iliadfr.insert({ pg : 11598, line : "reflux rapide, ne m'avaient reçu dans leur sein! Pour elles, dans"});
db.iliadfr.insert({ pg : 11599, line : "leur grotte profonde, pendant neuf ans, je forgeai mille"});
db.iliadfr.insert({ pg : 11600, line : "ornements, des agrafes, des noeuds, des colliers et des bracelets."});
db.iliadfr.insert({ pg : 11601, line : "Et l'immense fleuve Okéanos murmurait autour de la grotte. Et elle"});
db.iliadfr.insert({ pg : 11602, line : "n'était connue ni des dieux, ni des hommes, mais seulement de"});
db.iliadfr.insert({ pg : 11603, line : "Thétis et d'Eurynomè qui m'avaient sauvé. Et, maintenant, puisque"});
db.iliadfr.insert({ pg : 11604, line : "Thétis aux beaux cheveux vient dans ma demeure, je lui rendrai"});
db.iliadfr.insert({ pg : 11605, line : "grâce de m'avoir sauvé. Mais toi, offre-lui les mets hospitaliers,"});
db.iliadfr.insert({ pg : 11606, line : "tandis que je déposerai mes soufflets et tous mes instruments."});
db.iliadfr.insert({ pg : 11608, line : "Il parla ainsi. Et le corps monstrueux du dieu se redressa de"});
db.iliadfr.insert({ pg : 11609, line : "l'enclume; et il boitait, chancelant sur ses jambes grêles et"});
db.iliadfr.insert({ pg : 11610, line : "torses. Et il éloigna les soumets du feu, et il déposa dans un"});
db.iliadfr.insert({ pg : 11611, line : "coffre d'argent tous ses instruments familiers. Puis, une éponge"});
db.iliadfr.insert({ pg : 11612, line : "essuya sa face, ses deux mains, son cou robuste et sa poitrine"});
db.iliadfr.insert({ pg : 11613, line : "velue. Il mit une tunique, prit un sceptre énorme et sortit de la"});
db.iliadfr.insert({ pg : 11614, line : "forge en boitant. Et deux servantes soutenaient les pas du roi."});
db.iliadfr.insert({ pg : 11615, line : "Elles étaient d'or, semblables aux vierges vivantes qui pensent et"});
db.iliadfr.insert({ pg : 11616, line : "parlent, et que les dieux ont instruites. Soutenu par elles et"});
db.iliadfr.insert({ pg : 11617, line : "marchant à pas lourds, il vint s'asseoir auprès de Thétis, sur un"});
db.iliadfr.insert({ pg : 11618, line : "trône brillant. Et il prit les mains de la déesse et lui dit:"});
db.iliadfr.insert({ pg : 11620, line : "-- Thétis au long péplos, vénérable et chère, pourquoi es-tu venue"});
db.iliadfr.insert({ pg : 11621, line : "dans ma demeure où nous te voyons si rarement? Parle. Mon coeur"});
db.iliadfr.insert({ pg : 11622, line : "m'ordonne d'accomplir ton désir, si je le puis, et si c'est"});
db.iliadfr.insert({ pg : 11623, line : "possible."});
db.iliadfr.insert({ pg : 11625, line : "Et Thétis, versant des larmes, lui répondit:"});
db.iliadfr.insert({ pg : 11627, line : "-- Hèphaistos! parmi toutes les déesses qui sont dans l'Olympos,"});
db.iliadfr.insert({ pg : 11628, line : "en est-il une qui ait subi des maux aussi cruels que ceux dont"});
db.iliadfr.insert({ pg : 11629, line : "m'accable le Kronide Zeus? Seule, entre les déesses de la mer, il"});
db.iliadfr.insert({ pg : 11630, line : "m'a soumise à un homme, à l'Aiakide Pèleus; et j'ai subi à regret"});
db.iliadfr.insert({ pg : 11631, line : "la couche d'un homme! Et, maintenant, accablé par la triste"});
db.iliadfr.insert({ pg : 11632, line : "vieillesse, il gît dans sa demeure. Mais voici que j'ai d'autres"});
db.iliadfr.insert({ pg : 11633, line : "douleurs. Un fils est né de moi, le plus illustre des héros, et il"});
db.iliadfr.insert({ pg : 11634, line : "a grandi comme un arbre, et je l'ai nourri comme une plante dans"});
db.iliadfr.insert({ pg : 11635, line : "une terre fertile. Et je l'ai envoyé vers Ilios sur ses nefs aux"});
db.iliadfr.insert({ pg : 11636, line : "poupes recourbées, pour combattre les Troiens, et je ne le verrai"});
db.iliadfr.insert({ pg : 11637, line : "plus revenir dans ma demeure, dans la maison Pèléienne. Pendant"});
db.iliadfr.insert({ pg : 11638, line : "qu'il est vivant et qu'il voit la lumière de Hélios, il est"});
db.iliadfr.insert({ pg : 11639, line : "triste, et je ne puis le secourir. Les fils des Akhaiens lui"});
db.iliadfr.insert({ pg : 11640, line : "avaient donné pour récompense une vierge que le roi Agamemnôn lui"});
db.iliadfr.insert({ pg : 11641, line : "a enleva des mains, et il en gémissait dans son coeur. Mais voici"});
db.iliadfr.insert({ pg : 11642, line : "que les Troiens ont repoussé les Akhaiens jusqu'aux nefs et les y"});
db.iliadfr.insert({ pg : 11643, line : "ont renfermés. Les princes des Argiens ont supplié mon fils et lui"});
db.iliadfr.insert({ pg : 11644, line : "ont offert de nombreux et illustres présents. Il a refusé de"});
db.iliadfr.insert({ pg : 11645, line : "détourner lui-même leur ruine, mais il a envoyé Patroklos au"});
db.iliadfr.insert({ pg : 11646, line : "combat, couvert de ses armes et avec tout son peuple. Et, ce jour-"});
db.iliadfr.insert({ pg : 11647, line : "là, sans doute, ils eussent renversé la ville, si Apollôn n'eût"});
db.iliadfr.insert({ pg : 11648, line : "tué aux premiers rangs le brave fils de Ménoitios qui accablait"});
db.iliadfr.insert({ pg : 11649, line : "les Troiens, et n'eût donné la victoire à Hektôr. Et, maintenant,"});
db.iliadfr.insert({ pg : 11650, line : "j'embrasse tes genoux! Donne à mon fils, qui doit bientôt mourir,"});
db.iliadfr.insert({ pg : 11651, line : "un bouclier, un casque, de belles knèmides avec leurs agrafes et"});
db.iliadfr.insert({ pg : 11652, line : "une cuirasse, car son cher compagnon, tué par les Troiens, a perdu"});
db.iliadfr.insert({ pg : 11653, line : "ses armes, et il gémit, couché sur la terre!"});
db.iliadfr.insert({ pg : 11655, line : "Et l'illustre Boiteux des deux pieds lui répondit:"});
db.iliadfr.insert({ pg : 11657, line : "-- Rassure-toi, et n'aie plus d'inquiétudes dans ton esprit. Plût"});
db.iliadfr.insert({ pg : 11658, line : "aux dieux que je pusse le sauver de la mort lamentable quand le"});
db.iliadfr.insert({ pg : 11659, line : "lourd destin le saisira, aussi aisément que je vais lui donner de"});
db.iliadfr.insert({ pg : 11660, line : "belles armes qui empliront d'admiration la multitude des hommes."});
db.iliadfr.insert({ pg : 11662, line : "Ayant ainsi parlé, il la quitta, et, retournant à ses soufflets,"});
db.iliadfr.insert({ pg : 11663, line : "il les approcha du feu et leur ordonna de travailler. Et ils"});
db.iliadfr.insert({ pg : 11664, line : "répandirent leur souffle dans vingt fourneaux, tantôt violemment,"});
db.iliadfr.insert({ pg : 11665, line : "tantôt plus lentement, selon la volonté de Hèphaistos, pour"});
db.iliadfr.insert({ pg : 11666, line : "l'accomplissement de son oeuvre. Et il jeta dans le feu le dur"});
db.iliadfr.insert({ pg : 11667, line : "airain et l'étain, et l'or précieux et l'argent. Il posa sur un"});
db.iliadfr.insert({ pg : 11668, line : "tronc une vaste enclume, et il saisit d'une main le lourd marteau"});
db.iliadfr.insert({ pg : 11669, line : "et de l'autre la tenaille. Et il fit d'abord un bouclier grand et"});
db.iliadfr.insert({ pg : 11670, line : "solide, aux ornements variés, avec un contour triple et"});
db.iliadfr.insert({ pg : 11671, line : "resplendissant et une attache d'argent. Et il mit cinq bandes au"});
db.iliadfr.insert({ pg : 11672, line : "bouclier, et il y traça, dans son intelligence, une multitude"});
db.iliadfr.insert({ pg : 11673, line : "d'images. Il y représenta la terre et l'Ouranos, et la mer, et"});
db.iliadfr.insert({ pg : 11674, line : "l'infatigable Hélios, et l'orbe enflé de Sélènè, et tous les"});
db.iliadfr.insert({ pg : 11675, line : "astres dont l'Ouranos est couronné: les Plèiades, les Hyades, la"});
db.iliadfr.insert({ pg : 11676, line : "force d'Oriôn, et l'Ourse, qu'on nomme aussi le Chariot qui se"});
db.iliadfr.insert({ pg : 11677, line : "tourne sans cesse vers Oriôn, et qui, seule, ne tombe point dans"});
db.iliadfr.insert({ pg : 11678, line : "les eaux de l'Okéanos."});
db.iliadfr.insert({ pg : 11680, line : "Et il fit deux belles cités des hommes. Dans l'une on voyait des"});
db.iliadfr.insert({ pg : 11681, line : "noces et des festins solennels. Et les épouses, hors des chambres"});
db.iliadfr.insert({ pg : 11682, line : "nuptiales, étaient conduites par la ville, et de toutes parts"});
db.iliadfr.insert({ pg : 11683, line : "montait le chant d'hyménée, et les jeunes hommes dansaient en"});
db.iliadfr.insert({ pg : 11684, line : "rond, et les flûtes et les kithares résonnaient, et les femmes,"});
db.iliadfr.insert({ pg : 11685, line : "debout sous les portiques, admiraient ces choses."});
db.iliadfr.insert({ pg : 11687, line : "Et les peuples étaient assemblés dans l'agora, une querelle"});
db.iliadfr.insert({ pg : 11688, line : "s'étant élevée. Deux hommes se disputaient pour l'amende d'un"});
db.iliadfr.insert({ pg : 11689, line : "meurtre. L'un affirmait au peuple qu'il avait payé cette amende,"});
db.iliadfr.insert({ pg : 11690, line : "et l'autre niait l'avoir reçue. Et tous deux voulaient qu'un"});
db.iliadfr.insert({ pg : 11691, line : "arbitre finît leur querelle, et les citoyens les applaudissaient"});
db.iliadfr.insert({ pg : 11692, line : "l'un et l'autre. Les hérauts apaisaient le peuple, et les"});
db.iliadfr.insert({ pg : 11693, line : "vieillards étaient assis sur des pierres polies, en un cercle"});
db.iliadfr.insert({ pg : 11694, line : "sacré. Les hérauts portaient des sceptres en main; et les"});
db.iliadfr.insert({ pg : 11695, line : "plaideurs, prenant le sceptre, se défendaient tour à tour. Deux"});
db.iliadfr.insert({ pg : 11696, line : "talents d'or étaient déposés au milieu du cercle pour celui qui"});
db.iliadfr.insert({ pg : 11697, line : "parlerait selon la justice."});
db.iliadfr.insert({ pg : 11699, line : "Puis, deux armées, éclatantes d'airain, entouraient l'autre cité."});
db.iliadfr.insert({ pg : 11700, line : "Et les ennemis offraient aux citoyens, ou de détruire la ville, ou"});
db.iliadfr.insert({ pg : 11701, line : "de la partager, elle et tout ce qu'elle renfermait. Et ceux-ci n'y"});
db.iliadfr.insert({ pg : 11702, line : "consentaient pas, et ils s'armaient secrètement pour une"});
db.iliadfr.insert({ pg : 11703, line : "embuscade; et, sur les murailles veillaient les femmes, les"});
db.iliadfr.insert({ pg : 11704, line : "enfants et les vieillards. Mais les hommes marchaient, conduits"});
db.iliadfr.insert({ pg : 11705, line : "par Arès et par Athènè, tous deux en or, vêtus d'or, beaux et"});
db.iliadfr.insert({ pg : 11706, line : "grands sous leurs armes, comme il était convenable pour des dieux;"});
db.iliadfr.insert({ pg : 11707, line : "car les hommes étaient plus petits. Et, parvenus au lieu commode"});
db.iliadfr.insert({ pg : 11708, line : "pour l'embuscade, sur les bords du fleuve où boivent les"});
db.iliadfr.insert({ pg : 11709, line : "troupeaux, ils s'y cachaient, couverts de l'airain brillant."});
db.iliadfr.insert({ pg : 11711, line : "Deux sentinelles, placées plus loin, guettaient les brebis et les"});
db.iliadfr.insert({ pg : 11712, line : "boeufs aux cornes recourbées. Et les animaux s'avançaient suivis"});
db.iliadfr.insert({ pg : 11713, line : "de deux bergers qui se charmaient en jouant de la flûte, sans se"});
db.iliadfr.insert({ pg : 11714, line : "douter de l'embûche."});
db.iliadfr.insert({ pg : 11716, line : "Et les hommes cachés accouraient; et ils tuaient les boeufs et les"});
db.iliadfr.insert({ pg : 11717, line : "beaux troupeaux de blanches brebis, et les bergers eux-mêmes."});
db.iliadfr.insert({ pg : 11718, line : "Puis, ceux qui veillaient devant les tentes, entendant ce tumulte"});
db.iliadfr.insert({ pg : 11719, line : "parmi les boeufs, et montant sur leurs chars rapides, arrivaient"});
db.iliadfr.insert({ pg : 11720, line : "aussitôt et combattaient sur les bords du fleuve. Et ils se"});
db.iliadfr.insert({ pg : 11721, line : "frappaient avec les lances d'airain, parmi la discorde et le"});
db.iliadfr.insert({ pg : 11722, line : "tumulte et la kèr fatale. Et celle-ci blessait un guerrier, ou"});
db.iliadfr.insert({ pg : 11723, line : "saisissait cet autre sans blessure, ou traînait celui-là par les"});
db.iliadfr.insert({ pg : 11724, line : "pieds, à travers le carnage, et ses vêtements dégouttaient de"});
db.iliadfr.insert({ pg : 11725, line : "sang. Et tous semblaient des hommes vivants qui combattaient et"});
db.iliadfr.insert({ pg : 11726, line : "qui entraînaient de part et d'autre les cadavres."});
db.iliadfr.insert({ pg : 11728, line : "Puis, Hèphaistos représenta une terre grasse et molle et trois"});
db.iliadfr.insert({ pg : 11729, line : "fois labourée. Et les laboureurs menaient dans ce champ les"});
db.iliadfr.insert({ pg : 11730, line : "attelages qui retournaient la terre. Parvenus au bout, un homme"});
db.iliadfr.insert({ pg : 11731, line : "leur offrait à chacun une coupe de vin doux; et ils revenaient,"});
db.iliadfr.insert({ pg : 11732, line : "désirant achever les nouveaux sillons qu'ils creusaient. Et la"});
db.iliadfr.insert({ pg : 11733, line : "terre était d'or, et semblait noire derrière eux, et comme déjà"});
db.iliadfr.insert({ pg : 11734, line : "labourée. Tel était ce miracle de Hèphaistos."});
db.iliadfr.insert({ pg : 11736, line : "Puis, il représenta un champ de hauts épis que des moissonneurs"});
db.iliadfr.insert({ pg : 11737, line : "coupaient avec des faux tranchantes. Les épis tombaient, épais,"});
db.iliadfr.insert({ pg : 11738, line : "sur les bords du sillon, et d'autres étaient liés en gerbes. Trois"});
db.iliadfr.insert({ pg : 11739, line : "hommes liaient les gerbes, et, derrière eux, des enfants prenaient"});
db.iliadfr.insert({ pg : 11740, line : "dans leurs bras les épis et les leur offraient sans cesse. Le roi,"});
db.iliadfr.insert({ pg : 11741, line : "en silence, le sceptre en main et le coeur joyeux, était debout"});
db.iliadfr.insert({ pg : 11742, line : "auprès des sillons. Des hérauts, plus loin, sous un chêne,"});
db.iliadfr.insert({ pg : 11743, line : "préparaient, pour le repas, un grand boeuf qu'ils avaient tué, et"});
db.iliadfr.insert({ pg : 11744, line : "les femmes saupoudraient les viandes avec de la farine blanche,"});
db.iliadfr.insert({ pg : 11745, line : "pour le repas des moissonneurs."});
db.iliadfr.insert({ pg : 11747, line : "Puis, Hèphaistos représenta une belle vigne d'or chargée de"});
db.iliadfr.insert({ pg : 11748, line : "raisins, avec des rameaux d'or sombre et des pieds d'argent."});
db.iliadfr.insert({ pg : 11749, line : "Autour d'elle un fossé bleu, et, au-dessus, une haie d'étain. Et"});
db.iliadfr.insert({ pg : 11750, line : "la vigne n'avait qu'un sentier où marchaient les vendangeurs. Les"});
db.iliadfr.insert({ pg : 11751, line : "jeunes filles et les jeunes hommes qui aiment la gaîté portaient"});
db.iliadfr.insert({ pg : 11752, line : "le doux fruit dans des paniers d'osier. Un enfant, au milieu"});
db.iliadfr.insert({ pg : 11753, line : "d'eux, jouait harmonieusement d'une kithare sonore, et sa voix"});
db.iliadfr.insert({ pg : 11754, line : "fraîche s'unissait aux sons des cordes. Et ils le suivaient"});
db.iliadfr.insert({ pg : 11755, line : "chantant, dansant avec ardeur, et frappant tous ensemble la terre."});
db.iliadfr.insert({ pg : 11757, line : "Puis, Hèphaistos représenta un troupeau de boeufs aux grandes"});
db.iliadfr.insert({ pg : 11758, line : "cornes. Et ils étaient faits d'or et d'étain, et, hors de"});
db.iliadfr.insert({ pg : 11759, line : "l'étable, en mugissant, ils allaient au pâturage, le long du"});
db.iliadfr.insert({ pg : 11760, line : "fleuve sonore qui abondait en roseaux. Et quatre bergers d'or"});
db.iliadfr.insert({ pg : 11761, line : "conduisaient les boeufs, et neuf chiens rapides les suivaient. Et"});
db.iliadfr.insert({ pg : 11762, line : "voici que deux lions horribles saisissaient, en tête des vaches,"});
db.iliadfr.insert({ pg : 11763, line : "un taureau beuglant; et il était entraîné, poussant de longs"});
db.iliadfr.insert({ pg : 11764, line : "mugissements. Les chiens et les bergers les poursuivaient; mais"});
db.iliadfr.insert({ pg : 11765, line : "les lions déchiraient la peau du grand boeuf, et buvaient ses"});
db.iliadfr.insert({ pg : 11766, line : "entrailles et son sang noir. Et les bergers excitaient en vain les"});
db.iliadfr.insert({ pg : 11767, line : "chiens rapides qui refusaient de mordre les lions, et n'aboyaient"});
db.iliadfr.insert({ pg : 11768, line : "de près que pour fuir aussitôt."});
db.iliadfr.insert({ pg : 11770, line : "Puis, l'illustre Boiteux des deux pieds représenta un grand pacage"});
db.iliadfr.insert({ pg : 11771, line : "de brebis blanches, dans une grande vallée; et des étables, des"});
db.iliadfr.insert({ pg : 11772, line : "enclos et des bergeries couvertes."});
db.iliadfr.insert({ pg : 11774, line : "Puis, l'illustre Boiteux des deux pieds représenta un choeur de"});
db.iliadfr.insert({ pg : 11775, line : "danses, semblable à celui que, dans la grande Knôssos, Daidalos"});
db.iliadfr.insert({ pg : 11776, line : "fit autrefois pour Ariadnè aux beaux cheveux; et les adolescents"});
db.iliadfr.insert({ pg : 11777, line : "et les belles vierges dansaient avec ardeur en se tenant par la"});
db.iliadfr.insert({ pg : 11778, line : "main. Et celles-ci portaient des robes légères, et ceux-là des"});
db.iliadfr.insert({ pg : 11779, line : "tuniques finement tissées qui brillaient comme de l'huile. Elles"});
db.iliadfr.insert({ pg : 11780, line : "portaient de belles couronnes, et ils avaient des épées d'or"});
db.iliadfr.insert({ pg : 11781, line : "suspendues à des baudriers d'argent. Et, habilement, ils dansaient"});
db.iliadfr.insert({ pg : 11782, line : "en rond avec rapidité, comme la roue que le potier, assis au"});
db.iliadfr.insert({ pg : 11783, line : "travail, sent courir sous sa main. Et ils tournaient ainsi en"});
db.iliadfr.insert({ pg : 11784, line : "s'enlaçant par dessins variés; et la foule charmée se pressait"});
db.iliadfr.insert({ pg : 11785, line : "autour. Et deux sauteurs qui chantaient, bondissaient eux-mêmes au"});
db.iliadfr.insert({ pg : 11786, line : "milieu du choeur."});
db.iliadfr.insert({ pg : 11788, line : "Puis, Hèphaistos, tout autour du bouclier admirablement travaillé,"});
db.iliadfr.insert({ pg : 11789, line : "représenta la grande force du fleuve Okéanos."});
db.iliadfr.insert({ pg : 11791, line : "Et, après le bouclier grand et solide, il fit la cuirasse plus"});
db.iliadfr.insert({ pg : 11792, line : "éclatante que la splendeur du feu. Et il fit le casque épais,"});
db.iliadfr.insert({ pg : 11793, line : "beau, orné, et adapté aux tempes du Pèléide, et il le surmonta"});
db.iliadfr.insert({ pg : 11794, line : "d'une aigrette d'or. Puis il fit les knèmides d'étain flexible."});
db.iliadfr.insert({ pg : 11796, line : "Et, quand l'illustre Boiteux des deux pieds eut achevé ces armes,"});
db.iliadfr.insert({ pg : 11797, line : "il les déposa devant la mère d'Akhilleus, et celle-ci, comme"});
db.iliadfr.insert({ pg : 11798, line : "l'épervier, sauta du faîte de l'Olympos neigeux, emportant les"});
db.iliadfr.insert({ pg : 11799, line : "armes resplendissantes que Hèphaistos avait faites."});
db.iliadfr.insert({ pg : 11802, line : "Chant 19"});
db.iliadfr.insert({ pg : 11804, line : "Éôs au péplos couleur de safran sortait des flots d'Okéanos pour"});
db.iliadfr.insert({ pg : 11805, line : "porter la lumière aux immortels et aux hommes. Et Thétis parvint"});
db.iliadfr.insert({ pg : 11806, line : "aux nefs avec les présents du dieu. Et elle trouva son fils bien-"});
db.iliadfr.insert({ pg : 11807, line : "aimé entourant de ses bras Patroklos et pleurant amèrement. Et,"});
db.iliadfr.insert({ pg : 11808, line : "autour de lui, ses compagnons gémissaient. Mais la déesse parut au"});
db.iliadfr.insert({ pg : 11809, line : "milieu d'eux, prit la main d'Akhilleus et lui dit:"});
db.iliadfr.insert({ pg : 11811, line : "-- Mon enfant, malgré notre douleur, laissons-le, puisqu'il est"});
db.iliadfr.insert({ pg : 11812, line : "mort par la volonté des dieux. Reçois de Hèphaistos ces armes"});
db.iliadfr.insert({ pg : 11813, line : "illustres et belles, telles que jamais aucun homme n'en a porté"});
db.iliadfr.insert({ pg : 11814, line : "sur ses épaules."});
db.iliadfr.insert({ pg : 11816, line : "Ayant ainsi parlé, la déesse les déposa devant Akhilleus, et les"});
db.iliadfr.insert({ pg : 11817, line : "armes merveilleuses résonnèrent. La terreur saisit les Myrmidones,"});
db.iliadfr.insert({ pg : 11818, line : "et nul d'entre eux ne put en soutenir l'éclat, et ils tremblèrent;"});
db.iliadfr.insert({ pg : 11819, line : "mais Akhilleus, dès qu'il les vit, se sentit plus furieux, et,"});
db.iliadfr.insert({ pg : 11820, line : "sous ses paupières, ses yeux brûlaient, terribles, et tels que la"});
db.iliadfr.insert({ pg : 11821, line : "flamme. Il se réjouissait de tenir dans ses mains les présents"});
db.iliadfr.insert({ pg : 11822, line : "splendides du dieu; et, après avoir admiré, plein de joie, ce"});
db.iliadfr.insert({ pg : 11823, line : "travail merveilleux, aussitôt il dit à sa mère ces paroles ailées:"});
db.iliadfr.insert({ pg : 11825, line : "-- Ma mère, certes, un dieu t'a donné ces armes qui ne peuvent"});
db.iliadfr.insert({ pg : 11826, line : "être que l'oeuvre des immortels, et qu'un homme ne pourrait faire."});
db.iliadfr.insert({ pg : 11827, line : "Je vais m'armer à l'instant. Mais je crains que les mouches"});
db.iliadfr.insert({ pg : 11828, line : "pénètrent dans les blessures du brave fils de Ménoitios, y"});
db.iliadfr.insert({ pg : 11829, line : "engendrent des vers, et, souillant ce corps où la vie est éteinte,"});
db.iliadfr.insert({ pg : 11830, line : "corrompent tout le cadavre."});
db.iliadfr.insert({ pg : 11832, line : "Et la déesse Thétis aux pieds d'argent lui répondit:"});
db.iliadfr.insert({ pg : 11834, line : "-- Mon enfant, que ces inquiétudes ne soient point dans ton"});
db.iliadfr.insert({ pg : 11835, line : "esprit. Loin de Patroklos j'écarterai moi-même les essaims impurs"});
db.iliadfr.insert({ pg : 11836, line : "des mouches qui mangent les guerriers tués dans le combat. Ce"});
db.iliadfr.insert({ pg : 11837, line : "cadavre resterait couché ici toute une année, qu'il serait encore"});
db.iliadfr.insert({ pg : 11838, line : "sain, et plus frais même. Mais toi, appelle les héros Akhaiens à"});
db.iliadfr.insert({ pg : 11839, line : "l'agora, et, renonçant à ta colère contre le prince des peuples"});
db.iliadfr.insert({ pg : 11840, line : "Agamemnôn, hâte-toi de t'armer et revêts-toi de ton courage."});
db.iliadfr.insert({ pg : 11842, line : "Ayant ainsi parlé, elle le remplit de vigueur et d'audace; et elle"});
db.iliadfr.insert({ pg : 11843, line : "versa dans les narines de Patroklos l'ambroisie et le nektar"});
db.iliadfr.insert({ pg : 11844, line : "rouge, afin que le corps fût incorruptible."});
db.iliadfr.insert({ pg : 11846, line : "Et le divin Akhilleus courait sur le rivage de la mer, poussant"});
db.iliadfr.insert({ pg : 11847, line : "des cris horribles, et excitant les héros Akhaiens. Et ceux qui,"});
db.iliadfr.insert({ pg : 11848, line : "auparavant, restaient dans les nefs, et les pilotes qui tenaient"});
db.iliadfr.insert({ pg : 11849, line : "les gouvernails, et ceux mêmes qui distribuaient les vivres auprès"});
db.iliadfr.insert({ pg : 11850, line : "des nefs, tous allaient à l' agora où Akhilleus reparaissait,"});
db.iliadfr.insert({ pg : 11851, line : "après s'être éloigné longtemps du combat. Et les deux serviteurs"});
db.iliadfr.insert({ pg : 11852, line : "d'Arès, le belliqueux Tydéide et le divin Odysseus, boitant et"});
db.iliadfr.insert({ pg : 11853, line : "appuyés sur leurs lances, car ils souffraient encore de leurs"});
db.iliadfr.insert({ pg : 11854, line : "blessures, vinrent s'asseoir aux premiers rangs. Et le roi des"});
db.iliadfr.insert({ pg : 11855, line : "hommes, Agamemnôn, vint le dernier, étant blessé aussi, Koôn"});
db.iliadfr.insert({ pg : 11856, line : "Anténoride l'ayant frappé de sa lance d'airain, dans la rude"});
db.iliadfr.insert({ pg : 11857, line : "mêlée. Et quand tous les Akhaiens furent assemblés, Akhilleus aux"});
db.iliadfr.insert({ pg : 11858, line : "pieds rapides, se levant au milieu d'eux, parla ainsi:"});
db.iliadfr.insert({ pg : 11860, line : "-- Atréide, n'eût-il pas mieux valu nous entendre, quand, pleins"});
db.iliadfr.insert({ pg : 11861, line : "de colère, nous avons consumé notre coeur pour cette jeune femme?"});
db.iliadfr.insert({ pg : 11862, line : "Plût aux dieux que la flèche d'Artémis l'eût tuée sur les nefs, le"});
db.iliadfr.insert({ pg : 11863, line : "jour où je la pris dans Lyrnessos bien peuplée! Tant d'Akhaiens"});
db.iliadfr.insert({ pg : 11864, line : "n'auraient pas mordu la vaste terre sous des mains ennemies, à"});
db.iliadfr.insert({ pg : 11865, line : "cause de ma colère. Ceci n'a servi qu'à Hektôr et aux Troiens; et"});
db.iliadfr.insert({ pg : 11866, line : "je pense que les Akhaiens se souviendront longtemps de notre"});
db.iliadfr.insert({ pg : 11867, line : "querelle. Mais oublions le passé, malgré notre douleur; et, dans"});
db.iliadfr.insert({ pg : 11868, line : "notre poitrine, soumettons notre âme à la nécessité. Aujourd'hui,"});
db.iliadfr.insert({ pg : 11869, line : "je dépose ma colère. Il ne convient pas que je sois toujours"});
db.iliadfr.insert({ pg : 11870, line : "irrité. Mais toi, appelle promptement au combat les Akhaiens"});
db.iliadfr.insert({ pg : 11871, line : "chevelus, afin que je marche aux Troiens et que je voie s'ils"});
db.iliadfr.insert({ pg : 11872, line : "veulent dormir auprès des nefs. Il courbera volontiers les genoux,"});
db.iliadfr.insert({ pg : 11873, line : "celui qui aura échappé à nos lances dans le combat."});
db.iliadfr.insert({ pg : 11875, line : "Il parla ainsi, et les Akhaiens aux belles knèmides se réjouirent"});
db.iliadfr.insert({ pg : 11876, line : "que le magnanime Pèléiôn renonçât à sa colère. Et le roi des"});
db.iliadfr.insert({ pg : 11877, line : "hommes, Agamemnôn, parla de son siège, ne se levant point au"});
db.iliadfr.insert({ pg : 11878, line : "milieu d'eux:"});
db.iliadfr.insert({ pg : 11880, line : "-- Ô chers héros Danaens, serviteurs d'Arès, il est juste"});
db.iliadfr.insert({ pg : 11881, line : "d'écouter celui qui parle, et il ne convient point de"});
db.iliadfr.insert({ pg : 11882, line : "l'interrompre, car cela est pénible, même pour le plus habile. Qui"});
db.iliadfr.insert({ pg : 11883, line : "pourrait écouter et entendre au milieu du tumulte des hommes? La"});
db.iliadfr.insert({ pg : 11884, line : "voix sonore du meilleur agorète est vaine. Je parlerai au Pèléide."});
db.iliadfr.insert({ pg : 11885, line : "Vous, Argiens, écoutez mes paroles, et que chacun connaisse ma"});
db.iliadfr.insert({ pg : 11886, line : "pensée. Souvent les Akhaiens m'ont accusé, mais je n'ai point"});
db.iliadfr.insert({ pg : 11887, line : "causé leurs maux. Zeus, la moire, Érinnyes qui errent dans les"});
db.iliadfr.insert({ pg : 11888, line : "ténèbres, ont jeté la fureur dans mon âme, au milieu de l'agora,"});
db.iliadfr.insert({ pg : 11889, line : "le jour où j'ai enlevé la récompense d'Akhilleus. Mais qu'aurais-"});
db.iliadfr.insert({ pg : 11890, line : "je fait? Une déesse accomplit tout, la vénérable fille de Zeus, la"});
db.iliadfr.insert({ pg : 11891, line : "fatale Atè qui égare les hommes. Ses pieds aériens ne touchent"});
db.iliadfr.insert({ pg : 11892, line : "point la terre, mais elle passe sur la tête des hommes qu'elle"});
db.iliadfr.insert({ pg : 11893, line : "blesse, et elle n'enchaîne pas qu'eux. Autrefois, en effet, elle a"});
db.iliadfr.insert({ pg : 11894, line : "égaré Zeus qui l'emporte sur les hommes et les dieux. Hèrè trompa"});
db.iliadfr.insert({ pg : 11895, line : "le Kronide par ses ruses, le jour où Alkménè allait enfanter la"});
db.iliadfr.insert({ pg : 11896, line : "force Hèracléenne, dans Thèbè aux fortes murailles. Et, plein de"});
db.iliadfr.insert({ pg : 11897, line : "joie, Zeus dit au milieu de tous les dieux: -- Écoutez-moi, dieux"});
db.iliadfr.insert({ pg : 11898, line : "et déesses, afin que je dise ce que mon esprit m'inspire."});
db.iliadfr.insert({ pg : 11899, line : "Aujourd'hui, Eileithya, qui préside aux douloureux enfantements,"});
db.iliadfr.insert({ pg : 11900, line : "appellera à la lumière un homme de ceux qui sont de ma race et de"});
db.iliadfr.insert({ pg : 11901, line : "mon sang, et qui commandera sur tous ses voisins. Et la vénérable"});
db.iliadfr.insert({ pg : 11902, line : "Hèrè qui médite des ruses parla ainsi: -- Tu mens, et tu"});
db.iliadfr.insert({ pg : 11903, line : "n'accompliras point tes paroles. Allons, Olympien! jure, par un"});
db.iliadfr.insert({ pg : 11904, line : "inviolable serment, qu'il commandera sur tous ses voisins, l'homme"});
db.iliadfr.insert({ pg : 11905, line : "de ton sang et de ta race qui, aujourd'hui, tombera d'entre les"});
db.iliadfr.insert({ pg : 11906, line : "genoux d'une femme. Elle parla ainsi, et Zeus ne comprit point sa"});
db.iliadfr.insert({ pg : 11907, line : "ruse, et il jura un grand serment dont il devait souffrir dans la"});
db.iliadfr.insert({ pg : 11908, line : "suite. Et, quittant à la hâte le faîte de l'Olympos, Hèrè parvint"});
db.iliadfr.insert({ pg : 11909, line : "dans Argos Akhaienne où elle savait que l'illustre épouse de"});
db.iliadfr.insert({ pg : 11910, line : "Sthénélos Persèiade portait un fils dans son sein. Et elle le fit"});
db.iliadfr.insert({ pg : 11911, line : "naître avant le temps, à sept mois. Et elle retarda les douleurs"});
db.iliadfr.insert({ pg : 11912, line : "de l'enfantement et les couches d'Alkménè. Puis, l'annonçant au"});
db.iliadfr.insert({ pg : 11913, line : "Kroniôn Zeus, elle lui dit: -- Père Zeus qui tiens la foudre"});
db.iliadfr.insert({ pg : 11914, line : "éclatante, je t'annoncerai ceci: l'homme illustre est né qui"});
db.iliadfr.insert({ pg : 11915, line : "commandera sur les Argiens. C'est Eurystheus, fils de Sthénélos"});
db.iliadfr.insert({ pg : 11916, line : "Persèiade. Il est de ta race, et il n'est pas indigne de commander"});
db.iliadfr.insert({ pg : 11917, line : "sur les Argiens. Elle parla ainsi, et une douleur aiguë et"});
db.iliadfr.insert({ pg : 11918, line : "profonde blessa le coeur de Zeus. Et, saisissant Atè par ses"});
db.iliadfr.insert({ pg : 11919, line : "tresses brillantes, il jura, par un inviolable serment, qu'elle ne"});
db.iliadfr.insert({ pg : 11920, line : "reviendrait plus jamais dans l'Olympos et dans l'Ouranos étoilé,"});
db.iliadfr.insert({ pg : 11921, line : "Atè, qui égare tous les esprits. Il parla ainsi, et, la faisant"});
db.iliadfr.insert({ pg : 11922, line : "tournoyer, il la jeta, de l'Ouranos étoilé, au milieu des hommes."});
db.iliadfr.insert({ pg : 11923, line : "Et c'est par elle qu'il gémissait, quand il voyait son fils bien-"});
db.iliadfr.insert({ pg : 11924, line : "aimé accablé de travaux sous le joug violent d'Eurystheus. Et il"});
db.iliadfr.insert({ pg : 11925, line : "en est ainsi de moi. Quand le grand Hektôr au casque mouvant"});
db.iliadfr.insert({ pg : 11926, line : "accablait les Argiens auprès des poupes des nefs, je ne pouvais"});
db.iliadfr.insert({ pg : 11927, line : "oublier cette fureur qui m'avait égaré. Mais, puisque je t'ai"});
db.iliadfr.insert({ pg : 11928, line : "offensé et que Zeus m'a ravi l'esprit, je veux t'apaiser et te"});
db.iliadfr.insert({ pg : 11929, line : "faire des présents infinis. Va donc au combat et encourage les"});
db.iliadfr.insert({ pg : 11930, line : "troupes; et je préparerai les présents que le divin Odysseus, hier"});
db.iliadfr.insert({ pg : 11931, line : "sous tes tentes, t'a promis. Ou, si tu le désires, attends, malgré"});
db.iliadfr.insert({ pg : 11932, line : "ton ardeur à combattre. Des hérauts vont t'apporter ces présents,"});
db.iliadfr.insert({ pg : 11933, line : "de ma nef, et tu verras ce que je veux te donner pour t'apaiser."});
db.iliadfr.insert({ pg : 11935, line : "Et Akhilleus aux pieds rapides lui répondit:"});
db.iliadfr.insert({ pg : 11937, line : "-- Très llustre Atréide Agamemnôn, roi des hommes, si tu veux me"});
db.iliadfr.insert({ pg : 11938, line : "faire ces présents, comme cela est juste, ou les garder, tu le"});
db.iliadfr.insert({ pg : 11939, line : "peux. Ne songeons maintenant qu'à combattre. Il ne s'agit ni"});
db.iliadfr.insert({ pg : 11940, line : "d'éviter le combat, ni de perdre le temps, mais d'accomplir un"});
db.iliadfr.insert({ pg : 11941, line : "grand travail. Il faut qu'on revoie Akhilleus aux premiers rangs,"});
db.iliadfr.insert({ pg : 11942, line : "enfonçant de sa lance d'airain les phalanges troiennes, et que"});
db.iliadfr.insert({ pg : 11943, line : "chacun de vous se souvienne de combattre un ennemi."});
db.iliadfr.insert({ pg : 11945, line : "Et le sage Odysseus, lui répondant, parla ainsi:"});
db.iliadfr.insert({ pg : 11947, line : "-- Bien que tu sois brave, ô Akhilleus semblable à un dieu, ne"});
db.iliadfr.insert({ pg : 11948, line : "pousse point vers Ilios, contre les Troiens, les fils des Akhaiens"});
db.iliadfr.insert({ pg : 11949, line : "qui n'ont point mangé; car la mêlée sera longue, dès que les"});
db.iliadfr.insert({ pg : 11950, line : "phalanges des guerriers se seront heurtées, et qu'un dieu leur"});
db.iliadfr.insert({ pg : 11951, line : "aura inspiré à tous la vigueur. Ordonne que les Akhaiens se"});
db.iliadfr.insert({ pg : 11952, line : "nourrissent de pain et de vin dans les nefs rapides. Cela seul"});
db.iliadfr.insert({ pg : 11953, line : "donne la force et le courage. Un guerrier ne peut, sans manger,"});
db.iliadfr.insert({ pg : 11954, line : "combattre tout un jour, jusqu'à la chute de Hélios. Quelle que"});
db.iliadfr.insert({ pg : 11955, line : "soit son ardeur, ses membres sont lourds, la soif et la faim le"});
db.iliadfr.insert({ pg : 11956, line : "tourmentent, et ses genoux sont rompus. Mais celui qui a bu et"});
db.iliadfr.insert({ pg : 11957, line : "mangé combat tout un jour contre l'ennemi, plein de courage, et"});
db.iliadfr.insert({ pg : 11958, line : "ses membres ne sont las que lorsque tous se retirent de la mêlée."});
db.iliadfr.insert({ pg : 11959, line : "Renvoie l'armée et ordonne-lui de préparer le repas. Et le roi des"});
db.iliadfr.insert({ pg : 11960, line : "hommes, Agamemnôn, fera porter ses présents au milieu de l'agora,"});
db.iliadfr.insert({ pg : 11961, line : "afin que tous les Akhaiens les voient de leurs yeux; et tu te"});
db.iliadfr.insert({ pg : 11962, line : "réjouiras dans ton cour. Et Agamemnôn jurera, debout, au milieu"});
db.iliadfr.insert({ pg : 11963, line : "des Argiens, qu'il n'est jamais entré dans le lit de Breisèis, et"});
db.iliadfr.insert({ pg : 11964, line : "qu'il ne l'a point possédée, comme c'est la coutume, ô roi, des"});
db.iliadfr.insert({ pg : 11965, line : "hommes et des femmes. Et toi, Akhilleus, apaise ton coeur dans ta"});
db.iliadfr.insert({ pg : 11966, line : "poitrine. Ensuite, Agamemnôn t'offrira un festin sous sa tente,"});
db.iliadfr.insert({ pg : 11967, line : "afin que rien ne manque à ce qui t'est dû. Et toi. Atréide, sois"});
db.iliadfr.insert({ pg : 11968, line : "plus équitable désormais. Il est convenable qu'un roi apaise celui"});
db.iliadfr.insert({ pg : 11969, line : "qu'il a offensé le premier."});
db.iliadfr.insert({ pg : 11971, line : "Et le roi des hommes, Agamemnôn. lui répondit:"});
db.iliadfr.insert({ pg : 11973, line : "-- Laertiade, je me réjouis de ce que tu as dit. Tu n'as rien"});
db.iliadfr.insert({ pg : 11974, line : "oublié, et tu as tout expliqué convenablement. Certes, je veux"});
db.iliadfr.insert({ pg : 11975, line : "faire ce serment, car mon coeur me l'ordonne et je ne me"});
db.iliadfr.insert({ pg : 11976, line : "parjurerai point devant les dieux. Qu'Akhilleus attende, malgré"});
db.iliadfr.insert({ pg : 11977, line : "son désir de combattre, et que tous attendent réunis, jusqu'à ce"});
db.iliadfr.insert({ pg : 11978, line : "que les présents soient apportés de mes tentes et que nous ayons"});
db.iliadfr.insert({ pg : 11979, line : "consacré notre alliance. Et toi, Odysseus, je te le commande et te"});
db.iliadfr.insert({ pg : 11980, line : "l'ordonne, prends les plus illustres des jeunes fils des Akhaiens,"});
db.iliadfr.insert({ pg : 11981, line : "et qu'ils apportent de mes nefs tout ce que tu as promis hier au"});
db.iliadfr.insert({ pg : 11982, line : "Pèléide; et amène aussi les femmes. Et Talthybios préparera"});
db.iliadfr.insert({ pg : 11983, line : "promptement, dans le vaste camp des Akhaiens, le sanglier qui sera"});
db.iliadfr.insert({ pg : 11984, line : "tué, en offrande à Zeus et à Hélios."});
db.iliadfr.insert({ pg : 11986, line : "Et Akhilleus aux pieds rapides, lui répondant, parla ainsi:"});
db.iliadfr.insert({ pg : 11988, line : "--Atréide Agnmemnôn, très llustre roi des hommes, tu t'inquiéteras"});
db.iliadfr.insert({ pg : 11989, line : "de ceci quand la guerre aura pris fin et quand ma fureur sera"});
db.iliadfr.insert({ pg : 11990, line : "moins grande dans ma poitrine. Ils gisent encore sans sépulture"});
db.iliadfr.insert({ pg : 11991, line : "ceux qu'a tués le Priamide Hektôr, tandis que Zeus lui donnait la"});
db.iliadfr.insert({ pg : 11992, line : "victoire, et vous songez à manger! J'ordonnerai plutôt aux fils"});
db.iliadfr.insert({ pg : 11993, line : "des Akhaiens de combattre maintenant, sans avoir mangé, et de ne"});
db.iliadfr.insert({ pg : 11994, line : "préparer un grand repas qu'au coucher de Hélios, après avoir vengé"});
db.iliadfr.insert({ pg : 11995, line : "notre injure. Pour moi, rien n'entrera auparavant dans ma bouche,"});
db.iliadfr.insert({ pg : 11996, line : "ni pain, ni vin. Mon compagnon est mort; il est couché sous ma"});
db.iliadfr.insert({ pg : 11997, line : "tente, percé de l'airain aigu, les pieds du côté de l'entrée, et"});
db.iliadfr.insert({ pg : 11998, line : "mes autres compagnons pleurent autour de lui. Et je n'ai plus"});
db.iliadfr.insert({ pg : 11999, line : "d'autre désir dans le coeur que le carnage, le sang et le"});
db.iliadfr.insert({ pg : 12000, line : "gémissement des guerriers."});
db.iliadfr.insert({ pg : 12002, line : "Et le sage Odysseus, lui répondant, parla ainsi:"});
db.iliadfr.insert({ pg : 12004, line : "-- Ô Akhilleus Pèléide, le plus brave des Akhaiens, tu l'emportes"});
db.iliadfr.insert({ pg : 12005, line : "de beaucoup sur moi, et tu vaux beaucoup mieux que moi par ta"});
db.iliadfr.insert({ pg : 12006, line : "lance, mais ma sagesse est supérieure à la tienne, car je suis ton"});
db.iliadfr.insert({ pg : 12007, line : "aîné, et je sais plus de choses. C'est pourquoi, cède à mes"});
db.iliadfr.insert({ pg : 12008, line : "paroles. Le combat accable bientôt des hommes qui ont faim."});
db.iliadfr.insert({ pg : 12009, line : "L'airain couche d'abord sur la terre une moisson épaisse, mais"});
db.iliadfr.insert({ pg : 12010, line : "elle diminue quand Zeus, qui est le juge du combat des hommes,"});
db.iliadfr.insert({ pg : 12011, line : "incline ses balances. Ce n'est point par leur ventre vide que les"});
db.iliadfr.insert({ pg : 12012, line : "Akhaiens doivent pleurer les morts. Les nôtres tombent en grand"});
db.iliadfr.insert({ pg : 12013, line : "nombre tous les jours; quand donc pourrions-nous respirer? Il"});
db.iliadfr.insert({ pg : 12014, line : "faut, avec un esprit patient, ensevelir nos morts, et pleurer ce"});
db.iliadfr.insert({ pg : 12015, line : "jour-là; mais ceux que la guerre haïssable a épargnés, qu'ils"});
db.iliadfr.insert({ pg : 12016, line : "mangent et boivent, afin que, vêtus de l'airain indompté, ils"});
db.iliadfr.insert({ pg : 12017, line : "puissent mieux combatte l'ennemi, et sans relâche. Qu'aucun de"});
db.iliadfr.insert({ pg : 12018, line : "vous n'attende un meilleur conseil, car tout autre serait fatal à"});
db.iliadfr.insert({ pg : 12019, line : "qui resterait auprès des nefs des Argiens. Mais, bientôt, marchons"});
db.iliadfr.insert({ pg : 12020, line : "tous ensemble contre les Troiens dompteurs de chevaux, et"});
db.iliadfr.insert({ pg : 12021, line : "soulevons une rude mêlée."});
db.iliadfr.insert({ pg : 12023, line : "Il parla ainsi, et il choisit pour le suivre les fils de"});
db.iliadfr.insert({ pg : 12024, line : "l'illustre Nestôr, et Mégès Phyléide, et Thoas, et Mèrionès, et le"});
db.iliadfr.insert({ pg : 12025, line : "Kréiontiade Lykomèdès, et Mélanippos. Et ils arrivèrent aux tentes"});
db.iliadfr.insert({ pg : 12026, line : "de l'Atréide Agamemnôn, et aussitôt Odysseus parla, et le travail"});
db.iliadfr.insert({ pg : 12027, line : "s'acheva. Et ils emportèrent de la tente les sept trépieds qu'il"});
db.iliadfr.insert({ pg : 12028, line : "avait promis, et vingt splendides coupes. Et ils emmenèrent douze"});
db.iliadfr.insert({ pg : 12029, line : "chevaux et sept belles femmes habiles aux travaux, et la huitième"});
db.iliadfr.insert({ pg : 12030, line : "fut Breisèis aux belles joues. Et Odysseus marchait devant avec"});
db.iliadfr.insert({ pg : 12031, line : "dix talents d'or qu'il avait pesés; et les jeunes hommes d'Akhaiè"});
db.iliadfr.insert({ pg : 12032, line : "portaient ensemble les autres présents, et ils les déposèrent au"});
db.iliadfr.insert({ pg : 12033, line : "milieu de l'agora."});
db.iliadfr.insert({ pg : 12035, line : "Alors Agamemnôn se leva. Talthybios, semblable à un dieu par la"});
db.iliadfr.insert({ pg : 12036, line : "voix, debout auprès du prince des peuples, tenait un sanglier dans"});
db.iliadfr.insert({ pg : 12037, line : "ses mains. Et l'Atréide saisit le couteau toujours suspendu auprès"});
db.iliadfr.insert({ pg : 12038, line : "de la grande gaîne de son épée, et, coupant les soies du sanglier,"});
db.iliadfr.insert({ pg : 12039, line : "les mains levées vers Zeus, il les lui voua. Et les Argiens, assis"});
db.iliadfr.insert({ pg : 12040, line : "en silence, écoutaient le roi respectueusement. Et, suppliant, il"});
db.iliadfr.insert({ pg : 12041, line : "dit, regardant le large Ouranos:"});
db.iliadfr.insert({ pg : 12043, line : "-- Qu'ils le sachent tous, Zeus le plus haut et le très puissant,"});
db.iliadfr.insert({ pg : 12044, line : "et Gaia, et Hélios, et les Erinnyes qui, sous la terre, punissent"});
db.iliadfr.insert({ pg : 12045, line : "les hommes parjures:je n'ai jamais porté la main sur la vierge"});
db.iliadfr.insert({ pg : 12046, line : "Breisèis, ni partagé son lit, et je ne l'ai soumise à aucun"});
db.iliadfr.insert({ pg : 12047, line : "travail; mais elle est restée intacte dans mes tentes. Et si je ne"});
db.iliadfr.insert({ pg : 12048, line : "jure point la vérité, que les dieux m'envoient tous les maux dont"});
db.iliadfr.insert({ pg : 12049, line : "ils accablent celui qui les outrage en se parjurant."});
db.iliadfr.insert({ pg : 12051, line : "Il parla ainsi, et, de l'airain cruel, il coupa la gorge du"});
db.iliadfr.insert({ pg : 12052, line : "sanglier. Et Talthybios jeta, en tournant, la victime dans les"});
db.iliadfr.insert({ pg : 12053, line : "grands flots de la blanche mer, pour être mangée par les poissons."});
db.iliadfr.insert({ pg : 12054, line : "Et, se levant au milieu des belliqueux Argiens, Akhilleus dit:"});
db.iliadfr.insert({ pg : 12056, line : "-- Père Zeus! certes, tu causes de grands maux aux hommes."});
db.iliadfr.insert({ pg : 12057, line : "L'Atréide n'eût jamais excité la colère dans ma poitrine, et il ne"});
db.iliadfr.insert({ pg : 12058, line : "m'eût jamais enlevé cette jeune femme contre ma volonté dans un"});
db.iliadfr.insert({ pg : 12059, line : "mauvais dessein, si Zeus n'eût voulu donner la mort à une foule"});
db.iliadfr.insert({ pg : 12060, line : "d'Akhaiens. Maintenant, allez manger, afin que nous combattions."});
db.iliadfr.insert({ pg : 12062, line : "Il parla ainsi, et il rompit aussitôt l'agora, et tous se"});
db.iliadfr.insert({ pg : 12063, line : "dispersèrent, chacun vers sa nef. Et les magnanimes Myrmidones"});
db.iliadfr.insert({ pg : 12064, line : "emportèrent les présents vers la nef du divin Akhilleus, et ils"});
db.iliadfr.insert({ pg : 12065, line : "les déposèrent dans les tentes, faisant asseoir les femmes et"});
db.iliadfr.insert({ pg : 12066, line : "liant les chevaux auprès des chevaux."});
db.iliadfr.insert({ pg : 12068, line : "Et dès que Breisèis, semblable à Aphroditè d'or, eut vu Patroklos"});
db.iliadfr.insert({ pg : 12069, line : "percé de l'airain aigu, elle se lamenta en l'entourant de ses"});
db.iliadfr.insert({ pg : 12070, line : "bras, et elle déchira de ses mains sa poitrine, son cou délicat et"});
db.iliadfr.insert({ pg : 12071, line : "son beau visage. Et la jeune femme, semblable aux déesses, dit en"});
db.iliadfr.insert({ pg : 12072, line : "pleurant:"});
db.iliadfr.insert({ pg : 12074, line : "-- O Patroklos, si doux pour moi, malheureuse! Je t'ai laissé"});
db.iliadfr.insert({ pg : 12075, line : "vivant quand je quittai cette tente, et voici que je te retrouve"});
db.iliadfr.insert({ pg : 12076, line : "mort, prince des peuples! Pour moi le mal suit le mal. L'homme à"});
db.iliadfr.insert({ pg : 12077, line : "qui mon père et ma mère vénérable m'avaient donnée, je l'ai vu,"});
db.iliadfr.insert({ pg : 12078, line : "devant sa ville, percé de l'airain aigu. Et mes trois frères, que"});
db.iliadfr.insert({ pg : 12079, line : "ma mère avait enfantés, et que j'aimais, trouvèrent aussi leur"});
db.iliadfr.insert({ pg : 12080, line : "jour fatal. Et tu ne me permettais point de pleurer, quand le"});
db.iliadfr.insert({ pg : 12081, line : "rapide Akhilleus eut tué mon époux et renversé la ville du divin"});
db.iliadfr.insert({ pg : 12082, line : "Mynès, et tu me disais que tu ferais de moi la jeune épouse du"});
db.iliadfr.insert({ pg : 12083, line : "divin Akhilleus, et que tu me conduirais sur tes nefs dans la"});
db.iliadfr.insert({ pg : 12084, line : "Phthiè, pour y faire le festin nuptial au milieu des Myrmidones."});
db.iliadfr.insert({ pg : 12085, line : "Aussi toi qui étais si doux, je pleurerai toujours ta mort."});
db.iliadfr.insert({ pg : 12087, line : "Elle parla ainsi, en pleurant. Et les autres jeunes femmes"});
db.iliadfr.insert({ pg : 12088, line : "gémissaient, semblant pleurer sur Patroklos, et déplorant leurs"});
db.iliadfr.insert({ pg : 12089, line : "propres misères."});
db.iliadfr.insert({ pg : 12091, line : "Et les princes vénérables des Akhaiens, réunis autour d'Akhilleus,"});
db.iliadfr.insert({ pg : 12092, line : "le suppliaient de manger, mais il ne le voulait pas:"});
db.iliadfr.insert({ pg : 12094, line : "-- Je vous conjure, si mes chers compagnons veulent m'écouter, de"});
db.iliadfr.insert({ pg : 12095, line : "ne point m'ordonner de boire et de manger, car je suis en proie à"});
db.iliadfr.insert({ pg : 12096, line : "une amère douleur. Je puis attendre jusqu'au coucher de Hélios."});
db.iliadfr.insert({ pg : 12098, line : "Il parla ainsi et renvoya les autres rois, sauf les deux Atréides,"});
db.iliadfr.insert({ pg : 12099, line : "le divin Odysseus, Nestôr, Idoméneus et le vieux cavalier Phoinix,"});
db.iliadfr.insert({ pg : 12100, line : "qui restèrent pour charmer sa tristesse. Mais rien ne devait le"});
db.iliadfr.insert({ pg : 12101, line : "consoler, avant qu'il se fût jeté dans la mêlée sanglante. Et le"});
db.iliadfr.insert({ pg : 12102, line : "souvenir renouvelait ses gémissements, et il disait:"});
db.iliadfr.insert({ pg : 12104, line : "-- Certes, autrefois, ô malheureux, le plus cher de mes"});
db.iliadfr.insert({ pg : 12105, line : "compagnons, tu m'apprêtais toi-même, avec soin, un excellent"});
db.iliadfr.insert({ pg : 12106, line : "repas, quand les Akhaiens portaient la guerre lamentable aux"});
db.iliadfr.insert({ pg : 12107, line : "Troiens dompteurs de chevaux. Et, maintenant, tu gîs, percé par"});
db.iliadfr.insert({ pg : 12108, line : "l'airain, et mon coeur, plein du regret de ta mort, se refuse à"});
db.iliadfr.insert({ pg : 12109, line : "toute nourriture. Je ne pourrais subir une douleur plus amère,"});
db.iliadfr.insert({ pg : 12110, line : "même si j'apprenais la mort de mon père qui, peut-être, dans la"});
db.iliadfr.insert({ pg : 12111, line : "Phthiè, verse en ce moment des larmes, privé du secours de son"});
db.iliadfr.insert({ pg : 12112, line : "fils, tandis que, sur une terre étrangère je combats les Troiens"});
db.iliadfr.insert({ pg : 12113, line : "dompteurs de chevaux pour la cause de l'exécrable Hélénè; ou même,"});
db.iliadfr.insert({ pg : 12114, line : "si je regrettais mon fils bien-aimé, qu'on élève à Skyros,"});
db.iliadfr.insert({ pg : 12115, line : "Néoptolémos semblable à un dieu, s'il vit encore. Autrefois,"});
db.iliadfr.insert({ pg : 12116, line : "j'espérais dans mon coeur que je mourrais seul devant Troiè, loin"});
db.iliadfr.insert({ pg : 12117, line : "d'Argos féconde en chevaux, et que tu conduirais mon fils, de"});
db.iliadfr.insert({ pg : 12118, line : "Skyros vers la Phthiè, sur ta nef rapide; et que tu lui remettrais"});
db.iliadfr.insert({ pg : 12119, line : "mes domaines, mes serviteurs et ma haute et grande demeure. Car je"});
db.iliadfr.insert({ pg : 12120, line : "pense que Pèleus n'existe plus, ou que, s'il traîne un reste de"});
db.iliadfr.insert({ pg : 12121, line : "vie, il attend, accablé par l'affreuse vieillesse, qu'on lui porte"});
db.iliadfr.insert({ pg : 12122, line : "la triste nouvelle de ma mort."});
db.iliadfr.insert({ pg : 12124, line : "Il parla ainsi en pleurant, et les princes vénérables gémirent,"});
db.iliadfr.insert({ pg : 12125, line : "chacun se souvenant de ce qu'il avait laissé dans ses demeures. Et"});
db.iliadfr.insert({ pg : 12126, line : "le Kroniôn, les voyant pleurer, fut saisi de compassion, et il dit"});
db.iliadfr.insert({ pg : 12127, line : "à Athènè ces paroles ailées:"});
db.iliadfr.insert({ pg : 12129, line : "-- Ma fille, délaisses-tu déjà ce héros? Akhilleus n'est-il plus"});
db.iliadfr.insert({ pg : 12130, line : "rien dans ton esprit? Devant ses nefs aux antennes dressées, il"});
db.iliadfr.insert({ pg : 12131, line : "est assis, gémissant sur son cher compagnon. Les autres mangent,"});
db.iliadfr.insert({ pg : 12132, line : "et lui reste sans nourriture. Va! verse dans sa poitrine le nektar"});
db.iliadfr.insert({ pg : 12133, line : "et la douce ambroisie, pour que la faim ne l'accable point."});
db.iliadfr.insert({ pg : 12135, line : "Et, parlant ainsi, il excita Athènè déjà pleine d'ardeur. Et,"});
db.iliadfr.insert({ pg : 12136, line : "semblable à l'aigle marin aux cris perçants, elle sauta de"});
db.iliadfr.insert({ pg : 12137, line : "l'Ouranos dans l'aithèr; et tandis que les Akhaiens s'armaient"});
db.iliadfr.insert({ pg : 12138, line : "sous les tentes, elle versa dans la poitrine d'Akhilleus le nektar"});
db.iliadfr.insert({ pg : 12139, line : "et l'ambroisie désirable, pour que la faim mauvaise ne rompit pas"});
db.iliadfr.insert({ pg : 12140, line : "ses genoux. Puis, elle retourna dans la solide demeure de son père"});
db.iliadfr.insert({ pg : 12141, line : "très puissant, et les Akhaiens se répandirent hors des nefs"});
db.iliadfr.insert({ pg : 12142, line : "rapides."});
db.iliadfr.insert({ pg : 12144, line : "De même que les neiges épaisses volent dans l'air, refroidies par"});
db.iliadfr.insert({ pg : 12145, line : "le souffle impétueux de l'aithéréen Boréas, de même, hors des"});
db.iliadfr.insert({ pg : 12146, line : "nefs, se répandaient les casques solides et resplendissants, et"});
db.iliadfr.insert({ pg : 12147, line : "les boucliers bombés, et les cuirasses épaisses, et les lances de"});
db.iliadfr.insert({ pg : 12148, line : "frêne. Et la splendeur en montait dans l'Ouranos, et toute la"});
db.iliadfr.insert({ pg : 12149, line : "terre, au loin, riait de l'éclat de l'airain, et retentissait du"});
db.iliadfr.insert({ pg : 12150, line : "trépignement des pieds des guerriers. Et, au milieu d'eux,"});
db.iliadfr.insert({ pg : 12151, line : "s'armait le divin Akhilleus; et ses dents grinçaient, et ses yeux"});
db.iliadfr.insert({ pg : 12152, line : "flambaient comme le feu, et une affreuse douleur emplissait son"});
db.iliadfr.insert({ pg : 12153, line : "coeur; et, furieux contre les Troiens, il se couvrit des armes que"});
db.iliadfr.insert({ pg : 12154, line : "le dieu Hèphaistos lui avait faites. Et, d'abord, il attacha"});
db.iliadfr.insert({ pg : 12155, line : "autour de ses jambes, par des agrafes d'argent, les belles"});
db.iliadfr.insert({ pg : 12156, line : "knèmides. Puis il couvrit sa poitrine de la cuirasse. Il suspendit"});
db.iliadfr.insert({ pg : 12157, line : "l'épée d'airain aux clous d'argent à ses épaules, et il saisit le"});
db.iliadfr.insert({ pg : 12158, line : "bouclier immense et solide d'où sortait une longue clarté, comme"});
db.iliadfr.insert({ pg : 12159, line : "de Sélénè. De même que la splendeur d'un ardent incendie apparaît"});
db.iliadfr.insert({ pg : 12160, line : "de loin, sur la mer, aux matelots, et brûle, dans un enclos"});
db.iliadfr.insert({ pg : 12161, line : "solitaire, au faîte des montagnes, tandis que les rapides"});
db.iliadfr.insert({ pg : 12162, line : "tempêtes, sur la mer poissonneuse, les emportent loin de leurs"});
db.iliadfr.insert({ pg : 12163, line : "amis; de même l'éclat du beau et solide bouclier d'Akhilleus"});
db.iliadfr.insert({ pg : 12164, line : "montait dans l'air. Et il mit sur sa tête le casque lourd. Et le"});
db.iliadfr.insert({ pg : 12165, line : "casque à crinière luisait comme un astre, et les crins d'or que"});
db.iliadfr.insert({ pg : 12166, line : "Hèphaistos avait posés autour se mouvaient par masses. Et le divin"});
db.iliadfr.insert({ pg : 12167, line : "Akhilleus essaya ses armes, présents illustres, afin de voir si"});
db.iliadfr.insert({ pg : 12168, line : "elles convenaient à ses membres. Et elles étaient comme des ailes"});
db.iliadfr.insert({ pg : 12169, line : "qui enlevaient le prince des peuples. Et il retira de l'étui la"});
db.iliadfr.insert({ pg : 12170, line : "lance paternelle, lourde, immense et solide, que ne pouvait"});
db.iliadfr.insert({ pg : 12171, line : "soulever aucun des Akhaiens, et que, seul, Akhilleus savait"});
db.iliadfr.insert({ pg : 12172, line : "manier; la lance Pèliade que, du faîte du Pèlios, Khirôn avait"});
db.iliadfr.insert({ pg : 12173, line : "apportée à Pèleus, pour le meurtre des héros."});
db.iliadfr.insert({ pg : 12175, line : "Et Automédôn et Alkimos lièrent les chevaux au joug avec de belles"});
db.iliadfr.insert({ pg : 12176, line : "courroies; ils leur mirent les freins dans la bouche, et ils"});
db.iliadfr.insert({ pg : 12177, line : "raidirent les rênes vers le siège du char. Et Automédôn y monta,"});
db.iliadfr.insert({ pg : 12178, line : "saisissant d'une main habile le fouet brillant, et Akhilleus y"});
db.iliadfr.insert({ pg : 12179, line : "monta aussi, tout resplendissant sous ses armes, comme le matinal"});
db.iliadfr.insert({ pg : 12180, line : "Hypérionade, et il dit rudement aux chevaux de son père:"});
db.iliadfr.insert({ pg : 12182, line : "-- Xanthos et Balios, illustres enfants de Podargè, ramenez cette"});
db.iliadfr.insert({ pg : 12183, line : "fois votre conducteur parmi les Danaens, quand nous serons"});
db.iliadfr.insert({ pg : 12184, line : "rassasiés du combat, et ne l'abandonnez point mort comme"});
db.iliadfr.insert({ pg : 12185, line : "Patroklos."});
db.iliadfr.insert({ pg : 12187, line : "Et le cheval aux pieds rapides, Xanthos, lui parla sous le joug;"});
db.iliadfr.insert({ pg : 12188, line : "et il inclina la tête, et toute sa crinière. flottant autour du"});
db.iliadfr.insert({ pg : 12189, line : "timon, tombait jusqu'à terre. Et la déesse Hèrè aux bras blancs"});
db.iliadfr.insert({ pg : 12190, line : "lui permit de parler:"});
db.iliadfr.insert({ pg : 12192, line : "-- Certes, nous te sauverons aujourd'hui, très brave Akhilleus;"});
db.iliadfr.insert({ pg : 12193, line : "cependant, ton dernier jour approche. Ne nous en accuse point,"});
db.iliadfr.insert({ pg : 12194, line : "mais le grand Zeus et la moire puissante. Ce n'est ni par notre"});
db.iliadfr.insert({ pg : 12195, line : "lenteur, ni par notre lâcheté que les Troiens ont arraché tes"});
db.iliadfr.insert({ pg : 12196, line : "armes des épaules de Patroklos. C'est le dieu excellent que Lètô"});
db.iliadfr.insert({ pg : 12197, line : "aux beaux cheveux a enfanté, qui, ayant tué le Ménoitiade au"});
db.iliadfr.insert({ pg : 12198, line : "premier rang, a donné la victoire à Hektôr. Quand notre course"});
db.iliadfr.insert({ pg : 12199, line : "serait telle que le souffle de Zéphyros, le plus rapide des vents,"});
db.iliadfr.insert({ pg : 12200, line : "tu n'en tomberais pas moins sous les coups d'un dieu et d'un"});
db.iliadfr.insert({ pg : 12201, line : "homme."});
db.iliadfr.insert({ pg : 12203, line : "Et comme il parlait, les Érinnyes arrêtèrent sa voix, et Akhilleus"});
db.iliadfr.insert({ pg : 12204, line : "aux pieds rapides lui répondit, furieux:"});
db.iliadfr.insert({ pg : 12206, line : "-- Xanthos, pourquoi m'annoncer la mort? Que t'importe? Je sais"});
db.iliadfr.insert({ pg : 12207, line : "que ma destinée est de mourir ici, loin de mon père et de ma mère,"});
db.iliadfr.insert({ pg : 12208, line : "mais je ne m'arrêterai qu'après avoir assouvi les Troiens de"});
db.iliadfr.insert({ pg : 12209, line : "combats."});
db.iliadfr.insert({ pg : 12211, line : "Il parla ainsi, et, avec de grands cris, il poussa aux premiers"});
db.iliadfr.insert({ pg : 12212, line : "rangs les chevaux aux sabots massifs."});
db.iliadfr.insert({ pg : 12215, line : "Chant 20"});
db.iliadfr.insert({ pg : 12217, line : "Auprès des nefs aux poupes recourbées, et autour de toi, fils de"});
db.iliadfr.insert({ pg : 12218, line : "Pèleus, les Akhaiens insatiables de combats s'armaient ainsi, et"});
db.iliadfr.insert({ pg : 12219, line : "les Troiens, de leur côté, se rangeaient sur la hauteur de la"});
db.iliadfr.insert({ pg : 12220, line : "plaine."});
db.iliadfr.insert({ pg : 12222, line : "Et Zeus ordonna à Thémis de convoquer les dieux à l'agora, de"});
db.iliadfr.insert({ pg : 12223, line : "toutes les cimes de l'Olympos. Et celle-ci, volant çà et là, leur"});
db.iliadfr.insert({ pg : 12224, line : "commanda de se rendre à la demeure de Zeus. Et aucun des fleuves"});
db.iliadfr.insert({ pg : 12225, line : "n'y manqua, sauf Okéanos; ni aucune des nymphes qui habitent les"});
db.iliadfr.insert({ pg : 12226, line : "belles forêts, et les sources des fleuves et les prairies herbues."});
db.iliadfr.insert({ pg : 12227, line : "Et tous les dieux vinrent s'asseoir, dans la demeure de Zeus qui"});
db.iliadfr.insert({ pg : 12228, line : "amasse les nuées, sous les portiques brillants que Hèphaistos"});
db.iliadfr.insert({ pg : 12229, line : "avait habilement construits pour le père Zeus. Et ils vinrent"});
db.iliadfr.insert({ pg : 12230, line : "tous; et Poseidaôn, ayant entendu la déesse, vint aussi de la mer;"});
db.iliadfr.insert({ pg : 12231, line : "et il s'assit au milieu d'eux, et il interrogea la pensée de Zeus:"});
db.iliadfr.insert({ pg : 12233, line : "-- Pourquoi, ô foudroyant, convoques-tu de nouveau les dieux à"});
db.iliadfr.insert({ pg : 12234, line : "l'agora? Serait-ce pour délibérer sur les Troiens et les Akhaiens?"});
db.iliadfr.insert({ pg : 12235, line : "Bientôt, en effet, ils vont engager la bataille ardente."});
db.iliadfr.insert({ pg : 12237, line : "Et Zeus qui amasse les nuées, lui répondant, parla ainsi:"});
db.iliadfr.insert({ pg : 12239, line : "-- Tu as dit, Poseidaôn, dans quel dessein je vous ai tous réunis,"});
db.iliadfr.insert({ pg : 12240, line : "car ces peuples périssables m'occupent en effet. Assis au faîte de"});
db.iliadfr.insert({ pg : 12241, line : "l'Olympos, je me réjouirai en les regardant combattre, mais vous,"});
db.iliadfr.insert({ pg : 12242, line : "allez tous vers les Troiens et les Akhaiens. Secourez les uns ou"});
db.iliadfr.insert({ pg : 12243, line : "les autres, selon que votre coeur vous y poussera; car si"});
db.iliadfr.insert({ pg : 12244, line : "Akhilleus combat seul et librement les Troiens, jamais ils ne"});
db.iliadfr.insert({ pg : 12245, line : "soutiendront la rencontre du rapide Pèléiôn. Déjà, son aspect seul"});
db.iliadfr.insert({ pg : 12246, line : "les a épouvantés; et, maintenant qu'il est plein de fureur à cause"});
db.iliadfr.insert({ pg : 12247, line : "de son compagnon, je crains qu'il renverse les murailles d'Ilios,"});
db.iliadfr.insert({ pg : 12248, line : "malgré le destin."});
db.iliadfr.insert({ pg : 12250, line : "Le Kroniôn parla, suscitant une guerre inéluctable. Et tous les"});
db.iliadfr.insert({ pg : 12251, line : "dieux, opposés les uns aux autres, se préparèrent au combat. Et,"});
db.iliadfr.insert({ pg : 12252, line : "du côté des nefs, se rangèrent Hèrè, et Pallas Athènè, et"});
db.iliadfr.insert({ pg : 12253, line : "Poseidaôn qui entoure la terre, et Hermès utile et plein de"});
db.iliadfr.insert({ pg : 12254, line : "sagesse, et Hèphaistos, boiteux et frémissant dans sa force. Et,"});
db.iliadfr.insert({ pg : 12255, line : "du côté des Troiens, se rangèrent Arès aux armes mouvantes, et"});
db.iliadfr.insert({ pg : 12256, line : "Phoibos aux longs cheveux, et Artémis joyeuse de ses flèches, et"});
db.iliadfr.insert({ pg : 12257, line : "Lètô, et Xanthos, et Aphroditè qui aime les sourires."});
db.iliadfr.insert({ pg : 12259, line : "Tant que les dieux ne se mêlèrent point aux guerriers, les"});
db.iliadfr.insert({ pg : 12260, line : "Akhaiens furent pleins de confiance et d'orgueil, parce"});
db.iliadfr.insert({ pg : 12261, line : "quAkhilleus avait reparu, après s'être éloigné longtemps du"});
db.iliadfr.insert({ pg : 12262, line : "combat. Et la terreur rompit les genoux des Troiens quand ils"});
db.iliadfr.insert({ pg : 12263, line : "virent le Pèléiôn aux pieds rapides, resplendissant sous ses armes"});
db.iliadfr.insert({ pg : 12264, line : "et pareil au terrible Arès. Mais quand les dieux se furent mêlés"});
db.iliadfr.insert({ pg : 12265, line : "aux guerriers, la violente Éris excita les deux peuples. Et Athènè"});
db.iliadfr.insert({ pg : 12266, line : "poussa des cris, tantôt auprès du fossé creux, hors des murs,"});
db.iliadfr.insert({ pg : 12267, line : "tantôt le long des rivages retentissants. Et Arès, semblable à une"});
db.iliadfr.insert({ pg : 12268, line : "noire tempête, criait aussi, soit au faîte d'Ilios, en excitant"});
db.iliadfr.insert({ pg : 12269, line : "les Troiens, soit le long des belles collines du Simoïs. Ainsi les"});
db.iliadfr.insert({ pg : 12270, line : "dieux heureux engagèrent la mêlée violente entre les deux peuples."});
db.iliadfr.insert({ pg : 12272, line : "Et le père des hommes et des dieux tonna longuement dans les"});
db.iliadfr.insert({ pg : 12273, line : "hauteurs; et Poseidaôn ébranla la terre immense et les cimes des"});
db.iliadfr.insert({ pg : 12274, line : "montagnes; et les racines de l'Ida aux nombreuses sources"});
db.iliadfr.insert({ pg : 12275, line : "tremblèrent, et la ville des Troiens et les nefs des Akhaiens. Et"});
db.iliadfr.insert({ pg : 12276, line : "le souterrain Aidôneus, le roi des morts, trembla, et il sauta,"});
db.iliadfr.insert({ pg : 12277, line : "épouvanté, de son trône; et il cria, craignant que Poseidaôn qui"});
db.iliadfr.insert({ pg : 12278, line : "ébranle la terre l'entr'ouvrît, et que les demeures affreuses et"});
db.iliadfr.insert({ pg : 12279, line : "infectes, en horreur aux dieux eux-mêmes, fussent vues des mortels"});
db.iliadfr.insert({ pg : 12280, line : "et des immortels: tant fut terrible le retentissement du choc des"});
db.iliadfr.insert({ pg : 12281, line : "dieux."});
db.iliadfr.insert({ pg : 12283, line : "Et Phoibos Apollôn, avec ses flèches empennées, marchait contre le"});
db.iliadfr.insert({ pg : 12284, line : "roi Poseidaôn; et la déesse Athènè aux yeux clairs contre Arès, et"});
db.iliadfr.insert({ pg : 12285, line : "Artémis, soeur de l'archer Apollôn, joyeuse de porter les sonores"});
db.iliadfr.insert({ pg : 12286, line : "flèches dorées, contre Hèrè; et, contre Lètô, le sage et utile"});
db.iliadfr.insert({ pg : 12287, line : "Hermès; et, contre Hèphaistos, le grand fleuve aux profonds"});
db.iliadfr.insert({ pg : 12288, line : "tourbillons, que les dieux nomment Xanthos, et les hommes"});
db.iliadfr.insert({ pg : 12289, line : "Skamandros. Ainsi les dieux marchaient contre les dieux."});
db.iliadfr.insert({ pg : 12291, line : "Mais Akhilleus ne désirait rencontrer que le Priamide Hektôr dans"});
db.iliadfr.insert({ pg : 12292, line : "la mêlée, et il ne songeait qu'à boire le sang du brave Priamide."});
db.iliadfr.insert({ pg : 12293, line : "Et Apollôn qui soulève les peuples excita Ainéias contre le"});
db.iliadfr.insert({ pg : 12294, line : "Pèléide, et il le remplit d'une grande force, et semblable par la"});
db.iliadfr.insert({ pg : 12295, line : "voix à Lykaôn, fils de Priamos, le fils de Zeus dit à Ainéias:"});
db.iliadfr.insert({ pg : 12297, line : "-- Ainéias, prince des Troiens, où est la promesse que tu faisais"});
db.iliadfr.insert({ pg : 12298, line : "aux rois d'Ilios de combattre le Pèléide Akhilleus?"});
db.iliadfr.insert({ pg : 12300, line : "Et Ainéias, lui répondant, parla ainsi:"});
db.iliadfr.insert({ pg : 12302, line : "-- Priamide, pourquoi me pousses-tu à combattre l'orgueilleux"});
db.iliadfr.insert({ pg : 12303, line : "Pèléiôn? Je ne tiendrais pas tête pour la première fois au rapide"});
db.iliadfr.insert({ pg : 12304, line : "Akhilleus. Déjà, autrefois, de sa lance, il m'a chassé de l'Ida,"});
db.iliadfr.insert({ pg : 12305, line : "quand, ravissant nos boeufs, il détruisit Lyrnessos et Pèdasos;"});
db.iliadfr.insert({ pg : 12306, line : "mais Zeus me sauva, en donnant la force et la rapidité à mes"});
db.iliadfr.insert({ pg : 12307, line : "genoux. Certes, je serais tombé sous les mains d'Akhilleus et"});
db.iliadfr.insert({ pg : 12308, line : "d'Athènè qui marchait devant lui et l'excitait à tuer les Léléges"});
db.iliadfr.insert({ pg : 12309, line : "et les Troiens, à l'aide de sa lance d'airain. Aucun guerrier ne"});
db.iliadfr.insert({ pg : 12310, line : "peut lutter contre Akhilleus. Un des dieux est toujours auprès de"});
db.iliadfr.insert({ pg : 12311, line : "lui qui le préserve. Ses traits vont droit au but, et ne"});
db.iliadfr.insert({ pg : 12312, line : "s'arrêtent qu'après s'être enfoncés dans le corps de l'homme. Si"});
db.iliadfr.insert({ pg : 12313, line : "un dieu rendait le combat égal entre nous, il ne me dompterait pas"});
db.iliadfr.insert({ pg : 12314, line : "aisément, bien qu'il se vante d'être tout entier d'airain."});
db.iliadfr.insert({ pg : 12316, line : "Et le roi Apollôn, fils de Zeus, lui répondit:"});
db.iliadfr.insert({ pg : 12318, line : "-- Héros, il t'appartient aussi d'invoquer les dieux éternels. On"});
db.iliadfr.insert({ pg : 12319, line : "dit aussi, en effet, qu'Aphroditè, fille de Zeus, t'a enfanté, et"});
db.iliadfr.insert({ pg : 12320, line : "lui est né d'une déesse inférieure. Ta mère est fille de Zeus, et"});
db.iliadfr.insert({ pg : 12321, line : "la sienne est fille du Vieillard de la mer. Pousse droit à lui"});
db.iliadfr.insert({ pg : 12322, line : "l'airain indomptable, et que ses paroles injurieuses et ses"});
db.iliadfr.insert({ pg : 12323, line : "menaces ne t'arrêtent pas."});
db.iliadfr.insert({ pg : 12325, line : "Ayant ainsi parlé, il inspira une grande force au prince des"});
db.iliadfr.insert({ pg : 12326, line : "peuples, qui courut en avant, armé de l'airain splendide. Mais le"});
db.iliadfr.insert({ pg : 12327, line : "fils d'Ankhisès, courant au Pèléide à travers la mêlée des hommes,"});
db.iliadfr.insert({ pg : 12328, line : "fut aperçu par Hèrè aux bras blancs, et celle-ci, réunissant les"});
db.iliadfr.insert({ pg : 12329, line : "dieux, leur dit:"});
db.iliadfr.insert({ pg : 12331, line : "-- Poseidaôn et Athènè, songez à ceci dans votre esprit: Ainéias,"});
db.iliadfr.insert({ pg : 12332, line : "armé de l'airain splendide, court au Pèléide, et Phoibos Apollôn"});
db.iliadfr.insert({ pg : 12333, line : "l'y excite. Allons, écartons ce dieu, et qu'un de nous assiste"});
db.iliadfr.insert({ pg : 12334, line : "Akhilleus et lui donne la force et l'intrépidité. Qu'il sache que"});
db.iliadfr.insert({ pg : 12335, line : "les plus puissants des immortels l'aiment, et que ce sont les plus"});
db.iliadfr.insert({ pg : 12336, line : "faibles qui viennent en aide aux Troiens dans le combat. Tous,"});
db.iliadfr.insert({ pg : 12337, line : "nous sommes descendus de l'Ouranos dans la mêlée, afin de le"});
db.iliadfr.insert({ pg : 12338, line : "préserver des Troiens, en ce jour; et il subira ensuite ce que la"});
db.iliadfr.insert({ pg : 12339, line : "destinée lui a filé avec le lin, depuis que sa mère l'a enfanté."});
db.iliadfr.insert({ pg : 12340, line : "Si Akhilleus, dans ce combat, ne ressent pas l'inspiration des"});
db.iliadfr.insert({ pg : 12341, line : "dieux, il redoutera la rencontre d'un immortel, car l'apparition"});
db.iliadfr.insert({ pg : 12342, line : "des dieux épouvante les hommes."});
db.iliadfr.insert({ pg : 12344, line : "Et Poseidaôn qui ébranle la terre lui répondit:"});
db.iliadfr.insert({ pg : 12346, line : "-- Hèrè, ne t'irrite point hors de raison, car cela ne te convient"});
db.iliadfr.insert({ pg : 12347, line : "pas. Je ne veux point que nous combattions les autres dieux, étant"});
db.iliadfr.insert({ pg : 12348, line : "de beaucoup plus forts qu'eux. Asseyons-nous hors de la mêlée, sur"});
db.iliadfr.insert({ pg : 12349, line : "la colline, et laissons aux hommes le souci de la guerre. Si Arès"});
db.iliadfr.insert({ pg : 12350, line : "commence le combat, ou Phoibos Apollôn, et s'ils arrêtent"});
db.iliadfr.insert({ pg : 12351, line : "Akhilleus et l'empêchent d'agir, alors une lutte terrible"});
db.iliadfr.insert({ pg : 12352, line : "s'engagera entre eux et nous, et je pense que, promptement"});
db.iliadfr.insert({ pg : 12353, line : "vaincus, ils retourneront dans l'Ouranos, vers l'assemblée des"});
db.iliadfr.insert({ pg : 12354, line : "immortels, rudement domptés par nos mains irrésistibles."});
db.iliadfr.insert({ pg : 12356, line : "Ayant ainsi parlé, Poseidaôn aux cheveux bleus les précéda vers la"});
db.iliadfr.insert({ pg : 12357, line : "muraille haute du divin Hèraklès. Athènè et les Troiens avaient"});
db.iliadfr.insert({ pg : 12358, line : "autrefois élevé cette enceinte pour le mettre à l'abri de la"});
db.iliadfr.insert({ pg : 12359, line : "Baleine, quand ce monstre le poursuivait du rivage dans la plaine."});
db.iliadfr.insert({ pg : 12360, line : "Là, Poseidaôn et les autres dieux s'assirent, s'étant enveloppés"});
db.iliadfr.insert({ pg : 12361, line : "d'une épaisse nuée. Et, de leur côté, les immortels, défenseurs"});
db.iliadfr.insert({ pg : 12362, line : "d'Ilios, s'assirent sur les collines du Simoïs, autour de toi,"});
db.iliadfr.insert({ pg : 12363, line : "archer Apollôn, et de toi, Arès, destructeur des citadelles! Ainsi"});
db.iliadfr.insert({ pg : 12364, line : "tous les dieux étaient assis, et ils méditaient, retardant le"});
db.iliadfr.insert({ pg : 12365, line : "terrible combat, bien que Zeus, tranquille dans les hauteurs, les"});
db.iliadfr.insert({ pg : 12366, line : "y eût excités."});
db.iliadfr.insert({ pg : 12368, line : "Et toute la plaine était emplie et resplendissait de l'airain des"});
db.iliadfr.insert({ pg : 12369, line : "chevaux et des hommes, et la terre retentissait sous les pieds des"});
db.iliadfr.insert({ pg : 12370, line : "deux armées. Et, au milieu de tous, s'avançaient, prêts à"});
db.iliadfr.insert({ pg : 12371, line : "combattre, Ainéias Ankhisiade et le divin Akhilleus. Et Ainéias"});
db.iliadfr.insert({ pg : 12372, line : "marchait, menaçant, secouant son casque solide et portant devant"});
db.iliadfr.insert({ pg : 12373, line : "sa poitrine son bouclier terrible, et brandissant sa lance"});
db.iliadfr.insert({ pg : 12374, line : "d'airain. Et le Pèléide se ruait sur lui, comme un lion dangereux"});
db.iliadfr.insert({ pg : 12375, line : "que toute une foule désire tuer. Et il avance, méprisant ses"});
db.iliadfr.insert({ pg : 12376, line : "ennemis; mais, dès qu'un des jeunes hommes l'a blessé, il ouvre la"});
db.iliadfr.insert({ pg : 12377, line : "gueule, et l'écume jaillit à travers ses dents, et son coeur rugit"});
db.iliadfr.insert({ pg : 12378, line : "dans sa poitrine, et il se bat les deux flancs et les reins de sa"});
db.iliadfr.insert({ pg : 12379, line : "queue, s'animant au combat. Puis, les yeux flambants, il bondit"});
db.iliadfr.insert({ pg : 12380, line : "avec force droit sur les hommes, afin de les déchirer ou d'en être"});
db.iliadfr.insert({ pg : 12381, line : "tué lui-même. Ainsi sa force et son orgueil poussaient Akhilleus"});
db.iliadfr.insert({ pg : 12382, line : "contre le magnanime Ainéias. Et, quand ils se furent rencontrés,"});
db.iliadfr.insert({ pg : 12383, line : "le premier, le divin Akhilleus aux pieds rapides parla ainsi:"});
db.iliadfr.insert({ pg : 12385, line : "-- Ainéias, pourquoi sors-tu de la foule des guerriers? Désires-tu"});
db.iliadfr.insert({ pg : 12386, line : "me combattre dans l'espoir de commander aux Troiens dompteurs de"});
db.iliadfr.insert({ pg : 12387, line : "chevaux, avec la puissance de Priamos? Mais si tu me tuais,"});
db.iliadfr.insert({ pg : 12388, line : "Priamos ne te donnerait point cette récompense, car il a des fils,"});
db.iliadfr.insert({ pg : 12389, line : "et lui-même n'est pas insensé. Les Troiens, si tu me tuais,"});
db.iliadfr.insert({ pg : 12390, line : "t'auraient-ils promis un domaine excellent où tu jouirais de tes"});
db.iliadfr.insert({ pg : 12391, line : "vignes et de tes moissons? Mais je pense que tu le mériteras peu"});
db.iliadfr.insert({ pg : 12392, line : "aisément, car déjà je t'ai vu fuir devant ma lance. Ne te"});
db.iliadfr.insert({ pg : 12393, line : "souviens-tu pas que je t'ai précipité déjà des cimes Idaiennes,"});
db.iliadfr.insert({ pg : 12394, line : "loin de tes boeufs, et que, sans te retourner dans ta fuite, tu te"});
db.iliadfr.insert({ pg : 12395, line : "réfugias à Lyrnessos? Mais, l'ayant renversée, avec l'aide de Zeus"});
db.iliadfr.insert({ pg : 12396, line : "et d'Athènè, j'en emmenai toutes les femmes qui pleuraient leur"});
db.iliadfr.insert({ pg : 12397, line : "liberté. Zeus et les autres dieux te sauvèrent. Cependant, je ne"});
db.iliadfr.insert({ pg : 12398, line : "pense pas qu'ils te sauvent aujourd'hui comme tu l'espères. Je te"});
db.iliadfr.insert({ pg : 12399, line : "conseille donc de ne pas me tenir tête, et de rentrer dans la"});
db.iliadfr.insert({ pg : 12400, line : "foule avant qu'il te soit arrivé malheur. L'insensé ne connaît son"});
db.iliadfr.insert({ pg : 12401, line : "mal qu'après l'avoir subi."});
db.iliadfr.insert({ pg : 12403, line : "Et Ainéias lui répondit:"});
db.iliadfr.insert({ pg : 12405, line : "-- N'espère point, par des paroles, m'épouvanter comme un enfant,"});
db.iliadfr.insert({ pg : 12406, line : "car moi aussi je pourrais me répandre en outrages. L'un et l'autre"});
db.iliadfr.insert({ pg : 12407, line : "nous connaissons notre race et nos parents, sachant tous deux la"});
db.iliadfr.insert({ pg : 12408, line : "tradition des anciens hommes, bien que tu n'aies jamais vu mes"});
db.iliadfr.insert({ pg : 12409, line : "parents, ni moi les tiens. On dit que tu es le fils de l'illustre"});
db.iliadfr.insert({ pg : 12410, line : "Pèleus et que ta mère est la nymphe marine Thétis aux beaux"});
db.iliadfr.insert({ pg : 12411, line : "cheveux. Moi, je me glorifie d'être le fils du magnanime Ankhisès,"});
db.iliadfr.insert({ pg : 12412, line : "et ma mère est Aphroditè. Les uns ou les autres, aujourd'hui,"});
db.iliadfr.insert({ pg : 12413, line : "pleureront leur fils bien-aimé; car je ne pense point que des"});
db.iliadfr.insert({ pg : 12414, line : "paroles enfantines nous éloignent du combat. Veux-tu bien"});
db.iliadfr.insert({ pg : 12415, line : "connaître ma race, célèbre parmi la multitude des hommes? Zeus qui"});
db.iliadfr.insert({ pg : 12416, line : "amasse les nuées engendra d'abord Dardanos, et celui-ci bâtit"});
db.iliadfr.insert({ pg : 12417, line : "Dardaniè. Et la sainte Ilios, citadelle des hommes, ne s'élevait"});
db.iliadfr.insert({ pg : 12418, line : "point encore dans la plaine, et les peuples habitaient aux pieds"});
db.iliadfr.insert({ pg : 12419, line : "de l'Ida où abondent les sources. Et Dardanos engendra le roi"});
db.iliadfr.insert({ pg : 12420, line : "Érikhthonios, qui fut le plus riche des hommes. Dans ses marécages"});
db.iliadfr.insert({ pg : 12421, line : "paissaient trois mille juments fières de leurs poulains. Et"});
db.iliadfr.insert({ pg : 12422, line : "Boréas, sous la forme d'un cheval aux crins bleus, les aima et les"});
db.iliadfr.insert({ pg : 12423, line : "couvrit comme elles paissaient, et elles firent douze poulines qui"});
db.iliadfr.insert({ pg : 12424, line : "bondissaient dans les champs fertiles, courant sur la cime des"});
db.iliadfr.insert({ pg : 12425, line : "épis sans les courber. Et quand elles bondissaient sur le large"});
db.iliadfr.insert({ pg : 12426, line : "dos de la mer, elles couraient sur la cime des écumes blanches. Et"});
db.iliadfr.insert({ pg : 12427, line : "Érikthonios engendra le roi des Troiens, Trôos. Et Trôos engendra"});
db.iliadfr.insert({ pg : 12428, line : "trois fils irréprochables, Ilos, Assarakos et le divin Ganymèdès,"});
db.iliadfr.insert({ pg : 12429, line : "qui fut le plus beau des hommes mortels, et que les dieux"});
db.iliadfr.insert({ pg : 12430, line : "enlevèrent à cause de sa beauté, afin qu'il fût l'échanson de Zeus"});
db.iliadfr.insert({ pg : 12431, line : "et qu'il habitât parmi les immortels. Et Ilos engendra l'illustre"});
db.iliadfr.insert({ pg : 12432, line : "Laomédôn, et Laomédôn engendra Tithonos, Priamos, Lampos, Klytios"});
db.iliadfr.insert({ pg : 12433, line : "et Hikétaôn, nourrisson d'Arès. Mais Assarakos engendra Kapys, qui"});
db.iliadfr.insert({ pg : 12434, line : "engendra Ankhisès, et Ankhisès m'a engendré, comme Priamos a"});
db.iliadfr.insert({ pg : 12435, line : "engendré le divin Hektôr. Je me glorifie de ce sang et de cette"});
db.iliadfr.insert({ pg : 12436, line : "race. Zeus, comme il le veut, augmente ou diminue la vertu des"});
db.iliadfr.insert({ pg : 12437, line : "hommes, étant le plus puissant. Mais, debout dans la mêlée, ne"});
db.iliadfr.insert({ pg : 12438, line : "parlons point plus longtemps comme de petits enfants. Nous"});
db.iliadfr.insert({ pg : 12439, line : "pourrions aisément amasser plus d'injures que n'en porterait une"});
db.iliadfr.insert({ pg : 12440, line : "nef à cent avirons. La langue des hommes est rapide et abonde en"});
db.iliadfr.insert({ pg : 12441, line : "discours qui se multiplient de part et d'autre, et tout ce que tu"});
db.iliadfr.insert({ pg : 12442, line : "diras, tu pourras l'entendre. Faut-il que nous luttions d'injures"});
db.iliadfr.insert({ pg : 12443, line : "et d'outrages, comme des femmes furieuses qui combattent sur une"});
db.iliadfr.insert({ pg : 12444, line : "place publique à coups de mensonges et de vérités, car la colère"});
db.iliadfr.insert({ pg : 12445, line : "les mène? Les paroles ne me feront pas reculer avant que tu n'aies"});
db.iliadfr.insert({ pg : 12446, line : "combattu. Agis donc promptement, et goûtons tous deux de nos"});
db.iliadfr.insert({ pg : 12447, line : "lances d'airain."});
db.iliadfr.insert({ pg : 12449, line : "Il parla ainsi, et il poussa violemment la lance d'airain contre"});
db.iliadfr.insert({ pg : 12450, line : "le terrible bouclier, dont l'orbe résonna sous le coup. Et le"});
db.iliadfr.insert({ pg : 12451, line : "Pèléide, de sa main vigoureuse, tendit le bouclier loin de son"});
db.iliadfr.insert({ pg : 12452, line : "corps, craignant que la longue lance du magnanime Ainéias passât"});
db.iliadfr.insert({ pg : 12453, line : "au travers. L'insensé ne songeait pas que les présents glorieux"});
db.iliadfr.insert({ pg : 12454, line : "des dieux résistent aisément aux forces des hommes."});
db.iliadfr.insert({ pg : 12456, line : "La forte lance du belliqueux Ainéias ne traversa point le"});
db.iliadfr.insert({ pg : 12457, line : "bouclier, car l'or, présent d'un dieu, arrêta le coup, qui perça"});
db.iliadfr.insert({ pg : 12458, line : "deux lames. Et il y en avait encore trois que le Boiteux avait"});
db.iliadfr.insert({ pg : 12459, line : "disposées ainsi: deux lames d'airain par-dessus, deux lames"});
db.iliadfr.insert({ pg : 12460, line : "d'étain au-dessous, et, au milieu, une lame d'or qui arrêta la"});
db.iliadfr.insert({ pg : 12461, line : "pique d'airain. Alors Akhilleus jeta sa longue lance, qui frappa"});
db.iliadfr.insert({ pg : 12462, line : "le bord du bouclier égal d'Ainéias, là où l'airain et le cuir"});
db.iliadfr.insert({ pg : 12463, line : "étaient le moins épais. Et la lance du Pèliade traversa le"});
db.iliadfr.insert({ pg : 12464, line : "bouclier qui retentit. Et Ainéias le tendit loin de son corps, en"});
db.iliadfr.insert({ pg : 12465, line : "se courbant, plein de crainte. Et la lance, par-dessus son dos,"});
db.iliadfr.insert({ pg : 12466, line : "s'enfonça en terre, ayant rompu les deux lames du bouclier qui"});
db.iliadfr.insert({ pg : 12467, line : "abritait le Troien. Et celui-ci resta épouvanté, et la douleur"});
db.iliadfr.insert({ pg : 12468, line : "troubla ses yeux, quand il vit la grande lance enfoncée auprès de"});
db.iliadfr.insert({ pg : 12469, line : "lui."});
db.iliadfr.insert({ pg : 12471, line : "Et Akhilleus, arrachant de la gaîne son épée aiguë, se rua avec un"});
db.iliadfr.insert({ pg : 12472, line : "cri terrible. Et Ainéias saisit un lourd rocher, tel que deux"});
db.iliadfr.insert({ pg : 12473, line : "hommes de maintenant ne pourraient le porter; mais il le remuait"});
db.iliadfr.insert({ pg : 12474, line : "aisément. Alors, Ainéias eût frappé Akhilleus, qui se ruait, soit"});
db.iliadfr.insert({ pg : 12475, line : "au casque, soit au bouclier qui le préservait de la mort, et le"});
db.iliadfr.insert({ pg : 12476, line : "Pèléide, avec l'épée, lui eût arraché l'âme, si Poseidaôn qui"});
db.iliadfr.insert({ pg : 12477, line : "ébranle la terre ne s'en fût aperçu. Et aussitôt, il dit, au"});
db.iliadfr.insert({ pg : 12478, line : "milieu des dieux immortels:"});
db.iliadfr.insert({ pg : 12480, line : "-- Hélas! je gémis sur le magnanime Ainéias, qui va descendre chez"});
db.iliadfr.insert({ pg : 12481, line : "Aidès, dompté par le Pèléide. L'archer Apollôn a persuadé"});
db.iliadfr.insert({ pg : 12482, line : "l'insensé et ne le sauvera point. Mais, innocent qu'il est,"});
db.iliadfr.insert({ pg : 12483, line : "pourquoi subirait-il les maux mérités par d'autres? N'a-t-il point"});
db.iliadfr.insert({ pg : 12484, line : "toujours offert des présents agréables aux dieux qui habitent le"});
db.iliadfr.insert({ pg : 12485, line : "large Ouranos? Allons! sauvons-le de la mort, de peur que le"});
db.iliadfr.insert({ pg : 12486, line : "Kronide ne s'irrite si Akhilleus le tue. Sa destinée est de"});
db.iliadfr.insert({ pg : 12487, line : "survivre, afin que la race de Dardanos ne périsse point, lui que"});
db.iliadfr.insert({ pg : 12488, line : "le Kronide a le plus aimé parmi tous les enfants que lui ont"});
db.iliadfr.insert({ pg : 12489, line : "donnés les femmes mortelles. Le Kroniôn est plein de haine pour la"});
db.iliadfr.insert({ pg : 12490, line : "race de Priamos. La force d'Ainéias commandera sur les Troiens, et"});
db.iliadfr.insert({ pg : 12491, line : "les fils de ses fils régneront, et ceux qui naîtront dans les"});
db.iliadfr.insert({ pg : 12492, line : "temps à venir."});
db.iliadfr.insert({ pg : 12494, line : "Et la vénérable Hèrè aux yeux de boeuf lui répondit:"});
db.iliadfr.insert({ pg : 12496, line : "-- Poseidaôn, vois s'il te convient, dans ton esprit, de sauver"});
db.iliadfr.insert({ pg : 12497, line : "Ainéias ou de laisser le Pèléide Akhilleus le tuer; car nous avons"});
db.iliadfr.insert({ pg : 12498, line : "souvent juré, moi et Pallas Athènè, au milieu des dieux, que"});
db.iliadfr.insert({ pg : 12499, line : "jamais nous n'éloignerions le jour fatal d'un Troien, même quand"});
db.iliadfr.insert({ pg : 12500, line : "Troiè brûlerait tout entière dans le feu allumé par les fils des"});
db.iliadfr.insert({ pg : 12501, line : "Akhaiens."});
db.iliadfr.insert({ pg : 12503, line : "Et, dès que Poseidaôn qui ébranle la terre eut entendu ces"});
db.iliadfr.insert({ pg : 12504, line : "paroles, il se jeta dans la mêlée, à travers le retentissement des"});
db.iliadfr.insert({ pg : 12505, line : "lances, jusqu'au lieu où se trouvaient Ainéias et Akhilleus. Et il"});
db.iliadfr.insert({ pg : 12506, line : "couvrit d'un brouillard les yeux du Pèléide; et, arrachant du"});
db.iliadfr.insert({ pg : 12507, line : "bouclier du magnanime Ainéias la lance à pointe d'airain, il la"});
db.iliadfr.insert({ pg : 12508, line : "posa aux pieds d'Akhilleus. Puis, il enleva de terre Ainéias; et"});
db.iliadfr.insert({ pg : 12509, line : "celui-ci franchit les épaisses masses de guerriers et de chevaux,"});
db.iliadfr.insert({ pg : 12510, line : "poussé par la main du dieu. Et quand il fut arrivé aux dernières"});
db.iliadfr.insert({ pg : 12511, line : "lignes de la bataille, là où les Kaukônes s'armaient pour le"});
db.iliadfr.insert({ pg : 12512, line : "combat, Poseidaôn qui ébranle la terre, s'approchant, lui dit ces"});
db.iliadfr.insert({ pg : 12513, line : "paroles ailées:"});
db.iliadfr.insert({ pg : 12515, line : "-- Ainéias, qui d'entre les dieux t'a persuadé, insensé, de"});
db.iliadfr.insert({ pg : 12516, line : "combattre Akhilleus, qui est plus fort que toi et plus cher aux"});
db.iliadfr.insert({ pg : 12517, line : "immortels? Recule quand tu le rencontreras, de peur que, malgré la"});
db.iliadfr.insert({ pg : 12518, line : "moire, tu descendes chez Aidès. Mais, quand Akhilleus aura subi la"});
db.iliadfr.insert({ pg : 12519, line : "destinée et la mort, ose combattre aux premiers rangs, car aucun"});
db.iliadfr.insert({ pg : 12520, line : "autre des Akhaiens ne te tuera."});
db.iliadfr.insert({ pg : 12522, line : "Ayant ainsi parlé, il le quitta. Puis, il dispersa l'épais"});
db.iliadfr.insert({ pg : 12523, line : "brouillard qui couvrait les yeux d'Akhilleus, et celui-ci vit tout"});
db.iliadfr.insert({ pg : 12524, line : "clairement de ses yeux, et, plein de colère, il dit dans son"});
db.iliadfr.insert({ pg : 12525, line : "esprit:"});
db.iliadfr.insert({ pg : 12527, line : "-- Ô dieux! certes, voici un grand prodige. Ma lance gît sur la"});
db.iliadfr.insert({ pg : 12528, line : "terre, devant moi, et je ne vois plus le guerrier contre qui je"});
db.iliadfr.insert({ pg : 12529, line : "l'ai jetée et que je voulais tuer! Certes, Ainéias est cher aux"});
db.iliadfr.insert({ pg : 12530, line : "dieux immortels. Je pensais qu'il s'en vantait faussement. Qu'il"});
db.iliadfr.insert({ pg : 12531, line : "vive! Il n'aura plus le désir de me braver, maintenant qu'il a"});
db.iliadfr.insert({ pg : 12532, line : "évité la mort. Mais, allons! j'exhorterai les Danaens belliqueux"});
db.iliadfr.insert({ pg : 12533, line : "et j'éprouverai la force des autres Troiens."});
db.iliadfr.insert({ pg : 12535, line : "Il parla ainsi, et il courut à travers les rangs, commandant à"});
db.iliadfr.insert({ pg : 12536, line : "chaque guerrier:"});
db.iliadfr.insert({ pg : 12538, line : "-- Ne restez pas plus longtemps loin de l'ennemi, divins Akhaiens!"});
db.iliadfr.insert({ pg : 12539, line : "Marchez, homme contre homme, et prêts au combat. Il m'est"});
db.iliadfr.insert({ pg : 12540, line : "difficile, malgré ma force, de poursuivre et d'attaquer seul tant"});
db.iliadfr.insert({ pg : 12541, line : "de guerriers; ni Arès, bien qu'il soit un dieu immortel, ni"});
db.iliadfr.insert({ pg : 12542, line : "Athènè, n'y suffiraient. Je vous aiderai de mes mains, de mes"});
db.iliadfr.insert({ pg : 12543, line : "pieds, de toute ma vigueur, sans jamais faiblir; et je serai"});
db.iliadfr.insert({ pg : 12544, line : "partout, au travers de la mêlée; et je ne pense pas qu'aucun"});
db.iliadfr.insert({ pg : 12545, line : "Troien se réjouisse de rencontrer ma lance."});
db.iliadfr.insert({ pg : 12547, line : "Il parla ainsi, et, de son côté, l'illustre Hektôr animait les"});
db.iliadfr.insert({ pg : 12548, line : "Troiens, leur promettant qu'il combattrait Akhilleus:"});
db.iliadfr.insert({ pg : 12550, line : "-- Troiens magnanimes, ne craignez point Akhilleus. Moi aussi,"});
db.iliadfr.insert({ pg : 12551, line : "avec des paroles, je combattrais jusqu'aux immortels; mais, avec"});
db.iliadfr.insert({ pg : 12552, line : "la lance, ce serait impossible, car ils sont les plus forts."});
db.iliadfr.insert({ pg : 12553, line : "Akhilleus ne réussira point dans tout ce qu'il dit. S'il accomplit"});
db.iliadfr.insert({ pg : 12554, line : "une de ses menaces, il n'accomplira point l'autre. Je vais marcher"});
db.iliadfr.insert({ pg : 12555, line : "contre lui, quand même il serait tel que le feu par ses mains."});
db.iliadfr.insert({ pg : 12556, line : "Oui! quand même il serait tel que le feu par ses mains, quand il"});
db.iliadfr.insert({ pg : 12557, line : "serait par sa vigueur tel que le feu ardent."});
db.iliadfr.insert({ pg : 12559, line : "Il parla ainsi, et aussitôt les Troiens tendirent leurs lances, et"});
db.iliadfr.insert({ pg : 12560, line : "ils se serrèrent, et une grande clameur s'éleva. Mais Phoibos"});
db.iliadfr.insert({ pg : 12561, line : "Apollôn s'approcha de Hektôr et lui dit:"});
db.iliadfr.insert({ pg : 12563, line : "-- Hektôr, ne sors point des rangs contre Akhilleus. Reste dans le"});
db.iliadfr.insert({ pg : 12564, line : "tumulte de la mêlée, de peur qu'il te perce de la lance ou de"});
db.iliadfr.insert({ pg : 12565, line : "l'épée, de loin ou de près."});
db.iliadfr.insert({ pg : 12567, line : "Il parla ainsi, et le Priamide rentra dans la foule des guerriers,"});
db.iliadfr.insert({ pg : 12568, line : "plein de crainte, dès qu'il eut entendu la voix du dieu."});
db.iliadfr.insert({ pg : 12570, line : "Et Akhilleus, vêtu de courage et de force, se jeta sur les Troiens"});
db.iliadfr.insert({ pg : 12571, line : "en poussant des cris horribles. Et il tua d'abord le brave"});
db.iliadfr.insert({ pg : 12572, line : "Iphitiôn Otryntéide, chef de nombreux guerriers, et que la nymphe"});
db.iliadfr.insert({ pg : 12573, line : "Nèis avait conçu du destructeur de citadelles Otrynteus, sous le"});
db.iliadfr.insert({ pg : 12574, line : "neigeux Tmôlos, dans la fertile Hydè. Comme il se ruait en avant,"});
db.iliadfr.insert({ pg : 12575, line : "le divin Akhilleus le frappa au milieu de la tête, et celle-ci se"});
db.iliadfr.insert({ pg : 12576, line : "fendit en deux, et Iphitiôn tomba avec bruit, et le divin"});
db.iliadfr.insert({ pg : 12577, line : "Akhilleus se glorifia ainsi:"});
db.iliadfr.insert({ pg : 12579, line : "-- Te voilà couché sur la terre, Otryntéide, le plus effrayant des"});
db.iliadfr.insert({ pg : 12580, line : "hommes! Tu es mort ici, toi qui es né non loin du lac Gygaios où"});
db.iliadfr.insert({ pg : 12581, line : "est ton champ paternel, sur les bords poissonneux du Hyllos et du"});
db.iliadfr.insert({ pg : 12582, line : "Hermos tourbillonnant."});
db.iliadfr.insert({ pg : 12584, line : "Il parla ainsi, triomphant, et le brouillard couvrit les yeux de"});
db.iliadfr.insert({ pg : 12585, line : "Iphitiôn, que les chars des Akhaiens déchirèrent de leurs roues"});
db.iliadfr.insert({ pg : 12586, line : "aux premiers rangs. Et, après lui, Akhilleus tua Dèmoléôn, brave"});
db.iliadfr.insert({ pg : 12587, line : "fils d'Antènôr. Et il lui rompit la tempe à travers le casque"});
db.iliadfr.insert({ pg : 12588, line : "d'airain, et le casque d'airain n'arrêta point le coup, et la"});
db.iliadfr.insert({ pg : 12589, line : "pointe irrésistible brisa l'os en écrasant toute la cervelle. Et"});
db.iliadfr.insert({ pg : 12590, line : "c'est ainsi qu'Akhilleus tua Dèmoléôn qui se ruait sur lui."});
db.iliadfr.insert({ pg : 12592, line : "Et comme Hippodamas, sautant de son char, fuyait, Akhilleus le"});
db.iliadfr.insert({ pg : 12593, line : "perça dans le dos d'un coup de lance. Et le Troien rendit l'âme en"});
db.iliadfr.insert({ pg : 12594, line : "mugissant comme un taureau que des jeunes hommes entraînent à"});
db.iliadfr.insert({ pg : 12595, line : "l'autel du dieu de Hélikè, de Poseidaôn qui se réjouit du"});
db.iliadfr.insert({ pg : 12596, line : "sacrifice. Et c'est ainsi qu'il mugissait et que son âme abandonna"});
db.iliadfr.insert({ pg : 12597, line : "ses ossements."});
db.iliadfr.insert({ pg : 12599, line : "Puis Akhilleus poursuivit de sa lance le divin Polydôros Priamide,"});
db.iliadfr.insert({ pg : 12600, line : "à qui son père ne permettait point de combattre, étant le dernier-"});
db.iliadfr.insert({ pg : 12601, line : "né de ses enfants et le plus aimé de tous. Et il surpassait tous"});
db.iliadfr.insert({ pg : 12602, line : "les hommes à la course. Et il courait, dans une ardeur de"});
db.iliadfr.insert({ pg : 12603, line : "jeunesse, fier de son agilité, parmi les premiers combattants;"});
db.iliadfr.insert({ pg : 12604, line : "mais le divin Akhilleus, plus rapide que lui, le frappa dans le"});
db.iliadfr.insert({ pg : 12605, line : "dos, là où les agrafes d'or attachaient le baudrier sur la double"});
db.iliadfr.insert({ pg : 12606, line : "cuirasse. Et la pointe de la lance le traversa jusqu'au nombril,"});
db.iliadfr.insert({ pg : 12607, line : "et il tomba, hurlant, sur les genoux; et une nuée noire"});
db.iliadfr.insert({ pg : 12608, line : "l'enveloppa, tandis que, courbé sur la terre, il retenait ses"});
db.iliadfr.insert({ pg : 12609, line : "entrailles à pleines mains."});
db.iliadfr.insert({ pg : 12611, line : "Hektôr, voyant son frère Polydôros renversé et retenant ses"});
db.iliadfr.insert({ pg : 12612, line : "entrailles avec ses mains, sentit un brouillard sur ses yeux, et"});
db.iliadfr.insert({ pg : 12613, line : "il ne put se résoudre à combattre plus longtemps de loin, et il"});
db.iliadfr.insert({ pg : 12614, line : "vint à Akhilleus, secouant sa lance aiguë et semblable à la"});
db.iliadfr.insert({ pg : 12615, line : "flamme. Et Akhilleus le vit, et bondit en avant, et dit en"});
db.iliadfr.insert({ pg : 12616, line : "triomphant:"});
db.iliadfr.insert({ pg : 12618, line : "-- Voici donc l'homme qui m'a déchiré le coeur et qui a tué mon"});
db.iliadfr.insert({ pg : 12619, line : "irréprochable compagnon! Ne nous évitons pas plus longtemps dans"});
db.iliadfr.insert({ pg : 12620, line : "les détours de la mêlée."});
db.iliadfr.insert({ pg : 12622, line : "Il parla ainsi, et, regardant le divin Hektôr d'un oeil sombre, il"});
db.iliadfr.insert({ pg : 12623, line : "dit:"});
db.iliadfr.insert({ pg : 12625, line : "-- Viens! approche, afin de mourir plus vite!"});
db.iliadfr.insert({ pg : 12627, line : "Et Hektôr au casque mouvant lui répondit sans crainte:"});
db.iliadfr.insert({ pg : 12629, line : "-- Pèléide, n'espère point m'épouvanter par des paroles comme un"});
db.iliadfr.insert({ pg : 12630, line : "petit enfant. Moi aussi je pourrais parler injurieusement et avec"});
db.iliadfr.insert({ pg : 12631, line : "orgueil. Je sais que tu es brave et que je ne te vaux pas; mais"});
db.iliadfr.insert({ pg : 12632, line : "nos destinées sont sur les genoux des dieux. Bien que je sois"});
db.iliadfr.insert({ pg : 12633, line : "moins fort que toi, je t'arracherai peut-être l'âme d'un coup de"});
db.iliadfr.insert({ pg : 12634, line : "ma lance. Elle aussi, elle a une pointe perçante."});
db.iliadfr.insert({ pg : 12636, line : "Il parla ainsi, et, secouant sa lance, il la jeta; mais Athènè,"});
db.iliadfr.insert({ pg : 12637, line : "d'un souffle, l'écarta de l'illustre Akhilleus, et la repoussa"});
db.iliadfr.insert({ pg : 12638, line : "vers le divin Hektôr, et la fit tomber à ses pieds. Et Akhilleus,"});
db.iliadfr.insert({ pg : 12639, line : "furieux, se rua pour le tuer, en jetant des cris horribles; mais"});
db.iliadfr.insert({ pg : 12640, line : "Apollôn enleva aisément le Priamide, comme le peut un dieu; et il"});
db.iliadfr.insert({ pg : 12641, line : "l'enveloppa d'une épaisse nuée. Et trois fois le divin Akhilleus"});
db.iliadfr.insert({ pg : 12642, line : "aux pieds rapides, se précipitant, perça cette nuée épaisse de sa"});
db.iliadfr.insert({ pg : 12643, line : "lance d'airain. Et, une quatrième fois, semblable à un daimôn, il"});
db.iliadfr.insert({ pg : 12644, line : "se rua en avant, et il cria ces paroles outrageantes:"});
db.iliadfr.insert({ pg : 12646, line : "-- Chien! de nouveau tu échappes à la mort. Elle t'a approché de"});
db.iliadfr.insert({ pg : 12647, line : "près, mais Phoibos Apollôn t'a sauvé, lui à qui tu fais des voeux"});
db.iliadfr.insert({ pg : 12648, line : "quand tu marches à travers le retentissement des lances. Je te"});
db.iliadfr.insert({ pg : 12649, line : "tuerai, si je te rencontre encore, et si quelque dieu me vient en"});
db.iliadfr.insert({ pg : 12650, line : "aide. Maintenant, je poursuivrai les autres Troiens."});
db.iliadfr.insert({ pg : 12652, line : "Ayant ainsi parlé, il perça Dryops au milieu de la gorge, et"});
db.iliadfr.insert({ pg : 12653, line : "l'homme tomba à ses pieds, et il l'abandonna. Puis, il frappa de"});
db.iliadfr.insert({ pg : 12654, line : "sa lance, au genou, le large et grand Démokhos Philétoride; puis,"});
db.iliadfr.insert({ pg : 12655, line : "avec sa forte épée, il lui arracha l'âme. Et, courant sur Laogonos"});
db.iliadfr.insert({ pg : 12656, line : "et Dardanos, fils de Bias, il les renversa tous deux de leur char,"});
db.iliadfr.insert({ pg : 12657, line : "l'un d'un coup de lance, l'autre d'un coup d'épée."});
db.iliadfr.insert({ pg : 12659, line : "Et Trôos Alastoride, pensant qu'Akhilleus l'épargnerait, ne le"});
db.iliadfr.insert({ pg : 12660, line : "tuerait point et le prendrait vivant, ayant pitié de sa jeunesse,"});
db.iliadfr.insert({ pg : 12661, line : "vint embrasser ses genoux. Et l'insensé ne savait pas que le"});
db.iliadfr.insert({ pg : 12662, line : "Pèléide était inexorable, et qu'il n'était ni doux, ni tendre,"});
db.iliadfr.insert({ pg : 12663, line : "mais féroce. Et comme le Troien embrassait ses genoux en le"});
db.iliadfr.insert({ pg : 12664, line : "suppliant, Akhilleus lui perça le foie d'un coup d'épée et le lui"});
db.iliadfr.insert({ pg : 12665, line : "arracha. Un sang noir jaillit du corps de Trôos, et le brouillard"});
db.iliadfr.insert({ pg : 12666, line : "de la mort enveloppa ses yeux."});
db.iliadfr.insert({ pg : 12668, line : "Et Akhilleus perça Moulios d'un coup de lance, de l'une à l'autre"});
db.iliadfr.insert({ pg : 12669, line : "oreille. Et de son épée à lourde poignée il fendit par le milieu"});
db.iliadfr.insert({ pg : 12670, line : "la tête de l'Agènôride Ekheklos; et l'épée fuma ruisselante de"});
db.iliadfr.insert({ pg : 12671, line : "sang, et la noire mort et la moire violente couvrirent ses yeux."});
db.iliadfr.insert({ pg : 12673, line : "Et il frappa Deukaliôn là où se réunissent les nerfs du coude. La"});
db.iliadfr.insert({ pg : 12674, line : "pointe d'airain lui engourdit le bras, et il resta immobile,"});
db.iliadfr.insert({ pg : 12675, line : "voyant la mort devant lui. Et Akhilleus, d'un coup d'épée, lui"});
db.iliadfr.insert({ pg : 12676, line : "enleva la tête, qui tomba avec le casque. La moelle jaillit des"});
db.iliadfr.insert({ pg : 12677, line : "vertèbres, et il resta étendu contre terre."});
db.iliadfr.insert({ pg : 12679, line : "Puis, Akhilleus se jeta sur le brave Rhigmos, fils de Peireus, qui"});
db.iliadfr.insert({ pg : 12680, line : "était venu de la fertile Thrèkè. Et il le perça de sa lance dans"});
db.iliadfr.insert({ pg : 12681, line : "le ventre, et l'homme tomba de son char. Et comme Aréithoos,"});
db.iliadfr.insert({ pg : 12682, line : "compagnon de Rhigmos, faisait retourner les chevaux, Akhilleus, le"});
db.iliadfr.insert({ pg : 12683, line : "perçant dans le dos d'un coup de lance, le renversa du char; et"});
db.iliadfr.insert({ pg : 12684, line : "les chevaux s'enfuirent épouvantés."});
db.iliadfr.insert({ pg : 12686, line : "De même qu'un vaste incendie gronde dans les gorges profondes"});
db.iliadfr.insert({ pg : 12687, line : "d'une montagne aride, tandis que l'épaisse forêt brûle et que le"});
db.iliadfr.insert({ pg : 12688, line : "vent secoue et roule la flamme; de même Akhilleus courait, tel"});
db.iliadfr.insert({ pg : 12689, line : "qu'un daimôn, tuant tous ceux qu'il poursuivait, et la terre noire"});
db.iliadfr.insert({ pg : 12690, line : "ruisselait de sang."});
db.iliadfr.insert({ pg : 12692, line : "De même que deux boeufs au large front foulent, accouplés, l'orge"});
db.iliadfr.insert({ pg : 12693, line : "blanche dans une aire arrondie, et que les tiges frêles laissent"});
db.iliadfr.insert({ pg : 12694, line : "échapper les graines sous les pieds des boeufs qui mugissent; de"});
db.iliadfr.insert({ pg : 12695, line : "même, sous le magnanime Akhilleus, les chevaux aux sabots massifs"});
db.iliadfr.insert({ pg : 12696, line : "foulaient les cadavres et les boucliers. Et tout l'essieu était"});
db.iliadfr.insert({ pg : 12697, line : "inondé de sang, et toutes les parois du char ruisselaient des"});
db.iliadfr.insert({ pg : 12698, line : "gouttes de sang qui jaillissaient des roues et des sabots des"});
db.iliadfr.insert({ pg : 12699, line : "chevaux. Et le Pèléide était avide de gloire, et le sang souillait"});
db.iliadfr.insert({ pg : 12700, line : "ses mains inévitables."});
db.iliadfr.insert({ pg : 12703, line : "Chant 21"});
db.iliadfr.insert({ pg : 12705, line : "Et quand les Troiens furent arrivés au gué du fleuve au beau"});
db.iliadfr.insert({ pg : 12706, line : "cours, du Xanthos tourbillonnant qu'engendra l'immortel Zeus, le"});
db.iliadfr.insert({ pg : 12707, line : "Pèléide, partageant leurs phalanges, les rejeta dans la plaine,"});
db.iliadfr.insert({ pg : 12708, line : "vers la ville, là où les Akhaiens fuyaient, la veille, bouleversés"});
db.iliadfr.insert({ pg : 12709, line : "par la fureur de l'illustre Hektôr."});
db.iliadfr.insert({ pg : 12711, line : "Et les uns se précipitaient çà et là dans leur fuite, et, pour les"});
db.iliadfr.insert({ pg : 12712, line : "arrêter, Hèrè répandit devant eux une nuée épaisse; et les autres"});
db.iliadfr.insert({ pg : 12713, line : "roulaient dans le fleuve profond aux tourbillons d'argent. Ils y"});
db.iliadfr.insert({ pg : 12714, line : "tombaient avec un grand bruit, et les eaux et les rives"});
db.iliadfr.insert({ pg : 12715, line : "retentissaient, tandis qu'ils nageaient çà et là, en poussant des"});
db.iliadfr.insert({ pg : 12716, line : "cris, au milieu des tourbillons."});
db.iliadfr.insert({ pg : 12718, line : "De même que des sauterelles volent vers un fleuve, chassées par"});
db.iliadfr.insert({ pg : 12719, line : "l'incendie, et que le feu infatigable éclate brusquement avec plus"});
db.iliadfr.insert({ pg : 12720, line : "de violence, et qu'elles se jettent, épouvantées, dans l'eau; de"});
db.iliadfr.insert({ pg : 12721, line : "même, devant Akhilleus, le cours retentissant du Xanthos aux"});
db.iliadfr.insert({ pg : 12722, line : "profonds tourbillons s'emplissait confusément de chevaux et"});
db.iliadfr.insert({ pg : 12723, line : "d'hommes."});
db.iliadfr.insert({ pg : 12725, line : "Et le divin Akhilleus, laissant sa lance sur le bord, appuyée"});
db.iliadfr.insert({ pg : 12726, line : "contre un tamaris, et ne gardant que son épée, sauta lui-même dans"});
db.iliadfr.insert({ pg : 12727, line : "le fleuve, semblable à un daimôn, et méditant un oeuvre terrible."});
db.iliadfr.insert({ pg : 12728, line : "Et il frappait tout autour de lui; et il excitait de l'épée les"});
db.iliadfr.insert({ pg : 12729, line : "gémissements des blessés, et le sang rougissait l'eau."});
db.iliadfr.insert({ pg : 12731, line : "De même que les poissons qui fuient un grand dauphin emplissent,"});
db.iliadfr.insert({ pg : 12732, line : "épouvantés, les retraites secrètes des baies tranquilles, tandis"});
db.iliadfr.insert({ pg : 12733, line : "qu'il dévore tous ceux qu'il saisit; de même les Troiens, à"});
db.iliadfr.insert({ pg : 12734, line : "travers le courant impétueux du fleuve, se cachaient sous les"});
db.iliadfr.insert({ pg : 12735, line : "rochers. Et quand Akhilleus fut las de tuer, il tira du fleuve"});
db.iliadfr.insert({ pg : 12736, line : "douze jeunes hommes vivants qui devaient mourir, en offrande à"});
db.iliadfr.insert({ pg : 12737, line : "Patroklos Ménoitiade. Et les retirant du fleuve, tremblants comme"});
db.iliadfr.insert({ pg : 12738, line : "des faons, il leur lia les mains derrière le dos avec les belles"});
db.iliadfr.insert({ pg : 12739, line : "courroies qui retenaient leurs tuniques retroussées, et les remit"});
db.iliadfr.insert({ pg : 12740, line : "à ses compagnons pour être conduits aux nefs creuses. Puis, il se"});
db.iliadfr.insert({ pg : 12741, line : "rua en avant pour tuer encore."});
db.iliadfr.insert({ pg : 12743, line : "Et il aperçut un fils du Dardanide Priamos, Lykaôn, qui sortait du"});
db.iliadfr.insert({ pg : 12744, line : "fleuve. Et il l'avait autrefois enlevé, dans une marche de nuit,"});
db.iliadfr.insert({ pg : 12745, line : "loin du verger de son père. Et Lykaôn taillait avec l'airain"});
db.iliadfr.insert({ pg : 12746, line : "tranchant les jeunes branches d'un figuier pour en faire les deux"});
db.iliadfr.insert({ pg : 12747, line : "hémicycles d'un char. Et le divin Akhilleus survint brusquement"});
db.iliadfr.insert({ pg : 12748, line : "pour son malheur, et, l'emmenant sur ses nefs, il le vendit à"});
db.iliadfr.insert({ pg : 12749, line : "Lemnos bien bâtie, et le fils de Jèsôn l'acheta. Et Êétiôn"});
db.iliadfr.insert({ pg : 12750, line : "d'Imbros, son hôte, l'ayant racheté à grand prix, l'envoya dans la"});
db.iliadfr.insert({ pg : 12751, line : "divine Arisbè, d'où il revint en secret dans la demeure"});
db.iliadfr.insert({ pg : 12752, line : "paternelle. Et, depuis onze jours, il se réjouissait avec ses"});
db.iliadfr.insert({ pg : 12753, line : "amis, étant revenu de Lemnos, et, le douzième, un dieu le rejeta"});
db.iliadfr.insert({ pg : 12754, line : "aux mains d'Akhilleus, qui devait l'envoyer violemment chez Aidès."});
db.iliadfr.insert({ pg : 12755, line : "Et dès que le divin Akhilleus aux pieds rapides l'eut reconnu qui"});
db.iliadfr.insert({ pg : 12756, line : "sortait nu du fleuve, sans casque, sans bouclier et sans lance,"});
db.iliadfr.insert({ pg : 12757, line : "car il avait jeté ses armes, étant rompu de fatigue et couvert de"});
db.iliadfr.insert({ pg : 12758, line : "sueur, aussitôt le Pèléide irrité se dit dans son esprit"});
db.iliadfr.insert({ pg : 12759, line : "magnanime:"});
db.iliadfr.insert({ pg : 12761, line : "-- Ô dieux! certes, voici un grand prodige. Sans doute aussi les"});
db.iliadfr.insert({ pg : 12762, line : "Troiens magnanimes que j'ai tués se relèveront des ténèbres"});
db.iliadfr.insert({ pg : 12763, line : "noires, puisque celui-ci, que j'avais vendu dans la sainte Lemnos,"});
db.iliadfr.insert({ pg : 12764, line : "reparaît, ayant évité la mort. La profondeur de la blanche mer qui"});
db.iliadfr.insert({ pg : 12765, line : "engloutit tant de vivants ne l'a point arrêté. Allons! il sentira"});
db.iliadfr.insert({ pg : 12766, line : "la pointe de ma lance, et je verrai et je saurai s'il s'évadera de"});
db.iliadfr.insert({ pg : 12767, line : "même, et si la terre féconde le retiendra, elle qui dompte le"});
db.iliadfr.insert({ pg : 12768, line : "brave."});
db.iliadfr.insert({ pg : 12770, line : "Il pensait ainsi, immobile. Et Lykaôn vint à lui, tremblant et"});
db.iliadfr.insert({ pg : 12771, line : "désirant embrasser ses genoux, car il voulait éviter la mort"});
db.iliadfr.insert({ pg : 12772, line : "mauvaise et la kèr noire. Et le divin Akhilleus leva sa longue"});
db.iliadfr.insert({ pg : 12773, line : "lance pour le frapper; mais Lykaôn saisit ses genoux en se"});
db.iliadfr.insert({ pg : 12774, line : "courbant, et la lance, avide de mordre la chair, par-dessus son"});
db.iliadfr.insert({ pg : 12775, line : "dos s'enfonça en terre. Et, tenant d'une main la lance aiguë qu'il"});
db.iliadfr.insert({ pg : 12776, line : "ne lâchait point, et de l'autre bras entourant les genoux"});
db.iliadfr.insert({ pg : 12777, line : "d'Akhilleus, il le supplia par ces paroles ailées:"});
db.iliadfr.insert({ pg : 12779, line : "-- J'embrasse tes genoux, Akhilleus! honore-moi, aie pitié de moi!"});
db.iliadfr.insert({ pg : 12780, line : "Je suis ton suppliant, ô race divine! J'ai goûté sous ton toit les"});
db.iliadfr.insert({ pg : 12781, line : "dons de Dèmètèr, depuis le jour où tu m'enlevas de nos beaux"});
db.iliadfr.insert({ pg : 12782, line : "vergers pour me vendre, loin de mon père et de mes amis, dans la"});
db.iliadfr.insert({ pg : 12783, line : "sainte Lemnos, où je te valu le prix de cent boeufs. Et je fus"});
db.iliadfr.insert({ pg : 12784, line : "racheté pour trois fois autant. Voici le douzième jour, après tant"});
db.iliadfr.insert({ pg : 12785, line : "de maux soufferts, que je suis rentré dans Ilios, et de nouveau la"});
db.iliadfr.insert({ pg : 12786, line : "moire fatale me remet dans tes mains! Je dois être odieux au père"});
db.iliadfr.insert({ pg : 12787, line : "Zeus, qui me livre à toi de nouveau. Sans doute elle m'a enfanté"});
db.iliadfr.insert({ pg : 12788, line : "pour peu de jours ma mère Laothoè, fille du vieux Alteus qui"});
db.iliadfr.insert({ pg : 12789, line : "commande aux belliqueux Léléges, et qui habite la haute Pèdasos"});
db.iliadfr.insert({ pg : 12790, line : "sur les bords du fleuve Satnioïs. Et Priamos posséda Laothoè parmi"});
db.iliadfr.insert({ pg : 12791, line : "toutes ses femmes, et elle eut deux fils, et tu les auras tués"});
db.iliadfr.insert({ pg : 12792, line : "tous deux. En tête des hommes de pied tu as dompté Polydôros égal"});
db.iliadfr.insert({ pg : 12793, line : "à un dieu, en le perçant de ta lance aiguë. Et voici que le"});
db.iliadfr.insert({ pg : 12794, line : "malheur est maintenant sur moi, car je n'éviterai pas tes mains,"});
db.iliadfr.insert({ pg : 12795, line : "puisqu'un dieu m'y a jeté. Mais je te le dis, et que mes paroles"});
db.iliadfr.insert({ pg : 12796, line : "soient dans ton esprit: ne me tue point, puisque je ne suis pas le"});
db.iliadfr.insert({ pg : 12797, line : "frère utérin de Hektôr, qui a tué ton compagnon doux et brave."});
db.iliadfr.insert({ pg : 12799, line : "Et l'illustre fils de Priamos parla ainsi, suppliant; mais il"});
db.iliadfr.insert({ pg : 12800, line : "entendit une voix inexorable:"});
db.iliadfr.insert({ pg : 12802, line : "-- Insensé! ne parle plus jamais du prix de ton affranchissement."});
db.iliadfr.insert({ pg : 12803, line : "Avant le jour suprême de Patroklos, il me plaisait d'épargner les"});
db.iliadfr.insert({ pg : 12804, line : "Troiens. J'en ai pris un grand nombre vivants et je les ai vendus."});
db.iliadfr.insert({ pg : 12805, line : "Maintenant, aucun des Troiens qu'un dieu me jettera dans les mains"});
db.iliadfr.insert({ pg : 12806, line : "n'évitera la mort, surtout les fils de Priamos. Ami, meurs!"});
db.iliadfr.insert({ pg : 12807, line : "Pourquoi gémir en vain? Patroklos est bien mort, qui valait"});
db.iliadfr.insert({ pg : 12808, line : "beaucoup mieux que toi. Regarde! Je suis beau et grand, je suis né"});
db.iliadfr.insert({ pg : 12809, line : "d'un noble père; une déesse m'a enfanté; et cependant la mort et"});
db.iliadfr.insert({ pg : 12810, line : "la moire violente me saisiront, le matin, le soir ou à midi, et"});
db.iliadfr.insert({ pg : 12811, line : "quelqu'un m'arrachera l'âme, soit d'un coup de lance, soit d'une"});
db.iliadfr.insert({ pg : 12812, line : "flèche."});
db.iliadfr.insert({ pg : 12814, line : "Il parla ainsi, et les genoux et le coeur manquèrent au Priamide."});
db.iliadfr.insert({ pg : 12815, line : "Et, lâchant la lance, il s'assit, les mains étendues. Et"});
db.iliadfr.insert({ pg : 12816, line : "Akhilleus, tirant son épée aiguë, le frappa au cou, près de la"});
db.iliadfr.insert({ pg : 12817, line : "clavicule, et l'airain entra tout entier. Lykaôn tomba sur la"});
db.iliadfr.insert({ pg : 12818, line : "face; un sang noir jaillit et ruissela par terre. Et Akhilleus, le"});
db.iliadfr.insert({ pg : 12819, line : "saisissant par les pieds, le jeta dans le fleuve, et il l'insulta"});
db.iliadfr.insert({ pg : 12820, line : "en paroles rapides:"});
db.iliadfr.insert({ pg : 12822, line : "-- Va! reste avec les poissons, qui boiront tranquillement le sang"});
db.iliadfr.insert({ pg : 12823, line : "de ta blessure. Ta mère ne te déposera point sur le lit funèbre,"});
db.iliadfr.insert({ pg : 12824, line : "mais le Skamandros tourbillonnant t'emportera dans la vaste mer,"});
db.iliadfr.insert({ pg : 12825, line : "et quelque poisson, sautant sur l'eau, dévorera la chair blanche"});
db.iliadfr.insert({ pg : 12826, line : "de Lykaôn dans la noire horreur de l'abîme. Périssez tous, jusqu'à"});
db.iliadfr.insert({ pg : 12827, line : "ce que nous renversions la sainte Ilios! Fuyez, et moi je vous"});
db.iliadfr.insert({ pg : 12828, line : "tuerai en vous poursuivant. Il ne vous sauvera point, le fleuve au"});
db.iliadfr.insert({ pg : 12829, line : "beau cours, aux tourbillons d'argent, à qui vous sacrifiez tant de"});
db.iliadfr.insert({ pg : 12830, line : "taureaux et tant de chevaux vivants que vous jetez dans ses"});
db.iliadfr.insert({ pg : 12831, line : "tourbillons; mais vous périrez tous d'une mort violente, jusqu'à"});
db.iliadfr.insert({ pg : 12832, line : "ce que vous ayez expié le meurtre de Patroklos et le carnage des"});
db.iliadfr.insert({ pg : 12833, line : "Akhaiens que vous avez tués, moi absent, auprès des nefs rapides."});
db.iliadfr.insert({ pg : 12835, line : "Il parla ainsi, et le fleuve irrité délibérait dans son esprit"});
db.iliadfr.insert({ pg : 12836, line : "comment il réprimerait la fureur du divin Akhilleus et"});
db.iliadfr.insert({ pg : 12837, line : "repousserait cette calamité loin des Troiens."});
db.iliadfr.insert({ pg : 12839, line : "Et le fils de Pèleus, avec sa longue lance, sauta sur Astéropaios,"});
db.iliadfr.insert({ pg : 12840, line : "fils de Pèlégôn, afin de le tuer. Et le large Axios engendra"});
db.iliadfr.insert({ pg : 12841, line : "Pèlégôn, et il avait été conçu par l'aînée des filles"});
db.iliadfr.insert({ pg : 12842, line : "d'Akessamènos, Périboia, qui s'était unie à ce fleuve aux profonds"});
db.iliadfr.insert({ pg : 12843, line : "tourbillons. Et Akhilleus courait sur Astéropaios qui, hors du"});
db.iliadfr.insert({ pg : 12844, line : "fleuve, l'attendait, deux lances aux mains; car le Xanthos, irrité"});
db.iliadfr.insert({ pg : 12845, line : "à cause des jeunes hommes qu'Akhilleus avait égorgés dans ses"});
db.iliadfr.insert({ pg : 12846, line : "eaux, avait inspiré la force et le courage au Pèlégonide. Et quand"});
db.iliadfr.insert({ pg : 12847, line : "ils se furent rencontrés, le divin Pèléide aux pieds rapides lui"});
db.iliadfr.insert({ pg : 12848, line : "parla ainsi:"});
db.iliadfr.insert({ pg : 12850, line : "-- Qui es-tu parmi les hommes, toi qui oses m'attendre? Ce sont"});
db.iliadfr.insert({ pg : 12851, line : "les fils des malheureux qui s'opposent à mon courage."});
db.iliadfr.insert({ pg : 12853, line : "Et l'illustre fils de Pèlégôn lui répondit:"});
db.iliadfr.insert({ pg : 12855, line : "-- Magnanime Pèléide, pourquoi demander quelle est ma race? Je"});
db.iliadfr.insert({ pg : 12856, line : "viens de la Paioniè fertile et lointaine, et je commande les"});
db.iliadfr.insert({ pg : 12857, line : "Paiones aux longues lances. Il y a onze jours que je suis arrivé"});
db.iliadfr.insert({ pg : 12858, line : "dans Ilios. Je descends du large fleuve Axios qui répand ses eaux"});
db.iliadfr.insert({ pg : 12859, line : "limpides sur la terre, et qui engendra l'illustre Pèlégôn; et on"});
db.iliadfr.insert({ pg : 12860, line : "dit que Pèlégôn est mon père. Maintenant, divin Akhilleus,"});
db.iliadfr.insert({ pg : 12861, line : "combattons!"});
db.iliadfr.insert({ pg : 12863, line : "Il parla ainsi, menaçant. Et le divin Akhilleus leva la lance"});
db.iliadfr.insert({ pg : 12864, line : "Pèliade, et le héros Astéropaios, de ses deux mains à la fois,"});
db.iliadfr.insert({ pg : 12865, line : "jeta ses deux lances; et l'une, frappant le bouclier, ne put le"});
db.iliadfr.insert({ pg : 12866, line : "rompre, arrêtée par la lame d'or, présent d'un dieu; et l'autre"});
db.iliadfr.insert({ pg : 12867, line : "effleura le coude du bras droit. Le sang noir jaillit, et l'arme,"});
db.iliadfr.insert({ pg : 12868, line : "avide de mordre la chair, s'enfonça en terre. Alors Akhilleus"});
db.iliadfr.insert({ pg : 12869, line : "lança sa pique rapide contre Astéropaios, voulant le tuer; mais il"});
db.iliadfr.insert({ pg : 12870, line : "le manqua, et la pique de frêne, en frémissant, s'enfonça presque"});
db.iliadfr.insert({ pg : 12871, line : "en entier dans le tertre du bord. Et le Pèléide, tirant son épée"});
db.iliadfr.insert({ pg : 12872, line : "aiguë, se jeta sur Astéropaios qui s'efforçait d'arracher du"});
db.iliadfr.insert({ pg : 12873, line : "rivage la lance d'Akhilleus. Et, trois fois, il l'ébranla pour"});
db.iliadfr.insert({ pg : 12874, line : "l'arracher, et comme il allait, une quatrième fois, tenter de"});
db.iliadfr.insert({ pg : 12875, line : "rompre la lance de frêne de l'Aiakide, celui-ci lui arracha l'âme,"});
db.iliadfr.insert({ pg : 12876, line : "l'ayant frappé dans le ventre, au nombril. Et toutes les"});
db.iliadfr.insert({ pg : 12877, line : "entrailles s'échappèrent de la plaie, et la nuit couvrit ses yeux."});
db.iliadfr.insert({ pg : 12878, line : "Et Akhilleus, se jetant sur lui, le dépouilla de ses armes, et"});
db.iliadfr.insert({ pg : 12879, line : "dit, triomphant:"});
db.iliadfr.insert({ pg : 12881, line : "-- Reste là, couché. Il n'était pas aisé pour toi de combattre les"});
db.iliadfr.insert({ pg : 12882, line : "enfants du tout-puissant Kroniôn, bien que tu sois né d'un fleuve"});
db.iliadfr.insert({ pg : 12883, line : "au large cours, et moi je me glorifie d'être de la race du grand"});
db.iliadfr.insert({ pg : 12884, line : "Zeus. Pèleus Aiakide qui commande aux nombreux Myrmidones m'a"});
db.iliadfr.insert({ pg : 12885, line : "engendré, et Zeus a engendré Aiakos. Autant Zeus est supérieur aux"});
db.iliadfr.insert({ pg : 12886, line : "fleuves qui se jettent impétueusement dans la mer, autant la race"});
db.iliadfr.insert({ pg : 12887, line : "de Zeus est supérieure à celle des fleuves. Voici un grand fleuve"});
db.iliadfr.insert({ pg : 12888, line : "auprès de toi; qu'il te sauve, s'il peut. Mais il n'est point"});
db.iliadfr.insert({ pg : 12889, line : "permis de lutter contre Zeus Kroniôn. Le roi Akhéloios lui-même ne"});
db.iliadfr.insert({ pg : 12890, line : "se compare point à Zeus, ni la grande violence du profond Okéanos"});
db.iliadfr.insert({ pg : 12891, line : "d'où sont issus toute la mer, tous les fleuves, toutes les"});
db.iliadfr.insert({ pg : 12892, line : "fontaines et toutes les sources. Mais lui-même redoute la foudre"});
db.iliadfr.insert({ pg : 12893, line : "du grand Zeus, l'horrible tonnerre qui prolonge son retentissement"});
db.iliadfr.insert({ pg : 12894, line : "dans l'Ouranos."});
db.iliadfr.insert({ pg : 12896, line : "Il parla ainsi, et arrachant du rivage sa lance d'airain, il le"});
db.iliadfr.insert({ pg : 12897, line : "laissa mort sur le sable, et baigné par l'eau noire. Et les"});
db.iliadfr.insert({ pg : 12898, line : "anguilles et les poissons l'environnaient, mangeant la graisse de"});
db.iliadfr.insert({ pg : 12899, line : "ses reins. Et Akhilleus se jeta sur les cavaliers Paiones qui"});
db.iliadfr.insert({ pg : 12900, line : "s'enfuirent le long du fleuve tourbillonnant, quand ils virent"});
db.iliadfr.insert({ pg : 12901, line : "leur brave chef, dans le rude combat, tué d'un coup d'épée par les"});
db.iliadfr.insert({ pg : 12902, line : "mains d'Akhilleus."});
db.iliadfr.insert({ pg : 12904, line : "Et il tua Thersilokos, et Mydôn, et Astypylos, et Mnèsos, et"});
db.iliadfr.insert({ pg : 12905, line : "Thrasios, et Ainios, et Orphélestès. Et le rapide Akhilleus eût"});
db.iliadfr.insert({ pg : 12906, line : "tué beaucoup d'autres Paiones, si le fleuve aux profonds"});
db.iliadfr.insert({ pg : 12907, line : "tourbillons, irrité, et semblable à un homme, ne lui eût dit du"});
db.iliadfr.insert({ pg : 12908, line : "fond d'un tourbillon:"});
db.iliadfr.insert({ pg : 12910, line : "-- Ô Akhilleus, certes, tu es très brave; mais tu égorges"});
db.iliadfr.insert({ pg : 12911, line : "affreusement les hommes, et les dieux eux-mêmes te viennent en"});
db.iliadfr.insert({ pg : 12912, line : "aide. Si le fils de Kronos te livre tous les Troiens pour que tu"});
db.iliadfr.insert({ pg : 12913, line : "les détruises, du moins, les chassant hors de mon lit, tue-les"});
db.iliadfr.insert({ pg : 12914, line : "dans la plaine. Mes belles eaux sont pleines de cadavres, et je ne"});
db.iliadfr.insert({ pg : 12915, line : "puis mener à la mer mon cours divin entravé par les morts, et tu"});
db.iliadfr.insert({ pg : 12916, line : "ne cesses de tuer. Arrête, car l'horreur me saisit, ô prince des"});
db.iliadfr.insert({ pg : 12917, line : "peuples!"});
db.iliadfr.insert({ pg : 12919, line : "Et Akhilleus aux pieds rapides lui répondit:"});
db.iliadfr.insert({ pg : 12921, line : "-- Je ferai ce que tu veux, divin Skamandros; mais je ne cesserai"});
db.iliadfr.insert({ pg : 12922, line : "point d'égorger les Troiens insolents avant de les avoir enfermés"});
db.iliadfr.insert({ pg : 12923, line : "dans leur ville, et d'avoir trouvé Hektôr face à face, afin qu'il"});
db.iliadfr.insert({ pg : 12924, line : "me tue, ou que je le tue."});
db.iliadfr.insert({ pg : 12926, line : "Il parla ainsi et se jeta comme un daimôn sur les Troiens. Et le"});
db.iliadfr.insert({ pg : 12927, line : "fleuve aux profonds tourbillons dit à Apollôn:"});
db.iliadfr.insert({ pg : 12929, line : "-- Hélas! fils de Zeus, toi qui portes l'arc d'argent, tu n'obéis"});
db.iliadfr.insert({ pg : 12930, line : "pas au Kroniôn qui t'avait commandé de venir en aide aux Troiens,"});
db.iliadfr.insert({ pg : 12931, line : "et de les protéger jusqu'au moment où le crépuscule du soir"});
db.iliadfr.insert({ pg : 12932, line : "couvrira de son ombre la terre féconde."});
db.iliadfr.insert({ pg : 12934, line : "Il parla ainsi; mais Akhilleus sauta du rivage au milieu de l'eau,"});
db.iliadfr.insert({ pg : 12935, line : "et le fleuve se gonfla en bouillonnant, et, furieux, il roula ses"});
db.iliadfr.insert({ pg : 12936, line : "eaux bouleversées, soulevant tous les cadavres dont il était"});
db.iliadfr.insert({ pg : 12937, line : "plein, et qu'avait faits Akhilleus, et les rejetant sur ses bords"});
db.iliadfr.insert({ pg : 12938, line : "en mugissant comme un taureau. Mais il sauvait ceux qui vivaient"});
db.iliadfr.insert({ pg : 12939, line : "encore, en les cachant parmi ses belles eaux, dans ses tourbillons"});
db.iliadfr.insert({ pg : 12940, line : "profonds."});
db.iliadfr.insert({ pg : 12942, line : "Et l'eau tumultueuse et terrible montait autour d'Akhilleus en"});
db.iliadfr.insert({ pg : 12943, line : "heurtant son bouclier avec fureur, et il chancelait sur ses pieds."});
db.iliadfr.insert({ pg : 12944, line : "Et, alors, il saisit des deux mains un grand orme qui, tombant"});
db.iliadfr.insert({ pg : 12945, line : "déraciné, en déchirant toute la berge, amassa ses branches"});
db.iliadfr.insert({ pg : 12946, line : "épaisses en travers du courant, et, couché tout entier, fit un"});
db.iliadfr.insert({ pg : 12947, line : "pont sur le fleuve. Et Akhilleus, sautant de là hors du gouffre,"});
db.iliadfr.insert({ pg : 12948, line : "s'élança, épouvanté, dans la plaine. Mais le grand fleuve ne"});
db.iliadfr.insert({ pg : 12949, line : "s'arrêta point, et il assombrit la cime de ses flots, afin"});
db.iliadfr.insert({ pg : 12950, line : "d'éloigner le divin Akhilleus du combat, et de reculer la chute"});
db.iliadfr.insert({ pg : 12951, line : "d'Ilios."});
db.iliadfr.insert({ pg : 12953, line : "Et le Pèléide fuyait par bonds d'un jet de lance, avec"});
db.iliadfr.insert({ pg : 12954, line : "l'impétuosité de l'aigle noir, de l'aigle chasseur, le plus fort"});
db.iliadfr.insert({ pg : 12955, line : "et le plus rapide des oiseaux. C'est ainsi qu'il fuyait. Et"});
db.iliadfr.insert({ pg : 12956, line : "l'airain retentissait horriblement sur sa poitrine; et il se"});
db.iliadfr.insert({ pg : 12957, line : "dérobait en courant, mais le fleuve le poursuivait toujours à"});
db.iliadfr.insert({ pg : 12958, line : "grand bruit."});
db.iliadfr.insert({ pg : 12960, line : "Quand un fontainier a mené, d'une source profonde, un cours d'eau"});
db.iliadfr.insert({ pg : 12961, line : "à travers les plantations et les jardins, et qu'il a écarté avec"});
db.iliadfr.insert({ pg : 12962, line : "sa houe tous les obstacles à l'écoulement, les cailloux roulent"});
db.iliadfr.insert({ pg : 12963, line : "avec le flot qui murmure, et court sur la pente, et devance le"});
db.iliadfr.insert({ pg : 12964, line : "fontainier lui-même. C'est ainsi que le fleuve pressait toujours"});
db.iliadfr.insert({ pg : 12965, line : "Akhilleus, malgré sa rapidité, car les dieux sont plus puissants"});
db.iliadfr.insert({ pg : 12966, line : "que les hommes. Et toutes les fois que le divin et rapide"});
db.iliadfr.insert({ pg : 12967, line : "Akhilleus tentait de s'arrêter, afin de voir si tous les immortels"});
db.iliadfr.insert({ pg : 12968, line : "qui habitent le large Ouranos voulaient l'épouvanter, autant de"});
db.iliadfr.insert({ pg : 12969, line : "fois l'eau du fleuve divin se déroulait par-dessus ses épaules."});
db.iliadfr.insert({ pg : 12970, line : "Et, triste dans son coeur, il bondissait vers les hauteurs; mais"});
db.iliadfr.insert({ pg : 12971, line : "le Xanthos furieux heurtait obliquement ses genoux et dérobait le"});
db.iliadfr.insert({ pg : 12972, line : "fond sous ses pieds. Et le Pèléide hurla vers le large Ouranos:"});
db.iliadfr.insert({ pg : 12974, line : "-- Père Zeus! aucun des dieux ne veut-il me délivrer de ce fleuve,"});
db.iliadfr.insert({ pg : 12975, line : "moi, misérable! Je subirais ensuite ma destinée. Certes, nul"});
db.iliadfr.insert({ pg : 12976, line : "d'entre les Ouraniens n'est plus coupable que ma mère bien-aimée"});
db.iliadfr.insert({ pg : 12977, line : "qui m'a menti, disant que je devais périr par les flèches rapides"});
db.iliadfr.insert({ pg : 12978, line : "d'Apollôn sous les murs des Troiens cuirassés. Plût aux dieux que"});
db.iliadfr.insert({ pg : 12979, line : "Hektôr, le plus brave des hommes nourris ici, m'eût tué! Un brave"});
db.iliadfr.insert({ pg : 12980, line : "au moins eût tué un brave. Et, maintenant, voici que ma destinée"});
db.iliadfr.insert({ pg : 12981, line : "est de subir une mort honteuse, étouffé dans ce grand fleuve,"});
db.iliadfr.insert({ pg : 12982, line : "comme un petit porcher qu'un torrent a noyé, tandis qu'il le"});
db.iliadfr.insert({ pg : 12983, line : "traversait par un mauvais temps!"});
db.iliadfr.insert({ pg : 12985, line : "Il parla ainsi, et aussitôt Poseidaôn et Athènè s'approchèrent de"});
db.iliadfr.insert({ pg : 12986, line : "lui sous des formes humaines; et, prenant sa main entre leurs"});
db.iliadfr.insert({ pg : 12987, line : "mains, ils le rassurèrent. Et Poseidaôn qui ébranle la terre lui"});
db.iliadfr.insert({ pg : 12988, line : "dit:"});
db.iliadfr.insert({ pg : 12990, line : "-- Pèléide, rassure-toi, et cesse de craindre. Nous te venons en"});
db.iliadfr.insert({ pg : 12991, line : "aide, Athènè et moi, et Zeus nous approuve. Ta destinée n'est"});
db.iliadfr.insert({ pg : 12992, line : "point de mourir dans ce fleuve, et tu le verras bientôt s'apaiser."});
db.iliadfr.insert({ pg : 12993, line : "Mais nous te conseillerons sagement, si tu nous obéis. Ne cesse"});
db.iliadfr.insert({ pg : 12994, line : "point d'agir de tes mains dans la rude mêlée, que tu n'aies"});
db.iliadfr.insert({ pg : 12995, line : "renfermé les Troiens dans les illustres murailles d'Ilios, ceux du"});
db.iliadfr.insert({ pg : 12996, line : "moins qui t'auront échappé. Puis, ayant arraché l'âme de Hektôr,"});
db.iliadfr.insert({ pg : 12997, line : "retourne vers les nefs. Nous te réservons une grande gloire."});
db.iliadfr.insert({ pg : 12999, line : "Ayant ainsi parlé, ils rejoignirent les immortels. Et Akhilleus,"});
db.iliadfr.insert({ pg : 13000, line : "excité par les paroles des dieux, s'élança dans la plaine où l'eau"});
db.iliadfr.insert({ pg : 13001, line : "débordait de tous côtés, soulevant les belles armes des guerriers"});
db.iliadfr.insert({ pg : 13002, line : "morts, et les cadavres aussi. Et ses genoux le soutinrent contre"});
db.iliadfr.insert({ pg : 13003, line : "le courant impétueux, et le large fleuve ne put le retenir, car"});
db.iliadfr.insert({ pg : 13004, line : "Athènè lui avait donné une grande vigueur. Mais le Skamandros"});
db.iliadfr.insert({ pg : 13005, line : "n'apaisa point sa fureur, et il s'irrita plus encore contre le"});
db.iliadfr.insert({ pg : 13006, line : "Pèléide, et, soulevant toute son onde, il appela le Simoïs à"});
db.iliadfr.insert({ pg : 13007, line : "grands cris:"});
db.iliadfr.insert({ pg : 13009, line : "-- Cher frère, brisons tous deux la vigueur de cet homme qui"});
db.iliadfr.insert({ pg : 13010, line : "renversera bientôt la grande ville du roi Priamos, car les Troiens"});
db.iliadfr.insert({ pg : 13011, line : "ne combattent plus. Viens très promptement à mon aide. Emplis-toi"});
db.iliadfr.insert({ pg : 13012, line : "de toute l'eau des sources, enfle tous les torrents, et hausse une"});
db.iliadfr.insert({ pg : 13013, line : "grande houle pleine de bruit, de troncs d'arbres et de rochers,"});
db.iliadfr.insert({ pg : 13014, line : "afin que nous arrêtions cet homme féroce qui triomphe, et ose tout"});
db.iliadfr.insert({ pg : 13015, line : "ce qu'osent les dieux. Je jure ceci: à quoi lui serviront sa"});
db.iliadfr.insert({ pg : 13016, line : "force, sa beauté et ses belles armes, quand tout cela sera couché"});
db.iliadfr.insert({ pg : 13017, line : "au fond de mon lit, sous la boue? Et, lui-même, je l'envelopperai"});
db.iliadfr.insert({ pg : 13018, line : "de sables et de limons, et les Akhaiens ne pourront recueillir ses"});
db.iliadfr.insert({ pg : 13019, line : "os, tant je les enfouirai sous la boue. Et la boue sera son"});
db.iliadfr.insert({ pg : 13020, line : "sépulcre, et quand les Akhaiens voudront l'ensevelir, il n'aura"});
db.iliadfr.insert({ pg : 13021, line : "plus besoin de tombeau!"});
db.iliadfr.insert({ pg : 13023, line : "Il parla ainsi, et sur Akhilleus il se rua tout bouillonnant de"});
db.iliadfr.insert({ pg : 13024, line : "fureur, plein de bruit, d'écume, de sang et de cadavres. Et l'onde"});
db.iliadfr.insert({ pg : 13025, line : "pourprée du fleuve tombé de Zeus se dressa, saisissant le Pèléide."});
db.iliadfr.insert({ pg : 13026, line : "Et, alors, Hèrè poussa un cri, craignant que le grand fleuve"});
db.iliadfr.insert({ pg : 13027, line : "tourbillonnant engloutît Akhilleus, et elle dit aussitôt à son"});
db.iliadfr.insert({ pg : 13028, line : "fils bien-aimé Hèphaistos"});
db.iliadfr.insert({ pg : 13030, line : "Va, Hèphaistos, mon fils! combats le Xanthos tourbillonnant que"});
db.iliadfr.insert({ pg : 13031, line : "nous t'avons donné pour adversaire. Va! allume promptement tes"});
db.iliadfr.insert({ pg : 13032, line : "flammes innombrables. Moi, j'exciterai, du sein de la mer, la"});
db.iliadfr.insert({ pg : 13033, line : "violence de Zéphyros et du tempétueux Notos, afin que l'incendie"});
db.iliadfr.insert({ pg : 13034, line : "dévore les têtes et les armes des Troiens. Et toi, brûle tous les"});
db.iliadfr.insert({ pg : 13035, line : "arbres sur les rives du Xanthos, embrase-le lui-même, et n'écoute"});
db.iliadfr.insert({ pg : 13036, line : "ni ses flatteries, ni ses menaces; mais déploie toute ta violence,"});
db.iliadfr.insert({ pg : 13037, line : "jusqu'à ce que je t'avertisse; et, alors, éteins l'incendie"});
db.iliadfr.insert({ pg : 13038, line : "infatigable."});
db.iliadfr.insert({ pg : 13040, line : "Elle parla ainsi, et Hèphaistos alluma le vaste feu qui, d'abord,"});
db.iliadfr.insert({ pg : 13041, line : "consuma dans la plaine les nombreux cadavres qu'avait faits"});
db.iliadfr.insert({ pg : 13042, line : "Akhilleus. Et toute la plaine fut desséchée, et l'eau divine fut"});
db.iliadfr.insert({ pg : 13043, line : "réprimée. De même que Boréas, aux jours d'automne, sèche les"});
db.iliadfr.insert({ pg : 13044, line : "jardins récemment arrosés et réjouit le jardinier, de même le feu"});
db.iliadfr.insert({ pg : 13045, line : "dessécha la plaine et brûla les cadavres. Puis, Hèphaistos tourna"});
db.iliadfr.insert({ pg : 13046, line : "contre le fleuve sa flamme resplendissante; et les ormes"});
db.iliadfr.insert({ pg : 13047, line : "brûlaient, et les saules, et les tamaris; et le lotos brûlait, et"});
db.iliadfr.insert({ pg : 13048, line : "le glaïeul, et le cyprès, qui abondaient tous autour du fleuve aux"});
db.iliadfr.insert({ pg : 13049, line : "belles eaux. Et les anguilles et les poissons nageaient çà et là,"});
db.iliadfr.insert({ pg : 13050, line : "ou plongeaient dans les tourbillons, poursuivis par le souffle du"});
db.iliadfr.insert({ pg : 13051, line : "sage Hèphaistos. Et la force même du fleuve fut consumée, et il"});
db.iliadfr.insert({ pg : 13052, line : "cria ainsi:"});
db.iliadfr.insert({ pg : 13054, line : "-- Hèphaistos! aucun des dieux ne peut lutter contre toi. Je ne"});
db.iliadfr.insert({ pg : 13055, line : "combattrai point tes feux brûlants. Cesse donc. Le divin Akhilleus"});
db.iliadfr.insert({ pg : 13056, line : "peut chasser tous les Troiens de leur ville. Pourquoi les secourir"});
db.iliadfr.insert({ pg : 13057, line : "et que me fait leur querelle?"});
db.iliadfr.insert({ pg : 13059, line : "Il parla ainsi, brûlant, et ses eaux limpides bouillonnaient. De"});
db.iliadfr.insert({ pg : 13060, line : "même qu'un vase bout sur un grand feu qui fond la graisse d'un"});
db.iliadfr.insert({ pg : 13061, line : "sanglier gras, tandis que la flamme du bois sec l'enveloppe; de"});
db.iliadfr.insert({ pg : 13062, line : "même le beau cours du Xanthos brûlait, et l'eau bouillonnait, ne"});
db.iliadfr.insert({ pg : 13063, line : "pouvant plus couler dans son lit, tant le souffle ardent du sage"});
db.iliadfr.insert({ pg : 13064, line : "Hèphaistos la dévorait. Alors, le Xanthos implora Hèrè en paroles"});
db.iliadfr.insert({ pg : 13065, line : "rapides:"});
db.iliadfr.insert({ pg : 13067, line : "-- Hèrè! pourquoi ton fils me tourmente-t-il ainsi? Je ne suis"});
db.iliadfr.insert({ pg : 13068, line : "point, certes, aussi coupable que les autres dieux qui secourent"});
db.iliadfr.insert({ pg : 13069, line : "les Troiens. Je m'arrêterai moi-même, si tu ordonnes à ton fils de"});
db.iliadfr.insert({ pg : 13070, line : "cesser. Et je jure aussi de ne plus retarder le dernier jour des"});
db.iliadfr.insert({ pg : 13071, line : "Troiens, quand même Troiè périrait par le feu, quand même les fils"});
db.iliadfr.insert({ pg : 13072, line : "belliqueux des Akhaiens la consumeraient tout entière!"});
db.iliadfr.insert({ pg : 13074, line : "Et la déesse Hèrè aux bras blancs, l'ayant entendu, dit aussitôt à"});
db.iliadfr.insert({ pg : 13075, line : "son fils bien-aimé Hèphaistos:"});
db.iliadfr.insert({ pg : 13077, line : "-- Hèphaistos, arrête, mon illustre fils! Il ne convient pas qu'un"});
db.iliadfr.insert({ pg : 13078, line : "dieu soit tourmenté à cause d'un homme."});
db.iliadfr.insert({ pg : 13080, line : "Elle parla ainsi, et Hèphaistos éteignit le vaste incendie et"});
db.iliadfr.insert({ pg : 13081, line : "l'eau reprit son beau cours; et la force du Xanthos étant domptée,"});
db.iliadfr.insert({ pg : 13082, line : "ils cessèrent le combat; et, bien qu'irritée, Hèrè les apaisa tous"});
db.iliadfr.insert({ pg : 13083, line : "deux."});
db.iliadfr.insert({ pg : 13085, line : "Mais, alors, une querelle terrible s'éleva parmi les autres dieux,"});
db.iliadfr.insert({ pg : 13086, line : "et leur esprit leur inspira des pensées ennemies. Et ils coururent"});
db.iliadfr.insert({ pg : 13087, line : "les uns sur les autres; et la terre large rendit un son immense;"});
db.iliadfr.insert({ pg : 13088, line : "et, au-dessus, le grand Ouranos retentit. Et Zeus, assis sur"});
db.iliadfr.insert({ pg : 13089, line : "l'Olympos, se mit à rire; et la joie emplit son coeur quand il vit"});
db.iliadfr.insert({ pg : 13090, line : "la dissension des dieux. Et ils ne retardèrent point le combat."});
db.iliadfr.insert({ pg : 13091, line : "Arès, qui rompt les boucliers, attaqua, le premier, Athènè. Et il"});
db.iliadfr.insert({ pg : 13092, line : "lui dit cette parole outrageante, en brandissant sa lance"});
db.iliadfr.insert({ pg : 13093, line : "d'airain:"});
db.iliadfr.insert({ pg : 13095, line : "-- Mouche à chien! pourquoi pousses-tu les dieux au combat? Tu as"});
db.iliadfr.insert({ pg : 13096, line : "une audace insatiable et un esprit toujours violent. Ne te"});
db.iliadfr.insert({ pg : 13097, line : "souvient-il plus que tu as excité le Tydéide Diomèdès contre moi,"});
db.iliadfr.insert({ pg : 13098, line : "et que tu as conduit sa lance et déchiré mon beau corps? Je pense"});
db.iliadfr.insert({ pg : 13099, line : "que tu vas expier tous les maux que tu m'as causés."});
db.iliadfr.insert({ pg : 13101, line : "Il parla ainsi, et il frappa l'horrible aigide à franges d'or qui"});
db.iliadfr.insert({ pg : 13102, line : "ne craint même point la foudre de Zeus. C'est là que le sanglant"});
db.iliadfr.insert({ pg : 13103, line : "Arès frappa de sa longue lance la déesse. Et celle-ci, reculant,"});
db.iliadfr.insert({ pg : 13104, line : "saisit, de sa main puissante, un rocher noir, âpre, immense, qui"});
db.iliadfr.insert({ pg : 13105, line : "gisait dans la plaine, et dont les anciens hommes avaient fait la"});
db.iliadfr.insert({ pg : 13106, line : "borne d'un champ. Elle en frappa le terrible Arès à la gorge et"});
db.iliadfr.insert({ pg : 13107, line : "rompit ses forces. Et il tomba, couvrant de son corps sept"});
db.iliadfr.insert({ pg : 13108, line : "arpents; et ses cheveux furent souillés de poussière, et ses armes"});
db.iliadfr.insert({ pg : 13109, line : "retentirent sur lui. Et Pallas Athènè rit et l'insulta"});
db.iliadfr.insert({ pg : 13110, line : "orgueilleusement en paroles ailées:"});
db.iliadfr.insert({ pg : 13112, line : "Insensé, qui luttes contre moi, ne sais-tu pas que je me glorifie"});
db.iliadfr.insert({ pg : 13113, line : "d'être beaucoup plus puissante que toi? C'est ainsi que les"});
db.iliadfr.insert({ pg : 13114, line : "Érinnyes vengent ta mère qui te punit, dans sa colère, d'avoir"});
db.iliadfr.insert({ pg : 13115, line : "abandonné les Akhaiens pour secourir les Troiens insolents."});
db.iliadfr.insert({ pg : 13117, line : "Ayant ainsi parlé, elle détourna ses yeux splendides. Et voici"});
db.iliadfr.insert({ pg : 13118, line : "qu'Aphroditè, la fille de Zeus, conduisait par la main, hors de la"});
db.iliadfr.insert({ pg : 13119, line : "mêlée, Arès respirant à peine et recueillant ses esprits. Et la"});
db.iliadfr.insert({ pg : 13120, line : "déesse Hèrè aux bras blancs, l'ayant vue, dit à Athènè ces paroles"});
db.iliadfr.insert({ pg : 13121, line : "ailées:"});
db.iliadfr.insert({ pg : 13123, line : "-- Athènè, fille de Zeus tempétueux, vois-tu cette mouche à chien"});
db.iliadfr.insert({ pg : 13124, line : "qui emmène, hors de la mêlée, Arès, le fléau des vivants?"});
db.iliadfr.insert({ pg : 13125, line : "Poursuis-la."});
db.iliadfr.insert({ pg : 13127, line : "Elle parla ainsi, et Athènè, pleine de joie, se jeta sur"});
db.iliadfr.insert({ pg : 13128, line : "Aphroditè, et, la frappant de sa forte main sur la poitrine, elle"});
db.iliadfr.insert({ pg : 13129, line : "fit fléchir ses genoux et son coeur."});
db.iliadfr.insert({ pg : 13131, line : "Arès et Aphroditè restèrent ainsi, étendus tous deux sur la terre"});
db.iliadfr.insert({ pg : 13132, line : "féconde; et Athènè les insulta par ces paroles ailées:"});
db.iliadfr.insert({ pg : 13134, line : "-- Que ne sont-ils ainsi, tous les alliés des Troiens qui"});
db.iliadfr.insert({ pg : 13135, line : "combattent les Akhaiens cuirassés! Que n'ont-ils tous l'audace"});
db.iliadfr.insert({ pg : 13136, line : "d'Aphroditè qui, bravant ma force, a secouru Arès! Bientôt nous"});
db.iliadfr.insert({ pg : 13137, line : "cesserions de combattre, après avoir saccagé la haute citadelle"});
db.iliadfr.insert({ pg : 13138, line : "d'Ilios."});
db.iliadfr.insert({ pg : 13140, line : "Elle parla ainsi, et la déesse Hèrè aux bras blancs rit. Et le"});
db.iliadfr.insert({ pg : 13141, line : "puissant qui ébranle la terre dit à Apollôn:"});
db.iliadfr.insert({ pg : 13143, line : "-- Phoibos, pourquoi restons-nous éloignés l'un de l'autre? Il ne"});
db.iliadfr.insert({ pg : 13144, line : "convient point, quand les autres dieux sont aux mains, que nous"});
db.iliadfr.insert({ pg : 13145, line : "retournions, sans combat, dans l'Ouranos, dans la demeure d'airain"});
db.iliadfr.insert({ pg : 13146, line : "de Zeus. Commence, car tu es le plus jeune, et il serait honteux à"});
db.iliadfr.insert({ pg : 13147, line : "moi de t'attaquer, puisque je suis l'aîné et que je sais plus de"});
db.iliadfr.insert({ pg : 13148, line : "choses. Insensé! as-tu donc un coeur tellement oublieux, et ne te"});
db.iliadfr.insert({ pg : 13149, line : "souvient-il plus des maux que nous avons subis à Ilios, quand,"});
db.iliadfr.insert({ pg : 13150, line : "seuls d'entre les dieux, exilés par Zeus, il fallut servir"});
db.iliadfr.insert({ pg : 13151, line : "l'insolent Laomédôn pendant une année? Une récompense nous fut"});
db.iliadfr.insert({ pg : 13152, line : "promise, et il nous commandait. Et j'entourai d'une haute et belle"});
db.iliadfr.insert({ pg : 13153, line : "muraille la ville des Troiens, afin qu'elle fût inexpugnable; et"});
db.iliadfr.insert({ pg : 13154, line : "toi, Phoibos, tu menais paître, sur les nombreuses cimes de l'Ida"});
db.iliadfr.insert({ pg : 13155, line : "couvert de forêts, les boeufs aux pieds tors et aux cornes"});
db.iliadfr.insert({ pg : 13156, line : "recourbées. Mais quand les Heures charmantes amenèrent le jour de"});
db.iliadfr.insert({ pg : 13157, line : "la récompense, le parjure Laomédôn nous la refusa, nous chassant"});
db.iliadfr.insert({ pg : 13158, line : "avec outrage. Même, il te menaça de te lier les mains et les"});
db.iliadfr.insert({ pg : 13159, line : "pieds, et de te vendre dans les îles lointaines. Et il jura aussi"});
db.iliadfr.insert({ pg : 13160, line : "de nous couper les oreilles avec l'airain. Et nous partîmes,"});
db.iliadfr.insert({ pg : 13161, line : "irrités dans l'âme, à cause de la récompense promise qu'il nous"});
db.iliadfr.insert({ pg : 13162, line : "refusait. Est-ce de cela que tu es reconnaissant à son peuple? Et"});
db.iliadfr.insert({ pg : 13163, line : "ne devrais-tu pas te joindre à nous pour exterminer ces Troiens"});
db.iliadfr.insert({ pg : 13164, line : "parjures, eux, leurs enfants et leurs femmes?"});
db.iliadfr.insert({ pg : 13166, line : "Et le royal archer Apollôn lui répondit:"});
db.iliadfr.insert({ pg : 13168, line : "-- Poseidaôn qui ébranles la terre, tu me nommerais insensé, si je"});
db.iliadfr.insert({ pg : 13169, line : "combattais contre toi pour les hommes misérables qui verdissent un"});
db.iliadfr.insert({ pg : 13170, line : "jour semblables aux feuilles, et qui mangent les fruits de la"});
db.iliadfr.insert({ pg : 13171, line : "terre, et qui se flétrissent et meurent bientôt. Ne combattons"});
db.iliadfr.insert({ pg : 13172, line : "point, et laissons-les lutter entre eux."});
db.iliadfr.insert({ pg : 13174, line : "Il parla ainsi et s'éloigna, ne voulant point, par respect,"});
db.iliadfr.insert({ pg : 13175, line : "combattre le frère de son père. Et la vénérable Artémis, sa soeur,"});
db.iliadfr.insert({ pg : 13176, line : "chasseresse de bêtes fauves, lui adressa ces paroles injurieuses:"});
db.iliadfr.insert({ pg : 13178, line : "-- Tu fuis, ô archer! et tu laisses la victoire à Poseidaôn?"});
db.iliadfr.insert({ pg : 13179, line : "Lâche, pourquoi portes-tu un arc inutile? Je ne t'entendrai plus"});
db.iliadfr.insert({ pg : 13180, line : "désormais, dans les demeures paternelles, te vanter comme"});
db.iliadfr.insert({ pg : 13181, line : "auparavant, au milieu des dieux immortels, de combattre Poseidaôn"});
db.iliadfr.insert({ pg : 13182, line : "à forces égales!"});
db.iliadfr.insert({ pg : 13184, line : "Elle parla ainsi, et l'archer Apollôn ne lui répondit pas; mais la"});
db.iliadfr.insert({ pg : 13185, line : "vénérable épouse de Zeus, pleine de colère, insulta de ces paroles"});
db.iliadfr.insert({ pg : 13186, line : "injurieuses Artémis qui se réjouit de ses flèches:"});
db.iliadfr.insert({ pg : 13188, line : "-- Chienne hargneuse, comment oses-tu me tenir tête? Il te sera"});
db.iliadfr.insert({ pg : 13189, line : "difficile de me résister, bien que tu lances des flèches et que tu"});
db.iliadfr.insert({ pg : 13190, line : "sois comme une lionne pour les femmes que Zeus te permet de tuer à"});
db.iliadfr.insert({ pg : 13191, line : "ton gré. Il est plus aisé de percer, sur les montagnes, les bêtes"});
db.iliadfr.insert({ pg : 13192, line : "fauves et les biches sauvages que de lutter contre plus puissant"});
db.iliadfr.insert({ pg : 13193, line : "que soi. Mais si tu veux tenter le combat, viens! et tu sauras"});
db.iliadfr.insert({ pg : 13194, line : "combien ma force est supérieure à la tienne, bien que tu oses me"});
db.iliadfr.insert({ pg : 13195, line : "tenir tête!"});
db.iliadfr.insert({ pg : 13197, line : "Elle parla ainsi, et saisissant d'une main les deux mains"});
db.iliadfr.insert({ pg : 13198, line : "d'Artémis, de l'autre elle lui arracha le carquois des épaules, et"});
db.iliadfr.insert({ pg : 13199, line : "elle l'en souffleta en riant. Et comme Artémis s'agitait çà et là,"});
db.iliadfr.insert({ pg : 13200, line : "les flèches rapides se répandirent de tous côtés. Et Artémis"});
db.iliadfr.insert({ pg : 13201, line : "s'envola, pleurante, comme une colombe qui, loin d'un épervier, se"});
db.iliadfr.insert({ pg : 13202, line : "réfugie sous une roche creuse, car sa destinée n'est point de"});
db.iliadfr.insert({ pg : 13203, line : "périr. Ainsi, pleurante, elle s'enfuit, abandonnant son arc."});
db.iliadfr.insert({ pg : 13205, line : "Alors, le messager, tueur d'Argos, dit à Lètô:"});
db.iliadfr.insert({ pg : 13207, line : "-- Lètô, je ne combattrai point contre toi. Il est dangereux d'en"});
db.iliadfr.insert({ pg : 13208, line : "venir aux mains avec les épouses de Zeus qui amasse les nuées."});
db.iliadfr.insert({ pg : 13209, line : "Hâte-toi, et va te vanter parmi les dieux immortels de m'avoir"});
db.iliadfr.insert({ pg : 13210, line : "dompté par ta force."});
db.iliadfr.insert({ pg : 13212, line : "Il parla ainsi; et Lètô, ramassant l'arc et les flèches éparses"});
db.iliadfr.insert({ pg : 13213, line : "dans la poussière, et les emportant, suivit sa fille. Et celle-ci"});
db.iliadfr.insert({ pg : 13214, line : "parvint à l'Olympos, à la demeure d'airain de Zeus. Et, pleurante,"});
db.iliadfr.insert({ pg : 13215, line : "elle s'assit sur les genoux de son père, et son péplos ambroisien"});
db.iliadfr.insert({ pg : 13216, line : "frémissait. Et le père Kronide lui demanda, en souriant doucement:"});
db.iliadfr.insert({ pg : 13218, line : "-- Chère fille, qui d'entre les dieux t'a maltraitée ainsi"});
db.iliadfr.insert({ pg : 13219, line : "témérairement, comme si tu avais commis une faute devant tous?"});
db.iliadfr.insert({ pg : 13221, line : "Et Artémis à la belle couronne lui répondit:"});
db.iliadfr.insert({ pg : 13223, line : "-- Père, c'est ton épouse, Hèrè aux bras blancs, qui m'a frappée,"});
db.iliadfr.insert({ pg : 13224, line : "elle qui répand sans cesse la dissension parmi les immortels."});
db.iliadfr.insert({ pg : 13226, line : "Et tandis qu'ils se parlaient ainsi, Phoibos Apollôn descendit"});
db.iliadfr.insert({ pg : 13227, line : "dans la sainte Ilios, car il craignait que les Danaens ne"});
db.iliadfr.insert({ pg : 13228, line : "renversassent ses hautes murailles avant le jour fatal. Et les"});
db.iliadfr.insert({ pg : 13229, line : "autres dieux éternels retournèrent dans l'Olympos, les uns irrités"});
db.iliadfr.insert({ pg : 13230, line : "et les autres triomphants; et ils s'assirent auprès du père qui"});
db.iliadfr.insert({ pg : 13231, line : "amasse les nuées."});
db.iliadfr.insert({ pg : 13232, line : "Mais Akhilleus bouleversait les Troiens et leurs chevaux aux"});
db.iliadfr.insert({ pg : 13233, line : "sabots massifs. De même que la fumée monte d'une ville qui brûle,"});
db.iliadfr.insert({ pg : 13234, line : "jusque dans le large Ouranos; car la colère des dieux est sur elle"});
db.iliadfr.insert({ pg : 13235, line : "et accable de maux tous ses habitants; de même Akhilleus accablait"});
db.iliadfr.insert({ pg : 13236, line : "les Troiens."});
db.iliadfr.insert({ pg : 13238, line : "Et le vieux Priamos, debout sur une haute tour, reconnut le féroce"});
db.iliadfr.insert({ pg : 13239, line : "Akhilleus bouleversant et chassant devant lui les phalanges"});
db.iliadfr.insert({ pg : 13240, line : "Troiennes qui ne lui résistaient plus. Et il descendit de la tour"});
db.iliadfr.insert({ pg : 13241, line : "en se lamentant, et il dit aux gardes illustres des portes:"});
db.iliadfr.insert({ pg : 13243, line : "-- Tenez les portes ouvertes, tant que les peuples mis en fuite"});
db.iliadfr.insert({ pg : 13244, line : "accourront vers la ville. Certes, voici qu'Akhilleus les a"});
db.iliadfr.insert({ pg : 13245, line : "bouleversés et qu'il approche; mais dès que les phalanges"});
db.iliadfr.insert({ pg : 13246, line : "respireront derrière les murailles, refermez les battants massifs,"});
db.iliadfr.insert({ pg : 13247, line : "car je crains que cet homme désastreux se rue dans nos murs."});
db.iliadfr.insert({ pg : 13249, line : "Il parla ainsi, et ils ouvrirent les portes en retirant les"});
db.iliadfr.insert({ pg : 13250, line : "barrières, et ils offrirent le salut aux phalanges. Et Apollôn"});
db.iliadfr.insert({ pg : 13251, line : "s'élança au-devant des Troiens pour les secourir. Et ceux-ci, vers"});
db.iliadfr.insert({ pg : 13252, line : "les hautes murailles et la ville, dévorés de soif et couverts de"});
db.iliadfr.insert({ pg : 13253, line : "poussière, fuyaient. Et, furieux, Akhilleus les poursuivait de sa"});
db.iliadfr.insert({ pg : 13254, line : "lance, le coeur toujours plein de rage et du désir de la gloire."});
db.iliadfr.insert({ pg : 13256, line : "Alors, sans doute, les fils des Akhaiens eussent pris Troiè aux"});
db.iliadfr.insert({ pg : 13257, line : "portes élevées, si Phoibos Apollôn n'eût excité le divin Agènôr,"});
db.iliadfr.insert({ pg : 13258, line : "brave et irréprochable fils d'Antènôr. Et il lui versa l'audace"});
db.iliadfr.insert({ pg : 13259, line : "dans le coeur, et pour le sauver des lourdes mains de la mort, il"});
db.iliadfr.insert({ pg : 13260, line : "se tint auprès, appuyé contre un hêtre et enveloppé d'un épais"});
db.iliadfr.insert({ pg : 13261, line : "brouillard."});
db.iliadfr.insert({ pg : 13263, line : "Mais dès qu'Agènôr eut reconnu le destructeur de citadelles"});
db.iliadfr.insert({ pg : 13264, line : "Akhilleus, il s'arrêta, roulant mille pensées dans son esprit, et"});
db.iliadfr.insert({ pg : 13265, line : "il se dit dans son brave coeur, en gémissant:"});
db.iliadfr.insert({ pg : 13267, line : "-- Hélas! fuirai-je devant le brave Akhilleus, comme tous ceux-ci"});
db.iliadfr.insert({ pg : 13268, line : "dans leur épouvante? Il me saisira et me tuera comme un lâche que"});
db.iliadfr.insert({ pg : 13269, line : "je serai. Mais si, les laissant se disperser devant le Pèléide"});
db.iliadfr.insert({ pg : 13270, line : "Akhilleus, je fuyais à travers la plaine d'Ilios jusqu'aux cimes"});
db.iliadfr.insert({ pg : 13271, line : "de l'Ida, je m'y cacherais au milieu des taillis épais; et, le"});
db.iliadfr.insert({ pg : 13272, line : "soir, après avoir lavé mes sueurs au fleuve, je reviendrais à"});
db.iliadfr.insert({ pg : 13273, line : "Ilios. Mais pourquoi mon esprit délibère-t-il ainsi? Il me verra"});
db.iliadfr.insert({ pg : 13274, line : "quand je fuirai à travers la plaine, et, me poursuivant de ses"});
db.iliadfr.insert({ pg : 13275, line : "pieds rapides, il me saisira. Et alors je n'éviterai plus la mort"});
db.iliadfr.insert({ pg : 13276, line : "et les kères, car il est bien plus fort que tous les autres"});
db.iliadfr.insert({ pg : 13277, line : "hommes. Pourquoi n'irais-je pas à sa rencontre devant la ville?"});
db.iliadfr.insert({ pg : 13278, line : "Sans doute son corps est vulnérable à l'airain aigu, quoique le"});
db.iliadfr.insert({ pg : 13279, line : "Kronide Zeus lui donne la victoire."});
db.iliadfr.insert({ pg : 13281, line : "Ayant ainsi parlé, et son brave coeur l'excitant à combattre, il"});
db.iliadfr.insert({ pg : 13282, line : "attendit Akhilleus. De même qu'une panthère qui, du fond d'une"});
db.iliadfr.insert({ pg : 13283, line : "épaisse forêt, bondit, au-devant du chasseur, et que les"});
db.iliadfr.insert({ pg : 13284, line : "aboiements des chiens ne troublent ni n'épouvantent; et qui,"});
db.iliadfr.insert({ pg : 13285, line : "blessée d'un trait ou de l'épée, ou même percée de la lance, ne"});
db.iliadfr.insert({ pg : 13286, line : "recule point avant qu'elle ait déchiré son ennemi ou qu'il l'ait"});
db.iliadfr.insert({ pg : 13287, line : "tuée; de même le fils de l'illustre Antènôr, le divin Agènôr, ne"});
db.iliadfr.insert({ pg : 13288, line : "voulait point reculer avant de combattre Akhilleus. Et, tendant"});
db.iliadfr.insert({ pg : 13289, line : "son bouclier devant lui, et brandissant sa lance, il s'écria:"});
db.iliadfr.insert({ pg : 13291, line : "-- Certes, tu as espéré trop tôt, illustre Akhilleus, que tu"});
db.iliadfr.insert({ pg : 13292, line : "renverserais aujourd'hui la ville des braves Troiens. Insensé! tu"});
db.iliadfr.insert({ pg : 13293, line : "subiras encore bien des maux pour cela. Nous sommes, dans Ilios,"});
db.iliadfr.insert({ pg : 13294, line : "un grand nombre d'hommes courageux qui saurons défendre nos"});
db.iliadfr.insert({ pg : 13295, line : "parents bien-aimés, nos femmes et nos enfants; et c'est ici que tu"});
db.iliadfr.insert({ pg : 13296, line : "subiras ta destinée, bien que tu sois un guerrier terrible et"});
db.iliadfr.insert({ pg : 13297, line : "plein d'audace."});
db.iliadfr.insert({ pg : 13299, line : "Il parla ainsi, et lança sa pique aiguë d'une main vigoureuse. Et"});
db.iliadfr.insert({ pg : 13300, line : "il frappa la jambe d'Akhilleus, au-dessous du genou. Et l'airain"});
db.iliadfr.insert({ pg : 13301, line : "résonna contre l'étain récemment forgé de la knèmide qui repoussa"});
db.iliadfr.insert({ pg : 13302, line : "le coup, car elle était le présent d'un dieu. Et le Pèléide se"});
db.iliadfr.insert({ pg : 13303, line : "jeta sur le divin Agènôr. Mais Apollôn lui refusa la victoire, car"});
db.iliadfr.insert({ pg : 13304, line : "il lui enleva l'Anténoride en le couvrant d'un brouillard épais,"});
db.iliadfr.insert({ pg : 13305, line : "et il le retira sain et sauf du combat. Puis il détourna par une"});
db.iliadfr.insert({ pg : 13306, line : "ruse le Pèléide des Troiens, en se tenant devant lui, sous la"});
db.iliadfr.insert({ pg : 13307, line : "forme d'Agènôr. Et il le fuyait, se laissant poursuivre à travers"});
db.iliadfr.insert({ pg : 13308, line : "la plaine fertile et le long du Skamandros tourbillonnant, et le"});
db.iliadfr.insert({ pg : 13309, line : "devançant à peine pour l'égarer. Et, pendant ce temps, les Troiens"});
db.iliadfr.insert({ pg : 13310, line : "épouvantés rentraient en foule dans Ilios qui s'en emplissait. Et"});
db.iliadfr.insert({ pg : 13311, line : "ils ne s'arrêtaient point hors de la ville et des murs, pour"});
db.iliadfr.insert({ pg : 13312, line : "savoir qui avait péri ou qui fuyait; mais ils s'engloutissaient"});
db.iliadfr.insert({ pg : 13313, line : "ardemment dans Ilios, tous ceux que leurs pieds et leurs genoux"});
db.iliadfr.insert({ pg : 13314, line : "avaient sauvés."});
db.iliadfr.insert({ pg : 13317, line : "Chant 22"});
db.iliadfr.insert({ pg : 13319, line : "Ainsi les Troiens, chassés comme des faons, rentraient dans la"});
db.iliadfr.insert({ pg : 13320, line : "ville. Et ils séchaient leur sueur, et ils buvaient, apaisant leur"});
db.iliadfr.insert({ pg : 13321, line : "soif. Et les Akhaiens approchaient des murs, en lignes serrées et"});
db.iliadfr.insert({ pg : 13322, line : "le bouclier aux épaules. Mais la moire fatale fit que Hektôr resta"});
db.iliadfr.insert({ pg : 13323, line : "devant Ilios et les portes Skaies. Et Phoibos Apollôn dit au"});
db.iliadfr.insert({ pg : 13324, line : "Pèléide:"});
db.iliadfr.insert({ pg : 13326, line : "-- Pèléide aux pieds rapides, toi qui n'es qu'un mortel, pourquoi"});
db.iliadfr.insert({ pg : 13327, line : "poursuis-tu un dieu immortel? Ne vois-tu pas que je suis un dieu?"});
db.iliadfr.insert({ pg : 13328, line : "Mais ta fureur n'a point de fin. Ne songes-tu donc plus aux"});
db.iliadfr.insert({ pg : 13329, line : "Troiens que tu poursuivais, et qui se sont enfermés dans leur"});
db.iliadfr.insert({ pg : 13330, line : "ville, tandis que tu t'écartais de ce côté? Cependant tu ne me"});
db.iliadfr.insert({ pg : 13331, line : "tueras point, car je ne suis pas mortel."});
db.iliadfr.insert({ pg : 13333, line : "Et Akhilleus aux pieds rapides lui répondit, plein de colère:"});
db.iliadfr.insert({ pg : 13335, line : "-- Ô Apollôn, le plus funeste de tous les dieux, tu m'as aveuglé"});
db.iliadfr.insert({ pg : 13336, line : "en m'écartant des murailles! Sans doute, de nombreux Troiens"});
db.iliadfr.insert({ pg : 13337, line : "auraient encore mordu la terre avant de rentrer dans Ilios, et tu"});
db.iliadfr.insert({ pg : 13338, line : "m'as enlevé une grande gloire. Tu les as sauvés aisément, ne"});
db.iliadfr.insert({ pg : 13339, line : "redoutant point ma vengeance. Mais, certes, je me vengerais de"});
db.iliadfr.insert({ pg : 13340, line : "toi, si je le pouvais!"});
db.iliadfr.insert({ pg : 13342, line : "Ayant ainsi parlé, il s'élança vers la ville, en méditant de"});
db.iliadfr.insert({ pg : 13343, line : "grandes actions, tel qu'un cheval victorieux qui emporte aisément"});
db.iliadfr.insert({ pg : 13344, line : "un char dans la plaine. Ainsi Akhilleus agitait rapidement ses"});
db.iliadfr.insert({ pg : 13345, line : "pieds et ses genoux. Et le vieux Priamos l'aperçut le premier, se"});
db.iliadfr.insert({ pg : 13346, line : "ruant à travers la plaine, et resplendissant comme l'étoile"});
db.iliadfr.insert({ pg : 13347, line : "caniculaire dont les rayons éclatent parmi les astres innombrables"});
db.iliadfr.insert({ pg : 13348, line : "de la nuit, et qu'on nomme le chien d'Oriôn. Et c'est la plus"});
db.iliadfr.insert({ pg : 13349, line : "éclatante des étoiles, mais c'est aussi un signe funeste qui"});
db.iliadfr.insert({ pg : 13350, line : "présage une fièvre ardente aux misérables hommes mortels. Et"});
db.iliadfr.insert({ pg : 13351, line : "l'airain resplendissait ainsi autour de la poitrine d'Akhilleus"});
db.iliadfr.insert({ pg : 13352, line : "qui accourait."});
db.iliadfr.insert({ pg : 13354, line : "Et le vieillard se lamentait en se frappant la tête, et il levait"});
db.iliadfr.insert({ pg : 13355, line : "ses mains, et il pleurait, poussant des cris et suppliant son fils"});
db.iliadfr.insert({ pg : 13356, line : "bien-aimé. Et celui-ci était debout devant les portes, plein du"});
db.iliadfr.insert({ pg : 13357, line : "désir de combattre Akhilleus. Et le vieillard, les mains étendues,"});
db.iliadfr.insert({ pg : 13358, line : "lui dit d'une voix lamentable:"});
db.iliadfr.insert({ pg : 13360, line : "-- Hektôr, mon fils bien-aimé, n'attends point cet homme, étant"});
db.iliadfr.insert({ pg : 13361, line : "seul et loin des tiens, de peur que, tué par le Pèléiôn, tu ne"});
db.iliadfr.insert({ pg : 13362, line : "subisses ta destinée, car il est bien plus fort que toi. Ah! le"});
db.iliadfr.insert({ pg : 13363, line : "misérable, que n'est-il aussi cher aux dieux qu'à moi! Bientôt les"});
db.iliadfr.insert({ pg : 13364, line : "chiens et les oiseaux le dévoreraient étendu contre terre, et ma"});
db.iliadfr.insert({ pg : 13365, line : "douleur affreuse serait apaisée. De combien de braves enfants ne"});
db.iliadfr.insert({ pg : 13366, line : "m'a-t-il point privé, en les tuant, ou en les vendant aux îles"});
db.iliadfr.insert({ pg : 13367, line : "lointaines! Et je ne vois point, au milieu des Troiens rentrés"});
db.iliadfr.insert({ pg : 13368, line : "dans Ilios, mes deux fils Lykaôn et Polydôros, qu'a enfantés"});
db.iliadfr.insert({ pg : 13369, line : "Laothoè, la plus noble des femmes. S'ils sont vivants sous les"});
db.iliadfr.insert({ pg : 13370, line : "tentes, certes, nous les rachèterons avec de l'or et de l'airain,"});
db.iliadfr.insert({ pg : 13371, line : "car j'en ai beaucoup, et le vieux et illustre Altès en a beaucoup"});
db.iliadfr.insert({ pg : 13372, line : "donné à sa fille; mais s'ils sont morts, leur mère et moi qui les"});
db.iliadfr.insert({ pg : 13373, line : "avons engendrés, nous les pleurerons jusque dans les demeures"});
db.iliadfr.insert({ pg : 13374, line : "d'Aidès! Mais la douleur de nos peuples sera bien moindre si tu"});
db.iliadfr.insert({ pg : 13375, line : "n'es pas dompté par Akhilleus. Mon fils, rentre à la hâte dans nos"});
db.iliadfr.insert({ pg : 13376, line : "murs, pour le salut des Troiens et des Troiennes. Ne donne pas une"});
db.iliadfr.insert({ pg : 13377, line : "telle gloire au Pèléide, et ne te prive pas de la douce vie. Aie"});
db.iliadfr.insert({ pg : 13378, line : "pitié de moi, malheureux, qui vis encore, et à qui le père Zeus"});
db.iliadfr.insert({ pg : 13379, line : "réserve une affreuse destinée aux limites de la vieillesse, ayant"});
db.iliadfr.insert({ pg : 13380, line : "vu tous les maux m'accabler: mes fils tués, mes filles enlevées,"});
db.iliadfr.insert({ pg : 13381, line : "mes foyers renversés, mes petits-enfants écrasés contre terre et"});
db.iliadfr.insert({ pg : 13382, line : "les femmes de mes fils entraînées par les mains inexorables des"});
db.iliadfr.insert({ pg : 13383, line : "Akhaiens! Et moi-même, le dernier, les chiens mangeurs de chair"});
db.iliadfr.insert({ pg : 13384, line : "crue me déchireront sous mes portiques, après que j'aurai été"});
db.iliadfr.insert({ pg : 13385, line : "frappé de l'airain, ou qu'une lance m'aura arraché l'âme. Et ces"});
db.iliadfr.insert({ pg : 13386, line : "chiens, gardiens de mon seuil et nourris de ma table dans mes"});
db.iliadfr.insert({ pg : 13387, line : "demeures, furieux, et ayant bu tout mon sang, se coucheront sous"});
db.iliadfr.insert({ pg : 13388, line : "mes portiques! On peut regarder un jeune homme percé de l'airain"});
db.iliadfr.insert({ pg : 13389, line : "aigu et couché mort dans la mêlée, car il est toujours beau, bien"});
db.iliadfr.insert({ pg : 13390, line : "qu'il soit nu; mais une barbe blanche et les choses de la pudeur"});
db.iliadfr.insert({ pg : 13391, line : "déchirées par les chiens, c'est la plus misérable des destinées"});
db.iliadfr.insert({ pg : 13392, line : "pour les misérables mortels!"});
db.iliadfr.insert({ pg : 13394, line : "Le vieillard parla ainsi, et il arrachait ses cheveux blancs; mais"});
db.iliadfr.insert({ pg : 13395, line : "il ne fléchissait point l'âme de Hektôr. Et voici que sa mère"});
db.iliadfr.insert({ pg : 13396, line : "gémissait et pleurait, et que, découvrant son sein et soulevant"});
db.iliadfr.insert({ pg : 13397, line : "d'une main sa mamelle, elle dit ces paroles lamentables:"});
db.iliadfr.insert({ pg : 13399, line : "-- Hektôr, mon fils, respecte ce sein et prends pitié de moi! Si"});
db.iliadfr.insert({ pg : 13400, line : "jamais je t'ai donné cette mamelle qui apaisait tes vagissements"});
db.iliadfr.insert({ pg : 13401, line : "d'enfant, souviens-t'en, mon cher fils! Fuis cet homme, rentre"});
db.iliadfr.insert({ pg : 13402, line : "dans nos murs, ne t'arrête point pour le combattre. Car s'il te"});
db.iliadfr.insert({ pg : 13403, line : "tuait, ni moi qui t'ai enfanté, ni ta femme richement dotée, nous"});
db.iliadfr.insert({ pg : 13404, line : "ne te pleurerons sur ton lit funèbre; mais, loin de nous, auprès"});
db.iliadfr.insert({ pg : 13405, line : "des nefs des Argiens, les chiens rapides te mangeront!"});
db.iliadfr.insert({ pg : 13407, line : "Et ils gémissaient ainsi, conjurant leur fils bien-aimé mais ils"});
db.iliadfr.insert({ pg : 13408, line : "ne fléchissaient point l'âme de Hektôr, qui attendait le grand"});
db.iliadfr.insert({ pg : 13409, line : "Akhilleus. De même qu'un dragon montagnard nourri d'herbes"});
db.iliadfr.insert({ pg : 13410, line : "vénéneuses, et plein de rage, se tord devant son repaire avec des"});
db.iliadfr.insert({ pg : 13411, line : "yeux horribles, en attendant un homme qui approche; de même"});
db.iliadfr.insert({ pg : 13412, line : "Hektôr, plein d'un ferme courage, ne reculait point. Et, le"});
db.iliadfr.insert({ pg : 13413, line : "bouclier appuyé contre le relief de la tour, il se disait dans son"});
db.iliadfr.insert({ pg : 13414, line : "coeur:"});
db.iliadfr.insert({ pg : 13416, line : "-- Malheur à moi si je rentre dans les murailles! Polydamas"});
db.iliadfr.insert({ pg : 13417, line : "m'accablera de reproches, lui qui me conseillait de ramener les"});
db.iliadfr.insert({ pg : 13418, line : "Troiens dans la ville, cette nuit fatale où le divin Akhilleus"});
db.iliadfr.insert({ pg : 13419, line : "s'est levé. Je ne l'ai point écouté, et, certes, son conseil était"});
db.iliadfr.insert({ pg : 13420, line : "le meilleur. Et voici que j'ai perdu mon peuple par ma folie. Je"});
db.iliadfr.insert({ pg : 13421, line : "crains maintenant les Troiens et les Troiennes aux longs péplos."});
db.iliadfr.insert({ pg : 13422, line : "Le plus lâche pourra dire: -- Hektôr, trop confiant dans ses"});
db.iliadfr.insert({ pg : 13423, line : "forces, a perdu son peuple! Ils parleront ainsi. Mieux vaut ne"});
db.iliadfr.insert({ pg : 13424, line : "rentrer qu'après avoir tué Akhilleus, ou bien mourir glorieusement"});
db.iliadfr.insert({ pg : 13425, line : "pour Ilios. Si, déposant mon bouclier bombé et mon casque solide,"});
db.iliadfr.insert({ pg : 13426, line : "et appuyant ma lance au mur, j'allais au-devant du brave"});
db.iliadfr.insert({ pg : 13427, line : "Akhilleus? Si je lui promettais de rendre aux Atréides Hélénè et"});
db.iliadfr.insert({ pg : 13428, line : "toutes les richesses qu'Alexandros a portées à Troiè sur ses nefs"});
db.iliadfr.insert({ pg : 13429, line : "creuses? Car c'est là l'origine de nos querelles. Si j'offrais aux"});
db.iliadfr.insert({ pg : 13430, line : "Akhaiens de partager tout ce que la ville renferme, ayant fait"});
db.iliadfr.insert({ pg : 13431, line : "jurer par serment aux Troiens de ne rien cacher et de partager"});
db.iliadfr.insert({ pg : 13432, line : "tous les trésors que contient la riche Ilios? Mais à quoi songe"});
db.iliadfr.insert({ pg : 13433, line : "mon esprit? Je ne supplierai point Akhilleus, car il n'aurait ni"});
db.iliadfr.insert({ pg : 13434, line : "respect ni pitié pour moi, et, désarmé que je serais, il me"});
db.iliadfr.insert({ pg : 13435, line : "tuerait comme une femme. Non! Il ne s'agit point maintenant de"});
db.iliadfr.insert({ pg : 13436, line : "causer du chêne ou du rocher comme le jeune homme et la jeune"});
db.iliadfr.insert({ pg : 13437, line : "fille qui parlent entre eux; mais or il s'agit de combattre et de"});
db.iliadfr.insert({ pg : 13438, line : "voir à qui l'Olympien donnera la victoire."});
db.iliadfr.insert({ pg : 13440, line : "Et il songeait ainsi, attendant Akhilleus. Et le Pèléide"});
db.iliadfr.insert({ pg : 13441, line : "approchait semblable à l'impétueux guerrier Arès et brandissant de"});
db.iliadfr.insert({ pg : 13442, line : "la main droite la terrible lance Pèlienne. Et l'airain"});
db.iliadfr.insert({ pg : 13443, line : "resplendissait, semblable à l'éclair, ou au feu ardent, ou à"});
db.iliadfr.insert({ pg : 13444, line : "Hélios qui se lève. Mais dès que Hektôr l'eut vu, la terreur le"});
db.iliadfr.insert({ pg : 13445, line : "saisit et il ne put l'attendre; et, laissant les portes derrière"});
db.iliadfr.insert({ pg : 13446, line : "lui, il s'enfuit épouvanté. Et le Pèléide s'élança de ses pieds"});
db.iliadfr.insert({ pg : 13447, line : "rapides."});
db.iliadfr.insert({ pg : 13449, line : "De même que, sur les montagnes, un épervier, le plus rapide des"});
db.iliadfr.insert({ pg : 13450, line : "oiseaux, poursuit une colombe tremblante qui fuit d'un vol oblique"});
db.iliadfr.insert({ pg : 13451, line : "et qu'il presse avec des cris aigus, désirant l'atteindre et la"});
db.iliadfr.insert({ pg : 13452, line : "saisir; de même Akhilleus se précipitait, et Hektôr, tremblant,"});
db.iliadfr.insert({ pg : 13453, line : "fuyait devant lui sous les murs des Troiens, en agitant ses genoux"});
db.iliadfr.insert({ pg : 13454, line : "rapides. Et ils passèrent auprès de la colline et du haut figuier,"});
db.iliadfr.insert({ pg : 13455, line : "à travers le chemin et le long des murailles. Et ils parvinrent"});
db.iliadfr.insert({ pg : 13456, line : "près du fleuve au beau cours, là où jaillissent les deux fontaines"});
db.iliadfr.insert({ pg : 13457, line : "du Skamandros tourbillonnant. Et l'une coule, tiède, et une fumée"});
db.iliadfr.insert({ pg : 13458, line : "s'en exhale comme d'un grand feu; et l'autre filtre, pendant"});
db.iliadfr.insert({ pg : 13459, line : "l'été, froide comme la grêle, ou la neige, ou le dur cristal de"});
db.iliadfr.insert({ pg : 13460, line : "l'eau."});
db.iliadfr.insert({ pg : 13462, line : "Et auprès des fontaines, il y avait deux larges et belles cuves de"});
db.iliadfr.insert({ pg : 13463, line : "pierre où les femmes des Troiens et leurs filles charmantes"});
db.iliadfr.insert({ pg : 13464, line : "lavaient leurs robes splendides, au temps de la paix, avant"});
db.iliadfr.insert({ pg : 13465, line : "l'arrivée des Akhaiens. Et c'est là qu'ils couraient tous deux,"});
db.iliadfr.insert({ pg : 13466, line : "l'un fuyant, et l'autre le poursuivant. Et c'était un brave qui"});
db.iliadfr.insert({ pg : 13467, line : "fuyait, et un plus brave qui le poursuivait avec ardeur. Et ils ne"});
db.iliadfr.insert({ pg : 13468, line : "se disputaient point une victime, ni le dos d'un boeuf, prix de la"});
db.iliadfr.insert({ pg : 13469, line : "course parmi les hommes; mais ils couraient pour la vie de Hektôr"});
db.iliadfr.insert({ pg : 13470, line : "dompteur de chevaux."});
db.iliadfr.insert({ pg : 13472, line : "De même que deux chevaux rapidement élancés, dans les jeux"});
db.iliadfr.insert({ pg : 13473, line : "funéraires d'un guerrier, pour atteindre la borne et remporter un"});
db.iliadfr.insert({ pg : 13474, line : "prix magnifique, soit un trépied, soit une femme; de même ils"});
db.iliadfr.insert({ pg : 13475, line : "tournèrent trois fois, de leurs pieds rapides, autour de la ville"});
db.iliadfr.insert({ pg : 13476, line : "de Priamos. Et tous les dieux les regardaient. Et voici que le"});
db.iliadfr.insert({ pg : 13477, line : "père des dieux et des hommes parla ainsi:"});
db.iliadfr.insert({ pg : 13479, line : "-- Ô malheur! certes, je vois un homme qui m'est cher fuir autour"});
db.iliadfr.insert({ pg : 13480, line : "des murailles. Mon coeur s'attriste sur Hektôr, qui a souvent"});
db.iliadfr.insert({ pg : 13481, line : "brûlé pour moi de nombreuses cuisses de boeuf, sur les cimes du"});
db.iliadfr.insert({ pg : 13482, line : "grand Ida ou dans la citadelle d'Ilios. Le divin Akhilleus le"});
db.iliadfr.insert({ pg : 13483, line : "poursuit ardemment, de ses pieds rapides, autour de la ville de"});
db.iliadfr.insert({ pg : 13484, line : "Priamos. Allons, délibérez, ô dieux immortels. L'arracherons-nous"});
db.iliadfr.insert({ pg : 13485, line : "à la mort, ou dompterons-nous son courage par les mains du Pèléide"});
db.iliadfr.insert({ pg : 13486, line : "Akhilleus?"});
db.iliadfr.insert({ pg : 13488, line : "Et la déesse Athènè aux yeux clairs lui répondit:"});
db.iliadfr.insert({ pg : 13490, line : "-- Ô père foudroyant qui amasses les nuées, qu'as-tu dit? Tu veux"});
db.iliadfr.insert({ pg : 13491, line : "arracher à la mort lugubre cet homme mortel que la destinée a"});
db.iliadfr.insert({ pg : 13492, line : "marqué pour mourir! Fais-le; mais jamais, nous, les dieux, nous ne"});
db.iliadfr.insert({ pg : 13493, line : "t'approuverons."});
db.iliadfr.insert({ pg : 13495, line : "Et Zeus qui amasse les nuées, lui répondant, parla ainsi:"});
db.iliadfr.insert({ pg : 13497, line : "-- Rassure-toi, Tritogénéia, chère fille. Je n'ai point parlé dans"});
db.iliadfr.insert({ pg : 13498, line : "une volonté arrêtée, et je veux te complaire. Va, et agis comme tu"});
db.iliadfr.insert({ pg : 13499, line : "le voudras."});
db.iliadfr.insert({ pg : 13501, line : "Il parla ainsi, excitant Athènè déjà pleine d'ardeur; et elle"});
db.iliadfr.insert({ pg : 13502, line : "s'élança du faîte de l'Olympos."});
db.iliadfr.insert({ pg : 13504, line : "Et, cependant, le rapide Akhilleus pressait sans relâche Hektôr,"});
db.iliadfr.insert({ pg : 13505, line : "de même qu'un chien presse, sur les montagnes, le faon d'une"});
db.iliadfr.insert({ pg : 13506, line : "biche. Il le poursuit à travers les taillis et les vallées des"});
db.iliadfr.insert({ pg : 13507, line : "bois; et quand il se cache tremblant sous un buisson, le chien"});
db.iliadfr.insert({ pg : 13508, line : "flaire sa trace et le découvre aussitôt. De même Hektôr ne pouvait"});
db.iliadfr.insert({ pg : 13509, line : "se dérober au rapide Pèléiade. Autant de fois il voulait regagner"});
db.iliadfr.insert({ pg : 13510, line : "les portes Dardaniennes et l'abri des tours hautes et solides d'où"});
db.iliadfr.insert({ pg : 13511, line : "les Troiens pouvaient le secourir de leurs flèches, autant de fois"});
db.iliadfr.insert({ pg : 13512, line : "Akhilleus le poursuivait en le chassant vers la plaine; mais"});
db.iliadfr.insert({ pg : 13513, line : "Hektôr revenait toujours vers Ilios. De même que, dans un songe,"});
db.iliadfr.insert({ pg : 13514, line : "on poursuit un homme qui fuit, sans qu'on puisse l'atteindre et"});
db.iliadfr.insert({ pg : 13515, line : "qu'il puisse échapper, de même l'un ne pouvait saisir son ennemi,"});
db.iliadfr.insert({ pg : 13516, line : "ni celui-ci lui échapper. Mais comment Hektôr eût-il évité plus"});
db.iliadfr.insert({ pg : 13517, line : "longtemps les kères de la mort, si Apollôn, venant à son aide pour"});
db.iliadfr.insert({ pg : 13518, line : "la dernière fois, n'eût versé la vigueur dans ses genoux rapides?"});
db.iliadfr.insert({ pg : 13520, line : "Et le divin Akhilleus ordonnait à ses peuples, par un signe de"});
db.iliadfr.insert({ pg : 13521, line : "tête, de ne point lancer contre Hektôr de flèches mortelles, de"});
db.iliadfr.insert({ pg : 13522, line : "peur que quelqu'un le tuât et remportât cette gloire avant lui."});
db.iliadfr.insert({ pg : 13523, line : "Mais, comme ils revenaient pour la quatrième fois aux fontaines du"});
db.iliadfr.insert({ pg : 13524, line : "Skamandros, le père Zeus déploya ses balances d'or, et il y mit"});
db.iliadfr.insert({ pg : 13525, line : "deux kères de la mort violente, l'une pour Akhilleus et l'autre"});
db.iliadfr.insert({ pg : 13526, line : "pour Hektôr dompteur de chevaux. Et il les éleva en les tenant par"});
db.iliadfr.insert({ pg : 13527, line : "le milieu, et le jour fatal de Hektôr descendit vers les demeures"});
db.iliadfr.insert({ pg : 13528, line : "d'Aidès, et Phoibos Apollôn l'abandonna, et la déesse Athènè aux"});
db.iliadfr.insert({ pg : 13529, line : "yeux clairs, s'approchant du Pèléide, lui dit ces paroles ailées:"});
db.iliadfr.insert({ pg : 13531, line : "-- J'espère enfin, illustre Akhilleus cher à Zeus, que nous allons"});
db.iliadfr.insert({ pg : 13532, line : "remporter une grande gloire auprès des nefs Akhaiennes, en tuant"});
db.iliadfr.insert({ pg : 13533, line : "Hektôr insatiable de combats. Il ne peut plus nous échapper, même"});
db.iliadfr.insert({ pg : 13534, line : "quand l'archer Apollôn, faisant mille efforts pour le sauver, se"});
db.iliadfr.insert({ pg : 13535, line : "prosternerait devant le père Zeus tempétueux. Arrête-toi, et"});
db.iliadfr.insert({ pg : 13536, line : "respire. Je vais persuader le Priamide de venir à toi et de te"});
db.iliadfr.insert({ pg : 13537, line : "combattre."});
db.iliadfr.insert({ pg : 13539, line : "Athènè parla ainsi, et Akhilleus, plein de joie, s'arrêta, appuyé"});
db.iliadfr.insert({ pg : 13540, line : "sur sa lance d'airain. Et Athènè, le quittant, s'approcha du divin"});
db.iliadfr.insert({ pg : 13541, line : "Hektôr, étant semblable à Dèiphobos par le corps et par la voix."});
db.iliadfr.insert({ pg : 13542, line : "Et, debout auprès de lui, elle lui dit ces paroles ailées:"});
db.iliadfr.insert({ pg : 13544, line : "-- Ô mon frère, voici que le rapide Akhilleus te presse en te"});
db.iliadfr.insert({ pg : 13545, line : "poursuivant autour de la ville de Priamos. Tenons ferme et faisons"});
db.iliadfr.insert({ pg : 13546, line : "tête tous deux à l'ennemi."});
db.iliadfr.insert({ pg : 13548, line : "Et le grand Hektôr au casque mouvant lui répondit:"});
db.iliadfr.insert({ pg : 13550, line : "-- Dèiphobos, certes, tu étais déjà le plus cher de mes frères, de"});
db.iliadfr.insert({ pg : 13551, line : "tous ceux que Hékabè et Priamos ont engendrés; mais je dois"});
db.iliadfr.insert({ pg : 13552, line : "t'honorer bien plus dans mon coeur, aujourd'hui que, pour me"});
db.iliadfr.insert({ pg : 13553, line : "secourir, tu es sorti de nos murailles, où tous les autres restent"});
db.iliadfr.insert({ pg : 13554, line : "enfermés."});
db.iliadfr.insert({ pg : 13556, line : "Et la déesse Athènè aux yeux clairs lui répondit:"});
db.iliadfr.insert({ pg : 13558, line : "-- Ô mon frère, notre père et notre mère vénérable m'ont supplié à"});
db.iliadfr.insert({ pg : 13559, line : "genoux, et tous mes compagnons aussi, de rester dans les murs, car"});
db.iliadfr.insert({ pg : 13560, line : "tous sont épouvantés; mais mon âme était en proie à une amère"});
db.iliadfr.insert({ pg : 13561, line : "douleur. Maintenant, combattons bravement, et ne laissons point"});
db.iliadfr.insert({ pg : 13562, line : "nos lances en repos, et voyons si Akhilleus, nous ayant tués,"});
db.iliadfr.insert({ pg : 13563, line : "emportera nos dépouilles sanglantes vers les nefs creuses, ou s'il"});
db.iliadfr.insert({ pg : 13564, line : "sera dompté par ta lance."});
db.iliadfr.insert({ pg : 13566, line : "Athènè parla ainsi avec ruse et elle le précéda. Et dès qu'ils se"});
db.iliadfr.insert({ pg : 13567, line : "furent rencontrés, le grand Hektôr au casque mouvant parla ainsi"});
db.iliadfr.insert({ pg : 13568, line : "le premier:"});
db.iliadfr.insert({ pg : 13570, line : "-- Je ne te fuirai pas plus longtemps, fils de Pèleus. Je t'ai fui"});
db.iliadfr.insert({ pg : 13571, line : "trois fois autour de la grande ville de Priamos et je n'ai point"});
db.iliadfr.insert({ pg : 13572, line : "osé attendre ton attaque; mais voici que mon coeur me pousse à te"});
db.iliadfr.insert({ pg : 13573, line : "tenir tête. Je tuerai ou je serai tué. Mais attestons les dieux,"});
db.iliadfr.insert({ pg : 13574, line : "et qu'ils soient les fidèles témoins et les gardiens de nos"});
db.iliadfr.insert({ pg : 13575, line : "pactes. Je ne t'outragerai point cruellement, si Zeus me donne la"});
db.iliadfr.insert({ pg : 13576, line : "victoire et si je t'arrache l'âme; mais, Akhilleus, après t'avoir"});
db.iliadfr.insert({ pg : 13577, line : "dépouillé de tes belles armes, je rendrai ton cadavre aux"});
db.iliadfr.insert({ pg : 13578, line : "Akhaiens. Fais de même, et promets-le."});
db.iliadfr.insert({ pg : 13580, line : "Et Akhilleus aux pieds rapides, le regardant d'un oeil sombre, lui"});
db.iliadfr.insert({ pg : 13581, line : "répondit:"});
db.iliadfr.insert({ pg : 13583, line : "-- Hektôr, le plus exécrable des hommes, ne me parle point de"});
db.iliadfr.insert({ pg : 13584, line : "pactes. De même qu'il n'y a point d'alliances entre les lions et"});
db.iliadfr.insert({ pg : 13585, line : "les hommes, et que les loups et les agneaux, loin de s'accorder,"});
db.iliadfr.insert({ pg : 13586, line : "se haïssent toujours; de même il m'est impossible de ne pas te"});
db.iliadfr.insert({ pg : 13587, line : "haïr, et il n'y aura point de pactes entre nous avant qu'un des"});
db.iliadfr.insert({ pg : 13588, line : "deux ne tombe, rassasiant de son sang le terrible guerrier Arès."});
db.iliadfr.insert({ pg : 13589, line : "Rappelle tout ton courage. C'est maintenant que tu vas avoir"});
db.iliadfr.insert({ pg : 13590, line : "besoin de toute ton adresse et de toute ta vigueur, car tu n'as"});
db.iliadfr.insert({ pg : 13591, line : "plus de refuge, et voici que Pallas Athènè va te dompter par ma"});
db.iliadfr.insert({ pg : 13592, line : "lance, et que tu expieras en une fois les maux de mes compagnons"});
db.iliadfr.insert({ pg : 13593, line : "que tu as tués dans ta fureur!"});
db.iliadfr.insert({ pg : 13595, line : "Il parla ainsi, et, brandissant sa longue pique, il la lança; mais"});
db.iliadfr.insert({ pg : 13596, line : "l'illustre Hektôr la vit et l'évita; et la pique d'airain, passant"});
db.iliadfr.insert({ pg : 13597, line : "au-dessus de lui, s'enfonça en terre. Et Pallas Athènè, l'ayant"});
db.iliadfr.insert({ pg : 13598, line : "arrachée, la rendit à Akhilleus, sans que le prince des peuples,"});
db.iliadfr.insert({ pg : 13599, line : "Hektôr, s'en aperçût. Et le Priamide dit au brave Pèléide:"});
db.iliadfr.insert({ pg : 13601, line : "-- Tu m'as manqué, ô Akhilleus semblable aux dieux! Zeus ne"});
db.iliadfr.insert({ pg : 13602, line : "t'avait point enseigné ma destinée, comme tu le disais; mais ce"});
db.iliadfr.insert({ pg : 13603, line : "n'étaient que des paroles vaines et rusées, afin de m'effrayer et"});
db.iliadfr.insert({ pg : 13604, line : "de me faire oublier ma force et mon courage. Ce ne sera point dans"});
db.iliadfr.insert({ pg : 13605, line : "le dos que tu me perceras de ta lance, car je cours droit à toi."});
db.iliadfr.insert({ pg : 13606, line : "Frappe donc ma poitrine, si un dieu te l'accorde, et tente"});
db.iliadfr.insert({ pg : 13607, line : "maintenant d'éviter ma lance d'airain. Plût aux dieux que tu la"});
db.iliadfr.insert({ pg : 13608, line : "reçusses tout entière dans le corps! La guerre serait plus facile"});
db.iliadfr.insert({ pg : 13609, line : "aux Troiens si je te tuais, car tu es leur pire fléau."});
db.iliadfr.insert({ pg : 13611, line : "Il parla ainsi en brandissant sa longue pique, et il la lança; et"});
db.iliadfr.insert({ pg : 13612, line : "elle frappa, sans dévier, le milieu du bouclier du Pèléide; mais"});
db.iliadfr.insert({ pg : 13613, line : "le bouclier la repoussa au loin. Et Hektôr, irrité qu'un trait"});
db.iliadfr.insert({ pg : 13614, line : "inutile se fût échappé de sa main, resta plein de trouble, car il"});
db.iliadfr.insert({ pg : 13615, line : "n'avait que cette lance. Et il appela à grands cris Dèiphobos au"});
db.iliadfr.insert({ pg : 13616, line : "bouclier brillant, et il lui demanda une autre lance; mais,"});
db.iliadfr.insert({ pg : 13617, line : "Dèiphobos ayant disparu, Hektôr, dans son esprit, connut sa"});
db.iliadfr.insert({ pg : 13618, line : "destinée, et il dit:"});
db.iliadfr.insert({ pg : 13620, line : "-- Malheur à moi! voici que les dieux m'appellent à la mort. Je"});
db.iliadfr.insert({ pg : 13621, line : "croyais que le héros Dèiphobos était auprès de moi; mais il est"});
db.iliadfr.insert({ pg : 13622, line : "dans nos murs. C'est Athènè qui m'a trompé. La mauvaise mort est"});
db.iliadfr.insert({ pg : 13623, line : "proche; la voilà, plus de refuge. Ceci plaisait dès longtemps à"});
db.iliadfr.insert({ pg : 13624, line : "Zeus et au fils de Zeus, Apollôn, qui tous deux cependant"});
db.iliadfr.insert({ pg : 13625, line : "m'étaient bienveillants. Et voici que la moire va me saisir! Mais,"});
db.iliadfr.insert({ pg : 13626, line : "certes, je ne mourrai ni lâchement, ni sans gloire, et"});
db.iliadfr.insert({ pg : 13627, line : "j'accomplirai une grande action qu'apprendront les hommes futurs."});
db.iliadfr.insert({ pg : 13629, line : "Il parla ainsi, et, tirant l'épée aiguë qui pendait, grande et"});
db.iliadfr.insert({ pg : 13630, line : "lourde, sur son flanc, il se jeta sur Akhilleus, semblable à"});
db.iliadfr.insert({ pg : 13631, line : "l'aigle qui, planant dans les hauteurs, descend dans la plaine à"});
db.iliadfr.insert({ pg : 13632, line : "travers les nuées obscures, afin d'enlever la faible brebis ou le"});
db.iliadfr.insert({ pg : 13633, line : "lièvre timide. Ainsi se ruait Hektôr, en brandissant l'épée aiguë."});
db.iliadfr.insert({ pg : 13634, line : "Et Akhilleus, emplissant son coeur d'une rage féroce, se rua aussi"});
db.iliadfr.insert({ pg : 13635, line : "sur le Priamide. Et il portait son beau bouclier devant sa"});
db.iliadfr.insert({ pg : 13636, line : "poitrine, et il secouait son casque éclatant aux quatre cônes et"});
db.iliadfr.insert({ pg : 13637, line : "aux splendides crinières d'or mouvantes que Hèphaistos avait"});
db.iliadfr.insert({ pg : 13638, line : "fixées au sommet. Comme Hespéros, la plus belle des étoiles"});
db.iliadfr.insert({ pg : 13639, line : "ouraniennes, se lève au milieu des astres de la nuit, ainsi"});
db.iliadfr.insert({ pg : 13640, line : "resplendissait l'éclair de la pointe d'airain que le Pèléide"});
db.iliadfr.insert({ pg : 13641, line : "brandissait, pour la perte de Hektôr, cherchant sur son beau corps"});
db.iliadfr.insert({ pg : 13642, line : "la place où il frapperait. Les belles armes d'airain que le"});
db.iliadfr.insert({ pg : 13643, line : "Priamide avait arrachées au cadavre de Patroklos le couvraient en"});
db.iliadfr.insert({ pg : 13644, line : "entier, sauf à la jointure du cou et de l'épaule, là où la fuite"});
db.iliadfr.insert({ pg : 13645, line : "de l'âme est la plus prompte. C'est là que le divin Akhilleus"});
db.iliadfr.insert({ pg : 13646, line : "enfonça sa lance, dont la pointe traversa le cou de Hektôr; mais"});
db.iliadfr.insert({ pg : 13647, line : "la lourde lance d'airain ne trancha point le gosier, et il pouvait"});
db.iliadfr.insert({ pg : 13648, line : "encore parler. Il tomba dans la poussière, et le divin Akhilleus"});
db.iliadfr.insert({ pg : 13649, line : "se glorifia ainsi:"});
db.iliadfr.insert({ pg : 13651, line : "-- Hektôr, tu pensais peut-être, après avoir tué Patroklos,"});
db.iliadfr.insert({ pg : 13652, line : "n'avoir plus rien à craindre? Tu ne songeais point à moi qui étais"});
db.iliadfr.insert({ pg : 13653, line : "absent. Insensé! un vengeur plus fort lui restait sur les nefs"});
db.iliadfr.insert({ pg : 13654, line : "creuses, et c'était moi qui ai rompu tes genoux! Va! les chiens et"});
db.iliadfr.insert({ pg : 13655, line : "les oiseaux te déchireront honteusement, et les Akhaiens"});
db.iliadfr.insert({ pg : 13656, line : "enseveliront Patroklos!"});
db.iliadfr.insert({ pg : 13658, line : "Et Hektôr au casque mouvant lui répondit, parlant à peine:"});
db.iliadfr.insert({ pg : 13660, line : "-- Je te supplie par ton âme, par tes genoux, par tes parents, ne"});
db.iliadfr.insert({ pg : 13661, line : "laisse pas les chiens me déchirer auprès des nefs Akhaiennes."});
db.iliadfr.insert({ pg : 13662, line : "Accepte l'or et l'airain que te donneront mon père et ma mère"});
db.iliadfr.insert({ pg : 13663, line : "vénérable. Renvoie mon corps dans mes demeures, afin que les"});
db.iliadfr.insert({ pg : 13664, line : "Troiens et les Troiennes me déposent avec honneur sur le bûcher."});
db.iliadfr.insert({ pg : 13666, line : "Et Akhilleus aux pieds rapides, le regardant d'un oeil sombre, lui"});
db.iliadfr.insert({ pg : 13667, line : "dit:"});
db.iliadfr.insert({ pg : 13669, line : "-- Chien! ne me supplie ni par mes genoux, ni par mes parents."});
db.iliadfr.insert({ pg : 13670, line : "Plût aux dieux que j'eusse la force de manger ta chair crue, pour"});
db.iliadfr.insert({ pg : 13671, line : "le mal que tu m'as fait! Rien ne sauvera ta tête des chiens, quand"});
db.iliadfr.insert({ pg : 13672, line : "même on m'apporterait dix et vingt fois ton prix, et nulle autres"});
db.iliadfr.insert({ pg : 13673, line : "présents; quand même le Dardanide Priamos voudrait te racheter ton"});
db.iliadfr.insert({ pg : 13674, line : "poids d'or! Jamais la mère vénérable qui t'a enfanté ne te"});
db.iliadfr.insert({ pg : 13675, line : "pleurera couché sur un lit funèbre. Les chiens et les oiseaux te"});
db.iliadfr.insert({ pg : 13676, line : "déchireront tout entier!"});
db.iliadfr.insert({ pg : 13678, line : "Et Hektôr au casque mouvant lui répondit en mourant:"});
db.iliadfr.insert({ pg : 13680, line : "-- Certes, je prévoyais, te connaissant bien, que je ne te"});
db.iliadfr.insert({ pg : 13681, line : "fléchirais point, car ton coeur est de fer. Souviens-toi que les"});
db.iliadfr.insert({ pg : 13682, line : "dieux me vengeront le jour où Pâris et Phoibos Apollôn te tueront,"});
db.iliadfr.insert({ pg : 13683, line : "malgré ton courage, devant les portes Skaies."});
db.iliadfr.insert({ pg : 13685, line : "Et la mort l'ayant interrompu, son âme s'envola de son corps chez"});
db.iliadfr.insert({ pg : 13686, line : "Aidès, pleurant sa destinée mauvaise, sa vigueur et sa jeunesse."});
db.iliadfr.insert({ pg : 13688, line : "Et Akhilleus dit à son cadavre:"});
db.iliadfr.insert({ pg : 13690, line : "-- Meurs! Je subirai ma destinée quand Zeus et les autres dieux le"});
db.iliadfr.insert({ pg : 13691, line : "voudront."});
db.iliadfr.insert({ pg : 13693, line : "Ayant ainsi parlé, il arracha sa lance d'airain du cadavre, et, la"});
db.iliadfr.insert({ pg : 13694, line : "posant à l'écart, il dépouilla les épaules du Priamide de ses"});
db.iliadfr.insert({ pg : 13695, line : "armes sanglantes. Et les fils des Akhaiens accoururent, et ils"});
db.iliadfr.insert({ pg : 13696, line : "admiraient la grandeur et la beauté de Hektôr; et chacun le"});
db.iliadfr.insert({ pg : 13697, line : "blessait de nouveau, et ils disaient en se regardant:"});
db.iliadfr.insert({ pg : 13699, line : "-- Certes, Hektôr est maintenant plus aisé à manier que le jour où"});
db.iliadfr.insert({ pg : 13700, line : "il incendiait les nefs."});
db.iliadfr.insert({ pg : 13702, line : "Ils parlaient ainsi, et chacun le frappait. Mais aussitôt que le"});
db.iliadfr.insert({ pg : 13703, line : "divin Akhilleus aux pieds rapides eut dépouillé le Priamide de ses"});
db.iliadfr.insert({ pg : 13704, line : "armes, debout au milieu des Akhaiens, il leur dit ces paroles"});
db.iliadfr.insert({ pg : 13705, line : "ailées:"});
db.iliadfr.insert({ pg : 13707, line : "-- Ô amis, princes et chefs des Argiens, puisque les dieux m'ont"});
db.iliadfr.insert({ pg : 13708, line : "donné de tuer ce guerrier qui nous a accablés de plus de maux que"});
db.iliadfr.insert({ pg : 13709, line : "tous les autres à la fois, allons assiéger la ville, et sachons"});
db.iliadfr.insert({ pg : 13710, line : "quelle est la pensée des Troiens: s'ils veulent, le Priamide étant"});
db.iliadfr.insert({ pg : 13711, line : "mort, abandonner la citadelle, ou y rester, bien qu'ils aient"});
db.iliadfr.insert({ pg : 13712, line : "perdu Hektôr. Mais à quoi songe mon esprit? Il gît auprès des"});
db.iliadfr.insert({ pg : 13713, line : "nefs, mort, non pleuré, non enseveli, Patroklos, que je"});
db.iliadfr.insert({ pg : 13714, line : "n'oublierai jamais tant que je vivrai, et que mes genoux"});
db.iliadfr.insert({ pg : 13715, line : "remueront! Même quand les morts oublieraient chez Aidès, moi je me"});
db.iliadfr.insert({ pg : 13716, line : "souviendrai de mon cher compagnon. Et maintenant, ô fils des"});
db.iliadfr.insert({ pg : 13717, line : "Akhaiens, chantez les paians et retournons aux nefs en entraînant"});
db.iliadfr.insert({ pg : 13718, line : "ce cadavre. Nous avons remporté une grande gloire, nous avons tué"});
db.iliadfr.insert({ pg : 13719, line : "le divin Hektôr, à qui les Troiens adressaient des voeux, dans"});
db.iliadfr.insert({ pg : 13720, line : "leur ville, comme à un dieu."});
db.iliadfr.insert({ pg : 13722, line : "Il parla ainsi, et il outragea indignement le divin Hektôr. Il lui"});
db.iliadfr.insert({ pg : 13723, line : "perça les tendons des deux pieds, entre le talon et la cheville,"});
db.iliadfr.insert({ pg : 13724, line : "et il y passa des courroies. Et il l'attacha derrière le char,"});
db.iliadfr.insert({ pg : 13725, line : "laissant traîner la tête. Puis, déposant les armes illustres dans"});
db.iliadfr.insert({ pg : 13726, line : "le char, il y monta lui-même, et il fouetta les chevaux, qui"});
db.iliadfr.insert({ pg : 13727, line : "s'élancèrent avec ardeur. Et le Priamide Hektôr était ainsi traîné"});
db.iliadfr.insert({ pg : 13728, line : "dans un tourbillon de poussière, et ses cheveux noirs en étaient"});
db.iliadfr.insert({ pg : 13729, line : "souillés, et sa tête était ensevelie dans la poussière, cette tête"});
db.iliadfr.insert({ pg : 13730, line : "autrefois si belle que Zeus livrait maintenant à l'ennemi, pour"});
db.iliadfr.insert({ pg : 13731, line : "être outragée sur la terre de la patrie."});
db.iliadfr.insert({ pg : 13733, line : "Ainsi toute la tête de Hektôr était souillée de poussière. Et sa"});
db.iliadfr.insert({ pg : 13734, line : "mère, arrachant ses cheveux et déchirant son beau voile, gémissait"});
db.iliadfr.insert({ pg : 13735, line : "en voyant de loin son fils. Et son père pleurait misérablement, et"});
db.iliadfr.insert({ pg : 13736, line : "les peuples aussi hurlaient et pleuraient par la ville. On eût dit"});
db.iliadfr.insert({ pg : 13737, line : "que la haute Ilios croulait tout entière dans le feu. Et les"});
db.iliadfr.insert({ pg : 13738, line : "peuples retenaient à grand'peine le vieux Priamos désespéré qui"});
db.iliadfr.insert({ pg : 13739, line : "voulait sortir des portes Dardaniennes. Et, se prosternant devant"});
db.iliadfr.insert({ pg : 13740, line : "eux, il les suppliait, les nommant par leurs noms:"});
db.iliadfr.insert({ pg : 13742, line : "-- Mes amis, laissez-moi sortir seul de la ville, afin que j'aille"});
db.iliadfr.insert({ pg : 13743, line : "aux nefs des Akhaiens. Je supplierai cet homme impie qui accomplit"});
db.iliadfr.insert({ pg : 13744, line : "d'horribles actions. Il respectera peut-être mon âge, il aura"});
db.iliadfr.insert({ pg : 13745, line : "peut-être pitié de ma vieillesse; car son père aussi est vieux,"});
db.iliadfr.insert({ pg : 13746, line : "Pèleus, qui l'a engendré et nourri pour la ruine des Troiens, et"});
db.iliadfr.insert({ pg : 13747, line : "surtout pour m'accabler de maux. Que de fils florissants il m'a"});
db.iliadfr.insert({ pg : 13748, line : "tués! Et je gémis moins sur eux tous ensemble que sur le seul"});
db.iliadfr.insert({ pg : 13749, line : "Hektôr, dont le regret douloureux me fera descendre aux demeures"});
db.iliadfr.insert({ pg : 13750, line : "d'Aidès. Plût aux dieux qu'il fût mort dans nos bras! Au moins,"});
db.iliadfr.insert({ pg : 13751, line : "sur son cadavre, nous nous serions rassasiés de larmes et de"});
db.iliadfr.insert({ pg : 13752, line : "sanglots, la mère malheureuse qui l'a enfanté et moi!"});
db.iliadfr.insert({ pg : 13754, line : "Il parla ainsi en pleurant. Et tous les citoyens pleuraient. Et,"});
db.iliadfr.insert({ pg : 13755, line : "parmi les Troiennes, Hékabè commença le deuil sans fin:"});
db.iliadfr.insert({ pg : 13757, line : "-- Mon enfant! pourquoi suis-je encore vivante, malheureuse,"});
db.iliadfr.insert({ pg : 13758, line : "puisque tu es mort? Toi qui, les nuits et les jours, étais ma"});
db.iliadfr.insert({ pg : 13759, line : "gloire dans Ilios, et l'unique salut des Troiens et des Troiennes,"});
db.iliadfr.insert({ pg : 13760, line : "qui, dans la ville, te recevaient comme un dieu! Certes, tu"});
db.iliadfr.insert({ pg : 13761, line : "faisais toute leur gloire, quand tu vivais; mais voici que la"});
db.iliadfr.insert({ pg : 13762, line : "moire et la mort t'ont saisi!"});
db.iliadfr.insert({ pg : 13764, line : "Elle parla ainsi en pleurant. Et la femme de Hektôr ne savait rien"});
db.iliadfr.insert({ pg : 13765, line : "encore, aucun messager ne lui ayant annoncé que son époux était"});
db.iliadfr.insert({ pg : 13766, line : "resté hors des portes. Et, dans sa haute demeure fermée, elle"});
db.iliadfr.insert({ pg : 13767, line : "tissait une toile double, splendide et ornée de fleurs variées. Et"});
db.iliadfr.insert({ pg : 13768, line : "elle ordonnait aux servantes à la belle chevelure de préparer,"});
db.iliadfr.insert({ pg : 13769, line : "dans la demeure, et de mettre un grand trépied sur le feu, afin"});
db.iliadfr.insert({ pg : 13770, line : "qu'un bain chaud fût prêt pour Hektôr à son retour du combat."});
db.iliadfr.insert({ pg : 13771, line : "L'insensée ignorait qu'Athènè aux yeux clairs avait tué Hektôr par"});
db.iliadfr.insert({ pg : 13772, line : "les mains d'Akhilleus, loin de tous les bains. Mais elle entendit"});
db.iliadfr.insert({ pg : 13773, line : "des lamentations et des hurlements sur la tour. Et ses membres"});
db.iliadfr.insert({ pg : 13774, line : "tremblèrent, et la navette lui tomba des mains, et elle dit aux"});
db.iliadfr.insert({ pg : 13775, line : "servantes à la belle chevelure:"});
db.iliadfr.insert({ pg : 13777, line : "-- Venez. Que deux d'entre vous me suivent, afin que je voie ce"});
db.iliadfr.insert({ pg : 13778, line : "qui nous arrive, car j'ai entendu la voix de la vénérable mère de"});
db.iliadfr.insert({ pg : 13779, line : "Hektôr. Mon coeur bondit dans ma poitrine, et mes genoux"});
db.iliadfr.insert({ pg : 13780, line : "défaillent. Peut-être quelque malheur menace-t-il les fils de"});
db.iliadfr.insert({ pg : 13781, line : "Priamos. Plaise aux dieux que mes paroles soient vaines! Mais je"});
db.iliadfr.insert({ pg : 13782, line : "crains que le divin Akhilleus, ayant écarté le brave Hektôr de la"});
db.iliadfr.insert({ pg : 13783, line : "ville, le poursuive dans la plaine et dompte son courage. Car mon"});
db.iliadfr.insert({ pg : 13784, line : "époux ne reste point dans la foule des guerriers, et il combat en"});
db.iliadfr.insert({ pg : 13785, line : "tête de tous, ne le cédant à aucun."});
db.iliadfr.insert({ pg : 13787, line : "Elle parla ainsi et sortit de sa demeure, semblable à une"});
db.iliadfr.insert({ pg : 13788, line : "bakkhante et le coeur palpitant, et les servantes la suivaient."});
db.iliadfr.insert({ pg : 13789, line : "Arrivée sur la tour, au milieu de la foule des hommes, elle"});
db.iliadfr.insert({ pg : 13790, line : "s'arrêta, regardant du haut des murailles, et reconnut Hektôr"});
db.iliadfr.insert({ pg : 13791, line : "traîné devant la ville. Et les chevaux rapides le traînaient"});
db.iliadfr.insert({ pg : 13792, line : "indignement vers les nefs creuses des Akhaiens. Alors, une nuit"});
db.iliadfr.insert({ pg : 13793, line : "noire couvrit ses yeux, et elle tomba à la renverse, inanimée. Et"});
db.iliadfr.insert({ pg : 13794, line : "tous les riches ornements se détachèrent de sa tête, la"});
db.iliadfr.insert({ pg : 13795, line : "bandelette, le noeud, le réseau, et le voile que lui avait donné"});
db.iliadfr.insert({ pg : 13796, line : "Aphroditè d'or le jour où Hektôr au casque mouvant l'avait emmenée"});
db.iliadfr.insert({ pg : 13797, line : "de la demeure d'Êétiôn, après lui avoir donné une grande dot. Et"});
db.iliadfr.insert({ pg : 13798, line : "les soeurs et les belles-soeurs de Hektôr l'entouraient et la"});
db.iliadfr.insert({ pg : 13799, line : "soutenaient dans leurs bras, tandis qu'elle respirait à peine. Et"});
db.iliadfr.insert({ pg : 13800, line : "quand elle eut recouvré l'esprit, elle dit, gémissant au milieu"});
db.iliadfr.insert({ pg : 13801, line : "des Troiennes:"});
db.iliadfr.insert({ pg : 13803, line : "-- Hektôr! ô malheureuse que je suis! Nous sommes nés pour une"});
db.iliadfr.insert({ pg : 13804, line : "même destinée: toi, dans Troiè et dans la demeure de Priamos; moi,"});
db.iliadfr.insert({ pg : 13805, line : "dans Thèbè, sous le mont Plakos couvert de forêts, dans la demeure"});
db.iliadfr.insert({ pg : 13806, line : "d'Êétiôn, qui m'éleva toute petite, père malheureux d'une"});
db.iliadfr.insert({ pg : 13807, line : "malheureuse. Plût aux dieux qu'il ne m'eût point engendrée!"});
db.iliadfr.insert({ pg : 13808, line : "Maintenant tu descends vers les demeures d'Aidès, dans la terre"});
db.iliadfr.insert({ pg : 13809, line : "creuse, et tu me laisses, dans notre demeure, veuve et accablée de"});
db.iliadfr.insert({ pg : 13810, line : "deuil. Et ce petit enfant que nous avons engendré tous deux,"});
db.iliadfr.insert({ pg : 13811, line : "malheureux que nous sommes! tu ne le protégeras pas, Hektôr,"});
db.iliadfr.insert({ pg : 13812, line : "puisque tu es mort, et lui ne te servira point de soutien. Même"});
db.iliadfr.insert({ pg : 13813, line : "s'il échappait à cette guerre lamentable des Akhaiens, il ne peut"});
db.iliadfr.insert({ pg : 13814, line : "s'attendre qu'au travail et à la douleur, car ils lui enlèveront"});
db.iliadfr.insert({ pg : 13815, line : "ses biens. Le jour qui fait un enfant orphelin lui ôte aussi tous"});
db.iliadfr.insert({ pg : 13816, line : "ses jeunes amis. Il est triste au milieu de tous, et ses joues"});
db.iliadfr.insert({ pg : 13817, line : "sont toujours baignées de larmes. Indigent, il s'approche des"});
db.iliadfr.insert({ pg : 13818, line : "compagnons de son père, prenant l'un par le manteau et l'autre par"});
db.iliadfr.insert({ pg : 13819, line : "la tunique. Si l'un d'entre eux, dans sa pitié, lui offre une"});
db.iliadfr.insert({ pg : 13820, line : "petite coupe, elle mouille ses lèvres sans rafraîchir son palais."});
db.iliadfr.insert({ pg : 13821, line : "Le jeune homme, assis entre son père et sa mère, le repousse de la"});
db.iliadfr.insert({ pg : 13822, line : "table du festin, et, le frappant de ses mains, lui dit des paroles"});
db.iliadfr.insert({ pg : 13823, line : "injurieuses: -- Va-t'en! ton père n'est pas des nôtres! Et"});
db.iliadfr.insert({ pg : 13824, line : "l'enfant revient en pleurant auprès de sa mère veuve. Astyanax,"});
db.iliadfr.insert({ pg : 13825, line : "qui autrefois mangeait la moelle et la graisse des brebis sur les"});
db.iliadfr.insert({ pg : 13826, line : "genoux de son père; qui, lorsque le sommeil le prenait et qu'il"});
db.iliadfr.insert({ pg : 13827, line : "cessait de jouer, dormait dans un doux lit, aux bras de sa"});
db.iliadfr.insert({ pg : 13828, line : "nourrice, et le coeur rassasié de délices; maintenant Astyanax,"});
db.iliadfr.insert({ pg : 13829, line : "que les Troiens nommaient ainsi, car Hektôr défendait seul leurs"});
db.iliadfr.insert({ pg : 13830, line : "hautes murailles, subira mille maux, étant privé de son père bien-"});
db.iliadfr.insert({ pg : 13831, line : "aimé. Et voici, Hektôr, que les vers rampants te mangeront auprès"});
db.iliadfr.insert({ pg : 13832, line : "des nefs éperonnées, loin de tes parents, après que les chiens se"});
db.iliadfr.insert({ pg : 13833, line : "seront rassasiés de ta chair. Tu possédais, dans tes demeures, de"});
db.iliadfr.insert({ pg : 13834, line : "beaux et doux vêtements, oeuvre des femmes; mais je les brûlerai"});
db.iliadfr.insert({ pg : 13835, line : "tous dans le feu ardent, car ils ne te serviront pas et tu ne"});
db.iliadfr.insert({ pg : 13836, line : "seras pas enseveli avec eux. Qu'ils soient donc brûlés en ton"});
db.iliadfr.insert({ pg : 13837, line : "honneur au milieu des Troiens et des Troiennes!"});
db.iliadfr.insert({ pg : 13839, line : "Elle parla ainsi en pleurant, et toutes les femmes se lamentaient"});
db.iliadfr.insert({ pg : 13840, line : "comme elle."});
db.iliadfr.insert({ pg : 13843, line : "Chant 23"});
db.iliadfr.insert({ pg : 13845, line : "Et tandis qu'ils gémissaient ainsi par la ville, les Akhaiens"});
db.iliadfr.insert({ pg : 13846, line : "arrivèrent aux nefs et au Hellespontos. Et ils se dispersèrent, et"});
db.iliadfr.insert({ pg : 13847, line : "chacun rentra dans sa nef. Mais Akhilleus ne permit point aux"});
db.iliadfr.insert({ pg : 13848, line : "Myrmidones de se séparer, et il dit à ses braves compagnons:"});
db.iliadfr.insert({ pg : 13850, line : "-- Myrmidones aux chevaux rapides, mes chers compagnons, ne"});
db.iliadfr.insert({ pg : 13851, line : "détachons point des chars nos chevaux aux sabots massifs; mais,"});
db.iliadfr.insert({ pg : 13852, line : "avec nos chevaux et nos chars, pleurons Patroklos, car tel est"});
db.iliadfr.insert({ pg : 13853, line : "l'honneur dû aux morts. Après nous être rassasiés de deuil, nous"});
db.iliadfr.insert({ pg : 13854, line : "délierons nos chevaux, et, tous, nous prendrons notre repas ici."});
db.iliadfr.insert({ pg : 13856, line : "Il parla ainsi, et ils se lamentaient, et Akhilleus le premier."});
db.iliadfr.insert({ pg : 13857, line : "Et, en gémissant, ils poussèrent trois fois les chevaux aux belles"});
db.iliadfr.insert({ pg : 13858, line : "crinières autour du cadavre; et Thétis augmentait leur désir de"});
db.iliadfr.insert({ pg : 13859, line : "pleurer. Et, dans le regret du héros Patroklos, les larmes"});
db.iliadfr.insert({ pg : 13860, line : "baignaient les armes et arrosaient le sable. Au milieu d'eux, le"});
db.iliadfr.insert({ pg : 13861, line : "Pèléide commença le deuil lamentable, en posant ses mains tueuses"});
db.iliadfr.insert({ pg : 13862, line : "d'homme sur la poitrine de son ami:"});
db.iliadfr.insert({ pg : 13864, line : "-- Sois content de moi, ô Patroklos, dans les demeures d'Aidès."});
db.iliadfr.insert({ pg : 13865, line : "Tout ce que je t'ai promis, je l'accomplirai. Hektôr, jeté aux"});
db.iliadfr.insert({ pg : 13866, line : "chiens, sera déchiré par eux; et, pour te venger, je tuerai devant"});
db.iliadfr.insert({ pg : 13867, line : "ton bûcher douze nobles fils des Troiens."});
db.iliadfr.insert({ pg : 13869, line : "Il parla ainsi, et il outragea indignement le divin Hektôr en le"});
db.iliadfr.insert({ pg : 13870, line : "couchant dans la poussière devant le lit du Ménoitiade. Puis, les"});
db.iliadfr.insert({ pg : 13871, line : "Myrmidones quittèrent leurs splendides armes d'airain, dételèrent"});
db.iliadfr.insert({ pg : 13872, line : "leurs chevaux hennissants et s'assirent en foule autour de la nef"});
db.iliadfr.insert({ pg : 13873, line : "du rapide Aiakide, qui leur offrit le repas funèbre. Et beaucoup"});
db.iliadfr.insert({ pg : 13874, line : "de boeufs blancs mugissaient sous le fer, tandis qu'on les"});
db.iliadfr.insert({ pg : 13875, line : "égorgeait ainsi qu'un grand nombre de brebis et de chèvres"});
db.iliadfr.insert({ pg : 13876, line : "bêlantes. Et beaucoup de porcs gras cuisaient devant la flamme du"});
db.iliadfr.insert({ pg : 13877, line : "feu. Et le sang coulait abondamment autour du cadavre. Et les"});
db.iliadfr.insert({ pg : 13878, line : "princes Akhaiens conduisirent le prince Pèléiôn aux pieds rapides"});
db.iliadfr.insert({ pg : 13879, line : "vers le divin Agamemnôn, mais non sans peine, car le regret de son"});
db.iliadfr.insert({ pg : 13880, line : "compagnon emplissait son coeur."});
db.iliadfr.insert({ pg : 13882, line : "Et quand ils furent arrivés à la tente d'Agamemnôn, celui-ci"});
db.iliadfr.insert({ pg : 13883, line : "ordonna aux hérauts de poser un grand trépied sur le feu, afin que"});
db.iliadfr.insert({ pg : 13884, line : "le Pèléide, s'il y consentait, lavât le sang qui le souillait."});
db.iliadfr.insert({ pg : 13885, line : "Mais il s'y refusa toujours et jura un grand serment:"});
db.iliadfr.insert({ pg : 13887, line : "-- Non! par Zeus, le plus haut et le meilleur des dieux, je ne"});
db.iliadfr.insert({ pg : 13888, line : "purifierai point ma tête que je n'aie mis Patroklos sur le bûcher,"});
db.iliadfr.insert({ pg : 13889, line : "élevé son tombeau et coupé ma chevelure. Jamais, tant que je"});
db.iliadfr.insert({ pg : 13890, line : "vivrai, une telle douleur ne m'accablera plus. Mais achevons ce"});
db.iliadfr.insert({ pg : 13891, line : "repas odieux. Roi des hommes, Agamemnôn, commande qu'on apporte,"});
db.iliadfr.insert({ pg : 13892, line : "dès le matin, le bois du bûcher, et qu'on l'apprête, car il est"});
db.iliadfr.insert({ pg : 13893, line : "juste d'honorer ainsi Patroklos, qui subit les noires ténèbres. Et"});
db.iliadfr.insert({ pg : 13894, line : "le feu infatigable le consumera promptement à tous les yeux, et"});
db.iliadfr.insert({ pg : 13895, line : "les peuples retourneront aux travaux de la guerre."});
db.iliadfr.insert({ pg : 13897, line : "Il parla ainsi, et les princes, l'ayant entendu, lui obéirent. Et"});
db.iliadfr.insert({ pg : 13898, line : "tous, préparant le repas, mangèrent; et aucun ne se plaignit d'une"});
db.iliadfr.insert({ pg : 13899, line : "part inégale. Puis, ils se retirèrent sous les tentes pour y"});
db.iliadfr.insert({ pg : 13900, line : "dormir."});
db.iliadfr.insert({ pg : 13902, line : "Mais le Pèléide était couché, gémissant, sur le rivage de la mer"});
db.iliadfr.insert({ pg : 13903, line : "aux bruits sans nombre, au milieu des Myrmidones, en un lieu où"});
db.iliadfr.insert({ pg : 13904, line : "les flots blanchissaient le bord. Et le doux sommeil, lui versant"});
db.iliadfr.insert({ pg : 13905, line : "l'oubli de ses peines, l'enveloppa, car il avait fatigué ses beaux"});
db.iliadfr.insert({ pg : 13906, line : "membres en poursuivant Hektôr autour de la haute Ilios. Et l'âme"});
db.iliadfr.insert({ pg : 13907, line : "du malheureux Patroklos lui apparut, avec la grande taille, les"});
db.iliadfr.insert({ pg : 13908, line : "beaux yeux, la voix et jusqu'aux vêtements du héros. Elle s'arrêta"});
db.iliadfr.insert({ pg : 13909, line : "sur la tête d'Akhilleus et lui dit:"});
db.iliadfr.insert({ pg : 13911, line : "-- Tu dors, et tu m'oublies, Akhilleus. Vivant, tu ne me"});
db.iliadfr.insert({ pg : 13912, line : "négligeais point, et, mort, tu m'oublies. Ensevelis-moi, afin que"});
db.iliadfr.insert({ pg : 13913, line : "je passe promptement les portes d'Aidès. Les âmes, ombres des"});
db.iliadfr.insert({ pg : 13914, line : "morts, me chassent et ne me laissent point me mêler à elles au-"});
db.iliadfr.insert({ pg : 13915, line : "delà du fleuve; et je vais, errant en vain autour des larges"});
db.iliadfr.insert({ pg : 13916, line : "portes de la demeure d'Aidès. Donne-moi la main; je t'en supplie"});
db.iliadfr.insert({ pg : 13917, line : "en pleurant, car je ne reviendrai plus du Hadès, quand vous"});
db.iliadfr.insert({ pg : 13918, line : "m'aurez livré au bûcher. Jamais plus, vivants tous deux, nous ne"});
db.iliadfr.insert({ pg : 13919, line : "nous confierons l'un à l'autre, assis loin de nos compagnons, car"});
db.iliadfr.insert({ pg : 13920, line : "la kèr odieuse qui m'était échue dès ma naissance m'a enfin saisi."});
db.iliadfr.insert({ pg : 13921, line : "Ta moire fatale, ô Akhilleus égal aux dieux, est aussi de mourir"});
db.iliadfr.insert({ pg : 13922, line : "sous les murs des Troiens magnanimes! Mais je te demande ceci, et"});
db.iliadfr.insert({ pg : 13923, line : "puisses-tu me l'accorder: Akhilleus, que mes ossements ne soient"});
db.iliadfr.insert({ pg : 13924, line : "point séparés des tiens, mais qu'ils soient unis comme nous"});
db.iliadfr.insert({ pg : 13925, line : "l'avons été dans tes demeures. Quand Ménoitios m'y conduisit tout"});
db.iliadfr.insert({ pg : 13926, line : "enfant, d'Opoèn, parce que j'avais tué déplorablement, dans ma"});
db.iliadfr.insert({ pg : 13927, line : "colère, le fils d'Amphidamas, en jouant aux dés, le cavalier"});
db.iliadfr.insert({ pg : 13928, line : "Pèleus me reçut dans ses demeures, m'y éleva avec tendresse et me"});
db.iliadfr.insert({ pg : 13929, line : "nomma ton compagnon. Qu'une seule urne reçoive donc nos cendres,"});
db.iliadfr.insert({ pg : 13930, line : "cette urne d'or que t'a donnée ta mère vénérable."});
db.iliadfr.insert({ pg : 13932, line : "Et Akhilleus aux pieds rapides lui répondit:"});
db.iliadfr.insert({ pg : 13934, line : "-- Pourquoi es-tu venu, ô tête chère! et pourquoi me commander ces"});
db.iliadfr.insert({ pg : 13935, line : "choses? Je t'obéirai, et les accomplirai promptement. Mais reste,"});
db.iliadfr.insert({ pg : 13936, line : "que je t'embrasse un moment, au moins! Adoucissons notre amère"});
db.iliadfr.insert({ pg : 13937, line : "douleur."});
db.iliadfr.insert({ pg : 13939, line : "Il parla ainsi, et il étendit ses mains affectueuses; mais il ne"});
db.iliadfr.insert({ pg : 13940, line : "saisit rien, et l'âme rentra en terre comme une fumée, avec un"});
db.iliadfr.insert({ pg : 13941, line : "âpre murmure. Et Akhilleus se réveilla stupéfait et, frappant ses"});
db.iliadfr.insert({ pg : 13942, line : "mains, il dit ces paroles lugubres:"});
db.iliadfr.insert({ pg : 13944, line : "-- Ô dieux! l'âme existe encore dans le Hadès, mais comme une"});
db.iliadfr.insert({ pg : 13945, line : "vaine image, et sans corps. L'âme du malheureux Patroklos m'est"});
db.iliadfr.insert({ pg : 13946, line : "apparue cette nuit, pleurant et se lamentant, et semblable à lui-"});
db.iliadfr.insert({ pg : 13947, line : "même; et elle m'a ordonné d'accomplir ses voeux."});
db.iliadfr.insert({ pg : 13949, line : "Il parla ainsi, et il excita la douleur de tous les Myrmidones; et"});
db.iliadfr.insert({ pg : 13950, line : "Éôs aux doigts couleur de rose les trouva gémissant autour du"});
db.iliadfr.insert({ pg : 13951, line : "cadavre."});
db.iliadfr.insert({ pg : 13953, line : "Mais le roi Agamemnôn pressa les hommes et les mulets de sortir"});
db.iliadfr.insert({ pg : 13954, line : "des tentes et d'amener le bois. Et un brave guerrier les"});
db.iliadfr.insert({ pg : 13955, line : "commandait, Mèrionès, compagnon du courageux Idoméneus. Et ils"});
db.iliadfr.insert({ pg : 13956, line : "allaient, avec les haches qui tranchent le bois, et les cordes"});
db.iliadfr.insert({ pg : 13957, line : "bien tressées, et les mulets marchaient devant eux. Et,"});
db.iliadfr.insert({ pg : 13958, line : "franchissant les pentes, et les rudes montées et les précipices,"});
db.iliadfr.insert({ pg : 13959, line : "ils arrivèrent aux sommets de l'Ida où abondent les sources. Et,"});
db.iliadfr.insert({ pg : 13960, line : "aussitôt, de leurs haches pesantes, ils abattirent les chênes"});
db.iliadfr.insert({ pg : 13961, line : "feuillus qui tombaient à grand bruit. Et les Akhaiens y attelaient"});
db.iliadfr.insert({ pg : 13962, line : "les mulets qui dévoraient la terre de leurs pieds, se hâtant"});
db.iliadfr.insert({ pg : 13963, line : "d'emporter vers le camp leur charge à travers les broussailles"});
db.iliadfr.insert({ pg : 13964, line : "épaisses. Et les Akhaiens traînaient aussi les troncs feuillus,"});
db.iliadfr.insert({ pg : 13965, line : "ainsi que le commandait Mèrionès, le compagnon d'Idoméneus qui"});
db.iliadfr.insert({ pg : 13966, line : "aime les braves. Et ils déposèrent le bois sur le rivage, là où"});
db.iliadfr.insert({ pg : 13967, line : "Akhilleus avait marqué le grand tombeau de Patroklos et le sien."});
db.iliadfr.insert({ pg : 13969, line : "Puis, ayant amassé un immense monceau, ils s'assirent, attendant."});
db.iliadfr.insert({ pg : 13970, line : "Et Akhilleus ordonna aux braves Myrmidones de se couvrir de leurs"});
db.iliadfr.insert({ pg : 13971, line : "armes et de monter sur leurs chars. Et ils se hâtaient de s'armer"});
db.iliadfr.insert({ pg : 13972, line : "et de monter sur leurs chars, guerriers et conducteurs. Et,"});
db.iliadfr.insert({ pg : 13973, line : "derrière les cavaliers, s'avançaient des nuées d'hommes de pied;"});
db.iliadfr.insert({ pg : 13974, line : "et, au milieu d'eux, Patroklos était porté par ses compagnons, qui"});
db.iliadfr.insert({ pg : 13975, line : "couvraient son cadavre de leurs cheveux qu'ils arrachaient. Et,"});
db.iliadfr.insert({ pg : 13976, line : "triste, le divin Akhilleus soutenait la tête de son irréprochable"});
db.iliadfr.insert({ pg : 13977, line : "compagnon qu'il allait envoyer dans le Hadès."});
db.iliadfr.insert({ pg : 13979, line : "Et quand ils furent arrivés au lieu marqué par Akhilleus, ils"});
db.iliadfr.insert({ pg : 13980, line : "déposèrent le corps et bâtirent le bûcher. Et le divin Akhilleus"});
db.iliadfr.insert({ pg : 13981, line : "aux pieds rapides eut une autre pensée. Et il coupa, à l'écart, sa"});
db.iliadfr.insert({ pg : 13982, line : "chevelure blonde qu'il avait laissée croître pour le fleuve"});
db.iliadfr.insert({ pg : 13983, line : "Sperkhios; et, gémissant, il dit, les yeux sur la mer sombre:"});
db.iliadfr.insert({ pg : 13985, line : "-- Sperkhios! c'est en vain que mon père Pèleus te promit qu'à mon"});
db.iliadfr.insert({ pg : 13986, line : "retour dans la chère terre de la patrie je couperais ma chevelure,"});
db.iliadfr.insert({ pg : 13987, line : "et que je te sacrifierais de saintes hécatombes et cinquante"});
db.iliadfr.insert({ pg : 13988, line : "béliers, à ta source, là où sont ton temple et ton autel parfumé."});
db.iliadfr.insert({ pg : 13989, line : "Le vieillard te fit ce voeu; mais tu n'as point exaucé son désir,"});
db.iliadfr.insert({ pg : 13990, line : "car je ne reverrai plus la chère terre de la patrie. C'est au"});
db.iliadfr.insert({ pg : 13991, line : "héros Patroklos que j'offre ma chevelure pour qu'il l'emporte."});
db.iliadfr.insert({ pg : 13993, line : "Ayant ainsi parlé, il déposa sa chevelure entre les mains de son"});
db.iliadfr.insert({ pg : 13994, line : "cher compagnon, augmentant ainsi la douleur de tous, et la lumière"});
db.iliadfr.insert({ pg : 13995, line : "de Hélios fût tombée tandis qu'ils pleuraient encore, si"});
db.iliadfr.insert({ pg : 13996, line : "Akhilleus, s'approchant d'Agamemnôn, ne lui eût dit:"});
db.iliadfr.insert({ pg : 13998, line : "-- Atréide, à qui tout le peuple Akhaien obéit, plus tard il"});
db.iliadfr.insert({ pg : 13999, line : "pourra se rassasier de larmes. Commande-lui de s'éloigner du"});
db.iliadfr.insert({ pg : 14000, line : "bûcher et de préparer son repas. Nous, les chefs, qui avons un"});
db.iliadfr.insert({ pg : 14001, line : "plus grand souci de Patroklos, restons seuls."});
db.iliadfr.insert({ pg : 14003, line : "Et le roi des hommes, Agamemnôn, l'ayant entendu, renvoya aussitôt"});
db.iliadfr.insert({ pg : 14004, line : "le peuple vers les nefs égales; et les ensevelisseurs, restant"});
db.iliadfr.insert({ pg : 14005, line : "seuls, amassèrent le bois. Et ils firent le bûcher de cent pieds"});
db.iliadfr.insert({ pg : 14006, line : "sur toutes ses faces, et, sur son faîte, ils déposèrent, pleins de"});
db.iliadfr.insert({ pg : 14007, line : "tristesse, le cadavre de Patroklos. Puis, ils égorgèrent et"});
db.iliadfr.insert({ pg : 14008, line : "écorchèrent devant le bûcher une foule de brebis grasses et de"});
db.iliadfr.insert({ pg : 14009, line : "boeufs aux pieds flexibles. Et le magnanime Akhilleus, couvrant"});
db.iliadfr.insert({ pg : 14010, line : "tout le cadavre de leur graisse, de la tête aux pieds, entassa"});
db.iliadfr.insert({ pg : 14011, line : "tout autour leurs chairs écorchées. Et, s'inclinant sur le lit"});
db.iliadfr.insert({ pg : 14012, line : "funèbre, il y plaça des amphores de miel et d'huile. Puis, il jeta"});
db.iliadfr.insert({ pg : 14013, line : "sur le bûcher quatre chevaux aux beaux cous. Neuf chiens familiers"});
db.iliadfr.insert({ pg : 14014, line : "mangeaient autour de sa table. Il en tua deux qu'il jeta dans le"});
db.iliadfr.insert({ pg : 14015, line : "bûcher. Puis, accomplissant une mauvaise pensée, il égorgea douze"});
db.iliadfr.insert({ pg : 14016, line : "nobles enfants des Troiens magnanimes. Puis, il mit le feu au"});
db.iliadfr.insert({ pg : 14017, line : "bûcher, afin qu'il fût consumé, et il gémit, appelant son cher"});
db.iliadfr.insert({ pg : 14018, line : "compagnon:"});
db.iliadfr.insert({ pg : 14020, line : "-- Sois content de moi, ô Patroklos! dans le Hadès, car j'ai"});
db.iliadfr.insert({ pg : 14021, line : "accompli tout ce que je t'ai promis. Le feu consume avec toi douze"});
db.iliadfr.insert({ pg : 14022, line : "nobles enfants des magnanimes Troiens. Pour le Priamide Hektôr, je"});
db.iliadfr.insert({ pg : 14023, line : "ne le livrerai point au feu, mais aux chiens."});
db.iliadfr.insert({ pg : 14025, line : "Il parla ainsi dans sa colère; mais les chiens ne devaient point"});
db.iliadfr.insert({ pg : 14026, line : "déchirer Hektôr, car, jour et nuit, la fille de Zeus, Aphroditè,"});
db.iliadfr.insert({ pg : 14027, line : "les chassait au loin, oignant le corps d'une huile ambroisienne,"});
db.iliadfr.insert({ pg : 14028, line : "afin que le Pèléide ne le déchirât point en le traînant. Et"});
db.iliadfr.insert({ pg : 14029, line : "Phoibos Apollôn enveloppait d'une nuée ouranienne le lieu où était"});
db.iliadfr.insert({ pg : 14030, line : "couché le cadavre, de peur que la force de Hélios n'en desséchât"});
db.iliadfr.insert({ pg : 14031, line : "les nerfs et les chairs."});
db.iliadfr.insert({ pg : 14033, line : "Mais le bûcher de Patroklos ne brûlait point. Alors le divin"});
db.iliadfr.insert({ pg : 14034, line : "Akhilleus aux pieds rapides pria à l'écart les deux vents Boréas"});
db.iliadfr.insert({ pg : 14035, line : "et Zéphyros, leur promettant de riches sacrifices. Et, faisant des"});
db.iliadfr.insert({ pg : 14036, line : "libations avec une coupe d'or, il les supplia de venir, afin de"});
db.iliadfr.insert({ pg : 14037, line : "consumer promptement le cadavre, en enflammant le bûcher. Et la"});
db.iliadfr.insert({ pg : 14038, line : "rapide Iris entendit ses prières et s'envola en messagère auprès"});
db.iliadfr.insert({ pg : 14039, line : "des vents. Et, rassemblés en foule dans la demeure du violent"});
db.iliadfr.insert({ pg : 14040, line : "Zéphyros, ils célébraient un festin. Et la rapide Iris survint et"});
db.iliadfr.insert({ pg : 14041, line : "s'arrêta sur le seuil de pierre. Et, dès qu'ils l'eurent vue de"});
db.iliadfr.insert({ pg : 14042, line : "leurs yeux, tous se levèrent, et chacun l'appela près de lui. Mais"});
db.iliadfr.insert({ pg : 14043, line : "elle ne voulut point s'asseoir et leur dit:"});
db.iliadfr.insert({ pg : 14045, line : "-- Ce n'est pas le temps de m'asseoir. Je retourne aux bouches de"});
db.iliadfr.insert({ pg : 14046, line : "l'Okéanos, dans la terre des Aithiopiens, là où ils sacrifient des"});
db.iliadfr.insert({ pg : 14047, line : "hécatombes aux immortels, et j'en ai ma part. Mais Akhilleus"});
db.iliadfr.insert({ pg : 14048, line : "appelle Boréas et le sonore Zéphyros. Il les supplie de venir,"});
db.iliadfr.insert({ pg : 14049, line : "leur promettant de riches sacrifices s'ils excitent le feu à"});
db.iliadfr.insert({ pg : 14050, line : "consumer le bûcher sur lequel gît Patroklos que pleurent tous les"});
db.iliadfr.insert({ pg : 14051, line : "Akhaiens."});
db.iliadfr.insert({ pg : 14053, line : "Elle parla ainsi et s'envola. Et les deux vents se ruèrent avec un"});
db.iliadfr.insert({ pg : 14054, line : "bruit immense, chassant devant eux les nuées tumultueuses. Et ils"});
db.iliadfr.insert({ pg : 14055, line : "traversèrent la mer, et l'eau se souleva sous leur souffle"});
db.iliadfr.insert({ pg : 14056, line : "violent; et ils arrivèrent devant la riche Troiè et se jetèrent"});
db.iliadfr.insert({ pg : 14057, line : "sur le feu; et toute la nuit, soufflant horriblement, ils"});
db.iliadfr.insert({ pg : 14058, line : "irritèrent les flammes du bûcher; et, toute la nuit, le rapide"});
db.iliadfr.insert({ pg : 14059, line : "Akhilleus, puisant le vin à pleine coupe d'un kratère d'or, et le"});
db.iliadfr.insert({ pg : 14060, line : "répandant, arrosa la terre, appelant l'âme du malheureux"});
db.iliadfr.insert({ pg : 14061, line : "Patroklos. Comme un père qui se lamente, en brûlant les ossements"});
db.iliadfr.insert({ pg : 14062, line : "de son jeune fils dont la mort accable ses malheureux parents de"});
db.iliadfr.insert({ pg : 14063, line : "tristesse; de même Akhilleus gémissait en brûlant les ossements de"});
db.iliadfr.insert({ pg : 14064, line : "son compagnon, se roulant devant le bûcher, et se lamentant."});
db.iliadfr.insert({ pg : 14066, line : "Et quand l'étoile du matin reparut, messagère de lumière, et,"});
db.iliadfr.insert({ pg : 14067, line : "après elle, quand Éôs au péplos couleur de safran se répandit sur"});
db.iliadfr.insert({ pg : 14068, line : "la mer, alors le bûcher s'apaisa et la flamme s'éteignit, et les"});
db.iliadfr.insert({ pg : 14069, line : "vents partirent, s'en retournant dans leur demeure, à travers la"});
db.iliadfr.insert({ pg : 14070, line : "mer thrèkienne, dont les flots soulevés grondaient. Et le Pèléide,"});
db.iliadfr.insert({ pg : 14071, line : "quittant le bûcher, se coucha accablé de fatigue, et le doux"});
db.iliadfr.insert({ pg : 14072, line : "sommeil le saisit. Mais bientôt le bruit et le tumulte de ceux qui"});
db.iliadfr.insert({ pg : 14073, line : "se rassemblaient autour de l'Atréide le réveillèrent. Et il se"});
db.iliadfr.insert({ pg : 14074, line : "leva, et leur dit:"});
db.iliadfr.insert({ pg : 14076, line : "-- Atréides, et vous, princes des Akhaiens, éteignez avec du vin"});
db.iliadfr.insert({ pg : 14077, line : "noir toutes les parties du bûcher que le feu a brûlées, et nous"});
db.iliadfr.insert({ pg : 14078, line : "recueillerons les os de Patroklos Ménoitiade. Ils sont faciles à"});
db.iliadfr.insert({ pg : 14079, line : "reconnaître, car le cadavre était au milieu du bûcher, et, loin de"});
db.iliadfr.insert({ pg : 14080, line : "lui tout autour, brûlaient confusément les chevaux et les hommes."});
db.iliadfr.insert({ pg : 14081, line : "Déposons dans une urne d'or ces os recouverts d'une double"});
db.iliadfr.insert({ pg : 14082, line : "graisse, jusqu'à ce que je descende moi-même dans le Hadès. Je ne"});
db.iliadfr.insert({ pg : 14083, line : "demande point maintenant un grand sépulcre. Que celui-ci soit"});
db.iliadfr.insert({ pg : 14084, line : "simple. Mais vous, Akhaiens, qui survivrez sur vos nefs bien"});
db.iliadfr.insert({ pg : 14085, line : "construites, vous nous élèverez, après ma mort, un vaste et grand"});
db.iliadfr.insert({ pg : 14086, line : "tombeau."});
db.iliadfr.insert({ pg : 14088, line : "Il parla ainsi, et ils obéirent au rapide Pèléiôn. Et ils"});
db.iliadfr.insert({ pg : 14089, line : "éteignirent d'abord avec du vin noir toutes les parties du bûcher"});
db.iliadfr.insert({ pg : 14090, line : "que le feu avait brûlées; et la cendre épaisse tomba. Puis, en"});
db.iliadfr.insert({ pg : 14091, line : "pleurant, ils déposèrent dans une urne d'or, couverts d'une double"});
db.iliadfr.insert({ pg : 14092, line : "graisse, les os blancs de leur compagnon plein de douceur, et ils"});
db.iliadfr.insert({ pg : 14093, line : "mirent, sous la tente du Pèléide, cette urne enveloppée d'un voile"});
db.iliadfr.insert({ pg : 14094, line : "léger. Puis, marquant la place du tombeau, ils en creusèrent les"});
db.iliadfr.insert({ pg : 14095, line : "fondements autour du bûcher, et ils mirent la terre en monceau, et"});
db.iliadfr.insert({ pg : 14096, line : "ils partirent, ayant élevé le tombeau."});
db.iliadfr.insert({ pg : 14098, line : "Mais Akhilleus retint le peuple en ce lieu, et le fit asseoir en"});
db.iliadfr.insert({ pg : 14099, line : "un cercle immense, et il fit apporter des nefs les prix: des"});
db.iliadfr.insert({ pg : 14100, line : "vases, des trépieds, des chevaux, des mulets, des boeufs aux"});
db.iliadfr.insert({ pg : 14101, line : "fortes têtes, des femmes aux belles ceintures, et du fer brillant."});
db.iliadfr.insert({ pg : 14102, line : "Et, d'abord, il offrit des prix illustres aux cavaliers rapides:"});
db.iliadfr.insert({ pg : 14103, line : "une femme irréprochable, habile aux travaux, et un trépied à anse,"});
db.iliadfr.insert({ pg : 14104, line : "contenant vingt-deux mesures, pour le premier vainqueur; pour le"});
db.iliadfr.insert({ pg : 14105, line : "second, une jument de six ans, indomptée et pleine d'un mulet;"});
db.iliadfr.insert({ pg : 14106, line : "pour le troisième, un vase tout neuf, beau, blanc, et contenant"});
db.iliadfr.insert({ pg : 14107, line : "quatre mesures; pour le quatrième, deux talents d'or; et pour le"});
db.iliadfr.insert({ pg : 14108, line : "cinquième, une urne neuve à deux anses. Et le Pèléide se leva et"});
db.iliadfr.insert({ pg : 14109, line : "dit aux Argiens:"});
db.iliadfr.insert({ pg : 14111, line : "-- Atréides, et vous, très braves Akhaiens, voici, dans"});
db.iliadfr.insert({ pg : 14112, line : "l'enceinte, les prix offerts aux cavaliers. Si les Akhaiens"});
db.iliadfr.insert({ pg : 14113, line : "luttaient aujourd'hui pour un autre mort, certes, j'emporterais"});
db.iliadfr.insert({ pg : 14114, line : "ces prix dans mes tentes, car vous savez que mes chevaux"});
db.iliadfr.insert({ pg : 14115, line : "l'emportent sur tous, étant immortels. Poseidaôn les donna à mon"});
db.iliadfr.insert({ pg : 14116, line : "père Pèleus qui me les a donnés. Mais ni moi, ni mes chevaux aux"});
db.iliadfr.insert({ pg : 14117, line : "sabots massifs nous ne combattrons. Ils ont perdu l'irréprochable"});
db.iliadfr.insert({ pg : 14118, line : "vigueur de leur doux conducteur qui baignait leurs crinières"});
db.iliadfr.insert({ pg : 14119, line : "d'huile liquide, après les avoir lavées dans une eau pure; et"});
db.iliadfr.insert({ pg : 14120, line : "maintenant ils pleurent, les crinières pendantes, et ils restent"});
db.iliadfr.insert({ pg : 14121, line : "immobiles et pleins de tristesse. Mais vous qui, parmi tous les"});
db.iliadfr.insert({ pg : 14122, line : "Akhaiens, vous confiez en vos chevaux et en vos chars solides,"});
db.iliadfr.insert({ pg : 14123, line : "descendez dans l'enceinte."});
db.iliadfr.insert({ pg : 14125, line : "Le Pèléide parla ainsi, et de rapides cavaliers se levèrent. Et,"});
db.iliadfr.insert({ pg : 14126, line : "le premier, se leva le roi des hommes, Eumèlos, le fils bien-aimé"});
db.iliadfr.insert({ pg : 14127, line : "d'Admètès, très habile à mener un char. Et après lui, se leva le"});
db.iliadfr.insert({ pg : 14128, line : "brave Diomèdès Tydéide, conduisant sous le joug les chevaux de"});
db.iliadfr.insert({ pg : 14129, line : "Trôos qu'il avait enlevés autrefois à Ainéias, quand celui-ci fut"});
db.iliadfr.insert({ pg : 14130, line : "sauvé par Apollôn. Et, après Diomèdès, se leva le blond Ménélaos"});
db.iliadfr.insert({ pg : 14131, line : "Atréide, aimé de Zeus. Et il conduisait sous le joug deux chevaux"});
db.iliadfr.insert({ pg : 14132, line : "rapides: Aithè, jument d'Agamemnôn, et Podargos, qui lui"});
db.iliadfr.insert({ pg : 14133, line : "appartenait. Et l'Ankhisiade Ekhépôlos avait donné Aithè à"});
db.iliadfr.insert({ pg : 14134, line : "Agamemnôn, afin de ne point le suivre vers la haute Ilios. Et il"});
db.iliadfr.insert({ pg : 14135, line : "était resté, vivant dans les délices, car Zeus lui avait donné de"});
db.iliadfr.insert({ pg : 14136, line : "grandes richesses, et il habitait la grande Sikiôn. Et Ménélaos la"});
db.iliadfr.insert({ pg : 14137, line : "conduisait sous le joug, pleine d'ardeur. Et, après l'Atréide, se"});
db.iliadfr.insert({ pg : 14138, line : "leva, conduisant deux beaux chevaux, Antilokhos, l'illustre fils"});
db.iliadfr.insert({ pg : 14139, line : "du magnanime roi Nestôr Nèlèiade. Et les chevaux rapides qui"});
db.iliadfr.insert({ pg : 14140, line : "traînaient son char étaient pyliens. Et le père, debout auprès de"});
db.iliadfr.insert({ pg : 14141, line : "son fils, donnait des conseils excellents au jeune homme déjà"});
db.iliadfr.insert({ pg : 14142, line : "plein de prudence:"});
db.iliadfr.insert({ pg : 14144, line : "-- Antilokhos, certes, Zeus et Poseidaôn, t'ayant aimé tout jeune,"});
db.iliadfr.insert({ pg : 14145, line : "t'ont enseigné à mener un char; c'est pourquoi on ne peut"});
db.iliadfr.insert({ pg : 14146, line : "t'instruire davantage. Tu sais tourner habilement la borne, mais"});
db.iliadfr.insert({ pg : 14147, line : "tes chevaux sont lourds, et je crains un malheur. Les autres ne te"});
db.iliadfr.insert({ pg : 14148, line : "sont pas supérieurs en science, mais leurs chevaux sont plus"});
db.iliadfr.insert({ pg : 14149, line : "rapides. Allons, ami, réfléchis à tout, afin que les prix ne"});
db.iliadfr.insert({ pg : 14150, line : "t'échappent pas. Le bûcheron vaut mieux par l'adresse que par la"});
db.iliadfr.insert({ pg : 14151, line : "force. C'est par son art que le pilote dirige sur la noire mer une"});
db.iliadfr.insert({ pg : 14152, line : "nef rapide, battue par les vents; et le conducteur de chars"});
db.iliadfr.insert({ pg : 14153, line : "l'emporte par son habileté sur le conducteur de chars. Celui qui"});
db.iliadfr.insert({ pg : 14154, line : "s'abandonne à ses chevaux et à son char vagabonde follement çà et"});
db.iliadfr.insert({ pg : 14155, line : "là, et ses chevaux s'emportent dans le stade, et il ne peut les"});
db.iliadfr.insert({ pg : 14156, line : "retenir. Mais celui qui sait les choses utiles, quand il conduit"});
db.iliadfr.insert({ pg : 14157, line : "des chevaux lourds, regardant toujours la borne, l'effleure en la"});
db.iliadfr.insert({ pg : 14158, line : "tournant. Et il ne lâche point tout d'abord les rênes en cuir de"});
db.iliadfr.insert({ pg : 14159, line : "boeuf, mais, les tenant d'une main ferme, il observe celui qui le"});
db.iliadfr.insert({ pg : 14160, line : "précède. Je vais te montrer la borne. On la reconnaît aisément. Là"});
db.iliadfr.insert({ pg : 14161, line : "s'élève un tronc desséché, d'une aune environ hors de terre et que"});
db.iliadfr.insert({ pg : 14162, line : "la pluie ne peut nourrir. C'est le tronc d'un chêne ou d'un pin."});
db.iliadfr.insert({ pg : 14163, line : "Devant lui sont deux pierres blanches, posées de l'un et l'autre"});
db.iliadfr.insert({ pg : 14164, line : "côté, au détour du chemin, et, en deçà comme au-delà, s'étend"});
db.iliadfr.insert({ pg : 14165, line : "l'hippodrome aplani. C'est le tombeau d'un homme mort autrefois,"});
db.iliadfr.insert({ pg : 14166, line : "ou une limite plantée par les anciens hommes, et c'est la borne"});
db.iliadfr.insert({ pg : 14167, line : "que le divin Akhilleus aux pieds rapides vous a marquée. Quand tu"});
db.iliadfr.insert({ pg : 14168, line : "en approcheras, pousse tout auprès tes chevaux et ton char."});
db.iliadfr.insert({ pg : 14169, line : "Penche-toi, de ton char bien construit, un peu sur la gauche, et"});
db.iliadfr.insert({ pg : 14170, line : "excite le cheval de droite de la voix et du fouet, en lui lâchant"});
db.iliadfr.insert({ pg : 14171, line : "toutes les rênes. Que ton cheval de gauche rase la borne, de façon"});
db.iliadfr.insert({ pg : 14172, line : "que le moyeu de la roue la touche presque; mais évite de heurter"});
db.iliadfr.insert({ pg : 14173, line : "la pierre, de peur de blesser tes chevaux et de briser ton char,"});
db.iliadfr.insert({ pg : 14174, line : "ce qui ferait la joie des autres, mais ta propre honte. Enfin,"});
db.iliadfr.insert({ pg : 14175, line : "ami, sois adroit et prudent. Si tu peux dépasser la borne le"});
db.iliadfr.insert({ pg : 14176, line : "premier, il n'en est aucun qui ne te poursuive vivement, mais nul"});
db.iliadfr.insert({ pg : 14177, line : "ne te devancera, quand même on pousserait derrière toi le divin"});
db.iliadfr.insert({ pg : 14178, line : "Atréiôn, ce rapide cheval d'Adrestès, qui était de race divine, ou"});
db.iliadfr.insert({ pg : 14179, line : "même les illustres chevaux de Laomédôn qui furent nourris ici."});
db.iliadfr.insert({ pg : 14181, line : "Et le Nèlèiôn Nestôr, ayant ainsi parlé et enseigné toute chose à"});
db.iliadfr.insert({ pg : 14182, line : "son fils, se rassit. Et, le cinquième, Mèrionès conduisait deux"});
db.iliadfr.insert({ pg : 14183, line : "chevaux aux beaux crins."});
db.iliadfr.insert({ pg : 14185, line : "Puis, ils montèrent tous sur leurs chars, et ils jetèrent les"});
db.iliadfr.insert({ pg : 14186, line : "sorts; et Akhilleus les remua, et Antilokhos Nestôréide vint le"});
db.iliadfr.insert({ pg : 14187, line : "premier, puis le roi Eumèlos, puis l'Atréide Ménélaos illustre par"});
db.iliadfr.insert({ pg : 14188, line : "sa lance, puis Mèrionès, et le dernier fut le Tydéide, le plus"});
db.iliadfr.insert({ pg : 14189, line : "brave de tous. Et ils se placèrent dans cet ordre, et Akhilleus"});
db.iliadfr.insert({ pg : 14190, line : "leur marqua la borne, au loin dans la plaine; et il envoya comme"});
db.iliadfr.insert({ pg : 14191, line : "inspecteur le divin Phoinix, compagnon de son père, afin qu'il"});
db.iliadfr.insert({ pg : 14192, line : "surveillât la course et dît la vérité."});
db.iliadfr.insert({ pg : 14194, line : "Et tous ensemble, levant le fouet sur les chevaux et les excitant"});
db.iliadfr.insert({ pg : 14195, line : "du fouet et de la voix, s'élancèrent dans la plaine, loin des"});
db.iliadfr.insert({ pg : 14196, line : "nefs. Et la poussière montait autour de leurs poitrines, comme un"});
db.iliadfr.insert({ pg : 14197, line : "nuage ou comme une tempête; et les crinières flottaient au vent;"});
db.iliadfr.insert({ pg : 14198, line : "et les chars tantôt semblaient s'enfoncer en terre, et tantôt"});
db.iliadfr.insert({ pg : 14199, line : "bondissaient au-dessus. Mais les conducteurs se tenaient fermes"});
db.iliadfr.insert({ pg : 14200, line : "sur leurs sièges, et leur coeur palpitait du désir de la victoire,"});
db.iliadfr.insert({ pg : 14201, line : "et chacun excitait ses chevaux qui volaient, soulevant la"});
db.iliadfr.insert({ pg : 14202, line : "poussière de la plaine."});
db.iliadfr.insert({ pg : 14204, line : "Mais quand les chevaux rapides, ayant atteint la limite de la"});
db.iliadfr.insert({ pg : 14205, line : "course, revinrent vers la blanche mer, l'ardeur des combattants et"});
db.iliadfr.insert({ pg : 14206, line : "la vitesse de la course devinrent visibles. Et les rapides juments"});
db.iliadfr.insert({ pg : 14207, line : "du Phèrètiade parurent les premières; et les chevaux troiens de"});
db.iliadfr.insert({ pg : 14208, line : "Diomèdès les suivaient de si près, qu'ils semblaient monter sur le"});
db.iliadfr.insert({ pg : 14209, line : "char. Et le dos et les larges épaules d'Eumèlos étaient chauffés"});
db.iliadfr.insert({ pg : 14210, line : "de leur souffle, car ils posaient sur lui leurs têtes. Et, certes,"});
db.iliadfr.insert({ pg : 14211, line : "Diomèdès eût vaincu ou rendu la lutte égale, si Phoibos Apollôn,"});
db.iliadfr.insert({ pg : 14212, line : "irrité contre le fils de Tydeus, n'eût fait tomber de ses mains le"});
db.iliadfr.insert({ pg : 14213, line : "fouet splendide. Et des larmes de colère jaillirent de ses yeux,"});
db.iliadfr.insert({ pg : 14214, line : "quand il vit les juments d'Eumèlos se précipiter plus rapides, et"});
db.iliadfr.insert({ pg : 14215, line : "ses propres chevaux se ralentir, n'étant plus aiguillonnés."});
db.iliadfr.insert({ pg : 14217, line : "Mais Apollôn, retardant le Tydéide, ne put se cacher d'Athènè. Et,"});
db.iliadfr.insert({ pg : 14218, line : "courant au prince des peuples, elle lui rendit son fouet et"});
db.iliadfr.insert({ pg : 14219, line : "remplit ses chevaux de vigueur. Puis, furieuse, et poursuivant le"});
db.iliadfr.insert({ pg : 14220, line : "fils d'Admètès, elle brisa le joug des juments, qui se dérobèrent."});
db.iliadfr.insert({ pg : 14221, line : "Et le timon tomba rompu; et Eumèlos aussi tomba auprès de la roue,"});
db.iliadfr.insert({ pg : 14222, line : "se déchirant les bras, la bouche et les narines. Et il resta muet,"});
db.iliadfr.insert({ pg : 14223, line : "le front meurtri et les yeux pleins de larmes."});
db.iliadfr.insert({ pg : 14225, line : "Alors, Diomèdès, le devançant, poussa ses chevaux aux sabots"});
db.iliadfr.insert({ pg : 14226, line : "massifs, bien au-delà de tous, car Athènè leur avait donné une"});
db.iliadfr.insert({ pg : 14227, line : "grande vigueur et accordait la victoire au Tydéide. Et, après lui,"});
db.iliadfr.insert({ pg : 14228, line : "le blond Ménélaos Atréide menait son char, puis Antilokhos, qui"});
db.iliadfr.insert({ pg : 14229, line : "exhortait les chevaux de son père:"});
db.iliadfr.insert({ pg : 14231, line : "-- Prenez courage, et courez plus rapidement. Certes, je ne vous"});
db.iliadfr.insert({ pg : 14232, line : "ordonne point de lutter contre les chevaux du brave Tydéide, car"});
db.iliadfr.insert({ pg : 14233, line : "Athènè donne la vitesse à leurs pieds et accorde la victoire à"});
db.iliadfr.insert({ pg : 14234, line : "leur maître; mais atteignez les chevaux de l'Atréide, et ne"});
db.iliadfr.insert({ pg : 14235, line : "faiblissez point, de peur que Aithè, qui n'est qu'une jument, vous"});
db.iliadfr.insert({ pg : 14236, line : "couvre de honte."});
db.iliadfr.insert({ pg : 14238, line : "Pourquoi tardez-vous, mes braves? Mais je vous le dis, et, certes,"});
db.iliadfr.insert({ pg : 14239, line : "ceci s'accomplira: Nestôr, le prince des peuples, ne se souciera"});
db.iliadfr.insert({ pg : 14240, line : "plus de vous; et il vous percera de l'airain aigu, si, par"});
db.iliadfr.insert({ pg : 14241, line : "lâcheté, nous ne remportons qu'un prix vil. Hâtez-vous et"});
db.iliadfr.insert({ pg : 14242, line : "poursuivez promptement l'Atréide. Moi, je vais méditer une ruse,"});
db.iliadfr.insert({ pg : 14243, line : "et je le devancerai au détour du chemin, et je le tromperai."});
db.iliadfr.insert({ pg : 14245, line : "Il parla ainsi, et les chevaux, effrayés des menaces du prince,"});
db.iliadfr.insert({ pg : 14246, line : "coururent plus rapidement. Et le brave Antilokhos vit que le"});
db.iliadfr.insert({ pg : 14247, line : "chemin se rétrécissait. La terre était défoncée par l'amas des"});
db.iliadfr.insert({ pg : 14248, line : "eaux de l'hiver, et une partie du chemin était rompue, formant un"});
db.iliadfr.insert({ pg : 14249, line : "trou profond. C'était là que se dirigeait Ménélaos pour éviter le"});
db.iliadfr.insert({ pg : 14250, line : "choc des chars. Et Antilokhos y poussa aussi ses chevaux aux"});
db.iliadfr.insert({ pg : 14251, line : "sabots massifs, hors de la voie, sur le bord du terrain en pente."});
db.iliadfr.insert({ pg : 14252, line : "Et l'Atréide fut saisi de crainte et dit à Antilokhos:"});
db.iliadfr.insert({ pg : 14254, line : "-- Antilokhos, tu mènes tes chevaux avec imprudence. Le chemin est"});
db.iliadfr.insert({ pg : 14255, line : "étroit, mais il sera bientôt plus large. Prends garde de nous"});
db.iliadfr.insert({ pg : 14256, line : "briser tous deux en heurtant mon char."});
db.iliadfr.insert({ pg : 14258, line : "Il parla ainsi, mais Antilokhos, comme s'il ne l'avait point"});
db.iliadfr.insert({ pg : 14259, line : "entendu, aiguillonna plus encore ses chevaux. Aussi rapides que le"});
db.iliadfr.insert({ pg : 14260, line : "jet d'un disque que lance de l'épaule un jeune homme qui éprouve"});
db.iliadfr.insert({ pg : 14261, line : "ses forces, les deux chars s'élancèrent de front. Mais l'Atréide"});
db.iliadfr.insert({ pg : 14262, line : "ralentit sa course et attendit, de peur que les chevaux aux sabots"});
db.iliadfr.insert({ pg : 14263, line : "massifs, se heurtant dans le chemin, ne renversassent les chars,"});
db.iliadfr.insert({ pg : 14264, line : "et qu'Antilokhos et lui, en se hâtant pour la victoire, ne fussent"});
db.iliadfr.insert({ pg : 14265, line : "précipités dans la poussière. Mais le blond Ménélaos, irrité, lui"});
db.iliadfr.insert({ pg : 14266, line : "dit:"});
db.iliadfr.insert({ pg : 14268, line : "-- Antilokhos, aucun homme n'est plus perfide que toi! Va! c'est"});
db.iliadfr.insert({ pg : 14269, line : "bien faussement que nous te disions sage. Mais tu ne remporteras"});
db.iliadfr.insert({ pg : 14270, line : "point le prix sans te parjurer."});
db.iliadfr.insert({ pg : 14272, line : "Ayant ainsi parlé, il exhorta ses chevaux et leur cria:"});
db.iliadfr.insert({ pg : 14274, line : "-- Ne me retardez pas, et n'ayez point le coeur triste. Leurs"});
db.iliadfr.insert({ pg : 14275, line : "pieds et leurs genoux seront plus tôt fatigués que les vôtres, car"});
db.iliadfr.insert({ pg : 14276, line : "ils sont vieux tous deux."});
db.iliadfr.insert({ pg : 14278, line : "Il parla ainsi, et ses chevaux, effrayés par la voix du roi,"});
db.iliadfr.insert({ pg : 14279, line : "s'élancèrent, et atteignirent aussitôt ceux d'Antilokhos."});
db.iliadfr.insert({ pg : 14281, line : "Cependant les Argiens, assis dans le stade, regardaient les chars"});
db.iliadfr.insert({ pg : 14282, line : "qui volaient dans la plaine, en soulevant la poussière. Et"});
db.iliadfr.insert({ pg : 14283, line : "Idoméneus, chef des Krètois, les vit le premier. Étant assis hors"});
db.iliadfr.insert({ pg : 14284, line : "du stade, sur une hauteur, il entendit une voix qui excitait les"});
db.iliadfr.insert({ pg : 14285, line : "chevaux, et il vit celui qui accourait le premier, dont toute la"});
db.iliadfr.insert({ pg : 14286, line : "robe était rouge, et qui avait au front un signe blanc, rond comme"});
db.iliadfr.insert({ pg : 14287, line : "l'orbe de Sélénè. Et il se leva et dit aux Argiens:"});
db.iliadfr.insert({ pg : 14289, line : "-- Ô amis, princes et chefs des Argiens, voyez-vous ces chevaux"});
db.iliadfr.insert({ pg : 14290, line : "comme moi? Il me semble que ce sont d'autres chevaux et un autre"});
db.iliadfr.insert({ pg : 14291, line : "conducteur qui tiennent maintenant la tête. Peut-être les premiers"});
db.iliadfr.insert({ pg : 14292, line : "au départ ont-ils subi un malheur dans la plaine. Je les ai vus"});
db.iliadfr.insert({ pg : 14293, line : "tourner la borne et je ne les vois plus, et cependant j'embrasse"});
db.iliadfr.insert({ pg : 14294, line : "toute la plaine troienne. Ou les rênes auront échappé au"});
db.iliadfr.insert({ pg : 14295, line : "conducteur et il n'a pu tourner la borne heureusement, ou il est"});
db.iliadfr.insert({ pg : 14296, line : "tombé, brisant son char, et ses juments furieuses se sont"});
db.iliadfr.insert({ pg : 14297, line : "dérobées. Mais regardez vous-mêmes; je ne vois point clairement"});
db.iliadfr.insert({ pg : 14298, line : "encore; cependant, il me semble que c'est un guerrier Aitôlien qui"});
db.iliadfr.insert({ pg : 14299, line : "commande parmi les Argiens, le brave fils de Tydeus dompteur de"});
db.iliadfr.insert({ pg : 14300, line : "chevaux, Diomèdès."});
db.iliadfr.insert({ pg : 14302, line : "Et le rapide Aias, fils d'Oileus, lui répondit amèrement:"});
db.iliadfr.insert({ pg : 14304, line : "-- Idoméneus, pourquoi toujours bavarder? Ce sont ces mêmes"});
db.iliadfr.insert({ pg : 14305, line : "juments aux pieds aériens qui arrivent à travers la vaste plaine."});
db.iliadfr.insert({ pg : 14306, line : "Tu n'es certes pas le plus jeune parmi les Argiens, et les yeux"});
db.iliadfr.insert({ pg : 14307, line : "qui sortent de ta tête ne sont point les plus perçants. Mais tu"});
db.iliadfr.insert({ pg : 14308, line : "bavardes sans cesse. Il ne te convient pas de tant parler, car"});
db.iliadfr.insert({ pg : 14309, line : "beaucoup d'autres ici valent mieux que toi. Ce sont les juments"});
db.iliadfr.insert({ pg : 14310, line : "d'Eumèlos qui arrivent les premières, et c'est lui qui tient"});
db.iliadfr.insert({ pg : 14311, line : "toujours les rênes."});
db.iliadfr.insert({ pg : 14313, line : "Et le chef des Krètois, irrité, lui répondit:"});
db.iliadfr.insert({ pg : 14315, line : "-- Aias, excellent pour la querelle, homme injurieux, le dernier"});
db.iliadfr.insert({ pg : 14316, line : "des Argiens, ton âme est toute féroce! Allons! déposons un"});
db.iliadfr.insert({ pg : 14317, line : "trépied, ou un vase, et prenons tous deux pour arbitre l'Atréide"});
db.iliadfr.insert({ pg : 14318, line : "Agamemnôn. Qu'il dise quels sont ces chevaux, et tu le sauras à"});
db.iliadfr.insert({ pg : 14319, line : "tes dépens."});
db.iliadfr.insert({ pg : 14321, line : "Il parla ainsi, et le rapide Aias, fils d'Oileus, plein de colère,"});
db.iliadfr.insert({ pg : 14322, line : "se leva pour lui répondre par d'outrageantes paroles, et il y"});
db.iliadfr.insert({ pg : 14323, line : "aurait eu une querelle entre eux, si Akhilleus, s'étant levé,"});
db.iliadfr.insert({ pg : 14324, line : "n'eût parlé:"});
db.iliadfr.insert({ pg : 14326, line : "-- Ne vous adressez pas plus longtemps d'injurieuses paroles, Aias"});
db.iliadfr.insert({ pg : 14327, line : "et Idoméneus. Cela ne convient point, et vous blâmeriez qui en"});
db.iliadfr.insert({ pg : 14328, line : "ferait autant. Restez assis, et regardez. Ces chevaux qui se"});
db.iliadfr.insert({ pg : 14329, line : "hâtent pour la victoire vont arriver. Vous verrez alors quels sont"});
db.iliadfr.insert({ pg : 14330, line : "les premiers et les seconds."});
db.iliadfr.insert({ pg : 14332, line : "Il parla ainsi, et le Tydéide arriva, agitant sans relâche le"});
db.iliadfr.insert({ pg : 14333, line : "fouet sur ses chevaux, qui, en courant, soulevaient une haute"});
db.iliadfr.insert({ pg : 14334, line : "poussière qui enveloppait leur conducteur. Et le char, orné d'or"});
db.iliadfr.insert({ pg : 14335, line : "et d'étain, était enlevé par les chevaux rapides; et l'orbe des"});
db.iliadfr.insert({ pg : 14336, line : "roues laissait à peine une trace dans la poussière, tant ils"});
db.iliadfr.insert({ pg : 14337, line : "couraient rapidement. Et le char s'arrêta au milieu du stade; et"});
db.iliadfr.insert({ pg : 14338, line : "des flots de sueur coulaient de la tête et du poitrail des"});
db.iliadfr.insert({ pg : 14339, line : "chevaux. Et Diomèdès sauta de son char brillant et appuya le fouet"});
db.iliadfr.insert({ pg : 14340, line : "contre le joug. Et, sans tarder, le brave Sthénélos saisit le"});
db.iliadfr.insert({ pg : 14341, line : "prix. Il remit la femme et le trépied à deux anses à ses"});
db.iliadfr.insert({ pg : 14342, line : "magnanimes compagnons, et lui-même détela les chevaux."});
db.iliadfr.insert({ pg : 14344, line : "Et, après Diomèdès, le Nèlèiôn Antilokhos arriva, poussant ses"});
db.iliadfr.insert({ pg : 14345, line : "chevaux et devançant Ménélaos par ruse et non par la rapidité de"});
db.iliadfr.insert({ pg : 14346, line : "sa course. Et Ménélaos le poursuivait de près. Autant est près de"});
db.iliadfr.insert({ pg : 14347, line : "la roue un cheval qui traîne son maître, sur un char, dans la"});
db.iliadfr.insert({ pg : 14348, line : "plaine, tandis que les derniers crins de sa queue touchent les"});
db.iliadfr.insert({ pg : 14349, line : "jantes, et qu'il court à travers l'espace; autant Ménélaos suivait"});
db.iliadfr.insert({ pg : 14350, line : "de près le brave Antilokhos. Bien que resté en arrière à un jet de"});
db.iliadfr.insert({ pg : 14351, line : "disque, il l'avait atteint aussitôt, car Aithè aux beaux crins, la"});
db.iliadfr.insert({ pg : 14352, line : "jument d'Agamemnôn, avait redoublé d'ardeur; et si la course des"});
db.iliadfr.insert({ pg : 14353, line : "deux chars eût été plus longue, l'Atréide eût sans doute devancé"});
db.iliadfr.insert({ pg : 14354, line : "Antilokhos. Et Mèrionès, le brave compagnon d'Idoméneus, venait, à"});
db.iliadfr.insert({ pg : 14355, line : "un jet de lance, derrière l'illustre Ménélaos, ses chevaux étant"});
db.iliadfr.insert({ pg : 14356, line : "très lourds, et lui-même étant peu habile à conduire un char dans"});
db.iliadfr.insert({ pg : 14357, line : "le stade."});
db.iliadfr.insert({ pg : 14359, line : "Mais le fils d'Admètès venait le dernier de tous, traînant son"});
db.iliadfr.insert({ pg : 14360, line : "beau char et poussant ses chevaux devant lui. Et le divin"});
db.iliadfr.insert({ pg : 14361, line : "Akhilleus aux pieds rapides, le voyant, en eut compassion, et,"});
db.iliadfr.insert({ pg : 14362, line : "debout au milieu des Argiens, il dit ces paroles ailées:"});
db.iliadfr.insert({ pg : 14364, line : "-- Ce guerrier excellent ramène le dernier ses chevaux aux sabots"});
db.iliadfr.insert({ pg : 14365, line : "massifs. Donnons-lui donc le second prix, comme il est juste, et"});
db.iliadfr.insert({ pg : 14366, line : "le fils de Tydeus emportera le premier."});
db.iliadfr.insert({ pg : 14368, line : "Il parla ainsi, et tous y consentirent; et il allait donner à"});
db.iliadfr.insert({ pg : 14369, line : "Eumélos la jument promise, si Antilokhos, le fils du magnanime"});
db.iliadfr.insert({ pg : 14370, line : "Nestôr, se levant, n'eût répondu à bon droit au Pèléide Akhilleus:"});
db.iliadfr.insert({ pg : 14372, line : "-- Ô Akhilleus, je m'irriterai violemment contre toi, si tu fais"});
db.iliadfr.insert({ pg : 14373, line : "ce que tu as dit. Tu veux m'enlever mon prix, parce que, malgré"});
db.iliadfr.insert({ pg : 14374, line : "son habileté, Eumèlos a vu son char se rompre! Il devait supplier"});
db.iliadfr.insert({ pg : 14375, line : "les immortels. Il ne serait point arrivé le dernier. Si tu as"});
db.iliadfr.insert({ pg : 14376, line : "compassion de lui, et s'il t'est cher, il y a, sous ta tente,"});
db.iliadfr.insert({ pg : 14377, line : "beaucoup d'or, de l'airain, des brebis, des captives et des"});
db.iliadfr.insert({ pg : 14378, line : "chevaux aux sabots massifs. Donne-lui un plus grand prix que le"});
db.iliadfr.insert({ pg : 14379, line : "mien, dès maintenant, et que les Akhaiens y applaudissent, soit;"});
db.iliadfr.insert({ pg : 14380, line : "mais je ne céderai point mon prix. Que le guerrier qui voudrait me"});
db.iliadfr.insert({ pg : 14381, line : "le disputer combatte d'abord contre moi."});
db.iliadfr.insert({ pg : 14383, line : "Il parla ainsi, et le divin Akhilleus aux pieds vigoureux rit,"});
db.iliadfr.insert({ pg : 14384, line : "approuvant Antilokhos, parce qu'il l'aimait; et il lui répondit"});
db.iliadfr.insert({ pg : 14385, line : "ces paroles ailées:"});
db.iliadfr.insert({ pg : 14387, line : "-- Antilokhos, si tu veux que je prenne dans ma tente un autre"});
db.iliadfr.insert({ pg : 14388, line : "prix pour Eumèlos, je le ferai. Je lui donnerai la cuirasse que"});
db.iliadfr.insert({ pg : 14389, line : "j'enlevai à Astéropaios. Elle est d'or et entourée d'étain"});
db.iliadfr.insert({ pg : 14390, line : "brillant. Elle est digne de lui."});
db.iliadfr.insert({ pg : 14392, line : "Il parla ainsi, et il ordonna à son cher compagnon Automédôn de"});
db.iliadfr.insert({ pg : 14393, line : "l'apporter de sa tente, et Automédôn partit et l'apporta. Et"});
db.iliadfr.insert({ pg : 14394, line : "Akhilleus la remit aux mains d'Eumèlos, qui la reçut avec joie."});
db.iliadfr.insert({ pg : 14396, line : "Et Ménélaos se leva au milieu de tous, triste et violemment irrité"});
db.iliadfr.insert({ pg : 14397, line : "contre Antilokhos. Un héraut lui mit le sceptre entre les mains et"});
db.iliadfr.insert({ pg : 14398, line : "ordonna aux Argiens de faire silence, et le divin guerrier parla"});
db.iliadfr.insert({ pg : 14399, line : "ainsi:"});
db.iliadfr.insert({ pg : 14401, line : "--Antilokhos, toi qui étais plein de sagesse, pourquoi en as-tu"});
db.iliadfr.insert({ pg : 14402, line : "manqué? Tu as déshonoré ma gloire; tu as jeté en travers des miens"});
db.iliadfr.insert({ pg : 14403, line : "tes chevaux qui leur sont bien inférieurs. Vous, princes et chefs"});
db.iliadfr.insert({ pg : 14404, line : "des Argiens, jugez équitablement entre nous. Que nul d'entre les"});
db.iliadfr.insert({ pg : 14405, line : "Akhaiens aux tuniques d'airain ne puisse dire: Ménélaos a opprimé"});
db.iliadfr.insert({ pg : 14406, line : "Antilokhos par des paroles mensongères et a ravi son prix, car ses"});
db.iliadfr.insert({ pg : 14407, line : "chevaux ont été vaincus, mais lui l'a emporté par sa puissance."});
db.iliadfr.insert({ pg : 14408, line : "Mais je jugerai moi-même, et je ne pense pas qu'aucun des Danaens"});
db.iliadfr.insert({ pg : 14409, line : "me blâme, car mon jugement sera droit. Antilokhos, approche,"});
db.iliadfr.insert({ pg : 14410, line : "enfant de Zeus, comme il est juste. Debout, devant ton char,"});
db.iliadfr.insert({ pg : 14411, line : "prends en main ce fouet que tu agitais sur tes chevaux, et jure"});
db.iliadfr.insert({ pg : 14412, line : "par Poseidaôn qui entoure la terre que tu n'as point traversé ma"});
db.iliadfr.insert({ pg : 14413, line : "course par ruse."});
db.iliadfr.insert({ pg : 14415, line : "Et le sage Antilokhos lui répondit:"});
db.iliadfr.insert({ pg : 14417, line : "-- Pardonne maintenant, car je suis beaucoup plus jeune que toi,"});
db.iliadfr.insert({ pg : 14418, line : "roi Ménélaos, et tu es plus âgé et plus puissant. Tu sais quels"});
db.iliadfr.insert({ pg : 14419, line : "sont les défauts d'un jeune homme; l'esprit est très vif et la"});
db.iliadfr.insert({ pg : 14420, line : "réflexion très légère. Que ton coeur s'apaise. Je te donnerai moi-"});
db.iliadfr.insert({ pg : 14421, line : "même cette jument indomptée que j'ai reçue; et, si tu me demandais"});
db.iliadfr.insert({ pg : 14422, line : "plus encore, j'aimerais mieux te le donner aussi, ô fils de Zeus,"});
db.iliadfr.insert({ pg : 14423, line : "que de sortir pour toujours de ton coeur et d'être en exécration"});
db.iliadfr.insert({ pg : 14424, line : "aux dieux."});
db.iliadfr.insert({ pg : 14426, line : "Le fils du magnanime Nestôr parla ainsi et remit la jument entre"});
db.iliadfr.insert({ pg : 14427, line : "les mains de Ménélaos; et le coeur de celui-ci se remplit de joie,"});
db.iliadfr.insert({ pg : 14428, line : "comme les épis sous la rosée, quand les campagnes s'emplissent de"});
db.iliadfr.insert({ pg : 14429, line : "la moisson croissante. Ainsi, ton coeur fut joyeux, ô Ménélaos! Et"});
db.iliadfr.insert({ pg : 14430, line : "il répondit en paroles ailées:"});
db.iliadfr.insert({ pg : 14432, line : "-- Antilokhos, ma colère ne te résiste pas, car tu n'as jamais été"});
db.iliadfr.insert({ pg : 14433, line : "ni léger, ni injurieux. La jeunesse seule a égaré ta prudence;"});
db.iliadfr.insert({ pg : 14434, line : "mais prends garde désormais de tromper tes supérieurs par des"});
db.iliadfr.insert({ pg : 14435, line : "ruses. Un autre d'entre les Akhaiens ne m'eût point apaisé aussi"});
db.iliadfr.insert({ pg : 14436, line : "vite; mais toi, ton père excellent et ton frère, vous avez subi"});
db.iliadfr.insert({ pg : 14437, line : "beaucoup de maux pour ma cause. Donc, je me rends à ta prière, et"});
db.iliadfr.insert({ pg : 14438, line : "je te donne cette jument qui m'appartient, afin que tous les"});
db.iliadfr.insert({ pg : 14439, line : "Akhaiens soient témoins que mon coeur n'a jamais été ni"});
db.iliadfr.insert({ pg : 14440, line : "orgueilleux, ni dur."});
db.iliadfr.insert({ pg : 14442, line : "Il parla ainsi, et il donna la jument à Noèmôn, compagnon"});
db.iliadfr.insert({ pg : 14443, line : "d'Antilokhos. Lui-même, il prit le vase splendide, et Mèrionès"});
db.iliadfr.insert({ pg : 14444, line : "reçut les deux talents d'or, prix de sa course. Et le cinquième"});
db.iliadfr.insert({ pg : 14445, line : "prix restait, l'urne à deux anses. Et Akhilleus, la portant à"});
db.iliadfr.insert({ pg : 14446, line : "travers l'assemblée des Argiens, la donna à Nestôr, et lui dit:"});
db.iliadfr.insert({ pg : 14448, line : "-- Reçois ce présent, vieillard, et qu'il te soit un souvenir des"});
db.iliadfr.insert({ pg : 14449, line : "funérailles de Patroklos, que tu ne reverras plus parmi les"});
db.iliadfr.insert({ pg : 14450, line : "Argiens. Je te donne ce prix que tu n'as point disputé; car tu ne"});
db.iliadfr.insert({ pg : 14451, line : "combattras point avec les cestes, tu ne lutteras point, tu ne"});
db.iliadfr.insert({ pg : 14452, line : "lanceras point la pique et tu ne courras point, car la lourde"});
db.iliadfr.insert({ pg : 14453, line : "vieillesse t'accable."});
db.iliadfr.insert({ pg : 14455, line : "Ayant ainsi parlé, il lui mit l'urne aux mains, et Nestôr la"});
db.iliadfr.insert({ pg : 14456, line : "recevant avec joie, lui répondit ces paroles ailées:"});
db.iliadfr.insert({ pg : 14458, line : "-- Mon fils, certes, tu as bien parlé. Ami, je n'ai plus, en"});
db.iliadfr.insert({ pg : 14459, line : "effet, mes membres vigoureux. Mes pieds sont lourds et mes bras ne"});
db.iliadfr.insert({ pg : 14460, line : "sont plus agiles. Plût aux dieux que je fusse jeune, et que ma"});
db.iliadfr.insert({ pg : 14461, line : "force fût telle qu'à l'époque où les Épéiens ensevelirent le roi"});
db.iliadfr.insert({ pg : 14462, line : "Amarinkeus dans Bouprasiôn! Ses fils déposèrent des prix, et aucun"});
db.iliadfr.insert({ pg : 14463, line : "guerrier ne fut mon égal parmi les Épéiens, les Pyliens et les"});
db.iliadfr.insert({ pg : 14464, line : "magnanimes Aitôliens. Je vainquis au pugilat Klydomèdeus, fils"});
db.iliadfr.insert({ pg : 14465, line : "d'Énops; à la lutte, Agkaios le Pleurônien qui se leva contre moi."});
db.iliadfr.insert({ pg : 14466, line : "Je courus plus vite que le brave Iphiklos; je triomphai, au combat"});
db.iliadfr.insert({ pg : 14467, line : "de la lance, de Phyleus et de Polydôros; mais, à la course des"});
db.iliadfr.insert({ pg : 14468, line : "chars, par leur nombre, les Aktoriônes remportèrent la victoire,"});
db.iliadfr.insert({ pg : 14469, line : "et ils m'enlevèrent ainsi les plus beaux prix. Car ils étaient"});
db.iliadfr.insert({ pg : 14470, line : "deux: et l'un tenait fermement les rênes, et l'autre le fouet. Tel"});
db.iliadfr.insert({ pg : 14471, line : "j'étais autrefois, et maintenant de plus jeunes accomplissent ces"});
db.iliadfr.insert({ pg : 14472, line : "travaux, et il me faut obéir à la triste vieillesse; mais, alors,"});
db.iliadfr.insert({ pg : 14473, line : "j'excellais parmi les héros. Va! continue par d'autres combats les"});
db.iliadfr.insert({ pg : 14474, line : "funérailles de ton compagnon. J'accepte ce présent avec joie, et"});
db.iliadfr.insert({ pg : 14475, line : "mon coeur se réjouit de ce que tu te sois souvenu de moi qui te"});
db.iliadfr.insert({ pg : 14476, line : "suis bienveillant, et de ce que tu m'aies honoré, comme il est"});
db.iliadfr.insert({ pg : 14477, line : "juste qu'on m'honore parmi les Argiens. Que les dieux, en retour,"});
db.iliadfr.insert({ pg : 14478, line : "te comblent de leurs grâces!"});
db.iliadfr.insert({ pg : 14480, line : "Il parla ainsi, et le Pèléide s'en retourna à travers la grande"});
db.iliadfr.insert({ pg : 14481, line : "assemblée des Akhaiens, après avoir écouté jusqu'au bout la propre"});
db.iliadfr.insert({ pg : 14482, line : "louange du Nèlèiade."});
db.iliadfr.insert({ pg : 14484, line : "Et il déposa les prix pour le rude combat des poings. Et il amena"});
db.iliadfr.insert({ pg : 14485, line : "dans l'enceinte, et il lia de ses mains une mule laborieuse, de"});
db.iliadfr.insert({ pg : 14486, line : "six ans, indomptée et presque indomptable; et il déposa une coupe"});
db.iliadfr.insert({ pg : 14487, line : "ronde pour le vaincu. Et, debout, il dit au milieu des Argiens:"});
db.iliadfr.insert({ pg : 14489, line : "-- Atréides, et vous Akhaiens aux belles knèmides, j'appelle, pour"});
db.iliadfr.insert({ pg : 14490, line : "disputer ces prix, deux hommes vigoureux à se frapper de leurs"});
db.iliadfr.insert({ pg : 14491, line : "poings levés. Que tous les Akhaiens le sachent, celui à qui"});
db.iliadfr.insert({ pg : 14492, line : "Apollôn donnera la victoire, conduira dans sa tente cette mule"});
db.iliadfr.insert({ pg : 14493, line : "patiente, et le vaincu emportera cette coupe ronde."});
db.iliadfr.insert({ pg : 14495, line : "Il parla ainsi, et aussitôt un homme vigoureux et grand se leva,"});
db.iliadfr.insert({ pg : 14496, line : "Épéios, fils de Panopeus, habile au combat du poing. Il saisit la"});
db.iliadfr.insert({ pg : 14497, line : "mule laborieuse et dit:"});
db.iliadfr.insert({ pg : 14499, line : "-- Qu'il vienne, celui qui veut emporter cette coupe, car je ne"});
db.iliadfr.insert({ pg : 14500, line : "pense pas qu'aucun des Akhaiens puisse emmener cette mule, m'ayant"});
db.iliadfr.insert({ pg : 14501, line : "vaincu par le poing; car, en cela, je me glorifie de l'emporter"});
db.iliadfr.insert({ pg : 14502, line : "sur tous. N'est-ce point assez que je sois inférieur dans le"});
db.iliadfr.insert({ pg : 14503, line : "combat? Aucun homme ne peut exceller en toutes choses. Mais, je le"});
db.iliadfr.insert({ pg : 14504, line : "dis, et ma parole s'accomplira: je briserai le corps de mon"});
db.iliadfr.insert({ pg : 14505, line : "adversaire et je romprai ses os. Que ses amis s'assemblent ici en"});
db.iliadfr.insert({ pg : 14506, line : "grand nombre pour l'emporter, quand il sera tombé sous mes mains."});
db.iliadfr.insert({ pg : 14508, line : "Il parla ainsi, et tous restèrent muets. Et le seul Euryalos se"});
db.iliadfr.insert({ pg : 14509, line : "leva, homme illustre, fils du roi Mèkisteus Talionide qui,"});
db.iliadfr.insert({ pg : 14510, line : "autrefois, alla dans Thèbè aux funérailles d'Oidipous, et qui"});
db.iliadfr.insert({ pg : 14511, line : "l'emporta sur tous les Kadméiônes. Et l'illustre Tydéide"});
db.iliadfr.insert({ pg : 14512, line : "s'empressait autour d'Euryalos, l'animant de ses paroles, car il"});
db.iliadfr.insert({ pg : 14513, line : "lui souhaitait la victoire. Et il lui mit d'abord une ceinture, et"});
db.iliadfr.insert({ pg : 14514, line : "il l'arma de courroies faites du cuir d'un boeuf sauvage."});
db.iliadfr.insert({ pg : 14516, line : "Puis, les deux combattants s'avancèrent au milieu de l'enceinte."});
db.iliadfr.insert({ pg : 14517, line : "Et tous deux, levant à la fois leurs mains vigoureuses, se"});
db.iliadfr.insert({ pg : 14518, line : "frappèrent à la fois, en mêlant leurs poings lourds. Et on"});
db.iliadfr.insert({ pg : 14519, line : "entendait le bruit des mâchoires frappées; et la sueur coulait"});
db.iliadfr.insert({ pg : 14520, line : "chaude de tous leurs membres. Mais le divin Épéios, se ruant en"});
db.iliadfr.insert({ pg : 14521, line : "avant, frappa de tous les côtés la face d'Euryalos qui ne put"});
db.iliadfr.insert({ pg : 14522, line : "résister plus longtemps, et dont les membres défaillirent. De même"});
db.iliadfr.insert({ pg : 14523, line : "que le poisson qui est jeté, par le souffle furieux de Boréas,"});
db.iliadfr.insert({ pg : 14524, line : "dans les algues du bord, et que l'eau noire ressaisit; de même"});
db.iliadfr.insert({ pg : 14525, line : "Euryalos frappé bondit. Mais le magnanime Épéios le releva lui-"});
db.iliadfr.insert({ pg : 14526, line : "même, et ses chers compagnons, l'entourant, l'emmenèrent à travers"});
db.iliadfr.insert({ pg : 14527, line : "l'assemblée, les pieds traînants, vomissant un sang épais, et la"});
db.iliadfr.insert({ pg : 14528, line : "tête penchée. Et ils l'emmenaient ainsi, en le soutenant, et ils"});
db.iliadfr.insert({ pg : 14529, line : "emportèrent aussi la coupe ronde."});
db.iliadfr.insert({ pg : 14531, line : "Et le Pèléide déposa les prix de la lutte difficile devant les"});
db.iliadfr.insert({ pg : 14532, line : "Danaens: un grand trépied fait pour le feu, et destiné au"});
db.iliadfr.insert({ pg : 14533, line : "vainqueur, et que les Akhaiens, entre eux, estimèrent du prix de"});
db.iliadfr.insert({ pg : 14534, line : "douze boeufs; et, pour le vaincu, une femme habile aux travaux et"});
db.iliadfr.insert({ pg : 14535, line : "valant quatre boeufs. Et le Pèléide, debout, dit au milieu des"});
db.iliadfr.insert({ pg : 14536, line : "Argiens:"});
db.iliadfr.insert({ pg : 14538, line : "-- Qu'ils se lèvent, ceux qui osent combattre pour ce prix."});
db.iliadfr.insert({ pg : 14540, line : "Il parla ainsi, et aussitôt le grand Télamônien Aias se leva; et"});
db.iliadfr.insert({ pg : 14541, line : "le sage Odysseus, plein de ruses, se leva aussi. Et tous deux,"});
db.iliadfr.insert({ pg : 14542, line : "s'étant munis de ceintures, descendirent dans l'enceinte et se"});
db.iliadfr.insert({ pg : 14543, line : "saisirent de leurs mains vigoureuses, tels que deux poutres qu'un"});
db.iliadfr.insert({ pg : 14544, line : "habile charpentier unit au sommet d'une maison pour résister à la"});
db.iliadfr.insert({ pg : 14545, line : "violence du vent. Ainsi leurs reins, sous leurs mains vigoureuses,"});
db.iliadfr.insert({ pg : 14546, line : "craquèrent avec force, et leur sueur coula abondamment, et"});
db.iliadfr.insert({ pg : 14547, line : "d'épaisses tumeurs, rouges de sang, s'élevèrent sur leurs flancs"});
db.iliadfr.insert({ pg : 14548, line : "et leurs épaules. Et tous deux désiraient ardemment la victoire et"});
db.iliadfr.insert({ pg : 14549, line : "le trépied qui en était le prix; mais Odysseus ne pouvait ébranler"});
db.iliadfr.insert({ pg : 14550, line : "Aias, et Aias ne pouvait renverser Odysseus. Et déjà ils"});
db.iliadfr.insert({ pg : 14551, line : "fatiguaient l'attente des Akhaiens aux belles knèmides; mais le"});
db.iliadfr.insert({ pg : 14552, line : "grand Télamônien Aias dit alors à Odysseus:"});
db.iliadfr.insert({ pg : 14554, line : "-- Divin Laertiade, très sage Odysseus, enlève-moi, ou je"});
db.iliadfr.insert({ pg : 14555, line : "t'enlèverai, et Zeus fera le reste."});
db.iliadfr.insert({ pg : 14557, line : "Il parla ainsi, et il l'enleva; mais Odysseus n'oublia point ses"});
db.iliadfr.insert({ pg : 14558, line : "ruses, et, le frappant du pied sur le jarret, il fit ployer ses"});
db.iliadfr.insert({ pg : 14559, line : "membres, et, le renversant, tomba sur lui. Et les peuples étonnés"});
db.iliadfr.insert({ pg : 14560, line : "les admiraient. Alors le divin et patient Odysseus voulut à son"});
db.iliadfr.insert({ pg : 14561, line : "tour enlever Aias; mais il le souleva à peine, et ses genoux"});
db.iliadfr.insert({ pg : 14562, line : "ployèrent, et tous deux tombèrent côte à côte, et ils furent"});
db.iliadfr.insert({ pg : 14563, line : "souillés de poussière. Et, comme ils se relevaient une troisième"});
db.iliadfr.insert({ pg : 14564, line : "fois, Akhilleus se leva lui-même et les retint:"});
db.iliadfr.insert({ pg : 14566, line : "-- Ne combattez pas plus longtemps et ne vous épuisez pas. La"});
db.iliadfr.insert({ pg : 14567, line : "victoire est à tous deux. Allez donc, emportant des prix égaux, et"});
db.iliadfr.insert({ pg : 14568, line : "laissez combattre les autres Akhaiens."});
db.iliadfr.insert({ pg : 14570, line : "Il parla ainsi; et, l'ayant entendu, ils lui obéirent; et,"});
db.iliadfr.insert({ pg : 14571, line : "secouant leur poussière, ils se couvrirent de leurs vêtements."});
db.iliadfr.insert({ pg : 14573, line : "Alors le Pèléide déposa les prix de la course: un très beau"});
db.iliadfr.insert({ pg : 14574, line : "kratère d'argent contenant six mesures. Et il surpassait par sa"});
db.iliadfr.insert({ pg : 14575, line : "beauté tous ceux qui étaient sur la terre. Les habiles Sidônes"});
db.iliadfr.insert({ pg : 14576, line : "l'avaient admirablement travaillé; et des Phoinikes l'avaient"});
db.iliadfr.insert({ pg : 14577, line : "amené, à travers la mer bleue; et, arrivés au port, ils l'avaient"});
db.iliadfr.insert({ pg : 14578, line : "donné à Thoas. Le Iasonide Euneus l'avait cédé au héros Patroklos"});
db.iliadfr.insert({ pg : 14579, line : "pour l'affranchissement du Priamide Lykaôn; et Akhilleus le"});
db.iliadfr.insert({ pg : 14580, line : "proposa en prix aux plus habiles coureurs dans les jeux funèbres"});
db.iliadfr.insert({ pg : 14581, line : "de son ami. Puis, il offrit un boeuf énorme et très gras; puis,"});
db.iliadfr.insert({ pg : 14582, line : "enfin, un demi talent d'or. Et, debout, il dit au milieu des"});
db.iliadfr.insert({ pg : 14583, line : "Argiens:"});
db.iliadfr.insert({ pg : 14585, line : "-- Qu'ils se lèvent, ceux qui veulent combattre pour ce prix."});
db.iliadfr.insert({ pg : 14587, line : "Il parla ainsi, et, aussitôt, le rapide Aias, fils d'Oileus, se"});
db.iliadfr.insert({ pg : 14588, line : "leva; puis le sage Odysseus, puis Antilokhos, fils de Nestôr. Et"});
db.iliadfr.insert({ pg : 14589, line : "celui-ci dépassait tous les jeunes hommes à la course. Ils se"});
db.iliadfr.insert({ pg : 14590, line : "placèrent de front, et Akhilleus leur montra le but, et ils se"});
db.iliadfr.insert({ pg : 14591, line : "précipitèrent. L'Oiliade les devançait tous; puis, venait le divin"});
db.iliadfr.insert({ pg : 14592, line : "Odysseus. Autant la navette qu'une belle femme manie habilement,"});
db.iliadfr.insert({ pg : 14593, line : "approche de son sein, quand elle tire le fil à elle, autant"});
db.iliadfr.insert({ pg : 14594, line : "Odysseus était proche d'Aias, mettant ses pieds dans les pas de"});
db.iliadfr.insert({ pg : 14595, line : "celui-ci, avant que leur poussière se fût élevée. Ainsi le divin"});
db.iliadfr.insert({ pg : 14596, line : "Odysseus chauffait de son souffle la tête d'Aias. Et tous les"});
db.iliadfr.insert({ pg : 14597, line : "Akhaiens applaudissaient à son désir de la victoire et"});
db.iliadfr.insert({ pg : 14598, line : "l'excitaient à courir. Et comme ils approchaient du but, Odysseus"});
db.iliadfr.insert({ pg : 14599, line : "pria en lui-même Athènè aux yeux clairs:"});
db.iliadfr.insert({ pg : 14601, line : "-- Exauce-moi, déesse! soutiens-moi heureusement dans ma course."});
db.iliadfr.insert({ pg : 14603, line : "Il parla ainsi; et Pallas Athènè, l'exauçant, rendit ses membres"});
db.iliadfr.insert({ pg : 14604, line : "plus agiles et ses pieds plus légers. Et comme ils revenaient aux"});
db.iliadfr.insert({ pg : 14605, line : "prix, Athènè poussa Aias qui tomba, en courant, là où s'était"});
db.iliadfr.insert({ pg : 14606, line : "amassé le sang des boeufs mugissants qu'Akhilleus aux pieds"});
db.iliadfr.insert({ pg : 14607, line : "rapides avait tués devant le corps de Patroklos; et sa bouche et"});
db.iliadfr.insert({ pg : 14608, line : "ses narines furent emplies de fumier et du sang des boeufs; et le"});
db.iliadfr.insert({ pg : 14609, line : "divin et patient Odysseus, le devançant, saisit le kratère"});
db.iliadfr.insert({ pg : 14610, line : "d'argent. Et l'illustre Aias prit le boeuf; et se tenant d'une"});
db.iliadfr.insert({ pg : 14611, line : "main à l'une des cornes du boeuf sauvage, et rejetant le fumier de"});
db.iliadfr.insert({ pg : 14612, line : "sa bouche, il dit au milieu des Argiens:"});
db.iliadfr.insert({ pg : 14614, line : "-- Malheur à moi! certes, la déesse Athènè a embarrassé mes pieds,"});
db.iliadfr.insert({ pg : 14615, line : "elle qui accompagne et secourt toujours Odysseus, comme une mère."});
db.iliadfr.insert({ pg : 14617, line : "Il parla ainsi, et tous, en l'entendant, se mirent à rire. Et"});
db.iliadfr.insert({ pg : 14618, line : "Antilokhos enleva le dernier prix, et il dit en riant aux Argiens:"});
db.iliadfr.insert({ pg : 14620, line : "-- Je vous le dis à tous, et vous le voyez, amis; maintenant et"});
db.iliadfr.insert({ pg : 14621, line : "toujours, les immortels honorent les vieillards. Aias est un peu"});
db.iliadfr.insert({ pg : 14622, line : "plus âgé que moi; mais Odysseus est de la génération des hommes"});
db.iliadfr.insert({ pg : 14623, line : "anciens. Cependant, il a une verte vieillesse, et il est difficile"});
db.iliadfr.insert({ pg : 14624, line : "à tous les Akhaiens, si ce n'est à Akhilleus, de lutter avec lui à"});
db.iliadfr.insert({ pg : 14625, line : "la course."});
db.iliadfr.insert({ pg : 14627, line : "Il parla ainsi, louant le Pèléiôn aux pieds rapides. Et Akhilleus"});
db.iliadfr.insert({ pg : 14628, line : "lui répondit:"});
db.iliadfr.insert({ pg : 14630, line : "-- Antilokhos, tu ne m'auras point loué en vain, et je te donnerai"});
db.iliadfr.insert({ pg : 14631, line : "encore un autre demi-talent d'or."});
db.iliadfr.insert({ pg : 14633, line : "Ayant ainsi parlé, il le lui donna, et Antilokhos le reçut avec"});
db.iliadfr.insert({ pg : 14634, line : "joie. Puis, le Pèléide déposa dans l'enceinte une longue lance, un"});
db.iliadfr.insert({ pg : 14635, line : "bouclier et un casque; et c'étaient les armes que Patroklos avait"});
db.iliadfr.insert({ pg : 14636, line : "enlevées à Sarpèdôn. Et, debout, il dit au milieu des Argiens:"});
db.iliadfr.insert({ pg : 14638, line : "-- Que deux guerriers, parmi les plus braves, et couverts de leurs"});
db.iliadfr.insert({ pg : 14639, line : "armes d'airain, combattent devant la foule. À celui qui,"});
db.iliadfr.insert({ pg : 14640, line : "atteignant le premier le corps de l'autre, aura fait couler le"});
db.iliadfr.insert({ pg : 14641, line : "sang noir à travers les armes, je donnerai cette belle épée"});
db.iliadfr.insert({ pg : 14642, line : "Thrèkienne, aux clous d'argent, que j'enlevai à Astéropaios. Quant"});
db.iliadfr.insert({ pg : 14643, line : "à ces armes, elles seront communes; et je leur offrirai à tous"});
db.iliadfr.insert({ pg : 14644, line : "deux un beau repas dans mes tentes."});
db.iliadfr.insert({ pg : 14646, line : "Il parla ainsi, et, aussitôt, le grand Télamônien Aias se leva;"});
db.iliadfr.insert({ pg : 14647, line : "et, après lui, le brave Diomèdès Tydéide se leva aussi. Et tous"});
db.iliadfr.insert({ pg : 14648, line : "deux, à l'écart, s'étant armés, se présentèrent au milieu de tous,"});
db.iliadfr.insert({ pg : 14649, line : "prêts à combattre et se regardant avec des yeux terribles. Et la"});
db.iliadfr.insert({ pg : 14650, line : "terreur saisit tous les Akhaiens. Et quand les héros se furent"});
db.iliadfr.insert({ pg : 14651, line : "rencontrés, trois fois, se jetant l'un sur l'autre, ils"});
db.iliadfr.insert({ pg : 14652, line : "s'attaquèrent ardemment. Aias perça le bouclier de Diomèdès, mais"});
db.iliadfr.insert({ pg : 14653, line : "il n'atteignit point le corps que protégeait la cuirasse. Et le"});
db.iliadfr.insert({ pg : 14654, line : "Tydéide dirigea la pointe de sa lance, au-dessus du grand"});
db.iliadfr.insert({ pg : 14655, line : "bouclier, près du cou; mais les Akhaiens, craignant pour Aias,"});
db.iliadfr.insert({ pg : 14656, line : "fîrent cesser le combat et leur donnèrent des prix égaux."});
db.iliadfr.insert({ pg : 14657, line : "Cependant le héros Akhilleus donna au Tydéide la grande épée, avec"});
db.iliadfr.insert({ pg : 14658, line : "la gaîne et le riche baudrier."});
db.iliadfr.insert({ pg : 14660, line : "Puis, le Pèléide déposa un disque de fer brut que lançait"});
db.iliadfr.insert({ pg : 14661, line : "autrefois la force immense d'Êétiôn. Et le divin Akhilleus aux"});
db.iliadfr.insert({ pg : 14662, line : "pieds rapides, ayant tué Eétiôn, avait emporté cette masse dans"});
db.iliadfr.insert({ pg : 14663, line : "ses nefs, avec d'autres richesses. Et, debout, il dit au milieu"});
db.iliadfr.insert({ pg : 14664, line : "des Argiens:"});
db.iliadfr.insert({ pg : 14666, line : "-- Qu'ils se lèvent, ceux qui veulent tenter ce combat. Celui qui"});
db.iliadfr.insert({ pg : 14667, line : "possédera ce disque, s'il a des champs fertiles qui s'étendent au"});
db.iliadfr.insert({ pg : 14668, line : "loin, ne manquera point de fer pendant cinq années entières. Ni"});
db.iliadfr.insert({ pg : 14669, line : "ses bergers, ni ses laboureurs n'iront en acheter à la ville, car"});
db.iliadfr.insert({ pg : 14670, line : "ce disque lui en fournira."});
db.iliadfr.insert({ pg : 14672, line : "Il parla ainsi, et le belliqueux Polypoitès se leva; et, après"});
db.iliadfr.insert({ pg : 14673, line : "lui, la force du divin Léonteus; puis, Aias Télamôniade, puis le"});
db.iliadfr.insert({ pg : 14674, line : "divin Épéios. Et ils prirent place; et le divin Épéios saisit le"});
db.iliadfr.insert({ pg : 14675, line : "disque, et, le faisant tourner, le lança; et tous les Akhaiens se"});
db.iliadfr.insert({ pg : 14676, line : "mirent à rire. Le second qui le lança fut Léonteus, rejeton"});
db.iliadfr.insert({ pg : 14677, line : "d'Arès. Le troisième fut le grand Télamônien Aias qui, de sa main"});
db.iliadfr.insert({ pg : 14678, line : "vigoureuse, le jeta bien au-delà des autres. Mais quand le"});
db.iliadfr.insert({ pg : 14679, line : "belliqueux Polypoitès l'eut saisi, il le lança plus loin que tous,"});
db.iliadfr.insert({ pg : 14680, line : "de l'espace entier que franchit le bâton recourbé d'un bouvier,"});
db.iliadfr.insert({ pg : 14681, line : "que celui-ci fait voler à travers les vaches vagabondes."});
db.iliadfr.insert({ pg : 14683, line : "Et les Akhaiens poussèrent des acclamations, et les compagnons du"});
db.iliadfr.insert({ pg : 14684, line : "brave Polypoitès emportèrent dans les nefs creuses le prix de leur"});
db.iliadfr.insert({ pg : 14685, line : "roi."});
db.iliadfr.insert({ pg : 14687, line : "Puis, le Pèléide déposa, pour les archers habiles, dix grandes"});
db.iliadfr.insert({ pg : 14688, line : "haches à deux tranchants et dix petites haches, toutes en fer. Et"});
db.iliadfr.insert({ pg : 14689, line : "il fit dresser dans l'enceinte le mât noir d'une nef éperonnée;"});
db.iliadfr.insert({ pg : 14690, line : "et, au sommet du mât, il fit lier par un lien léger une colombe"});
db.iliadfr.insert({ pg : 14691, line : "tremblante, but des flèches:"});
db.iliadfr.insert({ pg : 14693, line : "-- Celui qui atteindra la colombe emportera les haches à deux"});
db.iliadfr.insert({ pg : 14694, line : "tranchants dans sa tente; et celui qui, moins adroit, et manquant"});
db.iliadfr.insert({ pg : 14695, line : "l'oiseau, aura coupé le lien, emportera les petites haches."});
db.iliadfr.insert({ pg : 14697, line : "Il parla ainsi, et le prince Teukros se leva aussitôt; et après"});
db.iliadfr.insert({ pg : 14698, line : "lui, Mèrionès, brave compagnon d'Idoméneus, se leva aussi. Et les"});
db.iliadfr.insert({ pg : 14699, line : "sorts ayant été remués dans un casque d'airain, celui de Teukros"});
db.iliadfr.insert({ pg : 14700, line : "parut le premier. Et, aussitôt, il lança une flèche avec vigueur,"});
db.iliadfr.insert({ pg : 14701, line : "oubliant de vouer à l'archer Apollôn une illustre hécatombe"});
db.iliadfr.insert({ pg : 14702, line : "d'agneaux premiers-nés. Et il manqua l'oiseau car Apollôn lui"});
db.iliadfr.insert({ pg : 14703, line : "envia cette gloire; mais il atteignit, auprès du pied, le lien qui"});
db.iliadfr.insert({ pg : 14704, line : "retenait l'oiseau; et la flèche amère trancha le lien, et la"});
db.iliadfr.insert({ pg : 14705, line : "colombe s'envola dans l'Ouranos, tandis que le lien retombait. Et"});
db.iliadfr.insert({ pg : 14706, line : "les Akhaiens poussèrent des acclamations. Mais, aussitôt,"});
db.iliadfr.insert({ pg : 14707, line : "Mèrionès, saisissant l'arc de la main de Teukros, car il tenait la"});
db.iliadfr.insert({ pg : 14708, line : "flèche prête, voua à l'archer Apollôn une illustre hécatombe"});
db.iliadfr.insert({ pg : 14709, line : "d'agneaux premiers-nés, et, tandis que la colombe montait en"});
db.iliadfr.insert({ pg : 14710, line : "tournoyant vers les hautes nuées, il l'atteignit sous l'aile. Le"});
db.iliadfr.insert({ pg : 14711, line : "trait la traversa et revint s'enfoncer en terre aux pieds de"});
db.iliadfr.insert({ pg : 14712, line : "Mèrionès; et l'oiseau tomba le long du mât noir de la nef"});
db.iliadfr.insert({ pg : 14713, line : "éperonnée, le cou pendant, et les plumes éparses, et son âme"});
db.iliadfr.insert({ pg : 14714, line : "s'envola de son corps. Et tous furent saisis d'admiration. Et"});
db.iliadfr.insert({ pg : 14715, line : "Mèrionés prit les dix haches à deux tranchants, et Teukros emporta"});
db.iliadfr.insert({ pg : 14716, line : "les petites haches dans sa tente."});
db.iliadfr.insert({ pg : 14718, line : "Puis, le Pèléide déposa une longue lance et un vase neuf et orné,"});
db.iliadfr.insert({ pg : 14719, line : "du prix d'un boeuf; et ceux qui devaient lancer la pique se"});
db.iliadfr.insert({ pg : 14720, line : "levèrent. Et l'Atréide Agamemnôn qui commande au loin se leva; et"});
db.iliadfr.insert({ pg : 14721, line : "Mèrionès, brave compagnon d'Idoméneus, se leva aussi. Mais le"});
db.iliadfr.insert({ pg : 14722, line : "divin et rapide Akhilleus leur dit:"});
db.iliadfr.insert({ pg : 14724, line : "-- Atréide, nous savons combien tu l'emportes sur tous par ta"});
db.iliadfr.insert({ pg : 14725, line : "force et ton habileté à la lance. Emporte donc ce prix dans tes"});
db.iliadfr.insert({ pg : 14726, line : "nefs creuses. Mais, si tu le veux, et tel est mon désir, donne"});
db.iliadfr.insert({ pg : 14727, line : "cette lance au héros Mèrionès."});
db.iliadfr.insert({ pg : 14729, line : "Il parla ainsi, et le roi des hommes Agamemnôn y consentit. Et"});
db.iliadfr.insert({ pg : 14730, line : "Akhilleus donna la lance d'airain à Mèrionés, et le roi Atréide"});
db.iliadfr.insert({ pg : 14731, line : "remit le vase magnifique au héraut Talthybios."});
db.iliadfr.insert({ pg : 14734, line : "Chant 24"});
db.iliadfr.insert({ pg : 14736, line : "Et les luttes ayant pris fin, les peuples se dispersèrent,"});
db.iliadfr.insert({ pg : 14737, line : "rentrant dans les nefs, afin de prendre leur repas et de jouir du"});
db.iliadfr.insert({ pg : 14738, line : "doux sommeil. Mais Akhilleus pleurait, se souvenant de son cher"});
db.iliadfr.insert({ pg : 14739, line : "compagnon; et le sommeil qui dompte tout ne le saisissait pas. Et"});
db.iliadfr.insert({ pg : 14740, line : "il se tournait çà et là, regrettant la force de Patroklos et son"});
db.iliadfr.insert({ pg : 14741, line : "coeur héroïque. Et il se souvenait des choses accomplies et des"});
db.iliadfr.insert({ pg : 14742, line : "maux soufferts ensemble, et de tous leurs combats en traversant la"});
db.iliadfr.insert({ pg : 14743, line : "mer dangereuse. Et, à ce souvenir, il versait des larmes, tantôt"});
db.iliadfr.insert({ pg : 14744, line : "couché sur le côté, tantôt sur le dos, tantôt le visage contre"});
db.iliadfr.insert({ pg : 14745, line : "terre. Puis, il se leva brusquement, et, plein de tristesse, il"});
db.iliadfr.insert({ pg : 14746, line : "erra sur le rivage de la mer. Et les premières lueurs d'Éôs"});
db.iliadfr.insert({ pg : 14747, line : "s'étant répandues sur les flots et sur les plages, il attela ses"});
db.iliadfr.insert({ pg : 14748, line : "chevaux rapides, et, liant Hektôr derrière le char, il le traîna"});
db.iliadfr.insert({ pg : 14749, line : "trois fois autour du tombeau du Ménoitiade. Puis, il rentra de"});
db.iliadfr.insert({ pg : 14750, line : "nouveau dans sa tente pour s'y reposer, et il laissa Hektôr"});
db.iliadfr.insert({ pg : 14751, line : "étendu, la face dans la poussière."});
db.iliadfr.insert({ pg : 14753, line : "Mais Apollôn, plein de pitié pour le guerrier sans vie, éloignait"});
db.iliadfr.insert({ pg : 14754, line : "du corps toute souillure et le couvrait tout entier de l'aigide"});
db.iliadfr.insert({ pg : 14755, line : "d'or, afin que le Pèléide, en le traînant, ne le déchirât point."});
db.iliadfr.insert({ pg : 14756, line : "C'est ainsi que, furieux, Akhilleus outrageait Hektôr; et les"});
db.iliadfr.insert({ pg : 14757, line : "dieux heureux qui le regardaient en avaient pitié, et ils"});
db.iliadfr.insert({ pg : 14758, line : "excitaient le vigilant tueur d'Argos à l'enlever. Et ceci plaisait"});
db.iliadfr.insert({ pg : 14759, line : "à tous les dieux, sauf à Hèrè, à Poseidaôn et à la vierge aux yeux"});
db.iliadfr.insert({ pg : 14760, line : "clairs, qui, tous trois, gardaient leur ancienne haine pour la"});
db.iliadfr.insert({ pg : 14761, line : "sainte Ilios, pour Priamos et son peuple, à cause de l'injure"});
db.iliadfr.insert({ pg : 14762, line : "d'Alexandros qui méprisa les déesses quand elles vinrent dans sa"});
db.iliadfr.insert({ pg : 14763, line : "cabane, où il couronna celle qui le remplit d'un désir funeste."});
db.iliadfr.insert({ pg : 14765, line : "Et quand Éôs se leva pour la douzième fois, Phoibos Apollôn parla"});
db.iliadfr.insert({ pg : 14766, line : "ainsi au milieu des immortels:"});
db.iliadfr.insert({ pg : 14768, line : "-- Ô dieux! vous êtes injustes et cruels. Pour vous, naguère,"});
db.iliadfr.insert({ pg : 14769, line : "Hektôr ne brûlait-il pas les cuisses des boeufs et des meilleures"});
db.iliadfr.insert({ pg : 14770, line : "chèvres? Et, maintenant, vous ne voulez pas même rendre son"});
db.iliadfr.insert({ pg : 14771, line : "cadavre à sa femme, à sa mère, à son fils, à son père Priamos et à"});
db.iliadfr.insert({ pg : 14772, line : "ses peuples, pour qu'ils le revoient et qu'ils le brûlent, et"});
db.iliadfr.insert({ pg : 14773, line : "qu'ils accomplissent ses funérailles. Ô dieux! vous ne voulez"});
db.iliadfr.insert({ pg : 14774, line : "protéger que le féroce Akhilleus dont les desseins sont"});
db.iliadfr.insert({ pg : 14775, line : "haïssables, dont le coeur est inflexible dans sa poitrine, et qui"});
db.iliadfr.insert({ pg : 14776, line : "est tel qu'un lion excité par sa grande force et par sa rage, qui"});
db.iliadfr.insert({ pg : 14777, line : "se jette sur les troupeaux des hommes pour les dévorer. Ainsi"});
db.iliadfr.insert({ pg : 14778, line : "Akhilleus a perdu toute compassion, et cette honte qui perd ou qui"});
db.iliadfr.insert({ pg : 14779, line : "aide les hommes. D'autres aussi peuvent perdre quelqu'un qui leur"});
db.iliadfr.insert({ pg : 14780, line : "est très cher, soit un frère, soit un fils; et ils pleurent et"});
db.iliadfr.insert({ pg : 14781, line : "gémissent, puis ils se consolent, car les moires ont donné aux"});
db.iliadfr.insert({ pg : 14782, line : "hommes un esprit patient. Mais lui, après avoir privé le divin"});
db.iliadfr.insert({ pg : 14783, line : "Hektôr de sa chère âme, l'attachant à son char, il le traîne"});
db.iliadfr.insert({ pg : 14784, line : "autour du tombeau de son compagnon. Cela n'est ni bon, ni juste."});
db.iliadfr.insert({ pg : 14785, line : "Qu'il craigne, bien que très brave, que nous nous irritions contre"});
db.iliadfr.insert({ pg : 14786, line : "lui, car, dans sa fureur, il outrage une poussière insensible."});
db.iliadfr.insert({ pg : 14788, line : "Et, pleine de colère, Hèrè aux bras blancs lui répondit:"});
db.iliadfr.insert({ pg : 14790, line : "-- Tu parles bien, archer, si on accorde des honneurs égaux à"});
db.iliadfr.insert({ pg : 14791, line : "Akhilleus et à Hektôr. Mais le Priamide a sucé la mamelle d'une"});
db.iliadfr.insert({ pg : 14792, line : "femme mortelle, tandis qu'Akhilleus est né d'une déesse que j'ai"});
db.iliadfr.insert({ pg : 14793, line : "nourrie moi-même et élevée avec tendresse, et que j'ai unie au"});
db.iliadfr.insert({ pg : 14794, line : "guerrier Pèleus cher aux immortels. Vous avez tous assisté à leurs"});
db.iliadfr.insert({ pg : 14795, line : "noces, ô dieux! et tu as pris part au festin, tenant ta kithare,"});
db.iliadfr.insert({ pg : 14796, line : "toi, protecteur des mauvais, et toujours perfide."});
db.iliadfr.insert({ pg : 14798, line : "Et Zeus qui amasse les nuées, lui répondant, parla ainsi:"});
db.iliadfr.insert({ pg : 14800, line : "-- Hèrè, ne t'irrite point contre les dieux. Un honneur égal ne"});
db.iliadfr.insert({ pg : 14801, line : "sera point fait à ces deux héros; mais Hektôr était le plus cher"});
db.iliadfr.insert({ pg : 14802, line : "aux dieux parmi les hommes qui sont dans Ilios. Et il m'était cher"});
db.iliadfr.insert({ pg : 14803, line : "à moi-même, car il n'oublia jamais les dons qui me sont agréables,"});
db.iliadfr.insert({ pg : 14804, line : "et jamais il n'a laissé mon autel manquer d'un repas abondant, de"});
db.iliadfr.insert({ pg : 14805, line : "libations et de parfums, car nous avons ces honneurs en partage."});
db.iliadfr.insert({ pg : 14806, line : "Mais, certes, nous ne ferons point enlever furtivement le brave"});
db.iliadfr.insert({ pg : 14807, line : "Hektôr, ce qui serait honteux, car Akhilleus serait averti par sa"});
db.iliadfr.insert({ pg : 14808, line : "mère qui est auprès de lui nuit et jour. Qu'un des dieux appelle"});
db.iliadfr.insert({ pg : 14809, line : "Thétis auprès de moi, et je lui dirai de sages paroles, afin"});
db.iliadfr.insert({ pg : 14810, line : "qu'Akhilleus reçoive les présents de Priamos et rende Hektôr."});
db.iliadfr.insert({ pg : 14812, line : "Il parla ainsi, et la messagère Iris aux pieds tourbillonnants"});
db.iliadfr.insert({ pg : 14813, line : "partit. Entre Samos et Imbros, elle sauta dans la noire mer qui"});
db.iliadfr.insert({ pg : 14814, line : "retentit. Et elle s'enfonça dans les profondeurs comme le plomb"});
db.iliadfr.insert({ pg : 14815, line : "qui, attaché à la corne d'un boeuf sauvage, descend, portant la"});
db.iliadfr.insert({ pg : 14816, line : "mort aux poissons voraces. Et elle trouva Thétis dans sa grotte"});
db.iliadfr.insert({ pg : 14817, line : "creuse; et autour d'elle les déesses de la mer étaient assises en"});
db.iliadfr.insert({ pg : 14818, line : "foule. Et là, Thétis pleurait la destinée de son fils"});
db.iliadfr.insert({ pg : 14819, line : "irréprochable qui devait mourir devant la riche Troiè, loin de sa"});
db.iliadfr.insert({ pg : 14820, line : "patrie. Et, s'approchant, la rapide Iris lui dit:"});
db.iliadfr.insert({ pg : 14822, line : "-- Lève-toi, Thétis. Zeus aux desseins éternels t'appelle."});
db.iliadfr.insert({ pg : 14824, line : "Et la déesse Thétis aux pieds d'argent lui répondit:"});
db.iliadfr.insert({ pg : 14826, line : "-- Pourquoi le grand dieu m'appelle-t-il? Je crains de me mêler"});
db.iliadfr.insert({ pg : 14827, line : "aux immortels, car je subis d'innombrables douleurs. J'irai"});
db.iliadfr.insert({ pg : 14828, line : "cependant, et, quoi qu'il ait dit, il n'aura point parlé en vain."});
db.iliadfr.insert({ pg : 14830, line : "Ayant ainsi parlé, la noble déesse prit un voile bleu, le plus"});
db.iliadfr.insert({ pg : 14831, line : "sombre de tous, et se hâta de partir. Et la rapide Iris aux pieds"});
db.iliadfr.insert({ pg : 14832, line : "aériens allait devant. Et l'eau de la mer s'entrouvrit devant"});
db.iliadfr.insert({ pg : 14833, line : "elles; et, montant sur le rivage, elles s'élancèrent dans"});
db.iliadfr.insert({ pg : 14834, line : "l'Ouranos. Et elles trouvèrent là le Kronide au large regard, et,"});
db.iliadfr.insert({ pg : 14835, line : "autour de lui, les éternels dieux heureux, assis et rassemblés. Et"});
db.iliadfr.insert({ pg : 14836, line : "Thétis s'assit auprès du père Zeus, Athènè lui ayant cédé sa"});
db.iliadfr.insert({ pg : 14837, line : "place. Hèrè lui mit en main une belle coupe d'or, en la consolant;"});
db.iliadfr.insert({ pg : 14838, line : "et Thétis, ayant bu, la lui rendit. Et le père des dieux et des"});
db.iliadfr.insert({ pg : 14839, line : "hommes parla le premier:"});
db.iliadfr.insert({ pg : 14841, line : "-- Déesse Thétis, tu es venue dans l'Olympos malgré ta tristesse,"});
db.iliadfr.insert({ pg : 14842, line : "car je sais que tu as dans le coeur une douleur insupportable."});
db.iliadfr.insert({ pg : 14843, line : "Cependant, je te dirai pourquoi je t'ai appelée. Depuis neuf jours"});
db.iliadfr.insert({ pg : 14844, line : "une dissension s'est élevée entre les immortels à cause du cadavre"});
db.iliadfr.insert({ pg : 14845, line : "de Hektôr, et d'Akhilleus destructeur de citadelles. Les dieux"});
db.iliadfr.insert({ pg : 14846, line : "excitaient le vigilant tueur d'Argos à enlever le corps du"});
db.iliadfr.insert({ pg : 14847, line : "Priamide; mais je protège la gloire d'Akhilleus, car j'ai gardé"});
db.iliadfr.insert({ pg : 14848, line : "mon respect et mon amitié pour toi. Va donc promptement à l'armée"});
db.iliadfr.insert({ pg : 14849, line : "des Argiens, et donne des ordres à ton fils. Dis-lui que les dieux"});
db.iliadfr.insert({ pg : 14850, line : "sont irrités, et que moi-même, plus que tous, je suis irrité"});
db.iliadfr.insert({ pg : 14851, line : "contre lui, parce que, dans sa fureur, il retient Hektôr auprès"});
db.iliadfr.insert({ pg : 14852, line : "des nefs aux poupes recourbées. S'il me redoute, qu'il le rende."});
db.iliadfr.insert({ pg : 14853, line : "Cependant, j'enverrai Iris au magnanime Priamos afin que, se"});
db.iliadfr.insert({ pg : 14854, line : "rendant aux nefs des Akhaiens, il rachète son fils bien-aimé, et"});
db.iliadfr.insert({ pg : 14855, line : "qu'il porte des présents qui fléchissent le coeur d'Akhilleus."});
db.iliadfr.insert({ pg : 14857, line : "Il parla ainsi, et la déesse Thétis aux pieds d'argent obéit. Et,"});
db.iliadfr.insert({ pg : 14858, line : "descendant à la hâte du faîte de l'Olympos, elle parvint à la"});
db.iliadfr.insert({ pg : 14859, line : "tente de son fils, et elle l'y trouva gémissant. Et, autour de"});
db.iliadfr.insert({ pg : 14860, line : "lui, ses compagnons préparaient activement le repas. Et une grande"});
db.iliadfr.insert({ pg : 14861, line : "brebis laineuse avait été tuée sous la tente. Et, auprès"});
db.iliadfr.insert({ pg : 14862, line : "d'Akhilleus, s'assit la mère vénérable. Et, le caressant de la"});
db.iliadfr.insert({ pg : 14863, line : "main, elle lui dit:"});
db.iliadfr.insert({ pg : 14865, line : "-- Mon enfant, jusques à quand, pleurant et gémissant, consumeras-"});
db.iliadfr.insert({ pg : 14866, line : "tu ton coeur, oubliant de manger et de dormir? Cependant il est"});
db.iliadfr.insert({ pg : 14867, line : "doux de s'unir par l'amour à une femme. Je ne te verrai pas"});
db.iliadfr.insert({ pg : 14868, line : "longtemps vivant; voici venir la mort et la moire toute-puissante."});
db.iliadfr.insert({ pg : 14869, line : "Mais écoute, car je te suis envoyée par Zeus. Il dit que tous les"});
db.iliadfr.insert({ pg : 14870, line : "dieux sont irrités contre toi, et que, plus que tous les"});
db.iliadfr.insert({ pg : 14871, line : "immortels, il est irrité aussi, parce que, dans ta fureur, tu"});
db.iliadfr.insert({ pg : 14872, line : "retiens Hektôr auprès des nefs éperonnées, et que tu ne le"});
db.iliadfr.insert({ pg : 14873, line : "renvoies point. Rends-le donc, et reçois le prix de son cadavre."});
db.iliadfr.insert({ pg : 14875, line : "Et Akhilleus aux pieds rapides, lui répondant, parla ainsi:"});
db.iliadfr.insert({ pg : 14877, line : "-- Qu'on apporte donc des présents et qu'on emporte ce cadavre,"});
db.iliadfr.insert({ pg : 14878, line : "puisque l'Olympien lui-même le veut."});
db.iliadfr.insert({ pg : 14880, line : "Et, auprès des nefs, la mère et le fils se parlaient ainsi en"});
db.iliadfr.insert({ pg : 14881, line : "paroles rapides. Et le Kronide envoya Iris vers la sainte Ilios:"});
db.iliadfr.insert({ pg : 14883, line : "-- Va, rapide Iris. Quitte ton siège dans l'Olympos, et ordonne,"});
db.iliadfr.insert({ pg : 14884, line : "dans Ilios, au magnanime Priamos qu'il aille aux nefs des Akhaiens"});
db.iliadfr.insert({ pg : 14885, line : "afin de racheter son fils bien-aimé, et qu'il porte à Akhilleus"});
db.iliadfr.insert({ pg : 14886, line : "des présents qui fléchissent son coeur. Qu'aucun autre Troien ne"});
db.iliadfr.insert({ pg : 14887, line : "le suive, sauf un héraut vénérable qui conduise les mulets et le"});
db.iliadfr.insert({ pg : 14888, line : "char rapide, et ramène vers la ville le cadavre de Hektôr que le"});
db.iliadfr.insert({ pg : 14889, line : "divin Akhilleus a tué. Et qu'il n'ait ni inquiétude, ni terreur."});
db.iliadfr.insert({ pg : 14890, line : "Nous lui donnerons pour guide le tueur d'Argos qui le conduira"});
db.iliadfr.insert({ pg : 14891, line : "jusqu'à Akhilleus. Et quand il sera entré dans la tente"});
db.iliadfr.insert({ pg : 14892, line : "d'Akhilleus, celui-ci ne le tuera point, et même il le défendra"});
db.iliadfr.insert({ pg : 14893, line : "contre tous, car il n'est ni violent, ni insensé, ni impie, et il"});
db.iliadfr.insert({ pg : 14894, line : "respectera un suppliant."});
db.iliadfr.insert({ pg : 14896, line : "Il parla ainsi, et la messagère Iris aux pieds tourbillonnants"});
db.iliadfr.insert({ pg : 14897, line : "s'élança et parvint aux demeures de Priamos, pleines de"});
db.iliadfr.insert({ pg : 14898, line : "gémissements et de deuil. Et les fils étaient assis dans la cour"});
db.iliadfr.insert({ pg : 14899, line : "autour de leur père, et ils trempaient de larmes leurs vêtements."});
db.iliadfr.insert({ pg : 14900, line : "Et, au milieu d'eux, le vieillard s'enveloppait dans son manteau,"});
db.iliadfr.insert({ pg : 14901, line : "et sa tête blanche et ses épaules étaient souillées de la cendre"});
db.iliadfr.insert({ pg : 14902, line : "qu'il y avait répandue de ses mains, en se roulant sur la terre."});
db.iliadfr.insert({ pg : 14903, line : "Et ses filles et ses belles-filles se lamentaient par les"});
db.iliadfr.insert({ pg : 14904, line : "demeures, se souvenant de tant de braves guerriers tombés morts"});
db.iliadfr.insert({ pg : 14905, line : "sous les mains des Argiens. Et la messagère de Zeus, s'approchant"});
db.iliadfr.insert({ pg : 14906, line : "de Priamos, lui parla à voix basse, car le tremblement agitait les"});
db.iliadfr.insert({ pg : 14907, line : "membres du vieillard:"});
db.iliadfr.insert({ pg : 14909, line : "-- Rassure-toi, Priamos Dardanide, et ne tremble pas. Je ne viens"});
db.iliadfr.insert({ pg : 14910, line : "point t'annoncer de malheur, mais une heureuse nouvelle. Je suis"});
db.iliadfr.insert({ pg : 14911, line : "envoyée par Zeus qui, de loin, prend souci de toi et te plaint."});
db.iliadfr.insert({ pg : 14912, line : "L'Olympien t'ordonne de racheter le divin Hektôr, et de porter à"});
db.iliadfr.insert({ pg : 14913, line : "Akhilleus des présents qui fléchissent son coeur. Qu'aucun autre"});
db.iliadfr.insert({ pg : 14914, line : "Troien ne te suive, sauf un héraut vénérable qui conduise les"});
db.iliadfr.insert({ pg : 14915, line : "mulets et le char rapide, et ramène vers la ville le cadavre de"});
db.iliadfr.insert({ pg : 14916, line : "Hektôr que le divin Akhilleus a tué. N'aie ni inquiétude, ni"});
db.iliadfr.insert({ pg : 14917, line : "terreur. Le tueur d'Argos sera ton guide et il te conduira jusqu'à"});
db.iliadfr.insert({ pg : 14918, line : "Akhilleus. Et quand il t'aura mené dans la tente d'Akhilleus,"});
db.iliadfr.insert({ pg : 14919, line : "celui-ci ne te tuera point, et même il te défendra contre tous,"});
db.iliadfr.insert({ pg : 14920, line : "car il n'est ni violent, ni insensé, ni impie, et il respectera un"});
db.iliadfr.insert({ pg : 14921, line : "suppliant."});
db.iliadfr.insert({ pg : 14923, line : "Ayant ainsi parlé, la rapide Iris partit. Et Priamos ordonna à ses"});
db.iliadfr.insert({ pg : 14924, line : "fils d'atteler les mulets au char, et d'y attacher une corbeille."});
db.iliadfr.insert({ pg : 14925, line : "Et il se rendit dans la chambre nuptiale, parfumée, en bois de"});
db.iliadfr.insert({ pg : 14926, line : "cèdre, et haute, et qui contenait beaucoup de choses admirables."});
db.iliadfr.insert({ pg : 14927, line : "Et il appela sa femme Hékabè, et il lui dit:"});
db.iliadfr.insert({ pg : 14929, line : "-- Ô chère! un messager oympien m'est venu de Zeus, afin qu'allant"});
db.iliadfr.insert({ pg : 14930, line : "aux nefs des Akhaiens, je rachète mon fils bien-aimé, et que je"});
db.iliadfr.insert({ pg : 14931, line : "porte à Akhilleus des présents qui fléchissent son coeur. Dis-moi"});
db.iliadfr.insert({ pg : 14932, line : "ce que tu penses dans ton esprit. Pour moi, mon courage et mon"});
db.iliadfr.insert({ pg : 14933, line : "coeur me poussent vers les nefs et la grande armée des Akhaiens."});
db.iliadfr.insert({ pg : 14935, line : "Il parla ainsi, et la femme se lamenta et répondit:"});
db.iliadfr.insert({ pg : 14937, line : "-- Malheur à moi! Tu as perdu cette prudence qui t'a illustré"});
db.iliadfr.insert({ pg : 14938, line : "parmi les étrangers et ceux auxquels tu commandes. Tu veux aller"});
db.iliadfr.insert({ pg : 14939, line : "seul vers les nefs des Akhaiens, et rencontrer cet homme qui t'a"});
db.iliadfr.insert({ pg : 14940, line : "tué tant de braves enfants! Sans doute ton coeur est de fer. Dès"});
db.iliadfr.insert({ pg : 14941, line : "qu'il t'aura vu et saisi, cet homme féroce et sans foi n'aura"});
db.iliadfr.insert({ pg : 14942, line : "point pitié de toi et ne te respectera point, et nous te"});
db.iliadfr.insert({ pg : 14943, line : "pleurerons seuls dans nos demeures. Lorsque la moire puissante"});
db.iliadfr.insert({ pg : 14944, line : "reçut Hektôr naissant dans ses langes, après que je l'eus enfanté,"});
db.iliadfr.insert({ pg : 14945, line : "elle le destina à rassasier les chiens rapides, loin de ses"});
db.iliadfr.insert({ pg : 14946, line : "parents, sous les yeux d'un guerrier féroce. Que ne puis-je,"});
db.iliadfr.insert({ pg : 14947, line : "attachée à cet homme, lui manger le coeur! Alors seraient expiés"});
db.iliadfr.insert({ pg : 14948, line : "les maux de mon fils qui, cependant, n'est point mort en lâche, et"});
db.iliadfr.insert({ pg : 14949, line : "qui, sans rien craindre et sans fuir, a combattu jusqu'à la fin"});
db.iliadfr.insert({ pg : 14950, line : "pour les Troiens et les Troiennes."});
db.iliadfr.insert({ pg : 14952, line : "Et le divin vieillard Priamos lui répondit:"});
db.iliadfr.insert({ pg : 14954, line : "-- Ne tente point de me retenir, et ne sois point dans nos"});
db.iliadfr.insert({ pg : 14955, line : "demeures un oiseau de mauvais augure. Si quelque homme terrestre"});
db.iliadfr.insert({ pg : 14956, line : "m'avait parlé, soit un divinateur, soit un hiérophante, je"});
db.iliadfr.insert({ pg : 14957, line : "croirais qu'il a menti, et je ne l'écouterais point; mais j'ai vu"});
db.iliadfr.insert({ pg : 14958, line : "et entendu une déesse, et je pars, car sa parole s'accomplira. Si"});
db.iliadfr.insert({ pg : 14959, line : "ma destinée est de périr auprès des nefs des Akhaiens aux tuniques"});
db.iliadfr.insert({ pg : 14960, line : "d'airain, soit! Akhilleus me tuera; tandis que je me rassasierai"});
db.iliadfr.insert({ pg : 14961, line : "de sanglots en embrassant mon fils."});
db.iliadfr.insert({ pg : 14963, line : "Il parla ainsi, et il ouvrit les beaux couvercles de ses coffres."});
db.iliadfr.insert({ pg : 14964, line : "Et il prit douze péplos magnifiques, douze couvertures simples,"});
db.iliadfr.insert({ pg : 14965, line : "autant de tapis, autant de beaux manteaux et autant de tuniques."});
db.iliadfr.insert({ pg : 14966, line : "Il prit dix talents pesant d'or, deux trépieds éclatants, quatre"});
db.iliadfr.insert({ pg : 14967, line : "vases et une coupe magnifique que les guerriers thrèkiens lui"});
db.iliadfr.insert({ pg : 14968, line : "avaient donnée, présent merveilleux, quand il était allé en envoyé"});
db.iliadfr.insert({ pg : 14969, line : "chez eux. Mais le vieillard en priva ses demeures, désirant dans"});
db.iliadfr.insert({ pg : 14970, line : "son coeur racheter son fils. Et il chassa loin du portique tous"});
db.iliadfr.insert({ pg : 14971, line : "les Troiens, en leur adressant ces paroles injurieuses:"});
db.iliadfr.insert({ pg : 14973, line : "-- Allez, misérables couverts d'opprobre! N'avez-vous point de"});
db.iliadfr.insert({ pg : 14974, line : "deuil dans vos demeures? Pourquoi vous occupez-vous de moi? Vous"});
db.iliadfr.insert({ pg : 14975, line : "réjouissez-vous des maux dont le Kronide Zeus m'accable, et de ce"});
db.iliadfr.insert({ pg : 14976, line : "que j'ai perdu mon fils excellent? Vous en sentirez aussi la"});
db.iliadfr.insert({ pg : 14977, line : "perte, car, maintenant qu'il est mort, vous serez une proie plus"});
db.iliadfr.insert({ pg : 14978, line : "facile pour les Akhaiens. Pour moi avant de voir de mes yeux la"});
db.iliadfr.insert({ pg : 14979, line : "ville renversée et saccagée, je descendrai dans les demeures"});
db.iliadfr.insert({ pg : 14980, line : "d'Aidès!"});
db.iliadfr.insert({ pg : 14982, line : "Il parla ainsi, et de son sceptre il repoussait les hommes, et"});
db.iliadfr.insert({ pg : 14983, line : "ceux-ci se retiraient devant le vieillard qui les chassait. Et il"});
db.iliadfr.insert({ pg : 14984, line : "appelait ses fils avec menace, injuriant Hélénos et Pâris, et le"});
db.iliadfr.insert({ pg : 14985, line : "divin Agathôn, et Pammôn, et Antiphôn, et le brave Politès, et"});
db.iliadfr.insert({ pg : 14986, line : "Dèiphobos, et Hippothoos, et le divin Aganos. Et le vieillard, les"});
db.iliadfr.insert({ pg : 14987, line : "appelant tous les neuf, leur commandait rudement:"});
db.iliadfr.insert({ pg : 14989, line : "-- Hâtez-vous, misérables et infâmes enfants! Plût aux dieux que"});
db.iliadfr.insert({ pg : 14990, line : "tous ensemble, au lieu de Hektôr, vous fussiez tombés devant les"});
db.iliadfr.insert({ pg : 14991, line : "nefs rapides! Malheureux que je suis! J'avais engendré, dans la"});
db.iliadfr.insert({ pg : 14992, line : "grande Troiè, des fils excellents, et pas un d'entre eux ne m'est"});
db.iliadfr.insert({ pg : 14993, line : "resté, ni l'illustre Mèstôr, ni Trôilos dompteur de chevaux, ni"});
db.iliadfr.insert({ pg : 14994, line : "Hektôr qui était comme un dieu parmi les hommes, et qui ne"});
db.iliadfr.insert({ pg : 14995, line : "semblait pas être le fils d'un homme, mais d'un dieu. Arès me les"});
db.iliadfr.insert({ pg : 14996, line : "a tous enlevés, et il ne me reste que des lâches, des menteurs,"});
db.iliadfr.insert({ pg : 14997, line : "des sauteurs qui ne sont habiles qu'aux danses, des voleurs"});
db.iliadfr.insert({ pg : 14998, line : "publics d'agneaux et de chevreaux! Ne vous hâterez-vous point de"});
db.iliadfr.insert({ pg : 14999, line : "me préparer ce char? N'y placerez-vous point toutes ces choses,"});
db.iliadfr.insert({ pg : 15000, line : "afin que je parte?"});
db.iliadfr.insert({ pg : 15002, line : "Il parla ainsi, et, redoutant les menaces de leur père, ils"});
db.iliadfr.insert({ pg : 15003, line : "amenèrent le beau char neuf, aux roues solides, attelé de mulets,"});
db.iliadfr.insert({ pg : 15004, line : "et ils y attachèrent une corbeille. Et ils prirent contre la"});
db.iliadfr.insert({ pg : 15005, line : "muraille le joug de buis, bossué et garni d'anneaux; et ils"});
db.iliadfr.insert({ pg : 15006, line : "prirent aussi les courroies du timon, longues de neuf coudées,"});
db.iliadfr.insert({ pg : 15007, line : "qu'ils attachèrent au bout du timon poli en les passant dans"});
db.iliadfr.insert({ pg : 15008, line : "l'anneau. Et ils les lièrent trois fois autour du bouton; puis,"});
db.iliadfr.insert({ pg : 15009, line : "les réunissant, ils les fixèrent par un noeud. Et ils apportèrent"});
db.iliadfr.insert({ pg : 15010, line : "de la chambre nuptiale les présents infinis destinés au rachat de"});
db.iliadfr.insert({ pg : 15011, line : "Hektôr, et ils les amassèrent sur le char. Puis, ils mirent sous"});
db.iliadfr.insert({ pg : 15012, line : "le joug les mulets aux sabots solides que les Mysiens avaient"});
db.iliadfr.insert({ pg : 15013, line : "autrefois donnés à Priamos. Et ils amenèrent aussi à Priamos les"});
db.iliadfr.insert({ pg : 15014, line : "chevaux que le vieillard nourrissait lui-même à la crèche polie."});
db.iliadfr.insert({ pg : 15015, line : "Et, sous les hauts portiques, le héraut et Priamos, tous deux"});
db.iliadfr.insert({ pg : 15016, line : "pleins de prudence, les attelèrent."});
db.iliadfr.insert({ pg : 15018, line : "Puis, Hékabè, le coeur triste, s'approcha d'eux, portant de sa"});
db.iliadfr.insert({ pg : 15019, line : "main droite un doux vin dans une coupe d'or, afin qu'ils fissent"});
db.iliadfr.insert({ pg : 15020, line : "des libations. Et, debout devant les chevaux, elle dit à Priamos:"});
db.iliadfr.insert({ pg : 15022, line : "-- Prends, et fais des libations au père Zeus, et prie-le, afin de"});
db.iliadfr.insert({ pg : 15023, line : "revenir dans tes demeures du milieu des ennemis, puisque ton coeur"});
db.iliadfr.insert({ pg : 15024, line : "te pousse vers les nefs, malgré moi. Supplie le Kroniôn Idaien qui"});
db.iliadfr.insert({ pg : 15025, line : "amasse les noires nuées et qui voit toute la terre d'Ilios."});
db.iliadfr.insert({ pg : 15026, line : "Demande-lui d'envoyer à ta droite celui des oiseaux qu'il aime le"});
db.iliadfr.insert({ pg : 15027, line : "mieux, et dont la force est la plus grande; et, le voyant de tes"});
db.iliadfr.insert({ pg : 15028, line : "yeux, tu marcheras, rassuré, vers les nefs des cavaliers Danaens."});
db.iliadfr.insert({ pg : 15029, line : "Mais si Zeus qui tonne au loin ne t'envoie point ce signe, je ne"});
db.iliadfr.insert({ pg : 15030, line : "te conseille point d'aller vers les nefs des Argiens, malgré ton"});
db.iliadfr.insert({ pg : 15031, line : "désir."});
db.iliadfr.insert({ pg : 15033, line : "Et Priamos semblable à un dieu, lui répondant, parla ainsi:"});
db.iliadfr.insert({ pg : 15035, line : "-- Ô femme, je ne repousserai point ton conseil. Il est bon"});
db.iliadfr.insert({ pg : 15036, line : "d'élever ses mains vers Zeus, afin qu'il ait pitié de nous."});
db.iliadfr.insert({ pg : 15038, line : "Le vieillard parla ainsi, et il ordonna à une servante de verser"});
db.iliadfr.insert({ pg : 15039, line : "une eau pure sur ses mains. Et la servante apporta le bassin et le"});
db.iliadfr.insert({ pg : 15040, line : "vase. Et Priamos, s'étant lavé les mains, reçut la coupe de"});
db.iliadfr.insert({ pg : 15041, line : "Hékabè; et, priant, debout au milieu de la cour, il répandit le"});
db.iliadfr.insert({ pg : 15042, line : "vin, regardant l'Ouranos et disant:"});
db.iliadfr.insert({ pg : 15044, line : "-- Père Zeus, qui règnes sur l'Ida, très glorieux, très grand,"});
db.iliadfr.insert({ pg : 15045, line : "accorde-moi de trouver grâce devant Akhilleus et de lui inspirer"});
db.iliadfr.insert({ pg : 15046, line : "de la compassion. Envoie à ma droite celui de tous les oiseaux que"});
db.iliadfr.insert({ pg : 15047, line : "tu aimes le mieux, et dont la force est la plus grande, afin que,"});
db.iliadfr.insert({ pg : 15048, line : "le voyant de mes yeux, je marche, rassuré, vers les nefs des"});
db.iliadfr.insert({ pg : 15049, line : "cavaliers Danaens."});
db.iliadfr.insert({ pg : 15051, line : "Il parla ainsi en priant, et le sage Zeus l'entendit, et il envoya"});
db.iliadfr.insert({ pg : 15052, line : "le plus véridique des oiseaux, l'aigle noir, le chasseur, celui"});
db.iliadfr.insert({ pg : 15053, line : "qu'on nomme le tacheté. Autant s'ouvrent les portes de la demeure"});
db.iliadfr.insert({ pg : 15054, line : "d'un homme riche, autant s'ouvraient ses deux ailes. Et il"});
db.iliadfr.insert({ pg : 15055, line : "apparut, volant à droite au-dessus de la ville; et tous se"});
db.iliadfr.insert({ pg : 15056, line : "réjouirent de le voir, et leur coeur fût joyeux dans leurs"});
db.iliadfr.insert({ pg : 15057, line : "poitrines."});
db.iliadfr.insert({ pg : 15059, line : "Et le vieillard monta aussitôt sur le beau char, et il le poussa"});
db.iliadfr.insert({ pg : 15060, line : "hors du vestibule et du portique sonore. Et les mulets traînaient"});
db.iliadfr.insert({ pg : 15061, line : "d'abord le char aux quatre roues, et le sage Idaios les"});
db.iliadfr.insert({ pg : 15062, line : "conduisait. Puis, venaient les chevaux que Priamos excitait du"});
db.iliadfr.insert({ pg : 15063, line : "fouet, et tous l'accompagnaient par la ville, en gémissant, comme"});
db.iliadfr.insert({ pg : 15064, line : "s'il allait à la mort. Et quand il fut descendu d'Ilios dans la"});
db.iliadfr.insert({ pg : 15065, line : "plaine, tous revinrent dans la ville, ses fils et ses gendres."});
db.iliadfr.insert({ pg : 15067, line : "Et Zeus au large regard, les voyant dans la plaine, eut pitié du"});
db.iliadfr.insert({ pg : 15068, line : "vieux Priamos, et, aussitôt, il dit à son fils bien-aimé Herméias:"});
db.iliadfr.insert({ pg : 15070, line : "-- Herméias, puisque tu te plais avec les hommes et que tu peux"});
db.iliadfr.insert({ pg : 15071, line : "exaucer qui tu veux, va! conduis Priamos aux nefs creuses des"});
db.iliadfr.insert({ pg : 15072, line : "Akhaiens, et fais qu'aucun des Danaens ne l'aperçoive avant qu'il"});
db.iliadfr.insert({ pg : 15073, line : "parvienne au Pèléide."});
db.iliadfr.insert({ pg : 15075, line : "Il parla ainsi, et le messager tueur d'Argos obéit. Et aussitôt il"});
db.iliadfr.insert({ pg : 15076, line : "attacha à ses talons de belles ailes immortelles et d'or qui le"});
db.iliadfr.insert({ pg : 15077, line : "portaient sur la mer et sur la terre immense comme le souffle du"});
db.iliadfr.insert({ pg : 15078, line : "vent. Et il prit la verge qui, selon qu'il le veut, ferme les"});
db.iliadfr.insert({ pg : 15079, line : "paupières des hommes ou les éveille. Et, la tenant à la main,"});
db.iliadfr.insert({ pg : 15080, line : "l'illustre tueur d'Argos s'envola et parvint aussitôt à Troiè et"});
db.iliadfr.insert({ pg : 15081, line : "au Hellespontos. Et il s'approcha, semblable à un jeune homme"});
db.iliadfr.insert({ pg : 15082, line : "royal dans la fleur de sa belle jeunesse."});
db.iliadfr.insert({ pg : 15084, line : "Et les deux vieillards, ayant dépassé la grande tombe d'Ilos,"});
db.iliadfr.insert({ pg : 15085, line : "arrêtèrent les mulets et les chevaux pour les faire boire au"});
db.iliadfr.insert({ pg : 15086, line : "fleuve. Et déjà l'ombre du soir se répandait sur la terre. Et le"});
db.iliadfr.insert({ pg : 15087, line : "héraut aperçut Herméias, non loin, et il dit à Priamos:"});
db.iliadfr.insert({ pg : 15089, line : "-- Prends garde, Dardanide! Ceci demande de la prudence. Je vois"});
db.iliadfr.insert({ pg : 15090, line : "un homme, et je pense que nous allons périr. Fuyons promptement"});
db.iliadfr.insert({ pg : 15091, line : "avec les chevaux, ou supplions-le en embrassant ses genoux. Peut-"});
db.iliadfr.insert({ pg : 15092, line : "être aura-t-il pitié de nous."});
db.iliadfr.insert({ pg : 15094, line : "Il parla ainsi et l'esprit de Priamos fut troublé, et il eut peur,"});
db.iliadfr.insert({ pg : 15095, line : "et ses cheveux se tinrent droits sur sa tête courbée, et il resta"});
db.iliadfr.insert({ pg : 15096, line : "stupéfait. Mais Herméias, s'approchant, lui prit la main et"});
db.iliadfr.insert({ pg : 15097, line : "l'interrogea ainsi:"});
db.iliadfr.insert({ pg : 15099, line : "-- Père, où mènes-tu ces chevaux et ces mulets, dans la nuit"});
db.iliadfr.insert({ pg : 15100, line : "solitaire, tandis que tous les autres hommes dorment? Ne crains-tu"});
db.iliadfr.insert({ pg : 15101, line : "pas les Akhaiens pleins de force, ces ennemis redoutables qui sont"});
db.iliadfr.insert({ pg : 15102, line : "près de toi? Si quelqu'un d'entre eux te rencontrait par la nuit"});
db.iliadfr.insert({ pg : 15103, line : "noire et rapide, emmenant tant de richesses, que ferais-tu? C'est"});
db.iliadfr.insert({ pg : 15104, line : "un vieillard qui te suit, et tu n'es plus assez jeune pour"});
db.iliadfr.insert({ pg : 15105, line : "repousser un guerrier qui vous attaquerait. Mais, loin de te"});
db.iliadfr.insert({ pg : 15106, line : "nuire, je te préserverai de tout mal, car tu me sembles mon père"});
db.iliadfr.insert({ pg : 15107, line : "bien-aimé."});
db.iliadfr.insert({ pg : 15109, line : "Et le vieux et divin Priamos lui répondit:"});
db.iliadfr.insert({ pg : 15111, line : "-- Mon cher fils, tu as dit la vérité. Mais un des dieux me"});
db.iliadfr.insert({ pg : 15112, line : "protège encore, puisqu'il envoie heureusement sur mon chemin un"});
db.iliadfr.insert({ pg : 15113, line : "guide tel que toi. Ton corps et ton visage sont beaux, ton esprit"});
db.iliadfr.insert({ pg : 15114, line : "est sage, et tu es né de parents heureux."});
db.iliadfr.insert({ pg : 15116, line : "Et le messager, tueur d'Argos, lui répondit:"});
db.iliadfr.insert({ pg : 15118, line : "-- Vieillard, tu n'as point parlé au hasard. Mais réponds, et dis"});
db.iliadfr.insert({ pg : 15119, line : "la vérité. Envoies-tu ces trésors nombreux et précieux à des"});
db.iliadfr.insert({ pg : 15120, line : "hommes étrangers, afin qu'on te les conserve? ou, dans votre"});
db.iliadfr.insert({ pg : 15121, line : "terreur, abandonnez-vous tous la sainte Ilios, car un guerrier"});
db.iliadfr.insert({ pg : 15122, line : "illustre est mort, ton fils, qui, dans le combat, ne le cédait"});
db.iliadfr.insert({ pg : 15123, line : "point aux Akhaiens?"});
db.iliadfr.insert({ pg : 15125, line : "Et le vieux et divin Priamos lui répondit:"});
db.iliadfr.insert({ pg : 15127, line : "-- Qui donc es-tu, ô excellent! Et de quels parents es-tu né, toi"});
db.iliadfr.insert({ pg : 15128, line : "qui parles si bien de la destinée de mon fils malheureux?"});
db.iliadfr.insert({ pg : 15130, line : "Et le messager, tueur d'Argos, lui répondit:"});
db.iliadfr.insert({ pg : 15132, line : "-- Tu m'interroges, vieillard, sur le divin Hektôr. Je l'ai vu"});
db.iliadfr.insert({ pg : 15133, line : "souvent de mes yeux dans la mêlée glorieuse, quand, repoussant"});
db.iliadfr.insert({ pg : 15134, line : "vers les nefs les Argiens dispersés, il les tuait de l'airain"});
db.iliadfr.insert({ pg : 15135, line : "aigu. Immobiles, nous l'admirions; car Akhilleus, irrité contre"});
db.iliadfr.insert({ pg : 15136, line : "l'Atréide, ne nous permettait point de combattre. Je suis son"});
db.iliadfr.insert({ pg : 15137, line : "serviteur, et la même nef bien construite nous a portés. Je suis"});
db.iliadfr.insert({ pg : 15138, line : "un des Myrmidones et mon père est Polyktôr. Il est riche et vieux"});
db.iliadfr.insert({ pg : 15139, line : "comme toi. Il a sept fils et je suis le septième. Ayant tiré au"});
db.iliadfr.insert({ pg : 15140, line : "sort avec eux, je fus désigné pour suivre Akhilleus. J'allais"});
db.iliadfr.insert({ pg : 15141, line : "maintenant des nefs dans la plaine. Demain matin les Akhaiens aux"});
db.iliadfr.insert({ pg : 15142, line : "sourcils arqués porteront le combat autour de la ville. Ils se"});
db.iliadfr.insert({ pg : 15143, line : "plaignent du repos, et les rois des Akhaiens ne peuvent retenir"});
db.iliadfr.insert({ pg : 15144, line : "les guerriers avides de combattre."});
db.iliadfr.insert({ pg : 15146, line : "Et le vieux et divin Priamos lui répondit:"});
db.iliadfr.insert({ pg : 15148, line : "-- Si tu es le serviteur du Pèlèiade Akhilleus, dis-moi toute la"});
db.iliadfr.insert({ pg : 15149, line : "vérité. Mon fils est-il encore auprès des nefs, ou déjà Akhilleus"});
db.iliadfr.insert({ pg : 15150, line : "a-t-il tranché tous ses membres, pour les livrer à ses chiens?"});
db.iliadfr.insert({ pg : 15152, line : "Et le messager, tueur d'Argos, lui répondit:"});
db.iliadfr.insert({ pg : 15154, line : "-- Ô vieillard, les chiens ne l'ont point encore mangé, ni les"});
db.iliadfr.insert({ pg : 15155, line : "oiseaux, mais il est couché devant la nef d'Akhilleus, sous la"});
db.iliadfr.insert({ pg : 15156, line : "tente. Voici douze jours et le corps n'est point corrompu, et les"});
db.iliadfr.insert({ pg : 15157, line : "vers, qui dévorent les guerriers tombés dans le combat, ne l'ont"});
db.iliadfr.insert({ pg : 15158, line : "point mangé. Mais Akhilleus le traîne sans pitié autour du tombeau"});
db.iliadfr.insert({ pg : 15159, line : "de son cher compagnon, dès que la divine Éôs reparaît, et il ne le"});
db.iliadfr.insert({ pg : 15160, line : "flétrit point. Tu admirerais, si tu le voyais, combien il est"});
db.iliadfr.insert({ pg : 15161, line : "frais. Le sang est lavé, il est sans aucune souillure, et toutes"});
db.iliadfr.insert({ pg : 15162, line : "les blessures sont fermées que beaucoup de guerriers lui ont"});
db.iliadfr.insert({ pg : 15163, line : "faites. Ainsi les dieux heureux prennent soin de ton fils, tout"});
db.iliadfr.insert({ pg : 15164, line : "mort qu'il est, parce qu'il leur était cher."});
db.iliadfr.insert({ pg : 15166, line : "Il parla ainsi, et le vieillard, plein de joie, lui répondit:"});
db.iliadfr.insert({ pg : 15168, line : "-- Ô mon enfant, certes, il est bon d'offrir aux immortels les"});
db.iliadfr.insert({ pg : 15169, line : "présents qui leur sont dus. Jamais mon fils, quand il vivait, n'a"});
db.iliadfr.insert({ pg : 15170, line : "oublié, dans ses demeures, les dieux qui habitent l'Olympos, et"});
db.iliadfr.insert({ pg : 15171, line : "voici qu'ils se souviennent de lui dans la mort. Reçois cette"});
db.iliadfr.insert({ pg : 15172, line : "belle coupe de ma main, fais qu'on me rende Hektôr, et conduis-"});
db.iliadfr.insert({ pg : 15173, line : "moi, à l'aide des dieux, jusqu'à la tente du Pèléide."});
db.iliadfr.insert({ pg : 15175, line : "Et le messager, tueur d'Argos, lui répondit:"});
db.iliadfr.insert({ pg : 15177, line : "-- Vieillard, tu veux tenter ma jeunesse, mais tu ne me"});
db.iliadfr.insert({ pg : 15178, line : "persuaderas point de prendre tes dons à l'insu d'Akhilleus. Je le"});
db.iliadfr.insert({ pg : 15179, line : "crains, en effet, et je le vénère trop dans mon coeur pour le"});
db.iliadfr.insert({ pg : 15180, line : "dépouiller, et il m'en arriverait malheur. Mais je"});
db.iliadfr.insert({ pg : 15181, line : "t'accompagnerais jusque dans l'illustre Argos, sur une nef rapide,"});
db.iliadfr.insert({ pg : 15182, line : "ou à pied; et aucun, si je te conduis, ne me bravera en"});
db.iliadfr.insert({ pg : 15183, line : "t'attaquant."});
db.iliadfr.insert({ pg : 15185, line : "Herméias, ayant ainsi parlé, sauta sur le char, saisit le fouet et"});
db.iliadfr.insert({ pg : 15186, line : "les rênes et inspira une grande force aux chevaux et aux mulets."});
db.iliadfr.insert({ pg : 15187, line : "Et ils arrivèrent au fossé et aux tours des nefs, là où les gardes"});
db.iliadfr.insert({ pg : 15188, line : "achevaient de prendre leur repas. Et le messager, tueur d'Argos,"});
db.iliadfr.insert({ pg : 15189, line : "répandit le sommeil sur eux tous; et, soulevant les barres, il"});
db.iliadfr.insert({ pg : 15190, line : "ouvrit les portes, et il fit entrer Priamos et ses présents"});
db.iliadfr.insert({ pg : 15191, line : "splendides dans le camp, et ils parvinrent à la grande tente du"});
db.iliadfr.insert({ pg : 15192, line : "Pèlèiade. Et les Myrmidones l'avaient faite pour leur roi avec des"});
db.iliadfr.insert({ pg : 15193, line : "planches de sapin, et ils l'avaient couverte d'un toit de joncs"});
db.iliadfr.insert({ pg : 15194, line : "coupés dans la prairie. Et tout autour ils avaient fait une grande"});
db.iliadfr.insert({ pg : 15195, line : "enceinte de pieux; et la porte en était fermée par un seul tronc"});
db.iliadfr.insert({ pg : 15196, line : "de sapin, barre énorme que trois hommes, les Akhaiens, ouvraient"});
db.iliadfr.insert({ pg : 15197, line : "et fermaient avec peine, et que le Pèléide soulevait seul. Le"});
db.iliadfr.insert({ pg : 15198, line : "bienveillant Herméias la retira pour Priamos, et il conduisit le"});
db.iliadfr.insert({ pg : 15199, line : "vieillard dans l'intérieur de la cour, avec les illustres présents"});
db.iliadfr.insert({ pg : 15200, line : "destinés à Akhilleus aux pieds rapides. Et il sauta du char sur la"});
db.iliadfr.insert({ pg : 15201, line : "terre, et il dit:"});
db.iliadfr.insert({ pg : 15203, line : "-- Ô vieillard, je suis Herméias, un dieu immortel, et Zeus m'a"});
db.iliadfr.insert({ pg : 15204, line : "envoyé pour te conduire. Mais je vais te quitter, et je ne me"});
db.iliadfr.insert({ pg : 15205, line : "montrerai point aux yeux d'Akhilleus, car il n'est point digne"});
db.iliadfr.insert({ pg : 15206, line : "d'un Immortel de protéger ainsi ouvertement les mortels. Toi,"});
db.iliadfr.insert({ pg : 15207, line : "entre, saisis les genoux du Pèléiôn et supplie-le au nom de son"});
db.iliadfr.insert({ pg : 15208, line : "père, de sa mère vénérable et de son fils, afin de toucher son"});
db.iliadfr.insert({ pg : 15209, line : "coeur."});
db.iliadfr.insert({ pg : 15211, line : "Ayant ainsi parlé, Herméias monta vers le haut Olympos; et Priamos"});
db.iliadfr.insert({ pg : 15212, line : "sauta du char sur la terre, et il laissa Idaios pour garder les"});
db.iliadfr.insert({ pg : 15213, line : "chevaux et les mulets, et il entra dans la tente où Akhilleus cher"});
db.iliadfr.insert({ pg : 15214, line : "à Zeus était assis. Et il le trouva. Ses compagnons étaient assis"});
db.iliadfr.insert({ pg : 15215, line : "à l'écart; et seuls, le héros Automédôn et le nourrisson d'Arès"});
db.iliadfr.insert({ pg : 15216, line : "Alkimos le servaient. Déjà il avait cessé de manger et de boire,"});
db.iliadfr.insert({ pg : 15217, line : "et la table était encore devant lui. Et le grand Priamos entra"});
db.iliadfr.insert({ pg : 15218, line : "sans être vu d'eux, et, s'approchant, il entoura de ses bras les"});
db.iliadfr.insert({ pg : 15219, line : "genoux d'Akhilleus, et il baisa les mains terribles et meurtrières"});
db.iliadfr.insert({ pg : 15220, line : "qui lui avaient tué tant de fils."});
db.iliadfr.insert({ pg : 15222, line : "Quand un homme a encouru une grande peine, ayant tué quelqu'un"});
db.iliadfr.insert({ pg : 15223, line : "dans sa patrie, et quand, exilé chez un peuple étranger, il entre"});
db.iliadfr.insert({ pg : 15224, line : "dans une riche demeure, tous ceux qui le voient restent"});
db.iliadfr.insert({ pg : 15225, line : "stupéfaits. Ainsi Akhilleus fut troublé en voyant le divin"});
db.iliadfr.insert({ pg : 15226, line : "Priamos; et les autres, pleins d'étonnement, se regardaient entre"});
db.iliadfr.insert({ pg : 15227, line : "eux. Et Priamos dit ces paroles suppliantes:"});
db.iliadfr.insert({ pg : 15229, line : "-- Souviens-toi de ton père, ô Akhilleus égal aux dieux! Il est de"});
db.iliadfr.insert({ pg : 15230, line : "mon âge et sur le seuil fatal de la vieillesse. Ses voisins"});
db.iliadfr.insert({ pg : 15231, line : "l'oppriment peut-être en ton absence, et il n'a personne qui"});
db.iliadfr.insert({ pg : 15232, line : "écarte loin de lui l'outrage et le malheur; mais, au moins, il"});
db.iliadfr.insert({ pg : 15233, line : "sait que tu es vivant, et il s'en réjouit dans son coeur, et il"});
db.iliadfr.insert({ pg : 15234, line : "espère tous les jours qu'il verra son fils bien-aimé de retour"});
db.iliadfr.insert({ pg : 15235, line : "d'Ilios. Mais, moi, malheureux! qui ai engendré des fils"});
db.iliadfr.insert({ pg : 15236, line : "irréprochables dans la grande Troiè, je ne sais s'il m'en reste un"});
db.iliadfr.insert({ pg : 15237, line : "seul. J'en avais cinquante quand les Akhaiens arrivèrent. Dix-neuf"});
db.iliadfr.insert({ pg : 15238, line : "étaient sortis du même sein, et plusieurs femmes avaient enfanté"});
db.iliadfr.insert({ pg : 15239, line : "les autres dans mes demeures. L'impétueux Arès a rompu les genoux"});
db.iliadfr.insert({ pg : 15240, line : "du plus grand nombre. Un seul défendait ma ville et mes peuples,"});
db.iliadfr.insert({ pg : 15241, line : "Hektôr, que tu viens de tuer tandis qu'il combattait pour sa"});
db.iliadfr.insert({ pg : 15242, line : "patrie. Et c'est pour lui que je viens aux nefs des Akhaiens; et"});
db.iliadfr.insert({ pg : 15243, line : "je t'apporte, afin de le racheter, des présents infinis. Respecte"});
db.iliadfr.insert({ pg : 15244, line : "les dieux, Akhilleus, et, te souvenant de ton père, aie pitié de"});
db.iliadfr.insert({ pg : 15245, line : "moi qui suis plus malheureux que lui, car j'ai pu, ce qu'aucun"});
db.iliadfr.insert({ pg : 15246, line : "homme n'a encore fait sur la terre, approcher de ma bouche les"});
db.iliadfr.insert({ pg : 15247, line : "mains de celui qui a tué mes enfants!"});
db.iliadfr.insert({ pg : 15249, line : "Il parla ainsi, et il remplit Akhilleus du regret de son père. Et"});
db.iliadfr.insert({ pg : 15250, line : "le Pèlèiade, prenant le vieillard par la main, le repoussa"});
db.iliadfr.insert({ pg : 15251, line : "doucement. Et ils se souvenaient tous deux; et Priamos, prosterné"});
db.iliadfr.insert({ pg : 15252, line : "aux pieds d'Akhilleus, pleurait de toutes ses larmes le tueur"});
db.iliadfr.insert({ pg : 15253, line : "d'hommes Hektôr; et Akhilleus pleurait son père et Patroklos, et"});
db.iliadfr.insert({ pg : 15254, line : "leurs gémissements retentissaient sous la tente."});
db.iliadfr.insert({ pg : 15256, line : "Puis, le divin Akhilleus, s'étant rassasié de larmes, sentit sa"});
db.iliadfr.insert({ pg : 15257, line : "douleur s'apaiser dans sa poitrine, et il se leva de son siège; et"});
db.iliadfr.insert({ pg : 15258, line : "plein de pitié pour cette tête et cette barbe blanche, il releva"});
db.iliadfr.insert({ pg : 15259, line : "le vieillard de sa main et lui dit ces paroles ailées:"});
db.iliadfr.insert({ pg : 15261, line : "-- Ah! malheureux! Certes, tu as subi des peines sans nombre dans"});
db.iliadfr.insert({ pg : 15262, line : "ton coeur. Comment as-tu osé venir seul vers les nefs des Akhaiens"});
db.iliadfr.insert({ pg : 15263, line : "et soutenir la vue de l'homme qui t'a tué tant de braves enfants?"});
db.iliadfr.insert({ pg : 15264, line : "Ton coeur est de fer. Mais prends ce siège, et, bien qu'affligés,"});
db.iliadfr.insert({ pg : 15265, line : "laissons nos douleurs s'apaiser, car le deuil ne nous rend rien."});
db.iliadfr.insert({ pg : 15266, line : "Les dieux ont destiné les misérables mortels à vivre pleins de"});
db.iliadfr.insert({ pg : 15267, line : "tristesse, et, seuls, ils n'ont point de soucis. Deux tonneaux"});
db.iliadfr.insert({ pg : 15268, line : "sont au seuil de Zeus, et l'un contient les maux, et l'autre les"});
db.iliadfr.insert({ pg : 15269, line : "biens. Et le foudroyant Zeus, mêlant ce qu'il donne, envoie tantôt"});
db.iliadfr.insert({ pg : 15270, line : "le mal et tantôt le bien. Et celui qui n'a reçu que des dons"});
db.iliadfr.insert({ pg : 15271, line : "malheureux est en proie à l'outrage, et la mauvaise faim le ronge"});
db.iliadfr.insert({ pg : 15272, line : "sur la terre féconde, et il va çà et là, non honoré des dieux ni"});
db.iliadfr.insert({ pg : 15273, line : "des hommes. Ainsi les dieux firent à Pèleus des dons illustres dès"});
db.iliadfr.insert({ pg : 15274, line : "sa naissance, et plus que tous les autres hommes il fut comblé de"});
db.iliadfr.insert({ pg : 15275, line : "félicités et de richesses, et il commanda aux Myrmidones, et,"});
db.iliadfr.insert({ pg : 15276, line : "mortel, il fut uni à une déesse. Mais les dieux le frappèrent d'un"});
db.iliadfr.insert({ pg : 15277, line : "mal: il fut privé d'une postérité héritière de sa puissance, et il"});
db.iliadfr.insert({ pg : 15278, line : "n'engendra qu'un fils qui doit bientôt mourir et qui ne soignera"});
db.iliadfr.insert({ pg : 15279, line : "point sa vieillesse; car, loin de ma patrie, je reste devant"});
db.iliadfr.insert({ pg : 15280, line : "Troiè, pour ton affliction et celle de tes enfants. Et toi-même,"});
db.iliadfr.insert({ pg : 15281, line : "vieillard, nous avons appris que tu étais heureux autrefois, et"});
db.iliadfr.insert({ pg : 15282, line : "que sur toute la terre qui va jusqu'à Lesbos de Makar, et, vers le"});
db.iliadfr.insert({ pg : 15283, line : "nord, jusqu'à la Phrygiè et le large Hellespontos, tu étais"});
db.iliadfr.insert({ pg : 15284, line : "illustre ô vieillard, par tes richesses et par tes enfants. Et"});
db.iliadfr.insert({ pg : 15285, line : "voici que les dieux t'ont frappé d'une calamité, et, depuis la"});
db.iliadfr.insert({ pg : 15286, line : "guerre et le carnage, des guerriers environnent ta ville. Sois"});
db.iliadfr.insert({ pg : 15287, line : "ferme, et ne te lamente point dans ton coeur sur l'inévitable"});
db.iliadfr.insert({ pg : 15288, line : "destinée. Tu ne feras point revivre ton fils par tes gémissements."});
db.iliadfr.insert({ pg : 15289, line : "Crains plutôt de subir d'autres maux."});
db.iliadfr.insert({ pg : 15291, line : "Et le vieux et divin Priamos lui répondit:"});
db.iliadfr.insert({ pg : 15293, line : "-- Ne me dis point de me reposer, ô nourrisson de Zeus, tant que"});
db.iliadfr.insert({ pg : 15294, line : "Hektôr est couché sans sépulture devant tes tentes. Rends-le-moi"});
db.iliadfr.insert({ pg : 15295, line : "promptement, afin je le voie de mes yeux, et reçois les présents"});
db.iliadfr.insert({ pg : 15296, line : "nombreux que nous te portons. Puisses-tu en jouir et retourner"});
db.iliadfr.insert({ pg : 15297, line : "dans la terre de ta patrie, puisque tu m'as laissé vivre et voir"});
db.iliadfr.insert({ pg : 15298, line : "la lumière de Hélios."});
db.iliadfr.insert({ pg : 15300, line : "Et Akhilleus aux pieds rapides, le regardant d'un oeil sombre, lui"});
db.iliadfr.insert({ pg : 15301, line : "répondit:"});
db.iliadfr.insert({ pg : 15303, line : "-- Vieillard, ne m'irrite pas davantage. Je sais que je dois te"});
db.iliadfr.insert({ pg : 15304, line : "rendre Hektôr. La mère qui m'a enfanté, la fille du Vieillard de"});
db.iliadfr.insert({ pg : 15305, line : "la mer, m'a été envoyée par Zeus. Et je sais aussi, Priamos, et tu"});
db.iliadfr.insert({ pg : 15306, line : "n'as pu me cacher, qu'un des dieux ta conduit aux nefs rapides"});
db.iliadfr.insert({ pg : 15307, line : "des Akhaiens. Aucun homme, bien que jeune et brave, n'eût osé"});
db.iliadfr.insert({ pg : 15308, line : "venir jusqu'au camp. Il n'eût point échappé aux gardes, ni soulevé"});
db.iliadfr.insert({ pg : 15309, line : "aisément les barrières de nos portes. Ne réveille donc point les"});
db.iliadfr.insert({ pg : 15310, line : "douleurs de mon âme. Bien que je t'aie reçu, vieillard, comme un"});
db.iliadfr.insert({ pg : 15311, line : "suppliant sous mes tentes, crains que je viole les ordres de Zeus"});
db.iliadfr.insert({ pg : 15312, line : "et que je te tue."});
db.iliadfr.insert({ pg : 15314, line : "Il parla ainsi, et le vieillard trembla et obéit. Et le Pèléide"});
db.iliadfr.insert({ pg : 15315, line : "sauta comme un lion hors de la tente. Et il n'était point seul, et"});
db.iliadfr.insert({ pg : 15316, line : "deux serviteurs le suivirent, le héros Automédôn et Alkimos. Et"});
db.iliadfr.insert({ pg : 15317, line : "Akhilleus les honorait entre tous ses compagnons depuis la mort de"});
db.iliadfr.insert({ pg : 15318, line : "Patroklos. Et ils dételèrent les chevaux et les mulets, et ils"});
db.iliadfr.insert({ pg : 15319, line : "firent entrer le héraut de Priamos et lui donnèrent un siège. Puis"});
db.iliadfr.insert({ pg : 15320, line : "ils enlevèrent du beau char les présents infinis qui rachetaient"});
db.iliadfr.insert({ pg : 15321, line : "Hektôr; mais ils y laissèrent deux manteaux et une riche tunique"});
db.iliadfr.insert({ pg : 15322, line : "pour envelopper le cadavre qu'on allait emporter dans Ilios."});
db.iliadfr.insert({ pg : 15324, line : "Et Akhilleus, appelant les femmes, leur ordonna de laver le"});
db.iliadfr.insert({ pg : 15325, line : "cadavre et de le parfumer à l'écart, afin que Priamos ne vît point"});
db.iliadfr.insert({ pg : 15326, line : "son fils, et de peur qu'en le voyant, le père ne pût contenir sa"});
db.iliadfr.insert({ pg : 15327, line : "colère dans son coeur irrité, et qu'Akhilleus, furieux, le tuât,"});
db.iliadfr.insert({ pg : 15328, line : "en violant les ordres de Zeus. Et après que les femmes, ayant lavé"});
db.iliadfr.insert({ pg : 15329, line : "et parfumé le cadavre, l'eurent enveloppé du beau manteau et de la"});
db.iliadfr.insert({ pg : 15330, line : "tunique, Akhilleus le souleva lui-même du lit funèbre, et, avec"});
db.iliadfr.insert({ pg : 15331, line : "l'aide de ses compagnons, il le plaça sur le beau char. Puis, il"});
db.iliadfr.insert({ pg : 15332, line : "appela en gémissant son cher compagnon:"});
db.iliadfr.insert({ pg : 15334, line : "-- Ne t'irrite point contre moi, Patroklos, si tu apprends, chez"});
db.iliadfr.insert({ pg : 15335, line : "Aidès, que j'ai rendu le divin Hektôr à son père bien-aimé; car il"});
db.iliadfr.insert({ pg : 15336, line : "m'a fait des présents honorables, dont je te réserve, comme il est"});
db.iliadfr.insert({ pg : 15337, line : "juste, une part égale."});
db.iliadfr.insert({ pg : 15339, line : "Le divin Akhilleus, ayant ainsi parlé, rentra dans sa tente. Et il"});
db.iliadfr.insert({ pg : 15340, line : "reprit le siège poli qu'il occupait en face de Priamos, et il lui"});
db.iliadfr.insert({ pg : 15341, line : "dit:"});
db.iliadfr.insert({ pg : 15343, line : "-- Ton fils t'est rendu, vieillard, comme tu l'as désiré. Il est"});
db.iliadfr.insert({ pg : 15344, line : "couché sur un lit. Tu le verras et tu l'emporteras au retour"});
db.iliadfr.insert({ pg : 15345, line : "d'Éôs. Maintenant, songeons au repas. Niobè aux beaux cheveux"});
db.iliadfr.insert({ pg : 15346, line : "elle-même se souvint de manger après que ses douze enfants eurent"});
db.iliadfr.insert({ pg : 15347, line : "péri dans ses demeures, six filles et autant de fils florissants"});
db.iliadfr.insert({ pg : 15348, line : "de jeunesse. Apollôn, irrité contre Niobè, tua ceux-ci de son arc"});
db.iliadfr.insert({ pg : 15349, line : "d'argent; et Artémis qui se réjouit de ses flèches tua celles-là,"});
db.iliadfr.insert({ pg : 15350, line : "parce que Niobè s'était égalée à Lètô aux belles joues, disant que"});
db.iliadfr.insert({ pg : 15351, line : "la déesse n'avait conçu que deux enfants, tandis qu'elle en avait"});
db.iliadfr.insert({ pg : 15352, line : "conçu de nombreux. Elle le disait, mais les deux enfants de Lètô"});
db.iliadfr.insert({ pg : 15353, line : "tuèrent tous les siens. Et depuis neuf jours ils étaient couchés"});
db.iliadfr.insert({ pg : 15354, line : "dans le sang, et nul ne les ensevelissait: le Kroniôn avait changé"});
db.iliadfr.insert({ pg : 15355, line : "ces peuples en pierres; mais, le dixième jour, les dieux les"});
db.iliadfr.insert({ pg : 15356, line : "ensevelirent. Et, cependant, Niobè se souvenait de manger"});
db.iliadfr.insert({ pg : 15357, line : "lorsqu'elle était fatiguée de pleurer. Et maintenant, au milieu"});
db.iliadfr.insert({ pg : 15358, line : "des rochers et des montagnes désertes, sur le Sipylos, où sont les"});
db.iliadfr.insert({ pg : 15359, line : "retraites des nymphes divines qui dansent autour de l'Akhélôios,"});
db.iliadfr.insert({ pg : 15360, line : "bien que changée en pierre par les dieux, elle souffre encore."});
db.iliadfr.insert({ pg : 15361, line : "Allons, divin vieillard, mangeons. Tu pleureras ensuite ton fils"});
db.iliadfr.insert({ pg : 15362, line : "bien-aimé, quand tu l'auras conduit dans Ilios. Là, il te fera"});
db.iliadfr.insert({ pg : 15363, line : "répandre des larmes."});
db.iliadfr.insert({ pg : 15365, line : "Le rapide Akhilleus parla ainsi, et, se levant, il tua une brebis"});
db.iliadfr.insert({ pg : 15366, line : "blanche. Et ses compagnons, l'ayant écorchée, la préparèrent avec"});
db.iliadfr.insert({ pg : 15367, line : "soin. Et, la coupant en morceaux, ils les fixèrent à des broches,"});
db.iliadfr.insert({ pg : 15368, line : "les rôtirent et les retirèrent à temps. Et Automédôn, prenant le"});
db.iliadfr.insert({ pg : 15369, line : "pain, le distribua sur la table dans de belles corbeilles. Et"});
db.iliadfr.insert({ pg : 15370, line : "Akhilleus distribua lui-même les chairs. Tous étendirent les mains"});
db.iliadfr.insert({ pg : 15371, line : "sur les mets qui étaient devant eux. Et quand ils n'eurent plus le"});
db.iliadfr.insert({ pg : 15372, line : "désir de boire et de manger, le Dardanide Priamos admira combien"});
db.iliadfr.insert({ pg : 15373, line : "Akhilleus était grand et beau et semblable aux dieux. Et Akhilleus"});
db.iliadfr.insert({ pg : 15374, line : "admirait aussi le Dardanide Priamos, son aspect vénérable et ses"});
db.iliadfr.insert({ pg : 15375, line : "sages paroles. Et, quand ils se furent admirés longtemps, le vieux"});
db.iliadfr.insert({ pg : 15376, line : "et divin Priamos parla ainsi:"});
db.iliadfr.insert({ pg : 15378, line : "-- Fais que je puisse me coucher promptement, nourrisson de Zeus,"});
db.iliadfr.insert({ pg : 15379, line : "afin que je jouisse du doux sommeil; car mes yeux ne se sont point"});
db.iliadfr.insert({ pg : 15380, line : "fermés sous mes paupières depuis que mon fils a rendu l'âme sous"});
db.iliadfr.insert({ pg : 15381, line : "tes mains. Je n'ai fait que me lamenter et subir des douleurs"});
db.iliadfr.insert({ pg : 15382, line : "infinies, prosterné sur le fumier, dans l'enceinte de ma cour. Et"});
db.iliadfr.insert({ pg : 15383, line : "je n'ai pris quelque nourriture, et je n'ai bu de vin qu'ici."});
db.iliadfr.insert({ pg : 15384, line : "Auparavant, je n'avais rien mangé."});
db.iliadfr.insert({ pg : 15386, line : "Il parla ainsi, et Akhilleus ordonna à ses compagnons et aux"});
db.iliadfr.insert({ pg : 15387, line : "femmes de préparer des lits sous le portique, et d'y étendre de"});
db.iliadfr.insert({ pg : 15388, line : "belles étoffes pourprées, puis des tapis, et, par-dessus, des"});
db.iliadfr.insert({ pg : 15389, line : "tuniques de laine. Et les femmes, sortant de la tente avec des"});
db.iliadfr.insert({ pg : 15390, line : "torches aux mains, préparèrent aussitôt deux lits. Et alors"});
db.iliadfr.insert({ pg : 15391, line : "Akhilleus aux pieds rapides dit avec bienveillance:"});
db.iliadfr.insert({ pg : 15393, line : "Tu dormiras hors de la tente, cher vieillard, de peur qu'un des"});
db.iliadfr.insert({ pg : 15394, line : "Akhaiens, venant me consulter, comme ils en ont coutume, ne"});
db.iliadfr.insert({ pg : 15395, line : "t'aperçoive dans la nuit noire et rapide. Et aussitôt il en"});
db.iliadfr.insert({ pg : 15396, line : "avertirait le prince des peuples Agamemnôn, et peut-être que le"});
db.iliadfr.insert({ pg : 15397, line : "rachat du cadavre serait retardé. Mais réponds-moi, et dis la"});
db.iliadfr.insert({ pg : 15398, line : "vérité. Combien de jours désires-tu pour ensevelir le divin"});
db.iliadfr.insert({ pg : 15399, line : "Hektôr, afin que je reste en repos pendant ce temps, et que je"});
db.iliadfr.insert({ pg : 15400, line : "retienne les peuples?"});
db.iliadfr.insert({ pg : 15402, line : "Et le vieux et divin Priamos lui répondit:"});
db.iliadfr.insert({ pg : 15404, line : "-- Si tu veux que je rende de justes honneurs au divin Hektôr, en"});
db.iliadfr.insert({ pg : 15405, line : "faisant cela, Akhilleus, tu exauceras mon voeu le plus cher. Tu"});
db.iliadfr.insert({ pg : 15406, line : "sais que nous sommes renfermés dans la ville, et loin de la"});
db.iliadfr.insert({ pg : 15407, line : "montagne où le bois doit être coupé, et que les Troiens sont"});
db.iliadfr.insert({ pg : 15408, line : "saisis de terreur. Pendant neuf jours nous pleurerons Hektôr dans"});
db.iliadfr.insert({ pg : 15409, line : "nos demeures; le dixième, nous l'ensevelirons, et le peuple fera"});
db.iliadfr.insert({ pg : 15410, line : "le repas funèbre; le onzième, nous le placerons dans le tombeau,"});
db.iliadfr.insert({ pg : 15411, line : "et, le douzième, nous combattrons de nouveau, s'il le faut."});
db.iliadfr.insert({ pg : 15413, line : "Et le divin Akhilleus aux pieds rapides lui répondit:"});
db.iliadfr.insert({ pg : 15415, line : "-- Vieillard Priamos, il en sera ainsi, selon ton désir; et"});
db.iliadfr.insert({ pg : 15416, line : "pendant ce temps, j'arrêterai la guerre."});
db.iliadfr.insert({ pg : 15418, line : "Ayant ainsi parlé, il serra la main droite du vieillard afin qu'il"});
db.iliadfr.insert({ pg : 15419, line : "cessât de craindre dans son coeur. Et le héraut et Priamos, tous"});
db.iliadfr.insert({ pg : 15420, line : "deux pleins de sagesse, s'endormirent sous le portique de la"});
db.iliadfr.insert({ pg : 15421, line : "tente. Et Akhilleus s'endormit dans le fond de sa tente bien"});
db.iliadfr.insert({ pg : 15422, line : "construite, et Breisèis aux belles joues coucha auprès de lui."});
db.iliadfr.insert({ pg : 15424, line : "Et tous les dieux et les hommes qui combattent à cheval dormaient"});
db.iliadfr.insert({ pg : 15425, line : "dans la nuit, domptés par le doux sommeil; mais le sommeil ne"});
db.iliadfr.insert({ pg : 15426, line : "saisit point le bienveillant Herméias, qui songeait à emmener le"});
db.iliadfr.insert({ pg : 15427, line : "roi Priamos du milieu des nefs, sans être vu des gardes sacrés des"});
db.iliadfr.insert({ pg : 15428, line : "portes. Et il s'approcha de sa tête et il lui dit:"});
db.iliadfr.insert({ pg : 15430, line : "-- Ô vieillard! ne crains-tu donc aucun malheur, que tu dormes"});
db.iliadfr.insert({ pg : 15431, line : "ainsi au milieu d'hommes ennemis, après qu'Akhilleus t'a épargné?"});
db.iliadfr.insert({ pg : 15432, line : "Maintenant que tu as racheté ton fils bien-aimé par de nombreux"});
db.iliadfr.insert({ pg : 15433, line : "présents, les fils qui te restent en donneront trois fois autant"});
db.iliadfr.insert({ pg : 15434, line : "pour te racheter vivant, si l'Atréide Agamemnôn te découvre, et si"});
db.iliadfr.insert({ pg : 15435, line : "tous les Akhaiens l'apprennent."});
db.iliadfr.insert({ pg : 15437, line : "Il parla ainsi, et le vieillard trembla; et il ordonna au héraut"});
db.iliadfr.insert({ pg : 15438, line : "de se lever. Et Herméias attela leurs mulets et leurs chevaux, et"});
db.iliadfr.insert({ pg : 15439, line : "il les conduisit rapidement à travers le camp, et nul ne les vit."});
db.iliadfr.insert({ pg : 15440, line : "Et quand ils furent arrivés au gué du fleuve au beau cours, du"});
db.iliadfr.insert({ pg : 15441, line : "Xanthos tourbillonnant que l'immortel Zeus engendra, Herméias"});
db.iliadfr.insert({ pg : 15442, line : "remonta vers le haut Olympos."});
db.iliadfr.insert({ pg : 15444, line : "Et déjà Éôs au péplos couleur de safran se répandait sur toute la"});
db.iliadfr.insert({ pg : 15445, line : "terre, et les deux vieillards poussaient les chevaux vers la"});
db.iliadfr.insert({ pg : 15446, line : "ville, en pleurant et en se lamentant, et les mulets portaient le"});
db.iliadfr.insert({ pg : 15447, line : "cadavre. Et nul ne les aperçut, parmi les hommes et les femmes aux"});
db.iliadfr.insert({ pg : 15448, line : "belles ceintures, avant Kassandrè semblable à Aphroditè d'or. Et,"});
db.iliadfr.insert({ pg : 15449, line : "du haut de Pergamos, elle vit son père bien-aimé, debout sur le"});
db.iliadfr.insert({ pg : 15450, line : "char, et le héraut, et le corps que les mulets amenaient sur le"});
db.iliadfr.insert({ pg : 15451, line : "lit funèbre. Et aussitôt elle pleura, et elle cria, par toute la"});
db.iliadfr.insert({ pg : 15452, line : "ville:"});
db.iliadfr.insert({ pg : 15454, line : "-- Voyez, Troiens et Troiennes! Si vous alliez autrefois au-devant"});
db.iliadfr.insert({ pg : 15455, line : "de Hektôr, le coeur plein de joie, quand il revenait vivant du"});
db.iliadfr.insert({ pg : 15456, line : "combat, voyez celui qui était l'orgueil de la ville et de tout un"});
db.iliadfr.insert({ pg : 15457, line : "peuple!"});
db.iliadfr.insert({ pg : 15459, line : "Elle parla ainsi, et nul parmi les hommes et les femmes ne resta"});
db.iliadfr.insert({ pg : 15460, line : "dans la ville, tant un deuil irrésistible les entraînait tous. Et"});
db.iliadfr.insert({ pg : 15461, line : "ils coururent, au-delà des portes, au-devant du cadavre. Et, les"});
db.iliadfr.insert({ pg : 15462, line : "premières, l'épouse bien-aimée et la mère vénérable, arrachant"});
db.iliadfr.insert({ pg : 15463, line : "leurs cheveux, se jetèrent sur le char en embrassant la tête de"});
db.iliadfr.insert({ pg : 15464, line : "Hektôr. Et tout autour la foule pleurait. Et certes, tout le jour,"});
db.iliadfr.insert({ pg : 15465, line : "jusqu'à la chute de Hélios, ils eussent gémi et pleuré devant les"});
db.iliadfr.insert({ pg : 15466, line : "portes, si Priamos, du haut de son char, n'eût dit à ses peuples:"});
db.iliadfr.insert({ pg : 15468, line : "-- Retirez-vous, afin que je passe avec les mulets. Nous nous"});
db.iliadfr.insert({ pg : 15469, line : "rassasierons de larmes quand j'aurai conduit ce corps dans ma"});
db.iliadfr.insert({ pg : 15470, line : "demeure."});
db.iliadfr.insert({ pg : 15472, line : "Il parla ainsi, et, se séparant, ils laissèrent le char passer."});
db.iliadfr.insert({ pg : 15473, line : "Puis, ayant conduit Hektôr dans les riches demeures, ils le"});
db.iliadfr.insert({ pg : 15474, line : "déposèrent sur un lit sculpté, et ils appelèrent les chanteurs"});
db.iliadfr.insert({ pg : 15475, line : "funèbres, et ceux-ci gémirent un chant lamentable auquel"});
db.iliadfr.insert({ pg : 15476, line : "succédaient les plaintes des femmes. Et, parmi celles-ci,"});
db.iliadfr.insert({ pg : 15477, line : "Andromakhè aux bras blancs commença le deuil, tenant dans ses"});
db.iliadfr.insert({ pg : 15478, line : "mains la tête du tueur d'hommes Hektôr:"});
db.iliadfr.insert({ pg : 15480, line : "-- Ô homme! tu es mort jeune, et tu m'as laissée veuve dans mes"});
db.iliadfr.insert({ pg : 15481, line : "demeures, et je ne pense pas qu'il parvienne à la puberté, ce fils"});
db.iliadfr.insert({ pg : 15482, line : "enfant que nous avons engendré tous deux, ô malheureux que nous"});
db.iliadfr.insert({ pg : 15483, line : "sommes! Avant cela, cette ville sera renversée de son faîte,"});
db.iliadfr.insert({ pg : 15484, line : "puisque son défenseur a péri, toi qui la protégeais, et ses femmes"});
db.iliadfr.insert({ pg : 15485, line : "fidèles et ses petits enfants. Elles seront enlevées sur les nefs"});
db.iliadfr.insert({ pg : 15486, line : "creuses, et moi avec elles. Et toi, mon enfant, tu me suivras et"});
db.iliadfr.insert({ pg : 15487, line : "tu me subiras de honteux travaux, te fatiguant pour un maître"});
db.iliadfr.insert({ pg : 15488, line : "féroce! ou bien un Akhaien, te faisant tourner de la main, te"});
db.iliadfr.insert({ pg : 15489, line : "jettera du haut d'une tour pour une mort affreuse, furieux que"});
db.iliadfr.insert({ pg : 15490, line : "Hektôr ait tué ou son frère, ou son père, ou son fils; car de"});
db.iliadfr.insert({ pg : 15491, line : "nombreux Akhaiens sont tombés, mordant la terre, sous ses mains."});
db.iliadfr.insert({ pg : 15492, line : "Et ton père n'était pas doux dans le combat, et c'est pour cela"});
db.iliadfr.insert({ pg : 15493, line : "que les peuples le pleurent par la ville. Ô Hektôr! tu accables"});
db.iliadfr.insert({ pg : 15494, line : "tes parents d'un deuil inconsolable, et tu me laisses surtout en"});
db.iliadfr.insert({ pg : 15495, line : "proie à d'affreuses douleurs, car, en mourant, tu ne m'auras point"});
db.iliadfr.insert({ pg : 15496, line : "tendu les bras de ton lit, et tu ne m'auras point dit quelque sage"});
db.iliadfr.insert({ pg : 15497, line : "parole dont je puisse me souvenir, les jours et les nuits, en"});
db.iliadfr.insert({ pg : 15498, line : "versant des larmes."});
db.iliadfr.insert({ pg : 15500, line : "Elle parla ainsi en pleurant, et les femmes gémirent avec elle;"});
db.iliadfr.insert({ pg : 15501, line : "et, au milieu de celles-ci, Hékabè continua le deuil désespéré:"});
db.iliadfr.insert({ pg : 15503, line : "-- Hektôr, le plus cher de tous mes enfants, certes, les dieux"});
db.iliadfr.insert({ pg : 15504, line : "t'aimaient pendant ta vie, car ils ont veillé sur toi dans la"});
db.iliadfr.insert({ pg : 15505, line : "mort. Akhilleus aux pieds rapides a vendu tous ceux de mes fils"});
db.iliadfr.insert({ pg : 15506, line : "qu'il a pu saisir, par-delà la mer stérile, à Samos, à Imbros, et"});
db.iliadfr.insert({ pg : 15507, line : "dans la barbare Lemnos. Et il t'a arraché l'âme avec l'airain"});
db.iliadfr.insert({ pg : 15508, line : "aigu, et il t'a traîné autour du tombeau de son compagnon"});
db.iliadfr.insert({ pg : 15509, line : "Patroklos que tu as tué et qu'il n'a point fait revivre; et,"});
db.iliadfr.insert({ pg : 15510, line : "maintenant, te voici couché comme si tu venais de mourir dans nos"});
db.iliadfr.insert({ pg : 15511, line : "demeures, frais et semblable à un homme que l'archer Apollôn vient"});
db.iliadfr.insert({ pg : 15512, line : "de frapper de ses divines flèches."});
db.iliadfr.insert({ pg : 15514, line : "Elle parla ainsi en pleurant, et elle excita les gémissements des"});
db.iliadfr.insert({ pg : 15515, line : "femmes; et, au milieu de celles-ci, Hélénè continua le deuil:"});
db.iliadfr.insert({ pg : 15517, line : "-- Hektôr, tu étais le plus cher de tous mes frères, car"});
db.iliadfr.insert({ pg : 15518, line : "Alexandros, plein de beauté, est mon époux, lui qui m'a conduite"});
db.iliadfr.insert({ pg : 15519, line : "dans Troiè. Plût aux dieux que j'eusse péri auparavant! Voici déjà"});
db.iliadfr.insert({ pg : 15520, line : "la vingtième année depuis que je suis venue, abandonnant ma"});
db.iliadfr.insert({ pg : 15521, line : "patrie, et jamais tu ne m'as dit une parole injurieuse ou dure, et"});
db.iliadfr.insert({ pg : 15522, line : "si l'un de mes frères, ou l'une des mes soeurs, ou ma belle-mère,"});
db.iliadfr.insert({ pg : 15523, line : "-- car Priamos me fut toujours un père plein de douceur, -- me"});
db.iliadfr.insert({ pg : 15524, line : "blâmait dans nos demeures, tu les avertissais et tu les apaisais"});
db.iliadfr.insert({ pg : 15525, line : "par ta douceur et par tes paroles bienveillantes. C'est pour cela"});
db.iliadfr.insert({ pg : 15526, line : "que je te pleure en gémissant, moi, malheureuse, qui n'aurai plus"});
db.iliadfr.insert({ pg : 15527, line : "jamais un protecteur ni un ami dans la grande Troiè, car tous"});
db.iliadfr.insert({ pg : 15528, line : "m'ont en horreur."});
db.iliadfr.insert({ pg : 15530, line : "Elle parla ainsi en pleurant, et tout le peuple gémit."});
db.iliadfr.insert({ pg : 15532, line : "Mais le vieux Priamos leur dit:"});
db.iliadfr.insert({ pg : 15534, line : "-- Troiens, amenez maintenant le bois dans la ville, et ne"});
db.iliadfr.insert({ pg : 15535, line : "craignez point les embûches profondes des Argiens, car Akhilleus,"});
db.iliadfr.insert({ pg : 15536, line : "en me renvoyant des nefs noires, m'a promis de ne point nous"});
db.iliadfr.insert({ pg : 15537, line : "attaquer avant qu'Éôs ne soit revenue pour la douzième fois."});
db.iliadfr.insert({ pg : 15539, line : "Il parla ainsi, et tous, attelant aux chars les boeufs et les"});
db.iliadfr.insert({ pg : 15540, line : "mulets, aussitôt se rassemblèrent devant la ville. Et, pendant"});
db.iliadfr.insert({ pg : 15541, line : "neuf jours, ils amenèrent des monceaux de bois. Et quand Éôs"});
db.iliadfr.insert({ pg : 15542, line : "reparut pour la dixième fois éclairant les mortels, ils placèrent,"});
db.iliadfr.insert({ pg : 15543, line : "en versant des larmes, le brave Hektôr sur le faite du bûcher, et"});
db.iliadfr.insert({ pg : 15544, line : "ils y mirent le feu. Et quand Éôs aux doigts rosés, née au matin,"});
db.iliadfr.insert({ pg : 15545, line : "reparut encore, tout le peuple se rassembla autour du bûcher de"});
db.iliadfr.insert({ pg : 15546, line : "l'illustre Hektôr. Et, après s'être rassemblés, ils éteignirent"});
db.iliadfr.insert({ pg : 15547, line : "d'abord le bûcher où la force du feu avait brûlé, avec du vin"});
db.iliadfr.insert({ pg : 15548, line : "noir. Puis, ses frères et ses compagnons recueillirent en"});
db.iliadfr.insert({ pg : 15549, line : "gémissant ses os blancs; et les larmes coulaient sur leurs joues."});
db.iliadfr.insert({ pg : 15550, line : "Et ils déposèrent dans une urne d'or ses os fumants, et ils"});
db.iliadfr.insert({ pg : 15551, line : "l'enveloppèrent de péplos pourprés. Puis, ils la mirent dans une"});
db.iliadfr.insert({ pg : 15552, line : "fosse creuse recouverte de grandes pierres, et, au-dessus, ils"});
db.iliadfr.insert({ pg : 15553, line : "élevèrent le tombeau. Et des sentinelles veillaient de tous côtés"});
db.iliadfr.insert({ pg : 15554, line : "de peur que les Akhaiens aux belles knèmides ne se jetassent sur"});
db.iliadfr.insert({ pg : 15555, line : "la ville. Puis, le tombeau étant achevé, ils se retirèrent et se"});
db.iliadfr.insert({ pg : 15556, line : "réunirent en foule, afin de prendre part à un repas solennel, dans"});
db.iliadfr.insert({ pg : 15557, line : "les demeures du roi Priamos, nourrisson de Zeus."});
db.iliadfr.insert({ pg : 15559, line : "Et c'est ainsi qu'ils accomplirent les funérailles de Hektôr"});
db.iliadfr.insert({ pg : 15560, line : "dompteur de chevaux."});
